SET NAMES UTF8;
ALTER TABLE genes ADD COLUMN GHR_metadata_ch TEXT CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci;
UPDATE genes SET GHR_metadata_ch = '{"gene": "JUP", "conditions": [{"name": "致心律失常性右室心肌病", "detail": "遗传学家庭参考资料提供有关心律失常性右室心肌病的信息。"}, {"name": "毛发角化病", "detail": "已经发现JUPgene中的几种突变引起一种形式的角化病，其毛发被分类为I型。这种形式的病症也称为纳克索斯病。它的特点是手掌和脚底上有厚厚的皮肤（掌plant角化病）；粗糙，干燥，细，紧卷，有时稀疏的头发；以及一种潜在的威胁生命的心脏病，称为心律失常性右室心肌病（ARVC）。(换行)导致I型毛发角化病的JUPgene突变导致异常短版本的珠蛋白的产生。异常的珠蛋白珠蛋白不能与其他桥粒蛋白适当地相互作用，这会改变桥粒的结构并阻止细胞有效地彼此附着。研究人员怀疑细胞之间的连接受损会使皮肤，头发和心肌更脆弱。随着时间的推移，由于这些组织暴露于机械应力（例如，皮肤表面上的摩擦或心肌的持续收缩和松弛），它们变得受损并且不能再正常工作。这种机制可能是毛发I型角化病中发生的皮肤，头发和心脏问题的基础。(换行)研究表明，异常短的珠蛋白也会损害Wnt信号，这似乎会导致心肌细胞被脂肪取代随着时间的推移细胞。这种异常的信号传导可能会影响受影响个体中ARVC的发展。"}, {"name": "其他疾病", "detail": "还发现JUPgene突变会引起一系列体征和症状，这些体征和症状与I型毛发角化病的体征和症状重叠（如上所述）。一些受影响的家庭有类似的皮肤和头发异常，但没有明显的心脏问题，而另一些家庭只有ARVC，皮肤和头发正常。尽管这些情况与珠蛋白的功能受损和桥粒异常有关，但尚不清楚该gene的突变如何导致这些不同的特征模式。（换行）JUPgene中的至少一个突变可引起称为致死性先天性大疱性表皮松解症（LCEB）的疾病。这种情况的特征包括非常脆弱的皮肤，容易起泡和脱落，完全没有头发（脱发）和指甲异常。皮肤异常导致婴儿早期严重的体液流失和死亡。发现导致LCEB的突变（称为Gln539Ter或Q539X）阻止了任何功能性珠蛋白的产生。结果，皮肤没有稳定的桥粒，因此皮肤细胞不能有效地彼此附着。由于受影响的个体在心脏病可能变得明显之前死亡，因此尚不清楚珠蛋白的完全丧失是否还会损害心脏中的桥粒。"}]}' WHERE id = 59;
UPDATE genes SET GHR_metadata_ch = '{"gene": "FKBP10", "conditions": [{"name": "Kuskokwim综合征", "detail": "FKBP10gene中至少有一个突变会导致Kuskokwim综合征，这是一种罕见病，会影响阿拉斯加西南部Yup''ik爱斯基摩人中的少数人。这种情况的特征是称为挛缩的关节畸形，尤其是在膝盖，脚踝和肘部。挛缩限制了受影响关节的运动。(换行)参与Kuskokwim综合征的FKBP10gene突变从FKBP10蛋白中删除了一个蛋白结构单元（氨基酸）。该突变写为Tyr293del，去除了293位的氨基酸酪氨酸。这种遗传变化导致产生不稳定且易于分解的蛋白质。结果，Kuskokwim综合征患者的FKBP10蛋白含量仅为正常水平的5%。尽管机理尚不清楚，但FKBP10蛋白的减少会导致胶原蛋白羟基化的严重降低，从而干扰胶原蛋白的交联。受影响个体中胶原分子的网络混乱。目前尚不清楚胶原基质的变化如何参与Kuskokwim综合征患者关节挛缩的发展。尚不清楚弹性蛋白异常是否也有助于这种疾病的特征。"}, {"name": "其他疾病", "detail": "FKBP10gene的突变也与其他结缔组织疾病有关。该gene的变化可导致罕见形式的成骨不全症，归类为XI型。XI型成骨不全症患者的骨头薄而脆，容易骨折。由FKBP10gene突变引起的另一种情况是布鲁克综合征1，其特征与成骨不全XI和Kuskokwim综合征（如上所述）相似：受影响的个体具有脆弱的骨骼以及通常影响膝盖，脚踝和肘部的关节挛缩。（换行）导致XI型成骨不全症和布鲁克综合征1的突变导致几乎不产生FKBP10蛋白。虽然Kuskokwim综合征患者有一些残留的胶原羟基化，但在成骨不全症XI型和布鲁克综合征1患者中几乎不存在。结果，胶原蛋白的交联受到严重损害：细胞外基质中的胶原蛋白原纤维很少，并且网络稀疏无序。胶原蛋白的这些变化如何导致XI型成骨不全症和布鲁克综合征1的体征和症状尚不清楚。另外，尚不清楚弹性蛋白的异常是否也与这些病症的发展有关。研究人员不确定为什么一些具有这些更严重的gene突变的人除了脆弱的骨骼外还有关节畸形，而另一些人则没有。"}]}' WHERE id = 73;
UPDATE genes SET GHR_metadata_ch = '{"gene": "MC2R", "conditions": [{"name": "家族性糖皮质激素缺乏症", "detail": "已发现MC2Rgene中的40多个突变会导致家族性糖皮质激素缺乏。这种情况的特征是可能危及生命的低血糖（低血糖），反复感染和肤色比其他家庭成员深（色素沉着过度）。MC2Rgene突变约占这种情况的25%。这些突变大多数会改变ACTH受体中的单个蛋白质结构单元（氨基酸）。结果，受体不能被转运至细胞膜或与ACTH结合。没有ACTH受体与其激素的结合，没有信号触发肾上腺产生糖皮质激素。这些激素的缺乏会损害血糖调节，免疫系统功能和其他细胞功能，从而导致家族性糖皮质激素缺乏症的体征和症状。"}, {"name": "原发性大结节性肾上腺增生", "detail": "遗传学家庭参考提供有关原发性大结节性肾上腺增生的信息。"}]}' WHERE id = 278;
UPDATE genes SET GHR_metadata_ch = '{"gene": "BLM", "conditions": [{"name": "布鲁姆综合症", "detail": "已在患有Bloom综合征的人中鉴定出BLMgene的突变，Bloom综合征是一种遗传性疾病，其特征是身材矮小，因日晒引起的皮疹和罹患癌症的风险增加。在这种情况下，改变的或无功能的BLM蛋白会增加姐妹染色单体交换的频率，并且细胞缺乏响应紫外线对DNA损伤的DNA修复活性。"}]}' WHERE id = 288;
UPDATE genes SET GHR_metadata_ch = '{"gene": "RMRP", "conditions": [{"name": "贫血发育不良", "detail": "在患有贫血性发育不良的人中至少鉴定出四个RMRPgene突变，该疾病的特征是严重的矮小身材（侏儒症）和其他骨骼异常。导致贫血性发育不良的RMRPgene突变会改变该gene产生的非编码RNA，而包含改变的非编码RNA的RNase MRP酶的核糖体RNA加工功能会受到损害。尽管具体机制尚不清楚，但该功能的损害可能会破坏骨骼发育，导致贫血性发育不良的体征和症状。"}, {"name": "软骨-头发发育不全", "detail": "在RMRPgene中已鉴定出100多种引起软骨-头发发育不全的突变。大约90%的这种疾病病例是由突变引起的，其中DNA结构单元（核苷酸）鸟嘌呤被RMRPgene中第70位的核苷酸腺嘌呤取代（写为70A>G）。在阿米什人中几乎所有已知的受影响个体，芬兰血统的约92%的受影响个体以及其他人群中的约一半的受影响个体中都发现了这种突变。RMRPgene中的（换行）突变可能导致产生非编码RNA，该RNA无法结合通常是RNase MRP酶复合物一部分的某些蛋白质。据信这些变化会影响酶的活性，从而干扰其在细胞内的重要功能。RNase MRP酶复合物受损会导致侏儒症，骨骼异常，免疫系统功能异常（免疫缺陷），癌症风险升高，头发稀疏生长（发育不全）以及软骨头发发育不全的其他体征和症状。"}, {"name": "其他疾病", "detail": "RMRPgene突变，包括一些导致软骨-头发发育不全的相同突变（如上所述），可能导致类似的疾病，称为干phy端发育不良而无发育不全。这种情况的特征是身材矮小和骨骼异常，通常不如软骨-头发发育不全严重。患有干骺端发育不良而没有发育不全的个体的头发结构或外观没有任何变化，但他们可能具有免疫缺陷和患癌症的风险增加。尚不清楚为什么相同的突变可能导致这两种情况。与无血管发育不良一起，它们通常被称为软骨-头发发育不全谱系障碍，其中无血管发育不良是最严重的形式，干phy端发育不良在轻度端没有过度肥大。"}]}' WHERE id = 334;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SLC52A2", "conditions": [{"name": "核黄素转运蛋白缺乏症神经病", "detail": "已发现SLC52A2gene中至少有11个突变引起核黄素转运蛋白缺乏性神经病。这种神经系统疾病包括两种以前被认为是分开的疾病：Brown-Vialetto-Van Laere综合征和Fazio Londe病。核黄素转运蛋白缺乏性神经病中涉及的某些gene突变会阻止RFVT2蛋白的产生。其他导致产生异常蛋白质，其运输核黄素的能力受损。目前尚不清楚这些变化如何导致导致听力丧失，面部和四肢肌肉无力以及患有该疾病的人的呼吸问题的神经问题。"}]}' WHERE id = 348;
UPDATE genes SET GHR_metadata_ch = '{"gene": "EDN3", "conditions": [{"name": "先天性巨结肠症", "detail": "已发现EDN3gene中约有10个突变会引起Hirschsprung病，这种疾病会导致严重的便秘或肠道阻塞。尽管Hirschsprung疾病是另一种称为Waardenburg综合征IV型（如下所述）的疾病的特征，但EDN3gene突变也可在没有Waardenburg综合征的人群中引起Hirschsprung疾病。这些突变改变了一个DNA构建模块（核苷酸）或在gene中插入了另一个核苷酸。EDN3gene的变化破坏了内皮素3的正常功能，阻止了其在肠神经发育中发挥其通常的作用。结果，这些细胞在胚胎发育期间不能正常形成。缺乏肠神经可防止粪便正常通过肠道，导致严重的便秘或肠道阻塞。"}, {"name": "瓦尔登堡综合症", "detail": "在患有IV型Waardenburg综合征（也称为Waardenburg-Shah综合征）的人中，已经鉴定出EDN3gene中至少有五个突变。这种类型的Waardenburg综合征的特征是皮肤，头发和眼睛的颜色发生变化；听力损失；和Hirschsprung病。EDN3突变会改变gene中的单个核苷酸，从而阻止功能性内皮素3蛋白的产生。因为活性内皮素3是形成肠神经和黑素细胞所必需的，所以这些细胞类型在胚胎发育过程中不能正常形成。肠某些部位的肠神经缺失会导致Hirschsprung病的体征和症状。黑素细胞的缺乏会影响皮肤，头发和眼睛的颜色，并导致Waardenburg综合征的听力损失。"}]}' WHERE id = 432;
UPDATE genes SET GHR_metadata_ch = '{"gene": "DLL4", "conditions": [{"name": "亚当斯-奥利弗综合症", "detail": "在患有Adams-Oliver综合征的人中至少发现了9个DLL4gene突变，这种疾病的特征是通常在头皮上的皮肤缺失区域（先天性皮肤发育不全）和手脚畸形。这些突变中的一些导致产生异常短的蛋白质，该蛋白质可能会迅速分解，从而导致DLL4短缺。其他突变会改变DLL4蛋白中的单个蛋白结构单元（氨基酸）。这些变化被认为会改变蛋白质的结构，削弱其功能。DLL4功能丧失可能是Adams-Oliver综合征患者血管异常的基础；但是，一些患有DLL4相关的Adams-Oliver综合征的人没有这些异常。目前尚不清楚DLL4功能的丧失如何导致该病的头皮和肢体异常。研究人员建议这些特征可能是由于出生前血管发育异常所致。"}]}' WHERE id = 433;
UPDATE genes SET GHR_metadata_ch = '{"gene": "CA12", "conditions": [{"name": "孤立性高氯多汗症", "detail": "CA12gene中的至少一个突变会导致孤立的高氯多汗症，这种疾病的特征是汗液中的盐含量异常高，这可能导致与血液中钠含量低相关的脱水（低钠性脱水）。导致这种情况的gene突变改变了CA 12蛋白中的单个蛋白结构单元（氨基酸），用氨基酸赖氨酸（写为Glu143Lys或E143K）取代了143位的氨基酸谷氨酸。改变的蛋白质的功能降低到正常的约70%。另外，改变的蛋白质比正常蛋白质更容易关闭（抑制），这进一步降低了蛋白质功能。据认为，改变的CA 12不太能够调节pH，并且它失去了控制汗液中释放出多少盐的能力，导致高氯多汗症。(换行)研究人员建议其他组织似乎不受CA12gene突变的影响，因为这些组织包含其他碳酸酐酶，可补偿CA 12功能的丧失。"}]}' WHERE id = 488;
UPDATE genes SET GHR_metadata_ch = '{"gene": "APP", "conditions": [{"name": "阿尔茨海默病", "detail": "APPgene的突变与早发性阿尔茨海默病有关，这种疾病在65岁之前观察到，是由于淀粉样蛋白前体蛋白缺陷导致淀粉样蛋白积聚和形成所致？肽和淀粉样斑块导致神经元死亡。"}, {"name": "遗传性脑淀粉样血管病", "detail": "已经发现APPgene的突变会引起遗传性脑淀粉样血管病，这种疾病的特征是出血性中风，成年中期观察到的痴呆和癫痫发作，运动和其他神经系统问题。该疾病的特征在于异常的淀粉样蛋白前体蛋白导致淀粉样蛋白的积累和形成？脑中的肽和淀粉样斑块。"}]}' WHERE id = 570;
UPDATE genes SET GHR_metadata_ch = '{"gene": "DOCK8", "conditions": [{"name": "常染色体隐性高IgE综合征", "detail": "已发现DOCK8gene中至少有110个突变会引起常染色体隐性高IgE综合征（AR-HIES），这是一种免疫系统疾病，会导致皮肤和呼吸道反复严重感染。与该疾病有关的大多数突变都会从DOCK8gene中删除DNA区域。如果产生任何蛋白质，这些缺失和其他DOCK8gene突变会导致异常短的蛋白质。结果，受影响的个体几乎没有或没有功能性DOCK8蛋白。(换行)DOCK8蛋白的缺乏会损害正常的免疫细胞发育和功能。据认为，缺乏DOCK8的T细胞在穿过密集空间（例如在皮肤内发现的空间）时无法保持其形状。异常细胞死亡，导致这些细胞数量减少。T细胞的短缺会削弱对外来入侵者的免疫反应，这是AR-HIES中常见的严重皮肤感染的原因。缺乏DOCK8还会损害B细胞成熟和抗体的产生。这种类型的免疫反应受损会导致这种疾病患者反复出现呼吸道感染。"}, {"name": "其他疾病", "detail": "已经在两个具有罕见形式智力障碍的个体中发现了影响DOCK8gene的突变，这些个体在医学文献中被描述为常染色体显性智力低下2。这些人有发育迟缓，包括言语迟缓或缺席。这种情况下涉及的突变导致产生异常短的DOCK8蛋白。与AR-HIES（如上所述）不同，与常染色体显性智力低下2相关的突变仅影响DOCK8gene的一个拷贝。研究人员不确定这些遗传变化如何导致智力残疾。"}]}' WHERE id = 591;
UPDATE genes SET GHR_metadata_ch = '{"gene": "MKRN3", "conditions": [{"name": "中枢性性早熟", "detail": "已发现20多个MKRN3gene突变引起中枢性性早熟。患有这种疾病的人青春期的迹象比正常人早，女孩在8岁之前，男孩在9岁之前。这些迹象包括女孩的乳房发育和月经期的开始，男孩的睾丸和阴茎增大，女孩和男孩的阴毛发育和生长突增。（换行）涉及中枢性性早熟的MKRN3gene突变被认为导致从该gene的一个拷贝产生无功能的MKRN3蛋白。因为该gene的另一个拷贝是无活性的，所以受影响的个体可能不产生功能性MKRN3蛋白。虽然机制尚不清楚，但研究人员推测，如果没有MKRN3蛋白来抑制GnRH的释放，下丘脑就会释放激素的爆发，从而比正常情况更早地刺激青春期的发作。(换行)因为只有父亲的MKRN3gene拷贝是活跃的，所以这种情况只能从一个人的父亲那里继承。"}]}' WHERE id = 675;
UPDATE genes SET GHR_metadata_ch = '{"gene": "NR5A1", "conditions": [{"name": "Swyer综合征", "detail": "NR5A1gene突变已在少数Swyer综合征患者中发现，Swyer综合征是一种影响性发育的疾病，也称为46，XY完全性腺发育不全或46，XY纯性腺发育不全。(换行)人们通常在每个细胞中有46条染色体。46条染色体中的两条，称为X和Y，被称为性染色体，因为它们有助于确定一个人是否会发展出男性或女性的性别特征。女孩和女人通常有两条X染色体（46，XX核型），而男孩和男人通常有一条X染色体和一条Y染色体（46，XY核型）。Swyer综合征患者NR5A1gene突变影响性别分化过程，阻止46，XY核型患者发生男性性腺（睾丸），导致女性生殖结构（子宫和输卵管）。"}, {"name": "其他疾病", "detail": "已在患有46，XY性发育障碍（也称为部分性腺发育不全）的人中鉴定出NR5A1gene突变。受影响的个体可能具有外表不清晰的男性或女性（模棱两可的生殖器）或生殖器和生殖器官的其他异常。他们也可能有肾上腺异常，产生几种激素，调节身体的许多基本功能。肾上腺异常可能导致这些激素缺乏，导致各种健康问题。（换行）在性腺不产生生殖细胞（卵或精子）的人群中也发现了影响性腺发育和功能的NR5A1gene突变。这些情况称为男性生精障碍和女性卵巢早衰，导致无法怀孕（不孕症）。"}]}' WHERE id = 733;
UPDATE genes SET GHR_metadata_ch = '{"gene": "CTSA", "conditions": [{"name": "半乳糖醛酸", "detail": "已发现CTSAgene中至少有20个突变引起半乳糖唾液酸中毒。这些突变中的大多数会改变组织蛋白酶A中的一个蛋白质结构单元（氨基酸）。在日本人群中，最常见的突变（写为SpDEx7）破坏了gene指令如何用于制备蛋白质。（换行）许多CTSA突变破坏组织蛋白酶A的蛋白质结构，削弱其与神经氨酸酶1和β-半乳糖苷酶或弹性蛋白结合蛋白结合的能力。结果，这些其他酶不起作用或过早分解。CTSAgene中的大多数突变会导致缺乏功能性组织蛋白酶a，并导致神经氨酸酶1，β-半乳糖苷酶和弹性蛋白结合蛋白的丢失。尚不清楚这四种蛋白质的缺乏如何导致半乳糖唾液酸中毒的体征和症状。"}]}' WHERE id = 754;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ZMYM2", "conditions": [{"name": "8p11骨髓增生综合征", "detail": "涉及ZMYM2gene的遗传变化会导致大多数8p11骨髓增生综合症。这种情况的特征是白细胞数量增加（骨髓增生性疾病）和淋巴瘤的发展，淋巴瘤是一种导致淋巴结肿瘤形成的血液相关癌症。骨髓增生性疾病通常发展成另一种形式的血癌，称为急性髓性白血病。8p11骨髓增生综合征最常见的原因是13号染色体和8号染色体之间遗传物质的重排（易位）。这种遗传变化将13号染色体上的ZMYM2gene的一部分与8号染色体上的FGFR1gene的一部分融合在一起。易位仅在癌细胞中发现。（换行）由正常FGFR1gene产生的蛋白质可以打开帮助细胞对其环境作出反应的细胞信号传导，例如通过刺激细胞生长。由融合的ZMYM2-FGFR1gene产生的蛋白质导致恒定的FGFR1信号传导。不受控制的信号传导促进连续的细胞生长和分裂，导致癌症。"}]}' WHERE id = 773;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SMARCA2", "conditions": [{"name": "Nicolaides-Baraitser综合征", "detail": "已发现SMARCA2gene中至少有50个突变引起Nicolaides-Baraitser综合征。这种情况的特征是多种异常，主要是头皮头发稀疏，头部小（小头畸形），独特的面部特征，身材矮小，手指异常，反复发作（癫痫病）以及中度至重度智力障碍，语言发展受损。几乎所有引起Nicolaides-Baraitser综合征的SMARCA2gene突变都会改变蛋白质中的单个蛋白质结构单元（氨基酸）。这些突变位于与ATP连接的蛋白质区域内，负责为SWI/SNF复合物提供能量。这些改变的蛋白质能够形成SWI/SNF复合物，但是这些复合物是无功能的，不能参与染色质重塑。这种调节过程的干扰改变了许多gene的活性，这可能解释了尼古拉德-巴拉特综合征的各种体征和症状。"}, {"name": "癌症", "detail": "已经在某些类型的癌症中发现了影响SMARCA2gene的常见DNA变体（多态性），特别是在吸烟者和头颈癌中的肺癌。与这些癌症相关的变化被描述为种系，这意味着它们从出生就存在并存在于身体的所有细胞中。（尽管上述引起Nicolaides-Baraitser综合征的突变也是种系，但患有该病症的个体患癌症的风险并未增加。）已发现两种特异性SMARCA2gene多态性与这些发展有关。癌症。这些变化发生在SMARCA2gene附近称为启动子区域的区域，该区域控制SMARCA2蛋白的产生。一项更改在位置-741（insTATTTTT）处添加了七个DNA构建基块（核苷酸）；另一个在启动子的-1321位（insTTTTAA）添加了六个核苷酸。仅当这两种多态性都存在于每个细胞中SMARCA2gene的两个拷贝中时，肺癌和头颈癌的发病率才会增加。(换行)这些多态性损害启动子的功能并降低SMARCA2gene的表达。降低的gene活性可能会降低或改变蛋白质的产生，这将导致SWI/SNF复合物的变化。这些变化可能损害正常的细胞分化，从而导致某些细胞类型的过度生长，从而导致癌症。或者，异常的SWI/SNF复合物可能会破坏有助于控制细胞生长和分裂的gene调控，从而导致癌症。目前尚不清楚为什么SMARCA2gene启动子区域的这些变化似乎仅与吸烟者的头颈癌和肺癌有关。"}]}' WHERE id = 788;
UPDATE genes SET GHR_metadata_ch = '{"gene": "GJB2", "conditions": [{"name": "Bart-Pumphrey综合征", "detail": "在Bart-Pumphrey综合征患者中至少鉴定出两个GJB2gene突变。这种情况的特征是指甲（白喉）白色变色，手掌和脚底皮肤增厚（掌跖角化病），手指和脚趾关节疣状生长（指关节垫）和听力损失。导致Bart-Pumphrey综合征的GJB2gene突变用蛋白质59位的氨基酸丝氨酸（Gly59Ser或G59S）代替蛋白质构建块（氨基酸）甘氨酸或用蛋白质54位的氨基酸赖氨酸代替氨基酸天冬酰胺（Asn54Lys或N54K）。改变的蛋白质可能破坏细胞中正常连接蛋白26的功能。这种破坏可能会影响皮肤的生长，并通过干扰声波向神经冲动的转换而损害听力。"}, {"name": "Hystrix样鱼鳞病伴耳聋", "detail": "在患有耳聋（HID）的hystrix样鱼鳞病患者中，至少鉴定出一种GJB2gene突变，该疾病的特征是皮肤干燥，鳞状（鱼鳞病）和听力下降，通常很严重。该突变用蛋白质位置50处的氨基酸天冬酰胺替换氨基酸天冬氨酸，写为Asp50Asn或D50N。人们认为该突变会导致通道不断泄漏离子，从而损害细胞健康并增加细胞死亡。皮肤和内耳细胞死亡可能是HID体征和症状的基础。(换行)由于D50N突变也可引起角膜炎-鱼鳞病-耳聋（KID）综合征（如下所述），因此许多研究人员将KID综合征和HID归类为单一疾病，他们称之为KID/HID。目前尚不清楚为什么有些人有这种gene突变而有些人没有眼睛问题。"}, {"name": "角膜炎-鱼鳞病-耳聋综合征", "detail": "在患有角膜炎-鱼鳞病-耳聋（KID）综合征的人中已经鉴定出至少9个GJB2gene突变，最常见的是D50N突变，该突变也发生在患有耳聋的hystrix样鱼鳞病中（如上所述）。KID综合征的特征是角膜炎，这是眼睛前表面（角膜）的炎症；厚而变红的干燥和鳞状皮肤斑块（鱼鳞病）；和耳聋。（换行）导致KID综合征的GJB2gene突变改变连接蛋白26中的单个氨基酸。突变被认为会导致通道不断泄漏离子，从而损害细胞健康并增加细胞死亡。皮肤和内耳中细胞的死亡可能是KID综合征中发生的鱼鳞病和耳聋的基础。目前尚不清楚GJB2gene突变如何影响眼睛。"}, {"name": "非综合征性听力损失", "detail": "研究人员已经鉴定出100多种可导致非综合征性听力损失的GJB2gene突变，这是与其他体征和症状无关的听力损失。该gene的突变可导致两种形式的非综合征性听力损失：DFNB1和DFNA3。(换行)DFNB1以常染色体隐性遗传，这意味着GJB2gene的两个拷贝都在每个细胞中发生了突变。这种形式的疾病约占所有常染色体隐性非综合征性听力损失病例的一半。它的特征是轻度至重度听力下降，这种情况在孩子学会说话（舌前）之前就存在，并且随着时间的推移不会变得更加严重。(换行)一些导致DFNB1缺失或插入GJB2gene内或附近的DNA构建块（碱基对）的突变。在许多人群中，尤其是在北欧血统的人群中，最常见的突变是在GJB2gene（写为35delG）的35位缺失一个碱基对。在亚洲人群中，经常报道的突变会删除位置235（235delC）处的碱基对。在具有东欧（Ashkenazi）犹太血统的人中，第167位（167delT）单个碱基对的缺失是常见的突变。导致DFNB1的GJB2gene突变较少见，将一个碱基对替换为不正确的碱基对或删除该gene附近的DNA片段。（换行）导致DFNB1的GJB2gene突变被描述为“功能丧失”，因为它们导致连接蛋白26的改变或无功能版本，这似乎破坏了间隙连接的组装或功能。在内耳中，异常或缺失的间隙连接可能会改变钾离子的水平，这可能会影响听力所需细胞的功能和存活。(换行)DFNA3以常染色体显性遗传，这意味着每个细胞中只有一个GJB2gene的突变拷贝足以引起这种情况。这种形式的听力损失可以是舌前的，也可以是孩子学会说话后开始的（舌后的）。听力损失的范围从轻度到严重，随着时间的推移变得更加严重，尤其影响听到高频声音的能力。（换行）导致DFNA3的GJB2gene突变将连接蛋白26中的一个氨基酸替换为错误的氨基酸。这些突变被描述为“显性负性”，这意味着它们导致连接蛋白26的异常形式，从而阻止了任何功能性间隙连接的形成。这些通道的缺失可能会影响内耳中对听力至关重要的细胞的功能和存活。"}, {"name": "掌plant角化病伴耳聋", "detail": "在患有耳聋的掌plant角化病患者中，至少鉴定出9种GJB2gene突变，这种疾病的特征是听力下降以及手掌和脚底皮肤异常厚。导致这种情况的GJB2gene突变会改变连接蛋白26中的单个氨基酸。改变的蛋白质可能破坏细胞中正常连接蛋白26的功能，并可能干扰其他连接蛋白的功能。这种破坏可能会影响皮肤的生长，并通过干扰声波向神经冲动的转换而损害听力。"}, {"name": "Vohwinkel综合征", "detail": "在患有Vohwinkel综合征的人中至少鉴定出三个GJB2gene突变，该病的特征是听力下降和皮肤异常。除了异常的皮肤斑块外，受影响的人的手指和脚趾周围还会出现异常纤维组织的紧密带，这可能会切断手指的循环并导致自发截肢。导致Vohwinkel综合征的GJB2gene突变改变了连接蛋白26中的单个氨基酸。改变的蛋白质可能破坏细胞中正常连接蛋白26的功能，并可能干扰其他连接蛋白的功能。这些异常可能会影响皮肤的生长，并通过干扰声波向神经冲动的转换而损害听力。"}]}' WHERE id = 847;
UPDATE genes SET GHR_metadata_ch = '{"gene": "GJB6", "conditions": [{"name": "克劳斯顿综合征", "detail": "已经在患有称为Clouston综合征的皮肤病的人中鉴定出至少四个GJB6gene突变，其也被称为汗性外胚层发育不良2。Clouston综合征的特征包括指甲异常，脱发以及手掌和脚底皮肤增厚。导致Clouston综合征的GJB6gene突变改变了连接蛋白30蛋白中的单个蛋白结构单元（氨基酸）。尽管尚未完全了解这些突变的影响，但它们会导致毛囊，指甲和皮肤中细胞的生长，分裂和成熟异常。"}, {"name": "非综合征性听力损失", "detail": "研究人员在患有非综合征性听力损失的个体中发现了一些GJB6gene突变，这是与其他体征和症状无关的听力损失。该gene的突变会导致一种称为DFNA3的非综合征性听力损失。这种形式的听力损失可以在孩子学会说话（舌前）之前出现，也可以在孩子学会说话（舌后）之后开始。听力损失的范围从轻度到严重，随着时间的推移变得更加严重，尤其影响听到高频声音的能力。(换行)据报道至少有两个GJB6gene突变引起DFNA3。这些突变中的每一个都改变连接蛋白30中的单个氨基酸。突变被描述为“显性负性”，因为它们导致连接蛋白30的异常形式，似乎阻止了功能性间隙连接的形成。这些通道的缺乏可能会改变内耳中钾离子的水平，这将破坏声波向神经冲动的转换。"}]}' WHERE id = 864;
UPDATE genes SET GHR_metadata_ch = '{"gene": "VLDLR", "conditions": [{"name": "VLDLR相关的小脑发育不全", "detail": "已发现VLDLRgene中至少有六个突变会导致VLDLR相关的小脑发育不全。这些突变阻止细胞产生任何功能性VLDL受体蛋白。没有这种蛋白质，成神经细胞就无法到达需要它们的大脑部位。大脑发育的这些问题主要影响小脑，小脑是协调运动的大脑部分。与VLDLR相关的小脑发育不全的人小脑异常小且发育不全，这导致平衡和协调（共济失调）和言语障碍。大脑的其他区域也受到影响，导致智力残疾和这种情况的其他主要特征。"}]}' WHERE id = 881;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SMARCB1", "conditions": [{"name": "棺材-西里斯综合症", "detail": "已经发现SMARCB1gene中至少有五个突变引起棺材-西里斯综合征。这种情况的特征是发育迟缓，第五（小指）手指或脚趾异常以及被描述为粗糙的特征性面部特征。这些SMARCB1gene突变改变或去除了SMARCB1蛋白中的单个蛋白结构单元（氨基酸）。尽管尚不清楚这些变化如何影响SWI/SNF复合物，但研究人员建议SMARCB1gene突变会导致染色质重塑异常。此过程的干扰会改变许多gene的活性并破坏几种细胞活性，这可以解释科芬－西里斯综合症的各种体征和症状。Coffin-Siris综合征患者似乎没有增加患癌症的风险（见下文）。"}, {"name": "神经鞘瘤病", "detail": "在神经鞘瘤病患者中发现了SMARCB1gene的二十多个突变，神经鞘瘤病是一种以多种非癌性（良性）肿瘤为特征的疾病，称为神经鞘瘤，生长在神经上。这种类型的肿瘤来自施万细胞，施万细胞是通常在神经周围形成绝缘层的特化细胞。（换行）与神经鞘瘤病相关的SMARCB1gene突变导致产生改变的SMARCB1蛋白，其功能降低但未消除。改变的蛋白质不太能够控制细胞如何生长和分裂，这可以使肿瘤发展。但是，尚不清楚为什么在神经鞘瘤病患者中这些突变主要与神经鞘瘤相关，而不是与其他类型的肿瘤相关。(换行)似乎仅SMARCB1中的突变不足以触发神经鞘瘤的发展。神经鞘瘤形成可能还需要在人的一生中获得并且仅存在于某些细胞中的其他遗传变化（体细胞突变）。(换行)一些SMARCB1gene突变的人从未发生肿瘤，这是一种称为外显率降低的情况。"}, {"name": "其他疾病", "detail": "SMARCB1gene突变可导致横纹肌样瘤易感综合征(RTPS)。患有这种疾病的人发生称为横纹肌样瘤的侵袭性癌性生长的风险增加，横纹肌样瘤形成于大脑(通常称为非典型畸胎样瘤/横纹肌样瘤)和肾脏(通常称为恶性横纹肌样瘤)。这些肿瘤通常发生于婴幼儿。一些患有RTPS的儿童也会发展成神经鞘瘤。RTPS是由存在于全身细胞中的SMARCB1gene突变(称为生殖细胞系突变)引起。肿瘤的发生需要一个额外的体细胞突变来删除gene的正常拷贝。综上所述，生殖细胞系和体细胞突变导致SMARCB1蛋白缺失。(换行)SMARCB1gene两个拷贝的体细胞突变导致SMARCB1蛋白缺失，导致儿童发生非遗传性(散发性)横纹肌样瘤。生殖细胞系或体细胞SMARCB1gene突变导致横纹肌样瘤的机制尚不清楚"}]}' WHERE id = 891;
UPDATE genes SET GHR_metadata_ch = '{"gene": "HLA-DPB1", "conditions": [{"name": "肉芽肿伴多血管炎", "detail": "至少有一种HLA-DPB1gene变异与肉芽肿性多血管炎(GPA)相关。当免疫系统功能失调并攻击身体自身组织和器官(自身免疫)时，就会发生这种情况，引起影响肺部、气道和肾脏的炎症。被称为HLA-DPB1*0401的相关变异体在GPA患者中比在无GPA患者中更常见;这种变异被认为会增加患上GPA的风险。(换行)由于HLA-DPB1gene参与免疫系统，其变化可能与损害肺、肾和其他器官的自身免疫反应和炎症有关。然而，目前尚不清楚HLA-DPB1gene变异在该疾病的发生发展中所起的具体作用。很可能是环境因素引发了遗传易感人群的疾病。其他遗传因素也可能参与GPA"}, {"name": "幼年特发性关节炎", "detail": "遗传学家庭参考提供有关青少年特发性关节炎的信息。"}, {"name": "类风湿关节炎", "detail": "遗传学家庭参考提供有关类风湿关节炎的信息。"}, {"name": "其他疾病", "detail": "HLA-DPB1gene的变体与铍（一种可能有毒的金属元素）的免疫反应有关。铍暴露可发生在制造工厂和核和航空航天工业中。暴露于铍的人中约2%至10%发生铍致敏或慢性铍疾病。致敏是响应铍暴露而发生的免疫反应；致敏可导致血液中某些免疫系统细胞数量增加，但可能不会导致任何症状。在一些人中，致敏导致慢性铍病，其是以形成少量炎性细胞（肉芽肿）为特征的肺病。肺会变得疤痕僵硬，失去功能。HLA-DPB1gene的变体在69位含有蛋白质结构单元（氨基酸）谷氨酸（写为E69）增加了发生铍致敏或慢性铍疾病的风险。"}]}' WHERE id = 894;
UPDATE genes SET GHR_metadata_ch = '{"gene": "CYP2R1", "conditions": [{"name": "维生素D依赖性佝偻病", "detail": "已发现CYP2R1gene中至少有四个突变会引起维生素D依赖型1B型rick病（VDDR1B）。这种骨骼发育障碍的特征是血液中钙（低钙血症）和磷酸盐（低磷血症）含量低，从而导致容易骨折的软而弱的骨骼。这种情况的一个共同特征是腿部异常弯曲（弯曲）。（换行）导致这种情况的CYP2R1gene突变严重降低或消除了25-羟化酶的功能。结果，维生素D不会转化为活性形式，也无法控制矿物质的吸收。由此导致的肠内钙和磷酸盐吸收的减少意味着这些矿物质中沉积在发育中的骨骼中的数量较少（骨骼矿化），从而导致骨骼柔软，虚弱和VDDR1B的其他特征。低钙血症还会导致某些受影响个体的肌肉无力。"}]}' WHERE id = 899;
UPDATE genes SET GHR_metadata_ch = '{"gene": "AVP", "conditions": [{"name": "神经垂体尿崩症", "detail": "已发现AVPgene中至少有60个突变会引起神经垂体尿崩症。这些突变中的大多数会改变前激素中的单个蛋白质结构单元（氨基酸），或导致该分子异常短的形式产生。研究表明，改变后的前激素被困在产生它的细胞内，而不是被转运到垂体。随着缺陷分子的积累，它会损坏并最终杀死这些细胞。由此导致的ADH短缺会阻止肾脏按原样重新吸收水分，并且身体会产生过量的尿液。这些水平衡问题是神经垂体尿崩症的特征。"}]}' WHERE id = 944;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ACTG1", "conditions": [{"name": "Baraitser-Winter综合征", "detail": "ACTG1gene的突变与Baraitser-Winter综合征有关，Baraitser-Winter综合征是一种罕见病，会影响大脑，眼睛和其他面部特征的发育。该综合征是由于突变引起的-损害其功能的肌动蛋白。"}, {"name": "耳聋，常染色体显性遗传", "detail": "ACTG1gene的突变与非综合征性听力损失有关，非综合征性听力损失是一种以部分或全部听力损失为特征的疾病。它根据遗传模式进行分类，例如常染色体显性（DFNA），常染色体隐性（DFNB），X连锁（DFNX）ot线粒体类型。在这种情况下，听力损失是单侧或双侧的，可能在高音，高音或低音时明显，并且随着年龄的增长可能稳定或逐渐发展。"}]}' WHERE id = 946;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ACTB", "conditions": [{"name": "Baraitser-Winter综合征", "detail": "ACTBgene突变与Baraitser-Winter综合征有关，Baraitser-Winter综合征是一种罕见病，会影响大脑，眼睛和其他面部特征的发育。该综合征是由于ACTB中的单个氨基酸变化损害了-肌动蛋白。"}, {"name": "中性粒细胞功能障碍和反复感染", "detail": "在GHR中找不到"}]}' WHERE id = 948;
UPDATE genes SET GHR_metadata_ch = '{"gene": "UBE3A", "conditions": [{"name": "天使人症候群", "detail": "大脑中UBE3Agene功能的丧失可能会导致Angelman综合征的许多特征，Angelman综合征是一种复杂的遗传疾病，主要影响神经系统。这种功能丧失是由影响gene母体拷贝的染色体变化或gene突变引起的。(换行)几种不同的遗传机制可以关闭（失活）或删除UBE3Agene。大多数Angelman综合征病例（约70%）发生在含有该gene的母体15号染色体片段被删除时。在另外11%的病例中，Angelman综合征是由UBE3Agene本身的突变引起的。这些突变大多数导致产生异常短的，无功能的泛素蛋白连接酶E3A。由于遗传自父亲的gene拷贝（父亲拷贝）通常在大脑的某些区域不活跃，因此母亲拷贝的丢失会阻止任何酶在这些大脑区域产生。酶功能的缺乏可能会导致Angelman综合征的主要体征和症状。（换行）涉及包含UBE3Agene的15号染色体区域的其他异常也可能导致Angelman综合征。这些染色体变化包括遗传物质的重排（易位）或控制UBE3Agene激活的DNA区域中的缺陷。像gene内的突变一样，这些染色体变化会阻止任何功能性泛素蛋白连接酶E3A在大脑的某些部位产生。"}]}' WHERE id = 1005;
UPDATE genes SET GHR_metadata_ch = '{"gene": "VAPB", "conditions": [{"name": "肌萎缩侧索硬化症", "detail": "遗传学家庭参考提供有关肌萎缩侧索硬化的信息。"}, {"name": "脊髓性肌萎缩", "detail": "研究人员已经在成年型脊髓性肌萎缩症患者中发现了一种VAPBgene突变。这种类型的脊髓性肌萎缩症的特征是肌肉无力和异常的肌肉运动，通常始于成年早期至中期。导致这种情况的突变将VAPB蛋白（写为Pro56Ser或P56S）中第56位的氨基酸丝氨酸替换为氨基酸脯氨酸。P56S突变可在某些人中引起肌萎缩性侧索硬化（如上所述），在另一些人中引起脊髓性肌萎缩。尚不清楚相同的突变如何导致不同的conditions。(换行)异常的VAPB蛋白无法打开（激活）未折叠的蛋白反应。结果，异常蛋白质在细胞中形成团块（聚集体），导致细胞死亡。控制肌肉运动的神经细胞似乎特别容易因蛋白质聚集而死亡。这些神经细胞的逐渐丧失是脊髓性肌萎缩症的体征和症状的基础。"}]}' WHERE id = 1234;
UPDATE genes SET GHR_metadata_ch = '{"gene": "COL11A2", "conditions": [{"name": "纤维软骨形成", "detail": "COL11A2gene的突变已经在纤维软骨生成2型的人群中被确认，这是一种骨骼生长障碍，拥有属性严重骨骼异常，听力丧失，视力丧失和肺发育受损，唿吸衰竭由于狭窄的胸部在婴儿早期和儿童期观察到。突变导致异常或无功能的pro-alpha2（XI）链产生，导致XI型胶原蛋白缺陷，从而破坏骨骼形成并引起内耳变化。"}, {"name": "非综合征性听力损失", "detail": "已在非综合征性听力损失，听力损失疾病且与其他体征和症状无关的人群中鉴定出COL11A2gene突变。发现COL11A2与常染色体显性遗传相关：DFNA13和非综合征性听力损失的常染色体隐性DFNB53形式。DFNA13中的听力损失是舌后的，始于儿童期或青春期。DFNB53是儿童的舌前听力损失类型。突变导致异常或无功能的pro-alpha2（XI）链产生，导致缺陷的XI型胶原蛋白破坏内耳的结构和功能。"}, {"name": "Otospondylomegaepi发育不良", "detail": "已发现COL11A2gene突变会导致耳突性巨骨epi骨发育不良（OSMED），该疾病的特征是骨骼异常，独特的面部特征和严重的听力下降。这种情况的这些症状类似于Weissenbacher-Zweymüller综合征和一种归类为III型的Stickler综合征。这种突变导致异常短版本的pro-alpha2（XI）链产生，其不能掺入XI胶原中，从而损害正常发育骨骼和内耳。"}, {"name": "斯蒂克勒综合症", "detail": "发现COL11A2gene突变与Stickler综合征有关，Stickler综合征是一种遗传性疾病，其特征是独特的面部外观，眼睛异常，听力下降和关节问题。"}, {"name": "Weissenbacher-Zweymüller综合征", "detail": "发现COL11A2gene突变与Weissenbacher-Zweymüller综合征有关，Weissenbacher-Zweymüller综合征是一种以骨骼异常，听力下降和独特的面部特征为特征的骨骼生长障碍。这些症状与OSMED和分类为III型的Stickler综合征非常相似。"}]}' WHERE id = 1334;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SGCG", "conditions": [{"name": "肢带型肌营养不良症", "detail": "在2C型肢带型肌营养不良症患者中已鉴定出SGCGgene中约40个突变。肢带型肌营养不良症是一组相关疾病，其特征是肌肉无力和消瘦，尤其是在肩膀，臀部和四肢。由影响肌糖蛋白复合物的gene突变引起的肢带型肌营养不良症称为肌糖蛋白病。（换行）SGCGgene突变可阻止肌糖蛋白复合物形成或结合并稳定肌营养不良蛋白复合物。这些复合物的问题会降低肌纤维的强度和弹性，并导致肢带型肌营养不良症的体征和症状。"}]}' WHERE id = 1360;
UPDATE genes SET GHR_metadata_ch = '{"gene": "MYO5B", "conditions": [{"name": "微绒毛包涵体病", "detail": "已发现MYO5Bgene中的40多个突变会引起微绒毛包涵体疾病。这种情况的特征是从婴儿期开始的慢性，威胁生命的腹泻。导致这种情况的MYO5Bgene突变导致肌球蛋白Vb功能降低或缺失。在排列小肠（肠上皮细胞）的细胞中，肌球蛋白Vb功能的缺乏会改变细胞极性。因此，肠上皮细胞不能正确地形成称为微绒毛的结构，微绒毛通常从细胞表面像小指一样突出，并在食物通过肠道时吸收食物中的营养物质和液体。在受影响的肠细胞内部，小块异常微绒毛与错位的消化蛋白混合形成微绒毛内含物，这导致肠细胞功能障碍。微绒毛形成不良的紊乱肠上皮细胞降低了肠道摄取营养和液体的能力。消化过程中无法吸收营养物质和液体导致微绒毛包涵体疾病患者严重腹泻，营养不良和脱水。"}]}' WHERE id = 1400;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SOD1", "conditions": [{"name": "肌萎缩侧索硬化症", "detail": "已发现SOD1gene中至少有200个突变会引起肌萎缩性侧索硬化症（ALS），这种疾病的特征是进行性肌肉无力，肌肉量减少和无法控制运动。这些突变大多数会改变超氧化物歧化酶中的一种蛋白质结构单元（氨基酸）。由SOD1gene突变引起的ALS美国人中，约有一半具有特定的突变，该突变将酶中第5位的氨基酸缬氨酸替换为氨基酸丙氨酸，称为Ala5Val或A5V。（由于蛋白质中氨基酸计数方式的变化，这种突变有时称为Ala4Val或A4V。）与其他gene突变引起的ALS相比，由A5V突变引起的ALS通常与较短的预期寿命相关。(换行)ALS是由控制肌肉运动（运动神经元）的神经细胞死亡引起的。目前尚不清楚为什么这些细胞对SOD1gene突变特别敏感。研究人员提出了几种改变的酶可能导致运动神经元死亡的方法。这些可能性包括有害超氧自由基的增加，其他类型的有毒自由基的产生增加，细胞死亡增加或可能对细胞有毒的错误折叠的超氧化物歧化酶的团块（聚集体）的积累。"}]}' WHERE id = 1408;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SHOX", "conditions": [{"name": "Langer mesomelic发育不良", "detail": "Langer粒细胞发育异常是由涉及每个细胞中SHOXgene两个拷贝的遗传变化引起的。该gene的缺失是导致这种情况的最常见变化。SHOXgene的突变也可能导致这种情况，附近通常有助于调节gene活性的遗传物质的缺失也是如此。这些变化大大减少或消除了产生的SHOX蛋白的量。缺乏这种蛋白质会破坏出生前开始的正常骨骼发育和生长。由此产生的骨骼异常包括身材矮小，手臂和腿部长骨极度缩短（mesomelia）以及腕部和前臂骨骼异常，称为Madelung畸形。"}, {"name": "Léri-Weill软骨发育不良", "detail": "Léri-Weill软骨发育不良是由涉及每个细胞中SHOXgene一个拷贝的遗传变化引起的。最常见的是，这种骨骼疾病是由SHOXgene的缺失引起的。可能导致该疾病的其他遗传变化包括SHOXgene突变或附近遗传物质的缺失，这些遗传物质通常有助于调节gene的活性。这些变化减少了产生的SHOX蛋白的量。这种蛋白质的缺乏会破坏出生前开始的正常骨骼发育和生长。产生的骨骼异常与Langer粒细胞发育异常相似，尽管它们的严重程度较轻。"}, {"name": "Mayer-Rokitansky-Küster-Hauser综合征", "detail": "遗传学家庭参考资料提供了有关Mayer-Rokitansky-Küster-Hauser综合征的信息。"}, {"name": "特纳综合症", "detail": "当一个正常的X染色体存在于女性细胞中而另一个性染色体缺失或结构改变时，就会发生特纳综合征。由于SHOXgene位于性染色体上，因此大多数特纳综合征女性在每个细胞中仅具有该gene的一个拷贝，而不是通常的两个拷贝。丢失该gene的一个拷贝会减少产生的SHOX蛋白的量。这种蛋白质的缺乏可能会导致身材矮小和骨骼异常（例如腕部和肘关节的异常旋转），这种情况在女性中很常见。"}, {"name": "其他疾病", "detail": "已经在一些身材矮小的人中发现了整个SHOXgene的缺失或该gene内或附近的突变。这种矮小的身材通常被描述为特发性的，这意味着它与疾病或综合症的特征无关。但是，已经发现一些身材矮小和SHOXgene改变的人有细微的骨骼异常。"}]}' WHERE id = 1419;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SACS", "conditions": [{"name": "Charlevoix-Saguenay的常染色体隐性痉挛性共济失调", "detail": "已发现SACSgene中约28个突变会导致Charlevoix-Saguenay的常染色体隐性痉挛性共济失调，通常称为ARSACS。在魁北克的受影响人群中经常发现两种突变。这些突变之一删除了SACSgene（写为6594delT）中6594位称为胸腺嘧啶的DNA构建块（核苷酸）。在魁北克的90%以上的ARSACS患者中发现了这种突变。另一个突变用SACSgene（写为C5254T）中5254位的核苷酸胸腺嘧啶取代了核苷酸胞嘧啶。这两个突变均导致产生异常短且无功能的囊泡蛋白。在魁北克以外的人群中引起ARSACS的突变是多种多样的，通常是该人或家庭所独有的。这些突变中的大多数要么删除一个或多个核苷酸，要么用SACSgene中的另一个核苷酸替换一个核苷酸。SACSgene中的突变会导致产生不稳定的囊蛋白，该蛋白不能正常运行。目前尚不清楚异常的囊泡蛋白如何影响大脑和骨骼肌并导致ARSACS的体征和症状。"}]}' WHERE id = 1428;
UPDATE genes SET GHR_metadata_ch = '{"gene": "C2", "conditions": [{"name": "补充成分2缺陷", "detail": "已发现C2gene中至少有五个突变导致补体成分2缺乏。这种疾病降低了免疫系统的正常功能，导致感染和自身免疫性疾病如系统性红斑狼疮（SLE）的风险增加。当免疫系统发生故障并攻击身体的组织和器官时，就会发生自身免疫性疾病。(换行)超过90%的补体成分2缺乏症患者的突变会从C2gene中删除28个DNA构建块（核苷酸）。这种突变阻止了任何补体成分2蛋白的产生。没有这种蛋白质形成C3转化酶，补体系统的激活就会停滞。结果，补体系统抵抗感染的能力下降。尚不清楚补体成分2缺乏如何导致对自身免疫性疾病的易感性增加。研究人员推测，功能失调的补体系统无法区分应该攻击什么，有时会攻击正常组织，导致自身免疫。或者，功能失调的补体系统可能会对入侵分子进行部分攻击，从而留下难以与人体组织区分开的外来片段，因此补体系统有时会攻击人体自身的细胞。补体成分2缺乏症的体征和症状的变异性可能与遗传和环境因素有关。"}, {"name": "年龄相关性黄斑变性", "detail": "遗传学家庭参考提供有关年龄相关性黄斑变性的信息。"}]}' WHERE id = 1449;
UPDATE genes SET GHR_metadata_ch = '{"gene": "HDAC8", "conditions": [{"name": "Cornelia de Lange综合征", "detail": "在患有Cornelia de Lange综合征（一种影响身体许多部位的发育障碍）的人中，已经鉴定出HDAC8gene中至少28个突变。研究人员估计，该gene的突变约占该病所有病例的5%。(换行)大多数HDAC8gene突变改变组蛋白脱乙酰酶8中的单个蛋白质构建块（氨基酸）或添加或删除酶中的少量氨基酸。所有这些突变似乎都降低或消除了酶的活性，这可能会改变粘着蛋白复合物的活性，并削弱其调节对正常发育至关重要的gene的能力。尽管研究人员尚不完全了解这些变化如何引起Cornelia de Lange综合征，但他们怀疑gene调控的改变可能是该病特征性的许多发育问题的基础。(换行)研究表明，HDAC8gene的突变引起的体征和症状模式与NIPBLgene的突变相关，后者是Cornelia de Lange综合征的最常见已知原因。与具有NIPBLgene突变的个体相比，具有HDAC8gene突变的受影响个体通常具有较轻的生长问题，较少的手臂和手部异常以及不同的特征性面部特征。他们更有可能延迟婴儿期头部（前font门）“软点”的闭合，眼睛间距大和牙齿异常。像具有NIPBLgene突变的受影响个体一样，具有HDAC8gene突变的个体可能具有严重的智力障碍。"}, {"name": "其他疾病", "detail": "在一个荷兰大家庭中，还发现了HDAC8gene的突变，其形式为X连锁智力障碍。（X连锁是指HDAC8gene位于X染色体上，这是两个性染色体之一。）受影响男性的体征和症状包括严重智力残疾，身材矮小，肥胖，乳房增大（男子女性型乳房），减少性激素产生（性腺机能减退），异常小头，小手和独特的面部特征。受影响的女性往往没有那么严重的体征和症状，包括学习障碍和异常的面部特征。（换行）鉴定的突变导致缺少片段的组蛋白脱乙酰酶8的版本。异常短的蛋白质可能会在发育过程中改变gene调控，从而导致该家族中描述的各种健康问题。(换行)这个家庭的状况被描述为威尔逊-特纳综合症，这是X连锁智力障碍的一种形式。但是，研究人员推测，它实际上可能与Cornelia de Lange综合征（如上所述）属于同一疾病谱。"}]}' WHERE id = 1464;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SPECC1L", "conditions": [{"name": "Opitz G/BBB综合征", "detail": "已发现SPECC1Lgene中至少有两个突变引起Opitz G/BBB综合征。这种情况会导致身体中线出现一些异常，包括眼睛间距大（眼部玻璃体肥大），可能导致呼吸或吞咽困难的喉咙畸形，脑畸形，明显的面部特征以及男性生殖器异常。导致Opitz G/BBB综合征的SPECC1Lgene突变会改变cytospin-A蛋白中的单个蛋白结构单元（氨基酸），从而降低蛋白与细胞骨架成分相互作用的能力。结果，微管紊乱，细胞难以迁移到适当的位置。因为SPECC1Lgene在面部发育中起作用，这个gene的突变可能解释了在一些患有Opitz G/BBB综合征的个体中，有或没有开口的唇部（唇裂）开口。但是，尚不清楚SPECC1Lgene突变如何引起该疾病的其他特征。"}, {"name": "其他疾病", "detail": "还发现SPECC1Lgene中的突变会在称为斜面部裂口（也称为Tessier裂口）的面部结构中引起开口。这些裂隙类似于唇裂和pa裂，但更为严重。面部裂痕可发生在面部的一侧或两侧；它们通常涉及口腔，并且可以包括直至眼睛的组织。受影响个体经历的健康问题取决于裂隙的严重程度，但通常包括饮食和呼吸问题。（换行）引起面部斜裂的SPECC1Lgene突变会削弱cytospin-A蛋白的功能。结果，蛋白质不能与细胞骨架相互作用，并且微管紊乱。细胞骨架的这些问题损害细胞迁移，特别是聚集在一起形成面部结构的细胞迁移。cytospin-A功能的降低会损害面部发育过程中的细胞迁移，从而留下组织通常应该在的开口或裂口。"}]}' WHERE id = 1471;
UPDATE genes SET GHR_metadata_ch = '{"gene": "CYP19A1", "conditions": [{"name": "芳香酶缺陷", "detail": "已发现CYP19A1gene中的20多个突变会导致芳香化酶缺乏症。这种情况的特征是雌激素水平降低和雄激素水平升高。这些异常的激素水平导致受影响女性的性发育受损，以及患有这种疾病的男性和女性的异常骨骼生长，胰岛素抵抗以及其他体征和症状。导致芳香化酶缺乏症的CYP19A1gene突变会降低或消除芳香化酶活性。缺乏芳香化酶功能导致在出生前和整个生命中无法将雄激素转化为雌激素。结果，雌激素产生减少，包括睾酮在内的雄激素水平增加。在怀孕胎儿受影响的妇女中，胎盘中过量的雄激素进入妇女的血液，并可能导致她有暂时的芳香化酶缺乏症状和体征。"}, {"name": "芳香化酶过剩综合征", "detail": "已经发现涉及CYP19A1gene的遗传物质的10多种重排引起芳香化酶过量综合征。这种情况的特征是雄激素向雌激素的转化增加。结果，受影响的男性乳房增大（男性乳房发育）和身材矮小；受影响的女性可能有月经不调和身材矮小。（换行）涉及CYP19A1gene的几种类型的遗传重排可引起芳香化酶过量综合征。一些引起芳香化酶过量综合征的gene重排复制了CYP19A1gene的一部分，使用于制备该酶的一些说明加倍。结果，产生比正常更多的酶。其他称为缺失的遗传重排可去除（删除）CYP19A1gene的一部分和附近的gene。然后将剩余的DNA融合在一起，产生由两个不同gene的部分组成的融合gene。这些融合gene始终包含CYP19A1gene的一部分，但可能涉及其他几个gene之一。作为这些融合gene的结果，CYP19A1gene在通常不表达的组织中是活性的（表达），因此产生比正常更多的芳香化酶。当DNA在两个位置被破坏并且所得的DNA片段被逆转并重新插入染色体时，发生另一种称为倒位的重排。涉及CYP19A1gene和附近gene的倒位也导致融合gene的产生。这些融合gene导致芳香化酶产生增加。（换行）由CYP19A1gene重排引起的芳香化酶产生的增加导致雌激素产生增加，这导致芳香化酶过量综合征的体征和症状。"}, {"name": "乳腺癌", "detail": "遗传学家庭参考提供有关乳腺癌的信息。"}]}' WHERE id = 1479;
UPDATE genes SET GHR_metadata_ch = '{"gene": "JAK2", "conditions": [{"name": "克罗恩病", "detail": "遗传学家庭参考提供有关克罗恩病的信息。"}, {"name": "原发性血小板增多症", "detail": "一些gene突变是在一个人的一生中获得的，并且仅存在于某些细胞中。这些被称为体细胞突变的变化不是遗传的。JAK2gene的体细胞突变与原发性血小板增多症有关，后者是一种以血小板数量增加为特征的疾病，血小板碎片参与正常的凝血。最常见的突变（写为Val617Phe或V617F）用蛋白质中617位的氨基酸苯丙氨酸取代了蛋白质结构单元（氨基酸）缬氨酸。在大约一半的原发性血小板增多症患者中发现了这种特殊的突变。少数受影响的个体在称为外显子12的JAK2gene的另一部分中具有体细胞突变。（换行）V617F JAK2gene突变导致JAK2蛋白的产生，该蛋白不断开启（组成性激活），其在原发性血小板增多症中导致称为巨核细胞的异常血细胞的过量产生。因为血小板是由巨核细胞形成的，所以巨核细胞的过量产生导致血小板数量增加。过量的血小板可引起异常凝血（血栓形成），导致原发性血小板增多症的许多体征和症状。"}, {"name": "真性红细胞增多症", "detail": "JAK2gene的体细胞突变与真性红细胞增多症有关，真性红细胞增多症是一种以血细胞生成不受控制为特征的疾病。在大约96%的真性红细胞增多症患者中发现了V617F突变。大约3%的受影响个体在JAK2gene的外显子12区域具有体细胞突变。（换行）JAK2gene突变导致组成型活化的JAK2蛋白的产生，这似乎改善了细胞的存活并增加了血细胞的产生。由于血液中有如此多的额外细胞，更容易形成异常的血凝块。此外，较厚的血液在整个身体中流动得更慢，这阻止了器官接受足够的氧气。真性红细胞增多症的许多体征和症状与身体组织缺氧有关。"}, {"name": "原发性骨髓纤维化", "detail": "体细胞JAK2gene突变也与原发性骨髓纤维化有关，原发性骨髓纤维化是骨髓被瘢痕组织（纤维化）取代的病症。在大约一半的原发性骨髓纤维化患者中发现了V617F突变。少数患有这种情况的人在该gene的外显子12区域具有突变。这些JAK2gene突变导致组成型活性JAK2蛋白，从而导致异常巨核细胞的过量生产。这些巨核细胞刺激其他细胞释放胶原蛋白，胶原蛋白通常为骨髓中的细胞提供结构支持，但在原发性骨髓纤维化中引起瘢痕组织形成。由于纤维化，骨髓不能产生足够的正常血细胞，导致病情的体征和症状。"}, {"name": "其他疾病", "detail": "体细胞JAK2gene突变也与几种相关病症有关。V617F突变偶尔在患有造血细胞癌（白血病）或其他骨髓疾病的人中发现。当由潜在的骨髓疾病引起时，由肝脏静脉阻塞引起的Budd-Chiari综合征也可能与V617F突变有关。尚不清楚一种特定的突变如何与几种情况相关联。"}]}' WHERE id = 1487;
UPDATE genes SET GHR_metadata_ch = '{"gene": "UPB1", "conditions": [{"name": "β-脲基丙酸酶缺乏症", "detail": "在β-脲基丙酸酶缺乏症患者中至少鉴定出16个UPB1gene突变。这种疾病导致尿液中释放出过量的N-氨基甲酰基-β-氨基异丁酸和N-氨基甲酰基-β-丙氨酸。受影响的个体也可能有各种神经系统问题，例如癫痫发作和智力残疾，范围从轻度到重度。一些患有β-脲基丙酸酯酶缺乏症的人没有神经系统症状，只有通过实验室检查才能诊断出该疾病。（换行）导致β-脲基丙酸酶缺乏的突变降低或消除β-脲基丙酸酶活性。这种酶功能的丧失减少了β-氨基异丁酸和β-丙氨酸的产生，并导致其前体分子N-氨基甲酰基-β-氨基异丁酸和N-氨基甲酰基-β-丙氨酸过量，这些分子在尿液中释放。β-氨基异丁酸和β-丙氨酸的产量减少可能会损害其在神经系统中的功能，从而导致某些β-脲基丙酸酶缺乏症患者出现神经系统问题。由特定UPB1gene突变引起的酶活性降低的程度，以及其他遗传和环境因素，可能决定患有β-脲基丙酸酶缺乏症的人是否会出现神经系统疾病以及这些问题的严重性。"}]}' WHERE id = 1549;
UPDATE genes SET GHR_metadata_ch = '{"gene": "QDPR", "conditions": [{"name": "四氢生物蝶呤缺乏症", "detail": "已发现QDPRgene中的30多个突变会导致四氢生物蝶呤缺乏。当这种情况由QDPRgene突变引起时，称为二氢蝶啶还原酶（DHPR）缺乏症。DHPR缺乏症约占所有四氢生物蝶呤缺乏症病例的三分之一。(换行)大多数QDPRgene突变改变醌类二氢蝶啶还原酶中的单个氨基酸，尽管一些突变将少量DNA插入QDPRgene或破坏gene说明用于制备酶的方式。醌类二氢蝶啶还原酶的变化大大降低或消除了酶的活性。没有足够的这种酶，四氢生物蝶呤就无法正确回收利用。结果，该辅因子不能参与化学反应，例如苯丙氨酸向酪氨酸的转化。如果苯丙氨酸不转化为酪氨酸，它可以在血液和其他组织中积累到毒性水平。大脑中的神经细胞对苯丙氨酸水平特别敏感，这就是为什么过量的这种物质会导致脑损伤的原因。(换行)另外，醌类二氢蝶啶还原酶活性的降低破坏了大脑中某些神经递质的产生。由于神经递质对于正常的大脑功能是必需的，因此这些大脑化学物质水平的变化会导致DHPR缺乏症患者的智力残疾。"}]}' WHERE id = 1556;
UPDATE genes SET GHR_metadata_ch = '{"gene": "MRAP", "conditions": [{"name": "家族性糖皮质激素缺乏症", "detail": "已发现MRAPgene中至少有13个突变会导致家族性糖皮质激素缺乏。这种情况的特征是可能危及生命的低血糖（低血糖），反复感染和肤色比其他家庭成员深（色素沉着过度）。MRAPgene突变约占这种情况的20%。这些突变中的大多数导致产生不能与ACTH受体相互作用的蛋白质，因此不能将其从ER转运到细胞膜。结果，ACTH受体不在需要与ACTH结合的细胞表面。没有ACTH受体与其激素的结合，没有信号触发肾上腺产生糖皮质激素。这些激素的缺乏会损害血糖调节，免疫系统功能和其他细胞功能，从而导致家族性糖皮质激素缺乏症的体征和症状。"}]}' WHERE id = 1567;
UPDATE genes SET GHR_metadata_ch = '{"gene": "LMX1B", "conditions": [{"name": "指甲-髌骨综合征", "detail": "已发现LMX1Bgene中至少有145个突变引起指甲pat骨综合征。大多数突变会导致LMX1B蛋白异常短而无功能的版本产生，或改变单个蛋白的结构单元（氨基酸）。将一个氨基酸替换为另一个氨基酸的突变会降低或消除蛋白质与DNA结合的能力，从而在早期发育过程中破坏其他gene的调控。整个LMX1Bgene或该gene的大部分缺失也已显示出引起指甲骨综合征。目前尚不清楚LMX1Bgene的突变如何导致指甲pat骨综合征的体征和症状。"}]}' WHERE id = 1627;
UPDATE genes SET GHR_metadata_ch = '{"gene": "FOXL2", "conditions": [{"name": "睑缘下垂，上睑下垂和上can逆综合征", "detail": "已发现FOXL2gene中的260多个突变会导致睑缘下垂，上睑下垂和上can逆综合征（BPES）。有两种类型的BPE；I型和II型都涉及眼睑异常，阻止它们完全打开。I型还包括女性早期卵巢功能丧失（原发性卵巢功能不全），这可能导致难以怀孕。（换行）很难预测由许多FOXL2gene突变引起的BPE类型。然而，导致FOXL2蛋白功能部分丧失的突变通常会导致BPES II型。这些突变可能会损害眼睑肌肉正常发育的调节，导致眼睑畸形无法完全打开。BPES II型患者的常见突变会在FOXL2蛋白的poly（A）道中添加额外的丙氨酸。导致FOXL2蛋白功能完全丧失的（换行）突变通常导致BPES I型。这些突变损害正常眼睑发育的调节和卵巢中的各种活性。这些变化导致眼睑畸形和卵巢颗粒细胞异常加速成熟以及卵细胞过早死亡。（换行）导致BPE的5%的突变发生在通常控制gene活性的相邻DNA区域中的FOXL2gene外，称为调节区域。引起BPES的突变中约有12%是涉及FOXL2gene的缺失。缺失的大小从单个DNA构建块（碱基对）到整个gene不等。一些患有BPE的人有大量的DNA缺失，不仅可以去除FOXL2gene，还可以去除一个或多个相邻gene。具有这些大的DNA缺失的个体具有BPES的体征和症状，但它们也可以具有其他特征。其他特征的组合取决于缺失中包含哪些gene，但可能包括异常小头（小头畸形），智力残疾，心脏缺陷和生长延迟。"}, {"name": "癌症", "detail": "一些gene突变是在一个人的一生中获得的，并且仅存在于某些细胞中。这些被称为体细胞突变的变化不是遗传的。在一种称为成人颗粒细胞瘤的成年期卵巢癌中发现了FOXL2gene的特定体细胞突变。此突变将FOXL2蛋白（写为Cys134Trp或C134W）中第134位的氨基酸色氨酸替换为氨基酸半胱氨酸。人们认为这种突变会干扰颗粒细胞增殖的调节并改变蛋白质在细胞凋亡中的作用。结果颗粒细胞生长和分裂不受调节，导致肿瘤形成。"}, {"name": "其他疾病", "detail": "FOXL2gene中至少有三个突变被认为会导致原发性卵巢功能不全，而BPES没有任何其他特征。原发性卵巢功能不全导致女性月经期变得不那么频繁，并最终在40岁之前停止。患有这种疾病的妇女可能难以怀孕（不孕）或完全无法怀孕（不孕）。（换行）导致原发性卵巢功能不全的FOXL2gene突变导致蛋白质功能降低，阻止FOXL2蛋白质控制调节卵巢中各种活性的gene。由此产生的异常gene活性加速了颗粒细胞的成熟并导致卵细胞过早死亡。这种改变的功能似乎影响蛋白质对卵巢活动的调节，但不影响眼睑。"}]}' WHERE id = 1760;
UPDATE genes SET GHR_metadata_ch = '{"gene": "OCA2", "conditions": [{"name": "眼皮肤白化病", "detail": "在2型眼皮肤白化病患者中已鉴定出OCA2gene的80多个突变。患有这种白化病的人通常有浅黄色，金色或浅棕色的头发；乳白色皮肤；浅色的眼睛；和视力问题。最常见的OCA2突变是该gene的大量缺失，这在撒哈拉以南非洲遗产的许多受影响个体中都发现。其他OCA2gene突变，包括单个DNA构建模块（碱基对）的变化和小的缺失，在其他人群中更为常见。OCA2gene的突变会破坏黑色素的正常产生，从而减少头发，皮肤和眼睛的着色并影响视力。"}, {"name": "天使人症候群", "detail": "OCA2gene位于15号染色体的一个区域，该区域通常在患有Angelman综合征的个体中缺失。该gene的丢失不会引起Angelman综合征的特征性神经系统特征；但是，患有这种情况的人缺少OCA2gene的一个副本，往往会有异常浅色的头发和白皙的皮肤。与具有该gene的两个功能拷贝的细胞相比，仅具有一个OCA2gene拷贝的细胞产生的P蛋白量减少，这会影响头发和皮肤的颜色。(换行)一小部分Angelman综合征患者也有2型眼皮肤白化病。当人们在每个细胞中都有两个OCA2gene的非功能性拷贝时，就会发生这种情况。除了删除15号染色体上的一个拷贝的OCA2gene外，这些个体在15号染色体的另一个拷贝上的OCA2gene中也有突变。结果，细胞产生很少或没有功能性P蛋白。缺乏P蛋白会破坏黑色素的产生，从而导致白化病的特征。"}, {"name": "普拉德-威利综合症", "detail": "在患有Prader-Willi综合征的个体中，通常会删除包含OCA2gene的15号染色体区域。该gene的丢失不会导致智力残疾和Prader-Willi综合征的其他特征；但是，患有这种情况的人缺少OCA2gene的一个副本，往往会有异常浅色的头发和白皙的皮肤。缺少OCA2gene拷贝的细胞比具有该gene两个功能拷贝的细胞产生更少的P蛋白，这会影响头发和皮肤的颜色。(换行)2型眼皮肤白化病也发生在少数Prader-Willi综合征患者中。当人们在每个细胞中都有两个OCA2gene的非功能性拷贝时，就会发生这种情况。除了删除15号染色体上的一个拷贝的OCA2gene外，这些个体在15号染色体的另一个拷贝上的OCA2gene中也有突变。结果，细胞产生很少或没有功能性P蛋白。缺乏P蛋白会破坏黑色素的产生，从而导致白化病的特征。"}]}' WHERE id = 2015;
UPDATE genes SET GHR_metadata_ch = '{"gene": "MMACHC", "conditions": [{"name": "甲基丙二酸血症伴高半胱氨酸尿症", "detail": "已发现数十种MMACHCgene突变会导致甲基丙二酸血症，并伴有高半胱氨酸尿症，cblC型，这是导致发育迟缓，眼缺陷，神经系统问题和血液异常的最常见形式的疾病。这种情况下最常见的突变称为271dupA，在gene的271位插入了一个额外的DNA构建块（核苷酸）。这种变化改变了蛋白质的蓝图，导致产生了无法发挥功能的异常短的蛋白质。其他MMACHCgene突变也导致功能受损的蛋白质的产生。功能性MMACHC蛋白的缺乏会阻止维生素B12的正常加工和运输，从而损害AdoCbl和MeCbl的生产。由于这两个辅因子均缺失，因此需要它们的酶（甲基丙二酰辅酶A突变酶和蛋氨酸合酶）无法正常运行。结果，某些氨基酸，脂质和胆固醇不被分解，同型半胱氨酸不能转化为甲硫氨酸。这种双重缺陷导致有毒化合物以及高半胱氨酸的积累，并减少体内蛋氨酸的产生。这种失衡的组合导致甲基丙二酸血症伴高半胱氨酸尿症的体征和症状。"}]}' WHERE id = 2059;
UPDATE genes SET GHR_metadata_ch = '{"gene": "RECQL4", "conditions": [{"name": "Baller-Gerold综合征", "detail": "在患有Baller-Gerold综合征的人中已经鉴定出RECQL4gene的几个突变。这些突变中的大多数会阻止任何RECQL4蛋白的产生或改变蛋白拼凑在一起的方式，从而破坏其正常功能。这种蛋白质的短缺可能会阻止正常的DNA复制和修复，从而随着时间的推移对一个人的遗传信息造成广泛的损害。目前尚不清楚这些变化如何导致Baller-Gerold综合征的各种体征和症状，包括某些颅骨的异常融合（颅缝早闭），身材矮小，前臂拇指或骨骼缺失（放射线畸形）和皮疹。"}, {"name": "RAPADILINO综合征", "detail": "在RAPADILINO综合征患者中，RECQL4gene至少鉴定出10个突变。这种情况具有许多特征，包括radial骨射线畸形，膝关节畸形或缺失，腹泻和身材矮小。该病首先在芬兰发现，RAPADILINO综合征最常见的突变是在所有受影响的芬兰血统个体以及其他人群中发现的。这种突变被称为IVS7+2delT，被称为剪接位点突变，它导致RECQL4蛋白被错误地拼接在一起。这种遗传变化导致缺少称为外显子7的区域的蛋白质的产生。改变的蛋白质不具有解旋酶活性，这可能会阻止正常的DNA复制和修复。尽管尚不清楚RECQL4gene突变如何导致RAPADILINO综合征的特定特征，但这些变化可能导致DNA错误的积累和细胞死亡。"}, {"name": "Rothmund-Thomson综合征", "detail": "在Rothmund-Thomson综合征患者中发现了RECQL4gene的40多个突变。这些突变可能会阻止任何RECQL4蛋白的产生，或导致该蛋白异常短而无功能的形式的产生。这种蛋白质的短缺可能会阻止正常的DNA复制和修复，从而随着时间的推移对一个人的遗传信息造成广泛的损害。需要进一步的研究来确定这些变化如何导致Rothmund-Thomson综合征的特征，包括皮疹，头发稀疏，身材矮小，骨骼异常以及某些癌症的风险增加。(换行)由于Rothmund-Thomson综合征，Baller-Gerold综合征和RAPADILINO综合征具有重叠特征并且可能由同一gene的突变引起，因此研究人员正在研究它们是单独的疾病还是具有重叠体征的单一综合征的一部分。症状。"}]}' WHERE id = 2151;
UPDATE genes SET GHR_metadata_ch = '{"gene": "HRAS", "conditions": [{"name": "先天性水痘症候群", "detail": "在患有Costello综合征的人中已经鉴定出HRASgene中的至少15个突变，Costello综合征是一种罕见病，会影响身体的许多部位并增加患癌性和非癌性肿瘤的风险。突变改变了H-Ras蛋白关键区域中的单个蛋白结构单元（氨基酸）。最常见的突变占所有Costello综合征病例的80%以上；它用蛋白质第12位的氨基酸丝氨酸取代了氨基酸甘氨酸（写为Gly12Ser或G12S）。（换行）导致Costello综合征的HRASgene突变导致H-Ras蛋白的产生，该蛋白在全身细胞中异常打开（激活）。过度活跃的蛋白质不是响应来自细胞外部的信号而触发细胞生长，而是指导细胞不断生长和分裂。这种不受控制的细胞分裂可导致非癌性和癌性肿瘤的形成。研究人员不确定HRASgene的突变如何导致Costello综合征的其他特征（例如智力残疾，独特的面部特征和心脏问题），但是许多体征和症状可能是由于细胞过度生长和异常细胞分裂引起的。"}, {"name": "表皮痣", "detail": "HRASgene中的突变与称为表皮痣（单数：痣）的异常，非癌性皮肤斑块的发展有关。这些斑块是由皮肤外层（表皮）中的细胞过度生长引起的。在大多数患有某种类型的表皮痣（称为皮脂腺）的人中发现了HRASgene突变。这种类型被归类为类器官表皮痣，因为它涉及构成皮肤结构（或器官）的细胞，通常是毛囊，汗腺或皮脂腺（皮肤中的腺体，产生保护皮肤和头发）。其他肿瘤通常在皮脂腺痣区域发展。在极少数情况下，这些肿瘤是癌性的。HRASgene突变在角质形成性表皮痣中较少见，角质形成性表皮痣是一种表皮痣，涉及称为角质形成细胞的特定类型的表皮细胞。角质形成表皮痣通常不与其他肿瘤相关。（换行）表皮痣是由出生前发育早期获得的gene突变引起的。突变仅存在于痣的细胞中，而不存在于其周围的正常皮肤细胞中。这些称为体细胞突变的变化不是遗传的。涉及表皮痣的体细胞HRASgene突变改变了H-Ras蛋白中的单个氨基酸。最常见的突变是在蛋白质位置12（写为Gly12Val或G12V）上用氨基酸缬氨酸替换了氨基酸甘氨酸。这些突变导致总是打开的H-Ras蛋白的产生。受影响的皮肤细胞比正常细胞生长和分裂更多，导致表皮痣。"}, {"name": "膀胱癌", "detail": "膀胱细胞中发生的体细胞HRASgene突变与某些膀胱癌病例有关。已经在显着百分比的膀胱肿瘤中鉴定了特定的突变，即可引起表皮痣的Gly12Val突变（如上所述）。由于这种遗传变化，改变的H-Ras蛋白在细胞内变得持续活跃。过度活跃的H-Ras蛋白指导细胞异常生长和分裂，导致不受控制的细胞分裂和肿瘤的形成。HRASgene的突变也与膀胱癌的进展和治疗后肿瘤复发的风险增加有关。"}, {"name": "头颈部鳞状细胞癌", "detail": "遗传学家庭参考提供有关头颈部鳞状细胞癌的信息。"}, {"name": "其他疾病", "detail": "体细胞HRASgene突变也参与Schimmelpenning综合征的发展，Schimmelpenning综合征是一种表皮痣综合征。受影响的个体除了大脑，眼睛或骨骼的异常外，还患有一种称为痣皮脂腺的表皮痣（如上所述）。这些其他系统的问题可能包括癫痫发作，智力障碍，眼结构中多余或缺失的组织碎片（绒毛膜瘤或淋巴瘤），骨骼发育不全以及一种称为病的疾病，导致骨骼软化和变弱。Schimmelpenning综合征是由表皮痣中涉及的相同gene突变引起的。据认为，由于体细胞突变会影响皮肤以外的其他组织，因此会出现其他体征和症状。"}, {"name": "其他癌症", "detail": "HRASgene中的体细胞突变可能与几种其他类型癌症的发展有关。这些突变导致H-Ras蛋白的一个版本，它总是活跃的，可以指导细胞在没有控制的情况下生长和分裂。研究表明，HRASgene突变可能在甲状腺癌和肾癌中很常见。在其他类型的癌症中也已经报道了HRASgene的活性（表达）增加。"}]}' WHERE id = 2153;
UPDATE genes SET GHR_metadata_ch = '{"gene": "MT-ND1", "conditions": [{"name": "莱伯遗传性视神经病变", "detail": "已知MT-ND1gene中的几个突变会引起Leber遗传性视神经病变。这些突变中的每一个都会改变gene中的单个DNA构建块（核苷酸）。一种常见的MT-ND1突变约占所有Leber遗传性视神经病变病例的13%。该突变用gene位置3460处的核苷酸腺嘌呤取代核苷酸鸟嘌呤（写为G3460A）。这种变化与Leber遗传性视神经病变的中度严重病例有关；但是，由于这种突变而导致视力丧失的人中有20%至40%会恢复一些视力。(换行)研究人员正在研究MT-ND1gene突变如何导致Leber遗传性视神经病变。这些遗传变化似乎破坏了线粒体内膜中复合物I的正常活性，这可能会影响ATP的产生。MT-ND1突变也可能增加线粒体内潜在有害分子（称为活性氧）的产生。然而，仍然不清楚为什么这些突变的影响通常局限于将视觉信息从眼睛传递到大脑（视神经）的神经。其他遗传和环境因素可能导致视力丧失和与Leber遗传性视神经病变相关的其他医学问题。"}, {"name": "线粒体脑病，乳酸性酸中毒和中风样发作", "detail": "MT-ND1突变是线粒体脑病，乳酸性酸中毒和中风样发作（MELAS）的罕见原因。大多数MELAS病例是由其他线粒体gene突变引起的，但也有少数病例是由MT-ND1gene突变引起的。在受影响的个体中，已经鉴定出少于五个突变，每个突变都会改变gene中的单个DNA结构单元（核苷酸）。这些遗传变化降低了复合物I的活性，从而破坏了线粒体内的能量产生。尽管这些异常对需要大量能量的组织（如大脑和肌肉）影响最大，但研究人员尚未确定MT-ND1gene的变化如何导致MELAS的特定体征和症状。"}, {"name": "利综合征", "detail": "遗传学家庭参考提供有关Leigh综合征的信息。"}, {"name": "线粒体复合体I缺乏", "detail": "遗传学家庭参考提供有关线粒体复合体I缺乏的信息。"}, {"name": "其他疾病", "detail": "在少数成人发作性肌张力障碍病例中，已经报道了MT-ND1gene的突变。肌张力障碍是一种运动障碍，涉及肌肉的非自愿张力（肌肉收缩），震颤和其他不受控制的运动。与这些罕见病例相关的MT-ND1突变在gene位置3796（写为A3796G）上用核苷酸鸟嘌呤取代了核苷酸腺嘌呤。需要进一步的研究来确定这种遗传变化是否与其他遗传和环境因素相结合，以增加发生成人发作性肌张力障碍的风险。"}]}' WHERE id = 2197;
UPDATE genes SET GHR_metadata_ch = '{"gene": "MT-ATP6", "conditions": [{"name": "利综合征", "detail": "在大约10%的Leigh综合征患者中发现了MT-ATP6gene的突变。Leigh综合征是一种进行性脑部疾病，通常出现在婴儿期或儿童早期。受影响的儿童可能会出现发育迟缓，肌肉无力，运动问题或呼吸困难。（换行）与Leigh综合征相关的MT-ATP6gene突变改变了MT-ATP6gene中的一个DNA构建块（核苷酸）。最常见的遗传变化是在8993位（写为T8993G）用核苷酸鸟嘌呤取代核苷酸胸腺嘧啶。导致Leigh综合征的突变会损害ATP合酶复合物的功能或稳定性，抑制ATP的产生并损害氧化磷酸化。(换行)尽管确切的机制尚不清楚，但研究人员认为，由于细胞中可用能量的减少，氧化磷酸化受损可导致细胞死亡。某些需要大量能量的组织，如大脑，肌肉和心脏，似乎对细胞能量的减少特别敏感。大脑中的细胞死亡可能会导致在Leigh综合征中看到的大脑特征性变化，从而导致疾病的体征和症状。其他敏感组织中的细胞死亡也可能有助于Leigh综合征的特征。"}, {"name": "线粒体复合体V缺乏", "detail": "遗传学家庭参考提供有关线粒体复合物V缺乏症的信息。"}, {"name": "神经病，共济失调和色素性视网膜炎", "detail": "引起Leigh综合征的某些突变也导致了类似但不太严重的疾病，称为神经病，共济失调和色素性视网膜炎（NARP）。在患有NARP的人中已经鉴定出MT-ATP6gene中的少量突变。这些突变中的每一个都改变MT-ATP6gene中的一个核苷酸。与Leigh综合征一样，与NARP相关的最常见的遗传变化是在8993位（写为T8993G）用核苷酸鸟嘌呤取代核苷酸胸腺嘧啶。导致NARP的突变改变ATP合酶的结构或功能，降低线粒体产生ATP的能力。尽管尚不清楚这些突变的确切作用，但研究人员仍在继续研究MT-ATP6gene的变化如何干扰ATP的产生并导致肌肉无力，视力丧失和NARP的其他特征。(换行)身体的大部分细胞含有数千个线粒体，每个线粒体都有一个或多个线粒体DNA拷贝。一些线粒体疾病的严重程度与每个具有特定遗传变化的细胞中线粒体的百分比有关。由于MT-ATP6gene突变而患有Leigh综合征的人倾向于具有非常高百分比的具有该突变的线粒体（从90%以上到95%）。NARP较不严重的特征是由于具有突变的线粒体百分比较低，通常为70%至90%。由于这两种情况是由相同的遗传变化引起的，并且可能发生在单个家庭的不同成员中，因此研究人员认为它们可能代表了一系列重叠特征，而不是两个不同的综合症。"}, {"name": "其他疾病", "detail": "类似于Leigh综合征（如上所述）的称为家族性双侧纹状体坏死的病症也可能由MT-ATP6gene的改变引起。在少数报告的具有这些突变的病例中，受影响的儿童发育迟缓，运动和协调问题，肌张力弱（肌张力低下）和头部异常小（小头畸形）。研究人员尚未确定为什么MT-ATP6突变会导致双侧纹状体坏死患儿出现这种体征和症状组合。"}]}' WHERE id = 2220;
UPDATE genes SET GHR_metadata_ch = '{"gene": "MT-ND4L", "conditions": [{"name": "莱伯遗传性视神经病变", "detail": "已经在几个患有Leber遗传性视神经病变的家族中鉴定出MT-ND4Lgene的突变。该突变可写为T10663C或Val65Ala，可改变NADH脱氢酶4L蛋白中的单个蛋白结构单元（氨基酸）。具体而言，它在蛋白质位置65处用氨基酸丙氨酸取代了氨基酸缬氨酸。(换行)研究人员尚未确定MT-ND4Lgene突变如何导致Leber遗传性视神经病变的视力丧失特征。这种遗传变化似乎破坏了线粒体内膜中复合物I的正常活性，这可能会影响ATP的产生。然而，目前尚不清楚为什么这种突变的影响仅限于将视觉信息从眼睛传递到大脑（视神经）的神经。其他遗传和环境因素可能有助于Leber遗传性视神经病变的特征。"}, {"name": "线粒体复合体I缺乏", "detail": "遗传学家庭参考提供有关线粒体复合体I缺乏的信息。"}]}' WHERE id = 2237;
UPDATE genes SET GHR_metadata_ch = '{"gene": "MT-ND4", "conditions": [{"name": "莱伯遗传性视神经病变", "detail": "已知MT-ND4gene中的几个突变会引起Leber遗传性视神经病变。这些突变中的每一个都会改变NADH脱氢酶4蛋白中的单个蛋白结构单元（氨基酸）。一种MT-ND4突变是Leber遗传性视神经病变的最常见原因；它负责全球所有案件的约70%。该突变可以写成G11778A或Arg340His，在蛋白质位置340处用氨基酸组氨酸取代了氨基酸精氨酸。这种突变倾向于导致严重的视力丧失，几乎没有恢复的机会。(换行)研究人员正在研究MT-ND4gene突变如何导致Leber遗传性视神经病变。这些遗传变化似乎阻止了复合物I与泛醌的正常相互作用，这可能会影响ATP的产生。MT-ND4突变也可能增加线粒体内潜在有害分子（称为活性氧）的产生。然而，仍然不清楚为什么这些突变的影响通常局限于将视觉信息从眼睛传递到大脑（视神经）的神经。其他遗传和环境因素可能导致视力丧失和与Leber遗传性视神经病变相关的其他医学问题。"}, {"name": "利综合征", "detail": "遗传学家庭参考提供有关Leigh综合征的信息。"}, {"name": "线粒体复合体I缺乏", "detail": "遗传学家庭参考提供有关线粒体复合体I缺乏的信息。"}, {"name": "其他疾病", "detail": "在少数患有Leigh综合征的人中也发现了MT-ND4gene的突变，Leigh综合征是一种进行性脑部疾病，通常出现在婴儿期或儿童早期。患病的儿童可能会出现呕吐，癫痫发作，发育迟缓，肌肉无力和运动问题。患有这种疾病的人也可能发生心脏病，肾脏问题和呼吸困难。（换行）可引起Leigh综合征的MT-ND4突变，写为C11777A或Arg340Ser，在蛋白质位置340处用氨基酸丝氨酸取代氨基酸精氨酸。这种遗传变化似乎破坏了线粒体中复合物I的正常功能。但是，尚不清楚该MT-ND4突变如何与Leigh综合征的特定特征相关。"}]}' WHERE id = 2242;
UPDATE genes SET GHR_metadata_ch = '{"gene": "MT-ND5", "conditions": [{"name": "线粒体脑病，乳酸性酸中毒和中风样发作", "detail": "MT-ND5gene的突变占线粒体脑病，乳酸性酸中毒和中风样发作（MELAS）的所有病例的一小部分。这些突变改变了gene中的单个DNA结构单元（核苷酸）。在至少五名具有MELAS特征的人中发现了特定的突变；该突变用gene位置13513处的核苷酸腺嘌呤取代核苷酸鸟嘌呤（写为G13513A）。导致MELAS的大多数MT-ND5突变已显示降低复合物I的活性，其破坏线粒体内的能量产生。尽管这些异常对需要大量能量的组织（如大脑和肌肉）影响最大，但研究人员尚未确定MT-ND5gene的变化如何导致MELAS的特定体征和症状。在具有MELAS与其他线粒体疾病组合的主要特征的患者中也鉴定了MT-ND5gene中的（换行）突变。例如，研究人员在几名具有MELAS征象和Leigh综合征某些特征的个体中发现了MT-ND5突变，Leigh综合征是一种进行性脑部疾病，通常出现在婴儿期或儿童早期。在其他情况下，患有MELAS和MT-ND5gene改变的人已经发展出突然的，进行性视力丧失，其特征是称为Leber遗传性视神经病变的眼病。据报道，有几个人患有所有这三种线粒体疾病的体征和症状？MELAS，Leigh综合征和Leber遗传性视神经病变。(换行)目前尚不清楚为什么MT-ND5gene的变化会导致如此多种症状和体征。即使在一个家庭中，受影响的个体也可能由于相同的遗传变化而导致不同的健康问题。"}, {"name": "利综合征", "detail": "遗传学家庭参考提供有关Leigh综合征的信息。"}, {"name": "线粒体复合体I缺乏", "detail": "遗传学家庭参考提供有关线粒体复合体I缺乏的信息。"}, {"name": "其他疾病", "detail": "已经在至少10名患有Leigh综合征的人中鉴定出MT-ND5gene的突变。患有这种疾病的儿童可能会出现呕吐，癫痫发作，发育迟缓，肌肉无力和运动问题。患有这种疾病的人也可能发生心脏病，肾脏问题和呼吸困难。一些患有MT-ND5突变的患儿具有Leigh综合征不常见的其他特征，包括出生前生长缓慢（子宫内发育迟缓）和独特的面部特征。（换行）负责Leigh综合征的MT-ND5突变改变了gene中的单个核苷酸。这些遗传变化破坏了复合物I的活性，削弱了线粒体产生能量的能力。但是，尚不清楚MT-ND5gene的突变如何与Leigh综合征的特定特征相关。"}]}' WHERE id = 2245;
UPDATE genes SET GHR_metadata_ch = '{"gene": "MT-ND6", "conditions": [{"name": "莱伯遗传性视神经病变", "detail": "在患有Leber遗传性视神经病变的人中已经鉴定出MT-ND6gene的几个突变。这些突变中的每一个都会改变NADH脱氢酶6蛋白中的单个蛋白结构单元（氨基酸）。一种常见的MT-ND6突变约占Leber遗传性视神经病变所有病例的14%，并且是法裔加拿大人中这种疾病的最常见原因。这种遗传变化被写为T14484C或Met64Val，在蛋白质位置64处用氨基酸缬氨酸取代了氨基酸蛋氨酸。T14484C突变与良好的长期预后相关；有这种gene改变的受影响人群有37%到65%的机会进行一些视觉恢复。(换行)研究人员正在研究MT-ND6gene突变如何导致Leber遗传性视神经病变。这些遗传变化似乎阻止了复合物I与泛醌的正常相互作用，这可能会影响ATP的产生。MT-ND4突变也可能增加线粒体内潜在有害分子（称为活性氧）的产生。然而，仍然不清楚为什么这些突变的影响通常局限于将视觉信息从眼睛传递到大脑（视神经）的神经。其他遗传和环境因素可能导致视力丧失和与Leber遗传性视神经病变相关的其他医学问题。"}, {"name": "利综合征", "detail": "遗传学家庭参考提供有关Leigh综合征的信息。"}, {"name": "线粒体复合体I缺乏", "detail": "遗传学家庭参考提供有关线粒体复合体I缺乏的信息。"}, {"name": "其他疾病", "detail": "在少数患有Leigh综合征的人中也发现了MT-ND6gene的突变，Leigh综合征是一种进行性脑部疾病，通常出现在婴儿期或儿童早期。患病的儿童可能会出现呕吐，癫痫发作，发育迟缓，肌肉无力和运动问题。患有这种疾病的人也可能发生心脏病，肾脏问题和呼吸困难。（换行）可导致Leigh综合征的MT-ND6突变，写为G14459A或Ala72Val，在蛋白质位置72处用氨基酸缬氨酸取代氨基酸丙氨酸。在患有Leber遗传性视神经病变和称为肌张力障碍的运动障碍的人中也发现了这种遗传变化，其涉及不自主的肌肉收缩，震颤和其他不受控制的运动。这种突变似乎破坏了线粒体中复合物I的正常组装或活性。但是，尚不清楚这种MT-ND6gene改变如何与Leigh综合征，Leber遗传性视神经病变或肌张力障碍的特定特征相关。还不清楚为什么单个突变会在不同的人中引起如此不同的体征和症状。"}]}' WHERE id = 2249;
UPDATE genes SET GHR_metadata_ch = '{"gene": "MT-CYB", "conditions": [{"name": "线粒体复合体III缺乏", "detail": "MT-CYBgene的突变可导致线粒体复合物III缺乏。当由该gene的突变引起时，该病症通常以肌肉无力（肌病）和疼痛为特征，特别是在运动期间（运动不耐受）。受影响更严重的人可能会遇到其他身体系统的问题，包括肝脏，肾脏，心脏和大脑。（换行）导致线粒体复合物III缺乏的大多数MT-CYBgene突变改变细胞色素b蛋白中的单个蛋白质构建块（氨基酸）或导致异常短的蛋白质。这些细胞色素b的改变会损害复合物III的形成，严重降低复合物的活性和氧化磷酸化。研究人员认为，氧化磷酸化受损可通过减少细胞中可用的能量而导致细胞死亡。据认为，需要大量能量的组织，例如肌肉，脑，心脏，肝脏和肾脏中的组织，受氧化磷酸化减少的影响最大。对这些组织和器官的损害导致线粒体复合物III缺乏的各种特征。（换行）线粒体DNA（mtDNA）中MT-CYBgene的位置可能有助于解释为什么有些人比其他人有更严重的病情特征。人体的大多数细胞含有数千个线粒体，每个线粒体都有一个或多个线粒体DNA拷贝。这些细胞可以具有含有突变和未突变DNA（异质性）的线粒体的混合物。由MT-CYBgene突变引起的线粒体复合物III缺乏的严重性被认为与具有线粒体DNA突变的线粒体的百分比有关。在大多数患有MT CYB相关线粒体复合体III缺乏症的人中，突变的线粒体DNA的百分比在骨骼肌中最高，这解释了在这些个体中发现肌病的原因。目前尚不清楚为什么该突变在肌肉组织中最普遍。"}]}' WHERE id = 2254;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SGCA", "conditions": [{"name": "肢带型肌营养不良症", "detail": "在2D型肢带型肌营养不良症患者中已鉴定出SGCAgene的70多个突变。肢带型肌营养不良症是一组相关疾病，其特征是肌肉无力和消瘦，尤其是在肩膀，臀部和四肢。由影响肌糖蛋白复合物的gene突变引起的肢带型肌营养不良症称为肌糖蛋白病。(换行)最常见的SGCAgene突变发生在约三分之一的2D型肢带型肌营养不良症患者中。此突变用α-糖聚糖蛋白（写为Arg77Cys或R77C）中第77位的半胱氨酸替换了蛋白质结构单元（氨基酸）精氨酸。其余已知的SGCAgene突变特定于单个家庭或某些人群。（换行）SGCAgene突变可阻止肌糖蛋白复合物形成或结合并稳定肌营养不良蛋白复合物。这些复合物的问题会降低肌纤维的强度和弹性，并导致肢带型肌营养不良症的体征和症状。"}]}' WHERE id = 2302;
UPDATE genes SET GHR_metadata_ch = '{"gene": "KCNQ1OT1", "conditions": [{"name": "Beckwith-Wiedemann综合征", "detail": "Beckwith-Wiedemann综合征是一种以过度生长和其他影响身体许多部位的体征和症状为特征的疾病。在这种情况下，至少有一半是由影响IC2区域的称为甲基化的过程中的变化引起的。具体而言，IC2区的母系遗传拷贝具有太少的甲基连接（低甲基化）。这种异常破坏了通常由IC2控制的几个gene的调节。IC2区域的低甲基化导致KCNQ1OT1gene的活性增加和其他附近gene的活性降低。由于这些gene中的一些参与指导生长，其活性的丧失导致过度生长和Beckwith-Wiedemann综合征的其他特征。(换行)在少数情况下，Beckwith-Wiedemann综合征是由IC2区母本遗传拷贝中的少量DNA缺失引起的。像异常甲基化一样，这些缺失破坏了几个gene的活性，包括KCNQ1OT1。"}]}' WHERE id = 2360;
UPDATE genes SET GHR_metadata_ch = '{"gene": "TSEN2", "conditions": [{"name": "桥小脑发育不全", "detail": "在患有称为桥小脑发育不全的大脑发育障碍的人中，已经鉴定出TSEN2gene中至少有四个突变。这种情况的主要特征包括发育迟缓，运动问题和智力障碍。TSEN2gene突变导致一小部分称为2型桥小脑发育不全（PCH2）的疾病。当PCH2由TSEN2gene突变产生时，有时会将其更具体地归类为PCH2B。（换行）导致PCH2B的TSEN2gene突变会损害tRNA剪接核酸内切酶复合物的功能，这可能会破坏RNA分子的加工并影响许多类型蛋白质的产生。出生前，这些变化似乎对快速生长的组织（例如大脑中的组织）产生最严重的影响。但是，尚不清楚在这种情况下，tRNA剪接核酸内切酶复合物功能降低如何导致大脑发育异常。"}]}' WHERE id = 2362;
UPDATE genes SET GHR_metadata_ch = '{"gene": "HLA-DRB1", "conditions": [{"name": "自身免疫性艾迪生病", "detail": "HLA-DRB1gene的某些变异与发展称为自身免疫性Addison病的自身免疫性疾病的风险增加有关。当免疫系统发生故障并攻击身体的组织和器官时，就会发生自身免疫性疾病。在自身免疫性艾迪生病中，免疫系统攻击肾上腺，肾上腺是位于每个肾脏顶部的小激素产生腺体。肾上腺产生的激素丧失导致疾病的特征，包括极度疲倦（疲劳），恶心，低血压（低血压）和异常黑暗的皮肤区域（色素沉着过度），特别是在经历过的区域很多摩擦，如腋窝，肘部和指关节。称为HLA-DRB1*04:04的特定HLA-DRB1gene变体是自身免疫性Addison疾病的最着名的危险因素。(换行)通常，免疫系统只对外来入侵者产生的蛋白质起反应，而不是对身体自身的蛋白质起反应。然而，在自身免疫性艾迪生病中，正常的肾上腺蛋白引发免疫反应；在约85%的自身免疫性Addison病患者中，蛋白质是21-羟化酶。在肾上腺中发现21-羟化酶，其在产生调节体内许多基本功能的多种激素中起关键作用。由21-羟化酶引发的延长的免疫攻击损害肾上腺（特别是腺体的外层，统称为肾上腺皮质），从而阻止激素的产生。肾上腺激素缺乏（肾上腺功能不全）会破坏体内的几种正常功能，导致自身免疫性艾迪生病的多种特征。尚不清楚HLA-DRB1*04:04和其他HLA-DRB1变异如何参与导致自身免疫性Addison病的不适当的免疫反应。"}, {"name": "克罗恩病", "detail": "遗传学家庭参考提供有关克罗恩病的信息。"}, {"name": "格雷夫斯病", "detail": "遗传学家庭参考提供有关格雷夫斯病的信息。"}, {"name": "桥本甲状腺炎", "detail": "遗传学家庭参考提供有关桥本甲状腺炎的信息。"}, {"name": "特发性炎症性肌病", "detail": "遗传学家庭参考提供有关特发性炎性肌病的信息。"}, {"name": "幼年特发性关节炎", "detail": "遗传学家庭参考提供有关青少年特发性关节炎的信息。"}, {"name": "多发性硬化症", "detail": "HLA-DRB1gene的变异与发生多发性硬化症的风险增加有关。这种情况会影响大脑和脊髓（中枢神经系统），导致肌肉无力，协调不良，麻木以及各种其他健康问题。该gene的一种变体称为HLA-DRB1*15:01，是多发性硬化风险最强的遗传因素。（换行）由于HLA-DRB1gene参与免疫系统，其变化可能与自身免疫反应和炎症有关，损害神经及其周围的保护性涂层（髓鞘），导致体征和多发性硬化症的症状。然而，尚不清楚HLA-DRB1gene变体在多发性硬化症的发展中起什么作用。这种情况可能涉及遗传和环境因素的组合。"}, {"name": "发作性睡病", "detail": "遗传学家庭参考提供有关发作性睡病的信息。"}, {"name": "银屑病关节炎", "detail": "遗传学家庭参考提供有关银屑病关节炎的信息。"}, {"name": "类风湿关节炎", "detail": "HLA-DRB1gene的几种常见变异与一个人患类风湿性关节炎的风险有关。该疾病引起主要影响关节的慢性异常炎症。HLA-DRB1是HLA复合物中与类风湿性关节炎相关的几种gene之一；该gene的变异是该疾病最重要的已知遗传风险因素。（换行）与类风湿性关节炎风险增加相关的HLA-DRB1gene变异影响β链中的单个蛋白质构建块（氨基酸）。这些变化发生在抗原识别结合沟附近，该结合沟是附着（结合）病毒或细菌肽的蛋白质的一部分。这种结合触发了攻击外来入侵者的免疫反应。尽管HLA-DRB1gene变异增加类风湿性关节炎风险的机制尚不清楚，但研究人员怀疑这与刺激异常免疫反应的肽结合变化有关。然而，许多其他遗传和环境因素也会导致一个人患类风湿性关节炎的整体风险。（换行）HLA-DRB1gene的一些变异似乎降低了患类风湿性关节炎的风险。目前还不清楚为什么这些特定的变化可能是保护性的。"}, {"name": "1型糖尿病", "detail": "HLA-DRB1gene和其他HLAgene变异的组合影响1型糖尿病的风险。1型糖尿病的特征在于由于激素胰岛素缺乏导致的高血糖水平，并且由胰腺中产生胰岛素的细胞的自身免疫损伤引起。（换行）1型糖尿病风险通过HLA-DRB1gene和称为HLA-DQA1和HLA-DQB1的其他HLAgene的变体的两种特定组合而最大程度地增加。HLAgene变体的组合称为HLA单倍型。一个单元型，写为DRB1*03:01-DQA1*05:01-DQB1*02，称为DR3。另一个单元型，写为DRB1*04:01/02/04/05/08-DQA1*03:01-DQB1*02，称为DR4。发生1型糖尿病风险最高的人在每个细胞中都有一份DR3单倍型和一份DR4单倍型。其他HLA单倍型仅轻度增加1型糖尿病的风险，而某些单倍型似乎可以预防这种情况的发展。其他gene和环境因素的变化也被认为会影响这种复杂疾病的风险。"}, {"name": "自身免疫性疾病", "detail": "HLA-DRB1gene的正常变异与许多其他自身免疫性疾病有关，包括天疱疮，结节病和本页列出的其他疾病。天疱疮是导致皮肤和粘膜（例如口腔潮湿衬里）严重起泡的病症。结节病是一种炎症发生在身体许多器官和组织中的疾病。(换行)目前还不清楚不同版本的HLA-DRB1gene如何影响发生自身免疫性疾病的风险。这些疾病通常是由多种环境和遗传因素共同导致的。其他HLA和非HLAgene的变化，其中一些仍然未知，也可能导致发展这些复杂病症的风险。"}]}' WHERE id = 2416;
UPDATE genes SET GHR_metadata_ch = '{"gene": "HLA-DQA1", "conditions": [{"name": "腹腔疾病", "detail": "已发现HLAgene变体（HLA单倍型）的至少两种特定组合增加发生乳糜泻的风险，乳糜泻是一种炎症损害肠道和其他器官和组织的疾病。这些单倍型之一称为DQ2，由与HLA-DQB1gene变体产生的蛋白质结合的HLA-DQA1gene变体产生的蛋白质组成，所述HLA-DQA1gene变体称为HLA-DQA1*05:01或HLA-DQA1*05:05称为HLA-DQB1*02:01或HLA-DQB1*02:02。另一种称为DQ8的单倍型由与由HLA-DQB1gene变体产生的蛋白质结合的HLA-DQA1gene变体产生的蛋白质组成，所述HLA-DQA1gene变体称为HLA-DQA1*03:01或HLA-DQA1*03:02称为HLA-DQB1*03:02。（换行）可能单独或一起发生的DQ2和DQ8单倍型似乎增加了对小麦，黑麦和大麦中发现的蛋白麸质的不适当免疫应答的风险。这种免疫系统故障导致乳糜泻中发生的身体器官和组织的损伤。但是，在30%的普通人群中也发现了DQ2和DQ8单倍型，只有3%的具有这些单倍型的人患有乳糜泻。"}, {"name": "自身免疫性艾迪生病", "detail": "遗传学家庭参考提供有关自身免疫性艾迪生病的信息。"}, {"name": "特发性炎症性肌病", "detail": "遗传学家庭参考提供有关特发性炎性肌病的信息。"}, {"name": "幼年特发性关节炎", "detail": "遗传学家庭参考提供有关青少年特发性关节炎的信息。"}, {"name": "发作性睡病", "detail": "遗传学家庭参考提供有关发作性睡病的信息。"}, {"name": "1型糖尿病", "detail": "HLA-DQA1gene和其他HLAgene变异的组合会影响1型糖尿病的风险。1型糖尿病的特征在于由于激素胰岛素缺乏导致的高血糖水平，并且由胰腺中产生胰岛素的细胞的自身免疫损伤引起。（换行）1型糖尿病风险通过涉及HLA-DQA1和HLA-DQB1gene变异的两种HLA单倍型以及称为HLA-DRB1的另一种HLAgene而增加最多。一个单元型，写为DRB1*03:01-DQA1*05:01-DQB1*02，称为DR3。另一个单元型，写为DRB1*04:01/02/04/05/08-DQA1*03:01-DQB1*02，称为DR4。发生1型糖尿病风险最高的人在每个细胞中都有一份DR3单倍型和一份DR4单倍型。其他HLA单倍型仅轻度增加1型糖尿病的风险，而某些单倍型似乎可以预防这种情况的发展。其他gene和环境因素的变化也被认为会影响这种复杂疾病的风险。"}, {"name": "自身免疫性疾病", "detail": "HLA-DQA1gene的某些正常变异与自身免疫性疾病的风险增加有关，自身免疫性疾病发生在免疫系统故障并攻击人体自身组织和器官时。目前尚不清楚HLA-DQA1gene的不同版本如何影响发生自身免疫性疾病的风险。这些conditions被认为是多种环境和遗传因素共同作用的结果。其他HLA和非HLAgene的变化，其中一些仍然未知，也可能导致发展这些复杂病症的风险。"}, {"name": "其他疾病", "detail": "HLA-DQA1gene的正常变异会影响人体识别外来入侵者（病原体）并对其做出反应的能力。例如，已经证明该gene的变异增加或减少了人感染诸如乙型肝炎和麻风病的机会，或者如果发生感染可能影响疾病的严重性。（换行）称为HLA-DQA1*02:01的HLA-DQA1gene的特定变体增加了用称为拉帕替尼的药物治疗的晚期乳腺癌患者的肝损伤风险。研究人员建议，该变体可能会增加免疫系统对药物的敏感性，从而导致损害肝脏的炎症。"}]}' WHERE id = 2463;
UPDATE genes SET GHR_metadata_ch = '{"gene": "TERC", "conditions": [{"name": "先天性角化不良", "detail": "在先天性角化不全患者中，至少鉴定出TERCgene的11个突变。这种疾病的特征是肤色变化（色素沉着），口腔内白斑（口腔白斑）以及指甲和脚趾甲异常形成（指甲营养不良）。先天性角化病患者发生几种危及生命的疾病的风险增加，包括癌症和称为肺纤维化的进行性肺病。许多受影响的个体也会出现一种称为再生障碍性贫血的严重疾病，也称为骨髓衰竭，这种情况发生在骨髓不能产生足够的新血细胞时。（换行）一些导致先天性角化不良的TERCgene突变导致hTR分子缺失或不稳定；其他改变了hTR与hTERT或端粒酶其他成分相互作用的方式。（换行）TERCgene突变导致端粒酶功能障碍，端粒维持受损和端粒长度减少。快速分裂的细胞特别容易受到端粒缩短的影响。结果，先天性角化病患者可能会遇到各种影响体内细胞快速分裂的问题，如甲床细胞，毛囊，皮肤，口腔内壁（口腔粘膜）和骨髓。（换行）端粒维持不足导致的染色体断裂和不稳定可能导致遗传变化，使细胞以不受控制的方式分裂，导致一些先天性角化不良患者发生癌症。"}, {"name": "特发性肺纤维化", "detail": "在进行性肺病特发性肺纤维化患者中已鉴定出TERCgene的几种突变。在家族性（家族性肺纤维化）病例中发现了该gene的突变，而在孤立（散发）病例中则较少见。一些由于TERCgene突变而患有特发性肺纤维化的个体的家庭成员具有先天性角化不全的其他特征（如上所述），例如再生障碍性贫血或癌症。TERCgene中的（换行）突变会降低或消除端粒酶的功能，从而使端粒在细胞分裂时变得异常短。缩短的端粒可能会触发快速分裂的细胞，例如排列在肺内的细胞，停止分裂或过早死亡。然而，研究人员不确定端粒缩短如何导致特发性肺纤维化特征性的进行性瘢痕形成和肺损伤。(换行)特发性肺纤维化是一种复杂的疾病，可能是由遗传和环境因素共同引起的。研究表明，许多患有TERCgene突变的受影响人群也可能暴露于环境风险因素，例如香烟烟雾或某些灰尘或烟雾。TERCgene的突变可能会增加一个人患特发性肺纤维化的风险，然后暴露于某些环境因素会触发该疾病。"}, {"name": "其他疾病", "detail": "在孤立的再生障碍性贫血患者中也发现了TERCgene突变，这是一种骨髓衰竭形式，没有先天性角化不全的其他身体特征。研究人员建议，影响端粒酶不同部分的突变可能是这些特征缺失的原因。一些人认为，由TERCgene突变引起的孤立的再生障碍性贫血实际上可能代表先天性角化不全的晚期发作形式，其中诸如指甲营养不良之类的身体特征较轻，可能并不明显。"}]}' WHERE id = 2495;
UPDATE genes SET GHR_metadata_ch = '{"gene": "FGFR1", "conditions": [{"name": "8p11骨髓增生综合征", "detail": "FGFR1gene参与一种称为8p11骨髓增生综合征的血癌。这种情况的特征是白细胞数量增加（骨髓增生性疾病）和淋巴瘤的发展，淋巴瘤是一种导致淋巴结肿瘤形成的血液相关癌症。骨髓增生性疾病通常发展成另一种形式的血癌，称为急性髓性白血病。8p11骨髓增生综合征是由8号染色体和另一条染色体之间的遗传物质重排（易位）导致的，该遗传物质将FGFR1gene的一部分与另一条染色体的另一个gene的一部分融合在一起。最常见的伴侣gene是13号染色体上的ZMYM2。这些易位仅在癌细胞中发现。（换行）不管配偶体gene如何，由融合gene产生的蛋白质开启FGFR1信号传导而不需要来自生长因子的刺激。不受控制的信号传导促进连续的细胞生长和分裂，导致癌症。"}, {"name": "脑颅皮肤脂肪瘤病", "detail": "在脑颅皮肤脂肪瘤病（ECCL）患者中已鉴定出FGFR1gene的至少两个突变，这是一种罕见病，其特征是非癌性肿瘤的生长以及其他影响大脑，眼睛和皮肤的异常。导致ECCL的突变不是从父母那里继承的；它们在出生前发育的早期阶段在一个细胞中随机出现。随着细胞继续生长和分裂，一些细胞将发生突变，而其他细胞则不会。具有和不具有gene突变的细胞混合物称为镶嵌。（换行）与ECCL相关的FGFR1gene突变改变了FGFR1蛋白中的单蛋白结构单元（氨基酸）。这些突变被描述为“功能获得”，因为它们过度激活受体，触发影响细胞生长和分裂的异常信号传导。研究人员正在研究这些信号变化如何导致肿瘤生长和ECCL的其他特征。"}, {"name": "Hartsfield综合征", "detail": "在患有Hartsfield综合征的人中，已经鉴定出FGFR1gene的至少七个突变，这是一种罕见的疾病，其特征是全前脑畸形，这是大脑发育异常，手脚畸形称为外生殖器。这些突变中的一些影响每个细胞中FGFR1gene的两个拷贝之一，从而导致该疾病的常染色体显性形式。研究人员认为，这些突变具有“显性负性”作用，这意味着由gene的突变拷贝产生的FGFR1蛋白版本会干扰由gene的正常拷贝产生的蛋白版本的活性。在其他情况下，突变发生在FGFR1gene的两个拷贝中，导致该疾病的常染色体隐性形式。与Hartsfield综合征相关的所有突变都大大降低或消除了FGFR1蛋白的功能，包括其与FGF结合的能力。结果，受体不能正确地传输信号，这损害了正常发育的许多方面。目前尚不清楚这些变化如何特异性导致全前脑和外胚层，这是Hartsfield综合征的特征。"}, {"name": "卡门氏症候群", "detail": "研究人员已经鉴定出140多种FGFR1gene突变，这些突变可引起Kallmann综合征，该疾病的特征是性腺功能低下性腺功能减退症（一种影响指导性发育的激素产生的疾病）和嗅觉受损。这种情况也会影响其他身体系统，其特征因受影响的个体而异。研究人员估计，FGFR1gene突变约占所有Kallmann综合征病例的10%。（换行）导致Kallmann综合征的FGFR1gene突变改变FGFR1蛋白中的单个氨基酸或导致产生异常小的无功能形式的蛋白。由于这些突变会降低或消除FGFR1的功能，从而阻止其正确传输信号，因此将其描述为“功能丧失”突变。研究表明，功能性FGFR1的缺乏会破坏发育中的大脑中嗅觉神经元和产生GnRH的神经元的迁移和存活。如果嗅神经细胞不延伸到嗅球，则人的嗅觉将受损或缺失。产生GnRH的神经元的错位或过早丢失会阻止性激素的产生，从而干扰正常的性发育并导致青春期延迟或缺失。(换行)一些由FGFR1gene突变引起的Kallmann综合征患者还有其他特征，如唇裂（唇裂），口腔顶部开口（腭裂），牙齿发育异常，以及手脚异常。尚不清楚FGFR1gene的突变如何导致这些其他体征和症状。由于这些特征因人而异，研究人员怀疑可能涉及其他遗传和环境因素。一些受影响的个体除FGFR1外还具有其他几个gene之一的突变，这些遗传变化可能有助于该病的多种特征。"}, {"name": "骨音发育不良", "detail": "FGFR1gene中的至少三个突变可引起罕见的疾病，称为骨球不典型增生。这种情况的特征是骨骼生长异常，导致头部和面部（颅面）异常和侏儒症。导致骨球不典型增生的FGFR1gene突变会改变FGFR1蛋白中的单个氨基酸。改变的FGFR1蛋白似乎引起延长的信号传导，从而促进颅骨中骨骼的过早融合并破坏手臂和腿部骨骼生长的调节，从而导致颅面畸形和四肢缩短。因为引起骨球不典型增生的FGFR1gene突变异常增加了FGFR1信号传导，所以它们被描述为“功能获得”突变。"}, {"name": "菲佛综合症", "detail": "FGFR1gene中的另一个“功能获得”突变可引起普发综合征。这种情况的特征是颅骨中某些骨骼过早融合（颅突狭窄），导致头部畸形和独特的面部特征。受影响的人也有手足异常。导致这种情况的FGFR1gene突变会改变FGFR1蛋白中的单个氨基酸；氨基酸脯氨酸在蛋白质252位被氨基酸精氨酸取代（写为Pro252Arg或P252R）。改变的FGFR1蛋白似乎引起延长的信号传导，从而促进颅骨的早期融合并影响手和脚骨骼的发育。"}, {"name": "其他疾病", "detail": "已发现FGFR1gene中的几个突变会导致某种形式的性腺功能减退性腺功能减退，而不会损害嗅觉。这种情况通常称为常态性孤立性腺功能减退性腺功能减退症（nIHH）。像引起Kallmann综合征的FGFR1gene突变（如上所述）一样，这些突变会降低或消除FGFR1蛋白的功能，从而阻止其正确传输信号。功能性FGFR1的缺乏破坏了发育中的大脑中产生GnRH的神经细胞的迁移，这影响了性激素的产生并导致青春期延迟或缺乏。目前尚不清楚为什么某些FGFR1gene突变会影响嗅觉（导致Kallmann综合征），而另一些则不会（导致nIHH）。已经发现一些突变在某些人中引起Kallmann综合征，在另一些人中引起nIHH。"}, {"name": "其他癌症", "detail": "在某些癌症中发现了涉及FGFR1gene的变化。这些遗传变化是体细胞的，这意味着它们不是遗传的，而是发生在引起肿瘤的细胞中。导致FGFR1gene异常大量拷贝的gene扩增发生在几种形式的癌症中。这些包括一些肺癌，食道癌，乳腺癌和口腔癌。另外，已经在几种类型的脑肿瘤中鉴定出改变FGFR1gene中单个氨基酸的突变。（换行）研究表明，与癌症相关的FGFR1的遗传变化异常地增加了FGFR1蛋白的活性并增强了其触发细胞内化学反应的能力。由此产生的不受控制的信号传导可以促进连续的细胞生长和分裂，这是癌症的标志。FGFR1gene的扩增与某些癌症的预后较差以及肿瘤扩散（转移）到身体其他部位的可能性增加有关。"}]}' WHERE id = 2514;
UPDATE genes SET GHR_metadata_ch = '{"gene": "NPRL3", "conditions": [{"name": "病灶可变的家族性局灶性癫痫", "detail": "已发现至少十个NPRL3gene突变会导致家族性局灶性癫痫，病灶可变（FFEVF），这是家族性复发性癫痫（癫痫）的一种罕见形式。这些突变大多数导致异常短的无功能蛋白的产生。结果，正常GATOR1复合物的形成减少，导致mTORC1的过度活性和mTOR途径的过度信号传导。尚不清楚异常活跃的mTOR途径如何导致FFEVF的癫痫发作。研究表明，大脑中mTOR通路信号的增加会导致神经细胞（突触）之间的连接发生变化，并增加神经细胞的激活（兴奋），从而引起癫痫发作。"}]}' WHERE id = 2522;
UPDATE genes SET GHR_metadata_ch = '{"gene": "WDR45", "conditions": [{"name": "β螺旋桨蛋白相关的神经变性", "detail": "在患有β螺旋桨蛋白相关神经变性（BPAN）的人中已鉴定出50多个WDR45gene突变，该疾病会损害神经系统。这种损害导致从婴儿期或幼儿期开始的发育迟缓和反复发作（癫痫），随着时间的推移变得更糟的运动问题以及成年后智力功能的逐渐丧失。受影响的个体最终在大脑中积累了铁，可以通过医学成像看到；因此，BPAN被归类为一种称为脑铁积聚（NBIA）的神经退行性疾病。（换行）在BPAN患者中鉴定的大多数WDR45gene突变被认为导致产生不稳定的WIPI4蛋白，其迅速分解，导致WIPI4蛋白功能丧失。没有功能性WIPI4蛋白，自噬过程受损，使细胞去除受损细胞器和废物的效率降低。研究人员认为，神经细胞（神经元）可能特别容易受到自噬受损的影响，因为它们具有长的延伸（轴突和树突），使得将废物从这些结构运输到细胞体内的溶酶体进行回收更加困难。废料可以积聚在这些区域并损坏它们。神经元损伤导致BPAN发生神经系统问题。"}]}' WHERE id = 2532;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ADA2", "conditions": [{"name": "腺苷脱氨酶2缺乏症", "detail": "已发现ADA2gene中至少有15个突变会导致腺苷脱氨酶2缺乏症，这种疾病的特征是各种器官和组织，特别是血管（血管炎）的异常炎症。这些突变严重降低或消除了腺苷脱氨酶2的功能。研究人员不完全了解这种酶功能的丧失如何导致ADA2缺乏症的特征。他们推测缺乏这种酶可能会破坏各种组织中促炎和抗炎巨噬细胞之间的平衡，导致异常炎症。该酶在维持血管结构完整性中的作用可以帮助解释为什么在这种疾病中血管最常受到炎症的影响。"}]}' WHERE id = 2534;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SLC2A1", "conditions": [{"name": "GLUT1缺陷综合征", "detail": "据报道，患有GLUT1缺乏症候群的人有150多个SLC2A1gene突变。这种疾病会导致多种神经系统症状，包括发育迟缓，智力残疾，运动问题和频繁发作（癫痫病）。导致GLUT1缺乏症候群的突变会降低或消除GLUT1蛋白的功能。功能较少的GLUT1蛋白会减少脑细胞可用的葡萄糖量，从而影响大脑的发育和功能。"}]}' WHERE id = 2551;
UPDATE genes SET GHR_metadata_ch = '{"gene": "NR3C2", "conditions": [{"name": "假性低醛固酮增多症1型", "detail": "在假性低醛固酮增多症1型（PHA1）患者中已经发现NR3C2gene的50多个突变，这种疾病通常始于婴儿期，其特征是血液中钠水平低（低钠血症）和钾水平高（高钾血症）。特别是，NR3C2gene突变与常染色体显性遗传PHA1有关，这是一种相对温和的疾病形式，可以在儿童时期得到改善。NR3C2gene中的（换行）突变导致无功能或功能异常的盐皮质激素受体蛋白无法正确调节转运钠和钾的专门蛋白。结果，钠重吸收和钾分泌均减少，引起低钠血症和高钾血症。"}, {"name": "其他疾病", "detail": "NR3C2gene中的一种特定突变可导致早发性高血压，并在怀孕期间严重恶化。患有这种疾病的人很小就会患上高血压（高血压）。这种情况会影响男性或女性，怀孕女性的高血压会恶化。（换行）这种情况下涉及的突变改变了盐皮质激素受体蛋白中的一个蛋白质结构单元（氨基酸）。氨基酸丝氨酸被蛋白质中第810位的氨基酸亮氨酸取代（写为Ser810Leu或S810L）。这种突变改变了受体的形状，从而使受体被非盐皮质激素（如孕酮和皮质醇）异常激活。盐皮质激素受体活性增加导致钠重吸收过多，从而导致高血压。怀孕期间孕酮水平升高，这就是怀孕女性病情恶化的原因。"}]}' WHERE id = 2596;
UPDATE genes SET GHR_metadata_ch = '{"gene": "HLA-DQB1", "conditions": [{"name": "腹腔疾病", "detail": "已发现HLAgene变体（HLA单倍型）的至少两种特定组合增加发生乳糜泻的风险，乳糜泻是一种炎症损害肠道和其他器官和组织的疾病。这些单倍型之一称为DQ2，由HLA-DQB1gene变体产生的蛋白质组成，所述HLA-DQB1gene变体称为HLA-DQB1*02:01或HLA-DQB1*02:02与由HLA-DQA1gene变体产生的蛋白质结合。称为HLA-DQA1*05:01或HLA-DQA1*05:05。另一种单倍型称为DQ8，由称为HLA-DQB1*03:02的HLA-DQB1gene变体产生的蛋白质与由称为HLA-DQA1*03:01的HLA-DQA1gene变体产生的蛋白质结合或HLA-DQA1*03:02。（换行）可能单独或一起发生的DQ2和DQ8单倍型似乎增加了对小麦，黑麦和大麦中发现的蛋白麸质的不适当免疫应答的风险。这种免疫系统故障导致乳糜泻中发生的身体器官和组织的损伤。但是，在30%的普通人群中也发现了DQ2和DQ8单倍型，只有3%的具有gene变异的个体患有乳糜泻。"}, {"name": "发作性睡病", "detail": "称为HLA-DQB1*06:02的HLA-DQB1gene版本增加了发生睡眠障碍发作性睡病的风险，特别是在患有猝倒症的人中。（Cataplexy是对强烈情绪（例如笑，惊讶或愤怒）的突然肌张力丧失。）尚不清楚HLA-DQB1*06:02如何导致这种升高的风险。然而，越来越多的证据表明发作性睡病与免疫系统功能障碍有关。（换行）与发作性睡病相关的睡眠异常可能是由于称为下丘脑的大脑部分中特定脑细胞（神经元）的丧失所致。这些细胞通常产生称为hypocretins（也称为orexins）的化学物质，其在体内具有许多重要功能。特别是，hypocretins调节每日睡眠-觉醒周期。研究人员推测，免疫系统异常可能会引发发作性睡病患者产生hypocretin的神经元的丢失。但是，没有直接证据表明免疫系统因素是造成这种损失的原因。(换行)大多数患有发作性睡病并伴有猝倒症的人都有HLA-DQB1*06:02变异，许多人还具有其他密切相关的HLAgene（包括HLA-DQA1）的特定版本。但是，这些变异在普通人群中非常普遍，只有一小部分HLAgene具有特定变异的人会发展发作性睡病。其他遗传和环境因素，例如某些细菌和病毒感染，也会影响发生这种疾病的机会。"}, {"name": "自身免疫性艾迪生病", "detail": "遗传学家庭参考提供有关自身免疫性艾迪生病的信息。"}, {"name": "幼年特发性关节炎", "detail": "遗传学家庭参考提供有关青少年特发性关节炎的信息。"}, {"name": "1型糖尿病", "detail": "HLA-DQB1gene和其他HLAgene变异的组合会影响1型糖尿病的风险。1型糖尿病的特征在于由于激素胰岛素缺乏导致的高血糖水平，并且由胰腺中产生胰岛素的细胞的自身免疫损伤引起。（换行）1型糖尿病风险通过HLA-DQB1和HLA-DQA1gene以及另一种称为HLA-DRB1的HLAgene的变异的两种特定组合而最大程度地增加。一个单元型，写为DRB1*03:01-DQA1*05:01-DQB1*02，称为DR3。另一个单元型，写为DRB1*04:01/02/04/05/08-DQA1*03:01-DQB1*02，称为DR4。发生1型糖尿病风险最高的人在每个细胞中都有一份DR3单倍型和一份DR4单倍型。其他HLA单倍型仅轻度增加1型糖尿病的风险，而某些单倍型似乎可以预防这种情况的发展。其他gene和环境因素的变化也被认为会影响这种复杂疾病的风险。"}, {"name": "自身免疫性疾病", "detail": "HLA-DQB1gene的正常变异与其他几种疾病有关。大多数这些疾病具有自身免疫基础，这意味着它们在免疫系统发生故障并攻击身体自身的组织和器官时发生。与HLA-DQB1相关的自身免疫性疾病包括多发性硬化症，天疱疮和1型糖尿病（如下所述）。(换行)多发性硬化症是大脑和脊髓（中枢神经系统）的慢性疾病，其导致肌肉无力，协调性差，麻木和各种其他健康问题。HLA-DQB1的几种变异似乎增加了患这种疾病的风险。这些变异之一HLA-DQB1*06:02是增加发作性睡病风险的gene的同一版本。(换行)一些证据表明HLA-DQB1gene也可能在几种形式的天疱疮中起作用，这种情况会导致皮肤和粘膜（如口腔潮湿的衬里）严重起泡。(换行)目前尚不清楚不同版本的HLA-DQB1gene如何影响发生自身免疫性疾病的风险。这些疾病通常是由多种环境和遗传因素共同导致的。其他HLA和非HLAgene的变化，其中一些仍然未知，也可能导致发展这些复杂病症的风险。"}]}' WHERE id = 2605;
UPDATE genes SET GHR_metadata_ch = '{"gene": "FAT4", "conditions": [{"name": "Hennekam综合征", "detail": "已发现FAT4gene中至少有7个突变会引起Hennekam综合征，这是一种由淋巴系统畸形引起的遗传性疾病，该系统由运输淋巴液和免疫细胞遍布全身的血管网络组成。导致Hennekam综合征的FAT4gene突变会降低FAT4蛋白的活性，这似乎会损害淋巴系统的正常发育。但是，机制尚不清楚。淋巴系统形成不良会导致淋巴管异常扩张（淋巴管扩张），并容易因液体积聚（淋巴水肿）和Hennekam综合征的其他特征而破裂（破裂），浮肿或肿胀。FAT4gene突变约占所有Hennekam综合征病例的25%。"}, {"name": "其他疾病", "detail": "在患有van Maldergem综合征的个体中也发现了FAT4gene的突变，该疾病的特征是智力残疾，听力下降，骨骼异常和称为脑室周围异位症的脑畸形。导致van Maldergem综合征的FAT4gene突变导致FAT4蛋白功能降低。据认为，大脑中FAT4蛋白活性的降低会破坏大脑中神经细胞的极性，从而导致脑室周围异位症。（换行）尚不清楚FAT4gene的突变如何在某些人中引起van Maldergem综合征的体征和症状，但在Hennekam综合征患者中导致不同的体征和症状（如上所述）。在van Maldergem综合征和Hennekam综合征患者中均发现了一种突变，该突变用FAT4蛋白中2375位的赖氨酸氨基酸取代了蛋白质结构单元（氨基酸）谷氨酸（写为Glu2375Lys或E2375K）。（折线）FAT4gene突变也已在许多类型的癌症中发现，包括一种称为头颈部鳞状细胞癌的皮肤癌，一种称为黑素瘤的侵袭性皮肤癌，一种称为肝细胞癌，胃癌的肝癌（胃））癌症和胰腺癌。这些癌症中涉及的FAT4gene突变与引起van Maldergem综合征和Hennekam综合征的突变不同（如上所述）。与癌症相关的FAT4gene突变称为体细胞突变；它们仅在癌变且未遗传的细胞中发现。这些突变很可能阻止FAT4蛋白充当肿瘤抑制因子，从而导致癌症特有的细胞无法控制的生长和分裂。van Maldergem综合征和Hennekam综合征患者不被认为患癌症的风险增加。"}]}' WHERE id = 2608;
UPDATE genes SET GHR_metadata_ch = '{"gene": "CHD2", "conditions": [{"name": "CHD2肌阵挛性脑病", "detail": "已经发现CHD2gene的突变会引起CHD2肌阵挛性脑病，这种疾病的特征是从儿童期开始反复发作（癫痫），脑功能异常（脑病）和智力障碍。这是由于阻止功能性产生或由于非功能性蛋白质产生而引起的，该蛋白质产生通过染色质重塑破坏了gene表达的调节。"}, {"name": "自闭症谱系障碍", "detail": "已在自闭症谱系障碍（ASD）患者中鉴定出CHD2gene突变，该疾病的特征是社交技能受损，沟通问题，重复行为，智力残疾，严重言语问题和脑畸形。与ASD相关的CHD2突变会损害蛋白质在染色质重塑中的功能，从而影响大脑发育。"}]}' WHERE id = 2641;
UPDATE genes SET GHR_metadata_ch = '{"gene": "CUL3", "conditions": [{"name": "假性醛固酮增多症2型", "detail": "CUL3gene中至少有17个突变可引起2型假性低醛固酮增多症（PHA2），这种疾病的特征是高血压（高血压）和血液中钾水平高（高钾血症）。这些突变导致产生缺少区域的异常短的cullin-3蛋白。研究表明，这种变化改变了E3泛素连接酶复合物的功能。尽管确切的机制尚不清楚，但这种变化导致WNK4蛋白的降解受损。由此产生的过量WNK4蛋白破坏了血压的正常控制，导致高血压和PHA2的其他特征。尚不清楚WNK1是否受E3泛素连接酶改变的影响，或者WNK1是否在由CUL3gene突变引起的PHA2中起作用。"}]}' WHERE id = 2675;
UPDATE genes SET GHR_metadata_ch = '{"gene": "MIR146A", "conditions": [{"name": "5q减综合征", "detail": "MIR146Agene参与称为5q减（5q-）综合征的病症。这种情况是一种称为骨髓增生异常综合征（MDS）的骨髓疾病，其中未成熟的血细胞不能正常发育。患有5q综合征的人通常缺乏红细胞（贫血）和巨核细胞异常。受影响的个体患上称为急性髓细胞性白血病（AML）的快速增长的血癌的风险也增加。（换行）5q-综合征是由染色体5的长（q）臂缺失DNA区域引起的。这种缺失发生在一个人一生中未成熟的血细胞中，并影响每个细胞中5号染色体的一个拷贝。大多数患有5q-综合征的人缺少约150万个DNA构建块（碱基对）的序列，也写为1.5兆碱基（Mb）。该缺失区域包含40个gene，通常包括MIR146A。丢失一个拷贝的MIR146Agene减少了细胞中microRNA miR-146a的量。结果，其产生通常被miR-146a阻断的蛋白质水平升高，这导致在5q-综合征中发生的巨核细胞的异常发育。研究表明，该病的其他特征与DNA缺失片段中的其他gene有关。"}]}' WHERE id = 2676;
UPDATE genes SET GHR_metadata_ch = '{"gene": "PPT1", "conditions": [{"name": "CLN1疾病", "detail": "已经发现PPT1gene中有65个以上的突变导致"}]}' WHERE id = 2684;
UPDATE genes SET GHR_metadata_ch = '{"gene": "GNAS", "conditions": [{"name": "McCune-Albright综合征", "detail": "在患有McCune-Albright综合征的人中至少鉴定出三个GNASgene突变，该疾病会影响骨骼，皮肤和几种产生激素（内分泌）的组织。这些突变导致G蛋白的异常形式，导致腺苷酸环化酶不断打开（组成性激活）。腺苷酸环化酶的组成性激活导致几种激素的过量产生，从而导致McCune-Albright综合征的体征和症状。(换行)McCune-Albright综合征不是遗传的。导致这种疾病的gene突变被描述为体细胞突变。体细胞突变不是从父母传给孩子，而是在一个人的一生中获得的，并且仅存在于某些细胞中。McCune-Albright综合征是由GNASgene的随机突变引起的，该突变发生在发育的早期。结果，人体的某些细胞具有正常版本的GNASgene，而其他细胞具有突变版本。这种现象称为镶嵌现象。这种疾病的严重程度及其具体特征取决于具有突变的GNASgene的细胞的数量和位置。"}, {"name": "原发性大结节性肾上腺增生", "detail": "在原发性大结节性肾上腺增生症（PMAH）患者中，已经鉴定出GNASgene中至少有两个突变，这种疾病导致肾上腺中形成多个肿块（结节），肾上腺是位于每个顶部的小激素产生腺体肾。这些结节导致肾上腺肿大（增生），并导致产生高于正常水平的激素皮质醇。皮质醇通常有助于维持血糖水平，保护身体免受身体压力，并抑制炎症。皮质醇水平升高可导致面部和上身体重增加，皮肤脆弱，骨质流失，疲劳和其他健康问题，这些问题通常发生在PMAH患者身上。（换行）导致PMAH的GNASgene突变被认为导致G蛋白过度活跃。研究表明，过度活跃的G蛋白可能会增加腺苷酸环化酶的水平，从而导致另一种称为环AMP（cAMP）的化合物过量生产。过量的cAMP可能触发异常细胞生长并导致PMAH特有的肾上腺结节。（换行）与McCune-Albright综合征一样，引起PMAH的GNASgene突变是体细胞突变，据信在胚胎发育早期发生。在两个肾上腺中都可以发现具有突变的GNASgene的细胞。"}, {"name": "进行性骨异型增生", "detail": "在进行性骨异型增生的人群中至少鉴定出14个GNASgene突变。人们通常从母亲那里继承每个gene的一个副本，从父亲那里继承一个副本。对于大多数gene，两个拷贝在所有细胞中都是活跃的或“打开的”。然而，对于一小部分gene，两个gene拷贝中只有一个是活跃的。对于其中一些gene，只有从一个人的父亲继承的副本（父亲的副本）是活跃的，而对于其他gene，只有从一个人的母亲继承的副本（母亲的副本）是活跃的。基于gene起源父母的gene激活的这些差异是由称为gene组印记的现象引起的。（换行）GNASgene具有复杂的gene组印记模式。在身体的某些部位，该gene的母本拷贝是活跃的，而在另一些部位，父本拷贝是活跃的。进行性骨异型增生是由影响gene父本拷贝的某些突变引起的。这些突变破坏了G蛋白的功能并削弱了其调节成骨的能力。成骨调节受损导致在进行性骨异型增生中看到的皮肤和肌肉中骨组织的异位产生。"}, {"name": "其他疾病", "detail": "GNASgene的突变还引起奥尔布赖特遗传性骨营养不良（AHO），其特征是身材矮小，肥胖，手指和脚趾异常短（短指），皮肤下骨组织异位发育以及其他骨骼异常。当导致AHO的突变从一个人的母亲那里遗传时，受影响的个体通常会患有AHO，并伴有对多种激素的抵抗（这种情况称为Ia型假性甲状旁腺功能减退症或PHPIa）。父系遗传突变可导致AHO无内分泌问题；这种情况称为假性假性甲状旁腺功能减退症（PPHP）。（换行）在内分泌腺肿瘤和骨骼中可能发生的纤维病变（发育不良）中发现了GNASgene的体细胞突变。据信这些突变导致过度活跃的G蛋白，其引发异常的细胞生长。由于具有突变的细胞在体内的分布不如McCune-Albright综合征（如上所述），因此异常生长仅限于特定的腺体或纤维病变。"}]}' WHERE id = 2710;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ATP2A1", "conditions": [{"name": "布罗迪肌病", "detail": "已经发现ATP2A1gene的突变会引起布罗迪肌病，布罗迪肌病是一种肌肉疾病，其特征在于运动后肌肉痉挛或由于无功能的酶而引起的剧烈活动，该酶会损害钙离子的运输，进而延迟肌肉松弛。"}]}' WHERE id = 2719;
UPDATE genes SET GHR_metadata_ch = '{"gene": "MPV17", "conditions": [{"name": "MPV17相关的肝脑线粒体DNA耗竭综合征", "detail": "已发现MPV17gene中的30多个突变会导致MPV17相关的肝脑线粒体DNA耗竭综合征，这种疾病的特征是婴儿期开始的肝脏疾病和神经系统问题。导致这种情况的大多数突变都会改变MPV17蛋白中的单个蛋白结构单元（氨基酸）。一种几乎完全影响美国西南部纳瓦霍族人口的突变将蛋白质中第50位的氨基酸谷氨酰胺替换为氨基酸精氨酸（写为R50Q）。这种突变导致产生不稳定的MPV17蛋白，该蛋白很快被分解。当这种情况发生在纳瓦霍人血统的人身上时，它被称为纳瓦霍人神经肝病。（换行）导致MPV17相关肝脑线粒体DNA耗竭综合征（包括R50Q突变）的MPV17蛋白的变化会损害蛋白功能并减少可用的蛋白质量。功能失调或缺失的MPV17蛋白会导致mtDNA维持问题，从而导致mtDNA数量减少（称为线粒体DNA耗竭）。线粒体DNA耗竭会损害人体许多细胞和组织中的线粒体功能，尤其是大脑，肝脏和其他具有高能量需求的组织。肝脏和大脑中线粒体功能的降低导致与MPV17相关的肝脑线粒体DNA耗竭综合征相关的肝衰竭和神经功能障碍。研究人员认为，细胞中可用的mtDNA越少，纳瓦霍神经肝病的特征就越严重。"}]}' WHERE id = 2732;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SKIV2L", "conditions": [{"name": "毛发肝肠综合征", "detail": "已发现SKIV2Lgene中至少有9个突变会引起毛发肝肠综合症，这是一种罕见病，会影响身体的许多部位。它的主要体征和症状包括从婴儿期开始的慢性腹泻，头发异常，独特的面部特征和肝病。该gene的突变可能会消除SKIV2L蛋白的功能。研究人员假设这种蛋白质功能的丧失会损害滑雪复合体和胞质外泌体的活性。但是，尚不清楚这些变化如何导致慢性腹泻和毛发肝肠综合征的其他特征。"}]}' WHERE id = 2777;
UPDATE genes SET GHR_metadata_ch = '{"gene": "MEGF8", "conditions": [{"name": "木匠综合症", "detail": "已发现MEGF8gene中至少有6个突变会引起Carpenter综合征，该病的特征是颅骨形成不规则，手指和脚趾异常以及许多其他特征。这些突变降低或消除了Megf8蛋白的功能。研究人员怀疑保留的蛋白质功能量可能会导致体征和症状的变化。目前尚不清楚MEGF8gene突变如何引起Carpenter综合征。突变可能会干扰身体许多部位的正常模式，从而导致这种疾病的特征。"}]}' WHERE id = 2785;
UPDATE genes SET GHR_metadata_ch = '{"gene": "COL1A1", "conditions": [{"name": "咖啡因病", "detail": "已经在婴儿皮质增生症（通常称为咖啡因病）中鉴定出COL1A1gene的突变，这种疾病在婴儿5个月时很明显。它的特征是软组织肿胀（肌肉），疼痛，过多的新骨形成（骨质增生）和其他骨骼异常，影响手臂和腿部长骨的颌骨，锁骨（锁骨）和骨干（骨干）。疼痛和肿胀通常会消失，并在几个月内发生骨重建。COL1A1中的突变导致产生大小和形状可变的I型胶原原纤维，从而导致这种疾病。"}, {"name": "埃勒斯-当洛二氏综合症", "detail": "已经发现COL1A1gene的突变会引起几种形式的Ehlers-Danlos综合征，例如：；关节炎型Ehlers-Danlos综合征，一种以出生时双髋活动过度和脱位为特征的疾病。COL1A1突变还与Ehlers-Danlos综合征的经典和血管类型有关，Ehlers-Danlos综合征的特征是皮肤柔软，高度伸展（弹性）且脆弱；成年后，疤痕异常，关节活动过度和受影响的人容易撕裂（破裂）主要动脉。血管类型与血管，肠和其他器官的破裂有关。这些是由于有缺陷或无功能的pro？1（I）导致I型胶原纤维破裂。"}, {"name": "成骨不全症", "detail": "COL1A1gene的突变已经被发现会导致成骨不全症，这是一种由于轻微创伤或无原因导致骨骼容易断裂的拥有属性，眼睛白色部分呈蓝色或灰色，身材矮小，听力丧失，呼吸系统问题，以及一种称为牙本质发育不全症的牙齿发育障碍。由于产生异常形式的I型胶原蛋白，COL1A1中的突变可导致该疾病的轻度（I型）至严重形式（II，III和IV型）。"}, {"name": "隆突性皮肤纤维肉瘤", "detail": "涉及COL1A1和PDGFBgene（COL1A1-PDGFB融合gene）的染色体17和22之间的体细胞易位与隆突性皮肤纤维肉瘤相关，隆突性皮肤纤维肉瘤是一种罕见类型的癌症，其导致皮肤深层的肿瘤。COL1A1-PDGFB融合蛋白受COL1A1gene序列控制，导致过量的PDGFB蛋白产生，异常刺激细胞增殖和分化，导致在这种情况下肿瘤形成。"}, {"name": "椎间盘疾病", "detail": "发现COL1A1gene突变与椎间盘疾病有关，椎间盘疾病是一种以分离脊柱（椎骨）的一个或多个椎间盘破裂（退化）为特征的病症，导致疼痛，虚弱和麻木。背部，颈部，手臂和腿部。"}, {"name": "其他疾病", "detail": "发现COL1A1gene突变与成骨不全症和Ehlers-Danlos综合征的体征和症状有关，这是由于pro-β中的其他氨基酸取代了甘氨酸？1（I）链，干扰pro-的组装和处理？1（I）链进入成熟的I型胶原分子。此外，COL1A1gene突变与骨质疏松症风险增加有关。"}]}' WHERE id = 2827;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ATXN3", "conditions": [{"name": "3型小脑萎缩症", "detail": "脊髓小脑性共济失调3型（SCA3）是一种以运动进行性问题为特征的疾病。SCA3是由ATXN3gene突变（称为三核苷酸重复扩增）产生的。该突变增加了ATXN3gene中重复的CAG区段的长度。SCA3患者有超过50个CAG重复。（换行）扩展的CAG区段导致产生异常长的ataxin-3蛋白版本，其折叠成错误的三维形状。这种无功能的ataxin-3蛋白无法从不再需要的蛋白中去除泛素。结果，这些不需要的蛋白质与遍在蛋白和共济失调蛋白-3一起聚集在一起，在细胞核内形成团块（聚集体）。目前尚不清楚这些聚集体如何影响细胞功能，因为它们存在于健康细胞和死亡细胞中。(换行)神经细胞（神经元）和其他类型的脑细胞受ATXN3gene突变的影响最大。SCA3与连接到脊髓（脑干），参与协调运动的大脑部分（小脑）和大脑其他区域的大脑部分的细胞死亡有关。这种情况也与脊髓神经元的死亡有关。随着时间的流逝，大脑和脊髓细胞中细胞的丢失会导致SCA3的体征和症状。"}]}' WHERE id = 2864;
UPDATE genes SET GHR_metadata_ch = '{"gene": "TAP2", "conditions": [{"name": "I型裸淋巴细胞综合征", "detail": "已发现TAP2gene中至少有七个突变会导致I型裸淋巴细胞综合征（BLS I）。这种免疫系统疾病通常会导致呼吸道反复细菌感染和皮肤上的开放性溃疡（溃疡），尽管一些BLS I患者没有这种情况的症状。参与BLS I的TAP2gene突变阻止功能性TAP2蛋白的产生。缺乏功能性TAP2会损害TAP复合物的形成，否则来自外来入侵者的肽不能转运到内质网并附着于MHC I类蛋白。因此，MHC I类蛋白质被分解，这导致这些蛋白质在细胞表面上短缺。缺乏MHC I类蛋白会损害人体对细菌的免疫反应，导致细菌反复感染。研究人员不确定为什么BLS患者我也不会感染病毒，但他们怀疑其他免疫过程能够识别和对抗病毒。TAP2gene突变如何参与皮肤溃疡的发展也不清楚。"}]}' WHERE id = 2892;
UPDATE genes SET GHR_metadata_ch = '{"gene": "HBA2", "conditions": [{"name": "α地中海贫血", "detail": "HBA1和/或HBA2gene的缺失是α地中海贫血的最常见原因。很少，这些gene中或附近的突变也可能导致这种疾病。当该疾病由α珠蛋白gene突变引起时，α地中海贫血的体征和症状往往比由这些gene的缺失引起的更为严重。(换行)人们在每个细胞中有两个HBA1gene拷贝和两个HBA2gene拷贝。每个副本都称为等位gene。对于每个gene，一个等位gene从一个人的父亲那里继承而另一个等位gene从一个人的母亲那里继承。结果，有四个等位gene产生α-珠蛋白。这些等位gene中的一些或全部丢失导致了不同类型的α地中海贫血。(换行)Hb Bart综合征是最严重的α地中海贫血形式，是由所有四个α珠蛋白等位gene的缺失引起的。这种情况的特征是出生前体内积聚过多的液体（胎儿积水），红细胞短缺（贫血）以及肝脏和脾脏肿大（肝脾肿大）。较轻的HbH疾病是由四个α珠蛋白等位gene中的三个缺失引起的。HbH疾病的特征是轻度至中度贫血，肝脾肿大以及眼睛和皮肤发黄（黄疸）。(换行)在Hb Bart综合征和HbH疾病中，α-珠蛋白的缺乏阻止细胞产生正常的血红蛋白。相反，细胞产生异常形式的血红蛋白，称为血红蛋白Bart（Hb Bart）或血红蛋白H（HbH）。这些异常的血红蛋白分子不能有效地将氧气输送到人体组织。用Hb Bart或HbH替代正常血红蛋白会导致贫血以及与α地中海贫血相关的其他严重健康问题。（换行）α地中海贫血的另外两种变体与α珠蛋白的量减少有关。由于细胞仍会产生一些正常的血红蛋白，因此这些变体往往很少或没有健康问题。四个α珠蛋白等位gene中的两个缺失导致α地中海贫血性状。具有α地中海贫血特征的人可能具有异常小的苍白红细胞和轻度贫血。在α地中海贫血沉默携带者中发现一个α珠蛋白等位gene缺失。这些人通常没有地中海贫血相关的体征或症状。"}, {"name": "其他疾病", "detail": "一种称为α地中海贫血发育迟缓16（ATR-16）的疾病是由16号染色体短（p）臂的遗传物质大量缺失引起的。这种情况的体征和症状是由许多gene的丧失引起的，包括HBA1和HBA2。（换行）HBA1和HBA2gene的缺失导致大多数ATR-16患者的α地中海贫血特征。其他gene的缺失会导致该疾病的其他特征，包括智力残疾，语言能力严重延迟，头部异常小（小头畸形）和独特的面部特征。受影响的男性也可能有睾丸下降（隐睾症）和阴茎下侧的尿道开口（尿道下裂）。(换行)ATR-16的体征和症状取决于缺失的大小。特别大的缺失可能包括负责多囊肾疾病的PKD1gene。该gene的缺失导致肾脏中多个囊肿的生长。如果缺失还包括TSC2gene，则受影响的个体将发展结节性硬化症。这种情况的特征是身体许多部位非癌性肿瘤的生长。"}]}' WHERE id = 2896;
UPDATE genes SET GHR_metadata_ch = '{"gene": "HBA1", "conditions": [{"name": "α地中海贫血", "detail": "HBA1和/或HBA2gene的缺失是α地中海贫血的最常见原因。很少，这些gene中或附近的突变也可能导致这种疾病。当该疾病由α珠蛋白gene突变引起时，α地中海贫血的体征和症状往往比由这些gene的缺失引起的更为严重。(换行)人们在每个细胞中有两个HBA1gene拷贝和两个HBA2gene拷贝。每个副本都称为等位gene。对于每个gene，一个等位gene从一个人的父亲那里继承而另一个等位gene从一个人的母亲那里继承。结果，有四个等位gene产生α-珠蛋白。这些等位gene中的一些或全部丢失导致了不同类型的α地中海贫血。(换行)Hb Bart综合征是最严重的α地中海贫血形式，是由所有四个α珠蛋白等位gene的缺失引起的。这种情况的特征是出生前体内积聚过多的液体（胎儿积水），红细胞短缺（贫血）以及肝脏和脾脏肿大（肝脾肿大）。较轻的HbH疾病是由四个α珠蛋白等位gene中的三个缺失引起的。HbH疾病的特征是轻度至中度贫血，肝脾肿大以及眼睛和皮肤发黄（黄疸）。(换行)在Hb Bart综合征和HbH疾病中，α-珠蛋白的缺乏阻止细胞产生正常的血红蛋白。相反，细胞产生异常形式的血红蛋白，称为血红蛋白Bart（Hb Bart）或血红蛋白H（HbH）。这些异常的血红蛋白分子不能有效地将氧气输送到人体组织。用Hb Bart或HbH替代正常血红蛋白会导致贫血以及与α地中海贫血相关的其他严重健康问题。（换行）α地中海贫血的另外两种变体与α珠蛋白的量减少有关。由于细胞仍会产生一些正常的血红蛋白，因此这些变体往往很少或没有健康问题。四个α珠蛋白等位gene中的两个缺失导致α地中海贫血性状。具有α地中海贫血特征的人可能具有异常小的苍白红细胞和轻度贫血。在α地中海贫血沉默携带者中发现一个α珠蛋白等位gene缺失。这些人通常没有地中海贫血相关的体征或症状。"}, {"name": "其他疾病", "detail": "一种称为α地中海贫血发育迟缓16（ATR-16）的疾病是由16号染色体短（p）臂的遗传物质大量缺失引起的。这种情况的体征和症状是由许多gene的丧失引起的，包括HBA1和HBA2。（换行）HBA1和HBA2gene的缺失导致大多数ATR-16患者的α地中海贫血特征。其他gene的缺失会导致该疾病的其他特征，包括智力残疾，语言能力严重延迟，头部异常小（小头畸形）和独特的面部特征。受影响的男性也可能有睾丸下降（隐睾症）和阴茎下侧的尿道开口（尿道下裂）。(换行)ATR-16的体征和症状取决于缺失的大小。特别大的缺失可能包括负责多囊肾疾病的PKD1gene。该gene的缺失导致肾脏中多个囊肿的生长。如果缺失还包括TSC2gene，则受影响的个体将发展结节性硬化症。这种情况的特征是身体许多部位非癌性肿瘤的生长。"}]}' WHERE id = 2900;
UPDATE genes SET GHR_metadata_ch = '{"gene": "POLR3A", "conditions": [{"name": "Pol III相关性白细胞营养不良", "detail": "至少有66个POLR3Agene突变与Pol III相关的白细胞营养不良有关。脑白质营养不良是涉及神经系统白质异常的病症。白质由被称为髓磷脂的脂肪物质覆盖的神经纤维组成，其隔离神经纤维并促进神经冲动的快速传递。形成髓磷脂的能力降低（髓鞘减少）会导致Pol III相关性白细胞营养不良的体征和症状，包括智力残疾和协调运动困难（共济失调）。在这种疾病中，牙齿（牙列）的发育也异常。（换行）在Pol III相关的脑白质营养不良中，POLR3Agene突变可能损害RNA聚合酶III酶的亚基正确组装的能力或导致RNA聚合酶III与DNA结合的能力受损。RNA聚合酶III分子功能降低可能会影响身体许多部位的发育和功能，但POLR3Agene突变与该疾病的具体体征和症状之间的关系尚不清楚。(换行)Pol III相关性脑白质营养不良患者可能有不同的体征和症状组合。这些不同的临床特征组合最初被描述为单独的疾病。受影响的个体可能被诊断为共济失调，牙列延迟和髓鞘减少（ADDH）；髓鞘减少，牙髓不足，性腺功能低下性腺功能减退（4H综合征）；震颤共济失调伴中枢性髓鞘减少（TACH）；少尿症的白细胞营养不良（LO）；或伴有小脑萎缩和call体发育不全（HCAHC）的髓鞘减少。由于后来发现这些疾病具有相同的遗传原因，研究人员现在将它们归类为单一病症Pol III相关脑白质营养不良的变异。"}, {"name": "带状疱疹", "detail": "遗传学家庭参考提供有关带状疱疹的信息。"}]}' WHERE id = 2926;
UPDATE genes SET GHR_metadata_ch = '{"gene": "DSP", "conditions": [{"name": "毛发角化病", "detail": "已经发现DSPgene中的几个突变会导致某种形式的角化病，其毛发被分类为II型。这种形式的病症也称为Carvajal综合征。它的特点是手掌和脚底上有厚厚的皮肤（掌plant角化病）；粗糙，干燥，细密，卷曲的头发；以及一种可能危及生命的心脏病，称为扩张型左心室心肌病。(换行)导致II型毛发角化病的DSPgene突变导致异常短版本的desmoplakin蛋白产生。异常蛋白质改变了桥粒的结构，阻止了细胞彼此有效附着。研究人员怀疑细胞之间的连接受损会使皮肤，头发和心肌更脆弱。随着时间的推移，由于这些组织暴露于机械应力（例如，皮肤表面上的摩擦或心肌的持续收缩和松弛），它们变得受损并且不能再正常工作。这种机制可能是II型毛发角化病中发生的皮肤，头发和心脏问题的基础。研究表明，这种情况下，异常的细胞信号传导也可能导致心肌病。"}, {"name": "致心律失常性右室心肌病", "detail": "遗传学家庭参考资料提供有关心律失常性右室心肌病的信息。"}, {"name": "其他疾病", "detail": "还发现DSPgene突变会引起一系列体征和症状，这些体征和症状与II型毛发的角化病重叠（如上所述）。少数家庭的皮肤，头发和心脏异常相似，牙齿缺失或异常小。其他家庭的皮肤和头发异常类似于II型毛发的角化病，但没有明显的心脏问题。还有一些仅患有掌plant角化病，没有任何其他特征。DSPgene突变还可能导致潜在的威胁生命的心脏病，称为心律失常性右室心肌病（ARVC），而皮肤和头发没有异常。尽管这些情况都与桥粒铂的功能受损和桥粒异常有关，但尚不清楚该gene的突变如何导致这些不同的特征模式。（换行）在患有致命性棘皮表皮松解性大疱性表皮松解症（LAEB）的患者中，已经鉴定出DSPgene中至少有四个突变。这种情况的特征包括非常脆弱的皮肤，容易起泡和脱落，完全没有头发（脱发），指甲异常或缺失，出生时出现的牙齿（新生儿牙齿）和心肌异常（心肌病）。皮肤异常导致婴儿早期严重的体液流失和死亡。像引起II型毛发角化病的突变一样，与LAEB相关的突变导致异常短版本的桥粒铂和桥粒功能受损。但是，与LAEB相关的蛋白质缺少其他区域，这可能解释了与这种情况相关的更严重的体征和症状。"}]}' WHERE id = 2979;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ACTA2", "conditions": [{"name": "主动脉瘤，家族性胸（or）", "detail": "ACTA2gene的突变与家族性胸主动脉瘤和解剖（家族性TAAD）有关，后者是由异常蛋白质引起的疾病，会干扰肉瘤阻止动脉伸展的能力。这种疾病涉及主动脉瘤和解剖，导致危及生命的内出血。"}, {"name": "其他疾病", "detail": "ACTA2gene的突变与多系统平滑肌功能障碍综合征（家族性TAAD）有关，后者是一种损害人体平滑肌活动的疾病。这种综合征是拥有属性血管异常，瞳孔对光的反应减弱，膀胱低张，肌肉收缩受损，使食物通过消化道（低蠕动）"}]}' WHERE id = 2986;
UPDATE genes SET GHR_metadata_ch = '{"gene": "PYGM", "conditions": [{"name": "糖原贮积病V型", "detail": "已发现PYGMgene中约130个突变会导致V型糖原贮积病（GSDV）。北美和欧洲人群中常见的一种突变称为Arg50Ter或R50X。该突变在制备肌磷酸化酶的说明书中产生过早的停止信号，这减少了酶的产生。肌磷酸化酶的缺乏会损害糖原的正常分解。导致GSDV的其他突变可能严重降低酶活性或改变酶折叠成三维形状的方式。有缺陷的酶不能分解糖原。结果，肌肉细胞不能产生足够的能量，因此肌肉容易疲劳。肌肉细胞能量产生减少导致GSDV的主要特征。"}]}' WHERE id = 2991;
UPDATE genes SET GHR_metadata_ch = '{"gene": "HIVEP2", "conditions": [{"name": "HIVEP2相关的智力障碍", "detail": "在患有称为HIVEP2相关智力障碍的神经系统疾病的个体中发现了HIVEP2gene的至少9个突变。这种情况的特征是言语和行走发育迟缓，中度至重度智力障碍，轻度身体异常（畸形特征）和肌张力弱（肌张力低下）等特征。HIVEP2gene突变被认为导致功能性HIVEP2蛋白的缺乏。尽管研究人员推测HIVEP2蛋白的缺乏会改变与大脑生长发育有关的几个gene的表达，但尚不清楚这些遗传变化如何导致与HIVEP2相关的智力障碍相关的特征。大脑生长发育异常可能是HIVEP2相关智力障碍的认知问题和其他神经系统特征的基础。目前尚不清楚HIVEP2gene突变如何导致这种情况下可能发生的异常身体特征和健康问题。"}]}' WHERE id = 2997;
UPDATE genes SET GHR_metadata_ch = '{"gene": "GLDC", "conditions": [{"name": "甘氨酸脑病", "detail": "GLDCgene突变约占所有甘氨酸脑病病例的80%。在受影响的个体中已鉴定出40多种突变。这些遗传变化中的许多改变了甘氨酸脱氢酶中的单个氨基酸。例如，芬兰人群中最常见的GLDC突变将氨基酸丝氨酸替换为酶中564位的氨基酸异亮氨酸（也称为Ser564Ile或S564I）。其他突变会插入或删除GLDCgene中的遗传物质，或破坏来自该gene的遗传信息如何剪接在一起，从而为生产甘氨酸脱氢酶创造蓝图。(换行)一些GLDC突变导致产生无功能形式的甘氨酸脱氢酶，而其他突变减少但不消除酶的活性。当该酶的改变形式掺入甘氨酸裂解酶复合物中时，它阻止复合物适当地分解甘氨酸。结果，过量的甘氨酸会在人体器官和组织中积聚到毒性水平。由脑和脊髓中有害量的这种分子引起的损伤是甘氨酸脑病特有的智力残疾，癫痫发作和呼吸困难的原因。"}]}' WHERE id = 3037;
UPDATE genes SET GHR_metadata_ch = '{"gene": "TFAP2B", "conditions": [{"name": "Char综合征", "detail": "在Char综合征患者中，TFAP2Bgene的突变少于10个。这些突变改变了转录因子AP-2的结构？。超过一半的已知突变会改变蛋白质中对DNA结合至关重要的区域。其他突变发生在调节gene活性所必需的蛋白质区域。TFAP2Bgene中至少有两个变化阻止了任何转录因子AP-2β的产生？。该蛋白质功能的丧失破坏了源自神经c的结构的正常发育，包括心脏和面部特征。这些组织的异常发育导致Char综合征的主要特征。"}, {"name": "其他疾病", "detail": "研究表明，TFAP2Bgene的几种正常变异（多态性）与2型糖尿病（最常见的糖尿病形式）的风险增加有关。患有这种疾病的人血糖水平很高，因为身体对胰腺产生的激素胰岛素没有正确的反应。这种激素控制着多少糖（以葡萄糖的形式）从血液流入细胞作为能量。研究人员提出，TFAP2Bgene的多态性会改变细胞对胰岛素的反应性，尤其是脂肪储存细胞（脂肪细胞）。(换行)尽管TFAP2Bgene的变化可能与2型糖尿病有关，但生活方式，遗传和环境因素的综合作用都在确定这种复杂疾病的风险中起作用。"}]}' WHERE id = 3136;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ZMPSTE24", "conditions": [{"name": "下颌骨发育不良", "detail": "ZMPSTE24gene中至少有四个突变会导致一种形式的下颌骨发育不良，称为下颌骨发育不良，伴有B型脂肪营养不良（MADB）。这种情况的特征是多种体征和症状，包括骨骼异常，斑驳或斑驳的肤色以及影响身体各个部位的皮肤下脂肪组织的流失（B型脂肪营养不良）。导致MADB的ZMPSTE24gene突变导致功能性ZMPSTE24蛋白减少。结果，prelamin a无法有效处理，并且会在细胞中积聚。此外，缺乏成熟的核纤层蛋白a。一些研究人员推测，这些变化会破坏细胞核，使细胞更加脆弱。尚不清楚ZMPSTE24gene突变的影响如何与MADB的特定体征和症状相关。"}, {"name": "其他疾病", "detail": "已经在患有称为致死性限制性皮肤病的疾病的新生儿中鉴定出完全消除ZMPSTE24蛋白功能的ZMPSTE24gene突变。患有这种疾病的婴儿皮肤紧绷，僵硬；肺不发达；和其他异常。他们通常无法生存到生命的第一周。没有任何功能性ZMPSTE24蛋白，prelamin A积累并且不存在成熟的lamin A；但是，尚不清楚这些变化如何导致致命的限制性皮肤病的严重体征和症状。"}]}' WHERE id = 3152;
UPDATE genes SET GHR_metadata_ch = '{"gene": "CYP21A2", "conditions": [{"name": "21-羟化酶缺乏症", "detail": "已发现CYP21A2gene中的100多个突变会导致21-羟化酶缺乏症。这些突变中的一些是由CYP21A2gene与称为假gene的相似但无功能的DNA片段之间的遗传物质交换引起的，假gene非常靠近6号染色体上的CYP21A2gene。这种类型的DNA交换称为gene转换。来自假gene的遗传物质含有错误，当引入CYP21A2gene时，会破坏gene指令用于制造蛋白质的方式。导致21-羟化酶缺乏症的其他突变会改变21-羟化酶中的单个蛋白质结构单元（氨基酸）或删除或插入CYP21A2gene中的DNA片段。(换行)研究人员描述了三种形式的21-羟化酶缺乏症。患有一种称为食盐浪费型疾病的个体具有CYP21A2突变，导致完全无功能的酶。具有这种情况的简单病毒化类型的人具有CYP21A2gene突变，其允许产生低水平的功能性酶。患有这种疾病的非经典类型的个体具有CYP21A2突变，导致产生的酶量减少，但酶比其他任何类型的酶都多。所有类型的21-羟化酶缺乏症都会干扰皮质醇和醛固酮的产生。通常用于形成这些激素的物质反而积聚在肾上腺中并转化为雄激素，雄激素是男性性激素。雄激素的过量产生导致21-羟化酶缺乏症患者的性发育异常。"}]}' WHERE id = 3171;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ARSE", "conditions": [{"name": "X连锁点状软骨发育不良1", "detail": "已经发现ARSEgene的突变会引起X连锁软骨发育不良点1，这是一种骨骼和软骨发育异常，仅在男性中发生，这是由于含有ARSEgene的X染色体小区域的缺失导致无功能的芳基硫酸酯酶E酶引起的。临床症状包括骨骼末端附近和软骨中可见的斑点（斑点），X射线，身材矮小，指尖和脚趾短，面部特征明显，呼吸问题，脊髓压迫，发育迟缓，听力下降，视力异常和心脏缺陷。"}]}' WHERE id = 3213;
UPDATE genes SET GHR_metadata_ch = '{"gene": "FGB", "conditions": [{"name": "先天性纤维蛋白原血症", "detail": "FGBgene的突变可导致先天性纤维蛋白原血症，由于血液中不存在纤维蛋白原蛋白，导致出血过多。导致这种情况的大多数FGBgene突变都会导致蛋白质形成（mRNA）异常简短的蓝图。如果有任何纤维蛋白原B？链产生，它不起作用。FGBgene中的一些突变导致形成不能从细胞释放的蛋白质，使得蛋白质实际上无功能。因为当FGBgene的两个拷贝都被改变时发生这种情况，所以完全没有功能性纤维蛋白原B？链。没有B？亚基，纤维蛋白原蛋白未组装，导致不存在纤维蛋白。结果，血液凝块不会因受伤而形成，导致出血过多。"}, {"name": "其他疾病", "detail": "FGBgene的一个或两个拷贝中的突变可引起其他出血性疾病，称为低纤维蛋白原血症，纤维蛋白原血症或低纤维蛋白原血症。(换行)低纤维蛋白原血症是以血液中纤维蛋白原水平降低为特征的病症。这种情况是由减少但不消除纤维蛋白原B产生的突变引起的？链。低纤维蛋白原血症患者可能会出现从轻度到重度的出血问题。通常，血液中纤维蛋白原越少，出血问题越严重。(换行)纤维蛋白原血症是以功能异常的纤维蛋白原为特征的病症，尽管蛋白质以正常水平存在。这种情况通常是由改变纤维蛋白原B中单个蛋白质结构单元（氨基酸）的突变引起的？链。这些突变会改变纤维蛋白原蛋白的功能，并根据功能变化而导致过度出血或异常凝血（血栓形成）。(换行)低纤维蛋白原血症是以血液中异常功能的纤维蛋白原蛋白水平低为特征的病症。如在纤维蛋白原血症中，这种情况可导致过度出血或血栓形成。"}]}' WHERE id = 3230;
UPDATE genes SET GHR_metadata_ch = '{"gene": "PSMB8", "conditions": [{"name": "Nakajo-Nishimura综合征", "detail": "已发现PSMB8gene中至少有一个突变会引起Nakajo-Nishimura综合征，这种情况仅在日本人群中有描述。鉴定出的突变改变了从PSMB8gene产生的蛋白质中的单个蛋白质结构单元（氨基酸），用蛋白质位置201处的氨基酸缬氨酸取代了氨基酸甘氨酸（写为Gly201Val或G201V）。这种突变大大减少了这种蛋白质的产生，从而损害了免疫蛋白酶体的正常组装并导致免疫系统故障。由于未知原因，发生故障的免疫系统会触发异常炎症，从而损害整个身体的组织。(换行)异常炎症可能是Nakajo-Nishimura综合征的许多体征和症状的基础，包括皮肤上出现红色肿胀肿块（结节性红斑），反复发烧，关节问题，肝脏和脾脏肿大（肝脾肿大））。尚不清楚PSMB8gene的突变如何导致该病的其他特征，包括主要在上身的肌肉无力和消瘦以及脂肪组织的丧失（脂肪营养不良）。因为从PSMB8gene产生的蛋白质可能参与脂肪细胞的成熟，所以研究表明该蛋白质的缺乏可能会干扰这些细胞的正常发育和功能。"}, {"name": "其他疾病", "detail": "在两种情况下也发现了PSMB8gene的突变，其体征和症状与Nakajo-Nishimura综合征的体征和症状重叠：一种称为关节挛缩，肌肉萎缩，小细胞性贫血和脂膜炎引起的脂肪营养不良（JMP）综合征；另一种称为慢性非典型中性粒细胞性皮肤病，伴有脂肪营养不良和体温升高（CANDLE）综合征。这三种情况均以皮肤异常和脂肪营养不良为特征。虽然它们通常被认为是单独的疾病，但一些研究人员认为它们可能代表不同形式的单一疾病。（换行）已在JMP综合征家庭和CANDLE综合征家庭中鉴定出一种PSMB8gene突变。此突变在蛋白质位置75（写为Thr75Met或T75M）上用氨基酸蛋氨酸替换了氨基酸苏氨酸。仅在患有CANDLE综合征的人中发现了另一种突变；它用信号替换氨基酸半胱氨酸以过早停止蛋白质生产（写为Cys135Ter或C135X）。这些突变中的每一个都大大减少了PSMB8gene的蛋白质产生。目前尚不清楚该gene的突变如何导致Nakajo-Nishimura综合征，JMP综合征和CANDLE综合征的体征和症状重叠但不同的模式。研究人员推测，gene不同区域的突变可能对蛋白质功能产生不同的影响。"}]}' WHERE id = 3266;
UPDATE genes SET GHR_metadata_ch = '{"gene": "NPC1", "conditions": [{"name": "尼曼匹克症", "detail": "已发现NPC1gene中的380多个突变会导致C1型尼曼-皮克病。这种类型的尼曼-匹克病的特征是细胞内脂肪堆积，导致运动问题，神经功能障碍，肺和肝病以及言语和进食问题。许多NPC1gene突变导致NPC1蛋白中单个蛋白结构单元（氨基酸）发生变化。这些突变通常会导致功能蛋白短缺，从而阻止胆固醇和其他脂质的移动，从而导致它们在细胞中积累。因为这些脂质不在细胞中的适当位置，所以许多需要脂质的正常细胞功能（例如细胞膜形成）受到损害。脂质的积累和细胞功能障碍最终导致细胞死亡，从而导致在C1型尼曼-皮克病中看到的组织和器官损伤。"}]}' WHERE id = 3385;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SF3B4", "conditions": [{"name": "纳格综合征", "detail": "已经发现SF3B4gene中超过30个突变导致Nager综合征，其主要特征在于面部，手和手臂的异常，例如面颊发育不全（颧骨发育不全），下颌小（小颌畸形）和拇指畸形或缺失。这种情况也会影响身体其他部位的发育。患有这种情况的人中有一半以上的SF3B4gene突变。这些突变阻止了SAP49蛋白的产生或导致了非功能蛋白的产生。尚不清楚功能性SAP49蛋白的缺乏如何导致纳格综合征的发展问题。研究人员怀疑剪接体形成的问题可能会损害mRNA的加工并改变参与人体多个部位发育的gene的活性。SAP49的丢失也可能损害BMP途径的信号传导，导致面部，手和手臂的骨骼异常发育。"}, {"name": "其他疾病", "detail": "已发现至少四个SF3B4gene突变会导致罗德里格斯的肢端发育不良，罗德里格斯是一种严重的疾病，会导致面部，手臂和腿部骨骼发育不足以及肺部异常。受影响的人的手臂骨骼异常短，因此手的位置非常靠近身体（腓肠肌）。另外，通常缺少小腿中称为腓骨的手指，脚趾和骨头。受影响的人的嘴顶（c裂），小外耳（小耳畸形）和小棘皮症也有开口。在这种情况下，微念头症很严重，并导致危及生命的呼吸问题；大多数受影响的婴儿在出生后无法生存。（换行）导致罗德里格斯肢端发育不良的SF3B4gene突变的影响尚不清楚。与纳格综合征（如上所述）相似，研究人员怀疑异常的剪接体功能和BMP信号通路的破坏会损害这种情况下婴儿的正常骨骼发育和肢体形成。"}]}' WHERE id = 3424;
UPDATE genes SET GHR_metadata_ch = '{"gene": "COL9A2", "conditions": [{"name": "多发性骨phy发育不良", "detail": "已发现COL9A2gene的突变会导致显性多发性骨phy发育异常，这是一种软骨和骨骼发育异常，主要影响手臂和腿部长骨的末端（骨phy）。该gene中的剪接位点突变将破坏所需的遗传信息？2（IX）链产生导致这种疾病的症状。"}, {"name": "斯蒂克勒综合症", "detail": "已经发现COL9A2gene的突变会引起Stickler综合征，这是一种遗传性疾病，其特征是独特的面部外观，眼睛异常，听力下降和关节问题。"}, {"name": "椎间盘疾病", "detail": "COL9A2gene的突变被发现与椎间盘疾病有关，这种疾病的拥有属性是一个或多个椎间盘的分解（退化），这些椎间盘分离脊柱（椎骨），导致背部疼痛、虚弱和麻木、颈部、手臂和腿部。"}]}' WHERE id = 3438;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ETFDH", "conditions": [{"name": "戊二酸血症II型", "detail": "ETFDHgene中的某些突变阻止了电子转移黄素蛋白脱氢酶的产生。其他突变导致产生缺陷酶，其不能在分解脂肪和蛋白质的一系列反应（代谢途径）中发挥其作用。这种酶缺乏使这些营养素以及营养素被部分分解时产生的化合物积累到异常水平，特别是当身体处于压力下时。代谢不完全的有毒产物会损害许多身体系统中的细胞，导致II型戊二酸血症的体征和症状。"}]}' WHERE id = 3449;
UPDATE genes SET GHR_metadata_ch = '{"gene": "HCFC1", "conditions": [{"name": "甲基丙二酸血症伴高半胱氨酸尿症", "detail": "在患有高半胱氨酸尿症，cblX型，一种导致发育迟缓，眼缺陷，神经系统疾病和血液异常的疾病的甲基丙二酸血症患者中，已鉴定出至少六个HCFC1gene突变。具有这种形式的个体在颅骨和面部发育中也有严重异常（颅面异常）。这些突变发生在帮助其与其他蛋白质相互作用的蛋白质区域。据认为，这些区域的变化阻止了HCF-1与转录因子相互作用，从而破坏了正常的gene活性。MMACHCgene活性的损害尤其阻碍了维生素B12的正常加工和运输，阻碍了AdoCbl和MeCbl的产生。由于这两个辅因子均缺失，因此需要它们的酶（甲基丙二酰辅酶A突变酶和蛋氨酸合酶）无法正常运行。结果，某些氨基酸，脂质和胆固醇不被分解，同型半胱氨酸不能转化为甲硫氨酸。这种双重缺陷导致有毒化合物以及高半胱氨酸的积累，并减少体内蛋氨酸的产生。这种失衡的组合导致甲基丙二酸血症伴高半胱氨酸尿症的体征和症状。cblX型个体的神经系统和发育问题尤其严重，部分原因是通常由HCF-1蛋白调节的其他gene的活性受到破坏。"}, {"name": "其他疾病", "detail": "在具有X连锁智力障碍的个体中也发现了HCFC1gene的突变。这些人的发育迟缓和其他神经系统问题，但未表现出cblX型高半胱氨酸尿症的甲基丙二酸血症的其他特征。HCFC1gene突变导致功能降低的HCF-1蛋白产生。这种蛋白质功能的部分降低似乎会破坏正常的大脑发育，导致X连锁残疾的特征，但不会严重影响维生素B12的加工。"}]}' WHERE id = 3485;
UPDATE genes SET GHR_metadata_ch = '{"gene": "RNASEH2C", "conditions": [{"name": "Aicardi-Goutières综合征", "detail": "在患有Aicardi-Goutières综合征的人中已鉴定出RNASEH2Cgene的至少26个突变，Aicardi-Goutières综合征是一种涉及严重脑功能障碍（脑病），皮肤病变和其他与免疫系统异常激活相关的健康问题的疾病。导致Aicardi-Goutières综合征的RNASEH2Cgene突变可能导致功能异常的RNase H2复合物。该复合物的异常功能可能破坏转录，DNA复制，DNA修复，细胞死亡（凋亡）或其他过程。这种破坏被认为导致细胞中不需要的DNA和RNA的积累。这些DNA和RNA片段可能被误认为是病毒入侵者的遗传物质，引发多种身体系统的免疫系统反应，导致严重的脑功能障碍（脑病），皮肤损伤以及Aicardi-Goutières综合征的其他体征和症状。"}]}' WHERE id = 3486;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ALDH7A1", "conditions": [{"name": "吡哆醇依赖性癫痫", "detail": "已经发现ALDH7A1gene中的多种突变引起吡ido醇依赖性癫痫。这些突变大多数是单个家族特有的。患有这种情况的多个人发生一种突变；它用抗quitin蛋白（写为Glu399Gln或E399Q）中第399位的氨基酸甘氨酸代替了氨基酸谷氨酰胺。所有引起吡ido醇依赖性癫痫的突变都会产生无功能的抗quitin蛋白。抗quitin的短缺（缺乏）导致了-氨基己二酸半醛，导致吡ido醇（一种源自食物的维生素B6）的活性受到破坏。吡ido醇在体内的许多过程中都起着作用，例如大脑中称为神经递质的氨基酸和化学物质的分解。目前尚不清楚吡ido醇的缺乏如何导致这种情况的癫痫发作。"}]}' WHERE id = 3514;
UPDATE genes SET GHR_metadata_ch = '{"gene": "USP9Y", "conditions": [{"name": "Y染色体不孕", "detail": "少数具有Y染色体不育的个体在USP9Ygene中具有突变或全部或部分gene的缺失。USP9Ygene的这些变化阻止了泛素特异性蛋白酶9的产生或导致异常短的无功能蛋白的产生。缺乏功能性泛素特异性蛋白酶9会损害精子细胞的产生，导致无法生育孩子。"}]}' WHERE id = 3516;
UPDATE genes SET GHR_metadata_ch = '{"gene": "COL9A3", "conditions": [{"name": "多发性骨phy发育不良", "detail": "已显示COL9A3gene中的至少三个突变会导致显性多发性骨phy发育不良。所有这些突变都破坏了遗传信息如何剪接在一起，从而为产生遗传信息奠定了蓝图？3（IX）链。这些称为剪接位点突变的突变改变了一个称为外显子3的gene区域附近的DNA构建块（核苷酸）。COL9A3gene中的这些突变导致从？3（IX）链。尚不清楚COL9A3中的突变如何引起显性多发性骨phy发育不良的体征和症状。(换行)IX型胶原蛋白中所有已鉴定的突变都删除了COL3结构域的一部分，这表明该区域具有重要功能。突变可能会影响IX型胶原蛋白正确折叠或与其他软骨成分相互作用的能力。"}, {"name": "椎间盘疾病", "detail": "遗传学家庭参考提供有关椎间盘疾病的信息。"}, {"name": "斯蒂克勒综合症", "detail": "遗传学家庭参考提供有关Stickler综合征的信息。"}]}' WHERE id = 3523;
UPDATE genes SET GHR_metadata_ch = '{"gene": "PHKA1", "conditions": [{"name": "IX型糖原贮积病", "detail": "已知PHKA1gene中至少有七个突变会导致一种形式的糖原贮积病IX型（GSD IX），称为GSD IXd或X连锁肌肉糖原病。这种形式的疾病很少见，尚未得到很好的理解。它会影响肌肉，并可能导致肌肉无力，疼痛和抽筋，尤其是在运动过程中，尽管有些受影响的人没有这种情况的迹象或症状。PHKA1gene的突变会降低肌肉细胞中磷酸化酶b激酶的活性，尽管其机制尚不清楚。这种酶功能的降低会损害糖原分解。结果，糖原在细胞中积累，并且葡萄糖不可用于能量。肌肉细胞中能量产生的减少导致GSD-IXd的特征。"}]}' WHERE id = 3542;
UPDATE genes SET GHR_metadata_ch = '{"gene": "PLEC", "conditions": [{"name": "大疱性表皮松解症伴幽门闭锁", "detail": "PLECgene中至少有9个突变可导致大疱性表皮松解并伴有幽门闭锁（EB-PA）。除皮肤起泡外，EB-PA患者天生还具有威胁生命的消化道阻塞，称为幽门闭锁。PLECgene突变约占所有EB-PA病例的15%。（换行）负责EB-PA的PLECgene突变改变了plectin的正常结构和功能。阻止细胞产生任何功能性凝集素的突变往往会导致更严重的体征和症状，而减少凝集素数量或改变蛋白质结构的突变通常会导致较轻的体征和症状。功能性凝集素的缺乏会破坏表皮与下层皮肤层的附着，从而使皮肤对摩擦和轻微创伤的抵抗力降低。尚不清楚PLECgene中的突变如何与幽门闭锁相关。"}, {"name": "先天性肌无力综合征", "detail": "遗传学家庭参考资料提供有关先天性肌无力综合症的信息。"}, {"name": "表皮溶解水疱症", "detail": "PLECgene中至少有一个突变与大疱性表皮松解症的特征有关，这种情况会导致皮肤非常脆弱并容易起泡。在少数家庭中发现了这种突变，这种疾病被称为Ogna型疾病。研究人员不确定这种罕见病症实际上是大疱性表皮松解症的一种亚型还是代表大疱性表皮松解症的一种单独形式。（换行）负责Ogna类型的大疱性表皮松解症的突变会改变plectin蛋白中的单个蛋白结构单元（氨基酸）。具体而言，这种遗传变化将蛋白质位置2000（写为Arg2000Trp或R2000W）上的氨基酸色氨酸替换为氨基酸精氨酸。研究表明，这种突变可能会改变蛋白质折叠成三维形状的方式，从而阻止其与构成细胞骨架的分子相互作用。在皮肤中，这些变化导致表皮细胞变得脆弱并且容易受损。结果，皮肤不易摩擦，轻微创伤和起泡。"}, {"name": "其他疾病", "detail": "PLECgene中的突变还引起至少一种其他形式的大疱性表皮松解症，称为伴有肌肉营养不良的大疱性表皮松解症。除皮肤起泡外，患有这种疾病的人在以后的生活中还会经历进行性肌肉无力和消瘦（萎缩）。已经发现超过20种PLECgene突变导致这种形式的疾病。这些突变改变了凝集素的结构，功能或产生。在皮肤中，减少量的功能性凝集素会破坏表皮与下面的皮肤层的附着，从而使皮肤对摩擦的抵抗力降低。肌肉组织中功能性凝集素的缺乏会影响肌肉张力（收缩）所需的关键结构，从而导致肌肉无力和萎缩。"}]}' WHERE id = 3584;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ARID1B", "conditions": [{"name": "棺材-西里斯综合症", "detail": "已经发现ARID1Bgene的突变导致Coffin-Siris综合征，其特征在于发育迟缓，第五（小指）手指或脚趾异常以及由于异常或无功能的ARID1A蛋白引起的特征性面部特征，其损害SWI/SNF复合物和染色质重塑。"}, {"name": "自闭症谱系障碍", "detail": "自闭症谱系障碍（ASD）患者中已鉴定出ARID1Bgene突变，该疾病的特征是社交技能受损，沟通问题，重复行为，智力残疾，严重言语问题和脑畸形。与ASD相关的ARID1B突变导致ARID1B蛋白量减少或损害蛋白在染色质重塑中的功能。"}, {"name": "癌症", "detail": "ARID1Bgene的体细胞突变与几种类型的癌症有关，例如乳腺癌，神经母细胞瘤和B细胞淋巴瘤。这些突变被认为与SWI/SNF复合物的变化有关，导致细胞分化的破坏。"}, {"name": "其他疾病", "detail": "ARID1Bgene的突变与智力障碍有关，而没有Coffin-Siris综合征的其他临床特征，该疾病的病因尚不清楚。"}]}' WHERE id = 3621;
UPDATE genes SET GHR_metadata_ch = '{"gene": "TAP1", "conditions": [{"name": "I型裸淋巴细胞综合征", "detail": "已发现TAP1gene中至少有6个突变会导致I型裸淋巴细胞综合征（BLS I）。这种免疫系统疾病通常会导致呼吸道反复细菌感染和皮肤上的开放性溃疡（溃疡），尽管一些BLS I患者没有这种情况的症状。参与BLS I的TAP1gene突变阻止功能性TAP1蛋白的产生。缺乏功能性TAP1会损害TAP复合物的形成，否则来自外来入侵者的肽不能转运到内质网并附着于MHC I类蛋白。因此，MHC I类蛋白质被分解，这导致这些蛋白质在细胞表面上短缺。缺乏MHC I类蛋白会损害人体对细菌的免疫反应，导致细菌反复感染。研究人员不确定为什么BLS患者我也不会感染病毒，但他们怀疑其他免疫过程能够识别和对抗病毒。还不清楚TAP1gene突变如何参与皮肤溃疡的发展。"}]}' WHERE id = 3624;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ACADSB", "conditions": [{"name": "短/支链酰基辅酶A脱氢酶缺乏症", "detail": "在具有短/支链酰基辅酶A脱氢酶（SBCAD）缺乏症的个体中，已鉴定出ACADSB中的几种突变，该个体由活性较低或没有活性的酶缺乏症引起。临床症状包括嗜睡，肌肉无力，癫痫发作，发育迟缓或其他健康问题。"}]}' WHERE id = 3672;
UPDATE genes SET GHR_metadata_ch = '{"gene": "HSPG2", "conditions": [{"name": "Schwartz-Jampel综合征", "detail": "已发现HSPG2gene中的30多个突变引起Schwartz-Jampel综合征。这种罕见病的特征是持续的肌肉收缩（肌强直）限制了运动，以及称为软骨发育不良的骨骼异常。大多数突变会减少产生的perlecan的量。其他突变导致仅部分起作用的perlecan版本。这种蛋白质的数量或功能的减少破坏了软骨和骨组织的正常发育，这是受影响个体软骨发育不良的基础。神经肌肉接头处功能性全白蛋白的减少可能会改变其他分子的平衡，这些分子在肌肉收缩和放松时会发出信号。结果，连续触发肌肉收缩，导致肌强直。"}, {"name": "其他疾病", "detail": "HSPG2gene的突变还可能导致另一种更严重的软骨发育不良形式，称为dyssegmental dysplasia，Silverman-Handmaker型。由于与这种罕见病相关的非常严重的异常，大多数受影响的个体在出生前死亡，死产或仅活到婴儿早期。在患有节段性发育不良的Silverman-Handmaker型患者中，至少鉴定出7个HSPG2gene突变。这些突变阻止了任何功能性全白蛋白的产生或运输。完全缺乏这种蛋白质会严重破坏软骨和骨组织的发育，从而导致这种非常严重的软骨发育不良。"}]}' WHERE id = 3751;
UPDATE genes SET GHR_metadata_ch = '{"gene": "PKHD1", "conditions": [{"name": "多囊肾病", "detail": "在多囊肾患者中已鉴定出PKHD1gene的270多个突变。这些突变会导致常染色体隐性隐性多囊肾病（ARPKD），这是一种严重的疾病，通常在出生时或婴儿早期就很明显。PKHD1突变包括单个DNA构建模块（碱基对）的变化以及gene中少量碱基对的插入或缺失。这些突变破坏了纤维囊蛋白的正常结构和功能，或导致产生异常小的，无功能的蛋白。研究人员尚未确定这些遗传变化如何导致多囊肾疾病特征性囊肿的形成。"}]}' WHERE id = 3770;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SI", "conditions": [{"name": "先天性蔗糖酶-异麦芽糖酶缺乏症", "detail": "已发现SIgene中至少有10个突变会导致先天性蔗糖酶-异麦芽糖酶缺乏症。这些突变破坏了蔗糖异麦芽糖酶的折叠和加工，酶在肠上皮细胞内的运输，酶向细胞表面的定向或其正常功能。这些细胞过程中的任何一种受损都会导致蔗糖酶-异麦芽糖酶无法有效分解蔗糖，麦芽糖或由这些糖分子（碳水化合物）制成的其他化合物。无法消化这些糖会导致先天性蔗糖酶-异麦芽糖酶缺乏症患者出现肠道不适。"}]}' WHERE id = 3783;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SLC22A5", "conditions": [{"name": "卡尼丁缺乏症", "detail": "已发现SLC22A5gene中的60多个突变导致原发性肉碱缺乏。这些突变中的一些会在制备OCTN2蛋白的说明中产生过早的终止信号，从而导致异常短的无功能蛋白。其他突变会改变OCTN2蛋白中的单个蛋白结构单元（氨基酸）。SLC22A5gene中的（换行）突变导致缺少或功能失调的OCTN2蛋白。结果，细胞内的肉碱短缺（缺乏）。没有肉碱，脂肪酸就不能进入线粒体并用于制造能量。能量产生减少会导致原发性肉碱缺乏的某些特征，例如肌肉无力和低血糖症。脂肪酸也可能积聚在细胞中并损害心脏，肝脏和肌肉。这种异常积聚会导致该疾病的其他体征和症状。"}, {"name": "克罗恩病", "detail": "遗传学家庭参考提供有关克罗恩病的信息。"}]}' WHERE id = 3827;
UPDATE genes SET GHR_metadata_ch = '{"gene": "APOB", "conditions": [{"name": "家族性低脂蛋白血症", "detail": "已经发现APOBgene的突变会导致家族性低脂蛋白血症（FHBL），这种疾病由于载脂蛋白B（APOB-48和APOB-100）的异常形式而损害人体吸收和运输脂肪的能力，其特征是胆固醇水平低，肝脂肪变性或脂肪肝，发展为慢性肝病（肝硬化），在某些情况下无法吸收维生素E和A导致脂肪泻。此外，这种疾病可能导致儿童时期无法壮成长。"}, {"name": "高胆固醇血症", "detail": "已知APOBgene的突变会导致一种遗传性高胆固醇血症，称为家族性缺陷载脂蛋白B-100（FDB），这种疾病的特征是血液中胆固醇水平很高，并与患心脏病的风险增加相关。该疾病是由于apoB-100突变引起的，导致胆固醇在皮肤，肌腱和冠状动脉中积聚。"}, {"name": "其他疾病", "detail": "由于血液中低密度脂蛋白水平较高，已知APOBgene的变异与没有遗传性胆固醇疾病的人患心脏病的风险增加有关。"}]}' WHERE id = 3898;
UPDATE genes SET GHR_metadata_ch = '{"gene": "CTNND1", "conditions": [{"name": "Blepharocheilodontic综合征", "detail": "在患有睑缘上皮（BCD）综合征的人中至少鉴定出三个CTNND1gene突变，这种疾病在出生时就存在，并导致主要影响眼睑和嘴巴的异常。这些突变被认为阻止了p120-catenin蛋白的产生或导致不稳定的蛋白迅速分解。p120-catenin蛋白的缺乏使E-cadherin进入细胞并被内吞作用过快破坏。这些蛋白质的缺乏会破坏正常发育，尤其是颅面发育，这被认为是BCD综合征特征的基础。"}]}' WHERE id = 3934;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ANOS1", "conditions": [{"name": "卡门氏症候群", "detail": "在患有Kallmann综合征的人中已鉴定出ANOS1gene的140多个突变，该疾病的特征是性腺功能低下性腺功能减退症（一种影响指导性发育的激素产生的疾病）和嗅觉受损。这种情况也会影响其他身体系统，其特征因受影响的个体而异。研究人员估计，ANOS1gene突变占所有Kallmann综合征病例的5%至10%。（换行）导致Kallmann综合征的ANOS1gene突变删除部分或全部gene，改变anosmin-1中的单个蛋白质结构单元（氨基酸）或改变蛋白质的大小。所有这些突变都破坏了胚胎发育过程中anosmin-1的正常产生或功能。研究人员怀疑，缺失或改变的蛋白质无法指导嗅觉神经细胞和产生GnRH的神经细胞迁移到发育中的大脑中的通常位置。如果嗅神经细胞未延伸至嗅球，则会损害人的嗅觉。产生GnRH的神经元的错位会阻止性激素的产生，从而干扰正常的性发育并导致青春期延迟或缺失。(换行)目前尚不清楚ANOS1gene突变如何导致Kallmann综合征的其他可能体征和症状，包括一个肾脏发育不良（单侧肾发育不全），听力下降和手部镜像运动（双手运动障碍）。由于这些特征因人而异，研究人员怀疑可能涉及其他遗传和环境因素。一些受影响的个体除ANOS1外，其他几个gene之一也有突变，这些遗传变化可能有助于疾病的不同特征。"}]}' WHERE id = 3965;
UPDATE genes SET GHR_metadata_ch = '{"gene": "MYO5A", "conditions": [{"name": "格里切利综合征", "detail": "在格里切利综合征患者中至少发现了MYO5Agene的两个突变。这些突变导致一种称为1型的疾病，其特征是皮肤异常光（色素沉着不足），银灰色头发和神经系统异常，导致发育迟缓，智力障碍和癫痫发作。已知的MYO5Agene突变阻止了功能性肌球蛋白Va的产生。由于非功能性蛋白无法与由MLPH和RAB27Agene制成的蛋白形成复合物，因此黑素体无法转运至黑素细胞的边缘。相反，这些结构聚集在黑素细胞的中心附近，将黑色素捕获在这些细胞内并防止皮肤和头发的正常色素沉着。神经元中肌球蛋白Va的丢失会破坏蛋白质和其他分子在这些细胞内外的运输，这可能会导致1型格里切利综合征中发现的神经系统问题。"}]}' WHERE id = 3989;
UPDATE genes SET GHR_metadata_ch = '{"gene": "MPL", "conditions": [{"name": "原发性血小板增多症", "detail": "MPLgene中的几个突变与少数原发性血小板增多症有关，这种疾病的特征是血液中血小板数量增加。血小板是参与血液凝固的血细胞碎片，并且在患有原发性血小板增多症的人中常见异常凝血（血栓形成）。（换行）与原发性血小板增多症相关的MPLgene突变改变血小板生成素受体蛋白中的单个蛋白质构建块（氨基酸）。该病的遗传形式称为家族性原发性血小板增多症，是由MPLgene突变引起的，该突变导致蛋白质505位的氨基酸天冬酰胺取代了氨基酸丝氨酸（写为Ser505Asn或S505N）。不在家庭中发生的原发性血小板增多症（偶发性原发性血小板增多症）与MPLgene突变相关，该突变导致515位氨基酸色氨酸被另一个氨基酸（最常见的是亮氨酸）取代。这些突变通常称为W515突变。（换行）位置505或515处的氨基酸变化导致血小板生成素受体蛋白不断打开（组成性激活），这在原发性血小板增多症中导致异常巨核细胞的过量产生和血小板数量的增加。过量的血小板可引起血栓形成，导致原发性血小板增多症的许多体征和症状。"}, {"name": "原发性骨髓纤维化", "detail": "在一些患有原发性骨髓纤维化的人中已经鉴定出MPLgene的几个突变。这种情况的特征是骨髓（产生血细胞的组织）中的瘢痕组织（纤维化）。(换行)像原发性血小板增多症一样，原发性骨髓纤维化与称为W515突变的MPLgene突变有关。这些突变导致组成型活化的血小板生成素受体蛋白，导致异常巨核细胞的过量产生。这些巨核细胞刺激其他细胞释放胶原蛋白，胶原蛋白通常为骨髓中的细胞提供结构支持，但在原发性骨髓纤维化中引起瘢痕组织形成。由于纤维化，骨髓不能产生足够的正常血细胞，导致病情的体征和症状。（换行）不知道相同的gene突变如何与不同的conditions相关联。"}, {"name": "其他疾病", "detail": "MPLgene中的突变会导致一种罕见的疾病，称为先天性巨核细胞血小板减少症（CAMT）。这种情况始于婴儿期，其特征是巨核细胞（巨核细胞减少症）和血小板（血小板减少症）数量少。CAMT可能导致骨髓功能受损，称为骨髓衰竭。（换行）参与CAMT的MPLgene突变导致功能降低或无功能的血小板生成素受体蛋白。没有血小板生成素受体功能的人患有称为CAMT I的严重疾病。具有一些剩余血小板生成素受体功能的人患有称为CAMT II的较轻形式的疾病。"}]}' WHERE id = 4045;
UPDATE genes SET GHR_metadata_ch = '{"gene": "RPS24", "conditions": [{"name": "钻石-布莱克凡贫血", "detail": "在患有Diamond-Blackfan贫血的个体中至少鉴定出六个RPS24gene突变。据信这些突变影响RPS24蛋白的稳定性或功能。研究表明，缺乏功能性核糖体蛋白可能会增加骨髓中造血细胞的自我破坏，导致红细胞数量减少（贫血）。细胞分裂的异常调节或凋亡的不适当触发可能会导致其他健康问题和异常的身体特征，从而影响某些Diamond-Blackfan贫血患者。"}]}' WHERE id = 4094;
UPDATE genes SET GHR_metadata_ch = '{"gene": "FGA", "conditions": [{"name": "先天性纤维蛋白原血症", "detail": "FGAgene的突变可导致先天性纤维蛋白原血症，这种情况由于血液中不存在纤维蛋白原蛋白而导致过度出血。导致这种情况的大多数FGAgene突变都会导致蛋白质形成（mRNA）异常简短的蓝图。如果有任何纤维蛋白原A？链产生，它不起作用。因为这种情况发生在FGAgene的两个拷贝都被改变时，完全没有功能性纤维蛋白原a？链。没有A？亚基，纤维蛋白原蛋白未组装，导致不存在纤维蛋白。因此，血液凝块不会因受伤而形成，导致先天性纤维蛋白原血症患者出血过多。"}, {"name": "其他疾病", "detail": "FGAgene一个或两个拷贝中的突变可引起其他出血性疾病，称为低纤维蛋白原血症，低纤维蛋白原血症或低纤维蛋白原血症。(换行)低纤维蛋白原血症是以血液中纤维蛋白原水平降低为特征的病症。这种情况是由减少但不消除纤维蛋白原Aβ产生的突变引起的？链。低纤维蛋白原血症患者可能会出现从轻度到重度的出血问题。通常，血液中纤维蛋白原越少，出血问题越严重。(换行)纤维蛋白原血症是以功能异常的纤维蛋白原为特征的病症，尽管蛋白质以正常水平存在。这种情况通常是由改变纤维蛋白原aβ中单个蛋白质结构单元（氨基酸）的突变引起的？链。这些突变会改变纤维蛋白原蛋白的功能，并根据功能变化而导致过度出血或异常凝血（血栓形成）。(换行)低纤维蛋白原血症是以血液中异常功能的纤维蛋白原蛋白水平低为特征的病症。如在纤维蛋白原血症中，这种情况可导致过度出血或血栓形成。FGAgene中的（换行）突变也导致称为遗传性肾淀粉样变性的病症的一些情况，其导致肾（肾）功能受损并导致肾衰竭。这种情况的特征是肾脏中称为淀粉样蛋白沉积物的蛋白质团块的积累。当这种情况是由FGAgene突变引起的时，淀粉样沉积物由异常的纤维蛋白原Aβ组成？链蛋白，这种情况有时称为纤维蛋白原淀粉样变性。这种情况下涉及的突变通常会改变纤维蛋白原aβ中的单个氨基酸？链，似乎不影响纤维蛋白原的凝血活性。"}]}' WHERE id = 4127;
UPDATE genes SET GHR_metadata_ch = '{"gene": "EFEMP2", "conditions": [{"name": "皮肤松弛", "detail": "在角质层松弛的人中，EFEMP2gene至少鉴定出三个突变。EFEMP2突变会导致一种称为I型常染色体隐性角质松弛的疾病，其特征是皮肤松弛和下垂，称为肺气肿的肺部疾病以及涉及心脏和血管的严重异常。EFEMP2gene中的（换行）突变可能会阻止细胞产生任何功能性fibulin-4。没有这种蛋白质，弹性纤维就不能正常地组装在细胞外基质中。正常弹性纤维的缺乏会削弱皮肤，血管，肺和其他器官的结缔组织。结缔组织中的这些缺陷是角质层松弛的主要特征。"}]}' WHERE id = 4197;
UPDATE genes SET GHR_metadata_ch = '{"gene": "REN", "conditions": [{"name": "REN相关的肾脏疾病", "detail": "已经发现RENgene中至少有四个突变会引起REN相关的肾脏疾病，在这种情况下，肾脏变得不太能够过滤体液和体内废物，从而导致肾衰竭。患有这种情况的个体在每个细胞中都有一个RENgene的突变拷贝和一个正常拷贝。与REN相关的肾脏疾病有关的突变会改变或去除肾素蛋白中的蛋白质结构单元（氨基酸）。这些变化发生在称为信号序列的蛋白质区域中，并且它们损害肾素的正常加工。异常蛋白质对通常产生肾素的肾细胞有毒。产生肾素的细胞逐渐死亡，这破坏了肾素-血管紧张素系统并导致进行性肾病。"}, {"name": "肾小管发育不全", "detail": "已发现RENgene中至少有11个突变会导致严重的肾脏疾病，称为肾小管发育不全。这种情况的特征是出生前肾脏发育异常，无法产生尿液（无尿）和严重的低血压（低血压）。这些问题导致羊水减少（羊水过少），从而导致一系列先天缺陷，称为波特序列。（换行）肾小管发育不全可能由肾素-血管紧张素系统中涉及的任何gene的两个拷贝中的突变引起。导致这种疾病的大多数RENgene突变都会阻止任何肾素蛋白的产生，从而导致无功能的肾素-血管紧张素系统。没有这个系统，肾脏就无法控制血压。由于血压低，血液流量减少（低灌注），并且在胎儿发育期间身体没有获得足够的氧气。结果，肾脏发育受损，导致肾小管发育不全的特征。"}]}' WHERE id = 4199;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SLC3A1", "conditions": [{"name": "胱氨酸尿症", "detail": "已发现SLC3A1gene中的120多个突变会引起胱氨酸尿症。这些突变中的许多改变单个DNA构建块（核苷酸）或插入或删除SLC3A1gene中的少量核苷酸。这些变化导致功能异常的转运蛋白复合物，导致某些氨基酸浓缩在尿液中。胱氨酸是唯一在膀胱或肾脏中形成晶体和结石的氨基酸，可导致胱氨酸尿症的体征和症状。"}, {"name": "其他疾病", "detail": "一些患有胱氨酸尿症的人具有较大的DNA缺失，不仅去除了SLC3A1gene，而且去除了一个或多个邻近gene。具有这些大的DNA缺失的个体具有胱氨酸尿症的体征和症状，但他们也可以具有其他特征。（换行）SLC3A1gene和邻近的PREPLgene的缺失导致肌张力低下-胱氨酸尿症综合征。除胱氨酸尿症外，患有这种疾病的人的肌张力低（肌张力低下）且喂养不良，通常在儿童早期就可以改善。他们还可能有眼睑下垂（上睑下垂），头部拉长（头颅畸形）和轻度智力障碍。大多数患有这种疾病的人身材矮小。（换行）SLC3A1gene，PREPLgene和C2orf34gene的缺失导致非典型性低渗-胱氨酸尿症综合征。除了肌张力低下-胱氨酸尿症的症状外，非典型形式的个体在智力和运动技能的发展中也有轻度至中度的延迟（精神运动延迟）。（换行）SLC3A1gene，PREPLgene，C2orf34gene和PPM1Bgene的缺失导致2p21缺失综合征。除了先前综合征的所有症状外，患有2p21缺失综合征的人出生后不久就会发作，中度至重度的精神运动延迟以及细胞从中获取大量能量的过程中的损伤（氧化磷酸化）。患有这种情况的人通常具有特征性的面部外观，具有突出的前额，长长的睫毛，平坦的鼻梁和异常弯曲的耳朵。"}]}' WHERE id = 4203;
UPDATE genes SET GHR_metadata_ch = '{"gene": "STXBP1", "conditions": [{"name": "STXBP1脑病伴癫痫", "detail": "已发现STXBP1gene中至少有85个突变会引起癫痫性STXBP1脑病。这种情况的特征是从婴儿期开始反复发作（癫痫），脑功能异常（脑病）和智力残疾。突变可以改变语法结合蛋白1的结构，导致异常短的蛋白，或添加或删除蛋白的一小部分。（换行）引起癫痫的STXBP1脑病的gene突变减少了该gene产生的功能性语法结合蛋白1的数量。这种蛋白质的缺乏会损害蛋白质复合物的形成，从而使囊泡融合并从神经元释放神经递质。神经递质水平的变化可导致神经元的不受控制的激活（兴奋），从而引起癫痫发作。这种改变的神经元活动似乎不会损害神经元的发育或存活；在这种情况下脑病和其他神经系统问题的原因尚不清楚。"}, {"name": "其他疾病", "detail": "已经发现STXBP1gene中的至少三个突变引起称为共济失调-震颤-迟缓综合征的病症。这种情况的特征是难以协调运动（共济失调），不自主的颤抖（震颤），智力残疾和发育迟缓。与患有癫痫的STXBP1脑病（如上所述）不同，患有共济失调-震颤-迟缓综合征的人不会发作。目前尚不清楚为什么STXBP1gene突变会在某些人而不是其他人中引起癫痫发作。"}]}' WHERE id = 4254;
UPDATE genes SET GHR_metadata_ch = '{"gene": "CLCN1", "conditions": [{"name": "先天性肌强直", "detail": "已在先天性肌强直患者中鉴定出CLCN1gene突变，该疾病的特征是肌肉问题，包括肌肉僵硬和其他肌肉问题。CLCN1突变会导致贝克尔病（一种常染色体隐性遗传形式）和汤姆森病（一种先天性肌强直的常染色体显性遗传形式）。"}]}' WHERE id = 4264;
UPDATE genes SET GHR_metadata_ch = '{"gene": "TNXB", "conditions": [{"name": "埃勒斯-当洛二氏综合症", "detail": "TNXBgene的突变在所有称为超活动型的Ehlers-Danlos综合征病例中所占的比例很小。Ehlers-Danlos综合征是一组影响支持皮肤，骨骼，血管以及许多其他器官和组织的结缔组织的疾病。超活动型的特征是关节运动范围异常大（活动过度）。导致这种形式的疾病的突变发生在每个细胞中TNXBgene的一个拷贝中。这些突变减少了细胞产生的功能性腱糖蛋白X的数量，从而降低了腱糖蛋白X与胶原蛋白和弹性纤维相互作用的能力。这些变化削弱了身体许多部位的结缔组织，从而导致了Ehlers-Danlos综合征的过度活动型的体征和症状。(换行)一些患有良性关节过度活动综合征（BJHS）的人也会减少腱生蛋白X蛋白的量，尽管在这些个体中未发现TNXBgene突变。这种情况会导致活动过度和慢性关节疼痛。良性关节过度活动综合征的体征和症状与过度活动型Ehlers-Danlos综合征的体征和症状明显重叠。研究表明它们可能是相同病症的形式。(换行)一些患有Ehlers-Danlos综合征的人每个细胞中TNXBgene的两个拷贝都有突变。这些人患有一种通常被称为古典型的疾病。它的体征和症状类似于经典类型的Ehlers-Danlos综合征，包括活动过度和皮肤柔软，高度伸展（弹性）和脆弱。但是，受影响的个体没有这种类型的异常疤痕。TNXBgene的两个拷贝中都发生的突变会阻止任何腱糖蛋白X蛋白的产生。这种蛋白质的损失严重破坏了胶原纤维和弹性纤维的组织，这显着削弱了结缔组织。"}]}' WHERE id = 4265;
UPDATE genes SET GHR_metadata_ch = '{"gene": "FGG", "conditions": [{"name": "先天性纤维蛋白原血症", "detail": "FGGgene的突变可导致先天性纤维蛋白原血症，这种情况由于血液中不存在纤维蛋白原蛋白而导致过度出血。导致这种情况的大多数FGGgene突变都会导致蛋白质形成（mRNA）异常简短的蓝图。如果有纤维蛋白原？链产生，它不起作用。因为这种情况发生在FGGgene的两个拷贝都被改变时，完全没有功能性纤维蛋白原？链。没有？亚基，纤维蛋白原蛋白未组装，导致不存在纤维蛋白。结果，血液凝块不会因受伤而形成，导致先天性纤维蛋白原血症患者出血过多。"}, {"name": "其他疾病", "detail": "FGGgene的一个或两个拷贝中的突变可引起其他出血性疾病，称为低纤维蛋白原血症，低纤维蛋白原血症或低纤维蛋白原血症。(换行)低纤维蛋白原血症是以血液中纤维蛋白原水平降低为特征的病症。这种情况是由减少但不消除纤维蛋白原产生的突变引起的？链。低纤维蛋白原血症患者可能会出现从轻度到重度的出血问题。通常，血液中纤维蛋白原越少，出血问题越严重。(换行)纤维蛋白原血症是以功能异常的纤维蛋白原为特征的病症，尽管蛋白质以正常水平存在。这种情况通常是由改变纤维蛋白原中单个蛋白质结构单元（氨基酸）的突变引起的？链。这些突变会改变纤维蛋白原蛋白的功能，并根据功能变化而导致过度出血或异常凝血（血栓形成）。(换行)低纤维蛋白原血症是以血液中异常功能的纤维蛋白原蛋白水平低为特征的病症。如在纤维蛋白原血症中，这种情况可导致过度出血或血栓形成。"}]}' WHERE id = 4396;
UPDATE genes SET GHR_metadata_ch = '{"gene": "NEU1", "conditions": [{"name": "唾液酸", "detail": "At least 42 mutations in the NEU1 gene have been found to cause sialidosis. Most of these mutations change single protein building blocks (amino acids) used to make the NEU1 enzyme. Mutations in the NEU1 gene lead to a shortage (deficiency) of the NEU1 enzyme. When this enzyme is lacking, large molecules that are usually broken down by the NEU1 enzyme accumulate inside lysosomes. Conditions such as sialidosis that cause large molecules to build up inside lysosomes are called lysosomal storage disorders. Mutations that eliminate NEU1 enzyme activity cause more severe signs and symptoms than those that result in some functional enzyme. It is unclear exactly how the accumulation of large molecules within lysosomes leads to the signs and symptoms of sialidosis.\r"}]}' WHERE id = 4410;
UPDATE genes SET GHR_metadata_ch = '{"gene": "RANBP2", "conditions": [{"name": "急性坏死性脑病1型", "detail": "已发现RANBP2gene中至少有三个突变会增加发生1型急性坏死性脑病的风险。这种情况，也称为感染诱发的急性脑病3或IIAE3的易感性，是一种罕见的脑部疾病（脑病），发生在病毒感染如流感后。这些突变改变了RANBP2蛋白中的单个蛋白结构单元（氨基酸），导致产生的蛋白由于形状改变或无法到达需要的核孔而无法正常运行。(换行)这些RANBP2gene突变本身不会引起健康问题；目前还不清楚他们如何参与病毒感染引发神经损伤的过程。虽然患有1型急性坏死性脑病的个体经常损伤并经常失去其血脑屏障，但RANBP2gene突变的影响尚不清楚。研究人员怀疑，发生急性坏死性脑病1型的个体在感染时会产生太多称为细胞因子的免疫系统蛋白。过量的细胞因子导致炎症延长，尽管改变的RANBP2蛋白在该过程中的作用尚不清楚。炎症是对损伤和外来入侵者（如病毒）的正常免疫系统反应。然而，过度的炎症会损害身体的许多组织。另外，当大量存在时，某些细胞因子可能对神经细胞有毒。怀疑改变的RANBP2蛋白和异常免疫反应的组合在个体对1型急性坏死性脑病复发发作的易感性中起作用。在1型急性坏死性脑病患者中，在脑或脊髓（中枢神经系统）的神经细胞中未发现病毒，因此免疫反应而非感染本身可能是神经系统症状的原因。和症状。"}, {"name": "癌症", "detail": "涉及RANBP2gene的遗传异常可导致称为炎性肌纤维母细胞瘤的异常生长。这种类型的肿瘤可以是非癌性的（良性的）或癌性的（恶性的），并且可以在整个身体的组织中发现。导致这种肿瘤的gene变化是体细胞的，这意味着它们是在一个人的一生中获得的，并且仅存在于某些细胞中。这些遗传异常导致2号染色体内的重排（易位），该重排将RANBP2gene的一部分与另一个称为ALK的gene融合。这两个gene的融合导致产生异常蛋白质，其促进不受控制的细胞生长，这可导致肿瘤的形成。"}]}' WHERE id = 4438;
UPDATE genes SET GHR_metadata_ch = '{"gene": "GPR143", "conditions": [{"name": "眼白化病", "detail": "在最常见的眼白化病患者中已鉴定出60多个GPR143突变，称为Nettleship跌倒类型或1型。大多数突变会改变GPR143蛋白的大小或形状。这些遗传变化通常会阻止异常蛋白质永远到达黑素体，在那里需要控制这些含色素结构的生长。在其他情况下，GPR143蛋白通常会到达黑素体，但突变会阻止该蛋白与其信号通路中的其他分子相互作用。如果没有功能性GPR143蛋白，皮肤细胞和视网膜中的黑素体可能会异常生长。目前尚不清楚这些巨大的黑素体（大黑素体）与眼白化病患者的视力丧失和其他眼部异常有何关系。(换行)大多数形式的白化病是由细胞内黑色素减少引起的。研究人员继续研究为什么当视网膜中的细胞似乎含有大量黑色素时会发生眼白化病。这种色素可能会集中到一些异常的大黑素体中，而不是均匀分布在细胞内许多正常大小的黑素体中。其他研究可能有助于阐明黑素体，黑色素分布与眼白化病特征性色素沉着水平降低之间的关系。"}]}' WHERE id = 4453;
UPDATE genes SET GHR_metadata_ch = '{"gene": "NAGLU", "conditions": [{"name": "黏多醣贮积症III型", "detail": "已发现NAGLUgene中至少118个突变会导致IIIB型粘多糖贮积病（MPS IIIB）。这些突变大多数会改变gene中的单个DNA结构单元（核苷酸）。所有引起MPS IIIB的突变都会降低或消除α-N-乙酰氨基葡萄糖苷酶的功能。（换行）缺乏α-N-乙酰氨基葡萄糖苷酶活性破坏了称为硫酸乙酰肝素的GAG子集的分解。结果，部分分解的硫酸乙酰肝素积聚在溶酶体内。研究人员认为，GAG的积累会干扰溶酶体内其他蛋白质的功能，并破坏细胞的正常功能。尚不清楚为什么硫酸乙酰肝素的积累主要影响MPS IIIB中的中枢神经系统。"}]}' WHERE id = 4476;
UPDATE genes SET GHR_metadata_ch = '{"gene": "MOCS1", "conditions": [{"name": "钼辅助因子缺乏", "detail": "MOCS1gene突变会导致一种称为钼辅因子缺乏症的疾病。这种疾病的特征是从生命早期开始发作，脑功能障碍随着时间的推移而恶化（脑病）；这种情况通常在儿童早期就致命。已发现MOCS1gene中至少有32个突变会导致某种形式的疾病，称为a型或互补组a。这是该病最常见的形式，约占病例的三分之二。（换行）与钼辅因子缺乏有关的MOCS1gene突变可能会消除MOCS1A，MOCS1B或两者的功能，尽管在极少数情况下不太严重，某些蛋白质功能可能会保留。如果没有这些蛋白质中的一种或两种的活性，则钼辅因子的生物合成就会受到损害。辅因子的丧失阻碍了依赖它的代谢酶的功能。(换行)由此导致的酶活性丧失导致某些化学物质的积累，包括亚硫酸盐，S-硫代半胱氨酸，黄嘌呤和次黄嘌呤，以及低水平的另一种化学物质。称为尿酸。（测试这些化学物质可以帮助诊断这种情况。）通常由亚硫酸盐氧化酶分解的亚硫酸盐是有毒的，特别是对大脑有毒。研究人员认为，亚硫酸盐（可能还有其他化学物质）异常高水平造成的损害会导致脑病，癫痫发作和钼辅因子缺乏症的其他特征。"}]}' WHERE id = 4486;
UPDATE genes SET GHR_metadata_ch = '{"gene": "POR", "conditions": [{"name": "细胞色素P450氧化还原酶缺乏症", "detail": "已发现PORgene中的50多个突变会导致细胞色素P450氧化还原酶缺乏。这种情况会导致荷尔蒙变化，从而影响生殖系统，骨骼和身体其他部位的发育。该疾病影响出生前和青春期的性发育，严重的病例还以骨骼异常为特征。(换行)导致细胞色素P450氧化还原酶缺乏的大多数突变会改变细胞色素P450氧化还原酶中的单个蛋白质结构单元（氨基酸）。PORgene突变会显着降低酶的活性，从而破坏类固醇激素的产生。性激素如睾酮和雌激素的变化导致性发育问题。(换行)细胞色素P450氧化还原酶活性降低也会破坏胆固醇的产生，这可能会损害严重细胞色素P450氧化还原酶缺乏症患者的正常骨形成。研究表明，称为视黄酸的分子在严重病例中发现的骨骼异常中也起作用。视黄酸的分解需要细胞色素P450氧化还原酶；如果缺乏细胞色素P450氧化还原酶阻止视黄酸分解，则由此产生的过量该分子可刺激骨骼的异常生长和融合。(换行)目前尚不清楚PORgene的突变是否会影响肝脏处理药物的方式。这种酶在药物代谢中的作用是一个活跃的研究领域。"}]}' WHERE id = 4582;
UPDATE genes SET GHR_metadata_ch = '{"gene": "FAS", "conditions": [{"name": "自身免疫性淋巴组织增生综合征", "detail": "在患有称为自身免疫性淋巴组织增生综合症（ALPS）的免疫系统疾病的人中，已经鉴定出FASgene的100多个突变。ALPS的特征是产生异常大量的免疫系统细胞（淋巴细胞），导致淋巴结肿大（淋巴结肿大），肝脏（肝肿大）和脾脏（脾肿大）。自身免疫性疾病在ALPS中也很常见，其中免疫系统发生故障并攻击人体自身的组织和器官。患有ALPS的人患免疫系统细胞（淋巴瘤）癌症的风险增加，并且患其他癌症的风险也可能增加。(换行)当免疫系统被激活以抵抗感染时，产生大量淋巴细胞。通常，这些淋巴细胞在不再需要时会发生凋亡。FASgene突变导致异常三聚体，干扰细胞凋亡的启动。过量的淋巴细胞积聚在身体的组织和器官中，并经常开始攻击它们，导致自身免疫性疾病。干扰细胞凋亡允许细胞在没有控制的情况下繁殖，导致淋巴瘤和其他癌症发生在患有这种疾病的人身上。"}, {"name": "癌症", "detail": "研究已将某些FASgene变异与罹患癌症（包括肺癌，乳腺癌和食道癌）的风险增加相关联。研究人员认为，这些变异可能会影响启动细胞凋亡的信号传导，增加细胞失控并导致癌症的风险。"}]}' WHERE id = 4588;
UPDATE genes SET GHR_metadata_ch = '{"gene": "HADHA", "conditions": [{"name": "长链3-羟酰基辅酶A脱氢酶缺乏症", "detail": "研究人员已经鉴定出几种导致长链3-羟酰基辅酶A脱氢酶（LCHAD）缺乏的HADHAgene突变。这些突变降低了线粒体三功能蛋白的长链3-羟酰基-CoA脱氢酶活性。（蛋白质的其他酶活性保持正常或接近正常。）许多HADHA突变会改变用于制造α亚基的蛋白质构件（氨基酸）之一。最常见的突变是在α亚基的474位用氨基酸谷氨酰胺取代氨基酸谷氨酸。此突变写为Glu474Gln或E474Q。Glu474Gln突变和其他氨基酸置换可能会改变α亚基的结构，从而阻止其正常运行。其他HADHA突变会导致α亚基异常短而无功能的版本。(换行)由于功能性α亚基的缺乏（缺乏），长链脂肪酸不能被代谢和加工。结果，这些脂肪酸没有转化为能量，这可能导致LCHAD缺乏的某些特征，例如缺乏能量（嗜睡）和低血糖（低血糖）。长链脂肪酸或部分代谢的脂肪酸也可能积聚并损害眼后部（视网膜）的肝脏，心脏，肌肉和光敏组织。这种异常堆积导致LCHAD缺乏的其他体征和症状。"}, {"name": "线粒体三功能蛋白缺乏症", "detail": "研究人员已经鉴定出几种导致线粒体三功能蛋白缺乏的HADHAgene突变。这些突变降低了线粒体三功能蛋白的所有三种酶活性。这些突变中的一些导致异常短的，无功能的α亚基，并导致线粒体三功能蛋白水平降低。其他突变将α亚基中的一个氨基酸替换为另一个氨基酸，这可能会改变亚基的结构并破坏酶复合物的所有三个功能。(换行)当线粒体三功能蛋白活性丧失时，长链脂肪酸不能被代谢和加工。结果，这些脂肪酸不会转化为能量，这可能导致这种疾病的某些特征，例如嗜睡和低血糖。长链脂肪酸或部分代谢的脂肪酸可能积聚在组织中并损害肝脏，心脏和肌肉。这种异常积聚导致线粒体三功能蛋白缺乏的其他体征和症状。"}, {"name": "其他疾病", "detail": "在少数情况下，HADHA突变似乎会增加女性在怀孕期间发生两种严重肝病的风险，称为妊娠急性脂肪肝（AFLP）和HELLP综合征。AFLP始于腹痛，可迅速发展为肝功能衰竭。HELLP代表溶血（红细胞分解），肝酶水平升高和血小板减少（与凝血有关的细胞碎片）。(换行)如果女性在HADHAgene的一个拷贝中具有突变并且她携带的胎儿具有两个拷贝的HADHA突变，特别是Glu474Gln突变，则女性更可能患有AFLP或HELLP综合征。关于怀孕期间母亲的HADHA突变与肝脏问题之间的关系知之甚少。一种可能性是胎儿或胎盘产生的部分代谢的长链脂肪酸积聚在母亲体内，对肝脏有毒。在极少数的母亲肝病病例中，母亲有一个拷贝的HADHAgene改变，胎儿不受影响。在这些情况下，母亲的HADHA突变在肝病中的作用尚不清楚。"}]}' WHERE id = 4684;
UPDATE genes SET GHR_metadata_ch = '{"gene": "LAMA3", "conditions": [{"name": "交界性大疱性表皮松解症", "detail": "在交界性大疱性表皮松解症（JEB）患者中已鉴定出LAMA3gene的30多个突变。该疾病的更严重形式称为Herlitz JEB，通常是由阻止任何功能性层粘连蛋白332产生的突变引起的。这些突变中的大多数会在制作所有三个版本的α亚基的说明中导致过早的终止信号，从而破坏层粘连蛋白332的组装。没有功能性层粘连蛋白332，表皮仅微弱地连接到皮肤的下层。摩擦或其他轻微创伤（例如摩擦或刮擦）可导致皮肤层分离，导致形成水泡。患有Herlitz JEB的婴儿会出现广泛的水疱，从而导致危及生命的并发症。（换行）其他LAMA3gene突变导致较温和形式的交界性大疱性表皮松解症，非Herlitz JEB。这些突变中的一些改变了层粘连蛋白332的α亚基中的单个蛋白质结构单元（氨基酸）。其他人添加或删除α亚基中的少量氨基酸或改变gene指令用于制备亚基的方式。导致较轻的JEB病例的遗传变化通常会导致层粘连蛋白332蛋白的产生，该蛋白保留了其某些功能。受影响的人会出现水泡，但可能仅限于手，脚，膝盖和肘部。"}, {"name": "喉-甲状腺-皮肤综合征", "detail": "已经发现LAMA3gene中的至少两个突变引起喉-皮肤-皮肤（LOC）综合征。这种罕见的疾病的特征是慢性皮肤溃疡和广泛形成的称为肉芽组织的红色凹凸不平的斑块。身体不同部位的肉芽组织积聚会导致严重的并发症，包括视力丧失和气道阻塞。LOC综合征的其他特征包括指甲畸形和牙齿异常。（换行）LOC综合征中涉及的突变导致层粘连蛋白332的α-3a亚基的异常短版本；alpha-3b1和alpha-3b2是正常的。含有改变的α亚基的层粘连蛋白不能有效地将表皮附着到皮肤的下层或调节伤口愈合。层粘连蛋白332的这些异常会导致慢性皮肤溃疡和肉芽组织过度生长，这是LOC综合征的特征。层粘连蛋白332无法执行其其他功能会导致这种情况下发生的指甲和牙齿异常。（换行）LOC综合征通常被认为是交界性大疱性表皮松解症的亚型（如上所述）。研究人员建议，仅影响α亚基的alpha-3a版本的LAMA3gene突变会导致LOC综合征，而也影响α亚基其他版本的突变会导致交界性表皮松解性大疱。"}]}' WHERE id = 4716;
UPDATE genes SET GHR_metadata_ch = '{"gene": "OPRM1", "conditions": [{"name": "阿片成瘾", "detail": "已经研究了OPRM1gene的常见变异（多态性）作为阿片类药物成瘾的危险因素。阿片类药物成瘾是一种长期（慢性）疾病，其特征是强烈的，有时无法控制的使用阿片类药物的冲动。阿片类药物成瘾对健康，社会和经济都有重大影响。(换行)研究得最好的OPRM1gene多态性会改变单个蛋白质构建块（氨基酸）的特定位置？阿片受体蛋白。具体而言，它在位置118处用氨基酸甘氨酸（G）替换了氨基酸丙氨酸（A），写为Ala118Gly或A118G。（此多态性也用唯一编号rs1799971标识。）A118G多态性可能会影响？阿片受体存在于神经元周围的膜中，以及受体传递化学信号的能力。关于这种多态性与阿片类药物成瘾的关联的研究结果好坏参半。一些研究表明，用甘氨酸（G）代替丙氨酸（A）会增加缓解疼痛所需的阿片类药物的量，并增加阿片类药物成瘾的风险。但是，其他研究发现多态性与阿片类药物成瘾之间没有关联，还有其他研究报道甘氨酸（G）型多态性的风险较低。(换行)A118G多态性的甘氨酸（G）版本在某些人群（例如亚洲或欧洲血统的人）中比在其他人群（例如非洲或非裔美国人血统的人）中更为常见。这些差异可能有助于解释为什么研究其在阿片成瘾中的作用的研究结果有矛盾的结果。研究人员建议，需要更多的人进行研究，以确认这种多态性与任何特定人群中阿片类药物成瘾风险之间的关联。(换行)除A118G以外，OPRM1gene的常见变异也与特定人群（例如汉族，欧美和非裔美国人）的阿片类药物成瘾有关。A118G多态性和其他常见变异是阿片类药物成瘾的许多可疑危险因素之一。健康，社会，经济和生活方式因素的组合可能与遗传因素相互作用，以确定个体患上这种复杂疾病的风险。"}, {"name": "其他疾病", "detail": "OPRM1gene的变异与对几种其他物质的成瘾有关。这个？阿片受体似乎在调节使用酒精，尼古丁和某些其他滥用药物所带来的快乐和回报方面发挥着关键作用。虽然这些物质不直接与？阿片受体，它们影响大脑中其他神经递质的水平，触发人体自身的内源性阿片类药物附着在受体上。这种相互作用开始了大脑中化学信号的级联，导致疼痛缓解和放松和快乐的感觉。(换行)与阿片类成瘾（如上所述）一样，大部分关于？阿片受体和其他成瘾性集中在A118G多态性上。关于这种多态性对尼古丁和酒精滥用的影响的研究结果不一致，不同的研究表明在118位甘氨酸（G）与丙氨酸（a）的风险增加，风险降低或无影响。一个人的地理和种族背景可能很重要，需要更大的研究来确定是否存在真正的关联。(换行)研究还发现，A118G多态性与多种其他特征之间存在关联，包括对身心疼痛的感知，对社会排斥的敏感性，对甜食和高脂食物的偏爱以及身体对压力的反应。目前尚不清楚这种遗传变异如何导致影响这些性状的大脑变化。（换行）OPRM1gene的另一个多态性，通常写为rs540825，与重度抑郁症患者中称为西酞普兰的药物的有效性有关。这种多态性改变了？阿片受体。具有这种多态性的重度抑郁症患者在用西酞普兰治疗时比没有多态性的患者更可能经历没有任何症状（缓解）的时期。"}]}' WHERE id = 4825;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SLC45A2", "conditions": [{"name": "眼皮肤白化病", "detail": "SLC45A2gene中的20多个突变与4型眼皮肤白化病有关。日本人群中最常见的SLC45A2突变会切换SLC45A2蛋白中的单个蛋白结构单元（氨基酸）。具体而言，此突变用蛋白质157位的氨基酸天冬酰胺（写为Asp157Asn或D157N）替换氨基酸天冬氨酸。其他突变，包括SLC45A2gene中单个氨基酸的变化以及遗传物质的缺失或插入，也已在全球多个人群中报道。该gene的突变降低或消除了SLC45A2蛋白在黑色素生成中的功能。因为这种蛋白质对于正常的色素沉着很重要，所以它的损失会导致皮肤，头发和眼睛的颜色变化以及4型眼皮肤白化病的特征性视力问题。"}]}' WHERE id = 4855;
UPDATE genes SET GHR_metadata_ch = '{"gene": "CTNNB1", "conditions": [{"name": "硬纤维瘤", "detail": "CTNNB1gene中的突变可引起一种侵袭性但非癌性（良性）生长，称为类胶质瘤。在所有非遗传性（散发性）类胶质瘤中约有85%发现CTNNB1gene突变。这些罕见的肿瘤来自结缔组织，结缔组织为骨骼，韧带和肌肉等结构提供了力量和灵活性。肿瘤常见于腹部，肩部，上臂或大腿。导致类胶质瘤的CTNNB1gene突变是体细胞性的，这意味着它们是在一个人的一生中获得的，仅存在于肿瘤细胞中。体细胞突变不是遗传的。（换行）引起类胶质瘤的CTNNB1gene突变通常发生在称为外显子3的gene区域。它们改变了β-连环蛋白中的单个蛋白质构建块（氨基酸）。这些突变导致异常稳定的β-catenin蛋白，当不再需要时就不会分解。结果，蛋白质在细胞内积累。过量的β-连环蛋白促进细胞不受控制的生长和分裂，从而形成类胶质瘤。"}, {"name": "毛细胞瘤", "detail": "CTNNB1gene的体细胞突变几乎存在于所有毛细胞瘤中，毛细胞瘤是一种与毛囊相关的良性皮肤肿瘤。（换行）在pilomatricomas中发现的CTNNB1gene突变被描述为功能获得性突变。它们会导致β-catenin蛋白始终打开（组成性激活），从而导致某些gene的异常激活。这些gene增加与毛囊基质相关的细胞的增殖和分化。细胞分裂过快，不受控制，导致毛细胞瘤的形成。(换行)几乎所有的pilomatricomas都是良性的，但是很小的百分比是癌性的（恶性的）。这种肿瘤的恶性形式被称为pilomatrix癌。像pilomatricomas一样，pilomatrix癌在CTNNB1gene中具有体细胞突变。目前还不清楚为什么这些肿瘤中有一些是癌性的，但大多数不是。"}, {"name": "产生醛固酮的腺瘤", "detail": "遗传学家庭参考提供有关产生醛固酮的腺瘤的信息。"}, {"name": "卵巢癌", "detail": "遗传学家庭参考提供有关卵巢癌的信息。"}, {"name": "其他癌症", "detail": "已经在几种其他类型的癌症中鉴定出CTNNB1gene的体细胞突变。这些包括结肠直肠癌，肝癌，甲状腺癌，卵巢癌，子宫内膜癌和皮肤癌，以及一种称为成神经管细胞瘤的脑肿瘤等。研究表明，当不再需要时，CTNNB1gene的功能获得性突变可防止β-catenin分解，从而使蛋白质在细胞内积累。过量的β-连环蛋白进入细胞核并促进细胞的不受控制的生长和分裂，从而使癌性肿瘤发展。（换行）由于CTNNB1gene的突变可导致正常细胞癌变，CTNNB1属于一类称为癌gene的gene。有时，其他癌gene的突变与CTNNB1gene突变一起发生，从而导致癌症。尚不清楚为什么CTNNB1gene的突变与几种不同类型的癌性和非癌性肿瘤有关。"}]}' WHERE id = 4857;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SAR1B", "conditions": [{"name": "乳糜微粒潴留病", "detail": "已发现SAR1Bgene中的10多个突变会引起乳糜微粒滞留病。这些突变大多数会改变SAR1B蛋白中的一个蛋白结构单元（氨基酸）。其他突变导致产生异常小版本的蛋白质，该蛋白质无法正常运行。突变破坏了SAR1B蛋白在细胞内转运乳糜微粒的能力，从而阻止了乳糜微粒释放到血液中。乳糜微粒的这种保留阻止了膳食脂肪和脂溶性维生素被身体使用，导致乳糜微粒保留疾病患者出现营养和发育问题。"}]}' WHERE id = 4867;
UPDATE genes SET GHR_metadata_ch = '{"gene": "MMAA", "conditions": [{"name": "甲基丙二酸血症", "detail": "已发现MMAAgene中的25个以上突变会引起甲基丙二酸血症，这种疾病的特征是进食困难，发育迟缓和长期健康问题。这些突变中的一些在gene中添加，删除或复制少量遗传物质。其他突变会改变用于制造MMAA蛋白的单个蛋白结构单元（氨基酸）。这些突变可导致产生不稳定的MMAA蛋白或异常小的，无功能的蛋白。尚不清楚异常的MMAA蛋白如何导致与甲基丙二酸血症相关的严重医学问题。研究表明，如果没有这种蛋白质的活性，可能无法正确制造AdoCbl。AdoCbl的缺乏会损害甲基丙二酰辅酶A突变酶的功能，从而导致某些蛋白质和脂质的不完全分解。这种缺陷使有毒化合物积聚在人体的器官和组织中。研究表明，缺乏AdoCbl会导致甲基丙二酰辅酶a突变酶功能受损，从而导致甲基丙二酸血症的体征和症状。"}]}' WHERE id = 4899;
UPDATE genes SET GHR_metadata_ch = '{"gene": "HADH", "conditions": [{"name": "3-羟酰基辅酶A脱氢酶缺乏症", "detail": "已经发现HADHgene中至少三个突变引起3-羟酰基-CoA脱氢酶缺乏。这些突变改变了用于制造3-羟酰基-CoA脱氢酶的单个蛋白质结构单元（氨基酸）。这些变化可能会改变酶的3维形状，从而损害其正常功能。缺乏（缺乏）功能性3-羟酰基辅酶a脱氢酶，中链和短链脂肪酸的(换行)代谢不正常。结果，这些脂肪酸不会转化为能量，这可能导致3-羟酰基辅酶a脱氢酶缺乏症的体征和症状，例如缺乏能量（嗜睡）和低血糖（低血糖）。未分解的中链和短链脂肪酸会积聚在组织中并损害肝脏，心脏和肌肉，从而引起严重的并发症。"}, {"name": "先天性高胰岛素血症", "detail": "遗传学家庭参考提供有关先天性高胰岛素血症的信息。"}, {"name": "其他疾病", "detail": "在少数家族性高胰岛素血症患者中已经报道了HADHgene的突变。这种疾病的特征是胰岛素水平异常高（高胰岛素血症）和血糖异常低（低血糖症）。(换行)研究人员已经鉴定出至少五个导致家族性高胰岛素血症的HADHgene突变。这些突变通过损害酶的功能或减少细胞中该酶的量而严重降低了3-羟酰基-CoA脱氢酶的活性。研究人员认为，胰腺中3-羟酰基辅酶A脱氢酶活性不足会导致家族性高胰岛素血症患者胰岛素分泌过多和低血糖。目前尚不清楚为什么引起家族性高胰岛素血症的HADHgene突变似乎仅影响胰腺。"}]}' WHERE id = 4906;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SGCD", "conditions": [{"name": "肢带型肌营养不良症", "detail": "在患有2F型肢带型肌营养不良症的人中，SGCDgene至少鉴定出14个突变。肢带型肌营养不良症是一组相关疾病，其特征是肌肉无力和消瘦，尤其是在肩膀，臀部和四肢。患有F型肢带型肌营养不良症的人在每个细胞的两个gene拷贝中均具有SGCD突变（常染色体隐性遗传模式）。(换行)少数2F型肢带型肌营养不良症患者出现了称为扩张型心肌病的心脏病。扩张型心肌病是一种心脏病，可扩大和削弱心脏（心脏）肌肉，阻止其有效抽血。扩张型心肌病进展迅速，可能危及生命。由影响肌糖蛋白复合物的gene突变引起的肢带型肌营养不良症（换行）形式称为肌糖蛋白病。SGCDgene突变可能会阻止肌糖蛋白复合物形成或结合并稳定肌营养不良蛋白复合物。这些复合物的问题会降低肌纤维的强度和弹性，并导致肢带型肌营养不良症的体征和症状。"}, {"name": "家族性扩张型心肌病", "detail": "遗传学家庭参考提供有关家族性扩张型心肌病的信息。"}, {"name": "其他疾病", "detail": "已经发现少数发展为扩张型心肌病而没有骨骼肌受累的人在每个细胞中的SGCDgene的一个拷贝中都有突变，这种遗传模式称为常染色体显性遗传。(换行)由于每个细胞中具有一个SGCDgene突变的其他个体的心肌均正常，因此一些研究人员质疑SGCDgene突变是否与常染色体显性扩张型心肌病有关。"}]}' WHERE id = 4919;
UPDATE genes SET GHR_metadata_ch = '{"gene": "TYRP1", "conditions": [{"name": "眼皮肤白化病", "detail": "已发现TYRP1gene中的少量突变会导致3型眼皮肤白化病。这种情况包括一种称为红褐色眼皮肤白化病的白化病，主要在南部非洲的黑皮肤人群中描述。受影响的人有红棕色的皮肤，姜或红头发，淡褐色或棕色的虹膜。已知有两个TYRP1突变会在非洲个体中引起这种形式的白化病。一个突变用过早停止蛋白质生产的信号替换了酪氨酸相关蛋白质1中的蛋白质结构单元（氨基酸）。此突变写为Ser166Ter或S166X，影响蛋白质166位的氨基酸丝氨酸。另一个突变，写为368delA，从TYRP1gene中删除了一个DNA构建块。在一些非非洲血统的受影响人群中，已经报道了该gene的其他改变。大多数TYRP1突变会导致酪氨酸酶相关蛋白1异常短而无功能的产生。由于该酶在正常色素沉着中起作用，因此其丢失会导致皮肤，头发和眼睛色素沉着的变化，这是眼皮肤白化病的特征。"}]}' WHERE id = 4921;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ADAMTS10", "conditions": [{"name": "威尔-马切萨尼综合症", "detail": "在Weill-Marchesani综合征患者中至少鉴定出ADAMTS10gene的五个突变。这些突变中的每一个都阻止细胞产生任何功能性ADAMTS10酶。研究人员推测，这种酶的缺失会破坏骨骼发育，导致身材矮小和手指和脚趾异常短（近距离接触）。ADAMTS10酶的缺乏也会干扰眼睛晶状体的发育和功能，从而导致眼睛异常和视力受损。此外，缺乏这种酶可能会破坏心脏的正常发育，导致Weill-Marchesani综合征患者偶尔出现心脏缺陷。"}]}' WHERE id = 4985;
UPDATE genes SET GHR_metadata_ch = '{"gene": "MID1", "conditions": [{"name": "Opitz G/BBB综合征", "detail": "已发现MID1gene中约90个突变引起Opitz G/BBB综合征。这种情况会导致身体中线出现一些异常，包括眼睛间距大（眼部玻璃体肥大），呼吸或吞咽困难，脑畸形，明显的面部特征和男性生殖器异常。大多数MID1gene突变会改变中线1蛋白中的单个蛋白结构单元（氨基酸）。其他突变会删除多个氨基酸，并可能导致异常短的蛋白质产生。这些突变导致中线-1功能下降，从而阻止蛋白质回收。结果，某些蛋白质不被回收，并且它们在细胞中积累。这种累积损害微管功能，导致细胞分裂和迁移问题。研究人员推测，改变的中线1蛋白会影响发育过程中细胞如何沿身体中线分裂和迁移，从而导致Opitz G/BBB综合征的特征。"}]}' WHERE id = 5032;
UPDATE genes SET GHR_metadata_ch = '{"gene": "MFSD8", "conditions": [{"name": "CLN7疾病", "detail": "已发现MFSD8gene中至少有30个突变引起CLN7疾病。这种情况通常始于儿童早期，丧失先前获得的技能（发育退化），反复发作（癫痫），肌肉抽搐（肌阵挛），难以协调运动（共济失调），言语障碍和视力丧失。心理功能和运动技能（例如坐着和走路）会随着年龄的增长而下降。患有CLN7疾病的人通常无法存活到十几岁。（换行）导致CLN7疾病的MFSD8gene突变可能导致产生结构或功能改变的蛋白质。一个MFSD8gene突变导致捷克共和国罗姆人中几乎所有CLN7疾病病例。该突变用MFSD8蛋白（写为T294K）中第294位的赖氨酸取代了蛋白质结构单元（氨基酸）苏氨酸。其他各种突变也会导致其他人群的疾病。(换行)尚不清楚改变的MFSD8蛋白如何导致CLN7疾病的严重神经系统特征。CLN7疾病的特征是溶酶体中蛋白质和其他物质的积累。这些积聚发生在全身的细胞中；然而，神经细胞似乎特别容易受到其影响。这些积累会导致细胞损伤，导致细胞死亡。患有CLN7疾病的个体在大脑的某些部位逐渐丧失神经细胞，这可能导致这种情况的体征和症状。"}]}' WHERE id = 5075;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SLC17A5", "conditions": [{"name": "唾液酸贮积病", "detail": "在SLC17A5gene中已鉴定出约20种引起唾液酸贮积病的突变。这些突变中的一些导致唾液酸不能正常发挥作用；其他防止唾液酸产生。在少数情况下，产生唾液酸但不能正确地将其引导至溶酶体膜。(换行)唾液酸贮积病有三种形式。主要在芬兰和瑞典人中发现的特定SLC17A5突变导致这种疾病的最不严重形式，称为Salla病。该突变用唾液酸蛋白（写为Arg39Cys或R39C）第39位的氨基酸半胱氨酸取代了蛋白质结构单元（氨基酸）精氨酸。其他对唾液酸蛋白功能具有更大破坏作用的SLC17A5gene突变会导致最严重的疾病形式，即婴儿游离唾液酸贮积病。被诊断患有中重度Salla病的个体在每个细胞中都有一个带有Salla病突变的SLC17A5gene拷贝，而该gene的第二个拷贝则具有更严重的突变。中重度Salla病的体征和症状的严重程度介于Salla病和婴儿游离唾液酸贮积病之间。（换行）减少或消除唾液酸活性的SLC17A5gene突变导致溶酶体中游离唾液酸的积累。目前尚不清楚这种积聚或大脑中唾液酸其他可能功能的破坏是如何导致唾液酸贮积病的特定体征和症状的。"}]}' WHERE id = 5078;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ZFP57", "conditions": [{"name": "6q24相关的短暂性新生儿糖尿病", "detail": "ZFP57gene的突变会导致约10%的6q24相关短暂性新生儿糖尿病病例，这是一种婴儿糖尿病。这种疾病是由6号染色体长臂（q）的一个称为6q24的区域中某些gene的过度活跃（过度表达）引起的。(换行)人们继承了两份gene，一份来自母亲，一份来自父亲。通常，每个gene的两个拷贝在细胞中都是活跃的或“打开的”。然而，在某些情况下，通常只打开两个拷贝中的一个。哪个拷贝是活跃的取决于原产地：一些gene通常只有当它们从一个人的父亲那里继承时才活跃；其他人只有从一个人的母亲那里继承时才活跃。这种现象被称为gene组印记。(换行)6q24区域包括父本表达的印迹gene，这意味着通常只有来自父亲的每个gene的拷贝才有活性。来自母亲的每个gene的拷贝通过甲基化而失活（沉默）。（换行）ZFP57gene突变导致称为印迹gene座（HIL）的低甲基化的gene沉默的普遍损伤，其影响许多印迹区域，包括6q24的印迹区域。6q24区域中与胰岛素分泌调节和细胞自我破坏（凋亡）有关的gene过度活跃，可能会减少分泌胰岛素的β细胞数量或损害其在受影响个体中的功能。胰岛素控制多少葡萄糖（一种糖）从血液传递到细胞转化为能量。胰岛素短缺导致与糖尿病相关的血糖控制受损。(换行)因为由ZFP57gene突变引起的HIL可以影响许多印迹gene的表达，所以这种机制可以解释在一些6q24相关的短暂性新生儿糖尿病患者中发生的额外健康问题，例如脑，心脏畸形，或肾脏。"}]}' WHERE id = 5089;
UPDATE genes SET GHR_metadata_ch = '{"gene": "LDB3", "conditions": [{"name": "肌原纤维肌病", "detail": "已经发现LDB3gene中至少三个突变引起肌原纤维肌病。这些突变改变了LDB3蛋白中的单个蛋白结构单元（氨基酸）。突变的LDB3蛋白与肌节中的其他肌肉蛋白聚集在一起形成团块（聚集体）。聚集体阻止这些蛋白质正常运作。功能异常的结蛋白蛋白无法与Z盘正确相互作用，从而导致肌节结构异常和肌原纤维形成问题。导致肌原纤维肌病的LDB3gene突变会损害肌纤维的功能，导致肌无力和这种情况的其他特征。"}, {"name": "家族性扩张型心肌病", "detail": "遗传学家庭参考提供有关家族性扩张型心肌病的信息。"}, {"name": "室致密化不全", "detail": "遗传学家庭参考提供有关左心室不紧致的信息。"}, {"name": "其他疾病", "detail": "LDB3gene的突变也会引起一种称为扩张型心肌病的心脏病。这种情况会扩大（扩张）并削弱心肌，阻止其有效地抽血。尽管心肌病是肌原纤维肌病的征兆，但某些由LDB3gene突变引起的扩张型心肌病病例与骨骼肌无力无关。研究人员已经确定LDB3gene中至少有两个突变会导致扩张型心肌病，而没有肌原纤维肌病的其他特征。这些突变写为Asp117Asn和Lys136Met，可改变LDB3蛋白中的单个氨基酸。研究人员不确定为什么LDB3gene中的某些突变会导致扩张型心肌病而不是肌原纤维肌病。"}]}' WHERE id = 5099;
UPDATE genes SET GHR_metadata_ch = '{"gene": "IFT122", "conditions": [{"name": "颅外胚层发育不良", "detail": "IFT122gene的突变可导致颅外胚层发育不良。这种情况的特征是头部拉长（头畸形），额头突出，面部特征其他独特；短骨头；以及某些称为外胚层组织的组织异常，包括牙齿，头发，指甲和皮肤。（换行）在颅外胚层发育不良患者中发现至少6个IFT122gene突变。这些突变减少了IFT122蛋白的数量或功能。IFT-A复合物这一成分的缺乏或活性降低会损害整个复合物的功能，从而破坏纤毛尖端蛋白质和物质的运输。结果，纤毛的组装和维护受到损害，这导致纤毛数量减少以及其形状和结构异常。尽管机制尚不清楚，但正常纤毛的丧失会阻碍骨骼和其他组织的正常发育，从而导致颅外胚层发育不良的特征。一些研究人员认为，鞭毛内运输受阻会阻止通过声波刺猬通路的信号传导，这可能会影响整个身体多个组织中的细胞生长和其他功能。"}]}' WHERE id = 5101;
UPDATE genes SET GHR_metadata_ch = '{"gene": "POLR1D", "conditions": [{"name": "Treacher Collins综合征", "detail": "在患有Treacher Collins综合征的人中，已经鉴定出POLR1Dgene中至少有20个突变，这种情况会影响骨骼和面部其他组织的发育。这些突变似乎改变了POLR1D蛋白的结构和功能，从而减少了细胞中功能性RNA聚合酶I和RNA聚合酶III的数量。因此，产生较少的rRNA。研究人员推测，rRNA的缺乏可能会触发某些参与面部骨骼和组织早期发育的细胞的自我破坏（凋亡）。异常的细胞死亡可能是Treacher Collins综合征中发现的面部发育特定问题的基础。但是，尚不清楚为什么减少rRNA的作用仅限于面部发育。"}]}' WHERE id = 5115;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SLC16A2", "conditions": [{"name": "Allan-Herndon-Dudley综合征", "detail": "在具有Allan-Herndon-Dudley综合征特征的人群中，已经鉴定出SLC16A2gene中至少有十二个突变。这些突变中的一些在gene中插入或删除遗传物质。其他突变会改变用于制造SLC16A2蛋白的单个蛋白结构单元（氨基酸）。所有这些遗传变化都会改变该蛋白的结构和功能，从而阻止其有效地将T3转运到神经细胞中。大脑某些部位缺乏这种关键激素会破坏正常的大脑发育，导致智力残疾和运动问题。(换行)如果T3不被神经细胞摄取，过量的这种激素会继续在血液中循环。循环T3的增加可能对某些器官如肝脏有毒。研究人员正在努力确定体内T3水平升高是否会导致Allan-Herndon-Dudley综合征的体征和症状。"}]}' WHERE id = 5127;
UPDATE genes SET GHR_metadata_ch = '{"gene": "HCCS", "conditions": [{"name": "线性皮肤缺损综合征的小眼症", "detail": "在患有线性皮肤缺损综合征的小眼症患者中，至少鉴定出三个HCCSgene突变。在受影响的个体中也发现了包括HCCSgene在内的遗传物质的缺失。HCCSgene突变导致无法执行其功能的全细胞色素c型合酶。包含HCCSgene的遗传物质的缺失阻止了该gene拷贝产生酶。功能性全细胞色素c型合酶的这种丧失可通过削弱细胞产生能量的能力来破坏细胞。另外，如果没有足够的全细胞色素c型合酶，受损的细胞可能无法进行凋亡。这些细胞可能会在称为坏死的过程中死亡，导致炎症并损害邻近的细胞。在早期发育过程中，这种扩散的细胞损伤可能导致具有线性皮肤缺陷综合症的小眼症的眼睛和皮肤异常。"}]}' WHERE id = 5179;
UPDATE genes SET GHR_metadata_ch = '{"gene": "PAX8", "conditions": [{"name": "先天性甲状腺功能减退症", "detail": "该gene中至少有15个突变会导致先天性甲状腺功能减退，这种疾病的特征是从出生开始甲状腺激素水平异常低。其他PAX8gene突变仅轻度降低甲状腺激素水平或无可检测作用。有时，同一家族成员中相同的突变具有不同的作用。(换行)大多数突变会改变用于制造PAX8蛋白的构建基块（氨基酸）之一。其他突变会破坏蛋白质的产生，从而导致PAX8蛋白质的异常小版本。几乎所有PAX8gene突变都会阻止PAX8蛋白有效结合DNA。一种突变改变了PAX8蛋白与其他转录因子之间的相互作用。结果，PAX8蛋白不能在调节某些gene的活性中发挥作用。(换行)PAX8gene突变患者的甲状腺异常小。这一发现表明，PAX8gene突变破坏了胚胎发育过程中甲状腺细胞的正常生长或存活。结果，甲状腺尺寸减小，可能无法产生正常量的甲状腺激素。由于PAX8gene突变引起的病例是由于甲状腺发育问题引起的，因此将其归类为甲状腺发育不全。"}, {"name": "肿瘤", "detail": "PAX8gene有时参与甲状腺肿瘤（肿瘤）的形成。在这些情况下，异常生长会影响称为滤泡性甲状腺细胞的特定细胞。其中一些生长称为滤泡性腺瘤，是非癌性的（良性的）。其他称为滤泡性癌的肿瘤是癌性的（恶性的）。在一些这些肿瘤中，染色体2上的PAX8gene与染色体3上的PPARGgene融合。当两条染色体的片段重排（易位）时，融合结果。目前尚不清楚融合的PAX8-PPARGgene如何影响滤泡性甲状腺细胞的生长，或者为什么一些肿瘤会癌变而另一些则是良性的。融合gene可能破坏细胞分裂的正常控制或触发促进肿瘤形成的新细胞活性。"}]}' WHERE id = 5211;
UPDATE genes SET GHR_metadata_ch = '{"gene": "WNT4", "conditions": [{"name": "苗勒氏发育不全和雄激素过多症", "detail": "已发现WNT4gene中至少有3个突变会引起苗勒氏发育不全和雄激素过多症，这种情况会影响女性的生殖系统。患有这种疾病的女孩和妇女通常子宫发育不全或缺失，月经不调。它们也可能具有异常高水平的雄激素，这可能导致痤疮和过多的面部毛发。（换行）涉及苗勒氏发育不全和雄激素过多症的WNT4gene突变改变了WNT4蛋白中的单个蛋白结构单元（氨基酸）。研究人员怀疑改变后的蛋白质不能像通常那样从细胞中释放出来；被捕获的蛋白质无法执行其通常的功能。WNT4失去调节可能会破坏女性生殖系统的发育并诱导雄激素的异常产生，从而导致苗勒氏发育不全和雄激素过多症。"}, {"name": "Dupuytren挛缩", "detail": "遗传学家庭参考提供有关Dupuytren挛缩的信息。"}, {"name": "其他疾病", "detail": "已经发现WNT4gene中的突变引起称为SERKAL（性逆转和肾，肾上腺和肺的异常发育）综合征的严重病症。在这种情况下，尽管女性具有典型的染色体模式，但仍可能发生男性性发育。仅在一个家庭中报告了SERKAL综合征，可能与生活不相容。导致SERKAL综合征的突变用WNT4蛋白（写为Ala114Val或A114V）中第114位的氨基酸缬氨酸取代了蛋白质结构单元（氨基酸）丙氨酸。该突变存在于每个细胞中WNT4gene的两个拷贝中，并可能消除WNT4蛋白的功能。WNT4蛋白的缺乏导致在SERKAL综合征中发现的各种各样的发育异常。（换行）染色体1特定区域中遗传物质的重复可导致WNT4gene的额外拷贝。拥有该gene的额外拷贝会导致产生额外的WNT4蛋白。尽管具有男性典型的染色体模式，但具有这种重复的人可能会发展出一些女性特征。这些人的子宫发育不全，睾丸功能不全。"}]}' WHERE id = 5227;
UPDATE genes SET GHR_metadata_ch = '{"gene": "MECP2", "conditions": [{"name": "MECP2复制综合征", "detail": "每个细胞中MECP2gene的额外拷贝（重复）会导致MECP2复制综合征，这种疾病的特征是智力残疾，发育迟缓和癫痫发作。这种情况对男性的影响比对女性的影响更大。当女性受到影响时，她们往往具有较温和的特征。重复发生在X染色体的长（q）臂上，包括MECP2gene；根据重复片段的大小，也可能涉及其他gene。重复的大小从100000到几百万个DNA构建块（碱基对）不等。（换行）MECP2gene的重复导致产生额外的MECP2蛋白并增加蛋白功能。导致大脑中gene调控和蛋白质产生的变化导致神经元功能异常。这些神经元变化破坏正常的大脑活动，导致MECP2复制综合征的体征和症状。"}, {"name": "MECP2相关的严重新生儿脑病", "detail": "MECP2gene中至少有19个突变引起MECP2相关的严重新生儿脑病。这种情况几乎只影响男性，其特征是头部小（小头畸形），运动障碍，呼吸问题和癫痫发作。在男性中引起这种情况的许多MECP2gene突变在女性中引起类似的疾病，称为Rett综合征（如下所述）。这些突变中的大多数会改变单个碱基对，插入或删除gene中的碱基对，或改变从gene中产生蛋白质的方式。DNA中的这些变化会改变MeCP2蛋白的结构或减少产生的蛋白质量。结果，细胞没有足够的MeCP2蛋白与DNA结合并调节其他gene。MeCP2的缺乏会改变通常由该蛋白控制的gene的活性。MECP2gene中的突变也可能破坏对神经元之间的通讯至关重要的蛋白质的可变剪接。尽管这些缺陷破坏了正常的大脑发育，但仍不清楚MECP2gene突变如何导致与MECP2相关的严重新生儿脑病的体征和症状。"}, {"name": "PPM-X综合征", "detail": "已经发现MECP2gene中的突变引起PPM-X综合征。该疾病的特征是轻度至重度智力障碍，双相情感障碍和称为帕金森氏症的运动异常模式。这种情况对男性的影响比对女性的影响更大；当女性受到影响时，她们往往只有轻度智力障碍。（换行）八种特定突变约占所有PPM-X综合征病例的一半。这些突变要么改变MeCP2蛋白中的单个蛋白结构单元（氨基酸），要么在制备该蛋白的说明中产生过早的终止信号。导致PPM-X综合征的突变导致MeCP2蛋白的产生，该蛋白不能与DNA或其他蛋白正确相互作用，因此不能控制gene的表达。目前尚不清楚MECP2gene突变如何导致PPM-X综合征的体征和症状，但是大脑中gene的失调可能在受影响个体的智力障碍以及运动和情绪障碍的发展中起作用。"}, {"name": "瑞特综合征", "detail": "在患有Rett综合征的女性中，已经鉴定出MECP2gene的620多个突变，Rett综合征是一种脑部疾病，会导致沟通，学习和协调方面的问题。这些突变包括单碱基对的变化，gene中DNA的插入或缺失，以及影响gene携带的信息如何用于产生蛋白质的变化。MECP2gene突变会改变MECP2蛋白的结构或减少产生的蛋白质量。由此导致的功能性MeCP2短缺可能会损害脑细胞中gene表达的调控，也可能破坏对神经元之间的通讯至关重要的蛋白质的可变剪接。研究表明，这些变化可能会降低某些神经元的活动并影响它们相互沟通的能力。目前尚不清楚这些变化如何导致Rett综合征的特定特征。"}, {"name": "自闭症谱系障碍", "detail": "遗传学家庭参考提供有关自闭症谱系障碍的信息。"}, {"name": "其他疾病", "detail": "在患有其他几种影响大脑的疾病的人中也发现了MECP2gene的突变。例如，MECP2gene突变与某些中度至重度X连锁智力障碍病例相关，而没有上述综合征的其他特征。此外，一些具有Rett综合征特征以及与Angelman综合征相似的体征和症状（以智力残疾，运动问题以及不适当的笑声和兴奋性为特征的疾病）的人的MECP2gene发生了突变。在某些自闭症谱系障碍病例中，已经报道了MECP2gene突变或gene活性的变化，这会影响沟通和社交互动。"}]}' WHERE id = 5230;
UPDATE genes SET GHR_metadata_ch = '{"gene": "FLT3", "conditions": [{"name": "核心结合因子急性髓性白血病", "detail": "遗传学家庭参考提供有关核心结合因子急性髓性白血病的信息。"}, {"name": "细胞遗传学正常的急性髓性白血病", "detail": "FLT3gene的变化与一种称为细胞遗传学正常的急性髓细胞性白血病（CN-AML）的血癌有关。虽然大的染色体异常可能参与急性髓性白血病的发展，但大约一半的病例没有这些异常；这些被归类为CN-AML。（换行）CN-AML中涉及的FLT3gene突变称为体细胞突变；它们仅在癌变且未遗传的细胞中发现。在CN-AML中发现了两种类型的FLT3gene突变。最常见的情况发生在多达34%的CN-AML病例中，称为FLT3内部串联重复（FLT3-ITD）。在这种类型的突变中，复制短序列的DNA并直接插入原始序列之后。复制的DNA序列的大小可以变化，但是所有FLT3-ITD突变都会导致跨越细胞膜的蛋白质区域发生变化，称为近膜结构域。改变的近膜结构域允许FLT3受体激活信号传导途径而不结合FL；受体总是打开，据说是组成性激活的。持续的信号传导导致异常，未成熟白细胞的不受控制的增殖，这是急性髓性白血病的标志。（换行）另一种类型的FLT3gene突变在约14%的CN-AML患者中发现。这些突变被称为FLT3-TKD突变，因为它们改变了称为酪氨酸激酶结构域（TKD）的蛋白质区域中的单个蛋白质构建块（氨基酸）。最常改变的氨基酸是835位的天冬酰胺；它通常被氨基酸酪氨酸取代。此突变写为Asp835Tyr或D835Y。像FLT3-ITD突变一样，FLT3-TKD突变导致组成性激活的FLT3受体和恒定的信号传导，从而导致急性髓细胞性白血病。"}]}' WHERE id = 5285;
UPDATE genes SET GHR_metadata_ch = '{"gene": "TGFB1", "conditions": [{"name": "Camurati-Engelmann病", "detail": "已发现TGFB1gene中至少有12个突变引起Camurati-Engelmann病。这种情况的特征是手臂，腿和头骨的骨骼异常厚（骨质增生）。骨质增生会导致骨痛，肌肉无力和大脑压力增加，从而导致神经系统问题，包括头痛以及听力和视力问题。（换行）大多数TGFB1gene突变改变TGFβ中的单个蛋白质构建块（氨基酸）-1蛋白。三种突变约占Camurati-Engelmann病病例的75%。这些突变中的两个改变了蛋白质中218位的氨基酸精氨酸。一个用氨基酸半胱氨酸（写为Arg218Cys或R218C）取代精氨酸，另一个用氨基酸组氨酸（写为Arg218His R218H）取代精氨酸。第三个突变用蛋白质225位的氨基酸精氨酸取代了氨基酸半胱氨酸（写为Cys225Arg C225R）。（换行）导致Camurati-Engelmann病的所有TGFB1gene突变导致产生过度活跃的TGFβ-1蛋白。这种异常的TGF-1蛋白质活性导致信号转导增加，导致更多的骨形成。结果，手臂，腿和头骨中的骨骼比正常人厚，导致患有Camurati-Engelmann病的人经常遇到的运动和神经问题。"}, {"name": "癌症", "detail": "据报道，TGFB1gene的变化与某些癌症有关。这些变体是在人的一生中获得的，仅存在于某些细胞中。研究表明，TGFB1gene变异与乳腺癌，结肠直肠癌，肺癌，肝癌和前列腺癌有关。改变的蛋白质表达可以增加促进癌症形成的几种细胞过程，例如细胞增殖，细胞运动性和滋养生长中的肿瘤的新血管（血管生成）的发展。"}]}' WHERE id = 5309;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ABCB7", "conditions": [{"name": "X连锁铁粒幼细胞性贫血和共济失调", "detail": "ABCB7gene的突变主要与X连锁铁粒幼细胞性贫血和共济失调有关，其特征在于贫血和由于蛋白质结构改变而导致的异常血红蛋白产生，从而导致血红素产生和铁稳态的破坏。然而，导致与协调，共济失调和其他运动异常相关的问题的ABCB7gene仍不清楚。"}]}' WHERE id = 5351;
UPDATE genes SET GHR_metadata_ch = '{"gene": "AMACR", "conditions": [{"name": "α-甲基酰基辅酶A消旋酶缺乏症", "detail": "已经发现AMACRgene的突变会导致α-甲基酰基辅酶a消旋酶（AMACR）缺乏，这是由酶缺乏和链烷酸积累引起的。它的特征是成年后开始出现各种神经系统问题，包括智力功能逐渐丧失（认知能力下降），癫痫发作，虚弱和感觉运动神经病。"}, {"name": "其他疾病", "detail": "已在患有威胁生命的疾病（称为先天性胆汁酸合成缺陷4型）的婴儿中鉴定出AMACRgene突变，该疾病在婴儿的头几个月以胆汁淤积，肝肿大和不可逆性肝病（肝硬化）为特征。"}]}' WHERE id = 5365;
UPDATE genes SET GHR_metadata_ch = '{"gene": "CNGB3", "conditions": [{"name": "色盲", "detail": "已经发现CNGB3gene中的突变会导致完全性色盲，该疾病的一种形式的特征是完全缺乏色觉和婴儿早期出现的其他视觉问题。该突变特异性地改变了通道的β亚基，导致蛋白质异常或无功能，从而导致蛋白质缺陷，并阻止视锥细胞进行光转导过程。"}, {"name": "视锥-视杆营养不良", "detail": "已经在患有视锥细胞营养不良的人中发现了CNGB3gene的突变，视锥细胞营养不良是一组影响视网膜的眼部疾病，会导致视力丧失并随着时间的推移变得严重。该疾病的特征是视力，畏光，色觉受损（色盲症），视野中心的盲点（盲点），周围视力丧失和不自主的眼球运动（眼球震颤）。"}, {"name": "其他疾病", "detail": "已经在进行性视锥细胞营养不良的人中发现了CNGB3gene的突变，这种疾病在儿童期或青春期影响视锥细胞在视网膜中的功能，其特征是模糊性增加和色觉丧失。"}]}' WHERE id = 5366;
UPDATE genes SET GHR_metadata_ch = '{"gene": "CACNA1F", "conditions": [{"name": "X连锁先天性静止性夜盲", "detail": "已在X连锁先天性静止性夜盲症患者中鉴定出CACNA1Fgene突变，该疾病是一种不完整的疾病，其特征是视力问题，包括视力下降，近视，眼球震颤，斜视，并且在弱光（夜盲症）下难以看见。这些突变改变了α-1亚基，并通过阻止钙离子跨感光细胞的正常转运而导致视觉信号的破坏，从而消除了CaV1.4的功能。"}, {"name": "视锥-视杆营养不良", "detail": "已经在患有视锥细胞营养不良的人中发现了CACNA1Fgene的突变，视锥细胞营养不良是一组影响视网膜的眼部疾病，会导致视力丧失并随着时间的推移变得严重。该疾病的特征是视力，畏光，色觉受损（色盲症），视野中心的盲点（盲点），周围视力丧失和不自主的眼球运动（眼球震颤）。"}, {"name": "其他疾病", "detail": "CACNA1F中的突变会导致其他几种罕见的视力障碍，例如：；陆地岛眼病、X连锁锥杆营养不良和X连锁视网膜疾病，拥有属性视力下降、眼球震颤、眼前部不规则曲率（散光）、近视、色觉异常、畏光和夜盲。一些影响个体表现出智力残疾。这些疾病是由于α-1亚基的结构和功能改变引起的，α-1亚基过度激活CaV1.4通道，破坏了视网膜中的视觉信号。"}]}' WHERE id = 5374;
UPDATE genes SET GHR_metadata_ch = '{"gene": "NLRP7", "conditions": [{"name": "复发性葡萄胎", "detail": "已发现NLRP7gene中的50多个突变会引起复发性葡萄胎。当胚胎发育不全，胎盘发育异常时，妊娠早期就会发生葡萄胎。胎盘是子宫中的一种固体结构，通常为生长中的胎儿提供营养，功能失调，表现为许多小囊，通常被描述为类似一堆葡萄。葡萄胎有癌变的危险。反复发生葡萄胎的妇女有一种称为复发性葡萄胎的病症。NLRP7gene突变是75%患有这种疾病的女性复发性葡萄胎的原因。（换行）导致复发性葡萄胎的NLRP7gene突变导致功能受损的蛋白质的产生或根本阻止任何蛋白质的产生。结果，正常的印迹过程可能受损，导致许多不应该表达的母体gene的激活。胚胎发育过程中多个gene的过度表达导致葡萄胎特征性的胎儿和胎盘组织发育不良。患有复发性葡萄胎的女性在包括卵母细胞在内的所有身体细胞中都有NLRP7gene突变，因此在这些卵细胞发生的每次妊娠中都会发生葡萄胎。此外，NLRP7gene突变导致白细胞介素-1β释放减慢，这会破坏身体的正常免疫反应，这种免疫反应会将葡萄胎识别为非生长妊娠或外来组织，并向身体发出信号将其清除。相反，葡萄胎留在体内。"}, {"name": "其他疾病", "detail": "NLRP7gene的某些变异（多态性）与每个细胞中一份NLRP7gene发生遗传变化的女性早期流产的风险增加有关。怀孕损失包括多次流产和非复发性（散发性）葡萄胎。与患有复发性葡萄胎的女性（如上所述）不同，具有这些多态性的女性能够正常怀孕。NLRP7gene变异似乎不是普通人群中妊娠流失的主要因素。遗传和环境因素都在胚胎发育的复杂性中起作用。"}]}' WHERE id = 5409;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SMAD4", "conditions": [{"name": "遗传性出血性毛细血管扩张", "detail": "已发现SMAD4gene中至少有27个突变会引起某种形式的遗传性出血性毛细血管扩张，这种疾病的特征是某些血管异常。特别是，一些较小的动脉（小动脉）异常地直接流入静脉而不是流入称为毛细血管的其他血管。这些异常称为动静脉畸形。当它们出现在皮肤表面附近的血管中时，它们被称为毛细血管扩张酶（单数是毛细血管扩张症）。（换行）由SMAD4gene突变引起的遗传性出血性毛细血管扩张的形式称为青少年息肉病/遗传性出血性毛细血管扩张综合征。患有这种疾病的人患有与遗传性出血性毛细血管扩张相关的血管问题，并且早年发展为肠道生长（息肉）的风险增加；息肉可能会癌变。（换行）导致这种疾病的SMAD4gene突变影响TGF-β？信号通路。该途径的破坏可能会干扰SMAD4蛋白的肿瘤抑制功能和静脉与动脉之间边界的适当发展，从而导致青少年息肉病/遗传性出血性毛细血管扩张综合征的体征和症状。"}, {"name": "青少年息肉综合征", "detail": "已发现SMAD4gene中至少有78个突变会引起青少年息肉综合症，这种疾病的特征是多种非癌性（良性）生长，称为青少年息肉。导致青少年息肉综合征的大多数SMAD4gene突变会导致异常短而无功能的蛋白质的产生。功能性SMAD4蛋白的缺乏会阻止与其他SMAD蛋白的结合，并干扰化学信号从细胞表面到细胞核的传递。SMAD蛋白复合物未被激活，无法转运至细胞核，在细胞核中需要调节细胞增殖和某些gene的活性。这种不受调节的细胞生长可导致青少年息肉病综合征患者的息肉形成。"}, {"name": "Myhre综合征", "detail": "在患有Myhre综合征的人中至少鉴定出SMAD4gene的五个突变，该病的特征是智力残疾，皮肤和内脏器官中疤痕组织的堆积（纤维化）以及其他影响多种身体系统和功能的问题。这些突变中的每一个都通过用不同的氨基酸替换它来影响蛋白质位置496或500处的蛋白质结构单元（氨基酸）。研究表明，这些突变导致异常稳定的SMAD4蛋白在细胞中保持活性的时间比所需的时间长。这些突变被归类为“功能获得”，因为它们增强了SMAD4蛋白的活性。活性SMAD4的可用性增加使蛋白质有更多时间与其他蛋白质相互作用，并可能导致TGF-β异常？在许多细胞类型中发出信号，这会影响多个身体系统的发育并导致Myhre综合征的体征和症状。"}, {"name": "胆管癌", "detail": "遗传学家庭参考提供有关胆管癌的信息。"}, {"name": "癌症", "detail": "SMAD4gene突变的人患各种癌症的风险似乎增加。这些gene突变中的一些是遗传的，而另一些是在一个人的一生中获得的。这种获得性（体细胞）突变仅存在于某些细胞中。SMAD4gene突变的细胞，无论是遗传的还是体细胞的，都可能失控增殖并导致肿瘤，通常在结肠或胰腺中。"}, {"name": "其他疾病", "detail": "除遗传性出血性毛细血管扩张症外，还在少数患有青少年息肉病和血管异常的个体中发现了SMAD4gene突变（如上所述）。这些异常包括主动脉的减弱和伸展（扩张），主动脉是将血液从心脏分配到身体其他部位的大血管。主动脉扩张可能导致血管壁（动脉瘤）隆起，或可能导致主动脉瓣泄漏，这可能导致主动脉壁（主动脉夹层）的各层突然撕裂。主动脉瘤和夹层可能危及生命。连接心脏四个腔室中的两个腔室的二尖瓣功能受损，还与由SMAD4gene突变引起的青少年息肉病相结合。"}]}' WHERE id = 5424;
UPDATE genes SET GHR_metadata_ch = '{"gene": "CRLF1", "conditions": [{"name": "冷诱发出汗综合征", "detail": "据报道，CRLF1gene中至少有10个突变会引起冷诱发出汗综合征，这是一种罕见病，其特征是调节体温和影响身体许多部位的其他异常问题。当这种情况是由CRLF1gene突变引起的时，它被称为CISS1。CRLF1gene中的（换行）突变导致CRLF1蛋白的非功能性版本的产生或大大减少功能性CRLF1蛋白的产生。因此，CRLF1无法与CLCF1蛋白相互作用并与CNTFR结合，从而禁用了CNTFR信号通路。(换行)研究人员认为，CNTFR信号传导失败是冷诱发出汗综合征的主要特征。交感神经系统发育过程中该信号通路的丧失可能有助于解释这种情况的特征性异常出汗，包括异常出汗模式和体温调节的相关问题。CNTFR途径参与运动神经元发育和骨骼发育，为该疾病的其他一些体征和症状提供了线索，包括独特的面部特征，面部肌肉无力和骨骼异常。但是，关于缺乏CNTFR信号如何导致这些变化的特征知之甚少。"}]}' WHERE id = 5427;
UPDATE genes SET GHR_metadata_ch = '{"gene": "COL4A4", "conditions": [{"name": "阿尔波特综合症", "detail": "已经发现COL4A4gene的突变会引起Alport综合征，Alport综合征是一种以肾脏瘢痕形成，肾衰竭为特征的疾病，并且还会导致内耳功能异常，从而导致听力丧失。突变严重减少或阻止了α4（IV）链的产生，导致肾脏，内耳和眼睛的基底膜中IV型胶原α3-4-5网络严重缺乏。"}, {"name": "圆锥角膜", "detail": "已经发现COL4A4gene的突变与圆锥角膜有关，圆锥角膜是一种影响角膜形状的疾病，导致角膜异常。临床症状包括近视（近视），矫正镜片无法改善的视力模糊（散光不规则）和视力丧失。"}, {"name": "其他疾病", "detail": "已经发现COL4A4gene中的突变引起薄基底膜肾病（以前称为良性家族性血尿），其特征在于血尿而没有任何其他肾病症状。"}]}' WHERE id = 5458;
UPDATE genes SET GHR_metadata_ch = '{"gene": "AMELX", "conditions": [{"name": "釉质发育不全", "detail": "AMELXgene的突变已在患有X连锁形式的牙齿发育障碍（称为牙釉质发育不全）的人中发现，其特征是牙齿异常，例如牙齿异常小，变色，凹陷或有凹槽，并且易于快速磨损和破损。遗传了AMELXgene改变拷贝的男性在这种类型中受到严重影响，因为他们只有X染色体的一个拷贝，并且几乎没有牙齿保护所需的牙釉质。"}]}' WHERE id = 5471;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SBF2", "conditions": [{"name": "进行性神经性腓骨肌萎缩症", "detail": "在患有Charcot-Marie-Tooth病（称为4B2型）的患者中，至少鉴定出五个SBF2gene突变。这些突变中的一些通过引入导致异常短蛋白的过早终止信号来改变SET结合因子2的结构。其他突变导致缺少关键片段的蛋白质的产生。所有这些突变都可能导致蛋白质功能异常。尽管尚不清楚SBF2gene突变如何导致这种疾病，但髓磷脂的产生可能受到破坏。不规则的髓鞘结构（称为折叠）是4B2型Charcot-Marie-Tooth病的特征性体征。(换行)患有这种疾病的个体也可能从儿童期或青春期开始经历眼内流体压力的累积（青光眼）。研究人员认为，青光眼的出现取决于SBF2gene突变的类型。导致蛋白质功能完全丧失的突变会干扰眼睛小梁网的发育，从而导致液体引流和青光眼受损。较不严重的突变（允许SET结合因子2蛋白发挥部分功能）不会引起青光眼。"}]}' WHERE id = 5555;
UPDATE genes SET GHR_metadata_ch = '{"gene": "MEN1", "conditions": [{"name": "家族性孤立性甲状旁腺功能亢进", "detail": "在某些家族性孤立性甲状旁腺功能亢进症中发现了MEN1gene的突变，这种疾病的特征是甲状旁腺过度活跃（原发性甲状旁腺功能亢进）。这些腺体有助于控制血液中钙的正常平衡。这种平衡在家族性孤立性甲状旁腺功能亢进症中被破坏，这可能导致高血钙水平（高钙血症），肾结石，骨骼变薄，恶心和呕吐，高血压（高血压），虚弱和疲劳。原发性甲状旁腺功能亢进是另一种称为1型多发性内分泌肿瘤的疾病的最常见征兆（如下所述）；然而，家族性孤立性甲状旁腺功能亢进症是在甲状旁腺功能亢进症患者中诊断出来的，而不是多发性内分泌肿瘤1型的其他特征。（换行）与家族性孤立性甲状旁腺功能亢进相关的MEN1gene中的许多突变改变了menin蛋白中的单蛋白结构单元（氨基酸）。据认为，这些氨基酸变化削弱了menin与其他蛋白质相互作用的能力。如果没有正常的menin功能，细胞可能分裂得太频繁，导致涉及甲状旁腺的肿瘤形成。研究人员推测，引起家族性孤立性甲状旁腺功能亢进的突变对menin功能的影响要比引起多发性内分泌肿瘤1型的突变轻。偶尔，家族性孤立性甲状旁腺功能亢进症患者后来发展为1型多发性内分泌肿瘤的特征，尽管大多数人从未如此。由MEN1gene突变引起的家族性孤立性甲状旁腺功能亢进可能是1型多发性内分泌肿瘤的早期或轻度形式。"}, {"name": "多发性内分泌肿瘤", "detail": "已发现MEN1gene中的1300多个突变会导致1型多发性内分泌肿瘤。多发性内分泌肿瘤通常涉及两个或多个身体产生激素的腺体（称为内分泌腺）中肿瘤的发展。这些肿瘤可以是非癌性的或癌性的。在1型多发性内分泌肿瘤中受影响的最常见的内分泌腺是甲状旁腺，垂体和胰腺，尽管也可能涉及其他内分泌腺和其他器官。（换行）导致多发性内分泌肿瘤1型的大多数MEN1gene突变导致产生异常短的无活性形式的menin或快速分解的不稳定蛋白质。这些突变的结果是，每个细胞中MEN1gene的一个副本都不产生功能蛋白。如果MEN1gene的第二个拷贝也被改变，则该细胞没有该gene的工作拷贝，也不会产生任何功能性menin。由于未知原因，第二种突变最常发生在内分泌腺细胞中。没有menin，这些细胞可能分裂得太频繁并形成肿瘤。虽然menin似乎是预防肿瘤形成所必需的，但研究人员尚未确定缺乏这种蛋白质如何导致多发性内分泌肿瘤1型的特定肿瘤特征。"}, {"name": "原发性大结节性肾上腺增生", "detail": "遗传学家庭参考提供有关原发性大结节性肾上腺增生的信息。"}, {"name": "其他肿瘤", "detail": "一些gene突变是在一个人的一生中获得的，并且仅存在于某些细胞中。这些被称为体细胞突变的变化不是遗传的。已经在几种类型的非遗传性（散发性）肿瘤中鉴定出MEN1gene的体细胞突变。具体而言，在甲状旁腺的非癌性肿瘤（甲状旁腺腺瘤）中发现了MEN1gene突变；胰腺肿瘤称为无功能的神经内分泌肿瘤，胃泌素瘤和胰岛素瘤；和肺部主要气道（支气管）的癌性肿瘤称为支气管类癌。在患有多发性内分泌肿瘤1型（如上所述）的人中也发现了许多这些肿瘤类型。与多发性内分泌肿瘤一样，仅当MEN1gene的两个拷贝在某些细胞中均失活时才会发生肿瘤。"}]}' WHERE id = 5566;
UPDATE genes SET GHR_metadata_ch = '{"gene": "CATSPER1", "conditions": [{"name": "CATSPER1相关的非综合征性男性不育", "detail": "已发现CATSPER1gene中至少有两个突变会导致CATSPER1相关的非综合征性男性不育。这些突变被认为导致CATSPER1蛋白的产生，该蛋白可能被细胞改变，失去功能或迅速分解（降解）。缺乏功能性CATSPER1蛋白会损害钙进入精子细胞，从而降低运动性并防止过度活化。缺乏超活化导致精子不能穿过卵细胞膜并实现受精。这些精子异常是受影响男性不育的原因。男性不育是CATSPER1相关的非综合征性男性不育的唯一症状。"}]}' WHERE id = 5584;
UPDATE genes SET GHR_metadata_ch = '{"gene": "RAB27A", "conditions": [{"name": "格里切利综合征", "detail": "在格里切利综合征患者中至少发现了RAB27Agene的24个突变。这些突变会导致一种称为2型疾病的形式，其特征是皮肤异常光（色素沉着不足），银灰色头发和免疫系统异常。已知的突变要么阻止任何Rab27a蛋白的产生，要么导致不能与MLPH和MYO5Agene产生的蛋白形成复合物的异常或不稳定蛋白的产生。功能性Rab27a蛋白的缺乏会损害黑素体向黑素细胞边缘的正常转运。相反，这些结构聚集在黑素细胞的中心附近，将黑色素捕获在这些细胞内并防止皮肤和头发的正常色素沉着。T淋巴细胞中Rab27a功能的丧失会损害细胞毒性颗粒的胞吐作用，使2型格里切利综合征患者容易反复感染。（线突破）通过尚不清楚的机制，免疫系统细胞中Rab27a的缺乏也会导致2型格里切利综合征患者出现噬血细胞淋巴组织细胞增多症（HLH）。这种情况触发免疫系统产生太多活化的T淋巴细胞和其他称为巨噬细胞（组织细胞）的免疫细胞。这些细胞的过度活跃会损害整个身体的器官和组织，如果不及时治疗，会导致危及生命的并发症。"}]}' WHERE id = 5662;
UPDATE genes SET GHR_metadata_ch = '{"gene": "COL4A3", "conditions": [{"name": "阿尔波特综合症", "detail": "已经发现COL4A3gene的突变引起Alport综合征，Alport综合征是一种引起肾脏瘢痕形成，肾衰竭并且还导致内耳功能异常导致听力丧失的疾病。突变严重减少或阻止了α3（IV）链的产生，导致肾脏，内耳和眼睛的基底膜中IV型胶原α3-4-5网络严重缺乏。"}, {"name": "圆锥角膜", "detail": "已经发现COL4A3gene的突变与圆锥角膜有关，圆锥角膜是一种影响角膜形状的疾病，导致角膜异常。临床症状包括近视（近视），矫正镜片无法改善的视力模糊（散光不规则）和视力丧失。"}, {"name": "其他疾病", "detail": "已经发现COL4A3gene的突变引起薄基底膜肾病（以前称为良性家族性血尿），其特征在于血尿而没有任何其他肾病症状。"}]}' WHERE id = 5666;
UPDATE genes SET GHR_metadata_ch = '{"gene": "CTSD", "conditions": [{"name": "CLN10疾病", "detail": "已经发现CTSDgene中至少有七个突变引起CLN10疾病。CLN10疾病的体征和症状通常在出生时就出现，包括肌肉僵硬，呼吸衰竭和严重癫痫发作；死亡通常发生在婴儿期。很少，CLN10疾病会在以后的生活中发展，协调和平衡能力差（共济失调），语言丧失，智力功能逐渐丧失（认知能力下降）和视力丧失。（换行）发现导致出生时存在的CLN10疾病的CTSDgene突变导致组织蛋白酶D酶活性完全缺乏。结果，蛋白质和脂肪不能正确分解并且在溶酶体内异常积累。虽然这些物质积聚在整个身体的细胞中，但神经细胞似乎特别容易受到异常细胞材料引起的损害。CLN10疾病中神经细胞的早期和广泛丧失会导致严重的体征和症状，并导致婴儿死亡。（换行）在CLN10疾病的晚发病例中，CTSDgene突变可能导致组织蛋白酶D酶的产生，其功能大大降低但并未消除。结果，一些蛋白质和脂肪被酶分解，因此这些物质在溶酶体中积累并导致神经细胞死亡需要更长的时间。"}]}' WHERE id = 5731;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ALK", "conditions": [{"name": "神经母细胞瘤", "detail": "在一些患有神经母细胞瘤的人中已经鉴定出ALKgene中的至少16个突变，神经母细胞瘤是一种由未成熟神经细胞（成神经细胞）组成的癌性肿瘤。当关键gene中的gene突变累积时，就会发生神经母细胞瘤和其他癌症？那些控制细胞增殖或分化的？允许细胞不受控制地生长和分裂形成肿瘤。在大多数情况下，这些gene变化是在一个人的一生中获得的，被称为体细胞突变。体细胞突变仅存在于某些细胞中，不会遗传。不太常见的是，增加患癌症风险的gene突变可以从父母那里遗传。两种类型的突变都发生在神经母细胞瘤中。ALKgene的体细胞突变发生在一些散发性神经母细胞瘤病例的发展过程中，ALKgene的遗传突变增加了发展家族性神经母细胞瘤的风险。（换行）ALKgene中的突变改变ALK受体酪氨酸激酶中的单个蛋白质构建块（氨基酸）。神经母细胞瘤中最常见的突变是在位置1275处用氨基酸谷氨酰胺取代氨基酸精氨酸（写为Arg1275Gln或R1275Q）。在家族性和散发性神经母细胞瘤中均发现了Arg1275Gln，并且是在两种类型的疾病中均发现的唯一常见ALKgene突变。(换行)偶尔，在神经母细胞瘤患者中发现额外的ALKgene拷贝。这种称为gene扩增的现象导致ALK受体酪氨酸激酶的过度表达。（换行）突变或过表达的ALK受体酪氨酸激酶不再需要来自细胞外部的刺激才能被磷酸化。结果，激酶和下游信号传导途径不断打开（组成性激活）。ALK受体酪氨酸激酶的组成型活化可以增加未成熟神经细胞的增殖，导致神经母细胞瘤。"}, {"name": "肺癌", "detail": "遗传学家庭参考提供有关肺癌的信息。"}, {"name": "其他癌症", "detail": "涉及染色体2上ALKgene的遗传物质的重排增加了发展几种其他类型癌症的风险。这些重排是体细胞突变，这意味着它们发生在一个人的一生中，并且仅存在于癌变的细胞中。（换行）一种称为易位的重排，在2号染色体和另一条染色体之间交换遗传物质。在间变性大细胞淋巴瘤（ALCL）患者中已经鉴定出至少15个涉及ALKgene的易位，ALCL是一种罕见的涉及称为T细胞的免疫细胞的癌症。ALCL中最常见的易位发生在2号染色体和5号染色体之间，称为t（2；5）。这种易位将ALKgene与NPMgene融合，并产生称为NPM-ALK的融合蛋白。另外，在炎性肌纤维母细胞瘤（IMT）中已经鉴定出涉及ALKgene的至少七个易位。IMT是一种罕见的癌症，其特征在于由炎性细胞和称为肌成纤维细胞的细胞组成的实体瘤，其在伤口愈合中是重要的。约有一半的IMT患者易位涉及ALKgene。（换行）当染色体2在两个位置断裂并且所得的DNA片段被逆转并重新插入染色体时，发生另一种类型的重排，称为倒位。一小部分患有非小细胞肺癌（最常见的肺癌类型）的人具有2号染色体的倒位。这种倒置将ALKgene与另一个称为EML4的gene融合，并产生EML4-ALK融合蛋白。（换行）由这些重排gene产生的融合蛋白具有ALK受体酪氨酸激酶和伴侣蛋白的功能。伴侣蛋白的存在允许ALK受体酪氨酸激酶磷酸化而无需二聚化。由ALK受体酪氨酸激酶激活的融合蛋白和信号传导途径被组成性激活，这可能异常增加未成熟神经细胞的增殖，导致癌症形成。"}]}' WHERE id = 5748;
UPDATE genes SET GHR_metadata_ch = '{"gene": "OPN1LW", "conditions": [{"name": "色觉缺陷", "detail": "涉及OPN1LWgene的几种遗传变化会导致红绿色色觉缺陷，这是一种色觉缺陷形式，使得难以或不可能区分红色，黄色和绿色的阴影。大多数红绿色视觉缺陷是由涉及OPN1LW和OPN1MWgene的结构重排引起的。因为这些gene非常相似，所以当gene从父母传给孩子时，它们偶尔会交换遗传物质。这种交换称为重组，最终可以从一个或两个gene中删除遗传物质，或导致形成含有部分OPN1LWgene和部分OPN1MWgene的杂合色素gene。不太常见的是，红绿色缺陷可能是由OPN1LWgene中单个DNA构建块（碱基对）的变化引起的。（换行）当OPN1LWgene突变导致完全无功能的L视锥细胞时，色觉完全取决于其他两种视锥细胞。由L锥功能完全丧失引起的特定类型的红绿色色觉缺陷称为protanopia。当部分功能性杂种色素gene替代正常的OPN1LWgene时，就会发生一种不太严重的红绿色视觉缺陷，称为protanomaly。由这些杂合gene制成的光色素通常具有损害红绿色视觉的异常视觉特性。（换行）OPN1LWgene的常见变异（多态性）解释了正常色觉的细微差异。这种变化改变了所得光色素中的单个蛋白质结构单元（氨基酸），用位置180处的氨基酸丙氨酸取代了氨基酸丝氨酸（写为Ser180Ala）。研究人员建议，Ser180Ala多态性在确定具有红绿色色觉缺陷的人的色觉丧失的严重程度中也起作用。(换行)一种罕见的色觉缺陷，蓝锥单色性，严重降低视力清晰度（视力）并影响感知大多数颜色的能力。这种情况还包括红绿色色觉缺陷通常不会发现的其他视觉问题。当遗传变化阻止OPN1MW和OPN1LWgene产生的视蛋白色素正常运行时，就会发生蓝锥单色性。在某些情况下，这种情况是由LCR的缺失引起的，LCR通常控制OPN1MW和OPN1LWgene的活性。LCR的丧失阻止了两种gene产生色素。结果，患有这种情况的人仅具有具有短波敏感感光色素（S锥）的功能锥，这导致视敏度降低和色觉差。视锥异常也是蓝锥单色症患者其他视力问题的基础。"}]}' WHERE id = 5782;
UPDATE genes SET GHR_metadata_ch = '{"gene": "FGF3", "conditions": [{"name": "先天性耳聋伴迷路发育不全，小眼症和小牙症", "detail": "FGF3gene的突变会导致一种称为先天性耳聋的疾病，并伴有迷路性发育不全，小眼症和小牙症（也称为LAMM综合征）。这种情况的特征包括缺乏内耳结构（迷路性发育不全），小外耳（小耳畸形）和小牙齿（小牙畸形）引起的耳聋。LAMM综合征中涉及的gene突变会改变FGF3蛋白中的单个蛋白结构单元（氨基酸）或导致异常短的蛋白。改变后的蛋白质可能功能降低或缺失，使其无法刺激细胞内的信号传导。FGF3功能的丧失会损害耳朵和牙齿的发育，从而导致LAMM综合征的特征。"}]}' WHERE id = 5818;
UPDATE genes SET GHR_metadata_ch = '{"gene": "HADHB", "conditions": [{"name": "线粒体三功能蛋白缺乏症", "detail": "研究人员已经确定HADHBgene中至少有26个突变会导致线粒体三功能蛋白缺乏。这些突变降低了线粒体三功能蛋白的所有三种酶活性。大多数突变会改变用于制造β亚基的蛋白质结构单元（氨基酸）之一。氨基酸的变化可能会改变亚基的结构，从而破坏酶复合物的所有三种活性。一些突变产生异常短的，无功能的β亚基，并导致线粒体三功能蛋白水平降低。(换行)线粒体三功能蛋白活性丧失，长链脂肪酸无法代谢和加工。结果，这些脂肪酸不会转化为能量，这可能导致这种疾病的某些特征，例如缺乏能量（嗜睡）和低血糖（低血糖）。长链脂肪酸或部分代谢的脂肪酸也可能积聚并损害肝脏，心脏和肌肉。这种异常积聚导致线粒体三功能蛋白缺乏的其他体征和症状。"}, {"name": "其他疾病", "detail": "已发现HADHBgene中的一些突变仅降低线粒体三功能蛋白的长链3-酮酰基-CoA硫解酶活性。这些突变改变了用于制造β亚基的单个氨基酸。孤立的长链3-酮酰基-CoA硫解酶缺乏症的体征和症状与线粒体三功能蛋白缺乏症相似。这些特征包括进食困难，嗜睡，低血糖，肌张力弱（肌张力低下）和肝脏问题。患有这种疾病的婴儿也有严重心脏问题，呼吸困难，昏迷和猝死的高风险。(换行)HADHB突变似乎增加了女性在怀孕期间发生两种严重肝脏疾病的风险，称为妊娠急性脂肪肝（AFLP）和HELLP综合征。AFLP始于腹痛，可迅速发展为肝功能衰竭。HELLP代表溶血（红细胞分解），肝酶水平升高和血小板减少（与凝血有关的细胞碎片）。一小部分在HADHBgene的一个拷贝中发生突变并携带在HADHBgene的两个拷贝中均具有突变的胎儿的妇女会患上这些母体肝病之一。关于怀孕期间母亲的HADHB突变与肝脏问题之间的关系知之甚少。一种可能性是由胎儿或胎盘产生的部分代谢的长链脂肪酸在母体中积累并对肝脏有毒。"}]}' WHERE id = 5823;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SERAC1", "conditions": [{"name": "MEGDEL综合征", "detail": "已发现SERAC1gene中至少有16个突变引起MEGDEL综合征。这种情况的特征是听力下降，神经系统问题，被称为Leigh样疾病的大脑中的某些变化以及尿液中异常大量的称为3-甲基谷氨酸的酸。引起这种情况的SERAC1gene突变减少了产生的SERAC1蛋白的量，或导致产生功能很少或没有功能的蛋白。结果，磷脂酰甘油重塑受损，这可能改变心磷脂的组成。研究人员推测，心磷脂异常会影响线粒体功能，减少细胞能量的产生并导致MEGDEL综合征的神经和听力问题。目前尚不清楚SERAC1gene突变如何导致尿液中3-甲基谷氨酸的异常释放。"}, {"name": "利综合征", "detail": "遗传学家庭参考提供有关Leigh综合征的信息。"}]}' WHERE id = 5848;
UPDATE genes SET GHR_metadata_ch = '{"gene": "GBA", "conditions": [{"name": "戈谢病", "detail": "在高雪氏病患者中已鉴定出GBAgene的380多个突变，高雪氏病是一种具有多种特征的疾病，会影响身体的许多部位。受影响的个体可能有肝脏和脾脏肿大（肝脾肿大），血细胞异常，很少有严重的神经系统问题。突变发生在每个细胞中gene的两个拷贝中。导致高雪氏病的大多数GBAgene突变会改变β-葡萄糖脑苷脂酶中的单个蛋白质结构单元（氨基酸），改变酶的结构并阻止其正常工作。其他突变会删除或插入GBAgene中的遗传物质，或导致产生异常短而无功能的酶。GBAgene中的（换行）突变大大降低或消除了细胞中β-葡萄糖脑苷脂酶的活性。结果，葡糖脑苷脂不能正确分解。这种分子和相关物质可以积聚在脾脏，肝脏，骨髓和其他器官中称为巨噬细胞的白细胞中。这些物质的异常积累和储存会损害组织和器官，从而导致高雪氏病的特征。"}, {"name": "帕金森病", "detail": "GBAgene的变化也与帕金森病和帕金森病有关，帕金森病和帕金森病是影响运动的类似疾病。特征包括震颤，平衡和协调能力受损（姿势不稳）。患有高雪氏病的人（如上所述）在每个细胞中的两个GBAgene拷贝中都有突变，而那些仅在该gene的一个拷贝中具有突变的人被称为携带者。高雪氏病患者和GBAgene突变携带者罹患帕金森氏病或帕金森氏症的风险增加。(换行)帕金森病和帕金森病的症状是由产生多巴胺的神经细胞损失引起的。多巴胺是一种化学信使，可在大脑内传递信号以产生平稳的身体运动。尚不清楚GBAgene突变如何与这些疾病相关。研究表明，该gene的变化可能通过损害溶酶体的功能而导致神经细胞中有毒物质的错误分解。或者，这些变化可能会增加异常蛋白质沉积物的形成。结果，有毒物质或蛋白质沉积物可能积累并杀死产生多巴胺的神经细胞，从而导致异常运动和平衡问题。"}, {"name": "路易氏体型失智症", "detail": "GBAgene突变会增加路易体患痴呆的风险；但是，某些GBAgene突变的人可能永远不会出现这种情况。路易体痴呆的特征是智力下降（痴呆）；幻觉；注意力和情绪的突然变化；以及帕金森氏病（如上所述）的运动问题，例如四肢僵硬，震颤以及平衡与协调能力受损。GBAgene一个拷贝中的（换行）突变导致产生改变的β-葡萄糖脑苷脂酶，尽管尚不清楚这些突变如何导致路易体的形成。异常的酶可能会干扰溶酶体的功能和称为α-突触核蛋白的蛋白质的正常分解，这会增加这些蛋白质积聚并形成路易体的风险。这些簇在整个大脑神经元中的积累损害细胞功能并最终导致细胞死亡。随着时间的推移，神经元的丧失越来越损害智力和运动功能以及情绪的调节，导致路易体痴呆的体征和症状。"}]}' WHERE id = 5853;
UPDATE genes SET GHR_metadata_ch = '{"gene": "POMT2", "conditions": [{"name": "沃克-沃伯格综合症", "detail": "已发现POMT2gene中至少有8个突变引起Walker-Warburg综合征。这种情况是一组称为先天性肌营养不良的疾病中最严重的形式。Walker-Warburg综合征会导致肌肉无力以及大脑和眼睛异常。由于这种情况引起的问题的严重性，受影响的个体通常无法存活到儿童早期。（换行）导致Walker-Warburg综合征的POMT2gene突变导致形成无法将甘露糖转移至-dystroglycan，阻止其正常糖基化。结果是-dystroglycan不再能够有效地将细胞锚定在周围的蛋白质和其他分子上。没有功能-dystroglycan为了稳定肌肉纤维，当它们反复收缩并使用时放松时会受到损坏。受损的纤维会随着时间的推移而减弱和死亡，这会影响Walker-Warburg综合征患者骨骼肌的发育，结构和功能。(换行)有缺陷-dystroglycan还影响大脑早期发育过程中神经元的迁移。一些神经元不是在到达预定目的地时停止，而是通过大脑表面迁移到周围充满液体的空间。研究人员认为，神经元迁移的这个问题会导致大脑异常，称为鹅卵石脑畸形，其中表面大脑缺乏正常的褶皱和凹槽，而是显得颠簸和不规则。关于POMT2gene突变在身体其他部位的影响知之甚少。"}, {"name": "肢带型肌营养不良症", "detail": "遗传学家庭参考资料提供有关肢带型肌营养不良症的信息。"}, {"name": "其他疾病", "detail": "POMT2gene的突变也与较不严重的肌肉营养不良有关，包括肌肉眼脑疾病和与POMT2相关的先天性肌营养不良（也称为MDDGB2）。肌肉眼脑疾病类似于Walker-Warburg综合征（如上所述），尽管受影响的个体通常可以存活到儿童期或青春期。与POMT2相关的先天性肌营养不良症会导致肌肉无力，脑部异常和智力残疾，但通常不会影响眼睛。（换行）导致这些情况的POMT2gene突变导致功能降低的POMT酶复合物。结果，糖基化-dystroglycan受损。由此产生的情况的严重程度似乎与-dystroglycan糖基化；糖基化程度越低，病情越严重。"}]}' WHERE id = 5855;
UPDATE genes SET GHR_metadata_ch = '{"gene": "PANK2", "conditions": [{"name": "泛酸激酶相关的神经变性", "detail": "在泛酸激酶相关的神经退行性变患者中，已经鉴定出PANK2gene中约100个突变。通常，患有更严重，更早发作的疾病的人具有PANK2突变，该突变阻止细胞产生任何功能性泛酸激酶2。受非典型，晚发型影响的人通常具有改变酶中单个氨基酸的突变，这使得酶不稳定或破坏其活性。在某些情况下，单个氨基酸变化允许酶保留某些功能。最常见的PANK2突变在酶的411位（写为Gly411Arg或G411R）用氨基酸精氨酸取代了氨基酸甘氨酸。（换行）当泛酸激酶2改变或缺失时，辅酶A的正常产生被破坏，并且潜在有害的化合物可以在脑中积聚。这种积聚导致肿胀，组织损伤和大脑某些区域的铁异常积聚。研究人员不确定缺乏功能性泛酸激酶2如何导致泛酸激酶相关神经变性的特定特征。由于该酶在线粒体中起作用，因此这种情况的体征和症状可能与能量产生受损有关。在患有HARP（低血脂蛋白血症，棘皮细胞增多症，色素性视网膜炎和苍白球变性）的人中也发现了PANK2gene的突变。HARP在历史上被描述为一种单独的综合征，但现在被认为是泛酸激酶相关的神经变性的一部分。尽管HARP比经典的泛酸激酶相关的神经变性少得多，但两种情况都涉及运动，痴呆和视力异常的问题。"}]}' WHERE id = 5871;
UPDATE genes SET GHR_metadata_ch = '{"gene": "KCNH2", "conditions": [{"name": "罗曼诺-沃德综合症", "detail": "KCNH2基因的突变会导致Romano-Ward综合征，这是一种最常见的心脏疾病，称为长QT综合征。这种基因的突变约占Romano-Ward综合征病例的25%。患有这种疾病的人，心脏肌肉在两次搏动之间需要比平时更长的时间来充电，这可能导致心律失常(心律失常)。(换行)超过900个KCNH2基因突变导致Romano-Ward综合征。其中一些突变改变了KCNH2蛋白中的单个蛋白质构件(氨基酸)，而其他突变则删除了蛋白质中的几个氨基酸。这些变化阻止蛋白质组装成离子通道或改变通道的结构或功能。因此，这些通道不能正常调节心肌细胞中钾离子的流动。减少的离子转运改变了心脏内电信号的传输，增加了可导致昏厥(晕厥)或猝死的不规则心跳的风险"}, {"name": "短QT综合征", "detail": "KCNH2gene的突变也可引起称为短QT综合征的心脏病。在患有这种情况的人中，心肌在心跳之间花费的时间比平时少。这种变化增加了可能导致晕厥或猝死的心律异常的风险。（换行）已发现KCNH2gene中至少有8个突变在少数受影响的家庭中引起短QT综合征。这些突变改变了KCNH2蛋白中的单个氨基酸。遗传变化改变了用KCNH2蛋白制成的离子通道的功能，增加了通道的活性。结果，在心跳期间的关键时刻，更多的钾离子从心肌细胞中流出，这可能导致心律不规则。"}, {"name": "家族性心房颤动", "detail": "遗传学家庭参考资料提供有关家族性心房颤动的信息。"}, {"name": "其他疾病", "detail": "某些药物，包括用于治疗心律失常，感染，癫痫发作，精神疾病和其他问题的药物，可能导致某些人心律异常。这种药物引起的心脏病被称为获得性长QT综合征，增加了心脏骤停和猝死的风险。一小部分获得性长QT综合征病例发生在KCNH2gene潜在变异的人群中。"}]}' WHERE id = 5885;
UPDATE genes SET GHR_metadata_ch = '{"gene": "GRIP1", "conditions": [{"name": "弗雷泽综合症", "detail": "已发现至少两个GRIP1gene突变引起Fraser综合征；这些突变涉及这种情况的一小部分情况。弗雷泽综合症会影响出生前的发育，其特征是眼睛被皮肤完全覆盖（隐性眼球），手指和脚趾之间的皮肤融合（皮肤上有组织），以及肾脏和其他器官和组织的异常。(换行)GRIP1gene突变会改变带有GRIP1蛋白制备说明的遗传蓝图。如果制造了任何GRIP1蛋白，则可能无法发挥作用。如果没有GRIP1，FRAS1和FREM2将无法到达正确的位置以形成FRAS/FREM复合体。皮肤基底膜中缺乏FRAS/FREM复合物会导致皮肤顶层脱离，从而在发育过程中形成水泡。这些水泡可能会阻止出生前某些结构的正确形成，从而导致隐性眼球炎和皮肤综合症。尚不清楚缺乏FRAS/FREM复合物如何导致肾脏异常和Fraser综合征的其他问题。"}]}' WHERE id = 5887;
UPDATE genes SET GHR_metadata_ch = '{"gene": "GP6", "conditions": [{"name": "糖蛋白VI缺乏症", "detail": "已发现GP6gene中至少有五个突变会导致糖蛋白VI缺乏，这是一种与形成血凝块的能力降低相关的出血性疾病。突变导致不产生GPVI蛋白；异常短的无功能GPVI蛋白；或不能与胶原蛋白结合的蛋白质。没有GPVI与胶原蛋白结合，血小板不能在损伤部位有效地聚集在一起形成凝块，导致流鼻血的风险增加，轻微损伤或手术后异常重度或长期出血，或与糖蛋白VI相关的其他出血问题不足。"}]}' WHERE id = 5892;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SPR", "conditions": [{"name": "多巴反应性肌张力障碍", "detail": "已经发现SPRgene中至少有四个突变引起多巴反应性肌张力障碍。这种情况的特征是不自主的肌肉收缩（肌张力障碍），震颤和其他不受控制的运动，通常对称为左旋多巴的药物治疗有反应。多巴反应性肌张力障碍可能是由每个细胞中SPRgene的一个拷贝或两个拷贝中的突变引起的。这些突变导致功能降低或缺失的Sepaapterin还原酶的产生。在身体的大多数部位，都有其他途径不使用Sepaapterin还原酶来生产四氢生物蝶呤，但是这些过程不会在大脑中发生。因此，患有Sepaapterin还原酶缺乏症的人大脑中缺乏四氢生物蝶呤，这会影响多巴胺和5-羟色胺的产生。这两种神经递质的缺乏会导致运动问题和多巴反应性肌张力障碍的其他特征。"}, {"name": "Sepaapterin还原酶缺乏症", "detail": "已发现SPRgene中的十几个突变会导致Sepaapterin还原酶缺乏症，这种疾病的特征是运动过程中出现问题。当每个细胞中两个拷贝的SPRgene突变时，就会导致Sepaapterin还原酶缺乏症。这些突变包括取代氨基酸的变化；改变gene指令拼接在一起产生酶的方式；或导致酶功能缩短。所有这些突变导致产生功能降低或无功能的酶。受影响个体中的常见突变会阻止酶中第150位的氨基酸精氨酸替换为氨基酸甘氨酸（写为Arg150Gly或R150G），从而阻止了任何Sepaapterin还原酶的产生。（换行）SPRgene突变破坏了Sepaapterin还原酶的产生。大多数SPRgene突变导致酶几乎没有功能。无功能的Sepaapterin还原酶导致缺乏四氢生物蝶呤，大脑中多巴胺和5-羟色胺的产生减少。这些神经递质的缺乏会导致运动异常和Sepaapterin还原酶缺乏症的其他特征。Sepiapterin还原酶缺乏症比多巴反应性肌张力障碍更严重，可能是因为SPRgene的两个拷贝都发生了突变，这导致了比多巴反应性肌张力障碍更严重的酶缺乏症，后者只有一个拷贝的gene发生了突变。"}]}' WHERE id = 5897;
UPDATE genes SET GHR_metadata_ch = '{"gene": "CHN1", "conditions": [{"name": "孤立的Duane缩回综合征", "detail": "在患有孤立的Duane缩回综合征的家庭中，已经鉴定出CHN1gene中至少有七个突变。研究人员认为，这种情况的特征是由CHN1蛋白的两个版本之一的变化引起的？2嵌合蛋白。每个鉴定出的突变都会改变一个蛋白质结构单元（氨基酸）？2嵌合蛋白。虽然有几个突变也会影响？1-chimaerin，研究人员认为这种变化在？单独的1-chimaerin不会引起孤立的Duane缩回综合征。（换行）CHN1突变改变了？2-chimaerin在发育中的神经系统中。这些遗传变化似乎使蛋白质过度活化，从而破坏了大脑某些部位神经元的正常生长。结果，颅神经VI和III及其控制的眼外肌不能正常发育。这些肌肉的异常发育和功能导致孤立的Duane缩回综合征的特征，包括眼球运动受限和相关的视力问题。"}]}' WHERE id = 5945;
UPDATE genes SET GHR_metadata_ch = '{"gene": "KCNQ4", "conditions": [{"name": "非综合征性听力损失", "detail": "在患有非综合征性听力损失的个体中已经报道了几种KCNQ4gene突变，这是与其他体征和症状无关的听力损失。该gene的突变会导致一种称为DFNA2的非综合征性听力损失。这种形式的听力损失通常在孩子学会说话（舌后）之后开始，尤其会影响听到高频声音的能力。DFNA2被描述为渐进式的，这意味着随着时间的推移它变得更加严重。(换行)大多数KCNQ4gene突变会改变用于制造KCNQ4蛋白的构建基块（氨基酸）之一。一些突变阻止通道到达需要运输钾离子的细胞膜。其他突变导致形成无法有效转运这些离子的异常通道。功能性KCNQ4通道的丧失似乎会导致内耳某些细胞中钾离子的积累，从而损害这些细胞并导致DFNA2患者进行性听力丧失。"}, {"name": "与年龄有关的听力损失", "detail": "遗传学家庭参考资料提供有关与年龄有关的听力损失的信息。"}]}' WHERE id = 5946;
UPDATE genes SET GHR_metadata_ch = '{"gene": "BCKDHA", "conditions": [{"name": "枫糖浆尿病", "detail": "在枫糖浆尿病患者中已经发现了BCKDHAgene的几个突变，这是一种严重的典型疾病，表现出明显的婴儿尿液甜味，拥有属性喂养不良，呕吐，缺乏能量（嗜睡），运动异常，发育迟缓。这种情况是由于BCKD酶复合物的α亚基中的单个氨基酸变化引起的，该变化破坏了支链氨基酸在细胞和组织中的分解和毒性积累。"}]}' WHERE id = 5953;
UPDATE genes SET GHR_metadata_ch = '{"gene": "LMNB1", "conditions": [{"name": "常染色体显性遗传性脑白质营养不良伴自主神经疾病", "detail": "已发现LMNB1gene中至少有30个突变会导致常染色体显性遗传性白细胞营养不良并伴有自主神经疾病（ADLD）。这种情况的特征是由于髓磷脂的丢失导致神经系统异常，髓磷脂是一种脂肪物质，可隔离神经纤维并促进神经冲动的快速传递。患有ADLD的人在三十多岁或四十多岁时开始出现自主神经系统问题，例如肠和膀胱功能困难。自主神经系统控制不自主的身体过程，如血压和体温的调节。然后，受影响的人会出现运动问题，这些问题会随着时间的推移逐渐恶化。(换行)几乎所有ADLD病例都是由LMNB1gene的异常额外拷贝（重复）引起的。由于这种重复，产生的lamin B1比正常情况更多。在极少数情况下，LMNB1gene开始附近遗传物质的缺失导致lamin B1的产生增加，从而导致ADLD。虽然在整个身体的细胞中都发现了lamin B1，但似乎大脑中的细胞对lamin B1的变化特别敏感。称为少突胶质细胞的细胞有助于用髓磷脂包被神经细胞，似乎受到特别的影响。增加的lamin B1活性导致对髓鞘功能重要的gene表达降低。另外，细胞中层粘连蛋白B1的量增加导致核膜硬化，这可能导致细胞功能问题。随着时间的推移，这些变化导致髓鞘生成和维持减少。（换行）在至少一个患有ADLD的家庭中，该病症是由gene开始附近的遗传物质的丢失（缺失）引起的。据认为，这种缺失去除了有助于控制LMNB1gene表达的DNA调节区。由于该区域的丢失，LMNB1过表达并且lamin B1蛋白的产生增加，类似于由LMNB1复制引起的情况。(换行)在ADLD患者中，髓鞘（脱髓鞘）的丧失发生在大脑和脊髓（中枢神经系统），通常在运动问题发展前几年。脊髓脱髓鞘可能通过损害神经信号从大脑到身体的传递而导致自主神经系统问题。运动问题可能是由于参与协调运动（小脑）的大脑区域以及沿脊髓延伸的神经细胞（皮质脊髓束）和控制自愿肌肉运动所致。"}]}' WHERE id = 5963;
UPDATE genes SET GHR_metadata_ch = '{"gene": "PEX7", "conditions": [{"name": "Refsum疾病", "detail": "PEX7gene突变引起Refsum病的所有病例的一小部分。已知导致这种情况的三个突变降低了过氧化物酶体生物发生因子7的活性，这破坏了几种关键酶（包括植烷酰辅酶A羟化酶）向过氧化物酶体的导入。没有足够的这些酶，过氧化物酶体就不能有效分解脂肪酸和其他物质。（线路中断）在Refsum病患者中，缺乏植烷酰辅酶a羟化酶可防止过氧化物酶体分解植烷酸。相反，这种物质逐渐积聚在身体的组织中。随着时间的流逝，植烷酸的积累对细胞有毒。然而，目前还不清楚这种物质的过量如何影响视觉和嗅觉，并导致Refsum疾病的其他特定特征。"}, {"name": "点状软骨发育不良", "detail": "已发现PEX7gene中的三十多个突变会导致点状软骨发育不良1型（RCDP1）。这些突变往往比引起Refsum疾病的突变更严重。与RCDP1相关的遗传变化通常会导致过氧化物酶体生物发生因子7完全失功能，或阻止细胞产生任何这种蛋白质。负责RCDP1的最常见突变在制造过氧化物酶体生物发生因子7（写为Leu292Ter或L292X）的说明中用蛋白质位置292处的氨基酸亮氨酸替换为过早的终止信号。这种突变导致蛋白质的非功能性版本。（换行）负责RCDP1的PEX7gene突变阻止过氧化物酶体生物发生因子7将关键酶，特别是烷基甘油磷酸合酶转运到过氧化物酶体中。烷基甘油磷酸合酶的缺乏阻止了缩醛磷脂的合成。这些脂质分子产生的问题似乎引起RCDP1的体征和症状。然而，研究人员仍在努力确定缺乏缩醛磷脂如何导致骨骼异常，智力残疾以及这种疾病的其他特征。"}]}' WHERE id = 5973;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SFRP4", "conditions": [{"name": "派尔病", "detail": "在患有称为Pyle病的骨病患者中发现了至少四个SFRP4gene突变。这种情况的特征是骨骼异常，其中手臂和腿部长骨的末端（干phy端）异常宽，类似于船oar或桨。Pyle病中的其他骨骼也可能异常，包括项圈骨骼（锁骨），肋骨以及手指和手中的骨骼。人们认为SFRP4gene突变会导致功能受损的异常短的SFRP4蛋白产生，或者根本不会导致SFRP4蛋白产生。研究表明，功能性SFRP4的丧失会失调Wnt信号，从而破坏正常的骨骼发育和重塑。骨形成异常导致Pyle病的特征。"}, {"name": "Dupuytren挛缩", "detail": "遗传学家庭参考提供有关Dupuytren挛缩的信息。"}]}' WHERE id = 6002;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ATP2A2", "conditions": [{"name": "达里埃病", "detail": "已发现ATP2A2gene中的130多个突变会引起Darier病。这些突变大多数会改变SERCA2酶中的单个蛋白质结构单元（氨基酸）。所有突变都会导致产生无功能的SERCA2酶，或者不会导致从该gene的一个拷贝中产生SERCA2。仅具有一个功能性拷贝的ATP2A2gene的细胞产生正常量的SERCA2蛋白的一半。认为SERCA2的量不足与外部因素如热和轻微损伤相结合会引起Darier病的体征和症状。"}]}' WHERE id = 6003;
UPDATE genes SET GHR_metadata_ch = '{"gene": "OPN1MW", "conditions": [{"name": "色觉缺陷", "detail": "涉及OPN1MWgene的几种遗传变化会导致红绿色色觉缺陷，这是一种色觉缺陷形式，使得难以或不可能区分红色，黄色和绿色的阴影。大多数红绿色视觉缺陷是由涉及OPN1LW和OPN1MWgene的结构重排引起的。因为这些gene非常相似，所以当gene从父母传给孩子时，它们偶尔会交换遗传物质。这种称为重组的交换最终可以从一个或两个gene中删除遗传物质或导致形成含有部分OPN1MWgene和部分OPN1LWgene的杂合色素gene。(换行)较少见的是，红绿色视觉缺陷可能是由OPN1MWgene突变引起的，该突变改变了中波敏感感光色素中的单个蛋白质构建块（一个氨基酸）。该突变用位置203处的氨基酸精氨酸取代氨基酸半胱氨酸（写为Cys203Arg或C203R）。（换行）当OPN1MWgene突变导致完全无功能的M视锥细胞时，色觉完全取决于其他两种视锥细胞。由M锥功能完全丧失引起的特定类型的红绿色色觉缺陷称为氘代。当部分功能性杂种色素gene替代正常的OPN1MWgene时，就会发生一种称为子宫肌瘤的不太严重的红绿色视觉缺陷。由这些杂合gene制成的光色素通常具有损害红绿色视觉的异常视觉特性。(换行)一种罕见的色觉缺陷，蓝锥单色性，严重降低视力清晰度（视力）并影响感知大多数颜色的能力。这种情况还包括红绿色色觉缺陷通常不会发现的其他视觉问题。当遗传变化阻止OPN1MW和OPN1LWgene产生的视蛋白色素正常运行时，就会发生蓝锥单色性。在某些情况下，这种情况是由LCR的缺失引起的，LCR通常控制OPN1MW和OPN1LWgene的活性。LCR的丧失阻止了两种gene产生色素。结果，患有这种情况的人仅具有具有短波敏感感光色素（S锥）的功能锥，这导致视敏度降低和色觉差。视锥异常也是蓝锥单色症患者其他视力问题的基础。"}]}' WHERE id = 6022;
UPDATE genes SET GHR_metadata_ch = '{"gene": "CHEK2", "conditions": [{"name": "乳腺癌", "detail": "已经发现CHEK2gene的突变与乳腺癌有关，乳腺癌是一种以乳腺中某些细胞的异常生长和增殖为特征的疾病，主要见于女性，但也见于男性。临床症状包括乳房内或乳房附近的肿块或增厚，乳房大小或形状的改变，乳头溢液，压痛或缩回（向内转向），皮肤刺激和凹陷，或由于肿瘤丧失引起的鳞屑CHEK2蛋白的抑制活性。"}, {"name": "李-佛美尼症候群", "detail": "CHEK2gene中称为1100delC的缺失突变已出现在患有Li-Fraumeni综合征的多个家族中，Li-Fraumeni综合征是一种患有多种癌症的疾病，包括乳腺癌，骨肉瘤，软组织肉瘤。白血病和肾上腺皮质癌。"}, {"name": "卵巢癌", "detail": "已发现CHEK2gene突变与卵巢癌有关，卵巢癌是一种以CHEK2蛋白的肿瘤抑制活性丧失而导致女性某些卵巢细胞异常生长和增殖为特征的疾病。随着癌症的进展，其表现出临床症状，包括骨盆或下腹部疼痛或沉重感，腹胀，进食时感觉充分，背痛，月经间或绝经后阴道出血，或尿或排便习惯改变。"}, {"name": "前列腺癌", "detail": "CHEK2gene的遗传突变与男性通常在中年或晚年出现的前列腺癌有关。癌症进展显示症状包括例如；难以开始或停止尿液流动，无法完全排空膀胱的感觉，尿液或精液中的血液，或由于CHEK2蛋白的肿瘤抑制活性丧失而引起的射精疼痛。"}, {"name": "其他癌症", "detail": "已经发现CHEK2gene的突变，包括1100delC突变gene，与影响人体许多器官和组织的遗传性和非遗传性（散发性）癌症有关，还与前列腺癌，乳腺癌，肺癌，结肠癌，肾癌，甲状腺癌和卵巢癌有关。"}]}' WHERE id = 6037;
UPDATE genes SET GHR_metadata_ch = '{"gene": "CHRNA4", "conditions": [{"name": "常染色体显性遗传夜间额叶癫痫", "detail": "在常染色体显性遗传性夜间额叶癫痫（ADNFLE）患者中，CHRNA4gene至少鉴定出四个突变。这些突变中的大多数会改变单个蛋白质的结构单元（氨基酸）？nAChR通道的4个亚基；一个突变将一个额外的氨基酸插入？4亚基。(换行)CHRNA4突变使nAChR通道对神经递质乙酰胆碱更加敏感，从而使通道比平时更容易打开。穿过细胞膜的离子流的增加改变了神经递质的释放，这改变了神经元之间的信号传导。研究人员认为，大脑中某些神经元的过度兴奋会引发与癫痫发作相关的异常大脑活动。目前尚不清楚为什么ADNFLE中出现的癫痫发作始于大脑额叶，最常发生在睡眠期间。"}, {"name": "其他疾病", "detail": "CHRNA4gene的几种变异（多态性）可能会导致一个人吸烟依赖的风险。香烟和其他形式的烟草含有尼古丁，尼古丁是一种与大脑中nAChR通道相互作用的药物，可以产生更高的幸福感和警觉性。大脑中的这些变化使尼古丁高度上瘾。(换行)由于尼古丁主要通过与nAChR通道相互作用而对大脑发挥作用，因此研究人员研究了这些通道的亚基，以了解遗传变化是否影响烟草依赖。他们发现，几种CHRNA4多态性与一个人上瘾的风险有关。每个多态性改变一个单一的氨基酸在？nAChR通道的4个亚基。这些变化改变了nAChR通道的结构，这可能会影响大脑中神经递质（包括多巴胺）的释放和摄取。目前还不清楚这些大脑化学变化如何影响依赖烟草的风险。(换行)研究表明，遗传因素在一个人易受烟草依赖的影响中起着重要作用。然而，像其他形式的成瘾一样，烟草依赖是由多种遗传和环境因素决定的复杂行为。"}]}' WHERE id = 6043;
UPDATE genes SET GHR_metadata_ch = '{"gene": "DCC", "conditions": [{"name": "先天性镜像运动障碍", "detail": "在患有先天性镜像运动障碍的人中已经鉴定出至少11种DCCgene突变，其中身体一侧的有意运动被另一侧的不自主运动镜像。这些突变改变netrin-1受体中的单个蛋白质构建块（氨基酸）或在制备蛋白质的说明书中引入过早的终止信号，导致蛋白质受损或缺失。功能性netrin-1受体蛋白不足会损害神经系统发育过程中轴突生长的控制。结果，来自大脑每一半的运动信号异常地传输到身体的两侧，导致镜子运动。"}, {"name": "癌症", "detail": "在超过70%的结肠直肠癌以及其他癌性肿瘤中发现了包括DCCgene在内的遗传物质的缺失。这种缺失不是遗传的，仅在肿瘤细胞中发现。DCCgene的缺失导致netrin-1受体的缺失。结果，netrin-1受体不能触发细胞凋亡，导致不受控制的细胞生长和分裂，导致癌症。"}]}' WHERE id = 6046;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ACAT1", "conditions": [{"name": "β-酮硫解酶缺乏症", "detail": "已经在由无功能的ACAT1酶缺陷引起的β-酮硫解酶缺乏症患者中鉴定出ACAT1突变。这种情况的特征是酮症酸中毒导致器官和组织损伤，主要是神经系统引起呕吐，脱水和其他健康问题。"}]}' WHERE id = 6125;
UPDATE genes SET GHR_metadata_ch = '{"gene": "GTF2H5", "conditions": [{"name": "毛滴虫营养不良", "detail": "已经发现GTF2H5gene中的至少三个突变引起毛发硫代营养不良。该gene的突变导致该病症的光敏形式，其特征在于对来自太阳光的UV射线的极端敏感性。(换行)每个已知的GTF2H5gene突变都会导致TTDA蛋白的非功能性产生。该蛋白质的丢失可能导致TFIIH复合物变得不稳定，这大大减少了细胞内该复合物的量。如果没有足够的TFIIH复合物，细胞就无法有效修复紫外线辐射引起的DNA损伤。DNA修复中的这些问题会导致患有光敏性毛发硫代营养不良症的人对阳光极为敏感。目前尚不清楚TTDA蛋白的丢失如何导致该病的其他特征，例如生长缓慢，智力残疾和头发脆弱。"}]}' WHERE id = 6135;
UPDATE genes SET GHR_metadata_ch = '{"gene": "MYBPC1", "conditions": [{"name": "1型远端关节置换术", "detail": "已发现MYBPC1gene中至少有一个突变会导致1型远端关节置换术，这种疾病的特征是手脚关节畸形（挛缩）。该突变改变了肌球蛋白结合蛋白C的慢同工型中的单个蛋白结构单元（氨基酸）。具体而言，该突变用蛋白236位的氨基酸精氨酸取代了氨基酸色氨酸（写为Trp236Arg或W236R）。目前尚不清楚缺陷蛋白如何导致1型远端关节置换术患者挛缩，或者为什么关节问题通常仅限于手和脚。但是，研究人员推测，挛缩可能与肌肉收缩问题有关，这些问题限制了出生前关节的运动。"}]}' WHERE id = 6158;
UPDATE genes SET GHR_metadata_ch = '{"gene": "KCNJ2", "conditions": [{"name": "安德森-塔维尔综合症", "detail": "在患有Andersen-Tawil综合征的人中已鉴定出KCNJ2gene的40多个突变，该疾病会导致肌肉无力（周期性麻痹），心律变化（心律不齐）和发育异常。这些突变大多数会改变KCNJ2蛋白中的单个蛋白结构单元（氨基酸）。KCNJ2gene中的（换行）突变导致产生无功能的钾通道。一些突变会改变通道的形状，因此它无法转运钾离子，而其他突变则阻止通道正确插入细胞膜。许多KCNJ2突变阻止PIP2有效结合并激活钾通道。如果KCNJ2蛋白无法与PIP2结合，则通道保持关闭状态，钾离子无法流过细胞膜。研究人员认为，PIP2结合问题是Andersen-Tawil综合征的主要原因。(换行)骨骼肌和心肌细胞中通道功能的丧失破坏了钾离子从这些细胞中的正常流动，导致周期性麻痹和心律不规则。尚不清楚KCNJ2gene的突变如何导致Andersen-Tawil综合征中常见的头部，面部和四肢发育异常。"}, {"name": "短QT综合征", "detail": "KCNJ2gene的突变也可引起称为短QT综合征的心脏病，这是一种心律失常。在患有这种情况的人中，心肌在心跳之间花费的时间比平时少。这种变化增加了可能导致晕厥或猝死的异常心律的风险。（换行）已发现KCNJ2gene中的至少两个突变在少数受影响的家庭中引起短QT综合征。这些突变改变了KCNJ2蛋白中的单个氨基酸，从而增加了用该蛋白制成的通道的活性。结果，在心跳期间的关键时刻，更多的钾离子从心肌细胞中流出，这可能导致心律不规则。"}, {"name": "家族性心房颤动", "detail": "遗传学家庭参考资料提供有关家族性心房颤动的信息。"}]}' WHERE id = 6161;
UPDATE genes SET GHR_metadata_ch = '{"gene": "KCNQ2", "conditions": [{"name": "良性家族性新生儿癫痫发作", "detail": "在大多数良性家族性新生儿癫痫发作（BFNS）患者中已鉴定出KCNQ2gene突变，这种疾病的特征是新生儿反复发作（癫痫）。癫痫发作始于生命的第3天左右，通常在1至4个月内消失。在患有这种情况的家庭中，已经鉴定出KCNQ2gene的60多个突变。有时，突变的蛋白质永远不会到达细胞表面形成通道，或者通道可能位于神经元的错误部分。或者，由突变蛋白形成的通道可能无法正常工作。这些突变的结果是，M电流减少或改变，从而导致神经元过度兴奋。研究人员认为，将M电流降低25%足以引起BFNS。当大脑中的神经元异常兴奋时会发生癫痫发作。目前尚不清楚为什么癫痫发作会在4个月大时停止。有人提出，由KCNQ2和KCNQ3蛋白形成的钾通道在防止新生儿神经元过度兴奋中起主要作用，但是在婴儿期还会出现其他阻止恒定神经元活动的机制。"}, {"name": "其他疾病", "detail": "KCNQ2gene的突变也与早发性癫痫性脑病有关，这是一种比BFNS（如上所述）更为严重的疾病，其特征是癫痫和严重的智力障碍。癫痫发作始于生命的最初几周，通常对治疗反应很小。他们通常会在几个月到几年内消失，但可以在童年后期返回。大多数受影响的人无法说话，他们的肌张力低（肌张力低下）或肌肉非常僵硬，导致运动困难。(换行)有人提出，引起早发性癫痫性脑病的KCNQ2gene突变会导致异常KCNQ2蛋白的产生，该蛋白仍可与正常亚基结合形成钾通道。但是，异常KCNQ2亚基的存在使通道无法正常工作，这可能导致M电流严重降低。神经元过度兴奋会导致癫痫发作和脑功能障碍（脑病）。(换行)由KCNQ2gene突变引起的早发性癫痫性脑病类似于一种称为Ohtahara综合征的疾病；但是，大田原综合症患者的癫痫发作通常不会消退。目前尚不清楚由KCNQ2gene突变引起的癫痫性脑病是Ohtahara综合征的一种形式还是单独的疾病。"}]}' WHERE id = 6186;
UPDATE genes SET GHR_metadata_ch = '{"gene": "EIF2B4", "conditions": [{"name": "白质消失的白质脑病", "detail": "EIF2B4gene的突变已在少数白质消失的白质脑病患者中发现，包括一些受影响的女性，其神经学特征伴有卵巢功能衰竭（卵巢白细胞营养不良）。这些突变导致eIF2B功能部分丧失。eIF2B功能受损使人体细胞更难以调节蛋白质合成并应对不断变化的conditions和压力。研究人员认为，白质中的细胞（神经纤维被称为髓鞘的脂肪物质覆盖，能够隔离和保护神经）可能特别受到压力异常反应的影响，导致白质消失的白质脑病的体征和症状。"}]}' WHERE id = 6201;
UPDATE genes SET GHR_metadata_ch = '{"gene": "PKLR", "conditions": [{"name": "丙酮酸激酶缺乏症", "detail": "在丙酮酸激酶缺乏症患者中已鉴定出PKLRgene的200多个突变。患有这种疾病的人每个细胞中都有两个PKLRgene突变。导致丙酮酸激酶缺乏症的大多数突变都会替代丙酮酸激酶中的单个蛋白质结构单元（氨基酸）或导致酶异常短。突变导致丙酮酸激酶功能降低，导致红细胞中ATP缺乏，糖酵解过程中较早产生的其他分子水平升高。异常的红细胞被脾脏聚集并破坏。(换行)由此导致的携氧红细胞（贫血）短缺导致极度疲倦（疲劳），异常苍白的皮肤（苍白）和呼吸短促。当红细胞被破坏时，铁和称为胆红素的分子被释放，导致这些物质在血液中循环过量。血液中过量的胆红素会导致眼睛和皮肤变黄（黄疸），并增加在胆囊或胆管（胆结石）中形成小卵石样沉积物的风险。"}, {"name": "其他疾病", "detail": "研究人员认为，在每个细胞中具有一个PKLRgene突变拷贝的人可以部分地预防疟疾，这是一种由某种类型的蚊子携带的传染病。导致功能性丙酮酸激酶数量减少的突变似乎使该寄生虫更难以侵入红细胞。研究表明，来自非洲人口的个体（疟疾是儿童的常见死亡原因）在每个细胞中携带一份突变的PKLRgene拷贝的频率是欧洲血统个体的两倍以上。在这些非洲人群中，PKLRgene突变的频率增加可能有助于抵抗疟疾。"}]}' WHERE id = 6255;
UPDATE genes SET GHR_metadata_ch = '{"gene": "BMPR1A", "conditions": [{"name": "青少年息肉综合征", "detail": "已经发现BMPR1Agene的突变会引起青少年息肉综合症，这种疾病的特征是细胞生长不受调节，导致20岁之前息肉形成，从而引起胃肠道出血，贫血，腹痛和腹泻。这是由于异常短的或无功能的蛋白质损害其在TGF-β中的配体结合而引起的？通路。"}]}' WHERE id = 6266;
UPDATE genes SET GHR_metadata_ch = '{"gene": "POLR1C", "conditions": [{"name": "Treacher Collins综合征", "detail": "在患有Treacher Collins综合征的人中，已经鉴定出POLR1Cgene中至少有6个突变，这种情况会影响骨骼和面部其他组织的发育。这些突变似乎改变了POLR1C蛋白的结构和功能，从而减少了细胞中功能性RNA聚合酶I和RNA聚合酶III的数量。因此，产生较少的rRNA。研究人员推测，rRNA的缺乏可能会触发某些参与面部骨骼和组织早期发育的细胞的自我破坏（凋亡）。异常的细胞死亡可能是Treacher Collins综合征中发现的面部发育特定问题的基础。但是，尚不清楚为什么减少rRNA的作用仅限于面部发育。"}]}' WHERE id = 6270;
UPDATE genes SET GHR_metadata_ch = '{"gene": "BCHE", "conditions": [{"name": "假胆碱酯酶缺乏症", "detail": "在假胆碱酯酶缺乏症患者中已经鉴定出BCHEgene的50多个突变，这种情况导致对胆碱酯和某些其他药物的敏感性增加。这些突变中的一些取代了假胆碱酯酶中的单个蛋白质结构单元（氨基酸），导致异常的酶无法正常运行。其他突变阻止假胆碱酯酶的产生。功能性假胆碱酯酶缺乏（缺乏）会损害人体有效分解胆碱酯药物的能力，从而导致药物作用异常延长。"}]}' WHERE id = 6273;
UPDATE genes SET GHR_metadata_ch = '{"gene": "COMP", "conditions": [{"name": "多发性骨phy发育不良", "detail": "已鉴定出COMPgene中导致显性多发性骨phy发育异常的20多个突变。这种疾病也可能是由其他四个gene的突变引起的；但是，大多数人的COMPgene都有突变。（换行）COMPgene中引起显性多发性骨phy发育异常的突变会改变一个蛋白质结构单元（氨基酸）或导致COMP蛋白质中氨基酸的少量添加或缺失。所有已鉴定的突变均发生在COMP蛋白的两个区域，分别称为III型和C端结构域。COMP突变导致COMP蛋白在内质网中折叠不当，内质网是参与蛋白质加工和运输的细胞结构。异常的COMP蛋白无法离开内质网，从而导致该细胞结构扩大。内质网最终变得如此之大以至于它不再能够正常运作，并且软骨细胞死亡。软骨细胞过早死亡导致长骨生长减少和身材矮小。(换行)研究人员认为，软骨细胞之间空间中缺乏COMP蛋白会导致异常软骨的形成。这种异常的软骨可能很容易分解，从而导致早发性骨关节炎。"}, {"name": "假性软骨发育不全", "detail": "在假性软骨发育不全的个体中已鉴定出COMPgene的约60个突变。在大约30%的受影响个体中发现了一种特定的突变。这种突变导致COMP蛋白中单个氨基酸（称为天冬氨酸）的缺失。该gene突变通常写成469delD或D469del。大多数其他COMPgene突变涉及将COMP蛋白中的一个氨基酸替换为另一个氨基酸。导致假性软骨发育不全的COMPgene突变也会导致COMP蛋白在内质网中积聚并最终导致软骨细胞死亡。目前尚不清楚为什么COMPgene中的某些突变会导致假性软骨发育不全，而其他突变会导致显性多发性骨phy发育不良。"}]}' WHERE id = 6274;
UPDATE genes SET GHR_metadata_ch = '{"gene": "UTP4", "conditions": [{"name": "北美印第安人儿童期肝硬化", "detail": "已发现UTP4gene中至少有一个突变会导致北美印第安人儿童期肝硬化，这是在魁北克的美洲原住民人群中发现的一种慢性肝病。已知的突变用蛋白质位置565（写为Arg565Trp或R565W）上的氨基酸色氨酸取代了氨基酸精氨酸。这种遗传变化可能会改变cirhin的结构和功能。但是，尚不清楚为什么UTP4gene突变的作用仅限于肝脏，或者它们如何引起这种疾病的进行性肝损伤。"}]}' WHERE id = 6304;
UPDATE genes SET GHR_metadata_ch = '{"gene": "MIR145", "conditions": [{"name": "5q减综合征", "detail": "MIR145gene参与称为5q减（5q-）综合征的病症。这种情况是一种称为骨髓增生异常综合征（MDS）的骨髓疾病，其中未成熟的血细胞不能正常发育。患有5q综合征的人通常缺乏红细胞（贫血）和巨核细胞异常。受影响的个体患上称为急性髓细胞性白血病（AML）的快速增长的血癌的风险也增加。（换行）5q-综合征是由染色体5的长（q）臂缺失DNA区域引起的。这种缺失发生在一个人一生中未成熟的血细胞中，并影响每个细胞中5号染色体的一个拷贝。大多数患有5q-综合征的人缺少约150万个DNA构建块（碱基对）的序列，也写为1.5兆碱基（Mb）。该缺失区域含有40个gene，包括MIR145。丢失一个拷贝的MIR145gene减少了细胞中microRNA miR-145的量。结果，其产生通常被miR-145阻断的蛋白质水平升高，这导致在5q-综合征中发生的巨核细胞的异常发育。研究表明，该病的其他特征与DNA缺失片段中的其他gene有关。"}]}' WHERE id = 6318;
UPDATE genes SET GHR_metadata_ch = '{"gene": "PRNP", "conditions": [{"name": "亨廷顿病样综合征", "detail": "已发现PRNPgene中的特定类型的突变会引起类似于亨廷顿病的体征和症状，包括不受控制的运动，情绪问题和思维能力丧失。研究人员提出这种情况称为亨廷顿病样1（HDL1）。（换行）与HDL1相关的PRNP突变涉及称为八肽重复的DNA片段。该区段提供了制备八种蛋白质构建块（氨基酸）的说明，所述八种蛋白质构建块连接以形成称为肽的蛋白质片段。八肽重复通常在PRNPgene中重复五次。在患有HDL1的人中，该细分重复11或13次。八肽重复序列大小的增加导致异常长版本的PrP的产生。目前尚不清楚异常蛋白质如何损害并最终破坏神经元，从而导致HDL1的特征。"}, {"name": "朊病毒病", "detail": "在家族性病毒病患者中已鉴定出PRNPgene的30多个突变，包括克雅氏病（CJD），Gerstmann-Sträussler-Scheinker综合征（GSS）和致命性家族性失眠（FFI）。这些疾病的主要特征包括记忆力，性格和行为的改变；智力功能下降（痴呆）；和异常运动，特别是协调运动困难（共济失调）。症状和体征随着时间的推移而恶化，最终导致死亡。（换行）一些引起家族性朊病毒病的PRNPgene突变改变了PrP中的单个氨基酸。其他突变会在蛋白质中插入其他氨基酸或导致蛋白质异常短的版本。这些变化改变了PrP的结构，导致从PRNPgene的一个拷贝中产生了异常形状的蛋白质，称为PrPSc。在一个尚未完全了解的过程中，PrPSc可以附着（绑定）到PrPC并促进其转化为PrPSc。异常蛋白质在大脑中积聚，形成团块，破坏或破坏神经元。这些细胞的损失在大脑中产生微观的海绵状孔（空泡），导致朊病毒病的体征和症状。(换行)研究人员已经确定了PRNPgene中影响PrP中单个氨基酸的几种常见变异（多态性）。这些多态性不会引起病毒疾病，但可能会影响一个人患这些疾病的风险。研究集中在PrP第129位的多态性的影响。在这个位置，人们可以拥有氨基酸蛋氨酸（Met）或氨基酸缬氨酸（Val）。该多态性写为Met129Val或M129V。由于人们从父母双方继承PRNPgene的一个拷贝，在第129位，一个人可以从父母双方（Met/Met）获得蛋氨酸，从父母双方（Val/Val）获得缬氨酸，从父母一方获得蛋氨酸，从父母另一方获得缬氨酸（Met/Val）。(换行)Met129Val多态性似乎会影响患病毒疾病的风险。大多数受影响的个体在129位具有相同的氨基酸（Met/Met或Val/Val），而不是不同的氨基酸（Met/Val）。在129位遇到/遇到也与发病年龄较早和疾病体征和症状更快恶化有关。"}, {"name": "威尔逊病", "detail": "据报道，Met129Val多态性会影响威尔逊病的发作，威尔逊病是一种遗传性疾病，其中体内积累了过量的铜。威尔逊病是由ATP7Bgene突变引起的，但研究表明，与Met/Val或Val/Val的人相比，PrP中第129位Met/Met的人几年后开始出现威尔逊病的症状。其他研究结果表明，这种多态性也可能影响威尔逊病患者出现的症状类型。在129位遇到Met/Met似乎与影响神经系统的症状（尤其是震颤）的发生增加有关。"}, {"name": "其他疾病", "detail": "Met129Val变异与健康年轻人在长期记忆任务上的表现差异有关。在一项研究中，在129位Met/Met或Met/Val的人在长期记忆任务中的表现优于Val/Val。目前还不清楚这些差异可能与记忆有关。"}]}' WHERE id = 6378;
UPDATE genes SET GHR_metadata_ch = '{"gene": "TRNT1", "conditions": [{"name": "TRNT1缺陷", "detail": "已经发现超过20个TRNT1gene突变导致TRNT1缺陷，这是一系列影响许多身体系统的体征和症状。特征可能包括称为铁粒幼细胞性贫血的血液疾病，反复发烧，称为B细胞的免疫细胞短缺，导致免疫系统受损（免疫缺陷），言语和运动技能发育迟缓以及导致视力问题的眼部异常。病情的严重程度因受影响的个体而异。（换行）导致TRNT1缺陷的TRNT1gene突变导致功能性TRNT1蛋白的短缺（缺陷）。结果，CCA三核苷酸向tRNA分子的添加受到损害。研究人员怀疑，如果没有修饰，tRNA参与蛋白质合成的能力就会降低。研究表明，TRNT1的缺乏会阻止某些参与能量产生的线粒体蛋白复合物的形成。尚不清楚细胞质蛋白合成是否也受到影响。能量的减少可能损害许多身体系统中的细胞，导致TRNT1缺乏的各种体征和症状。研究人员认为，导致TRNT1功能更大损害的突变会导致更严重的体征和症状。"}]}' WHERE id = 6398;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SOX9", "conditions": [{"name": "Campolic发育不良", "detail": "已经发现涉及SOX9gene的70多个突变会导致Campolic发育异常，这是一种影响骨骼发育，性别决定和身体其他过程的疾病，在新生儿期通常会危及生命。大多数坎波型不典型增生病例是由SOX9gene内的突变引起的。这些突变阻止了SOX9蛋白的产生或导致蛋白作为转录因子的功能受损。大约5%的病例是由SOX9gene附近发生的染色体异常引起的。这些染色体异常破坏了通常调节SOX9gene活性的称为增强子的DNA区域。所有这些遗传变化都会阻止SOX9蛋白正确控制骨骼，生殖器官和身体其他部位正常发育所必需的gene。这些结构的异常发育会导致坎波型不典型增生的体征和症状。（换行）具有较轻形式的坎波型不典型增生的个体更可能在SOX9gene附近具有染色体异常，而不是gene内的突变。"}, {"name": "孤立的皮埃尔·罗宾序列", "detail": "SOX9gene附近发生的遗传变化导致一些孤立的Pierre Robin序列的病例。患有这种情况的人下颌小（微棘皮症），舌头比正常人（舌苔病）放置得更远，这会阻塞气道。大多数受影响的人出生时的嘴顶也有开口（left裂）。Pierre Robin序列的这些病例被描述为孤立的，因为它们在没有其他体征和症状的情况下发生。（换行）与分离的Pierre Robin序列相关的遗传变化被认为破坏了在下颌发育期间通常调节SOX9gene活性的增强子区域，这降低了SOX9gene活性。结果，SOX9蛋白无法正确控制正常颌骨发育所必需的gene，从而导致微棘皮症。下颌发育不全会影响舌头的位置和pa的形成，从而导致舌苔病，通常会导致c裂。"}, {"name": "46，XX睾丸性发育障碍", "detail": "遗传学家庭参考资料提供有关46，XX睾丸性发育障碍的信息。"}, {"name": "Swyer综合征", "detail": "遗传学家庭参考提供有关Swyer综合征的信息。"}]}' WHERE id = 6406;
UPDATE genes SET GHR_metadata_ch = '{"gene": "TNNI2", "conditions": [{"name": "谢尔顿-霍尔综合征", "detail": "在谢尔顿-霍尔综合征患者中至少鉴定出8个TNNI2gene突变。这种疾病会影响出生前的肌肉和骨骼发育，其特征是关节畸形（挛缩）限制了手和脚的运动。研究人员建议，引起Sheldon-Hall综合征的TNNI2gene突变可能会阻止肌钙蛋白复合物阻止粗细丝结合以控制肌肉收缩，从而导致挛缩以及与Sheldon-Hall综合征相关的其他肌肉和骨骼异常。"}]}' WHERE id = 6408;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ENG", "conditions": [{"name": "遗传性出血性毛细血管扩张", "detail": "已发现ENGgene中的数十个突变会导致1型遗传性出血性毛细血管扩张。许多ENGgene突变将内皮糖蛋白中的一个蛋白质结构单元（氨基酸）替换为另一个氨基酸，从而损害了该蛋白的功能。其他突变会阻止内皮糖蛋白的产生或导致异常小的蛋白无法发挥作用。功能性内皮糖蛋白的缺乏似乎会干扰动脉和静脉之间边界的发展，从而导致1型遗传性出血性毛细血管扩张的体征和症状。"}, {"name": "肺动脉高压", "detail": "遗传学家庭参考资料提供有关肺动脉高压的信息。"}]}' WHERE id = 6412;
UPDATE genes SET GHR_metadata_ch = '{"gene": "HLA-B", "conditions": [{"name": "强直性脊柱炎", "detail": "HLA-Bgene的几种变异增加了发生强直性脊柱炎的风险，特别是称为HLA-B27的版本。不确定这种变化如何导致风险增加。研究人员推测，HLA-B27可能异常显示引发免疫反应的肽，导致引起关节炎的炎症过程。其他研究表明，这种疾病的关节炎症特征可能是由于HLA-B27蛋白折叠不当或细胞表面存在异常形式的蛋白质所致。尽管许多强直性脊柱炎患者都有HLA-B27变异，但大多数患有此版本HLA-Bgene的人从未患上这种疾病。其他遗传和环境因素，其中许多是未知的，影响发展强直性脊柱炎的机会并影响其进展。"}, {"name": "白塞病", "detail": "几种形式的HLA-Bgene，特别是HLA-B51，与发展Behçet病（一种影响身体许多部位的慢性炎症）的风险增加有关。这个协会在日本，中东和亚洲其他地区的人群中最强。研究人员不知道HLA-B51如何增加这种疾病的风险。尽管许多患有Behçet病的人都有HLA-B51变异，但大多数具有此版本HLA-Bgene的人从未患上这种疾病。似乎其他因素，例如病毒或细菌感染以及其他gene的变化，也可能影响这种复杂疾病的发展。"}, {"name": "幼年特发性关节炎", "detail": "遗传学家庭参考提供有关青少年特发性关节炎的信息。"}, {"name": "银屑病关节炎", "detail": "遗传学家庭参考提供有关银屑病关节炎的信息。"}, {"name": "类风湿关节炎", "detail": "遗传学家庭参考提供有关类风湿关节炎的信息。"}, {"name": "带状疱疹", "detail": "遗传学家庭参考提供有关带状疱疹的信息。"}, {"name": "史蒂文斯-约翰逊综合征/中毒性表皮坏死松解症", "detail": "已经研究了HLA-Bgene的几种变异作为史蒂文斯-约翰逊综合征/中毒性表皮坏死松解症（SJS/TEN）的危险因素，这是一种潜在的威胁生命的皮肤反应，最常由药物引发。例如，HLA-B*1502变异会增加服用某些用于治疗癫痫发作的药物（尤其是称为卡马西平的药物）的人发生SJS/TEN的风险。该gene的这一版本在汉族或东南亚血统的人群中最为常见。该gene的另一个版本HLA-B*5801增加了用别嘌呤醇（一种用于治疗肾结石和痛风的药物，这是一种由尿酸积聚引起的关节炎形式）治疗的人的SJS/TEN风险。关节）。尽管HLA-B*5801在非亚洲人群中发生的频率较低，但这种关联已在东南亚人和非亚洲血统人群中得到证实。(换行)研究表明，与SJS/TEN相关的HLA-Bgene变异会导致免疫系统对某些药物异常反应。在一个尚不清楚的过程中，触发药物引起称为细胞毒性T细胞和自然杀伤（NK）细胞的免疫细胞释放称为颗粒溶素的物质。该物质破坏皮肤和粘膜中的细胞，包括口腔和气道内壁。这些细胞的死亡会导致严重的起泡和剥落，从而可能危及生命。(换行)大多数HLA-Bgene变异与SJS/TEN风险增加相关的人即使接触可能引发这种疾病的药物也不会出现这种情况。研究人员认为，额外的遗传和其中许多未知的非遗传因素可能在特定个体是否发展SJS/TEN中起作用。"}, {"name": "其他疾病", "detail": "HLA-B27变体与一组与强直性脊柱炎相关的炎性关节疾病相关。这些情况被称为脊柱关节病。这些疾病中的一些与称为牛皮癣的常见皮肤病或引起肠壁炎症（炎性肠病）的慢性疾病有关。脊柱关节病之一，反应性关节炎，通常由胃肠道或生殖道的细菌感染引发。感染后，受影响的个体可能会出现关节炎，背痛和眼部炎症。像强直性脊柱炎一样，许多因素可能导致反应性关节炎和其他脊柱关节病的发展。（换行）在人类免疫缺陷病毒（HIV）感染者中，HLA-BgeneHLA-B*5701版本增加了药物阿巴卡韦不良反应（超敏反应）的风险。这种药物可以减缓HIV-1病毒在体内的传播。阿巴卡韦过敏的人在用这种药物治疗时经常会出现发烧，发冷，皮疹，胃部不适和其他症状。(换行)HLA-Bgene的几种变异似乎在HIV感染进展为获得性免疫缺陷综合征（AIDS）中起作用。艾滋病是一种损害免疫系统，阻止其有效防御身体免受感染的疾病。艾滋病的体征和症状可能直到感染艾滋病毒后10年或更长时间才出现。研究表明，患有HLA-B27或HLA-B57的HIV感染者往往比平时进展得更慢。另一方面，研究人员认为，患有HLA-B35的HIV阳性个体往往比平时更快地出现艾滋病的体征和症状。其他因素也影响艾滋病毒感染艾滋病的进展。(换行)另一种版本的HLA-BgeneHLA-B53已被证明有助于预防严重疟疾，这是一种由蚊子携带的寄生虫引起的疾病。HLA-B53在西非人群中最常见，疟疾是儿童死亡的常见原因。研究表明，此版本的HLA-Bgene可能有助于免疫系统更有效地应对引起疟疾的寄生虫。"}]}' WHERE id = 6418;
UPDATE genes SET GHR_metadata_ch = '{"gene": "DNAJC19", "conditions": [{"name": "扩张型心肌病伴共济失调综合征", "detail": "已经发现DNAJC19gene中的至少两个突变引起共济失调（DCMA）综合征的扩张型心肌病。这种情况的特征是心脏问题，运动困难，生长缓慢，男性生殖器异常以及影响多个身体系统的其他特征。DNAJC19gene突变导致产生异常缩短的蛋白质，该蛋白质可能功能受损。在加拿大Dariusleut Hutterite人群中，DCMA综合征最常见，这种情况是由一种突变（写为IVS3-1G C）引起的，这种突变导致gene指令被用来制造DNAJC19蛋白的方式中断，导致部分蛋白缺失。(换行)研究人员推测，缺乏功能性DNAJC19蛋白会改变蛋白质进出线粒体的运输。当太多或太少的蛋白质进出线粒体时，能量产生和线粒体存活可以减少。具有高能量需求的组织，例如心脏和大脑，特别容易受到细胞能量产生减少的影响。细胞能量的这种损失可能损害这些组织和其他组织，导致心脏问题，运动困难和DCMA综合征的其他特征。"}]}' WHERE id = 6428;
UPDATE genes SET GHR_metadata_ch = '{"gene": "IL7R", "conditions": [{"name": "多发性硬化症", "detail": "IL-7Rgene的常见变异增加了发生多发性硬化症的风险。这种情况会影响大脑和脊髓（中枢神经系统），导致肌肉无力，协调不良，麻木以及各种其他健康问题。涉及多发性硬化症的遗传变异影响IL-7受体α链中的单个蛋白质构建块（氨基酸），指定位置244处的氨基酸异亮氨酸而不是氨基酸苏氨酸（写为Thr244Ile）。含有该版本的α链的IL-7受体不嵌入细胞表面，而是在细胞内发现。尚不清楚该α链变体是否影响TSLP受体。（换行）由于IL7Rgene参与免疫系统的调节，其变化可能参与自身免疫反应和炎症，损害神经及其周围的保护性涂层（髓鞘），导致体征和多发性硬化症的症状。（当免疫系统发生故障并攻击身体自身的组织和器官，在这种情况下是神经系统的组织时，就会发生自身免疫。）然而，目前尚不清楚IL-7Rgene变异在多发性硬化症的发展中究竟起什么作用。可能涉及遗传和环境因素的组合。"}, {"name": "联合免疫缺陷病", "detail": "遗传学家庭参考提供有关Omenn综合征的信息。"}, {"name": "其他疾病", "detail": "IL7Rgene的突变可导致一种严重的联合免疫缺陷症（SCID），其中受影响的个体免疫功能下降，容易反复和持续感染。具有IL7Rgene突变的受影响个体几乎没有T细胞，但B细胞和另一种称为NK细胞的免疫细胞数量正常。这种形式的conditions称为T-B+NK+SCID。导致SCID的许多IL7Rgene突变会阻止IL-7受体α链的产生，从而损害IL-7受体和TSLP受体信号传导。没有这种信号传导，T细胞的发育就会受到干扰。缺乏T细胞会降低免疫系统抵抗感染的能力，导致T-B+NK+SCID患者反复感染。IL7Rgene中的（换行）突变也可引起血液形成细胞的癌症，特别是B细胞急性淋巴细胞白血病（ALL），其特征在于血液中B细胞数量增加，以及T细胞ALL，其特征在于T细胞数量增加。与这些癌症相关的突变导致IL-7受体α链改变。随着这种改变，即使没有细胞因子相互作用，信号传导途径（如由IL-7受体或TSLP受体触发的信号传导途径）也不断开启（组成型活性）。恒定的信号传导增加B细胞或T细胞的增殖和存活，导致ALL。"}]}' WHERE id = 6431;
UPDATE genes SET GHR_metadata_ch = '{"gene": "TRAPPC2", "conditions": [{"name": "X连锁脊柱骨赘发育迟缓", "detail": "已发现TRAPPC2gene中的50多个突变会导致X连锁性脊柱骨赘发育迟缓。这种情况会损害骨骼生长，几乎只发生在男性中，通常出现在6至10岁之间。几乎所有突变都会导致无功能的sedlin蛋白。结果，包括前胶原在内的大蛋白不能被转运出内质网。缺乏前胶原转运会减少细胞中成熟胶原蛋白的量，从而损害骨骼，软骨和其他结缔组织的发育。骨骼发育的这种破坏很可能导致X连锁脊柱骨赘发育迟缓的许多体征和症状，尽管目前尚不清楚为什么骨骼问题直到儿童期后期才出现。"}]}' WHERE id = 6465;
UPDATE genes SET GHR_metadata_ch = '{"gene": "RUNX2", "conditions": [{"name": "颅骨发育不良", "detail": "已在患有颅骨发育不良的个体中鉴定出RUNX2gene的约200个突变，这种情况主要影响骨骼和牙齿的发育。一些突变改变了RUNX2蛋白中的一个蛋白质构建块（氨基酸）。其他突变会引入过早的终止信号，从而导致异常短的无功能蛋白。有时，整个gene会丢失。（换行）这些遗传变化减少或消除了每个细胞中一个RUNX2gene拷贝产生的蛋白质的活性，从而减少了功能性RUNX2蛋白质的总量。功能性RUNX2蛋白的这种缺乏会干扰骨骼，软骨和牙齿的正常发育，从而导致颅骨发育不良的体征和症状。在极少数情况下，包含RUNX2和其他附近gene的遗传物质缺失的个体可能会经历其他特征，例如由于这些gene的丢失而导致的发育延迟。"}, {"name": "其他疾病", "detail": "RUNX2gene片段的额外副本（重复）会导致称为干phy端发育异常，上颌骨发育不全和近距离畸形（MDMHB）的疾病。这种情况的特征是长骨（干meta端）末端附近异常，上颌骨（上颌骨）发育不全和手指短（近距离接触）。在这种疾病中也可能发生其他骨骼异常。研究表明，每个细胞中RUNX2gene一个拷贝中的额外遗传物质会改变RUNX2蛋白的功能，并可能干扰构建骨骼（成骨细胞）的细胞的成熟。然而，改变的RUNX2功能与MDMHB的特定体征和症状之间的关系尚不清楚。"}]}' WHERE id = 6491;
UPDATE genes SET GHR_metadata_ch = '{"gene": "FKRP", "conditions": [{"name": "沃克-沃伯格综合症", "detail": "已发现FKRPgene中至少有五个突变引起Walker-Warburg综合征。这种情况是一组称为先天性肌营养不良的疾病中最严重的形式。Walker-Warburg综合征会导致骨骼肌无力以及大脑和眼睛异常。由于这种情况引起的问题的严重性，受影响的个体通常无法存活到儿童早期。（换行）涉及Walker-Warburg综合征的许多FKRPgene突变会改变FKRP中的单个蛋白质结构单元（氨基酸）。改变后的蛋白质无法到达高尔基体，而是被分解，从而减少了功能性FKRP的量。(换行)FKRP的缺乏会阻止正常的糖基化-dystroglycan。结果是-dystroglycan不再能够有效地将细胞锚定在周围的蛋白质和其他分子上。没有功能-dystroglycan为了稳定肌肉纤维，当它们反复收缩并使用时放松时会受到损坏。受损的纤维会随着时间的推移而减弱和死亡，这会影响Walker-Warburg综合征患者骨骼肌的发育，结构和功能。(换行)有缺陷-dystroglycan还影响大脑早期发育过程中神经元的迁移。一些神经元不是在到达预定目的地时停止，而是通过大脑表面迁移到周围充满液体的空间。研究人员认为，神经元迁移的这个问题会导致大脑异常，称为鹅卵石脑畸形，其中表面大脑缺乏正常的褶皱和凹槽，而是显得颠簸和不规则。关于FKRPgene突变在身体其他部位的影响知之甚少。"}, {"name": "肢带型肌营养不良症", "detail": "遗传学家庭参考资料提供有关肢带型肌营养不良症的信息。"}, {"name": "其他疾病", "detail": "在少数患有1C型先天性肌营养不良症（MDC1C）的人中发现了FKRPgene的突变，该突变会导致肌肉无力，脑部异常和智力残疾，但通常不会影响眼睛。FKRPgene的突变很少与肌肉眼脑疾病相关，后者会导致肌肉无力，眼睛问题和智力残疾。肌肉眼脑疾病的体征和症状不如Walker-Warburg综合征（如上所述）严重。目前尚不清楚FKRPgene的突变如何引起几种不同的肌肉营养不良。"}]}' WHERE id = 6495;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SMARCAL1", "conditions": [{"name": "Schimke免疫性骨发育不良", "detail": "已发现SMARCAL1gene中的40多个突变会增加Schimke免疫性骨发育不良的风险。与Schimke免疫性骨发育不良相关的突变破坏了SMARCAL1蛋白的通常功能或阻止了任何功能蛋白的产生。具有导致完全缺乏功能蛋白的突变的人比具有导致活性但功能异常的蛋白的突变的人倾向于具有更严重的这种疾病形式。SMARCAL1gene的突变被认为通过影响蛋白质活性，蛋白质稳定性或蛋白质与染色质结合的能力而导致疾病。尚不清楚SMARCAL1突变如何导致Schimke免疫性骨发育不良患者的身材矮小，肾脏疾病和免疫系统减弱。为了使具有SMARCAL1gene突变的人发展为Schimke免疫性骨发育不良，还必须存在其他目前未知的遗传或环境因素。"}]}' WHERE id = 6500;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ATM", "conditions": [{"name": "共济失调毛细血管扩张", "detail": "已知ATMgene中的突变会导致共济失调-毛细血管扩张，这是一种严重疾病，会影响控制大脑运动的脑细胞，其特征是不受控制的细胞生长导致癌变性肿瘤。这是由于异常或无功能的ATM蛋白失去了识别DNA损伤和激活DNA修复的能力而引起的。临床特征包括儿童早期共济失调，行走困难，平衡和手部协调问题，不自主的抽搐运动（舞蹈病），肌肉抽搐（肌阵挛），神经病，发展为慢性肺部感染，并与白血病和淋巴瘤风险增加有关。"}, {"name": "乳腺癌", "detail": "ATMgene的突变已知会导致乳腺癌，这是一种拥有属性肿块或在乳房内或附近增厚的肿瘤，乳房大小或形状的改变，乳头溢液，压痛，或收缩（向内转动），以及皮肤刺激，凹陷，或鳞屑，并与转移性癌症的风险增加有关"}, {"name": "其他癌症", "detail": "ATMgene的突变与罹患其他几种癌症（包括乳腺癌，胃癌，膀胱癌，胰腺癌，肺癌和卵巢癌）的风险增加有关。"}]}' WHERE id = 6544;
UPDATE genes SET GHR_metadata_ch = '{"gene": "PRODH", "conditions": [{"name": "高蛋白血症", "detail": "已发现PRODHgene中至少有15个突变会降低脯氨酸氧化酶的活性。这些突变将一个氨基酸替换为酶中的另一个氨基酸，导致其在脯氨酸分解（降解）中的功能效率较低。脯氨酸氧化酶功能的降低会导致体内脯氨酸的积累，在严重的情况下，高脯氨酸血症会导致癫痫发作，智力障碍或其他神经或精神疾病。"}, {"name": "其他疾病", "detail": "几项研究表明PRODHgene变异与精神疾病（如精神分裂症）之间存在关联，而另一些研究则没有显着关联。PRODHgene中的大多数变异导致脯氨酸脱氢酶中一个氨基酸替换为另一个氨基酸。氨基酸取代降低了酶的活性，导致脯氨酸的分解效率降低。研究人员认为，脯氨酸水平升高可能会影响某些在大脑神经元（神经递质）之间传递信号的化学物质的作用，从而导致精神疾病的风险增加。"}]}' WHERE id = 6554;
UPDATE genes SET GHR_metadata_ch = '{"gene": "GUCY2D", "conditions": [{"name": "视锥-视杆营养不良", "detail": "在患有视锥细胞营养不良的视力障碍患者中，已鉴定出GUCY2Dgene至少10个突变。与这种情况相关的问题包括视力丧失（敏锐度），对光的敏感性增加（畏光）和色觉受损。这些视力问题随着时间的推移而恶化。（换行）导致锥杆营养不良的突变发生在每个细胞中GUCY2Dgene的两个拷贝之一中。这些突变约占常染色体显性锥杆营养不良的一种形式的病例的四分之一。这些突变中的大多数会影响GUCY2D蛋白中的特定蛋白结构单元（氨基酸），从而将838位的精氨酸替换为其他几个氨基酸之一。这些遗传变化损害正常的光转导，导致感光细胞随时间恶化。这些细胞的丢失导致视锥细胞营养不良的特征性进行性视力问题。"}, {"name": "莱伯先天性黑蒙", "detail": "已发现GUCY2Dgene中的160多个突变会导致Leber先天性黑病，这种疾病的特征是婴儿期开始出现视力丧失。该gene的突变占该病所有病例的6%至21%。（换行）导致Leber先天性黑蒙的突变发生在每个细胞中GUCY2Dgene的两个拷贝中。这些遗传变化中的大多数会导致GUCY2D蛋白异常短而无功能的版本。缺乏这种蛋白质可防止感光细胞暴露于光照后恢复其黑暗状态。结果，光转导过程几乎完全关闭，导致从生命的早期开始就出现严重的视力障碍。"}]}' WHERE id = 6559;
UPDATE genes SET GHR_metadata_ch = '{"gene": "CTNND2", "conditions": [{"name": "自闭症谱系障碍", "detail": "遗传学家庭参考提供有关自闭症谱系障碍的信息。"}, {"name": "猫叫综合症", "detail": "CTNND2gene位于5号染色体的一个区域，该区域在cri du chat综合征患者中经常被删除。这种删除的结果是，许多患有这种情况的人在每个细胞中都缺少一个CTNND2gene拷贝。该gene的丢失可能会在某些受影响的个体中导致严重的智力残疾。研究人员怀疑，智力障碍可能是由于神经系统早期发育过程中神经元迁移的破坏所致。(换行)没有CTNND2gene缺失的cri du chat综合征患者往往具有较轻的智力障碍或正常智力。"}]}' WHERE id = 6568;
UPDATE genes SET GHR_metadata_ch = '{"gene": "OFD1", "conditions": [{"name": "口腔面部数字综合征", "detail": "在I型口腔面部数字综合征患者中发现了OFD1gene的约100个突变，这是该疾病的最常见形式。这些突变包括单个DNA构建模块（碱基对）的变化以及OFD1gene中遗传物质的较大缺失。这些遗传变化中的大多数导致异常短而无功能的OFD1蛋白产生。目前尚不清楚功能性OFD1蛋白的缺乏如何导致I型口腔面部数字综合征的特定特征。但是，研究表明，缺乏这种蛋白会阻止纤毛的正常形成，从而影响许多组织和器官的发育。"}, {"name": "伯特症候群", "detail": "遗传学家庭参考提供有关Joubert综合征的信息。"}, {"name": "原发性睫状运动障碍", "detail": "遗传学家庭参考提供有关原发性睫状运动障碍的信息。"}, {"name": "辛普森-戈拉比-贝梅尔综合征", "detail": "遗传学家庭参考资料提供有关Simpson-Golabi-Behmel综合征的信息。"}, {"name": "其他疾病", "detail": "OFD1gene的突变也可能导致其他几种疾病，其特征与口腔面部数字综合征的特征重叠。在Joubert综合征患者中至少鉴定出两个突变。这种疾病的特征是特定的大脑异常，肌肉张力弱（肌张力低下），发育迟缓，异常眼球运动和呼吸问题。在一个具有X连锁智力障碍形式的家庭中发现了另一个OFD1gene突变。受影响的人患有严重的智力障碍，异常大的头部（大头畸形）和呼吸问题。(换行)导致Joubert综合征和X连锁智力障碍的OFD1突变导致异常短版本的OFD1蛋白产生。但是，研究表明，这些突变导致的蛋白质比引起口腔面部数字综合症的突变更长。这种蛋白质可能在纤毛中保留其一些通常的功能。异常蛋白质可能以某种方式破坏纤毛的发育或功能，尽管尚不清楚这些变化如何导致Joubert综合征或X连锁智力障碍的体征和症状。"}]}' WHERE id = 6570;
UPDATE genes SET GHR_metadata_ch = '{"gene": "RAF1", "conditions": [{"name": "努南综合征", "detail": "在RAF1gene中已鉴定出25种以上引起Noonan综合征的突变。Noonan综合征的特征是面部特征轻度异常，身材矮小，心脏缺陷，出血问题，骨骼畸形以及许多其他体征和症状。RAF1gene突变会改变RAF1蛋白中的单个蛋白结构单元（氨基酸）。这些变化增加蛋白质活性并破坏RAS/MAPK信号传导途径的调节，导致细胞分裂，凋亡，细胞分化和细胞迁移的问题。研究人员认为，正常细胞过程中的这种破坏在Noonan综合征的体征和症状，特别是心脏异常中起作用。已经注意到，由RAF1gene突变引起的努南综合征患者比其他努南综合征患者有更高的心脏缺陷发生率，特别是一种叫做肥厚性梗阻型心肌病的疾病，这种疾病是心肌增厚，迫使心脏更努力地抽血。"}, {"name": "Noonan综合征伴多发性扁豆素", "detail": "已经发现RAF1gene中的至少两个突变引起具有多种扁豆素的Noonan综合征（以前称为LEOPARD综合征）。这种情况的特征是多个棕色皮肤斑点（扁豆素），心脏缺陷，身材矮小，胸部凹陷或突出以及独特的面部特征。RAF1gene突变改变了RAF1蛋白的单个氨基酸：一个突变用257位的氨基酸亮氨酸代替氨基酸丝氨酸（写为Ser257Leu或S257L），另一个突变用613位的氨基酸缬氨酸代替氨基酸亮氨酸（写为Leu613Val或L613V）。（换行）据信，导致具有多种扁豆素的Noonan综合征的RAF1gene变化会异常激活RAF1蛋白，从而破坏控制细胞功能（例如生长和分裂）的RAS/MAPK信号通路的调节。这种失调可导致具有多种扁豆素的Noonan综合征的各种特征。"}, {"name": "癌症", "detail": "一些gene突变是在一个人的一生中获得的，并且仅存在于某些细胞中。这些变化称为体细胞突变，不是遗传的。RAF1gene中的体细胞突变与几种类型的癌症的发展有关。这些突变导致RAF1蛋白始终具有活性，并可以指导细胞不受控制地生长和分裂。研究表明，RAF1gene突变可能在卵巢癌，肺癌和结肠直肠癌中发现。RAF1gene中的体细胞突变是癌症的罕见原因。（换行）由于尚不清楚的原因，RAF1gene的遗传突变似乎并未增加患有多种扁豆素或Noonan综合征的Noonan综合征患者的癌症风险。"}]}' WHERE id = 6617;
UPDATE genes SET GHR_metadata_ch = '{"gene": "EIF2B3", "conditions": [{"name": "白质消失的白质脑病", "detail": "EIF2B3gene的突变已在少数白质消失的白质脑病患者中发现。这些突变导致eIF2B功能部分丧失。eIF2B功能受损使人体细胞更难以调节蛋白质合成并应对不断变化的conditions和压力。研究人员认为，白质中的细胞（神经纤维被称为髓鞘的脂肪物质覆盖，能够隔离和保护神经）可能特别受到压力异常反应的影响，导致白质消失的白质脑病的体征和症状。"}]}' WHERE id = 6629;
UPDATE genes SET GHR_metadata_ch = '{"gene": "FOXP3", "conditions": [{"name": "免疫失调，多内分泌病，肠病，X连锁综合征", "detail": "已发现FOXP3gene中的60多个突变会导致免疫失调，多内分泌病，肠病，X连锁（IPEX）综合征。这种罕见病的特征是在受影响的个体中发展出多种自身免疫性疾病，通常会影响肠道，皮肤和产生激素（内分泌）的腺体。IPEX综合征中涉及的大多数FOXP3gene突变都会改变FOXP3蛋白区域中与DNA结合或导致异常短而无功能的蛋白产生的蛋白结构单元（氨基酸）。FOXP3gene的突变会损害调节性T细胞的正常功能。没有这些细胞的功能，身体就无法控制免疫反应。正常的身体组织和器官受到攻击，导致IPEX综合征患者发生多种自身免疫性疾病。"}, {"name": "1型糖尿病", "detail": "遗传学家庭参考提供有关1型糖尿病的信息。"}]}' WHERE id = 6634;
UPDATE genes SET GHR_metadata_ch = '{"gene": "FAM111B", "conditions": [{"name": "遗传性纤维化鬼臼皮病伴肌腱挛缩，肌病和肺纤维化", "detail": "在患有肌腱挛缩，肌病和肺纤维化（POIKTMP）的遗传性纤维化鬼臼皮病患者中，已经鉴定出FAM111Bgene中至少有六个突变。这种疾病影响身体的许多部位，特别是皮肤，肌肉，肺和胰腺。（换行）导致POIKTMP的FAM111Bgene突变导致每个细胞中该gene的一个拷贝产生异常的FAM111B蛋白。由于在POIKTMP患者中发现的大多数FAM111B突变都会改变肽酶结构域中的单个蛋白质构建块（氨基酸），因此研究人员认为这些突变会改变蛋白质的功能，而FAM111B功能的这些变化是POIKTMP各种体征和症状的基础。"}]}' WHERE id = 6668;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SUMF1", "conditions": [{"name": "多种硫酸酯酶缺乏症", "detail": "已发现SUMF1gene中至少35个突变会导致多种硫酸酯酶缺乏症。这种情况在出生或儿童早期就很明显，其特征是神经功能下降，皮肤鳞屑（鱼鳞病）和骨骼异常。导致多种硫酸酯酶缺乏症的大多数SUMF1gene突变会改变FGE酶中的单个氨基酸。这些变化严重降低了酶的功能或产生了不稳定的酶，该酶很快被分解。由于FGE酶修饰了所有已知的硫酸酯酶，因此多种硫酸酯酶的活性受到损害。未分解的含硫酸盐分子在细胞中积聚，通常导致细胞死亡。特定组织（尤其是大脑，骨骼和皮肤）中细胞的死亡会导致多种硫酸酯酶缺乏症的许多体征和症状。研究表明，导致FGE酶功能降低的突变与病情较轻有关，而导致产生不稳定FGE酶的突变往往与多种硫酸酯酶缺乏症的病情较重有关。"}]}' WHERE id = 6684;
UPDATE genes SET GHR_metadata_ch = '{"gene": "LIFR", "conditions": [{"name": "Stüve-Wiedemann综合征", "detail": "已发现LIFRgene中至少有27个突变引起Stüve-Wiedemann综合征。这种严重疾病的特征是腿部异常弯曲（弯曲），呼吸问题以及危险的高体温（体温过高），通常在婴儿期危及生命。涉及Stüve-Wiedemann综合征的大多数LIFRgene突变都会阻止任何LIFR蛋白的产生。其他突变导致产生可能无法起作用的改变的蛋白质。没有功能性LIFR，信号会受损。LIFR信号的缺乏会破坏正常的骨骼生长，从而导致低骨矿物质密度（骨质减少），弯曲的腿和Stüve-Wiedemann综合征中常见的其他骨骼问题。另外，神经细胞，特别是涉及自主神经系统的神经细胞的发育是异常的，导致这种病症的呼吸，摄食和调节体温特征的问题。"}]}' WHERE id = 6687;
UPDATE genes SET GHR_metadata_ch = '{"gene": "EDAR", "conditions": [{"name": "少汗性外胚层发育不良", "detail": "在少汗性外胚层发育不良的人群中已鉴定出EDARgene的约20个突变。这些突变大多数会改变受体蛋白中的单个蛋白结构单元（氨基酸），尽管也会发生EDARgene遗传物质的缺失。一些EDAR突变导致异常形式的ectodysplasin A受体的产生。这些遗传变化破坏了形成外胚层结构（例如毛囊和汗腺）所需的信号传导途径。当这种类型的突变存在于每个细胞的EDARgene的一个拷贝中时，会导致多汗症外胚层发育不良的常染色体显性形式。（换行）其他EDAR突变阻止细胞产生任何ectodysplasin A受体蛋白。结果，该受体不能触发外胚层-中胚层相互作用和外胚层结构正常发育所必需的化学信号。当这种类型的突变存在于每个细胞的EDARgene的两个副本中时，会导致常染色体隐性形式的多汗性外胚层发育不良。"}]}' WHERE id = 6688;
UPDATE genes SET GHR_metadata_ch = '{"gene": "PCCB", "conditions": [{"name": "丙酸血症", "detail": "在丙酸血症患者中已鉴定出PCCBgene的55个以上突变。这些突变包括单个DNA构建模块（核苷酸）的变化以及PCCBgene中遗传物质的插入或缺失。PCCB突变会阻止功能性丙酰辅酶A羧化酶的产生或降低酶的活性。改变或缺失的酶可防止蛋白质和脂质的某些部分正确分解。结果，丙酰辅酶a和其他潜在有毒化合物可在体内积聚至毒性水平。这种积聚会损害大脑和神经系统，导致与丙酸血症相关的严重健康问题。"}]}' WHERE id = 6689;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SLC2A10", "conditions": [{"name": "动脉曲折综合征", "detail": "在动脉曲折综合征患者中至少发现了23个SLC2A10gene突变，这是一种结缔组织病，其特征是从心脏到身体其他部位（动脉）的血管异常弯曲和扭曲（曲折）和其他健康问题。（换行）导致动脉曲折综合征的突变减少或消除GLUT10功能。通过尚不清楚的机制，功能性GLUT10蛋白的缺乏（缺乏）导致TGF-β的过度活性（上调）？信令。过度的生长信号导致动脉伸长。由于动脉的终点是固定的，额外的长度扭曲和弯曲，导致曲折。过度活跃的TGF-β？信号传导还干扰身体其他部位结缔组织的正常形成，导致关节和皮肤异常以及动脉曲折综合征的其他特征。（换行）与GLUT10缺乏症相关的线粒体功能变化也可能影响心血管系统的发育，但线粒体功能与动脉曲折综合征的具体体征和症状之间的关系尚不清楚。"}, {"name": "其他疾病", "detail": "SLC2A10gene的几种正常变异（多态性）与2型糖尿病患者外周动脉疾病的风险增加有关，2型糖尿病是一种对激素胰岛素的抗性导致血液中葡萄糖水平过高（高血糖）的疾病。外周动脉疾病是一种病症，其中动脉内壁（动脉粥样硬化）中的脂肪沉积物和瘢痕样组织的积累减少了流向腿部的血流，在行走时引起疼痛。包括外周动脉疾病在内的血管问题在2型糖尿病中很常见，并且被认为与高血糖对TGF-β的影响有关？信令。由SLC2A10gene变异引起的GLUT10蛋白的改变也可能影响TGF-β？发信号并增加糖尿病中血管问题的风险。"}]}' WHERE id = 6700;
UPDATE genes SET GHR_metadata_ch = '{"gene": "GNAT1", "conditions": [{"name": "常染色体显性先天性静止性夜盲", "detail": "已发现GNAT1gene中至少有两个突变会导致常染色体显性先天性固定性夜盲，其特征是无法在弱光下看到。(换行)这些突变之一削弱了蛋白质激活cGMP-PDE的能力；另一个突变导致蛋白质不断打开（组成性激活）。这两个突变都破坏了产生视觉信号从视杆细胞发送到大脑的途径。一个无功能的-转导蛋白停止信号传导途径。什么时候-转导蛋白被组成性激活，即使在强光下，杆细胞发送到大脑的信号也不断发生。来自视杆细胞的视觉信息然后被大脑感知为没有意义，导致夜盲症。"}]}' WHERE id = 6716;
UPDATE genes SET GHR_metadata_ch = '{"gene": "LCAT", "conditions": [{"name": "完全LCAT缺乏", "detail": "在完全LCAT缺乏症患者中已鉴定出LCATgene的70多个突变，该疾病主要导致角膜混浊和肾脏问题。具有完全LCAT缺陷的个体在每个细胞中的LCATgene的两个拷贝中都有突变。这些突变大多数会改变LCAT酶序列中的单个氨基酸。其他人在LCATgene中添加或删除少量遗传物质。这些突变要么阻止LCAT的产生，要么损害α-LCAT和β-LCAT的活性，从而降低酶将胆固醇附着在脂蛋白上的能力。降低体内胆固醇的这种机制的损害导致角膜，肾脏和其他组织和器官中的胆固醇沉积。这些沉积物会导致完全LCAT缺乏的体征和症状。"}, {"name": "鱼眼病", "detail": "在患有鱼眼疾病（也称为部分LCAT缺乏症）的人中，已经鉴定出LCATgene中至少有18个突变。这种疾病会导致眼睛（角膜）清晰覆盖的混浊。患有鱼眼疾病的个体在每个细胞的LCATgene的两个拷贝中都有突变。这些突变大多数会改变LCAT序列中的单个蛋白质结构单元（氨基酸）。这些突变削弱了α-LCAT的活性，降低了酶将胆固醇附着在HDL上的能力。这种降低体内胆固醇的机制的破坏导致角膜中的胆固醇沉积，逐渐导致它们变得混浊。"}, {"name": "其他疾病", "detail": "在每个细胞中具有一个LCATgene突变的人可能具有增加的动脉粥样硬化风险，动脉粥样硬化是动脉内壁中富含胆固醇的脂肪沉积物和瘢痕样组织的积累，其可阻碍血液流动并导致心脏病发作，中风和其他健康问题。单个LCATgene突变可能会降低αLCAT活性以及胆固醇与HDL的结合，从而导致胆固醇从血液中的转运效率降低，并导致动脉粥样硬化的发展。(换行)一些患有鱼眼疾病或完全LCAT缺乏症的人也会发生动脉粥样硬化。但是，由于动脉粥样硬化在普通人群中非常普遍，并且鱼眼疾病和完全LCAT缺乏症都非常罕见，因此很难确定受影响的个体是否罹患动脉粥样硬化的风险增加。"}]}' WHERE id = 6806;
UPDATE genes SET GHR_metadata_ch = '{"gene": "PLAGL1", "conditions": [{"name": "6q24相关的短暂性新生儿糖尿病", "detail": "6q24相关的短暂性新生儿糖尿病是婴儿中发生的一种糖尿病，是由PLAGL1gene的过度活跃（过度表达）引起的。PLAGL1gene的过表达可以通过三种方式发生。6q24相关的短暂性新生儿糖尿病病例中约有40%是由称为6号染色体父本单亲二体性（UPD）的遗传变化引起的。在父系UPD中，人们从父亲那里继承了两个染色体副本，而不是从每个父母那里继承了一个副本。父本UPD导致人们拥有父本表达的印迹gene的两个活动副本，而不是父亲的一个活动副本和母亲的一个非活动副本。(换行)当来自父亲的染色体6的拷贝具有包括PLAGL1gene的遗传物质的重复时，另外40%的6q24相关的短暂性新生儿糖尿病病例发生。（换行）PLAGL1gene过表达可能发生的第三种机制是gene母体拷贝的沉默受损（母体低甲基化）。6q24相关的短暂性新生儿糖尿病病例中约有20%是由母体甲基化不足引起的。一些患有这种疾病的人在6q24区域的母体拷贝中发生了遗传变化，从而阻止了该区域的gene沉默。其他受影响的个体具有涉及许多印迹区域的gene沉默的更普遍的损伤，称为印迹gene座的低甲基化（HIL）。HIL由其他gene的突变产生。(换行)目前尚不清楚PLAGL1gene的过表达如何导致6q24相关的短暂性新生儿糖尿病以及为什么婴儿期后病情好转。研究人员建议，PLAGL1过表达可能会减少分泌胰岛素的β细胞的数量或损害其在受影响个体中的功能。缺乏足够的胰岛素导致与糖尿病相关的血糖控制受损。在患有6q24相关的短暂性新生儿糖尿病的个体中，这些体征和症状最有可能发生在生理压力时期，包括婴儿期的快速增长，儿童疾病和怀孕。由于胰岛素在早期发育过程中起着生长促进剂的作用，因此缺乏这种激素可能是6q24相关的短暂性新生儿糖尿病中产前生长缓慢的原因。"}, {"name": "癌症", "detail": "已经在各种癌性肿瘤中鉴定出PLAGL1gene的过度沉默（高甲基化），包括卵巢癌和免疫系统细胞癌（淋巴瘤）。PLAGL1gene高甲基化导致PLAGL1蛋白的产生减少。PLAGL1蛋白的缺乏可能损害其在调节细胞周期中的作用并干扰细胞凋亡。结果，细胞可能生长和分裂过快或以不受控制的方式分裂，导致癌症。"}]}' WHERE id = 6820;
UPDATE genes SET GHR_metadata_ch = '{"gene": "FREM1", "conditions": [{"name": "曼尼托巴-oculotrichoanal综合征", "detail": "在曼尼托巴眼睑综合征患者中至少鉴定出两个FREM1gene突变。这种情况涉及几个特征性的身体特征，特别是影响眼睛（眼睛），头发（毛发）和肛门（肛门）。突变会从FREM1gene中删除遗传物质或导致过早的终止信号，从而导致异常短的FREM1蛋白，并被认为会导致无功能的蛋白。(换行)缺乏功能性FREM1蛋白会干扰其在胚胎基底膜发育中的作用，也可能影响FRAS1和FREM2蛋白的位置，稳定性或功能。曼尼托巴-眼球富集综合征的特征可能是由于基底膜锚定功能受损导致邻近胚胎组织无法正确融合所致。"}, {"name": "其他疾病", "detail": "在患有称为双裂鼻，肾发育不全和肛门直肠畸形综合症（有时称为BNAR）的疾病的人中，至少鉴定出三个FREM1gene突变。这些突变改变了FREM1蛋白中的单个蛋白结构单元（氨基酸）或导致蛋白异常缩短。导致BNAR的突变可能破坏FREM1蛋白在特定组织胚胎基底膜中的作用，导致鼻软骨裂（双歧鼻）、肾脏缺失（肾发育不全）、肛门开口变窄或错位（肛门直肠畸形）以及这种疾病的其他特征。研究人员建议，具有重叠特征的BNAR和曼尼托巴oculotrichoanal综合征可能被视为单一疾病谱的一部分。"}]}' WHERE id = 6837;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SOX2", "conditions": [{"name": "视光发育不良", "detail": "遗传学家庭参考提供有关视光发育不良的信息。"}, {"name": "SOX2失眼症综合征", "detail": "已发现SOX2gene中至少有33个突变引起SOX2失眼症候群。这些突变中的一些阻止gene产生任何SOX2蛋白，而另一些则导致异常短而无功能的蛋白产生。一些突变会改变SOX2蛋白中的单个蛋白结构单元（氨基酸）。所有这些突变都破坏了蛋白质调节眼睛和身体其他部位正常发育所必需的gene的能力。这些结构的异常发育会导致SOX2失眼症综合征的体征和症状。"}, {"name": "合并垂体激素缺乏症", "detail": "遗传学家庭参考资料提供有关合并垂体激素缺乏症的信息。"}, {"name": "小眼症", "detail": "遗传学家庭参考提供有关小眼症的信息。"}]}' WHERE id = 6913;
UPDATE genes SET GHR_metadata_ch = '{"gene": "OAT", "conditions": [{"name": "脉络膜和视网膜的回旋萎缩", "detail": "已发现超过60种OATgene突变引起脉络膜和视网膜的回旋萎缩（通常缩短为回旋萎缩）。这些突变导致功能性鸟氨酸氨基转移酶的量减少。该酶的缺乏阻碍了鸟氨酸向P5C的转化。结果，过量的鸟氨酸积聚在血液中（高鸟氨酸血症），并且产生的P5C低于正常水平。尚不清楚这些变化如何导致进行性视力丧失以及有时与回旋萎缩相关的其他特征。研究人员建议，P5C缺乏可能会干扰视网膜的功能，视网膜是排列在眼睛后部的专门光敏组织。还提出过量的鸟氨酸可以抑制称为肌酸的分子的产生。体内许多组织都需要肌酸来正确储存和使用能量。它参与为肌肉收缩提供能量，并且在神经系统功能中也很重要。"}]}' WHERE id = 6920;
UPDATE genes SET GHR_metadata_ch = '{"gene": "CACNB4", "conditions": [{"name": "阵发性共济失调", "detail": "研究人员已经确定CACNB4gene中至少有一个突变可能导致发作性共济失调。这种突变是在法国-加拿大家庭中发现的，发作性共济失调5型（EA5），改变了钙通道中的单个蛋白质结构单元（氨基酸）？4亚基。具体而言，它在位置104（写为Cys104Phe或C104F）上用氨基酸苯丙氨酸取代了氨基酸半胱氨酸。科学家推测，这种遗传变化可能会改变？4个亚基与其他蛋白质相互作用，并以某种方式破坏大脑中钙通道的正常功能。目前尚不清楚这些影响如何导致运动不协调的发作以及发作性共济失调的其他体征和症状。"}, {"name": "青少年肌阵挛性癫痫", "detail": "遗传学家庭参考提供有关青少年肌阵挛性癫痫的信息。"}, {"name": "其他疾病", "detail": "在少数家庭中，CACNB4gene的突变与癫痫有关。这些突变之一会在482位（写为Arg482Ter或R482X）过早停止蛋白质生产。这种突变导致异常缩短？4个亚基缺少一个与大分子相互作用的关键区域？1亚基。钙通道改变？4亚基比平时更快关闭，减少钙离子流入细胞。钙离子转运受损可能会破坏神经细胞之间的通讯，从而导致具有这种遗传变化的人癫痫发作。(换行)另一个CACNB4突变Cys104Phe似乎在至少一个家庭中引起癫痫。这种遗传变化（如上所述）也已在与发作性共济失调无关的家庭中发现。研究人员不确定为什么这种单一突变似乎是两种不同神经系统疾病的基础。他们怀疑可能涉及其他遗传和环境因素。"}]}' WHERE id = 6923;
UPDATE genes SET GHR_metadata_ch = '{"gene": "DCAF17", "conditions": [{"name": "Woodhouse-Sakati综合征", "detail": "在Woodhouse-Sakati综合征患者中至少发现了11个DCAF17gene突变，这是一种影响人体激素分泌腺（内分泌系统）和神经系统网络的疾病。这种情况的特征是青春期延迟或缺失，脱发，异常运动，听力下降和智力残疾。（换行）导致Woodhouse-Sakati综合征的大多数DCAF17gene突变导致蛋白质异常短且迅速分解或其正常功能受损。DCAF17蛋白功能的丧失可能是Woodhouse-Sakati综合征的特征，尽管尚不清楚该蛋白的缺乏如何导致激素异常以及该病的其他体征和症状。"}]}' WHERE id = 6959;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ATP6V1B1", "conditions": [{"name": "肾小管性酸中毒伴耳聋", "detail": "已在患有耳聋的肾小管性酸中毒患者中鉴定出ATP6V1B1gene突变，耳聋是一种以代谢性酸中毒，骨骼无力和感觉神经性听力减退为特征的疾病。这是由于V-ATPases质子泵功能受损引起的。"}]}' WHERE id = 6979;
UPDATE genes SET GHR_metadata_ch = '{"gene": "FLNA", "conditions": [{"name": "额ome骨发育不良", "detail": "在额ome骨发育不良的人中，已经鉴定出FLNAgene区域中称为外显子4、22、29、33和44至46的15个以上突变。这种情况涉及骨骼发育异常和其他健康问题，包括肾脏，心脏和肺部缺陷。导致额ome骨发育不良的FLNAgene突变被描述为“功能获得”，因为它们似乎增强了纤维蛋白A蛋白的活性或赋予该蛋白新的非典型功能。FLNAgene中的不同突变似乎会在蛋白质中产生特定变化，从而导致被归类为单个FLNA相关疾病的特定体征和症状。研究人员认为，额ome骨发育不良所涉及的突变可能会改变纤维蛋白A蛋白帮助调节骨骼发育过程的方式，但尚不清楚该蛋白的变化如何与疾病的特定体征和症状相关。"}, {"name": "肠假性梗阻", "detail": "在患有假性肠梗阻的人中，已经鉴定出FLNAgene中至少有3个突变，这种疾病的特征是使食物通过消化道（蠕动）的肌肉收缩受损。（换行）引起肠假性梗阻的FLNAgene突变包括遗传物质的缺失或重复。人们认为这些突变会降低纤维蛋白A蛋白的水平或损害其功能；这种类型的突变称为“功能丧失”。研究表明，出生前发育过程中，纤维蛋白A功能降低可能会影响胃肠道平滑肌细胞的形状，从而导致这些肌肉分层异常。平滑肌排列内脏；他们收缩和放松而不受有意识的控制。在消化道中，这些肌肉的异常分层可能会干扰蠕动。（换行）遗传物质的缺失或重复会影响全部或部分FLNAgene，还可能包括X染色体上的附近gene。这些额外gene的变化可能解释了某些受影响个体中发生的其他一些体征和症状，例如神经系统异常和异常的面部特征。"}, {"name": "Melnick-Needles综合征", "detail": "在患有Melnick-Needles综合征的人中，已经鉴定出FLNAgene区域中称为外显子22的至少10个突变。这种情况涉及骨骼发育异常和其他健康问题。与Melnick-Needles综合征相关的FLNAgene突变被描述为“功能获得”，因为它们似乎增强了丝素A蛋白的活性或为该蛋白提供了新的非典型功能。研究人员认为，与Melnick-Needles综合征有关的突变可能会改变filamin A蛋白帮助调节骨骼发育过程的方式，但尚不清楚该蛋白的变化如何与疾病的特定体征和症状相关。"}, {"name": "Otopalatodigital综合征1型", "detail": "在患有1型otopalatodigital综合征的人中，至少发现了FLNAgene区域中称为外显子3、4和5的四个突变。这种情况主要涉及骨骼发育异常。导致1型otopalatodigital综合征的FLNAgene突变均导致与肌动蛋白结合的区域中的纤维蛋白A蛋白发生变化。导致1型otopalatodigital综合征的突变被描述为“功能获得”，因为它们似乎增强了丝素A蛋白的活性或赋予该蛋白新的非典型功能。研究人员认为，涉及1型otopalatodigital综合征的突变可能会改变纤维蛋白A蛋白帮助调节骨骼发育过程的方式，但尚不清楚该蛋白的变化如何与疾病的特定体征和症状相关。"}, {"name": "Otopalatodigital综合征2型", "detail": "在患有2型otopalatodigital综合征的人中，已经鉴定出FLNAgene区域中至少16个称为外显子3、4和5的突变。这种情况涉及骨骼发育异常，通常比1型otopalatodigital综合征更为严重（如上所述）。外显子3、4和5中的突变导致与肌动蛋白结合的区域中的纤维蛋白A蛋白发生变化。导致2型otopalatodigital综合征的突变被描述为“功能获得”，因为它们似乎增强了丝素A蛋白的活性或赋予该蛋白新的非典型功能。研究人员认为，涉及2型otopalatodigital综合征的突变可能会改变filamin A蛋白帮助调节骨骼发育过程的方式，但尚不清楚蛋白的变化如何与疾病的特定体征和症状相关，或者为什么同一地区的突变会导致一系列症状和体征。"}, {"name": "脑室周围异位", "detail": "在脑室周围异位症患者中已鉴定出130多个FLNAgene突变，在这种情况下，胎儿大脑的早期发育过程中神经细胞（神经元）无法正常移动（迁移），从而导致癫痫发作和其他神经系统问题。这些突变中的大多数会导致蛋白质太短而无法执行其功能，从而使细胞骨架混乱并损害细胞运动。在发育过程中不能正常迁移的神经元在靠近大脑中心的充满液体的腔（心室）周围形成团块，导致脑室周围异位症的体征和症状。（折线）在某些情况下，突变导致一个蛋白质结构单元（氨基酸）被蛋白质序列中的另一个氨基酸取代。这些突变可能导致部分功能蛋白的产生，从而导致疾病的轻度形式。"}, {"name": "X连锁心脏瓣膜发育不良", "detail": "已发现FLNAgene中至少有四个突变会导致X连锁心脏瓣膜发育不良，这种疾病的特征是心脏瓣膜异常厚。这些突变大多数会改变纤维蛋白A蛋白中的单个蛋白结构单元。这些突变可能会改变蛋白质的形状，削弱其与肌动蛋白和其他蛋白质结合的能力。结果，细胞骨架减弱，瓣膜细胞以及细胞外基质紊乱。电池未正确定位在阀门内，因此阀门变形。此外，当心脏抽血时，细胞改变形状的能力下降会损害瓣膜打开和关闭的能力。似乎在异常的细胞外基质中产生过量的蛋白质，导致瓣膜变厚并进一步削弱其正常打开和关闭的能力。(换行)目前尚不清楚为什么心脏瓣膜是唯一受这些FLNAgene突变影响的组织。导致X连锁心脏瓣膜发育不良的突变发生在gene的不同部分，而不是引起其他疾病的突变（如上所述）。已经提出，受这些突变影响的纤维蛋白A蛋白区域对于结合在心脏发育中起重要作用的其他蛋白是必需的。"}, {"name": "FG综合征", "detail": "遗传学家庭参考提供有关FG综合征的信息。"}]}' WHERE id = 6992;
UPDATE genes SET GHR_metadata_ch = '{"gene": "BMPR2", "conditions": [{"name": "肺动脉高压", "detail": "已经发现BMPR2gene的突变是肺动脉高压，其特征是肺动脉高压，其他症状包括头晕，脚踝或腿部浮肿，胸痛和心率加快。这是由2型骨形态发生蛋白受体的组装破坏或异常蛋白产生引起的，这会削弱其信号转导活性。"}, {"name": "肺静脉闭塞性疾病", "detail": "已经发现BMPR2gene的突变会引起肺静脉闭塞性疾病（PVOD），这种疾病的特征是氧合血液从肺部阻塞（阻塞）到心脏的肺静脉，症状包括呼吸困难，劳累时疲劳，头晕，嗜睡，躺下时呼吸困难，咳嗽，皮肤发蓝（紫绀），胸痛，昏厥和肺水肿。"}]}' WHERE id = 7011;
UPDATE genes SET GHR_metadata_ch = '{"gene": "PNPO", "conditions": [{"name": "吡哆醛5''-磷酸依赖性癫痫", "detail": "已发现PNPOgene中至少有7个突变会引起吡ido醛5''-磷酸依赖性癫痫。这些突变大多数会改变吡ido醇5''-磷酸氧化酶中的一个蛋白质结构单元（氨基酸），从而损害其正常功能。所得酶不能有效代谢吡ido醇和吡ido胺以产生PLP。PLP的短缺会破坏许多其他需要PLP才能有效的蛋白质和酶的功能。目前尚不清楚PLP的缺乏如何影响大脑并导致这种情况的癫痫发作。"}]}' WHERE id = 7074;
UPDATE genes SET GHR_metadata_ch = '{"gene": "LAMP2", "conditions": [{"name": "达农病", "detail": "LAMP2gene中有许多突变可导致Danon病。达农病是一种以心肌减弱（心肌病），骨骼肌减弱（肌病）和智力障碍为特征的疾病。这种情况对男性的影响比女性严重。（换行）导致Danon病的LAMP2gene突变导致产生很少或没有LAMP-2蛋白。大多数突变影响LAMP-2蛋白的所有三种同工型。但是，仅影响LAMP-2B同工型的突变也会引起Danon病，这表明该病是由LAMP-2B蛋白的缺陷引起的。(换行)一些研究表明，在没有LAMP-2蛋白的细胞中，自噬泡和溶酶体之间的融合发生得更慢，这可能导致自噬泡的积累。达农病患者的心脏和骨骼肌细胞中存在异常大量的自噬泡。这种积累可能导致肌肉细胞破裂，从而导致达农病中出现的肌肉无力。(换行)一些具有LAMP2gene突变的人发展为肥厚型心肌病，而没有Danon病的其他特征。肥厚型心肌病是心肌增厚，可能使心脏更难抽血。在具有LAMP2gene突变的人中，心脏的左下腔通常是受影响的区域，称为左心室。由LAMP2突变引起的肥厚型心肌病患者通常具有控制称为心脏预激的心跳的电信号的特定异常。目前尚不清楚这是单独的疾病还是Danon疾病的轻度形式。此外，尚不清楚为什么某些具有LAMP2突变的人会发展为肥厚型心肌病，而不会发展为Danon病的其他特征。"}]}' WHERE id = 7088;
UPDATE genes SET GHR_metadata_ch = '{"gene": "B3GLCT", "conditions": [{"name": "彼得斯综合症", "detail": "已经发现B3GLCTgene的突变引起Peters plus综合征，该病症的特征在于眼睛异常，身材矮小，智力残疾和独特的面部特征。该病是由于异常短而无功能的酶引起的，该酶损害糖基化并可能破坏许多蛋白质的功能。"}]}' WHERE id = 7116;
UPDATE genes SET GHR_metadata_ch = '{"gene": "LIPA", "conditions": [{"name": "溶酶体酸性脂肪酶缺乏症", "detail": "已发现LIPAgene中约60个突变导致溶酶体酸性脂肪酶缺乏症。这种遗传性疾病的特征是全身细胞和组织中有害脂质的积累。LIPAgene中的突变导致功能性溶酶体酸性脂肪酶短缺（缺乏）。病情的严重程度取决于可用的工作酶数量。在完全丧失酶活性的个体中，该病始于婴儿期，通常是致命的。在具有一些剩余酶活性的个体中，酶活性的量通常决定病症的严重程度。(换行)最常见的LIPAgene突变，在大约一半的溶酶体酸性脂肪酶缺乏症患者中发现，这种突变始于儿童期或之后，破坏了该gene指令用于制备溶酶体酸性脂肪酶的方式。这种特殊的突变称为剪接位点突变，用DNA构建块（核苷酸）鸟嘌呤代替了称为外显子8的gene区域附近的核苷酸腺嘌呤（写为IVS8-1G>a）。这种突变导致24种蛋白质结构单元（氨基酸）的缺失。在每个细胞的LIPAgene的两个拷贝中均具有IVS8-1G>A突变的人具有正常量的溶酶体酸性脂肪酶活性的5%。（换行）溶酶体酸性脂肪酶活性的降低或不存在导致溶酶体中甘油三酯，胆固醇酯和其他脂质的积累，导致脂肪在多种组织中积聚。身体无法从这些脂质的分解中产生胆固醇，导致胆固醇生成的替代方法增加，并且血液中的胆固醇水平高于正常水平。多余的脂质被运送到肝脏去除。由于其中许多未正确分解，因此无法将其从体内取出；相反；它们积聚在肝脏中，导致肝脏疾病。脂质在组织中的逐渐积累导致器官功能障碍以及溶酶体酸性脂肪酶缺乏症的体征和症状。"}]}' WHERE id = 7151;
UPDATE genes SET GHR_metadata_ch = '{"gene": "BRAF", "conditions": [{"name": "心血管系统皮肤综合征", "detail": "已经发现BRAFgene中的突变引起心脏皮肤综合征，这种情况尤其影响心脏（心脏），具有不同面部特征（面部）和皮肤和头发（皮肤）的面部。遗传变化异常激活蛋白质并破坏全身细胞中的RAS/MAPK信号通路。"}, {"name": "Erdheim-Chester病", "detail": "已经发现BRAFgene中的几个突变在一些患有Erdheim-Chester病的人中被鉴定，这是一种罕见的病症，其特征在于组织细胞的异常产生和积累，主要影响骨骼，导致骨骼增厚和疼痛，并且影响大脑，眼睛，肺，肝脏，肾脏和其他器官。这些变化（V600E体细胞突变）异常激活BRAF蛋白并破坏细胞增殖的调节，从而允许组织细胞的不受控制的生长和分裂。"}, {"name": "巨大的先天性黑素细胞痣", "detail": "已发现V600E BRAF突变会导致巨大的先天性黑素细胞痣，这种疾病的特征是出生时出现大量非癌性异常深色皮肤斑块，并伴有黑色素瘤风险增加。"}, {"name": "努南综合征", "detail": "发现BRAFgene中的几个突变与Noonan综合征有关，Noonan综合征的特征是面部特征轻度异常，身材矮小，心脏缺陷，出血问题和骨骼畸形。"}, {"name": "Noonan综合征伴多发性扁豆素", "detail": "已经发现BRAFgene中的几个突变导致具有多个痣的Noonan综合征（以前称为LEOPARD综合征），其特征在于多个棕色皮肤斑点（痣），心脏缺陷，身材矮小，胸部凹陷或突出，以及独特的面部特征。该病是由于异常激活BRAF蛋白并破坏RAS/MAPK信号通路调节的突变引起的。"}, {"name": "胆管癌", "detail": "已知BRAFgene的突变会引起胆管癌，这是一种从胆汁酸开始的癌症，一种在早期没有任何症状直到扩散到其他组织的癌症。临床症状包括黄疸，瘙痒，尿液深色，食欲不振，无意识的体重减轻，腹痛以及浅色和油腻的大便，由于其与许多不同疾病的重叠特征，被称为“非特异性”。胆管癌根据位置分类，如：；肝内胆管癌，肝门周围胆管癌（Klatskin肿瘤）和远端胆管癌。"}, {"name": "胃肠道间质瘤", "detail": "发现BRAFgene突变与胃肠道间质瘤（GIST）有关，胃肠道间质瘤是由胃肠道Cajal间质细胞（ICC）生长而影响胃或小肠的肿瘤。临床症状包括腹部疼痛或肿胀导致贫血，恶心，呕吐，食欲不振或体重减轻，虚弱和疲倦。GIST可能是家族性或散发性的，伴有一种或多种具有其他症状的肿瘤，例如：；其他GIST细胞增生，深色皮肤斑块和荨麻疹色素沉着瘙痒的褐色皮肤。"}, {"name": "朗格汉斯细胞组织细胞增生症", "detail": "发现BRAFgene中的体细胞突变（主要是V600E）与朗格汉斯细胞组织细胞增生有关，朗格汉斯细胞组织细胞增生是一种朗格汉斯细胞在多种组织和器官中异常积聚的疾病，导致形成称为肉芽肿的肿瘤。这是由于异常激活BRAF蛋白的突变导致朗格汉斯细胞无法控制地生长和分裂所致。"}, {"name": "肺癌", "detail": "发现BRAFgene突变与肺癌有关，肺癌根据受影响细胞的大小分为两种类型，称为小细胞肺癌和非小细胞肺癌。小细胞癌通常转移到肝，脑，骨和肾上腺，分为影响肺泡，气管和支气管的腺癌，鳞状细胞癌和大细胞肺癌。大细胞癌起源于上皮细胞，包括非小细胞肺癌。"}, {"name": "多发性骨髓瘤", "detail": "发现BRAFgene突变与多发性骨髓瘤有关，多发性骨髓瘤是一种影响骨髓细胞的癌症，其特征是颅骨，脊柱，肋骨和骨盆中的骨组织丧失，伴有高钙血症，导致恶心和食欲不振，过度口渴，疲劳，肌肉无力和浆细胞中的其他异常导致贫血，异常苍白的皮肤，白细胞减少，异常出血和瘀伤。"}, {"name": "癌症", "detail": "发现BRAFgene中的体细胞突变与几种类型的癌症有关，例如黑素瘤，结肠癌和直肠癌，卵巢癌和甲状腺癌。突变异常激活BRAF蛋白，导致导致癌症的细胞生长失控。"}]}' WHERE id = 7154;
UPDATE genes SET GHR_metadata_ch = '{"gene": "HNF1B", "conditions": [{"name": "17q12缺失综合征", "detail": "17q12缺失综合征是由每个细胞中一小块17号染色​​体缺失引起的疾病。17q12缺失综合征的体征和症状可能包括肾脏异常（尤其是肾脏中充满液体的囊，称为囊肿）和一种称为5型年轻成熟型糖尿病的糖尿病（MODY5）。肾囊肿和MODY5的组合有时被称为肾囊肿和糖尿病（RCAD）综合征。17q12缺失综合征的其他特征包括尿路和生殖系统异常，发育迟缓，智力残疾以及行为或精神疾病。与17q12缺失综合征相关的健康问题差异很大，即使在同一家庭的受影响成员中也是如此。（换行）染色体17中被删除的部分位于染色体的长（q）臂上指定为q12的位置。染色体的这一区域包含至少15个gene，包括HNF1B。该区域的缺失导致每个细胞中HNF1Bgene的一个拷贝丢失，从而导致HNF1B蛋白的量减少。这种蛋白质的缺乏可能会破坏包括肾脏和胰腺在内的多个器官正常发育所必需的gene调控。研究表明，在17q12缺失综合征患者中，丢失一份HNF1Bgene是肾脏和泌尿道异常以及MODY5的基础。"}, {"name": "前列腺癌", "detail": "遗传学家庭参考提供有关前列腺癌的信息。"}, {"name": "其他疾病", "detail": "HNF1Bgene内的突变也可能导致多器官系统异常。与HNF1Bgene突变相关的某些特征与17q12缺失综合征（如上所述）相同，包括RCAD综合征以及泌尿道，生殖系统和其他器官异常。像该综合征的体征和症状一样，与HNF1Bgene突变相关的健康问题在受影响的个体之间差异很大。但是，与17q12缺失不同，尚未发现HNF1Bgene突变会导致发育迟缓，智力残疾或行为或精神疾病。（换行）已鉴定出HNF1Bgene中近200个突变。这些突变中的许多改变了HNF1B蛋白中的单个蛋白结构单元（氨基酸）。其他导致异常形状的蛋白质或阻止从该gene的一个拷贝产生任何功能性蛋白质。功能性HNF1B蛋白的缺乏可能会破坏几个器官正常发育所必需的gene调控。目前尚不清楚为什么这些突变会影响不同人群的不同器官系统。"}]}' WHERE id = 7170;
UPDATE genes SET GHR_metadata_ch = '{"gene": "PRX", "conditions": [{"name": "进行性神经性腓骨肌萎缩症", "detail": "研究人员已经鉴定出至少10种PRXgene突变，这些突变会导致一种称为4F型（有时称为Dejerine-Sottas综合征）的Charcot-Marie-Tooth病。这些突变导致异常短的L-periaxin蛋白无法维持髓磷脂结构。髓鞘结构破坏可导致髓鞘丢失（脱髓鞘）和神经冲动传递受损。结果，周围神经不太能够激活用于运动的肌肉或将信息从感觉细胞传递回大脑。这些周围神经异常引起4F型Charcot-Marie-Tooth病的体征和症状。"}]}' WHERE id = 7171;
UPDATE genes SET GHR_metadata_ch = '{"gene": "KAT6B", "conditions": [{"name": "Genitopatellar综合征", "detail": "在患有生殖pat骨综合征的人中至少鉴定出KAT6Bgene的8个突变，这是一种罕见的疾病，其特征是生殖器异常，膝盖（骨）缺失或发育不全，智力残疾以及影响身体其他部位的异常。引起生殖pat骨综合征的突变发生在称为外显子18的区域的KAT6Bgene末端附近。这些突变导致缩短的组蛋白乙酰转移酶的产生。研究人员怀疑，缩短的酶的功能可能与全长酶不同，从而在早期发育过程中改变了各种gene的调控。由于改变的酶具有不同的功能，因此这些突变被描述为“功能获得”。但是，尚不清楚这些变化如何导致生殖pat骨综合征的特定特征。"}, {"name": "Ohdo综合征，Say-Barber-Biesecker-Young-Simpson变异", "detail": "已发现KAT6Bgene中的10多个突变会导致Ohdo综合征的Say-Barber-Biesecker-Young-Simpson（SBBYS）变异。尽管两种情况之间某些特定的发育异常有所不同，但这种情况的体征和症状与生殖pat骨综合征（如上所述）重叠。在整个KAT6Bgene中都发现了导致Ohdo综合征SBBYS变异的突变，尽管其中许多发生在第18外显子上。研究表明，这些突变可能会阻止每个细胞中一个拷贝的KAT6Bgene产生功能性组蛋白乙酰转移酶。这种酶的缺乏会损害早期发育过程中各种gene的调控。因为这些突变导致酶的减少，所以它们被描述为“功能丧失”。但是，尚不清楚这些变化如何导致疾病的特定特征。"}, {"name": "癌症", "detail": "涉及KAT6Bgene的遗传变化与某些类型的癌症有关。这些突变是体细胞的，这意味着它们是在一个人的一生中获得的，并且仅存在于某些细胞中。遗传变化是破坏包含KAT6Bgene的10号染色体区域的染色体重排（易位）。研究人员发现，在一些患有称为急性髓性白血病（AML）的造血细胞癌的人中，染色体10的这一区域与染色体16的一部分相连。在一些患有治疗相关性骨髓增生异常综合征的人中也发现了这种易位，这是一种在一个人接受另一种癌症化疗后可能发生的血液疾病。（换行）目前尚不清楚涉及KAT6Bgene的易位如何与癌症的发展相关。这些变化可能会改变组蛋白修饰，从而阻止gene活性的正常调节。受损的gene调控可能通过允许异常细胞不受控制地生长和分裂而促进癌症的生长。"}, {"name": "肿瘤", "detail": "在一些患有子宫平滑肌瘤的人中也发现了涉及KAT6Bgene的体细胞变化，子宫平滑肌瘤是子宫中的非癌性生长，也称为子宫肌瘤。子宫平滑肌瘤在成年女性中很常见。这些生长会导致盆腔疼痛和异常出血，并且在某些情况下会导致无法生育孩子（不孕症）。与子宫平滑肌瘤相关的遗传变化是在含有KAT6Bgene的10号染色体区域和17号染色​​体的特定区域之间的易位。目前还不清楚这种易位如何与肿瘤发展相关。损害正常gene调控的组蛋白修饰的变化可能允许某些细胞以不受控制的方式分裂，导致肿瘤的生长。"}]}' WHERE id = 7184;
UPDATE genes SET GHR_metadata_ch = '{"gene": "UROD", "conditions": [{"name": "卟啉症", "detail": "URODgene的突变导致两种形式的卟啉症，迟发性皮肤卟啉症和肝红细胞生成性卟啉症。迟发性皮肤卟啉症是最常见的卟啉症类型；与肝红细胞生成性卟啉症相比，其体征和症状往往较轻，并且在生命中出现较晚。当每个细胞中URODgene的一个拷贝发生突变时，会增加发生迟发性皮肤卟啉症的风险。（多种遗传和非遗传因素导致这种形式的卟啉症。）每个细胞中URODgene的两个拷贝中的突变都会引起肝红细胞生成性卟啉症。（换行）超过50个URODgene突变与迟发性皮肤卟啉症有关。这些突变使整个身体的尿卟啉原脱羧酶活性降低了约50%。结果，称为卟啉的化合物从肝脏开始在器官和组织中累积到毒性水平。这种积累，加上酒精使用，吸烟，某些激素，过量铁，丙型肝炎或艾滋病毒感染等非遗传因素，导致了这种类型的卟啉症。（换行）在患有肝红细胞生成性卟啉症的人中已经鉴定出URODgene中的至少10个突变。这些突变中的一些也与迟发性皮肤卟啉症有关。引起肝红细胞生成性卟啉症的突变会使尿卟啉原脱羧酶的活性降低至正常水平的10%以下。这种酶的缺乏使得称为卟啉的化合物能够在体内积聚。这些化合物是在血红素生成的正常过程中形成的，但是尿卟啉原脱羧酶活性的降低使它们积累到毒性水平。卟啉的这种异常堆积导致了这种卟啉症的特征。"}]}' WHERE id = 7209;
UPDATE genes SET GHR_metadata_ch = '{"gene": "RET", "conditions": [{"name": "先天性巨结肠症", "detail": "RETgene的突变是Hirschsprung病的最常见遗传原因，Hirschsprung病是一种导致严重便秘或肠道阻塞的疾病。已知有200多个RETgene突变会导致这种情况。这些遗传变化导致RET蛋白无法发挥功能，无法与生长因子相互作用或在细胞内传递信号。没有RET蛋白信号传导，肠神经不能正常发育。这些神经控制使粪便通过肠道的收缩，而它们的缺失会导致Hirschsprung病特有的肠道问题。"}, {"name": "多发性内分泌肿瘤", "detail": "已知RETgene中超过25个突变会导致一种称为2型的多发性内分泌肿瘤。多发性内分泌肿瘤通常涉及两个或多个身体产生激素的腺体（称为内分泌腺）中肿瘤的发展。这些肿瘤可以是非癌性的或癌性的。2型多发性内分泌肿瘤分为三种亚型：2A型，2B型和家族性甲状腺髓样癌。这些亚型的特征在于其特征性体征和症状以及特定肿瘤的风险。（换行）导致多发性内分泌肿瘤2型的大多数RETgene突变改变RET蛋白中的单蛋白构建块（氨基酸）。2A型最常见的原因是突变，该突变将634位的氨基酸半胱氨酸替换为氨基酸精氨酸（写为Cys634Arg或C634R）。超过90%的2B型病例是由突变引起的，该突变用918位的氨基酸苏氨酸取代了氨基酸蛋氨酸（写为Met918Thr或M918T）。几种氨基酸取代可引起家族性甲状腺髓样癌。（换行）导致2型多发性内分泌肿瘤的突变导致过度活跃的RET蛋白，其可以传递信号而不首先附着于细胞外的生长因子。过度活跃的蛋白质可能会触发细胞异常生长和分裂，从而导致内分泌系统和其他组织中肿瘤的形成。引起2型多发性内分泌肿瘤的过度活化的RETgene突变与引起Hirschsprung病的失活突变（如上所述）有很大不同；这两种疾病很少发生在同一个人身上。"}, {"name": "非综合征性副神经节瘤", "detail": "RETgene的突变增加了发展一种称为嗜铬细胞瘤的副神经节瘤的风险。副神经节瘤是神经系统的肿瘤，通常是非癌性的（良性的）。嗜铬细胞瘤特别影响肾上腺，肾上腺是位于每个肾脏顶部的产生激素的小腺体。嗜铬细胞瘤是2型多发性内分泌肿瘤的特征，但它们也可以是非综合征性的，这意味着它们在没有该综合征的其他体征和症状的情况下发生。与非综合征性嗜铬细胞瘤相关的RETgene突变会改变RET蛋白中的单个氨基酸。与2型多发性内分泌肿瘤一样，该突变可能导致RET蛋白过度活跃，从而触发细胞无法控制地生长和分裂，并可能导致肿瘤的形成。"}, {"name": "肺癌", "detail": "遗传学家庭参考提供有关肺癌的信息。"}, {"name": "其他癌症", "detail": "一些gene突变是在一个人的一生中获得的，并且仅存在于某些细胞中。这些被称为体细胞突变的变化不是遗传的。已经在几种非遗传性（散发性）癌症中鉴定出RETgene的体细胞变化。涉及RETgene的染色体重排是散发性甲状腺癌的最常见原因之一，称为乳头状甲状腺癌（也称为RET/PTC）。此外，RETgene的体细胞突变可能导致非家族性甲状腺髓样癌（一种甲状腺癌，也可能作为多发性内分泌肿瘤的一部分发生）。"}]}' WHERE id = 7237;
UPDATE genes SET GHR_metadata_ch = '{"gene": "EWSR1", "conditions": [{"name": "尤文肉瘤", "detail": "涉及EWSR1gene的突变可引起一种称为尤因肉瘤的癌性肿瘤。这些肿瘤发展在骨骼或软组织，如神经和软骨。尤因肉瘤有几种类型，包括骨尤因肉瘤，骨外尤因肉瘤，周围原始神经外胚层肿瘤和阿斯金肿瘤。导致这些肿瘤的突变是在一个人的一生中获得的，并且仅存在于肿瘤细胞中。这种类型的遗传变化称为体细胞突变，不是遗传的。导致尤因肉瘤的最常见突变是22号染色体和11号染色体之间遗传物质的重排（易位）。这种易位，写为t（11；22），将22号染色体上的部分EWSR1gene与11号染色体上另一个称为FLI1的gene融合，产生EWSR1/FLI1融合gene。（换行）由EWSR1/FLI1融合gene产生的蛋白质，称为EWS/FLI，具有两种gene的蛋白质产物的功能。由FLI1gene产生的FLI蛋白附着于DNA并调节转录。该蛋白质控制某些细胞类型的生长和发育。EWS/FLI蛋白具有FLI蛋白的DNA结合功能以及EWS蛋白的转录调节功能。据认为，EWS/FLI蛋白异常开启和关闭各种gene的转录。这种转录失调导致不受控制的生长和分裂（增殖）以及细胞的异常成熟和存活，从而导致肿瘤发展。（换行）EWSR1/FLI1融合gene发生在约85%的尤因肉瘤中。将EWSR1gene与其他与FLI1gene相关的gene融合的易位也可能导致这些类型的肿瘤，尽管这些替代易位相对不常见。由较不常见的易位产生的融合蛋白具有与EWS/FLI蛋白相同的功能。"}, {"name": "其他癌症", "detail": "涉及EWSR1gene的易位涉及许多类型的软组织肉瘤，这些肉瘤是在软组织如脂肪，肌肉和神经中引起肿瘤的癌症。这些易位将EWSR1gene与其他几个gene之一融合在一起。这些其他gene，有时被称为融合伴侣，似乎决定了发展的肿瘤类型。（换行）EWSR1/WT1融合gene引起结缔组织增生性小圆细胞瘤，这是一种常见于腹部的软组织肉瘤。EWSR1/ATF1融合gene参与一种罕见的癌症，称为软组织透明细胞肉瘤（或软组织恶性黑色素瘤）。在这种类型的癌症中，肿瘤通常在肌腱中发展，特别是在膝盖，脚和脚踝中。产生EWSR1/NR4A3融合gene的易位会导致骨骼外粘液样软骨肉瘤，这是一种罕见的软组织肿瘤，通常发生在下半身，例如大腿或臀部。EWSR1/DDIT3融合gene有时在粘液样脂肪肉瘤中发现，尽管它是这种癌症的罕见原因。粘液样脂肪肉瘤发生在身体许多部位的脂肪组织中。"}]}' WHERE id = 7251;
UPDATE genes SET GHR_metadata_ch = '{"gene": "TNNT3", "conditions": [{"name": "谢尔顿-霍尔综合征", "detail": "在谢尔顿-霍尔综合征患者中至少鉴定出三个TNNT3gene突变。这种疾病会影响出生前的肌肉和骨骼发育，其特征是关节畸形（挛缩）限制了手和脚的运动。导致Sheldon-Hall综合征的TNNT3gene突变可能会阻止肌钙蛋白复合物阻止粗细丝结合以控制肌肉收缩，从而导致挛缩以及与Sheldon-Hall综合征相关的其他肌肉和骨骼异常。"}]}' WHERE id = 7283;
UPDATE genes SET GHR_metadata_ch = '{"gene": "EFHC1", "conditions": [{"name": "青少年肌阵挛性癫痫", "detail": "EFHC1gene的突变已在少数青少年肌阵挛性癫痫患者中发现。这种情况通常始于儿童期或青春期，并引起反复的肌阵挛性癫痫发作，其特征是快速，不受控制的肌肉抽搐。受影响的个体还可以有其他类型的癫痫发作，称为全身性强直阵挛性癫痫发作（或大发作）和失神发作。与青少年肌阵挛性癫痫相关的大多数gene突变都取代了EFHC1蛋白中的单个蛋白结构单元（氨基酸）。认为改变的蛋白质的功能降低。尽管尚不清楚EFHC1gene突变如何导致青少年肌阵挛性癫痫，但研究人员建议，EFHC1蛋白功能的降低会减少细胞凋亡，导致神经元比正常人多，并破坏钙稳态。总之，这些变化可能导致神经元的过度刺激，引起青少年肌阵挛性癫痫的癫痫发作特征。"}]}' WHERE id = 7332;
UPDATE genes SET GHR_metadata_ch = '{"gene": "PIGA", "conditions": [{"name": "阵发性夜间血红蛋白尿", "detail": "一些gene突变是在一个人的一生中获得的，并且仅存在于某些细胞中。这些被称为体细胞突变的变化不是遗传的。在阵发性夜间血红蛋白尿患者中，PIGAgene的体细胞突变发生在称为造血干细胞的造血细胞中。造血干细胞产生携带氧气的红细胞（红细胞），保护身体免受感染的白细胞（白细胞）和参与血液凝固的血小板（血小板）。(换行)阵发性夜间血红蛋白尿患者的造血干细胞中有一个或多个PIGAgene突变，导致血细胞异常。随着异常造血干细胞的繁殖，形成异常血细胞群，以及正常造血干细胞产生的正常血细胞。体内异常血细胞的比例影响阵发性夜间血红蛋白尿的体征和症状的严重程度。(换行)研究人员已经在PIGAgene中鉴定出100多个体细胞突变。这些突变中的一些会改变A类磷脂酰肌醇聚糖中蛋白质结构单元（氨基酸）的数量或类型，从而损害其功能。其他突变导致在制备磷脂酰肌醇聚糖a类的说明中插入过早的终止信号。结果，产生了通常不稳定的异常小的蛋白质。"}, {"name": "辛普森-戈拉比-贝梅尔综合征", "detail": "遗传学家庭参考资料提供有关Simpson-Golabi-Behmel综合征的信息。"}]}' WHERE id = 7333;
UPDATE genes SET GHR_metadata_ch = '{"gene": "CLCN5", "conditions": [{"name": "牙病", "detail": "已经发现CLCN5gene中的突变引起Dent疾病1，Dent疾病1是一种可导致肾衰竭的慢性肾病，其特征在于骨缺陷，肾结石和相关的健康问题。这是由于异常短且无功能的蛋白质引起的，该蛋白质破坏了肾脏中内体pH的调节和近端小管的整体功能。"}, {"name": "遗传性低磷佝偻病", "detail": "CLCN5gene的突变与遗传性低磷酸盐血症性ets病有关，这是一种与血液中磷酸盐含量低（低磷酸盐血症）有关的疾病。临床症状始于儿童早期，包括低磷血症，生长缓慢，比同龄人短，骨骼异常等；骨痛，弯曲的腿或膝盖弯曲（小腿向外倾斜的情况）。成人的其他功能包括：；颅骨融合症，肠病，骨软化症，在极少数情况下伴有高钙尿症。"}]}' WHERE id = 7334;
UPDATE genes SET GHR_metadata_ch = '{"gene": "NBN", "conditions": [{"name": "综合征如奈梅亨破损综合症", "detail": "已发现NBNgene中至少有10个突变会引起奈梅亨断裂综合症，这种疾病的特征是生长缓慢，反复感染和罹患癌症的风险增加。导致奈梅亨断裂综合症的NBNgene突变通常会导致异常短版本的脑啡肽蛋白产生。在大多数受影响的个体中发现的突变，特别是在东欧的斯拉夫种群中，从NBNgene（写为657 661del5）中删除了5个DNA构建块（核苷酸）。这种突变导致产生称为p70-nibrin的nibrin蛋白的缩短版本。这种缩短的蛋白质在响应DNA损伤方面不如正常的脑啡肽有效，但是p70脑啡肽似乎确实具有一些残留功能。（换行）当DNA断裂未得到适当修复时，遗传损伤可能会累积。DNA中错误的积累会触发细胞异常生长和分裂，从而增加奈梅亨断裂综合症患者患癌的风险。人们认为，Nibrin在调节细胞分裂和细胞生长（增殖）中的作用会导致在受影响的个体中看到的免疫系统问题。缺乏功能性脑啡肽导致免疫细胞增殖减少。产生的免疫细胞数量减少导致免疫系统故障。目前尚不清楚NBNgene的突变如何引起奈梅亨断裂综合症的其他特征。"}, {"name": "乳腺癌", "detail": "遗传学家庭参考提供有关乳腺癌的信息。"}, {"name": "卵巢癌", "detail": "遗传学家庭参考提供有关卵巢癌的信息。"}, {"name": "前列腺癌", "detail": "遗传学家庭参考提供有关前列腺癌的信息。"}, {"name": "其他癌症", "detail": "NBNgene的遗传突变，包括上述c.657_661del5突变，也与其他几种类型的癌症有关。东欧人群的研究报告指出，每个细胞中NBNgene一个拷贝发生突变的人可能比不携带NBN突变的人更容易患乳腺癌、前列腺癌、卵巢癌、侵袭性皮肤癌（黑色素瘤）或血液形成细胞癌（白血病）。在NBNgene的一个拷贝中发生突变的细胞不能像没有这些突变的细胞那样有效地修复DNA。人们认为DNA损伤会随着时间的推移而累积，这可能会触发细胞无法控制地生长和分裂，并增加患癌症的风险。"}]}' WHERE id = 7336;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ACE", "conditions": [{"name": "肾小管发育不全", "detail": "ACEgene的突变与严重的肾脏疾病有关，称为肾小管发育不全，由无功能的血管紧张素转换酶引起，该酶损害血管紧张素II的形成。该疾病的特征是灌注不足，出生前肾脏发育异常，无尿和严重低血压，导致羊水减少（羊水过少），从而导致一系列出生缺陷，称为波特序列。"}, {"name": "其他疾病", "detail": "在携带两个I等位gene（II），两个D等位gene（DD）或每个（ID）的一个等位gene的个体中观察到称为ACE I/D（插入/缺失）多态性的ACEgene的遗传变异。与中风风险增加和糖尿病并发症有关。"}]}' WHERE id = 7337;
UPDATE genes SET GHR_metadata_ch = '{"gene": "TREM2", "conditions": [{"name": "多囊性脂质膜性骨发育不良伴硬化性白质脑病", "detail": "在患有硬化性白质脑病（通常称为PLOSL）的多囊性脂质膜性骨发育不良患者中，已经鉴定出TREM2gene中至少有10个突变。一些突变阻止细胞产生任何TREM2蛋白，而另一些则导致异常短而无功能的蛋白产生。还有其他突变会改变TREM2蛋白的结构，阻止其到达细胞表面。(换行)研究人员认为PLOSL的体征和症状与破骨细胞和小胶质细胞中TYROBP-TREM2信号缺陷有关。这种疾病所见的骨骼异常可能与破骨细胞功能异常有关，破骨细胞在骨骼重塑过程中吸收骨组织的能力较弱。在中枢神经系统中，通过TYROBP-TREM2复合物的信号传导缺陷会导致小胶质细胞的广泛异常。研究人员正在努力确定这些异常如何导致与PLOSL相关的神经系统问题。"}]}' WHERE id = 7355;
UPDATE genes SET GHR_metadata_ch = '{"gene": "MAPT", "conditions": [{"name": "额颞叶痴呆伴帕金森氏症17", "detail": "已发现MAPTgene中的40多个突变会导致帕金森氏症17（FTDP-17）引起额颞叶痴呆。这些突变中的一些改变tau蛋白中的单个氨基酸，最常见于微管结合区。这些突变降低了tau与微管结合的能力，从而破坏了许多重要的细胞功能。(换行)其他MAPTgene突变改变了gene指令用于构建tau蛋白的方式。与具有三个重复片段的tau的产生相比，这些突变中的大多数增加了具有四个重复片段的tau的产生。由此导致的大脑中tau亚型的失衡会干扰脑细胞的正常功能。(换行)以尚未完全理解的方式，负责FTDP-17的MAPTgene突变导致神经元和其他脑细胞中异常tau的积累。尽管尚不清楚它们对细胞功能和存活有什么影响，但这些有缺陷的tau团块会随着时间的推移而积累。FTDP-17的特征是大脑中称为额叶和颞叶的区域中的细胞逐渐死亡。额叶参与推理，计划，判断和解决问题，而颞叶帮助处理听力，言语，记忆和情绪。这些大脑区域中细胞的丢失导致FTDP-17的主要特征，包括性格和行为的改变，言语和语言异常以及运动问题。"}, {"name": "进行性核上性麻痹", "detail": "已经发现MAPTgene中的几个突变引起进行性核上性麻痹。但是，该gene的突变似乎是这种疾病的罕见原因。（换行）MAPTgene中至少有一个正常变异（多态性）与发展为进行性核上性麻痹的风险增加有关。这种多态性被称为H1单倍型，在进行性核上性麻痹患者中比在普通人群中更常见。目前尚不清楚这种遗传变异究竟如何增加患这种疾病的风险。(换行)进行性核上性麻痹的特征似乎与tau蛋白异常有关。在具有MAPTgene突变的人中，遗传变化会破坏蛋白质的正常结构和功能。但是，在没有MAPTgene突变的人中也发现了异常的tau。有缺陷的tau蛋白在神经元和其他脑细胞内组装成异常的团块，尽管尚不清楚这些团块对细胞功能和存活有什么影响。进行性核上性麻痹的特征是脑细胞逐渐死亡，特别是在大脑深处对协调运动至关重要的结构中。脑细胞的这种丧失是进行性核上性麻痹的主要特征的基础，包括运动，视力，言语和思维（认知）方面的问题。"}, {"name": "其他疾病", "detail": "还发现MAPTgene的突变会引起其他类似于FTDP-17和进行性核上性麻痹的脑部疾病。这些疾病包括皮质基底变性，伴有呼吸衰竭的tauopathy和伴有癫痫发作（癫痫）的痴呆形式。尽管这些情况的体征和症状模式有所不同，但它们都涉及性格，行为或认知的变化以及运动问题。导致这些疾病的MAPTgene突变导致脑细胞中异常tau的积累。尽管tau积累对细胞功能和存活的影响尚不清楚，但这些疾病的特征是大脑区域中脑细胞的死亡，这对于认知，情绪和协调运动至关重要。(换行)因为所有这些疾病的特征在于脑中tau的异常累积，所以它们被称为tau蛋白病。一些研究人员建议，由MAPTgene突变引起的tauopathies组不应被描述为具有不同体征和症状的光谱的一部分。"}]}' WHERE id = 7404;
UPDATE genes SET GHR_metadata_ch = '{"gene": "BBS1", "conditions": [{"name": "巴德-毕德氏症候群", "detail": "已经在患有Bardet-Biedl综合征的人中鉴定出BBS1gene的突变，该疾病的特征是眼睛异常，包括视力丧失，肥胖，多指，肾脏异常和智力障碍。该病是由于异常短或无功能的蛋白质引起的，该蛋白质损害纤毛形成并导致感觉知觉异常。"}]}' WHERE id = 7408;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ATRX", "conditions": [{"name": "α地中海贫血X连锁智力障碍综合征", "detail": "已经在患有α地中海贫血X连锁智力障碍综合征的人中鉴定出ATRXgene的突变，该疾病的特征在于发育迟缓，独特的面部特征。这是由于单个氨基酸的变化破坏了ATRX蛋白的稳定性，导致gene表达受损，HBA1和HBA2活性受损"}, {"name": "其他疾病", "detail": "已经在患有血液和骨髓疾病的人中发现了ATRXgene的体细胞突变，称为骨髓增生异常综合症（MDS）。在某些情况下，体细胞突变会降低MDS患者中导致α地中海贫血的HBA1和HBA2的活性，MDS是一种称为α地中海贫血-骨髓增生异常综合症（ATMDS）的组合疾病。"}]}' WHERE id = 7418;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SLC25A1", "conditions": [{"name": "2-羟基戊二酸尿症", "detail": "已发现SLC25A1gene中至少有12个突变会引起2-羟基戊二酸尿症，称为D，L-2-羟基戊二酸尿症（D，L-2-HGA）。这种情况会导致严重的脑部异常，这在婴儿早期就很明显。（换行）每个已知的SLC25A1gene突变都会大大降低SLC25A1蛋白的功能。结果，柠檬酸盐和苹果酸盐不能运输进出线粒体，这破坏了细胞内的能量产生。通过尚未完全了解的过程，缺乏柠檬酸盐和苹果酸盐转运允许其他化合物在细胞内异常积聚。这些化合物包括D-2-羟基戊二酸和L-2-羟基戊二酸，它们在高水平上会损伤细胞并导致细胞死亡。脑细胞似乎最容易受到这些化合物的毒性作用，这可以解释为什么D，L-2-HGA的体征和症状主要涉及大脑。研究人员怀疑，其他分子的不平衡，特别是柠檬酸盐，也会导致D，L-2-HGA联合的严重症状和体征"}]}' WHERE id = 7423;
UPDATE genes SET GHR_metadata_ch = '{"gene": "TCF4", "conditions": [{"name": "皮特-霍普金斯综合征", "detail": "已发现TCF4gene中至少有50个突变会引起Pitt-Hopkins综合征，这种疾病的特征是严重的智力残疾和呼吸问题。一些突变删除了TCF4gene内的一些DNA（核苷酸）构建块，而其他突变删除了TCF4gene以及围绕它的许多gene。还有其他TCF4gene突变取代了单个核苷酸。突变的大小似乎并不影响疾病的严重程度；缺失较大的人和单核苷酸变化的人似乎具有相似的体征和症状。（换行）TCF4gene突变破坏了蛋白质结合DNA和控制某些gene活性的能力。这些gene突变通常不会影响TCF4蛋白与其他蛋白结合的能力。TCF4蛋白无法结合DNA并控制某些gene的活性，特别是那些与神经系统发育和功能有关的gene，会导致皮特-霍普金斯综合征的体征和症状。附着在非功能性TCF4蛋白上的正常蛋白的丢失也可能是造成这种情况的原因。"}, {"name": "远端18q缺失综合征", "detail": "遗传学家庭参考提供有关远端18q缺失综合征的信息。"}, {"name": "Fuchs内皮营养不良", "detail": "遗传学家庭参考提供有关Fuchs内皮营养不良的信息。"}]}' WHERE id = 7428;
UPDATE genes SET GHR_metadata_ch = '{"gene": "BRCA2", "conditions": [{"name": "乳腺癌", "detail": "BRCA2gene的遗传突变与女性和男性患乳腺癌的风险增加有关。临床症状包括乳房内或乳房附近肿块或增厚，乳房大小或形状改变，乳头溢液，压痛，或缩回（向内转动），皮肤刺激和凹陷，或鳞屑。BRCA1的遗传变化会导致蛋白质异常或无功能，该蛋白质无法修复受损的DNA，从而导致不受控制的生长和细胞分裂，从而导致肿瘤形成。"}, {"name": "卵巢癌", "detail": "BRCA2gene中的种系突变与乳腺癌风险增加相关，卵巢癌风险增加约12-25%，通常称为遗传性乳腺癌和卵巢癌（HBOC）综合征。癌症进展表现出临床症状，包括骨盆或下腹部疼痛或沉重感，腹胀，进食时感觉充分，背痛，月经间或绝经后阴道出血，或尿或排便习惯改变。"}, {"name": "前列腺癌", "detail": "BRCA2gene的遗传突变与男性通常在中年或晚年出现的前列腺癌风险增加有关。癌症进展显示症状包括例如；难以开始或停止尿液流动，无法完全排空膀胱，尿液或精液中的血液或射精疼痛。这是由于BRCA2肿瘤抑制活性的异常丧失所致，该活性未能修复受损的DNA，从而导致不受控制的生长和细胞分裂，从而导致肿瘤形成。"}, {"name": "范可尼贫血", "detail": "BRCA2gene突变与Fanconi贫血有关，Fanconi贫血是一种以骨髓衰竭，身体异常，器官缺陷为特征的疾病，并且某些癌症（包括急性髓性白血病（AML）或头，颈，皮肤肿瘤）的风险增加，胃肠系统或生殖道。"}, {"name": "其他癌症", "detail": "BRCA2gene的遗传突变与其他几种癌症的风险增加相关，例如：；胰腺癌和黑色素瘤。BRCA2的遗传变化会导致蛋白质异常或无功能，该蛋白质无法修复受损的DNA，从而导致不受控制的生长和细胞分裂，从而导致肿瘤形成。"}]}' WHERE id = 7430;
UPDATE genes SET GHR_metadata_ch = '{"gene": "PRRT2", "conditions": [{"name": "家族性偏瘫性偏头痛", "detail": "在家族性偏瘫偏头痛患者中，PRRT2gene至少鉴定出两个突变。这种情况的特征是偏头痛，伴有一种称为先兆的神经症状。在家族性偏瘫性偏头痛中，先兆包括身体一侧暂时麻木或虚弱（偏瘫）。（换行）在多个家族性偏瘫偏头痛患者中发现的一个PRRT2gene突变在该gene中插入了一个额外的DNA构建块（核苷酸）。（此更改写为649dupC。）这两个已知的突变都会改变用于制造该蛋白质的蓝图，并导致产生异常短的PRRT2蛋白质，该蛋白质会迅速分解。结果，受影响的个体缺乏PRRT2蛋白。研究人员推测，这种短缺会影响SNAP25蛋白的功能，导致神经元之间的信号异常，尽管引起家族性偏瘫偏头痛的机制尚不清楚。据认为，大脑中信号传导的变化导致该疾病特征性的严重头痛的发展。"}, {"name": "家族性阵发性运动诱发性运动障碍", "detail": "已发现PRRT2gene中的10多个突变会引起称为家族性阵发性运动原性运动障碍的神经系统疾病。这种情况的特征是突然的运动触发了身体的非自愿抽搐或摇晃，例如迅速站起来或受到惊吓。649dupC突变（如上所述）是家族性阵发性运动原性运动障碍中最常见的遗传变化。这种情况下涉及的大多数PRRT2gene突变，包括649dupC，都会导致异常短的蛋白质迅速分解。结果，受影响的个体的PRRT2蛋白比正常人少。研究人员推测，PRRT2的缺乏会影响SNAP25蛋白的功能，从而导致神经元之间的信号异常。改变的神经元活动可能是家族性阵发性运动诱发性运动障碍的运动问题的基础，但确切的机制尚不清楚。"}, {"name": "其他疾病", "detail": "已发现PRRT2gene突变会引起其他神经系统疾病，包括良性家族性婴儿癫痫发作（BFIS），婴儿惊厥和胆囊炎（ICCA）。BFIS的特征是从婴儿期开始反复发作，通常在2岁时消失。ICCA的特征是婴儿良性癫痫发作（如BFIS中发生的发作）和非自愿运动发作（如家族性阵发性运动原性运动障碍中发生的发作）（如上所述）。引起家族性阵发性运动原性运动障碍和家族性偏瘫性偏头痛的649dupC突变也可引起BFIS和ICCA。目前尚不清楚这种突变如何在某些人中引起一种疾病，而在另一些人中引起另一种疾病。(换行)尽管已将其描述为单独的疾病，但研究人员推测阵发性运动原性运动障碍，BFIS，ICCA和家族性偏瘫性偏头痛可能代表了一系列相关疾病。在一些具有PRRT2gene突变的家庭中，受影响的个体状况不同；例如，一个可能有阵发性运动诱发性运动障碍，另一个可能有家族性偏瘫性偏头痛。有时，受影响的个人具有这些相关conditions中的一种以上的特征。此外，可引起家族性阵发性运动原性运动障碍和家族性偏瘫性偏头痛的649dupC突变也与BFIS和ICCA有关。目前尚不清楚这种突变如何引起多种疾病。环境因素和遗传因素的组合可能会影响受影响个体发展的体征和症状的模式。"}]}' WHERE id = 7432;
UPDATE genes SET GHR_metadata_ch = '{"gene": "KCNE1", "conditions": [{"name": "Jervell和Lange-Nielsen综合征", "detail": "在患有Jervell和Lange-Nielsen综合征的人中，已经鉴定出KCNE1gene中至少有四个突变，这种情况会导致心律异常（心律不齐）和出生后严重的听力丧失。大约10%的病例是由该gene突变引起的。受影响的人通常在每个细胞的两个KCNE1gene拷贝中都有突变。这些突变改变了KCNE1蛋白中的单个蛋白结构单元（氨基酸），从而破坏了该蛋白的正常结构。改变的KCNE1蛋白不能调节钾离子通过内耳和心肌通道的流动。通道功能的丧失导致Jervell和Lange-Nielsen综合征的心律失常和听力丧失。"}, {"name": "其他疾病", "detail": "某些药物，包括用于治疗心律失常，感染，癫痫发作和精神病的药物，可能导致某些人心律异常。这种药物引起的心脏病被称为获得性长QT综合征，增加了心脏骤停和猝死的风险。一小部分获得性长QT综合征病例发生在KCNE1gene潜在突变的人群中。"}]}' WHERE id = 7470;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ALOX12B", "conditions": [{"name": "非大疱性先天性鱼鳞状红皮病", "detail": "已经发现ALOX12Bgene中的几个突变引起非大疱性先天性鱼鳞状红皮病（NBCIE），这是由无功能的12R-LOX酶引起的病症，其特征在于皮肤异常，例如发红，发展成细白色鳞片，增加感染风险和过度脱水。"}, {"name": "其他疾病", "detail": "已经发现ALOX12Bgene中的几个突变会引起鱼鳞病，称为自愈胶棉婴儿，这种疾病的特征是出生时覆盖皮肤的紧密透明的鞘称为胶棉膜，通常在生命的最初几周内脱落。"}]}' WHERE id = 7475;
UPDATE genes SET GHR_metadata_ch = '{"gene": "TPO", "conditions": [{"name": "先天性甲状腺功能减退症", "detail": "TPOgene突变可导致先天性甲状腺功能减退，这种疾病的特征是从出生开始甲状腺激素水平异常低。涉及这种情况的TPOgene突变删除，添加或改变TPOgene中的DNA构建块（碱基对）。一些突变会导致异常小的甲状腺过氧化物酶，在将其插入细胞膜之前会分解。其他突变会改变酶的3维形状，从而阻止其在细胞膜内正常运行。没有功能性甲状腺过氧化物酶，甲状腺吸收的碘不会添加到甲状腺球蛋白中。结果，甲状腺激素的产生不存在或减少，导致先天性甲状腺功能减退症的特征。在大多数受影响的个体中，甲状腺肿大（甲状腺肿）以弥补激素产生减少。由于TPOgene突变引起的病例是甲状腺激素合成中断的结果，因此将其归类为甲状腺功能异常。"}]}' WHERE id = 7478;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SLURP1", "conditions": [{"name": "Mal de Meleda", "detail": "已发现SLURP1gene中至少有15个突变会引起mal de Meleda，这是一种罕见的疾病，其特征是手脚皮肤坚韧，增厚。在手掌和脚底上，增厚称为掌plant角化病；增厚的皮肤也延伸到手和脚的背部，一直延伸到手腕和脚踝。这种情况下涉及的SLURP1gene突变导致产生改变的SLURP-1蛋白，该蛋白不稳定并迅速分解（如果完全产生任何蛋白）。结果，受影响的个体几乎没有SLURP-1蛋白。目前还不清楚缺乏这种蛋白质是如何导致mal de Meleda发生皮肤问题的。研究人员推测，如果没有SLURP-1，受nAChR信号控制的gene的活性就会改变，从而导致皮肤细胞过度生长或通常会死亡的细胞存活。细胞过多会导致皮肤增厚。目前尚不清楚为什么手脚皮肤受到特别影响。"}]}' WHERE id = 7481;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SLC37A4", "conditions": [{"name": "I型糖原贮积病", "detail": "已发现SLC37A4gene中的80多个突变会导致Ib型糖原贮积病（GSDIb）。这些突变大多数会改变6-磷酸葡萄糖转位酶中的单个蛋白质结构单元（氨基酸）。SLC37A4gene突变破坏了6磷酸葡萄糖转位酶的正常功能，并阻止了6磷酸葡萄糖向内质网的转运。如果6-磷酸葡萄糖无法到达内质网，则无法分解，也不会产生葡萄糖。未分解成葡萄糖的葡萄糖6-磷酸转化为脂肪和糖原，糖原是储存在细胞内的复合糖。储存在细胞内的过多脂肪和糖原可能是有毒的。这种累积会损害整个身体的器官和组织，特别是肝脏和肾脏，导致GSDIb的体征和症状。由于尚不清楚的原因，SLC37A4gene的突变也导致GSDIb患者缺乏白细胞（中性粒细胞减少）。"}]}' WHERE id = 7549;
UPDATE genes SET GHR_metadata_ch = '{"gene": "PROKR2", "conditions": [{"name": "卡门氏症候群", "detail": "PROKR2gene中至少有30个突变可引起Kallmann综合征，这是一种以性腺功能低下性腺功能减退症（一种影响指导性发育的激素产生的疾病）和嗅觉受损为特征的疾病。研究人员估计，PROKR2和PROK2gene的突变加起来约占所有Kallmann综合征病例的9%。（换行）导致Kallmann综合征的大多数PROKR2gene突变会改变prokineticin受体2中的单个蛋白质结构单元（氨基酸）。这些突变破坏了受体的功能，影响了其触发细胞内化学信号的能力。这种信号的丢失破坏了发育中的大脑中嗅觉神经元和产生GnRH的神经元的迁移和存活。如果嗅神经细胞不延伸到嗅球，则人的嗅觉将受损或缺失。产生GnRH的神经元的错位或过早丢失会阻止性激素的产生，从而干扰正常的性发育并导致青春期延迟或缺失。(换行)由于Kallmann综合征的特征和严重程度因人而异，研究人员认为可能涉及其他遗传和环境因素。一些受影响的个体除PROKR2外，其他几个gene之一也有突变，这些遗传变化可能有助于疾病的不同特征。"}, {"name": "合并垂体激素缺乏症", "detail": "遗传学家庭参考资料提供有关合并垂体激素缺乏症的信息。"}, {"name": "视光发育不良", "detail": "遗传学家庭参考提供有关视光发育不良的信息。"}, {"name": "其他疾病", "detail": "仅在具有Kallmann综合征的两个主要特征之一（如上所述）的人中发现了PROKR2gene的一些突变：促性腺激素性性腺功能减退症或嗅觉受损。当性腺功能减退性腺功能减退症以正常的嗅觉发生时，称为常态性孤立性腺功能减退性腺功能减退症（nIHH）。没有性腺功能减退性腺功能减退症的嗅觉受损称为孤立性先天性失眠（ICA）。像引起Kallmann综合征的PROKR2gene突变一样，与这些情况相关的突变会损害prokineticin受体2的功能，从而阻止其正确传输信号。这种信号的丢失会破坏发育中的大脑中产生GnRH的神经细胞或嗅觉神经元的迁移。目前尚不清楚为什么该gene的某些突变会导致Kallmann综合征患者性腺功能低下性腺功能减退和嗅觉受损，而nIHH或ICA患者仅具有这些特征之一。"}]}' WHERE id = 7589;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ALOXE3", "conditions": [{"name": "非大疱性先天性鱼鳞状红皮病", "detail": "已发现ALOXE3gene中的几个突变会引起非大疱性先天性鱼鳞状红皮病（NBCIE），这是一种由无功能的eLOX3酶引起的疾病，其特征是皮肤异常，例如发红，出现细小的白色鳞屑，感染风险增加和过度脱水。"}]}' WHERE id = 7599;
UPDATE genes SET GHR_metadata_ch = '{"gene": "GDF3", "conditions": [{"name": "Klippel-Feil综合征", "detail": "已经发现GDF3gene中至少有四个突变引起Klippel-Feil综合征，其特征在于颈部（颈椎）中两个或更多个脊柱骨的异常连接（融合）以及影响许多其他特征的各种其他特征身体的部分。导致Klippel-Feil综合征的GDF3gene突变取代了GDF3蛋白中的单个蛋白结构单元（氨基酸）。这些突变可能导致功能蛋白减少。尽管GDF3蛋白参与骨骼生长，但尚不清楚这种蛋白的缺乏如何导致Klippel-Feil综合征患者的颈椎分离不完全。"}, {"name": "缺损", "detail": "遗传学家庭参考提供有关结肠癌的信息。"}, {"name": "小眼症", "detail": "遗传学家庭参考提供有关小眼症的信息。"}]}' WHERE id = 7667;
UPDATE genes SET GHR_metadata_ch = '{"gene": "APC", "conditions": [{"name": "硬纤维瘤", "detail": "已经发现APCgene中的几种突变具有一种由结缔组织称为类胶质瘤引起的异常良性生长。APCgene突变导致腹内类胶质瘤导致肠梗阻，便秘，并且还引起腹外类胶质瘤，导致手臂，腿部运动异常，由于受影响关节的活动受限而跛行。"}, {"name": "家族性腺瘤性息肉病", "detail": "APCgene的突变已经在家族性腺瘤性息肉病（FAP）的典型和减毒类型的家族中被确认，FAP是一种在结肠中形成异常生长（息肉）的拥有属性，导致大肠癌，这取决于APCgene突变的位置。"}, {"name": "原发性大结节性肾上腺增生", "detail": "APCgene的突变与原发性大结节性肾上腺皮质增生（PMAH）有关，该疾病的特征是肾上腺有多个肿块（结节），肾上腺增生和皮质醇激素水平升高。这种疾病被认为是库欣综合征的一种形式，被认为表现出这种综合征的一些亚临床症状。"}, {"name": "其他癌症", "detail": "APCgene的突变与其他称为Turcot综合征的FAP相关疾病有关，该疾病的特征是大肠癌与髓母细胞瘤相关。另外，APCgene中的体细胞突变与一小部分胃癌（胃癌）的发展有关。"}]}' WHERE id = 7692;
UPDATE genes SET GHR_metadata_ch = '{"gene": "RUNX1", "conditions": [{"name": "核心结合因子急性髓性白血病", "detail": "在大约7%的患有一种称为急性髓细胞性白血病（AML）的血癌的个体中发现了涉及RUNX1gene的遗传物质的重排（易位）。易位，写为t（8；21），结合来自21号染色体和8号染色体的遗传信息，将21号染色体上的RUNX1gene与8号染色体上的geneRUNX1T1（也称为ETO）融合。由于这种遗传变化会影响CBF，因此该疾病被归类为核心结合因子AML（CBF-AML）。（换行）得到的融合蛋白RUNX1-ETO能够形成CBF并附着于DNA，就像正常的RUNX1蛋白一样；然而，它不是打开gene，而是关闭gene。这种gene活性的变化阻碍了血细胞的成熟（分化），并导致产生异常的，未成熟的白细胞，称为髓样细胞。尽管t（8；21）对于白血病的发展很重要，但髓样母细胞发育成癌性白血病细胞通常需要一个或多个其他gene的突变。"}, {"name": "细胞遗传学正常的急性髓性白血病", "detail": "遗传学家庭参考提供有关细胞遗传学正常的急性髓细胞性白血病的信息。"}, {"name": "类风湿关节炎", "detail": "遗传学家庭参考提供有关类风湿关节炎的信息。"}, {"name": "其他疾病", "detail": "涉及RUNX1gene的易位和其他类型的突变与不同类型的白血病和相关的血液疾病有关，包括急性淋巴细胞白血病（ALL），慢性粒单核细胞白血病（CMML），易患急性髓性白血病的家族性血小板疾病和骨髓增生异常综合征（MDS）。根据突变的类型，这些情况可能与gene活性调节受损或正常gene功能丧失有关。与这些疾病相关的RUNX1gene突变是体细胞突变，不是遗传的。它们只存在于身体的某些细胞中。"}]}' WHERE id = 7711;
UPDATE genes SET GHR_metadata_ch = '{"gene": "CYP11B1", "conditions": [{"name": "11-β-羟化酶缺乏症引起的先天性肾上腺皮质增生", "detail": "已发现CYP11B1gene中的80多个突变会由于11β-羟化酶缺乏症（一种肾上腺产生过量的男性性激素（雄激素）的疾病）而导致先天性肾上腺皮质增生（CAH）。这些突变大多数会改变11β-羟化酶中的单个蛋白质结构单元（氨基酸），并降低该酶的功能。严重降低或消除酶功能的CYP11B1gene突变通常由于11-β羟化酶缺乏症而导致经典形式的CAH。允许某些酶功能的突变通常会导致该疾病的非经典形式。（换行）由于11-β羟化酶缺乏导致经典形式的CAH的一些突变将CYP11B1gene的部分与称为CYP11B2的附近gene的部分融合。CYP11B2gene的添加部分包含一个称为启动子区域的部分，该部分通常控制（调节）由CYP11B2gene制成的蛋白质的产生。结果，CYP11B1gene受CYP11B2gene启动子区域而不是其自身的启动子区域调节。此外，融合通常会删除CYP11B1gene的一部分。gene调控和结构的这些变化减少了11-β羟化酶的产生。（换行）由于11-β羟化酶缺乏导致的两种类型的CAH都干扰皮质醇和皮质酮的产生。用于形成这些激素的分子反而在肾上腺中积聚并转化为雄激素。由于11β-羟化酶缺乏症，雄激素的过量产生导致CAH患者的性发育异常。11-β-羟化酶转化为皮质酮的物质11-脱氧皮质酮分子的积累增加了盐的滞留，由于11-β-羟化酶缺乏症，导致经典CAH患者出现高血压（高血压）。"}, {"name": "家族性醛固酮增多症", "detail": "影响CYP11B1gene的遗传变化会导致I型家族性醛固酮增多症，这是一种导致高血压的疾病。这种变化将CYP11B1gene的一部分称为启动子区域（通常有助于开始产生11-β羟化酶）连接到CYP11B2gene的一部分，该部分提供了制备醛固酮合酶的说明。（换行）通过与CYP11B1gene的启动子区域结合，ACTH通常触发11-β羟化酶的产生。在融合gene中，ACTH结合异常触发醛固酮合酶的产生。高水平的醛固酮合酶导致醛固酮过量产生，从而导致与I型家族性醛固酮增多症相关的高血压。"}]}' WHERE id = 7749;
UPDATE genes SET GHR_metadata_ch = '{"gene": "TNNI3", "conditions": [{"name": "家族性肥厚型心肌病", "detail": "TNNI3gene的突变可引起家族性肥厚型心肌病，这种疾病的特征是心肌增厚（肥大）。在这种情况下，只有不到5%的人发现TNNI3gene突变。尽管一些肥厚型心肌病患者没有明显的健康影响，但所有受影响的个体都有心力衰竭和猝死的风险增加。(换行)家族性肥厚型心肌病中大多数TNNI3gene突变会改变心肌肌钙蛋白I蛋白中的单个蛋白结构单元（氨基酸）。改变后的蛋白质可能会掺入肌钙蛋白复合物中，但可能无法正常发挥作用。目前尚不清楚这些突变如何导致家族性肥厚型心肌病的特征。(换行)在某些人中，肥厚型心肌病发展为限制性心肌病（如下所述），尽管很难区分这两种疾病。"}, {"name": "家族性限制性心肌病", "detail": "已发现TNNI3gene中约有10个突变会引起家族性限制性心肌病，其特征是心肌僵硬。这些突变大多数会改变心肌肌钙蛋白I蛋白中的单个氨基酸，从而损害该蛋白的功能。改变的蛋白质通常不能与肌动蛋白结合。结果，心肌松弛被破坏，导致心脏功能异常，血流受损以及家族性限制性心肌病的体征和症状，例如疲劳和晕厥。"}, {"name": "家族性扩张型心肌病", "detail": "遗传学家庭参考提供有关家族性扩张型心肌病的信息。"}, {"name": "其他疾病", "detail": "TNNI3gene的突变也可引起另一种称为扩张型心肌病的心脏病。这种情况会削弱并扩大心脏，阻止其有效抽血。扩张型心肌病会增加心力衰竭和过早死亡的风险。"}]}' WHERE id = 7763;
UPDATE genes SET GHR_metadata_ch = '{"gene": "PGAP2", "conditions": [{"name": "马布里综合征", "detail": "已发现至少五个PGAP2gene突变引起马布里综合征。马布里综合征的特征包括智力残疾，独特的面部特征，血液中称为碱性磷酸酶的酶水平升高（高磷血症）以及其他体征和症状。这些突变改变了PGAP2蛋白中的单个蛋白结构单元（氨基酸），并可能降低了该蛋白的活性。结果，PGAP2蛋白无法有效修饰GPI锚，可能会削弱锚自身及其相关蛋白附着在细胞膜上的能力。不能附着在细胞膜上的GPI锚相关蛋白从细胞中释放出来。（换行）称为碱性磷酸酶的酶通常通过GPI锚附着于细胞膜。但是，当锚受损时，碱性磷酸酶会从细胞中释放出来。碱性磷酸酶的这种异常释放是造成马布里综合征高磷血症的原因。目前尚不清楚PGAP2gene突变如何导致Mabry综合征的其他特征，但这些体征和症状可能是由于缺乏适当的GPI将蛋白质锚定在细胞膜上。"}]}' WHERE id = 7773;
UPDATE genes SET GHR_metadata_ch = '{"gene": "BBS10", "conditions": [{"name": "巴德-毕德氏症候群", "detail": "在Bardet-Biedl综合征患者中已鉴定出BBS10gene的几种突变，该疾病的特征是眼部异常，包括视力丧失，肥胖，手指和/或脚趾过多（多指），肾脏异常和智力障碍。该病是由于异常短或无功能的蛋白质引起的，该蛋白质损害纤毛形成并导致感觉知觉异常。"}]}' WHERE id = 7778;
UPDATE genes SET GHR_metadata_ch = '{"gene": "RPS19", "conditions": [{"name": "钻石-布莱克凡贫血", "detail": "在患有Diamond-Blackfan贫血的个体中已鉴定出120多个RPS19gene突变。据信这些突变影响RPS19蛋白的稳定性或功能。研究表明，缺乏功能性核糖体蛋白可能会增加骨髓中造血细胞的自我破坏，导致红细胞数量减少（贫血）。细胞分裂的异常调节或凋亡的不适当触发可能会导致其他健康问题和异常的身体特征，从而影响某些Diamond-Blackfan贫血患者。"}]}' WHERE id = 7787;
UPDATE genes SET GHR_metadata_ch = '{"gene": "TFAP2A", "conditions": [{"name": "分支眼面部综合征", "detail": "TFAP2Agene的突变会导致一种称为分支眼面部综合征的疾病，其特征是颈部皮肤异常，眼睛和耳朵畸形以及独特的面部特征。这种情况下涉及的大多数TFAP2Agene突变都会改变转录因子AP-2中的单个蛋白质结构单元（氨基酸）？蛋白质。这些变化倾向于发生在蛋白质的一个区域，使其能够与DNA结合。虽然氨基酸对转录因子AP-2的影响？未知，该蛋白质的DNA结合功能可能会受损。没有这种功能，蛋白质就无法在发育过程中控制gene的活性。TFAP2Agene突变破坏了源自branch弓的结构的发展，从而导致了branch眼面部综合征的特征。"}]}' WHERE id = 7823;
UPDATE genes SET GHR_metadata_ch = '{"gene": "RPS26", "conditions": [{"name": "钻石-布莱克凡贫血", "detail": "在患有Diamond-Blackfan贫血的个体中至少鉴定出9个RPS26gene突变。据信这些突变影响RPS26蛋白的稳定性或功能。研究表明，缺乏功能性核糖体蛋白可能会增加骨髓中造血细胞的自我破坏，导致红细胞数量减少（贫血）。细胞分裂的异常调节或凋亡的不适当触发可能会导致其他健康问题和异常的身体特征，从而影响某些Diamond-Blackfan贫血患者。"}]}' WHERE id = 7824;
UPDATE genes SET GHR_metadata_ch = '{"gene": "CYP11B2", "conditions": [{"name": "皮质酮甲基氧化酶缺乏症", "detail": "已经鉴定出至少30种引起皮质酮甲基氧化酶缺乏症（也称为醛固酮合酶缺乏症）的CYP11B2gene突变。这些突变导致醛固酮的产生不足，从而损害了肾脏将盐（氯化钠或NaCl）重吸收到血液中并在尿液中释放钾的能力。结果，过量的钠（Na+）和氯（Cl-）带电原子（离子）形式的盐离开体内尿液，而没有足够的钾释放。由此导致的体内离子失衡是这种疾病的体征和症状的基础，包括恶心，呕吐，脱水，低血压，极度疲倦（疲劳）和肌肉无力。"}, {"name": "家族性醛固酮增多症", "detail": "影响CYP11B2gene的遗传变化会导致I型家族性醛固酮增多症，这是一种导致高血压（高血压）的疾病。此更改将CYP11B2gene的一部分连接（融合），该部分包含将醛固酮合酶制备的说明与附近称为CYP11B1的gene的一部分连接。CYP11B1gene的附加部分包含一个称为启动子区域的部分，该部分通常有助于从CYP11B1gene开始生产称为11-β-羟化酶的酶。（换行）通过与CYP11B1gene的启动子区域结合，一种称为促肾上腺皮质激素（ACTH）的激素通常会触发11-β羟化酶的产生。在融合gene中，ACTH结合异常触发醛固酮合酶的产生。高水平的醛固酮合酶导致醛固酮过量产生，从而导致与I型家族性醛固酮增多症相关的高血压。"}, {"name": "其他疾病", "detail": "CYP11B2gene的正常变异（多态性）与心血管问题的风险增加有关，包括高血压，由于缺乏血液流向大脑（缺血性中风）引起的中风，以及心力衰竭患者称为心房颤动的心律异常。研究人员认为，CYP11B2gene变异导致的血压控制差异可能导致这些心血管问题的风险增加。"}]}' WHERE id = 7825;
UPDATE genes SET GHR_metadata_ch = '{"gene": "HSPB1", "conditions": [{"name": "进行性神经性腓骨肌萎缩症", "detail": "据报道，患有Charcot-Marie-Tooth病（称为2F型）的个体至少有3个HSPB1gene突变。Charcot-Marie-Tooth病是一组影响周围神经的进行性疾病。周围神经将大脑和脊髓连接到肌肉和感觉细胞，检测触觉，疼痛，热和声音等感觉。(换行)导致Charcot-Marie-Tooth病的每个HSPB1gene突变都会改变用于制造热激蛋白beta-1的单个蛋白质结构单元（氨基酸）。一个突变用蛋白质位置135处的氨基酸苯丙氨酸取代了氨基酸丝氨酸（写为Ser135Phe或S135F）。其他突变将氨基酸精氨酸替换为位置127（Arg127Trp或R127W）或位置136（Arg136Trp或R136W）的氨基酸色氨酸。这些突变改变了蛋白质的一个区域，该区域对于热激蛋白beta-1正常运行至关重要。（换行）尚不清楚HSPB1gene突变如何导致2F型Charcot-Marie-Tooth病特征性的轴突异常。研究人员认为，改变热休克蛋白β-1的分子聚集在一起（聚集）并干扰神经细胞的正常功能，特别是轴突功能。另一种可能性是改变的蛋白质破坏了神经丝的组装，这影响了轴突直径并损害了神经冲动的传递。"}, {"name": "远端遗传性运动神经病，II型", "detail": "研究人员已经鉴定出至少六个HSPB1gene突变，这些突变会导致一种称为II型远端遗传性运动神经病的疾病，类似于Charcot-Marie-Tooth病。II型远端遗传性运动神经病影响周围神经，其特征是进行性无力，主要发生在脚和腿上。与Charcot-Marie-Tooth病，远端遗传性运动神经病不同，II型不影响感觉细胞。（换行）导致远端遗传性运动神经病的HSPB1gene突变，II型改变热休克蛋白β-1中的单个氨基酸。尚不清楚这些突变如何导致这种疾病的体征和症状。与引起Charcot-Marie-Tooth病的HSPB1gene突变一样，研究表明，改变后的蛋白质可能更可能形成聚集体并阻止对神经轴突正常功能必不可少的物质的运输。涉及该蛋白质的其他细胞功能的破坏也可能导致周围神经疾病。"}]}' WHERE id = 7831;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ALX1", "conditions": [{"name": "额鼻发育不良", "detail": "已经发现ALX1gene中至少三个突变引起额鼻发育不良。ALX1gene突变会导致一种称为3型额鼻发育不良的疾病，其特征是面部中心结构严重畸形。导致这种情况的ALX1gene突变会改变蛋白质的结构，并削弱其与DNA结合并调节gene功能的能力。结果，发育过程中细胞的增殖和迁移受到控制，这可能导致眼睛小或缺失，鼻子或嘴巴的开口（裂口）以及3型额鼻发育不良的其他严重面部畸形。"}]}' WHERE id = 7844;
UPDATE genes SET GHR_metadata_ch = '{"gene": "PITX1", "conditions": [{"name": "Liebenberg综合征", "detail": "PITX1gene附近DNA的变化会导致Liebenberg综合征，这是一种罕见的疾病，其特征是手臂异常发育，导致手指短（近距离接触），称为挛缩的关节畸形，限制了肘部和腕部的运动以及其他骨骼和肌肉异常。这些遗传变化会删除，插入或重排PITX1gene附近的遗传物质；在受影响的个体中已经鉴定出至少五个这样的突变。这些变化影响称为调节元件的DNA区域，其有助于打开或关闭gene（分别称为增强子或阻遏物）。它们控制某些gene活跃的时间和地点。导致Liebenberg综合征的突变可能会将促进上肢发育相关gene活性的增强子重新定位在PITX1gene附近。或者，突变可以去除在上肢发育过程中通常关闭PITX1gene的阻遏物。结果，PITX1gene在手臂和手部发育过程中异常活跃。因为PITX1蛋白通常指导下肢结构，所以手臂和手中的骨骼，肌肉和肌腱的发育更像腿和脚中的肌腱，从而导致了Liebenberg综合征的特征。患有这种情况的人下肢发育正常。"}, {"name": "其他疾病", "detail": "已发现PITX1gene突变会导致下肢异常，包括向内和向上转弯的脚（马蹄内翻足）以及小腿中称为胫骨（胫骨血尿）的骨骼缺失或严重缩短。这些gene突变也已在脚趾外（多指）的人中发现，特别是一种严重的异常形式，称为镜像多指。这些突变删除了PITX1gene或整个gene的大部分，或改变了PITX1蛋白中的单个蛋白构建块（氨基酸）。与引起Liebenberg综合征的突变（如上所述）不同，这些遗传变化减少了功能性PITX1蛋白的量，从而破坏了下肢的正常发育。具有这些PITX1gene突变的个体上肢正常。"}]}' WHERE id = 7886;
UPDATE genes SET GHR_metadata_ch = '{"gene": "KL", "conditions": [{"name": "高磷酸盐血症家族性肿瘤性钙化病", "detail": "已经发现KLgene中的至少一种突变引起高磷酸盐血症性家族性肿瘤性钙质沉着症（HFTC），其特征在于血液中磷酸盐水平增加（高磷酸盐血症）和体内磷酸盐和钙的异常沉积（钙质沉着症）。组织。导致HFTC的KLgene突变用蛋白质序列中第193位的氨基酸精氨酸（写为His193Arg或H193R）取代蛋白质结构单元（氨基酸）组氨酸。这种突变导致缺乏功能性α-klotho。结果，FGF受体1未被激活，使其不能用于成纤维细胞生长因子23结合。成纤维细胞生长因子23蛋白是正常的，但它不能与其受体结合，也不能发出停止磷酸盐重吸收的信号。结果，过多的磷酸盐被重新吸收到血液中，导致高磷酸盐血症和随后的煅烧。"}]}' WHERE id = 7888;
UPDATE genes SET GHR_metadata_ch = '{"gene": "RPL11", "conditions": [{"name": "钻石-布莱克凡贫血", "detail": "在患有Diamond-Blackfan贫血的个体中已鉴定出25个以上的RPL11gene突变。据信这些突变影响RPL11蛋白的稳定性或功能。研究表明，缺乏功能性核糖体蛋白可能会增加骨髓中造血细胞的自我破坏，导致红细胞数量减少（贫血）。细胞分裂的异常调节或凋亡的不适当触发可能会导致其他健康问题和异常的身体特征，从而影响某些Diamond-Blackfan贫血患者。"}]}' WHERE id = 7902;
UPDATE genes SET GHR_metadata_ch = '{"gene": "FGF8", "conditions": [{"name": "卡门氏症候群", "detail": "在患有Kallmann综合征的人中，已经鉴定出FGF8gene的至少七个突变，该疾病的特征是性腺功能低下性腺功能减退症（一种影响直接性发育的激素产生的疾病）和嗅觉受损。这种情况也会影响其他身体系统，其特征因受影响的个体而异。研究人员估计，FGF8gene突变占所有Kallmann综合征病例的一小部分。（换行）导致Kallmann综合征的大多数FGF8gene突变改变FGF8蛋白中的单个蛋白质构建块（氨基酸）。这些突变降低或消除了蛋白质的功能，包括其与FGFR1结合的能力。研究表明，功能性FGF8的缺乏会破坏发育中的大脑中嗅觉神经元和产生GnRH的神经元的迁移和存活。如果嗅神经细胞不延伸到嗅球，则人的嗅觉将受损或缺失。产生GnRH的神经元的错位或过早丢失会阻止性激素的产生，从而干扰正常的性发育并导致青春期延迟或缺失。(换行)一些由FGF8gene突变引起的Kallmann综合征患者具有额外的特征，例如唇裂（唇裂），口顶开口（腭裂），以及称为双手运动的病症，其中一只手的运动被另一只手镜像。目前尚不清楚FGF8gene的突变如何导致这些其他体征和症状。由于这些特征因人而异，研究人员怀疑可能涉及其他遗传和环境因素。一些受影响的个体除FGF8外，其他几个gene之一也有突变，这些遗传变化可能导致疾病的不同特征。"}, {"name": "非综合征性全前脑", "detail": "遗传学家庭参考资料提供有关非综合征性全前脑的信息。"}, {"name": "非综合征性全前脑", "detail": "遗传学家庭参考资料提供有关非综合征性全前脑的信息。"}, {"name": "其他疾病", "detail": "已发现FGF8gene中的几种突变会导致某种形式的性腺功能减退性腺功能减退，而不会损害嗅觉。这种情况通常称为常态性孤立性腺功能减退性腺功能减退症（nIHH）。像大多数引起Kallmann综合征的FGF8gene突变（如上所述）一样，引起nIHH的突变会改变FGF8蛋白中的单个氨基酸。这些突变降低或消除了FGF8的功能，包括其与FGFR1结合的能力。功能性FGF8的缺乏会破坏发育中的大脑中产生GnRH的神经细胞的迁移，从而影响性激素的产生并导致青春期延迟或缺失。目前尚不清楚为什么某些FGF8gene突变会影响嗅觉（导致Kallmann综合征），而另一些则不会（导致nIHH）。已经发现至少有一种突变在某些人中引起Kallmann综合征，在另一些人中引起nIHH；这种遗传变化将FGF8蛋白（写为Arg127Gly或R127G）第127位的氨基酸甘氨酸替换为氨基酸精氨酸。"}]}' WHERE id = 7913;
UPDATE genes SET GHR_metadata_ch = '{"gene": "MCCC1", "conditions": [{"name": "3-甲基巴豆酰辅酶A羧化酶缺乏症", "detail": "在患有3-甲基巴豆酰基-CoA羧化酶缺乏症（也称为3-MCC缺乏症）的人中，已经鉴定出MCCC1gene中至少有30个突变。这些突变大多数会改变3-MCC中的单个氨基酸，但少数突变会导致酶的异常短版本的产生。MCCC1gene中的突变严重降低或消除了3-MCC的活性。结果，亮氨酸不能被正确分解，并且亮氨酸加工的副产物在体内积累到毒性水平。这些有毒物质会损害大脑，导致3-MCC缺乏症的特征性体征和症状。"}]}' WHERE id = 7941;
UPDATE genes SET GHR_metadata_ch = '{"gene": "PNP", "conditions": [{"name": "嘌呤核苷磷酸化酶缺乏症", "detail": "在嘌呤核苷磷酸化酶缺乏症患者中至少鉴定出24个PNPgene突变。这些突变大多数会改变嘌呤核苷磷酸化酶中的单个蛋白质结构单元（氨基酸）。突变降低或消除嘌呤核苷磷酸化酶的活性。由此产生的过量废物分子和涉及它们的进一步反应导致称为脱氧鸟苷三磷酸（dGTP）的物质积累到对淋巴细胞有毒的水平。（换行）胸腺中的未成熟淋巴细胞特别容易受到dGTP毒性累积的影响，这会损害它们并引发其自我破坏（细胞凋亡）。其他淋巴组织中淋巴细胞的数量也大大减少，导致免疫缺陷和易受嘌呤核苷磷酸化酶缺陷所特有的严重感染。"}]}' WHERE id = 8000;
UPDATE genes SET GHR_metadata_ch = '{"gene": "TRPM1", "conditions": [{"name": "常染色体隐性先天性静止性夜盲", "detail": "已发现TRPM1gene中的35个以上突变会导致常染色体隐性先天性固定性夜盲，其特征是无法在弱光和其他视力问题（例如近视（近视））中看到。在患有这种情况的所有人中，大约有一半发现了TRPM1gene的突变。(换行)大多数TRPM1gene突变会改变TRPM1通道中的单个蛋白质结构单元（氨基酸），并改变通道的结构或阻止通道到达双极细胞膜。结果，TRPM1通道无功能，可防止双极细胞中继视觉信号。大脑没有接收到杆发送的视觉信息，导致难以在弱光下看到。"}]}' WHERE id = 8003;
UPDATE genes SET GHR_metadata_ch = '{"gene": "PDCD10", "conditions": [{"name": "脑海绵状血管畸形", "detail": "在脑海绵状畸形的家庭中，已经鉴定出PDCD10gene的十几个突变，这些家族是大脑中较弱且易于泄漏的血管集合。这些突变包括整个gene的缺失，小片段DNA的缺失以及单个DNA构建模块（核苷酸）的变化。这些突变导致PDCD10蛋白异常或缺失。目前尚不清楚PDCD10gene的突变如何导致脑海绵状畸形的形成。（换行）PDCD10gene突变约占家族性脑海绵状畸形病例的10%。"}]}' WHERE id = 8023;
UPDATE genes SET GHR_metadata_ch = '{"gene": "P3H1", "conditions": [{"name": "成骨不全症", "detail": "在患有罕见，严重形式的成骨不全症（分类为VIII型）的人中，已经鉴定出P3H1gene中至少有四个突变。这些突变阻止细胞产生任何功能性脯氨酰-3羟化酶1。没有这种酶，某些形式的胶原蛋白不会通过脯氨酸3-羟基化而被修饰。改变后的胶原蛋白分子折叠不正确，某些异常胶原蛋白从细胞中分泌的速度比平时慢。这些胶原蛋白缺陷会削弱结缔组织，导致生长极其缓慢，骨骼变薄而脆弱，可能在出生前骨折。"}]}' WHERE id = 8029;
UPDATE genes SET GHR_metadata_ch = '{"gene": "OSMR", "conditions": [{"name": "原发性局部皮肤淀粉样变性", "detail": "已发现OSMRgene中至少有13个突变会引起1型原发性局部皮肤淀粉样变性（PLCA），这是一种发痒的皮肤病，其中在皮肤中积聚了称为淀粉样蛋白的异常蛋白质团。这些突变改变了OSMR中的单个蛋白质构建块（氨基酸）？。大多数会改变被认为与II型OSM受体的其他亚基或IL-31受体相互作用的蛋白质区域，并可能损害这些受体的形成。（换行）OSMRgene突变减少OSM和IL-31触发的化学信号。研究人员推测，这种减少的信号传导可能使细胞更有可能发生凋亡。一些研究表明，皮肤细胞的凋亡释放出形成淀粉样蛋白的异常蛋白质。有人提出，细胞凋亡是由刮擦发痒的皮肤引起的，但OSMRgene变化在皮肤瘙痒中的作用尚不清楚。"}]}' WHERE id = 8049;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SLC46A1", "conditions": [{"name": "遗传性叶酸吸收不良", "detail": "在遗传性叶酸吸收不良的人群中，已经鉴定出SLC46A1gene的10多个突变。这些突变导致PCFT蛋白中一个蛋白质结构单元（氨基酸）替换为另一个氨基酸，或导致PCFT蛋白比正常情况短。突变的PCFT蛋白几乎没有活性。在某些情况下，异常蛋白质不会转运到细胞膜，因此无法执行其功能。PCFT不活动会损害身体从食物中吸收叶酸的能力，从而导致遗传性叶酸吸收不良的体征和症状。"}]}' WHERE id = 8060;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SLC19A3", "conditions": [{"name": "生物素-硫胺素反应性基底神经节疾病", "detail": "在患有生物素-硫胺素反应性基底神经节疾病的人中，已经鉴定出SLC19A3gene中至少有七个突变，该疾病涉及脑功能障碍（脑病）的反复发作以及各种逐渐恶化的神经系统问题。SLC19A3gene突变可能导致蛋白质将硫胺素转运到细胞中的能力受损，从而导致维生素吸收减少并导致神经功能障碍。使用医学成像，可以在大脑的多个部位看到异常，包括一组称为基底神经节的结构，这些结构有助于控制运动，但是这些特定的大脑异常与硫胺素转运蛋白异常之间的关系尚不清楚。"}, {"name": "利综合征", "detail": "遗传学家庭参考提供有关Leigh综合征的信息。"}, {"name": "其他疾病", "detail": "SLC19A3gene突变也已在患有其他神经系统疾病的个体中发现，其体征和症状与生物素-硫胺素反应性基底神经节疾病的体征和症状重叠（如上所述）。这些疾病包括一种称为早期婴儿致死性脑病的疾病，以及另一种从婴儿早期开始并引起癫痫发作和脑部恶化（萎缩）的疾病。还发现少数具有与神经系统疾病Leigh综合征和Wernicke脑病相似的体征和症状的个体具有SLC19A3gene突变。目前尚不清楚为什么该gene的突变会在不同的个体中引起不同的体征和症状。"}]}' WHERE id = 8090;
UPDATE genes SET GHR_metadata_ch = '{"gene": "MAGT1", "conditions": [{"name": "X连锁免疫缺陷伴镁缺乏症，爱泼斯坦-巴尔病毒感染和瘤形成", "detail": "已经鉴定出至少五个MAGT1gene突变，这些突变导致与镁缺陷，爱泼斯坦-巴尔病毒感染和瘤形成的X连锁免疫缺陷（通常由缩写XMEN已知）。XMEN是一种影响男性免疫系统的疾病。它涉及慢性EBV感染和称为淋巴瘤的免疫系统细胞癌症的风险增加。conditionsname中的“瘤形成”一词是指这些淋巴瘤；瘤形成是指组织异常生长的总称。(换行)MAGT1gene突变损害镁转运蛋白的功能，减少进入T细胞的镁量。这种镁缺乏会阻止T细胞有效激活以靶向EBV和其他感染。不受控制的EBV感染增加了发生淋巴瘤的可能性。镁转运异常导致CD4+T细胞生成受损可能是XMEN患者缺乏此类T细胞的原因，导致预防感染和疾病的能力下降。"}]}' WHERE id = 8115;
UPDATE genes SET GHR_metadata_ch = '{"gene": "MUTYH", "conditions": [{"name": "家族性腺瘤性息肉病", "detail": "MUTYHgene中的突变会导致家族性腺瘤性息肉病（也称为MYH相关性息肉病）的常染色体隐性形式。该gene的突变影响细胞纠正DNA复制过程中出错的能力。在具有常染色体隐性家族性腺瘤性息肉病的个体中，每个细胞中MUTYHgene的两个拷贝都发生了突变。该gene中的大多数突变导致产生无功能或低功能的MYH糖基化酶。当细胞中的碱基切除修复受损时，其他gene的突变会累积，导致细胞过度生长并可能形成肿瘤。改变MYH糖基化酶中蛋白质（氨基酸）结构单元序列的两个突变在欧洲人后裔中很常见。一个突变用位置165处的氨基酸半胱氨酸取代了氨基酸酪氨酸（写为Tyr165Cys或Y165C）。另一个突变将氨基酸甘氨酸与382位的氨基酸天冬氨酸切换（写为Gly382Asp或G382D）。"}]}' WHERE id = 8119;
UPDATE genes SET GHR_metadata_ch = '{"gene": "CAPN3", "conditions": [{"name": "肢带型肌营养不良症", "detail": "已经在患有2A型肢带型肌营养不良症（也称为钙蛋白酶病）的人中发现了CAPN3gene的突变，这种疾病的特征是肌肉无力和肩膀，臀部和四肢消瘦。这是由于较短或不稳定的calpain-3酶破坏了其切割蛋白质以从肌小节中去除的能力。"}]}' WHERE id = 8125;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SYNE1", "conditions": [{"name": "常染色体隐性小脑共济失调1型", "detail": "已发现SYNE1gene中至少有七个突变会导致常染色体隐性小脑共济失调1型（ARCA1）。所有已识别的突变都会在制备Syne-1蛋白的说明中产生过早的终止信号，从而导致功能异常短的蛋白受损。功能失调的Syne-1蛋白被认为会损害浦肯野细胞功能并破坏小脑神经元之间的信号传导。小脑中脑细胞的丢失会导致ARCA1特有的运动问题，但尚不清楚这种细胞丢失与浦肯野细胞功能受损如何相关。"}, {"name": "艾梅氏肌失养症", "detail": "遗传学家庭参考资料提供有关Emery-Dreifuss肌营养不良症的信息。"}]}' WHERE id = 8129;
UPDATE genes SET GHR_metadata_ch = '{"gene": "HSPB8", "conditions": [{"name": "进行性神经性腓骨肌萎缩症", "detail": "据报道，在一个中国大家庭中，有十几个人患有HSPB8gene突变，这种疾病被称为2L型Charcot-Marie-Tooth病。Charcot-Marie-Tooth病是一组影响周围神经的进行性疾病。周围神经将大脑和脊髓连接到肌肉和感觉细胞，检测触觉，疼痛，热和声音等感觉。（换行）导致Charcot-Marie-Tooth病的HSPB8gene突变将蛋白质构建块（氨基酸）赖氨酸改变为蛋白质位置141处的氨基酸天冬酰胺，写为Lys141Asn或K141N。(换行)尚不清楚HSPB8gene突变如何导致2L型Charcot-Marie-Tooth病的体征和症状。研究表明，改变后的热激蛋白beta-8与热激蛋白beta-1的相互作用更强，并且更可能形成团块（聚集体）。聚集体可以阻止对神经轴突的正常功能必不可少的物质的运输。"}, {"name": "远端遗传性运动神经病，II型", "detail": "研究人员已经鉴定出至少两个HSPB8gene突变，这些突变会导致一种称为II型远端遗传性运动神经病的疾病，类似于Charcot-Marie-Tooth病。II型远端遗传性运动神经病影响周围神经，其特征是进行性无力，主要发生在脚和腿上。与Charcot-Marie-Tooth病，远端遗传性运动神经病不同，II型不影响感觉细胞。（换行）引起远端遗传性运动神经病的HSPB8gene突变之一，II型（Lys141Asn或K141N）也与2L型Charcot-Marie-Tooth病有关。另一个突变用谷氨酸（写为Lys141Glu或K141E）取代了相同位置的赖氨酸，似乎以相同的方式增加了蛋白质的聚集。尚不清楚为什么相同的HSPB8gene突变会引起任何一种疾病。研究人员认为，周围神经内蛋白质聚集体的不同分布可能导致不同的体征和症状模式。涉及该蛋白质的其他细胞功能的破坏也可能导致周围神经疾病。"}]}' WHERE id = 8133;
UPDATE genes SET GHR_metadata_ch = '{"gene": "FGFR2", "conditions": [{"name": "Apert综合征", "detail": "已发现FGFR2gene中至少有七个突变会引起Apert综合征，这种情况会导致颅骨过早闭合（颅突狭窄），导致头部畸形和独特的面部特征以及手指和脚趾异常。几乎所有Apert综合征病例都是由FGFR2gene的两个突变之一引起的。这些突变改变了FGFR2蛋白中的单个蛋白结构单元（氨基酸），从而改变了该蛋白的3维结构。一个突变用蛋白质252位的氨基酸色氨酸取代氨基酸丝氨酸（写为Ser252Trp）。另一个突变在位置253处用氨基酸精氨酸取代了氨基酸脯氨酸（写为Pro253Arg）。改变的FGFR2蛋白似乎引起更强的信号传导，从而促进颅骨，手和脚的骨骼过早融合。"}, {"name": "Beare-Stevenson cutis gyrata综合征", "detail": "已发现FGFR2gene中至少有3个突变会引起Beare-Stevenson cutis gyrata综合征，这种疾病会导致颅突狭窄，导致头部畸形和独特的面部特征，以及称为cutis gyrata的皮肤异常。最常见的突变是用蛋白质（写为Tyr375Cys）中375位的半胱氨酸替换氨基酸酪氨酸。导致Beare-Stevenson cutis gyrata综合征的FGFR2gene突变似乎过度激活了FGFR2蛋白的信号传导，从而促进了颅骨中骨的过早融合。"}, {"name": "Crouzon综合征", "detail": "FGFR2gene中至少有40个突变可引起Crouzon综合征，这是一种导致颅突狭窄的疾病，导致头部畸形和独特的面部特征。导致Crouzon综合征的大多数突变都会改变FGFR2gene中的单个DNA构建块（核苷酸）。还已知少量核苷酸的插入和缺失会引起疾病。FGFR2中的这些突变似乎过度激活了FGFR2蛋白的信号传导，从而促进了颅骨中骨的过早融合。"}, {"name": "杰克逊-韦斯综合征", "detail": "已发现FGFR2gene中至少有六个突变引起杰克逊-韦斯综合征。这种情况会导致颅骨融合症，导致头部畸形，面部特征明显和足部异常。每个突变都会改变FGFR2蛋白区域中称为IgIII结构域的单个氨基酸，这对于接收信号和与生长因子相互作用至关重要。突变似乎过度激活了FGFR2蛋白的信号传导，从而促进了颅骨的过早融合并影响了脚的骨骼发育。"}, {"name": "泪-耳-牙本质-数字综合征", "detail": "已经发现FGFR2gene中的至少两个突变引起泪-耳-牙本质-数字（LADD）综合征。这些突变降低了FGFR2受体蛋白与其生长因子结合时触发细胞内化学反应的能力。在一些患有LADD综合征的人中发生的突变用FGFR2受体蛋白（写为Ala628Thr或A628T）中628位的氨基酸苏氨酸替换氨基酸丙氨酸。(换行)LADD综合征的主要特征是异常泪液产生，听力丧失的耳朵畸形，唾液产生减少，牙齿小和手部畸形。导致LADD综合征的FGFR2gene突变会降低受体蛋白的功能，从而导致细胞信号传导减少。细胞信号传导中的这些缺陷破坏了细胞的成熟和发育，从而导致LADD综合征患者的眼睛，口腔，耳朵和骨骼中腺体的异常形成。"}, {"name": "菲佛综合症", "detail": "FGFR2gene中的25个以上突变可引起Pfeiffer综合征，该病会导致颅骨融合症，导致头部畸形和独特的面部特征以及手足异常。导致这种情况的几种突变会改变FGFR2蛋白关键区域（称为IgIII域）中半胱氨酸氨基酸的数量。其余的突变影响半胱氨酸以外的氨基酸或导致FGFR2蛋白缺失一个或多个氨基酸。这些突变似乎过度激活了FGFR2蛋白的信号传导，从而促进了颅骨的过早融合并影响了手和脚的骨骼发育。"}, {"name": "乳腺癌", "detail": "遗传学家庭参考提供有关乳腺癌的信息。"}, {"name": "胆管癌", "detail": "遗传学家庭参考提供有关胆管癌的信息。"}, {"name": "表皮痣", "detail": "遗传学家庭参考提供有关表皮痣的信息。"}, {"name": "前列腺癌", "detail": "遗传学家庭参考提供有关前列腺癌的信息。"}, {"name": "癌症", "detail": "FGFR2gene活性（表达）的改变与某些癌症有关。改变的gene表达可以增强几种癌症相关事件，例如细胞分裂（增殖），细胞运动和滋养生长中的肿瘤的新血管的发育。（换行）FGFR2gene在某些类型的胃癌中异常活跃（过度表达），并且这种扩增与不良的疾病结果相关。在前列腺癌患者中也发现了FGFR2gene的异常表达。两种特异性FGFR2同种型IIIb和IIIc的表达的变化似乎与前列腺癌进展相关。然而，这种表达变化是复杂的，并且根据前列腺肿瘤的类型而变化。更晚期的肿瘤可能显示IIIb同种型增加，而其他前列腺肿瘤显示IIIb减少但IIIc增加。FGFR2gene表达的改变也与卵巢癌，乳腺癌，宫颈癌，胰腺癌和头颈癌有关。"}]}' WHERE id = 8162;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ECM1", "conditions": [{"name": "类脂蛋白沉着症", "detail": "已发现ECM1gene中至少有55个突变会引起类脂蛋白沉着症，这种情况是由遍布全身各种组织的大量小团块（沉积物）蛋白质和其他分子引起的。受影响的人通常声音嘶哑，皮肤异常以及神经和呼吸系统问题。（换行）通常，引起类脂蛋白沉着的突变发生在称为外显子6和外显子7的ECM1gene区域。在世界各地的多个个体中发现了一种从ECM1gene第6外显子（写为507delT）中删除单个DNA构建块（核苷酸）的突变。该gene第7外显子中发生的另一种突变在南非的受影响个体中很常见，并导致制备该蛋白的说明（写为Gln276Ter或Q276X）中出现过早的终止信号。导致类脂蛋白沉着的ECM1gene突变导致产生无功能的蛋白质或根本不产生蛋白质。（换行）缺乏功能性ECM1蛋白减少ECM1与其他蛋白之间的结合，导致不稳定的细胞外基质。如果没有细胞外基质的充分支持，皮肤和其他组织中的细胞就会减弱。但是，尚不清楚皮肤和其他组织中沉积的原因。不稳定的细胞外基质可能导致邻近细胞过量产生蛋白质和其他物质。当这些过量物质积聚在组织中时，它们可能产生脂质蛋白沉着的特征性沉积物。"}]}' WHERE id = 8163;
UPDATE genes SET GHR_metadata_ch = '{"gene": "TMEM127", "conditions": [{"name": "非综合征性副神经节瘤", "detail": "TMEM127gene的突变会增加发生与神经系统相关的非癌性肿瘤（称为副神经节瘤或嗜铬细胞瘤（一种副神经节瘤））的风险。TMEM127gene突变最常见于嗜铬细胞瘤患者，而在其他副神经节瘤患者中很少见。具体而言，TMEM127gene突变与非综合征性副神经节瘤或嗜铬细胞瘤相关，这意味着肿瘤的发生没有遗传综合征的其他特征。在患有这些肿瘤之一的人中，至少鉴定出19个TMEM127gene突变。TMEM127gene突变会增加肿瘤形成的风险。与副神经节瘤或嗜铬细胞瘤相关的TMEM127gene突变会改变TMEM127蛋白序列中的单个蛋白结构单元（氨基酸）或导致蛋白缩短。(换行)大多数患有TMEM127相关性副神经节瘤或嗜铬细胞瘤的人会获得额外的突变，从而删除该gene的正常拷贝。第二种突变称为体细胞突变，是在一个人的一生中获得的，仅存在于肿瘤细胞中。这两个突变共同导致TMEM127蛋白减少或缺失。结果，由TMEM127蛋白控制的细胞生长途径异常活跃，导致肿瘤形成。"}]}' WHERE id = 8167;
UPDATE genes SET GHR_metadata_ch = '{"gene": "DNAH5", "conditions": [{"name": "原发性睫状运动障碍", "detail": "已发现DNAH5gene中的80多个突变会引起原发性睫状运动障碍，这是一种以呼吸道感染，器官放置异常和无法生育（不育）为特征的疾病。DNAH5gene突变导致重链缺失或异常5。如果没有此子单元的正常版本，ODA将无法正确形成，并且可能会缩短或不存在。结果，纤毛不能产生来回弯曲所需的力。纤毛缺陷导致原发性睫状运动障碍的特征。"}, {"name": "异位症候群", "detail": "遗传学家庭参考资料提供有关异型症候群的信息。"}]}' WHERE id = 8212;
UPDATE genes SET GHR_metadata_ch = '{"gene": "NF2", "conditions": [{"name": "2型神经纤维瘤病", "detail": "在2型神经纤维瘤病患者中已鉴定出NF2gene的400多个突变，该疾病的特征是神经系统中非癌性（良性）肿瘤的生长。与这种情况相关的最常见的肿瘤是双侧前庭神经鞘瘤，其沿着从内耳到大脑（听觉神经）携带信息的神经发展。这些肿瘤来自施万细胞。2型神经纤维瘤病患者也可能发展其他神经系统肿瘤，包括脑膜瘤和室管膜瘤。（换行）导致2型神经纤维瘤病的NF2gene突变被归类为种系，这意味着它们存在于身体的所有细胞中。大多数NF2gene突变会导致merlin蛋白异常缩短。这种短蛋白不能在细胞中发挥其正常的肿瘤抑制功能。研究表明，merlin功能的丧失使神经系统中的某些细胞，尤其是雪旺氏细胞，能够频繁繁殖并形成肿瘤。"}, {"name": "神经鞘瘤病", "detail": "NF2gene中的非遗传（体细胞）突变与称为神经鞘瘤病的疾病有关，该疾病类似于2型神经纤维瘤病（如上所述）。神经鞘瘤病的特征是在全身神经上生长的多个神经鞘瘤的发展。患有神经鞘瘤病的人通常不会发展为双侧前庭神经鞘瘤或2型神经纤维瘤病可能发生的其他肿瘤。（换行）与引起2型神经纤维瘤病的种系NF2gene突变不同，与神经鞘瘤病相关的体细胞突变仅发生在引起肿瘤的细胞中。似乎这些体细胞NF2gene突变不会引起神经鞘瘤病，尽管它们很可能是导致肿瘤形成的遗传因素之一。"}, {"name": "肿瘤", "detail": "NF2gene中的体细胞突变参与了良性和癌性几种其他类型肿瘤的发展。这些突变触发细胞在没有控制或命令的情况下生长和分裂，导致肿瘤的形成。(换行)NF2gene的丢失或失活通常与单个（分离的）神经系统肿瘤的发展有关，包括脑膜瘤，室管膜瘤和神经鞘瘤。虽然这些肿瘤是2型神经纤维瘤病和神经鞘瘤病（如上所述）的一部分，但是在没有这些疾病的人中可能发生孤立的肿瘤。研究人员已经确定，在许多间皮瘤病例中，NF2gene的丢失或失活也会发生，间皮瘤是一种癌性肿瘤，可能出现在肺和胸腔（胸膜）或腹部（腹膜）的内层。（换行）体细胞NF2gene突变在大多数其他形式的癌症中似乎并不常见。在某些情况下，NF2gene突变与肿瘤生长或扩散的速度（疾病进展）或肿瘤对治疗有反应的可能性有关。"}]}' WHERE id = 8214;
UPDATE genes SET GHR_metadata_ch = '{"gene": "KANSL1", "conditions": [{"name": "Koolen de Vries综合征", "detail": "KANSL1gene突变或包括该gene在内的遗传物质缺失会导致Koolen-de Vries综合征。这种疾病的特征是发育迟缓，智力残疾，开朗和善于交际的性格以及各种身体异常。(换行)每个细胞中丢失一个拷贝的KANSL1gene会损害身体各个器官和组织的正常发育和功能，但尚不清楚KANSL1gene丢失与Koolen-de Vries综合征的特定体征和症状之间的关系。"}]}' WHERE id = 8217;
UPDATE genes SET GHR_metadata_ch = '{"gene": "DNAJC5", "conditions": [{"name": "CLN4疾病", "detail": "已发现DNAJC5gene中至少有两个突变引起CLN4疾病。CLN4疾病是一种遗传性疾病，主要影响神经系统。这种情况通常始于成年期，运动和智力功能问题会随着时间的推移而恶化。（换行）DNAJC5gene突变之一用CSP中第115位的氨基酸精氨酸取代蛋白质结构单元（氨基酸）亮氨酸？蛋白质（写为L115R）。另一个突变删除了蛋白质中第116位的氨基酸亮氨酸（写为L116del）。受影响的个体在每个细胞的DNAJC5gene的一个拷贝中具有这些突变之一，这导致产生无法与突触小泡膜结合的改变的蛋白质。由此导致的蛋白质再循环减少导致功能性蛋白质的短缺（缺乏），这损害了神经冲动传递的效率。异常CSP？蛋白质也可能与功能性CSP结合？由DNAJC5gene的正常拷贝产生并阻止其与突触小泡缔合的蛋白质，进一步削弱了脉冲传递。如果神经细胞之间没有交流，神经功能就会受损，从而导致CLN4疾病的特征。(换行)CLN4疾病的特征是溶酶体中蛋白质和其他物质的积累，溶酶体是细胞中消化和回收物质的区室。这些积聚发生在全身的细胞中；然而，神经细胞似乎特别容易受到其影响。积累会导致细胞损伤，导致细胞死亡。大脑和其他组织中神经细胞的进行性死亡导致CLN4疾病的神经功能下降。但是，尚不清楚DNAJC5gene中的突变如何参与溶酶体中物质的积累。"}]}' WHERE id = 8239;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ITGB4", "conditions": [{"name": "大疱性表皮松解症伴幽门闭锁", "detail": "已发现ITGB4gene中至少有60个突变会导致大疱性表皮松解并伴有幽门闭锁（EB-PA）。除皮肤起泡外，EB-PA患者天生还具有威胁生命的消化道阻塞，称为幽门闭锁。ITGB4gene突变约占所有EB-PA病例的80%。（换行）ITGB4gene突变改变了正常的结构和功能？4整合素亚基或阻止细胞产生足够的该亚基。结果是？6?4整合素有缺陷或缺失。导致完全或接近完全丧失的突变？6?4整合素倾向于引起更严重的EB-PA体征和症状，而改变该蛋白结构或稳定性的突变通常会引起更轻微的体征和症状。功能短缺？6?4整合素导致表皮细胞脆弱，容易受损。摩擦或其他轻微创伤可导致皮肤层分离，导致水泡广泛形成。尚不清楚ITGB4gene的突变如何与幽门闭锁相关。"}, {"name": "癌症", "detail": "研究人员相信？6?4整合素可能在称为癌的癌性肿瘤的进展中起关键作用。这些癌症出现在上皮细胞中，并可能影响许多组织和器官，包括乳腺癌，肺癌，肝癌，结肠癌和皮肤癌。（折线）的位置和活动的变化？6?4癌细胞内的整合素与癌症的进展有关。整合素蛋白激活关键信号分子，触发癌细胞通过身体迁移并侵入其他组织。这些信号还使癌细胞更能抵抗自我破坏（细胞凋亡）。(换行)最近的研究表明，除了其在现有癌症进展中的作用外？6?4整合素可能参与这些肿瘤的初始形成。"}]}' WHERE id = 8328;
UPDATE genes SET GHR_metadata_ch = '{"gene": "IGF2", "conditions": [{"name": "Beckwith-Wiedemann综合征", "detail": "Beckwith-Wiedemann综合征是一种以过度生长和其他影响身体许多部位的体征和症状为特征的疾病，可能由影响IC1区域的变化引起。在一些患有这种情况的人中，IC1区域的母系遗传拷贝和父系遗传拷贝都附着有甲基（超甲基化）。因为IC1区域控制IGF2和H19gene的gene组印迹，所以这种异常破坏了两个gene的调控。具体而言，IC1区域的高甲基化导致许多组织中IGF2gene的活性增加和H19gene活性的丧失。促进生长的IGF2gene活性的增加和通常抑制生长的H19gene活性的丧失一起导致Beckwith-Wiedemann综合征患者的过度生长。（换行）在少数情况下，Beckwith-Wiedemann综合征是由IC1区域少量DNA缺失引起的。像异常甲基化一样，这些缺失会改变IGF2和H19gene的活性。"}, {"name": "前列腺癌", "detail": "遗传学家庭参考提供有关前列腺癌的信息。"}, {"name": "罗素-银综合症", "detail": "IC1区域甲基化的变化也是Russell-Silver综合征的一些病例的原因，Russell-Silver综合征是一种以出生前后生长缓慢为特征的疾病。这些变化与Beckwith-Wiedemann综合征不同，对生长有相反的影响。（换行）在Russell-Silver综合征中，IC1区域的父系遗传拷贝通常具有太少的甲基连接（低甲基化）。IC1区域的低甲基化导致许多组织中IGF2gene活性的丧失和H19gene活性的增加。通常促进生长的IGF2gene活性的丧失和抑制生长的H19gene活性的增加一起导致罗素-银综合征患者的生长不良和身材矮小。"}, {"name": "癌症", "detail": "IGF2gene活性的增加与许多类型的癌症有关。通常，IGF2gene会进行gene组印迹，只有从一个人父亲那里继承的副本才具有活性。然而，在某些癌症中，该gene的父本遗传和母本遗传拷贝均具有活性，从而增加了细胞可以产生的胰岛素样生长因子2的量。这种现象称为印迹损失（LOI）。增加量的胰岛素样生长因子2可以刺激肿瘤细胞的生长并防止受损细胞被破坏。（换行）IGF2gene印迹的丧失已经在称为胚胎性肿瘤的几种类型的癌症中被鉴定。这些肿瘤包括一种称为肾母细胞瘤的肾癌，一种称为横纹肌肉瘤的肌肉组织癌，以及一种称为肝母细胞瘤的肝癌。在许多其他类型的癌症中也发现了IGF2gene印迹的丧失，包括造血细胞癌（白血病）和乳腺癌，前列腺癌，肺癌，结肠癌和肝癌。在某些类型的癌症中，胰岛素样生长因子2水平升高与肿瘤进展和预后不良有关。"}]}' WHERE id = 8337;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ETHE1", "conditions": [{"name": "乙基丙二酸脑病变", "detail": "在乙基丙二酸脑病患者中已鉴定出ETHE1gene的30多个突变。这种罕见的情况会影响身体的许多部位，包括神经系统，血管和肠道。体征和症状包括发育迟缓，运动异常，皮肤下微小红斑皮疹（瘀斑），手脚蓝色变色（肢端紫cyan病）和慢性腹泻。(换行)大多数引起乙基丙二酸脑病的突变会导致ETHE1酶的非功能性产生或阻止细胞产生任何这种酶。功能酶的缺乏会阻止硫化物正常分解，从而使该分子积聚在细胞中。硫化物的积累抑制了COX的活性，COX破坏了线粒体能量的产生并损害了整个身体的组织和器官。研究人员认为，过量硫化物对大脑，肌肉，血管和肠道内壁的影响是乙基丙二酸脑病大多数主要特征的基础。"}, {"name": "利综合征", "detail": "遗传学家庭参考提供有关Leigh综合征的信息。"}]}' WHERE id = 8393;
UPDATE genes SET GHR_metadata_ch = '{"gene": "COL3A1", "conditions": [{"name": "埃勒斯-当洛二氏综合症", "detail": "已经发现COL2A1gene的突变会引起一种称为血管型的Ehlers-Danlos综合征，这种疾病会导致危及生命的并发症，包括血管，肠和其他器官的撕裂（破裂）。导致这种疾病形式的突变会改变III型胶原蛋白原分子的结构和产生，从而导致III型胶原蛋白组装的破坏。"}]}' WHERE id = 8458;
UPDATE genes SET GHR_metadata_ch = '{"gene": "HLCS", "conditions": [{"name": "全羧化酶合成酶缺乏症", "detail": "在全羧化酶合成酶缺乏症患者中已鉴定出HLCSgene中约30个突变。这些突变大多数会改变全羧化酶合成酶中的单个蛋白质结构单元（氨基酸）。许多已知的突变发生在与生物素结合的酶区域。这些遗传变化降低了酶将生物素附着于羧化酶和组蛋白的能力。没有生物素，羧化酶保持无活性，无法有效处理蛋白质，脂肪和碳水化合物。缺乏全羧化酶合成酶活性也可能改变某些对正常发育重要的gene的调控。研究人员认为，这些酶功能缺陷是呼吸问题，皮疹以及全羧化酶合成酶缺乏症的其他特征性体征和症状的基础。"}]}' WHERE id = 8498;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ATP7A", "conditions": [{"name": "皮肤松弛", "detail": "ATP7Agene的突变导致称为枕角综合征或X连锁角质松弛的病症，其被认为是Menkes综合征的轻度形式，其特征在于皮肤松弛和下垂，楔形钙沉积在骨骼底部的骨骼中。头骨（枕骨），粗糙的头发和松散的关节。这是由于蛋白质产量减少所致，这会损害食物中铜的吸收并阻止其正常分布到整个身体的细胞中。"}, {"name": "Menkes综合征", "detail": "ATP7Agene的突变与Menkes综合征有关，Menkes综合征的特征是头发稀疏，弯曲，failure壮成长，神经系统恶化，肌张力低下，面部特征下垂，癫痫发作，发育迟缓和智力障碍。这是由于ATP7A蛋白缩短而引起的，ATP7A蛋白缩短了食物中铜的吸收并阻止了铜在整个身体中正常分布到细胞中。"}]}' WHERE id = 8515;
UPDATE genes SET GHR_metadata_ch = '{"gene": "KCTD1", "conditions": [{"name": "头皮耳乳头综合征", "detail": "在头皮-耳-乳头综合征患者中，至少鉴定出KCTD1gene的10个突变；顾名思义，这种情况的特征是头皮，耳朵和乳头异常。与头皮-耳-乳头综合征相关的突变影响KCTD1蛋白的BTB结构域并损害其转录阻遏物功能。这种损伤导致参与外胚层发育的gene的异常调节。改变的gene活性破坏了由外胚层（外胚层发育不良）引起的组织的正常发育，并导致头皮-耳-乳头综合征的体征和症状。"}]}' WHERE id = 8523;
UPDATE genes SET GHR_metadata_ch = '{"gene": "DUX4", "conditions": [{"name": "面肩肱型肌营养不良症", "detail": "包含DUX4gene的4号染色体D4Z4区的变化会导致面肩肱型肌营养不良。这种疾病的特征是肌肉无力和消瘦（萎缩），随着时间的推移缓慢恶化。已经描述了两种类型的疾病：1型（FSHD1）和2型（FSHD2）。两种类型均由D4Z4区的低甲基化引起，其中DNA的甲基连接少于正常。在FSHD1中，发生低甲基化是因为D4Z4区域异常缩短（收缩），包含1至10个重复序列，而不是通常的11至100个重复序列。在FSHD2中，低甲基化通常是由称为SMCHD1的gene突变引起的，该gene通常会使D4Z4区甲基化。（换行）D4Z4区域的低甲基化可防止DUX4gene在通常关闭的细胞和组织（例如成年肌肉细胞）中沉默。但是，D4Z4区域的低甲基化仅在与“允许的”4号染色体一起发生时才导致面肩肱型肌营养不良。“允许”染色体包含一个工作的pLAM序列，该序列允许从异常活跃的DUX4gene产生蛋白质。研究人员认为，这种蛋白质会影响其他gene的活性，特别是在肌肉细胞中。但是，尚不清楚DUX4蛋白的存在如何破坏或破坏这些细胞，从而导致进行性肌肉无力和萎缩。"}]}' WHERE id = 8536;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ADAMTSL4", "conditions": [{"name": "孤立的ectopia lentis", "detail": "已发现ADAMTSL4gene中至少有15个突变会导致孤立的轻度外翻。在这种情况下，一只或两只眼睛的晶状体偏心（移位），这会导致视力问题。在欧洲血统的受影响个体中经常发现的ADAMTSL4gene突变从该gene中删除了20个DNA构建块（核苷酸）（写为767_787del20）。这种突变导致产生异常短且无功能的蛋白质。缺乏功能性ADAMTSL4蛋白可能会降低原纤维蛋白1有效形成微原纤维的能力。结果，减少了细丝以将晶状体锚定在眼睛前部的中心位置，从而导致晶状体移位和孤立的角膜外翻的视觉问题。虽然ADAMTSL4蛋白遍布全身，但据认为其他蛋白可以补偿其在眼睛以外的组织中的功能，这很可能解释了为什么在这种情况下只有眼睛受到影响。"}, {"name": "其他疾病", "detail": "一些ADAMTSL4gene突变会导致一种称为ectopia lentis et pupillae的眼部疾病。在这种情况下，镜片和瞳孔都偏离中心；它们通常以相反的方向移位。患有lentopia lentis和pupillae的人的眼睛和视力问题类似于患有孤立性lentopia lentis（如上所述）的人，包括近视（近视），远视（远视）或眼前曲率不规则（散光）。他们还可能在很小的时候出现晶状体混浊（白内障）或眼睛压力增加（青光眼）。与孤立的lentopia lentis相似，导致lentopia lentis和pupillae的ADAMTSL4gene突变导致微纤维的产生减少或微纤维受损的形成，从而阻止了某些结构正确锚定在眼睛中。目前尚不清楚为什么某些ADAMTSL4gene突变仅影响晶状体，而另一些也影响瞳孔。"}]}' WHERE id = 8560;
UPDATE genes SET GHR_metadata_ch = '{"gene": "INS", "conditions": [{"name": "永久性新生儿糖尿病", "detail": "在永久性新生儿糖尿病患者中，INSgene至少鉴定出10个突变。患有这种疾病的人通常出生体重低，并在生命的头6个月内出现血糖升高（高血糖症）。（换行）导致永久性新生儿糖尿病的INSgene突变改变蛋白质序列中的单个蛋白质构建块（氨基酸）。据信这些突变破坏了胰岛素原链的切割或A和B链的结合以形成胰岛素，从而导致血糖控制受损。"}, {"name": "1型糖尿病", "detail": "遗传学家庭参考提供有关1型糖尿病的信息。"}, {"name": "其他疾病", "detail": "INSgene的突变也可引起其他涉及胰岛素产生和血糖控制的疾病。一些具有INSgene突变的个体的血液中胰岛素原水平升高（高胰岛素原血症），并且血糖控制也可能受损。INSgene突变还与一种称为年轻成熟型糖尿病（MODY）的疾病有关。该术语是指由单个gene的变化引起的相对轻度糖尿病的遗传形式。"}]}' WHERE id = 8564;
UPDATE genes SET GHR_metadata_ch = '{"gene": "CAV3", "conditions": [{"name": "CAV3相关的远端肌病", "detail": "已经在患有远端肌病的人中鉴定出CAV3gene的突变，该疾病导致手脚远端肌肉功能丧失。它的特征是肌肉无力，肌肉消瘦并导致重复性紧张（per击引起的快速收缩），小腿肌肉肥大。该疾病的发生是由于肌肉细胞膜中小窝蛋白3蛋白的缺乏，这破坏了结构完整性和引起细胞凋亡的细胞信号传导。"}, {"name": "孤立性高CK血症", "detail": "在患有孤立性高CK血症的个体中，几乎没有发现CAV3gene突变，这种疾病的特征是由于缺乏caveolin-3蛋白而导致血液中肌酸激酶水平升高，而caveolin-3蛋白可能会损害肌肉细胞而没有任何明显的症状。"}, {"name": "肢带型肌营养不良症", "detail": "在患有1C型肢带型肌营养不良症的个体中，几乎没有发现CAV3gene突变，这种疾病的特征是由于缺乏caveolin-3蛋白导致肌肉组织损伤而导致肌肉无力以及肩膀，臀部和四肢消瘦。"}, {"name": "涟漪肌病", "detail": "在患有波纹状肌肉疾病的人中，几乎没有发现CAV3gene突变，在这种情况下，肌肉对运动或压力异常敏感（易怒），并以肌肉痉挛，僵硬和肌肉伸展为特征。这是由于缺乏caveolin-3蛋白而引起的，该蛋白会损害肌肉细胞中的钙水平，从而导致异常的肌肉收缩。"}, {"name": "罗曼诺-沃德综合症", "detail": "CAV3gene中很少有突变与一种称为Romano-Ward综合征的长QT综合征有关，这种疾病会破坏心脏的正常节律（心律不齐），导致某些受影响的人昏厥（晕厥）或心脏骤停并猝死。"}, {"name": "其他疾病", "detail": "CAV3gene中很少有突变与肥厚型心肌病有关，肥厚型心肌病是一种导致心肌增厚的疾病，会损害正常的血液抽取并可能导致心力衰竭。"}]}' WHERE id = 8570;
UPDATE genes SET GHR_metadata_ch = '{"gene": "DUX4", "conditions": [{"name": "面肩肱型肌营养不良症", "detail": "包含DUX4gene的4号染色体D4Z4区的变化会导致面肩肱型肌营养不良。这种疾病的特征是肌肉无力和消瘦（萎缩），随着时间的推移缓慢恶化。已经描述了两种类型的疾病：1型（FSHD1）和2型（FSHD2）。两种类型均由D4Z4区的低甲基化引起，其中DNA的甲基连接少于正常。在FSHD1中，发生低甲基化是因为D4Z4区域异常缩短（收缩），包含1至10个重复序列，而不是通常的11至100个重复序列。在FSHD2中，低甲基化通常是由称为SMCHD1的gene突变引起的，该gene通常会使D4Z4区甲基化。（换行）D4Z4区域的低甲基化可防止DUX4gene在通常关闭的细胞和组织（例如成年肌肉细胞）中沉默。但是，D4Z4区域的低甲基化仅在与“允许的”4号染色体一起发生时才导致面肩肱型肌营养不良。“允许”染色体包含一个工作的pLAM序列，该序列允许从异常活跃的DUX4gene产生蛋白质。研究人员认为，这种蛋白质会影响其他gene的活性，特别是在肌肉细胞中。但是，尚不清楚DUX4蛋白的存在如何破坏或破坏这些细胞，从而导致进行性肌肉无力和萎缩。"}]}' WHERE id = 8615;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SURF1", "conditions": [{"name": "细胞色素c氧化酶缺乏症", "detail": "遗传学家庭参考资料提供有关细胞色素c氧化酶缺乏症的信息。"}, {"name": "利综合征", "detail": "在患有Leigh综合征的人中已鉴定出80多种不同的SURF1gene突变，Leigh综合征是一种进行性脑部疾病，通常出现在婴儿期或儿童早期。受影响的儿童可能会出现发育迟缓，肌肉无力，运动问题或呼吸困难。(换行)大约10%至15%的Leigh综合征患者的SURF1gene发生突变。大多数SURF1gene突变会导致蛋白质异常短。其他突变取代了SURF1蛋白中的单个蛋白构建基块。突变的蛋白质在细胞中分解，导致不存在SURF1蛋白质。SURF1蛋白的缺乏会阻碍COX复合物的正确形成。结果，COX酶活性严重降低，这导致氧化磷酸化受损。(换行)尽管确切的机制尚不清楚，但研究人员认为，由于细胞中可用能量的减少，氧化磷酸化受损可导致细胞死亡。某些需要大量能量的组织，如大脑，肌肉和心脏，似乎对细胞能量的减少特别敏感。大脑中的细胞死亡可能会导致在Leigh综合征中看到的大脑特征性变化，从而导致疾病的体征和症状。其他敏感组织中的细胞死亡也可能有助于Leigh综合征的特征。"}]}' WHERE id = 8646;
UPDATE genes SET GHR_metadata_ch = '{"gene": "MPLKIP", "conditions": [{"name": "毛滴虫营养不良", "detail": "在患有毛发硫代营养不良症的人中，已经鉴定出MPLKIPgene中至少有八个突变。这些突变会导致某些非光敏性疾病，这与对日光的紫外线（UV）的极端敏感性无关。(换行)所有已知的MPLKIPgene突变都会阻止任何功能性MPLKIP蛋白的产生。尚不清楚这种蛋白质的损失如何导致毛发硫代营养不良的特征，包括生长缓慢，智力残疾和头发脆弱。"}]}' WHERE id = 8653;
UPDATE genes SET GHR_metadata_ch = '{"gene": "GALE", "conditions": [{"name": "半乳糖血症", "detail": "在患有半乳糖血症（称为III型或半乳糖差向异构酶缺乏症）的人中，已经鉴定出GALEgene的20多个突变。这种情况的体征和症状在出生后不久开始，可以从轻度到重度变化。大多数遗传变化会改变UDP-半乳糖-4-表异构酶中的单个蛋白质结构单元（氨基酸），从而使该酶不稳定或损害其正常功能。(换行)一些GALEgene突变严重降低或消除了人体所有组织中UDP-半乳糖-4-表异构酶的活性。这些遗传变化导致严重形式的半乳糖血症III型被描述为广义形式。酶活性的丧失阻止细胞加工从饮食中获得的半乳糖。结果，与半乳糖加工相关的化合物可以在体内积聚至毒性水平。这些物质的积累会损害组织和器官，导致严重的并发症，例如眼睛晶状体混浊（白内障），智力残疾以及对肝脏，肾脏和大脑的损害。（换行）GALEgene中的其他突变仅降低红细胞中UDP-半乳糖-4-差向异构酶的活性。这些遗传变化是描述为外周形式的更温和的III型半乳糖血症的基础，受影响的个体可能没有任何通常与半乳糖血症相关的并发症，通常不需要治疗。研究人员尚不清楚为什么某些GALE突变的影响仅限于血细胞，而其他突变会影响人体的所有组织并导致严重的健康问题。"}]}' WHERE id = 8658;
UPDATE genes SET GHR_metadata_ch = '{"gene": "STIM1", "conditions": [{"name": "Stormorken综合征", "detail": "STIM1gene的突变会导致Stormorken综合征，这是一种罕见的疾病，其特征是称为血小板的血细胞碎片数量少（血小板减少症）和随之而来的出血问题，称为肾小管聚集性肌病的肌肉疾病（如下所述）以及其他异常。这种情况下涉及的突变改变了STIM1蛋白中的单个蛋白结构单元（氨基酸），用蛋白304位的氨基酸色氨酸取代了氨基酸精氨酸（写为Arg304Trp或R304W）。当钙离子水平高时，这种变化发生在被认为与保持其关闭有关的蛋白质区域中。由于这种变化，改变后的STIM1蛋白不断开启（持续激活），不断刺激钙离子通过CRAC通道进入，而不管ER中的离子水平如何。由于gene改变增强了蛋白的活性，被描述为一种“功能增强”突变。研究人员认为，血小板中异常的离子流导致细胞碎片比平时更早分解，导致Stormorken综合征患者出现血小板减少和出血问题。尚不清楚组成型活性STIM1如何导致该疾病的其他特征。"}, {"name": "管状聚集性肌病", "detail": "至少有五个功能获得性突变会导致肾小管聚集性肌病，而没有Stormorken综合征的其他特征（如上所述）。在这种疾病中，蛋白质在肌肉细胞中异常积聚，形成称为管状聚集体的管状结构团块。管状聚集性肌病会导致肌肉疼痛，抽筋或无力，并随着时间的推移而恶化。这种情况下涉及的突变会改变STIM1中的单个氨基酸，特别是在用于感测钙离子水平的蛋白质区域。像引起Stormorken综合征的功能获得性突变一样，这些突变导致产生组成型活性STIM1蛋白，该蛋白持续刺激钙离子流过CRAC通道。目前尚不清楚异常离子流如何导致肾小管聚集性肌病。有证据表明，管状聚集体由通常是肌浆网一部分的蛋白质组成。尽管其机制尚不清楚，但一些研究人员推测聚集体是肌肉细胞中钙水平不受控制的结果，可能是由于STIM1活性异常所致。(换行)研究人员不确定为什么另一个功能获得性突变会导致Stormorken综合征的其他体征和症状。"}, {"name": "其他疾病", "detail": "STIM1gene中至少有三个突变导致称为原发性免疫缺陷的免疫系统疾病10。由于免疫系统问题，患有这种疾病的个体反复感染可能危及生命。导致原发性免疫缺陷10的突变被称为“功能丧失”突变，因为它们降低或消除了STIM1蛋白的功能。在没有STIM1刺激的情况下，通过CRAC通道的钙离子流受到损害，这阻碍了免疫系统细胞抵抗感染的能力。"}]}' WHERE id = 8703;
UPDATE genes SET GHR_metadata_ch = '{"gene": "TH", "conditions": [{"name": "多巴反应性肌张力障碍", "detail": "已经发现THgene中的二十多个突变引起多巴反应性肌张力障碍。这种情况的特征是不自主的肌肉收缩（肌张力障碍），震颤和其他不受控制的运动，通常对称为左旋多巴的药物治疗有反应。导致这种情况的大多数THgene突变会改变酪氨酸羟化酶中的单个蛋白质结构单元（氨基酸），从而导致功能性酶的减少。正常酪氨酸羟化酶的减少导致多巴胺产生的减少，这引起多巴反应性肌张力障碍特有的运动问题。产生的功能酶的量与体征和症状的严重程度有关。功能较弱的酶会导致更严重的症状。"}, {"name": "酪胺酸羥化酶缺乏症", "detail": "在酪氨酸羟化酶（TH）缺乏症患者中已鉴定出THgene的20多个突变。这些突变导致酪氨酸羟化酶活性降低。结果，身体产生较少的多巴胺，去甲肾上腺素和肾上腺素。这些儿茶酚胺对于正常的神经系统功能是必需的，其水平的变化会导致TH缺乏症患者出现异常运动，神经系统功能障碍和其他神经系统问题。(换行)多巴反应性肌张力障碍有时被认为是酪氨酸羟化酶缺乏症的轻度形式。尚不确定它们是两种独立的疾病还是同一疾病谱的一部分。"}, {"name": "其他疾病", "detail": "某些常见的TH变异（多态性）会改变儿茶酚胺的产生，从而影响发展与自主神经系统调节相关的疾病的风险。这些THgene多态性影响血压随压力增加的程度，并可能增加高血压（高血压）的风险。（换行）一种THgene多态性与婴儿猝死综合征（SIDS）有关。小岛屿发展中国家是1岁以下婴儿死亡的主要原因。它的特点是突然和无法解释的死亡，通常在睡眠期间。这种多态性称为等位gene*9.3，是欧洲人后裔中最常见的THgene多态性，与其他婴儿相比，死于SIDS的婴儿比例更高。该gene的这个版本可能会影响婴儿呼吸或觉醒的调节。"}]}' WHERE id = 8721;
UPDATE genes SET GHR_metadata_ch = '{"gene": "LIPC", "conditions": [{"name": "肝脂肪酶缺乏症", "detail": "已发现LIPCgene中至少有10个突变会导致肝脂肪酶缺乏。这种情况导致血液中各种脂肪（脂质）水平异常，尽管尚不清楚这些变化是否会影响患心脏病的风险。导致这种情况的LIPCgene突变会改变肝脂肪酶中的单个蛋白质结构单元（氨基酸）。这些突变阻止酶从肝脏释放或降低其在血液中的活性。结果，VLDL和IDL不能有效地转化为LDL，并且携带胆固醇和甘油三酯的HDL保留在血流中。目前尚不清楚这种脂肪水平的变化对肝脂肪酶缺乏症患者有什么影响，因为一些受影响的人在成年中期会在动脉壁（动脉粥样硬化）和心脏病上积聚脂肪沉积物，而另一些则没有。"}, {"name": "年龄相关性黄斑变性", "detail": "遗传学家庭参考提供有关年龄相关性黄斑变性的信息。"}]}' WHERE id = 8724;
UPDATE genes SET GHR_metadata_ch = '{"gene": "FERMT1", "conditions": [{"name": "金德勒综合症", "detail": "在Kindler综合征患者中已鉴定出FERMT1gene的70多个突变。这种疾病是一种罕见的大疱性表皮松解症，是一组遗传病，可导致皮肤非常脆弱并容易起泡。Kindler综合征还影响口腔，眼睛，食道，肠，生殖器和泌尿系统的潮湿衬里（粘膜），导致这些组织非常脆弱。此外，Kindler综合征患者罹患一种称为鳞状细胞癌的癌症的风险增加。（换行）FERMT1gene中的大多数突变都会阻止任何功能性kindlin-1的产生。缺乏这种蛋白质会破坏许多基本的细胞功能。例如，没有kindlin-1的角质形成细胞结构异常，无法正常生长或分裂。它们也不太能够将表皮附着到皮肤的下层（真皮）。这些变化使皮肤脆弱，容易起泡。同样，粘膜上皮细胞中缺乏kindlin-1会造成损伤，使这些组织极为脆弱。目前尚不清楚Kindler综合征患者中kindlin-1的缺乏与鳞状细胞癌的关系。"}]}' WHERE id = 8742;
UPDATE genes SET GHR_metadata_ch = '{"gene": "CDKL5", "conditions": [{"name": "CDKL5缺乏症", "detail": "已发现CDKL5gene中的几个突变会导致CDKL5缺乏症，这是一种罕见病，其特征是从婴儿期开始发作并延迟发育。这是由于蛋白质缺陷导致的，该蛋白质损害大脑发育或改变其在神经元中的活性。另外，激酶结构域中的突变破坏CDKL5结果的能力与CDKL5缺乏症的更严重的体征和症状有关。"}]}' WHERE id = 8765;
UPDATE genes SET GHR_metadata_ch = '{"gene": "PRKAR1A", "conditions": [{"name": "粘液瘤", "detail": "已发现PRKAR1Agene中的117个以上突变引起卡尼复合体。这些突变中的大多数会导致异常的1型α调节亚基，该亚基会被细胞迅速分解（降解）。缺乏这种调节亚基会导致蛋白激酶A的开启频率高于正常水平，从而导致细胞增殖失控。卡尼复合体的体征和症状与身体许多部位细胞的不受控制的生长有关。"}]}' WHERE id = 8766;
UPDATE genes SET GHR_metadata_ch = '{"gene": "EMD", "conditions": [{"name": "艾梅氏肌失养症", "detail": "据报道，患有Emery-Dreifuss肌营养不良症的人的EMDgene突变超过100个。这种情况会影响骨骼肌和心肌，导致称为挛缩的关节畸形，从而限制某些关节的运动；肌肉无力和消瘦会随着时间的推移而恶化；和心脏问题，包括猝死的风险增加。(换行)几乎所有的EMDgene突变都会阻止细胞产生任何emerin蛋白。研究人员尚未确定缺乏这种蛋白质如何导致金刚砂-Dreifuss肌营养不良症的骨骼肌和心肌异常。然而，研究表明，缺乏emerin可能会破坏核膜中其他蛋白质的功能。这些变化可能会改变某些gene的活性或削弱细胞核的结构，使细胞更加脆弱。（换行）在极少数情况下，Emery-Dreifuss肌营养不良症是由EMD突变引起的，这些突变改变了emerin蛋白中的单个结构单元（氨基酸）。这些突变导致产生异常形式的emerin，该emerin无法与其他蛋白质相互作用或无法正确插入核膜。这种类型的突变可能是某些具有异常轻度体征和症状的金刚砂-Dreifuss肌营养不良症的原因。"}]}' WHERE id = 8775;
UPDATE genes SET GHR_metadata_ch = '{"gene": "RS1", "conditions": [{"name": "X连锁青少年视网膜劈裂", "detail": "已发现RS1gene中的220多个突变会导致X连锁的青少年视网膜分裂症。这种疾病会导致视网膜中形成微小的裂痕（裂痕）或眼泪，从而导致男性进行性视力丧失。尽管已鉴定出许多不同类型的突变，但大多数RS1gene突变会改变视黄醛壳素蛋白中的一个蛋白质结构单元（氨基酸）。研究表明，RS1gene中的各种突变会改变蛋白质的3维结构，损害蛋白质将细胞附着在一起的能力（细胞粘附），导致蛋白质在视网膜细胞内错位或阻止蛋白质产生。视黄醛壳聚糖蛋白功能或产生的变化破坏了其在视网膜维持和组织中的作用。结果，可能发生视网膜分裂或撕裂，导致与X连锁的青少年视网膜分裂症相关的视力问题。"}]}' WHERE id = 8823;
UPDATE genes SET GHR_metadata_ch = '{"gene": "CARD11", "conditions": [{"name": "特应性皮炎", "detail": "在患有特应性皮炎（也称为特应性湿疹）的皮肤病患者中，至少鉴定出五个CARD11gene突变。这种情况的特点是皮肤干燥，发痒和红疹。“特应性”一词表示与过敏有关。虽然特应性皮炎并不总是由于过敏反应，但它通常与其他过敏性疾病有关。由CARD11gene突变引起的特应性皮炎患者通常还有其他过敏性疾病，如哮喘和环境（如花粉）或食物过敏。由于免疫系统问题（免疫缺陷），这些人中的许多人也有反复感染。(换行)特应性皮炎通常被认为是受多种遗传和环境因素影响的复杂病症，每种因素对发展该病症的总体风险仅有少量贡献。但是，CARD11gene突变似乎在没有其他因素的情况下引起特应性皮炎。这些突变可能仅占该病病例的一小部分。（换行）每个细胞中CARD11gene的两个拷贝之一的突变足以引起特应性皮炎。这些突变导致产生不能正常起作用的改变的蛋白质。由gene的突变拷贝产生的改变的蛋白质干扰由gene的非突变拷贝产生的正常蛋白质（这种突变被描述为“显性阴性”），因此细胞中功能性CARD11蛋白质的量减少。这些遗传变化被认为可以阻止CBM信号体复合物的形成，从而削弱NF-κB的信号传导？B和mTORC1。没有这些信号，T细胞就不能正常发育或功能。这些细胞的数量是正常的，但它们对外来入侵者的反应减弱，导致反复感染。(换行)尚不清楚由CARD11gene突变引起的免疫功能障碍如何导致特应性皮炎和过敏性疾病。特应性皮炎最初不是由过敏反应引起的，尽管有时会引起过敏反应的物质（过敏原）被认为有助于皮疹的发作。"}, {"name": "联合免疫缺陷病", "detail": "遗传学家庭参考提供有关Omenn综合征的信息。"}, {"name": "癌症", "detail": "CARD11gene的突变也与B细胞（主要是弥漫性大B细胞淋巴瘤）和T细胞（成人T细胞白血病/淋巴瘤）的癌症有关。这些突变不是遗传的；相反，它们出现在一个人的一生中，仅在引起癌症的B细胞或T细胞中发现。这些遗传变化被称为“功能获得”突变，因为它们导致产生总是打开的改变的CARD11蛋白，即使没有B或T细胞识别外来物质也是如此。结果，NF-？B不断激活。不受管制的NF-？B信号传导允许这些细胞在没有控制的情况下生长和分裂，促进癌症的发展。"}, {"name": "其他疾病", "detail": "CARD11gene突变可引起其他免疫系统疾病。已发现该gene中至少有3个突变会导致一种严重的联合免疫缺陷（SCID），称为免疫缺陷11。SCID是一组以外来入侵者几乎完全缺乏免疫保护为特征的疾病。免疫缺陷11的特征是呼吸道反复严重感染，尤其是由称为吉氏肺孢子虫的真菌引起的肺炎。当CARD11gene的两个拷贝都发生突变时，就会发生免疫缺陷11。突变被描述为“功能丧失”，因为它们导致异常短而无功能的CARD11蛋白的产生。尽管细胞数量正常，但缺乏CARD11功能会降低T细胞抵抗外来入侵者的能力，从而导致反复感染。具有免疫缺陷11的个体似乎没有增加特应性皮炎（如上所述）或过敏的风险。（换行）CARD11gene中至少有四个突变引起另一种称为B细胞扩增的免疫细胞疾病，其中NF-？B和T细胞无能（BENTA）。这种情况的特征是称为B细胞（B细胞淋巴细胞增多）的免疫系统细胞过多，B细胞淋巴瘤的风险增加以及对感染的易感性。导致BENTA的突变是遗传的，并发生在CARD11gene的一个拷贝中。像涉及B细胞和T细胞癌的那些（如上所述）一样，引起BENTA的遗传变化是“功能获得”突变；它们导致产生总是打开的改变的CARD11蛋白，导致恒定的NF-？B信令。过度活跃的NF-？B促进B细胞的增殖，这可导致B细胞淋巴瘤。但是，这些异常细胞无法对感染做出反应。T细胞也是异常的，不能抵抗外来入侵者（这种现象称为无能）。这些免疫细胞问题导致BENTA患者感染的风险增加。"}]}' WHERE id = 8832;
UPDATE genes SET GHR_metadata_ch = '{"gene": "PLG", "conditions": [{"name": "先天性纤溶酶原缺乏症", "detail": "在先天性纤溶酶原缺乏症患者中已鉴定出PLGgene的50多个突变。这种疾病的特征是粘膜上的硬生长，粘膜是排列身体开口（例如眼睑和口腔内部）的潮湿组织。先天性纤溶酶原缺乏症最常影响结膜，结膜是保护眼睛白色部分（巩膜）和眼睑排列的粘膜。（换行）PLGgene突变可以减少产生的纤溶酶原的量，其功能或两者。当突变影响纤溶酶原水平以及蛋白质的活性时，可以说受影响的个体患有I型先天性纤溶酶原缺乏症，其特征在于先前描述的生长。据说具有PLGgene突变导致纤溶酶原水平正常且活性降低的人患有II型先天性纤溶酶原缺乏症或血浆蛋白原血症。这种形式的情况通常没有症状。（换行）功能性纤溶酶原的减少导致较少的纤溶酶分解纤维蛋白，导致纤维蛋白积聚。过量的纤维蛋白和由此产生的组织炎症导致先天性纤溶酶原缺乏症特有的炎症生长。(换行)目前尚不清楚为什么过量的纤维蛋白在粘膜中积聚，但通常不会导致血管中的异常凝块（血栓形成）。研究人员建议，血液中的其他酶也可能分解纤维蛋白，有助于补偿纤溶酶原水平的降低。"}]}' WHERE id = 8840;
UPDATE genes SET GHR_metadata_ch = '{"gene": "CFI", "conditions": [{"name": "补充因子I缺乏症", "detail": "在补体因子I缺乏症患者中已鉴定出CFIgene的几种突变，补体因子I缺乏症是一种以免疫系统功能障碍为特征并可能导致自身免疫性疾病的疾病。该病是由于CFI蛋白异常或无功能或缺乏引起的，这导致补体系统的激活失控，从而降低了血液中的C3蛋白。"}, {"name": "年龄相关性黄斑变性", "detail": "在与年龄相关的黄斑变性患者中已鉴定出CFIgene的几种突变，黄斑变性是一种主要影响黄斑的眼病，导致视力丧失，随着时间的推移，老年人的视力逐渐发展。这是由于在视网膜下积聚了称为玻璃疣的淡黄色沉积物。"}, {"name": "非典型溶血性尿毒症综合征", "detail": "在非典型溶血性尿毒症综合征患者中已鉴定出CFIgene的几种突变，这种情况会导致肾脏小血管中的血栓（血栓）异常。该病的特征是与凝血异常有关的三个主要特征：溶血性贫血，血小板减少和肾衰竭。"}, {"name": "C3肾小球病", "detail": "已经发现CFIgene中的几个突变引起C3肾小球病，C3肾小球病是导致肾损伤和终末期肾病（ESRD）的罕见形式的肾病，其特征在于血液中C3蛋白水平低，蛋白尿，血尿，减少身体许多部位的尿量和肿胀量。"}, {"name": "其他疾病", "detail": "在患有孤立的C3沉积物的肾小球肾炎患者中发现了CFIgene的几个突变。这种情况也可能发生在补体因子I缺乏症患者中，其特征是肾脏功能异常导致肾脏损害和蛋白尿。该病是由于CFI蛋白异常或无功能，导致攻击肾细胞的补体系统失控激活。"}]}' WHERE id = 8841;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SLC22A12", "conditions": [{"name": "肾低尿酸血症", "detail": "已发现SLC22A12gene中的30多个突变会引起肾性低尿酸血症。这种情况导致血液中尿酸的量减少。肾性低尿酸血症通常不会引起任何健康问题，但在运动后会导致疼痛和恶心，肾结石或尿液中的血液（血尿）。导致肾脏低尿酸血症的大多数突变会取代URAT1蛋白中的单个蛋白结构单元（氨基酸），并降低蛋白将尿酸重吸收到血液中的能力。在受影响的日本和韩国个体中，最常见的突变是用过早的终止信号（Trp258Ter或W258X）替换258位的氨基酸色氨酸，从而导致蛋白质异常短。URAT1重吸收尿酸的能力降低导致血液中尿酸缺乏和尿液损失过多。虽然尚不清楚尿酸水平的这些变化如何导致肾性低尿酸血症的体征和症状，但尿酸的抗氧化特性的丧失可能与通过肾脏的尿酸增加在尿液中释放有助于这种情况的特点。"}, {"name": "其他疾病", "detail": "一些研究发现SLC22A12gene的变异与称为痛风的病症有关，痛风是由关节中的尿酸结晶引起的关节炎的一种形式。这些变体可能会损害URAT1蛋白将尿酸释放到尿液中的能力。结果，过多的尿酸被重新吸收到血液中，导致体内尿酸积聚。这种过量的尿酸通常以晶体的形式积聚在身体的关节中，导致疼痛的关节炎。但是，其他研究尚未发现SLC22A12gene变异与痛风之间存在关联。虽然SLC22A12gene在痛风中的作用可能尚不清楚，但已知生活方式，遗传和环境因素的组合在确定这种复杂疾病的风险中起作用。"}]}' WHERE id = 8844;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SERPINI1", "conditions": [{"name": "家族性脑病伴神经鞘膜包涵体", "detail": "已发现SERPINI1gene中至少有四个突变会导致家族性脑病，并伴有神经丝氨酸蛋白酶抑制剂包涵体（FENIB）。这些突变中的每一个都改变了用于制造神经丝氨酸蛋白酶抑制剂蛋白的单个蛋白质构建块（氨基酸）。这些变化导致产生异常形状，不稳定形式的神经丝氨酸蛋白酶抑制剂。在神经元内，有缺陷的神经丝氨酸蛋白酶抑制剂蛋白可以彼此附着并形成称为神经丝氨酸蛋白酶抑制剂包涵体或柯林斯体的团块。这些团块破坏细胞的正常功能并最终导致细胞死亡。大脑某些部位神经元的逐渐丧失导致非尼患者进行性痴呆。（换行）SERPINI1突变也降低或消除神经丝氨酸蛋白酶抑制剂抑制神经元中tPA的能力。研究人员认为，tPA的未经检查的活动也可能导致这种情况的体征和症状。(换行)一些SERPINI1突变导致比其他形式更严重的FENIB形式。该疾病的严重程度及其发病年龄与神经元内柯林斯体的数量有关。一种称为神经丝氨酸蛋白酶抑制剂的突变与中度形式的疾病有关，该疾病从四五十年代开始导致智力功能逐渐下降。这种遗传变化将神经丝氨酸蛋白（写为Ser49Pro或S49P）中第49位的氨基酸脯氨酸替换为氨基酸丝氨酸。(换行)其他SERPINI1突变会导致更严重的FENIB形式，其特征是癫痫发作以及痴呆症以外的突然，不自主的肌肉抽搐或抽搐（肌阵挛）发作。这些迹象最早可出现在一个人的青少年身上。这些严重的突变之一称为波特兰神经丝氨酸蛋白酶抑制剂，用神经丝氨酸蛋白酶抑制剂蛋白（写为Ser52Arg或S52R）中第52位的氨基酸精氨酸取代了氨基酸丝氨酸。另一个突变在蛋白质位置392处用另外两个氨基酸之一谷氨酸或精氨酸取代了氨基酸甘氨酸。（这些突变分别写为Gly392Glu和Gly392Arg。）具有这些遗传变化之一的儿童会经历非常严重的疾病形式，包括癫痫发作和无法控制的肌肉抽搐（肌阵挛性癫痫）以及发育迟缓。"}]}' WHERE id = 8854;
UPDATE genes SET GHR_metadata_ch = '{"gene": "MAP2K1", "conditions": [{"name": "心血管系统皮肤综合征", "detail": "已经在患有心血管系统皮肤综合征的人中鉴定出MAP2K1gene中的至少13个突变。这种情况会影响身体的许多部位，尤其是心脏（心脏），面部特征（面部）以及皮肤和头发（皮肤）。这些MAP2K1gene突变中的每一个都会改变MEK1蛋白激酶中的单个蛋白结构单元（氨基酸）。这些遗传变化异常激活蛋白质，从而破坏全身细胞中严格调控的RAS/MAPK信号通路。改变的信号传导干扰许多器官和组织的正常发育，导致心脏皮肤综合征的特征。"}, {"name": "Noonan综合征伴多发性扁豆素", "detail": "已经发现MAP2K1gene中的至少一种突变引起具有多种扁豆素的Noonan综合征（以前称为LEOPARD综合征）。这种情况的特征是多个棕色皮肤斑点（扁豆素），心脏缺陷，身材矮小，胸部凹陷或突出以及独特的面部特征。（换行）鉴定的MAP2K1gene突变用MEK1蛋白激酶中102位氨基酸甘氨酸（写为Glu102Gly或E102G）取代氨基酸谷氨酸。这种变化可能导致全身细胞中RAS/MAPK信号通路的激活增加。增加的信号传导干扰许多器官和组织的正常发育，导致具有多种扁豆素的Noonan综合征的特征。(换行)目前尚不清楚为什么E102G突变会导致Noonan综合征伴有多种lentigines，而其他MAP2K1gene突变会导致不同的疾病，例如心血管系统皮肤综合征（如上所述）。"}, {"name": "朗格汉斯细胞组织细胞增生症", "detail": "遗传学家庭参考提供有关朗格汉斯细胞组织细胞增生症的信息。"}, {"name": "肺癌", "detail": "遗传学家庭参考提供有关肺癌的信息。"}, {"name": "努南综合征", "detail": "遗传学家庭参考资料提供有关Noonan综合征的信息。"}]}' WHERE id = 8855;
UPDATE genes SET GHR_metadata_ch = '{"gene": "VRK1", "conditions": [{"name": "桥小脑发育不全", "detail": "在患有称为桥小脑发育不全的大脑发育障碍的人中，已经鉴定出VRK1gene的至少两个突变。这种情况的主要特征包括发育迟缓，运动问题和智力障碍。VRK1gene突变导致一小部分称为1型桥小脑发育不全（PCH1）的疾病。当PCH1由VRK1gene突变产生时，有时会将其更具体地归类为PCH1A。（换行）导致PCH1A的VRK1gene突变显着减少细胞中产生的痘苗相关激酶1的量。这种蛋白质的缺乏使其无法执行其通常的功能，包括调节转录因子的活性。尽管这些变化可能会影响细胞的生长和分裂，但尚不清楚它们如何导致PCH1A患者的大脑发育异常。"}, {"name": "其他疾病", "detail": "VRK1gene中的至少三个突变被认为会引起另一种罕见的脑部疾病，已被描述为遗传性运动和感觉神经病（HMSN）的一种形式。HMSN是一组影响周围神经的疾病，周围神经将大脑和脊髓连接到肌肉以及检测触觉，疼痛和温度的感觉细胞。由VRK1gene突变导致的HMSN患者的肌张力较弱（肌张力低下），并且运动技能（如坐，站立和行走）的发展延迟。他们的头部也异常小（小头畸形），但智力正常。（换行）与该疾病相关的VRK1gene突变改变了痘苗相关激酶1中的单个蛋白质构建块（氨基酸）。研究人员正在努力确定VRK1gene的变化如何导致与该疾病相关的神经系统问题。（换行）在患有PCH1A的个体中也发现了与HMSN相关的突变之一（如上所述）。目前尚不清楚为什么VRK1gene突变会导致不同人群的不同神经系统异常。还不清楚为什么这些突变的影响似乎仅限于神经系统，因为牛痘相关激酶1在人体的许多细胞和组织中都具有活性。"}]}' WHERE id = 8868;
UPDATE genes SET GHR_metadata_ch = '{"gene": "PGK1", "conditions": [{"name": "磷酸甘油酸激酶缺乏症", "detail": "已发现PGK1gene中至少有18个突变导致磷酸甘油酸激酶缺乏。在一些受影响的个体中，这种情况导致红细胞短缺（慢性溶血性贫血），伴有或不伴有神经症状。在其他情况下，该病的特征是肌肉无力和抽筋。（换行）大多数PGK1gene突变改变磷酸甘油酸激酶中的单个蛋白质构建块（氨基酸）。还报道了一些其他类型的突变，包括PGK1gene中少量DNA的插入和缺失。研究表明，PGK1gene突变会降低磷酸甘油酸激酶的活性，从而破坏正常的能量产生并导致细胞损伤或细胞死亡。目前尚不清楚为什么这种异常会优先影响某些人的红细胞和脑细胞以及其他人的肌肉细胞。研究人员推测，不同的PGK1gene突变可能对不同类型细胞中磷酸甘油酸激酶的活性产生不同的影响。"}]}' WHERE id = 8869;
UPDATE genes SET GHR_metadata_ch = '{"gene": "HMGCL", "conditions": [{"name": "3-羟基-3-甲基戊二酰辅酶A裂解酶缺乏症", "detail": "在患有3-羟甲基-3-甲基戊二酰辅酶A裂解酶缺乏症（也称为HMG-CoA裂解酶缺乏症）的人中，已经鉴定出HMGCLgene的25个以上突变。这些突变大多数会改变HMG-CoA裂解酶中的单个氨基酸。例如，沙特阿拉伯人口中最常见的突变是在41位的氨基酸谷氨酰胺取代了氨基酸精氨酸（写为Arg41Gln或R41Q）。其他HMGCL突变导致异常短的酶的产生，该酶缺少关键片段。（换行）如果突变降低或消除HMG-CoA裂解酶的活性，则身体无法正确加工亮氨酸或制造酮。当亮氨酸不能正常加工时，称为有机酸的化学副产物会积聚并使血液过酸（代谢性酸中毒）。酮的缺乏会导致血糖水平变得危险的低（低血糖）。代谢性酸中毒和低血糖的影响会损害细胞，特别是在大脑中，导致HMG-CoA裂解酶缺乏症儿童患病严重。"}]}' WHERE id = 8873;
UPDATE genes SET GHR_metadata_ch = '{"gene": "COQ4", "conditions": [{"name": "初级辅酶Q10缺乏症", "detail": "已发现COQ4gene中至少有12个突变会引起一种称为初级辅酶Q10缺乏症的疾病。这种罕见疾病通常在婴儿期或儿童早期就很明显，但可以在任何年龄发生。它可以影响身体的许多部位，最常见的是大脑，肌肉和肾脏。与该疾病相关的COQ4gene突变大大减少或消除了COQ4蛋白的产生，从而阻止了辅酶Q10的正常产生。研究表明，辅酶Q10的缺乏（缺乏）会损害氧化磷酸化并增加细胞对自由基损伤的脆弱性。辅酶Q10的缺乏也可能破坏嘧啶的产生。这些变化可能导致整个身体的细胞发生功能障碍，这可能有助于解释可能受初级辅酶Q10缺乏影响的器官和组织的多样性。"}]}' WHERE id = 8937;
UPDATE genes SET GHR_metadata_ch = '{"gene": "FUCA1", "conditions": [{"name": "岩藻糖中毒", "detail": "已发现FUCA1gene中至少有26个突变引起岩藻糖中毒。这些突变大多数导致异常短的，无功能的α-L-岩藻糖苷酶。没有这种酶，糖脂和糖蛋白就不能完全分解。这些部分分解的化合物积聚在溶酶体中并导致整个身体的细胞发生故障。脑细胞对糖脂和糖蛋白的积累特别敏感，这可能导致细胞死亡。脑细胞的丢失被认为会引起岩藻糖中毒的神经系统症状。糖脂和糖蛋白的积累也发生在其他器官中，例如肝脏，脾脏，皮肤，心脏，胰腺和肾脏，从而导致岩藻糖中毒的其他症状。"}]}' WHERE id = 8944;
UPDATE genes SET GHR_metadata_ch = '{"gene": "FECH", "conditions": [{"name": "卟啉症", "detail": "在患有称为促红细胞生成性原卟啉症的卟啉症患者中，已鉴定出FECHgene的110多个突变。FECHgene一个拷贝中的突变使每个细胞的铁螯合酶产生减少约一半。但是，这不足以引起卟啉症的体征和症状；患有这种疾病的人还必须继承FECH的第二份更改副本。在一些受影响的个体中，FECHgene的第二个拷贝也是无功能的，并且细胞几乎不产生铁螯合酶。在其他受影响的个体中，FECHgene的第二个拷贝保留了其某些功能。该gene的这个版本被描述为低表达等位gene。它减少但不消除细胞内产生的铁螯合酶的量。每个细胞中FECHgene的两个突变拷贝或该gene的一个突变拷贝和一个低表达等位gene的组合对于促红细胞生成性原卟啉症的发展是必需的。(换行)功能性铁螯合酶的缺乏使得称为卟啉的化合物在发育中的红细胞中积聚。这些化合物是在血红素生产的正常过程中形成的，但是铁螯合酶活性的降低使它们积累到毒性水平。过量的卟啉可以从发育中的红细胞中泄漏出来，并通过血流输送到皮肤和其他组织。皮肤中高水平的这些化合物会导致对阳光的过度敏感，这是这种情况的特征。胆囊中大量的卟啉也会引起胆结石。不太常见的是，这些化合物在肝脏中的积累会导致肝脏损伤。"}]}' WHERE id = 8974;
UPDATE genes SET GHR_metadata_ch = '{"gene": "RHO", "conditions": [{"name": "常染色体显性先天性静止性夜盲", "detail": "已发现RHOgene中至少有四个突变会导致常染色体显性先天性固定性夜盲，其特征是在弱光下视力丧失，并随着时间的推移保持稳定（静止）。与色素性视网膜炎（如下所述）不同，常染色体显性先天性固定性夜盲症不会影响白天的视力。（换行）导致常染色体显性先天性静止性夜盲的RHOgene突变导致视紫红质蛋白不断开启（组成型活性）。由于蛋白质不再需要激活光，杆细胞发送到大脑的信号不断发生，即使在明亮的光线下也是如此。来自视杆细胞的视觉信息然后被大脑感知为没有意义，导致夜盲症。(换行)研究人员不确定为什么RHOgene中的某些组成性激活突变会导致先天性静止性夜盲，而另一些会导致与色素性视网膜炎相关的更严重的视力丧失。"}, {"name": "视网膜色素变性", "detail": "在色素性视网膜炎患者中已鉴定出150多个RHOgene突变。RHOgene突变占所有常染色体显性遗传性视网膜色素变性病例的20%至30%，这被认为是该疾病的最常见形式。很少，RHOgene突变引起常染色体隐性视网膜色素变性。但是，这种形式的疾病通常是由其他gene的突变引起的。（换行）负责视网膜色素变性的大多数RHOgene突变改变视紫红质蛋白的折叠或转运。一些突变导致视紫红质被组成性激活，而不是响应于光而被激活。研究表明，视紫红质的改变形式会干扰基本的细胞功能，导致视杆自我破坏（发生凋亡）。由于视杆对于低光照conditions下的视力至关重要，因此这些细胞的丢失会导致色素性视网膜炎患者进行性夜盲。(换行)色素性视网膜炎也与视锥细胞逐渐丧失有关，视锥细胞通常在强光下提供视力。视锥细胞的死亡导致隧道视力并最终导致许多受影响个体失明。目前尚不清楚RHOgene的突变如何影响视锥细胞的功能和存活。"}]}' WHERE id = 8977;
UPDATE genes SET GHR_metadata_ch = '{"gene": "PHKA2", "conditions": [{"name": "IX型糖原贮积病", "detail": "已知PHKA2gene中至少有90个突变会导致某种形式的糖原贮积病IX型（GSD IX），称为GSD IXa或X连锁肝糖原病（XLG）。这是GSD IX的最常见形式，约占病例的75%。GSD IXa影响肝功能，其特征包括肝脏肿大（肝肿大），生长缓慢和低血糖期（低血糖）。这些功能通常会随着时间而改善。但是，一些受影响的个体在肝脏中有疤痕组织（纤维化）积聚，很少会发展为不可逆的肝病（肝硬化）。PHKA2gene中的（换行）突变降低了肝细胞中磷酸化酶b激酶的活性，尽管其机制尚不清楚。这种酶功能的降低会损害糖原分解。结果，糖原在细胞中积累，并且葡萄糖不可用于能量。肝脏中的糖原积累导致肝肿大并可能损害器官。无法将糖原分解为能量会导致低血糖症和GSD IXa的其他特征。（换行）GSD IXa有两种亚型，称为XLG1和XLG2，根据各种组织中磷酸化酶b激酶的活性进行分类。在更常见的亚型XLG1中，肝脏和红细胞中的酶活性降低。相反，在XLG2中，通过实验室测试测量时，该酶的活性在肝脏中显示为低或正常，在红细胞中显示为正常或高。亚型根据症状无法区分。"}]}' WHERE id = 8981;
UPDATE genes SET GHR_metadata_ch = '{"gene": "TTC37", "conditions": [{"name": "毛发肝肠综合征", "detail": "已发现TTC37gene中至少有25个突变会引起毛发肝肠综合症，这是一种罕见病，会影响身体的许多部位。它的主要体征和症状包括从婴儿期开始的慢性腹泻，头发异常，独特的面部特征和肝病。该gene的突变可能会消除TTC37蛋白的功能。研究人员假设这种蛋白质功能的丧失会损害滑雪复合体和胞质外泌体的活性。但是，尚不清楚这些变化如何导致慢性腹泻和毛发肝肠综合征的其他特征。"}]}' WHERE id = 8983;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ATP1A3", "conditions": [{"name": "儿童交替性偏瘫", "detail": "已发现ATP1A3gene的突变会导致儿童交替性偏瘫，这是一种神经系统疾病，其特征是反复发作的暂时性麻痹，通常会影响身体的一侧（偏瘫）。这是由于α-3亚基中单个氨基酸的变化导致Na+/K+ATPase缺陷，从而削弱了其转运离子的能力。"}, {"name": "快速发作的肌张力障碍-帕金森病", "detail": "已经在患有快速发作的肌张力障碍-帕金森病（一种以运动异常为特征的疾病）的个人和家庭中鉴定出ATP1A3gene的突变。这是由α-3亚基中的单个氨基酸变化引起的，导致Na+/K+ATP酶缺陷，这削弱了其转运钠离子的能力并破坏了大脑中神经元的电活动。"}]}' WHERE id = 9027;
UPDATE genes SET GHR_metadata_ch = '{"gene": "FOXN1", "conditions": [{"name": "T细胞免疫缺陷，先天性脱发和指甲营养不良", "detail": "已发现FOXN1gene中至少有一个突变会导致T细胞免疫缺陷，先天性脱发和指甲营养不良，这种情况会影响免疫系统以及头发和指甲的生长。已知的突变称为Arg255Ter或R255X，它用停止蛋白质生产的信号取代了蛋白质结构单元（氨基酸）精氨酸。突变gene未产生功能性FOXN1蛋白。(换行)缺乏FOXN1蛋白会阻止胸腺的形成。当这个腺体不存在时，免疫系统不能产生成熟的功能性T细胞来抵抗感染。结果，患有T细胞免疫缺陷，先天性脱发和指甲营养不良的人从生命早期开始就出现了反复发作的严重感染。FOXN1蛋白的丢失还可以防止毛囊的形成，从而导致没有头发（脱发）。此外，这种蛋白质的缺乏会导致指甲和脚趾甲畸形（指甲营养不良）。(换行)研究人员描述了至少两例T细胞免疫缺陷，先天性脱发和指甲营养不良的中枢神经系统异常。但是，尚不清楚中枢神经系统异常是否是这种情况的共同特征。目前尚不清楚FOXN1蛋白的短缺如何导致这些异常。"}]}' WHERE id = 9058;
UPDATE genes SET GHR_metadata_ch = '{"gene": "AQP2", "conditions": [{"name": "肾源性尿崩症", "detail": "在肾源性尿崩症患者中已鉴定出AQP2gene至少40个突变。(换行)大多数已知的AQP2gene突变导致水通道蛋白2蛋白错误折叠成不正确的三维形状。错误折叠的蛋白质被捕获在细胞内，在那里它无法到达细胞膜来运输水分子。一些突变导致功能性水通道蛋白2水通道的产生，但是这些通道在细胞内错误路由并且不到达细胞膜。(换行)如果水通道蛋白2水通道未插入收集导管细胞的膜中，则肾脏无法响应来自ADH的信号。因此，收集管道不会像应该的那样重新吸收水分，并且身体会产生过量的尿液。这些水平衡问题是肾源性尿崩症的特征。"}]}' WHERE id = 9087;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SPART", "conditions": [{"name": "Troyer综合征", "detail": "在Troyer综合征患者中发现了一种突变。此突变删除了SPARTgene中的DNA构建块（核苷酸）。具体而言，此突变删除了gene1110位的核苷酸腺苷，写为1110delA。这种突变导致异常缩短的，无功能的斯巴丁蛋白。目前尚不清楚这种突变如何导致Troyer综合征的体征和症状。"}]}' WHERE id = 9089;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SEPSECS", "conditions": [{"name": "桥小脑发育不全", "detail": "在患有称为桥小脑发育不全的大脑发育障碍的人中，已经鉴定出SEPSECSgene中的至少三个突变。这种情况的主要特征包括发育迟缓，运动问题和智力障碍。在伊拉克和摩洛哥血统的几个家庭中，已经发现SEPSECSgene突变会导致一种称为2型桥小脑发育不全（PCH2）的疾病。当PCH2由SEPSECSgene突变产生时，有时会将其更具体地归类为PCH2D。PCH2D的体征和症状似乎不如其他形式的小脑发育不全严重。研究人员还将PCH2D称为进行性小脑萎缩（PCCA）。（换行）导致PCH2D的SEPSECSgene突变完全消除了SEPSECS的功能。缺乏这种酶的功能会损害硒代半胱氨酸的产生和随后的硒蛋白的形成。目前尚不清楚这些蛋白质的缺乏如何导致PCH2D患者大脑发育异常。"}]}' WHERE id = 9104;
UPDATE genes SET GHR_metadata_ch = '{"gene": "PNPLA2", "conditions": [{"name": "中性脂质贮积病伴肌病", "detail": "已发现PNPLA2gene中至少有五个突变会引起肌病的中性脂质贮积病。这些突变中的一些导致酶异常起作用。其他突变阻止酶永远到达脂滴，因此它无法与甘油三酸酯相互作用。甘油三酸酯分解的任何破坏都会导致这些脂肪在全身肌肉和其他组织中积聚，从而导致中性脂质贮积病伴肌病的体征和症状。"}]}' WHERE id = 9105;
UPDATE genes SET GHR_metadata_ch = '{"gene": "RUNX1T1", "conditions": [{"name": "核心结合因子急性髓性白血病", "detail": "在大约7%的患有一种称为急性髓细胞性白血病（AML）的血液癌的个体中发现了涉及RUNX1T1gene的遗传物质的重排（易位）。这种易位，写为t（8；21），结合了来自8号染色体和21号染色体的遗传信息，将21号染色体上称为RUNX1的gene与8号染色体上的RUNX1T1gene融合在一起。当与这种易位相关联时，该病症被分类为核心结合因子AML（CBF-AML）。（换行）由正常RUNX1gene产生的蛋白质是称为核心结合因子（CBF）的蛋白质复合物的一部分。作为CBF的一部分，RUNX1蛋白附着于DNA的特定区域并打开（激活）参与血细胞发育的gene。与RUNX1一样，由t（8；21）易位产生的融合蛋白，称为RUNX1-ETO，附着于DNA；然而，由于涉及ETO，融合蛋白关闭与血细胞发育相关的gene而不是打开它们。这种gene活性的变化阻断了血细胞的成熟（分化）并导致异常，未成熟的白细胞的产生。细胞称为骨髓原始细胞。尽管t（8；21）对于白血病的发展很重要，但髓样母细胞通常需要一个或多个其他遗传变化才能发展成癌性白血病细胞。"}]}' WHERE id = 9108;
UPDATE genes SET GHR_metadata_ch = '{"gene": "PRSS1", "conditions": [{"name": "遗传性胰腺炎", "detail": "已发现PRSS1gene中的40多个突变会导致遗传性胰腺炎，这种疾病的特征是胰腺（胰腺炎）反复发作，可导致胰腺功能丧失。这些突变大多数会改变阳离子胰蛋白酶原中的单个蛋白质结构单元（氨基酸）。一些PRSS1gene突变导致阳离子胰蛋白酶原酶的产生，该酶在仍在胰腺中时会过早转化为胰蛋白酶。其他突变可防止胰蛋白酶分解。导致遗传性胰腺炎的最常见PRSS1gene突变是将氨基酸精氨酸替换为酶中第122位的氨基酸组氨酸（写为Arg122His或R122H）。这种突变的结果是，即使该酶不再与钙结合，也无法分解该酶。(换行)胰腺中的胰蛋白酶活性可损害胰腺组织并且还可引发免疫应答，引起胰腺炎症并导致胰腺炎发作。"}]}' WHERE id = 9137;
UPDATE genes SET GHR_metadata_ch = '{"gene": "EPM2A", "conditions": [{"name": "Lafora进行性肌阵挛性癫痫", "detail": "在Lafora进行性肌阵挛性癫痫患者中，已鉴定出EPM2Agene的50多个突变。这些突变中的许多改变了laforin蛋白中的单个蛋白结构单元（氨基酸）。其他突变会删除或插入EPM2Agene中的遗传物质。该gene中几乎所有的突变都会阻止细胞产生任何laforin或导致蛋白质的非功能性产生。（换行）目前尚不清楚EPM2Agene的突变如何导致Lafora进行性肌阵挛性癫痫的主要特征。研究表明，laforin的缺失会阻止细胞调节糖原的产生。结果，在许多类型的细胞内形成了称为Lafora体的独特团块。Lafora身体由异常形式的糖原（称为聚葡聚糖）组成，无法分解并用作燃料。相反，聚葡聚糖聚集形成可破坏细胞的团块。神经元似乎特别容易受到这种类型的损害。尽管在人体的许多组织中都发现了Lafora尸体，但Lafora进行性肌阵挛性癫痫的体征和症状仅限于神经系统。(换行)研究人员不确定功能性laforin的丧失如何促进Lafora体的形成。但是，缺乏这种蛋白质最终会导致神经元死亡，从而干扰大脑的正常功能。神经元变性可能是Lafora进行性肌阵挛性癫痫发作，运动异常，智力下降和其他神经系统问题的基础。"}]}' WHERE id = 9151;
UPDATE genes SET GHR_metadata_ch = '{"gene": "FBN2", "conditions": [{"name": "先天性挛缩蛛网膜", "detail": "已发现FBN2gene中的20多个突变可导致先天性挛缩蛛网膜。这些突变大多数会改变原纤维蛋白2蛋白中的一个蛋白结构单元（氨基酸），通常用不同的氨基酸替换氨基酸半胱氨酸。另一个氨基酸取代半胱氨酸可以改变原纤维蛋白2的结构或功能。大多数其他FBN2突变会破坏FBN2gene说明用于制备原纤维蛋白2蛋白的方式。所有这些突变减少了可用于形成微纤维的原纤维蛋白2的量。微纤维形成的减少可能会削弱弹性纤维并导致TGF-β生长因子的过度活化，从而导致先天性挛缩蛛网膜的体征和症状。"}]}' WHERE id = 9162;
UPDATE genes SET GHR_metadata_ch = '{"gene": "GFM1", "conditions": [{"name": "Combined oxidative phosphorylation deficiency 1", "detail": "已发现GFM1gene中至少18个突变导致组合的氧化磷酸化缺陷1。这种情况会导致严重的神经和肝功能障碍。受影响的人通常无法存活到儿童早期。大多数GFM1gene突变会改变线粒体翻译延伸因子G1酶中的单个氨基酸。这种改变减少或消除了酶的功能。结果，产生较少的参与氧化磷酸化的线粒体蛋白。具有高能量需求的器官，例如脑和肝，特别受到所导致的氧化磷酸化损伤的影响。这些组织中的能量短缺导致细胞死亡，导致氧化磷酸化缺乏症患者的神经和肝脏问题1。据认为，在这种情况下，需要大量能量的其他组织，例如心脏和其他肌肉，不会受到影响，因为它们具有可以执行线粒体蛋白质生产过程的其他酶。"}, {"name": "利综合征", "detail": "遗传学家庭参考提供有关Leigh综合征的信息。"}]}' WHERE id = 9185;
UPDATE genes SET GHR_metadata_ch = '{"gene": "GLI3", "conditions": [{"name": "肩call骨综合征", "detail": "据报道，至少有两个GLI3gene突变的人有肩胼胝体综合征的特征，这是一种罕见的疾病，拥有属性某些大脑异常，额外的手指和脚趾（多指），以及独特的面部特征，包括宽间隔的眼睛（超远距离）和突出的前额。这些体征和症状与Greig头多突综合征（如下所述）明显重叠，因此有时将GLI3gene突变导致的肩call骨综合征视为该病的严重形式。（换行）导致顶call综合征的GLI3gene突变会改变GLI3蛋白特定区域中的单个蛋白结构单元（氨基酸），从而破坏该蛋白的功能。故障蛋白可能会在早期发育过程中改变某些gene的表达。GLI3蛋白在大脑和肢体模式中的作用可能有助于解释为什么突变会导致大脑异常，多指畸形以及顶call综合征的其他特征。"}, {"name": "Greig头多突综合征", "detail": "在Greig头多突综合征患者中已鉴定出GLI3gene至少120个突变，这是一种罕见的疾病，其特征是多指，过度肌肉发达，额头宽大和头部异常大（大头畸形）。与Greig头多突综合征相关的遗传变化包括少量DNA的插入或缺失以及gene关键区域中单个DNA构建块（碱基对）的变化。这种情况也可能是由涉及包含GLI3gene的7号染色体区域的染色体异常引起的。导致Greig头多突综合征的遗传变化阻止了每个细胞中该gene的一个拷贝产生任何功能性GLI3蛋白。结果，在早期发育过程中，只有正常量的这种蛋白质的一半可用于控制靶gene的表达。尚不清楚减少量的GLI3蛋白如何破坏四肢，头部和面部的发育并引起Greig头多突综合征的特定特征。"}, {"name": "Pallister-Hall综合征", "detail": "已发现GLI3gene中的40多个突变会引起Pallister-Hall综合征，这是一种罕见病，其主要特征包括多指畸形，大脑中称为下丘脑错构瘤的异常生长以及称为双会厌的气道畸形。导致Pallister-Hall综合征的大多数突变都发生在gene的中间附近，从而在制造GLI3蛋白的说明中产生过早的终止信号。结果，细胞产生异常短的蛋白质版本。与可以打开或关闭靶gene的全长GLI3蛋白不同，短蛋白只能抑制靶gene的表达。尽管这种变化明显破坏了胚胎发育，但尚不清楚GLI3蛋白功能的改变如何导致Pallister-Hall综合征的各种体征和症状。"}, {"name": "其他疾病", "detail": "在患有多指而没有顶call综合征，Greig头多指综合征或Pallister-Hall综合征（如上所述）的其他特征的人中发现了GLI3gene的突变。这些病例被描述为孤立的或非综合征的，因为多指发生时没有其他体征和症状。GLI3gene突变可导致多种形式的分离多指。这些包括后轴多指A型（PAP-A）和A/B型（PAP-A/B），其特征是小指或小脚趾旁边有一个额外的数字。GLI3gene突变也可能导致前轴多指IV型（PPD-IV），其特征是拇指或大脚趾（拇趾）旁边有多余的手指，某些手指和脚趾之间的皮肤融合（皮肤上有组织）。PPD-IV还可以在手或脚的其他位置包括额外的数字。PPD-IV所见的多指模式与Greig头多指综合征相似，一些研究人员认为PPD-IV可能是该综合征的一种非常轻微的形式。"}]}' WHERE id = 9218;
UPDATE genes SET GHR_metadata_ch = '{"gene": "DSC2", "conditions": [{"name": "毛发角化病", "detail": "已发现DSC2gene中至少有一个突变会导致一种形式的角化病，其毛发被分类为III型。其特征是手掌和脚底上有厚厚的call状皮肤（掌plant角化病）；粗糙，干燥，细密，卷曲的头发；以及一种潜在的威胁生命的心脏病，称为心律失常性右室心肌病（ARVC）。（换行）已知的突变，写为1841delG，从DSC2gene中删除一个DNA构建块（碱基对）。desmocollin-2蛋白的异常形式改变了桥粒的结构，阻止了细胞有效地彼此附着。研究人员怀疑细胞之间的连接受损会使皮肤，头发和心肌更脆弱。随着时间的推移，由于这些组织暴露于机械应力（例如，皮肤表面上的摩擦或心肌的持续收缩和松弛），它们变得受损并且不能再正常工作。这种机制可能是III型毛发性角化病中发生的皮肤，头发和心脏问题的基础。研究表明，异常的细胞信号传导也可能导致患有这种疾病的人患心肌病。(换行)DSC2gene中的其他几个突变会导致ARVC，而没有掌plant角化病或毛发。目前尚不清楚为什么该gene中的某些突变会影响皮肤和头发，而另一些则不会。"}, {"name": "致心律失常性右室心肌病", "detail": "遗传学家庭参考资料提供有关心律失常性右室心肌病的信息。"}]}' WHERE id = 9228;
UPDATE genes SET GHR_metadata_ch = '{"gene": "RFXAP", "conditions": [{"name": "裸淋巴细胞综合征II型", "detail": "已发现RFXAPgene中至少有七个突变会引起称为II型裸淋巴细胞综合征（BLS II）的免疫系统疾病。BLS II是一种联合免疫缺陷病（CID），其中受影响的个体实际上没有针对外来入侵者的免疫保护。因此，患有BLS II的个体在呼吸道，胃肠道和泌尿道中持续感染，这可能危及生命。（换行）RFXAPgene中的突变导致产生异常短的RFXAP蛋白，如果产生任何蛋白质，则可能无法正常运作。这些变化损害RFX复合物与DNA的结合，这阻止了MHC II类蛋白的转录。因此，淋巴细胞在其表面缺乏任何MHC II类蛋白，并且身体难以除去细菌，病毒和真菌，导致BLS II特有的持续感染。"}, {"name": "其他疾病", "detail": "已发现RFXAPgene中的至少一种突变引起裸淋巴细胞综合征III型（BLS III）。这种类型的裸淋巴细胞综合征的特征在于淋巴细胞表面上不存在MHC II类蛋白和减少量的MHC I类蛋白。但是，尚不清楚BLS III是否是一种独特的诊断方法。一些医生认为这种情况是BLS II（如上所述）。两种类型的BLS都会导致持续的，危及生命的感染。（换行）参与BLS III的RFXAPgene突变可能损害RFX复合物的功能，阻止MHC II类gene的转录并降低MHC I类gene的活性。没有这些gene产生的重要免疫系统蛋白质，身体就不太能够抵抗感染。目前尚不清楚为什么某些RFXAPgene突变会损害MHC I类蛋白的产生，而另一些则不会。"}]}' WHERE id = 9229;
UPDATE genes SET GHR_metadata_ch = '{"gene": "PRKN", "conditions": [{"name": "帕金森病", "detail": "研究人员已经鉴定出200多种引起帕金森氏病的PRKNgene突变，帕金森氏病的特征是运动和平衡方面的进行性问题。该gene的突变与20岁以前出现的青少年型帕金森病以及50岁以后开始的一些更常见的晚发型帕金森病有关。（换行）一些PRKNgene突变导致异常小的parkin蛋白，该蛋白无功能并在细胞内迅速分解（降解）。其他突变会在PRKNgene中插入，删除或更改DNA构建块（核苷酸），从而导致parkin蛋白的缺陷版本或阻止该蛋白的产生。与帕金森氏病相关的PRKNgene突变通常会导致帕金活性丧失。(换行)目前尚不清楚PRKNgene突变如何引起帕金森氏病。帕金活性的丧失可能会干扰泛素-蛋白酶体系统，从而使不需要的蛋白质积聚。这些蛋白质的积累可能破坏正常的细胞活动，例如突触小泡的供应和释放，特别是那些含有称为多巴胺的化学信使的细胞活动。由于帕金通常在大脑中含量丰富，因此其丢失可能导致神经细胞（包括产生多巴胺的神经细胞）受损或死亡。产生多巴胺的神经细胞的丧失是帕金森病的特征。（换行）PRKNgene中的突变也可能破坏线粒体的调节。研究人员推测，产生多巴胺的神经细胞中的线粒体功能障碍可能在引起帕金森病的体征和症状中起重要作用。"}, {"name": "肺癌", "detail": "遗传学家庭参考提供有关肺癌的信息。"}, {"name": "卵巢癌", "detail": "遗传学家庭参考提供有关卵巢癌的信息。"}, {"name": "癌症", "detail": "PRKNgene跨越6号染色体上称为FRA6E的区域的一部分。该区域被称为脆弱区域，因为它不稳定并且易于破裂和重排。已经报道了几种形式的人类癌症中涉及FRA6E区域的变化，包括成胶质细胞瘤（一种脑癌），结肠直肠癌，肺癌和卵巢癌。在某些这些癌性肿瘤中，FRA6E区的片段（包括部分或全部PRKNgene）被异常删除或复制。这些遗传变化被描述为体细胞，因为它们仅在肿瘤细胞中发生并且不遗传。这些改变的结果是，这些细胞中的帕金活性降低或不存在。由于人们认为帕金可以起到抑癌作用，因此该蛋白功能的不足可能会使细胞以不受控制的方式生长和分裂，从而导致肿瘤形成。"}, {"name": "其他疾病", "detail": "研究表明，PRKNgene（以及称为PACRG的邻近gene）中的常见变异（多态性）会增加患汉森病（也称为麻风病）的风险。该疾病影响神经和皮肤，由细菌麻风分枝杆菌引起。尚不清楚PRKN多态性如何增加对汉森病的易感性。研究人员认为泛素-蛋白酶体系统可能在控制感染中发挥作用。PRKNgene的多态性可能会巧妙地改变parkin的功能，从而使泛素-蛋白酶体系统效率降低。"}]}' WHERE id = 9249;
UPDATE genes SET GHR_metadata_ch = '{"gene": "COL5A2", "conditions": [{"name": "埃勒斯-当洛二氏综合症", "detail": "COL5A2gene的突变会导致一种称为经典型的Ehlers-Danlos综合征，这种疾病的特征是皮肤柔软，高度伸展（弹性），脆弱，异常疤痕和活动过度。这些突变严重改变了pro-？2（V）链导致皮肤和其他组织中含有V型和I型胶原蛋白的原纤维组装紊乱。"}]}' WHERE id = 9277;
UPDATE genes SET GHR_metadata_ch = '{"gene": "AKR1D1", "conditions": [{"name": "先天性胆汁酸合成缺陷2型", "detail": "已经发现AKR1D1gene中的几个突变导致先天性胆汁酸合成缺陷2型，该病症的特征在于由于缺陷酶引起的胆汁淤积，其损害7-羟基-4-胆甾烯-3-酮至7-羟基-5-cholesten-3-one，而是转化为异常的胆汁酸化合物，导致其在肝脏中积聚。"}]}' WHERE id = 9341;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ATP8B1", "conditions": [{"name": "良性复发性肝内胆汁淤积", "detail": "已经发现ATP8B1gene的突变是良性复发性肝内胆汁淤积1型（BRIC1），其特征是偶尔发生胆汁分泌受损，导致严重瘙痒（瘙痒）和黄疸。这是由于ATP8B1蛋白的结构和功能改变导致胆汁在肝细胞中蓄积所致。"}, {"name": "进行性家族性肝内胆汁淤积", "detail": "ATP8B1gene的突变已被发现会导致一种严重的肝脏疾病，称为进行性家族性肝内胆汁淤积症1型（PFIC1），这种疾病的特征是身材矮小、耳聋、腹泻、胰腺炎、成年期血液和肝衰竭中脂溶性维生素（维生素a、D、E和K）水平低。这是由于ATP8B1蛋白的结构和功能改变导致胆汁在肝细胞中蓄积所致。"}]}' WHERE id = 9348;
UPDATE genes SET GHR_metadata_ch = '{"gene": "PDE6C", "conditions": [{"name": "色盲", "detail": "已发现PDE6Cgene中至少有19个突变引起视力障碍色盲。这些突变是相对较小比例的完全性色盲的基础，该色盲是一种以婴儿早期完全缺乏色觉和其他视觉问题为特征的疾病。PDE6Cgene突变也已在少数色盲不完全的个体中发现，这是一种与色觉受限相关的轻度疾病。（换行）与色盲相关的PDE6Cgene突变影响α-prime亚基的功能。完全失色症的基础突变基本上消除了视锥细胞特异性磷酸二酯酶的活性。这种酶的损伤破坏了视锥细胞的光转导过程；杆通常不受影响。由于色觉需要视锥细胞，因此完全色盲的人只能看到黑色，白色和灰色阴影。他们还存在与视锥细胞功能异常有关的其他视觉问题，包括锐度降低（视力低下），对光的敏感性增加（畏光）以及不自主的来回眼球运动（眼球震颤）。PDE6Cgene中的（换行）突变会降低但不会消除视锥细胞特异性磷酸二酯酶的活性，从而导致不完全的色盲。与完全色盲的人相比，不完全色盲的人具有相似但不太严重的视力问题，并且他们保留了一些色觉。"}, {"name": "视锥-视杆营养不良", "detail": "遗传学家庭参考提供有关锥杆营养不良的信息。"}]}' WHERE id = 9440;
UPDATE genes SET GHR_metadata_ch = '{"gene": "DYRK1A", "conditions": [{"name": "自闭症谱系障碍", "detail": "自闭症谱系障碍（ASD）患者中至少鉴定出11种DYRK1Agene突变，这是一种以社交技能受损，沟通问题和重复行为为特征的多种疾病。大多数与DYRK1Agene突变相关的ASD患者也有其他体征和症状。在某些情况下，它们具有其他功能的特定组合，包括智力残疾，言语问题，焦虑症和异常小头（小头畸形）。这些人可能出现的其他体征和症状包括反复发作（癫痫），特征性的面部特征，肌张力弱（肌张力低下），足部异常和行走问题（步态障碍）。这种体征和症状有时被称为DYRK1A相关的智力障碍综合症。（换行）DYRK1Agene突变导致DYRK1A酶或功能异常的酶丢失。DYRK1A酶功能受损或缺失可能导致gene表达异常调节并破坏正常的神经发育。但是，尚不清楚DYRK1Agene突变与ASD的体征和症状之间的具体关系，以及具有这些突变的人可能出现的其他特征。"}]}' WHERE id = 9441;
UPDATE genes SET GHR_metadata_ch = '{"gene": "MCEE", "conditions": [{"name": "甲基丙二酸血症", "detail": "已发现MCEEgene中至少有3个突变会引起甲基丙二酸血症，这种疾病的特征是进食困难，发育迟缓和长期健康问题。这些突变被认为导致产生功能很少或没有功能的甲基丙二酰辅酶a差向异构酶。由MCEEgene突变引起的甲基丙二酸血症患者的体征和症状通常比其他gene突变引起的患者轻。这些特征可能更温和，因为存在将丙酰辅酶A转化为琥珀酰辅酶A的替代途径，该途径不涉及甲基丙二酰辅酶A差向异构酶，因此即使MCEEgene中存在突变，也会产生一些琥珀酰辅酶A。这种替代途径无法补偿常规途径中发生的某些分子的分解，因此具有MCEEgene突变的人仍然具有某些氨基酸和某些脂肪的副产物的积累。结果，这些有毒化合物积聚在人体的器官和组织中，引起甲基丙二酸血症的体征和症状。"}]}' WHERE id = 9478;
UPDATE genes SET GHR_metadata_ch = '{"gene": "NFU1", "conditions": [{"name": "多发性线粒体功能障碍综合征", "detail": "NFU1gene的突变可导致多种线粒体功能障碍综合征。这种严重疾病的特征是损害一种以上的线粒体功能，例如复合物I，II或III，丙酮酸脱氢酶，α-酮戊二酸脱氢酶或甘氨酸裂解系统的活性降低。患病的婴儿通常患有严重的脑功能障碍（脑病）和体内称为乳酸的化学物质水平升高（乳酸性酸中毒）。这些婴儿通常无法在婴儿期后存活。（换行）NFU1gene突变导致产生改变的NFU-1蛋白，其可能快速分解。尽管某些突变会影响NFU-1蛋白的两种同工型，但线粒体版本的丢失似乎是造成这种情况的原因。线粒体NFU-1蛋白的缺乏会损害Fe-S簇的形成。因此，受Fe-S簇存在影响的蛋白质，包括那些参与能量产生和甘氨酸分解的蛋白质，不能正常发挥作用。复合物I，II或III，丙酮酸脱氢酶或α-酮戊二酸脱氢酶的活性降低会导致潜在的致命性乳酸性酸中毒，脑病以及多种线粒体功能障碍综合征的其他体征和症状。在一些受影响的个体中，甘氨酸裂解系统的损伤导致甘氨酸的积累（高甘氨酸血症）。"}]}' WHERE id = 9479;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ASAH1", "conditions": [{"name": "法伯脂肪肉芽肿病", "detail": "已发现ASAH1gene中至少有20个突变引起Farber脂肪肉芽肿病。这种情况的特征是脂肪（脂质）在全身细胞中积累，特别是在关节周围。与Farber脂肪肉芽肿病相关的大多数突变会改变酸性神经酰胺酶中的单个蛋白质结构单元（氨基酸），从而严重降低酶的活性，通常不到正常水平的十分之一。结果，该酶不能适当地分解神经酰胺，并且它们积聚在各种细胞的溶酶体中，包括在肺，肝，肌肉，脑，软骨和骨中。目前尚不清楚神经酰胺的积累如何损害细胞的正常功能，但这些受损的细胞导致了Farber脂肪肉芽肿病特有的声音，皮肤和关节问题。神经酰胺影响各种细胞功能，这些过程的异常调节也可能导致这种情况的特征。"}, {"name": "嵴髓性肌肉萎缩症伴进行性肌阵挛性癫痫", "detail": "已发现ASAH1gene中至少有四个突变会导致进行性肌阵挛性癫痫（SMA-PME）引起脊髓性肌萎缩症。这种情况的特征是肌肉无力和消瘦（萎缩），以及从儿童期开始的癫痫发作和无法控制的肌肉抽搐（肌阵挛性癫痫）的结合。导致SMA-PME的ASAH1gene突变导致酸性神经酰胺酶活性降低至低于正常水平的三分之一。酸性神经酰胺酶活性的降低导致神经酰胺的低效分解和其分解产物鞘氨醇和脂肪酸的产生受损。神经酰胺的增加以及鞘氨醇和脂肪酸的减少可能在SMA-PME特征的发展中起作用，但确切的机制尚不清楚。（换行）与SMA-PME相关的酸性神经酰胺酶活性的降低小于在另一种称为Farber脂肉芽肿病的情况下发生的情况（如上所述）。研究人员怀疑SMA-PME中少量的酶活性会导致某些神经酰胺分解，因此神经酰胺不会像Farber脂肪肉芽肿病那样广泛积累和破坏细胞。但是，由于SMA-PME非常罕见，因此酶变化的影响仍不清楚。"}]}' WHERE id = 9544;
UPDATE genes SET GHR_metadata_ch = '{"gene": "RFXANK", "conditions": [{"name": "裸淋巴细胞综合征II型", "detail": "RFXANKgene中的突变是称为裸淋巴细胞综合征II型（BLS II）的免疫系统疾病的最常见遗传原因。在受影响的个体中已鉴定出该gene的40多个突变。BLS II是一种联合免疫缺陷病（CID），其中受影响的个体实际上没有针对外来入侵者的免疫保护。因此，患有BLS II的个体在呼吸道，胃肠道和泌尿道中持续感染，这可能危及生命。RFXANKgene中的（换行）突变导致产生可能无法正常运行的改变的蛋白质。这些变化损害RFX复合物与DNA的结合，这阻止了MHC II类蛋白的转录。因此，淋巴细胞在其表面缺乏任何MHC II类蛋白，并且身体难以除去细菌，病毒和真菌，导致BLS II特有的持续感染。"}]}' WHERE id = 9569;
UPDATE genes SET GHR_metadata_ch = '{"gene": "DDX11", "conditions": [{"name": "华沙破损综合症", "detail": "已发现DDX11gene中至少有三个突变引起华沙断裂综合征。这种情况会导致多种异常，包括生长受损，独特的面部特征，听力下降和心脏畸形。导致华沙断裂综合征的突变严重降低或完全消除了ChlR1酶的活性。结果，该酶不能与DNA结合，也不能解开DNA链以帮助DNA复制和修复。缺乏功能性ChlR1会损害细胞分裂并导致DNA损伤的积累。这种DNA损伤可能表现为DNA中的断裂，从而使该病症得名。目前尚不清楚DNA维持中的这些问题如何导致华沙断裂综合征的特定异常特征。"}]}' WHERE id = 9581;
UPDATE genes SET GHR_metadata_ch = '{"gene": "GP1BB", "conditions": [{"name": "伯纳德-苏里尔综合症", "detail": "已发现至少32个GP1BBgene突变会引起Bernard-Soulier综合征，该病的特征是血小板数量比正常人减少（大血小板减少症）和出血过多。这些突变导致产生改变的GPIb？可能过早分解或无法到达血小板表面的亚基。血小板表面缺乏该亚基会阻止GPIb-IX-V复合物的形成。没有GPIb-IX-V，血小板不能在损伤部位聚集形成凝块，导致与Bernard-Soulier综合征相关的出血问题。"}]}' WHERE id = 9597;
UPDATE genes SET GHR_metadata_ch = '{"gene": "POLH", "conditions": [{"name": "着色性干皮症", "detail": "已发现POLHgene中的30多个突变会导致色素干皮症（XP-V）的变异类型。像其他形式的色素干皮症一样，XP-V的特征是对日光紫外线的敏感性增加。但是，这种形式的疾病通常与神经系统异常（例如发育迟缓和听力下降）无关。(换行)大多数POLHgene突变会阻止任何可检测的DNA聚合酶eta的产生。这种酶的丢失会阻止细胞有效复制受损的DNA。如果没有这种耐受DNA损伤的机制，则由于暴露于紫外线而导致的错误会累积在控制细胞生长和分裂的gene中。这些错误会导致细胞生长过快且无法控制。结果，患有XP-V的人在皮肤暴露在阳光下的区域患皮肤癌的风险增加。"}]}' WHERE id = 9661;
UPDATE genes SET GHR_metadata_ch = '{"gene": "TAZ", "conditions": [{"name": "巴特综合征", "detail": "已发现TAZgene中有160多个突变引起Barth综合征。这种罕见病几乎只发生在男性中，其特征是心脏肿大和虚弱（扩张型心肌病），肌肉无力，反复感染和身材矮小。导致Barth综合征的TAZgene突变导致产生功能很少或没有功能的tafazzin蛋白。结果，亚油酸不添加到心磷脂中，这导致正常线粒体形状和功能的问题，例如能量产生和蛋白质转运。由于线粒体能量产生减少，具有高能量需求的组织（例如心脏和其他肌肉）最容易发生细胞死亡。此外，受影响的白细胞具有异常形状的线粒体，这可能损害其生长（增殖）和成熟（分化）的能力，导致免疫系统减弱和反复感染。线粒体功能异常可能导致Barth综合征的其他体征和症状。"}, {"name": "家族性扩张型心肌病", "detail": "遗传学家庭参考提供有关家族性扩张型心肌病的信息。"}, {"name": "室致密化不全", "detail": "遗传学家庭参考提供有关左心室不紧致的信息。"}, {"name": "其他疾病", "detail": "TAZgene中的某些突变会导致扩张型心肌病，而没有Barth综合征的其他特征（如上所述）。扩张型心肌病是心脏变弱和扩大并且不能有效泵血的病症，通常导致心力衰竭。血流量减少可导致腿部和腹部肿胀，肺部积液，血栓风险增加。TAZgene中的（换行）突变也可引起称为孤立性左心室心肌致密化不全（INVM）的心脏病。当心脏的左下腔（左心室）未正确发育时，就会出现这种情况。在INVM中，心肌减弱，无法有效地抽血。也可能发生异常心律（心律失常）。INVM经常导致心力衰竭。"}]}' WHERE id = 9682;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SLC25A19", "conditions": [{"name": "阿米什人致命的小头畸形", "detail": "所有已知的具有阿米什人致死性小头畸形的个体都有一个突变，其中蛋白质结构单元（氨基酸）丙氨酸取代了SLC25A19蛋白177位的氨基酸甘氨酸，命名为Gly177Ala或G177A。研究人员认为，这种突变会干扰硫胺素焦磷酸盐向线粒体的转运以及α-酮戊二酸脱氢酶复合物的活性，从而导致大脑发育异常和阿米什人致死性小头畸形的尿液中α-酮戊二酸过量。"}, {"name": "利综合征", "detail": "遗传学家庭参考提供有关Leigh综合征的信息。"}]}' WHERE id = 9695;
UPDATE genes SET GHR_metadata_ch = '{"gene": "HBB", "conditions": [{"name": "β地中海贫血", "detail": "已发现HBBgene中近400个突变引起β地中海贫血。大多数突变涉及HBBgene内或附近的单个DNA构建块（核苷酸）的变化。其他突变会插入或删除HBBgene中的少量核苷酸。（换行）降低β珠蛋白产生的HBBgene突变导致一种称为β加（B+）地中海贫血的病症。阻止细胞产生任何β珠蛋白的突变会导致β零（B0）地中海贫血。（换行）构成血红蛋白的亚基的问题，包括低水平的β-珠蛋白，减少或消除该分子的产生。缺乏血红蛋白会破坏红细胞的正常发育。成熟红细胞的短缺可以将输送到组织的氧气量减少到满足身体能量需求所需的量以下。身体组织缺氧会导致生长不良，器官损伤以及与β地中海贫血相关的其他健康问题。"}, {"name": "高铁血红蛋白血症，β-珠蛋白型", "detail": "已发现HBBgene中的10多个突变会引起高铁血红蛋白血症（β珠蛋白类型），这是一种改变红细胞内血红蛋白的疾病。这些突变通常影响与血红素结合的蛋白质区域。为了使血红蛋白与氧结合，血红素分子内的铁需要呈亚铁（Fe2+）的形式。血红素中的铁可以变成另一种称为三价铁（Fe3+）的铁，它不能结合氧。含有三价铁的血红蛋白被称为高铁血红蛋白，不能有效地将氧气输送到身体组织。（换行）在高铁血红蛋白血症中，β珠蛋白类型，HBBgene突变改变β珠蛋白并促进血红素铁从亚铁变为铁。这种改变的血红蛋白使血液呈棕色，并导致皮肤，嘴唇和指甲呈蓝色（紫绀）。高铁血红蛋白血症，β-珠蛋白类型的体征和症状通常仅限于紫绀，不会引起任何健康问题。但是，在极少数情况下，严重的高铁血红蛋白血症（β珠蛋白类型）会引起头痛，虚弱和疲劳。"}, {"name": "镰状细胞病", "detail": "镰状细胞性贫血是镰状细胞病的一种常见形式，是由HBBgene的特定突变引起的。这种突变导致产生称为血红蛋白S或HbS的异常形式的β-珠蛋白。在这种情况下，血红蛋白S取代血红蛋白中的两个β-珠蛋白亚基。该突变改变了β珠蛋白中的单个蛋白质结构单元（氨基酸）。具体地，将氨基酸谷氨酸替换为β-珠蛋白中第6位的氨基酸缬氨酸，写为Glu6Val或E6V。用缬氨酸代替谷氨酸会导致异常的血红蛋白S亚基粘在一起并形成长而刚性的分子，从而使红细胞弯曲成镰状（新月形）。镰状细胞过早死亡，可导致红细胞短缺（贫血）。镰状细胞坚硬，可阻塞小血管，造成严重疼痛和器官损伤。（换行）HBBgene突变也可引起β珠蛋白的其他异常，导致其他类型的镰状细胞病。这些异常形式的β-珠蛋白通常由字母表的字母或有时由name指定。在这些其他类型的镰状细胞病中，只有一个β-珠蛋白亚基被血红蛋白S取代。另一个β-珠蛋白亚基被不同的异常变异取代，例如血红蛋白C或血红蛋白E.（换行）血红蛋白SC（HbSC）疾病，β-珠蛋白亚基被血红蛋白S和血红蛋白C取代。当氨基酸赖氨酸取代β-珠蛋白（书面Glu6Lys或E6K）中第6位的氨基酸谷氨酸时，血红蛋白C产生。血红蛋白SC疾病的严重程度是可变的，但它可能与镰状细胞性贫血一样严重。当β-珠蛋白（书面Glu26Lys或E26K）中第26位的氨基酸赖氨酸取代氨基酸谷氨酸时，就会引起血红蛋白E（HbE）。在一些情况下，血红蛋白E突变与血红蛋白S一起存在。在这些情况下，人可能具有与镰状细胞性贫血相关的更严重的体征和症状，例如疼痛发作，贫血和脾功能异常。（换行）当产生血红蛋白S和β地中海贫血的突变一起发生时，引起称为血红蛋白镰状β地中海贫血（HbSBetaThal）的其他病症。镰状细胞病与β零（B0）地中海贫血相结合的突变会导致严重的疾病，而镰状细胞病与β加（B+）地中海贫血相结合的疾病则较轻。"}, {"name": "其他疾病", "detail": "在HBBgene中已经鉴定出数百种变异。这些变化导致产生不同版本的β-珠蛋白。其中一些变化不会引起明显的体征或症状，并且由于其他原因进行血液检查时会发现，而其他变化可能会影响一个人的健康。两种最常见的变体是血红蛋白C和血红蛋白E.（换行）血红蛋白C（HbC），由β-珠蛋白中的Glu6Lys突变引起，在西非血统的人群中比在其他人群中更常见。在血红蛋白中有两个血红蛋白C亚基而不是正常β-珠蛋白的人患有称为血红蛋白C病的轻度病症。这种情况经常导致慢性贫血，其中红细胞过早分解。（换行）由β珠蛋白中的Glu26Lys突变引起的血红蛋白E（HbE）是东南亚人群中最常见的血红蛋白变体。当一个人的血红蛋白中有两个血红蛋白E亚基代替β-珠蛋白时，就会发生称为血红蛋白E病的轻度贫血。在某些情况下，一起发现产生血红蛋白E和β地中海贫血的突变。具有这种血红蛋白组合的人可以具有从轻度贫血到重度地中海贫血的体征和症状。"}]}' WHERE id = 9719;
UPDATE genes SET GHR_metadata_ch = '{"gene": "PC", "conditions": [{"name": "丙酮酸羧化酶缺乏症", "detail": "在丙酮酸羧化酶缺乏症患者中已鉴定出PCgene的30多个突变。这种情况会导致乳酸和其他潜在有毒化合物积聚在血液中。高水平的这些物质会损害身体的器官和组织，特别是在神经系统中。(换行)大多数PCgene突变会改变丙酮酸羧化酶中的单个蛋白质结构单元（氨基酸），从而减少细胞中该酶的量或破坏其将丙酮酸有效转化为草酰乙酸的能力。其他遗传变化导致产生完全无功能的异常短版本的酶。（换行）如果丙酮酸羧化酶缺失或改变，它不能发挥其产生葡萄糖的作用。糖异生的任何破坏都会损害人体在线粒体中产生能量的能力。另外，丙酮酸羧化酶的损失允许乳酸和氨以及其他化合物积聚并损伤器官和组织。研究人员认为，神经系统中丙酮酸羧化酶功能的丧失，特别是该酶在髓磷脂形成和神经递质产生中的作用，也有助于丙酮酸羧化酶缺乏症的神经系统特征。"}]}' WHERE id = 9722;
UPDATE genes SET GHR_metadata_ch = '{"gene": "CEP290", "conditions": [{"name": "莱伯先天性黑蒙", "detail": "已发现CEP290gene中的几个突变会导致Leber先天性黑ama病（LCA），这是一种主要影响视网膜的眼部疾病，导致婴儿期严重和早期视力障碍。其他视觉异常包括畏光，眼睛不自主运动（眼球震颤）和极度远视（远视）。CEP290突变导致较短或无功能的蛋白质产生，从而损害视网膜感光细胞中的纤毛，从而导致LCA。"}, {"name": "巴德-毕德氏症候群", "detail": "CEP290gene的一些突变与巴德-毕德氏症候群有关，巴德-毕德氏症候群是一种眼部疾病，也影响身体的许多部位，它是由于视网膜退化导致的拥有属性视力丧失，其他眼部异常包括周边视力发展的盲点和儿童中期中心视力模糊，青春期或成年早期合法失明。其他临床特征包括导致2型糖尿病的肥胖，高血压，高胆固醇血症，多指，智力障碍，肾脏和生殖器异常。"}, {"name": "伯特症候群", "detail": "CEP290gene中的一些突变与茹贝尔综合征有关，茹贝尔综合征是一种影响身体许多部位的疾病，它是大脑异常的拥有属性组合，一起被称为臼齿征。其他临床特征包括低张力、共济失调、婴儿期呼吸异常快（呼吸过度）或慢（呼吸暂停）、眼部运动失用、，发育迟缓和智力残疾，独特的面部特征，其他眼部异常，例如视网膜营养不良和淋巴瘤，肾脏疾病和肝脏异常。"}, {"name": "Meckel综合征", "detail": "CEP290gene的一些突变与梅克尔综合征有关，梅克尔综合征是一种影响身体许多部位的疾病，它的拥有属性是肾脏肿大，有大量充满液体的囊肿，枕叶脑膨出，多指畸形，肝脏纤维化，大脑和脊髓异常，包括出生缺陷，称为神经管缺陷，眼睛和其他面部特征，心脏，骨骼，泌尿系统和生殖器的发育异常。"}, {"name": "高级Løken综合征", "detail": "CEP290gene中的几个突变与Senior-Løken综合征有关，Senior-Løken综合征是一种罕见疾病，其特征是称为肾炎的肾脏疾病和称为Leber先天性黑ama病的眼部疾病相结合。"}, {"name": "其他疾病", "detail": "在与纤毛异常相关的其他综合症（称为纤毛病）中，已经鉴定出CEP290gene的几个突变，包括Joubert综合症，Meckel综合症，Senior-Løken综合症和Bardet-Biedl综合症。这些疾病的特征明显重叠，并且是由于CEP290突变破坏纤毛功能引起的。"}]}' WHERE id = 9731;
UPDATE genes SET GHR_metadata_ch = '{"gene": "CDKN2A", "conditions": [{"name": "头颈部鳞状细胞癌", "detail": "CDKN2Agene的体细胞突变与头颈部鳞状细胞癌（HNSCC）有关，头颈部鳞状细胞癌是一种发生在口腔，鼻子和喉咙潮湿内层的癌性肿瘤。突变导致部分或无功能的p16（INK4a）蛋白产生或引起启动子高甲基化，从而关闭p16（INK4a）或p14（ARF）的产生，从而导致肿瘤抑制活性的丧失。"}, {"name": "肺癌", "detail": "CDKN2Agene的体细胞突变与肺癌有关，这种情况会导致肺部某些细胞异常生长，并且在早期阶段不会出现症状。一些受影响的个体表现出胸痛，频繁咳嗽，粘液中的血液，呼吸问题，吞咽或说话困难，食欲不振和体重减轻，疲劳或面部或颈部肿胀。"}, {"name": "其他癌症", "detail": "CDKN2Agene的遗传突变与其他癌症有关，包括黑色素瘤，乳腺癌，肺癌和胰腺癌。突变与单一类型的癌症有关，或者可能导致家庭中具有多种类型癌症的癌症易感综合征。这些是由于受损或无功能的p16（INK4a）或较少见的导致肿瘤形成的p14（ARF）蛋白引起的。"}]}' WHERE id = 9749;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SLC34A2", "conditions": [{"name": "肺泡微结石", "detail": "在肺泡微石症患者中已鉴定出至少18个SLC34A2gene突变，该疾病中许多称为磷酸钙的化合物的微小碎片（微石）逐渐积聚在肺泡中。这些沉积物最终导致广泛的肺损伤（间质性肺病），导致呼吸问题。（换行）SLC34A2gene突变被认为会损害IIb型磷酸钠共转运蛋白的活性，导致磷酸盐在肺泡中积聚。积累的磷酸盐形成导致肺泡微结石的体征和症状的微晶石。"}]}' WHERE id = 9762;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SP110", "conditions": [{"name": "伴有免疫缺陷的肝静脉闭塞性疾病", "detail": "已经发现SP110gene中的至少五个突变引起具有免疫缺陷（VODI）的肝静脉闭塞性疾病。这些突变中的每一个都导致产生SP110核体蛋白的非功能性形式。缺乏功能性蛋白质会损害免疫系统抵御外来入侵者的能力，从而导致复发性和持续性感染的发展。目前尚不清楚SP110核体蛋白的丢失如何破坏肝脏中的血流，导致肝脏肿大（肝肿大），瘢痕组织积聚（肝纤维化）和肝功能衰竭。"}, {"name": "其他疾病", "detail": "已经研究了SP110gene中的几种常见变异（多态性）作为肺（肺）结核的危险因素。这种疾病是一种传染性细菌感染。至少有一项研究发现，SP110gene的某些变异可能会影响感染引起肺结核的细菌的风险。但是，其他几项研究尚未发现这种关联。SP110变异可能不是该疾病的主要遗传危险因素。"}]}' WHERE id = 9769;
UPDATE genes SET GHR_metadata_ch = '{"gene": "DSG4", "conditions": [{"name": "常染色体隐性遗传不足", "detail": "已发现DSG4gene中至少有10个突变会引起常染色体隐性遗传不足，这种情况会导致头皮上稀疏的头发生长（发育不足），而身体其他部位的发生频率较低。删除DSG4gene中一条遗传物质的特定突变（写为Ex5_8）是巴基斯坦血统个体患病的常见原因。这种突变削弱了蛋白质帮助细胞彼此附着的能力。其他DSG4gene突变会导致异常DSG4蛋白的产生，该蛋白无法在毛囊或皮肤内的细胞之间传递信号。结果，毛囊在结构上异常并且通常不发达。不规则的毛囊会改变毛干的结构和生长，导致脆弱的头发容易断裂。缺乏正常的DSG4蛋白功能可能会削弱皮肤，并导致有时在常染色体隐性遗传不足的个体中出现的皮肤问题。"}, {"name": "Monilethrix", "detail": "在患有monilethrix的人中发现了DSG4gene的突变，monilethrix是一种头发状况，其特征是头发呈串珠状。头发也短，脆，容易断裂。与这种情况相关的突变可以影响DSG4蛋白的任何部分，但是这些变化通常会改变细胞外结构域，该结构域是细胞外与其他细胞相互作用的蛋白区域。在患有monilethrix的人中，受影响的毛干的皮质似乎异常。但是，尚不清楚DSG4gene的突变如何与皮质异常或头发的珠状外观相关。(换行)尚不清楚为什么某些具有DSG4gene突变的个体会发展为monilethrix，而另一些个体会发展为常染色体隐性遗传不足（如上所述）。这些情况可能代表同一疾病的不同形式。"}]}' WHERE id = 9781;
UPDATE genes SET GHR_metadata_ch = '{"gene": "TBXT", "conditions": [{"name": "脊索瘤", "detail": "TBXTgene的变化与脊索瘤有关。脊索瘤是沿着脊柱或颅底发生的癌性肿瘤，逐渐进一步延伸到骨骼和附近的软组织中。肿瘤从脊索的残余细胞发展而来，开始不受控制地生长和分裂。（换行）在几个家族中鉴定的TBXTgene的遗传重复与发展脊索瘤的风险增加相关。在家族中没有该病史的脊索瘤患者中也发现了TBXTgene的重复或活性（表达）增加。在这些个体中，变化仅发生在肿瘤细胞中，而不是遗传的。（换行）重复和TBXTgene表达的增加均导致产生过量的brachyury蛋白。过量的brachyury蛋白促成脊索瘤发展的具体机制尚不清楚。"}, {"name": "其他疾病", "detail": "TBXTgene的变异与神经管缺陷（包括脊柱裂）的风险增加有关。当神经管在胚胎发育的最初几周未能完全闭合时，会导致神经管缺陷，从而导致严重程度不同的脊柱和大脑异常。（换行）与神经管缺陷相关的TBXTgene变异（称为TIVS7 T/C）改变了称为内含子7的gene区域中的单个DNA构建块（核苷酸）。这种遗传变化的影响尚不清楚。研究人员认为，它可能会影响早期发育中brachyury蛋白产生的调节，增加神经管缺陷的风险。"}]}' WHERE id = 9795;
UPDATE genes SET GHR_metadata_ch = '{"gene": "PLCB4", "conditions": [{"name": "耳con突综合征", "detail": "已发现PLCB4gene中至少有9个突变会引起耳con突综合征，这种疾病主要影响耳朵和下颌（下颌骨）的发育。鉴定出的突变改变了磷脂酶C的β4同工型中的单个蛋白质结构单元（氨基酸）。这些变化可能会改变蛋白质的结构并损害磷酸肌醇的循环。异常信号会改变下颌的形成：下颌的形状不像上颌骨（上颌骨）那样正常发育，而不是正常发育。形状异常会导致下巴异常小（微棘皮症）和下颌功能问题。研究人员正在努力确定该gene的突变如何导致与耳con突综合征相关的其他发育异常。"}]}' WHERE id = 9840;
UPDATE genes SET GHR_metadata_ch = '{"gene": "GNPTG", "conditions": [{"name": "粘脂病IIIγ", "detail": "已发现GNPTGgene中至少有8个突变会引起粘膜脂溢性疾病IIIγ。这些突变导致GlcNAc-1-磷酸转移酶的活性降低，从而破坏了用M6P标记水解酶的过程。不接受M6P标签的消化酶最终进入细胞外，在那里它们的活性增加。溶酶体内这些消化酶的缺乏导致大分子在那里积累。导致分子在溶酶体内积聚的conditions，包括粘液脂血症IIIγ，被称为溶酶体贮积病。粘脂病IIIγ的体征和症状很可能是由于溶酶体内缺乏水解酶以及这些酶在细胞外的作用所致。"}]}' WHERE id = 9873;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ADAMTS13", "conditions": [{"name": "血栓性血小板减少性紫癜", "detail": "由于有缺陷/无功能的ADAMTS13酶削弱了其在血流中正常处理von Willebrand因子的活性，导致全身凝块异常，因此在家族性血栓性血小板减少性紫癜患者中报告了ADAMTS13gene的几种突变。这种异常的凝块阻塞血液流过小血管，对大脑，肾脏，心脏和其他器官造成损害。此外，怀孕，腹泻，手术和感染等因素也会引发异常凝血。"}]}' WHERE id = 9882;
UPDATE genes SET GHR_metadata_ch = '{"gene": "NPHP1", "conditions": [{"name": "肾单位", "detail": "已发现NPHP1gene中至少有23个突变会引起称为1型肾炎的肾脏疾病。1型是最常见的肾炎类型，约占该疾病病例的20%。Nephronophthisis的特征是肾脏发炎和瘢痕形成，最终导致危及生命的肾功能衰竭（终末期肾病或ESRD）。在1型肾病中，ESRD通常发生在13岁左右。（换行）1型肾炎最常见的遗传变化是2号染色体上的一个大缺失，它删除了整个NPHP1gene。去除全部或大部分gene或一小部分gene的其他缺失也可能与这种情况有关。一些突变会改变单个蛋白质的结构单元（氨基酸）或导致异常短的nephrocystin-1蛋白。据认为，患有1型肾炎的人很少或没有功能性nephrocystin-1。缺乏这种蛋白质可能会以某种方式损害纤毛的功能，这可能会破坏发育过程中重要的化学信号传导途径。尽管研究人员认为纤毛缺陷是造成肾炎的原因，但其机制仍不清楚。"}, {"name": "伯特症候群", "detail": "遗传学家庭参考提供有关Joubert综合征的信息。"}, {"name": "高级Løken综合征", "detail": "遗传学家庭参考资料提供有关高级Løken综合征的信息。"}, {"name": "其他疾病", "detail": "NPHP1gene的突变可导致综合征，其中包括肾病；这种情况称为肾炎相关纤毛病。高级Løken综合征的特征是肾炎和视网膜破裂（视网膜变性）相结合。Joubert综合征是一种多系统疾病，通常涉及神经系统问题，可能包括肾炎，眼部异常，肝脏疾病和其他异常。与nephronophthisis 1型（如上所述）一样，NPHP1gene突变可能会损害纤毛功能。纤毛缺陷可能会导致这些与肾炎相关的纤毛病的特征，尽管目前尚不清楚这些缺陷如何导致每种疾病的特定异常。尚不清楚为什么某些具有NPHP1gene突变的个体会出现Senior-Løken综合征或Joubert综合征的其他体征和症状。"}]}' WHERE id = 9935;
UPDATE genes SET GHR_metadata_ch = '{"gene": "TBX1", "conditions": [{"name": "22q11.2缺失综合征", "detail": "大多数22q11.2缺失综合征病例是由22号染色体的一小块缺失引起的。染色体的这一区域包含30至40个gene，包括TBX1gene。在少数没有22号染色体缺失的受影响个体中，TBX1gene的突变被认为是该综合征的特征性体征和症状的原因。鉴定出的突变包括TBX1gene中单个DNA构建块（碱基对）的变化以及该gene中少量遗传物质的缺失。这些突变中的一些减少了细胞中产生的T-box 1蛋白的量，而其他突变则改变了该蛋白的功能。这些遗传变化可能影响T-box 1蛋白与DNA结合并调节其他gene活性的能力。(换行)研究人员认为，由于gene突变或22号染色体部分缺失，TBX1gene的变化是22q11.2缺失综合征的许多特征的原因。具体而言，T-box 1数量的减少或蛋白质正常功能的改变与心脏缺陷，口腔顶部开口（腭裂），独特的面部特征，听力损失和低钙水平有关。一些研究表明，TBX1gene的缺失也可能与受影响个体的行为问题有关。"}]}' WHERE id = 9940;
UPDATE genes SET GHR_metadata_ch = '{"gene": "NPRL2", "conditions": [{"name": "病灶可变的家族性局灶性癫痫", "detail": "已发现至少6个NPRL2gene突变会导致家族性局灶性癫痫，病灶可变（FFEVF），这是家族性复发性癫痫（癫痫）的一种罕见形式。这些突变大多数导致异常短的无功能蛋白的产生。结果，正常GATOR1复合物的形成减少，导致mTORC1的过度活性和mTOR途径的过度信号传导。尚不清楚异常活跃的mTOR途径如何导致FFEVF的癫痫发作。研究表明，大脑中mTOR通路信号的增加会导致神经细胞（突触）之间的连接发生变化，并增加神经细胞的激活（兴奋），从而引起癫痫发作。"}]}' WHERE id = 9962;
UPDATE genes SET GHR_metadata_ch = '{"gene": "TTR", "conditions": [{"name": "运甲状腺素蛋白淀粉样变性", "detail": "已发现TTRgene中的100多个突变会引起运甲状腺素蛋白淀粉样变性。几乎所有这些突变都会改变运甲状腺素蛋白中的一个蛋白质结构单元（氨基酸）。在运甲状腺素蛋白淀粉样变性患者中发现的最常见突变是用运甲状腺素蛋白中第30位的氨基酸蛋氨酸替代氨基酸缬氨酸（写为Val30Met或V30M）。这种突变在葡萄牙和瑞典人群中最常见，尽管在全世界的受影响人群中都有发现。另一个常见的突变是在运甲状腺素蛋白（写为Val122Ile或V122I）中第122位的氨基酸异亮氨酸取代了氨基酸缬氨酸。据估计，有3%至3.9%的非洲裔美国人和5%的西非人口患有这种突变。(换行)大多数引起运甲状腺素蛋白淀粉样变性的TTRgene突变被认为会改变运甲状腺素蛋白的结构，削弱其与其他运甲状腺素蛋白结合的能力并改变其正常功能。"}, {"name": "其他疾病", "detail": "在老年人中，运甲状腺素蛋白的沉积会引起一种称为老年系统性淀粉样变性的疾病。患有这种情况的人的TTRgene没有突变；由于尚不清楚的原因，运甲状腺素蛋白异常开始形成蛋白沉积物。患有这种疾病的人中淀粉样变性最常见的地方是心脏，导致缓慢进行性心力衰竭。淀粉样变性的其他部位可能包括肺，血管和肾脏。据估计，80岁以上的人中有10%至25%患有老年性系统性淀粉样变性。"}]}' WHERE id = 9965;
UPDATE genes SET GHR_metadata_ch = '{"gene": "PDHA1", "conditions": [{"name": "缺乏症", "detail": "PDHA1gene突变是丙酮酸脱氢酶缺乏症的最常见原因，约占这种情况的80%。丙酮酸脱氢酶缺乏症的特征是体内一种称为乳酸的化学物质（乳酸性酸中毒）可能危及生命，发育迟缓和神经系统问题。在受影响的个体中已鉴定出数十种PDHA1gene突变。这些突变分为两组。一组包括向PDHA1gene添加或删除DNA构建块（核苷酸）的突变（分别称为插入和缺失突变）。这些类型的突变在受影响的女性中比男性更常见。另一组包括突变，这些突变会改变E1 alpha蛋白中的单个蛋白结构单元（氨基酸），或在制备该蛋白的说明中导致过早的终止信号（分别称为错义和无义突变）。这些类型的突变在受影响的男性中发生的频率高于女性。与丙酮酸脱氢酶缺乏症相关的PDHA1gene（换行）突变导致E1-α蛋白量减少或导致功能异常的异常蛋白。异常蛋白质可能无法与E1-beta相互作用形成E1酶，也无法与E1酶进行化学反应所需的其他因素相互作用。功能性E1-α的降低导致丙酮酸脱氢酶复合物活性降低。随着这种复合物功能的降低，丙酮酸积累并在另一种化学反应中转化为乳酸，引起乳酸性酸中毒。另外，细胞能量的产生减少了。特别依赖于这种能量形式的大脑受到严重影响，导致与丙酮酸脱氢酶缺乏症相关的神经系统问题。"}, {"name": "利综合征", "detail": "遗传学家庭参考提供有关Leigh综合征的信息。"}]}' WHERE id = 9994;
UPDATE genes SET GHR_metadata_ch = '{"gene": "XIAP", "conditions": [{"name": "X连锁淋巴组织增生性疾病", "detail": "XIAPgene突变已在一些X连锁淋巴组织增生性疾病（XLP）患者中发现。这些突变减少或消除了XIAP蛋白的产生。目前尚不清楚缺乏XIAP蛋白是如何导致XLP的症状和体征的，包括淋巴细胞的增殖，这种增殖会破坏血液形成细胞并损害肝脏和其他器官（噬血细胞综合症）。"}]}' WHERE id = 10070;
UPDATE genes SET GHR_metadata_ch = '{"gene": "RNASET2", "conditions": [{"name": "RNAse T2缺陷型白质脑病", "detail": "在患有RNAse T2缺陷型白质脑病的人中至少鉴定出10个RNASET2gene突变。这种疾病涉及大脑异常，导致神经系统问题在婴儿期变得明显，影响智力和运动技能的发展，例如坐着和爬行。（换行）导致RNAse T2缺陷型白质脑病的RNASET2gene突变导致核糖核酸酶T2蛋白功能丧失。尚不清楚该蛋白的丢失如何导致RNAse T2缺陷型白质脑病的脑部异常和神经系统问题。研究人员指出，RNAse T2缺陷型白质脑病的体征和症状与妊娠期间传播给胎儿（先天性CMV）的特定病毒（称为巨细胞病毒（CMV））感染引起的症状和体征相似。他们正在寻求了解病毒感染或身体对其的反应，以及核糖核酸酶T2功能的丧失如何对发育中的大脑产生类似的影响。据认为，两者都可能与血管生成的变化或对尚未正确分解的RNA的免疫系统反应有关。"}]}' WHERE id = 10148;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SIX3", "conditions": [{"name": "非综合征性全前脑", "detail": "已发现SIX3gene中至少有60个突变引起非综合征性全前脑。当大脑在早期发育过程中未能分成两个半球时，就会发生这种情况。SIX3gene突变是非综合征性全前脑的第三大最常见原因。尽管该gene的突变可导致轻度至重度的病症，但与导致非综合征性全前脑（换行）的其他gene的突变相比，它们往往导致更严重的体征和症状。SIX3gene突变以不同方式改变SIX3蛋白的结构；但是，它们都破坏了蛋白质与DNA结合的能力。结果，涉及正常眼睛和前脑发育的gene未被适当激活或抑制。如果没有这些gene的正确活性，眼睛将无法正常形成，大脑也不会分成两个半球。非综合征性全前脑的体征和症状是由大脑和面部的异常发育引起的。"}]}' WHERE id = 10163;
UPDATE genes SET GHR_metadata_ch = '{"gene": "CLCN7", "conditions": [{"name": "骨质疏松症", "detail": "已经在患有骨质疏松症的人中鉴定出CLCN7gene的突变，骨质疏松症是一种与骨骼有关的疾病，其结构也异常并且易于骨折。CLCN7可通过损害ClC-7通道的功能而引起常染色体隐性骨质疏松症（ARO），常染色体显性骨质疏松症（ADO）或中间常染色体骨质疏松症（IAO），从而破坏破骨细胞在骨重塑中的功能。"}]}' WHERE id = 10172;
UPDATE genes SET GHR_metadata_ch = '{"gene": "AFF4", "conditions": [{"name": "CHOPS综合征", "detail": "AFF4gene的突变会导致CHOPS综合征，这种疾病涉及出生时出现的多种异常（先天性）。name“CHOPS”是该疾病特征列表的缩写，包括认知障碍，面部粗糙，心脏缺陷，肥胖，肺（肺）受累，身材矮小和骨骼异常。(换行)在CHOPS综合征患者中鉴定出的AFF4gene突变会改变AFF4蛋白中的单个蛋白结构单元（氨基酸）。这些突变被认为会导致不再需要时不会分解的AFF4蛋白，因此比平时可用的AFF4蛋白更多。过量的AFF4蛋白会干扰正常的转录暂停。这种转录失调导致多个器官和组织发育中的问题，导致CHOPS综合征的体征和症状。"}, {"name": "癌症", "detail": "AFF4gene偶尔参与在某些称为婴儿急性淋巴细胞白血病和混合谱系白血病的血液癌中发现的染色体重排（易位）。易位将AFF4gene与另一个称为KMT2A（以前称为MLL）的gene融合在一起。融合gene提供了制备结合了由AFF4和KMT2A产生的蛋白质特征的异常蛋白质的说明。异常蛋白质可能会破坏SEC的转录延伸功能，从而改变正常的gene活性（表达）并导致白血病中发生的细胞生长失控。研究人员正在努力确定异常的具体影响以及它们如何导致这些特定的癌症。"}]}' WHERE id = 10174;
UPDATE genes SET GHR_metadata_ch = '{"gene": "UROS", "conditions": [{"name": "卟啉症", "detail": "已发现UROSgene中的35个以上突变会导致一种形式的卟啉症，称为先天性红细胞生成性卟啉症。这些突变大多数会改变尿卟啉原III合酶中的单个蛋白质结构单元（氨基酸）。在所有先天性红细胞生成性卟啉症病例的约三分之一中发现的最常见的UROSgene突变用73位氨基酸精氨酸取代氨基酸半胱氨酸（写为Cys73Arg或C73R）。其他几个突变发生在调节UROSgene活性的DNA附近区域。（换行）UROSgene内或附近的突变会改变尿卟啉原III合酶的结构和功能，从而降低酶的活性。功能性尿卟啉原III合酶的缺乏使得称为卟啉的化合物在发育中的红细胞中积聚。这些化合物是在血红素生成的正常过程中形成的，但是尿卟啉原III合酶活性的降低使它们积累到毒性水平。过量的卟啉可以从发育中的红细胞中泄漏出来，并通过血流输送到皮肤和其他组织。这些物质在皮肤中的积累会导致对日光过敏和先天性红细胞生成性卟啉症的其他特征。"}]}' WHERE id = 10210;
UPDATE genes SET GHR_metadata_ch = '{"gene": "HFE", "conditions": [{"name": "遗传性血色素沉着症", "detail": "研究人员已经确定了HFEgene中的20多种突变，这些突变会导致一种称为1型的遗传性血色素沉着病。两种特定的突变是造成这种疾病的大多数情况的原因。这些突变中的每一个都会改变HFE蛋白中的一种蛋白质结构单元（氨基酸）。一个突变将氨基酸半胱氨酸替换为蛋白质氨基酸链（写为Cys282Tyr或C282Y）中第282位的氨基酸酪氨酸。另一个突变用位置63处的氨基酸天冬氨酸取代了氨基酸组氨酸（写为His63Asp或H63D）。（换行）Cys282Tyr突变阻止改变的HFE蛋白到达细胞表面，因此它不能与铁调素和转铁蛋白受体相互作用。结果，铁调节被破坏，并且饮食中吸收了太多的铁。膳食铁吸收的这种增加导致1型血色素沉着病的铁超负荷特征。"}, {"name": "卟啉症", "detail": "导致遗传性血色素沉着病的HFEgene突变也增加了发展最常见形式的卟啉症迟发性皮肤卟啉症的风险。这些突变在迟发性皮肤卟啉病患者中比在未受影响的人群中更常见。(换行)研究人员怀疑HFEgene突变可能通过增加铁的吸收来触发这种类型的卟啉症。过量铁的积累与其他遗传和非遗传因素相结合，会干扰称为血红素的分子的产生。血红素是称为血蛋白的含铁蛋白的组分，包括血红蛋白（在血液中携带氧气的蛋白质）。血红素产生的障碍允许称为卟啉的其他化合物在肝脏和其他器官中累积至毒性水平。这些化合物是在血红素生产的正常过程中形成的，但是过量的铁和其他因素使它们积累到有毒水平。卟啉的异常积累导致迟发性皮肤卟啉病的特征。"}, {"name": "X连锁铁粒幼细胞性贫血", "detail": "Cys282Tyr突变是遗传性血色素沉着病的常见原因，当它与ALAS2gene突变一起遗传时，也可能增加X连锁铁粒幼细胞性贫血中铁超负荷的严重性。HFE和ALAS2突变的组合通过进一步增加饮食中铁的吸收，导致更严重的X连锁铁粒幼细胞性贫血的体征和症状，从而导致更大的铁超负荷。"}]}' WHERE id = 10212;
UPDATE genes SET GHR_metadata_ch = '{"gene": "CTC1", "conditions": [{"name": "外套加综合症", "detail": "在Coats plus综合征患者中已鉴定出CTC1gene至少20个突变。这种疾病的特征是称为Coats病的眼部疾病以及大脑，骨骼，胃肠系统和身体其他部位的异常。(换行)大多数患有Coats plus综合征的人在每个细胞中的一个CTC1gene拷贝中具有突变，其消除了CTC1蛋白的功能，并且该gene的另一个拷贝中的突变减少但不消除蛋白质的功能。突变的这种组合仅留下少量功能性CTC1蛋白可作为CST复合物的一部分起作用。尽管尚不清楚对端粒结构和功能的影响，但由此产生的这种复合物的损伤会影响端粒的复制。一些研究发现，具有CTC1gene突变的人端粒异常短，而其他研究发现端粒长度没有变化。研究人员正在努力确定具有CTC1gene突变的人的端粒如何不同，以及这些变化如何成为Coats plus综合征各种体征和症状的基础。"}, {"name": "先天性角化不良", "detail": "遗传学家庭参考提供有关先天性角化不全的信息。"}]}' WHERE id = 10213;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ALDH5A1", "conditions": [{"name": "琥珀酸半醛脱氢酶缺陷", "detail": "发现ALDH5A1gene中的几个突变会导致琥珀酸半醛脱氢酶缺乏症，这种疾病的特征是发育延迟，癫痫发作和其他特征，这是由于酶缺陷或无功能而损害GABA代谢所致。"}]}' WHERE id = 10225;
UPDATE genes SET GHR_metadata_ch = '{"gene": "TCN2", "conditions": [{"name": "转钴胺素缺乏症", "detail": "已发现TCN2gene中的20多个突变会导致转钴胺素缺乏。这种情况损害了钴胺素从血液到全身细胞的运输。受影响的个体难以增重并以预期的速度增长（壮成长），呕吐，腹泻，所有类型血细胞短缺和神经系统问题。许多TCN2gene突变导致转钴胺素完全或接近完全缺乏（缺乏）。其他TCN2gene突变会导致无法结合钴胺素的反钴胺素蛋白或无法结合细胞表面受体的蛋白。由此导致的细胞内钴胺素缺乏会干扰某些酶的功能，从而影响许多细胞活性。结果，可以发展出转钴胺素缺乏症特征的各种体征和症状。"}]}' WHERE id = 10239;
UPDATE genes SET GHR_metadata_ch = '{"gene": "CRX", "conditions": [{"name": "视锥-视杆营养不良", "detail": "已发现CRXgene中的20多个突变会导致视锥细胞营养不良。与这种情况相关的问题包括视力丧失（敏锐度），对光的敏感性增加（畏光）和色觉受损。这些视力问题随着时间的推移而恶化。锥杆营养不良是由每个细胞中CRXgene的两个拷贝之一中发生的突变引起的。CRXgene突变约占常染色体显性锥杆营养不良的一种形式的病例的四分之一。这些突变导致可用于调节视网膜中其他gene的功能性锥杆同源盒蛋白的数量减少。结果，杆细胞和视锥细胞的维持不足，并且这些细胞随时间恶化，导致视锥细胞营养不良的视力问题。研究人员认为，有足够的锥杆同源盒蛋白功能来允许感光细胞分化，但是不能维持细胞的长期维持。(换行)已发现几种CRXgene突变在不同个体中引起不同形式的视力丧失。目前尚不清楚CRXgene的突变如何引起不同的眼部疾病。"}, {"name": "莱伯先天性黑蒙", "detail": "遗传学家庭参考提供有关Leber先天性黑ama病的信息。"}, {"name": "视网膜色素变性", "detail": "遗传学家庭参考提供有关视网膜色素变性的信息。"}]}' WHERE id = 10296;
UPDATE genes SET GHR_metadata_ch = '{"gene": "TEK", "conditions": [{"name": "多发性皮肤和粘膜静脉畸形", "detail": "已发现TEKgene中至少有8个突变会引起多种皮肤和粘膜静脉畸形（也称为VMCM）。这些突变改变了TEK受体酪氨酸激酶中的单个蛋白质结构单元（氨基酸）。最常见的突变是在TEK受体（写为Arg849Trp或R849W）的849位氨基酸色氨酸取代了氨基酸精氨酸。R849W突变以及导致这种情况的大多数其他突变会导致TEK受体始终打开（过度活跃）。(换行)认为过度活跃的TEK受体破坏内皮细胞和平滑肌细胞之间的通讯。尚不清楚这些细胞之间缺乏通讯如何导致静脉畸形。这些异常血管在维持内皮细胞的同时显示出平滑肌细胞的缺乏。静脉畸形会导致VMCM特有的皮肤或粘膜表面以下的病变。"}]}' WHERE id = 10307;
UPDATE genes SET GHR_metadata_ch = '{"gene": "TGFBI", "conditions": [{"name": "I型格子状角膜营养不良", "detail": "TGFBIgene中至少有26个突变可引起I型晶格状角膜营养不良。这种遗传性眼病的特征是在角膜中形成晶格状的蛋白质堆积（称为淀粉样沉积物）。这些沉积物使角膜浑浊，并导致受影响个体的视力障碍。角膜由几层组织组成。在I型晶格状角膜营养不良中，沉积物出现在角膜基质层中，并含有改变的TGFBI蛋白。（换行）涉及I型晶格角膜营养不良的TGFBIgene突变改变了TGFBI蛋白中的单个蛋白结构单元（氨基酸）。最常见的突变是在124位的半胱氨酸氨基酸取代了精氨酸氨基酸（写为Arg124Cys或R124C）。当这种情况是由这种突变引起的时，通常称为I型经典晶格角膜营养不良。改变的TGFBI蛋白异常聚集在一起并形成淀粉样沉积物。但是，尚不清楚由gene突变引起的变化如何诱导蛋白质形成这些沉积物。"}, {"name": "圆锥角膜", "detail": "遗传学家庭参考提供有关圆锥角膜的信息。"}, {"name": "其他疾病", "detail": "TGFBIgene突变与许多其他类型的角膜营养不良有关，包括Groenouw角膜营养不良，Avellino角膜营养不良，IIIA型格子状角膜营养不良，Reis-Bucklers角膜营养不良，Thiel-Behnke角膜营养不良和上皮基底膜角膜营养不良。角膜营养不良的特征都在于角膜中蛋白质沉积物的积累，这导致视力障碍。这些沉积物以各种形状和图案形成，并出现在角膜的不同层中，这有助于确定不同类型的角膜营养不良。（换行）导致这些病症的TGFBIgene突变改变TGFBI蛋白中的单个氨基酸。大多数突变会改变在蛋白质124和555位发现的氨基酸，尽管也可能发生其他氨基酸变化。某些突变与特定的角膜营养不良密切相关。例如，用氨基酸组氨酸（写为Arg124His）替换124位氨基酸精氨酸的突变最常引起阿韦利诺角膜营养不良。用氨基酸赖氨酸（写为Arg124Lys）替换124位氨基酸精氨酸的突变最常引起Reis-Bucklers角膜营养不良。用氨基酸谷氨酰胺（写为Arg555Gln）替换555位氨基酸精氨酸的突变最常引起Thiel-Behnke角膜营养不良。而且，用氨基酸色氨酸（写为Arg555Trp）替换555位氨基酸精氨酸的突变最常引起Groenouw角膜营养不良。"}]}' WHERE id = 10308;
UPDATE genes SET GHR_metadata_ch = '{"gene": "COQ8B", "conditions": [{"name": "初级辅酶Q10缺乏症", "detail": "已发现COQ8Bgene中至少有11个突变会引起一种称为初级辅酶Q10缺乏症的疾病。这种罕见疾病通常在婴儿期或儿童早期就很明显，但可以在任何年龄发生。它可以影响身体的许多部位，最常见的是大脑，肌肉和肾脏。与该疾病相关的COQ8Bgene突变大大降低或消除了COQ8B蛋白的功能，从而阻止了辅酶Q10的正常产生。研究表明，辅酶Q10的缺乏（缺乏）会损害氧化磷酸化并增加细胞对自由基损伤的脆弱性。辅酶Q10的缺乏也可能破坏嘧啶的产生。这些变化可能导致整个身体的细胞发生功能障碍，这可能有助于解释可能受初级辅酶Q10缺乏影响的器官和组织的多样性。"}]}' WHERE id = 10316;
UPDATE genes SET GHR_metadata_ch = '{"gene": "PITX2", "conditions": [{"name": "Axenfeld-Rieger综合征", "detail": "已发现PITX2gene中的45个以上突变会导致1型Axenfeld-Rieger综合征，这种疾病会影响眼前节和身体其他部位的发育。大多数PITX2gene突变会减少细胞中产生的功能性PITX2蛋白的量。但是，某些遗传变化（例如PITX2gene的重复）会增加PITX2蛋白的数量或功能。这种蛋白质太少或太多都会破坏正常发育所需的其他gene的调控。(换行)眼睛发育似乎对PITX2蛋白活性的变化最敏感，眼前节异常是Axenfeld-Rieger综合征的主要特征。但是，PITX2蛋白量的变化也可能导致患有这种疾病的人独特的面部特征，牙齿异常以及身体其他部位发育的问题。"}, {"name": "彼得斯异常", "detail": "已经发现PITX2gene中至少有一个突变引起彼得斯异常。这种情况的特征是眼前节异常发育和角膜混浊。这种突变（写为IVS3AS，A T，-2）改变了PITX2蛋白拼接的方式。据认为，这种改变的蛋白质破坏了细胞向其在发育中的前节中适当位置的运动调节，导致该区域的结构异常形成和彼得斯异常的其他特征。"}, {"name": "其他疾病", "detail": "PITX2gene的突变也已在其他眼部疾病中发现。像Axenfeld-Rieger综合征和Peters异常一样，这些情况主要涉及眼前节。PITX2gene的突变可引起角膜环状皮样病变，这是一种与角膜肿瘤样生长有关的疾病。此外，可能会发生导致虹膜发育不全的情况，包括虹膜发育不全或2型虹膜虹膜发育不全。Iridogoniodysgenesis类型2也与眼睛压力增加（青光眼）的风险升高有关。"}]}' WHERE id = 10341;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SLC40A1", "conditions": [{"name": "遗传性血色素沉着症", "detail": "研究人员已经确定了SLC40A1gene中大约15个突变，这些突变会导致一种称为铁转运蛋白病的遗传性血色素沉着病，有时也称为4型血色素沉着病。几乎所有这些突变都会改变铁转运蛋白中的单个蛋白质结构单元（氨基酸）。铁转运蛋白的异常形式不允许铁从肠或网状内皮细胞正常转运和释放。结果，损害了体内铁水平的调节并导致铁超负荷。每个细胞中该gene的一个突变拷贝足以引起铁转运蛋白疾病。"}, {"name": "非洲铁超载", "detail": "一些研究表明，SLC40A1gene的特定变异会稍微增加非洲人后裔铁储备增加的风险，这可能导致非洲人铁超负荷。这种影响似乎在男性中更为明显，这可能与铁加工中的性别差异有关。（换行）与铁储存增加相关的SLC40A1gene变异用铁转运蛋白序列中第248位的氨基酸组氨酸取代氨基酸谷氨酰胺，并写为Q248H或Gln248His。在非洲人后裔中有5%至20%被发现，但在其他人群中普遍没有。Q248H的变化可能会影响铁转运蛋白帮助调节体内铁水平的方式，从而导致非洲铁超负荷的风险增加。具有这种变化的人可能会继承这种情况的风险增加，但不会继承conditions本身。并非所有患有这种疾病的人都有gene变异，并非所有具有这种变异的人都会患上这种疾病。"}]}' WHERE id = 10342;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SMN2", "conditions": [{"name": "脊髓性肌萎缩", "detail": "通常，人们在每个细胞中有两个SMN1gene拷贝和最多两个SMN2gene拷贝。在脊髓性肌萎缩症患者中，SMN1gene的两个拷贝都被改变或缺失。在某些情况下，个体具有SMN2gene的三个或更多副本。在患有脊髓性肌萎缩症的患者中，SMN2gene的其他拷贝与该病的轻度病程有关。（换行）当SMN1gene的两个拷贝都被改变或缺失时，从该gene产生很少或没有SMN蛋白。额外的SMN2gene可以帮助替换由于SMN1gene突变而丢失的某些SMN蛋白。但是，由于SMN2gene仅产生少量的全尺寸SMN蛋白，因此仍然会出现脊髓性肌萎缩症的症状。通常，与具有两个SMN2gene拷贝的个体相比，具有三个或更多SMN2gene拷贝的受影响个体的症状较轻，并且在以后的生活中开始。"}]}' WHERE id = 10391;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SMN1", "conditions": [{"name": "脊髓性肌萎缩", "detail": "大约95%的脊髓性肌萎缩症患者的突变会删除每个细胞中SMN1gene两个拷贝中称为外显子7的部分。结果，很少或没有产生SMN蛋白。在约5%的患有这种疾病的人中，SMN1gene的一个拷贝具有外显子7的缺失，而另一个拷贝具有不同的突变，从而破坏了SMN蛋白的产生或功能。研究人员已经确定SMN1gene中至少有65个突变会导致脊髓性肌萎缩症。(换行)运动神经元似乎特别容易受到SMN蛋白短缺的影响并过早死亡。研究人员建议，SMN蛋白的缺乏会导致处理pre-mRNA所需的机器组装效率低下。没有成熟的mRNA，细胞生长和功能所必需的蛋白质的产生就会被破坏。一些研究结果表明，SMN蛋白的缺乏会损害轴突和树突的形成和功能，可能导致神经元死亡。虽然神经元死亡的原因尚不清楚，但导致脊髓性肌萎缩症的体征和症状的是运动神经元的丧失。(换行)在某些脊髓性肌萎缩症病例中，除了SMN1gene突变外，受影响的个体在每个细胞中都有三个或更多SMN2gene拷贝。额外的SMN2gene可以帮助替换由于SMN1gene突变而丢失的某些SMN蛋白。通常，与具有两个SMN2gene拷贝的个体相比，具有三个或更多SMN2gene拷贝的受影响个体的症状较轻，并且在以后的生活中开始。"}, {"name": "肌萎缩侧索硬化症", "detail": "遗传学家庭参考提供有关肌萎缩侧索硬化的信息。"}]}' WHERE id = 10393;
UPDATE genes SET GHR_metadata_ch = '{"gene": "KRT12", "conditions": [{"name": "梅斯曼角膜营养不良", "detail": "已发现KRT12gene中至少有20个突变会引起Meesmann角膜营养不良，这是一种以角膜上皮中微小囊肿形成为特征的眼病。(换行)几乎所有与Meesmann角膜营养不良相关的KRT12gene突变都会改变角蛋白12蛋白中的单个蛋白结构单元（氨基酸）。这些变化发生在蛋白质区域，这些区域对于中间丝的形成和稳定性至关重要。改变的角蛋白12蛋白干扰中间丝的组装，削弱角膜上皮的结构框架。结果，角膜的这一外层异常脆弱，并形成了以Meesmann角膜营养不良为特征的囊肿。囊肿可能含有大量异常角蛋白和其他细胞碎片。当囊肿破裂（破裂）时，会引起眼睛刺激，对光的敏感性增加（畏光）和相关症状。"}]}' WHERE id = 10402;
UPDATE genes SET GHR_metadata_ch = '{"gene": "PIK3CA", "conditions": [{"name": "Klippel-Trenaunay综合征", "detail": "已发现PIK3CAgene中至少有五个突变引起Klippel-Trenaunay综合征。这种情况的特征是红色的胎记，称为葡萄酒色斑，软组织（例如皮肤和肌肉）和骨骼的异常过度生长以及静脉畸形。与这种情况相关的PIK3CAgene突变不是从父母那里继承的；它们在出生前发育的早期阶段在一个细胞中随机出现。随着细胞在发育过程中继续分裂，由第一个异常细胞产生的细胞将发生突变，而其他细胞则不会。具有和不具有gene突变的细胞混合物称为镶嵌。（换行）与Klippel-Trenaunay综合征相关的PIK3CAgene突变改变了p110中的单个蛋白质构建块（氨基酸）？蛋白质。这些变化导致产生改变的p110？使PI3K异常活跃的亚基。改变的酶触发细胞中不受调节的化学信号传导，这允许细胞连续生长和分裂。细胞增殖增加导致骨骼，软组织和血管异常生长。（换行）尽管PIK3CAgene突变参与癌症（如下所述）和由该gene变化引起的细胞过度生长，但患有Klippel-Trenaunay综合征的个体似乎没有患癌症的风险升高。"}, {"name": "巨脑-毛细血管畸形综合征", "detail": "已经发现PIK3CAgene中至少15个突变引起称为巨脑-毛细血管畸形综合征（MCAP）的病症，其特征在于脑过度生长（巨脑）和由皮肤中小血管增大引起的异常（毛细血管畸形）。引起MCAP的突变与引起Klippel-Trenaunay综合征的突变重叠（如上所述）。这些突变不是从父母那里继承的；它们在出生前的发育早期随机出现在一个细胞中，并导致镶嵌。突变在不同组织中的存在有助于解释为什么同一gene突变会导致多种情况。（换行）涉及MCAP的大多数PIK3CAgene突变改变p110中的单个氨基酸？蛋白质。这些突变导致产生改变的p110？使PI3K异常活跃的亚基。由此产生的不受管制的信号使细胞不断生长和分裂。脑和其他组织器官中细胞增殖的增加导致MCAP的过度生长特征。（换行）尽管PIK3CAgene突变参与许多癌症（如下所述）并且由该gene的变化引起的细胞过度生长，但具有MCAP的个体似乎没有升高的患癌症的风险。"}, {"name": "表皮痣", "detail": "在多达四分之一的患有某种类型的表皮痣的人中发现了PIK3CAgene的突变（复数：痣）。具体而言，PIK3CAgene突变与某些角质形成细胞表皮痣有关，这是由称为角质形成细胞的皮肤细胞组成的异常皮肤生长。在其他类型的表皮痣中未发现PIK3CAgene突变。（换行）在表皮痣中发现的最常见的PIK3CAgene突变用p110位置545处的氨基酸甘氨酸取代氨基酸谷氨酸？蛋白质（写为Glu545Gly或E545G）。研究表明，这种突变导致细胞比正常生长和分裂更多。由此导致的皮肤细胞过度生长导致表皮痣的形成。与这种情况相关的遗传变化是体细胞突变，仅存在于痣细胞中。（换行）尽管PIK3CAgene突变参与许多癌症（如上所述）并且由该gene的变化引起的细胞过度生长，但具有表皮痣的个体似乎没有升高的患癌症的风险。"}, {"name": "头颈部鳞状细胞癌", "detail": "遗传学家庭参考提供有关头颈部鳞状细胞癌的信息。"}, {"name": "肺癌", "detail": "遗传学家庭参考提供有关肺癌的信息。"}, {"name": "卵巢癌", "detail": "遗传学家庭参考提供有关卵巢癌的信息。"}, {"name": "癌症", "detail": "PIK3CAgene的突变存在于许多类型的癌症中，包括卵巢癌，乳腺癌，肺癌，脑癌和胃癌。这些突变还涉及结肠癌（大肠）和直肠癌，它们统称为结直肠癌。涉及癌症的PIK3CAgene突变是体细胞的，这意味着它们是在一个人的一生中获得的，并且仅存在于引起癌症的细胞中。这些突变改变了p110中的单个氨基酸？蛋白质。两个常见的突变发生在同一区域，并改变了p110的542位或545位的氨基酸谷氨酸？氨基酸赖氨酸的蛋白质（分别写为Glu542Lys和Glu545Lys）。另外两个常见的突变发生在另一个区域，改变了p110位置1047处的氨基酸组氨酸？氨基酸精氨酸或亮氨酸（分别写为His1047Arg和His1047Leu）。（换行）癌症相关的PIK3CAgene突变导致产生改变的p110？允许PI3K发出信号而无需调节的亚基。增加的信号传导可导致细胞不受控制的增殖，导致癌症的发展。但是，PIK3CAgene突变本身可能不会引起癌症。研究人员怀疑某些癌症病例可能是由PIK3CA突变和其他影响癌症风险的gene突变共同导致的。"}, {"name": "其他疾病", "detail": "已经发现PIK3CAgene中的突变，包括在一些癌症中发现的突变，引起与组织过度生长有关的几种其他病症。这些情况包括半巨脑症；纤维脂肪增生；以及称为先天性脂肪瘤过度生长，血管畸形，表皮痣和骨骼或脊柱异常（CLOVES）综合征的疾病。半巨脑畸形的特征是大脑一侧增大，可引起癫痫发作和智力障碍。纤维脂肪增生导致身体各个区域的纤维和脂肪（脂肪）组织过度生长，这导致身体的不同部分（例如下半身，单个手臂或腿，或一个或多个手指或脚趾）增大。CLOVES综合征具有多种特征，除了血管，皮肤和骨骼异常之外，还包括腹部脂肪组织的过度生长，其通常与皮肤上的红色胎记相关。尚不清楚患有这些疾病的个体是否罹患癌症的风险增加。（换行）与Klippel-Trenaunay综合征，MCAP和表皮痣（如上所述）一样，这些疾病所涉及的遗传变化发生在发育早期，仅在身体的某些细胞中发现。这种镶嵌现象有助于解释为什么相同的gene突变会导致不同的情况。由PIK3CAgene突变引起的过度生长障碍一起被称为PIK3CA相关的过度生长谱（PROS）。"}]}' WHERE id = 10405;
UPDATE genes SET GHR_metadata_ch = '{"gene": "DLAT", "conditions": [{"name": "缺乏症", "detail": "在丙酮酸脱氢酶缺乏症患者中，已经鉴定出DLATgene中至少有两个突变；DLATgene的突变是这种情况的非常罕见的原因。丙酮酸脱氢酶缺乏症的特征是体内一种称为乳酸的化学物质（乳酸性酸中毒）可能危及生命，发育迟缓和神经系统问题。DLATgene中的（换行）突变导致异常的E2酶和丙酮酸脱氢酶复合物的活性降低，尽管其机制尚不清楚。随着这种复合物活性的降低，丙酮酸积累并在另一种化学反应中转化为乳酸，引起乳酸性酸中毒。另外，细胞能量的产生减少了。特别依赖于这种能量形式的大脑受到严重影响，导致与丙酮酸脱氢酶缺乏症相关的神经系统问题。"}, {"name": "利综合征", "detail": "遗传学家庭参考提供有关Leigh综合征的信息。"}]}' WHERE id = 10407;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SMAD3", "conditions": [{"name": "Loeys-Dietz综合征", "detail": "已发现SMAD3gene中至少有35个突变会导致III型Loeys-Dietz综合征。这种疾病会影响结缔组织，结缔组织为血管，骨骼和身体的许多其他部位提供结构和支持。III型Loeys-Dietz综合征的特征是血管异常，骨骼和关节畸形以及皮肤异常。导致这种疾病的某些突变会在SMAD3gene中插入或删除少量遗传物质，而其他突变会导致SMAD3蛋白中单个蛋白结构单元（氨基酸）发生变化。这些突变导致产生无功能的SMAD3蛋白。尽管SMAD3功能降低，但TGF-β？途径过度活跃。研究人员推测，该信号通路中其他蛋白质的活性增加，以弥补SMAD3活性的缺乏；然而，导致信号增加的确切机制尚不清楚。过度活跃的信号通路导致细胞增殖和gene激活失调，特别是影响血管，软骨和皮肤发育。这些变化导致典型的Loeys-Dietz综合征III型异常。"}, {"name": "家族性胸主动脉瘤和解剖", "detail": "遗传学家庭参考资料提供有关家族性胸主动脉瘤和解剖的信息。"}]}' WHERE id = 10415;
UPDATE genes SET GHR_metadata_ch = '{"gene": "HINT1", "conditions": [{"name": "常染色体隐性轴索神经病伴神经肌强直", "detail": "已发现HINT1gene中至少有9个突变会引起神经性肌强直的常染色体隐性轴突神经病。这种神经状况影响周围神经，周围神经将大脑和脊髓连接到肌肉和检测触觉等感觉的感觉细胞。患有这种疾病的人通常会在脚，腿和手上出现肌肉无力，并且在拉紧后（神经肌强直）会延迟肌肉松弛。一些受影响的人的感觉轻度受损。（换行）导致这种情况的HINT1gene突变改变了HINT1蛋白中的单个蛋白结构单元（氨基酸）。这些变化降低或消除了蛋白质进行水解反应的能力。有时改变的蛋白质会过早分解。尚不清楚功能性HINT1蛋白的丧失如何影响周围神经或导致这种情况的体征和症状。"}]}' WHERE id = 10430;
UPDATE genes SET GHR_metadata_ch = '{"gene": "KCNQ1", "conditions": [{"name": "家族性心房颤动", "detail": "KCNQ1gene的改变是家族性心房颤动的罕见原因，家族性心房颤动是心脏正常节律（心律不齐）的破坏，其特征是心脏上腔（心房）的电活动不协调。已经发现几种突变引起该病；这些遗传变化改变了KCNQ1蛋白中的单个蛋白结构单元（氨基酸）。在心肌细胞中，突变似乎增加了钾离子通过由KCNQ1蛋白形成的通道的流量。增强的离子传输可以破坏心脏的正常节律，导致心房颤动。"}, {"name": "Jervell和Lange-Nielsen综合征", "detail": "已发现30多个KCNQ1gene突变会导致Jervell和Lange-Nielsen综合征，这种疾病会导致心律不齐和出生后严重的听力丧失。大约90%的病例是由该gene突变引起的。这些突变通常存在于每个细胞中KCNQ1gene的两个拷贝中。这些变化中的大多数导致异常短而无功能的KCNQ1蛋白产生，该蛋白不能用于构建钾通道。其他突变会改变该蛋白质中的少量氨基酸，从而改变通道的正常结构和功能。这些通道无法在内耳和心肌中正确运输钾离子，导致Jervell和Lange-Nielsen综合征的听力损失和心律不齐。"}, {"name": "罗曼诺-沃德综合症", "detail": "KCNQ1gene突变被认为是Romano-Ward综合征的最常见原因，约占病例的三分之一。这种情况是一种称为长QT综合征的心律失常形式。在患有Romano-Ward综合征的人中，心肌在心跳之间需要比平时更长的时间来充电。(换行)已鉴定出导致Romano-Ward综合征的600多个KCNQ1gene突变。导致Romano-Ward综合征的突变通常仅存在于每个细胞中KCNQ1gene的一个拷贝中。这些突变大多数会改变KCNQ1蛋白中的单个氨基酸或插入或删除少量氨基酸。这些变化使蛋白质形成通道，但降低了通道将钾离子转运出心肌细胞的能力。减少的离子传输会改变心脏中电信号的传输，增加不规则心跳的风险，从而导致晕厥（晕厥）或猝死。"}, {"name": "短QT综合征", "detail": "KCNQ1gene中至少有两个突变可引起称为短QT综合征的心脏病。在患有这种情况的人中，心肌在心跳之间花费的时间比平时少。这种变化增加了可能导致晕厥或猝死的心律异常的风险。（换行）与短QT综合征相关的KCNQ1gene突变改变KCNQ1蛋白中的单个氨基酸。突变改变了用KCNQ1蛋白制成的离子通道的功能，增加了通道的活性。结果，在心跳期间的关键时刻，更多的钾离子从心肌细胞中流出，这可能导致心律不规则。"}, {"name": "妊娠糖尿病", "detail": "遗传学家庭参考提供有关妊娠糖尿病的信息。"}, {"name": "其他疾病", "detail": "KCNQ1gene的突变与其他几种与心律异常有关的疾病有关，包括婴儿猝死综合征（SIDS）和获得性长QT综合征。(换行)小岛屿发展中国家是一岁以下婴儿死亡的主要原因。它的特点是突然和无法解释的死亡，通常在睡眠期间。尽管SIDS的病因通常未知，但研究人员在少数情况下已鉴定出KCNQ1gene突变。其他遗传和环境因素，其中许多尚未确定，也参与确定小岛屿发展中国家的风险。(换行)某些药物，包括用于治疗心律失常，感染，癫痫发作和精神障碍的药物，可能导致某些人心律异常。这种药物引起的心脏病被称为获得性长QT综合征，增加了心脏骤停和猝死的风险。一小部分获得性长QT综合征病例发生在KCNQ1gene潜在变异的人群中。"}]}' WHERE id = 10433;
UPDATE genes SET GHR_metadata_ch = '{"gene": "IFT140", "conditions": [{"name": "Mainzer-Saldino综合征", "detail": "在Mainzer-Saldino综合征患者中至少鉴定出9个IFT140gene突变，Mainzer-Saldino综合征是一种以肾脏疾病，眼部疾病和骨骼异常为特征的疾病。导致Mainzer-Saldino综合征的IFT140gene突变可能会改变IFT140蛋白的形状或与其他IFT蛋白的相互作用，可能会损害IFT-A的组装以及纤毛的发育或维持。结果，可能存在或功能较少的纤毛，影响身体的许多器官和组织，并导致Mainzer-Saldino综合征的体征和症状。由纤毛问题引起并涉及骨骼异常的疾病，例如Mainzer-Saldino综合征，称为骨骼肌纤毛病。"}, {"name": "窒息性胸营养不良", "detail": "遗传学家庭参考提供有关窒息性胸肌营养不良的信息。"}]}' WHERE id = 10452;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ARFGEF2", "conditions": [{"name": "脑室周围异位", "detail": "ARFGEF2gene的突变与脑室周围异位症有关，这种疾病会损害囊泡运输，从而影响大脑发育过程中的细胞迁移并导致脑室周围的神经元团块。受影响的人在青少年时期表现出癫痫发作，智力正常，尽管有一些轻度智力障碍，阅读障碍，严重的脑畸形，小头畸形，发育迟缓，反复感染，血管异常，有时可能与Ehlers-Danlos综合征有关。"}]}' WHERE id = 10459;
UPDATE genes SET GHR_metadata_ch = '{"gene": "C9orf72", "conditions": [{"name": "肌萎缩侧索硬化症", "detail": "已发现C9orf72gene的突变会引起肌萎缩性侧索硬化症（ALS），这种疾病的特征是进行性肌肉无力，肌肉量减少和无法控制运动。这些突变影响gene的GGGGCC区段。当这一系列核苷酸重复太多次时，它可能导致ALS。这种类型的突变称为六核苷酸重复扩增。尽管尚不清楚引起疾病需要多少个六核苷酸重复序列，但研究人员认为，重复序列超过30个可导致ALS。(换行)目前尚不清楚六核苷酸重复扩增是降低C9orf72蛋白质功能还是导致功能异常的蛋白质的产生，从而破坏细胞中RNA和蛋白质的产生，从而导致蛋白质团块（聚集体）的形成。在ALS中，大尺寸的运动神经元被认为使这些细胞容易受到正常细胞功能损伤的影响。C9orf72蛋白功能的破坏可能导致运动神经元细胞过早死亡，从而导致ALS的体征和症状。(换行)一些由C9orf72gene突变引起的ALS患者也会出现额颞叶痴呆（FTD），这是一种影响人格，行为和语言的进行性脑部疾病。目前尚不清楚为什么有些具有C9orf72gene突变的人会发展为FTD，而另一些则不会。患有这两种疾病的人被诊断患有ALS-FTD。"}]}' WHERE id = 10474;
UPDATE genes SET GHR_metadata_ch = '{"gene": "KLLN", "conditions": [{"name": "考登综合症", "detail": "一些Cowden综合征和类似的称为Cowden样综合征的病例是由KLLNgene的改变引起的。这些病症的特征是称为错构瘤的多种肿瘤样生长和发展某些癌症的风险增加。当考登综合症和考登样综合症是由KLLNgene突变引起的，这种情况与罹患乳腺癌和肾癌的风险特别高有关。（换行）与这些conditions相关的遗传变化称为启动子高甲基化。启动子是靠近控制gene活性（表达）的gene的DNA区域。当太多称为甲基的小分子附着于启动子区域时，发生高甲基化。额外的甲基降低了KLLNgene的表达，这意味着产生的杀伤素更少。减少的killin量可能使异常细胞存活并不适当地增殖，这可能导致肿瘤的形成。（换行）KLLNgene的启动子区域与另一个genePTEN共享。单个启动子控制两个gene的表达。然而，似乎启动子高甲基化仅影响KLLNgene的表达；具有这种类型的遗传变化的人具有PTENgene的正常表达。PTENgene中的其他类型的突变可引起Cowden综合征和Cowden样综合征。"}]}' WHERE id = 10477;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ITPKC", "conditions": [{"name": "川崎病", "detail": "ITPKCgene的变异与川崎病的风险增加有关，川崎病是一种突然且有时间限制的（急性）疾病，会影响婴儿和儿童，导致长期发烧，发炎和其他体征和症状。该变异改变了称为内含子1的gene区域中的单个DNA构建块（核苷酸）。它似乎降低了ITPKCgene转录的效率，这是生产ITPKC酶的第一步。研究人员认为，这种变异可能会减少ITPKC酶的量，并干扰人体限制T细胞活性的能力，从而导致炎症，损害血管并导致川崎病的体征和症状。"}]}' WHERE id = 10478;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ATXN1", "conditions": [{"name": "脊髓小脑性共济失调1型", "detail": "脊髓小脑性共济失调1型（SCA1）是一种以运动进行性问题为特征的疾病。SCA1是由ATXN1gene突变（称为三核苷酸重复扩增）产生的。该突变增加了ATXN1gene中重复的CAG区段的长度。患有SCA1的人在大多数细胞中具有40到80多个CAG重复序列。（换行）扩展的CAG区段导致产生异常长的ataxin-1蛋白版本，其折叠成错误的三维形状。这种异常蛋白质与其他蛋白质聚集在细胞核内形成团块（聚集体）。这些聚集体阻止ataxin-1蛋白正常运作，从而损害细胞。由于尚不清楚的原因，仅在大脑和脊髓（中枢神经系统）中发现了ataxin-1的聚集体。某些称为浦肯野细胞的脑细胞似乎对这些聚集体的积累特别敏感。浦肯野细胞位于协调运动（小脑）的大脑部分，并参与神经细胞（神经元）之间的化学信号传导。中枢神经系统细胞，特别是浦肯野细胞中蛋白质聚集体的积累导致细胞死亡。随着时间的流逝，这些单元的丢失会导致SCA1特有的运动问题。"}]}' WHERE id = 10487;
UPDATE genes SET GHR_metadata_ch = '{"gene": "PTEN", "conditions": [{"name": "Bannayan-Riley-Ruvalcaba综合征", "detail": "已发现PTENgene中的30多个突变会导致Bannayan-Riley-Ruvalcaba综合征。这种情况的共同特征包括头部较大（大头畸形），多发性非癌性肿瘤和称为错构瘤的肿瘤样生长，以及男性阴茎上的深色雀斑。Bannayan-Riley-Ruvalcaba综合征是通常被认为是PTEN错构瘤肿瘤综合征的几种相关疾病之一（如下所述）。(换行)导致Bannayan-Riley-Ruvalcaba综合征的某些突变会改变PTENgene中的单个DNA构建块（碱基对）或插入或删除少量碱基对。其他突变导致异常短的酶或减少产生的酶的量。在大约10%的病例中，Bannayan-Riley-Ruvalcaba综合征是由大量遗传物质的缺失引起的，这些遗传物质包括部分或全部PTENgene。所有这些遗传变化都阻止了PTEN酶有效调节细胞增殖，从而导致不受控制的细胞生长以及错构瘤和其他类型肿瘤的形成。目前尚不清楚PTENgene突变如何引起大头畸形和Bannayan-Riley-Ruvalcaba综合征的其他特征。"}, {"name": "考登综合症", "detail": "研究人员已经确定了PTENgene中的300多个突变，这些突变可能导致Cowden综合征或类似的称为Cowden样综合征的疾病。这些疾病的特征是多发性错构瘤的生长和罹患某些癌症的风险增加，尤其是乳腺癌，甲状腺癌和子宫内膜癌（子宫内膜癌）。Cowden综合征和Cowden样综合征被认为是PTEN错构瘤肿瘤综合征的一部分（如下所述）。导致Cowden综合征和Cowden样综合征的突变包括少量碱基对的变化，在某些情况下，还包括PTENgene中大量遗传物质的缺失。这些突变导致产生不能正常工作或根本不起作用的PTEN酶。改变的酶不能抑制细胞分裂或信号异常细胞死亡，这有助于错构瘤和癌性肿瘤的发展。"}, {"name": "乳腺癌", "detail": "PTENgene的遗传突变会增加患乳腺癌的风险。在许多情况下，这种增加的风险是考登综合症的一部分（如上所述）。PTENgene的遗传突变被认为仅占所有乳腺癌病例的一小部分。（换行）非遗传（体细胞）PTENgene突变发生在没有该疾病家族史的女性的一些乳腺癌中。体细胞突变不是遗传性的，也不会作为家族性癌症综合征的一部分发生。它们是在一个人的一生中获得的，只发生在乳房的某些细胞中。这些突变削弱了PTEN酶的抑癌功能，使细胞在没有控制或命令的情况下生长和分裂。这种不受控制的细胞生长有助于形成癌性肿瘤。研究表明，功能性PTEN酶的丧失还与对用于治疗乳腺癌的称为曲妥珠单抗（赫赛汀）的药物的不良反应有关。"}, {"name": "自闭症谱系障碍", "detail": "遗传学家庭参考提供有关自闭症谱系障碍的信息。"}, {"name": "头颈部鳞状细胞癌", "detail": "遗传学家庭参考提供有关头颈部鳞状细胞癌的信息。"}, {"name": "肺癌", "detail": "遗传学家庭参考提供有关肺癌的信息。"}, {"name": "前列腺癌", "detail": "遗传学家庭参考提供有关前列腺癌的信息。"}, {"name": "其他疾病", "detail": "由PTENgene突变引起的几种相关疾病，包括Bannayan-Riley-Ruvalcaba综合征和Cowden综合征，通常一起被视为PTEN错构瘤肿瘤综合征。导致这些病症的突变存在于整个身体的细胞中，并且通常是从亲本遗传的。导致PTEN错构瘤肿瘤综合征的某些突变会导致PTEN酶的缺陷版本，该版本无法发挥其作为肿瘤抑制因子的功能。其他突变阻止PTENgene完全产生任何酶。没有功能性PTEN酶，细胞分裂就无法有效控制，受损细胞继续不适当地分裂，从而导致错构瘤和其他肿瘤的发展。(换行)在一些已发表的病例报告中，PTENgene的突变与变形杆菌综合征有关，变形杆菌综合征是一种罕见病，其特征是骨骼，皮肤和其他组织不对称过度生长。但是，现在许多研究人员认为，具有PTENgene突变和不对称过度生长的个体不符合诊断变形杆菌综合征的严格指南。相反，这些人的病情被认为是PTEN错构瘤肿瘤综合征的一部分。针对该病提出的一个name是节段性过度生长，脂肪瘤病，动静脉畸形和表皮痣（SOLAMEN）综合征；另一种是2型节段性Cowden综合征。然而，一些科学文章仍然提到PTEN相关的变形杆菌综合征。(换行)PTENgene突变已在几名同时具有大头畸形和自闭症谱系障碍特征的人中发现，这会影响沟通和社交互动。这些突变中的许多改变了PTEN酶中的单个蛋白质结构单元（氨基酸），或导致异常短版本的酶的产生。尚不清楚PTENgene的变化如何与患自闭症谱系障碍的风险相关。在患有PTEN错构瘤肿瘤综合征的家庭中也已经报道了其中一些突变，目前尚不清楚这些突变如何引起不同的疾病。"}, {"name": "其他癌症", "detail": "PTENgene中的体细胞突变是人类癌症中最常见的遗传变化之一。与体细胞突变相关的癌症不是遗传性的，也不会作为癌症综合征的一部分发生。PTENgene的体细胞突变已在许多类型的癌症中报道，研究表明PTEN可能是前列腺癌和子宫内膜癌中最常见的突变gene。PTENgene突变也常见于称为胶质母细胞瘤和星形细胞瘤的脑肿瘤，以及称为黑素瘤的侵袭性皮肤癌。PTENgene中的突变降低或消除了PTEN酶的肿瘤抑制功能。这种酶功能的丧失可能使某些细胞无法控制地分裂，从而促进癌性肿瘤的生长。在某些情况下，PTENgene突变的存在与肿瘤生长的更晚期有关。"}]}' WHERE id = 10499;
UPDATE genes SET GHR_metadata_ch = '{"gene": "FREM2", "conditions": [{"name": "弗雷泽综合症", "detail": "已发现FREM2gene中至少有两个突变引起Fraser综合征；这些突变涉及这种情况的一小部分情况。弗雷泽综合症会影响出生前的发育，其特征是眼睛被皮肤完全覆盖（隐性眼球），手指和脚趾之间的皮肤融合（皮肤上有组织），以及肾脏和其他器官和组织的异常。（换行）Fraser综合征中涉及的FREM2gene突变导致产生异常的FREM2蛋白，该蛋白可能无法正常运行。结果，不能形成FRAS/FREM复合物。皮肤基底膜中缺乏FRAS/FREM复合物会导致皮肤顶层脱离，从而在发育过程中形成水泡。这些水泡可能会阻止出生前某些结构的正确形成，从而导致隐性眼球炎和皮肤综合症。尚不清楚缺乏FRAS/FREM复合物如何导致肾脏异常和Fraser综合征的其他问题。"}, {"name": "其他疾病", "detail": "在肾脏和泌尿道异常但没有Fraser综合征的其他体征和症状的人中也发现了FREM2gene的突变（如上所述）。此类异常归为先天性肾脏和泌尿道异常（CAKUT）。参与CAKUT的FREM2gene突变通常会改变FREM2蛋白中的单个蛋白结构单元（氨基酸）。研究人员推测，这些突变的影响比引起弗雷泽综合症的突变的影响要轻；一些FREM2蛋白功能可能仍然存在。这些gene突变如何影响FRAS/FREM复合体或导致肾脏发育不全和其他CAKUT尚不清楚。"}]}' WHERE id = 10524;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SLC1A3", "conditions": [{"name": "阵发性共济失调", "detail": "已经发现SLC1A3gene中的至少一种突变引起发作性共济失调6型（EA6）。该突变改变EAAT1蛋白中的单个蛋白质构建块（氨基酸），用位置290处的氨基酸精氨酸取代氨基酸脯氨酸（写为Pro290Arg）。研究表明，这种遗传变化可能会损害EAAT1从神经元之间的空间去除谷氨酸的能力。谷氨酸摄取受损可能会使大脑中的某些神经元过度兴奋，从而破坏这些细胞之间的正常通讯。尽管神经元之间信号传导的变化是发作性共济失调患者中不协调运动发作的基础，但尚不清楚谷氨酸转运的改变如何导致该病的特定特征。"}]}' WHERE id = 10551;
UPDATE genes SET GHR_metadata_ch = '{"gene": "IL1A", "conditions": [{"name": "强直性脊柱炎", "detail": "已经发现IL1Agene中的几种变异（多态性）影响强直性脊柱炎的风险。这些变异中的每一个都会改变白介素1α中的单个蛋白质结构单元（氨基酸）。目前尚不清楚这些变异如何改变蛋白质的功能。研究表明，IL1A变异的影响可能与白介素-1α在促进炎症中的作用有关。其他遗传和环境因素，其中许多是未知的，也影响发展强直性脊柱炎的机会。"}, {"name": "特发性炎症性肌病", "detail": "遗传学家庭参考提供有关特发性炎性肌病的信息。"}, {"name": "椎间盘疾病", "detail": "遗传学家庭参考提供有关椎间盘疾病的信息。"}, {"name": "圆锥角膜", "detail": "遗传学家庭参考提供有关圆锥角膜的信息。"}, {"name": "其他疾病", "detail": "已经研究了IL1Agene的变异作为与异常炎症相关的其他几种疾病的潜在危险因素。这些包括慢性牙龈（牙周）疾病，称为慢性骨髓炎的进行性骨感染和称为开角型青光眼的眼病。（换行）影响IL1Agene的最充分研究的变异是gene起始附近的调节DNA区域中单个DNA构建块（核苷酸）的变化（写为IL1A-889 C>T）。这种变化影响细胞内白细胞介素-1α的产生。研究人员还发现了IL1Agene的第二个常见变异，写为IL1A+4845 G>T，它改变了该gene中的单个核苷酸。这种变化可能影响白介素-1α对钙蛋白酶裂解的敏感性。(换行)目前尚不清楚IL1Agene的变化如何影响炎症性疾病的风险。研究表明，IL1A变异的影响可能与白介素-1α在促进炎症中的作用有关。其他遗传和环境因素也可能影响发展这些复杂疾病的机会。"}]}' WHERE id = 10557;
UPDATE genes SET GHR_metadata_ch = '{"gene": "GALK1", "conditions": [{"name": "半乳糖血症", "detail": "在患有半乳糖血症（称为II型或半乳糖激酶缺乏症）的人中，已经鉴定出GALK1gene的30多个突变。患病的婴儿会出现晶状体混浊（白内障），但除此之外，几乎没有与经典半乳糖血症相关的长期并发症。这些突变大多数会改变半乳糖激酶1中的单个蛋白质结构单元（氨基酸）。一些突变从GALK1gene中删除了少量遗传物质，导致该酶的不稳定或失活形式。(换行)功能性半乳糖激酶1的缺乏阻止细胞加工从饮食中获得的半乳糖。结果，半乳糖和称为半乳糖醇的相关糖可以积聚，特别是在构成眼睛晶状体的细胞中。这些物质的积累会损坏镜片，导致镜片变得混浊并导致视力模糊。"}]}' WHERE id = 10572;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ATP6V0A4", "conditions": [{"name": "肾小管性酸中毒伴耳聋", "detail": "在患有耳聋的肾小管性酸中毒患者中已鉴定出几种ATP6V0A4gene突变，该疾病涉及血液中过多的酸（代谢性酸中毒），骨骼无力以及内耳变化引起的听力损失（感觉神经性听力损失）。ATP6V0A4gene中的（换行）突变会损害V-ATPase质子泵的功能。结果，肾脏不能控制血液的酸度，这导致由骨矿物质损失（脱矿质）和代谢性酸中毒的其他后果引起的骨无力。身体控制内耳液体pH值的能力也受到损害，导致感觉神经性听力损失。"}, {"name": "其他疾病", "detail": "ATP6V0A4gene突变也已在诊断为听力正常的肾小管性酸中毒患者中发现，通常在婴儿期或儿童早期。然而，听力损失有时直到成年后才发展为伴有耳聋的肾小管性酸中毒（如上所述），因此其中一些人后来被发现患有这种疾病。"}]}' WHERE id = 10583;
UPDATE genes SET GHR_metadata_ch = '{"gene": "COMT", "conditions": [{"name": "22q11.2缺失综合征", "detail": "22q11.2缺失综合征的特征性体征和症状是由22号染色体的一小块缺失引起的。通常缺失的染色体区域包含30至40个gene，包括COMTgene。缺失的结果是，患有这种疾病的人每个细胞中只有一个COMTgene拷贝，而不是通常的两个拷贝。（换行）每个细胞中COMTgene的一个拷贝的丢失导致脑中儿茶酚-O-甲基转移酶水平的异常调节。研究人员认为，前额叶皮层中涉及这种酶的变化可能有助于解释与22q11.2缺失综合征相关的行为问题和精神疾病的风险增加。然而，关于儿茶酚-O-甲基转移酶活性与该病症特有的特定精神和情绪问题之间的关系知之甚少。患有22q11.2缺失综合征的人比没有这种情况的人更容易患精神分裂症，抑郁症，焦虑症和双相情感障碍。"}, {"name": "阿片成瘾", "detail": "遗传学家庭参考提供有关阿片类药物成瘾的信息。"}, {"name": "其他疾病", "detail": "COMTgene的变异也可能与没有22q11.2缺失综合征的人的精神疾病有关。研究人员广泛研究了COMTgene变化与精神分裂症风险之间的潜在联系。大多数研究集中在儿茶酚-O-甲基转移酶中特定常见变异（多态性）的影响。这种变化改变了酶中的单个蛋白质结构单元（氨基酸），用氨基酸蛋氨酸替代了氨基酸缬氨酸。在较长形式的酶中，这种变化发生在位置158（写为Val158Met）。在较短形式的酶中，它发生在位置108（写为Val108Met）。研究人员经常将此符号缩短为Val108/158Met。这种变化影响儿茶酚-O-甲基转移酶的稳定性和活性，这会改变酶分解前额叶皮层神经递质的能力。(换行)对精神分裂症患者Val108/158Met多态性的研究结果好坏参半。尽管大多数研究报告没有证据表明蛋氨酸或缬氨酸在该位置的风险增加，但一些研究发现缬氨酸在108/158位置的人患精神分裂症的风险略有增加。缬氨酸处于这一位置与精神分裂症患者常见的思维过程差异有关，包括工作记忆，行为抑制和注意力问题。COMTgene的其他变化也可能导致这些差异。COMTgene的变异是正在研究的许多因素之一，有助于解释精神分裂症的原因。大量的遗传和生活方式因素，其中大部分仍然未知，可能决定了患此病的风险。(换行)Val108/158Met多态性也与影响思想（认知）和情绪的其他疾病有关。例如，研究人员研究了这种变异作为双相情感障碍，恐慌症，焦虑症，强迫症（OCD），进食障碍和注意力缺陷多动障碍（ADHD）的可能危险因素。研究表明，这些情况可能与前额叶皮层信息处理效率低下有关。然而，与精神分裂症一样，许多因素在确定这些复杂疾病的风险中起作用。"}]}' WHERE id = 10596;
UPDATE genes SET GHR_metadata_ch = '{"gene": "VCAN", "conditions": [{"name": "瓦格纳综合症", "detail": "已发现VCANgene中至少有11个突变会引起Wagner综合征，这种情况会导致从儿童期或成年早期开始进行性视力丧失。导致Wagner综合征的VCANgene突变破坏了该gene说明用于制作versican的方式。这些突变发生在gene的两个区域，称为内含子7和外显子8；这些区域中的突变导致versican同工型V0和V1的产生减少，同工型V2和V3的产生增加。研究人员认为，玻璃体中versican同工型的这种失衡会削弱versican与其他蛋白质的相互作用，从而导致玻璃体变得不稳定。玻璃体缺乏稳定性会影响眼睛的其他区域，并导致瓦格纳综合症患者出现视力问题。尚不清楚为什么VCANgene突变似乎仅影响视力。"}]}' WHERE id = 10661;
UPDATE genes SET GHR_metadata_ch = '{"gene": "CDH1", "conditions": [{"name": "Blepharocheilodontic综合征", "detail": "CDH1gene的遗传突变已在人睑裂（BCD）综合征中得到鉴定，该综合征是一种以眼睑和嘴巴异常为特征的先天性疾病，包括双侧唇裂和c裂。这是由于有缺陷的E-钙粘蛋白影响颅面发育所致。"}, {"name": "乳腺癌", "detail": "CDH1gene的遗传突变与女性患乳腺癌的风险增加有关，乳腺癌是称为遗传性弥漫性胃癌（HDGC）的遗传性癌症的一部分。这是由于肿瘤抑制因子的丧失和E-钙粘蛋白的细胞粘附活性的丧失导致癌症和癌症转移。"}, {"name": "遗传性弥漫性胃癌", "detail": "已发现CDH1gene的遗传突变会导致家族性癌症，称为遗传性弥漫性胃癌（HDGC）。HDGC患者形成癌症需要与胃癌风险增加和其他体细胞突变相关的疾病。HDGC还与女性患乳腺癌的风险增加有关。该病症是由于肿瘤抑制因子的丧失和E-钙粘蛋白的细胞粘附活性的丧失导致癌症和癌症转移。"}, {"name": "卵巢癌", "detail": "CDH1gene中的几个突变与女性患卵巢癌的风险增加有关。该病症是由于E-钙粘蛋白的肿瘤抑制活性丧失导致卵巢中细胞的不受控制的生长和分裂而引起的。"}, {"name": "前列腺癌", "detail": "CDH1gene中的几个突变与男性患前列腺癌的风险增加有关。该病症是由于E-钙粘蛋白的肿瘤抑制活性丧失导致前列腺的不受控制的生长和分裂而引起的。"}, {"name": "其他疾病", "detail": "CDH1gene中的几个遗传突变与发展为唇裂，c裂或两者兼有（口面部裂口）的风险增加相关，而没有其他BCD综合征症状且有或没有HDGC家族史。该病症是由于改变的E-钙粘蛋白损害颅面发育而引起的。"}, {"name": "其他癌症", "detail": "CDH1gene中的几种体细胞突变与几种癌症的风险增加相关，包括女性的子宫内膜癌和卵巢癌以及男性的前列腺癌。"}]}' WHERE id = 10691;
UPDATE genes SET GHR_metadata_ch = '{"gene": "CDKN1C", "conditions": [{"name": "Beckwith-Wiedemann综合征", "detail": "CDKN1Cgene的一些突变与Beckwith-Wiedemann综合征有关，Beckwith-Wiedemann综合征的特征是导致婴儿过度生长，导致巨大儿并表现出其他症状，例如：；腹壁开口，脐疝，大舌症，内脏肥大，婴儿低血糖和肾脏异常。这种情况是由于调节CDKN1Cgene的IC2区域的母系遗传拷贝中的少量DNA的低甲基化或缺失或由于CDKN1Cgene的母系遗传拷贝内的突变导致其丧失肿瘤抑制活性。"}, {"name": "宫内生长受限，干phy端发育不良，先天性肾上腺发育不全和生殖器异常", "detail": "CDKN1Cgene的一些突变与宫内生长迟缓、干骺端发育不良、先天性肾上腺皮质发育不全和生殖器异常有关，通常称为IMAGe，这是一种罕见的综合征，影响身体许多部位的生长，是出生前后生长缓慢、骨骼异常、激素变化、，和男性生殖器异常。CDKN1C突变取代了单个氨基酸增殖细胞核抗原（PCNA）结合域，从而阻止了CDKN1C的正常分解并导致其功能获得。"}]}' WHERE id = 10696;
UPDATE genes SET GHR_metadata_ch = '{"gene": "XDH", "conditions": [{"name": "遗传性黄嘌呤尿症", "detail": "已发现XDHgene中至少有12个突变会导致I型遗传性黄嘌呤尿症，这种情况最常影响肾脏。这些突变降低或消除了黄嘌呤脱氢酶的活性。结果，该酶不能用于进行嘌呤分解的最后两个步骤。由于黄嘌呤不会转化为尿酸，因此受影响的人血液和尿液中的黄嘌呤水平很高，尿酸水平很低。过量的黄嘌呤可以积聚在肾脏和其他组织中。在肾脏中，黄嘌呤形成微小的晶体，偶尔会积聚形成肾结石。这些结石会损害肾功能并最终导致肾功能衰竭。不常见的是，黄嘌呤晶体积聚在肌肉中，引起疼痛和痉挛。在一些患有遗传性黄嘌呤尿症的人中，该病症不会引起任何健康问题。"}]}' WHERE id = 10720;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SDHD", "conditions": [{"name": "Hereditary paraganglioma-pheochromocytoma", "detail": "在遗传性副神经节瘤嗜铬细胞瘤1型患者中，已经鉴定出SDHDgene的100多个突变。患有这种疾病的人患有副神经节瘤，嗜铬细胞瘤或两者兼有。这些非癌性（良性）肿瘤与神经系统有关。遗传的SDHDgene突变使个体易患此病。需要删除gene正常拷贝的其他突变才能引起1型遗传性副神经节瘤嗜铬细胞瘤。第二种突变称为体细胞突变，是在一个人的一生中获得的，仅存在于肿瘤细胞中。（换行）大多数遗传的SDHDgene突变会改变SDHD蛋白序列中的单个蛋白结构单元（氨基酸）或导致蛋白缩短。结果，SDH酶活性很少或没有。由于突变的SDH酶无法将琥珀酸转化为富马酸，因此琥珀酸会在细胞中积累。过量的琥珀酸盐异常稳定HIF，HIF也在细胞中积聚。过量的HIF刺激细胞分裂并在不需要时触发血管的产生。快速和不受控制的细胞分裂以及新血管的形成可导致遗传性副神经节瘤嗜铬细胞瘤患者的肿瘤发展。"}, {"name": "非综合征性副神经节瘤", "detail": "在某些非综合征性副神经节瘤或嗜铬细胞瘤病例中发现了SDHDgene突变，这是该病的非遗传形式。这些突变大多数会改变SDHD蛋白中的单个氨基酸。与1型遗传性副神经节瘤嗜铬细胞瘤一样，这些突变有望降低SDH酶的活性，从而稳定HIF蛋白，使其在细胞中积聚。过量的HIF蛋白异常刺激细胞分裂和血管形成，这可能导致肿瘤形成。"}, {"name": "考登综合症", "detail": "在患有考登综合症或称为考登综合症的类似疾病的人中，已经鉴定出SDHDgene的至少五个变体。这些病症的特征是称为错构瘤的多种肿瘤样生长和发展某些癌症的风险增加。当SDHDgene突变引起Cowden综合征和Cowden样综合征时，该疾病与罹患乳腺癌和甲状腺癌的风险特别高有关。（换行）与Cowden综合征和Cowden样综合征相关的SDHDgene变体改变SDHD蛋白中的单个氨基酸，这可能改变SDH酶的功能。研究表明，有缺陷的酶可以使细胞不受控制地生长和分裂，从而导致错构瘤和癌性肿瘤的形成。但是，研究人员不确定所鉴定的SDHDgene变异是否与Cowden综合征和Cowden样综合征直接相关。没有这些conditions的特征，很少在人中发现上述某些变体。"}, {"name": "胃肠道间质瘤", "detail": "遗传学家庭参考提供有关胃肠道间质瘤的信息。"}, {"name": "其他癌症", "detail": "在少数患有Carney-Stratakis综合征的人中发现了SDHDgene的突变。患有这种病症的人患有一种称为胃肠道间质瘤（GIST）的胃肠道癌症和一种与神经系统相关的非癌性肿瘤，称为副神经节瘤或嗜铬细胞瘤（一种副神经节瘤）。遗传的SDHDgene突变使个体容易患癌症。需要删除gene正常拷贝的其他突变才能引起Carney-Stratakis综合征。第二种突变称为体细胞突变，是在一个人的一生中获得的，仅存在于肿瘤细胞中。SDHDgene的（换行）突变导致SDH酶活性丧失，从而导致异常的缺氧信号传导和肿瘤的形成。"}]}' WHERE id = 10741;
UPDATE genes SET GHR_metadata_ch = '{"gene": "LGI1", "conditions": [{"name": "常染色体显性遗传性部分性癫痫伴听觉特征", "detail": "在具有听觉特征的常染色体显性遗传性部分性癫痫患者（ADPEAF）中，LGI1gene至少鉴定出22个突变。一些LGI1突变会改变上皮蛋白中的单个蛋白结构单元（氨基酸），从而改变蛋白的结构。其他突变导致蛋白质异常短而无功能的形式的产生。研究人员怀疑，改变后的蛋白质无法分泌，这将使其被困在细胞内，无法执行其通常的功能。尽管LGI1突变破坏了上皮蛋白的功能，但尚不清楚这种改变的蛋白如何导致大脑癫痫发作。"}, {"name": "癌症", "detail": "当首次描述LGI1gene时，研究人员认为它可能在称为神经胶质瘤的脑肿瘤的生长和发展中起作用。Epitempin被认为是一种肿瘤抑制因子，它是一种蛋白质，可以防止细胞生长和分裂过快或以不受控制的方式。最近的研究质疑了上皮蛋白在癌性肿瘤中的作用。由于在神经胶质瘤中未发现LGI1突变，并且患有ADPEAF的人似乎没有大大增加患这些肿瘤的风险，因此现在看来，上皮蛋白不太可能起到抑癌作用。"}]}' WHERE id = 10768;
UPDATE genes SET GHR_metadata_ch = '{"gene": "MAT1A", "conditions": [{"name": "高蛋氨酸血症", "detail": "已发现MAT1Agene中约有15个突变会降低蛋氨酸腺苷转移酶的活性。这些突变中的大多数将一个氨基酸替换为酶中的另一个氨基酸，从而使其处理蛋氨酸的效率降低。其他突变在制备蛋氨酸腺苷基转移酶的说明中引入了过早的终止信号。结果，产生缩短的无功能酶。蛋氨酸腺苷基转移酶功能的降低导致体内蛋氨酸的积累和AdoMet生产效率的降低，并且在严重的情况下会引起神经系统问题。"}]}' WHERE id = 10788;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SLC12A6", "conditions": [{"name": "安德森综合症", "detail": "在患有Andermann综合征的人中至少鉴定出六个SLC12A6gene突变。法裔加拿大人后裔的几乎所有受影响个体在SLC12A6gene的两个拷贝中都具有相同的突变，其中DNA构建块（核苷酸）鸟嘌呤在2436位缺失（写为2436delG）。这种突变在Saguenay-Lac-St种群中很常见-魁北克东北部的Jean和Charlevoix地区。导致Andermann综合征的大多数SLC12A6gene突变会导致K-Cl共转运蛋白缩短且无功能。(换行)SLC12A6gene产生的功能蛋白缺乏被认为会干扰call体的发育和神经的维持，这些神经传递运动和感觉所需的信号，从而导致Andermann综合征的体征和症状。"}]}' WHERE id = 10799;
UPDATE genes SET GHR_metadata_ch = '{"gene": "CDAN1", "conditions": [{"name": "先天性贫血性贫血", "detail": "在患有先天性贫血性贫血（CDA）I型的人中，已经鉴定出CDAN1gene的几个突变，该疾病的特征是贫血，肝脾肿大和铁的异常积累导致器官损伤。这是由于缺乏破坏红细胞正常发育的功能性codanin-1引起的。"}]}' WHERE id = 10815;
UPDATE genes SET GHR_metadata_ch = '{"gene": "EZH2", "conditions": [{"name": "韦弗综合症", "detail": "在Weaver综合征患者中已鉴定出30多个EZH2gene突变，涉及身材高大，智力残疾程度不同（通常为轻度）和特征性的面部特征。这些功能可以包括宽阔的额头；眼睛间距大（超视力）；大而低沉的耳朵；下巴凹陷；下颌小（微棘皮症）。一些受影响的人头部较大（大头畸形）。与Weaver综合征相关的大多数EZH2gene突变会改变EZH2酶中的单个蛋白质结构单元（氨基酸）；其他人从EZH2gene中插入或删除少量遗传物质，导致产生改变的EZH2酶。目前尚不清楚这些EZH2gene突变如何导致Weaver综合征的异常特征。"}, {"name": "前列腺癌", "detail": "遗传学家庭参考提供有关前列腺癌的信息。"}, {"name": "癌症", "detail": "EZH2gene的变化与各种类型的癌症有关。已经在血液形成组织（淋巴瘤和白血病）的癌症中鉴定了该gene的突变。这些突变被描述为“功能获得”，因为它们似乎增强了EZH2酶的活性或为该酶提供了新的非典型功能。此外，已经在前列腺，乳腺和身体其他部位的癌性肿瘤中鉴定出EZH2gene的过度活性（过表达）。涉及EZH2gene的变化可能损害细胞分裂（增殖）的正常控制，使细胞生长和分裂过快或以不受控制的方式生长和分裂，并导致癌症的发展。"}]}' WHERE id = 10823;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ABHD5", "conditions": [{"name": "Chanarin-Dorfman综合征", "detail": "ABHD5中的突变会导致Chanarin-Dorfman综合征，这是由于蛋白质缺陷导致ATGL酶激活失败和甘油三酸酯分解导致脂肪在人体各个器官和组织中积聚所致。"}]}' WHERE id = 10826;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ANK1", "conditions": [{"name": "遗传性球形红细胞增多症", "detail": "ANK1gene的突变与遗传性球囊增多症有关，遗传性球囊增多症是一种以锚蛋白1蛋白缺陷引起的贫血，黄疸和脾肿大为特征的疾病。"}]}' WHERE id = 10854;
UPDATE genes SET GHR_metadata_ch = '{"gene": "RPS6KA3", "conditions": [{"name": "棺材-劳里综合征", "detail": "在Coffin-Lowry综合征患者中已鉴定出RPS6KA3gene的125个以上突变，该疾病与智力残疾和骨骼异常有关。所有这些突变严重降低或消除了RPS6KA3蛋白的活性。一些突变插入或删除gene中的遗传物质或改变gene指令如何用于构建蛋白质。其他突变会改变RPS6KA3蛋白中的单个蛋白结构单元（氨基酸）。(换行)研究人员不完全了解RPS6KA3gene的突变如何导致Coffin-Lowry综合征的体征和症状。功能性RPS6KA3蛋白似乎对学习和记忆很重要，但其在骨骼中的作用尚不清楚。"}, {"name": "其他疾病", "detail": "RPS6KA3突变已在一些智障人士中发现，但没有Coffin-Lowry综合征的大多数其他特征。由于RPS6KA3gene位于X染色体上，因此这种情况称为X连锁智力障碍。研究人员认为，RPS6KA3突变是智力残疾的罕见原因，因为只有少数受影响的家庭具有该gene的突变。"}]}' WHERE id = 10891;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ORC1", "conditions": [{"name": "梅尔-戈林综合征", "detail": "ORC1gene的突变会导致Meier-Gorlin综合征，这种疾病的特征是身材矮小，膝盖发育不全和耳朵小。这些突变通常通过改变单个蛋白质构建块（氨基酸）或导致产生异常短版本的ORC1蛋白质来改变ORC1蛋白质。结果，复制前复合体的组装受到损害，这破坏了复制许可；但是，尚不清楚复制许可证的减少如何导致Meier-Gorlin综合征。研究人员推测，这种减少会延迟细胞分裂过程，从而减慢发育过程中骨骼和其他组织的生长，并可能导致疾病的特征。一些研究表明，ORC1的改变导致中心体和中心体的拷贝过多，这也可能延迟细胞分裂。其他研究表明，ORC1的变化会损害纤毛的功能，这可能会延迟某些组织的发育，并成为Meier-Gorlin综合征特征性膝关节和耳朵异常发育的基础。"}]}' WHERE id = 10892;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SLC25A15", "conditions": [{"name": "鸟氨酸转位酶缺乏症", "detail": "在受鸟氨酸转位酶缺乏症影响的个体中，已鉴定出SLC25A15gene中约17个突变。突变导致线粒体鸟氨酸转运蛋白不稳定，形状错误或缺乏将鸟氨酸带入线粒体基质的能力。鸟氨酸运输的这种失败导致尿素循环的中断和氨的积累，从而导致鸟氨酸转位酶缺乏症的体征和症状。"}]}' WHERE id = 10895;
UPDATE genes SET GHR_metadata_ch = '{"gene": "LAMB3", "conditions": [{"name": "交界性大疱性表皮松解症", "detail": "在患有大疱性表皮松解症（JEB）的人中，已经鉴定出LAMB3gene的80多个突变。该疾病的更严重形式称为Herlitz JEB，通常是由严重破坏功能性层粘连蛋白332产生的突变引起的。这些突变中的大多数会在制备层粘连蛋白332的β亚基的说明中导致过早的终止信号，从而阻止了该蛋白的组装。没有层粘连蛋白332，表皮仅微弱地连接到皮肤的下层。摩擦或其他轻微创伤（例如摩擦或刮擦）可导致皮肤层分离，导致形成水泡。患有Herlitz JEB的婴儿会出现广泛的水疱，从而导致危及生命的并发症。（换行）其他LAMB3gene突变导致较温和形式的交界性表皮松解性大疱性非Herlitz JEB。这些突变中的一些改变了层粘连蛋白332的β亚基中的单个蛋白质结构单元（氨基酸）。其他人在β亚基中添加或删除少量氨基酸或改变gene指令用于制备亚基的方式。负责非Herlitz JEB的遗传变化通常会导致层粘连蛋白332蛋白的产生，该蛋白保留了其某些功能。受影响的人会出现水泡，但可能仅限于手，脚，膝盖和肘部，并且在新生儿期后通常会有所改善。"}, {"name": "釉质发育不全", "detail": "遗传学家庭参考资料提供有关牙釉质发育不全的信息。"}]}' WHERE id = 10906;
UPDATE genes SET GHR_metadata_ch = '{"gene": "MKKS", "conditions": [{"name": "McKusick-Kaufman综合征", "detail": "在旧秩序阿米什人中，患有McKusick-Kaufman综合征的人中发现了MKKSgene的两个突变。这些突变中的每一个都会改变MKKS蛋白中的单个蛋白结构单元（氨基酸）。一个突变用蛋白质位置84处的氨基酸酪氨酸取代氨基酸组氨酸（写为His84Tyr或H84Y）。另一个突变用蛋白质242位的氨基酸丝氨酸取代了氨基酸丙氨酸（写为Ala242Ser或A242S）。受影响的阿米什人在MKKSgene的两个副本中都有这两个突变。（换行）McKusick-Kaufman综合征的基础突变改变了MKKS蛋白的结构。尽管改变后的蛋白质会破坏出生前身体多个部位的发育，但尚不清楚MKKS突变如何导致该疾病的特定特征。(换行)McKusick-Kaufman综合征的体征和症状与另一种称为Bardet-Biedl综合征的疾病的体征和症状明显重叠，这可能使这两种疾病在婴儿期和幼儿期难以区分。尽管这两种综合征都可能由MKKSgene的变化引起，但仍不清楚为什么某些突变会导致McKusick-Kaufman综合征，而另一些突变会导致Bardet-Biedl综合征。"}, {"name": "巴德-毕德氏症候群", "detail": "遗传学家庭参考提供有关Bardet-Biedl综合征的信息。"}]}' WHERE id = 10933;
UPDATE genes SET GHR_metadata_ch = '{"gene": "AGPS", "conditions": [{"name": "点状软骨发育不良", "detail": "已经发现AGPSgene中的几个突变引起点状软骨发育不良3型（RCDP3），这是由于烷基甘油磷酸合酶缺陷引起的疾病，其破坏过氧化物酶体功能并减少细胞内缩醛磷脂的量。该疾病的特征是骨骼异常，上臂和大腿（根状茎）的骨骼缩短，独特的面部特征，智力残疾和呼吸系统疾病。"}]}' WHERE id = 10946;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SATB2", "conditions": [{"name": "SATB2相关综合征", "detail": "已经发现SATB2gene中的突变引起SATB2相关综合征。患有这种情况的人有智力障碍和严重的言语问题。它们也可能在嘴顶有开口，牙齿异常或头部和面部的其他异常（颅面异常）。这些突变中的一些是去除了包括SATB2在内的多个gene的大片DNA的缺失。其他突变会在SATB2gene内添加，删除或重排较小的DNA片段。还有其他突变会改变SATB2gene中的单个DNA构建基块（核苷酸）。这些遗传变化可能会减少功能性SATB2蛋白的数量。SATB2功能的降低被认为会损害大脑和颅面结构的正常发育，从而导致智力残疾，言语延迟，颅面异常和SATB2相关综合征的其他特征。(换行)SATB2相关综合征的体征和症状通常相似，无论引起突变的类型如何。然而，一些具有大缺失的个体，包括其他gene，具有罕见的病症特征，例如心脏问题，生殖器和泌尿道（泌尿生殖道），皮肤或头发。这些特征被认为与SATB2附近其他gene的丢失有关。"}]}' WHERE id = 10968;
UPDATE genes SET GHR_metadata_ch = '{"gene": "APTX", "conditions": [{"name": "共济失调伴动眼性失用症", "detail": "已经发现APTXgene的突变会导致1型动眼性失用症共济失调，这种疾病的特征是共济失调和眼睛左右运动的问题（动眼性失用症）。它是由于aprataxin缺陷引起的，该缺陷破坏了其修复DNA损伤的能力，并主要影响导致小脑死亡的脑细胞。"}]}' WHERE id = 10976;
UPDATE genes SET GHR_metadata_ch = '{"gene": "JAG1", "conditions": [{"name": "阿拉吉欧综合症", "detail": "在患有Alagille综合征的人中，JAG1gene至少鉴定出226个突变。这些突变中的大多数会导致异常短的Jagged-1蛋白缺失通常跨越细胞膜（跨膜结构域）的片段。其他突变会干扰蛋白质在细胞内的正确运输（运输），从而阻止其到达细胞膜。Jagged-1蛋白在细胞膜上的丢失阻止了其与Notch蛋白的相互作用并阻止了细胞信号传导。Notch信号的缺乏会导致发育错误，从而导致肝脏胆管缺失或变窄，心脏缺陷，独特的面部特征以及身体其他部位的变化。具有JAG1gene突变的人可能有一个或多个这些问题。特别是，一些受影响的个体具有称为法洛四联症的心脏缺陷的特定组合，而没有Alagille综合征的其他体征或症状。即使在同一个家庭中，与Alagille综合征相关的问题的类型和严重性也可能有所不同。"}, {"name": "癌症", "detail": "JAG1gene活性（表达）的增加与某些癌症有关，包括乳腺癌和头颈部肿瘤。JAG1gene表达的增加可能促进滋养生长中的肿瘤的新血管的发育。改变的gene表达还可以增强其他癌症相关事件，例如细胞分裂（增殖）和炎症反应。"}]}' WHERE id = 10987;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ITGA6", "conditions": [{"name": "大疱性表皮松解症伴幽门闭锁", "detail": "已发现ITGA6gene中至少有五个突变会导致大疱性表皮松解并伴有幽门闭锁（EB-PA）。除皮肤起泡外，EB-PA患者天生还具有威胁生命的消化道阻塞，称为幽门闭锁。ITGA6gene突变约占所有EB-PA病例的5%。（换行）负责EB-PA的ITGA6gene突变导致功能丧失？6?4整合素。这些突变改变了？6整联蛋白亚基或阻止细胞产生足够的该亚基。由此导致的功能短缺？6?4整合素导致表皮细胞脆弱，容易受损。摩擦或其他轻微创伤可导致皮肤层分离，导致水泡广泛形成。尚不清楚ITGA6gene的突变如何与幽门闭锁相关。"}, {"name": "前列腺癌", "detail": "遗传学家庭参考提供有关前列腺癌的信息。"}, {"name": "癌症", "detail": "研究人员相信这两者？6?1整合素和？6?4整合素可能在称为癌的癌性肿瘤的进展中起关键作用。这些癌症出现在上皮细胞中，并可能影响许多组织和器官，包括乳腺癌，肺癌，肝癌，前列腺癌和皮肤癌。（折线）的位置和活动的变化？6?1整合素和？6?4癌细胞内的整合素与癌症的进展有关。整合素蛋白激活关键信号分子，触发癌细胞通过身体迁移并侵入其他组织。这些信号还使癌细胞更能抵抗自我破坏（细胞凋亡）。(换行)最近的研究表明，除了它们在现有癌症进展中的作用外？6?1整合素和？6?4整合素可能参与这些肿瘤的初始形成。"}]}' WHERE id = 11004;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ALMS1", "conditions": [{"name": "阿尔斯特伦症候群", "detail": "已经在患有Alström综合征的人中鉴定出ALMS1gene的突变，该疾病是由于大脑中蛋白质缺陷/异常导致进食过多和胰腺引起胰岛素抵抗导致糖尿病和肥胖症引起的。它还与人体许多组织和器官中的纤毛功能障碍有关。"}]}' WHERE id = 11013;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SLC52A3", "conditions": [{"name": "核黄素转运蛋白缺乏症神经病", "detail": "已经发现SLC52A3gene中的二十多个突变引起核黄素转运蛋白缺乏性神经病。这种神经系统疾病包括两种以前被认为是分开的疾病：Brown-Vialetto-Van Laere综合征和Fazio Londe病。这种情况下涉及的gene突变导致异常RFVT3蛋白的产生。一些突变导致产生不能到达细胞膜的改变的蛋白质，因此它不能将核黄素转运到细胞中。其他突变导致可以到达细胞膜的蛋白质版本，但其作为转运蛋白的功能受损。这些变化会损害小肠中核黄素的吸收。由此导致的核黄素短缺导致FAD和FMN减少。但是，尚不清楚这些变化如何导致神经问题，从而导致听力下降，面部和四肢肌肉无力以及患有该疾病的人的呼吸问题。"}]}' WHERE id = 11148;
UPDATE genes SET GHR_metadata_ch = '{"gene": "PNKD", "conditions": [{"name": "家族性阵发性非运动原性运动障碍", "detail": "已经显示PNKDgene中的至少三个突变引起家族性阵发性非运动原性运动障碍，其特征在于不自主运动的发作。两个最常见的突变，每个都在几个受影响的家族中发现，用PNKD蛋白中的氨基酸缬氨酸取代了蛋白质结构单元（氨基酸）丙氨酸。其中一个突变发生在位置7（写为Ala7Val或A7V），另一个突变发生在位置9（写为Ala9Val或A9V）。研究表明，PNKDgene突变会改变PNKD蛋白的结构并干扰其功能。尚不清楚PNKDgene的突变如何导致家族性阵发性非运动原性运动障碍的体征和症状。"}]}' WHERE id = 11170;
UPDATE genes SET GHR_metadata_ch = '{"gene": "IRF6", "conditions": [{"name": "lite肉翼状syndrome肉综合征", "detail": "导致pop肉翼状syndrome肉综合征的IRF6gene突变可能会改变转录因子对某些gene活性的影响。这会影响面部，皮肤和生殖器中组织的发育和成熟，从而导致面部和生殖器异常，皮肤织带以及pop肉翼状syndrome肉综合征中手指或脚趾的融合（综合征）。"}, {"name": "范德沃德综合症", "detail": "导致范德沃德综合症的IRF6gene突变会阻止每个细胞中该gene的一个拷贝产生任何功能蛋白。IRF6蛋白的缺乏会影响颅骨和面部组织的发育和成熟。这些异常是范德沃德综合症的体征和症状的基础，包括唇裂，c裂（口腔顶部的开口）以及下唇的凹坑或土墩。"}, {"name": "其他疾病", "detail": "IRF6gene的某些变异与唇裂，pa裂或两者兼有的风险增加有关。当这些特征出现而没有其他体征或症状时，该病症称为孤立的唇裂和/或腭裂。据信IRF6gene变异会影响IRF6蛋白作为转录因子的功能，这可能会干扰面部的正常发育。"}]}' WHERE id = 11174;
UPDATE genes SET GHR_metadata_ch = '{"gene": "LPL", "conditions": [{"name": "家族性脂蛋白脂肪酶缺乏症", "detail": "已发现LPLgene中的220多个突变会导致家族性脂蛋白脂肪酶缺乏症。这种情况破坏了体内甘油三酸酯的正常分解，导致这些脂肪增加。在欧洲血统的人中，最常见的突变是用酶中第188位的氨基酸谷氨酸（写为Gly188Glu或G188E）代替蛋白质结构单元（氨基酸）甘氨酸。导致家族性脂蛋白脂肪酶缺乏症的突变会降低或消除脂蛋白脂肪酶活性，从而阻止该酶有效分解血液中的甘油三酸酯。结果，附着于脂蛋白的甘油三酯积聚在血液和组织中，导致胰腺炎症（胰腺炎），肝脏和脾脏肿大（肝脾肿大），皮肤脂肪沉积（爆发性黄瘤）以及其他体征和家族性脂蛋白脂肪酶缺乏症的症状。"}, {"name": "其他疾病", "detail": "LPLgene的某些变异已显示会影响血液中的脂肪水平。LPLgene变异可能导致脂蛋白脂肪酶的产生，其分解甘油三酸酯的能力发生改变。在某些情况下，酶过度活跃，导致脂肪含量低。在其他情况下，酶受损，导致脂肪水平升高，这种情况称为高脂血症。高脂血症患者发生动脉粥样硬化的风险高于正常风险，动脉粥样硬化是脂肪沉积积聚在动脉壁上的一种情况。这种脂肪物质会随着时间的推移而硬化，最终阻塞动脉并增加心脏病发作或中风的机会。目前尚不清楚LPLgene变异在动脉粥样硬化的发展中起多大作用，因为大量的遗传和环境因素决定了发展这种复杂疾病的风险。"}]}' WHERE id = 11190;
UPDATE genes SET GHR_metadata_ch = '{"gene": "CLCF1", "conditions": [{"name": "冷诱发出汗综合征", "detail": "据报道，CLCF1gene中至少有四个突变会引起冷诱发出汗综合症，这是一种罕见病，其特征是调节体温和影响身体许多部位的其他异常问题。当这种情况是由CLCF1gene突变引起的时，它被称为CISS2。CLCF1gene中的（换行）突变导致CLCF1蛋白的非功能性版本的产生。缺陷蛋白无法与CLRF1蛋白相互作用并与CNTFR结合，从而禁用CNTFR信号通路。(换行)研究人员认为，CNTFR信号传导失败是冷诱发出汗综合征的主要特征。交感神经系统发育过程中该信号通路的丧失可能有助于解释这种情况的特征性异常出汗，包括异常出汗模式和体温调节的相关问题。CNTFR途径参与运动神经元发育和骨骼发育，为该疾病的其他一些体征和症状提供了线索，包括独特的面部特征，面部肌肉无力和骨骼异常。但是，关于缺乏CNTFR信号如何导致这些变化的特征知之甚少。"}]}' WHERE id = 11209;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SMS", "conditions": [{"name": "斯奈德-罗宾逊综合症", "detail": "已发现SMSgene中至少有四个突变会引起Snyder-Robinson综合征，这是一种以智力残疾以及骨骼和肌肉异常为特征的疾病。这种情况只发生在男性身上。大多数已识别的突变会改变精胺合酶中的单个蛋白质结构单元（氨基酸）。所有已知的突变都大大降低或消除了精胺合酶的活性，从而减少了细胞中精胺的量。这种多胺的缺乏显然会影响正常发育，包括大脑，肌肉和骨骼的发育，但尚不清楚它如何导致Snyder-Robinson综合征的特定体征和症状。"}]}' WHERE id = 11212;
UPDATE genes SET GHR_metadata_ch = '{"gene": "RSPO4", "conditions": [{"name": "先天性甲虫", "detail": "已发现RSPO4gene中至少有17个突变会导致先天性甲虫。这种情况的特征是出生时没有指甲和脚趾甲（甲癣）。几乎所有导致这种情况的RSPO4gene突变都会影响弗林蛋白酶样结构域，从而损害蛋白质功能。一些突变破坏了弗林蛋白酶样结构域的结构，而另一些突变导致产生了不包含这些结构域的异常短蛋白。结果，R-spondin-4不能参与Wnt信号通路，指甲发育不正常或根本没有发育。"}]}' WHERE id = 11224;
UPDATE genes SET GHR_metadata_ch = '{"gene": "PTS", "conditions": [{"name": "四氢生物蝶呤缺乏症", "detail": "已发现PTSgene中超过45个突变导致四氢生物蝶呤缺乏。当这种情况是由PTSgene突变引起的时，它被称为6-丙酮酰四氢蝶呤合酶（PTS）缺乏症。PTS缺乏症占所有四氢生物蝶呤缺乏症病例的一半以上。(换行)大多数PTSgene突变改变6-丙酮酰四氢蝶呤合酶中的单个氨基酸，尽管一些突变插入或删除PTSgene中的少量DNA或破坏gene说明用于制备酶的方式。6-丙酮酰四氢蝶呤合酶的变化大大降低或消除了酶的活性。没有足够的这种酶，几乎不会或根本不会产生四氢生物蝶呤。结果，该辅因子不能参与化学反应，例如苯丙氨酸向酪氨酸的转化。如果苯丙氨酸不转化为酪氨酸，它可以在血液和其他组织中积累到毒性水平。大脑中的神经细胞对苯丙氨酸水平特别敏感，这就是为什么过量的这种物质会导致脑损伤的原因。(换行)另外，6-丙酮酰四氢蝶呤合酶活性的降低破坏了大脑中某些神经递质的产生。由于神经递质是正常大脑功能所必需的，因此这些化学物质水平的变化会导致PTS缺乏症患者的智力残疾。"}]}' WHERE id = 11238;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SYNGAP1", "conditions": [{"name": "SYNGAP1相关的智力障碍", "detail": "已发现SYNGAP1gene中至少有40个突变会导致SYNGAP1相关的智力障碍。除了轻度至中度智力障碍外，这种情况还通常伴有其他神经系统问题，包括反复发作（癫痫）和自闭症谱系障碍，这会影响沟通和社交互动。与SYNGAP1相关的智力障碍有关的gene突变阻止了该gene一个拷贝产生功能性SynGAP蛋白，从而降低了该蛋白在细胞中的活性。研究表明，SynGAP活性的降低可在神经细胞中产生多种作用，包括推动突触过早发育（成熟）。SynGAP活性降低触发的变化破坏了大脑中作为学习和记忆基础的突触适应，从而导致认知障碍和SYNGAP1相关智力障碍的其他神经系统问题。"}, {"name": "自闭症谱系障碍", "detail": "自闭症谱系障碍（ASD）患者中至少鉴定出5个SYNGAP1gene突变，这种疾病在儿童发育早期就出现，严重程度各不相同，其特征是社交技能受损，沟通问题和重复行为。这些突变导致SynGAP蛋白功能受损或阻止该蛋白的产生。具有这些突变的个体中突触适应的变化可能是ASD行为异常特征的基础。尚不清楚为什么某些具有SYNGAP1gene突变的人会发展为ASD，而另一些人则具有与SYNGAP1相关的智力障碍的其他特征（如上所述）。"}]}' WHERE id = 11255;
UPDATE genes SET GHR_metadata_ch = '{"gene": "DOLK", "conditions": [{"name": "DOLK先天性糖基化障碍", "detail": "已发现DOLKgene中至少有六个突变会导致DOLK先天性糖基化疾病（DOLK-CDG，以前称为Im型先天性糖基化疾病）。这种情况通常会导致心脏衰弱和扩大（扩张型心肌病），但也可能导致神经系统问题以及其他体征和症状。（换行）DOLKgene突变会改变二元醇激酶中的单个蛋白质结构单元（氨基酸），从而导致酶的活性降低或缺失。如果没有功能正常的二元醇激酶，则不会产生磷酸二元醇，糖基化也无法正常进行。特别是，一种已知的稳定心肌纤维的蛋白质，称为α-dystroglycan，已被证明在DOLK-CDG患者中具有降低的糖基化作用。α-dystroglycan的糖基化受损会破坏其正常功能，当心肌纤维反复收缩和放松时会损害其正常功能。随着时间的推移，纤维减弱并分解，导致扩张型心肌病。DOLK-CDG的其他体征和症状可能是由于其他器官和组织中其他蛋白质的糖基化异常所致。"}]}' WHERE id = 11300;
UPDATE genes SET GHR_metadata_ch = '{"gene": "DVL1", "conditions": [{"name": "罗宾诺综合症", "detail": "已发现DVL1gene中至少有六个突变会导致Robinow综合征的常染色体显性遗传形式，这种情况会影响身体许多部位，尤其是骨骼的发育。常染色体显性遗传意味着每个细胞中一个拷贝的改变的gene足以引起疾病。DVL1gene突变是这种情况的变异类型的基础，称为骨硬化形式。骨硬化形式的体征和症状除了Robinow综合征的常见特征外，还包括骨矿物质密度增加（骨硬化）。（换行）所有已识别的DVL1gene突变都发生在称为外显子14的gene区域。预计每个突变都会从DVL1蛋白的一端去除一段蛋白结构单元（氨基酸），并添加100多个新氨基酸。研究人员正在努力确定这些结构变化如何影响蛋白质的功能。这些变化可能具有显性负效应，这意味着从DVL1gene的一个拷贝产生的改变的蛋白质干扰从该gene的另一个拷贝产生的正常蛋白质的功能。或者，这些变化可能会产生功能获得效应，从而使改变后的蛋白质具有新的功能，但尚未确定。无论哪种方式，改变的蛋白质都可能损害Wnt信号传导。这些信号通路的问题破坏了许多器官和组织的正常发育，从而导致了Robinow综合征的特征。目前尚不清楚为什么DVL1gene突变除了通常的症状和体征外还会引起骨硬化。"}]}' WHERE id = 11312;
UPDATE genes SET GHR_metadata_ch = '{"gene": "PHEX", "conditions": [{"name": "遗传性低磷佝偻病", "detail": "已发现PHEXgene中的200多个突变会导致最常见的遗传性低磷病，即X连锁低磷病。这些突变使PHEX酶失活，使其无法裂解其他蛋白质。(换行)研究人员不确定PHEXgene的突变如何导致血液中磷酸盐水平低（低磷血症）以及X连锁低磷病患者骨骼生长的相关问题。由于许多受影响的个体的成纤维细胞生长因子23水平升高，因此PHEXgene突变很可能以某种方式改变了该蛋白质的产生。成纤维细胞生长因子23的增加减少了肾脏对磷酸盐的重吸收，导致低磷血症。但是，由于一些受影响的个体的成纤维细胞生长因子23水平正常，因此研究人员还正在考虑其他途径，通过这些途径，突变的PHEXgene可能导致X连锁的低磷酸盐血症性rick病。"}]}' WHERE id = 11328;
UPDATE genes SET GHR_metadata_ch = '{"gene": "PRF1", "conditions": [{"name": "家族性吞噬性淋巴细胞组织细胞增生症", "detail": "在家族性吞噬性淋巴细胞组织细胞增生症患者中已鉴定出90多个PRF1gene突变。这些突变导致穿孔素蛋白缺陷的产生或阻止穿孔素的产生。由此导致的功能性穿孔素短缺，使其无法在细胞破坏和免疫系统调节中发挥作用，从而导致家族性吞噬性淋巴细胞组织细胞增生症的过度免疫反应特征。"}, {"name": "癌症", "detail": "具有PRF1gene突变的人罹患血液形成细胞（白血病和淋巴瘤）癌症的风险增加。这些个体中的一些还具有家族性吞噬性淋巴细胞组织细胞增生症。PRF1gene突变会损害免疫系统破坏异常细胞的能力，使其以不受控制的方式生长和分裂，并导致癌症的发展。"}]}' WHERE id = 11349;
UPDATE genes SET GHR_metadata_ch = '{"gene": "NIPBL", "conditions": [{"name": "Cornelia de Lange综合征", "detail": "在患有Cornelia de Lange综合征（一种影响身体许多部位的发育障碍）的人中，已经鉴定出NIPBLgene的300多个突变。该gene的突变是Cornelia de Lange综合征最常见的已知原因，占所有病例的一半以上。(换行)已经报道了许多不同种类的NIPBLgene突变；大多数导致从每个细胞中的一个gene拷贝产生异常短的（截短的），无功能的delangin蛋白形式。这些突变减少了细胞中产生的delangin的总量，这可能会改变粘着蛋白复合物的活性，并削弱其调节对正常发育至关重要的gene的能力。尽管研究人员尚不完全了解这些变化如何引起Cornelia de Lange综合征，但他们怀疑gene调控的改变可能是该病特征性的许多发育问题的基础。研究表明，与导致蛋白质部分功能性版本的突变相比，导致非功能性版本的delangin的突变倾向于引起更严重的体征和症状。"}]}' WHERE id = 11352;
UPDATE genes SET GHR_metadata_ch = '{"gene": "IL17RC", "conditions": [{"name": "家族性念珠菌病", "detail": "在家族性念珠菌病患者中已经鉴定出IL17RCgene中的至少三个突变，家族性念珠菌病是由念珠菌真菌引起的感染（通常称为酵母感染）的遗传倾向。大多数患有家族性念珠菌病的人从幼儿期开始就患有皮肤，指甲和粘膜的慢性酵母感染（统称为慢性粘膜皮肤念珠菌病）。（换行）IL17RCgene突变导致不存在IL17RC受体蛋白。该蛋白质的缺失损害了IL-17细胞因子和IL-17信号传导的结合。这种受损的信号传导减少了对抗念珠菌至关重要的抗微生物肽和炎性分子的产生，并导致家族性念珠菌病中发生的慢性感染。"}]}' WHERE id = 11354;
UPDATE genes SET GHR_metadata_ch = '{"gene": "UNC13D", "conditions": [{"name": "家族性吞噬性淋巴细胞组织细胞增生症", "detail": "在家族性吞噬性淋巴细胞组织细胞增生症患者中已鉴定出50多个UNC13Dgene突变。这些突变中的大多数会改变gene指令拼接在一起以产生UNC13D蛋白的方式，从而导致蛋白功能异常。由此导致的功能性UNC13D蛋白短缺会干扰其在细胞破坏和免疫系统调节中的作用，从而导致家族性吞噬性淋巴细胞组织细胞增生症的过度免疫反应特征。"}, {"name": "其他疾病", "detail": "A variant (polymorphism) of the UNC13D gene has been associated with a higher risk of a complication called macrophage activation syndrome in people with systemic juvenile idiopathic arthritis (SJIA). SJIA is an autoimmune disorder that causes persistent joint inflammation beginning in childhood. Autoimmune disorders occur when the immune system malfunctions and attacks the body''s own tissues and organs. Macrophage activation syndrome is a life-threatening complication of SJIA with symptoms similar to those of familial hemophagocytic lymphohistiocytosis, including fever, an enlarged liver and spleen, liver damage, and low numbers of blood cells. (line-break) The UNC13D gene variant associated with macrophage activation syndrome in people with SJIA consists of a specific combination of individual changes in 12 DNA building blocks (nucleotides). It is unknown how this genetic change results in an increased risk of this complication in affected individuals.\r"}]}' WHERE id = 11385;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ERCC6", "conditions": [{"name": "柯凯因氏症候群", "detail": "已经鉴定出60多种引起Cockayne综合征的ERCC6gene突变。这种罕见的疾病包括多种特征，包括异常小的头部大小（小头畸形），生长缓慢导致身材矮小，发育迟缓以及对日光的敏感性增加（光敏性）。(换行)许多导致Cockayne综合征的ERCC6gene突变导致产生异常短版本的CSB蛋白，该蛋白无法正常运行。其他突变会改变CSB蛋白中的单个结构单元（氨基酸），这也会导致蛋白功能异常。（换行）ERCC6gene突变导致Cockayne综合征的机制尚不清楚。改变的CSB蛋白可能会阻碍DNA修复，并且可能无法协助gene转录。结果，受损的DNA不能固定，这会破坏gene转录并阻止蛋白质的正常生产。这些异常损害细胞功能并最终导致许多器官和组织中细胞的死亡。错误的DNA修复是受影响个体光敏性的基础，研究人员怀疑它也有助于Cockayne综合征的其他特征。目前尚不清楚ERCC6gene突变如何引起这种情况的所有不同特征。"}, {"name": "紫外线敏感综合症", "detail": "ERCC6gene中的至少一种突变可引起紫外线敏感综合征，这种疾病的特征是对来自太阳的紫外线异常敏感。紫外线敏感综合征患者容易晒伤，皮肤雀斑或其他肤色变化（色素沉着）。已知的突变称为Arg77Ter或R77X，用CSB蛋白中第77位的过早终止信号替换了氨基酸精氨酸。如果产生任何蛋白质，则异常短且迅速分解。没有这种蛋白质，皮肤细胞就无法修复紫外线引起的DNA损伤，并且受损gene的转录被阻断。但是，尚不清楚CSB蛋白的丢失如何引起紫外线敏感综合征。另外，尚不清楚为什么Arg77Ter突变会引起光敏性而没有Cockayne综合征的其他特征（如上所述）。"}, {"name": "年龄相关性黄斑变性", "detail": "遗传学家庭参考提供有关年龄相关性黄斑变性的信息。"}]}' WHERE id = 11420;
UPDATE genes SET GHR_metadata_ch = '{"gene": "RBPJ", "conditions": [{"name": "亚当斯-奥利弗综合症", "detail": "RBPJgene的突变会导致Adams-Oliver综合征，这种疾病的特征是通常在头皮上的皮肤缺失区域（先天性皮肤发育不全）和手脚畸形。这些突变改变了RBP-J蛋白中的单个蛋白结构单元，改变了通常与DNA结合的蛋白区域。改变后的蛋白质无法与DNA结合，从而阻止了特定gene的激活。gene活性的这些变化会损害头部顶部皮肤和手脚骨骼的正常发育。目前尚不清楚为什么发育受损主要影响这些组织。"}, {"name": "类风湿关节炎", "detail": "遗传学家庭参考提供有关类风湿关节炎的信息。"}]}' WHERE id = 11441;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SMPD1", "conditions": [{"name": "尼曼匹克症", "detail": "已发现SMPD1gene中至少有175个突变导致A型和B型Niemann-Pick病。这些类型的Niemann-Pick病的特征是细胞内脂肪堆积，导致肺部疾病和肝脏肿大脾肿大（肝脾肿大）。A型更为严重，其特征是儿童早期严重的神经功能障碍。导致酶功能完全丧失的SMPD1gene突变倾向于导致Niemann-Pick病A型。在Ashkenazi（东欧和中欧）犹太人群中，三种突变导致约90%的Niemann-Pick病A型病例。导致产生保留某些活性的酶的突变通常会导致B型Niemann-Pick病。细胞内酶活性的降低使鞘磷脂在细胞中积累。这种脂质的积累导致细胞功能障碍并最终死亡。随着时间的推移，细胞损失会损害Niemann-Pick病A型和B型患者的组织和器官功能，包括脑，肺，脾和肝。"}]}' WHERE id = 11466;
UPDATE genes SET GHR_metadata_ch = '{"gene": "PDP1", "conditions": [{"name": "缺乏症", "detail": "在丙酮酸脱氢酶缺乏症患者中，PDP1gene至少鉴定出一种突变；PDP1gene的突变是这种情况的非常罕见的原因。丙酮酸脱氢酶缺乏症的特征是体内一种称为乳酸的化学物质（乳酸性酸中毒）可能危及生命，发育迟缓和神经系统问题。（换行）鉴定出的突变去除了丙酮酸脱氢酶磷酸酶1蛋白的一个蛋白质构件（氨基酸），该蛋白质构件被认为改变了其形状。异常蛋白质无法从丙酮酸脱氢酶复合物中去除磷酸基团，从而降低了复合物的活性。随着这种复合物活性的降低，丙酮酸积累并在另一种化学反应中转化为乳酸，引起乳酸性酸中毒。另外，细胞能量的产生减少了。特别依赖于这种能量形式的大脑受到严重影响，导致与丙酮酸脱氢酶缺乏症相关的神经系统问题。"}]}' WHERE id = 11470;
UPDATE genes SET GHR_metadata_ch = '{"gene": "WNT3", "conditions": [{"name": "Tetra-amelia综合征", "detail": "WNT3gene的突变已显示在土耳其一个大家庭的成员中引起四眼症候群。这种突变发生在每个细胞中WNT3gene的两个拷贝中，在制备WNT3蛋白的说明中用一个过早的终止信号替换了一个蛋白构建块（氨基酸）。此突变写为Gln83Ter或Q83X。(换行)研究人员认为，Gln83Ter突变会导致WNT3蛋白异常短而无功能的产生。WNT3蛋白的丢失会破坏出生前的正常肢体形成，并导致与四眼症候群相关的其他严重出生缺陷。"}]}' WHERE id = 11534;
UPDATE genes SET GHR_metadata_ch = '{"gene": "PDE6B", "conditions": [{"name": "常染色体显性先天性静止性夜盲", "detail": "已发现PDE6Bgene中至少有一个突变会导致常染色体显性先天性固定性夜盲，其特征是无法在弱光下看到。此突变将蛋白质结构单元（氨基酸）组氨酸更改为β亚基（写为His258Asp或H258N）中第258位的氨基酸天冬酰胺。这种变化损害了cGMP-PDE复合物的正常功能，导致其不断打开（组成性激活）。由于cGMP-PDE复合物始终处于活动状态，因此即使在明亮的光线下，杆细胞发送到大脑的信号也会不断发生。来自视杆细胞的视觉信息然后被大脑感知为没有意义，导致夜盲症。"}, {"name": "视网膜色素变性", "detail": "遗传学家庭参考提供有关视网膜色素变性的信息。"}]}' WHERE id = 11549;
UPDATE genes SET GHR_metadata_ch = '{"gene": "FOXP2", "conditions": [{"name": "FOXP2相关的言语和语言障碍", "detail": "涉及FOXP2gene的一些变化可能导致与FOXP2相关的语音和语言障碍，这种情况会影响从儿童早期开始的语音和语言的发展。一些受影响的个体具有删除7号染色体的一小部分的缺失，包括FOXP2gene和几个相邻gene。患有这种情况的其他人的FOXP2gene本身也有突变。较少见的是，FOXP2相关的言语和语言障碍是由于7号染色体结构的重排（如易位）或从母亲那里继承了7号染色体的两个拷贝，而不是从父母双方那里继承了一个拷贝（这种现象称为母亲单亲二体性或母亲UPD）。尚不清楚拥有7号染色体的两个母体拷贝如何影响FOXP2gene的活性。(换行)FOXP2相关言语和语言障碍的遗传变化破坏了FOXP2gene的活性。因为叉头盒P2是转录因子，所以这些变化会影响发育中的大脑中其他gene的活性。研究人员正在努力确定哪些gene参与其中，以及它们活动的变化如何导致异常的言语和语言发展。"}]}' WHERE id = 11551;
UPDATE genes SET GHR_metadata_ch = '{"gene": "AGA", "conditions": [{"name": "天冬氨酰葡糖胺尿症", "detail": "AGAgene中的一些突变与由于酶缺陷引起的称为天冬氨酰葡糖胺尿症的疾病有关，该酶不能分解糖蛋白导致其积累。这种疾病的特征是大脑中神经细胞的丧失，导致智力残疾，言语丧失并可能发展为癫痫发作。其他症状包括骨质疏松症和眼部玻璃体肥大。"}]}' WHERE id = 11639;
UPDATE genes SET GHR_metadata_ch = '{"gene": "IL36RN", "conditions": [{"name": "广义脓疱型银屑病", "detail": "已经发现十几个IL36RNgene突变会增加对称为广义脓疱性牛皮癣（GPP）的严重皮肤病的易感性。患有这种情况的人会反复发作，其中大部分皮肤变红并发炎，并形成小的充满脓液的水泡（脓疱）。皮肤问题可伴有全身发热和其他炎症迹象（全身性炎症）。这些发作被认为是由感染，某些药物，月经，怀孕或身体其他压力引起的。（换行）与GPP相关的IL36RNgene突变减少皮肤中IL-36Ra蛋白的量或完全消除它。没有IL-36Rα的控制，促进炎症的信号传导途径过度活跃，导致不受控制的炎症，特别是在皮肤中。(换行)IL36RNgene突变会增加患GPP的风险。并非每个gene突变的人都有炎症的特征性问题。人们认为这种复杂的状况是遗传和环境因素共同作用的结果。"}, {"name": "其他疾病", "detail": "IL36RNgene突变也与涉及脓疱形成的其他炎症性皮肤病有关。像GPP（如上所述）一样，急性全身性发疹性脓疱病（AGEP）的特征是通常由抗生素或其他药物触发的身体大面积皮疹和脓疱。（发炎的皮肤的某些特征可区分AGEP和GPP。）其他与IL36RN相关的疾病被归类为局部脓疱性牛皮癣的形式，因为在身体的有限区域发现了水泡。例如，掌plant脓疱病会影响手和脚，而持续性肢端皮炎会影响手指和脚趾的尖端。目前尚不清楚同一gene中的突变如何导致这些不同的情况。(换行)IL36RNgene突变已在患有地理性舌头疾病的个体中发现，其中舌头的顶部和侧面具有类似于地图的不规则斑块。这些补丁与感染或其他健康问题无关。一些患有GPP的人具有地理上的舌头，但是这种异常也可能在没有其他病症的特征的情况下发生。"}]}' WHERE id = 11723;
UPDATE genes SET GHR_metadata_ch = '{"gene": "NFKBIA", "conditions": [{"name": "免疫缺陷性无汗性外胚层发育不良", "detail": "已发现NFKBIAgene中至少有五个突变会导致免疫缺陷（EDA-ID）的无汗外胚层发育不良。EDA-ID是一种以免疫系统功能降低为特征的疾病，导致反复感染和外胚层组织异常发育。导致EDA-ID的NFKBIAgene突变会损害蛋白质的分解。结果，核因子-κ-B未被激活或从IKK复合物中释放。如果细胞核中没有可用于调节gene活性的核因子-κ-B，则免疫细胞和形成外胚层组织的细胞内的某些信号传导途径被破坏，导致免疫缺陷和外胚层组织的不完全发育，如EDA-ID。"}]}' WHERE id = 11735;
UPDATE genes SET GHR_metadata_ch = '{"gene": "KLHL3", "conditions": [{"name": "假性醛固酮增多症2型", "detail": "已发现至少36个KLHL3gene突变会导致2型假性低醛固酮增多症（PHA2），这种疾病的特征是高血压（高血压）和血液中钾水平高（高钾血症）。这些突变改变了KLHL3蛋白，削弱了其附着于E3泛素连接酶复合物或WNK4的能力。结果，该复合物不能用遍在蛋白标记WNK4，并且蛋白质的降解受到损害。过量的WNK4会破坏血压的正常控制，从而导致高血压和PHA2的其他特征。尚不清楚WNK1是否受到异常E3泛素连接酶复合物的影响，或者WNK1是否在由KLHL3gene突变引起的PHA2的发展中起作用。"}]}' WHERE id = 11742;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SPAST", "conditions": [{"name": "痉挛性截瘫4型", "detail": "已发现SPASTgene中的240多个突变会导致4型痉挛性截瘫。这些突变大多数会改变spastin的结构或破坏其产生，从而削弱其调节微管的能力。微管无法运输细胞成分，尤其是在神经细胞中，被认为是导致4型痉挛性截瘫的主要体征和症状的原因。SPASTgene中的（换行）突变约占常染色体显性遗传性痉挛性截瘫的40%。"}]}' WHERE id = 11747;
UPDATE genes SET GHR_metadata_ch = '{"gene": "CPT2", "conditions": [{"name": "肉碱棕榈酰转移酶II缺乏症", "detail": "Mutations in the CPT2 gene have been found to cause carnitine palmitoyltransferase II (CPT II) deficiency, a disorder characterized by muscle pain and weakness, hypoglycemia, hypoketosis, fatty acids and long-chain acylcarnitines accumulation in cells leading to the damage of the liver, heart, and muscles. Mutations can either lead to lethal neonatal form and a severe infantile hepatocardiomuscular form or less severe myopathic form of the disorder depending upon the extremely reduced or partially reduced CPT2 enzyme activity. "}]}' WHERE id = 11791;
UPDATE genes SET GHR_metadata_ch = '{"gene": "TBP", "conditions": [{"name": "亨廷顿病样综合征", "detail": "已经发现TBPgene中的特定类型的突变会引起进行性脑部疾病，称为亨廷顿病样4（HDL4）或脊髓小脑共济失调17型（SCA17）。这种疾病的特征在受影响的个体之间差异很大。这种情况首先被描述为HDL4，其体征和症状与亨廷顿舞蹈病非常相似，包括不受控制的运动，情绪问题和思维能力丧失。这种疾病现在更常被称为SCA17，因为协调运动（共济失调）和其他运动问题是最常见的体征和症状。尚不清楚为什么某些具有TBP突变的人患有类似亨廷顿病的疾病，而另一些人则患有更突出的共济失调。（换行）与HDL4/SCA17相关的突变增加了TBPgene中CAG/CAA三核苷酸重复序列的大小。患有这种情况的人有43至66次CAG/CAA重复。具有43至48个CAG/CAA重复的人可能有也可能没有体征和症状，而具有49个或更多重复的人几乎总是发展为该疾病。（换行）TBPgene中CAG/CAA重复序列数量的增加导致异常长版本的TATA盒结合蛋白的产生。异常蛋白质在大脑的神经细胞（神经元）中积聚并破坏这些细胞的正常功能。大脑某些区域神经元的功能障碍和最终死亡是HDL4/SCA17体征和症状的基础。由于TBPgene在整个身体中都有活性，因此尚不清楚为什么该gene突变的影响仅限于大脑。"}]}' WHERE id = 11793;
UPDATE genes SET GHR_metadata_ch = '{"gene": "CLN6", "conditions": [{"name": "CLN6疾病", "detail": "已发现CLN6gene突变会引起CLN6（类固醇脂褐藻病，神经元，6）疾病，这种疾病主要影响儿童早期至晚期的运动和智力发育，其特征是智力功能和运动问题恶化。CLN6突变会导致异常蛋白质迅速降解并损害其功能，从而导致蛋白质和其他物质在溶酶体中积聚，从而导致神经细胞损伤和细胞死亡。"}]}' WHERE id = 11795;
UPDATE genes SET GHR_metadata_ch = '{"gene": "PCBD1", "conditions": [{"name": "四氢生物蝶呤缺乏症", "detail": "已发现PCBD1gene中至少有9个突变会导致四氢生物蝶呤缺乏。当这种情况由PCBD1gene突变引起时，它被称为蝶呤4α-甲醇胺脱水酶（PCD）缺乏症。PCD缺乏症约占所有四氢生物蝶呤缺乏症病例的5%。（换行）PCBD1gene中的一些突变改变了蝶呤-4α-甲醇胺脱水酶中的单个氨基酸，而其他突变在制备该酶的说明中引入了过早的终止信号。蝶呤-4α-甲醇胺脱水酶的变化会降低酶的活性，从而影响人体回收四氢生物蝶呤的能力。结果，较少的这种辅因子可用于参与化学反应，例如苯丙氨酸向酪氨酸的转化。如果苯丙氨酸不转化为酪氨酸，过量会在血液和其他组织中积聚。(换行)尽管PCD缺乏症患者的血液中苯丙氨酸水平通常升高，但这种四氢生物蝶呤缺乏症很少引起重大的医学问题。研究人员认为，其他酶可以弥补PCBD1gene突变患者中pterin-4α-甲醇胺脱水酶活性的降低。"}]}' WHERE id = 11851;
UPDATE genes SET GHR_metadata_ch = '{"gene": "CHAT", "conditions": [{"name": "先天性肌无力综合征", "detail": "已经发现CHATgene的突变会导致先天性肌无力综合症，这种疾病的特征是肌肉无力（肌无力），随着体力消耗而恶化，并且还可能表现出短暂的呼吸暂停（呼吸暂停），导致发。这是由于蛋白质缺陷导致的，该蛋白质失败或减少了乙酰胆碱的产生，从而损害了通过肌肉细胞的离子流并抑制了肌肉运动。"}]}' WHERE id = 11946;
UPDATE genes SET GHR_metadata_ch = '{"gene": "EOGT", "conditions": [{"name": "亚当斯-奥利弗综合症", "detail": "在患有Adams-Oliver综合征的个体中发现了EOGTgene中的至少三个突变。这种情况的特征是通常在头皮上的皮肤缺失区域（先天性皮肤发育不全）和手脚畸形。在阿拉伯人口中发现的与这种情况有关的最常见的EOGTgene突变导致异常短的蛋白质。其他突变会改变EOGT蛋白中的单个蛋白结构单元（氨基酸）。研究表明，EOGTgene突变会降低或消除蛋白质转移N-乙酰氨基葡萄糖的能力。目前尚不清楚这种损伤对细胞有什么影响，或者它如何导致Adams-Oliver综合征的特征。"}]}' WHERE id = 11949;
UPDATE genes SET GHR_metadata_ch = '{"gene": "APOE", "conditions": [{"name": "阿尔茨海默病", "detail": "一个或两个APOEgenee4版本拷贝的遗传增加了个体患迟发性阿尔茨海默病的风险，这是脑组织中淀粉样斑块数量增加导致受影响人群中神经元死亡的一种情况。"}, {"name": "与年龄有关的听力损失", "detail": "APOEgene的变异与年龄相关性听力丧失（也称为老年性耳聋）有关，老年性耳聋是一种随着年龄增长听力下降的疾病，患有这种疾病的个体表现出诸如听力言语困难，耳鸣（耳鸣）或头晕和平衡问题（presbystasis）。"}, {"name": "年龄相关性黄斑变性", "detail": "APOEgene的变异与年龄相关性黄斑变性有关，黄斑变性是一种影响中央视力所必需的黄斑的疾病。一种称为干燥形式的疾病的常见形式的特征在于视网膜下称为玻璃疣的黄色沉积物，随着时间的推移导致视力缓慢丧失。湿形式迅速影响视力丧失，其特征在于黄斑下异常脆弱的血管的生长。"}, {"name": "其他疾病", "detail": "载脂蛋白E的变体与几种心血管疾病有关。APOE e4等位gene与动脉粥样硬化有关，导致中风和心脏病发作的风险增加。APOE e2等位gene增加个体？s患III型高脂蛋白血症的风险，其特征在于血液中胆固醇水平升高，某些甘油三酯和β极低密度脂蛋白（β-VLDL）积聚导致黄瘤和动脉粥样硬化。"}]}' WHERE id = 11979;
UPDATE genes SET GHR_metadata_ch = '{"gene": "GABRA1", "conditions": [{"name": "青少年肌阵挛性癫痫", "detail": "已经在至少一个患有青少年肌阵挛性癫痫的家庭中鉴定出GABRA1gene的突变。这种情况通常始于儿童期或青春期，并引起反复的肌阵挛性癫痫发作，其特征是快速，不受控制的肌肉抽搐。受影响的个体还可以有其他类型的癫痫发作，称为全身性强直阵挛性癫痫发作（或大发作）和失神发作。与这种情况相关的突变改变了？1亚基。蛋白质322位的氨基酸丙氨酸被氨基酸天冬酰胺取代。该gene突变被写为Ala322Asp或A322D。（换行）这种GABRA1gene突变导致形成异常？降低GABAA受体功能的1个亚基。含有异常亚基的GABAA受体在到达细胞膜之前被分解。研究表明，改变的受体也会干扰细胞内的正常受体，导致正常受体的额外损失。由于GABAA受体功能的降低，神经元之间的信号传导不受调节，这可能导致神经元的过度刺激。研究人员认为，大脑中某些神经元的过度刺激会引发与癫痫发作相关的异常大脑活动。"}]}' WHERE id = 11980;
UPDATE genes SET GHR_metadata_ch = '{"gene": "OTULIN", "conditions": [{"name": "Otulipenia", "detail": "已经发现OTULINgene中至少有三个突变会引起otulipenia，这是一种从婴儿期开始就引起体内异常炎症的疾病。患病婴儿反复发烧，腹泻，关节疼痛和皮疹。otulipenia中涉及的gene突变会降低OTULIN蛋白的功能。结果，线性泛素链的去除受损，并且引起炎症的信号传导途径异常活跃。导致过度炎症导致otulipenia的体征和症状并损害体内器官；如果不及时治疗，可能会危及生命。"}]}' WHERE id = 11987;
UPDATE genes SET GHR_metadata_ch = '{"gene": "MUT", "conditions": [{"name": "甲基丙二酸血症", "detail": "在甲基丙二酸血症患者中已鉴定出MUTgene的200多个突变，这种疾病的特征是进食困难，发育迟缓和长期健康问题。这些遗传变化阻止了功能性甲基丙二酰辅酶A突变酶的产生或降低了酶的活性。结果，某些蛋白质和脂质不能正确分解。这种缺陷使甲基丙二酰辅酶A和其他有毒化合物积聚在人体的器官和组织中，引起甲基丙二酸血症的体征和症状。阻止任何功能性甲基丙二酰辅酶a突变酶产生的（换行）突变会导致一种称为mut0的甲基丙二酸血症。Mut0是这种疾病的最严重形式，预后最差。改变酶结构但不能完全消除其活性的突变会导致某种形式的疾病，称为mut-。mut-form通常不太严重，症状比mut0形式更多。"}]}' WHERE id = 11988;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ANKH", "conditions": [{"name": "颅骨phy骨发育不良", "detail": "已发现ANKHgene突变会导致颅骨phy骨发育不良，这种疾病的特征是颅骨（颅骨）骨骼逐渐增厚和干phy端发育不良。这是由于有缺陷的ANKH蛋白导致骨矿化增加所致。"}, {"name": "其他疾病", "detail": "已发现ANKHgene突变会导致罕见的遗传形式的焦磷酸钙二水合物沉积病（CPPDD）（也称为软骨钙化病或假性痛风），这种疾病的特征是焦磷酸钙二水合物晶体在关节软骨中积聚。患有这种疾病的个体表现出软骨薄弱，容易破裂，关节疼痛和炎症。"}]}' WHERE id = 12036;
UPDATE genes SET GHR_metadata_ch = '{"gene": "TGM5", "conditions": [{"name": "肢端脱皮皮肤综合征", "detail": "已发现TGM5gene中至少有22个突变会导致肢端脱皮皮肤综合症。这种情况的特征是无痛地剥落在手和脚上最明显的皮肤顶层，但也会影响手臂和腿。大多数突变会改变转谷氨酰胺酶5中的单个蛋白质结构单元（氨基酸），包括欧洲血统中最常见的突变，该突变将113位的氨基酸半胱氨酸替换为氨基酸甘氨酸（写为Gly113Cys或G113C）。TGM5gene突变减少了产生的转谷氨酰胺酶5的量或阻止了细胞产生任何这种酶。转谷氨酰胺酶5的缺乏会损害蛋白质交联，这会削弱角质化的细胞膜，并使表皮的最外层细胞容易与下面的皮肤分离并剥离。这种剥离在手和脚上最明显，可能是因为这些区域往往严重暴露在潮湿和摩擦中。"}]}' WHERE id = 12054;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ARX", "conditions": [{"name": "早期婴儿癫痫性脑病1", "detail": "已经发现ARXgene的突变会引起早期婴儿癫痫性脑病1（EIEE1），这种疾病的特征是在生命的第一年反复发作，称为婴儿痉挛，智力残疾和其他神经系统问题。该疾病是由于ARXgene中过量的聚丙氨酸重复扩增破坏了其控制某些中间神经元正常发育和迁移的功能而引起的。"}, {"name": "帕丁顿综合征", "detail": "已经在患有Partington综合征的人中鉴定出ARXgene的突变，Partington综合征是一种神经系统疾病，其特征在于智力残疾和运动问题，称为局灶性肌张力障碍，主要影响手。该疾病是由ARX蛋白中第二个聚丙氨酸束中额外的丙氨酸重复引起的，导致其活性受损并破坏了发育中大脑中正常的神经元间迁移。"}, {"name": "X连锁性颅脑畸形伴生殖器异常", "detail": "ARXgene中的几个突变与生殖器异常（XLAG）的X连锁性lissencephaly有关，这是一种神经系统疾病，其特征是大脑发育异常并伴有lissencephaly（外观光滑），男性生殖器异常和慢性腹泻。与男性相比，具有ARXgene突变的女性没有或没有非常不严重的症状，例如call体发育不全，一定程度的智力残疾和反复发作。"}, {"name": "其他疾病", "detail": "ARXgene中的几种突变与多种损害大脑功能的疾病有关，例如：；没有其他神经系统问题的智力障碍，称为X连锁智力障碍（XLID）或有时为非综合征性XLID，以及称为X连锁肌阵挛性癫痫并伴有智力障碍和痉挛的疾病。此外，ARXgene突变会导致一种具有结构性脑畸形的综合征，称为Proud综合征，该疾病的特征是call体发育不全，男性生殖器异常和生殖器异常的脑积水。"}]}' WHERE id = 12075;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ARHGAP31", "conditions": [{"name": "亚当斯-奥利弗综合症", "detail": "已知ARHGAP31gene的突变会引起Adams-Oliver综合征，这种疾病的特征是通常在头皮上的先天性皮肤发育不全（皮肤缺失）区域，以及由于产生异常短的ARHGAP31蛋白而导致的手脚畸形GAP活性不能抑制Cdc42和Rac1信号传导。"}]}' WHERE id = 12100;
UPDATE genes SET GHR_metadata_ch = '{"gene": "RPS7", "conditions": [{"name": "钻石-布莱克凡贫血", "detail": "在至少一个受Diamond-Blackfan贫血影响的家庭中已鉴定出RPS7gene突变。该突变改变了称为内含子3的gene区域中的单个DNA构建块（核苷酸），并写为IVS3DS+1G>a。据信该突变影响RPS7蛋白的稳定性或功能，并可能损害核糖体的组装，但是该突变的具体作用尚不清楚。研究表明，缺乏功能性核糖体蛋白可能会增加骨髓中造血细胞的自我破坏，导致红细胞数量减少（贫血）。细胞分裂的异常调节或凋亡的不适当触发可能会导致其他健康问题和异常的身体特征，从而影响某些Diamond-Blackfan贫血患者。"}]}' WHERE id = 12110;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SH2D1A", "conditions": [{"name": "X连锁淋巴组织增生性疾病", "detail": "在X连锁淋巴组织增生性疾病（XLP）患者中已鉴定出70多个SH2D1Agene突变。一些SH2D1Agene突变会损害SAP功能。其他会导致异常短的蛋白质不稳定或无功能，或阻止产生任何SAP。功能性SAP的丧失破坏了对免疫系统的适当控制，并可能导致对这种疾病中发生的爱泼斯坦-巴尔病毒感染的威胁生命的免疫反应。此外，当有缺陷的淋巴细胞未被细胞凋亡适当破坏时，免疫系统细胞（淋巴瘤）的癌症可能会在受影响的个体中发展。"}]}' WHERE id = 12130;
UPDATE genes SET GHR_metadata_ch = '{"gene": "NR0B1", "conditions": [{"name": "X连锁肾上腺发育不全先天性", "detail": "已鉴定出导致X连锁肾上腺发育不全先天性的110多个NR0B1突变。这些遗传变化中的一些是NR0B1gene全部或部分的缺失。其他突变导致DAX1蛋白异常短版本的产生。还有其他突变会改变DAX1关键区域中的单个蛋白质结构单元（氨基酸）。（换行）导致X连锁肾上腺发育不全先天性的大多数突变阻止NR0B1gene产生任何活性DAX1蛋白。DAX1的缺乏会破坏体内激素产生组织的正常发育和功能。当内分泌腺如肾上腺，下丘脑，垂体和性腺不能产生适量的特定激素时，就会产生这种情况的主要特征。"}, {"name": "Swyer综合征", "detail": "遗传学家庭参考提供有关Swyer综合征的信息。"}, {"name": "其他疾病", "detail": "在某些情况下，遗传物质会从包含多个gene（包括NR0B1）的X染色体区域中删除。这种缺失导致称为先天性肾上腺发育不全的病症，伴有复杂的甘油激酶缺乏症。除了先天性肾上腺发育不全的体征和症状外，患有这种疾病的人可能会延迟发育和调节血糖水平的问题。在极少数情况下，缺失还包括与杜兴氏肌营养不良症相关的gene。具有这种较大缺失的人除了具有复杂的甘油激酶缺乏症的先天性肾上腺发育不全的其他特征外，还具有进行性肌肉无力和消瘦。"}]}' WHERE id = 12168;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SLC29A3", "conditions": [{"name": "组织细胞增生症-淋巴结病加综合征", "detail": "SLC29A3gene的突变会导致组织细胞增生-淋巴结病加综合征，这是一组具有影响身体许多部位的体征和症状重叠的疾病。该谱中疾病常见的特征是组织细胞增多症，其是称为组织细胞的免疫系统细胞的过度生长。这些细胞异常积聚在组织中，通常在颈部的淋巴结中。这些细胞在淋巴结中的积聚导致淋巴结肿胀（淋巴结病）。其他功能可能包括异常黑暗（色素沉着过度）的皮肤斑块，头发过度生长（过度肥大），糖尿病和听力下降。（换行）涉及这一系列疾病的SLC29A3gene突变降低或消除ENT3蛋白的活性。研究人员不确定ENT3活性的丧失如何导致组织细胞增生和组织细胞增生-淋巴结病加综合征的其他特征。他们推测，由此导致的核苷转运障碍导致溶酶体和其他细胞结构中核苷的积累。过量的核苷可能损害细胞功能。ENT3活性的缺乏也可能导致线粒体中核苷的量减少。这种核苷短缺可能会损害细胞能量的产生，从而影响许多身体系统。目前尚不清楚SLC29A3gene突变如何导致不同的体征和症状模式，即使在同一家族中也是如此。"}]}' WHERE id = 12180;
UPDATE genes SET GHR_metadata_ch = '{"gene": "CCBE1", "conditions": [{"name": "Hennekam综合征", "detail": "已发现CCBE1gene中至少有13个突变引起Hennekam综合征，这是一种由淋巴系统畸形引起的遗传性疾病。大多数CCBE1gene突变会改变CCBE1蛋白中的单个蛋白结构单元（氨基酸），从而导致蛋白三维形状的变化。异常蛋白质不能在淋巴管上皮的形成中起作用。淋巴系统形成不良会导致淋巴管异常扩张（淋巴管扩张），并容易因液体积聚（淋巴水肿）和Hennekam综合征的其他特征而破裂（破裂），浮肿或肿胀。CCBE1gene突变约占所有Hennekam综合征病例的25%。"}]}' WHERE id = 12197;
UPDATE genes SET GHR_metadata_ch = '{"gene": "PGAM2", "conditions": [{"name": "磷酸甘油酸变位酶缺乏症", "detail": "已发现PGAM2gene中至少有五个突变会导致磷酸甘油酸突变酶缺乏症。这些突变中最常见的突变写为Trp78Ter或W78X，在制备磷酸甘油酸变位酶的说明中用过早的终止信号替换了蛋白质结构单元（氨基酸）色氨酸。这种突变导致产生异常短的，无功能的酶形式。其他突变改变磷酸甘油酸变位酶中的单个氨基酸。PGAM2gene中的（换行）突变大大降低磷酸甘油酸变位酶的活性，其破坏骨骼肌细胞中的能量产生。这种缺陷是受影响个体剧烈运动后发生的肌肉痉挛，肌肉衰竭以及相关体征和症状的基础。"}]}' WHERE id = 12231;
UPDATE genes SET GHR_metadata_ch = '{"gene": "EPAS1", "conditions": [{"name": "家族性红细胞增多症", "detail": "已发现EPAS1gene中至少有五个突变会引起家族性红细胞增多症，这是一种遗传性疾病，其特征是红细胞数量增加和血栓异常风险增加。当家族性红细胞增多症由EPAS1gene突变引起时，通常称为ECYT4。（换行）EPAS1gene突变改变HIF-2α中的单个蛋白质构建块（氨基酸）？蛋白质。这些变化阻止HIF-2？与靶向其降解的蛋白质正常相互作用。结果，HIF-2？不能有效降解，即使有足够的氧气，HIF也会积聚在细胞中。当不再需要时，额外HIF的存在导致红细胞的产生，导致血流中这些细胞过量。"}]}' WHERE id = 12239;
UPDATE genes SET GHR_metadata_ch = '{"gene": "TOR1A", "conditions": [{"name": "早发性原发性肌张力障碍", "detail": "TOR1Agene中的特定突变会导致大多数早期发作的原发性肌张力障碍。这种突变通常称为GAG缺失或delta GAG，可从TOR1Agene中删除三个DNA构建块（碱基对）。所得的torsinA蛋白在关键区域缺少一个蛋白结构单元（氨基酸）。改变的蛋白质对大脑神经细胞功能的影响尚不清楚。患有早发性原发性肌张力障碍的人没有神经细胞的丢失或大脑结构的明显变化，这可以解释这种情况下出现的异常肌肉收缩。取而代之的是，改变的torsinA蛋白可能会对神经细胞之间的连接产生微妙的影响，并可能破坏控制运动的神经细胞之间的化学信号传导。研究人员正在努力确定这种蛋白质的变化如何导致这种疾病的特征。"}, {"name": "良性基本眼睑痉挛", "detail": "几项研究检查了TOR1Agene的常见变异（多态性）与几种形式的成人发作性肌张力障碍（包括良性原发性眼睑痉挛）之间的可能关系。这些研究的结果好坏参半。一些研究表明，某些多态性会增加一个人患良性原发性眼睑痉挛的风险。但是，其他研究发现TOR1Agene的变化与这种疾病的风险之间没有联系。研究人员仍在努力阐明TOR1Agene的变异是否与良性原发性眼睑痉挛和其他成人发作性肌张力障碍有关。"}]}' WHERE id = 12246;
UPDATE genes SET GHR_metadata_ch = '{"gene": "CDH23", "conditions": [{"name": "非综合征性听力损失", "detail": "在患有非综合征性听力损失的人中已经鉴定出CDH23gene的几个突变，该疾病的特征是听力损失而没有任何其他症状。该gene的突变会导致一种称为DFNB12的听力损失，这种形式发生在孩子学会说话（舌前）之前，并且严重到严重。这是由于钙黏着蛋白23蛋白的改变而引起的，该蛋白破坏了内耳中立体纤毛的发育，导致听力下降。"}, {"name": "亚瑟综合症", "detail": "CDH23gene中的一些突变与称为ID型Usher综合征（USH1D）的I型Usher综合征相关，该疾病的特征是听力丧失，视力丧失以及平衡与协调问题相结合。这是由于钙粘蛋白23蛋白缺陷或无功能而引起的，该蛋白破坏了纤毛的正常发育和功能，并且视网膜中缺乏该蛋白会导致色素性视网膜炎。"}, {"name": "与年龄有关的听力损失", "detail": "CDH23gene中的几个突变与年龄相关性听力损失（也称为老年性耳聋）有关，这种疾病的特征是随着年龄的增长听力下降，受影响的个体表现出听到高频声音（包括言语）的能力下降，耳鸣（耳鸣）或头晕和平衡问题（老年性耳聋）。"}]}' WHERE id = 12253;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SRY", "conditions": [{"name": "Swyer综合征", "detail": "已经在大约15%的Swyer综合征患者中发现了SRYgene突变，也称为46，XY完全性腺发育不全或46，XY纯性腺发育不全。引起Swyer综合征的SRYgene突变会阻止性别决定区Y蛋白的产生或导致无功能蛋白的产生。尽管具有典型的男性核型，但其细胞不产生功能性性别决定区Y蛋白的胎儿不会发育睾丸，但会发育子宫和输卵管。"}, {"name": "46，XX睾丸性发育障碍", "detail": "在大多数患有46，XX睾丸性发育障碍的个体中，这种情况是由染色体之间遗传物质的异常交换（易位）引起的。这种交换是在患病父亲的精子细胞形成过程中作为随机事件发生的。SRYgene在这种疾病中错位，几乎总是放在X染色体上。具有携带SRYgene的X染色体的胎儿尽管没有Y染色体也会发展为男性特征。"}, {"name": "其他疾病", "detail": "在少数患有46，XY性发育障碍或部分性腺发育不全的人中，已经鉴定出损害但不消除性别决定区Y蛋白功能的SRYgene突变。受影响的个体可能具有外表不清晰的男性或女性（模棱两可的生殖器）或生殖器和生殖器官的其他异常。(换行)大约10%的同时患有睾丸和卵巢组织的个体（一种称为性发育的卵睾疾病）具有两条X染色体，其中一条带有错位的SRYgene拷贝。"}]}' WHERE id = 12279;
UPDATE genes SET GHR_metadata_ch = '{"gene": "COLEC11", "conditions": [{"name": "3MC综合征", "detail": "在患有3MC综合征的人中至少鉴定出5个COLEC11gene突变，该疾病的特征是异常的面部特征和影响身体其他组织和器官的各种问题。导致3MC综合征的COLEC11gene突变削弱或消除CL-K1蛋白的功能，导致胚胎发育中细胞迁移的错误控制，并导致该疾病中发生的各种异常。研究人员认为，免疫系统中存在可以补偿凝集素补体途径问题的平行途径可以解释3MC综合征中免疫系统问题的缺失。"}]}' WHERE id = 12288;
UPDATE genes SET GHR_metadata_ch = '{"gene": "IFT80", "conditions": [{"name": "窒息性胸营养不良", "detail": "IFT80gene的突变是第一个发现导致窒息性胸肌营养不良的遗传变化，这是一种遗传性的骨骼生长疾病，其特征是胸部小，肋骨短，手臂和腿部骨骼缩短。此后，IFT80gene中至少有六个突变与这种疾病有关。这些突变大多数会改变IFT80蛋白中的单个蛋白结构单元（氨基酸）。用改变的蛋白质制成的IFT复合物B不能正常工作，这破坏了纤毛内物质的运输。研究人员推测，IFT的这些变化会改变某些信号通路，包括声波刺猬通路，这可能是窒息性胸肌营养不良的骨骼生长特征异常的基础。(换行)在一些受影响的个体中，窒息性胸营养不良也与肾脏，肝脏，视网膜和其他组织的异常有关。但是，当该疾病是由IFT80gene突变引起的时，其特征通常仅限于骨骼生长问题。造成这种差异的原因尚不清楚。"}]}' WHERE id = 12306;
UPDATE genes SET GHR_metadata_ch = '{"gene": "AIP", "conditions": [{"name": "家族性孤立性垂体腺瘤", "detail": "在家族性孤立性垂体腺瘤（FIPA）中已经报道了AIPgene的几种突变，这是垂体中非癌性肿瘤（称为垂体腺瘤）发展的一种情况。AIPgene突变的人最常发展为一种称为生长激素瘤的垂体腺瘤，通常发生在较年轻的年龄，并且比没有AIPgene突变的人大。"}, {"name": "其他疾病", "detail": "据报道，散发性大腺瘤患者的AIPgene发生了一些突变，这种疾病的特征是在没有该病家族史的个体中发生了大的（大）垂体腺瘤。这发生在较年轻的年龄（<30岁），通常是从未受影响的父母那里继承的。"}]}' WHERE id = 12317;
UPDATE genes SET GHR_metadata_ch = '{"gene": "RASA1", "conditions": [{"name": "毛细血管畸形-动静脉畸形综合征", "detail": "已经发现RASA1gene中的几十个突变引起毛细血管畸形-动静脉畸形综合征（CM-AVM），其是以血管系统异常为特征的病症。负责CM-AVM的大多数突变都会阻止功能性p120-RasGAP蛋白的产生。结果，该蛋白无法控制RAS/MAPK信号传导。目前尚不清楚这种严格调控的信号通路的变化如何导致CM-AVM患者所见的特定血管异常。"}, {"name": "帕克斯韦伯综合症", "detail": "在帕克斯韦伯综合征患者中已鉴定出RASA1gene的几种突变。当疾病由RASA1gene突变引起时，受影响的个体通常具有多种血管异常，称为毛细血管畸形。帕克斯韦伯综合症的特征还在于血管系统的其他异常和一个肢体（最常见的是一条腿）的过度生长。（换行）与引起CM-AVM的RASA1gene突变一样，负责Parkes Weber综合征的突变阻止功能性p120 RasGAP蛋白的产生。该蛋白质活性的丧失破坏了RAS/MAPK信号传导的正常调节。尚不清楚缺乏p120-RasGAP如何导致Parkes-Weber综合征中发生的特定血管异常和肢体过度生长。"}, {"name": "癌症", "detail": "已经以称为基底细胞癌的皮肤癌形式检测到RASA1gene中的至少三个突变。这些突变被描述为体细胞突变，这意味着它们发生在一个人的一生中，并且仅存在于癌变的细胞中。研究人员怀疑RASA1gene突变会导致p120-RasGAP蛋白功能丧失，这可能使RAS/MAPK信号传导以不受控制的方式进行。这种未经检查的RAS/MAPK信号传导可能导致细胞增殖失控和癌性肿瘤的形成。RASA1gene突变仅在所有基底细胞癌的一小部分中发现，并且它们不被认为是这些癌症的主要原因。"}]}' WHERE id = 12318;
UPDATE genes SET GHR_metadata_ch = '{"gene": "DEPDC5", "conditions": [{"name": "病灶可变的家族性局灶性癫痫", "detail": "已发现DEPDC5gene中的80多个突变会导致家族性局灶性癫痫，病灶可变（FFEVF），这是家族性复发性癫痫（癫痫）的一种罕见形式。受影响的个体经历局灶性癫痫发作，这是不会导致意识丧失的癫痫发作。大多数DEPDC5gene突变会导致产生异常短的蛋白质，该蛋白质会迅速分解。结果，正常GATOR1复合物的形成减少，导致mTORC1的过度活性和mTOR途径的过度信号传导。尚不清楚异常活跃的mTOR途径如何导致FFEVF的局灶性癫痫发作。研究表明，大脑中mTOR通路信号的增加会导致神经细胞（突触）之间的连接发生变化，并增加神经细胞的激活（兴奋），从而引起癫痫发作。(换行)由于未知原因，一些由DEPDC5gene突变引起的FFEVF患者从未出现这种情况，这种情况称为外显率降低。据估计，具有DEPDC5gene突变的个体中有60%继续发展FFEVF。"}, {"name": "其他疾病", "detail": "DEPDC5gene的突变可引起其他癫痫发作，称为家族性颞叶内侧癫痫和婴儿痉挛。与FFEVF患者（如上所述）相似，家族性颞叶内侧癫痫患者有局灶性癫痫发作。他们在癫痫发作期间也可能有déjàvu，恐惧或恶心的感觉。婴儿痉挛是通常在1岁之前出现的癫痫发作，涉及反复的肌肉收缩。(换行)与FFEVF一样，大多数引起家族性颞叶内侧癫痫或婴儿痉挛的DEPDC5gene突变会导致GATOR1复合物形成减少和mTOR通路异常活跃。目前尚不清楚为什么具有相同gene突变的个体会发展出不同的癫痫发作。"}]}' WHERE id = 12323;
UPDATE genes SET GHR_metadata_ch = '{"gene": "POMC", "conditions": [{"name": "Proopiomelanocortin缺乏症", "detail": "在患有proopiomelanocortin（POMC）缺乏症的人中发现了POMCgene的几个突变。这些gene突变导致产生异常短版本的POMC蛋白或根本不产生蛋白。结果，不存在由POMC制成的肽，包括ACTH-MSH-MSH-内啡肽，有时-MSH。这些肽的缺失阻止了通过其受体蛋白的信号传导并破坏了体内的某些功能。没有ACTH，皮质醇的产生就会减少，从而导致受影响个体的低血糖（低血糖）和其他问题。减少-皮肤中的MSH会减少黑素细胞中色素的产生，从而导致POMC缺乏症患者经常出现红发和苍白的皮肤。受刺激的大脑信号丢失-MSH和-MSH失调身体的能量平衡，导致暴饮暴食和严重肥胖。短缺-MSH还是-内啡肽似乎不会对患有这种疾病的人造成健康问题。"}]}' WHERE id = 12328;
UPDATE genes SET GHR_metadata_ch = '{"gene": "PTPN11", "conditions": [{"name": "努南综合征", "detail": "在PTPN11gene中已鉴定出90多种引起Noonan综合征的突变。这种情况的特征是面部特征轻度异常，身材矮小，心脏缺陷，出血问题，骨骼畸形以及许多其他体征和症状。大多数PTPN11gene突变取代了用于制造SHP-2蛋白的单个氨基酸。所得蛋白质要么连续打开（活动），要么具有延长的激活时间，而不是响应其他细胞蛋白质而迅速打开和关闭。蛋白质活性的这种增加破坏了控制细胞功能（例如增殖）的RAS/MAPK信号传导途径的调节。这种失调可能导致心脏缺陷，生长问题，骨骼异常和Noonan综合征的其他特征。(换行)很少，由PTPN11gene突变引起的Noonan综合征患者也会发展为青少年骨髓单核细胞白血病，这是一种通常影响儿童或青少年的血癌。"}, {"name": "Noonan综合征伴多发性扁豆素", "detail": "已发现PTPN11gene中至少有11个突变会导致Noonan综合征并伴有多种扁豆素（以前称为LEOPARD综合征）。这种情况的特征是多个棕色皮肤斑点（扁豆素），心脏缺陷，身材矮小，胸部凹陷或突出以及独特的面部特征。两个突变约占Noonan综合征病例的65%，由PTPN11gene突变引起的多发性扁豆素改变了SHP-2蛋白中的单个蛋白质构件（氨基酸）：一个突变用位于279位的氨基酸半胱氨酸代替氨基酸酪氨酸（写为Tyr279Cys或Y279C），另一个突变用氨基酸苏氨酸代替氨基酸468位的氨基酸蛋氨酸（写为Thr468Met或T468M）。（换行）据信，所有已知的PTPN11gene变化都会导致多种扁豆素引起Noonan综合征，从而破坏SHP-2蛋白的正常功能。蛋白质功能的这种降低削弱了控制细胞功能（例如生长和分裂）的RAS/MAPK信号传导途径的激活。这种失调可导致具有多种扁豆素的Noonan综合征的各种特征。(换行)尽管PTPN11gene是致癌gene，但这种蛋白质功能的降低似乎并未增加患有多种扁豆素的Noonan综合征患者的癌症风险。"}, {"name": "癌症", "detail": "gene突变可以在一个人的一生中获得，并且仅存在于某些细胞中。这种类型的突变称为体细胞突变，它不是遗传的。PTPN11gene中的体细胞突变会增加患少年骨髓单核细胞白血病的风险。这些突变导致SHP-2蛋白持续活跃。SHP-2蛋白的过度活性破坏了控制未成熟血细胞产生的途径的调节。结果，某些白细胞过量产生，导致这种类型的白血病。在约35%的青少年骨髓单核细胞白血病患者中发现了PTPN11gene的体细胞突变。(换行)一些研究表明，PTPN11gene的体细胞突变也与其他血液疾病有关，包括慢性粒单核细胞白血病，骨髓增生异常综合症，非综合征性急性髓细胞性白血病和急性淋巴细胞性白血病。在极少数情况下，在肺癌，结肠癌，脑癌，甲状腺癌以及一种称为黑素瘤的皮肤癌中发现了体细胞PTPN11gene突变。"}, {"name": "其他疾病", "detail": "PTPN11gene的突变可引起称为软骨瘤病的疾病。这种情况的特征是手和脚的骨骼上有多个良性（非癌性）骨肿瘤，称为骨软骨瘤。患有这种疾病的人也会发展内生软骨瘤，这是软骨的良性生长。在软骨软骨病患者中，生长形成于长骨末端或髋骨两侧。软骨病的生长特征通常在儿童时期发展，并且由于不了解的原因，通常会随着时间的推移而消失。"}]}' WHERE id = 12339;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ANK2", "conditions": [{"name": "锚蛋白B综合征", "detail": "ANK1gene的突变已经被发现会引起锚蛋白B综合征，这种疾病的拥有属性是多种心脏问题，包括心脏自然起搏器（窦房结）的异常、心动过缓、心律失常、晕厥，以及由于锚蛋白B蛋白缺陷而导致猝死，锚蛋白B蛋白不能靶向心肌细胞中的离子通道和离子转运蛋白。有时这种情况与QT间期延长有关，被归类为长QT综合征4。"}, {"name": "自闭症谱系障碍", "detail": "ANK1gene的突变与自闭症谱系障碍（ASD）有关，自闭症谱系障碍（ASD）的特征是在儿童发育的早期以不同程度的严重性观察到。症状包括社交技能受损，沟通问题和重复行为，这些困难可能会干扰受影响的个人在社交，学术和就业环境中发挥作用的能力。患有ASD的人患精神病的风险也增加，例如焦虑，抑郁，强迫症和进食障碍。"}]}' WHERE id = 12368;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SDHAF2", "conditions": [{"name": "遗传性副神经节瘤嗜铬细胞瘤", "detail": "在遗传性副神经节瘤嗜铬细胞瘤2型患者中，已经鉴定出SDHAF2gene中至少有一个突变。患有这种疾病的人患有副神经节瘤，嗜铬细胞瘤或两者兼有。这些非癌性（良性）肿瘤与神经系统有关。该突变取代了SDHAF2蛋白中的蛋白结构单元（氨基酸）。具体而言，氨基酸甘氨酸在位置78处被氨基酸精氨酸取代（写为Gly78Arg或G78R）。突变的SDHAF2蛋白与SDH复合物之间的相互作用受到损害，并且FAD辅因子的附着减少。结果，SDH酶是无功能的。由于突变的SDH酶无法将琥珀酸转化为富马酸，因此琥珀酸会在细胞中积累。过量的琥珀酸盐异常稳定HIF，HIF也在细胞中积累。过量的HIF刺激细胞分裂并在不需要时触发血管的产生。快速和不受控制的细胞分裂以及新血管的形成可导致遗传性副神经节瘤嗜铬细胞瘤患者的肿瘤发展。"}]}' WHERE id = 12417;
UPDATE genes SET GHR_metadata_ch = '{"gene": "HOXB13", "conditions": [{"name": "前列腺癌", "detail": "HOXB13gene中至少有两个突变与前列腺癌风险增加有关；在患有HOXB13突变的受影响男性中，该疾病也可能更具侵略性。这些突变存在于人体的每个细胞中，可以从一代传递到下一代。结果，它们与聚集在家庭中的癌症有关。但是，并非每个继承HOXB13gene突变的人最终都会发展为前列腺癌。其他遗传，环境和生活方式因素也会导致一个人的癌症风险。（换行）与前列腺癌相关的HOXB13gene突变改变了HOXB13蛋白的MEIS相互作用结构域。研究人员建议，这些变化可能会削弱这些域调节HOXB13蛋白与DNA相互作用的能力。结果，该蛋白质的肿瘤抑制功能受损，导致不受控制的细胞增殖，从而导致前列腺癌。"}]}' WHERE id = 12481;
UPDATE genes SET GHR_metadata_ch = '{"gene": "TSEN54", "conditions": [{"name": "桥小脑发育不全", "detail": "在患有称为桥小脑发育不全的大脑发育障碍的人中，已经鉴定出TSEN54gene的几个突变。这种情况的主要特征包括发育迟缓，运动问题和智力障碍。TSEN54gene突变是一种称为2型桥小脑发育不全（PCH2）的疾病的最常见原因。当PCH2由TSEN54gene突变产生时，有时会将其更具体地归类为PCH2A。TSEN54gene的突变也会导致4型桥小脑发育不全（PCH4），并且似乎是1型桥小脑发育不全（PCH1）的罕见原因。（换行）TSEN54gene中最常见的突变用TSEN54蛋白（写为Ala307Ser或A307S）中307位的氨基酸丝氨酸取代氨基酸丙氨酸。在所有PCH2患者中，约有90%的人在每个细胞的TSEN54gene的两个拷贝中都有这种突变。至少一个被诊断患有PCH1的人在该gene的两个拷贝中也都有突变。大多数具有PCH4的个体在每个细胞的TSEN54gene的一个拷贝中具有常见的Ala307Ser突变，而在该gene的另一个拷贝中具有不同的突变。（换行）导致桥小脑发育不全的TSEN54gene突变会损害tRNA剪接核酸内切酶复合物的功能，这可能会破坏RNA分子的加工并影响许多类型蛋白质的产生。出生前，这些变化似乎对快速生长的组织（例如大脑中的组织）产生最严重的影响。但是，尚不清楚tRNA剪接核酸内切酶复合物功能降低如何导致桥小脑发育不全患者的大脑发育异常。"}]}' WHERE id = 12521;
UPDATE genes SET GHR_metadata_ch = '{"gene": "POGLUT1", "conditions": [{"name": "Dowling-Degos病", "detail": "已发现POGLUT1gene中至少有11个突变引起Dowling-Degos病。这种情况会导致各种皮肤异常，包括异常深色肤色（色素沉着过度）的特征性花边图案，这种花边图案最常发生在身体的褶皱和折痕中。(换行)导致Dowling-Degos病的大多数POGLUT1gene突变会导致蛋白质异常短而无功能或改变单个蛋白质结构单元（氨基酸），从而导致蛋白质功能部分丧失。结果，蛋白质O-葡糖基转移酶1不能或不能将葡萄糖分子添加到Notch受体。没有这些糖分子，Notch受体就不能与其配体结合，Notch途径也会停止。由于Notch通路的多种功能影响许多身体系统，Dowling-Degos病仅影响皮肤，因此尚不清楚这种病症的体征和症状是否是由于Notch信号受损或蛋白O-葡萄糖基转移酶1未知功能的破坏在黑素细胞或其他皮肤细胞中。"}]}' WHERE id = 12541;
UPDATE genes SET GHR_metadata_ch = '{"gene": "DMD", "conditions": [{"name": "杜兴氏和贝克尔肌营养不良症", "detail": "在患有Duchenne和Becker形式的肌营养不良症的人中，已经鉴定出DMDgene的2000多个突变。这些疾病几乎只发生在男性中，其特征是进行性肌肉无力和消瘦（萎缩）以及称为扩张型心肌病的心脏疾病。导致这些情况的大多数突变都会删除DMDgene的一部分。其他突变异常复制gene的一部分或改变gene中的少量DNA构建块（核苷酸）。(换行)引起贝克尔肌营养不良症的突变，通常具有较轻的特征，并且比杜兴氏肌营养不良症出现的年龄晚，通常会导致肌营养不良蛋白的异常形式保留某些功能。导致更严重的杜兴氏肌营养不良症的突变通常会阻止产生任何功能性肌营养不良蛋白。(换行)没有足够功能性肌营养不良蛋白的骨骼肌和心肌细胞随着肌肉反复收缩和使用放松而受损。受损的细胞会随着时间的流逝而减弱和死亡，从而导致在Duchenne和Becker肌营养不良症中出现的特征性肌肉无力和心脏问题。"}, {"name": "X连锁扩张型心肌病", "detail": "DMDgene中的30多个突变可导致X连锁形式的家族性扩张型心肌病。这种心脏状况会扩大并削弱心肌，阻止心脏有效抽血。尽管扩张型心肌病是Duchenne和Becker肌营养不良症的征兆（如上所述），但X连锁扩张型心肌病通常与骨骼肌无力和消瘦无关。（换行）引起X连锁扩张型心肌病的突变优先影响心肌细胞中肌营养不良蛋白的活性。这些突变的结果是，受影响的个体通常心脏中几乎没有功能性肌营养不良蛋白。如果没有足够的这种蛋白质，心肌细胞就会随着心肌反复收缩和松弛而受损。受损的肌肉细胞会随着时间的流逝而减弱并死亡，从而导致X连锁扩张型心肌病的心脏问题。（换行）引起X连锁扩张型心肌病的突变通常导致骨骼肌细胞中肌营养不良蛋白的量减少。但是，存在足够的这种蛋白质以防止骨骼肌无力和消瘦。"}, {"name": "家族性扩张型心肌病", "detail": "遗传学家庭参考提供有关家族性扩张型心肌病的信息。"}]}' WHERE id = 12552;
UPDATE genes SET GHR_metadata_ch = '{"gene": "MYOT", "conditions": [{"name": "肌原纤维肌病", "detail": "已发现MYOTgene中至少有五个突变引起肌原纤维肌病。这些突变大多数位于称为外显子2的gene区域。导致肌原纤维肌病的MYOTgene突变改变了肌醇蛋白中的单个蛋白质结构单元（氨基酸）。突变的肌醇蛋白与肌节中的其他肌肉蛋白聚集在一起形成团块（聚集体）。聚集体阻止这些蛋白质正常运作。功能异常的肌醇蛋白不能与其他蛋白正确结合，从而阻止了肉瘤和肌原纤维的形成。导致肌原纤维肌病的MYOTgene突变会损害肌纤维的功能，导致肌无力和这种情况的其他特征。"}, {"name": "肢带型肌营养不良症", "detail": "遗传学家庭参考资料提供有关肢带型肌营养不良症的信息。"}]}' WHERE id = 12575;
UPDATE genes SET GHR_metadata_ch = '{"gene": "RETREG1", "conditions": [{"name": "II型遗传性感觉和自主神经病", "detail": "RETREG1gene的突变是导致一种称为HSAN2B的II型遗传性感觉和自主神经病（HSAN2）的原因；在受影响的个体中至少鉴定出五个突变。HSAN2B患者失去感觉疼痛或感觉冷热的能力。RETREG1gene突变可能导致异常短而无功能的蛋白质。由此导致的功能缺失RETREG1蛋白损害内质网的自噬并改变感觉和自主神经元中高尔基体的结构。（线路中断）研究人员怀疑，当不再需要时，无法分解内质网的一部分，随后这些结构和其他蛋白质在细胞中的积累导致细胞死亡。自噬受损导致感觉神经元和自主神经元的丧失，导致HSAN2B的体征和症状。目前尚不清楚高尔基体异常在这种情况下起什么作用。"}]}' WHERE id = 12582;
UPDATE genes SET GHR_metadata_ch = '{"gene": "DNMT3A", "conditions": [{"name": "DNMT3A过度生长综合征", "detail": "已发现DNMT3Agene中至少有16个突变引起DNMT3A过度生长综合征。这种情况的特征是出生前后的生长速度快于正常水平，独特的面部外观和智力残疾。（换行）在身体的所有细胞中发现导致DNMT3A过度生长综合征的DNMT3Agene突变，并导致正常酶功能下降。这些DNMT3Agene突变中的一些导致正常酶功能的降低。结果，DNA甲基化减少，特别是影响出生前的DNA甲基化。目前尚不清楚其他突变如何影响蛋白质功能。DNA甲基化的降低可能会破坏重要发育gene的正常调控，尽管这些变化如何导致DNMT3A过度生长综合征的特定特征尚不清楚。（换行）至少一个导致DNMT3A过度生长综合征的DNMT3Agene改变会删除整个DNMT3Agene和多个相邻gene。尽管这种缺失会导致DNMT3A过度生长综合征的体征和症状，但很难确定其他缺失gene在受影响个体的健康问题发展中起什么作用。"}, {"name": "细胞遗传学正常的急性髓性白血病", "detail": "DNMT3Agene的突变与一种称为细胞遗传学正常的急性髓细胞性白血病（CN-AML）的血癌有关。虽然大的染色体异常可能参与急性髓性白血病的发展，但大约一半的病例没有这些异常；这些被归类为CN-AML。多达三分之一的CN-AML患者的DNMT3Agene发生突变。（换行）CN-AML中涉及的DNMT3Agene突变称为体细胞突变；它们通常存在于一小部分细胞中，并且突变不是遗传的。大多数改变DNA甲基转移酶3α酶中的单个蛋白质构建块（氨基酸）。研究表明，这些变化使得酶不能完全甲基化DNA。还认为细胞中甲基化模式的改变改变了几种gene的活性；一些通常沉默的gene可能会被打开。研究人员推测，改变的gene活性会阻止造血干细胞正常分化，从而导致急性髓性白血病特有的异常，未成熟白细胞过量产生。"}, {"name": "其他癌症", "detail": "在另一种称为T细胞急性淋巴细胞白血病的血液癌中也相对频繁地发现了体细胞DNMT3Agene突变。如在细胞遗传学正常的急性髓性白血病（如上所述）中，突变破坏了细胞中正常的甲基化模式，从而阻止了分化。目前尚不清楚为什么一些具有DNMT3Agene突变的人会发展为急性髓细胞性白血病，而另一些人会发展为急性淋巴细胞性白血病。"}]}' WHERE id = 12602;
UPDATE genes SET GHR_metadata_ch = '{"gene": "VHL", "conditions": [{"name": "家族性红细胞增多症", "detail": "已发现VHLgene中至少有10个遗传突变会引起家族性红细胞增多症，这种疾病的特征是红细胞数量增加和血栓异常风险增加。当VHLgene突变导致家族性红细胞增多时，通常将其命名为ECYT2。（换行）在俄罗斯楚瓦什族人群中发现了与家族性红细胞增多症相关的第一个VHLgene突变。（此后也在其他地理区域发现。）突变改变了VHL蛋白中的单个蛋白质构建块（氨基酸），用200位氨基酸色氨酸取代了氨基酸精氨酸（写为Arg200Trp或R200W）。这种突变破坏了VHL蛋白的功能，特别是其靶向HIF-2α的能力？被打破。结果，即使有足够的氧气，HIF也会在细胞中积累。当不再需要时，额外HIF的存在导致红细胞的产生，这导致血流中这些细胞过量。（折线）其他可能导致家族性红细胞增多的VHLgene突变也会改变VHL蛋白中的单个氨基酸。这些遗传变化被认为对蛋白质功能的影响与Arg200Trp突变相似。这些突变已在楚瓦什人口和全球其他地区发现。"}, {"name": "冯·希佩尔·林道综合征", "detail": "在患有von Hippel-Lindau综合征的人中，已经鉴定出VHLgene的370多个遗传突变，该疾病的特征是在人体许多不同部位形成了肿瘤和充满液体的囊（囊肿）。与这种情况相关的VHLgene突变要么阻止任何VHL蛋白的产生，要么导致该蛋白异常形式的产生。（换行）当VHL蛋白改变或缺失时，VCB-CUL2复合物不能靶向HIF-2？和其他蛋白质被分解。因此，HIF可以在细胞中积累。过量的HIF刺激细胞异常分裂，并在不需要时触发血管的产生。快速和不受控制的细胞分裂，以及新血管的异常形成，可导致von Hippel-Lindau综合征患者的囊肿和肿瘤的发展。"}, {"name": "非综合征性副神经节瘤", "detail": "VHLgene的突变会增加发生称为副神经节瘤或嗜铬细胞瘤（一种副神经节瘤）的神经系统肿瘤的风险。嗜铬细胞瘤是von Hippel-Lindau综合征的特征，但它们和其他副神经节瘤也可以非综合征性发生（没有该综合征的其他体征和症状）。（换行）与非综合征性副神经节瘤或嗜铬细胞瘤相关的VHLgene突变可以遗传或可以自发发生。与这种情况相关的一些自发突变发生在生殖细胞（卵或精子）的形成过程中或受精后，称为从头突变。这种类型的突变存在于身体的每个细胞中。在这种情况下发现的其他自发突变称为体细胞突变，是在一个人的一生中获得的，仅存在于肿瘤细胞中。（换行）在非综合征性副神经节瘤或嗜铬细胞瘤中发现的VHLgene突变改变VHL蛋白中的单个氨基酸或产生异常短的蛋白。这些变化破坏了蛋白质的功能。与von Hippel-Lindau综合征一样，当VHL蛋白改变时，HIF-2？蛋白质不会分解，而是在细胞中积累。过量的HIF刺激细胞异常分裂，并在不需要时触发血管的产生，这可导致副神经节瘤或嗜铬细胞瘤的发展。"}, {"name": "癌症", "detail": "VHLgene中的体细胞（非遗传）突变与一种称为透明细胞肾细胞癌的肾癌有关。这种类型的癌症在没有遗传性VHL突变的人群中发展时被描述为零星的。（换行）而不是在体内的每个细胞中发生，体细胞VHL突变仅在某些肾细胞中发生。这些遗传变化阻止细胞产生功能性VHL蛋白。缺乏这种蛋白质使细胞异常生长和分裂，这可能导致散发性肾肿瘤的发展。另外，肾细胞中VHLgene的体细胞突变可促进现有肾肿瘤的生长。"}, {"name": "其他疾病", "detail": "已经在一种称为血管母细胞瘤的肿瘤中鉴定出VHLgene的突变。这些肿瘤由新形成的血管制成，并倾向于在脑和脊髓中发展。血管母细胞瘤是von Hippel-Lindau综合征的特征，但它们也可能偶发（无其他症状和体征）。当这些肿瘤在没有von Hippel-Lindau综合征的人中发展时，它们与VHLgene的体细胞突变有关。与血管母细胞瘤相关的体细胞突变是在一个人的一生中获得的，仅存在于产生血液和血管的细胞中。(换行)目前尚不清楚VHLgene中的遗传和体细胞突变如何与如此广泛的不同conditions相关联。"}]}' WHERE id = 12604;
UPDATE genes SET GHR_metadata_ch = '{"gene": "XPC", "conditions": [{"name": "着色性干皮症", "detail": "已经发现XPCgene中的40多个突变会引起色素干皮症。该gene的突变是美国和欧洲这种疾病的最常见原因。(换行)大多数XPCgene突变会阻止任何XPC蛋白的产生。这种蛋白质的丢失使细胞无法正常修复DNA损伤。结果，异常在DNA中积累，导致细胞发生故障并最终癌变或死亡。DNA修复中的这些问题会导致色素干皮症患者对阳光下的紫外线极为敏感。当紫外线破坏控制细胞生长和分裂的gene时，细胞会生长得太快，并且无法控制。结果，患有色素干皮症的人患癌症的风险大大增加。这些癌症最常发生在暴露于阳光下的身体部位，如皮肤和眼睛。(换行)与其他一些干性色素性皮肤病不同，当该疾病是由XPCgene突变引起的时，通常与神经系统异常（例如发育迟缓和听力下降）无关。目前还不清楚为什么有些色素干皮症患者会出现神经系统异常，而另一些则不会。"}]}' WHERE id = 12632;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SLC5A1", "conditions": [{"name": "葡萄糖-半乳糖吸收不良", "detail": "在SLC5A1gene中已鉴定出40多种引起葡萄糖-半乳糖吸收不良的突变。一些突变导致蛋白质太短而无法发挥功能。其他突变会导致蛋白质长度正常，但折叠不正确，从而阻止其移动到需要的细胞膜上。（换行）阻止钠/葡萄糖共转运蛋白执行其功能的突变导致肠道中葡萄糖和半乳糖的积累。这种主动转运失败会阻止葡萄糖和半乳糖被吸收并为身体提供营养。此外，通常会与糖一起穿过刷状缘运输的水保留在肠道中，与粪便一起排泄，导致身体组织脱水和严重腹泻。"}]}' WHERE id = 12633;
UPDATE genes SET GHR_metadata_ch = '{"gene": "DNAI1", "conditions": [{"name": "原发性睫状运动障碍", "detail": "已发现DNAI1gene中至少有21个突变会引起原发性睫状运动障碍，这是一种以呼吸道感染，器官放置异常和无法生育（不育）为特征的疾病。DNAI1gene突变导致中间链1缺失或异常。如果没有此子单元的正常版本，ODA将无法正确形成，并且可能会缩短或不存在。结果，纤毛不能产生来回弯曲所需的力。纤毛缺陷导致原发性睫状运动障碍的特征。"}, {"name": "异位症候群", "detail": "遗传学家庭参考资料提供有关异型症候群的信息。"}]}' WHERE id = 12697;
UPDATE genes SET GHR_metadata_ch = '{"gene": "KCNJ11", "conditions": [{"name": "先天性高胰岛素血症", "detail": "已发现KCNJ11gene中的30多个突变会导致先天性高胰岛素血症。这种情况会导致低血糖（低血糖）频繁发作，能量减少和烦躁不安。这些突变大多数会改变蛋白质序列中的单个蛋白质结构单元（氨基酸），从而降低或阻止K-ATP通道的活性。K-ATP通道功能的丧失导致胰岛素从β细胞不断释放。结果，葡萄糖迅速从血流中除去。如果不进行治疗，先天性高胰岛素血症引起的低血糖症可能会导致严重的并发症，例如智力障碍和癫痫发作。"}, {"name": "永久性新生儿糖尿病", "detail": "在永久性新生儿糖尿病患者中，已鉴定出KCNJ11gene至少30个突变。患有这种疾病的人通常出生体重低，并在生命的头6个月内出现血糖升高（高血糖症）。(换行)导致永久性新生儿糖尿病的KCNJ11gene突变改变了蛋白质序列中的单个氨基酸。这些突变导致K-ATP通道不关闭，导致β细胞胰岛素分泌减少和血糖控制受损。"}, {"name": "妊娠糖尿病", "detail": "遗传学家庭参考提供有关妊娠糖尿病的信息。"}, {"name": "其他疾病", "detail": "与永久性新生儿糖尿病（见上文）相比，其他对K-ATP通道功能影响相对较轻的KCNJ11gene突变会导致一种称为短暂性新生儿糖尿病的疾病。患有这种疾病的婴儿在生命的头6个月内患有高血糖症，但到18个月大时血糖恢复正常。但是，受影响的个体通常在青春期或成年早期再次出现高血糖症。与永久性新生儿糖尿病一样，引起短暂性新生儿糖尿病的KCNJ11gene突变也会干扰K-ATP通道的关闭并导致胰岛素分泌减少。（换行）KCNJ11gene的正常变异（多态性）与2型糖尿病（最常见的糖尿病形式）的风险增加有关。该变体导致K-ATP通道的改变，用位置23处的氨基酸赖氨酸替换氨基酸谷氨酸，记为Glu23Lys或E23K。患有2型糖尿病的人患有高血糖症，因为身体对β细胞分泌的胰岛素没有正确反应。同样的变异也与心脏对压力反应的变化有关，导致心力衰竭的风险增加。尽管KCNJ11gene的变化可能与2型糖尿病和心力衰竭有关，但生活方式，遗传和环境因素的综合作用都在确定这些复杂疾病的风险中起作用。"}]}' WHERE id = 12733;
UPDATE genes SET GHR_metadata_ch = '{"gene": "MSTN", "conditions": [{"name": "肌肉生长抑制素相关的肌肉肥大", "detail": "已发现MSTNgene中至少有一个突变会引起肌生长抑制素相关的肌肉肥大，这是一种罕见的疾病，其特征是肌肉质量和力量增加。这种突变被写为IVS1+5 g A，扰乱了gene指令制备肌肉生长抑制素的方式。结果，细胞产生很少或没有功能性肌肉生长抑制素。肌肉细胞中该蛋白质的损失导致肌肉组织的过度生长。它似乎不会在受影响的个人中引起任何其他医疗问题。"}]}' WHERE id = 12750;
UPDATE genes SET GHR_metadata_ch = '{"gene": "TGM3", "conditions": [{"name": "难辨的头发综合症", "detail": "已发现TGM3gene中至少有一个突变会导致难以梳理的头发综合症。这种情况的特点是干燥，卷曲，金色的头皮头发不能梳理。这种情况通常会随着时间的推移而改善，到青春期，患有难以梳理的头发综合症的人的头发平坦且质地正常或接近正常。（换行）已经鉴定的TGM3gene突变导致用于制备转谷氨酰胺酶3的说明书中的过早停止信号，导致具有严重降低的活性的异常短的酶。功能酶的缺乏（缺乏）会损害某些蛋白质之间的交联。特别是，毛干蛋白trichohyalin不能与其他trichohyalin蛋白或称为角蛋白中间丝的分子结合。这些蛋白质和分子需要彼此结合以形成使毛干呈圆柱形的交联。因为转谷氨酰胺酶3不能促进这些交联，所以毛干的横截面变成三角形，心形或扁平形。这些有角度的发干导致卷发的头发不会平躺，这是典型的难以梳理的头发综合症。"}]}' WHERE id = 12755;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ASS1", "conditions": [{"name": "瓜氨酸血症", "detail": "已知ASS1gene的突变会引起I型瓜氨酸血症，这是一种严重的疾病，其特征是健康问题，例如在出生的最初几天观察到进食不良，呕吐，癫痫发作和意识丧失。这是由于酶的结构变化引起的，该变化在第三步破坏了尿素循环，导致有毒的氨积聚在血液中。"}]}' WHERE id = 12773;
UPDATE genes SET GHR_metadata_ch = '{"gene": "GNPTAB", "conditions": [{"name": "粘脂病II alpha/beta", "detail": "已发现GNPTABgene中约有50个突变会引起粘膜脂溢性疾病II alpha/beta。这些突变阻止了任何功能性GlcNAc-1-磷酸转移酶的产生。没有这种酶，水解酶就不能用M6P标记并转运到溶酶体中。相反，水解酶最终在细胞外并具有增加的消化活性。溶酶体内缺乏水解酶会导致大分子在那里积聚。导致分子在溶酶体内积聚的疾病，包括粘膜脂溢性疾病II alpha/beta，被称为溶酶体贮积病。粘脂病IIα/β的体征和症状很可能是由于溶酶体内缺乏水解酶以及这些酶在细胞外的作用引起的。"}, {"name": "粘脂病III alpha/beta", "detail": "还发现GNPTABgene中的突变会引起粘膜脂溢性疾病III alpha/beta。受影响的个体具有导致GlcNAc-1-磷酸转移酶活性降低的突变，从而破坏了用M6P标记水解酶的能力。不接受M6P标签的消化酶最终进入细胞外，在那里它们的活性增加。溶酶体内这些消化酶的缺乏导致大分子在那里积累。粘脂病IIIα/β也被认为是溶酶体贮积病。粘脂病III alpha/beta的体征和症状很可能是由于溶酶体内缺乏水解酶以及这些酶在细胞外的作用所致。"}]}' WHERE id = 12787;
UPDATE genes SET GHR_metadata_ch = '{"gene": "LHX1", "conditions": [{"name": "17q12缺失综合征", "detail": "17q12缺失综合征是由每个细胞中一小块17号染色​​体缺失引起的疾病。17q12缺失综合征的体征和症状可包括肾脏，泌尿道和生殖系统异常；一种称为5型年轻发病型糖尿病（MODY5）的糖尿病；发展迟缓；智力障碍；和行为或精神疾病。一些具有这种染色体变化的女性患有Mayer-Rokitansky-Küster-Hauser综合征，其特征是阴道和子宫发育不全或缺失。与17q12缺失综合征相关的特征差异很大，即使在同一家庭的受影响成员之间也是如此。（换行）染色体17中被删除的部分位于染色体的长（q）臂上指定为q12的位置。染色体的这一区域包含15个gene，包括LHX1。该区域的缺失导致每个细胞中LHX1gene的一个拷贝丢失，从而导致LHX1蛋白的量减少。这种蛋白质的缺乏可能会破坏包括大脑和女性生殖系统在内的多个器官正常发育所必需的gene调控。研究人员怀疑，丢失一份LHX1gene会导致17q12缺失综合征患者的智力残疾，行为和精神疾病以及Mayer-Rokitansky-Küster-Hauser综合征。"}, {"name": "Mayer-Rokitansky-Küster-Hauser综合征", "detail": "遗传学家庭参考资料提供了有关Mayer-Rokitansky-Küster-Hauser综合征的信息。"}]}' WHERE id = 12791;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ABCC8", "conditions": [{"name": "先天性高胰岛素血症", "detail": "ABCC8gene的突变与一种称为先天性高胰岛素血症的疾病有关，其特征是存在缺陷蛋白，该蛋白会干扰K-ATP通道的正确形成及其功能。该病会导致低血糖频繁发作，能量减少以及先天性高胰岛素血症引起的烦躁和低血糖，如果不及时治疗，可能会导致智力残疾和癫痫发作。"}, {"name": "永久性新生儿糖尿病", "detail": "已经在具有永久性新生儿糖尿病的个体中鉴定出ABCC8gene的突变，其特征在于出生体重低，出生后头6个月内出现高血糖。此外，由于K-ATP通道和蛋白质序列缺陷，个体胰岛素分泌减少，血糖控制受损。"}, {"name": "其他疾病", "detail": "对K-ATP通道功能有轻度影响的ABCC8gene突变会导致短暂的新生儿糖尿病。以这种情况为特征的个体在头6个月内表现出高血糖，到18个月大时血糖恢复正常；但是，受影响的个体通常在青春期或成年早期发展为高血糖症。同样，发现ABCC8突变与2型糖尿病风险增加有关。"}]}' WHERE id = 12794;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ADNP", "conditions": [{"name": "ADNP综合征", "detail": "已发现至少22个ADNPgene突变引起ADNP综合征。这种情况的特征是智力残疾和自闭症谱系障碍，其特征是沟通和社交互动受损。受影响的个体也可以具有独特的面部特征和各种其他体征和症状。大多数ADNPgene突变被认为会导致异常短的ADNP蛋白的产生。尽管尚不清楚这些遗传变化如何导致ADNP综合征，但研究人员推测异常短的蛋白质可以附着在DNA上，但不能与SWI/SNF复合物相互作用。结果，染色质重塑受损。此过程的干扰会改变许多gene的活性，并破坏人体某些组织和器官（包括大脑）的发育或功能。这些变化可能解释了智力障碍，自闭症谱系障碍以及ADNP综合征的其他各种体征和症状。"}, {"name": "自闭症谱系障碍", "detail": "遗传学家庭参考提供有关自闭症谱系障碍的信息。"}]}' WHERE id = 12803;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ATP2C1", "conditions": [{"name": "良性慢性天疱疮", "detail": "已发现ATP2C1gene中的100多个突变会引起良性慢性天疱疮，这是一种罕见的皮肤病，其特征是皮肤呈红色，生的和起泡的区域。该gene的突变减少了功能性hSPCA1蛋白的量，这损害了高尔基体中钙离子的储存。由于未知原因，这种异常的钙存储比其他类型的细胞对角质形成细胞的影响更大。钙调节问题会损害许多细胞功能，包括细胞粘附。结果，角质形成细胞彼此不紧密粘附，这导致表皮变得脆弱并且对轻微创伤的抵抗力降低。由于皮肤容易受损，因此会形成生的起泡区域，特别是在有水分和摩擦的皮肤褶皱处。（换行）尽管ATP2C1gene突变也可能影响锰在细胞内的转运，但锰调节异常并不被认为有助于良性慢性天疱疮的体征和症状。"}]}' WHERE id = 12805;
UPDATE genes SET GHR_metadata_ch = '{"gene": "HPD", "conditions": [{"name": "酪氨酸血症", "detail": "研究人员已经鉴定出至少六个导致III型酪氨酸血症的HPDgene突变。这种情况的特征是神经系统问题，例如智力残疾，癫痫发作以及定期失去平衡和协调能力（间歇性共济失调）。导致这种情况的某些突变会改变4-羟苯基丙酮酸双加氧酶中的单个氨基酸。其他突变导致产生异常小的酶。这些突变的结果是，4-羟基苯基丙酮酸双加氧酶的活性异常低或不存在。结果，该酶不能在酪氨酸分解中发挥作用，因此4-羟基苯基丙酮酸转化为有毒化合物而不是高纯酸。随着这些有毒化合物在细胞中积聚，它们可能损害功能并最终导致细胞死亡。神经系统中的细胞对这种毒性积累特别敏感。神经细胞的损伤和死亡可能导致III型酪氨酸血症的特征。"}, {"name": "其他疾病", "detail": "已经发现至少两种HPDgene突变引起称为hawkinsinuria的罕见病症。在婴儿中，这种疾病的特征是体重增加失败并以预期的速度增长（无法壮成长）和血液中异常高的酸水平（酸中毒）。导致hawkinsinuria的HPDgene突变导致酶活性降低，因此4-羟基苯基丙酮酸不能有效地转化为高纯酸。相反，一些4-羟基苯基丙酮酸形成一种不寻常的含硫氨基酸，称为hawkinsin。目前尚不清楚hawkinsin的产生如何导致hawkinsinuria的特征。"}]}' WHERE id = 12814;
UPDATE genes SET GHR_metadata_ch = '{"gene": "DYSF", "conditions": [{"name": "肢带型肌营养不良症", "detail": "DYSFgene中已鉴定出140多种可导致2B型肢带型肌营养不良的突变。肢带型肌营养不良症是一组相关疾病，其特征是肌肉无力和消瘦（萎缩），尤其是在肩膀，臀部，大腿和上臂。由DYSFgene突变引起的肌肉问题，例如2B型肢带型肌营养不良症等（见下文），被称为dysferlinopathies。（换行）DYSFgene突变可能破坏dysferlin蛋白的功能并干扰肌肉修复过程。研究表明，受损肌肉纤维的修复效率低下可能导致炎症和肌肉变性，从而导致肌肉无力。"}, {"name": "三好肌病", "detail": "已发现DYSFgene中的100多个突变会引起三好肌病。这种情况是一种肌肉疾病，其特征是远离身体中心（远端肌肉），尤其是腿部肌肉的进行性无力和萎缩。在三好肌病患者中发现的DYSFgene突变会改变dysferlin蛋白中的单个氨基酸，从而损害该蛋白的功能或导致产生无功能的蛋白。日本血统的人患此病的常见原因是一种突变，该突变用dysferlin中999位的氨基酸半胱氨酸取代了氨基酸色氨酸（写为Trp999Cys或W999C）。(换行)缺乏正常的dysferlin会导致修复对肌纤维肌膜造成的损伤的能力降低。结果，损伤累积并导致肌纤维萎缩。目前尚不清楚为什么引起三好肌病的DYSFgene突变会导致这种疾病的特征性虚弱和萎缩的特定模式。(换行)一些研究人员认为三好肌病是肢带型肌营养不良症的一种变体（见上文），而不是单独的疾病，因为它们是由同一gene的突变引起的，并且具有重叠的体征和症状。"}, {"name": "其他疾病", "detail": "DYSFgene突变还引起另一种称为胫骨前部发作的远端肌病的神经营养不良症。在这种情况下，肌肉无力在小腿肌肉中最为明显。这种情况首先在称为胫骨前肌的肌肉中变得明显，该肌肉位于小腿的前部并有助于弯曲脚。胫骨前部发作的远端肌病随后会影响大腿肌肉，受影响的个体最终需要轮椅帮助。"}]}' WHERE id = 12825;
UPDATE genes SET GHR_metadata_ch = '{"gene": "AURKC", "conditions": [{"name": "大精子症", "detail": "已发现AURKCgene中至少有四个突变引起大精子症。这种情况导致男性精子异常，导致无法生育孩子（不孕症）。导致大精子症的AURKCgene突变导致产生无功能的蛋白质或快速分解的蛋白质。在北非血统的男性中，这种情况的常见原因之一是从AURKCgene（写144delC）中删除了一个DNA构建块（核苷酸）。由改变的gene产生的蛋白质异常短且无功能。（换行）缺乏极光激酶C阻断精子细胞中的细胞分裂。结果，精子细胞具有额外的染色体，通常每个染色体有四个拷贝而不是通常的一个。染色体数目的这种增加扩大了精子细胞的头部并导致存在多个尾巴（鞭毛）。由于额外的遗传物质，如果这些异常精子细胞中的一个与卵细胞结合，胚胎将不会发育或怀孕将导致流产。"}]}' WHERE id = 12834;
UPDATE genes SET GHR_metadata_ch = '{"gene": "POU3F4", "conditions": [{"name": "非综合征性听力损失", "detail": "涉及POU3F4gene的突变会导致非综合征性听力损失，这是与其他体征和症状无关的听力损失。该gene的突变会导致某种形式的听力损失，称为DFNX2，有时也称为Nance耳聋。DFNX2是X连锁非综合征性听力损失的最常见类型，约占所有病例的一半。X连锁conditions是由X染色体上的gene突变引起的，X染色体是两个性染色体之一。(换行)DFNX2的体征和症状在男性和女性中有所不同。在男性中，这种形式的听力损失始于孩子学会说话（舌前），随着时间的推移变得更加严重，通常涉及内耳和中耳的异常（混合性听力损失）。在中耳，其中一块小骨（镫骨）无法正常移动，这会干扰听力。DFNX2的这种特征性标志称为镫骨固定。在修复这种异常的手术过程中，受影响的男性极有可能发生称为淋巴周分泌物的并发症，这是内耳积液的泄漏，可能导致永久性的完全听力丧失。具有POU3F4gene突变的女性受到的影响较小，几乎没有或没有听力损失，也没有明显的中耳异常。当听力损失发生时，通常在孩子学会说话（舌后）后开始。(换行)已发现超过50个POU3F4gene突变引起DFNX2。这些遗传变化中的许多会改变POU3F4蛋白中的单个蛋白结构单元（氨基酸），或删除部分或全部POU3F4gene。在某些情况下，已在POU3F4gene附近的DNA中发现了突变。研究人员认为，该DNA可能在调节POU3F4gene中起作用。该gene中或附近的突变会阻止细胞产生任何POU3F4蛋白或损害蛋白与DNA结合至关重要的区域。缺乏功能性POU3F4蛋白可能会破坏中耳和内耳结构的正常发育，从而导致听力下降。"}]}' WHERE id = 12866;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ITGB3", "conditions": [{"name": "格兰兹曼血小板减少症", "detail": "已发现ITGB3gene中至少有130个突变会引起Glanzmann血小板减少症，这是一种罕见的出血性疾病。导致这种疾病的突变发生在每个细胞中gene的两个拷贝中，并且损害β3亚基的产生或活性，这破坏了功能性整联蛋白的形成？IIb？3.功能性整合素缺乏（缺乏）？IIb？3防止纤维蛋白原或其他蛋白质的充分结合，损害血栓的形成。缺乏血小板凝聚力会导致格兰兹曼血小板减少症患者经历长时间或自发性出血事件。"}, {"name": "Osteopetrosis", "detail": "遗传学家庭参考提供有关骨质疏松症的信息。"}, {"name": "其他疾病", "detail": "ITGB3gene的突变也可引起另一种罕见的出血性疾病，称为血小板型出血性疾病16。患有这种疾病的人有类似于格兰兹曼血小板减少症（如上所述）的体征和症状，包括频繁的流鼻血（鼻出血），牙龈出血，或皮肤下出血引起的皮肤红色或紫色斑点（瘀斑），但发作通常较温和。(换行)与Glanzmann血小板减少症不同，这种疾病是由每个细胞中只有一个ITGB3gene拷贝的突变引起的，并且突变导致形成一些整联蛋白？IIb？3即使没有形成凝块，异常打开（活动）。这种异常活跃的蛋白质无法到达血小板表面，在凝块形成过程中需要与其他血小板结合。过度活跃的整合素？IIb？3在血小板形成期间不适当地结合细胞内的凝血蛋白，导致血小板变得畸形和大。形状异常的血小板寿命缩短，因此血小板数量经常减少，这会损害血块形成。（减少血小板数量的组合被称为大血小板减少症。）（换行）由于导致这种疾病的突变只影响ITGB3gene的一个拷贝，一些正常的整合素形成并产生正常的血小板，这就解释了受影响个体的轻微体征和症状。"}]}' WHERE id = 12875;
UPDATE genes SET GHR_metadata_ch = '{"gene": "OCRL", "conditions": [{"name": "牙病", "detail": "已发现OCRLgene中至少有20个突变引起Dent病2。这种形式的Dent疾病的特征是慢性肾脏问题；一些受影响的人还患有轻度智力障碍，肌肉张力弱（肌张力低下）和眼睛晶状体混浊（白内障），不会损害视力。一些研究人员认为Dent病2是Lowe综合征的轻度变异，下面将对此进行讨论。（换行）导致Dent疾病2的OCRLgene突变减少或消除OCRL酶的功能。这些变化损害某些分子的运输和肌动蛋白细胞骨架的调节。它们还可能通过改变纤毛的结构或功能来影响细胞信号传导。这些重要细胞活动的破坏可能损害肾功能，导致尿液中蛋白质过量（蛋白尿），肾结石，最终导致肾功能衰竭。尚不清楚OCRL酶的丧失如何导致这种情况的其他体征和症状。(换行)由于OCRL酶存在于整个身体中，因此尚不清楚为什么Dent疾病2主要影响肾脏，并在较小程度上影响大脑，眼睛和其他组织。其他酶可能能够补偿未受影响组织中有缺陷的OCRL酶。"}, {"name": "洛综合征", "detail": "在患有Lowe综合征（一种影响眼睛，大脑和肾脏的疾病）的个体中，已经鉴定出OCRLgene的120多个突变。这些突变中的一些阻止了任何OCRL酶的产生。其他突变会降低或消除酶的活性或阻止其与细胞内其他蛋白质相互作用。研究人员认为，这些遗传变化的影响与引起Dent疾病2的突变相似：这些突变可能会改变细胞内分子的运输，肌动蛋白细胞骨架的调节以及纤毛的结构和功能。尚不清楚为什么某些OCRLgene突变会导致Lowe综合征，而另一些会导致Dent疾病2。(换行)由于OCRL酶存在于全身，因此尚不清楚为什么Lowe综合征主要影响肾脏，大脑和眼睛。与Dent疾病2一样，其他酶也可能能够补偿未受影响组织中有缺陷的OCRL酶。"}]}' WHERE id = 12880;
UPDATE genes SET GHR_metadata_ch = '{"gene": "DCN", "conditions": [{"name": "先天性基质角膜营养不良", "detail": "在先天性基质角膜营养不良的家庭中，已经鉴定出DCNgene的几个突变。每个已知的突变都会导致异常短版本的核心蛋白聚糖蛋白的产生。这种异常蛋白质干扰角膜中胶原纤维的组织。由于排列不良的胶原纤维积聚，角膜变得混浊。这些角膜变化导致视力丧失（视力下降）和其他与视力障碍有关的眼部异常。"}]}' WHERE id = 12902;
UPDATE genes SET GHR_metadata_ch = '{"gene": "TNFRSF11A", "conditions": [{"name": "骨质疏松症", "detail": "遗传学家庭参考提供有关骨质疏松症的信息。"}, {"name": "骨Paget病", "detail": "已发现TNFRSF11Agene中至少有两个非常相似的突变会导致罕见的，早发性的佩吉特骨病。这两个突变都是重复，这意味着它们异常地复制了gene内的一段遗传物质。这些突变中的每一个都导致产生RANK蛋白，该蛋白包含几个额外的蛋白结构单元（氨基酸）。（线突破）通过一种尚不为人所知的机制，TNFRSF11Agene中的重复突变似乎过度激活了促进破骨细胞形成的化学信号通路。增加的信号传导刺激过多破骨细胞的产生并触发这些细胞异常分解骨。在患有早发性Paget骨病的人中，受影响的骨头被分解并替换得比平时快得多。当新骨组织生长时，它比正常骨更弱，组织更少。骨重塑的这些问题导致某些骨骼变得异常大，畸形并且容易断裂（断裂）。"}, {"name": "其他疾病", "detail": "TNFRSF11Agene的突变可导致其他几种罕见的骨病，包括两种非常相似的疾病，称为家族性扩张性骨溶解（FEO）和扩张性骨骼高磷血症（ESH）。这些疾病的体征和症状与早发性佩吉特骨病的体征和症状重叠。事实上，一些研究人员认为，FEO，ESH和早发性Paget骨病实际上可能是单一病症的稍微不同的形式。FEO和ESH都出现在生命的早期，其特征是骨骼异常，牙齿脱落和进行性听力丧失。(换行)像骨骼的早期Paget病一样，FEO和ESH是由TNFRSF11Agene的重复突变引起的。研究表明，这些突变过度激活RANK，导致其刺激过多破骨细胞的产生并触发这些细胞异常分解骨骼。由此导致的骨重塑失衡导致这些疾病的主要特征。目前尚不清楚为什么TNFRSF11Agene中的重复突变会导致几种不同的骨骼疾病。（换行）TNFRSF11Agene突变也会引起称为常染色体隐性骨质疏松症（ARO）的骨病。这种疾病出现在婴儿期，其特征是骨骼异常致密。骨密度增加导致各种并发症，包括骨折风险增加，视力障碍，听力丧失以及与骨髓缺陷相关的免疫系统问题。TNFRSF11Agene的突变似乎是ARO的罕见原因；在受影响的个体中发现少于10个突变。这些突变大多数会改变RANK蛋白中的单个氨基酸，从而阻止其在未成熟破骨细胞表面接收信号。结果，患有这种疾病的人完全没有成熟的功能性破骨细胞。没有这些专门的细胞来分解骨组织，整个骨骼就会形成多余的骨骼。"}]}' WHERE id = 12986;
UPDATE genes SET GHR_metadata_ch = '{"gene": "XK", "conditions": [{"name": "McLeod neuroacanthocytosis综合征", "detail": "已经发现XKgene中大约30个突变引起McLeod神经棘皮细胞增多症综合征。这些突变中的大多数导致异常短的，无功能的XK蛋白的产生或根本不产生XK蛋白。XK蛋白缺失或异常也会影响另一种血型抗原Kell蛋白，导致其在红细胞表面的流行程度降低。XKgene的突变如何导致畸形的红细胞和McLeod神经棘皮细胞增多症综合征的运动问题尚不清楚。"}]}' WHERE id = 12989;
UPDATE genes SET GHR_metadata_ch = '{"gene": "DNMT1", "conditions": [{"name": "常染色体显性小脑共济失调，耳聋和发作性睡病", "detail": "在患有常染色体显性小脑共济失调，耳聋和发作性睡病（ADCADN）的神经系统疾病患者中，至少鉴定出四个DNMT1gene突变。这种疾病的特征包括难以协调运动（共济失调），内耳异常引起的听力损失（感音神经性耳聋）和白天过度嗜睡（发作性睡病）。随着疾病的进展，认知能力下降。手臂和腿部麻木，刺痛或疼痛（感觉神经病）也可能发生。受影响的人通常可以存活到四五十年代。（换行）与该病症相关的DNMT1gene突变影响DNA甲基转移酶1酶的区域，称为靶向序列，其有助于将甲基化过程引导至DNA的正确区段。这些突变的结果是甲基化异常，这会影响多个gene的表达。组成神经系统的神经元的维持被破坏，导致ADCADN的体征和症状。"}, {"name": "遗传性感觉和自主神经病IE型", "detail": "在患有另一种称为遗传性感觉和自主神经病IE型（HSAN IE）的神经系统疾病的人中，至少鉴定出10个DNMT1gene突变。与ADCADN（如上所述）一样，HSAN IE患者患有感音神经性耳聋，感觉神经病，认知能力下降和寿命缩短。但是，它们通常没有ADCADN中发生的共济失调或发作性睡病，并且HSAN患者的感觉神经病在生命早期就开始了，即在这种疾病中也可能发生癫痫发作。（换行）引起HSAN IE的DNMT1gene突变，如引起ADCADN的gene突变，影响DNA甲基转移酶1酶的靶向序列。然而，导致HSAN IE的大多数突变发生在称为外显子20的gene的一部分中并影响酶的靶向序列的一端，而引起ADCADN的突变主要位于称为外显子21的gene的附近部分并且影响酶的靶向序列的另一端。（换行）由于DNMT1gene突变，甲基化异常并且构成神经系统的神经元的维持受损。但是，尚不清楚突变如何引起HSAN IE的特定体征和症状，或者影响DNA甲基转移酶1酶靶向序列的突变如何引起HSAN IE或ADCADN。"}, {"name": "癌症", "detail": "DNMT1gene的几种正常变异（多态性）与癌症风险增加有关，包括乳腺癌和胃癌。这些变异存在于整个身体的细胞中，可以从父母传给孩子，可能会影响DNA甲基转移酶1酶的活性及其在gene沉默中的作用。特定gene沉默的改变可导致异常的细胞生长和分裂，并增加患癌症的风险。（换行）此外，在某些称为神经胶质瘤的脑癌中已经鉴定出DNMT1gene的活性（过表达）增加。这种过度表达所涉及的遗传变化是体细胞的，这意味着它们仅发生在肿瘤细胞中，而不是遗传的。研究人员认为，DNMT1gene的过度表达可能导致称为肿瘤抑制gene的甲基化和沉默。当肿瘤抑制gene沉默时，细胞可以不受控制地生长和分裂，这可能导致癌症。"}]}' WHERE id = 12991;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ABL1", "conditions": [{"name": "慢性粒细胞白血病", "detail": "涉及ABL1gene和BCRgene的染色体易位通常称为费城染色体（称为BCR-ABL1融合gene），导致一种严重类型的缓慢生长的血癌，称为慢性粒细胞白血病，其特征在于过度疲劳的异常白细胞的过量产生（疲劳），发烧，体重减轻和脾脏肿大。"}, {"name": "其他癌症", "detail": "涉及具有BCR的ABL1gene，通常称为BCR-ABL1或ETV6-ABL1的ETV6gene的染色体易位与称为急性白血病和B细胞急性淋巴细胞白血病的其他快速生长的血细胞癌相关。"}]}' WHERE id = 12992;
UPDATE genes SET GHR_metadata_ch = '{"gene": "CYBB", "conditions": [{"name": "慢性肉芽肿病", "detail": "已发现CYBBgene中的650多个突变会引起慢性肉芽肿性疾病。患有这种疾病的人由于免疫系统减弱而发生反复发作的感染和炎症的风险增加。CYBBgene的突变导致约70%的这种情况。这些突变大多数会改变细胞色素b-245β链中蛋白质（氨基酸）的单个结构单元，或导致其异常短而无功能。改变的蛋白质不仅会降低β链的功能，还会降低其α链伴侣的功能。没有这些亚基，NADPH氧化酶就无法正常组装或发挥作用。结果，吞噬细胞不能产生活性氧来杀死外来入侵者，并且嗜中性粒细胞活性不受调节。缺乏NADPH氧化酶会使受影响的个体容易受到多种类型的感染和过度炎症的影响。"}]}' WHERE id = 13003;
UPDATE genes SET GHR_metadata_ch = '{"gene": "GPHN", "conditions": [{"name": "钼辅助因子缺乏", "detail": "GPHNgene突变会导致一种称为钼辅因子缺乏症的疾病。这种疾病的特征是从生命早期开始发作，脑功能障碍随着时间的推移而恶化（脑病）；这种情况通常在儿童早期就致命。已发现GPHNgene中至少有两个突变会导致某种形式的疾病，称为C型或互补组C。这是该病的最罕见形式，仅影响少数个体。（换行）涉及钼辅因子缺乏症的GPHNgene突变可能会降低或消除gephyrin的功能。已知的突变会损害gephyrin执行钼辅因子生物合成的最后两个步骤中的一个或两个步骤的能力。没有辅因子，依赖它的代谢酶就不能发挥作用。(换行)由此导致的酶活性丧失导致某些化学物质的积累，包括亚硫酸盐，S-硫代半胱氨酸，黄嘌呤和次黄嘌呤，以及低水平的另一种称为尿酸的化学物质。（测试这些化学物质可以帮助诊断这种情况。）通常由亚硫酸盐氧化酶分解的亚硫酸盐是有毒的，特别是对大脑有毒。研究人员认为，亚硫酸盐（可能还有其他化学物质）异常高水平造成的损害会导致脑病，癫痫发作和钼辅因子缺乏症的其他特征。"}, {"name": "遗传性高血压", "detail": "遗传学家庭参考提供有关遗传性多发性上睑下垂的信息。"}]}' WHERE id = 13005;
UPDATE genes SET GHR_metadata_ch = '{"gene": "GALT", "conditions": [{"name": "半乳糖血症", "detail": "在患有典型半乳糖血症的人中，已经鉴定出GALTgene的300多个突变，这种疾病会在出生后不久开始引起威胁生命的体征和症状。这些突变中的大多数严重降低或消除了半乳糖-1-磷酸尿嘧啶转移酶的活性。这种酶的缺乏会阻止细胞加工从饮食中获得的半乳糖。结果，半乳糖-1-磷酸和相关化合物可在体内累积至毒性水平。这些物质的积累会损害组织和器官，导致与经典半乳糖血症相关的严重医学问题。（换行）GALTgene中的大多数变化改变半乳糖-1-磷酸尿嘧啶转移酶中的单个蛋白质构建块（氨基酸）。在欧洲白人和北美人中，最常见的GALT突变是将氨基酸谷氨酰胺替换为酶中188位的氨基酸精氨酸（写为Gln188Arg或Q188R）。另一种突变几乎只发生在非洲人后裔中。这种遗传变化将氨基酸亮氨酸替换为位置135处的氨基酸丝氨酸（写为Ser135Leu或S135L）。（换行）称为Duarte变体的特定GALT突变导致半乳糖血症的形式，其并发症不如经典类型严重。该突变用蛋白质位置314（写为Asn314Asp或N314D）的氨基酸天冬氨酸取代氨基酸天冬酰胺。Duarte变体减少但不消除半乳糖-1-磷酸尿嘧啶转移酶的活性。与该变体相关的体征和症状往往较轻，因为该酶保留了其正常活性的5%至20%。"}]}' WHERE id = 13014;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SUCLA2", "conditions": [{"name": "琥珀酸辅酶A连接酶缺乏症", "detail": "在琥珀酸CoA连接酶缺乏症患者中，已经鉴定出SUCLA2gene中至少有四个突变。这些突变中的每一个都会改变A-SUCL的结构，从而降低酶的活性。但是，SUCLA2gene突变不会影响琥珀酸CoA连接酶G-SUCL的其他版本。研究表明，G-SUCL的活性可能能够补偿某些组织中a-SUCL的损失。(换行)A-SUCL的短缺（缺乏）导致在需要大量能量的脑，肌肉和其他组织中产生和维持线粒体DNA的问题。线粒体DNA量的减少（称为线粒体DNA消耗）损害线粒体功能和细胞内能量的产生。这些问题导致肌肉张力弱（肌张力低下），发育迟缓以及琥珀酸CoA连接酶缺乏症的其他特征。"}, {"name": "利综合征", "detail": "遗传学家庭参考提供有关Leigh综合征的信息。"}]}' WHERE id = 13018;
UPDATE genes SET GHR_metadata_ch = '{"gene": "FGD4", "conditions": [{"name": "进行性神经性腓骨肌萎缩症", "detail": "在患有4H型Charcot-Marie-Tooth病的人中，已经鉴定出FGD4gene中至少有五个突变。这些突变会改变蛋白质的形状或导致异常短的无功能蛋白质。frabin在细胞信号传导中的作用中断会导致周围神经系统异常髓鞘形成，从而导致4H型Charcot-Marie-Tooth病的体征和症状。"}]}' WHERE id = 13025;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ELOVL4", "conditions": [{"name": "Stargardt黄斑变性", "detail": "已经发现ELOVL4gene中至少三个突变引起Stargardt黄斑变性。这些突变会在用于制备ELOVL4蛋白的说明中产生过早的终止信号。结果，该蛋白质不能保留在感光细胞的内质网中。相反，ELOVL4蛋白形成团块（聚集体）。这些聚集体不能产生非常长链的脂肪酸，并可能干扰细胞功能，最终导致细胞死亡。感光细胞的丧失会导致Stargardt黄斑变性患者进行性视力丧失。ELOVL4gene的突变是这种情况的罕见原因。"}, {"name": "年龄相关性黄斑变性", "detail": "遗传学家庭参考提供有关年龄相关性黄斑变性的信息。"}]}' WHERE id = 13051;
UPDATE genes SET GHR_metadata_ch = '{"gene": "FAM83H", "conditions": [{"name": "釉质发育不全", "detail": "FAM83Hgene中至少有20个突变以常染色体显性形式存在，这是一种称为牙釉质发育不全的牙齿发育障碍。常染色体显性遗传意味着每个细胞中FAM83Hgene的一个拷贝被改变。所有已知的FAM83Hgene突变都会导致异常短的蛋白质产生。正常蛋白质存在于细胞内充满液体的空间（细胞质）中，而改变的蛋白质则存在于细胞核中。研究人员推测，由gene的突变拷贝产生的改变的蛋白质会干扰由gene的非突变拷贝产生的正常蛋白质的功能（此类突变被描述为“显性阴性”）。但是，研究人员不知道这种改变的蛋白质如何导致牙齿异常稀薄，粗糙，黄褐色的牙釉质。"}]}' WHERE id = 13118;
UPDATE genes SET GHR_metadata_ch = '{"gene": "LDLRAP1", "conditions": [{"name": "高胆固醇血症", "detail": "LDLRAP1gene中的10多个突变已显示出一种遗传性高胆固醇形式，称为常染色体隐性高胆固醇血症。这些突变导致异常小的，无功能的LDLRAP1蛋白产生，或阻止细胞产生任何这种蛋白。没有LDLRAP1蛋白，低密度脂蛋白受体不能有效地从血流中去除低密度脂蛋白。尽管受体仍然可以正常结合低密度脂蛋白，但这些分子不能正确转运到细胞（特别是肝细胞）中。结果，许多额外的低密度脂蛋白保留在血液中。(换行)由于低密度脂蛋白是血液中胆固醇的主要携带者，因此LDLRAP1gene突变的人血液中的胆固醇水平非常高。当过量的胆固醇通过血液循环时，它会异常沉积在向心脏（冠状动脉）供血的皮肤，肌腱和动脉等组织中。冠状动脉壁中胆固醇的积累大大增加了心脏病发作的风险。"}]}' WHERE id = 13128;
UPDATE genes SET GHR_metadata_ch = '{"gene": "NOP56", "conditions": [{"name": "小脑萎缩症36型", "detail": "NOP56gene突变会导致脊髓小脑共济失调36型（SCA36），这是一种以进行性运动问题为特征的疾病，通常始于成年中期。在患有SCA36的人中，内含子1中的GGCCTG字符串至少重复650次。（换行）为了根据gene中携带的遗传指令制造蛋白质，形成称为信使RNA（mRNA）的分子。该分子充当蛋白质生产的遗传蓝图。但是，NOP56gene中GGCCTG重复序列数量的大量增加破坏了NOP56 mRNA的正常结构。异常的NOP56 mRNA分子在神经元核内形成称为RNA灶的团块。其他蛋白质被困在RNA病灶中，在那里它们无法发挥作用。这些蛋白质对于控制gene活性或蛋白质产生可能是重要的。(换行)此外，研究人员认为，NOP56gene中六核苷酸重复序列的大量扩增可能会降低附近称为MIR1292的gene的活性。MIR1292gene提供了制备一种RNA的说明，该RNA调节产生称为谷氨酸受体的蛋白质的gene的活性（表达）。这些蛋白质存在于神经元表面，使这些细胞能够相互通信。Mir1292 RNA产量的减少可导致谷氨酸受体产量的增加。增加的受体活性可能会过度兴奋神经元，从而破坏细胞之间的正常通讯并可能导致运动困难。（换行）RNA焦点和过度兴奋的神经元的组合可能导致神经元随时间死亡。由于NOP56gene在小脑神经元中特别活跃，因此这些细胞特别受gene扩增的影响，导致小脑细胞死亡。这部分大脑的恶化会导致共济失调以及SCA36的其他体征和症状。"}]}' WHERE id = 13142;
UPDATE genes SET GHR_metadata_ch = '{"gene": "PKD1", "conditions": [{"name": "多囊肾病", "detail": "在多囊肾患者中已鉴定出PKD1gene的250多个突变。这些突变约占常染色体显性遗传性多囊肾病（ADPKD）病例的85%，这是该疾病的最常见类型。PKD1gene中的突变包括DNA构建块（碱基对）的缺失或插入以及一个或多个碱基对的改变。预计大多数PKD1突变会产生异常小的，无功能的polycystin-1蛋白。尽管研究人员不确定缺乏polycystin-1如何导致囊肿的形成，但它可能会破坏蛋白质在细胞内和原发纤毛中的信号传导功能。结果，衬在肾小管内的细胞可能异常生长和分裂，导致多囊肾病特征性的许多囊肿的生长。"}]}' WHERE id = 13147;
UPDATE genes SET GHR_metadata_ch = '{"gene": "G6PD", "conditions": [{"name": "6-磷酸葡萄糖脱氢酶缺乏症", "detail": "在G6PDgene中已鉴定出200多种导致6-磷酸葡萄糖脱氢酶缺乏的突变。几乎所有这些突变都会导致6-磷酸葡萄糖脱氢酶中单个结构单元（氨基酸）的变化。这些变化破坏了酶的正常结构和功能，或减少了细胞中产生的酶的量。(换行)没有足够的功能性葡萄糖-6-磷酸脱氢酶，红细胞就无法保护自己免受活性氧的破坏作用。受损的细胞可能会过早破裂和分解（发生溶血）。诸如感染，某些药物和摄入蚕豆等因素可以增加活性氧的水平，导致红细胞溶血的速度快于身体可以替代它们的速度。红细胞的这种损失引起溶血性贫血的体征和症状，这是葡萄糖-6-磷酸脱氢酶缺乏症的特征。"}]}' WHERE id = 13148;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ITM2B", "conditions": [{"name": "遗传性脑淀粉样血管病", "detail": "已经发现ITM2Bgene中的两个突变引起称为遗传性脑淀粉样血管病的病症。当这种情况是由ITM2Bgene突变引起的时，其特征是运动问题和智力功能下降（痴呆）。ITM2Bgene突变会导致两种形式的疾病，称为家族性英国痴呆和家族性丹麦痴呆，以首次诊断该疾病的地区命名。导致英国类型的ITM2Bgene突变导致产生比正常情况更长的蛋白质。ITM2B蛋白通常具有一个停止信号，该信号指示在何处停止该蛋白序列，以使所有制成的ITM2B蛋白都相同。导致英国类型的突变会改变停止信号，从而使蛋白质中添加更多长度。此突变写为Ter267Arg或X267R。导致丹麦类型的突变是相似的，但是不是改变终止信号，而是向gene中添加了额外的DNA片段，这意味着蛋白质更长。此突变写为795-796insTTTAATTTGT。（换行）导致英国型或丹麦型的ITM2Bgene突变产生细长的蛋白质，分别称为ABri或ADan，具有改变的三维形状，其倾向于聚集在一起（聚集）。这些聚集的蛋白质形成称为淀粉样蛋白沉积物的团块，其积聚在大脑的特定区域及其血管中。淀粉样蛋白沉积物（称为斑块）触发补体系统的激活，补体系统是一组免疫系统蛋白，它们共同作用以破坏病原体，引发炎症并清除细胞和组织中的碎片。其他免疫系统反应也被激活，这些反应都攻击沉积物周围的区域。补体系统和其他反应导致大脑各个部位的细胞死亡和组织损伤。这些异常是家族性英国痴呆和家族性丹麦痴呆类型的遗传性脑淀粉样血管病的体征和症状的基础。"}]}' WHERE id = 13191;
UPDATE genes SET GHR_metadata_ch = '{"gene": "TPP1", "conditions": [{"name": "CLN2疾病", "detail": "已发现TPP1gene中至少有115个突变引起CLN2疾病。这种情况通常会从儿童早期开始损害运动和智力发育，导致运动障碍逐渐恶化和智力功能下降。此外，受影响的儿童经常会出现反复发作（癫痫）和视力障碍。在某些情况下，CLN2疾病的体征和症状直到儿童晚期才出现，通常在4岁以后。（换行）导致CLN2疾病的大多数TPP1gene突变会改变三肽基肽酶1中的单个氨基酸，从而导致酶活性严重降低。功能酶的减少导致某些肽的不完全分解。CLN2疾病的特征是溶酶体中蛋白质或肽和其他物质的积累。这些积聚发生在全身的细胞中；然而，神经细胞似乎特别容易受到其影响。积累会导致细胞损伤，导致细胞死亡。脑和其他组织中神经细胞的进行性死亡导致CLN2疾病的体征和症状。(换行)在儿童期后期被诊断患有CLN2疾病的个体可能具有TPP1gene突变，导致产生具有少量正常功能的酶。这些个体的蛋白质功能高于那些从儿童早期就患有这种疾病的个体。结果，肽和其他物质积聚在溶酶体中并损伤神经细胞需要更长的时间。"}, {"name": "其他疾病", "detail": "还发现TPP1gene的突变会导致7型脊髓小脑共济失调（SCA7），这是一种以运动进行性问题为特征的疾病。在儿童时期，患有SCA7的人会出现行走困难；言语障碍（构音障碍）；和眼球运动问题，例如眼睛的不自主运动（眼球震颤），快速眼球运动（扫视）以及眼睛左右移动困难（动眼性运动失用）。患有SCA7的人会逐渐丧失大脑各个部位的细胞（萎缩），尤其是在小脑内，小脑是参与协调运动的大脑区域。(换行)与患有CLN2疾病的个体（如上所述）相比，患有SCA7的个体可能具有更高水平的正常功能的三肽基肽酶1。结果，与CLN2疾病相比，SCA7具有更轻的体征和症状，并且倾向于在儿童晚期或青春期发展。检查时，来自某些患有SCA7的个体的细胞显示出溶酶体积累，而来自其他受影响个体的细胞则没有。"}]}' WHERE id = 13210;
UPDATE genes SET GHR_metadata_ch = '{"gene": "RPGR", "conditions": [{"name": "视网膜色素变性", "detail": "已经发现RPGRgene中的300多个突变引起色素性视网膜炎的X连锁形式。这种情况主要影响男性，导致儿童早期夜盲症，随后进行白天视力丧失。RPGRgene突变约占所有X连锁性色素性视网膜炎病例的70%。（换行）导致X连锁性视网膜色素变性的大多数突变发生在RPGR蛋白的ORF15外显子中。这些突变通常会导致蛋白质异常短而功能异常。RPGR蛋白结构的变化可能会破坏感光细胞中纤毛的正常功能。然而，目前还不清楚这些变化如何导致光感受器的逐渐丧失以及由此导致的视网膜色素变性特征性的视力问题。"}, {"name": "视锥-视杆营养不良", "detail": "遗传学家庭参考提供有关锥杆营养不良的信息。"}, {"name": "原发性睫状运动障碍", "detail": "遗传学家庭参考提供有关原发性睫状运动障碍的信息。"}, {"name": "其他疾病", "detail": "尽管大多数RPGRgene突变会导致X连锁性色素性视网膜炎，但在患有其他视网膜疾病的人中发现了ORF15外显子的一些突变。这些包括视锥细胞营养不良，视锥细胞营养不良和萎缩性黄斑变性。这些视网膜疾病的特征是进行性视力异常，尽管它们的体征和症状与色素性视网膜炎不同。（换行）在色素性视网膜炎和影响身体其他部位的体征和症状的组合中，已经报道了几种另外的RPGRgene突变。除了进行性视力丧失外，受影响的人还患有慢性呼吸道和鼻窦感染，耳部反复感染（中耳炎）和听力下降。(换行)目前尚不清楚为什么RPGRgene突变会引起多种疾病。研究表明，某些突变可能会破坏纤毛在包括内耳和呼吸道在内的多种组织中的功能。这些组织中的纤毛功能异常可能是某些受影响个体听力下降和呼吸异常的基础。但是，研究人员仍在努力确定RPGRgene突变如何引起涉及视网膜和身体其他部位的特定异常。"}]}' WHERE id = 13234;
UPDATE genes SET GHR_metadata_ch = '{"gene": "RB1", "conditions": [{"name": "视网膜母细胞瘤", "detail": "在视网膜母细胞瘤患者中已鉴定出RB1gene的数百种突变，视网膜母细胞瘤是一种罕见的眼癌，通常会影响幼儿。这种癌症在视网膜中发展，视网膜是眼睛后部检测光线和颜色的特殊光敏组织。研究人员估计，所有视网膜母细胞瘤中有40%是生发性的，这意味着RB1突变发生在人体的所有细胞中，并且可以传递给下一代。其他60%是非生发性的，这意味着RB1突变仅在眼睛中发生，不能传递给下一代。（换行）在生发性视网膜母细胞瘤中，RB1突变存在于身体的所有细胞中。为了使视网膜母细胞瘤发展，RB1gene的另一个拷贝也必须突变或丢失。第二种突变通常发生在视网膜细胞的生命早期。具有两个RB1gene拷贝改变的细胞不产生功能性pRB，并且无法有效调节细胞分裂。结果，缺乏功能性pRB的视网膜细胞可以不受控制地分裂以形成癌性肿瘤。一些研究表明，其他遗传变化会影响视网膜母细胞瘤的发展；这些变化可能有助于解释不同人群肿瘤发展和生长的变化。(换行)在生发性视网膜母细胞瘤患者中，RB1突变会增加眼外其他几种癌症的风险。具体而言，这些人更有可能患上脑部松果体癌（松果体瘤），一种称为骨肉瘤的骨癌，一种软组织癌（如肌肉癌）和一种侵袭性皮肤癌（称为黑色素瘤）。(换行)非生发性视网膜母细胞瘤发生在家族中没有疾病史的人身上。受影响的个体出生时具有RB1gene的两个正常拷贝。然后，通常在儿童早期，视网膜细胞中gene的两个拷贝都获得突变或丢失。这些遗传变化阻止细胞产生任何功能性pRB。这种蛋白质的丧失使得视网膜细胞在没有控制或有序的情况下生长和分裂，导致癌性肿瘤的发展。"}, {"name": "膀胱癌", "detail": "一些gene突变是在一个人的一生中获得的，并且仅存在于某些细胞中。这些被称为体细胞突变的变化不是遗传的。在某些膀胱癌病例中，已经报道了关闭（灭活）RB1gene的体细胞突变。RB1中的突变被认为有助于膀胱癌的发展，这些遗传变化可能有助于预测肿瘤是否会迅速生长并扩散到其他组织。"}, {"name": "肺癌", "detail": "遗传学家庭参考提供有关肺癌的信息。"}, {"name": "其他癌症", "detail": "除膀胱癌外，RB1gene的体细胞突变还与许多其他类型的癌症有关。例如，在某些肺癌，乳腺癌，称为骨肉瘤的骨癌和称为黑素瘤的侵袭性皮肤癌病例中，已经报道了RB1gene的变化。在一些白血病中也发现了体细胞RB1突变，这些白血病是造血细胞的癌症。癌细胞中的体细胞RB1突变使pRB失活，因此它不再能有效调节细胞分裂。"}]}' WHERE id = 13244;
UPDATE genes SET GHR_metadata_ch = '{"gene": "LARGE1", "conditions": [{"name": "沃克-沃伯格综合症", "detail": "已发现LARGE1gene中至少有7个突变引起Walker-Warburg综合征。这种情况是一组称为先天性肌营养不良的疾病中最严重的形式。Walker-Warburg综合征会导致骨骼肌无力以及大脑和眼睛异常。由于这种情况引起的问题的严重性，受影响的个体通常无法存活到儿童早期。（换行）涉及Walker-Warburg综合征的LARGE1gene突变阻止了正常的糖基化-dystroglycan。结果是-dystroglycan不再能够有效地将细胞锚定在周围的蛋白质和其他分子上。没有功能-dystroglycan为了稳定肌肉纤维，当它们反复收缩并使用时放松时会受到损坏。受损的纤维会随着时间的推移而减弱和死亡，这会影响Walker-Warburg综合征患者骨骼肌的发育，结构和功能。(换行)有缺陷-dystroglycan还影响大脑早期发育过程中神经元的迁移。一些神经元不是在到达预定目的地时停止，而是通过大脑表面迁移到周围充满液体的空间。研究人员认为，神经元迁移的这个问题会导致大脑异常，称为鹅卵石脑畸形，其中表面大脑缺乏正常的褶皱和凹槽，而是显得颠簸和不规则。关于LARGE1gene突变对身体其他部位的影响知之甚少。"}, {"name": "其他疾病", "detail": "LARGE1gene的突变也与较不严重的先天性肌营养不良有关，称为1D型先天性肌营养不良（MDC1D）。这种情况会导致肌肉无力，脑部异常和智力残疾，但不会影响眼睛。与Walker-Warburg综合征（如上所述）一样，导致MDC1D的LARGE1gene突变阻止了正常的糖基化-dystroglycan。目前尚不清楚该gene的突变如何引起几种不同的肌肉营养不良。"}]}' WHERE id = 13247;
UPDATE genes SET GHR_metadata_ch = '{"gene": "BIN1", "conditions": [{"name": "中心核肌病", "detail": "已发现BIN1gene中的几个突变会引起中心核肌病，这种疾病的特征是骨骼肌肌病。这是由于蛋白质中单个氨基酸的变化，改变了T小管的形成以及防止了肌肉的收缩和松弛而引起的。"}]}' WHERE id = 13271;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ACVR1", "conditions": [{"name": "骨化性纤维增生症进展", "detail": "ACVR1gene中的几个突变与进行性骨化性纤维增生有关，这种疾病的特征是激活素受体的组成性激活，这是由于这些突变抑制了FKBP12的结合，并导致骨骼，软骨过度生长和关节融合。"}]}' WHERE id = 13293;
UPDATE genes SET GHR_metadata_ch = '{"gene": "RFX5", "conditions": [{"name": "裸淋巴细胞综合征II型", "detail": "已发现RFX5gene中至少有七个突变会引起称为II型裸淋巴细胞综合征（BLS II）的免疫系统疾病。BLS II是一种联合免疫缺陷病（CID），其中受影响的个体实际上没有针对外来入侵者的免疫保护。因此，患有BLS II的个体在呼吸道，胃肠道和泌尿道中持续感染，这可能危及生命。RFX5gene中的（换行）突变导致产生异常短的RFX5蛋白，该蛋白可能无法正常运行。这些变化损害RFX复合物与DNA的结合，这阻止了MHC II类蛋白的转录。因此，淋巴细胞在其表面缺乏任何MHC II类蛋白，并且身体难以除去细菌，病毒和真菌，导致BLS II特有的持续感染。"}]}' WHERE id = 13310;
UPDATE genes SET GHR_metadata_ch = '{"gene": "USH2A", "conditions": [{"name": "视网膜色素变性", "detail": "据报道，USH2Agene中有数十种突变会引起色素性视网膜炎，这是一种视力障碍，会导致视网膜的感光细胞逐渐恶化。USH2Agene突变是色素性视网膜炎常染色体隐性遗传形式的最常见原因，占所有病例的10%至15%。这种形式的疾病被描述为非综合症，这意味着它与遗传综合症（例如，下面描述的Usher综合症）的一部分与其他体征和症状无关。（换行）导致视网膜色素变性的USH2Agene突变改变了usherin蛋白中的单蛋白结构单元（氨基酸）。通过一种尚不为人所知的机制，这些遗传变化导致视网膜中称为光感受器的专门光受体细胞逐渐分解。这些细胞的丧失是这种情况下进行性视力丧失的特征。"}, {"name": "亚瑟综合症", "detail": "在II型Usher综合征患者中已鉴定出USH2Agene的400多个突变，其特征是听力损失和与色素性视网膜炎相关的视力丧失相结合。具体而言，USH2Agene突变会导致一种称为II a型Usher综合征（USH2A）的疾病，占II型Usher综合征所有病例的一半以上。（换行）这些突变中的几个改变了usherin蛋白中的单个氨基酸。这些突变通常导致蛋白质异常短版本的产生或阻止细胞产生任何功能性usherin。其他突变会在USH2Agene中插入或删除少量DNA，这可能会损害usherin的正常功能。研究人员尚未确定缺失或改变的usherin蛋白如何导致II a型Usher综合征的特征性听力障碍和视力丧失。(换行)目前尚不清楚为什么某些USH2Agene突变会导致II a型Usher综合征，而其他突变会导致色素性视网膜炎而无听力损失。"}]}' WHERE id = 13314;
UPDATE genes SET GHR_metadata_ch = '{"gene": "OTC", "conditions": [{"name": "鸟氨酸转氨甲酰酶缺乏症", "detail": "鸟氨酸转氨甲酰酶缺乏症患者中已鉴定出500多个OTCgene突变，这是一种遗传性疾病，可导致氨在血液中积聚。当蛋白质在体内分解时形成的氨，如果水平变得过高则是有毒的。神经系统对过量氨的影响特别敏感。（换行）导致鸟氨酸转氨甲酰酶缺乏的OTCgene突变导致鸟氨酸转氨甲酰酶短于正常或形状错误，或阻止产生任何酶。酶的形状影响其控制化学反应的能力。如果鸟氨酸转氨甲酰酶畸形或缺失，则无法发挥其在尿素循环中的作用。过量的氮不会转化为尿素排泄，氨会积聚在体内。氨的积累会导致神经系统问题以及鸟氨酸转氨甲酰酶缺乏症的其他体征和症状。"}]}' WHERE id = 13321;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SALL4", "conditions": [{"name": "杜安放射线综合征", "detail": "在患有Duane radial射线综合征（也称为Okihiro综合征）或非常相似的称为顶肾眼综合征的人中，已经鉴定出SALL4gene的25个以上突变。研究人员怀疑Duane radial射线综合征和顶肾眼综合征是一组重叠的综合征的一部分，具有许多可能的体征和症状。（换行）大多数SALL4gene突变在制备SALL4蛋白的说明中产生过早的停止信号。结果，细胞不从该gene的一个拷贝产生任何功能性蛋白质。研究人员正在研究减少SALL4蛋白的量如何破坏Duane radial射线综合征和顶肾眼综合征患者的眼睛，心脏和四肢发育。"}, {"name": "其他疾病", "detail": "已发现SALL4gene中至少有一个突变会引起IVIC综合征，该病的体征和症状与Duane radial射线综合征和顶肾眼综合征的体征和症状重叠。首字母缩略词IVIC代表Venezolano deInvestigacionesCientìficas研究所，该研究所是首次描述病情的中心。IVIC综合征的主要特征包括手臂和手部骨骼异常形成，听力下降以及由眼睛周围的肌肉（眼外肌）异常引起的眼球运动问题。（换行）负责IVIC综合征的SALL4gene突变在制备SALL4蛋白的说明书中产生过早的停止信号。结果，细胞从该gene的一个拷贝产生异常短的蛋白质形式。目前尚不清楚这种缩短的蛋白质是否完全无功能，或者是否保留了其某些作为转录因子的功能。研究人员正在努力确定这种SALL4gene突变如何破坏早期发育并导致IVIC综合征的特征。"}]}' WHERE id = 13365;
UPDATE genes SET GHR_metadata_ch = '{"gene": "LPAR6", "conditions": [{"name": "常染色体隐性遗传不足", "detail": "已发现30多个LPAR6gene突变会导致常染色体隐性遗传不足，这种情况会导致头皮以及身体其他部位的头发稀疏生长（发育不足）。一些突变特定于巴基斯坦血统的人群。LPAR6gene突变导致产生异常的LPA6蛋白，该蛋白不能与LPA结合以调节毛囊内的细胞增殖和分化。结果，毛囊在结构上异常并且通常不发达。不规则的毛囊会改变毛干的结构和生长，导致脆弱的头发容易断裂。表皮中LPA6蛋白功能的缺乏可能导致皮肤问题，有时在常染色体隐性遗传不足的个体中可见。"}, {"name": "其他疾病", "detail": "LPAR6gene的突变也可引起称为常染色体隐性毛发的毛发状况。患有这种疾病的人的头发异常粗糙，干燥，细密，卷曲。毛发通常仅影响头皮，并且从出生时就存在。在某些情况下，受影响的人会随着年龄的增长而发展为低毛病。某些LPAR6gene突变会在某些人中引起常染色体隐性毛发，在另一些人中甚至在同一家庭成员中引起常染色体隐性毛发不足（如上所述）。由于共同的遗传原因和重叠特征，不确定这两种情况是单独的疾病还是同一疾病谱的一部分。"}]}' WHERE id = 13395;
UPDATE genes SET GHR_metadata_ch = '{"gene": "PRPH2", "conditions": [{"name": "玻璃状黄斑营养不良", "detail": "PRPH2gene的突变是导致某些成人发作的玻璃状黄斑营养不良的原因。在患有这种疾病的人中已经鉴定出几种突变，其中大多数改变了外周蛋白2中的单个蛋白质构建块（氨基酸）。这些突变会改变蛋白质的结构或导致异常短而无功能的蛋白质产生。当外周蛋白2改变或缺失时，光感受器随时间分解（退化）。这种光感受器的丧失是玻璃状黄斑营养不良的视网膜异常和进行性视力丧失的特征。目前尚不清楚为什么PRPH2突变仅影响患有这种疾病的人的中央视力。"}, {"name": "视锥-视杆营养不良", "detail": "遗传学家庭参考提供有关锥杆营养不良的信息。"}, {"name": "视网膜色素变性", "detail": "遗传学家庭参考提供有关视网膜色素变性的信息。"}, {"name": "其他视网膜营养不良", "detail": "PRPH2gene的突变会导致多种其他视网膜疾病。这些情况中的每一种都涉及感光细胞的缓慢变性，导致进行性视力丧失。在PRPH2gene中总共鉴定出100多个突变。这些突变中的许多会导致常染色体显性遗传性视网膜色素变性，这是一种眼病，首先破坏夜视和侧（周围）视力，最终可能导致失明。PRPH2突变还引起一组视网膜疾病，称为视网膜色素上皮的模式营养不良。这些疾病通常始于成年中期，其特征是视网膜下细胞中色素异常积聚。(换行)某些PRPH2突变可在同一家庭的受影响成员中引起不同的眼部疾病。例如，研究人员报告了在具有相同PRPH2突变的不同个体中患有色素性视网膜炎，视网膜色素上皮模式营养不良和点状视网膜炎（一种类似于色素性视网膜炎的眼部疾病）的家庭。目前尚不清楚为什么该gene的突变会导致如此广泛的视网膜异常。"}]}' WHERE id = 13399;
UPDATE genes SET GHR_metadata_ch = '{"gene": "RDH5", "conditions": [{"name": "白底眼底", "detail": "已发现RDH5gene中至少有48个突变会引起白底眼底，这种疾病的特征是夜视能力受损和视网膜上出现白黄色斑点。RDH5gene突变被认为会降低或消除11-顺式视黄醇脱氢酶5酶的功能，从而导致缺乏11-顺式视黄醛。如果光感受器中没有这个重要的分子，视觉必不可少的电信号就不会受到刺激，视力也会受到损害。由于视杆依赖11-顺式视黄醇脱氢酶5来产生11-顺式视网膜，因此弱光下的视力特别受该酶功能受损的影响。研究人员推测，11-顺式视黄醇脱氢酶5的损伤也会导致11-顺式视黄醇和相关分子的积累，形成白底眼底的斑点特征。"}]}' WHERE id = 13403;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ERCC2", "conditions": [{"name": "毛滴虫营养不良", "detail": "已发现ERCC2gene中至少有20个突变引起毛发硫代营养不良。该gene中的突变是该病症的光敏形式的最常见原因，其特征在于对来自太阳光的UV射线的极端敏感性。（换行）研究表明，负责毛发硫代营养不良的ERCC2gene突变减少了细胞中功能性TFIIH复合物的数量。没有足够的这种复合物，细胞就无法有效修复紫外线辐射引起的DNA损伤。DNA修复中的这些问题会导致患有光敏性毛发硫代营养不良症的人对阳光极为敏感。该病的其他特征，例如生长缓慢，智力残疾和头发脆弱，可能是由于出生前后正常发育所需gene转录的问题所致。(换行)与色素干皮症（如下所述）不同，毛发硫代营养不良与皮肤癌风险增加无关。研究人员正在努力确定为什么ERCC2gene中的某些突变会影响一个人的癌症风险，而另一些则不会。"}, {"name": "着色性干皮症", "detail": "在色素干皮症患者中已鉴定出ERCC2gene的二十多个突变。该gene的突变是美国干性皮肤色素变性的第二大常见原因。（换行）负责着色性干皮病的ERCC2gene突变阻止TFIIH复合物有效修复受损的DNA。结果，异常在DNA中积累，导致细胞发生故障并最终癌变或死亡。DNA修复中的这些问题会导致色素干皮症患者对阳光下的紫外线极为敏感。当紫外线破坏控制细胞生长和分裂的gene时，细胞会生长得太快，并且无法控制。结果，患有色素干皮症的人患癌症的风险大大增加。这些癌症最常发生在暴露于阳光下的身体部位，如皮肤和眼睛。(换行)当ERCC2gene突变引起色素干皮症时，通常与进行性神经系统异常有关。这些神经系统问题包括听力丧失，协调性差，行走困难，运动问题，智力功能丧失，吞咽和说话困难以及癫痫发作。尽管大脑没有暴露在紫外线下，但神经系统异常被认为是DNA损伤累积的结果。研究人员怀疑其他因素会破坏神经细胞中的DNA。目前还不清楚为什么有些色素干皮症患者会出现神经系统异常，而另一些则不会。"}, {"name": "其他疾病", "detail": "很少，ERCC2gene的突变会在同一个人中引起色素干皮症和硫代营养不良症的特征。这种情况称为色素干皮症/硫代营养不良症（XP/TTD）复合体。ERCC2gene突变也已在少数具有色素干皮症和另一种与DNA修复缺陷有关的疾病（称为Cockayne综合征）的体征和症状的个体中发现。这些功能的组合称为色素干皮症/Cockayne综合征（XP/CS）复合体。(换行)研究人员不确定该单个gene的突变如何导致具有多种体征和症状的几种不同疾病。研究表明，不同的ERCC2gene突变以不同的方式影响TFIIH复合物的稳定性和功能。突变对XPD蛋白与构成TFIIH复合物的其他蛋白之间的相互作用也有不同的影响。这些变化可能解释了色素干皮症，毛发硫代营养不良以及XP/TTD和XP/CS复合物的不同特征。"}]}' WHERE id = 13407;
UPDATE genes SET GHR_metadata_ch = '{"gene": "BCOR", "conditions": [{"name": "Lenz小眼症候群", "detail": "在一个患有Lenz小眼症候群的家庭中发现了BCORgene的突变。这种突变会改变BCL6核心加压因子中的单个蛋白质结构单元（氨基酸），从而改变蛋白质的结构。具体而言，该突变用位置85处的氨基酸亮氨酸取代了氨基酸脯氨酸（写为Pro85Leu）。由于BCL6核心加压因子在早期发育中起着至关重要的作用，因此该蛋白的缺陷形式会破坏出生前眼睛以及其他几个器官和组织的正常形成。（换行）BCORgene位于X染色体（两个性染色体之一）上，Lenz小眼症的遗传被描述为X连锁隐性遗传。X连锁隐性疾病在男性中比在女性中更常见。Lenz小眼症候群仅在男性中发现。"}, {"name": "眼面部心电综合征", "detail": "BCORgene中的突变也可引起眼面心（OFCD）综合征。这些突变中的一些从BCORgene中删除了大量的遗传物质，而其他突变改变了gene的指示，使得不能产生BCL6共抑制蛋白。这种蛋白质的丢失会破坏出生前眼睛和其他几个器官和组织的正常发育。(换行)与仅在男性中发现的Lenz小眼症候群不同，OFCD综合征仅在女性中发生。OFCD综合征具有X连锁显性遗传模式，这意味着每个细胞中BCORgene的一个改变的拷贝足以引起该病。OFCD综合征基础的遗传变化阻止了任何BCL6核心加压蛋白的产生。在每个细胞中只有一个X染色体的男性中，这些突变导致BCL6 corepressor完全丧失。缺乏这种蛋白质似乎在发育的早期就具有致命性，因此没有男性出生时患有OFCD综合征。在每个细胞中有两个X染色体的雌性中，某些细胞产生正常量的BCL6核心加压蛋白，而其他细胞则不产生。这种蛋白质的总体减少导致OFCD综合征的体征和症状。"}, {"name": "小眼症", "detail": "遗传学家庭参考提供有关小眼症的信息。"}]}' WHERE id = 13426;
UPDATE genes SET GHR_metadata_ch = '{"gene": "VCP", "conditions": [{"name": "包涵体肌病伴早发性Paget病和额颞叶痴呆", "detail": "在患有早发性Paget病和额颞痴呆（IBMPFD）的包涵体肌病患者中，已鉴定出VCPgene至少有10个突变。这些突变各自改变了含valosin的蛋白质中的单个蛋白质结构单元（氨基酸）。该酶结构的变化破坏了其作为泛素-蛋白酶体系统的一部分分解其他蛋白质的能力。结果，过量和异常的蛋白质可能在肌肉，骨骼和脑细胞中积聚。蛋白质形成团块（聚集体），干扰这些细胞的正常功能。尚不清楚对肌肉，骨骼和脑细胞的损害如何导致IBMPFD的特定特征。"}, {"name": "肌萎缩侧索硬化症", "detail": "遗传学家庭参考提供有关肌萎缩侧索硬化的信息。"}]}' WHERE id = 13449;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SELENON", "conditions": [{"name": "多微核病", "detail": "在患有经典形式的多微核疾病的人中，已经鉴定出硒离子gene中至少有17个突变。这些遗传变化中的许多导致硒蛋白N异常短版本的产生。其他突变会改变蛋白质关键区域中的单个蛋白质结构单元（氨基酸）。硒蛋白N的结构和功能变化的影响尚不清楚，研究人员正在努力确定这些变化如何导致肌肉无力和多微核疾病的其他特征。"}, {"name": "其他疾病", "detail": "SELENONgene的突变还导致其他几种罕见的肌肉疾病，包括刚性脊柱肌营养不良和结蛋白相关的肌病，并伴有马洛里体样内含物。这些情况会导致肌肉无力，特别是在躯干和颈部的肌肉中。受影响的个体还具有肌张力降低（肌张力低下），脊柱弯曲异常（脊柱侧弯）和严重的呼吸问题。由于它们具有相似的体征和症状模式，并且是由同一gene的突变引起的，因此许多研究人员认为这些情况都是具有可变体征和症状的单一综合征的一部分。在一起，由硒gene突变引起的肌肉疾病被称为硒相关（或SEPN1相关）肌病。"}]}' WHERE id = 13454;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SCARB2", "conditions": [{"name": "行动肌阵挛？肾功能衰竭综合征", "detail": "SCARB2gene中至少有20个突变与动作性肌阵挛有关？肾功能衰竭（AMRF）综合征。这种罕见的情况会导致不自主的肌肉抽搐或抽搐发作，尤其是在试图进行有意的运动（动作性肌阵挛）时。AMRF综合征的另一个常见特征是肾脏（肾脏）疾病；尽管在conditionsname中被引用，但是每个患有该病症的人的肾功能都不受影响。（换行）与AMRF综合征相关的SCARB2gene突变导致产生改变的LIMP-2蛋白，其粘附在内质网中并且不能到达溶酶体。结果，β-葡糖脑苷脂酶向溶酶体的运动受到损害。尽管尚不清楚其机制，但认为这些结构中β-葡萄糖脑苷脂酶的缺乏会导致AMRF综合征的体征和症状。研究人员正在努力了解为什么一些具有SCARB2gene突变的人有肾脏问题而另一些人则没有。"}]}' WHERE id = 13474;
UPDATE genes SET GHR_metadata_ch = '{"gene": "NKX2-1", "conditions": [{"name": "脑-肺-甲状腺综合征", "detail": "已发现NKX2-1gene中至少有100个突变会引起脑-肺-甲状腺综合征，其中包括一组影响脑，肺和甲状腺的疾病。大约一半的受影响个体在所有三个器官上都有问题，而其他人在其中一个或两个器官上有问题。该综合征最常见的特征是良性遗传性舞蹈病，涉及面部，躯干和四肢的非自愿抽搐运动（舞蹈病）以及其他不受控制的运动；严重呼吸困难（呼吸窘迫综合征）；并减少甲状腺功能（甲状腺功能减退症）。（换行）涉及脑-肺-甲状腺综合征的许多NKX2-1gene突变导致异常短的同源框蛋白Nkx-2.1不能正常运作。其他突变会改变蛋白质中的单个蛋白质结构单元（氨基酸），从而削弱其附着于DNA的能力。还有一些删除了整个NKX2-1gene。功能性同源盒蛋白Nkx-2.1的缺乏改变了对脑，肺和甲状腺的正常发育和功能重要的gene的表达。表面活性剂蛋白的产生减少，导致呼吸困难；参与甲状腺激素产生的gene表达受损，导致甲状腺功能减退；大脑发育受损，可能是由于中间神经元形成或迁移不当所致，这可能是脑-肺-甲状腺综合征特征性运动异常的基础。"}]}' WHERE id = 13481;
UPDATE genes SET GHR_metadata_ch = '{"gene": "DHCR24", "conditions": [{"name": "去胆固醇", "detail": "已发现DHCR24gene中至少有七个突变引起去胆固醇过多症。去胆固醇过多症是一种以神经系统问题为特征的疾病，例如脑部异常和发育迟缓，还可能包括其他体征和症状。导致这种情况的突变改变了24-脱氢胆固醇还原酶中的单个蛋白质结构单元（氨基酸）。结果，酶活性降低，胆固醇产量降低。由于大脑仅依靠细胞产生胆固醇，因此受到的影响最大。没有足够的胆固醇，细胞膜不能正常形成，神经细胞不受髓磷脂保护，导致这些细胞死亡。另外，由于发生的细胞数量迅速增加，胆固醇产生的减少在出生前比在其他发育时期具有更严重的影响。出生前正常细胞形成的破坏可能是去胆固醇过多症的其他发育异常的原因。"}]}' WHERE id = 13483;
UPDATE genes SET GHR_metadata_ch = '{"gene": "CBS", "conditions": [{"name": "同型半胱氨酸尿症", "detail": "CBSgene的一些突变与同型半胱氨酸尿症有关，同型半胱氨酸尿症是一种拥有属性近视、眼前晶状体脱位、凝血异常风险增加、易患骨质疏松症的脆性骨骼、其他骨骼异常、发育迟缓和学习问题。这是由于胱硫醚β合酶功能丧失导致血液中有毒同型半胱氨酸积累所致。"}]}' WHERE id = 13484;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SRCAP", "conditions": [{"name": "浮港综合症", "detail": "在Floating Harbor综合征患者中至少鉴定出5个SRCAPgene突变，该疾病涉及身材矮小，骨骼矿化减慢（骨龄延迟），言语发育延迟和特征性面部特征。导致浮港综合征的SRCAPgene突变可能导致蛋白质改变，从而干扰CREBBPgene的正常激活，从而导致发育问题。但是，SRCAPgene突变与浮港综合征的具体体征和症状之间的关系尚不清楚。"}]}' WHERE id = 13491;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ALS2", "conditions": [{"name": "肌萎缩侧索硬化症", "detail": "已发现ALS2gene突变会导致一种称为肌萎缩性侧索硬化症（ALS）的进行性疾病，该疾病的特征是萎缩，肌肉抽搐，抽筋，僵硬或无力，肌肉质量下降以及无法控制运动，吞咽困难和构音障碍"}, {"name": "婴儿发作性遗传性痉挛性麻痹", "detail": "ALS2gene的一些突变已被发现引起婴儿起病的上行性遗传性痉挛性麻痹，这种疾病是由无功能或异常的alsin蛋白引起的，拥有属性进行性无力，腿部、手臂、颈部和头部肌肉僵硬，从出生后的头两年开始。"}, {"name": "青少年原发性侧索硬化症", "detail": "已发现ALS2gene突变会导致青少年原发性侧索硬化症，这种疾病的特征是手臂，腿部和面部肌肉的进行性无力和僵硬，通常始于儿童期，原因是alsin蛋白的丢失或缺陷。"}]}' WHERE id = 13503;
UPDATE genes SET GHR_metadata_ch = '{"gene": "TSHR", "conditions": [{"name": "先天性甲状腺功能减退症", "detail": "在先天性甲状腺功能减退症患者中已鉴定出几种TSHRgene突变，这种疾病的特征是从出生开始甲状腺激素水平异常低。TSHRgene突变改变了用于制造促甲状腺激素受体的蛋白质结构单元（氨基酸）之一。这些突变中的一些阻止受体正确地跨过膜，并且在某些情况下，整个受体保留在细胞内。结果，受体不能与促甲状腺激素正常相互作用。即使受体正确跨过膜，其他突变也会损害受体与促甲状腺激素结合的能力。(换行)没有正常功能的受体，甲状腺激素的产生不会受到刺激。身体试图通过产生更多的促甲状腺激素来纠正阻塞的刺激。在某些情况下，促甲状腺激素水平的升高可以补偿具有轻微缺陷的受体，并且甲状腺功能正常。在其他情况下，甲状腺激素水平仍然很低，导致轻度至重度先天性甲状腺功能减退症。受损的促甲状腺激素受体也可能破坏甲状腺发育，结果腺体比正常小。由TSHRgene突变引起的先天性甲状腺功能减退症有时被归类为甲状腺发育不全，因为甲状腺的发育受到损害。"}, {"name": "格雷夫斯病", "detail": "遗传学家庭参考提供有关格雷夫斯病的信息。"}, {"name": "肿瘤", "detail": "Sometimes gene mutations are acquired during a person''s lifetime and are present only in certain cells. This type of mutation is called somatic, and it is not inherited. Somatic mutations in the TSHR gene have been identified in thyroid tumors. These mutations are found only in the tumor cells. (line-break) Somatic TSHR gene mutations have been reported in many cases of noncancerous (benign) thyroid tumors, called nodules or adenomas, which are associated with an overactive thyroid (hyperthyroidism). Somatic mutations have also been identified in some cancerous (malignant) thyroid tumors known as thyroid carcinomas. As a result of these somatic mutations, the thyroid stimulating hormone receptor is continuously activated, which could prompt the overgrowth of thyroid cells.\r"}, {"name": "其他疾病", "detail": "TSHR gene mutations can cause disorders associated with hyperthyroidism. These mutations change one of the amino acids used to make the thyroid stimulating hormone receptor. As a result, the receptor is continuously activated and overstimulates the production of thyroid hormones. TSHR gene mutations can lead to an enlarged thyroid gland (goiter) and symptoms of hyperthyroidism, such as a rapid heartbeat. Hyperthyroidism that is present from birth is called nonautoimmune congenital hyperthyroidism (or sporadic toxic thyroid hyperplasia). Onset of hyperthyroidism that begins in childhood or adulthood is known as nonautoimmune autosomal dominant hyperthyroidism (or hereditary toxic thyroid hyperplasia).\r"}]}' WHERE id = 13514;
UPDATE genes SET GHR_metadata_ch = '{"gene": "C19orf12", "conditions": [{"name": "线粒体膜蛋白相关的神经变性", "detail": "已发现C19orf12gene中至少有28个突变会引起一种称为线粒体膜蛋白相关神经变性（MPAN）的疾病，其特征是运动和神经系统问题逐渐恶化。受影响的个体在大脑的某些区域也有异常的铁蓄积。导致这种情况的gene突变会改变C19orf12蛋白中的单个蛋白结构单元（氨基酸）或导致异常短的蛋白。这些变化可能会降低或消除蛋白质的功能。在患有这种疾病的波兰血统的人中发现了一个C19orf12gene突变，该突变从该gene中删除了11个DNA结构单元（核苷酸）。这种遗传变化导致异常短的蛋白质的产生，其迅速被分解。目前尚不清楚C19orf12蛋白功能的丧失如何导致MPAN的体征和症状。研究人员正在努力确定脂质稳态问题与脑铁蓄积之间是否存在联系，或者这些异常可能如何导致这种疾病的特征。（换行）C19orf12gene突变可引起一系列相关疾病，其中包括MPAN的一些但不是全部特征。例如，一些受影响的人有运动问题，例如肌肉僵硬（痉挛），但大脑中没有铁蓄积；这些人被认为患有遗传性痉挛性截瘫43型。"}]}' WHERE id = 13519;
UPDATE genes SET GHR_metadata_ch = '{"gene": "BSND", "conditions": [{"name": "巴特综合症", "detail": "在患有IV型Bartter综合征的人中已经鉴定出BSNDgene的几种突变，这种疾病会导致严重的威胁生命的问题，这些问题在出生前或出生后不久就变得明显。受影响的个体表现出临床特征，例如内耳异常引起的听力下降，failure壮成长，脱水，便秘和多尿。这是由于BSNDgene突变影响barttin调节ClC-Ka和ClC-Kb通道的能力所致。"}, {"name": "非综合征性听力损失", "detail": "BSNDgene中的几个突变与非综合征性听力损失，部分或全部听力损失的异常有关，而与其他体征和症状无关。非综合征性听力损失有几种形式，由于内耳结构受损导致永久性听力损失，大多数形式被描述为感觉神经性。"}]}' WHERE id = 13520;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SPRED1", "conditions": [{"name": "Legius综合征", "detail": "SPRED1gene的突变会导致Legius综合征，该病的特征是多个咖啡色斑点，这些斑点是皮肤上平坦的斑块，比周围区域暗。（换行）在整个SPRED1gene中已鉴定出几种突变。这些突变导致无功能的Spred-1蛋白，通常是因为突变的gene为缩短的蛋白提供了指示。无功能的Spred-1蛋白无法结合并阻断Raf蛋白的激活，这意味着Ras/MAPK途径持续活跃。目前尚不清楚SPRED1gene的突变如何引起Legius综合征的体征和症状。"}]}' WHERE id = 13532;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SMC1A", "conditions": [{"name": "Cornelia de Lange综合征", "detail": "在患有Cornelia de Lange综合征（一种影响身体许多部位的发育障碍）的人中，已经鉴定出SMC1Agene的35个以上突变。研究人员估计，该gene的突变约占该病所有病例的5%。（换行）导致Cornelia de Lange综合征的大多数SMC1Agene突变会改变SMC1蛋白中的单个蛋白结构单元（氨基酸）。这些突变改变了蛋白质的结构和功能，这可能会干扰粘着蛋白复合物的活性，并削弱其调节对正常发育至关重要的gene的能力。尽管研究人员尚不完全了解这些变化如何引起Cornelia de Lange综合征，但他们怀疑gene调控的改变可能是该病特征性的许多发育问题的基础。(换行)研究表明，SMC1Agene中的突变倾向于导致某种形式的Cornelia de Lange综合征，其特征相对较轻。与最常见的疾病原因NIPBLgene突变相比，SMC1Agene突变通常不会引起发育和生长的显着延迟，也不太可能引起重大的先天缺陷。"}]}' WHERE id = 13536;
UPDATE genes SET GHR_metadata_ch = '{"gene": "PCSK9", "conditions": [{"name": "高胆固醇血症", "detail": "研究人员已经鉴定出几种PCSK9突变，这些突变会导致遗传形式的高胆固醇（高胆固醇血症）。这些突变改变了PCSK9蛋白中的单个蛋白结构单元（氨基酸）。研究人员将导致高胆固醇血症的突变描述为“功能获得”，因为它们似乎增强了PCSK9蛋白的活性或为该蛋白提供了新的非典型功能。(换行)过度活跃的PCSK9蛋白显着减少肝细胞表面低密度脂蛋白受体的数量。研究人员推测，改变后的蛋白质可能会导致这些受体比平时更快地分解。由于从血液中去除低密度脂蛋白的受体较少，因此PCSK9gene功能获得性突变的人的血液胆固醇水平非常高。当过量的胆固醇通过血液循环时，它会异常沉积在向心脏（冠状动脉）供血的皮肤，肌腱和动脉等组织中。冠状动脉壁中胆固醇的积累大大增加了心脏病发作的风险。"}, {"name": "家族性低脂蛋白血症", "detail": "遗传学家庭参考资料提供有关家族性低脂蛋白血症的信息。"}, {"name": "其他疾病", "detail": "PCSK9gene的其他突变导致血液胆固醇水平降低（低胆固醇血症）。这些遗传变化降低了PCSK9蛋白的活性或减少了细胞中产生的该蛋白的量。研究人员将这种类型的突变描述为“功能丧失”。PCSK9gene的功能丧失突变似乎比导致高胆固醇血症的功能获得突变更为普遍。（换行）PCSK9gene中的功能丧失突变导致肝细胞表面上低密度脂蛋白受体的数量增加。额外的受体可以比平时更快地从血液中去除低密度脂蛋白，这减少了血液中循环的胆固醇量。研究表明，由PCSK9突变引起的胆固醇水平降低的人患心脏病的风险明显低于平均水平。(换行)研究人员怀疑PCSK9gene的正常变化（多态性）是造成没有遗传性胆固醇疾病的人血液胆固醇水平变化的原因。特别是，科学家正在努力确定哪些多态性与血液中胆固醇水平相对较低和心脏病风险降低有关。"}]}' WHERE id = 13542;
UPDATE genes SET GHR_metadata_ch = '{"gene": "FANCG", "conditions": [{"name": "范可尼贫血", "detail": "已发现FANCGgene中的50多个突变会引起范可尼贫血，这是一种以骨髓功能下降，癌症风险增加和身体异常为特征的疾病。在所有范可尼贫血病例中，约有10%是由FANCGgene突变引起的。当范可尼贫血是由该gene的突变引起的时，与其他gene的突变引起的疾病相比，它通常与更严重的血细胞短缺有关。（换行）导致Fanconi贫血的FANCGgene中的大多数突变导致蛋白质功能缺失或降低。具有导致不产生蛋白质的突变的个体通常比具有允许某些FANCG蛋白质产生的突变的个体具有更严重的体征或症状。由于FANCG蛋白功能降低，FA核心复合物无法发挥功能，整个FA途径被破坏。结果，DNA损伤无法有效修复，ICL会随着时间的推移而累积。ICL阻止DNA复制，最终导致由于不能产生新的DNA分子而导致的异常细胞死亡或由于缺乏DNA修复过程而导致的不受控制的细胞生长。快速分裂的细胞，例如骨髓细胞和发育中的胎儿细胞，尤其受到影响。这些细胞的死亡导致血细胞减少和范可尼贫血的身体异常。当DNA中的错误累积导致不受控制的细胞生长时，受影响的个体可能会发展为白血病或其他癌症。"}]}' WHERE id = 13560;
UPDATE genes SET GHR_metadata_ch = '{"gene": "TBX5", "conditions": [{"name": "Holt-Oram综合征", "detail": "已发现TBX5gene中的70多个突变引起Holt-Oram综合征。这些突变大多数阻止了T-box 5蛋白的产生。其他突变改变了用于制造T-box 5蛋白的蛋白结构单元（氨基酸）之一。研究人员认为，氨基酸的变化会损害蛋白质与DNA结合的能力。由于TBX5突变，对心脏和上肢发育重要的gene可能未被激活。心脏和上肢的异常发育是Holt-Oram综合征的特征。"}]}' WHERE id = 13620;
UPDATE genes SET GHR_metadata_ch = '{"gene": "TGFB2", "conditions": [{"name": "Loeys-Dietz综合征", "detail": "已发现TGFB2gene中至少有20个突变引起IV型Loeys-Dietz综合征。该疾病影响结缔组织，结缔组织为血管，骨骼和身体的许多其他部位提供结构和支持。IV型Loeys-Dietz综合征的特征是血管异常，心脏缺陷和骨骼畸形。导致这种情况的TGFB2gene突变导致产生TGFβ-2种功能很少或没有的蛋白质。结果，蛋白质不能与其受体结合。虽然TGF-2蛋白及其受体不结合，TGF-？通路信号的发生强度甚至比正常情况更大。研究人员推测，这种信号通路中其他蛋白质的活性增加，以弥补TGFβ的减少-2活动；然而，导致信号增加的确切机制尚不清楚。过度活跃的信号通路破坏结缔组织和各种身体系统的发育，并导致IV型Loeys-Dietz综合征的体征和症状。（换行）已发现一些突变，可删除整个TGFB2gene和附近的一些遗传物质。具有这些缺失的人通常具有Loeys-Dietz综合征的特征，以及通常与疾病无关的特征，例如智力障碍和运动问题。研究人员正在努力确定哪些gene由于这些缺失而缺失，以及它们的缺失如何导致这些额外的体征和症状。"}]}' WHERE id = 13621;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SUCLG1", "conditions": [{"name": "琥珀酸辅酶A连接酶缺乏症", "detail": "在琥珀酸CoA连接酶缺乏症患者中，已经鉴定出SUCLG1gene中至少有两个突变。已经发现一种突变引起非常严重的疾病形式，称为致命的婴儿乳酸性酸中毒。患有这种疾病的儿童通常只在出生后几天生活。导致致命性婴儿乳酸性酸中毒的突变从SUCLG1gene中删除了少量遗传物质，从而完全消除了两种形式的琥珀酸CoA连接酶的活性。（换行）至少一种其他SUCLG1gene突变导致琥珀酸CoA连接酶缺乏症的严重程度有所降低，其特征是肌张力非常弱（严重的肌张力低下），运动不受控制，听力下降和发育迟缓。该突变改变了琥珀酸-CoA连接酶中的单个蛋白质结构单元（氨基酸），其降低但不消除a-SUCL和G-SUCL的活性。（换行）琥珀酸CoA连接酶的缺乏（缺乏）导致线粒体DNA的产生和维持问题。线粒体DNA数量的减少（称为线粒体DNA耗竭）会损害人体许多细胞和组织中的线粒体功能。琥珀酸CoA连接酶活性的完全丧失似乎比酶活性的部分丧失具有更严重的影响。"}, {"name": "利综合征", "detail": "遗传学家庭参考提供有关Leigh综合征的信息。"}]}' WHERE id = 13622;
UPDATE genes SET GHR_metadata_ch = '{"gene": "BCKDHB", "conditions": [{"name": "枫糖浆尿病", "detail": "在枫糖浆尿病患者中已经发现了BCKDHBgene的几个突变，这是一种严重的典型疾病，表现出明显的婴儿尿液甜味，拥有属性喂养不良，呕吐，缺乏能量（嗜睡），运动异常，发育迟缓。这种情况是由于BCKD酶复合物的β亚基中的单个氨基酸变化引起的，该变化破坏了支链氨基酸在细胞和组织中的分解和毒性积累。"}]}' WHERE id = 13627;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SLC25A4", "conditions": [{"name": "进行性眼外肌麻痹", "detail": "据报道，SLC25A4gene中至少有五个突变会引起称为进行性眼外肌麻痹的眼病。这种疾病削弱了控制眼球运动并导致眼睑下垂（上睑下垂）的肌肉。当由SLC25A4gene突变引起时，进行性外眼肌麻痹以常染色体显性遗传，这意味着每个细胞中该gene的一个拷贝都发生了突变。这些突变会损害ADP和ATP进出线粒体的运动；但是，尚不清楚这些变化在疾病原因中起什么作用。（换行）线粒体各自含有少量DNA，称为线粒体DNA（mtDNA），这对于这些结构的正常功能是必需的。尽管机制尚不清楚，但SLC25A4gene的突变导致肌肉组织中mtDNA的遗传物质大量缺失。尽管该病的特征可能与氧化磷酸化受损有关，但研究人员尚未确定mtDNA的缺失如何导致进行性眼外肌麻痹的特定体征和症状。已经提出，眼肌通常受到线粒体缺陷的影响，因为它们特别依赖于氧化磷酸化作为能量。"}, {"name": "其他疾病", "detail": "SLC25A4gene的突变也可能导致以肌病和肥厚型心肌病为特征的疾病。肌病是用于运动的肌肉的无力，心肌病是心肌的增厚，迫使心脏更努力地抽血。这种疾病以常染色体隐性遗传，这意味着每个细胞中SLC25A4gene的两个拷贝都发生了突变。与这种疾病相关的突变阻止了ANT1通道的产生。与进行性眼外肌麻痹（如上所述）一样，突变导致mtDNA缺失，尽管其机制尚不清楚。研究人员不确定mtDNA的缺失如何导致肌病和肥厚型心肌病。"}]}' WHERE id = 13641;
UPDATE genes SET GHR_metadata_ch = '{"gene": "PIGO", "conditions": [{"name": "马布里综合征", "detail": "已发现PIGOgene中至少有3个突变会引起Mabry综合征，这是一种以智力残疾，独特的面部特征，血液中称为碱性磷酸酶的酶水平升高（高磷血症）以及其他体征和症状为特征的疾病。这些突变改变了GPI-ET3酶中的单个蛋白质结构单元（氨基酸）。改变后的蛋白质无法将乙醇胺磷酸酯添加到GPI锚的末端。不完整的GPI锚不能附着在蛋白质上；没有锚，蛋白质不能与细胞膜结合并从细胞中释放出来。（换行）称为碱性磷酸酶的酶通常通过GPI锚附着于细胞膜。但是，当锚受损时，碱性磷酸酶会从细胞中释放出来。碱性磷酸酶的这种异常释放是造成马布里综合征高磷血症的原因。目前尚不清楚PIGOgene突变如何导致Mabry综合征的其他特征，但这些体征和症状可能是由于缺乏将蛋白质正确的GPI锚定在细胞膜上所致。"}]}' WHERE id = 13656;
UPDATE genes SET GHR_metadata_ch = '{"gene": "PHKG2", "conditions": [{"name": "IX型糖原贮积病", "detail": "已知PHKG2gene中至少有30个突变会导致某种形式的糖原贮积病IX型（GSD IX），称为GSD IXc。这种形式的疾病会影响肝功能，并在受影响的个体中引起肝脏肿大（肝肿大），生长缓慢和低血糖期（低血糖）。这些功能通常会随着时间而改善。然而，PHKG2gene突变与更严重的体征和症状相关，例如不可逆的肝病（肝硬化）。PHKG2gene中的（换行）突变降低了肝细胞中磷酸化酶b激酶的活性，尽管其机制尚不清楚。这种酶功能的降低会损害糖原分解。结果，糖原在细胞中积累，并且葡萄糖不可用于能量。无法分解肝脏中的糖原会导致GSD IXc的特征。"}]}' WHERE id = 13660;
UPDATE genes SET GHR_metadata_ch = '{"gene": "RNF216", "conditions": [{"name": "戈登·霍姆斯综合症", "detail": "已发现至少有8个RNF216gene突变引起戈登·霍姆斯综合征，这是一种罕见病，其特征是指导性发育（促性腺激素性性腺功能减退症）和难以协调运动（小脑性共济失调）的激素产生减少。许多由RNF216gene突变引起的Gordon Holmes综合征患者的智力功能下降（痴呆）。这些突变削弱了RNF216蛋白标记不需要分解的蛋白的能力。Arc的破坏破坏了正常的突触连接和可塑性，这可能导致Gordon Holmes综合征患者的痴呆。尚不清楚缺乏RNF216蛋白功能如何导致性腺功能低下性腺功能减退或小脑性共济失调。"}]}' WHERE id = 13676;
UPDATE genes SET GHR_metadata_ch = '{"gene": "EIF2B5", "conditions": [{"name": "白质消失的白质脑病", "detail": "大约65%的白质消失的白质脑病患者（包括严重，在魁北克和曼尼托巴的Cree和Chippewayan人群（Cree白质脑病）和一些受影响的女性中发现的早发形式，其神经系统特征伴有卵巢功能衰竭（卵巢白细胞营养不良）。这些突变导致eIF2B功能部分丧失。eIF2B功能受损使人体细胞更难以调节蛋白质合成并应对不断变化的conditions和压力。研究人员认为，白质中的细胞（神经纤维被称为髓鞘的脂肪物质覆盖，能够隔离和保护神经）可能特别受到压力异常反应的影响，导致白质消失的白质脑病的体征和症状。"}]}' WHERE id = 13680;
UPDATE genes SET GHR_metadata_ch = '{"gene": "POMT1", "conditions": [{"name": "沃克-沃伯格综合症", "detail": "已发现POMT1gene中至少有24个突变会引起Walker-Warburg综合征，这是一组称为先天性肌营养不良的疾病中最严重的形式。患有Walker-Warburg综合征的人患有骨骼肌无力以及大脑和眼睛异常。由于这种情况引起的问题的严重性，受影响的个体通常无法存活到儿童早期。（换行）导致Walker-Warburg综合征的POMT1gene突变导致形成无法将甘露糖转移至-dystroglycan，阻止其正常糖基化。结果是-dystroglycan不再能够有效地将细胞锚定在周围的蛋白质和其他分子上。没有功能-dystroglycan为了稳定肌肉纤维，当它们反复收缩并使用时放松时会受到损坏。受损的纤维会随着时间的推移而减弱和死亡，这会影响Walker-Warburg综合征患者骨骼肌的发育，结构和功能。(换行)有缺陷-dystroglycan还影响大脑早期发育过程中神经元的迁移。一些神经元不是在到达预定目的地时停止，而是通过大脑表面迁移到周围充满液体的空间。研究人员认为，神经元迁移的这个问题会导致大脑异常，称为鹅卵石脑畸形，其中表面大脑缺乏正常的褶皱和凹槽，而是显得颠簸和不规则。关于POMT1gene突变在身体其他部位的影响知之甚少。"}, {"name": "肢带型肌营养不良症", "detail": "遗传学家庭参考资料提供有关肢带型肌营养不良症的信息。"}, {"name": "其他疾病", "detail": "POMT1gene的突变也与较不严重的肌肉营养不良有关，包括肌肉眼脑疾病和与POMT1相关的先天性肌营养不良（也称为MDDGB1）。肌肉眼脑疾病类似于Walker-Warburg综合征（如上所述），尽管受影响的个体通常可以存活到儿童期或青春期。与POMT1相关的先天性肌营养不良症会导致肌肉无力，脑部异常和智力残疾，但通常不会影响眼睛。（换行）导致这些情况的POMT1gene突变导致功能降低的POMT酶复合物。结果，糖基化-dystroglycan受损。由此产生的情况的严重程度似乎与-dystroglycan糖基化；糖基化程度越低，病情越严重。"}]}' WHERE id = 13703;
UPDATE genes SET GHR_metadata_ch = '{"gene": "TCIRG1", "conditions": [{"name": "骨质疏松症", "detail": "在骨质疏松症患者中已鉴定出TCIRG1gene的60多个突变。这些突变导致该疾病的最严重形式，常染色体隐性骨质疏松症（ARO）。(换行)许多TCIRG1gene突变改变了gene指令如何用于制备V-ATP酶的a3亚基。其他突变会改变a3亚基中的单个蛋白质结构单元（氨基酸），或导致产生异常短版本的亚基。研究表明，大多数负责骨质疏松症的TCIRG1gene突变都消除了a3亚基的功能。(换行)没有a3亚基，V-ATPase无法将质子泵出破骨细胞。结果，褶皱边界和骨表面之间的隔室未酸化，并且骨不能被分解。当新骨形成时旧骨不被分解时，整个骨骼中的骨变得异常致密。骨骼在结构上也异常，使其容易骨折。骨重塑的这些问题是常染色体隐性骨质疏松症的主要特征。"}, {"name": "严重的先天性中性粒细胞减少症", "detail": "遗传学家庭参考提供有关严重先天性中性粒细胞减少症的信息。"}]}' WHERE id = 13710;
UPDATE genes SET GHR_metadata_ch = '{"gene": "LZTR1", "conditions": [{"name": "神经鞘瘤病", "detail": "在神经鞘瘤病患者中发现了LZTR1gene的50多种不同突变，神经鞘瘤病是一种以多种非癌性（良性）肿瘤为特征的疾病，称为神经鞘瘤，生长在神经上。这种类型的肿瘤来自施万细胞，施万细胞是通常在神经周围形成绝缘层的特化细胞。（换行）与神经鞘瘤病相关的LZTR1gene突变导致产生改变的LZTR1蛋白，该蛋白不能控制细胞生长和分裂，从而使肿瘤发展。尚不清楚为什么这些gene突变在神经鞘瘤病患者中主要与神经鞘瘤相关，而不是与其他类型的肿瘤相关。（换行）似乎仅LZTR1gene的突变不足以触发神经鞘瘤的发展。神经鞘瘤的形成也可能需要在一个人的一生中获得并且仅存在于某些细胞中的其他遗传变化（体细胞突变）。(换行)一些LZTR1gene突变的人从未发生肿瘤，这是一种称为外显率降低的情况。"}, {"name": "努南综合征", "detail": "遗传学家庭参考资料提供有关Noonan综合征的信息。"}, {"name": "癌症", "detail": "在一种称为胶质母细胞瘤的癌性（恶性）脑肿瘤中也发现了LZTR1gene的突变。这些突变被分类为体细胞突变，仅存在于引起肿瘤的细胞中。科学家认为，LZTR1gene的体细胞变化或该gene的缺失是使某些脑细胞无法控制地生长和分裂形成肿瘤的因素之一。"}]}' WHERE id = 13714;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SLC30A10", "conditions": [{"name": "高锰血症伴肌张力障碍", "detail": "在患有肌张力障碍，红细胞增多症和肝硬化的高锰血症患者（HMDPC，也称为患有肌张力障碍的高锰血症1）中，已经鉴定出SLC30A10gene的十几个突变。这种遗传性疾病的特征是血液，大脑和肝脏中锰含量高（高锰血症）。该疾病引起运动问题，例如肌肉的不自主张力（肌张力障碍）；红细胞数量增加（红细胞增多症）；和肝脏异常，包括不可逆转的肝病（肝硬化）。SLC30A10gene中的（换行）突变会损害锰从细胞中的运输，从而使该元素在脑和肝细胞中积累。锰积聚在大脑的一个区域，有助于控制运动，破坏神经细胞并导致HMDPC特有的运动问题。肝脏中锰积累造成的损害会导致特征性的肝脏问题。高水平的锰有助于增加红细胞的产生，因此过量的这种元素可能是红细胞增多症的基础。"}]}' WHERE id = 13723;
UPDATE genes SET GHR_metadata_ch = '{"gene": "BEST1", "conditions": [{"name": "玻璃状黄斑营养不良", "detail": "在玻璃状黄斑营养不良患者中已鉴定出BEST1gene的100多个突变。这些突变可导致疾病的早发形式（称为最佳疾病）或成人发病形式。两种类型的玻璃状黄斑营养不良的特征在于视网膜细胞中脂肪黄色素（脂褐素）的积累。色素上皮。随着时间的推移，这种物质的异常积累会损害对清晰中心视力至关重要的光感受器。（换行）涉及玻璃状黄斑营养不良的大多数BEST1突变改变了bestrophin-1中的单蛋白构建块（氨基酸）。改变后的蛋白质可能会形成异常形状的通道，该通道无法正确调节氯离子流入或流出视网膜色素上皮细胞的流量。目前尚不清楚这种缺陷与玻璃体黄斑营养不良患者脂褐素的积累和中心视力的逐渐丧失有何关系。"}, {"name": "年龄相关性黄斑变性", "detail": "遗传学家庭参考提供有关年龄相关性黄斑变性的信息。"}, {"name": "常染色体显性玻璃体视网膜脉络膜病变", "detail": "BEST1gene突变可引起一种罕见的眼部疾病，称为常染色体显性玻璃体视网膜脉络膜病变（ADVIRC）；在受影响的个体中发现了该gene中至少四个突变。ADVIRC的特征是充满眼睛（玻璃体），视网膜和视网膜内血管网络（脉络膜）的透明凝胶异常。这些异常可能导致视力障碍。（换行）参与ADVIRC的BEST1gene突变改变gene中的单个DNA构建块（核苷酸）。这些变化改变了gene指令如何用于制造bestrophin-1，从而导致产生缺少某些片段或具有额外片段的蛋白质版本。目前尚不清楚这些版本的bestrophin-1如何影响氯离子转运或导致ADVIRC特有的眼部异常。研究人员怀疑异常与视网膜色素上皮或感光细胞的缺陷有关。"}, {"name": "视网膜色素变性", "detail": "遗传学家庭参考提供有关视网膜色素变性的信息。"}, {"name": "其他疾病", "detail": "BEST1gene突变会导致其他几种眼部疾病。例如，已经在具有眼睛异常例如眼睛压力增加（青光眼）的个体中发现了该gene的突变；晶状体混浊（白内障）；以及一种称为纳米眼病的疾病，其特征是眼睛很小，远视。这些眼睛异常可能与视网膜色素上皮的缺陷有关。（换行）最近描述的称为常染色体隐性遗传性眼病（ARB）的眼病也是由BEST1gene突变引起的。这种情况的特征是进行性视力丧失和常染色体隐性遗传。常染色体隐性遗传意味着受影响的个体在每个细胞的BEST1gene的两个拷贝中都有突变。导致ARB的突变会改变bestrophin-1的结构或阻止蛋白质的产生。bestrophin-1的异常或丢失会损害氯离子流入或流出视网膜色素上皮细胞。目前尚不清楚bestrophin-1的变化如何导致这种疾病患者的视力丧失。(换行)BEST1gene突变也在少数眼部异常与视网膜色素变性相似的个体中发现，尽管一些医生认为这些个体具有ADVIRC或ARB的形式（如上所述）。色素性视网膜炎是一组相关的眼部疾病，当视网膜中的光感受器逐渐恶化，导致视力丧失时发生。参与视网膜色素变性的BEST1gene突变似乎损害了氯离子的流动，但尚不清楚这些变化如何导致这种疾病的视力问题。(换行)此外，研究人员还研究了与年龄相关性黄斑变性相关的BEST1gene突变。这种眼病是全世界老年人视力丧失的主要原因。尽管尚不清楚突变是否与疾病的发展有关，但在少数与年龄相关的黄斑变性的人中发现了BEST1gene的突变。BEST1gene的变化可能不是这种常见眼疾的主要危险因素。遗传和环境因素的结合可能决定发生年龄相关性黄斑变性的风险。"}]}' WHERE id = 13724;
UPDATE genes SET GHR_metadata_ch = '{"gene": "CYP24A1", "conditions": [{"name": "特发性婴儿高钙血症", "detail": "CYP24A1gene中的20多个突变已被发现引起一种称为婴儿高钙血症1的特发性婴儿高钙血症，其特征在于血液（高钙血症）和尿液（高钙尿症）中的高水平钙和肾脏中的钙沉积（肾钙质沉着症）。高钙血症通常会导致婴儿呕吐，进食不良以及无法以预期的速度生长和增重（无法壮成长），尽管一些受影响的个体直到成年才出现这种疾病的体征和症状。受影响成年人的特征（无论是否在婴儿期出现症状）通常包括高钙尿症，肾钙化病和肾结石（肾结石病），尽管它们可能不会引起任何明显的健康问题。（换行）导致婴儿高钙血症1的CYP24A1gene突变降低或消除24-羟化酶的活性。这种酶功能的不足会损害钙三醇的分解。由此产生的过量钙三醇增加钙吸收到血液中，引起高钙血症。肾脏中钙吸收的失调导致高钙尿症，肾钙质沉着症和肾结石病。"}]}' WHERE id = 13729;
UPDATE genes SET GHR_metadata_ch = '{"gene": "CAV1", "conditions": [{"name": "先天性全身性脂肪营养不良", "detail": "已发现CAV1gene中至少有一个突变会导致3型先天性全身性脂肪营养不良（也称为Berardinelli-Seip先天性脂肪营养不良）。这种罕见病的特征是几乎完全没有脂肪组织和非常肌肉的外观。脂肪组织短缺导致多种健康问题，包括在血流中循环的称为甘油三酯的高水平脂肪（高甘油三酯血症）和糖尿病。先天性全身性脂肪营养不良3型的其他特征包括生长不良和身材矮小。（换行）鉴定的突变用小窝蛋白-1蛋白（写为Glu38Ter或E38X）的38位的过早终止信号替换单个蛋白质构建块（氨基酸）。这种突变发生在CAV1gene的两个拷贝中在每个细胞中，它阻止细胞产生任何功能性小窝蛋白-1。目前尚不清楚这种蛋白质的缺乏如何导致先天性全身性脂肪营养不良3型的特殊特征。然而，小窝蛋白-1的缺乏可能破坏脂肪细胞的正常发育和功能，这将阻止脂肪正常储存在脂肪组织中。缺乏体脂是这种疾病的许多体征和症状的基础。"}, {"name": "肺动脉高压", "detail": "遗传学家庭参考资料提供有关肺动脉高压的信息。"}, {"name": "其他疾病", "detail": "除了先天性全身性脂肪营养不良（如上所述）外，还发现CAV1gene的突变会引起其他几种形式的脂肪营养不良，所有这些都涉及脂肪组织的丢失。在非典型性部分脂肪营养不良的人中至少鉴定出两种突变，这种疾病的特征是面部和上半身皮肤下的脂肪流失。受影响的人从出生开始就患有高甘油三酯血症和晶状体混浊（先天性白内障）。导致这种情况的CAV1gene突变存在于每个细胞中该gene的一个拷贝中。它们减少了细胞内产生的小窝蛋白-1的量。（换行）已发现CAV1gene中的另外两个突变引起新生儿发作的全身性脂肪营养不良综合征。这种情况的体征和症状包括除臀部，手掌和脚底外的身体脂肪总体流失。其他功能包括皮肤稀薄，可见血管；一个大的三角形头；并在儿童时期缓慢增加体重。导致这种情况的CAV1gene突变也发生在每个细胞的一个gene拷贝中，并减少了细胞中产生的caveolin-1的数量。(换行)由于CAV1gene突变可引起几种不同形式的脂肪营养不良，因此很明显，它在脂肪组织的发育中起着至关重要的作用。然而，研究人员仍在努力确定小窝蛋白-1的短缺如何导致体内脂肪的流失以及这些疾病的相关特征。"}]}' WHERE id = 13730;
UPDATE genes SET GHR_metadata_ch = '{"gene": "IKBKG", "conditions": [{"name": "免疫缺陷性无汗性外胚层发育不良", "detail": "已发现IKBKGgene中的20多个突变会导致免疫缺陷（EDA-ID）的无汗外胚层发育不良。EDA-ID是一种以免疫系统功能降低为特征的疾病，导致反复感染和外胚层组织异常发育。导致EDA-ID的IKBKGgene突变损害了IKBKG蛋白的功能，但并未完全消除其调节核因子-κ-B的能力。这些变化破坏了形成外胚层组织的免疫细胞和细胞内的某些信号传导途径，导致免疫缺陷和外胚层组织的不完全发育。EDA-ID体征和症状的严重程度取决于剩余的蛋白质功能量；蛋白质功能水平越高，疾病越轻。"}, {"name": "色素失禁", "detail": "在色素失禁患者中已鉴定出IKBKGgene的30多个突变，色素失禁是一种以皮肤，牙齿和指甲异常以及视力丧失和脱发为特征的疾病。最常见的突变是复杂的重排，可从IKBKGgene中删除某些遗传物质，占该病所有病例的80%以上。这种突变可能导致产生异常小的，无功能的IKBKG蛋白。其他患有色素失禁的人具有阻止任何IKBKG蛋白产生的突变。没有这种蛋白质，核因子-κ-B就不能被激活。没有活性核因子-κ-B的细胞对触发它们自我毁灭的信号更敏感。导致的异常细胞死亡可能导致色素失禁的体征和症状。"}, {"name": "骨质疏松症", "detail": "已发现IKBKGgene中的几个突变会导致罕见的骨质疏松症，并具有X连锁遗传模式。研究人员通常将这种情况称为OL-EDA-ID，它是源自该疾病每个主要特征的首字母缩写。除了骨质疏松症的异常致密的骨骼外，OL-EDA-ID还与液体积聚（淋巴水肿）和无汗性外胚层发育不良引起的异常肿胀有关，这会影响皮肤，头发，牙齿和汗腺。受影响的个体也有免疫缺陷，这会导致严重的反复感染。（换行）负责OL-EDA-ID的突变损害了IKBKG蛋白的正常功能，这降低了核因子-κ-B的活化。这些变化破坏了形成外胚层组织的免疫细胞和细胞内的某些信号传导途径，导致免疫缺陷和外胚层组织的不完全发育。目前尚不清楚IKBKG突变如何导致OL-EDA-ID的其他特征，尽管体征和症状可能是由其他类型细胞（包括骨形成细胞）中异常的核因子-κB信号传导引起的。"}, {"name": "其他疾病", "detail": "IKBKGgene突变也解释了一些称为X连锁易感性分枝杆菌病的病例。患有这种疾病的人感染称为分枝杆菌的细菌形式的风险增加。其中一些外来入侵者被描述为“机会主义”生物，因为它们不会在免疫系统正常的人群中引起疾病​​。另一种类型的分枝杆菌会导致结核病，这是一种可能严重或危及生命的呼吸系统疾病。负责X连锁分枝杆菌病易感性的IKBKGgene突变改变了IKBKG蛋白的结构。有缺陷的蛋白质破坏了免疫细胞内的某些信号传导途径，从而阻止了免疫系统有效防御身体免受分枝杆菌感染。"}]}' WHERE id = 13766;
UPDATE genes SET GHR_metadata_ch = '{"gene": "NOG", "conditions": [{"name": "t腕联合综合征", "detail": "在患有称为骨-腕联合综合征的人中，已经鉴定出NOGgene的几种突变。这种情况的特征是腕骨（腕骨）和脚踝（骨）中的各个骨骼融合，以及组成每个手指和脚趾的骨骼之间的关节融合（指交）。交指使手指和脚趾僵硬，难以弯曲。(换行)这种情况是由NOGgene突变引起的，该突变改变了noggin蛋白中的单个蛋白结构单元（氨基酸）。这些突变改变了头蛋白的结构或稳定性，损害了头蛋白从细胞中的运输，或降低了该蛋白与BMP结合的能力，从而导致功能性头蛋白的减少。随着noggin功能的降低，BMPs异常刺激应该没有骨骼的关节区域的骨形成，导致t骨-腕联合综合征患者出现骨融合。"}, {"name": "其他疾病", "detail": "其他几种与骨骼有关的疾病是由NOGgene突变引起的。这些gene突变改变了noggin蛋白中的单个蛋白结构单元（氨基酸）。(换行)近端交指的特征在于在手指和脚趾的骨骼之间的关节处融合，特别是在手指基部的关节处融合。其他体征和症状包括异常短的中指，蹼状脚趾和由于耳朵中的骨头融合（骨固定）引起的听力损失。(换行)多发性骨疣综合征1的特征是交感神经和特征性的面部特征，例如宽鼻子和薄嘴唇。此外，受影响的个体可以融合手，脚，臀部和脊柱上部（颈椎）的骨骼。患有这种情况的人可能因骨固定而听力下降。(换行)拇指和脚趾宽大（也称为Teunissen-Cremers综合征）的镫骨强直患者由于镫骨固定，远视，拇指和大脚趾而听力丧失。一些受影响的个体可能融合了颈椎和特征性的面部特征，如在多发性突触综合征1中所见。(换行)近距离B2型的特征是手指和脚趾短，这是因为手指的中骨或形成手指尖端的骨骼异常小或不存在。此外，患有这种疾病的人可能会出现交指症，腕骨融合以及两个或多个手指或脚趾之间的皮肤连接（综合征）。（换行）与tar腕联合综合征一样，引起这些疾病的NOGgene突变会减少功能性noggin蛋白的量。由于未知原因，相同的突变可能导致不同人群的不同疾病。由于共同的遗传原因和重叠特征，研究人员建议这些疾病，包括骨-腕联合综合征，代表了一系列相关疾病，称为NOG相关的指交频谱疾病（NOG-SSD）。"}]}' WHERE id = 13767;
UPDATE genes SET GHR_metadata_ch = '{"gene": "IDUA", "conditions": [{"name": "黏多醣贮积症I型", "detail": "已发现IDUAgene中的100多个突变会导致I型粘多糖贮积病（MPS I）。改变一个DNA结构单元（核苷酸）的突变是最常见的。所有引起MPS I的突变都会降低或完全消除α-L-异丁烯酸酶的功能。通常无法确定某种突变是否会导致MPS I严重或减弱；但是，不产生任何α-L-异丁烯酸酶的人患有这种疾病的严重形式。（换行）缺乏α-L-艾杜糖醛酸酶活性导致溶酶体内硫酸乙酰肝素和硫酸皮肤素的积累。这些GAG的积累增加了溶酶体的大小，这就是为什么MPS I中许多组织和器官扩大的原因。研究人员认为，积累的GAG也可能干扰溶酶体内其他蛋白质的功能并破坏分子内部的运动。细胞。"}]}' WHERE id = 13775;
UPDATE genes SET GHR_metadata_ch = '{"gene": "MOCOS", "conditions": [{"name": "遗传性黄嘌呤尿症", "detail": "已发现MOCOSgene中至少有四个突变会导致II型遗传性黄嘌呤尿症，这种情况最常影响肾脏。这些突变大多数会改变钼辅因子硫化酶中的单个蛋白质结构单元（氨基酸）。这些突变的影响尚不完全清楚，但它们可能会改变酶的形状和功能。如果钼辅因子硫化酶不能向钼辅因子中添加硫，则黄嘌呤脱氢酶和醛氧化酶不被激活。醛氧化酶活性的丧失似乎没有引起任何体征或症状。然而，黄嘌呤脱氢酶活性的丧失阻止了黄嘌呤向尿酸的转化，导致黄嘌呤在肾脏和其他组织中积累。过量的黄嘌呤会形成微小的晶体，积聚在肾脏中，偶尔会导致结石的形成，从而损害肾功能并最终导致肾功能衰竭。不常见的是，黄嘌呤晶体积聚在肌肉中，引起疼痛和痉挛。在一些患有II型遗传性黄嘌呤尿症的人中，该病症不会引起任何健康问题。"}]}' WHERE id = 13780;
UPDATE genes SET GHR_metadata_ch = '{"gene": "CHM", "conditions": [{"name": "脉络膜血症", "detail": "已经发现CHMgene中的突变引起脉络膜血症，该病症的特征在于由于缺乏或无功能蛋白引起的进行性视力丧失，所述蛋白破坏REP-1蛋白与Rab蛋白的结合，所述Rab蛋白不能帮助细胞内运输导致过早的细胞死亡。"}]}' WHERE id = 13798;
UPDATE genes SET GHR_metadata_ch = '{"gene": "TBXAS1", "conditions": [{"name": "Ghosal hematodiaphyseal发育不良", "detail": "已发现TBXAS1gene中至少有四个突变会导致Ghosal血骨干phy发育不良。这种情况的特征是骨骼异常厚，骨髓瘢痕形成（纤维化）引起的红细胞缺乏（贫血）。（换行）每个已知的突变都会改变血栓素a合酶1中的单个蛋白质结构单元（氨基酸），从而严重降低酶的活性。该酶活性的缺乏阻止了前列腺素H2转化为血栓素A2。结果，细胞比平时具有更多的前列腺素H2。前列腺素H2转化为几种相关分子，包括前列腺素E2，其被认为参与骨重塑和控制未成熟红细胞的生长。研究人员推测，前列腺素H2过量导致前列腺素E2水平升高，导致人体血液干骺端发育不良患者出现骨骼异常和贫血。然而，缺乏血栓素a合酶1活性导致该病症的特定特征的确切机制仍不清楚。（换行）血栓素A合酶1活性的缺乏也降低了细胞中血栓素A2的水平。虽然这种分子在止血中起着关键作用，但患有Ghosal血液干骺端发育不良的人似乎没有凝血问题。研究人员怀疑其他参与血管收缩和血小板聚集的分子可能能够弥补这些个体中血栓素A2的缺乏。"}]}' WHERE id = 13800;
UPDATE genes SET GHR_metadata_ch = '{"gene": "C8A", "conditions": [{"name": "补充成分8缺陷", "detail": "已发现C8Agene中至少有两个突变导致I型补体成分8缺陷。这种情况是一种免疫系统疾病，称为免疫缺陷，其中免疫系统无法有效保护身体免受外来入侵者的侵害。如细菌。补体成分8缺乏的人发生反复感染的风险显着增加，特别是脑膜炎奈瑟球菌引起脑膜炎，这是一种严重的疾病，涉及脑和脊髓周围膜的炎症。(换行)C8Agene的突变改变了蛋白质生产的蓝图；如果产生，异常短的α亚基可能会迅速分解。由此导致的这种蛋白质的短缺损害补体成分8的形成。该组分的缺乏阻止了膜攻击复合物的形成。没有这部分免疫反应，受影响的个体容易感染，特别是奈瑟菌。"}, {"name": "C3肾小球病", "detail": "遗传学家庭参考提供有关C3肾小球病的信息。"}]}' WHERE id = 13820;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SDHA", "conditions": [{"name": "非综合征性副神经节瘤", "detail": "在患有副神经节瘤或嗜铬细胞瘤（一种副神经节瘤）的人中，已经鉴定出SDHAgene中的至少三个突变，它们是与神经系统相关的非癌性（良性）肿瘤。SDHAgene突变最常见于副神经节瘤患者，但已在嗜铬细胞瘤患者中发现。具体而言，SDHAgene突变与非综合征性副神经节瘤或嗜铬细胞瘤相关，这意味着肿瘤不是遗传综合征的一部分。（换行）SDHAgene中的单个突变增加了个体发展该病症的风险。但是，需要删除gene正常拷贝的其他突变才能引起肿瘤形成。第二种突变称为体细胞突变，是在一个人的一生中获得的，仅存在于肿瘤细胞中。（换行）与非综合征性副神经节瘤或嗜铬细胞瘤相关的SDHAgene突变改变SDHA蛋白质序列中的单个蛋白质构建块（氨基酸）或导致蛋白质缩短。结果，SDH酶活性很少或没有。由于突变的SDH酶无法将琥珀酸转化为富马酸，因此琥珀酸会在细胞中积累。过量的琥珀酸盐异常稳定HIF，HIF也在细胞中积聚。过量的HIF刺激细胞分裂并在不需要时触发血管的产生。快速和不受控制的细胞分裂，以及新血管的形成，可导致肿瘤的发展。"}, {"name": "胃肠道间质瘤", "detail": "遗传学家庭参考提供有关胃肠道间质瘤的信息。"}, {"name": "遗传性副神经节瘤嗜铬细胞瘤", "detail": "遗传学家庭参考资料提供有关遗传性副神经节瘤嗜铬细胞瘤的信息。"}, {"name": "利综合征", "detail": "遗传学家庭参考提供有关Leigh综合征的信息。"}, {"name": "其他疾病", "detail": "已经在少数患有Leigh综合征的人中发现了SDHAgene的突变，Leigh综合征是一种进行性脑部疾病，通常出现在婴儿期或儿童早期。患病的儿童可能会出现呕吐，癫痫发作，发育迟缓，肌肉无力和运动问题。患有这种疾病的人也可能发生心脏病，肾脏问题和呼吸困难。（换行）负责Leigh综合征的SDHAgene突变改变SDHA蛋白中的单个氨基酸或导致异常短的蛋白质。这些遗传变化破坏了SDH酶的活性，削弱了线粒体产生能量的能力。但是，尚不清楚SDHAgene的突变如何与Leigh综合征的特定特征相关。"}]}' WHERE id = 13832;
UPDATE genes SET GHR_metadata_ch = '{"gene": "C8B", "conditions": [{"name": "补充成分8缺陷", "detail": "C8Bgene中的几个突变导致II型补体成分8缺乏。这种情况是一种称为免疫缺陷的免疫系统疾病，其中免疫系统不能有效地保护身体免受外来入侵者如细菌的侵害。补体成分8缺乏的人发生反复感染的风险显着增加，特别是脑膜炎奈瑟球菌引起脑膜炎，这是一种严重的疾病，涉及脑和脊髓周围膜的炎症。（换行）涉及补体成分8缺陷的C8Bgene突变最常见于称为C至T转换的类型，其中称为胞嘧啶（C）的DNA构建块（核苷酸）变为核苷酸胸腺嘧啶（T）。最常见的是，这种变化发生在称为外显子9的gene区域，但也可能发生在其他区域。这些突变改变了蛋白质结构单元的序列，导致异常短的蛋白质，如果产生，很可能会迅速分解。其他类型的突变可能发生在C8Bgene中；它们还会导致异常短的蛋白质迅速分解。由此导致的这种蛋白质的短缺损害补体成分8的形成。该组分的缺乏阻止了膜攻击复合物的形成。没有这部分免疫反应，受影响的个体容易感染，特别是奈瑟菌。"}]}' WHERE id = 13862;
UPDATE genes SET GHR_metadata_ch = '{"gene": "GDF6", "conditions": [{"name": "Klippel-Feil综合征", "detail": "已经发现GDF6gene中至少有10个突变引起Klippel-Feil综合征，其特征在于颈部（颈椎）中两个或更多个脊柱骨的异常连接（融合）以及影响许多其他特征的各种其他特征身体的部分。导致Klippel-Feil综合征的大多数GDF6gene突变取代了GDF6蛋白中的单个蛋白结构单元（氨基酸）。这些突变可能导致功能蛋白减少。尽管GDF6蛋白参与骨骼生长和椎骨的形成，但尚不清楚这种蛋白的缺乏如何导致Klippel-Feil综合征患者的颈椎分离不完全。"}, {"name": "缺损", "detail": "遗传学家庭参考提供有关结肠癌的信息。"}, {"name": "小眼症", "detail": "遗传学家庭参考提供有关小眼症的信息。"}]}' WHERE id = 13888;
UPDATE genes SET GHR_metadata_ch = '{"gene": "NYX", "conditions": [{"name": "X连锁先天性静止性夜盲", "detail": "在X连锁先天性静止性夜盲患者中，已鉴定出NYXgene的50多个突变。该gene的突变导致该疾病的完整形式，其特征在于在弱光（夜盲症）下难以看到，锐度丧失（视力下降），严重近视（高度近视），眼睛的不自主运动（眼球震颤）和不朝同一方向看的眼睛（斜视）。(换行)许多NYX突变会改变nyctalopin中的单个蛋白质结构单元（氨基酸）。NYX突变可以改变蛋白质的大小或形状，或阻止其附着在感光细胞表面。功能性nyctalopin的丧失会破坏感光细胞传输视觉信号的能力，从而损害视力。杆的功能被严重破坏，而锥体的功能仅受到轻微影响。"}, {"name": "其他疾病", "detail": "已发现NYXgene中至少有两个突变会引起高度近视，而没有X连锁先天性固定性夜盲的其他视觉问题。导致高度近视的突变每个都会改变nyctalopin中的单个氨基酸，这预计会导致蛋白质不稳定。研究人员不确定为什么这些突变会导致高度近视而没有任何其他视力异常。"}]}' WHERE id = 13919;
UPDATE genes SET GHR_metadata_ch = '{"gene": "LARS2", "conditions": [{"name": "Perrault综合征", "detail": "在患有Perrault综合征的个体中发现了LARS2gene的至少三个突变，这种疾病的特征是受影响的男性和女性听力下降，而受影响的女性卵巢异常。参与Perrault综合征的LARS2gene突变降低或消除了线粒体亮氨酰tRNA合成酶的活性。功能性线粒体亮氨酰-tRNA合成酶的缺乏阻止了线粒体内新蛋白质的正常组装。研究人员推测，蛋白质组装受损会破坏线粒体能量的产生。但是，尚不清楚LARS2gene突变如何在受影响的个体中导致听力问题和卵巢异常。"}]}' WHERE id = 13944;
UPDATE genes SET GHR_metadata_ch = '{"gene": "PSAP", "conditions": [{"name": "变色性白细胞营养不良", "detail": "在少数患有异色性白细胞营养不良（一种导致神经系统功能恶化的疾病）的个体中，研究人员已经鉴定出导致saposin B蛋白短缺（缺乏）的PSAPgene突变。这种缺陷会干扰硫化物的分解。结果，这些物质可以在神经系统中累积到毒性水平。(换行)硫化物的累积逐渐破坏髓鞘，髓鞘是保护神经和促进神经冲动有效传递的覆盖物。髓鞘的破坏导致白质损失（脑白质营养不良）和神经系统功能受损，导致异染性脑白质营养不良的体征和症状。"}, {"name": "其他疾病", "detail": "在少数个体中，PSAPgene的突变会干扰saposin C蛋白的功能，从而导致类似于严重形式的高雪氏病的疾病。这种情况的体征和症状包括神经系统疾病和肝脾肿大（肝脾肿大）。如果没有足够的saposin C激活蛋白，则葡萄糖脑苷脂酶不能有效分解葡萄糖脑苷脂。结果，葡糖脑苷脂以典型的高雪氏病形式积累在人体组织中。在具有类似于另一种称为Krabbe病的白细胞营养不良的体征和症状的个体中也鉴定出一些PSAPgene突变。（换行）此外，已鉴定出PSAPgene中的一些突变，这些突变阻止了一种以上的saposin蛋白的产生。具有这些突变的个体在其神经系统和其他器官中大量积累鞘脂。这种积累导致非常严重的神经系统疾病，呼吸系统疾病和肝脾肿大。"}]}' WHERE id = 14005;
UPDATE genes SET GHR_metadata_ch = '{"gene": "STRC", "conditions": [{"name": "非综合征性听力损失", "detail": "研究人员在患有非综合征性听力损失的个体中发现了一些STRCgene突变，这是与其他体征和症状无关的听力损失。该gene的突变会导致一种称为DFNB16的非综合征性听力损失。这种形式的听力损失可以在孩子学会说话（舌前）之前出现，也可以在孩子学会说话（舌后）之后开始。听力损失的范围从轻微到严重，尤其影响听到高频声音的能力。（换行）导致非综合征性听力损失的STRCgene突变向STRCgene添加少量DNA或从gene中删除DNA。在许多情况下，该突变会删除包含整个STRCgene的15号染色体。该gene中的突变导致产生无功能形式的立体霉素或阻止产生任何该蛋白质。功能性立体纤毛蛋白的丧失可能会改变立体纤毛的结构，从而阻止它们对声波正常反应。结果，毛细胞无法将声音转换为电脉冲，从而导致DFNB16患者的听力下降。"}, {"name": "听觉障碍和男性不育", "detail": "感音神经性耳聋和男性不育是由15号染色体长臂上的遗传物质缺失引起的疾病。这种情况的特征是听力下降和无法生孩子。（换行）通常被删除的染色体区域包含多个gene，包括STRCgene。患有这种情况的人在每个细胞的15号染色体的两个拷贝中都有缺失。缺失的结果是，受影响的个体缺少STRCgene的两个拷贝，并且没有产生立体定向蛋白。缺乏立体纤毛蛋白可能会干扰立体纤毛的正常功能，并损害这些结构对声波的反应方式，从而导致听力损失。在15号染色体的同一区域中另一个geneCATSPER2的丢失是造成受影响男性不育的原因。"}]}' WHERE id = 14007;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ARMS2", "conditions": [{"name": "年龄相关性黄斑变性", "detail": "ARMS2gene位于10号染色体的长（q）臂上，称为10q26。该地区与发生年龄相关性黄斑变性的风险密切相关，黄斑变性是老年人视力丧失的常见原因。研究人员已经确定了ARMS2gene及其附近的几种变异（多态性），这些变异可能解释了10q26区域与年龄相关性黄斑变性之间的关联。对这些变异的最佳研究称为rs10490924，它改变了ARMS2蛋白中的单个蛋白结构单元（氨基酸）。另一个常见的变异是复杂的变化，它会删除ARMS2gene的一部分并插入新的遗传物质，也可能导致疾病风险。(换行)目前尚不清楚ARMS2gene的多态性如何与年龄相关性黄斑变性相关。在10q26区域，ARMS2gene位于称为HTRA1的gene旁边；还已经研究了该gene的变化作为该疾病的危险因素。由于这两个gene非常接近，因此很难确定一个或另一个gene的变化或两个gene的可能变化是否导致疾病风险增加。年龄相关性黄斑变性是一种复杂的疾病，可能是多种遗传和环境因素共同作用的结果。"}]}' WHERE id = 14012;
UPDATE genes SET GHR_metadata_ch = '{"gene": "HTRA1", "conditions": [{"name": "伴有皮质下梗塞和白质脑病的脑常染色体隐性动脉病", "detail": "已发现HTRA1gene中至少有四个突变导致伴有皮质下梗塞和白质脑病的脑常染色体隐性遗传性动脉病，通常称为CARASIL。这种情况的特征是中风的风险大大增加，认知功能恶化（痴呆），过早脱发（脱发）和腰痛发作。这些体征和症状通常在成年初期至中期变得明显。（换行）负责CARASIL的HTRA1gene突变减少或消除HTRA1酶的功能。结果，该酶不能抑制TGF-β的信号传导？蛋白质。研究人员怀疑TGF-β异常增加？信号改变小血管的结构，特别是在大脑中。这些血管异常增加了中风的风险并导致大脑许多区域的神经细胞（神经元）死亡。TGF-β失调？信号传导也可能是CARASIL患者脱发和背痛的基础，尽管TGF-β异常与？信令和这些功能不太清楚。"}, {"name": "年龄相关性黄斑变性", "detail": "HTRA1gene位于10号染色体的长（q）臂上，称为10q26。该地区与发生年龄相关性黄斑变性的风险密切相关，黄斑变性是老年人视力丧失的常见原因。研究人员已经确定了HTRA1gene的几种变异（多态性），这些变异可以解释10q26区域与年龄相关性黄斑变性之间的关联。在称为启动子区域的gene区域中发现了一种变体，称为rs11200638，该区域开始产生HTRA1酶。（换行）目前尚不清楚HTRA1gene多态性如何与年龄相关性黄斑变性相关。在10q26区域，HTRA1gene位于称为ARMS2的gene旁边；还已经研究了该gene的变化作为该疾病的危险因素。由于这两个gene非常接近，因此很难确定一个或另一个gene的变化或两个gene的可能变化是否导致疾病风险增加。年龄相关性黄斑变性是一种复杂的疾病，可能是多种遗传和环境因素共同作用的结果。"}, {"name": "癌症", "detail": "在某些癌性肿瘤中，HTRA1gene的活性（下调）低于正常细胞。研究发现这种gene在几种形式的癌症中下调，包括卵巢癌，发生在子宫内膜的癌症（子宫内膜癌），一种称为黑素瘤的皮肤癌，以及一种称为肝细胞癌的肝癌。HTRA1酶量减少可能与肿瘤进展，癌症扩散（转移）到身体其他部位的风险以及对化学疗法的抵抗力有关。然而，HTRA1gene下调影响癌症发展和生长的机制尚不清楚。"}]}' WHERE id = 14028;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SFTPC", "conditions": [{"name": "表面活性剂功能障碍", "detail": "在表面活性剂功能障碍的人群中，已经鉴定出SFTPCgene的35个以上突变。当这种情况是由SFTPCgene突变引起的（有时称为SP-C功能障碍）时，它可能导致新生儿严重的呼吸问题或儿童或成人逐渐出现轻度的呼吸问题。（换行）与表面活性剂功能障碍相关的SFTPCgene突变影响SP-C蛋白的加工。许多突变发生在称为BRICHOS结构域的gene的特定区域中，这似乎与SP-C蛋白的加工和细胞放置有关。SFTPCgene中的（换行）突变导致成熟SP-C的减少或缺失以及SP-C异常形式的积累。尚不清楚这些结果中的哪一个会导致SP-C功能障碍的体征和症状。缺乏成熟的SP-C会导致表面活性剂组成异常和表面活性剂功能降低。功能性表面活性剂的丧失会增加肺泡的表面张力，导致呼吸困难和肺部塌陷。另外，研究表明，异常加工的SP-C蛋白形成错误的三维形状并积聚在肺细胞内。这些错误折叠的蛋白质可能触发细胞反应，导致细胞损伤和死亡。这种损害可能破坏表面活性剂的产生和释放，导致与表面活性剂功能障碍相关的呼吸问题。"}, {"name": "特发性肺纤维化", "detail": "遗传学家庭参考提供有关特发性肺纤维化的信息。"}]}' WHERE id = 14029;
UPDATE genes SET GHR_metadata_ch = '{"gene": "FTL", "conditions": [{"name": "高铁蛋白血症白内障综合征", "detail": "在高铁蛋白血症白内障综合征患者中，已经鉴定出FTLgene中至少31个突变。受此疾病影响的个体血液（高铁蛋白血症）和身体组织中铁蛋白过量。这种蛋白质的积累始于生命的早期，导致婴儿期眼睛晶状体混浊（白内障）。（换行）引起高铁蛋白血症白内障综合征的突变发现于称为铁反应元件（IRE）的gene片段中。IRE通常可以附着（结合）称为铁调节蛋白（IRP）的蛋白质。当发生这种结合时，停止FTLgene的活性（表达）以防止产生过多的铁蛋白轻链。这通常发生在铁含量低的情况下，因为在这种情况下，储存铁需要较少的铁蛋白。FTLgene的IRE区段中的突变阻止其与IRP结合，干扰铁蛋白产生与铁水平匹配的机制并导致形成过量的铁蛋白。"}, {"name": "神经铁蛋白病", "detail": "在患有神经铁蛋白病的人中，已经鉴定出FTLgene中至少有四个突变，其中铁逐渐在大脑中积累。这些突变都影响称为外显子4的gene区域。在患有这种疾病的人中检测到的最常见突变是在gene序列的460和461位之间插入DNA构建块（核苷酸）腺嘌呤。研究人员认为，大多数具有这种突变的家庭来自1800年之前居住在英格兰西北部的共同祖先。在受神经铁蛋白病影响的单个个体或家庭中发现了其他三个已知突变。导致神经铁蛋白病的FTLgene突变被认为会降低铁蛋白储存铁的能力，导致大脑神经细胞（神经元）释放过量的铁。细胞可以通过产生更多的铁蛋白来响应以试图处理游离铁。过量的铁和铁蛋白会积聚在大脑中，尤其是在某些有助于控制运动的区域（基底神经节），从而导致运动问题和神经铁蛋白病中出现的其他神经系统变化。"}]}' WHERE id = 14031;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SAA1", "conditions": [{"name": "家族性地中海热", "detail": "对家族性地中海热患者的一些研究表明，患有α型血清淀粉样蛋白A1蛋白会增加发生称为淀粉样变性的严重并发症的风险。淀粉样变性涉及蛋白质沉积物的积累，如果不及时治疗，可能导致肾衰竭。研究表明，患有家族性地中海热的人也患有该蛋白的alpha版本，其发生淀粉样变性的可能性是beta或gamma版本的人的2至7倍。（换行）在炎症发作期间，例如在家族性地中海热中发生的炎症，体内产生更多的血清淀粉样蛋白A1。这种蛋白质和相关化合物可能在人体器官和组织中形成异常团块。然而，目前尚不清楚α型血清淀粉样蛋白A1如何增加患有这种疾病的人对淀粉样变性的易感性（或者，β和γ型如何预防这种并发症）。"}, {"name": "其他疾病", "detail": "在患有某些其他炎性疾病的人中，研究表明血清淀粉样蛋白A1蛋白的变体也改变了淀粉样变性的风险。例如，在日本人群中，该蛋白的γ版本似乎增加了类风湿关节炎成年人中淀粉样变性的风险。在患有青少年慢性关节炎的白人中，α型表示发生淀粉样变性的高风险。（换行）在慢性炎症期间，例如在这些疾病中发生的，体内产生更多的血清淀粉样蛋白A1。这种蛋白质和相关化合物可能在人体器官和组织中形成异常团块。但是，尚不清楚某些版本的血清淀粉样蛋白A1如何增加对淀粉样变性的敏感性。"}]}' WHERE id = 14060;
UPDATE genes SET GHR_metadata_ch = '{"gene": "GNMT", "conditions": [{"name": "高蛋氨酸血症", "detail": "在高蛋氨酸血症患者中，已经描述了GNMTgene的三个突变。在一个意大利家系中，一个突变用氨基酸脯氨酸代替49位氨基酸亮氨酸（写为Leu49Pro或L49P），另一个突变用氨基酸天冬酰胺代替176位氨基酸组氨酸（写为His176Asn或H176N）。在罗马血统的希腊患者中，鉴定出一种突变，该突变用氨基酸丝氨酸替代了位置140处的氨基酸天冬酰胺（写为Asn140Ser或N140S）。由这些突变导致的甘氨酸N-甲基转移酶活性降低在受影响的个体中引起高蛋氨酸血症。"}, {"name": "前列腺癌", "detail": "遗传学家庭参考提供有关前列腺癌的信息。"}, {"name": "癌症", "detail": "GNMTgene的某些遗传变异与肝癌和前列腺癌的风险增加有关。在癌性肿瘤中发现的其他GNMTgene突变是在一个人的一生中获得的，并且仅存在于某些细胞中。这些被称为体细胞突变的变化不是遗传的。GNMTgene突变可能会损害甘氨酸N-甲基转移酶的功能，例如加工肝脏中潜在的致癌物质并帮助调节其他gene，包括负责控制细胞生长的gene。当细胞生长过快或以不受控制的方式生长时，可形成癌性肿瘤。"}]}' WHERE id = 14064;
UPDATE genes SET GHR_metadata_ch = '{"gene": "CASK", "conditions": [{"name": "与CASK相关的智力障碍", "detail": "CASKgene的突变已经在与CASK相关的智能障碍患者身上被发现，这种疾病影响大脑发育，主要有两种形式：一种称为小头畸形伴脑桥和小脑发育不全（MICPCH），另一种称为X连锁智能障碍（XL-ID）伴或不伴眼球震颤。这是由于蛋白质缺陷导致的，该蛋白质无法与FRMD7相互作用并损害神经元信号传导。"}, {"name": "FG综合征", "detail": "CASKgene的突变与FG综合征有关，FG综合征是一种仅影响男性几个身体部位的遗传疾病，其特征是智力残疾，肌张力低下，脑部异常，便秘，肛门开口阻塞，肢端亢进和相对大头畸形。"}]}' WHERE id = 14070;
UPDATE genes SET GHR_metadata_ch = '{"gene": "HSD17B10", "conditions": [{"name": "HSD10疾病", "detail": "已发现至少八个HSD17B10gene突变会导致HSD10疾病，该疾病的特征是智力残疾，言语和运动受损以及心肌衰弱（心肌病）。这种疾病对男性的影响比女性严重；许多受影响的男性无法存活到儿童早期。(换行)几乎所有突变都会改变HSD10蛋白中的单个氨基酸。在多个不相关的个​​体中发现的一种突变，将蛋白质中第130位的氨基酸半胱氨酸替换为氨基酸精氨酸（写为Arg130Cys或R130C）。这种突变导致无功能的蛋白质迅速被细胞分解。其他突变也减少了细胞中功能性HSD10蛋白的量。（换行）HSD10的缺乏会损害mtRNase P复合物的功能，从而破坏线粒体前体RNA的加工并最终影响细胞中能量的产生。能量的减少特别影响大脑，眼睛和心脏，导致HSD10疾病的特征。(换行)虽然由HSD17B10gene突变引起的HSD10蛋白短缺也可能减少体内神经甾体和其他物质的加工，但这种机制似乎与HSD10疾病的发展无关。（换行）一些个体具有HSD17B10gene突变，其仅影响2-甲基-3-羟基丁酰-CoA的分解，其可以通过实验室测试检测，但没有HSD10疾病的体征和症状。"}, {"name": "其他疾病", "detail": "HSD17B10gene中的一种特定突变会导致某种形式的X连锁智力障碍，称为MRXS10。这种疾病的特征是智力残疾，四肢无法控制的运动（胆囊炎）和异常行为。该突变用HSD17B10gene（写为605C>A）中605位的核苷酸腺嘌呤取代了DNA构建块（核苷酸）半胱氨酸。尽管此突变不会改变组成HSD10蛋白的氨基酸序列，但它可以破坏gene中信息如何剪接在一起以制作产生该蛋白的蓝图。结果，HSD10蛋白的产量减少。目前尚不清楚HSD10的短缺如何导致MRXS10的体征和症状。(换行)已发现至少22名智力残疾且无其他体征和症状的个体具有少量重复的遗传物质，包括HSD17B10和X染色体上的附近gene。HSD17B10gene的额外副本导致HSD10蛋白的产量增加，这可能会影响神经类固醇的调节。X染色体此区域中其他gene的额外副本也可能在导致智力残疾中起作用。"}]}' WHERE id = 14072;
UPDATE genes SET GHR_metadata_ch = '{"gene": "GYS1", "conditions": [{"name": "0型糖原贮积症", "detail": "At least four mutations in the GYS1 gene have been found to cause a form of glycogen storage disease type 0 (GSD 0) that affects cardiac and skeletal muscle. Most GYS1 gene mutations that cause this condition lead to a lack of functional muscle glycogen synthase, resulting in a complete absence of glycogen in muscle cells. Normally, glycogen is formed from the leftover glucose that is not immediately used by cells after glucose is consumed during meals. In people with GSD 0, who cannot form glycogen, the extra sugar is released by the body. As a result, people with muscle GSD 0 do not have any stored energy, which leads to muscle pain, weakness, or episodes of fainting following moderate physical activity. Since there is no glycogen in cardiac muscle, affected individuals are also at an increased risk of cardiac arrest and sudden death, particularly after physical activity.\r"}]}' WHERE id = 14106;
UPDATE genes SET GHR_metadata_ch = '{"gene": "POGZ", "conditions": [{"name": "自闭症谱系障碍", "detail": "自闭症谱系障碍（ASD）患者中至少鉴定出14种POGZgene突变，这是一种以社交技能受损，沟通问题和重复行为为特征的多种疾病。这些突变从POGZgene中添加或去除遗传物质，改变POGZ蛋白序列中的单个氨基酸，或引入过早的终止信号，从而导致异常短的无功能蛋白。（换行）虽然确切的疾病机制尚不清楚，但研究表明，与ASD相关的POGZgene突变会导致POGZ蛋白与染色质结合能力受损。缺乏染色质结合可能导致异常的染色质重塑，改变参与大脑发育并导致ASD的gene的正常表达。"}, {"name": "其他疾病", "detail": "在智障人士中也发现了POGZgene突变，通常还有其他影响身体各个部位的体征和症状。POGZgene突变被认为会损害POGZ蛋白的染色质结合功能，导致影响大脑和其他身体系统发育的异常gene表达，但是这些突变与智能障碍发育以及受影响个体的其他体征和症状之间的具体联系尚不清楚。"}]}' WHERE id = 14111;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ATXN2", "conditions": [{"name": "2型小脑萎缩症", "detail": "脊髓小脑性共济失调2型（SCA2）是一种以运动进行性问题为特征的疾病。SCA2是由ATXN2gene突变（称为三核苷酸重复扩增）产生的。该突变增加了ATXN2gene中重复的CAG区段的长度。在ATXN2gene中具有32个或更多重复CAG重复的人会发展SCA2。(换行)目前还不清楚异常长的CAG片段如何影响ataxin-2蛋白的功能。异常蛋白质显然会导致细胞死亡，因为患有SCA2的人会显示出脑细胞的丢失。某些称为浦肯野细胞的脑细胞似乎对异常紫杉素2的存在特别敏感。浦肯野细胞位于协调运动（小脑）的大脑部分，并参与神经细胞（神经元）之间的化学信号传导。尚不清楚异常的ataxin-2蛋白如何导致Purkinje和其他脑细胞死亡。随着时间的推移，这些细胞的损失导致SCA2特有的运动问题。"}, {"name": "肌萎缩侧索硬化症", "detail": "遗传学家庭参考提供有关肌萎缩侧索硬化的信息。"}]}' WHERE id = 14140;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SETX", "conditions": [{"name": "肌萎缩侧索硬化症", "detail": "遗传学家庭参考提供有关肌萎缩侧索硬化的信息。"}, {"name": "共济失调伴动眼性失用症", "detail": "已发现SETXgene中至少有115个突变会导致2型动眼性失用症共济失调。这种情况的特征是难以协调运动（共济失调）和眼睛左右运动的问题（动眼性运动失用）。大多数突变取代了senataxin中的单个氨基酸。共济失调与动眼性失用症2型相关的突变被认为破坏了senataxin的解旋酶功能。缺乏功能性senataxin会破坏DNA修复，并可能导致细胞中DNA损伤的积累，特别是影响参与协调运动的大脑部分（小脑）的脑细胞。这种积累可导致小脑细胞死亡，从而导致2型动眼性失用症共济失调的特征性运动问题。"}]}' WHERE id = 14145;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ABCA3", "conditions": [{"name": "表面活性剂功能障碍", "detail": "这种ABCA3gene的突变与一种称为表面活性剂功能障碍或ABCA3缺乏症的疾病有关，这种疾病导致磷脂运输障碍，表面活性剂蛋白的异常加工和组成，导致进行性肺泡表面张力，呼吸障碍和肺萎陷。ABCA3缺乏会导致婴儿出现更严重或致命的呼吸问题，而导致儿童或成人出现较轻的呼吸问题。"}]}' WHERE id = 14146;
UPDATE genes SET GHR_metadata_ch = '{"gene": "EIF2AK4", "conditions": [{"name": "肺静脉闭塞性疾病", "detail": "EIF2AK4gene突变是称为肺静脉闭塞性疾病（PVOD）的主要遗传原因；在受影响的个体中发现了该gene中至少22个突变。在PVOD中，过量的纤维组织积聚在肺中的小血管中，这些小血管将含氧血液从肺输送到心脏（肺静脉）。这种积聚会使血管变窄（闭塞）并损害血流。因为血液流过肺部是困难的，所以携带需要从心脏（肺动脉）向肺部充氧的血液的血管中的压力升高。这些血管中的压力增加被称为肺动脉高压。（换行）PVOD中涉及的EIF2AK4gene突变可能导致功能蛋白的完全丧失。目前尚不清楚EIF2AK4蛋白功能的缺失如何导致PVOD特有的肺静脉异常。"}, {"name": "肺动脉高压", "detail": "遗传学家庭参考资料提供有关肺动脉高压的信息。"}]}' WHERE id = 14156;
UPDATE genes SET GHR_metadata_ch = '{"gene": "CSTB", "conditions": [{"name": "Unverricht-Lundborg病", "detail": "在几乎所有受影响的个体中，Unverricht-Lundborg病是由CSTBgene中十二聚体重复序列的拷贝数增加（扩增）引起的。大多数患有这种疾病的人在两个CSTBgene拷贝中都有超过30个十二聚体序列重复。(换行)在少数个体中，CSTBgene的一个拷贝具有扩展的十二聚体重复序列，而第二个拷贝则带有其他九个已识别突变之一。这些突变中的一些将半胱氨酸蛋白酶抑制剂B蛋白中的一个蛋白质构建块（氨基酸）替换为另一个氨基酸。其他导致蛋白质缩短，可能功能不正常或根本不起作用，或导致蛋白质拼接不正确。据报道，只有一个患有Unverricht-Lundborg病的个体在每个细胞的两个gene拷贝中都有十二聚体重复扩增以外的突变。（换行）CSTBgene中扩增的十二聚体重复似乎干扰胱抑素B蛋白的产生。受影响个体中半胱氨酸蛋白酶抑制剂B的水平仅为正常水平的5%至10%，组织蛋白酶水平显着增加。据信这些变化会引起Unverricht-Lundborg病的体征和症状，但具体机制尚不清楚。"}]}' WHERE id = 14164;
UPDATE genes SET GHR_metadata_ch = '{"gene": "DKC1", "conditions": [{"name": "先天性角化不良", "detail": "在先天性角化病患者中已鉴定出DKC1gene的40多个突变。这种疾病的特征是肤色变化（色素沉着），口腔内白斑（口腔白斑）以及指甲和脚趾甲异常形成（指甲营养不良）。先天性角化病患者发生几种危及生命的疾病的风险增加，包括癌症和称为肺纤维化的进行性肺病。许多受影响的个体也会出现一种称为再生障碍性贫血的严重疾病，也称为骨髓衰竭，这种情况发生在骨髓不能产生足够的新血细胞时。(换行)导致先天性角化不全的大多数DKC1gene突变会改变dyskerin蛋白中的单个氨基酸。研究人员认为，这些变化可能会干扰dyskerin蛋白与hTR结合的能力，从而导致端粒酶复合物功能异常。(换行)端粒酶功能受损会阻止端粒的正常维持并导致端粒长度减少。快速分裂的细胞特别容易受到端粒缩短的影响。结果，先天性角化病患者可能会遇到各种影响体内细胞快速分裂的问题，如甲床细胞，毛囊，皮肤，口腔内壁（口腔粘膜）和骨髓。（换行）端粒维持不足导致的染色体断裂和不稳定可能导致遗传变化，使细胞以不受控制的方式分裂，导致一些先天性角化不良患者发生癌症。"}]}' WHERE id = 14175;
UPDATE genes SET GHR_metadata_ch = '{"gene": "RNASEH2B", "conditions": [{"name": "Aicardi-Goutières综合征", "detail": "在患有Aicardi-Goutières综合征的人中，已经鉴定出RNASEH2Bgene中至少有76个突变，Aicardi-Goutières综合征是一种涉及严重脑功能障碍（脑病），皮肤病变和其他与免疫系统异常激活相关的健康问题的疾病。导致Aicardi-Goutières综合征的RNASEH2Bgene突变可能导致功能异常的RNase H2复合物。该复合物的异常功能可能破坏转录，DNA复制，DNA修复，细胞死亡（凋亡）或其他过程。这种破坏被认为导致细胞中不需要的DNA和RNA的积累。这些DNA和RNA片段可能被误认为是病毒入侵者的遗传物质，引发多种身体系统的免疫系统反应，导致严重的脑功能障碍（脑病），皮肤损伤以及Aicardi-Goutières综合征的其他体征和症状。"}]}' WHERE id = 14199;
UPDATE genes SET GHR_metadata_ch = '{"gene": "CATSPER2", "conditions": [{"name": "听觉障碍和男性不育", "detail": "The symptoms of sensorineural deafness and male infertility are caused by a deletion of genetic material on the long (q) arm of chromosome 15. The chromosomal region that is typically deleted contains multiple genes, including the CATSPER2 gene. People with this condition have the deletion in both copies of chromosome 15 in each cell. As a result of the deletion, affected individuals are missing both copies of the CATSPER2 gene, and no CATSPER2 protein is produced. A lack of CATSPER2 protein impairs calcium entry into the sperm cell, which decreases motility and prevents hyperactivation. Lack of hyperactivation results in sperm that are unable to push through the membrane of the egg cell and achieve fertilization. These sperm abnormalities are the cause of infertility in affected males.\r"}]}' WHERE id = 14215;
UPDATE genes SET GHR_metadata_ch = '{"gene": "OXCT1", "conditions": [{"name": "琥珀酰辅酶A:3-酮酸辅酶A转移酶缺乏症", "detail": "已发现OXCT1gene中至少有20个突变会导致SCOT缺乏症，这种疾病的特征是极度疲倦，食欲不振和癫痫发作，称为酮症酸中毒发作。大多数OXCT1gene突变会导致SCOT酶中单个蛋白质结构单元（氨基酸）发生变化，并导致该酶几乎没有功能。功能酶量的减少导致无法分解酮，通常导致能量产生减少和血液中酮水平升高。如果这些迹象变得严重，可能会发生酮症酸中毒发作。"}]}' WHERE id = 14220;
UPDATE genes SET GHR_metadata_ch = '{"gene": "LRP5", "conditions": [{"name": "家族性渗出性玻璃体视网膜病变", "detail": "More than 15 mutations in the LRP5 gene have been identified in people with the eye disease familial exudative vitreoretinopathy. Some of these mutations change single protein building blocks (amino acids) in the LRP5 protein, while others insert or delete genetic material in the gene. Most of these mutations reduce the amount of functional LRP5 protein that is produced within cells. (line-break) A reduction in the amount of LRP5 protein disrupts chemical signaling in the developing eye, which interferes with the formation of blood vessels at the edges of the retina. The resulting abnormal blood supply to this tissue can lead to retinal damage and vision loss. Because the LRP5 protein plays a role in bone formation, LRP5 gene mutations also cause reduced bone mineral density in some people with familial exudative vitreoretinopathy.\r"}, {"name": "青少年原发性骨质疏松症", "detail": "在青少年原发性骨质疏松症患者中至少发现了5个LRP5gene突变。患有这种疾病的人从童年开始就具有较低的骨矿物质密度和较薄的骨骼（骨质疏松症）。骨质疏松症导致骨骼变脆并容易断裂，从而导致多处骨折。导致这种情况的LRP5gene突变导致LRP5蛋白无法沿Wnt信号通路传递化学信号。由此产生的信号减少破坏了骨矿物质密度的调节，导致年轻时骨质疏松。"}, {"name": "骨质疏松-假性胶质瘤综合征", "detail": "已经鉴定出40多种导致骨质疏松-假性神经胶质瘤综合征的LRP5gene突变。从童年开始，患有这种疾病的人的骨矿物质密度和骨质疏松症极低，从而导致多处骨折。受影响的人还患有眼部异常，从出生或婴儿早期就引起视力障碍。许多导致骨质疏松-假性神经胶质瘤综合征的LRP5gene突变会阻止细胞产生任何LRP5蛋白。其他突变会改变LRP5蛋白中的单个氨基酸。这些异常蛋白质不能插入细胞的外膜，这使得它们不能执行其功能。LRP5蛋白功能的丧失破坏了骨骼形成和正常视网膜发育所需的化学信号传导途径，从而导致骨质疏松-假性神经胶质瘤综合征的骨骼和眼睛异常。目前尚不清楚为什么某些LRP5gene突变会影响眼睛发育，而另一些则不会。"}, {"name": "其他疾病", "detail": "研究表明，LRP5gene的改变可能会影响成年后患骨质疏松症的风险。其他遗传和环境因素可能导致这种常见疾病。（换行）其他LRP5gene突变导致与骨矿物质密度增加相关的疾病。这些包括1型常染色体显性骨质疏松症和常染色体显性骨硬化症。在某些情况下，这些情况可能导致异常的骨骼生长和相关的骨骼异常。很少，受影响的个体在大脑中有听力损失或循环问题。其他骨密度增加的人没有任何相关的健康问题。导致骨矿物质密度综合征增加的突变会过度激活LRP5蛋白，从而增加细胞内Wnt信号传导并增强骨形成。"}]}' WHERE id = 14234;
UPDATE genes SET GHR_metadata_ch = '{"gene": "NHLRC1", "conditions": [{"name": "Lafora进行性肌阵挛性癫痫", "detail": "在Lafora进行性肌阵挛性癫痫患者中，已鉴定出NHLRC1gene的45个以上突变。这些突变中的许多改变了malin蛋白中的单个蛋白结构单元（氨基酸）。其他突变会删除或插入NHLRC1gene中的遗传物质。该gene中的几乎所有突变都阻止细胞产生任何马林蛋白或导致该蛋白的非功能性形式的产生。（换行）最常见的NHLRC1gene突变用malin蛋白中第69位的氨基酸丙氨酸取代氨基酸脯氨酸（写为Pro69Ala或P69A）。在葡萄牙，意大利和西班牙血统的许多受影响个体中都发现了这种突变。第二个最常见的NHLRC1gene突变在制造马林（写为Gly158Ter或G158X）的说明中用过早的终止信号替换了氨基酸甘氨酸。在几个不同种族的受影响个体中都发现了这种突变。（换行）目前尚不清楚NHLRC1gene的突变如何导致Lafora进行性肌阵挛性癫痫的主要特征。研究表明，马林蛋白的缺失会阻止细胞调节糖原的产生。结果，在许多类型的细胞内形成了称为Lafora体的独特团块。Lafora身体由异常形式的糖原（称为聚葡聚糖）组成，无法分解并用作燃料。相反，聚葡聚糖聚集形成可破坏细胞的团块。神经元似乎特别容易受到这种类型的损害。尽管在人体的许多组织中都发现了Lafora尸体，但Lafora进行性肌阵挛性癫痫的体征和症状仅限于神经系统。(换行)研究人员不确定功能性马林蛋白的丧失如何导致Lafora体的形成。但是，缺乏这种蛋白质最终会导致神经元死亡，从而干扰大脑的正常功能。神经元变性可能是Lafora进行性肌阵挛性癫痫发作，运动异常，智力下降和其他神经系统问题的基础。"}]}' WHERE id = 14240;
UPDATE genes SET GHR_metadata_ch = '{"gene": "TPMT", "conditions": [{"name": "硫嘌呤S-甲基转移酶缺乏症", "detail": "TPMTgene的变化导致TPMT缺乏，这是TPMT酶活性的降低。没有足够的这种酶，人体就不能通过将硫嘌呤代谢成无活性的化合物来“关闭”硫嘌呤药物。药物在体内停留的时间更长，并继续破坏未经检查的细胞，导致骨髓损伤（造血毒性）。这种损伤导致骨髓抑制，骨髓不能产生足够的红细胞，白细胞和血小板。这些细胞的短缺会导致各种健康问题，其中最严重的问题包括异常出血和潜在威胁生命的感染风险增加。尽管任何服用硫嘌呤类药物的人都可能发生造血毒性，但TPMT缺乏症患者发生这种并发症的风险最高。（换行）TPMTgene可分为低活性或高活性。当以损害TPMT酶活性的方式改变gene时，将其描述为低活性。当gene未改变且TPMT活性正常时，将其描述为高活性。因为每个细胞中存在该gene的两个拷贝，所以每个人可以具有两个低活性拷贝，一个低活性拷贝和一个高活性拷贝，或两个高活性拷贝。在每个细胞中具有两个TPMTgene低活性拷贝的人都有TPMT缺乏症，除非用硫嘌呤类药物治疗，否则发生造血毒性的风险最大，除非给予的剂量远低于通常的剂量。具有一个高活性拷贝和一个低活性拷贝的人具有适度降低的酶活性，并且除非给予显着较低剂量的药物，否则这种并发症的风险也增加。具有两个高活性拷贝的人具有正常的TPMT活性，并且用硫嘌呤药物治疗不会增加造血毒性的风险。(换行)在TPMT缺乏症患者中发现了40多个TPMTgene的低活性版本（等位gene）。这些等位gene中的每一个都包含gene中的一个或多个变化，这些变化会降低TPMT酶的稳定性和活性。TPMT*3A和TPMT*3C这两个特定的等位gene占该病病例的90%以上。研究表明，TPMT*3A是白人中最常见的低活性等位gene，而TPMT*3C是亚洲人，非洲人和非裔美国人中最常见的低活性等位gene。"}]}' WHERE id = 14247;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SLC20A2", "conditions": [{"name": "家族性特发性基底节钙化", "detail": "已发现约20个SLC20A2gene突变引起家族性特发性基底神经节钙化（FIBGC）。这种情况的特征是大脑中钙的异常沉积（钙化）以及运动和精神问题。导致FIBGC的大多数突变会改变PiT-2蛋白中的单个蛋白结构单元（氨基酸），并严重损害其将磷酸盐转运到细胞中的能力。结果，血液中的磷酸盐水平上升。在大脑中，过量的磷酸盐与钙结合形成沉积物。(换行)尽管SLC20A2gene在整个身体中都是活跃的（表达），但其活性在基底神经节和FIBGC涉及的其他大脑区域最高，这可以解释为什么这些突变的影响仅限于这些区域。"}]}' WHERE id = 14251;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ALDH3A2", "conditions": [{"name": "Sjögren-Larsson综合征", "detail": "Several mutations in ALAS2 gene are found to cause Sjögren-Larsson syndrome, a condition characterized by ichthyosis, neurological abnormalities and eye problems resulted due to defective FALDH enzyme which fails to break down fatty aldehyde molecules leading to its accumulation in cells. "}]}' WHERE id = 14277;
UPDATE genes SET GHR_metadata_ch = '{"gene": "APOA1", "conditions": [{"name": "家族性HDL缺乏症", "detail": "APOA1gene的突变会导致家族性HDL缺乏症，这是一种遗传性疾病，其特征是血液中HDL含量低，早发性心血管疾病的风险升高，通常发生在50岁之前。这些突变导致apoA-I蛋白改变。一些版本的改变的蛋白质不太能够促进从细胞中去除胆固醇和磷脂，这减少了可用于形成HDL的这些物质的量。其他版本的改变的蛋白质不太能够刺激胆固醇酯化，这意味着胆固醇不能被整合到HDL颗粒中。两种类型的突变都会导致HDL水平降低。HDL的短缺（缺乏）被认为会增加心血管疾病的风险。"}, {"name": "其他疾病", "detail": "APOA1gene的突变也可引起称为家族性内脏淀粉样变性的病症，其特征在于内部器官（内脏）中蛋白质（淀粉样变性）的异常积累。导致这种情况的突变会改变apoA-I蛋白。异常的apoA-I蛋白粘在一起形成淀粉样蛋白沉积物，损害受影响器官的功能。肝脏，肾脏和心脏通常受淀粉样变性的影响。根据所涉及的器官，病症的体征和症状各不相同。受影响的个体可能患有肝脏肿大（肝肿大），慢性肾脏疾病或一种称为心肌病的心脏病。但是，在某些人中，病情非常轻微，没有明显的体征或症状。"}]}' WHERE id = 14290;
UPDATE genes SET GHR_metadata_ch = '{"gene": "MAOA", "conditions": [{"name": "单胺氧化酶A缺乏症", "detail": "MAOAgene中的突变导致单胺氧化酶A缺乏。这种情况几乎只影响男性，其特征是轻度智力残疾和行为问题，包括攻击性和暴力爆发。在某些情况下，特定食物似乎会使病情恶化。MAOAgene突变会降低单胺氧化酶A的活性，从而导致血清素和其他神经递质在大脑中积聚。目前还不清楚这种累积是如何导致单胺氧化酶A缺乏的症状和体征的。研究人员推测，过量的某些神经递质，特别是5-羟色胺和去甲肾上腺素，可能会损害受影响个体控制冲动的能力，导致攻击性爆发。一些研究表明，单胺氧化酶A活性降低会改变大脑某些区域的发育，这可能导致单胺氧化酶A缺乏症患者的智力残疾和行为问题。研究人员怀疑酪胺的积累可能导致与疾病相关的问题，这可能是为什么这种分子含量高的食物会使症状恶化的原因。"}, {"name": "其他疾病", "detail": "Genetic changes that affect the MAOA gene have been linked to multiple disorders. Some of these genetic changes remove pieces of DNA (deletion mutations) that include the MAOA gene. Deletion mutations that remove both the MAOA gene and the nearby MAOB gene have been found in individuals with severely delayed development of mental and motor skills, weak muscle tone (hypotonia), and repetitive hand movements. Deletion mutations that remove these two genes and another nearby gene called NDP have also been found. The NDP gene is associated with a condition called Norrie disease, which causes blindness and sometimes mild developmental delays and problems with other body systems. Individuals missing the MAOA, MAOB, and NDP genes have severe intellectual disability, difficulty with social interactions (autism spectrum disorders), and seizures in addition to features of Norrie disease. Researchers speculate that loss of the MAOA or MAOB gene underlies the neurological problems in individuals with deletion mutations. (line-break) Several common genetic variants (polymorphisms) in or near the MAOA gene have been found to affect the gene''s activity. The most studied polymorphism, called MAOA-uVNTR, occurs in an area near the MAOA gene, called the promoter region, that controls gene activity. In this region, a string of 30 DNA building blocks (nucleotides) is repeated, end-to-end, two to five times. Studies show that when the string of nucleotides is repeated 3.5 or four times, more monoamine oxidase A protein is produced than when the nucleotides are repeated only two or three times. For this reason, versions of DNA (alleles) with 3.5 or four repeats are referred to as high-activity alleles. Versions with only two or three repeats, which result in lower than normal amounts of monoamine oxidase A, are considered low-activity alleles. It is unclear what effect five repeats has on MAOA gene activity. (line-break) Low-activity MAOA-uVNTR alleles are associated with aggressive behavior. Several reports found the effect only in males, but some other reports indicate that both males and females with low-activity alleles can be prone to aggression. Some studies indicate that low-activity alleles specifically increase the risk of violence and aggression in individuals who were abused as children. Researchers are studying how these MAOA gene polymorphisms are involved in modulating behavior and the role of environmental factors, such as childhood abuse or situations in which a person is provoked. (line-break) In contrast, high-activity MAOA-uVNTR alleles appear to increase the risk of panic disorder in females. Panic disorder is a severe anxiety disorder characterized by episodes of overwhelming fear (panic attacks) with no obvious trigger. It is unclear how high amounts of monoamine oxidase A contribute to panic disorder. (line-break) Other polymorphisms that can affect MAOA gene activity may also be associated with aggression. The roles of MAOA-uVNTR and other polymorphisms are also being studied in depression, bipolar disorder, alcoholism, drug addiction, and many other conditions.\r"}]}' WHERE id = 14292;
UPDATE genes SET GHR_metadata_ch = '{"gene": "MCCC2", "conditions": [{"name": "3-methylcrotonyl-CoA carboxylase deficiency", "detail": "More than 40 mutations in the MCCC2 gene have been identified in people with 3-methylcrotonyl-CoA carboxylase deficiency (also known as 3-MCC deficiency). Most of these mutations change single amino acids in 3-MCC, which severely reduces the activity of the enzyme. Other mutations prevent the production of any functional enzyme. As a result, leucine cannot be broken down properly, and byproducts of leucine processing build up to toxic levels in the body. These toxic substances can damage the brain, causing the characteristic signs and symptoms of 3-MCC deficiency.\r"}]}' WHERE id = 14295;
UPDATE genes SET GHR_metadata_ch = '{"gene": "MYH9", "conditions": [{"name": "MYH9相关疾病", "detail": "已发现MYH9gene中超过45个突变引起MYH9相关疾病。这种疾病的特征是出血问题，听力下降，肾脏（肾脏）疾病和眼睛晶状体混浊（白内障）。导致这种情况的大多数突变都会改变肌球蛋白9蛋白中的单个蛋白结构单元（氨基酸）。与位于蛋白质尾部的突变相比，位于肌球蛋白头部附近的突变倾向于导致更严重的疾病。涉及蛋白质中702位氨基酸精氨酸的重复突变往往会导致许多问题，包括血小板数量严重减少（血小板减少症），早发性肾脏疾病和婴儿期听力下降。MYH9gene中的（换行）突变导致产生无功能的蛋白质。无功能的肌球蛋白9蛋白无法与其他亚基正确相互作用形成肌球蛋白IIA。仅表达肌球蛋白IIA的血小板受功能性肌球蛋白9缺乏的影响最大，这是所有患有MYH9相关疾病的个体所见的血小板减少症。"}, {"name": "非综合征性听力损失", "detail": "遗传学家庭参考资料提供有关非综合征性听力损失的信息。"}]}' WHERE id = 14307;
UPDATE genes SET GHR_metadata_ch = '{"gene": "FIG4", "conditions": [{"name": "进行性神经性腓骨肌萎缩症", "detail": "在患有Charcot-Marie-Tooth疾病4J型的个体中，已经鉴定出FIG4gene中至少有五个突变。这些突变之一用41位的氨基酸苏氨酸（写为Ile41Thr或I41T）取代了蛋白质结构单元（氨基酸）异亮氨酸。已在每个患有4J型Charcot-Marie-Tooth病的人的FIG4gene的一个副本中鉴定出此突变。另外，每个个体在其FIG4gene的另一个拷贝中具有另一个突变。这些突变改变了蛋白质序列中的单个氨基酸或导致异常短的无功能蛋白质。研究人员正在努力确定FIG4gene的突变如何导致4J型Charcot-Marie-Tooth病的特定体征和症状。"}, {"name": "肌萎缩侧索硬化症", "detail": "遗传学家庭参考提供有关肌萎缩侧索硬化的信息。"}]}' WHERE id = 14329;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ACOX1", "conditions": [{"name": "过氧化物酶体酰基辅酶A氧化酶缺乏症", "detail": "在过氧化物酶体酰基辅酶A氧化酶缺乏症的个体中已鉴定出ACOX1的几种突变，该突变是由于酶缺陷引起的，该酶抑制VLCFA分解代谢导致其积累。这种缺陷的特征是神经系统炎症，髓鞘破坏导致白细胞营养不良和其他一些神经系统异常。"}]}' WHERE id = 14331;
UPDATE genes SET GHR_metadata_ch = '{"gene": "RGS9BP", "conditions": [{"name": "运动迟缓", "detail": "已发现RGS9BPgene中至少有六个突变会引起运动迟缓，这是一种罕见的影响视力的疾病。在患有弱视症的人中，眼睛比平时更适应不断变化的光线conditions（例如，在阳光明暗的日子里从黑暗的电影院走到日光下或开车进入黑暗的隧道）。一些受影响的人也很难看到小的移动物体，例如网球。（换行）导致运动迟缓的RGS9BPgene突变阻止RGS9结合蛋白将RGS9蛋白锚定到感光细胞。由此导致的RGS9蛋白功能丧失阻止了光感受器在响应光后迅速恢复。通常，它们会在一秒钟内恢复到静止状态，但在具有RGS9BPgene突变的人中，可能需要十秒或更长时间。在此期间，光感受器不能响应光。这种延迟会导致暂时失明，以响应不断变化的光线conditions，并可能干扰看到小物体运动时的物体。"}]}' WHERE id = 14343;
UPDATE genes SET GHR_metadata_ch = '{"gene": "WDR19", "conditions": [{"name": "颅外胚层发育不良", "detail": "在颅外胚层发育不良的个体中发现了WDR19gene的至少两个突变。这种情况的特征是头部拉长（头畸形），额头突出，面部特征其他独特；短骨头；以及某些称为外胚层组织的组织异常，包括牙齿，头发，指甲和皮肤。颅外胚层发育不良还可能引起多种其他问题，包括称为肾炎的肾脏疾病和眼睛异常。（换行）涉及颅外胚层发育不良的WDR19gene突变减少了功能性WDR19蛋白的量。IFT-A复合物这一成分的缺乏或活性降低会损害整个复合物的功能，从而破坏纤毛尖端蛋白质和物质的运输。结果，纤毛的组装和维护受到损害，这导致纤毛数量减少以及其形状和结构异常。尽管机制尚不清楚，但正常纤毛的丧失会阻碍骨骼和其他组织的正常发育，从而导致颅外胚层发育不良的特征。一些研究人员认为，鞭毛内运输受阻会阻止通过声波刺猬通路的信号传导，这可能会影响整个身体多个组织中的细胞生长和其他功能。"}, {"name": "窒息性胸营养不良", "detail": "遗传学家庭参考提供有关窒息性胸肌营养不良的信息。"}, {"name": "肾单位", "detail": "遗传学家庭参考提供有关肾炎的信息。"}, {"name": "视网膜色素变性", "detail": "遗传学家庭参考提供有关视网膜色素变性的信息。"}, {"name": "高级Løken综合征", "detail": "遗传学家庭参考资料提供有关高级Løken综合征的信息。"}]}' WHERE id = 14345;
UPDATE genes SET GHR_metadata_ch = '{"gene": "OPA3", "conditions": [{"name": "Costeff综合征", "detail": "已经发现OPA3gene中至少三个突变引起Costeff综合征。这种情况的特征是视力丧失，运动问题和智力残疾。Costeff综合征是由每个细胞中OPA3gene的两个拷贝中的突变引起的，导致OPA3蛋白功能丧失。在伊拉克犹太人口中引起Costeff综合征的OPA3gene突变（写为143-1G>C）改变了OPA3gene指令整合在一起制备蛋白质的方式，从而导致功能性蛋白质的缺乏。没有任何功能性OPA3蛋白的细胞线粒体形状异常。这些细胞可能比正常情况下减少了能量产生并更快死亡，从而降低了人体组织中的能量利用率。目前还不清楚为什么控制视觉和运动的细胞受到特别影响。"}, {"name": "其他疾病", "detail": "OPA3gene突变也可引起常染色体显性视神经萎缩和白内障（ADOAC）。患有这种情况的人的视神经变性（萎缩），将信息从眼睛传递到大脑，并且眼睛的晶状体混浊（白内障）。这些视力问题通常发生在双眼中，并且可以从童年到成年早期的任何时候开始。一些受影响的人还有其他特征，例如听力下降或运动问题。(换行)患有ADOAC的人每个细胞中OPA3gene的一个拷贝都有突变。导致ADOAC的突变可能导致异常OPA3蛋白的产生，从而干扰细胞的功能。这些细胞可能具有减少的能量产生，导致早期细胞死亡。目前还不清楚为什么眼睛内的细胞受到特别的影响。"}]}' WHERE id = 14346;
UPDATE genes SET GHR_metadata_ch = '{"gene": "TPM2", "conditions": [{"name": "帽肌病", "detail": "在患有帽肌病的人中至少鉴定出三个TPM2gene突变，帽肌病是一种导致肌肉无力（肌病）和肌张力差（肌张力低下）的疾病。这些突变删除或复制了TPM2gene中的遗传物质，或替换了TPM2gene中的单个蛋白质结构单元（氨基酸）-tropomyosin蛋白质序列。这些TPM2gene突变的具体作用尚不清楚，但研究人员建议它们可能会干扰正常的肌动蛋白-肌球蛋白结合，损害肌肉收缩并导致帽肌病中发生的肌肉无力。"}, {"name": "1型远端关节置换术", "detail": "已发现TPM2gene中至少有3个突变会导致1型远端关节置换术，这种疾病的特征是手脚关节畸形（挛缩）。目前尚不清楚这些突变如何导致1型远端关节置换术患者挛缩，或者为什么关节问题通常仅限于手和脚。但是，研究人员推测，挛缩可能与肌肉收缩问题有关，这些问题限制了出生前关节的运动。"}, {"name": "先天性纤维类型不均衡", "detail": "遗传学家庭参考资料提供有关先天性纤维类型不均衡的信息。"}, {"name": "Nemaline myopathy", "detail": "遗传学家庭参考资料提供有关肾上腺肌病的信息。"}, {"name": "谢尔顿-霍尔综合征", "detail": "在Sheldon-Hall综合征患者中已鉴定出至少六个TPM2gene突变，Sheldon-Hall综合征是一种类似于1型远端关节置换术（如上所述）的肌肉和骨骼疾病，会损害手和脚的关节运动。TPM2gene的突变可能会改变-原肌球蛋白并破坏蛋白质在控制肌肉收缩中的正常功能，从而导致挛缩以及与这种情况相关的其他肌肉和骨骼异常。"}]}' WHERE id = 14373;
UPDATE genes SET GHR_metadata_ch = '{"gene": "MITF", "conditions": [{"name": "蒂兹综合症", "detail": "在患有Tietz综合征的人中至少鉴定出两个MITFgene突变，其特征是出生时听力严重丧失，皮肤白皙和头发浅色。研究人员建议，Tietz综合征可能是Waardenburg综合征的一种严重形式（如下所述）。（换行）导致Tietz综合征的MITFgene突变删除或改变黑素生成相关转录因子结构的基本基序区域中的单个蛋白质构建块（氨基酸）。掺入异常黑素生成相关转录因子的二聚体不能转运到细胞核中与DNA结合。结果，大多数二聚体不能与DNA结合，这影响黑素细胞的发育和黑色素的产生。由此导致的内耳黑素细胞减少或缺失导致听力损失。黑色素生成减少（色素沉着不足）是Tietz综合征特征性的浅色皮肤和头发颜色。"}, {"name": "瓦尔登堡综合症", "detail": "在患有II型Waardenburg综合征的人中，已经鉴定出MITFgene的35个以上突变，该疾病可导致听力丧失以及头发，皮肤和眼睛的颜色（色素沉着）发生变化。一些MITFgene突变改变了用于制造黑素生成相关转录因子的氨基酸，其改变了螺旋-环-螺旋或亮氨酸拉链基序。其他突变导致蛋白质的异常小版本。研究人员认为，这两种类型的突变都会破坏二聚体的形成。尽管产生了一些二聚体，但该量不足以完全发育黑素细胞。结果，皮肤，头发，眼睛和内耳的某些区域缺乏黑素细胞。这种短缺导致与Waardenburg综合征相关的听力损失和色素沉着的斑片状损失。"}, {"name": "癌症", "detail": "在患有称为黑素瘤的侵袭性皮肤癌的人中也发现了MITFgene突变。这些突变大多数是体细胞突变，这意味着它们发生在一个人的一生中，并且仅存在于某些细胞中，在这种情况下会引起黑色素瘤。有时该突变是遗传的，并在人体的每个细胞中发现（称为种系突变）。一些与黑色素瘤相关的MITFgene突变直接增加细胞生长和分裂（增殖）。其他突变具有间接作用，增加参与增殖的其他gene的活性（表达），并导致黑素瘤中发生的异常细胞生长。"}]}' WHERE id = 14390;
UPDATE genes SET GHR_metadata_ch = '{"gene": "NDP", "conditions": [{"name": "家族性渗出性玻璃体视网膜病变", "detail": "已经发现几种NDPgene突变引起眼部疾病家族性渗出性玻璃体视网膜病变。这些突变改变了norrin蛋白中的单个蛋白结构单元（氨基酸），改变了norrin的正常折叠或阻止了它与frizzled-4的结合。有缺陷的norrin破坏了发育中的眼睛中的化学信号传导，从而干扰了视网膜边缘血管的形成。在一些家族性渗出性玻璃体视网膜病变患者中，由此导致的对该组织的异常血液供应导致视网膜损伤和视力丧失。"}, {"name": "诺里病", "detail": "在诺里氏病患者中已鉴定出NDPgene的75个以上突变。这些突变影响norrin蛋白与frizzled-4结合的能力，从而干扰视网膜细胞独特的感觉功能。结果，大量未成熟的视网膜细胞积聚在眼后部。norrin在建立供应眼睛的血管中的作用中断最终导致一些组织分解。(换行)Norrin也表达在身体的其他系统中，该疾病的影响可能很普遍，包括智力残疾，癫痫发作，行为问题和发育迟缓。特定异常及其严重程度取决于NDPgene突变的类型和位置。删除NDPgene部分的突变会阻止norrin的产生，并导致严重的问题，影响眼睛以外的许多身体系统。删除或改变单个氨基酸的突变通常会导致较不广泛的影响。"}, {"name": "其他视网膜营养不良", "detail": "NDPgene突变可能导致影响视网膜的其他疾病。一种突变与称为Coats病的疾病有关。这种疾病导致视网膜血管渗漏和视网膜脱离，视网膜各层分离，导致视力丧失。持续性增生性原发性玻璃体（PHPV）是另一种可能由NDPgene突变引起的视网膜疾病。在持续增生的原发性玻璃体中，出生前在眼睛中发现的血管残留物在眼后部和晶状体之间仍为纤维状白色茎。持续增生的原发性玻璃体可通过视网膜脱离，晶状体混浊（白内障）或眼内压力增加（青光眼）导致视力丧失，从而损害视神经。(换行)此外，NDPgene突变可能会影响影响某些早产儿的视网膜疾病进程。早产儿视网膜病变是视网膜中出现异常血管并可引起视网膜脱离的病症。早产儿视网膜病变的婴儿可能会随着时间的推移而病情改善，但是一些NDPgene突变与病情恶化有关。"}]}' WHERE id = 14391;
UPDATE genes SET GHR_metadata_ch = '{"gene": "BCS1L", "conditions": [{"name": "Björnstad综合征", "detail": "已发现BCS1Lgene中的几个突变会引起Björnstad综合征，这种疾病的特征是头发异常，称为pili torti（或“扭曲的头发”）和听力下降。这种情况是由于蛋白质改变而引起的，这会损害Rieske Fe/S蛋白质向复合物III的添加，从而导致其积累并减少线粒体氧化磷酸化。"}, {"name": "GRACILE综合征", "detail": "已经发现BCS1Lgene中的几个突变会引起GRACILE综合征，这是一种严重的疾病，其特征是受影响的患有肾脏和肝脏问题的婴儿，体内铁水平升高，并且在出生后不能存活超过几个月。这种情况是由于蛋白质异常引起的，从而减少了线粒体中复合物III的产生和氧化磷酸化。"}, {"name": "线粒体复合体III缺乏", "detail": "已发现BCS1Lgene中的几个突变会导致线粒体复合物III缺乏，这是一种以肝病，肾小管病和脑病为特征的疾病。这种情况是由于线粒体数量增加和活性氧产生增加以及细胞缺乏能量。"}, {"name": "利综合征", "detail": "BCS1Lgene的一些突变与称为Leigh综合征的神经系统疾病有关，Leigh综合征是一种以精神运动退化，呼吸衰竭，failure壮衰竭，肌张力低下，肌张力障碍和共济失调为特征的疾病。"}]}' WHERE id = 14408;
UPDATE genes SET GHR_metadata_ch = '{"gene": "TBC1D24", "conditions": [{"name": "DOORS综合征", "detail": "在DOORS综合征患者中已鉴定出TBC1D24gene至少10个突变，DOORS综合征是一种涉及出生时出现的多种异常（先天性）的疾病。“DOORS”是包括耳聋在内的疾病主要特征的缩写；指甲短或缺失（甲状肌营养不良）；手指和脚趾短（骨营养不良）；发育迟缓和智力障碍（以前称为智力低下）；和癫痫发作。一些患有DOORS综合征的人并不具有所有这些功能。（换行）导致DOORS综合征的大多数TBC1D24gene突变都会改变TBC1D24蛋白序列中的单个蛋白结构单元（氨基酸）。这些突变被认为降低或消除了TBC1D24蛋白的功能，但是TBC1D24功能丧失导致DOORS综合征的体征和症状的具体机制尚不清楚。"}, {"name": "婴儿恶性移行性部分性癫痫发作", "detail": "遗传学家庭参考提供有关婴儿恶性迁移部分性癫痫发作的信息。"}, {"name": "非综合征性听力损失", "detail": "遗传学家庭参考资料提供有关非综合征性听力损失的信息。"}, {"name": "其他疾病", "detail": "TBC1D24gene突变也已在其他癫痫发作患者中发现，包括家族性婴儿肌阵挛性癫痫（FIME），进行性肌阵挛性癫痫（PME）和一种早期婴儿癫痫性脑病（EIEE16；也称为婴儿恶性迁移性部分性癫痫发作16）。这些突变可能导致与大脑发育有关的TBC1D24蛋白功能受损，但突变与这些疾病之间的具体联系尚不清楚。"}]}' WHERE id = 14410;
UPDATE genes SET GHR_metadata_ch = '{"gene": "PPP2R5D", "conditions": [{"name": "PPP2R5D相关的智力障碍", "detail": "已发现至少七个PPP2R5Dgene突变会导致中度至重度智力障碍。与PPP2R5D相关的智力障碍患者通常也具有较弱的肌张力（肌张力低下）以及言语和行走发育延迟。这些突变影响gene的一个拷贝。它们改变了B56中的单个蛋白质构建块（氨基酸）？蛋白质。尽管这些变化的影响尚不清楚，但研究人员怀疑它们会改变或损害PP2A酶的活性。PP2A酶活性异常或降低被认为会破坏神经元的信号传导途径，损害其正常发育和功能，这可能是智力障碍和PPP2R5D相关智力障碍的其他特征的基础。"}]}' WHERE id = 14419;
UPDATE genes SET GHR_metadata_ch = '{"gene": "CYP2C19", "conditions": [{"name": "氯吡格雷抵抗", "detail": "CYP2C19gene的多种变异（多态性）与氯吡格雷耐药有关，氯吡格雷耐药是一种药物氯吡格雷在接受治疗的人群中效果不如正常的情况。与氯吡格雷耐药相关的多态性降低了酶转化的能力。药物为其活性形式。（线条中断）gene的正常版本，编写为CYP2C19*1，提供了生产正常功能的CYP2C19酶的说明。如果一个人在每个细胞中都有CYP2C19*1gene版本的两个副本，他们就能够正常转换氯吡格雷。与氯吡格雷耐药相关的两种最常见的CYP2C19gene多态性（称为CYP2C19*2和CYP2C19*3）导致产生不能激活氯吡格雷的非功能性CYP2C19酶。(换行)氯吡格雷耐药个体可分为两组：中间代谢者或代谢不良者。拥有该gene的一个CYP2C19*1版本和该gene的CYP2C19*2或CYP2C19*3版本的一个副本的人将氯吡格雷转化为其活性形式的能力降低，并被归类为中间代谢者。具有两个gene拷贝的gene的CYP2C19*2或CYP2C19*3版本的人可以很少或根本不转化药物，并且被归类为不良代谢者。由于氯吡格雷耐药患者氯吡格雷转化为活性形式受损，因此该药无法抑制P2RY12受体功能。没有活性氯吡格雷干扰，P2RY12受体继续促进血小板聚集和血凝块形成，这可能导致有这些病史的个体发生心脏病发作，中风和血栓形成。(换行)重要的是要注意，并非所有具有CYP2C19gene突变的个体都具有氯吡格雷抗性。这些发生氯吡格雷耐药风险增加的个体在用药物治疗时可能会或可能不会有不良反应。除了特定gene的变化外，许多其他因素，包括性别，年龄，体重，饮食和其他药物，都会在身体对氯吡格雷的反应中发挥作用。"}, {"name": "其他疾病", "detail": "CYP2C19gene中与氯吡格雷耐药相关的多态性（如上所述）也可能影响其他药物的加工。由于CYP2C19酶参与许多药物的代谢，因此该酶的变化可产生广泛的影响。受CYP2C19gene多态性影响的其他药物包括质子泵抑制剂，用于治疗胃溃疡和其他疾病；抗抑郁药，用于治疗精神疾病；抗惊厥药，用于治疗癫痫发作；催眠药和镇静剂，用作助睡剂；用于预防疟疾感染的抗疟药物；和抗逆转录病毒药物，用于防止病毒复制。（换行）最常见的是，CYP2C19gene的变化导致这些药物的代谢受损，这降低了它们的有效性。CYP2C19gene（称为CYP2C19*17）的一个变化增加了酶代谢药物的能力。具有两个拷贝的CYP2C19*17多态性的个体通常被归类为超快速代谢者。"}]}' WHERE id = 14421;
UPDATE genes SET GHR_metadata_ch = '{"gene": "HSD3B7", "conditions": [{"name": "先天性胆汁酸合成缺陷1型", "detail": "已发现HSD3B7gene中至少有17个突变会导致1型先天性胆汁酸合成缺陷。这种情况的特征是胆汁淤积，这种情况会损害肝细胞产生和释放称为胆汁的消化液。大多数HSD3B7gene突变从gene中删除一个或两个DNA构建块（碱基对）或替换酶中的单个蛋白质构建块（氨基酸）。这些突变导致产生3-HSD7酶几乎没有功能。没有足够的功能3-HSD7酶，转化率为7-羟基胆固醇到7-羟基-4-胆甾烯-3-酮受损。7-相反，羟基胆固醇会转化为异常的胆汁酸化合物，这些化合物不能从肝脏转运到肠道，在肠道中需要胆汁酸来吸收脂肪和脂溶性维生素。胆汁酸的产生和释放受损会导致胆汁淤积。结果，肝脏中积聚了胆固醇和异常胆汁酸，并且脂溶性维生素未被吸收，从而导致1型先天性胆汁酸合成缺陷的体征和症状。"}]}' WHERE id = 14458;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SLC11A2", "conditions": [{"name": "铁超负荷的低色素性小细胞性贫血", "detail": "已发现SLC11A2gene中至少有七个突变会导致铁超负荷的低色素性小细胞性贫血。这种情况的特征是出生时明显缺乏红细胞（贫血）。产生的红细胞异常小（小细胞）和苍白（低色）。肝脏中铁也逐渐积累。（换行）导致这种情况的大多数SLC11A2gene突变改变了DMT1蛋白中的单个蛋白质构建块（氨基酸）。这些突变导致DMT1蛋白产量减少，蛋白功能降低或蛋白到达细胞中正确位置的能力受损。在成红细胞中，即使血液中含有丰富的铁，DMT1蛋白的缺乏也会减少细胞内转运的铁量。结果，健康红细胞的发育受损，导致这些细胞短缺。在十二指肠中，DMT1蛋白的缺乏会降低铁的吸收。为了补偿，细胞增加了功能性DMT1蛋白的产量，从而增加了铁的吸收。由于红细胞不能利用被吸收的铁，它会积聚在肝脏中，最终损害肝功能。红细胞中铁的缺乏和肝脏中铁的积累导致铁超负荷的低色素性小细胞性贫血的体征和症状。"}]}' WHERE id = 14459;
UPDATE genes SET GHR_metadata_ch = '{"gene": "CHST3", "conditions": [{"name": "CHST3相关的骨骼发育不良", "detail": "已发现CHST3gene突变会导致CHST3相关的骨骼发育不良，这种疾病的特征是进行性骨，关节和软骨异常，例如：；身材矮小，关节脱位。这是由于缺陷或无功能的C6ST-1酶引起的，该酶降低或消除了其活性，从而破坏了软骨和骨骼的硫酸化和正常发育。"}]}' WHERE id = 14461;
UPDATE genes SET GHR_metadata_ch = '{"gene": "TSC1", "conditions": [{"name": "淋巴管平滑肌瘤病", "detail": "TSC1gene的突变可引起称为淋巴管平滑肌瘤病（LAM）的疾病，尽管TSC2gene的突变似乎是造成该疾病大多数情况的原因。这种破坏性的肺部疾病是由肺部平滑肌样组织的异常过度生长引起的。它几乎只发生在女性身上，引起咳嗽，呼吸短促，胸痛和肺部塌陷。（换行）LAM可以单独发生（分离的或散发的LAM）或与称为结节性硬化症复合体（如下所述）的病症组合发生。研究人员建议，散发性LAM可能是由发育早期发生的TSC1gene随机突变引起的。结果，身体的一些细胞具有正常版本的gene，而其他细胞具有突变版本。这种情况称为马赛克。当女性一生中某些细胞中TSC1gene的另一个拷贝发生突变（体细胞突变）时，她可能会发展为LAM。"}, {"name": "结节性硬化症", "detail": "在患有结节性硬化症的个体中已鉴定出TSC1gene的400多个突变，该疾病的特征是发育问题和身体许多部位非癌性肿瘤的生长。这些突变大多数涉及TSC1gene中DNA的小缺失或插入。某些突变会在制作ha素的说明中产生过早的停止信号。(换行)患有TSC1相关结节性硬化症复合体的人出生时每个细胞中都有一个TSC1gene的突变拷贝。这种突变阻止了细胞从该gene的拷贝中产生功能性哈马汀。但是，通常从TSC1gene的另一个正常拷贝中产生足够的hamartin，以有效调节细胞生长。对于某些类型的肿瘤发展，涉及该gene的另一个拷贝的第二个突变必须在人的一生中在某些细胞中发生。（换行）当TSC1gene的两个拷贝在特定细胞中突变时，该细胞不能产生任何功能性hamartin。这种蛋白质的损失使得细胞以不受控制的方式生长和分裂以形成肿瘤。ha素的缺乏也会干扰某些细胞的正常发育。在患有TSC1相关性结节性硬化症的人中，第二个TSC1gene突变通常发生在受影响人一生中的多个细胞中。在不同类型的细胞中失去ha素会破坏正常发育，并导致许多不同器官和组织中肿瘤的生长。"}, {"name": "膀胱癌", "detail": "膀胱细胞中发生的TSC1gene的体细胞突变与某些膀胱癌病例有关。在其中一些细胞中，缺少一个拷贝的TSC1gene，因为包含该gene的9号染色体区域已被删除；该gene的另一个拷贝具有干扰其功能的突变。结果，很少或没有产生ha素。膀胱细胞中ha素的丢失可能使这些细胞在没有控制的情况下生长和分裂，从而导致癌性肿瘤的形成。（换行）尚不清楚为什么遗传的TSC1gene突变导致结节性硬化症复合体的非癌性生长，而该gene的体细胞突变与癌性肿瘤的发展有关。"}, {"name": "其他疾病", "detail": "TSC1gene中的遗传突变可引起称为泰勒球囊细胞类型的局灶性皮质发育不良的疾病。这种疾病涉及大脑的畸形，大脑的大脑额叶部分负责思考和学习。局灶性皮质发育不良会在受影响的个体中引起严重的反复发作（癫痫）。"}]}' WHERE id = 14472;
UPDATE genes SET GHR_metadata_ch = '{"gene": "CYP2C9", "conditions": [{"name": "华法林敏感性", "detail": "某些常见的CYP2C9gene变异（多态性）会降低CYP2C9酶的活性，可能导致一种称为华法林敏感性的疾病。改变的酶减慢了华法林的分解（代谢），使药物在体内保持较长时间的活性。具有华法林敏感性的人比平时花费更长的时间来代谢华法林，并且可能需要比通常处方更低剂量的药物。（换行）两种最常见的CYP2C9gene多态性改变CYP2C9酶中的单个蛋白质构建块（氨基酸）。第一个称为CYP2C9*2，用144位的半胱氨酸替换氨基酸精氨酸，命名为Arg144Cys或R144C。CYP2C9*2多态性导致华法林代谢降低，以至于具有这种变异的人的处方剂量通常降低三分之一。第二个多态性称为CYP2C9*3，用位置359处的氨基酸亮氨酸替换了氨基酸异亮氨酸，命名为Ile359Leu或I359L。在具有CYP2C9*3多态性的人中，处方剂量通常减少五分之一。在极少数同时具有CYP2C9*2和CYP2C9*3多态性的情况下，酶活性严重降低，华法林代谢非常缓慢。如果华法林敏感的人服用华法林的平均剂量（或更多），他们有过量服用的风险，这可能导致大脑，胃肠道或其他组织异常出血，并可能导致严重的健康问题或死亡。"}]}' WHERE id = 14474;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SLC7A9", "conditions": [{"name": "胱氨酸尿症", "detail": "已发现SLC7A9gene中至少有95个突变引起胱氨酸尿症。这些突变中的许多改变单个DNA构建块（核苷酸）或插入或删除SLC7A9gene中的少量核苷酸。这些变化导致功能异常的转运蛋白复合物，导致某些氨基酸浓缩在尿液中。胱氨酸是唯一在膀胱或肾脏中形成晶体和结石的氨基酸，可导致胱氨酸尿症的体征和症状。"}]}' WHERE id = 14484;
UPDATE genes SET GHR_metadata_ch = '{"gene": "BUB1B", "conditions": [{"name": "马赛克杂色非整倍性综合征", "detail": "已发现BUB1Bgene中的十几个突变会导致1型镶嵌杂色非整倍性（MVA）综合征。这种情况的特征是染色体数目异常的细胞，这种情况称为非整倍性。受影响的个体生长缓慢，头部异常小（小头畸形），儿童时期患癌症的风险增加。（换行）当BUB1Bgene的两个拷贝都具有突变时，发生1型MVA综合征。这些突变减少了细胞中BUBR1蛋白的数量或损害了蛋白的功能。功能性BUBR1蛋白的缺乏会导致细胞分裂过程中染色单体分布的错误。没有BUBR1蛋白，即使所有染色单体都不附着在纺锤体微管上，也可以进行细胞分裂。未连接的染色单体未正确定位以进行分离，并且所得细胞具有异常数量的染色体。(换行)研究表明，延迟细胞分裂直至正确时间的过程受损是MVA综合征患癌症风险增加的基础，尽管其机制尚不完全清楚。还不清楚BUB1Bgene突变或非整倍性如何导致MVA综合征的其他特征。研究人员推测异常细胞会发生自我毁灭（细胞凋亡）。MVA综合征的体征和症状可能是由于早期发育过程中各种组织的细胞损失所致。"}, {"name": "其他疾病", "detail": "BUB1Bgene的突变也可能导致与染色体相关的问题，称为过早的染色单体分离性状。尽管具有此特征的个体具有染色体异常，表明细胞分裂过程中正常染色体分离存在问题，但受影响的个体通常没有与该特征相关的健康问题。这种性状发生在BUB1Bgene两个拷贝之一具有遗传变化的个体中。"}]}' WHERE id = 14486;
UPDATE genes SET GHR_metadata_ch = '{"gene": "KDM6A", "conditions": [{"name": "歌舞伎症候群", "detail": "在歌舞uki综合症患者中已鉴定出KDM6Agene至少35个突变，该疾病的特征是独特的面部特征，智力残疾和影响身体其他部位的异常。(换行)与歌舞uki综合症相关的大多数KDM6Agene突变会删除KDM6Agene序列中的遗传物质或导致过早的终止信号，从而导致赖氨酸特异性脱甲基酶6A酶异常短。这些突变的结果是该酶无功能。缺乏功能性赖氨酸特异性脱甲基酶6A酶会破坏其在组蛋白脱甲基中的作用，并损害人体许多器官和组织中某些gene的适当调节，从而导致歌舞uki综合症的发育和功能特征异常。(换行)尽管赖氨酸特异性脱甲基酶6A被认为是一种肿瘤抑制因子，但这种酶功能的丧失似乎并未增加歌舞uki综合症患者的癌症风险。"}, {"name": "癌症", "detail": "一些KDM6Agene突变不是遗传的，而是在一个人的一生中发生的。这种称为体细胞突变的突变仅存在于某些细胞中。已经在某些癌症中鉴定了KDM6Agene中的体细胞突变。这些包括乳腺癌，食道癌，结肠癌，肾癌和脑癌，以及称为髓样白血病和多发性骨髓瘤的造血细胞癌。这些突变中的大多数会导致异常短的，无功能的赖氨酸特异性脱甲基酶6A酶，该酶无法发挥其作为肿瘤抑制因子的作用，从而导致癌症的发展。"}]}' WHERE id = 14504;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ERCC3", "conditions": [{"name": "毛滴虫营养不良", "detail": "ERCC3gene的突变似乎是毛发硫代营养不良的罕见原因。该gene中的至少一种突变可引起该病症的光敏形式，其特征在于对来自太阳光的UV射线的极端敏感性。（换行）已知引起毛发硫代营养不良的单个ERCC3gene突变会改变XPB蛋白中的一个蛋白结构单元（氨基酸）；具体而言，它用蛋白质119位的氨基酸脯氨酸（写为Thr119Pro或T119P）取代了氨基酸苏氨酸。这种突变可能使TFIIH复合物不稳定，并降低了其修复由紫外线辐射引起的DNA损伤的能力。DNA修复问题会导致患有光敏性毛滴虫营养不良症的人对阳光极为敏感。该病的其他特征，例如生长缓慢，智力残疾和头发脆弱，可能是由于出生前后正常发育所需gene转录的问题所致。(换行)与色素干皮症（如下所述）不同，毛发硫代营养不良与皮肤癌风险增加无关。研究人员正在努力确定为什么ERCC3gene中的某些突变会影响一个人的癌症风险，而另一些则不会。"}, {"name": "着色性干皮症", "detail": "ERCC3gene的突变似乎也是色素干皮症的罕见原因。在患有这种情况的人中已经鉴定出单个ERCC3gene突变。这种突变改变了XPB蛋白中的一个蛋白结构单元（氨基酸）；具体而言，它用蛋白质99位的氨基酸丝氨酸取代了氨基酸苯丙氨酸（写为Phe99Ser或F99S）。这种突变大大降低了TFIIH复合物修复受损DNA的能力。结果，异常在DNA中积累，导致细胞发生故障并最终癌变或死亡。DNA修复中的这些问题会导致色素干皮症患者对阳光下的紫外线极为敏感。当紫外线破坏控制细胞生长和分裂的gene时，细胞会生长得太快，并且无法控制。结果，患有色素干皮症的人患癌症的风险大大增加。这些癌症最常发生在暴露于阳光下的身体部位，如皮肤和眼睛。(换行)除了对阳光敏感外，色素干皮症有时还伴有进行性神经系统异常。在具有Phe99Ser突变的受影响个体中，神经系统异常相对较轻，包括听力下降和协调性差。研究表明，尽管大脑没有暴露在紫外线下，但与这种情况相关的神经系统异常是由DNA损伤累积引起的。研究人员怀疑其他因素可能会破坏神经细胞中的DNA。目前还不清楚为什么有些色素干皮症患者会出现神经系统异常，而另一些则不会。"}, {"name": "其他疾病", "detail": "ERCC3gene中的几个突变可引起色素干皮症和另一种与DNA修复缺陷有关的疾病，称为Cockayne综合征。当这种特征组合出现在同一个人中时，它被称为色素干皮症/Cockayne综合征（XP/CS）复合体。XP/CS复合体的体征和症状包括极端的阳光敏感性，皮肤癌风险增加，身材矮小，听力下降，协调性差和智力障碍。(换行)研究人员不确定该单个gene的突变如何导致具有多种体征和症状的几种不同疾病。研究表明，不同的ERCC3gene突变以不同的方式影响TFIIH复合物的稳定性和功能。这些变化可能解释了色素干皮症，毛发硫代营养不良症和XP/CS复合体的不同特征。"}]}' WHERE id = 14512;
UPDATE genes SET GHR_metadata_ch = '{"gene": "LDHA", "conditions": [{"name": "乳酸脱氢酶缺乏症", "detail": "已发现LDHAgene中至少有8个突变会导致乳酸脱氢酶缺乏症，特别是乳酸脱氢酶A缺乏症。这种情况的特征是极度疲倦，高强度运动或其他剧烈运动后肌肉组织破裂。LDHAgene突变导致产生异常的乳酸脱氢酶-一种不能附着（结合）到其他亚基以形成乳酸脱氢酶的亚基。缺乏功能性亚基会减少形成的酶的量，主要影响骨骼肌。结果，糖原不能有效分解，导致肌肉细胞能量减少。当肌肉细胞在剧烈的体力活动中没有获得足够的能量时，肌肉变得虚弱，肌肉组织可能会分解，正如乳酸脱氢酶A缺乏症患者所经历的那样。"}]}' WHERE id = 14525;
UPDATE genes SET GHR_metadata_ch = '{"gene": "THAP1", "conditions": [{"name": "肌张力障碍6", "detail": "在肌张力障碍患者中已鉴定出70多个THAP1gene突变6。肌张力障碍6是肌张力障碍的多种形式之一，肌张力障碍是一组以不自主运动，各种肌肉的扭曲（扭转）和拉紧以及受影响的身体部位异常定位为特征的疾病。（换行）导致肌张力障碍6的大多数THAP1gene突变会改变THAP1蛋白中的单个蛋白结构单元（氨基酸）或导致过早的终止信号，从而导致异常短的蛋白。研究表明，许多突变会影响THAP1蛋白的稳定性，从而减少可用于DNA结合的功能性THAP1蛋白的数量。其他可能会损害蛋白质与正确的DNA区域结合的能力。DNA结合问题可能破坏gene活性的适当调节，导致肌张力障碍的体征和症状6。（换行）特定的THAP1gene突变特定于首先描述肌张力障碍6的美国中西部的门诺族人群。该突变改变了称为外显子2的gene区域中的DNA序列。一些研究人员使用术语DYT6肌张力障碍指由该特定突变引起的肌张力障碍，而使用更广泛的术语THAP1肌张力障碍指由任何THAP1gene突变引起的肌张力障碍。通常，影响与DNA结合的THAP1蛋白区域的突变，包括在门诺族人群中发现的突变，往往比影响该蛋白其他区域的突变导致更严重的体征和症状。"}]}' WHERE id = 14526;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SPINK5", "conditions": [{"name": "Netherton综合征", "detail": "Netherton综合征患者中至少有70个SPINK5gene突变，这是一种涉及皮肤和头发异常的疾病，具有过敏，哮喘和称为湿疹的炎症性皮肤病的高风险。SPINK5gene中的突变导致无法控制丝氨酸肽酶活性的LEKT1蛋白。LEKT1功能的缺乏使丝氨酸肽酶异常活跃，并分解角质层中过多的蛋白质。结果，发生过度的皮肤脱落，并且角质层异常薄并且容易分解，导致Netherton综合征中发生的皮肤异常。LEKT1功能丧失也会导致头发异常生长。导致Netherton综合征患者过敏，哮喘和湿疹的免疫功能障碍可能是由于缺乏LEKT1控制参与触发免疫系统功能的肽酶所致。也认为涉及由异常皮肤中的微生物侵入引起的免疫系统的过度激活。"}, {"name": "其他疾病", "detail": "SPINK5gene的正常变异（多态性）与免疫系统异常触发（超敏反应）的风险增加有关，称为特应性。特应性导致过敏，湿疹和哮喘等疾病。SPINK5gene变异可能会影响LEKT1控制参与触发免疫系统的肽酶的能力，从而导致这些疾病的风险增加。"}]}' WHERE id = 14530;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ATP7B", "conditions": [{"name": "威尔逊病", "detail": "已经发现ATP7Bgene的突变会引起Wilson病，Wilson病是一种以肝脏和精神疾病为特征的疾病，包括黄疸，疲劳，食欲不振，腹部肿胀笨拙，震颤，行走困难，言语问题，思维能力受损，抑郁，焦虑和情绪波动这是由于缩短，ATPase 2异常或无功能，会损害体内多余铜的去除。"}]}' WHERE id = 14546;
UPDATE genes SET GHR_metadata_ch = '{"gene": "PMS2", "conditions": [{"name": "林奇综合症", "detail": "据报道，约有2%的Lynch综合征家庭已发现PMS2gene突变。林奇综合症增加了许多类型癌症的风险，尤其是结肠癌（大肠）和直肠癌，这些癌症统称为大肠癌。Lynch综合征患者的子宫内膜癌（子宫内膜癌），卵巢癌，胃癌，小肠癌，肝癌，胆囊癌，上尿路癌和脑癌的风险也增加。这种情况下涉及的PMS2gene突变导致异常短或无活性的PMS2蛋白的产生，该蛋白无法有效修复DNA复制过程中产生的错误。随着细胞继续分裂，错误累积，这可能导致细胞功能异常，增加结肠或身体其他部位形成肿瘤的风险。（换行）PMS2gene中的一些突变可引起称为Turcot综合征的Lynch综合征的变体。除结肠直肠癌外，患有Turcot综合征的人倾向于发展一种称为胶质母细胞瘤的特定类型的脑肿瘤。"}, {"name": "卵巢癌", "detail": "遗传学家庭参考提供有关卵巢癌的信息。"}, {"name": "其他癌症", "detail": "虽然林奇综合征与一个拷贝的PMS2gene突变相关，但很少有受影响家庭中的个体继承两个PMS2gene突变，每个父母一个。在这些情况下，最常见的是在gene的两个拷贝中都发生相同的突变（纯合突变）。具有纯合PMS2gene突变的人患有不同于Lynch综合征的综合征。除结直肠癌外，这些人还可能患上血液癌症（白血病或淋巴瘤）。这些个体中的一些还将发展为神经纤维瘤病的特征，包括沿神经生长的非癌性肿瘤（神经纤维瘤）和称为咖啡色斑点的浅棕色皮肤斑块。这些人的结肠癌发病非常早，通常发生在儿童时期。这种涉及结肠癌，白血病或淋巴瘤以及神经纤维瘤病的综合征有时称为结肠。"}]}' WHERE id = 14555;
UPDATE genes SET GHR_metadata_ch = '{"gene": "HSD17B4", "conditions": [{"name": "D-双功能蛋白缺乏症", "detail": "在患有D-双功能蛋白缺乏症的个体中已鉴定出60多个HSD17B4gene突变，D-双功能蛋白缺乏症是一种严重的疾病，从婴儿期开始就导致神经系统功能恶化（神经变性）。导致D-双功能蛋白缺乏的HSD17B4gene突变可影响D-双功能蛋白的一种或两种酶活性；但是，这种区别似乎并不影响疾病的严重程度或特征。(换行)D-双功能蛋白的一种或两种酶活性的损害阻止其有效地分解脂肪酸。结果，这些脂肪酸在体内积累。目前尚不清楚脂肪酸积累如何导致D-双功能蛋白缺乏的特定特征；然而，这种积累可能导致大脑的异常发育和髓鞘的分解，髓鞘是保护神经和促进神经冲动的有效传递的覆盖物。髓鞘的破坏导致脑和脊髓中含髓鞘组织（白质）的损失；白质损失被描述为脑白质营养不良。大脑发育异常和白细胞营养不良可能是D-双功能蛋白缺乏症中发生的神经系统异常的基础。"}, {"name": "Perrault综合征", "detail": "已发现至少两个HSD17B4gene突变引起Perrault综合征，该病的特征是受影响的男性和女性听力下降，而受影响的女性卵巢异常。在这种情况下涉及的HSD17B4gene突变减少了产生的功能性D-双功能蛋白的量。尚不清楚这些突变对受影响个体的脂肪酸分解有什么影响，或者该突变如何导致Perrault综合征的体征和症状。"}]}' WHERE id = 14566;
UPDATE genes SET GHR_metadata_ch = '{"gene": "NCF4", "conditions": [{"name": "慢性肉芽肿病", "detail": "已发现NCF4gene中至少有两个突变引起慢性肉芽肿性疾病。患有这种疾病的人由于免疫系统减弱而发生反复发作的感染和炎症的风险增加。NCF4gene的突变是这种情况的罕见原因。这些突变导致功能性NADPH氧化酶减少。该蛋白质的缺乏（缺乏）会损害NADPH氧化酶的组装或功能。结果，吞噬细胞不能产生活性氧来杀死外来入侵者，并且嗜中性粒细胞活性不受调节。缺乏NADPH氧化酶会使受影响的个体容易受到多种类型的感染和过度炎症的影响。"}]}' WHERE id = 14656;
UPDATE genes SET GHR_metadata_ch = '{"gene": "GALC", "conditions": [{"name": "克拉伯病", "detail": "已经鉴定出200多种引起Krabbe病的GALCgene突变。Krabbe病是一种脑部疾病，通常始于婴儿期（婴儿Krabbe病），并导致运动和饮食问题，发育受损和癫痫发作。在欧洲血统的受影响个体中，最常见的突变（通常称为30 kb del）删除了GALCgene的大部分。其他突变将其他DNA构建块（核苷酸）插入GALCgene，从gene中删除少量核苷酸，或用不正确的核苷酸替换单个核苷酸。(换行)这些GALCgene突变严重降低或消除了半乳糖基神经酰胺酶的活性。结果，半乳糖神经酰胺和精神分裂症不能被分解。这些半乳糖脂的积累会损害髓磷脂形成细胞，从而损害髓磷脂的形成并导致神经系统中髓磷脂的损失（脱髓鞘）。没有髓磷脂，大脑和身体其他部位的神经无法正常传递信号，导致克拉伯病的体征和症状。(换行)有些人在儿童期，青春期或成年期出现Krabbe病症状（迟发性Krabbe病）。据认为，这些个体具有允许半乳糖基神经酰胺酶某些活性的突变，这延迟了病症的发作。该病的严重程度也可能受到GALCgene中影响半乳糖基神经酰胺酶活性的其他常见变异（多态性）的影响。"}]}' WHERE id = 14658;
UPDATE genes SET GHR_metadata_ch = '{"gene": "WNK1", "conditions": [{"name": "II型遗传性感觉和自主神经病", "detail": "WNK1gene中的突变负责一种称为HSAN2A的II型遗传性感觉和自主神经病（HSAN2）。HSAN2A患者失去感觉疼痛或感觉冷热的能力。在HSAN2A患者中已鉴定出WNK1gene的十几个突变。所有这些突变导致可能无功能的异常缩短的WNK1/HSN2蛋白。HSAN2A患者的感觉神经元数量减少；但是，尚不清楚异常的WNK1/HSN2蛋白在这种损失中所起的作用。感觉神经元的丧失导致HSAN2A的体征和症状。（换行）HSAN2A中涉及的WNK1gene突变似乎不影响L-WNK1或KS-WNK1亚型。"}, {"name": "假性醛固酮增多症2型", "detail": "已发现WNK1gene中至少有两个突变会导致2型假性低醛固酮增多症（PHA2），这种疾病的特征是高血压（高血压）和血液中钾水平高（高钾血症）。这种情况下涉及的突变会从WNK1gene中删除大量DNA构建块（核苷酸）。这些缺失导致WNK1gene和过量的L-WNK1蛋白的活性增加。L-WNK1蛋白的增加异常增加钠的重吸收并阻止钾的分泌，从而导致高血压和高钾血症。（换行）PHA2中涉及的WNK1gene突变似乎不影响KS-WNK1或WNK1/HSN2亚型。"}, {"name": "其他疾病", "detail": "研究表明，在没有PHA2的人群中，WNK1gene的正常变异与高血压（高血压）风险增加相关（如上所述）。遗传变异和环境因素的结合可能会影响这种复杂状况的发展。"}]}' WHERE id = 14662;
UPDATE genes SET GHR_metadata_ch = '{"gene": "GHR", "conditions": [{"name": "Laron综合征", "detail": "已发现GHRgene中至少有70个突变会引起Laron综合征，这是一种罕见的矮小形式，其特征还在于肥胖，独特的面部外观以及影响多个身体系统的其他体征和症状。所有鉴定出的突变均损害生长激素受体的功能。大多数突变会影响受体的细胞外区域，从而阻止其有效结合生长激素。一些突变影响受体的细胞内区域，降低或消除其触发促进生长的信号的能力。(换行)虽然GHRgene突变的人产生生长激素，但有缺陷的受体通过产生IGF-I或触发细胞生长和分裂来阻止细胞对激素作出反应。细胞无法对被称为生长激素不敏感的生长激素起反应，破坏了许多不同组织的正常生长。当生长激素不能充分刺激骨骼生长时，会导致身材矮小。由对生长激素不敏感引起的代谢变化以及由此导致的IGF-I短缺导致该病症的许多其他特征，包括肥胖。(换行)研究表明，Laron综合征患者的癌症和2型糖尿病风险显着降低。尽管患有肥胖症（癌症和2型糖尿病的危险因素），但受影响的人似乎比未受影响的亲戚患这些常见疾病的频率要低得多。研究人员正在努力确定GHRgene的突变如何保护Laron综合征患者免于发展这些疾病。研究表明，对生长激素不敏感可能有助于防止可能导致癌性肿瘤发展的细胞不受控制的生长和分裂。生长激素不敏感也似乎改变了身体对胰岛素的反应，胰岛素是一种调节血糖水平的激素。抵抗胰岛素作用是2型糖尿病的主要危险因素。Laron综合征患者的情况相反，对胰岛素的敏感性增加，这可能有助于解释他们患这种疾病的风险降低。"}, {"name": "其他疾病", "detail": "已经在涉及生长问题的几种conditions下研究了生长激素受体的两种同种型。这些情况包括由于生长激素缺乏（缺乏）而导致的身材矮小，出生前开始的生长缓慢（胎龄小）以及原因不明的身材矮小（特发性身材矮小）。一些研究发现，患有这些疾病的儿童对治疗的反应不同，这取决于他们所具有的生长激素受体的哪种同种型。具体而言，与fl GHR相比，d3 GHR与治疗期间更快的生长相关。其他研究没有发现增长的差异，尽管所有的研究都使用了不同的方法，包括相对较少的参与者。(换行)生长激素受体的两种同种型也在肢端肥大症的成年人中进行了研究，肢端肥大症是一种以生长激素水平升高为特征的疾病。异常高的水平通常是由脑垂体中的非癌性肿瘤引起的，脑垂体是产生生长激素的大脑的一部分。为了应对过量的生长激素，手，脚和脸部的骨骼变得异常大。这种情况的其他体征和症状可包括皮肤增厚，出汗和体味增加，某些器官增大，肌肉无力和过度疲劳（疲劳）。在肢端肥大症的成年人中，研究表明，与具有fl GHR相比，具有D3GHR同种型与更好的治疗反应相关。然而，与具有fl GHR相比，具有d3 GHR似乎增加了该疾病的某些并发症的风险。这些差异的原因尚不完全清楚。(换行)一个人是否有fl GHR或d3 GHR似乎不影响没有生长障碍的人的成人身高。许多遗传和非遗传因素，其中一些是未知的，影响一个人的身高。"}]}' WHERE id = 14664;
UPDATE genes SET GHR_metadata_ch = '{"gene": "COLQ", "conditions": [{"name": "先天性肌无力综合征", "detail": "已发现COLQgene突变会导致先天性肌无力综合症，这种疾病的特征是肌肉无力（肌无力），随着体力消耗而恶化，还可能表现出短暂的呼吸暂停（呼吸暂停），导致发。这是由于缺陷，缩短或无功能的蛋白质导致的，该蛋白质无法与神经肌肉接头处的乙酰胆碱酯酶相互作用。"}]}' WHERE id = 14673;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SEC23B", "conditions": [{"name": "先天性贫血性贫血", "detail": "在II型先天性贫血性贫血（CDA）患者中，已经鉴定出SEC23Bgene至少20个突变。这些突变大多数会改变SEC23B蛋白中的单个蛋白结构单元（氨基酸）。其他突变会从SEC23Bgene中删除遗传物质，或改变gene指示用于制备SEC23B蛋白的方式。负责II型CDA的突变可能会破坏SEC23B蛋白的功能。但是，研究人员怀疑这些突变并不能完全消除蛋白质的功能，而蛋白质似乎对生命至关重要。（换行）尚不清楚SEC23B突变如何引起II型CDA的特征。SEC23B蛋白异常会导致成红细胞产生异常形状并可能具有额外核的成红细胞。这些有缺陷的成红细胞不能发育成功能性成熟红细胞。由此导致的健康红细胞短缺导致贫血的特征性体征和症状，以及并发症，包括肝脏和脾脏肿大（肝脾肿大）以及可能损害身体器官的铁的异常累积。"}]}' WHERE id = 14677;
UPDATE genes SET GHR_metadata_ch = '{"gene": "RP2", "conditions": [{"name": "视网膜色素变性", "detail": "在X连锁形式的色素性视网膜炎患者中，已经鉴定出RP2gene的70多个突变。这种情况主要影响男性，导致儿童早期夜盲症，随后进行白天视力丧失。RP2gene突变占所有X连锁性色素性视网膜炎病例的10%至15%。（换行）RP2gene中的大多数突变导致产生异常短版本的RP2蛋白。一些突变会改变RP2蛋白中的单个结构单元（氨基酸）。这些变化改变了蛋白质的结构和功能，这可能破坏了感光细胞的稳定性或维持。感光细胞的逐渐丧失是色素性视网膜炎进行性视力丧失的基础。"}]}' WHERE id = 14699;
UPDATE genes SET GHR_metadata_ch = '{"gene": "STAMBP", "conditions": [{"name": "小头畸形-毛细血管畸形综合征", "detail": "在小头畸形-毛细血管畸形综合征患者中至少发现了13个STAMBPgene突变，这是一种遗传性疾病，其特征是头部大小异常小（小头畸形），发育迟缓和智力残疾，反复发作（癫痫），以及皮肤中的小血管称为毛细血管（毛细血管畸形）。（换行）已知的STAMBPgene突变减少或消除STAM结合蛋白的产生。这种短缺允许受损或不需要的蛋白质在细胞内积聚而不是被降解或再循环，这可能损害细胞并导致它们自毁（经历细胞凋亡）。研究人员怀疑，出生前开始的脑细胞异常凋亡可能导致小头畸形，并在小头畸形-毛细血管畸形综合征患者中发现潜在的脑部异常。STAM结合蛋白的缺乏也改变了正常发育所必需的多种信号传导途径，这可能是毛细血管畸形和该病症的其他体征和症状的基础。"}]}' WHERE id = 14711;
UPDATE genes SET GHR_metadata_ch = '{"gene": "CHRNE", "conditions": [{"name": "先天性肌无力综合征", "detail": "已发现CHRNEgene突变会导致先天性肌无力综合症，以肌肉无力（肌无力），呼吸暂停，运动技能（如爬行和行走）发育迟缓为特征的疾病。这是由于改变了？亚基导致AChR蛋白信号转导增加或减少，从而损害神经肌肉接头处的细胞间通讯。"}]}' WHERE id = 14739;
UPDATE genes SET GHR_metadata_ch = '{"gene": "STAT1", "conditions": [{"name": "家族性念珠菌病", "detail": "在家族性念珠菌病患者中已鉴定出至少35个STAT1gene突变，这是由念珠菌真菌引起的感染的遗传趋势（通常称为酵母感染）。大多数患有家族性念珠菌病的人从儿童早期就开始慢性感染皮肤，指甲和粘膜，例如口腔内壁，统称为慢性粘膜皮肤念珠菌病。一些具有STAT1gene突变的人还具有其他特征，例如对其他感染的敏感性增加以及自身免疫性疾病的风险增加，其中免疫系统攻击人体自身的组织或器官。由STAT1gene突变引起的体征和症状的这种组合有时称为免疫缺陷31C。（换行）在家族性念珠菌病患者中鉴定出的STAT1gene突变被描述为“功能获得”，因为它们增加了细胞中活化的STAT1蛋白的量。通过增加STAT1对IL-17途径的抑制作用，这些突变削弱了人体抵抗念珠菌的能力，并导致家族性念珠菌病中发生的慢性感染。活化的STAT1蛋白增加对其他信号通路的影响被认为是免疫缺陷31C中可能发生的各种特征的基础，但具体机制尚不清楚。"}, {"name": "带状疱疹", "detail": "遗传学家庭参考提供有关带状疱疹的信息。"}, {"name": "其他疾病", "detail": "STAT1gene突变也可能引起其他免疫系统问题，称为免疫缺陷31A和免疫缺陷31B。与家族性念珠菌病相关的功能获得性突变（如上所述）相反，这些STAT1gene突变被描述为“功能丧失”突变，因为它们损害或消除了蛋白质的正常作用。导致免疫缺陷31A的STAT1gene突变损害STAT1蛋白在IFNG途径中的功能，导致对分枝杆菌的易感性增加。在更严重的疾病免疫缺陷31B中，STAT1gene突变会同时损害IFNG途径和IFNA/B途径，从而导致对单纯疱疹和病毒性脑炎等病毒感染以及结核病等分枝杆菌感染均易感。"}]}' WHERE id = 14754;
UPDATE genes SET GHR_metadata_ch = '{"gene": "VKORC1", "conditions": [{"name": "华法林抵抗", "detail": "VKORC1gene的多种变异（多态性）与华法林耐药性有关，在这种情况下，个体需要比通常处方更高剂量的华法林药物。华法林是一种血液稀释剂，这意味着它可以防止血栓形成。它通过与VKORC1酶连接（结合）并防止（抑制）凝血蛋白的活化而起作用。(换行)华法林耐药人群中最常见的VKORC1gene多态性改变了VKORC1酶中的单个蛋白质结构单元（氨基酸）。具体而言，氨基酸天冬氨酸在位置36处被氨基酸酪氨酸取代（写为Asp36Tyr或D36Y）。这种多态性导致形成与华法林结合能力降低的VKORC1酶。结果，需要更多的华法林来抑制VKORC1酶并停止凝血过程。如果华法林耐药的人需要华法林治疗并服用平均剂量，他们将仍然有发生潜在有害血凝块的风险。"}, {"name": "华法林敏感性", "detail": "VKORC1gene的多种多态性与华法林敏感性有关，在这种情况下，个体需要的华法林剂量低于通常规定的剂量。（换行）华法林敏感性人群中最常见的VKORC1gene多态性，称为VKORC1A，改变了VKORC1gene附近区域中的单个DNA构建块（核苷酸），该区域控制复合物的产生。具体而言，将核苷酸鸟嘌呤替换为核苷酸腺嘌呤（写为-1639G>A）。VKORC1A多态性在亚洲和欧洲血统的个体中特别常见。gene改变导致产生的酶减少，并减少了可用于将维生素K转化为有助于激活凝血蛋白的形式的VKORC1酶的量。由于VKORC1酶的量减少，因此抑制复合物作用所需的华法林较少，从而导致华法林敏感性。如果华法林敏感的人服用华法林的平均剂量（或更多），他们有过量服用的风险，这可能导致大脑，胃肠道或其他组织异常出血，并可能导致严重的健康问题或死亡。"}]}' WHERE id = 14764;
UPDATE genes SET GHR_metadata_ch = '{"gene": "GP1BA", "conditions": [{"name": "伯纳德-苏里尔综合症", "detail": "已发现至少54个GP1BAgene突变会引起Bernard-Soulier综合征，该病的特征是血小板数量比正常人减少（大血小板减少症）和出血过多。其中一些突变导致产生改变的GPIb？可能过早分解或无法到达血小板表面的亚基。血小板表面缺乏该亚基会阻止GPIb-IX-V复合物的形成。没有GPIb-IX-V，血小板不能在损伤部位聚集形成凝块，导致与Bernard-Soulier综合征相关的出血问题。其他突变导致产生可形成GPIb-IX-V复合物但不能与冯维勒布兰德因子相互作用的亚基，这也损害凝血所必需的血小板积聚。"}, {"name": "其他疾病", "detail": "GP1BAgene中至少有六个突变可引起另一种称为血小板型von Willebrand病（也称为伪von Willebrand病）的出血性疾病。这种疾病的特征是血液中血小板数量减少（血小板减少症）和轻度出血异常。与引起Bernard-Soulier综合征的突变（如上所述）相反，涉及血小板型von Willebrand病的突变导致GPIb-IX-V复合物与von Willebrand因子的过度结合。因为含有改变的复合物的血小板附着于血管性血友病因子而不损伤血管，所以当发生损伤时可用于血块形成的血小板较少，这导致血小板型血管性血友病患者出血过多。"}]}' WHERE id = 14782;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SIX5", "conditions": [{"name": "支气管肾/支气管综合征", "detail": "在患有支气管肾（BOR）综合征的人中发现了SIX5gene的至少四个突变，这种情况会破坏颈部组织的发育并导致耳朵和肾脏的畸形。BOR综合征被认为是患有分支性（BO）综合征的疾病谱的一部分，除了肾脏（肾）畸形外，它具有许多与BOR综合征相同的特征。(换行)研究人员现在质疑SIX5gene的突变是否引起BOR综合征。最初报道在该gene中具有突变的一些受影响的个体后来也被发现在EYA1gene中也具有突变。研究人员怀疑EYA1gene突变可能是这些人患病的真正原因。（换行）每个已识别的SIX5gene突变都会改变SIX5蛋白中的单个蛋白结构单元（氨基酸），从而改变该蛋白与EYA1gene产生的蛋白的相互作用。因为这种蛋白质相互作用对于胚胎发育过程中某些gene的激活是必需的，所以改变后的SIX5蛋白质有可能破坏出生前的发育。BOR综合征的主要体征和症状是由第二branch弓，耳朵和肾脏的异常发育引起的。"}]}' WHERE id = 14789;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SETBP1", "conditions": [{"name": "Schinzel-Giedion综合征", "detail": "在患有Schinzel-Giedion综合征的儿童中至少鉴定出SETBP1gene的五个突变，这是一种出生时明显的严重疾病，其特征是独特的面部特征和许多身体系统异常。受影响个体中的gene突变聚集在称为外显子4的gene的一个区域中。但是，突变对gene或蛋白质功能的影响尚不清楚。研究人员正在努力了解SETBP1gene的突变如何引起Schinzel-Giedion综合征的体征和症状。"}]}' WHERE id = 14807;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ACY1", "conditions": [{"name": "氨酰化酶1缺乏症", "detail": "由于有缺陷或无功能的酶导致尿液中释放出过量的N-乙酰化氨基酸，因此在患有称为氨酰酶1缺乏症的人中发现了ACY1gene的几种突变。这种情况的特征是智力和运动技能的发展延迟以及其他神经系统问题。"}]}' WHERE id = 14822;
UPDATE genes SET GHR_metadata_ch = '{"gene": "DMPK", "conditions": [{"name": "肌强直性营养不良", "detail": "1型强直性肌营养不良症是由DMPKgene突变（称为三核苷酸重复扩增）引起的。该突变增加了DMPKgene中重复的CTG区段的大小。患有1型肌强直性营养不良的人在大多数细胞中具有50至5000个CTG重复。在某些类型的细胞中，例如肌肉细胞，重复次数可能更多。(换行)突变的DMPKgene产生信使RNA的改变形式，这是通常用于指导蛋白质生产的gene的分子蓝图。研究人员发现，改变后的信使RNA捕获蛋白质，在细胞内形成团块。团块干扰许多其他蛋白质的产生。这些变化阻止了其他组织中的肌肉细胞和细胞正常运作，导致肌肉无力和1型肌强直性营养不良的其他特征。（换行）三核苷酸重复扩增的大小与体征和症状的严重程度相关。具有1型强直性肌营养不良的经典特征的人，包括成年后开始的肌肉无力和消瘦，通常具有100至1000次CTG重复。患有更严重的先天性1型肌强直性营养不良的人往往具有更多的CTG重复序列，通常超过2000个。这种形式的疾病在婴儿期很明显，可能涉及威胁生命的健康问题。（换行）随着改变的DMPKgene从一代传递到下一代，CTG重复扩增的大小通常会增加。尚无35至49个CTG重复序列的人发展为1型强直性营养不良的报道，但如果CTG重复序列的数量增加，他们的孩子有患这种疾病的风险。从35到49的重复长度称为预突变。"}]}' WHERE id = 14824;
UPDATE genes SET GHR_metadata_ch = '{"gene": "PKP2", "conditions": [{"name": "致心律失常性右室心肌病", "detail": "在心律失常性右室心肌病（ARVC）患者中，已鉴定出PKP2gene的50多个突变。这种情况最常影响右心室周围的心肌，右心室是心脏的两个下腔之一。ARVC会增加异常心跳（心律不齐）和猝死的风险。（换行）一些PKP2gene突变导致产生异常短版本的plakophilin 2。其他突变通过添加，删除或改变其一个或多个蛋白质结构单元（氨基酸）来改变亲脂蛋白2的结构。研究表明，改变的蛋白质会损害桥粒的形成和功能。(换行)没有正常的桥粒，心肌细胞彼此分离并死亡，特别是当心肌处于压力下时（例如在剧烈运动期间）。受损的心肌逐渐被脂肪和瘢痕组织取代。随着这种异常组织的积聚，右心室壁伸展，阻止心脏有效抽血。这些变化也会破坏控制心跳的电信号，从而导致心律失常。"}]}' WHERE id = 14834;
UPDATE genes SET GHR_metadata_ch = '{"gene": "TMPRSS6", "conditions": [{"name": "铁难治性缺铁性贫血", "detail": "已发现TMPRSS6gene中至少有40个突变会导致遗传性贫血，称为铁难治性缺铁性贫血。这种情况的特点是血液中铁缺乏（缺乏），对铁治疗有抵抗力（难治性）。（换行）TMPRSS6gene突变大大减少了功能性matriptase-2的数量，阻止其控制hepcidin水平。由此产生的铁调素活性的升高阻止了铁通过肠的吸收和铁从储存中的释放。当血液中没有足够的铁时，会产生较少的血红蛋白，红细胞不能有效地将氧气输送到身体的细胞和组织。缺氧导致贫血的体征和症状，包括疲倦（疲劳），虚弱和皮肤苍白。"}]}' WHERE id = 14846;
UPDATE genes SET GHR_metadata_ch = '{"gene": "F8", "conditions": [{"name": "血友病", "detail": "F8gene的突变会导致血友病A，这是这种出血性疾病的最常见形式。已经鉴定出该gene的1300多种改变。这些突变中的一些改变gene中的单个DNA构建块（碱基对），而另一些则删除或插入多个碱基对。严重血友病A患者中最常见的突变是称为倒位的遗传物质的重排。这种倒置涉及F8gene的大部分。F8gene中的（换行）突变导致产生异常形式的凝血因子VIII或减少该蛋白的量。改变或缺失的蛋白质不能有效参与血液凝固过程。结果，血液凝块不能在受伤时正确形成。这些凝血问题导致出血过多，难以控制。一些突变，例如上述的大反转，几乎完全消除了凝血因子VIII的活性并导致严重的血友病。其他突变会降低但不会消除蛋白质的活性，从而导致轻度或中度血友病。"}]}' WHERE id = 14863;
UPDATE genes SET GHR_metadata_ch = '{"gene": "AIRE", "conditions": [{"name": "自身免疫性多内分泌病念珠菌病外胚层营养不良", "detail": "在自身免疫性多内分泌病-念珠菌病-外胚层营养不良（APECED）患者中，已经发现AIREgene的几个突变。APECED是一种遗传性疾病，拥有属性为慢性粘膜皮肤念珠菌病（CMC），由于免疫过程受损，IL-17通路被称为念珠菌。其他症状包括内分泌系统功能障碍，肾上腺，甲状旁腺和其他器官的自身免疫性损伤。"}]}' WHERE id = 14893;
UPDATE genes SET GHR_metadata_ch = '{"gene": "FOXC1", "conditions": [{"name": "Axenfeld-Rieger综合征", "detail": "已发现FOXC1gene中的50多个突变会导致3型Axenfeld-Rieger综合征，这种疾病主要影响眼前节的发育，但也可能影响身体的其他部位。许多FOXC1gene突变会减少产生的功能性FOXC1蛋白的数量，或导致无法调节其他gene活性的缺陷蛋白。其他遗传变化（例如FOXC1gene的重复）可能会增加FOXC1蛋白的数量或功能。这种蛋白质的活性太少或太多都会破坏正常发育所需的其他gene的调控。(换行)FOXC1蛋白的量或功能的变化会损害眼前节的发育，从而导致Axenfeld-Rieger综合征的眼部异常。在某些情况下，涉及FOXC1蛋白的变化也会导致身体其他部位发育的问题。"}, {"name": "彼得斯异常", "detail": "已经发现FOXC1gene中至少有两个突变引起彼得斯异常。这种情况的特征是前节异常发育和角膜混浊。导致彼得斯异常的突变可能会破坏蛋白质调节发育gene表达的能力，尤其是影响眼睛的能力。虹膜，角膜和前节其他结构的异常形成导致彼得斯异常的特征。导致Peters异常的FOXC1gene突变可导致同一家族成员中其他相关的眼部疾病，例如Axenfeld-Rieger综合征（如上所述）。"}, {"name": "Dandy-Walker畸形", "detail": "遗传学家庭参考提供有关Dandy-Walker畸形的信息。"}, {"name": "其他疾病", "detail": "FOXC1gene的突变也已在另一种称为虹膜虹膜发育不全1型的眼疾中发现。像Axenfeld-Rieger综合征和Peters异常一样，这种情况主要涉及眼前节。Iridogoniodysgenesis 1型与虹膜发育不全和眼睛压力增加（青光眼）的风险增加有关。(换行)FOXC1gene突变已在一些大脑发育异常的人中报道。改变FOXC1蛋白中单个蛋白结构单元（氨基酸）的突变与小脑的缺陷有关，小脑是参与协调运动的大脑的一部分。另外，从包括FOXC1gene和几个相邻gene的6号染色体区域中遗传物质的缺失与称为Dandy-Walker畸形的小脑结构异常有关。"}]}' WHERE id = 14896;
UPDATE genes SET GHR_metadata_ch = '{"gene": "CPT1A", "conditions": [{"name": "肉碱棕榈酰转移酶I缺乏症", "detail": "已发现CPT1Agene的突变会导致肉碱棕榈酰转移酶I（CPT I）缺乏，这种情况下能量产生减少，导致由于CPT1A酶活性降低或消除而引起的低血糖和低酮症。"}, {"name": "其他疾病", "detail": "CPT1Agene的突变与怀孕期间被称为妊娠急性脂肪肝的妇女患肝脏疾病的风险增加有关，其特征是腹痛导致肝衰竭、肝脏脂肪异常堆积、低血糖、高氨血症和肝酶异常。"}]}' WHERE id = 14926;
UPDATE genes SET GHR_metadata_ch = '{"gene": "RPS10", "conditions": [{"name": "钻石-布莱克凡贫血", "detail": "在患有Diamond-Blackfan贫血的个体中至少鉴定出三个RPS10gene突变。据信这些突变会导致异常短的，无功能的RPS10蛋白，这可能会损害核糖体的组装，但突变的具体作用尚不清楚。研究表明，缺乏功能性核糖体蛋白可能会增加骨髓中造血细胞的自我破坏，导致红细胞数量减少（贫血）。细胞分裂的异常调节或凋亡的不适当触发可能会导致其他健康问题和异常的身体特征，从而影响某些Diamond-Blackfan贫血患者。"}]}' WHERE id = 14931;
UPDATE genes SET GHR_metadata_ch = '{"gene": "TTN", "conditions": [{"name": "中心核肌病", "detail": "已发现TTNgene中至少有12个突变会引起中心核肌病，这种疾病的特征是骨骼肌肌肉无力（肌病）。这些突变中的大多数会改变gene指令用于产生titin的方式，从而导致异常蛋白质的产生。异常的titin蛋白在肌肉细胞中的活性降低或改变。目前尚不清楚这些突变如何引起中心核肌病，但改变后的蛋白质很可能无法与肌节中的其他蛋白质相互作用，从而导致肌节功能障碍。异常的肉瘤会阻止肌肉细胞正常收缩和松弛，从而导致中心核肌病的特征性肌无力。"}, {"name": "致命性心肌病的早发性肌病", "detail": "在患有致命性心肌病（EOMFC）的早发性肌病患者中，已经鉴定出TTNgene中至少有两个突变，这是一种影响骨骼肌和心肌的遗传性肌肉疾病。这些遗传变化发生在TTNgene的末端附近，并导致异常短版本的titin蛋白的产生。有缺陷的蛋白质破坏了肉瘤的功能，阻止了骨骼肌和心肌的发育和正常工作。这些肌肉异常是EOMFC特征的基础，包括骨骼肌无力和一种称为扩张型心肌病的心脏病。"}, {"name": "家族性扩张型心肌病", "detail": "已发现TTNgene中的50多个突变会引起家族性扩张型心肌病，这种情况会削弱并扩大心脏，阻止其有效抽血。家族性扩张型心肌病的体征和症状通常始于成年中期，并导致心力衰竭。TTNgene突变约占所有家族性扩张型心肌病病例的20%。这些突变导致异常短的titin蛋白的产生。目前尚不清楚这种改变的蛋白质如何引起家族性扩张型心肌病，但它可能损害肌节功能并破坏化学信号传导。肌节功能的改变会降低心脏收缩，减弱和变薄心肌的能力，并导致家族性扩张型心肌病的体征和症状。"}, {"name": "遗传性肌病伴早期唿吸衰竭", "detail": "已发现TTNgene中至少有一个突变会导致遗传性肌病并伴有早期呼吸衰竭（HMERF），这是一种遗传性肌肉疾病，主要影响靠近身体中心的肌肉（近端肌肉）和呼吸所需的肌肉。鉴定出的突变改变了titin蛋白中的单个蛋白结构单元（氨基酸）。具体而言，它用蛋白质279位的氨基酸色氨酸取代了氨基酸精氨酸（写为Arg279Trp或R279W）。研究表明，这种变化破坏了titin与肉瘤内其他蛋白质的相互作用，并干扰了蛋白质在化学信号传导中的作用。因此，随着时间的流逝，肌肉纤维会受损并变弱。目前尚不清楚为什么这些影响通常仅限于近端肌肉和呼吸所涉及的肌肉。"}, {"name": "肢带型肌营养不良症", "detail": "已经发现至少一种TTNgene突变引起2J型肢带型肌营养不良症（LGMD2J）。肢带型肌营养不良症是一组相关疾病，其特征是骨骼肌无力和消瘦，尤其是在肩膀，臀部和四肢。LGMD2J是一种仅在芬兰人群中发现的肢带型肌营养不良症，所有受影响的个体都具有相同的TTNgene突变。这种遗传变化删除了几个氨基酸，并在titin蛋白末端附近用其他氨基酸取代了它们。这种复杂的突变称为FINmaj。FINmaj突变可能会破坏titin与肌肉收缩所需的其他蛋白质的相互作用。收缩能力下降会导致肌肉随着时间的流逝而减弱和浪费，从而导致肢带型肌营养不良症的体征和症状。"}, {"name": "胫骨肌营养不良", "detail": "在胫骨肌营养不良症患者中已鉴定出TTNgene的几种突变，这种情况主要影响小腿前部的肌肉。如上所述，已发现FINmaj突变在芬兰血统的所有受影响人群中引起胫骨肌营养不良。其他TTNgene突变会在非芬兰欧洲人群中引起胫骨肌营养不良。(换行)研究人员预测，导致胫骨肌营养不良的TTNgene突变（包括FINmaj）会改变titin蛋白与肉瘤内其他蛋白相互作用的能力。突变也可能干扰蛋白质在化学信号传导中的作用。这些变化破坏了正常的肌肉收缩，导致肌肉随着时间的推移而减弱和浪费。目前尚不清楚为什么这些影响通常仅限于胫骨肌营养不良症的小腿肌肉。"}, {"name": "家族性肥厚型心肌病", "detail": "遗传学家庭参考提供有关家族性肥厚型心肌病的信息。"}, {"name": "其他疾病", "detail": "TTNgene的突变也可引起称为9型家族性肥厚型心肌病的心肌疾病。肥厚型心肌病是心肌增厚，迫使心脏更加努力地抽血。这种情况通常与异常的心跳（心律不齐）有关，并可能导致心力衰竭和猝死。研究人员在9型家族性肥厚型心肌病患者中发现了至少三个TTNgene突变。（换行）导致这种心脏病的突变可能破坏titin的正常结构和功能。遗传变化可能会改变titin与其他肌肉蛋白的相互作用或破坏其在化学信号传导中的作用。研究人员正在努力确定为什么TTNgene突变导致的某些疾病主要影响心肌，有些疾病主要影响骨骼肌，有些则同时影响两者。他们怀疑这些差异可能与TTNgene突变的位置以及不同肌肉中产生的多种titin有关。"}]}' WHERE id = 14938;
UPDATE genes SET GHR_metadata_ch = '{"gene": "CHRNG", "conditions": [{"name": "多发性翼状syndrome肉综合征", "detail": "已发现CHRNGgene突变会导致多发性翼状syndrome肉综合征，这种疾病的特征是出生前皮肤织带（翼状gium肉）和肌肉运动不足（运动障碍）。这是由于受损或缺失？破坏AChR蛋白并消除神经肌肉接头处细胞间通讯的亚基。"}]}' WHERE id = 14940;
UPDATE genes SET GHR_metadata_ch = '{"gene": "UBA1", "conditions": [{"name": "脊髓性肌萎缩", "detail": "已发现UBA1gene中至少三个突变引起X连锁的婴儿脊髓性肌萎缩症。这些突变中的每一个都改变UBA1gene中的一个DNA构建块（核苷酸）。其中两个突变（写为1617G>T和1639A>G）被认为会导致功能受损的酶。第三个突变（写为1731C>T）导致UBA1gene活性降低，导致产生较少的酶。功能酶水平降低会破坏蛋白质降解过程。细胞中蛋白质的积累会导致细胞死亡；控制肌肉运动的运动神经细胞特别容易受到蛋白质积聚的损害。"}]}' WHERE id = 14955;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SMCHD1", "conditions": [{"name": "Bosma-arhinia小眼症候群", "detail": "已经发现至少九个SMCHD1gene突变引起Bosma-arhinia小眼症候群（BAMS）。患有这种罕见病的人的鼻子异常小或缺失（精神错乱），眼睛异常小（小眼症），并且缺乏某些指导性发育的激素（促性腺激素性性腺功能减退症）。（换行）BAM中涉及的gene突变改变了SMCHD1蛋白中的单个蛋白构建块（氨基酸）。研究人员不确定这些变化如何影响蛋白质的功能并导致BAM特有的发育问题。SMCHD1gene突变可能导致与头部和面部发育有关的gene异常沉默。鼻发育问题可能会影响促性腺激素释放激素（GnRH）神经元，它们是控制生殖激素释放的神经细胞。GnRH神经元起源于发育中的鼻子，然后移动到大脑。这些神经元发育受损可以解释受影响个体的性腺功能低下性腺功能减退。(换行)患有BAMS的个体的一些家庭成员具有较轻的症状，例如嗅觉降低（嗅觉丧失），没有BAMS其他特征的精神错乱，或较不严重的鼻子异常。研究人员怀疑其他遗传因素会导致症状的严重性，尽管这些因素尚不清楚。"}, {"name": "面肩肱型肌营养不良症", "detail": "已经发现SMCHD1gene中的数十种突变会引起面肩肱型肌营养不良症，这种疾病的特征是肌肉无力和消瘦（萎缩），随着时间的推移会缓慢恶化。已经描述了两种形式的疾病：1型（FSHD1）和2型（FSHD2）。SMCHD1gene的变化似乎在两种类型中都起作用。（换行）SMCHD1gene突变导致大多数FSHD2病例。这些突变减少了可用于沉默D4Z4区的SMCHD1蛋白的量。该区域的甲基比平时少（低甲基化），这与DUX4gene在通常被关闭的细胞和组织（例如成年肌肉细胞）中的沉默受损有关。然而，D4Z4区域的低甲基化只会导致面肩胛肱型肌营养不良症，只有当它发生在那些至少有一个4号染色体拷贝被描述为“允许”的人群中时。“允许”4号染色体有一个DNA功能区，称为pLAM序列，位于DUX4gene旁边。pLAM序列对于DUX4蛋白的生产是必需的。（相反，“非许可”4号染色体不包含功能性pLAM序列，从而阻止了任何DUX4蛋白的产生。）研究人员认为DUX4蛋白会影响其他gene的活性，尤其是在肌肉细胞中。然而，尚不清楚这种蛋白质的存在如何损害或破坏这些细胞，导致进行性肌肉无力和萎缩。（换行）研究表明，SMCHD1gene的突变会增加患有其他类型的面肩肱型肌营养不良症FSHD1的人的疾病严重程度。当D4Z4区域异常缩短（收缩）时，会产生FSHD1，该区域包含1至10个重复，而不是通常的11至100个重复。研究人员怀疑，收缩的D4Z4区和SMCHD1gene突变的结合会导致D4Z4区连接的甲基更少，这使得DUX4gene具有很高的活性。在具有这两种遗传变化的人中，过度活跃的gene会导致严重的肌肉无力和萎缩。"}, {"name": "其他疾病", "detail": "在没有BAMS其他特征（如上所述）的患有精神错乱的个体中也发现了SMCHD1gene突变，这被称为孤立性精神错乱。研究人员正在努力了解为什么某些具有该gene突变的人仅发展为精神错乱，而另一些人则有其他异常。"}]}' WHERE id = 14959;
UPDATE genes SET GHR_metadata_ch = '{"gene": "HGSNAT", "conditions": [{"name": "黏多醣贮积症III型", "detail": "已发现HGSNATgene中至少有54个突变会导致IIIC型粘多糖贮积病（MPS IIIC）。这些突变大多数会改变gene中的单个DNA结构单元（核苷酸）。所有引起MPS IIIC的突变都会降低或消除N-乙酰基转移酶的功能。（换行）缺乏N-乙酰转移酶活性破坏硫酸乙酰肝素的分解。结果，部分分解的硫酸乙酰肝素积聚在溶酶体内。研究人员认为，GAG的积累会干扰溶酶体内其他蛋白质的功能，并破坏细胞的正常功能。尚不清楚为什么硫酸乙酰肝素的积累主要影响MPS IIIC中的中枢神经系统。"}]}' WHERE id = 14992;
UPDATE genes SET GHR_metadata_ch = '{"gene": "UVSSA", "conditions": [{"name": "紫外线敏感综合症", "detail": "UVSSAgene中的突变会导致紫外线敏感综合征，这是一种以阳光敏感为特征的疾病。患有这种情况的人容易晒伤，皮肤有雀斑或其他肤色变化（色素沉着）。已经鉴定出至少三个UVSSAgene突变，并且这些突变消除了UVSSA蛋白的产生。没有这种蛋白质，皮肤细胞就无法修复紫外线引起的DNA损伤，并且受损gene的转录被阻断。但是，尚不清楚UVSSA蛋白的丢失究竟如何引起紫外线敏感综合征的体征和症状。"}]}' WHERE id = 15005;
UPDATE genes SET GHR_metadata_ch = '{"gene": "TRPM4", "conditions": [{"name": "进行性家族性心脏传导阻滞", "detail": "已发现TRPM4gene中的一些突变会导致进行性家族性心脏传导阻滞。这种情况会改变心脏的正常跳动，并可能导致晕厥（晕厥）或心脏骤停和死亡。TRPM4gene突变会改变TRPM4通道中的单个蛋白质结构单元（氨基酸）。改变后的通道在细胞膜上的活性增加，由于阳离子流量增加，这可能会改变细胞产生电信号的能力。具有这些改变的通道的心脏细胞难以产生和传输协调正常心跳的电信号。此信号的中断会导致心脏传导阻滞。随着时间的推移，这些受损的心脏细胞的死亡可导致瘢痕组织（纤维化）的累积，使心脏阻滞恶化。"}, {"name": "布鲁格达氏症候群", "detail": "遗传学家庭参考提供有关Brugada综合征的信息。"}]}' WHERE id = 15019;
UPDATE genes SET GHR_metadata_ch = '{"gene": "CEBPA", "conditions": [{"name": "CEBPA突变的家族性急性髓细胞性白血病", "detail": "在患有家族性急性髓细胞性白血病且CEBPA突变的家庭中，已经鉴定出CEBPAgene中至少有六个突变，CEBPA是一种称为急性髓细胞性白血病的血癌。这些遗传突变几乎存在于人体每个细胞的一生中。突变导致较短版本的CCAAT/增强子结合蛋白α。这种缩短的蛋白质是由每个细胞中CEBPAgene的一个拷贝产生的，并且据信它会干扰由该gene的第二个拷贝产生的正常蛋白质的肿瘤抑制功能。据信CCAAT/增强子结合蛋白α的肿瘤抑制功能的缺失破坏了血细胞产生的调节，导致急性髓性白血病中发生的异常细胞的不受控制的产生。（换行）除了每个细胞中一个CEBPAgene拷贝的遗传突变外，大多数具有突变CEBPA的家族性急性髓性白血病患者也在CEBPAgene的第二个拷贝中获得突变。另外的突变称为体细胞突变，仅在癌性白血病细胞中发现，不遗传。已在白血病细胞中鉴定出的体细胞CEBPAgene突变通常会降低CCAAT/增强子结合蛋白α的DNA结合能力。研究人员建议，尽管尚不清楚该突变究竟如何参与急性髓细胞性白血病的发展，但第二个突变可能会影响血细胞的正常分化。"}, {"name": "细胞遗传学正常的急性髓性白血病", "detail": "在一些患有急性髓细胞性白血病（称为细胞遗传学正常的急性髓细胞性白血病（CN-AML））的人中，已经鉴定出CEBPAgene的突变。虽然大的染色体异常可能参与急性髓性白血病的发展，但大约一半的病例没有这些异常；这些被归类为CN-AML。在大约18%的CN-AML患者中发现了该gene的突变。当与CEBPAgene突变相关时，这种情况可以遗传，在这种情况下，它被称为CEBPA突变的家族性急性髓细胞性白血病（如上所述），或不遗传（CEBPA突变的散发性急性髓细胞性白血病）。（换行）两种类型的CEBPAgene突变可以以CN-AML的遗传形式和非遗传形式发生。一种类型导致产生异常短的蛋白质，其干扰正常形式的CCAAT/增强子结合蛋白α的肿瘤抑制功能。另一种类型的突变阻断了CCAAT/增强子结合蛋白α的DNA结合能力。DNA结合受损会干扰蛋白质调节gene表达的能力并损害其肿瘤抑制功能。CCAAT/增强子结合蛋白α的肿瘤抑制功能受损导致急性髓性白血病中发生的异常白细胞的不受控制的产生。(换行)在所有散发性和家族性CEBPAgene突变的急性髓细胞性白血病患者中，有50%至75%的人在每个白血病细胞中都有两个突变的CEBPAgene。其余只有一个CEBPAgene突变。在偶发性病例中，突变仅出现在白血病细胞中，在家族性病例中，突变遍布全身。其他gene的体细胞突变也可能有助于CN-AML的发展。"}]}' WHERE id = 15031;
UPDATE genes SET GHR_metadata_ch = '{"gene": "IGHMBP2", "conditions": [{"name": "嵴髓性肌肉萎缩症唿吸窘迫1型", "detail": "已发现IGHMBP2gene中的60多个突变会导致1型呼吸窘迫（SMARD1）引起脊髓性肌萎缩症。SMARD1是一种遗传性疾病，通常在婴儿期就开始引起肌肉无力和呼吸衰竭。导致这种情况的大多数突变会改变IGHMBP2蛋白中的单个蛋白结构单元（氨基酸），并破坏蛋白解开DNA和RNA的能力。解旋酶功能的丧失阻碍了DNA复制以及RNA和蛋白质的产生。这些问题特别影响α运动神经元，α运动神经元是控制肌肉运动的脑干和脊髓中的特化细胞。改变的IGHMBP2蛋白会导致这些神经元受损并随时间死亡，尽管确切的机制尚不清楚。SMARD1儿童的α运动神经元累积死亡导致呼吸问题和进行性肌无力。"}]}' WHERE id = 15039;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ALG6", "conditions": [{"name": "ALG6先天性糖基化疾病", "detail": "ALG6gene的突变已被发现引起ALG6先天性糖基化障碍（ALG6-CDG，又称先天性糖基化障碍Ic型），这是一种拥有属性发育迟缓、视力问题、癫痫发作以及其他损害糖基化过程的酶缺陷/异常引起的体征和症状。"}]}' WHERE id = 15041;
UPDATE genes SET GHR_metadata_ch = '{"gene": "GNE", "conditions": [{"name": "包涵体肌病2", "detail": "在包涵体肌病患者中已鉴定出GNEgene的40多个突变2。这些突变大多数会改变酶几个区域中的单个蛋白质结构单元（氨基酸）。一些突变会删除一段酶或以其他方式改变其结构。（换行）不同的GNE突变导致不同人群中的包涵体肌病2。一种突变导致伊朗犹太人患有这种疾病；这种遗传变化将氨基酸蛋氨酸替换为称为激酶结构域（写为Met712Thr或M712T）的酶区域中712位的氨基酸苏氨酸。在称为Nonaka肌病的日本人群中，最常见的GNE突变用酶激酶结构域（写为Val572Leu或V572L）中第572位的氨基酸亮氨酸取代了氨基酸缬氨酸。（换行）导致包涵体肌病2的突变降低了由GNEgene产生的酶的活性，这降低了唾液酸的产生。结果，较少的这种简单的糖可用于附着于细胞表面分子。研究人员正在努力确定唾液酸短缺如何导致包涵体肌病患者进行性肌无力2。唾液酸对于许多不同细胞和组织的正常功能很重要，因此尚不清楚为什么这种疾病的体征和症状似乎仅限于骨骼肌。"}, {"name": "唾液酸", "detail": "已经发现GNEgene中的几个突变引起唾液尿症。这些突变中的每一个都改变了称为变构位点的酶区域中的单个氨基酸。该区域对于酶的正常调节至关重要。（换行）仔细控制由GNEgene产生的酶，以确保细胞产生适量的唾液酸。当不再需要唾液酸时，反馈系统关闭酶。变构位点的突变破坏了这种反馈机制，导致唾液酸的过量生产。这种简单的糖在细胞内积聚并在尿液中排泄。研究人员正在努力确定体内唾液酸的积累如何干扰唾液尿症患者的正常发育。"}]}' WHERE id = 15048;
UPDATE genes SET GHR_metadata_ch = '{"gene": "CYP17A1", "conditions": [{"name": "17α-羟化酶/17,20-裂解酶缺乏症", "detail": "已经发现CYP17A1gene中的数十个突变导致17-羟化酶/17,20-裂解酶缺乏症。这种情况会影响某些产生激素的腺体的功能，导致高血压（高血压）和性发育异常。与这种情况相关的突变减少或消除了17-羟化酶和17,20-裂解酶活性。减少这些活动导致部分17-羟化酶/17,20-裂解酶缺乏症，而这些活性的完全丧失会导致更严重的疾病形式，称为完全17-羟化酶/17,20-裂解酶缺乏症。(换行)没有17-羟化酶活性，孕烯醇酮和孕酮不会转化为17-羟基孕烯醇酮或17-羟基孕酮，从而损害糖皮质激素的产生。相反，孕烯醇酮和黄体酮被加工形成盐皮质激素。过量的这些盐调节激素导致高血压和血液中钾水平低（低钾血症）。(换行)17,20-裂解酶活性的丧失会损害性激素的产生。在女性中，缺乏女性性激素会破坏内部生殖器官（卵巢和子宫）的发育以及继发性特征，如乳房和月经期。在染色体男性（具有X和Y染色体的个体）中，缺乏男性性激素导致外生殖器的异常发育。根据病情的严重程度，这些受影响的个体可能具有异常的男性生殖器，看不清楚男性或女性的生殖器（模棱两可的生殖器）或典型的女性生殖器。"}, {"name": "其他疾病", "detail": "已发现少量CYP17A1gene突变会导致孤立的17,20-裂合酶缺乏症，其特征是性发育异常而无高血压或低钾血症。这些突变改变了CYP17A1蛋白的一个区域，该区域在该酶的17,20-裂解酶功能中起作用，但在其17--羟化酶功能。结果，17,20-裂解酶活性严重降低，但17-羟化酶活性正常。和17岁一样-羟化酶/17,20-裂解酶缺乏症（如上所述），17,20-裂解酶活性受损会破坏性激素的产生，导致内部或外部生殖器官异常发育，并导致受影响个体的青春期延迟或缺失。"}]}' WHERE id = 15056;
UPDATE genes SET GHR_metadata_ch = '{"gene": "STAT4", "conditions": [{"name": "系统性硬皮病", "detail": "STAT4gene的正常变异与发生系统性硬皮病的风险增加有关，系统性硬皮病是一种自身免疫性疾病，其特征是皮肤和内脏器官中疤痕组织（纤维化）的积累。尽管已知STAT4gene可刺激免疫系统对病原体的反应，但尚不清楚gene变异如何导致系统性硬皮病的风险增加。研究人员认为，遗传和环境因素的结合可能在疾病的发展中起作用。"}, {"name": "幼年特发性关节炎", "detail": "遗传学家庭参考提供有关青少年特发性关节炎的信息。"}, {"name": "类风湿关节炎", "detail": "遗传学家庭参考提供有关类风湿关节炎的信息。"}, {"name": "系统性红斑狼疮", "detail": "遗传学家庭参考提供有关系统性红斑狼疮的信息。"}, {"name": "自身免疫性疾病", "detail": "研究表明，STAT4gene的正常变异与几种自身免疫性疾病的风险增加有关。当免疫系统发生故障并攻击身体的组织和器官时，就会发生自身免疫性疾病。这些疾病包括系统性红斑狼疮，类风湿性关节炎和干燥综合征。（换行）与自身免疫性疾病风险增加相关的变体改变了STAT4gene中的单个DNA构建块（核苷酸）。尚不清楚gene变异如何导致这些疾病的风险增加。研究人员认为，遗传和环境因素的结合可能在自身免疫的发展中起作用。"}]}' WHERE id = 15063;
UPDATE genes SET GHR_metadata_ch = '{"gene": "PEPD", "conditions": [{"name": "脯氨酸酶缺乏症", "detail": "在患有脯氨酸蛋白酶缺乏症的人中，已经鉴定出PEPDgene中至少19个突变，脯氨酸蛋白酶缺乏症是一种具有多种体征和症状的疾病，包括皮肤问题和智力障碍。在脯氨酸酶缺乏症患者中鉴定出的PEPDgene突变导致脯氨酸酶活性丧失。(换行)尚不清楚缺乏脯氨酸酶活性如何导致脯氨酸酶缺乏症的各种体征和症状。研究人员已经提出，未被分解的二肽的积累可能导致细胞死亡。当细胞死亡时，其内容物被释放到周围组织中，这可能引起炎症并导致在蛋白酶缺乏症中看到的皮肤问题。细胞外基质重塑过程中胶原蛋白分解受损也可能导致皮肤问题。脯氨酸酶缺乏症中发生的智力障碍可能是由于加工神经肽的问题所致，神经肽是富含脯氨酸的脑信号蛋白。尚不清楚缺乏蛋白水解酶活性如何导致蛋白水解酶缺乏症的其他特征。"}]}' WHERE id = 15064;
UPDATE genes SET GHR_metadata_ch = '{"gene": "PROC", "conditions": [{"name": "蛋白C缺乏症", "detail": "已发现PROCgene中至少有270个突变导致蛋白C缺乏。这些突变大多数会改变蛋白C中的单个蛋白结构单元（氨基酸），从而破坏其控制凝血的能力。根据PROCgene的突变，蛋白C缺乏症可分为I型和II型。（换行）I型蛋白C缺乏症是由导致蛋白C水平降低的PROCgene突变引起的。受影响的个体没有足够的蛋白C来控制血液凝固，这导致蛋白C中异常血栓形成的风险增加不足。（换行）导致II型蛋白C缺乏的突变导致产生活性降低的蛋白C改变。患有这种疾病的个体的蛋白C水平正常，但该蛋白无法与参与凝血的其他分子相互作用。如果蛋白C不能控制血液凝固，可能会形成异常的血栓。"}]}' WHERE id = 15068;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ACTG2", "conditions": [{"name": "肠假性梗阻", "detail": "在假性肠梗阻患者中已鉴定出ACTG2gene的几种突变，这种疾病的特征是平滑肌收缩受损，肠蠕动和膀胱收缩减少，从而抑制尿液排泄。"}, {"name": "巨囊藻-微结肠-肠蠕动综合征", "detail": "ACTG2gene中的几个遗传突变与肠道假性梗阻有关，这种情况会损害导致肠道的平滑肌收缩，膀胱引起蠕动障碍并抑制排尿。"}, {"name": "其他疾病", "detail": "ACTG2gene中的几个遗传突变与MMIHS的ACTG2相关疾病有关，例如i）肠旋转不良，这种情况发生在肠折叠不正确时；取而代之的是，它们异常扭曲，导致食物移动阻塞ii）复发性尿路感染和膀胱功能受损，这比MMIHS轻，或发生在较晚的年龄。"}]}' WHERE id = 15083;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SKI", "conditions": [{"name": "Shprintzen-Goldberg综合征", "detail": "在患有Shprintzen-Goldberg综合征的人中发现了SKIgene的至少10个突变，该病的特征是独特的面部特征，骨骼异常和智力残疾。这些突变大多数会改变SKI蛋白中的单个蛋白结构单元（氨基酸）。许多突变改变了与SMAD蛋白结合的SKI蛋白区域。据认为，改变的滑雪蛋白不能附着于SMAD蛋白，这允许TGF-β？信号继续不受控制。过量的TGF-β？信号改变了gene活性的调节，并可能破坏包括骨骼和大脑在内的许多身体系统的发育，从而导致Shprintzen-Goldberg综合征的各种体征和症状。"}]}' WHERE id = 15088;
UPDATE genes SET GHR_metadata_ch = '{"gene": "GH1", "conditions": [{"name": "孤立的生长激素缺乏症", "detail": "已发现GH1gene中的70多个突变会导致孤立的生长激素缺乏症，这种疾病的特征是生长缓慢和身材矮小。阻止生长激素产生的突变，例如大部分gene的缺失，会导致孤立的IA型生长激素缺乏症和6个月大时的严重生长衰竭。在整个gene中发现了导致IB型的GH1gene突变，并允许产生一些生长激素。（换行）导致分离的生长激素缺乏II型的大多数GH1gene突变发生在称为内含子3的gene的一部分中。这些突变导致生长激素的产生比正常情况短。这种短版本的生长激素不会从细胞中释放出来，也不能在体内工作。此外，这种较短的生长激素会干扰正常生长激素的功能，因此几乎没有可用于的功能性生长激素。可用生长激素的减少导致II型孤立生长激素缺乏症的体征和症状。"}]}' WHERE id = 15122;
UPDATE genes SET GHR_metadata_ch = '{"gene": "LPIN2", "conditions": [{"name": "马吉德综合症", "detail": "At least three mutations in the LPIN2 gene have been identified in people with Majeed syndrome. These mutations alter the structure and function of lipin-2, leading to chronic, abnormal inflammation in some of the body''s tissues. It is unclear how LPIN2 gene mutations lead to the specific features of Majeed syndrome, including bone disease, a shortage of red blood cells (anemia), and inflammatory skin disorders.\r"}]}' WHERE id = 15156;
UPDATE genes SET GHR_metadata_ch = '{"gene": "BTD", "conditions": [{"name": "生物素酶缺乏症", "detail": "在患有生物素酶缺乏症的人中已经鉴定出BTDgene的几种突变，这种疾病会影响身体的许多部位，如果不加以治疗会导致发育延迟。BTD中的突变可导致称为严重生物素酶缺乏症的严重形式，或由于生物素酶活性降低导致生物素回收受损而导致的称为部分生物素酶缺乏症的较轻形式。"}, {"name": "利综合征", "detail": "在患有Leigh综合征的人中已经鉴定出BTDgene的几种突变，Leigh综合征是在生命的第一年观察到的一种神经系统疾病，其特征是精神和运动能力的逐步丧失（精神运动退化）和导致死亡的呼吸衰竭。临床症状包括呕吐，腹泻，吞咽困难，这会破坏婴儿的进食，壮成长，肌张力低下，肌张力障碍，共济失调和周围神经病变。"}]}' WHERE id = 15157;
UPDATE genes SET GHR_metadata_ch = '{"gene": "CUL7", "conditions": [{"name": "3-M综合征", "detail": "在患有3-M综合征的人中，已经鉴定出CUL7gene中至少25个突变。这些突变中的一些将一个蛋白质结构单元（氨基酸）替换为cullin-7蛋白中的另一个氨基酸。其他导致cullin-7蛋白异常短且无功能。(换行)西伯利亚雅库特族中具有3-M综合征变异的个体在每个细胞的CUL7gene的两个拷贝中均具有特定的突变。在制备cullin-7蛋白（写为Gln1553Ter或Q1553X）的说明中，此突变用过早的终止信号替换了氨基酸谷氨酰胺。（换行）CUL7gene中的突变破坏了cullin-7蛋白将E3泛素连接酶复合物的成分聚集在一起的能力，从而干扰了用泛素标记其他蛋白的过程（泛素化）。尚不清楚泛素化受损如何导致生长迟缓以及3-M综合征的其他体征和症状。"}]}' WHERE id = 15160;
UPDATE genes SET GHR_metadata_ch = '{"gene": "IVD", "conditions": [{"name": "异戊酸血症", "detail": "At least 25 mutations in the IVD gene have been identified in people with isovaleric acidemia. Some of these mutations disrupt the normal function of the enzyme, while other mutations prevent the cell from producing any functional enzyme. As a result, the body is unable to break down leucine properly.  Defects in leucine processing allow  several potentially harmful substances, including a compound called isovaleric acid, to build up to toxic levels in the body. An accumulation of isovaleric acid causes people with isovaleric acidemia to have a characteristic odor of sweaty feet. The buildup of isovaleric acid and related compounds also damages the brain and nervous system, leading to poor feeding, lack of energy (lethargy), seizures, and the other signs and symptoms of isovaleric acidemia.\r"}]}' WHERE id = 15161;
UPDATE genes SET GHR_metadata_ch = '{"gene": "PRKAG2", "conditions": [{"name": "沃尔夫-帕金森-怀特综合症", "detail": "在PRKAG2gene中已鉴定出至少七个引起Wolff-Parkinson-White综合征的突变。一些具有这些突变的人还具有肥厚型心肌病的特征，肥厚型心肌病是一种心脏病，会扩大并削弱心脏（心脏）肌肉。研究人员不确定PRKAG2突变如何导致这些心脏疾病的发展。研究表明，这些突变改变了心脏中AMP激活的蛋白激酶的活性，破坏了该酶对细胞能量需求变化做出反应的能力。但是，尚不清楚遗传变化是使酶过度活化还是降低其活性。（换行）研究表明，AMP激活的蛋白激酶活性的变化允许称为糖原的复合糖在心肌细胞内异常积聚。这种物质的积累扩大了这些细胞，这可能导致肥厚型心肌病。研究人员继续调查心脏中糖原的异常积累是否也与Wolff-Parkinson-White综合征所特有的电信号问题有关。(换行)其他研究发现，改变的AMP激活的蛋白激酶活性与心脏中某些离子通道调节的变化有关。这些变化可能有助于解释Wolff-Parkinson-White综合征患者异常心律（心律不齐）的风险增加。"}, {"name": "家族性心房颤动", "detail": "遗传学家庭参考资料提供有关家族性心房颤动的信息。"}, {"name": "家族性肥厚型心肌病", "detail": "遗传学家庭参考提供有关家族性肥厚型心肌病的信息。"}, {"name": "其他疾病", "detail": "在患有其他心脏病的人中发现了PRKAG2gene的几个突变。例如，该gene中的特定突变导致称为心脏致死性先天性糖原贮积病的非常严重的心脏病形式。具有这种突变的人天生心脏非常肿大（心脏肥大），心脏内的电信号异常。这些异常会在生命早期导致呼吸窘迫和心力衰竭。导致这种情况的突变改变了AMP激活的蛋白激酶gamma-2亚基中的单个蛋白结构单元（氨基酸）。具体而言，此突变将氨基酸精氨酸替换为位置531处的氨基酸谷氨酰胺（写为Arg531Gln或R531Q）。研究表明，这种严重的疾病可能与心肌细胞内糖原的异常积累有关。(换行)PRKAG2gene的其他突变与影响心肌和骨骼肌的疾病有关。这些突变改变了AMP激活的蛋白激酶的γ-2亚基中的单个氨基酸。具有这些遗传变化的个体除了肥厚型心肌病和心脏内异常的电信号传导外，通常还会经历肌肉疼痛和僵硬，尤其是在运动后。尚不清楚为什么某些PRKAG2突变的作用似乎仅限于心脏，而其他突变会引起影响心肌和骨骼肌的体征和症状。"}]}' WHERE id = 15170;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SLC39A14", "conditions": [{"name": "高锰血症伴肌张力障碍", "detail": "至少有5个SLC39A14gene突变被发现会引起高锰血症和肌张力障碍2，这种情况始于儿童早期，拥有属性血液和大脑中锰含量高（高锰血症），肌肉不自主紧张（肌张力障碍），以及其他运动问题。这些突变削弱了锰向肝细胞的运输。结果，该元件不能通过胆汁从体内移除。过量的锰在血液中积累，随后在脑细胞中积累，特别是在帮助控制运动的大脑区域中的细胞。高水平的锰会破坏这些细胞，导致神经系统问题，使控制运动变得困难。"}]}' WHERE id = 15171;
UPDATE genes SET GHR_metadata_ch = '{"gene": "CYP4V2", "conditions": [{"name": "Bietti晶体营养不良", "detail": "在Bietti晶体营养不良患者中至少发现了42个CYP4V2gene突变，这种疾病中，许多小的，黄色或白色的晶体状脂肪（脂质）化合物沉积物积聚在眼睛后部的光敏组织中（视网膜）。沉积物损害视网膜，导致进行性视力丧失。（换行）导致Bietti晶体营养不良的CYP4V2gene突变预计会以降低或消除其活性的方式改变CYP4V2酶的结构。突变可能影响脂质分解；但是，尚不清楚它们如何导致Bietti晶体营养不良的特定体征和症状。由于未知原因，具有相同CYP4V2gene突变的个体的体征和症状严重程度差异显着。"}, {"name": "其他疾病", "detail": "CYP4V2gene的某些常见变异（多态性）与一种称为深静脉血栓形成（DVT）的血凝块的风险增加有关。DVT最常发生在腿或手臂的深静脉中。如果这些凝块通过血液传播，它们可以存放在肺部并导致危及生命的并发症，称为肺栓塞。虽然已知4号染色体上的附近gene与凝血有关，但尚不清楚CYP4V2gene变异如何影响这一过程。"}]}' WHERE id = 15265;
UPDATE genes SET GHR_metadata_ch = '{"gene": "TCHH", "conditions": [{"name": "难辨的头发综合症", "detail": "已发现TCHHgene中至少有一个突变会导致难以梳理的头发综合症。这种情况的特点是干燥，卷曲，金色的头皮头发不能梳理。这种情况通常会随着时间的推移而改善，到青春期，患有难以梳理的头发综合症的人的头发平坦且质地正常或接近正常。（换行）已鉴定的TCHHgene突变导致用于制备毛透明质酸的说明书中的过早停止信号，导致活性严重降低的异常短蛋白质。功能蛋白的缺乏（缺乏）减少了三透明质酸蛋白和角蛋白中间丝之间形成的交联。结果，毛干的横截面变成三角形，心形或平坦。这些有角度的发干导致卷发的头发不会平躺，这是典型的难以梳理的头发综合症。"}]}' WHERE id = 15271;
UPDATE genes SET GHR_metadata_ch = '{"gene": "FLG", "conditions": [{"name": "特应性皮炎", "detail": "FLGgene的突变与称为特应性皮炎（也称为特应性湿疹）的皮肤病密切相关。这种情况的特点是皮肤干燥，发痒和红疹。特应性皮炎患者中有20%至30%患有FLGgene突变，而无特应性皮炎的普通人群中有8%至10%患有FLGgene突变。在该gene中发生突变只会增加发生特应性皮炎的风险。并非每个患有FLGgene突变的人都会患上这种疾病。其他gene和环境因素导致特应性皮炎。在FLGgene的两个拷贝中都有突变的个体比仅在该gene的一个拷贝中具有突变的个体发生特应性皮炎的风险更大。具有两个突变拷贝的人的病情通常更为严重。（换行）除了FLGgene突变之外，由每个profilaggrin分子产生的丝聚蛋白的数量与特应性皮炎的发展有关；具有产生10个拷贝的遗传指令的个体比具有产生11或12个拷贝的指示的个体发生疾病的风险更大。（换行）在特应性皮炎患者中已经鉴定出FLGgene中大约40个突变。这些突变导致产生异常短的原丝蛋白分子，其不能被切割以产生丝聚蛋白。由此导致的丝聚蛋白短缺会损害皮肤的屏障功能。此外，丝聚蛋白的缺乏导致天然保湿因子的缺乏，这使得过量的水通过皮肤损失并导致受影响个体的皮肤干燥。(换行)研究表明，由丝聚蛋白缺乏引起的皮肤屏障功能受损有助于过敏性疾病的发展。特应性皮炎最初不是由过敏反应引起的，尽管有时会引起过敏反应的物质（过敏原）被认为有助于皮疹的发作。此外，特应性皮炎患者发生其他过敏性疾病的可能性增加，包括哮喘，花粉热和食物过敏。（发展过敏性疾病的倾向被称为特应性疾病。）"}, {"name": "其他疾病", "detail": "已经发现超过40种FLGgene突变引起称为寻常鱼鳞病的相对常见的皮肤病，其特征在于皮肤干燥，鳞状，特别是在冬季空气湿度低的时候。受影响的人的手掌和脚底也可能有异常突出的折痕（手掌和足底超线性），过敏性疾病（包括特应性皮炎）的风险增加（如上所述）。通常，在FLGgene的一个拷贝中发生突变的个体受到轻度影响；他们可能只有季节性症状，或者可能永远没有明显的皮肤问题。具有该gene的两个突变拷贝的个体具有可能全年出现的更严重的症状。（换行）一些与特应性皮炎相关的突变也在寻常型鱼鳞病患者中发现。与特应性皮炎一样，突变导致异常短的原丝蛋白分子的产生，该分子不能被切割以产生丝聚蛋白。丝聚蛋白的缺乏及其加工产生的分子导致寻常鱼鳞病的干燥，鳞状皮肤特征。皮肤屏障功能的损害可能导致一些寻常性鱼鳞病患者发生过敏性疾病。具有FLGgene突变的个体患哮喘的风险增加，但前提是他们也患有特应性皮炎。FLGgene突变也会增加花粉热，食物过敏和皮肤对镍敏感的风险，无论该人是否患有特应性皮炎。其他因素，例如与猫一起生活和吸烟烟草制品，可能分别增加寻常型鱼鳞病患者特应性皮炎和哮喘的风险。"}]}' WHERE id = 15301;
UPDATE genes SET GHR_metadata_ch = '{"gene": "DGUOK", "conditions": [{"name": "脱氧鸟苷激酶缺乏症", "detail": "在脱氧鸟苷激酶缺乏症患者中已鉴定出DGUOKgene中约40个突变。这些突变中的一些改变了脱氧鸟苷激酶中的单个蛋白质结构单元（氨基酸）。其他突变导致异常缩短的无功能酶或导致酶错误地拼接在一起。DGUOKgene中的（换行）突变降低或消除了脱氧鸟苷激酶的活性。酶活性降低导致线粒体DNA的产生和维持问题。线粒体DNA数量的减少（称为线粒体DNA耗竭）会损害人体许多细胞和组织中的线粒体功能。这些问题导致与脱氧鸟苷激酶缺乏症相关的神经和肝功能障碍。"}]}' WHERE id = 15328;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ZAP70", "conditions": [{"name": "ZAP70相关的严重联合免疫缺陷", "detail": "More than 12 mutations in the ZAP70 gene have been identified in people with ZAP70-related severe combined immunodeficiency (SCID). These mutations either change single protein building blocks (amino acids) in the protein sequence or disrupt how genetic information is pieced together to make the blueprint for producing the protein. (line-break) Mutations in the ZAP70 gene prevent the production of zeta-chain-associated protein kinase or result in a protein that is unstable and cannot perform its function. A loss of functional zeta-chain-associated protein kinase leads to the absence of CD8+ T cells and an excess of inactive CD4+ T cells. The resulting shortage of active T cells causes people with ZAP70-related SCID to be more susceptible to infection.\r"}]}' WHERE id = 15344;
UPDATE genes SET GHR_metadata_ch = '{"gene": "BCR", "conditions": [{"name": "慢性粒细胞白血病", "detail": "涉及BCRgene的遗传重排（易位）引起一种称为慢性粒细胞白血病的造血细胞癌症。这种生长缓慢的癌症导致异常白细胞的过量产生。该病的常见特征包括过度疲倦（疲劳），发烧，体重减轻和脾脏肿大。（换行）这种情况下涉及的易位，写为t（9；22），将来自9号染色体的ABL1gene的一部分与来自22号染色体的BCRgene的一部分融合，产生称为BCR-ABL1的异常融合gene。包含一条9号染色体和BCR-ABL1融合gene的异常22号染色体通常称为费城染色体。易位是在一个人的一生中获得的，仅存在于异常的血细胞中。这种类型的遗传变化称为体细胞突变，不是遗传的。（换行）由异常融合gene产生的称为BCR-ABL1的蛋白质发出细胞生长和分裂的信号，并阻断异常或不需要的细胞的自我破坏。BCR-ABL1蛋白始终打开，因此受影响血细胞的生长和分裂不受控制，导致异常细胞的过量生产。（换行）费城染色体的存在为分子疗法提供了目标。"}, {"name": "其他癌症", "detail": "BCR-ABL1融合gene（如上所述）也参与称为急性白血病的快速生长的血细胞癌。在5%的儿童和高达30%的成人中发现了B细胞急性淋巴细胞白血病，在急性髓性白血病中很少见。与慢性粒细胞白血病一样，BCR-ABL1蛋白刺激异常白细胞的过量产生，从而导致癌症。发生的血癌形式很可能受到获得突变和其他发生的遗传变化的血细胞类型的影响。"}]}' WHERE id = 15352;
UPDATE genes SET GHR_metadata_ch = '{"gene": "FGFR3", "conditions": [{"name": "软骨发育不全", "detail": "FGFR3gene中的两个突变导致超过99%的软骨发育不全病例，这是短肢侏儒症的一种形式。两种突变导致FGFR3蛋白发生相同的变化。具体地，蛋白质结构单元（氨基酸）甘氨酸被蛋白质位置380处的氨基酸精氨酸取代（写为Gly380Arg或G380R）。研究人员认为，这种遗传变化会导致受体过度活跃，从而导致这种疾病中发生的骨骼生长紊乱。"}, {"name": "Crouzon综合征伴黑棘皮症", "detail": "在患有黑棘皮症的Crouzon综合征患者中发现了一个单一的FGFR3gene突变，这种突变导致颅骨过早连接（颅缝早闭），导致头部畸形和独特的面部特征，以及一种称为黑棘皮症的皮肤异常。导致这种情况的遗传变化将FGFR3蛋白（写为Ala391Glu或A391E）391位的氨基酸谷氨酸替换为氨基酸丙氨酸。改变后的受体比正常情况下更容易打开，即使没有生长因子的附着，也可以触发信号通路。由此产生的FGFR3蛋白过度活跃会破坏颅骨和皮肤的正常生长，从而导致黑棘皮症的Crouzon综合征特征。"}, {"name": "表皮痣", "detail": "在约30%患有某种类型的表皮痣（复数：痣）的人中发现了FGFR3gene的突变。具体而言，FGFR3gene突变与某些角质形成细胞表皮痣有关，这是由称为角质形成细胞的皮肤细胞组成的异常皮肤生长。在其他类型的表皮痣中未发现FGFR3gene突变。（换行）表皮痣中最常见的FGFR3gene突变改变FGFR3蛋白中的单个氨基酸。氨基酸精氨酸在248位被氨基酸半胱氨酸取代（写为Arg248Cys或R248C）。该突变产生的蛋白质在不附着生长因子的情况下打开，这意味着FGFR3蛋白质始终具有活性。研究表明，具有这种FGFR3gene突变的细胞比正常细胞生长和分裂更多。由此导致的皮肤细胞过度生长导致表皮痣。(换行)在表皮痣中发现的FGFR3gene突变也发生在患有另一种称为脂溢性角化病的皮肤异常的人以及患有死亡性发育不良，患有黑棘皮病的Crouzon综合征和SADDAN的人中（每个在本页的另一节中描述）。然而，与骨骼状况相反，与表皮痣相关的突变是在出生前发育的早期阶段随机出现的体细胞突变。突变仅存在于痣的细胞中，而不存在于其周围的正常皮肤细胞中。"}, {"name": "软骨发育不良", "detail": "在软骨发育不良的人中已经鉴定出FGFR3gene的20多个突变，软骨发育不良是另一种形式的短肢侏儒症，比软骨发育不全轻。许多情况是由两个特定的FGFR3gene突变之一引起的，这两个突变均导致FGFR3蛋白发生相同的变化。具体而言，将氨基酸天冬酰胺替换为蛋白质540位的氨基酸赖氨酸（写为Asn540Lys或N540K）。其他FGFR3gene突变可能会导致少数软骨发育不良。尽管尚未解释这些突变的影响，但它们可能导致受体轻度过度活跃，从而导致该疾病中发生的骨骼生长紊乱。"}, {"name": "泪-耳-牙本质-数字综合征", "detail": "已经发现FGFR3gene中的至少一种突变引起泪-耳-牙本质-数字（LADD）综合征。LADD综合征的主要特征是异常的泪液产生，耳朵畸形并伴有听力损失，唾液产生减少，牙齿小和手畸形。导致LADD综合征的FGFR3gene突变用FGFR3受体蛋白（写为Asp513Asn或D513N）中513位的氨基酸天冬酰胺取代氨基酸天冬氨酸。这种突变很可能会降低FGFR3受体蛋白与其生长因子连接时触发细胞内化学信号传导的能力。细胞信号传导中的这些缺陷破坏了细胞的成熟和发育，从而导致LADD综合征患者的耳朵，骨骼和眼睛和嘴巴中的腺体异常形成。"}, {"name": "Muenke综合征", "detail": "已显示FGFR3gene中的单个突变会引起Muenke综合征，这是一种导致颅突狭窄，导致头部畸形和独特面部特征的疾病。导致这种情况的突变将FGFR3蛋白（写为Pro250Arg或P250R）中250位的氨基酸脯氨酸替换为氨基酸精氨酸。这种突变导致产生过度活跃的受体，这使得颅骨的骨骼比正常人更快融合。（换行）Pro250Arg突变也已在一些明显孤立的冠状颅突神经病患者中发现。这种情况的特征是生长线过早融合，该生长线从耳朵到耳朵穿过头部顶部（冠状缝线）。患有孤立性冠状颅突狭窄的人没有Muenke综合征的其他特征，例如听力下降，手脚异常或发育迟缓。"}, {"name": "SADDAN", "detail": "在患有SADDAN（严重的软骨发育不全，发育迟缓和黑棘皮病）的人中，已经鉴定出FGFR3gene的一种突变。SADDAN的特征是短肢侏儒症（软骨发育不全）；深刻的发展延迟；和厚实，黑暗，天鹅绒般的皮肤。导致这种情况的遗传变化将氨基酸蛋氨酸替换为FGFR3蛋白650位的氨基酸赖氨酸（写为Lys650Met或K650M）。研究人员认为，这种突变会强烈过度激活FGFR3蛋白，从而导致严重的骨骼生长问题。尚不确定该突变如何导致发育迟缓或黑棘皮病。"}, {"name": "Thanatophoric发育不良", "detail": "已经在患有死亡性发育不良的人中鉴定出FGFR3gene的突变，这是一种严重的骨骼疾病，其特征是四肢极短且胸部狭窄。已发现至少10个FGFR3gene突变引起I型肌萎缩性发育不良。这些突变大多数会改变FGFR3蛋白中的单个氨基酸。最常见的突变是将氨基酸半胱氨酸替换为蛋白质248位的氨基酸精氨酸（写为Arg248Cys或R248C）。其他突变导致蛋白质比正常情况更长。(换行)仅显示一种突变引起II型肌痛性发育不良。该突变用FGFR3蛋白（写为Lys650Glu或K650E）的650位氨基酸谷氨酸取代氨基酸赖氨酸。与引起I型肌痛性不典型增生的突变相比，这种变化影响FGFR3蛋白的不同部分。（换行）导致两种类型的死亡发育不良的遗传变化导致FGFR3受体过度活跃，这导致在这种情况下发生的骨生长的严重问题。"}, {"name": "膀胱癌", "detail": "一些gene突变是在一个人的一生中获得的，并且仅存在于某些细胞中。这些被称为体细胞突变的变化不是遗传的。FGFR3gene的体细胞突变与某些膀胱癌病例有关。这些突变使FGFR3蛋白过度活化，这可能指导膀胱细胞异常生长和分裂。这种不受控制的细胞分裂导致膀胱肿瘤的形成。（换行）FGFR3gene中的体细胞突变仅在膀胱细胞中发生时与膀胱癌相关。当这些相同的突变从亲本遗传（并且发生在身体的所有细胞中）时，它们引起骨骼疾病而不是发育不良（如上所述）。"}, {"name": "多发性骨髓瘤", "detail": "遗传学家庭参考提供有关多发性骨髓瘤的信息。"}, {"name": "其他疾病", "detail": "已发现至少两个FGFR3gene突变会导致一种罕见的疾病，称为喜树状，身材高大，听力下降综合征（CATSHL综合征）。患有这种情况的人比平均水平高，通常会有听力损失。他们还可能有永久弯曲的手指或脚趾（弯曲）和其他骨骼异常。研究人员建议，与CATSHL综合征有关的FGFR3gene突变会降低FGFR3蛋白的功能，尽管目前尚不清楚该突变如何导致该病的体征和症状。脂溢性角化病患者中有30%〜70%发现FGFR3gene突变，脂溢性角化病是由皮肤细胞过度生长引起的皮肤小，黑，非癌（良性）肿瘤。脂溢性角化病在成年期发展，并且在大多数50岁以上的人中可见。与脂溢性角化病相关的FGFR3gene突变是体细胞突变，不会遗传。在脂溢性角化病患者中至少鉴定出9个FGFR3gene突变。这些突变改变了FGFR3蛋白中的单个氨基酸。突变的FGFR3蛋白异常活跃，导致皮肤细胞过度生长，导致脂溢性角化病。已经提出，脂溢性角化病中涉及的突变可能是由于暴露于紫外线（UV）引起的。（换行）在表皮痣中发现的体细胞Arg248Cys FGFR3gene突变（如上所述）也可引起Garcia-Hafner-Happle综合征（也称为成纤维细胞生长因子受体3表皮痣综合征）。这种情况的特点是柔软，天鹅绒般的角质形成细胞表皮痣和神经系统问题，如癫痫发作，智力残疾，连接大脑两半（胼call体）的组织发育不全，以及脑细胞丢失（皮质萎缩））。据认为，神经系统问题的发生是因为体细胞突变除了影响皮肤中的脑细胞外，还影响脑细胞。"}, {"name": "其他癌症", "detail": "除膀胱癌外，FGFR3gene的体细胞突变还与白细胞癌（多发性骨髓瘤）和宫颈癌有关。一些多发性骨髓瘤病例与涉及14号染色体和含有FGFR3gene的4号染色体区域的遗传物质重排（易位）有关。与宫颈癌相关的突变是FGFR3gene中单核苷酸的变化。（换行）导致多发性骨髓瘤和宫颈癌的FGFR3gene突变被认为在某些细胞中过度活化FGFR3蛋白。突变的受体在没有来自细胞外部的信号的情况下指导细胞生长和分裂。这种不受控制的分裂可能导致癌细胞过度生长。"}]}' WHERE id = 15360;
UPDATE genes SET GHR_metadata_ch = '{"gene": "FUS", "conditions": [{"name": "肌萎缩侧索硬化症", "detail": "已发现FUSgene中至少有85个突变会引起肌萎缩性侧索硬化症（ALS），这种疾病的特征是进行性肌肉无力，肌肉量减少和无法控制运动。这些突变大多数会改变FUS蛋白中的单个蛋白结构单元，并经常影响参与DNA结合和mRNA加工的蛋白区域。这些突变可能会干扰mRNA从细胞核中的转运。结果，FUS蛋白和mRNA被困在细胞内，并可能形成团块（聚集体），在某些ALS患者的控制肌肉运动（运动神经元）的神经细胞中发现了团块（聚集体）。目前尚不清楚蛋白质聚集体是否会导致导致ALS的神经细胞死亡。与由其他gene突变引起的散发性ALS或ALS的个体相比，由FUSgene突变引起的ALS患者倾向于在年轻时发展该疾病，并且预期寿命缩短。(换行)很少，由FUSgene突变引起的ALS患者也会出现额颞叶痴呆（FTD），这是一种进行性脑部疾病，会影响人格，行为和语言。目前尚不清楚为什么有些具有FUSgene突变的人会发展为FTD，而另一些则不会。患有这两种疾病的人被诊断患有ALS-FTD。"}, {"name": "尤文肉瘤", "detail": "遗传学家庭参考提供有关尤因肉瘤的信息。"}, {"name": "癌症", "detail": "涉及FUSgene的特定突变涉及几种类型的癌症。导致这些肿瘤的突变是在一个人的一生中获得的，并且仅存在于肿瘤细胞中。这种类型的遗传变化称为体细胞突变，不是遗传的。最常见的是，该gene的突变发现于称为软组织肉瘤的肿瘤中，其在骨骼或软组织如神经或软骨中发展。在粘液样脂肪肉瘤中也发现了FUSgene突变，粘液样脂肪肉瘤发生在人体的脂肪组织中，在骨髓中的血液形成细胞癌中也被称为急性髓细胞性白血病（AML）。与这些癌症相关的遗传变化是16号染色体（FUSgene所在的位置）与其他染色体之间遗传物质的重排（易位）。这些易位破坏了FUSgene中间的16号染色体，并将其与另一条染色体上的另一个gene融合，从而创建了融合gene。融合gene通常具有所涉及的两个gene的部分功能。FUSgene促进DNA转录和蛋白质产生，有助于促进细胞生长；该gene可能与另一个可以使细胞生长快速持续的gene融合。当细胞生长不受控制时，癌症就会发展。"}]}' WHERE id = 15373;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ERAP1", "conditions": [{"name": "强直性脊柱炎", "detail": "已发现ERAP1gene的几种变异（多态性）会影响强直性脊柱炎的风险。这些变异中的每一个都会改变内质网氨肽酶1中的单个蛋白质结构单元（氨基酸）。尽管研究人员认为蛋白质结构的变化可能会改变其两个主要功能之一，但对这些变化的影响知之甚少。目前还不清楚这些变化如何导致一个人患强直性脊柱炎的风险。其他遗传和环境因素，其中许多是未知的，也影响发展这种情况的机会。"}]}' WHERE id = 15382;
UPDATE genes SET GHR_metadata_ch = '{"gene": "CYP27A1", "conditions": [{"name": "脑腱黄瘤病", "detail": "在CYP27A1gene中已鉴定出至少90种引起脑腱黄瘤病的突变。脑腱黄瘤病是一种以身体许多部位的脂肪（脂质）异常储存为特征的疾病。大多数CYP27A1gene突变会改变固醇27-羟化酶中的一个蛋白质结构单元（氨基酸）。最常见的突变是将蛋白质中362位的氨基酸精氨酸变为氨基酸半胱氨酸（写为Arg362Cys或R362C）。氨基酸的变化通常会破坏蛋白质的正常功能，并削弱其帮助形成鹅去氧胆酸的能力。其他突变导致不产生功能性酶。结果，其他分子通过替代途径形成。一种名为胆固醇的分子，类似于胆固醇，在血液和组织中产生并积累。胆固醇也会积聚在组织中，但血液中的胆固醇水平通常是正常的。胆固醇和胆固醇在人体组织中的积累会导致脑腱黄瘤病的体征和症状。"}]}' WHERE id = 15396;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ARID1A", "conditions": [{"name": "棺材-西里斯综合症", "detail": "已知ARID1Agene的突变会引起Coffin-Siris综合征，这是一种以发育迟缓，第五（小指）手指或脚趾异常为特征的病症，以及由于产生部分活性的ARID1A蛋白而导致的特征性面部特征，所述ARID1A蛋白损害SWI/SNF复合物和染色质重塑。"}, {"name": "胆管癌", "detail": "已知ARID1Agene的突变会引起胆管癌，这是一种从胆汁酸开始的癌症，在胆汁酸的早期阶段没有任何症状，直到扩散到其他组织。临床症状包括黄疸，瘙痒，尿液深色，食欲不振，无意识的体重减轻，腹痛以及浅色和油腻的大便，由于其与许多不同疾病的重叠特征，被称为“非特异性”。胆管癌根据位置分类，如：；肝内胆管癌，肝门周围胆管癌（Klatskin肿瘤）和远端胆管癌。"}, {"name": "癌症", "detail": "已经在许多类型的癌症中发现了ARID1Agene的突变，包括女性的卵巢癌和子宫内膜癌（子宫内膜癌）以及肾癌，胃癌，膀胱癌，肺癌，乳腺癌和脑癌，并且被认为是由于SWI/SNF复合物的变化会损害正常的细胞分化。"}]}' WHERE id = 15407;
UPDATE genes SET GHR_metadata_ch = '{"gene": "CHD8", "conditions": [{"name": "自闭症谱系障碍", "detail": "自闭症谱系障碍（ASD）患者中已鉴定出30多种CHD8gene突变，这是一种以社交技能受损，沟通问题和重复行为为特征的多种疾病。CHD8gene的突变会损害CHD8蛋白的功能，并可能干扰其帮助控制大脑神经元数量和生长的能力。大脑部分过度的神经元和过度生长与ASD有关，但这些异常与疾病的行为特征之间的关系尚不清楚。"}]}' WHERE id = 15424;
UPDATE genes SET GHR_metadata_ch = '{"gene": "HGD", "conditions": [{"name": "碱性磷酸酶尿症", "detail": "在患有黑酸尿症的人中，HGDgene已鉴定出65个以上的突变。这些突变大多数会改变用于构建高尿酸氧化酶的单个氨基酸。例如，在欧洲人群中，最常见的HGD突变是在368位蛋白质（也称为Met368Val）上用氨基酸缬氨酸替代氨基酸蛋氨酸。（换行）HGDgene中的突变通过改变其结构使高尿酸氧化酶失活。如果没有这种酶的功能版本，苯丙氨酸和酪氨酸就不能正确分解，高纯酸会在体内积聚。过量的高纯酸和相关化合物沉积在结缔组织如软骨和皮肤中，导致它们变黑。随着时间的推移，这种物质在关节中的累积会导致关节炎。尿中也会排出尿黑酸，使尿液暴露在空气中会变黑。"}]}' WHERE id = 15430;
UPDATE genes SET GHR_metadata_ch = '{"gene": "GPI", "conditions": [{"name": "葡萄糖磷酸异构酶缺乏症", "detail": "在葡萄糖磷酸异构酶（GPI）缺乏症患者中已鉴定出30多个GPIgene突变。GPI缺乏症是一种遗传性疾病，会影响红细胞，红细胞将氧气输送到人体组织。患有这种疾病的人患有称为慢性溶血性贫血的病症，其中红细胞过早分解（经历溶血），导致红细胞短缺（贫血）。一些受影响的人也有神经系统问题，包括智力残疾和协调运动困难（共济失调）。(换行)导致GPI缺乏的大多数突变都取代了GPI酶中的单个蛋白质结构单元（氨基酸）。这些突变中的一些可能导致较不稳定的同型二聚体，损害糖酵解途径中酶的活性。由此导致的参与糖酵解途径的分子的不平衡最终损害红细胞维持其结构的能力，导致溶血。（换行）其他GPIgene突变可能导致单体更容易分解，从而干扰其在神经细胞中的功能。此外，单体的短缺阻碍了同型二聚体的形成，这损害了糖酵解途径。这些突变已在患有溶血性贫血和神经系统疾病的GPI缺乏症患者中发现。"}]}' WHERE id = 15431;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ADAMTSL2", "conditions": [{"name": "凝胶体发育不良", "detail": "在由于ADAMTS12蛋白缺陷引起的凝胶体发育异常的人中，已经鉴定出ADAMTSL2gene的几个突变，这些突变改变了不同组织中的微原纤维网络，破坏了细胞的正常功能。这种疾病的特征是身材矮小和心脏病。"}]}' WHERE id = 15448;
UPDATE genes SET GHR_metadata_ch = '{"gene": "PHF8", "conditions": [{"name": "X连锁智力障碍，Siderius型", "detail": "已发现PHF8gene中至少有四个突变会导致X连锁智力障碍，即Siderius型。这种情况的特征是轻度至中度智力障碍，仅在男性中发生。患病男孩的嘴唇通常有一个开口（唇裂），嘴顶有一个开口（c裂）。大多数PHF8gene突变会导致异常短的蛋白质从细胞核中转运出来。在细胞核之外，PHF8蛋白不能与染色质相互作用来调节gene表达。其他突变会损害蛋白质从染色质内组蛋白中去除甲基的能力，从而导致gene表达下降。虽然确切的疾病机制尚不清楚，但出生前脑细胞核中蛋白质功能受损或PHF8蛋白缺乏可能会阻止染色质重塑，改变与智力功能和颅骨中线结构形成有关的gene的正常表达。这种改变的gene表达导致患有X连锁智力障碍，Siderius型男性的智力障碍和唇left裂。"}]}' WHERE id = 15454;
UPDATE genes SET GHR_metadata_ch = '{"gene": "GRHPR", "conditions": [{"name": "原发性高草酸尿症", "detail": "已发现GRHPRgene中的25个以上突变会导致2型原发性高草酸尿症。这种情况是由称为草酸盐的物质过量产生引起的。过量的这种物质会导致肾脏和膀胱结石，这些结石始于儿童期，通常在成年早期导致肾脏疾病。草酸盐在全身其他多种组织中的沉积（全身性草酸沉着症）可能会导致其他健康问题。（换行）GRHPRgene突变破坏乙醛酸和羟基丙酮酸还原酶的产生或改变其结构。结果，酶活性不存在或严重降低，并且乙醛酸酯向乙醇酸酯的转化受到损害。乙醛酸酯积聚并转化为称为草酸盐的化合物。草酸盐通过肾脏过滤，或者作为废物在尿液中排泄，或者与钙结合形成草酸钙，草酸钙是肾脏和膀胱结石的主要成分。血液中草酸盐水平升高可导致全身性草酸沉着症，特别是影响2型原发性高草酸尿症患者的骨骼和血管壁。"}]}' WHERE id = 15456;
UPDATE genes SET GHR_metadata_ch = '{"gene": "BOLA3", "conditions": [{"name": "多发性线粒体功能障碍综合征", "detail": "已发现BOLA3gene中至少有三个突变会引起多种线粒体功能障碍综合症。这种严重疾病的特征是损害一种以上的线粒体功能，例如复合物I，II或III，丙酮酸脱氢酶，α-酮戊二酸脱氢酶或甘氨酸裂解系统的活性降低。患病的婴儿通常患有严重的脑功能障碍（脑病）和体内称为乳酸的化学物质水平升高（乳酸性酸中毒）。这些婴儿通常无法在婴儿期后存活。（换行）BOLA3gene突变导致产生可能快速分解的改变的蛋白质。尽管某些突变会影响BOLA3蛋白的两种同工型，但线粒体形式的丧失似乎是造成这种情况的原因。线粒体BOLA3蛋白的缺乏会损害Fe-S簇的形成。因此，受Fe-S簇存在影响的蛋白质，包括那些参与能量产生和甘氨酸分解的蛋白质，不能正常发挥作用。复合物I，II或III，丙酮酸脱氢酶或α-酮戊二酸脱氢酶的活性降低会导致潜在的致命性乳酸性酸中毒，脑病以及多种线粒体功能障碍综合征的其他体征和症状。在一些受影响的个体中，甘氨酸裂解系统的损伤导致甘氨酸的积累（高甘氨酸血症）。"}]}' WHERE id = 15461;
UPDATE genes SET GHR_metadata_ch = '{"gene": "DBH", "conditions": [{"name": "多巴胺β羟化酶缺乏症", "detail": "已经发现DBHgene中至少有六个突变引起多巴胺-羟化酶缺乏症。最常见的突变（通常写为IVS1+2T>C）会干扰多巴胺的正常加工吗-羟化酶。这种突变的结果是，产生了异常短的，无功能的酶形式。缺乏功能性多巴胺-羟化酶导致去甲肾上腺素短缺，导致难以调节血压和多巴胺中出现的其他自主神经系统问题-羟化酶缺乏症。"}, {"name": "其他疾病", "detail": "研究表明，DBHgene的某些变异（多态性）与注意力缺陷多动障碍（ADHD）的风险增加有关。DBHgene多态性也被认为会增加精神分裂症或单相重度抑郁症患者出现精神病症状的风险。然而，其他研究并未支持这些发现。据信许多遗传和环境因素是造成这些复杂状况的原因。"}]}' WHERE id = 15482;
UPDATE genes SET GHR_metadata_ch = '{"gene": "BSCL2", "conditions": [{"name": "进行性神经性腓骨肌萎缩症", "detail": "BSCL2gene的突变与2型Charcot-Marie-Tooth病有关，该疾病影响周围神经，其特征是手臂和腿部肌肉无力。gene突变改变蛋白质结构，导致其积累，导致大脑运动神经元受损。"}, {"name": "先天性全身性脂肪营养不良", "detail": "已经在患有先天性全身性脂肪营养不良（也称为Berardinelli-Seip先天性脂肪营养不良）2型的人中鉴定出BSCL2gene的突变，这是一种罕见的病症，其特征在于完全没有脂肪组织和非常肌肉的外观引起症状，例如：；高甘油三酯血症，糖尿病和智力障碍。该病症是由无功能的seipin蛋白引起的，该蛋白破坏包括脂滴在内的脂肪细胞的正常发育和功能。"}, {"name": "远端遗传性运动神经病，V型", "detail": "已经在患有远端遗传性运动神经病V型的人中鉴定出BSCL2gene的突变，V型是一种进行性疾病，其影响脊髓中的运动神经元，导致肌肉无力。该病是由于改变的seipin蛋白引起的，该蛋白积聚在内质网中并损害运动神经元。"}, {"name": "银综合症", "detail": "据报道，BSCL2gene中的几个突变会引起银综合症，该病的特征是肌肉无力，手部肌肉消瘦和腿部肌肉痉挛异常。该病是由于改变的seipin蛋白引起的，该蛋白在神经元内积累，导致细胞死亡。"}]}' WHERE id = 15491;
UPDATE genes SET GHR_metadata_ch = '{"gene": "TAT", "conditions": [{"name": "酪氨酸血症", "detail": "已发现至少22个TATgene突变引起II型酪氨酸血症。这种情况通常会影响眼睛，皮肤和智力发育。大多数这些突变改变TATgene内的单个DNA构建块（碱基对）。研究表明，改变后的TATgene产生的酪氨酸氨基转移酶活性降低。其他突变删除全部或部分TATgene，消除酶活性。这些突变的结果是酪氨酸未正确分解。酪氨酸水平升高，一些酪氨酸转化为可能对细胞有毒的其他分子。目前尚不清楚酪氨酸分解受损如何导致II型酪氨酸血症的皮肤，眼睛和智力问题。"}]}' WHERE id = 15519;
UPDATE genes SET GHR_metadata_ch = '{"gene": "RAD51", "conditions": [{"name": "先天性镜像运动障碍", "detail": "在患有先天性镜像运动障碍的人中，已经鉴定出至少四个RAD51gene突变，在这种情况下，身体一侧的故意运动反映了另一侧的非自愿运动。这些突变会改变RAD51蛋白质序列中的单个蛋白质结构单元（氨基酸），或在制备蛋白质的说明中引入过早的终止信号，从而导致蛋白质受损或缺失。功能性RAD51蛋白的这种短缺如何影响神经系统发育并导致先天性镜像运动障碍的体征和症状尚不清楚。"}, {"name": "乳腺癌", "detail": "遗传学家庭参考提供有关乳腺癌的信息。"}]}' WHERE id = 15532;
UPDATE genes SET GHR_metadata_ch = '{"gene": "LBR", "conditions": [{"name": "格林伯格发育不良", "detail": "已经发现LBRgene中的几个突变引起格林伯格发育不良。这种严重疾病的特征是异常的骨形成，并且在出生前是致命的。研究表明，这种情况是由层粘连蛋白B受体的固醇还原酶功能丧失引起的。一些导致格林伯格发育不良的LBRgene突变会改变lamin B受体固醇还原酶结构域中的单个蛋白质结构单元（氨基酸），从而导致固醇还原酶活性丧失。其他突变导致异常短的蛋白质可能无法正常工作。（换行）核纤层蛋白B受体的甾醇还原酶功能的丧失破坏了细胞内胆固醇的正常合成。缺乏这种功能还可能使胆固醇合成的潜在有毒副产物积聚在人体组织中。研究人员怀疑低胆固醇水平或其他物质的积累会破坏身体许多部位的生长和发育。但是，尚不清楚胆固醇合成的紊乱如何导致格林伯格发育不良的特定特征。"}, {"name": "其他疾病", "detail": "Mutations in the LBR gene can also cause Pelger-Huët anomaly, a condition that affects the shape of the nucleus and the structure of chromatin in certain cells but causes no outward signs or symptoms. The nuclei of blood cells called granulocytes normally have multiple segments (or lobes). In contrast, the nuclei of granulocytes in people with Pelger-Huët anomaly have fewer segments than normal (they are hypolobulated). In addition, the chromatin in affected cells appears coarser than usual when viewed under a microscope. (line-break) Mutations that cause Pelger-Huët anomaly usually lead to an abnormally short lamin B receptor and reduce the amount of functional protein. It is thought that impairment of the receptor''s DNA-binding domain leads to this condition.\r"}]}' WHERE id = 15540;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ANTXR2", "conditions": [{"name": "婴儿系统性透明质酸病", "detail": "已经发现ANTXR2gene中的几个突变引起婴儿全身性玻璃样变，其特征在于牙龈肥大，关节畸形（挛缩），儿童中非癌性肿块的形成，发展为蛋白质丢失性肠病，导致严重的腹泻，不能茁壮成长，以及一般消瘦和体重减轻（恶病质）。这导致有缺陷的ANTXR2蛋白导致透明（透明）物质在身体组织中异常积累。"}, {"name": "青少年透明纤维瘤病", "detail": "ANTXR2gene的一些突变已被证明会引起幼年透明纤维瘤病，这种疾病包括拥有属性肥大、关节畸形（挛缩）、骨骼异常和皮肤肿块，这些部位经常出现在手、颈部、头皮、耳朵和鼻子、关节折痕和生殖区。这是由破坏基底膜形成的有缺陷的ANTXR2蛋白引起的。"}]}' WHERE id = 15548;
UPDATE genes SET GHR_metadata_ch = '{"gene": "WNK4", "conditions": [{"name": "假性醛固酮增多症2型", "detail": "已发现WNK4gene中至少有8个突变会引起2型假性低醛固酮增多症（PHA2），这种疾病的特征是高血压（高血压）和血液中钾水平高（高钾血症）。这种情况下涉及的突变会改变WNK4蛋白中的单个蛋白结构单元（氨基酸）。WNK4蛋白的改变会损害其分解，导致WNK4蛋白水平高于正常水平，WNK4活性升高。WNK4活性的增加导致钠重吸收增加和钾分泌减少，从而导致高血压和高钾血症。"}, {"name": "其他疾病", "detail": "研究表明，在没有假性低醛固酮增多症2型的人群中，WNK4gene的正常变异与高血压（高血压）风险增加有关。遗传变异和环境因素的结合可能会影响这种复杂状况的发展。"}]}' WHERE id = 15549;
UPDATE genes SET GHR_metadata_ch = '{"gene": "PIGV", "conditions": [{"name": "马布里综合征", "detail": "已发现PIGVgene中至少有14个突变会引起马布里综合症，该病的特征是智力残疾，独特的面部特征，血液中称为碱性磷酸酶的酶水平升高（高磷血症）以及其他体征和症状。这些突变改变了GPI甘露糖基转移酶2酶中的单个蛋白质结构单元（氨基酸）。改变后的蛋白质无法将甘露糖添加到形成的GPI锚中。不完整的GPI锚不能附着在蛋白质上；没有锚，蛋白质不能与细胞膜结合并从细胞中释放出来。（换行）称为碱性磷酸酶的酶通常附着于GPI锚。但是，当锚受损时，碱性磷酸酶不能锚定在细胞膜上。相反，碱性磷酸酶从细胞中释放出来。碱性磷酸酶的这种异常释放是造成马布里综合征高磷血症的原因。目前尚不清楚PIGVgene突变如何导致Mabry综合征的其他特征，但这些体征和症状可能是由于缺乏将蛋白质正确的GPI锚定在细胞膜上所致。"}]}' WHERE id = 15560;
UPDATE genes SET GHR_metadata_ch = '{"gene": "KLKB1", "conditions": [{"name": "前激肽释放酶缺乏症", "detail": "在患有称为前激肽释放酶缺乏症的血液病患者中，至少鉴定出9个KLKB1gene突变，通常不会引起任何健康问题。由于其他原因进行血液检查时通常会发现这种情况。（换行）导致这种情况的KLKB1gene突变减少或消除受影响个体血液中的功能性血浆激肽释放酶，并可能损害内在凝血途径。研究人员认为，功能性血浆激肽释放酶蛋白的缺乏（缺乏）通常不会引起任何症状，因为另一个称为外在凝血途径（也称为组织因子途径）的过程可以补偿受损的内在凝血途径。任何一种途径都可以激活凝血过程后期所需的蛋白质。"}]}' WHERE id = 15563;
UPDATE genes SET GHR_metadata_ch = '{"gene": "TGIF1", "conditions": [{"name": "非综合征性全前脑", "detail": "已发现TGIF1gene中至少有13个突变引起非综合征性全前脑畸形。当大脑在早期发育过程中未能分成两半（半球）时，就会发生这种情况。TGIF1gene突变是非综合征性全前脑的第四大最常见原因。这些突变破坏了蛋白质与DNA结合或与其他蛋白质相互作用的能力。结果，TG相互作用因子不能阻断TGF-β的信号？途径和视黄酸。如果涉及前脑发育的信号没有得到适当的调节，大脑就不会分成两个半球。非综合征性全前脑的体征和症状是由大脑和面部的异常发育引起的。"}]}' WHERE id = 15567;
UPDATE genes SET GHR_metadata_ch = '{"gene": "LOR", "conditions": [{"name": "Vohwinkel综合征", "detail": "在具有Vohwinkel综合征变异形式（有时称为loricrin角化病）的人中，已经鉴定出LORgene中至少有两个突变。这种疾病的特征是皮肤异常，包括广泛干燥，鳞状的皮肤（鱼鳞病），尤其是四肢。导致Vohwinkel综合征变异形式的突变改变了loricrin蛋白的结构；改变后的蛋白质被困在细胞内，无法到达角质化的包膜。尽管其他蛋白质可以部分补偿缺失的loricrin，但某些角质细胞的包膜比正常细胞薄，从而导致干燥，鳞状的皮肤（鱼鳞病）和其他与Vohwinkel综合征变异形式相关的皮肤异常。"}]}' WHERE id = 15573;
UPDATE genes SET GHR_metadata_ch = '{"gene": "LEPR", "conditions": [{"name": "瘦素受体缺乏", "detail": "已经鉴定出至少18种引起瘦素受体缺乏的LEPRgene突变；这种疾病与过度饥饿，大量体重增加和指导性发育的激素产生减少（促性腺激素性性腺功能减退症）有关。一些突变导致较少的受体蛋白到达发生瘦素结合的细胞表面。到达细胞表面的受体可能与瘦素结合，但其信号传导功能受损。瘦素信号的短缺破坏了正常的饥饿感和饱腹感，导致体重极度增加。(换行)由于促性腺激素性性腺功能减退症发生在瘦素受体缺乏症中，研究人员建议瘦素受体信号传导也参与调节人体对控制性发育的激素的反应，并且这种反应受LEPRgene突变的影响。但是，这种影响的机制尚不清楚。"}]}' WHERE id = 15579;
UPDATE genes SET GHR_metadata_ch = '{"gene": "F11", "conditions": [{"name": "XI因子缺乏症", "detail": "已发现F11gene中约有250个突变会引起罕见的出血性疾病，称为XI因子缺乏症。这种疾病虽然通常是轻微的，但可能导致流鼻血，容易瘀伤，皮肤下出血，牙龈出血，以及手术，牙科手术或创伤后长时间或过度出血。导致XI因子缺乏的F11gene突变会减少血液中XI因子的数量或导致功能受损的XI因子蛋白的产生。功能因子XI的缺乏会减慢血液凝固，导致异常出血。（换行）剩余的功能因子XI的量取决于特定的突变以及每个细胞中F11gene的一个或两个拷贝是否具有突变。但是，受影响个体出血问题的严重程度不一定与血液中XI因子的量相对应，甚至在同一家庭中也可能有所不同。其他遗传和环境因素可能在确定这种情况的严重程度中起作用。"}]}' WHERE id = 15612;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ZFYVE26", "conditions": [{"name": "痉挛性截瘫15型", "detail": "已发现ZFYVE26gene中的30多个突变会导致15型痉挛性截瘫。这种情况的特征是进行性运动问题，智力障碍和视力问题。导致这种情况的许多ZFYVE26gene突变导致缩短的spastizin蛋白迅速分解。结果，不产生功能性自噬体，不能发生自噬，并且细胞内材料的再循环减少。无法分解不需要的材料，以及随后这些材料在细胞中的积累，导致细胞功能障碍并常常导致细胞死亡。大脑和身体其他部位的细胞损失是痉挛性截瘫15型的许多特征的原因。(换行)尚不清楚缺乏spastizin蛋白是否会干扰正常的胞质分裂，以及细胞分裂受损是否会导致15型痉挛性截瘫的体征和症状。"}]}' WHERE id = 15615;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SPG11", "conditions": [{"name": "痉挛性截瘫11型", "detail": "已发现SPG11gene中的65个以上突变会导致11型痉挛性截瘫。这些突变大多数会改变spatacsin蛋白的结构。改变的spatacsin蛋白对神经系统的影响尚不清楚。研究人员建议，spatacsin中的突变可能会干扰该蛋白在轴突维持中的拟议作用，从而引起11型痉挛性截瘫的体征和症状。"}, {"name": "肌萎缩侧索硬化症", "detail": "遗传学家庭参考提供有关肌萎缩侧索硬化的信息。"}]}' WHERE id = 15629;
UPDATE genes SET GHR_metadata_ch = '{"gene": "KIF21A", "conditions": [{"name": "先天性眼外肌纤维化", "detail": "在患有先天性眼外肌纤维化的人中，已经鉴定出KIF21Agene的至少11个突变。这些突变导致最常见的疾病形式CFEOM1，并且是另一种称为CFEOM3的疾病的罕见原因。（换行）每个已知的KIF21A突变都会改变驱动蛋白家族成员21A中的单个蛋白质结构单元（氨基酸）。这些变化大多数发生在蛋白质的茎区域。这些突变改变了蛋白质的结构，这可能会干扰其在神经元内运输货物的能力。结果，它们控制的一些颅神经和眼外肌不能正常发育。这些肌肉的异常发育和功能导致眼外肌先天性纤维化的特征，包括眼球运动受限和相关的视力问题。"}]}' WHERE id = 15663;
UPDATE genes SET GHR_metadata_ch = '{"gene": "LETM1", "conditions": [{"name": "沃尔夫-赫希霍恩综合症", "detail": "LETM1gene位于4号染色体的一个区域，该区域在具有Wolf-Hirschhorn综合征典型特征的人中被删除。这种缺失的结果是，受影响的个体在每个细胞中都缺失了一个LETM1gene拷贝。研究表明，该gene的缺失会改变线粒体的结构；但是，尚不清楚这种异常与Wolf-Hirschhorn综合征的体征和症状如何相关。具体而言，LETM1gene的丢失与癫痫发作或大脑中其他异常电活动有关。"}]}' WHERE id = 15673;
UPDATE genes SET GHR_metadata_ch = '{"gene": "VPS13B", "conditions": [{"name": "科恩综合症", "detail": "在患有Cohen综合征的个体中发现了VPS13Bgene的150多种不同突变。患有这种情况的人通常具有智力残疾，眼睛问题和独特的面部特征。该病的另一个常见特征是肥胖，特别是在躯干周围而不是手臂和腿部（躯干肥胖）。导致Cohen综合征的大多数突变会在制造VPS13B蛋白的说明中导致过早的停止信号。研究人员认为，这种遗传变化会导致蛋白质异常短而无功能的产生。研究表明，这种蛋白质功能的丧失会破坏高尔基体的组织并损害正常的糖基化。但是，尚不清楚功能性VPS13B蛋白的缺乏或这些细胞变化如何导致Cohen综合征的体征和症状。研究人员推测，神经元发育问题是智力残疾，眼睛问题和Cohen综合征其他特征的基础。脂肪储存异常可能会导致患者躯干肥胖。(换行)在芬兰人口中，有75%的Cohen综合征患者的VPS13Bgene的两个拷贝都有突变，从而删除了两个DNA构建基块（碱基对）。此突变有时被写为3348_3349delCT。删除会在制作VPS13B蛋白的说明中引起过早的停止信号。(换行)在Old Order Amish人群中发生两种常见的突变。第一个突变添加一个碱基对，有时写为9258_9259insT。该突变在制备VPS13B蛋白的说明中产生过早的终止信号。第二个突变改变了VPS13B蛋白中的单个蛋白结构单元（氨基酸）。具体而言，此突变将氨基酸异亮氨酸替换为位置2820处的氨基酸苏氨酸（写为Ile2820Thr或I2820T）。在芬兰和阿米什人之外，VPS13Bgene的几乎所有突变都仅发生在一个或少数家庭中。"}]}' WHERE id = 15711;
UPDATE genes SET GHR_metadata_ch = '{"gene": "DNM2", "conditions": [{"name": "中心核肌病", "detail": "已发现DNM2gene中至少有24个突变会引起中心核肌病，这种疾病的特征是骨骼肌（用于运动的肌肉）中的肌肉无力（肌病）。这些突变中的大多数会改变gene中称为外显子8，外显子11和外显子16的区域中的单个DNA构建块（核苷酸）。这些突变导致动力蛋白2的结构发生变化。引起中心核肌病的DNM2gene突变被描述为“功能增强”，因为它们似乎增强了dynamin 2的活性，影响内吞作用，导致类似于微管的结构紊乱，称为横向小管（T小管），这些结构存在于肌纤维膜内。T小管是正常肌肉紧张（收缩）和放松所必需的。由于DNM2gene突变，肌肉细胞的结构变得异常，无法正常收缩和放松，从而导致肌肉无力，这是中心核肌病的特征。"}, {"name": "进行性神经性腓骨肌萎缩症", "detail": "研究人员已经确定了一些DNM2gene突变，这些突变导致一种称为显性中间B的Charcot-Marie-Tooth病。Charcot-Marie-Tooth病是一组影响周围神经的进行性疾病，周围神经将大脑和脊髓连接到肌肉和感觉细胞，检测触觉，疼痛，热量和声音等感觉。周围神经受损可导致脚，腿和手的感觉丧失和肌肉萎缩（萎缩）。（换行）此外，在患有另一种称为2型Charcot-Marie-Tooth病的个体中，至少鉴定出三个DNM2gene突变。这些突变改变或删除了动力蛋白2中的单个氨基酸。（换行）导致Charcot-Marie-Tooth病的DNM2gene突变被描述为“功能丧失”，因为它们损害了dynamin 2蛋白的活性。这些突变可能破坏内吞作用，干扰微管在细胞骨架中的排列，并干扰细胞组织。研究人员建议，这些突变也可能导致神经周围的雪旺氏细胞功能障碍。施万细胞形成髓鞘，髓鞘是隔离和保护某些神经细胞并促进神经冲动的有效传递的脂肪覆盖物。尚不清楚DNM2gene突变如何引起Charcot-Marie-Tooth病的体征和症状。"}]}' WHERE id = 15713;
UPDATE genes SET GHR_metadata_ch = '{"gene": "HEXA", "conditions": [{"name": "泰萨克斯病", "detail": "在HEXAgene中已鉴定出120多种引起Tay-Sachs病的突变。这些突变降低或消除了β-己糖胺酶A的活性，从而阻止了该酶分解GM2神经节苷脂。结果，这种物质积累到毒性水平，特别是在脑和脊髓的神经细胞中。由GM2神经节苷脂积聚引起的进行性损伤导致这些细胞的破坏，这导致Tay-Sachs病的体征和症状。(换行)大多数已知的HEXA突变会导致β-己糖胺酶a完全失功能。这些突变会导致婴儿期出现严重的Tay-Sachs病。其他突变减少但不消除β-己糖胺酶A的活性；这些遗传变化是导致生命后期出现的不太严重的Tay-Sachs病的原因。"}]}' WHERE id = 15715;
UPDATE genes SET GHR_metadata_ch = '{"gene": "PORCN", "conditions": [{"name": "局灶性皮肤发育不全", "detail": "已发现PORCNgene中至少29个突变会引起局灶性皮肤发育不全。这些突变可能会改变蛋白质的结构，导致异常短版本的蛋白质产生，或删除整个PORCNgene。所有这些突变似乎导致不存在任何功能性PORCN蛋白。研究人员认为，如果没有PORCN蛋白，Wnt蛋白就不能从细胞中释放出来。当Wnt蛋白不能离开细胞时，它们不能参与对正常发育至关重要的化学信号传导途径。"}]}' WHERE id = 15716;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SOX10", "conditions": [{"name": "瓦尔登堡综合症", "detail": "在患有II型和IV型Waardenburg综合征（也称为Waardenburg-Shah综合征）的人中，已经鉴定出SOX10gene的30多个突变。两种类型的Waardenburg综合征的特征都在于皮肤，头发和眼睛颜色的变化以及听力损失。IV型患者还患有称为Hirschsprung病的肠道疾病，导致严重的便秘或肠道阻塞。大多数SOX10突变会导致异常形式的SOX10蛋白产生，或阻止该gene产生任何蛋白。异常或缺失的SOX10蛋白无法控制向神经rest细胞发出信号成为特定细胞类型的gene。结果，肠神经和黑素细胞在胚胎发育期间不能正常形成。肠某些部位的肠神经缺失会导致Hirschsprung病的体征和症状。黑素细胞的缺乏会影响皮肤，头发和眼睛的颜色，并导致Waardenburg综合征的听力损失。(换行)研究人员发现，SOX10gene的突变也会引起类似的疾病，称为周围性脱髓鞘性神经病，中枢性脱髓鞘性白细胞营养不良，Waardenburg综合征和Hirschsprung病（PCWH）。这种罕见的病症是Waardenburg综合征IV型的变体，其也影响神经系统的其他部分。像引起其他类型的Waardenburg综合征的突变一样，负责PCWH的突变导致产生异常版本的SOX10蛋白，该蛋白无法指导其他gene的活性。"}, {"name": "卡门氏症候群", "detail": "遗传学家庭参考资料提供有关Kallmann综合征的信息。"}]}' WHERE id = 15718;
UPDATE genes SET GHR_metadata_ch = '{"gene": "EXOSC3", "conditions": [{"name": "桥小脑发育不全", "detail": "在患有称为桥小脑发育不全的大脑发育障碍的人中，已经鉴定出EXOSC3gene中至少有16个突变。这种情况的主要特征包括整体发育迟缓，头部异常小（小头畸形）和智力障碍。EXOSC3gene突变导致约一半的称为1型桥小脑发育不全（PCH1）的疾病。当PCH1由EXOSC3gene突变产生时，有时会将其更具体地归类为PCH1B。除了上面列出的功能外，PCH1B还会导致肌肉运动问题，这是由于脊髓中称为运动神经元的专门神经细胞丢失所致。（换行）导致PCH1B的EXOSC3gene突变导致外泌体组分3蛋白功能降低或无功能。最常见的突变会改变外泌体成分3中的单个蛋白质结构单元（氨基酸）；它在蛋白质位置132（写为Asp132Ala或D132A）用氨基酸丙氨酸取代氨基酸天冬氨酸。具有这种突变的人比具有其他EXOSC3gene突变的人的大脑异常严重程度要低一些。该gene的改变可能损害RNA外泌体的活性，但尚不清楚这些改变如何导致大脑发育和PCH1B特征性运动神经元丧失的问题。"}]}' WHERE id = 15726;
UPDATE genes SET GHR_metadata_ch = '{"gene": "EPX", "conditions": [{"name": "嗜酸性粒细胞过氧化物酶缺乏症", "detail": "已经发现至少三种突变引起嗜酸性粒细胞过氧化物酶缺乏。这种情况影响嗜酸性粒细胞，但在受影响的个体中不会引起健康问题。这些突变减少或阻止嗜酸性粒细胞过氧化物酶的产生或导致蛋白质不稳定且无功能。结果，嗜酸性粒细胞严重减少了嗜酸性粒细胞过氧化物酶的量或根本没有。缺乏嗜酸性粒细胞过氧化物酶的细胞较小并且可能具有结构变化，但是受影响的嗜酸性粒细胞内的其他蛋白质是正常的，并且嗜酸性粒细胞过氧化物酶的丧失似乎不损害这些细胞的功能。"}]}' WHERE id = 15756;
UPDATE genes SET GHR_metadata_ch = '{"gene": "MBL2", "conditions": [{"name": "甘露糖结合凝集素缺乏症", "detail": "MBL2gene的几种常见突变可导致称为甘露糖结合凝集素缺乏症的疾病。患有这种疾病的人甘露糖结合凝集素水平低，可能容易反复感染。几种与疾病相关的突变发生在称为外显子1的MBL2gene区域中，并导致甘露糖结合凝集素亚基中单个蛋白质结构单元（氨基酸）的改变。其他突变发生在MBL2gene附近称为启动子区域的DNA区域，这有助于控制甘露糖结合凝集素亚基的产生。(换行)甘露糖结合凝集素亚基中单个氨基酸的变化消除了其组装成功能性甘露糖结合凝集素的能力。同样，MBL2gene启动子区域中的某些突变会减少甘露糖结合凝集素亚基的产生，从而导致可用于蛋白质组装的亚基数量减少，功能性蛋白质数量减少。随着甘露糖结合凝集素水平的降低，身体无法有效识别和对抗外来入侵者。因此，感染在患有这种疾病的人中可能更常见。但是，研究人员认为，甘露糖结合凝集素缺乏症的发展涉及许多因素，包括其他遗传和环境因素。"}]}' WHERE id = 15763;
UPDATE genes SET GHR_metadata_ch = '{"gene": "NSD2", "conditions": [{"name": "沃尔夫-赫希霍恩综合症", "detail": "NSD2gene位于Wolf-Hirschhorn综合征患者中缺失的4号染色体区域。这种缺失的结果是，受影响的个体在每个细胞中缺失了一个NSD2gene拷贝。NSD2gene的丢失可能会破坏其他几个gene的调控，尽管这些gene尚未被鉴定。研究人员推测，发育过程中异常的gene调控会导致该疾病的许多特征，包括智力残疾，生长迟缓和独特的面部外观。"}, {"name": "癌症", "detail": "涉及NSD2gene的染色体重排（易位）与多发性骨髓瘤有关，多发性骨髓瘤是一种从骨髓细胞开始的癌症。在所有多发性骨髓瘤的15%至20%中发现这种重排。易位写为t（4；14）（p16；q32），异常融合了4号染色体上的NSD2gene和14号染色体上另一个gene的一部分。这些gene的融合使NSD2gene过度活化，这似乎促进了癌细胞的不受控制的生长和分裂。"}]}' WHERE id = 15768;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SH3TC2", "conditions": [{"name": "进行性神经性腓骨肌萎缩症", "detail": "研究人员已经鉴定出至少19种SH3TC2gene突变，这些突变会导致一种称为4C型的Charcot-Marie-Tooth病。Charcot-Marie-Tooth病是一组影响周围神经的进行性疾病。周围神经将大脑和脊髓连接到肌肉和感觉细胞，检测触觉，疼痛，热和声音等感觉。（换行）导致Charcot-Marie-Tooth病的大多数SH3TC2gene突变破坏SH3TC2蛋白的产生，导致该蛋白的异常短或不稳定形式。一些突变改变了用于制造SH3TC2蛋白的构建基块（氨基酸）之一，从而改变了蛋白的结构。尚不清楚SH3TC2gene突变如何引起4C型Charcot-Marie-Tooth病的体征和症状。"}]}' WHERE id = 15808;
UPDATE genes SET GHR_metadata_ch = '{"gene": "COL5A1", "conditions": [{"name": "埃勒斯-当洛二氏综合症", "detail": "COL4A5gene的突变会导致一种称为经典型的Ehlers-Danlos综合征，这种疾病的特征是皮肤柔软，高度伸展（弹性），脆弱，异常疤痕和活动过度。突变严重减少了pro-？1（V）链的产生导致皮肤和其他组织中含有V型和I型胶原的原纤维的无序组装。"}, {"name": "圆锥角膜", "detail": "已经发现COL5A1gene的突变与圆锥角膜有关，圆锥角膜是一种影响角膜形状的疾病，导致角膜异常。临床症状包括近视（近视），矫正镜片无法改善的视力模糊（散光不规则）和视力丧失。"}]}' WHERE id = 15812;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ARMC5", "conditions": [{"name": "原发性大结节性肾上腺增生", "detail": "在原发性大结节性肾上腺增生症（PMAH）患者中，至少发现了ARMC5gene的24个突变，这种疾病导致肾上腺形成多个肿块（结节），肾上腺是位于每个肾脏顶部的小型激素分泌腺。这些结节导致肾上腺肿大（增生），并导致产生高于正常水平的激素皮质醇。皮质醇通常有助于维持血糖水平，保护身体免受身体压力，并抑制炎症。皮质醇水平升高可导致面部和上身体重增加，皮肤脆弱，骨质流失，疲劳和其他健康问题，这些问题通常发生在PMAH患者身上。(换行)由ARMC5gene突变引起的PMAH患者在每个细胞中继承一个拷贝的突变gene。但是，仅当受影响的个体在肾上腺某些细胞的ARMC5gene的另一个拷贝中获得第二个突变时，才会出现这种情况。第二个突变被描述为体细胞突变。体细胞突变不是从父母传给孩子，而是在一个人的一生中获得的，并且仅存在于某些细胞中。由于PMAH也需要发生体细胞突变，因此一些遗传了改变后的ARMC5gene的人从未患上这种疾病，这种情况称为外显率降低。（换行）导致PMAH的ARMC5gene突变被认为会损害蛋白质的肿瘤抑制功能，从而导致某些细胞过度生长。目前尚不清楚为什么这种过度生长仅限于PMAH患者的肾上腺。"}]}' WHERE id = 15843;
UPDATE genes SET GHR_metadata_ch = '{"gene": "EBP", "conditions": [{"name": "X连锁点状软骨发育不良2", "detail": "已发现EBPgene中超过55个突变会导致X连锁软骨发育不良点2，这种情况几乎仅在女性中发生，其特征是骨骼，皮肤和眼睛异常。在女性中造成这种情况的一些突变从EBPgene中插入或删除少量遗传物质，而其他突变改变3中的单个蛋白质构建块（氨基酸）-羟基类固醇-？8,?7-异构酶。所有这些突变都会损害酶的正常功能，从而阻止细胞产生足够的胆固醇。这种酶的缺乏还允许胆固醇产生的潜在有毒副产物积聚在体内。低胆固醇水平和其他物质积累的结合可能会破坏许多身体系统的生长和发育。然而，尚不清楚这种胆固醇产生的紊乱如何导致X连锁软骨发育不良2的特定特征。(换行)很少有男性报告严重形式的X连锁软骨发育不良2。这些情况是由3中涉及单个氨基酸的变化引起的-羟基类固醇-？8,?7-异构酶。受影响的男性具有与受影响的女性相同的一些特征，以及大脑结构的变化，中度至深度的发育延迟以及其他先天缺陷。"}]}' WHERE id = 15865;
UPDATE genes SET GHR_metadata_ch = '{"gene": "PLA2G6", "conditions": [{"name": "婴儿神经轴突营养不良", "detail": "在患有婴儿神经轴突营养不良的人中，已经鉴定出PLA2G6gene中至少有50个突变，这是一种进行性神经系统疾病，会导致智力残疾和运动问题。PLA2G6gene中的突变消除或严重损害了PLA2 VI组酶的功能。PLA2 VI组酶功能的损害可能破坏细胞膜的维持，并导致轴突中称为球体的肿胀的发展，轴突是从神经细胞（神经元）延伸并将冲动传递给肌肉和其他神经元的纤维。尽管尚不清楚该酶功能的变化如何导致婴儿神经轴突营养不良的体征和症状，但在该疾病和称为泛酸激酶相关神经变性的类似疾病中均发现了磷脂代谢问题。这些疾病以及更常见的阿尔茨海默病和帕金森病也与脑铁代谢的变化有关。研究人员正在研究磷脂缺陷，脑铁和神经细胞损伤之间的联系，但尚未确定某些婴儿神经轴索营养不良患者体内发生的铁蓄积如何可能导致该疾病的特征。"}, {"name": "其他疾病", "detail": "PLA2G6gene突变还可引起非典型的神经轴索营养不良和PLA2G6相关的肌张力障碍-帕金森病，这是生命后期发生神经功能恶化（神经变性）的情况。术语PLA2G6相关神经变性（PLAN）通常用于包括由PLA2G6突变引起的整个神经退行性疾病。(换行)非典型神经轴突营养不良（非典型NAD）是一种具有与婴儿神经轴突营养不良相似的体征和症状的疾病，但发生较晚且进展较慢。非典型的NAD通常出现在儿童早期，但在某些情况下直到十几岁才显现出来。(换行)PLA2G6相关的肌张力障碍-帕金森病也由PLA2G6gene突变引起，并涉及成年期发生的运动异常。肌张力障碍是肌肉的非自愿张力，帕金森氏症包括一组运动问题，包括异常缓慢的运动（运动迟缓），肌肉僵硬，震颤以及无法保持身体直立和平衡（姿势不稳）。（换行）这两种迟发性疾病都是由PLA2G6gene突变引起的，据信这些突变对PLA2 VI组酶功能的影响不如引起婴儿神经轴突营养不良的突变严重。"}]}' WHERE id = 15871;
UPDATE genes SET GHR_metadata_ch = '{"gene": "CLN5", "conditions": [{"name": "CLN5疾病", "detail": "已发现CLN5gene突变会导致CLN5（类固醇脂褐藻病，神经元，5）疾病，这种疾病会损害智力和运动发育，导致行走和智力功能困难，受影响的儿童会反复发作（癫痫）和视力丧失。大多数CLN5突变会干扰前蛋白的加工或改变蛋白的结构/功能，并损害其向溶酶体的转运和某些蛋白的分解，从而导致其在细胞中蓄积，从而损害神经细胞。"}]}' WHERE id = 15873;
UPDATE genes SET GHR_metadata_ch = '{"gene": "PROK2", "conditions": [{"name": "卡门氏症候群", "detail": "在患有Kallmann综合征的人中已鉴定出PROK2gene的至少16个突变，该疾病的特征是性腺功能低下性腺功能减退症（一种影响指导性发育的激素产生的疾病）和嗅觉受损。研究人员估计，PROK2和PROKR2gene的突变加起来约占所有Kallmann综合征病例的9%。（换行）导致Kallmann综合征的大多数PROK2gene突变会改变prokineticin 2中的单个蛋白质结构单元（氨基酸）。这些突变破坏了蛋白质的活性，影响了其与其受体结合以正常发送信号的能力。研究表明，这种信号的丢失会破坏发育中的大脑中嗅觉神经元和产生GnRH的神经元的迁移和存活。如果嗅神经细胞不延伸到嗅球，则人的嗅觉将受损或缺失。产生GnRH的神经元的错位或过早丢失会阻止性激素的产生，从而干扰正常的性发育并导致青春期延迟或缺失。(换行)由于Kallmann综合征的特征和严重程度因人而异，研究人员认为可能涉及其他遗传和环境因素。一些受影响的个体除PROK2外，其他几个gene之一也有突变，这些遗传变化可能有助于疾病的不同特征。"}]}' WHERE id = 15875;
UPDATE genes SET GHR_metadata_ch = '{"gene": "CFTR", "conditions": [{"name": "先天性双侧输精管缺如", "detail": "CFTRgene中的几个突变已被鉴定为先天性双侧输精管缺失，这是男性的一种状况，它通过阻塞睾丸（输精管）中的粘液（通过抑制精子的运输）来影响生育能力。这是由于CFTR的部分或异常功能引起的，该功能破坏了氯离子和水的运动，导致男性生殖道中粘液的产生异常厚而粘稠。"}, {"name": "囊性纤维化", "detail": "已经在患有囊性纤维化的人中发现了CFTRgene的突变，其特征是粘稠的粘液堆积，该粘液会损害人体的许多器官，从而导致呼吸系统的逐步损害和慢性消化系统问题。突变改变了氯离子通道的产生，结构，功能或稳定性，这损害了氯离子的运输和水的移动，导致产生异常粘稠的粘液，粘液阻碍了气道和腺体。"}, {"name": "遗传性胰腺炎", "detail": "已发现CFTRgene突变与遗传性胰腺炎有关，遗传性胰腺炎是一种遗传性疾病，其特征是胰腺（胰腺炎）反复发作，导致永久性组织损伤和胰腺功能丧失。临床症状包括在儿童晚期观察到的腹痛，发烧，恶心或呕吐，导致急性和慢性胰腺炎，胰腺钙化异常，胰腺纤维化和1型糖尿病。"}, {"name": "其他疾病", "detail": "已经在患有影响消化系统或呼吸系统的孤立问题的人中鉴定出CFTRgene的突变，例如：；特发性胰腺炎是引起腹痛，恶心，呕吐，发热和鼻-鼻窦炎的胰腺炎症，是以鼻窦疼痛和压力，头痛，发热和鼻塞或引流为特征的鼻窦组织的慢性炎症。其他呼吸系统问题包括以下情况：；支气管扩张，其损害肺的支气管和过敏性支气管肺曲霉菌病，由对某种类型的真菌感染的超敏反应引起。"}]}' WHERE id = 15906;
UPDATE genes SET GHR_metadata_ch = '{"gene": "MEFV", "conditions": [{"name": "家族性地中海热", "detail": "已经鉴定出80多种引起家族性地中海热的MEFVgene突变。一些突变会从MEFVgene中删除少量DNA，这可能导致异常小的无功能蛋白。但是，大多数MEFVgene突变会改变用于制备吡啶的蛋白质结构单元（氨基酸）之一。最常见的突变是在蛋白质位置694（写为Met694Val或M694V）上用氨基酸缬氨酸替换氨基酸蛋氨酸。在家族性地中海热患者中，这种特殊的突变也与淀粉样变性的风险增加有关，淀粉样变性是一种异常的蛋白质沉积可导致肾衰竭的并发症。一些证据表明，另一个称为SAA1的gene的变异可以进一步改变M694V突变人群中发生淀粉样变性的风险。（换行）MEFVgene突变导致无法正常运行的吡啶或畸形的吡啶蛋白数量减少。结果，吡啶不能在控制炎症中发挥其推测的作用，从而导致不适当或延长的炎症反应。腹部，胸部，关节或皮肤的发烧和炎症是家族性地中海热的迹象。"}]}' WHERE id = 15926;
UPDATE genes SET GHR_metadata_ch = '{"gene": "PAH", "conditions": [{"name": "苯丙酮尿症", "detail": "在苯丙酮尿症（PKU）患者中已鉴定出PAHgene的500多个突变。这些突变大多数改变苯丙氨酸羟化酶中的单个氨基酸。例如，许多种群中最常见的突变是将氨基酸精氨酸替换为位置408的氨基酸色氨酸（写为Arg408Trp或R408W）。其他PAH突变会从gene中删除少量DNA或破坏gene说明用于制备苯丙氨酸羟化酶的方式。(换行)PAH突变会降低苯丙氨酸羟化酶的活性，阻止其有效加工苯丙氨酸。结果，该氨基酸可在血液和其他组织中累积至毒性水平。由于大脑中的神经细胞对苯丙氨酸水平特别敏感，过量的这种物质会导致脑损伤。(换行)当苯丙氨酸羟化酶活性严重降低或不存在时，经典的PKU是该疾病的最严重形式。未经治疗的经典PKU患者的苯丙氨酸水平高到足以引起严重的脑损伤和其他严重的医疗问题。PAHgene中允许酶保留某些活性的突变会导致这种情况的温和版本，例如变体PKU或非PKU高苯丙氨酸血症。"}]}' WHERE id = 15973;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ITGB2", "conditions": [{"name": "白细胞粘附缺陷1型", "detail": "已发现ITGB2gene中至少有90个突变会导致1型白细胞粘附缺陷。这种情况的特点是严重的，反复感染和受伤后愈合延迟。导致这种情况的许多突变改变了单个蛋白质结构单元（氨基酸）？2亚基并导致产生a？2个不能与其他亚基结合形成整联蛋白的亚基。很少，有缺陷？2个亚基能够形成整联蛋白，但整联蛋白不能结合配体。缺乏这些整联蛋白的白细胞不能附着于血管壁或穿过血管壁以促进免疫应答。结果，对损伤和外来入侵者（例如细菌和真菌）的反应降低，导致频繁感染，伤口愈合延迟以及1型白细胞粘附缺陷的其他体征和症状。"}]}' WHERE id = 15987;
UPDATE genes SET GHR_metadata_ch = '{"gene": "CLN8", "conditions": [{"name": "CLN8疾病", "detail": "已经发现CLN8gene的突变导致CLN8（蜡样脂褐质沉着症，神经元，8）疾病，主要影响遗传性疾病的遗传性疾病和以复发性癫痫（癫痫）和智力功能下降为特征的较不严重形式的CLN8疾病。发生在5-10岁之间。更严重的CLN8疾病的特征是在2-7岁时运动和认知技能，癫痫和视力丧失逐渐恶化，并且仅在儿童晚期或青春期存活。CLN6突变导致蛋白质丢失或减少，从而损害其功能，并导致大脑中脂质水平降低，从而导致神经细胞损伤和细胞死亡。"}]}' WHERE id = 16016;
UPDATE genes SET GHR_metadata_ch = '{"gene": "CDC6", "conditions": [{"name": "梅尔-戈林综合征", "detail": "CDC6gene中至少有一个突变会导致Meier-Gorlin综合征，这种疾病的特征是身材矮小，膝盖发育不全和耳朵小。这种突变是造成这种情况的罕见原因，它改变了CDC6蛋白中的单个蛋白结构单元（氨基酸），用氨基酸精氨酸（写为Thr323Arg）替换了323位的苏氨酸。结果，复制前复合体的组装受到损害，这破坏了复制许可；但是，尚不清楚复制许可证的减少如何导致Meier-Gorlin综合征。研究人员推测，这种减少会延迟细胞分裂过程，从而减缓发育过程中骨骼和其他组织的生长。尚不清楚为什么Meier-Gorlin综合征会特别影响膝盖和耳朵的发育。"}]}' WHERE id = 16040;
UPDATE genes SET GHR_metadata_ch = '{"gene": "PSEN2", "conditions": [{"name": "阿尔茨海默病", "detail": "已显示PSEN2gene中至少有11个突变会引起早发性阿尔茨海默氏病。该gene的突变占该疾病所有早发病例的不到5%。(换行)导致早发性阿尔茨海默氏病的两个最常见的PSEN2突变改变了用于制备早老素2的单蛋白结构单元（氨基酸）。一个突变将氨基酸天冬酰胺替换为位置141处的氨基酸异亮氨酸（写为Asn141Ile或N141I）。另一个突变将第239位的氨基酸蛋氨酸变为氨基酸缬氨酸（写为Met239Val或M239V）。这些突变似乎破坏了淀粉样蛋白前体蛋白的加工，导致淀粉样蛋白β肽的过量生产。该蛋白质片段可以在脑中积聚并形成称为淀粉样斑块的团块，其是阿尔茨海默病的特征。有毒的淀粉样β肽和淀粉样斑块的积累可能导致神经元死亡以及该疾病的进行性体征和症状。"}, {"name": "家族性扩张型心肌病", "detail": "遗传学家庭参考提供有关家族性扩张型心肌病的信息。"}]}' WHERE id = 16073;
UPDATE genes SET GHR_metadata_ch = '{"gene": "MASP1", "conditions": [{"name": "3MC综合征", "detail": "在患有3MC综合征的人中至少鉴定出10个MASP1gene突变，该疾病的特征是异常的面部特征和影响身体其他组织和器官的各种问题。导致3MC综合征的MASP1gene突变影响MASP-3蛋白；除MASP-3外，有些还会影响MASP-1蛋白。蛋白质变化导致胚胎发育中细胞迁移的错误控制，导致在该疾病中发生的各种异常。研究人员认为，免疫系统中存在可以补偿凝集素补体途径问题的平行途径可以解释3MC综合征中免疫系统问题的缺失。"}]}' WHERE id = 16077;
UPDATE genes SET GHR_metadata_ch = '{"gene": "RARA", "conditions": [{"name": "急性早幼粒细胞白血病", "detail": "gene突变可以在一个人的一生中获得，并且仅存在于某些细胞中。这些突变称为体细胞突变，它们不是遗传的。涉及RARAgene的体细胞突变引起急性早幼粒细胞白血病，即血液形成组织（骨髓）的癌症。急性早幼粒细胞白血病的特征在于骨髓中早幼粒细胞的积累。15号和17号染色​​体之间遗传物质的重排（易位），写为t（15；17），将17号染色​​体上的RARAgene的一部分与15号染色体上的另一个gene的一部分融合，称为PML。由这种融合genePML-RAR产生的蛋白质？蛋白质的功能不同于正常PML和RARAgene的蛋白质产物。(换行)PML-RAR？蛋白质与DNA结合并抑制gene转录，就像正常的RAR一样？蛋白质。但是，PML-RAR？蛋白质对诱导gene转录的信号没有反应，因此gene仍然受到抑制。（换行）另外，PML蛋白（PMLgene的产物）的功能被破坏。PML蛋白阻断细胞生长和分裂（增殖）并与其他蛋白结合诱导自我破坏（凋亡）。但是，PML-RAR？蛋白质不会阻止增殖或诱导细胞凋亡。(换行)PML-RAR？蛋白质在早幼粒细胞阶段阻断血细胞的分化并允许异常的细胞增殖。结果，过量的早幼粒细胞在骨髓中积累并且不能形成正常的白细胞，导致急性早幼粒细胞白血病。"}]}' WHERE id = 16096;
UPDATE genes SET GHR_metadata_ch = '{"gene": "COQ8A", "conditions": [{"name": "初级辅酶Q10缺乏症", "detail": "已发现COQ8Agene中至少有36个突变会引起一种称为原发性辅酶Q10缺乏症的疾病。这种罕见疾病通常在婴儿期或儿童早期就很明显，但可以在任何年龄发生。它可以影响身体的许多部位，最常见的是大脑，肌肉和肾脏。与该疾病相关的COQ8Agene突变会改变COQ8A蛋白的结构或阻止其产生，从而损害辅酶Q10的正常产生。研究表明，辅酶Q10的缺乏（缺乏）会损害氧化磷酸化并增加细胞对自由基损伤的脆弱性。辅酶Q10的缺乏也可能破坏嘧啶的产生。这些变化可能导致整个身体的细胞发生功能障碍，这可能有助于解释可能受初级辅酶Q10缺乏影响的器官和组织的多样性。"}]}' WHERE id = 16135;
UPDATE genes SET GHR_metadata_ch = '{"gene": "KCNT1", "conditions": [{"name": "婴儿恶性移行性部分性癫痫发作", "detail": "在具有恶性迁移的婴儿部分发作（MMPSI）的个体中发现了至少六个KCNT1gene突变。这种情况的特征是在6个月大之前开始反复发作，以及严重的发育迟缓。在MMPSI中，大脑中的癫痫发作活动可以在发作期间从一个区域扩散（迁移）到另一个区域。（换行）参与MMPSI的KCNT1gene突变改变KCNT1蛋白中的单蛋白构建块（氨基酸）。用改变的KCNT1蛋白制成的钾通道产生的电流异常增加，就好像通道被PKC打开一样。增加的电流允许大脑中神经元的不受调节的激发。当神经元异常兴奋时，会发生癫痫发作。反复发作会导致这种情况的特征性发育迟缓。"}, {"name": "常染色体显性遗传夜间额叶癫痫", "detail": "遗传学家庭参考提供有关常染色体显性夜间额叶癫痫的信息。"}, {"name": "其他疾病", "detail": "在几名常染色体显性遗传性夜间额叶癫痫（ADNFLE）患者中发现了KCNT1gene的突变，这导致癫痫发作通常在受影响的人睡觉时夜间（夜间）发生。除癫痫发作外，大多数具有KCNT1gene突变的受影响个体还患有精神病问题，例如攻击性，无反应发作（卡塔托尼亚）或对现实的扭曲看法（精神病），大约一半患有智力障碍。这种情况下涉及的KCNT1gene突变会改变KCNT1蛋白中的单个氨基酸；但是，尚不清楚这些变化对钾通道的功能有什么影响，或者它们如何导致ADNFLE的特征。"}]}' WHERE id = 16155;
UPDATE genes SET GHR_metadata_ch = '{"gene": "NPM1", "conditions": [{"name": "急性早幼粒细胞白血病", "detail": "遗传学家庭参考提供有关急性早幼粒细胞白血病的信息。"}, {"name": "细胞遗传学正常的急性髓性白血病", "detail": "NPM1gene的突变与一种称为细胞遗传学正常的急性髓细胞性白血病（CN-AML）的血细胞癌有关。虽然大的染色体异常可能参与急性髓性白血病的发展，但大约一半的病例没有这些异常；这些被归类为CN-AML。大约64%的CN-AML患者的NPM1gene有突变。（换行）CN-AML中涉及的NPM1gene突变称为体细胞突变；它们仅在癌变且未遗传的细胞中发现。这些突变发生在称为外显子12的gene区域。他们改变了用于制造核磷蛋白的蓝图，导致产生具有改变的蛋白质结构单元（氨基酸）序列的蛋白质。这些改变改变了蛋白质位置290处的氨基酸色氨酸，通常改变了位置288处的色氨酸。这两个色氨酸对于蛋白质在核仁中的定位很重要。新序列还为蛋白质移出细胞核提供信号（称为核输出信号）。结果，在细胞质而不是核仁中发现了核磷蛋白。(换行)尚不清楚核磷蛋白的异常定位如何导致急性髓性白血病。研究表明它影响ARF蛋白的功能。由于其与改变的核磷蛋白的相互作用，在具有这些遗传变化的细胞的细胞质中也发现了ARF蛋白。另外，改变的核磷蛋白蛋白不能保护ARF免于分解。据信ARF蛋白的肿瘤抑制功能的降低导致在急性髓性白血病中发生的异常白细胞的不受控制的产生。NPM1gene突变的其他影响也可能与白血病的发展有关。"}, {"name": "其他癌症", "detail": "在另一种称为间变性大细胞淋巴瘤（ALCL）的血细胞癌中发现了涉及NPM1gene的遗传重排（易位）。在ALCL中，称为T细胞的白细胞无法控制地生长和分裂，并经常在淋巴结中形成肿瘤。这种癌症所涉及的易位称为t（2；5），将5号染色体上的NPM1gene的一部分与2号染色体上的另一个称为ALK的gene的一部分融合在一起。这种重排是体细胞突变。（换行）由融合gene产生的蛋白质称为NPM-ALK。像正常激活的ALK蛋白一样，NPM-ALK打开信号通路，刺激细胞生长和分裂（增殖）和成熟（分化）。与ALK不同，NPM-ALK蛋白始终具有活性，这会导致不受控制的细胞增殖并导致ALCL。"}]}' WHERE id = 16166;
UPDATE genes SET GHR_metadata_ch = '{"gene": "FGD1", "conditions": [{"name": "Aarskog-Scott综合征", "detail": "已发现FGD1gene中的40多个突变会引起Aarskog-Scott综合征，这是一种主要发生在男性中的罕见病。患病男孩通常具有独特的面部特征，生殖器异常，儿童矮小身材和其他骨骼异常。FGD1gene突变导致产生功能异常的FGD1蛋白，从而破坏Cdc42信号传导。改变Cdc42信号的传输可能会损害骨骼和其他组织的正常发育，从而导致Aarskog-Scott综合征患者发生各种各样的异常。"}]}' WHERE id = 16208;
UPDATE genes SET GHR_metadata_ch = '{"gene": "CST3", "conditions": [{"name": "遗传性脑淀粉样血管病", "detail": "已发现CST3gene中至少有一个突变会引起遗传性脑淀粉样血管病，这种疾病的特征是中风和智力功能下降（痴呆），始于成年中期。已鉴定出的CST3gene突变会导致一种遗传性脑淀粉样血管病，称为冰岛型。该突变用胱抑素C蛋白（写为Leu68Gln或L68Q）中第68位的氨基酸谷氨酰胺取代了蛋白质结构单元（氨基酸）亮氨酸。这种异常的半胱氨酸蛋白酶抑制剂C蛋白不如正常蛋白稳定，更容易聚集在一起（聚集）。聚集的蛋白质形成称为淀粉样蛋白沉积物的团块，其主要在脑中积聚在血管壁中，但也在身体其他区域（例如皮肤，脾脏和淋巴结）的血管中积聚。这些淀粉样蛋白沉积物（称为斑块）的积累在脑外似乎没有任何健康影响。在大脑中，淀粉样斑块取代了肌肉纤维和弹性纤维，使血管具有柔韧性，使其变得虚弱并易于破裂。大脑中的这种休息导致出血（出血性中风），这可能导致脑损伤和痴呆。"}, {"name": "年龄相关性黄斑变性", "detail": "遗传学家庭参考提供有关年龄相关性黄斑变性的信息。"}]}' WHERE id = 16210;
UPDATE genes SET GHR_metadata_ch = '{"gene": "DHCR7", "conditions": [{"name": "史密斯-莱姆利-奥皮兹综合症", "detail": "在DHCR7gene中已鉴定出120多种引起Smith-Lemli-Opitz综合征的突变。最常见的突变，写为IVS8-1G>C，会改变gene中的单个DNA构建块（核苷酸）。这种变化干扰了7-脱氢胆固醇还原酶的正常加工。另一个常见的突变经常发生在地中海遗产的受影响个体中。该突变用酶中第93位的另一个氨基酸蛋氨酸（写为Thr93Met或T93M）替换了一个称为苏氨酸的蛋白质结构单元（氨基酸）。（换行）大多数已知的DHCR7突变会改变7-脱氢胆固醇还原酶中的单个氨基酸。这些突变降低了该酶将7-脱氢胆固醇转化为胆固醇的能力。其他突变会插入或删除DHCR7gene中的核苷酸或导致产生异常短的酶；这些突变消除了酶的活性。没有功能性7-脱氢胆固醇还原酶，细胞就无法产生足够的胆固醇。另外，胆固醇产生的潜在有毒副产物（例如7-脱氢胆固醇）可以积聚在血液和其他组织中。低胆固醇水平和相关物质积累的结合可能破坏许多身体系统的生长和发育。但是，尚不清楚胆固醇产生中的这种干扰如何导致史密斯-莱姆利-奥皮兹综合症的特定特征。"}]}' WHERE id = 16244;
UPDATE genes SET GHR_metadata_ch = '{"gene": "IL23R", "conditions": [{"name": "强直性脊柱炎", "detail": "已发现IL23Rgene的几种变异（多态性）会影响强直性脊柱炎的风险。这些变化之一似乎减少了患这种疾病的可能性。这种遗传变化改变了IL-23受体中的单个蛋白质构建块（氨基酸），用蛋白质位置381处的氨基酸谷氨酰胺取代氨基酸精氨酸（写为Arg381Gln或R381Q）。其他IL23R变异似乎增加了患强直性脊柱炎的风险。目前尚不清楚这些变化如何与一个人患上这种疾病的风险相关，但研究表明，IL23R变异的影响可能与IL-23受体在炎症中的作用有关。其他遗传和环境因素，其中许多是未知的，也影响发展强直性脊柱炎的机会。"}, {"name": "克罗恩病", "detail": "已发现IL23Rgene内或附近的几种变异会影响患克罗恩病的风险。这些关联主要在北欧血统的人中发现。例如，作为强直性脊柱炎（如上所述）的保护因子的Arg381Gln似乎也降低了患克罗恩病的风险。虽然目前还不清楚这种变化如何预防克罗恩病，但研究人员认为受体在触发肠壁炎症中的作用可能是其与这种疾病相关的基础。"}, {"name": "银屑病关节炎", "detail": "遗传学家庭参考提供有关银屑病关节炎的信息。"}, {"name": "溃疡性结肠炎", "detail": "遗传学家庭参考提供有关溃疡性结肠炎的信息。"}, {"name": "其他疾病", "detail": "IL23Rgene的变异也与其他几种免疫系统相关疾病的风险有关，包括称为牛皮癣的皮肤病。患有这种慢性炎症的人有红色，刺激性皮肤斑块，通常被片状白色鳞片覆盖。牛皮癣可能是由免疫系统故障引起的，其中身体的免疫反应转向自身，错误地攻击健康的皮肤细胞。（换行）每个已知的IL23R变异都会改变IL-23受体中的单个氨基酸。这些变体之一Arg381Gln似乎可以降低患牛皮癣的风险。（如上所述，这种变异还被证明可以预防强直性脊柱炎和克罗恩病。）其他IL23R变异可能会增加患牛皮癣的风险。研究人员认为，IL23Rgene的改变可能导致免疫系统调节的一般问题，这可能有助于解释为什么这些变异与以免疫系统功能障碍为特征的几种不同疾病有关。"}]}' WHERE id = 16246;
UPDATE genes SET GHR_metadata_ch = '{"gene": "DUOX2", "conditions": [{"name": "先天性甲状腺功能减退症", "detail": "研究人员已经确定了几种导致先天性甲状腺功能减退的DUOX2gene突变，这种疾病的特征是出生时甲状腺激素水平降低。这些突变大多数导致双氧化酶2酶的异常小版本。其余的突变改变了用于制造酶的结构单元（氨基酸）之一，这可能会改变酶的结构。所有DUOX2gene突变都限制了酶产生过氧化氢的能力。没有足够的过氧化氢，甲状腺激素的产生就会中断。在某些情况下，甲状腺肿大（甲状腺肿）以弥补甲状腺激素产生减少。由于DUOX2gene突变引起的病例是由于甲状腺激素合成的破坏，因此将其归类为甲状腺功能异常。（换行）甲状腺激素产生的减少受具有突变的DUOX2gene数量的影响。体内的每个细胞都有两个DUOX2gene拷贝。如果该gene的两个拷贝都有突变，则甲状腺中的细胞几乎不会产生过氧化氢。结果，甲状腺激素水平极低，导致严重的先天性甲状腺功能减退症。如果仅DUOX2gene的一个拷贝发生突变，则会产生一些过氧化氢。结果，甲状腺激素水平略有降低，导致轻度先天性甲状腺功能减退。有时，轻度先天性甲状腺功能减退是暂时的（短暂的），婴儿期甲状腺激素水平低会随着年龄的增长而增加。"}]}' WHERE id = 16253;
UPDATE genes SET GHR_metadata_ch = '{"gene": "DYNC1H1", "conditions": [{"name": "进行性神经性腓骨肌萎缩症", "detail": "遗传学家庭参考资料提供了有关Charcot-Marie-Tooth病的信息。"}, {"name": "脊髓性肌萎缩", "detail": "已发现DYNC1H1gene中至少有三个突变会引起一种称为脊髓性肌萎缩症，下肢显性疾病（SMA-LED）的疾病。这种形式的脊髓性肌萎缩症的特征是腿部肌肉无力，这在大腿肌肉（股四头肌）中最为严重。导致SMA-LED的DYNC1H1gene突变取代了动力蛋白复合物重链亚基中的单个蛋白质结构单元（氨基酸）。这些变化破坏了动力蛋白复合物的核心并损害了其功能。结果，细胞内蛋白质，突触小泡和其他物质的运动减少。控制肌肉运动的神经元中突触小泡运输的丧失被认为是SMA-LED患者经历的肌肉无力的原因。目前还不清楚为什么这种情况只影响下肢。"}]}' WHERE id = 16255;
UPDATE genes SET GHR_metadata_ch = '{"gene": "WAS", "conditions": [{"name": "Wiskott-Aldrich综合征", "detail": "已经发现WASgene中超过350个突变引起Wiskott-Aldrich综合征，该病症的特征在于形成血凝块的能力降低和免疫系统功能异常（免疫缺陷）。这些突变大多数导致异常短而无功能的WASP产生或阻止任何WASP产生。结果，WASP无法中继信号，从而破坏了发育中的血细胞中肌动蛋白细胞骨架的功能。缺乏WASP的白细胞对环境的反应能力下降，并形成免疫突触。结果，白细胞对外来入侵者的反应能力较差，导致许多与Wiskott-Aldrich综合征相关的免疫问题。同样，当帮助血凝块形成的细胞碎片（血小板）缺乏功能性WASP时，它们的发育就会受到损害，从而导致血小板大小减少和早期细胞死亡。白细胞和血小板的损伤是Wiskott-Aldrich综合征特有的免疫缺陷和出血问题的主要原因。"}, {"name": "X连锁低血小板计数", "detail": "已发现WASgene中的60多个突变会引起X连锁性血小板减少症，这是一种血液疾病，其特征是血小板数量和大小减少，导致出血时间延长。还可能发生免疫问题，例如对感染的敏感性增加。其中大多数是gene突变改变了WASP中的单个蛋白质结构单元（氨基酸）。突变通常会导致产生改变的蛋白质，该蛋白质无法有效地将信号从细胞膜传递到肌动蛋白细胞骨架。在X连锁血小板减少症患者中，这些信号传导问题主要影响血小板的发育。在某些情况下，白细胞会受到影响。当白细胞中的WASP功能受损时，这些细胞对外来入侵者的反应能力降低，并且更有可能发生免疫紊乱。（换行）一些WASgene突变在一些个体中引起X连锁血小板减少症，而在另一些个体中引起称为Wiskott-Aldrich综合征（如上所述）的相关病症。这些突变通常会阻止任何WASP的产生。尚不清楚为什么某些具有这些突变的人具有X连锁血小板减少症相对较轻的特征，而另一些人则具有Wiskott-Aldrich综合征的严重症状。"}, {"name": "严重的先天性中性粒细胞减少症", "detail": "遗传学家庭参考提供有关严重先天性中性粒细胞减少症的信息。"}, {"name": "其他疾病", "detail": "WASgene中的突变可引起称为严重先天性中性粒细胞减少症的疾病，其特征在于白细胞水平低（中性粒细胞减少症）和造血干细胞存活率降低。患有这种疾病的人往往患有免疫缺陷，导致反复感染。导致严重先天性中性粒细胞减少的WASgene突变导致WASP区域（称为Cdc42结合位点）发生变化。该位点必须连接（结合）称为Cdc42的蛋白质才能打开（激活）WASP。导致严重先天性中性粒细胞减少的突变导致WASP始终活跃（组成性活跃），即使在没有Cdc42蛋白的情况下也是如此。尚不完全了解组成型活性WASP如何导致严重的先天性中性粒细胞减少症的体征和症状。"}]}' WHERE id = 16259;
UPDATE genes SET GHR_metadata_ch = '{"gene": "DHODH", "conditions": [{"name": "米勒综合症", "detail": "已发现DHODHgene中至少有11个突变引起米勒综合症。这些突变中的大多数会改变二氢乳清酸脱氢酶中的单个蛋白质结构单元（氨基酸），这可能会损害酶正常运行的能力。尚不清楚DHODHgene突变如何导致Miller综合征的体征和症状。"}]}' WHERE id = 16279;
UPDATE genes SET GHR_metadata_ch = '{"gene": "TRIP13", "conditions": [{"name": "马赛克杂色非整倍性综合征", "detail": "已经发现至少两个TRIP13gene突变引起镶嵌杂色非整倍性（MVA）综合征。这种情况通常以染色体数目异常的细胞为特征，这种情况称为非整倍性。由TRIP13gene突变引起的MVA综合征患者在儿童时期罹患一种称为Wilms肿瘤的肾癌的风险很高。它们也可能生长缓慢，头部异常小（小头畸形）。（换行）涉及MVA综合征的TRIP13gene突变导致产生异常短的蛋白质，其迅速分解。结果，细胞缺乏TRIP13蛋白。这种蛋白质的缺乏会损害纺锤体装配检查点，即使不是所有的染色单体都附着在纺锤体微管上，细胞分裂也会进行。未连接的染色单体不能正确定位以进行分离，并且所得细胞通常具有异常数量的染色体。一些具有TRIP13gene突变的人的染色体异常，表明染色单体分离存在问题，尽管他们的细胞中没有异常数量的染色体。这些人还有MVA综合征的其他体征和症状。(换行)研究表明，纺锤体装配检查点的问题是MVA综合征癌症发展的基础，尽管其机制尚不完全清楚。还不清楚TRIP13gene突变如何导致MVA综合征的其他特征。研究人员推测异常细胞会发生自我毁灭（细胞凋亡）。MVA综合征的体征和症状可能是由于早期发育过程中各种组织的细胞损失所致。"}]}' WHERE id = 16280;
UPDATE genes SET GHR_metadata_ch = '{"gene": "EDNRB", "conditions": [{"name": "先天性巨结肠症", "detail": "已发现EDNRBgene中的30多个突变会引起Hirschsprung病，Hirschsprung病是一种导致严重便秘或肠道阻塞的疾病。尽管Hirschsprung病是另一种称为Waardenburg综合征IV型（如下所述）的疾病的特征，但EDNRBgene突变也可在没有Waardenburg综合征的人中引起Hirschsprung病。在EDNRBgene的两个拷贝之一中具有突变的人倾向于发展Hirschsprung疾病，而在该gene的两个拷贝中具有突变的人通常发展Waardenburg综合征IV型。这些突变中的大多数改变单个DNA构建块（核苷酸）在gene中。EDNRBgene的改变破坏了B型内皮素受体的正常功能，阻止了其在肠神经发育中发挥其通常的作用。结果，这些细胞在胚胎发育期间不能正常形成。缺乏肠神经可防止粪便正常通过肠道，导致严重的便秘或肠道阻塞。"}, {"name": "瓦尔登堡综合症", "detail": "在患有IV型Waardenburg综合征（也称为Waardenburg-Shah综合征）的人中，已经鉴定出EDNRBgene的十几个突变。这种类型的Waardenburg综合征的特征是皮肤，头发和眼睛的颜色发生变化；听力损失；和Hirschsprung病。EDNRBgene中的突变破坏了B型内皮素受体的正常功能，或导致异常小而无功能的蛋白质产生。因为受体是形成肠神经和黑素细胞所必需的，所以这些细胞类型在胚胎发育过程中不能正常形成。肠某些部位的肠神经缺失会导致Hirschsprung病的体征和症状。黑素细胞的缺乏会影响皮肤，头发和眼睛的颜色，并导致Waardenburg综合征的听力损失。"}, {"name": "癌症", "detail": "多项研究表明，EDNRBgene的遗传变异可能与黑色素瘤的风险增加有关，黑色素瘤是黑色素细胞开始的常见皮肤癌形式。但是，其他研究尚未显示出这种关联，并且该gene在癌症风险中的作用仍不清楚。"}]}' WHERE id = 16303;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SMARCA4", "conditions": [{"name": "棺材-西里斯综合症", "detail": "SMARCA4gene中至少有六个突变可引起棺材-西里斯综合征。这种情况的特征是发育迟缓，第五（小指）手指或脚趾异常以及被描述为粗糙的特征性面部特征。Coffin-Siris综合征中涉及的SMARCA4gene突变会改变BRG1蛋白中的单个蛋白结构单元（氨基酸）或从中去除氨基酸。尽管尚不清楚这些变化如何影响SWI/SNF复合物，但研究人员建议SMARCA4gene突变会导致染色质重塑异常。此过程的干扰会改变许多gene的活性并破坏多个细胞过程，这可以解释科芬－西里斯综合症的各种体征和症状。Coffin-Siris综合征患者似乎没有增加患癌症的风险（见下文）。"}, {"name": "肺癌", "detail": "遗传学家庭参考提供有关肺癌的信息。"}, {"name": "癌症", "detail": "已经在某些类型的癌症，特别是肺癌中发现了SMARCA4gene的突变。这些突变是体细胞的，这意味着它们是在一个人的一生中获得的，并且仅存在于肿瘤细胞中。尽管认为涉及SWI/SNF复合物的变化，但SMARCA4gene突变导致肺癌的机制尚不清楚。这些变化可能损害正常的细胞分化，从而导致某些细胞类型的过度生长，从而导致癌症。或者，异常的SWI/SNF复合物可能会破坏有助于控制细胞生长和分裂的gene调控，从而导致癌症。除了SMARCA4gene突变之外，其他遗传变化可能是癌症发展所必需的。"}]}' WHERE id = 16309;
UPDATE genes SET GHR_metadata_ch = '{"gene": "TFR2", "conditions": [{"name": "遗传性血色素沉着症", "detail": "在TFR2gene中已经鉴定出至少九种引起遗传性血色素沉着病形式的突变，称为3型。TFR2gene中的某些突变阻止了转铁蛋白受体2的产生。其他突变会导致蛋白质具有错误的蛋白质结构单元（氨基酸）序列或太短而无法正常运行的蛋白质。这些突变可能损害调节铁向某些细胞输入的能力。TFR2gene中的（换行）突变也被认为有助于体内低水平的铁调素，这允许从饮食中吸收过多的铁。发生这种情况时，多余的铁会储存在人体组织中，尤其是肝脏。铁超负荷导致器官损伤和3型血色素沉着病的其他体征和症状。"}]}' WHERE id = 16313;
UPDATE genes SET GHR_metadata_ch = '{"gene": "CARD9", "conditions": [{"name": "家族性念珠菌病", "detail": "在家族性念珠菌病患者中已鉴定出至少15个CARD9gene突变，这是一种由念珠菌真菌引起的感染的遗传倾向（通常称为酵母感染）。大多数患有家族性念珠菌病的人患有皮肤，指甲和粘膜的慢性酵母感染。这种称为慢性粘膜皮肤念珠菌病的体征和症状通常始于儿童早期。由CARD9gene突变引起的家族性念珠菌病患者也可能发展为系统性念珠菌病，这是念珠菌侵入血液和重要器官，尤其是大脑的潜在威胁生命的疾病。在某些患有这种疾病的人中也发现了由其他类型的真菌引起的感染，这种疾病有时被称为CARD9缺乏症。CARD9gene中的（换行）突变会损害通常有助于识别念珠菌的多种信号传导途径，并被认为可阻断（抑制）IL-17途径的活性。该途径的损害减少了身体对念珠菌的免疫反应，导致家族性念珠菌病患者发生慢性或复发性酵母感染。还认为该突变会损害嗜中性粒细胞的募集，从而对抗大脑和其他器官中的念珠菌感染，这可能导致系统性念珠菌病。"}]}' WHERE id = 16391;
UPDATE genes SET GHR_metadata_ch = '{"gene": "FRMD7", "conditions": [{"name": "X连锁婴儿眼球震颤", "detail": "已发现FRMD7gene中的35个以上突变引起X连锁婴儿眼球震颤。这些突变大多数会改变FRMD7蛋白中的单个蛋白结构单元（氨基酸）。FRMD7gene中的突变可能导致产生不稳定且无法执行其正常功能的蛋白质。缺乏功能性FRMD7蛋白被认为会破坏视网膜和控制眼球运动的大脑区域中神经细胞的发育。这些神经细胞的异常发育可能会导致X连锁婴儿眼球震颤的特征性不自主的左右眼球运动。"}]}' WHERE id = 16396;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SLITRK1", "conditions": [{"name": "图雷特综合症", "detail": "已经在少数图雷特综合症患者中发现了涉及SLITRK1gene的突变。这些突变之一，写为del1264C，从gene中删除了一个DNA构建块（碱基对）。这种突变导致SLITRK1蛋白异常短而无功能的产生。另一个突变改变了gene附近DNA区域中的单个碱基对。这种变化可能会干扰SLITRK1蛋白的产生。尽管SLITRK1gene在已知与Tourette综合征有关的大脑区域有活性，但尚不清楚该gene内或附近的突变如何导致这种情况的行为特征。由于很少有图雷特综合症患者报告突变，因此尚未证实SLITRK1gene与这种疾病的关联。"}]}' WHERE id = 16457;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SLITRK6", "conditions": [{"name": "耳聋和近视综合征", "detail": "在耳聋和近视综合征患者中已鉴定出至少三个SLITRK6gene突变，该疾病会导致听力下降和严重近视（高度近视）。导致耳聋和近视综合征的突变会导致异常短的SLITRK6蛋白无法正确锚定在细胞膜上。结果，蛋白质不能正常运作。SLITRK6蛋白功能受损会导致内耳神经发育异常，眼球生长控制不当，从而导致耳聋和近视综合征中出现听力下降和近视。"}]}' WHERE id = 16463;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ATG16L1", "conditions": [{"name": "克罗恩病", "detail": "ATG16L1gene的至少一种变异与克罗恩病的风险增加有关，特别是一种影响小肠下部（回肠）和结肠的疾病。这种增加的风险主要发生在北欧血统的人身上。鉴定出的ATG16L1变异会改变ATG16L1蛋白关键区域中的单个蛋白结构单元（氨基酸）。具体而言，它在蛋白质位置300（写为Thr300Ala或T300A）上用氨基酸丙氨酸取代了氨基酸苏氨酸。（换行）ATG16L1gene的这种变化损害了自噬过程，允许磨损的细胞部分和有害细菌在否则会被破坏时持续存在。这些细胞成分和细菌可能引发不适当的免疫系统反应，导致肠壁慢性炎症和克罗恩病特有的消化问题。研究人员继续研究ATG16L1gene的变化与一个人患这种疾病的风险之间的关系。"}]}' WHERE id = 16492;
UPDATE genes SET GHR_metadata_ch = '{"gene": "CNGA3", "conditions": [{"name": "色盲", "detail": "已经发现CNGA3gene中的100多个突变会导致视力障碍色盲。这些突变约占完全性色盲病例的25%，该疾病的特征是完全缺乏色觉和婴儿早期出现的其他视觉问题。CNGA3gene突变也已在少数色盲不完全的个体中发现，这是一种与色觉受限相关的轻度疾病。（换行）完全色盲的CNGA3gene突变影响α亚基的产生或功能。在某些情况下，不会产生蛋白质。在其他情况下，蛋白质被改变并且不能正常工作。没有α亚基或具有异常亚基的CNG通道组装不起作用；它们防止锥体进行光转导。研究人员推测，一些有缺陷的通道允许阳离子大量流入视锥细胞，最终导致这些细胞自毁（发生凋亡）。视锥细胞功能的丧失是完全色盲患者缺乏色觉和其他视觉问题的基础。（换行）CNGA3gene中的一些突变减少但不消除视锥细胞中CNG通道的功能。这些突变会导致不完全的色盲，因为部分功能的视锥细胞可以将某些视觉信息传输到大脑。(换行)由于这些CNG通道特定于视锥细胞，因此杆通常不受此疾病的影响。"}, {"name": "视锥-视杆营养不良", "detail": "遗传学家庭参考提供有关锥杆营养不良的信息。"}, {"name": "其他疾病", "detail": "在少数进行性视锥细胞营养不良的病例中也发现了CNGA3gene的突变。像色盲症（如上所述）一样，这种情况会影响视锥细胞在视网膜中的功能。但是，与色盲症不同，进行性视锥细胞营养不良与出生时正常工作但在儿童期或青春期开始出现功能障碍的视锥细胞有关。随着时间的流逝，进行性视锥细胞营养不良的人会变得越来越模糊和色觉丧失。目前尚不清楚为什么某些CNGA3gene突变会导致色盲，而另一些会导致进行性视锥细胞营养不良。"}]}' WHERE id = 16527;
UPDATE genes SET GHR_metadata_ch = '{"gene": "DCTN1", "conditions": [{"name": "佩里综合征", "detail": "已发现DCTN1gene中至少有五个突变引起佩里综合征。这种进行性脑部疾病的特征是运动异常模式，称为帕金森氏症，精神病变化，体重减轻和呼吸异常缓慢（换气不足）。(换行)导致佩里综合征的大多数突变都会改变dynactin-1蛋白中的单个蛋白结构单元（氨基酸）。这些遗传变化削弱了dynactin-1的p150胶合版本与dynactin复合物和微管结合的能力。不完整的dynactin复合物在细胞内运输物质的能力降低。神经元正常功能所需材料的缓慢或异常运输导致这些细胞发生故障并最终死亡。调节运动，情绪和呼吸的大脑区域神经元的逐渐丧失是佩里综合征的体征和症状的基础。"}, {"name": "肌萎缩侧索硬化症", "detail": "遗传学家庭参考提供有关肌萎缩侧索硬化的信息。"}, {"name": "其他疾病", "detail": "研究人员已经鉴定出至少一种导致神经系统疾病的DCTN1gene突变，称为远端遗传性运动神经病VIIB型。这种疾病的体征和症状首先出现在成年初期，包括呼吸困难和面部和手部肌肉进行性无力。脚和腿的肌肉无力发展较晚。导致这种疾病的突变改变了用于制造dynactin-1的氨基酸之一。具体而言，它用蛋白质59位的氨基酸丝氨酸取代了氨基酸甘氨酸（写为Gly59Ser或G59S）。目前尚不清楚这种突变如何导致远端遗传性运动神经病VIIB型。改变的蛋白质可能导致异常的dynactin复合物并干扰复合物与微管之间的相互作用，这将破坏运输活性并损害神经元中轴突的功能。（换行）至少一个DCTN1gene突变与称为额颞叶痴呆（FTD）的脑部疾病相关，而没有肌萎缩侧索硬化（ALS）的特征。这种疾病发生在成年中后期，其特征是性格和行为的改变，这可能使受影响的个体难以以社交方式与他人互动。言语和语言的变化也可能发生，例如使用正确单词的问题和语言理解的困难。与该疾病相关的DCTN1gene突变将氨基酸精氨酸替换为蛋白质位置1101（写为Arg1101Lys或R1101K）上的氨基酸赖氨酸。目前尚不清楚这种突变如何导致FTD。这种突变可能会改变dynactin-1的3维形状，从而可能削弱其与dynactin复合物和微管的结合。这种受损的结合可以减缓轴突正常功能和神经冲动的有效传递所需的材料的运输。"}]}' WHERE id = 16531;
UPDATE genes SET GHR_metadata_ch = '{"gene": "RPE65", "conditions": [{"name": "莱伯先天性黑蒙", "detail": "已发现RPE65gene中的30多个突变会导致Leber先天性黑ama病。该gene的突变占该病所有病例的6%至16%。（换行）RPE65gene突变导致RPE65蛋白功能部分或全部丧失。结果，全反式视网膜不能转化回11-顺式视网膜，并且在视网膜色素上皮中形成过量的11-顺式视网膜。这些异常会阻止视觉循环，从而导致从生命的早期开始就出现严重的视觉障碍。"}, {"name": "白底眼底", "detail": "遗传学家庭参考提供有关白底眼底的信息。"}, {"name": "视网膜色素变性", "detail": "遗传学家庭参考提供有关视网膜色素变性的信息。"}, {"name": "其他疾病", "detail": "在另一种称为色素性视网膜炎的眼疾患者中，已经鉴定出RPE65gene的20多个突变。这种情况的特征是由视网膜中感光细胞的逐渐退化引起的进行性视力丧失。色素性视网膜炎的第一个迹象通常是夜视丧失，这在儿童时期就很明显。多年来，疾病进展到破坏侧（周边）视力和中央视力。最终可能导致失明。（换行）导致视网膜色素变性的RPE65gene突变破坏RPE65蛋白功能，其通过损害视觉周期导致视力丧失。该gene的突变似乎是视网膜色素变性的罕见原因；这些遗传变化仅占所有病例的一小部分。"}]}' WHERE id = 16548;
UPDATE genes SET GHR_metadata_ch = '{"gene": "TIMM8A", "conditions": [{"name": "耳聋-肌张力障碍-视神经病变综合征", "detail": "已发现TIMM8Agene中至少有15个突变会导致耳聋-肌张力障碍-视神经病变（DDON）综合征。这些突变大多数导致线粒体内缺乏功能性TIMM8A蛋白，从而阻止了TIMM8A/TIMM13复合物的形成。研究人员认为，缺乏这种复合物会导致蛋白质跨膜间隙的异常转运，尽管目前尚不清楚异常的蛋白质转运如何影响线粒体的功能并引起DDON综合征的体征和症状。(换行)一些患有DDON综合征的人具有大的DNA缺失，其去除整个TIMM8Agene和称为BTK的相邻gene的一端。BTKgene中的突变会导致X连锁无球蛋白血症（XLA），其特征是对感染的敏感性增加。具有大DNA缺失的个体（包括TIMM8Agene和BTKgene）具有DDON综合征和XLA的体征和症状。"}]}' WHERE id = 16563;
UPDATE genes SET GHR_metadata_ch = '{"gene": "CREBBP", "conditions": [{"name": "鲁宾斯坦-泰必氏综合症", "detail": "涉及CREBBPgene的突变或染色体重排与Rubinstein-Taybi综合征有关，Rubinstein-Taybi综合征是一种由于产生具有部分功能的CREB蛋白而影响出生前后正常发育的疾病。这种情况的临床特征包括身材矮小，中度至重度智力障碍，独特的面部特征，宽大的拇指和脚趾。其他功能包括眼睛异常，心脏和肾脏缺陷，牙齿问题和肥胖。"}, {"name": "癌症", "detail": "CREBBPgene的染色体重排与急性髓细胞性白血病（AML），慢性粒细胞性白血病和骨髓增生异常综合症有关。该gene的体细胞突变与卵巢癌有关，这是由于卵巢细胞中异常短而无活性的CREB结合蛋白的产生，导致细胞生长和分裂的正常调节受到破坏。"}]}' WHERE id = 16569;
UPDATE genes SET GHR_metadata_ch = '{"gene": "GATA1", "conditions": [{"name": "贫血性贫血和血小板减少症", "detail": "已发现GATA1gene中至少有八个不同的突变会引起促红细胞生成性贫血和血小板减少症。这些突变大多数会改变GATA1蛋白中的单个蛋白结构单元（氨基酸）。GATA1gene突变破坏了蛋白质与DNA结合或与其他蛋白质相互作用的能力。GATA1蛋白正常功能的这种损害导致增殖增加，分化减少和未成熟血细胞过早死亡。未成熟的血细胞不能发挥专门的成熟血细胞的功能。缺乏分化导致红细胞（贫血）和参与凝血（血小板减少症）的血小板短缺，这是造血功能障碍性贫血和血小板减少症的特征。"}, {"name": "癌症", "detail": "一些gene突变可以在一个人的一生中获得，并且仅存在于某些细胞中。这些突变称为体细胞突变，它们不是遗传的。GATA1gene中的体细胞突变会增加罹患称为短暂异常骨髓生成（TAM）的造血细胞疾病的风险。这些突变通常发生在胎儿发育过程中，并且增加的风险仅适用于在每个细胞中出生时带有21号染色体额外拷贝的人，这种情况称为21三体性或唐氏综合症。（线路中断）大约10%的唐氏综合症患者通常在出生时或不久后发生TAM。TAM的特征在于血液，肝脏和骨髓中未成熟的巨核细胞前体细胞的积累。在大多数情况下，TAM不会引起任何体征或症状，并在三到四个月内消失。然而，大约20%的TAM婴儿会出现严重的并发症，如肝脏肿大（肝肿大），肝脏瘢痕组织积聚（肝纤维化），出生前体内液体积聚过多（胎儿水肿）和心脏失败。（换行）据估计，20%至30%的TAM患儿后来会患上称为急性巨核细胞白血病（AMKL）的造血细胞癌。在具有TAM或AMKL的个体中发现的体细胞GATA1gene突变阻止了正常长度GATA1蛋白的产生，并且仅允许产生较短版本的GATA1s。目前尚不清楚为什么体细胞GATA1gene突变会增加唐氏综合症患者发生这些骨髓疾病的风险。"}]}' WHERE id = 16573;
UPDATE genes SET GHR_metadata_ch = '{"gene": "PDGFB", "conditions": [{"name": "隆突性皮肤纤维肉瘤", "detail": "隆突性皮肤纤维肉瘤是一种罕见的癌症，可在皮肤深层引起肿瘤，其特征是涉及PDGFBgene的体细胞突变。体细胞突变不是遗传的，而是在一个人的一生中获得的，仅存在于某些细胞中。隆突性皮肤纤维肉瘤与染色体17和22之间遗传物质的重排（易位）有关。这种易位，写为t（17；22），将22号染色体上的PDGFBgene的一部分与17号染色​​体上的另一个gene的一部分（称为COL1A1）融合在一起。易位位于一条或多条额外的染色体上，这些染色体可以是正常的线形或圆形。所得的组合（融合）gene称为COL1A1-PDGFB。(换行)COL1A1-PDGFB融合gene提供了制备融合蛋白的说明。在易位中，PDGFBgene失去了抑制其活性的部分DNA，并且COL1A1-PDGFB融合蛋白的产生受COL1A1gene序列控制。结果，gene融合导致产生大量的融合蛋白。COL1A1-PDGFB蛋白形成二聚体，并像正常的PDGFB前体蛋白一样进行加工。加工过程去除了COL1A1部分，该部分形成了一种研究人员认为具有活性PDGF-BB蛋白功能的蛋白。过量的PDGF-BB蛋白异常刺激细胞增殖和分化，导致在隆突性皮肤纤维肉瘤中看到的肿瘤形成。"}]}' WHERE id = 16579;
UPDATE genes SET GHR_metadata_ch = '{"gene": "BTK", "conditions": [{"name": "孤立的生长激素缺乏症", "detail": "已发现BTKgene中很少有突变会导致III型孤立的生长激素缺乏症，这种疾病的特征是生长缓慢，身材矮小以及免疫系统减弱，伴有丙种球蛋白血症，受影响的个体容易感染。"}, {"name": "X连锁无丙种球蛋白血症", "detail": "已经发现BTKgene中的突变引起X连锁的丙种球蛋白血症（XLA），这种情况在男性中由于缺乏BTK蛋白而引起，并且以复发性肺和细菌感染为特征。由于包括BTKgene和TIMM8Agene在内的大量DNA缺失，一些人同时患有XLA和肌张力障碍-视神经病变（DDON）综合征的体征和症状。"}]}' WHERE id = 16585;
UPDATE genes SET GHR_metadata_ch = '{"gene": "LDLR", "conditions": [{"name": "高胆固醇血症", "detail": "LDLRgene的突变会导致高胆固醇的遗传形式，称为家族性高胆固醇血症。在该gene中已鉴定出1000多种突变。这些遗传变化中的一些减少了细胞内产生的低密度脂蛋白受体的数量。其他突变破坏了受体从血液中去除低密度脂蛋白的能力。结果，LDLRgene突变的人的血液胆固醇水平很高。当过量的胆固醇通过血液循环时，它会异常沉积在向心脏（冠状动脉）供血的皮肤，肌腱和动脉等组织中。冠状动脉壁中胆固醇的积累大大增加了心脏病发作的风险。(换行)大多数家族性高胆固醇血症患者从受影响的父母那里继承一份LDLRgene的改变拷贝，从另一父母那里继承一份gene的正常拷贝。这些病例与早期心脏病的风险增加有关，通常从四十或五十多岁开始。很少有家族性高胆固醇血症的人出生时带有两个LDLRgene突变拷贝。当该人有两个受影响的父母时，就会发生这种情况，每个父母都传递了一个改变的gene拷贝。两个LDLR突变的存在导致通常在儿童时期出现的更严重的高胆固醇血症形式。"}]}' WHERE id = 16596;
UPDATE genes SET GHR_metadata_ch = '{"gene": "LRRK2", "conditions": [{"name": "帕金森病", "detail": "研究人员已经在晚发性帕金森病（最常见的疾病形式，出现在50岁以后）的家庭中发现了100多个LRRK2gene突变。这些突变取代了dardarin蛋白中的单个氨基酸，从而影响了该蛋白的结构和功能。目前尚不清楚LRRK2gene突变如何导致帕金森病特有的运动和平衡问题。（换行）在法国和西班牙之间的巴斯克地区，帕金森病的相对常见原因是在蛋白质位置1441处用氨基酸甘氨酸替换氨基酸精氨酸的突变（写为Arg1441Gly或R1441G）。蛋白质namedardarin来自巴斯克语“dardara”，意思是震颤，这是帕金森氏病的一个特征。(换行)对来自世界各地的几个不同人群的研究表明，在3%至7%的家族性帕金森氏病病例中，常见的LRRK2gene突变。该突变用蛋白质位置2019处的氨基酸丝氨酸取代氨基酸甘氨酸（写为Gly2019Ser或G2019S）。在家族病例中，Gly2019Ser突变的发生率在北非的阿拉伯人和Ashkenazi（东欧和中欧）犹太血统的人中最高，在亚洲和北欧人口中最低。在没有家族病史的散发性帕金森氏病病例中，也有1%至3%的人报告了这种特殊的突变。（换行）在中国和日本人群中的研究已经确定了LRRK2gene突变，其在帕金森病患者中比在没有该疾病的人群中更频繁地发生。该突变用蛋白质位置2385处的氨基酸精氨酸取代氨基酸甘氨酸（写为Gly2385Arg或G2385R）。在这些人群中，这种突变似乎增加了帕金森氏病的风险。"}, {"name": "克罗恩病", "detail": "遗传学家庭参考提供有关克罗恩病的信息。"}]}' WHERE id = 16613;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SNAI2", "conditions": [{"name": "Piebaldism", "detail": "SNAI2gene的一个拷贝在一些斑驳病的情况下被删除，这种情况的特征在于由缺乏色素细胞（黑素细胞）引起的皮肤和头发的白色斑块。丢失一份gene可能会减少snail 2蛋白的产生。snail 2蛋白的缺乏可能会破坏皮肤和头发某些区域黑素细胞的发育，导致色素的斑片状损失。"}, {"name": "瓦尔登堡综合症", "detail": "在II型Waardenburg综合征的一些病例中，SNAI2gene的两个拷贝都缺失。没有该gene的拷贝，snail 2蛋白不存在。缺乏蜗牛2可能会破坏皮肤，头发，眼睛和内耳某些区域黑素细胞的发育，导致听力丧失和色素沉着斑片状丧失，这是Waardenburg综合征II型的特征。"}]}' WHERE id = 16623;
UPDATE genes SET GHR_metadata_ch = '{"gene": "COL18A1", "conditions": [{"name": "诺氏综合征", "detail": "在患有诺氏综合症的人中，已经鉴定出COL18A1gene至少有十几个突变，这种疾病的特征是严重的视力问题和称为枕叶脑膨出的颅骨缺损。大多数COL18A1gene突变会导致用于制造胶原蛋白XVIII的alpha 1亚基的遗传蓝图异常短。还有其他一些突变可以替代蛋白质序列中的单个蛋白质结构单元（氨基酸）。尽管该过程尚不清楚，但COL18A1gene突变导致胶原蛋白XVIII丢失。大多数COL18A1gene突变影响胶原蛋白XVIII的所有同工型，但至少一个突变仅影响短同工型。胶原蛋白XVIII蛋白的一种或所有同工型的丧失可能会导致诺氏综合症的体征和症状。目前尚不清楚内皮抑素是否与这种情况有关。"}]}' WHERE id = 16632;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ANO5", "conditions": [{"name": "骨干phy发育不良", "detail": "已经在患有称为骨干phy发育不良的骨骼疾病的人中鉴定出ANO5gene的突变，该疾病的特征是骨骼脆弱，下颌问题和其他骨骼异常。"}, {"name": "肢带型肌营养不良症", "detail": "已经在患有2L型肢带型肌营养不良症的人中鉴定出ANO5gene的突变，该病症的特征在于由于缺陷/过早的anoctamin-5蛋白引起的肩部，臀部，大腿和上臂的肌肉无力和萎缩（萎缩）。损害其作为肌细胞中氯离子通道的活性。"}, {"name": "三好肌病", "detail": "已经发现ANO5gene的突变会引起三好肌病，三好肌病是一种肌肉疾病，其特征是由于anoctamin-5蛋白异常而导致进行性肌无力和远端肌肉萎缩，这会削弱其作为肌肉细胞中氯离子通道的活性。三好肌病可能是肢带型肌营养不良症的一种变异。"}]}' WHERE id = 16646;
UPDATE genes SET GHR_metadata_ch = '{"gene": "HCN4", "conditions": [{"name": "病态窦房结综合征", "detail": "在患有病态窦房结综合征的人中，已经鉴定出HCN4gene中至少有五个突变，病态窦房结综合征是一种影响SA节点功能的心脏病。这些突变大多数会改变HCN4通道中的单个蛋白质结构单元（氨基酸）。在某些情况下，更少的改变的通道到达细胞膜，在那里它们需要运输离子。在其他情况下，通道位于正确的位置，但具有异常结构，改变离子如何流过它。所有突变都会减少离子进入SA节点细胞的整体流动，从而阻止它产生控制电信号心跳。这些变化增加了心跳异常缓慢（心动过缓）的风险，这可能导致头晕，头晕，晕厥（晕厥）和相关症状。在心跳缓慢而没有任何其他症状（无症状性心动过缓）的人中也发现了HCN4gene突变。"}, {"name": "布鲁格达氏症候群", "detail": "遗传学家庭参考提供有关Brugada综合征的信息。"}, {"name": "室致密化不全", "detail": "遗传学家庭参考提供有关左心室不紧致的信息。"}]}' WHERE id = 16652;
UPDATE genes SET GHR_metadata_ch = '{"gene": "NNT", "conditions": [{"name": "家族性糖皮质激素缺乏症", "detail": "已发现NNTgene中至少有25个突变会导致家族性糖皮质激素缺乏。这种情况的特征是可能危及生命的低血糖（低血糖），反复感染和肤色比其他家庭成员深（色素沉着过度）。家族性糖皮质激素缺乏症的特征是由于肾上腺无法产生一组称为糖皮质激素的激素引起的，这些激素在体内的许多功能中起作用。NNTgene突变约占这种情况的10%。(换行)导致家族性糖皮质激素缺乏的大多数突变会改变烟酰胺核苷酸转氢酶中的单个蛋白质结构单元（氨基酸）。这些突变削弱了酶产生NADPH的能力，导致肾上腺中活性氧的增加。随着时间的推移，这些有毒分子会损害肾上腺细胞的功能并导致这些细胞的死亡（细胞凋亡），从而减少糖皮质激素的产生。这些激素的缺乏会损害血糖调节，免疫系统功能和其他细胞功能，从而导致家族性糖皮质激素缺乏症的体征和症状。(换行)目前尚不清楚为什么NNTgene突变似乎只影响肾上腺功能。研究人员建议，由NNTgene突变引起的家族性糖皮质激素缺乏症患者随着时间的推移可能会在其他组织中出现问题，因为活性氧会发挥其有害作用。"}]}' WHERE id = 16660;
UPDATE genes SET GHR_metadata_ch = '{"gene": "NOTCH1", "conditions": [{"name": "亚当斯-奥利弗综合症", "detail": "已发现NOTCH1gene中至少有15个突变会引起Adams-Oliver综合征，该病的特征是通常在头皮上的皮肤缺失区域（先天性皮肤发育不全）和手脚畸形。这些突变通常是遗传性的，存在于身体的每个细胞中。Adams-Oliver综合征中涉及的某些NOTCH1gene突变导致异常短蛋白的产生，该蛋白可能会迅速分解，从而导致NOTCH1短缺。其他突变会改变Notch1蛋白中的单个蛋白结构单元（氨基酸）。这些变化被认为会改变蛋白质的结构，削弱其功能。Notch1信号的丢失可能是Adams-Oliver综合征患者血管和心脏异常的基础。但是，尽管这些类型的异常在具有NOTCH1gene突变的受影响个体中比在具有不同gene突变的个体中更为常见，但一些与NOTCH1相关的Adams-Oliver综合征的人没有血管或心脏异常。尚不清楚Notch1功能丧失如何导致该病的头皮和四肢异常。研究人员建议这些特征可能是由于出生前血管发育异常所致。"}, {"name": "严重的先天性心脏病", "detail": "遗传学家庭参考资料提供有关严重先天性心脏病的信息。"}, {"name": "头颈部鳞状细胞癌", "detail": "在约15%的头颈部鳞状细胞癌（HNSCC）中发现了NOTCH1gene的突变。这种类型的癌性肿瘤发生在口腔，鼻子和喉咙的潮湿衬里。与这种情况相关的NOTCH1gene突变是在一个人的一生中获得的，仅在肿瘤细胞中发现；这些变化称为体细胞突变。NOTCH1gene中的突变可能会减少或消除功能性NOTCH1蛋白的产生，或导致无法参与细胞信号传导的蛋白的产生。没有Notch1蛋白的肿瘤抑制功能，细胞可以在没有控制的情况下生长和分裂，导致肿瘤形成。"}, {"name": "其他疾病", "detail": "NOTCH1gene的突变会损害出生前的正常心脏发育，从而导致心脏和相关结构异常。在将主动脉连接到心脏（主动脉瓣）的瓣膜中发生一种这样的异常。主动脉是将血液从心脏分配到身体其他部位的大血管。主动脉瓣通常有三个瓣或瓣尖，开放让血液离开心脏并聚集在一起以防止血液重新进入心脏。然而，在大约1%到2%的人中，主动脉瓣只有两个瓣，这被称为二尖瓣主动脉瓣。NOTCH1gene突变似乎与4%至10%的二尖瓣主动脉瓣病例有关。(换行)患有二尖瓣主动脉瓣的个体发生其他主动脉异常的风险高于正常风险，例如主动脉壁隆起（胸主动脉瘤）或主动脉壁层突然撕裂（主动脉夹层）。另外，二尖瓣主动脉瓣患者可能会发生主动脉瓣上钙的积聚。研究人员怀疑NOTCH1gene突变在胸主动脉瘤的发展和瓣膜钙化中也起作用。（换行）NOTCH1gene突变也与严重的先天性心脏病有关。患有这种情况的个体具有一种或多种影响血液流入，流出或通过心脏的特定心脏异常。一些心脏缺陷涉及心脏本身内的结构，例如心脏的两个下腔室（心室）或控制血液流过心脏的瓣膜。其他影响进出心脏的大血管（包括主动脉和肺动脉）的结构。还有一些涉及这些结构异常的组合。"}, {"name": "其他癌症", "detail": "在其他类型的癌症中也发现了NOTCH1gene突变，特别是在称为T细胞急性淋巴细胞白血病和慢性淋巴细胞白血病的血细胞癌以及一种称为非小细胞肺癌的肺癌中。与HNSCC（如上所述）不同，在这些癌症中发现的NOTCH1gene突变异常地打开（激活）NOTCH1信号传导。增加的活性可导致不受控制的细胞生长和分裂，这可导致癌症的发展。研究人员正在努力了解激活和失活NOTCH1gene突变如何导致癌症发展。"}]}' WHERE id = 16701;
UPDATE genes SET GHR_metadata_ch = '{"gene": "UCHL1", "conditions": [{"name": "帕金森病", "detail": "UCHL1gene中相对常见的变异（多态性）可以降低患帕金森氏病的风险，帕金森氏病的特征是运动和平衡方面的进行性问题。该变化导致用于制备遍在蛋白羧基末端酯酶L1的结构单元（氨基酸）之一的变化。具有多态性的人具有氨基酸酪氨酸（写为Ser18Tyr或S18Y），而不是氨基酸链中第18位的丝氨酸。这种变化在中国和日本人口中最为常见，而在欧洲人口中则较少发生。该多态性降低了泛素羧基末端酯酶L1的连接酶活性，但对水解酶活性影响很小。一些研究表明，具有S18Y多态性可能有助于预防帕金森氏病，尤其是在年轻人中。但是，其他研究并未显示出这种效果。尚不清楚这种氨基酸变异如何降低患帕金森氏病的风险。(换行)UCHL1gene的不同变化可能会增加帕金森氏病的风险。在该疾病的两个兄弟姐妹中已经报道了这种突变。该突变用泛素羧基末端酯酶L1（写为Ile93Met或I93M）中第93位的氨基酸蛋氨酸取代了氨基酸异亮氨酸。突变导致水解酶活性降低，这可能破坏泛素-蛋白酶体系统。不需要的蛋白质不是被降解，而是累积到损害或杀死大脑中神经细胞的毒性水平。这些细胞的丧失削弱了大脑和肌肉之间的交流，最终大脑无法控制肌肉运动。目前尚不清楚该UCHL1gene突变是否是帕金森氏病的真正危险因素，因为仅在一个家庭中发现了该突变。"}]}' WHERE id = 16721;
UPDATE genes SET GHR_metadata_ch = '{"gene": "EPCAM", "conditions": [{"name": "林奇综合症", "detail": "EPCAMgene中的某些突变与Lynch综合征相关，Lynch综合征会增加患多种类型癌症的风险，尤其是大肠癌（结肠癌）和直肠癌（统称为结直肠癌）。这些突变占Lynch综合征病例的6%。在2号染色体上，EPCAMgene位于另一个称为MSH2的gene旁边。每个gene都提供了制备单个信使RNA（mRNA）的说明，该信使RNA可作为制备蛋白质的遗传蓝图。Lynch综合征中涉及的EPCAMgene突变去除了一个信号传导该gene末端的区域，从而导致形成了一个长的mRNA，其中包括EPCAM和MSH2。（换行）由于未知原因，这些EPCAMgene突变导致MSH2gene被称为启动子高甲基化的机制关闭（失活）。启动子是靠近控制gene活性（表达）的gene开始的DNA区域。当太多称为甲基的小分子附着于启动子区域时，发生高甲基化。附着在MSH2启动子上的额外甲基降低了MSH2gene的表达，这意味着上皮细胞中产生的蛋白质更少。(换行)MSH2蛋白在修复DNA错误中起着至关重要的作用；这种蛋白质的丢失会阻止正确的DNA修复，并且随着细胞继续分裂，错误会累积。这些错误可能导致不受控制的细胞生长并增加患癌症的风险。"}, {"name": "其他疾病", "detail": "EPCAMgene的突变也可引起先天簇状肠病。这种情况的特征是肠上皮细胞异常发育。在这种情况下，绒毛是排列在小肠上的小指状突起，是异常的。特别是，它们的尖端有“簇状”的上皮外细胞。通常，这些投影提供了大大增加的表面积以吸收营养。改变后的绒毛比正常组织吸收营养和液体的能力差，导致危及生命的腹泻和生长不良。先天簇状肠病在新生儿出生后几天内发展并持续一生。这种情况下涉及的EPCAMgene突变导致功能性EPCAM蛋白的丧失。EpICD信号的丧失导致肠上皮细胞异常发育，从而导致先天簇状肠病。"}]}' WHERE id = 16740;
UPDATE genes SET GHR_metadata_ch = '{"gene": "AGPAT2", "conditions": [{"name": "先天性全身性脂肪营养不良", "detail": "在1型先天性全身性脂肪营养不良（也称为Berardinelli-Seip先天性脂肪营养不良）患者中，已鉴定出AGPAT2gene至少26个突变。这种罕见病的特征是几乎完全没有脂肪组织和非常肌肉的外观。脂肪组织短缺导致多种健康问题，包括在血流中循环的高水平甘油三酯（高甘油三酯血症）和糖尿病。（换行）导致先天性全身性脂肪营养不良1型的AGPAT2gene突变大大降低或消除了AGPAT2酶的活性。研究表明，这种酶活性的丧失减少了脂肪细胞中三酰基甘油的产生和储存，这阻止了这些细胞储存脂肪。缺乏酶活性也可能降低脂肪细胞中甘油磷脂的水平，这会改变细胞膜的结构并破坏这些细胞内的正常信号传导。所有这些异常都阻止身体正常地将脂肪储存在脂肪组织中。导致的体内脂肪缺乏是先天性全身性脂肪营养不良1型的各种体征和症状的基础。"}]}' WHERE id = 16745;
UPDATE genes SET GHR_metadata_ch = '{"gene": "AGTR1", "conditions": [{"name": "肾小管发育不全", "detail": "已发现AGTR1gene的几种变异导致称为肾小管发育不全的严重肾脏疾病，其特征在于出生前肾脏发育异常，无尿，严重低血压（低血压），血流量减少（低灌注）和身体不足。在胎儿发育期间获得足够的氧气。这些问题是由于羊水减少（羊水过少）导致一系列先天缺陷（称为波特序列）引起的。"}, {"name": "其他疾病", "detail": "据报道，AGTR1gene的几种变异与一种称为原发性高血压，心脏病或糖尿病肾病（一种影响肾功能的糖尿病并发症）的高血压（高血压）的风险增加有关。"}]}' WHERE id = 16772;
UPDATE genes SET GHR_metadata_ch = '{"gene": "MYH3", "conditions": [{"name": "弗里曼-谢尔顿综合症", "detail": "在Freeman-Sheldon综合征患者中至少鉴定出26个MYH3gene突变。这种疾病会影响出生前的肌肉和骨骼发育，其特征是关节畸形（挛缩）限制了手和脚的运动。研究人员认为，引起Freeman-Sheldon综合征的MYH3突变会影响胚胎骨骼肌肌球蛋白重链3蛋白与ATP相互作用的方式，从而降低胎儿肌肉细胞收缩的能力。肌肉收缩的这种损害可能会干扰胎儿的肌肉发育，从而导致挛缩以及与Freeman-Sheldon综合征相关的其他肌肉和骨骼异常。尚不清楚MYH3gene突变如何与该疾病的其他特征相关。"}, {"name": "谢尔顿-霍尔综合征", "detail": "在患有Sheldon-Hall综合征的人中至少鉴定出15个MYH3gene突变，Sheldon-Hall综合征是一种类似于Freeman-Sheldon综合征的肌肉和骨骼疾病（如上所述），会损害手和脚的关节运动。据信引起Sheldon-Hall综合征的MYH3gene突变会干扰胚胎骨骼肌肌球蛋白重链3蛋白与肌动蛋白和其他肌肉蛋白结合的能力，并且还可能损害粗丝的形成。这些突变可能会阻止肌肉收缩得到适当控制，并干扰出生前的肌肉发育，从而导致挛缩以及与Sheldon-Hall综合征相关的其他肌肉和骨骼异常。"}]}' WHERE id = 16774;
UPDATE genes SET GHR_metadata_ch = '{"gene": "KANK2", "conditions": [{"name": "毛发角化病", "detail": "At least one mutation in the KANK2 gene has been found to cause a form of keratoderma with woolly hair classified as type IV. It is characterized by thick, calloused skin on the palms of the hands and soles of the feet (palmoplantar keratoderma); coarse, dry, fine, tightly curled, and sparse scalp hair; and abnormal fingernails and toenails. Unlike other forms of keratoderma with woolly hair, type IV does not appear to cause heart disease. (line-break) The identified mutation changes a single protein building block (amino acid) in SIP, replacing the amino acid alanine with the amino acid valine at position 670 (written as Ala670Val or A670V). Researchers predict that this mutation affects a region of SIP that is critical for its interaction with SRCs, which would ultimately lead to changes in gene activity. However, it is unclear how this mutation affects growth of the hair and skin, or why its effects appear to be limited to these tissues.\r"}, {"name": "其他疾病", "detail": "在患有一种称为肾病综合征的肾脏疾病的家庭中，已经鉴定出KANK2gene的至少两个其他突变。这种情况的特点是肾脏无法过滤血液中的废物，导致尿液中的蛋白质，肿胀（水肿）和可能的肾功能衰竭。与此conditions相关的两个KANK2gene突变都会改变SIP中的单个氨基酸。预计这些变化会损害蛋白质的功能。研究表明，SIP对于称为足细胞的肾细胞的正常功能是必需的，这对于过滤血液中的废物至关重要。然而，尚不清楚改变的SIP蛋白如何影响足细胞功能。(换行)目前尚不清楚为什么某些KANK2gene突变会影响皮肤和头发，而另一些会影响肾脏。研究人员怀疑突变可能发生在gene的不同区域，对SIP结构和功能有不同的影响。"}]}' WHERE id = 16778;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ABCB4", "conditions": [{"name": "进行性家族性肝内胆汁淤积", "detail": "ABCB4gene的突变与一种称为进行性家族性肝内胆汁淤积症3型（PFIC3）的严重肝病有关，其特征是存在无功能蛋白，导致磷脂运动受到抑制并与胆汁结合并导致肝衰竭。"}, {"name": "妊娠期肝内胆汁淤积", "detail": "具有ABCB4gene突变的女性患妊娠肝内胆汁淤积症的风险增加，其特征是胆汁分泌受损，妊娠晚期瘙痒，后来消失。怀孕期间蛋白质功能受损，并且认为其减少磷脂的运动和胆汁积聚，导致肝功能受损。"}, {"name": "其他疾病", "detail": "ABCB4gene的突变与一种罕见的疾病有关，称为低磷脂相关性胆石症（LPAC），其特征是存在缺陷蛋白，该蛋白削弱了其将磷脂转移到胆汁中的能力。这导致胆结石，胆固醇结晶或胆汁淤积的形成，引起疼痛，发烧，恶心和胰腺炎的症状。"}]}' WHERE id = 16785;
UPDATE genes SET GHR_metadata_ch = '{"gene": "GATM", "conditions": [{"name": "精氨酸：甘氨酸酰胺基转移酶缺乏症", "detail": "GATMgene中至少有两个突变会导致精氨酸：甘氨酸酰胺基转移酶缺乏症，一种涉及发育迟缓，智力残疾以及某些情况下肌肉无力的疾病。这些突变导致产生异常缩短的精氨酸：甘氨酸脒基转移酶或破坏遗传信息如何拼接在一起以制作产生该酶的蓝图。（换行）GATMgene突变会干扰精氨酸：甘氨酸酰胺基转移酶参与肌酸合成的能力，从而导致肌酸短缺。精氨酸：甘氨酸酰胺基转移酶缺乏症的影响在需要大量能量的器官和组织（尤其是大脑）中最为严重。"}]}' WHERE id = 16797;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ORC6", "conditions": [{"name": "梅尔-戈林综合征", "detail": "已发现ORC6gene中至少有两个突变会引起Meier-Gorlin综合征，这种疾病的特征是身材矮小，膝盖发育不全和耳朵小。一个突变改变ORC6蛋白质中的单个蛋白质构建块（氨基酸），用氨基酸丝氨酸（写为Tyr232Ser）替换蛋白质232位的氨基酸酪氨酸。另一个gene突变导致产生异常短的蛋白质。这些更改会损害复制前复合体的组装，从而破坏复制许可；但是，尚不清楚复制许可证的减少如何导致Meier-Gorlin综合征。研究人员推测，这种减少会延迟细胞分裂过程，从而减缓发育过程中骨骼和其他组织的生长。一些研究表明，ORC6的改变会损害胞质分裂，这也可能延迟细胞分裂。不知道为什么膝盖和耳朵的发育特别延迟。"}]}' WHERE id = 16807;
UPDATE genes SET GHR_metadata_ch = '{"gene": "CTDP1", "conditions": [{"name": "先天性白内障，面部畸形和神经病", "detail": "到目前为止，每个患有CCFDN的人在每个细胞的CTDP1gene的两个拷贝中都具有相同的突变。这种突变被写为IVS6+389C>T，改变了gene指令拼接在一起以产生羧基末端结构域磷酸酶1蛋白的方式。更改后的指令会引入过早的停止信号，从而导致无法调节转录的异常短而无功能的蛋白质。转录过程的缺陷调节影响身体许多部位的发育和功能。尚不清楚非功能性羧基末端结构域磷酸酶1蛋白如何导致CCFDN的特定体征和症状。"}]}' WHERE id = 16837;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SLC6A19", "conditions": [{"name": "Hartnup病", "detail": "已发现SLC6A19gene中至少有23个突变引起Hartnup病。这种情况的特征是尿液中氨基酸水平升高（氨基酸尿症）。有些人会出现皮疹或运动或认知问题。导致Hartnup病的大多数突变都会改变B0AT1蛋白中的单个氨基酸，从而降低其活性。在多个受影响的家族中鉴定出的突变将蛋白质中第173位的氨基酸天冬酰胺替换为氨基酸天冬氨酸（写为Asp173Asn或D173N）。(换行)B0AT1活性降低导致大量中性氨基酸作为废物从体内去除。结果，受影响的个体缺乏（缺乏）某些氨基酸和维生素。大多数受影响的人从均衡饮食中获得所需的营养。然而，由于他们的饮食，疾病，压力或各种其他原因，一些人营养不足，并且可能发展皮疹或运动或精神问题。研究人员认为，这些特征中的许多与维生素B3（烟酸）缺乏有关，其主要成分之一是氨基酸色氨酸。"}]}' WHERE id = 16838;
UPDATE genes SET GHR_metadata_ch = '{"gene": "BAP1", "conditions": [{"name": "BAP1肿瘤易感综合征", "detail": "BAP1gene的突变会导致BAP1肿瘤易感综合征。患有这种疾病的人患多种类型的非癌性（良性）和癌性（恶性）肿瘤的风险增加，特别是某些皮肤肿瘤（非典型Spitz肿瘤，皮肤黑素瘤和基底细胞癌）；眼睛（葡萄膜黑色素瘤）；肾脏（透明细胞肾细胞癌）；和一种称为间皮的组织，排列在胸部，腹部和内脏（恶性间皮瘤）上。研究人员仍在确定其他形式的癌症是否与BAP1肿瘤易感综合征相关。(换行)受影响的个体在BAP1gene的一个拷贝中继承突变。这些基本上存在于人体每个细胞中的突变被归类为种系突变。大多数种系BAP1gene突变会导致异常短的BAP1蛋白，该蛋白可能会过早分解。其他种系突变会改变BAP1蛋白中的单个蛋白结构单元（氨基酸），并可能损害其功能。在大多数情况下，第二个非遗传（体细胞）突变发生在引起肿瘤的细胞中gene的正常拷贝中。种系和体细胞突变共同导致肿瘤细胞中BAP1蛋白功能的丧失。(换行)减少或丧失该蛋白质的功能可能会阻止从某些蛋白质中去除泛素分子。尽管尚不清楚BAP1功能的变化如何导致BAP1肿瘤易感综合征，但研究人员推测，通常由BAP1去泛素化调节的蛋白质活性的改变可能促进细胞增殖或存活，从而导致肿瘤形成。"}, {"name": "胆管癌", "detail": "遗传学家庭参考提供有关胆管癌的信息。"}, {"name": "其他癌症", "detail": "在没有种系BAP1gene突变的情况下，已在葡萄膜黑色素瘤，恶性间皮瘤和透明细胞肾细胞癌肿瘤中发现了体细胞BAP1gene突变。在这些情况下，癌症发生在没有癌症家族史的人群中，被认为是零星的。受影响的个体不会发展出与BAP1肿瘤易感综合征相关的其他肿瘤类型（如上所述）。"}]}' WHERE id = 16873;
UPDATE genes SET GHR_metadata_ch = '{"gene": "DOCK6", "conditions": [{"name": "亚当斯-奥利弗综合症", "detail": "DOCK6gene的突变会导致Adams-Oliver综合征，这种疾病的特征是通常在头皮上的皮肤缺失区域（先天性皮肤发育不全）和手脚畸形。与DOCK6相关的Adams-Oliver综合征相比，与其他gene相关的病例更常见神经系统异常，例如脑或眼畸形和智力障碍。这种情况下涉及的大多数DOCK6gene突变都会导致产生异常短的DOCK6蛋白，该蛋白可能无法发挥作用。其他突变会改变DOCK6蛋白中的单个蛋白结构单元（氨基酸），从而损害该蛋白的正常功能。DOCK6无法打开Cdc42或Rac1会导致其信号传导减少，从而损害某些组织的正常发育，包括头部顶部的皮肤和手脚的骨骼。"}]}' WHERE id = 16880;
UPDATE genes SET GHR_metadata_ch = '{"gene": "FXN", "conditions": [{"name": "弗里德里希共济失调", "detail": "Friedreich共济失调是由于FXNgene中GAA三核苷酸重复序列的拷贝数增加（扩展）所致。在患有这种情况的人中，GAA段异常重复66至1000次以上。GAA三核苷酸重复序列的长度似乎与Friedreich共济失调症状出现的年龄有关。GAA片段重复次数少于300次的人比GAA三核苷酸重复次数较大的人倾向于出现较晚的症状（25岁以后）。(换行)大多数患有Friedreich共济失调的个体在FXNgene的两个拷贝中均具有扩展的GAA三核苷酸重复序列。患有这种疾病的人中约有2%在FXNgene的一个副本中具有扩展的GAA三核苷酸重复序列，而在该gene的另一个副本中具有另一种突变。在大多数情况下，其他突变会改变FXNgene内的单个DNA构建块（核苷酸）。(换行)尚不完全了解FXNgene突变如何引起Friedreich共济失调。该gene的突变破坏了frataxin的产生，大大减少了细胞中该蛋白的量。frataxin的缺乏似乎会降低含有铁硫簇的蛋白质的活性，这可能会损害线粒体中能量的产生。frataxin量不足的细胞对可能破坏和破坏细胞的反应性分子（自由基）也特别敏感。大脑，脊髓和肌肉中受损或能量供应不足的细胞可能无法正常工作，从而导致Friedreich共济失调的体征和症状。"}]}' WHERE id = 16892;
UPDATE genes SET GHR_metadata_ch = '{"gene": "FGF10", "conditions": [{"name": "泪-耳-牙本质-数字综合征", "detail": "已发现FGF10gene中至少有三个突变会导致泪-耳-牙本质-数字（LADD）综合征。这种疾病影响泪腺系统（眼睛中产生和分泌眼泪的系统），耳朵，唾液腺（口腔中产生唾液的腺体），牙齿，手，有时还有其他部分的形成。身体的一部分。LADD综合征的主要特征是异常的泪液产生，耳朵畸形并伴有听力损失，唾液产生减少，牙齿小和手畸形。（换行）导致LADD综合征的FGF10gene突变降低了FGF10蛋白的量和活性。较少的生长因子可用于结合受体，从而减少细胞内的信号传导。细胞信号传导的减少会破坏细胞的成熟和发育，从而导致LADD综合征患者的眼睛和嘴巴中的耳朵，骨骼和腺体异常形成。"}, {"name": "其他疾病", "detail": "还发现FGF10gene的突变引起类似于LADD综合征的病症，称为泪腺和唾液腺发育不全（ALSG）。患有这种情况的个体缺乏（再生障碍性）或小（发育不良）泪腺，其在眼睛中分泌眼泪，以及再生障碍性或发育不良的唾液腺。这种情况通常会导致眼睛烦躁，慢性流泪（溢泪），口干（口干症）以及对蛀牙的敏感性增加。有时，涉及泪液产生的其他结构也会受到影响。人们认为，与引起LADD综合征的FGF10gene突变相比，引起ALSG的突变对细胞信号传导的破坏性较小，尽管在极少数情况下，相同的突变会引起这两种情况。由于ALSG和LADD综合征的特征之间存在重叠，并且由于它们是由同一gene的突变引起的，因此很难辨别它们是不同的疾病还是同一疾病谱的一部分。"}]}' WHERE id = 16914;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SMARCE1", "conditions": [{"name": "棺材-西里斯综合症", "detail": "SMARCE1gene中至少有一个突变会导致Coffin-Siris综合征，其特征是发育迟缓，第五（小指）手指或脚趾异常以及被描述为粗糙的特征性面部特征。该gene突变改变了SMARCE1蛋白中的单个蛋白结构单元（氨基酸）；蛋白质73位的氨基酸酪氨酸被氨基酸半胱氨酸（写为Tyr73Cys）取代。尽管尚不清楚这种变化如何影响SWI/SNF复合物，但研究人员建议该突变导致染色质重塑异常。此过程的干扰会改变许多gene的活性并破坏多个细胞过程，这可以解释科芬－西里斯综合症的各种体征和症状。"}]}' WHERE id = 16956;
UPDATE genes SET GHR_metadata_ch = '{"gene": "TXNL4A", "conditions": [{"name": "烧伤McKeown综合征", "detail": "在烧伤McKeown综合征患者中至少鉴定出10个TXNL4Agene突变，这种疾病从出生（先天性）就存在，涉及鼻腔异常，特征性面部特征，听力下降，心脏异常和身材矮小。大多数烧伤McKeown综合征患者在每个细胞的两个TXNL4Agene拷贝中都有不同的遗传变化；这种情况称为复合杂合性。在该gene的一个拷贝中，TXNL4Agene突变导致功能受损的蛋白质，或者根本不产生蛋白质。TXNL4Agene的另一个副本在TXNL4Agene附近称为启动子区域的区域中缺失了少量遗传物质，该区域控制该gene的蛋白质生产。这种遗传变化减少了蛋白质的产生。(换行)研究表明，由TXNL4Agene产生的蛋白质的量减少影响主要剪接体的组装并改变特定mRNA分子组的产生。这些变化的细节及其与烧伤McKeown综合征的具体体征和症状的关系尚不清楚。然而，已显示剪接体中涉及的几个gene的突变会导致其他conditions异常，从而影响头部和面部（颅面畸形），因此颅面发育被认为对剪接体问题特别敏感。"}]}' WHERE id = 16970;
UPDATE genes SET GHR_metadata_ch = '{"gene": "WISP3", "conditions": [{"name": "进行性假性类风湿性不典型增生", "detail": "WISP3gene的突变会导致进行性假性类风湿发育不良（PPRD），这种情况会导致手，臀部，膝盖和脊柱的关节僵硬和疼痛。关节问题随着时间的推移而恶化，关节的运动受到限制。这种情况下涉及的大多数突变都会导致产生异常短的WISP3蛋白，该蛋白可能是无功能的。其他突变会改变蛋白质中的单个蛋白质结构单元（氨基酸）。WISP3蛋白功能的丧失可能会破坏正常的软骨维持和骨骼生长，从而导致PPRD中的关节问题。"}, {"name": "幼年特发性关节炎", "detail": "遗传学家庭参考提供有关青少年特发性关节炎的信息。"}]}' WHERE id = 16981;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SGO1", "conditions": [{"name": "慢性心房和肠道心律失常", "detail": "在患有慢性心房和肠道心律不齐（CAID）的人中至少鉴定出一种SGO1gene突变，这是一种影响心脏和消化系统正常节律运动的疾病。导致CAID的SGO1gene突变将蛋白质结构单元（氨基酸）替换为赖氨酸，并在蛋白质位置23处的氨基酸谷氨酸替换为Lys23Glu或K23E。研究人员认为，这种突变可能导致内聚蛋白复合物不能将姐妹染色单体结合在一起，从而导致细胞分裂过程中染色体稳定性下降。这种不稳定性被认为会导致肠道肌肉和心脏自然起搏器（窦房结）中细胞的早期衰老（衰老），导致维持心脏和肠道的适当节律运动并导致CAID的体征和症状的问题。(换行)尚不清楚为什么SGO1gene突变会特异性影响CAID中的心脏和肠道。研究人员认为，SGO1gene在某些胚胎组织中的活性（表达）或SGO1蛋白在窦房结和有助于控制肠道运动节律的细胞中的特定功能可能是该疾病的特征。"}]}' WHERE id = 16999;
UPDATE genes SET GHR_metadata_ch = '{"gene": "COL6A1", "conditions": [{"name": "胶原VI相关的肌病", "detail": "已经发现COL6A1gene的突变与胶原VI相关的肌病有关，胶原VI相关的肌病是一组严重程度不同且以肌肉无力和关节畸形为特征的疾病，称为挛缩。这是由于更改或有缺陷或无功能引起的？1（VI）链破坏功能性VI型胶原分子的形成。"}]}' WHERE id = 17022;
UPDATE genes SET GHR_metadata_ch = '{"gene": "PQBP1", "conditions": [{"name": "肾宁综合征", "detail": "在患有Renpenning综合征的人中至少鉴定出14个PQBP1gene突变，该疾病几乎仅在男性中发生，并导致智力残疾和特征性身体特征。导致Renpenning综合征的大多数PQBP1gene突变导致异常短的聚谷氨酰胺结合蛋白1。缩短或以其他方式异常的蛋白质的功能可能会受损，并干扰神经元中的正常gene表达，从而导致大脑异常发育以及Renpenning综合征的体征和症状。"}]}' WHERE id = 17040;
UPDATE genes SET GHR_metadata_ch = '{"gene": "TERT", "conditions": [{"name": "先天性角化不良", "detail": "在先天性角化不全患者中已鉴定出TERTgene中至少18个突变。这种疾病的特征是肤色变化（色素沉着），口腔内白斑（口腔白斑）以及指甲和脚趾甲异常形成（指甲营养不良）。先天性角化病患者发生几种危及生命的疾病的风险增加，包括癌症和称为肺纤维化的进行性肺病。许多受影响的个体也会出现一种称为再生障碍性贫血的严重疾病，也称为骨髓衰竭，这种情况发生在骨髓不能产生足够的新血细胞时。（换行）导致先天性角化不良的大多数TERTgene突变改变hTERT蛋白中的单个蛋白质构建块（氨基酸），导致其不稳定或功能失调。突变干扰端粒酶功能，导致端粒维持受损和端粒长度减少。快速分裂的细胞特别容易受到端粒缩短的影响。结果，先天性角化病患者可能会遇到各种影响体内细胞快速分裂的问题，如甲床细胞，毛囊，皮肤，口腔内壁（口腔粘膜）和骨髓。（换行）端粒维持不足导致的染色体断裂和不稳定可能导致遗传变化，使细胞以不受控制的方式分裂，导致一些先天性角化不良患者发生癌症。"}, {"name": "特发性肺纤维化", "detail": "在进行性肺病特发性肺纤维化患者中，已鉴定出TERTgene至少23个突变。在家族性（家族性肺纤维化）病例中发现了该gene的突变，而在孤立（散发）病例中则较少见。一些由于TERCgene突变而患有特发性肺纤维化的个体的家庭成员具有先天性角化不全的其他特征（如上所述），例如再生障碍性贫血或癌症。TERTgene中的（换行）突变减少或消除端粒酶的功能，这使得端粒在细胞分裂时变得异常短。缩短的端粒可能会触发快速分裂的细胞，例如排列在肺内的细胞，停止分裂或过早死亡。然而，研究人员不确定端粒缩短如何导致特发性肺纤维化特征性的进行性瘢痕形成和肺损伤。(换行)特发性肺纤维化是一种复杂的疾病，可能是由遗传和环境因素共同引起的。研究表明，许多受影响的TERTgene突变患者也可能暴露于环境风险因素，如香烟烟雾或某些灰尘或烟雾。TERTgene的突变可能会增加一个人患特发性肺纤维化的风险，然后暴露于某些环境因素会触发该疾病。"}, {"name": "乳腺癌", "detail": "遗传学家庭参考提供有关乳腺癌的信息。"}, {"name": "胆管癌", "detail": "遗传学家庭参考提供有关胆管癌的信息。"}, {"name": "癌症", "detail": "TERTgene的突变与各种癌症的风险增加有关，特别是一种称为黑素瘤的皮肤癌和一种称为急性髓性白血病的血癌。研究人员建议，这些突变可能会损害端粒的维持并导致DNA损伤。对有助于控制细胞生长和发育的gene的损害可导致不受控制的细胞生长并导致这些癌症的发展。"}, {"name": "其他疾病", "detail": "在孤立性再生障碍性贫血患者中也发现了TERTgene突变，这是一种骨髓衰竭形式，没有先天性角化不良的其他身体特征。研究人员建议，影响端粒酶不同部分的突变可能是这些特征缺失的原因。一些人认为，由TERTgene突变引起的孤立的再生障碍性贫血实际上可能代表先天性角化不全的晚期发作形式，其中诸如指甲营养不良之类的身体特征较轻，可能并不明显。"}]}' WHERE id = 17042;
UPDATE genes SET GHR_metadata_ch = '{"gene": "PML", "conditions": [{"name": "急性早幼粒细胞白血病", "detail": "gene突变可以在一个人的一生中获得，并且仅存在于某些细胞中。这些突变称为体细胞突变，它们不是遗传的。涉及PMLgene的体细胞突变引起急性早幼粒细胞白血病，即血液形成组织（骨髓）的癌症。急性早幼粒细胞白血病的特征在于骨髓中未成熟白细胞（称为早幼粒细胞）的积累。15号和17号染色​​体之间遗传物质的重排（易位），写为t（15；17），将15号染色体上的PMLgene的一部分与17号染色​​体上另一个称为RARA的gene的一部分融合在一起。由这种融合genePML-RAR产生的蛋白质？，功能不同于正常PML和RARAgene的蛋白质产物。(换行)PML-RAR？蛋白质不定位于PML-NB，并且结构不能正确形成。PML-RAR？蛋白质不能阻断细胞增殖或诱导细胞凋亡。(换行)另外，RAR的功能？RARAgene的产物蛋白质被破坏。通常，该蛋白质参与gene转录的调节，这是蛋白质生产的第一步。具体而言，该蛋白质有助于控制在早幼粒细胞阶段以外的白细胞成熟（分化）中重要的某些gene的转录。但是，PML-RAR？蛋白质阻断（抑制）gene转录。(换行)PML-RAR？蛋白质允许异常细胞增殖并在早幼粒细胞阶段阻断白细胞的分化。结果，过量的早幼粒细胞在骨髓中积累并且不能形成正常的白细胞，导致急性早幼粒细胞白血病。"}]}' WHERE id = 17053;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ACADS", "conditions": [{"name": "短链酰基辅酶A脱氢酶缺乏症", "detail": "已经发现ACAD中的几种突变由于无功能的蛋白质而导致短链酰基辅酶A脱氢酶（SCAD）缺乏，这阻止了酶活性代谢短链脂肪酸。临床特征包括缺乏能量（嗜睡），低血糖（低血糖），肌张力差（肌张力低下）和虚弱。"}]}' WHERE id = 17113;
UPDATE genes SET GHR_metadata_ch = '{"gene": "HAMP", "conditions": [{"name": "遗传性血色素沉着症", "detail": "HAMPgene中至少有8个突变可引起遗传性血色素沉着病。HAMPgene突变的人会受到严重类型的青少年血色素沉着病（有时称为2型血色素沉着病）的影响，这种病始于10岁至30岁。HAMPgene突变的人即使身体有足够的铁供应，也无法制造正常的铁调素，也无法抑制铁的吸收。受影响的人的器官变得铁超载，特别是肝脏和心脏，导致这种疾病的器官损伤特征。"}]}' WHERE id = 17121;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SCN4A", "conditions": [{"name": "高钾性周期性麻痹", "detail": "已发现SCN4Agene中有10多个突变会引起高钾性周期性麻痹，这种情况会导致极端的肌肉无力发作，通常与血液中的高钾（高钾血症）有关。这些突变改变了SCN4A蛋白中的单个结构单元（氨基酸），这会改变骨骼肌细胞中钠通道的结构和功能。这些变化延迟了SCN4A蛋白通道的关闭或阻止通道保持关闭。结果，钠离子异常地继续流入肌细胞。钠离子的这种增加触发了肌肉细胞中钾的释放，这导致更多的钠通道打开并刺激更多的钠离子流入这些细胞。离子转运的这些变化降低了骨骼肌收缩的能力，导致肌肉无力或麻痹发作。"}, {"name": "低钾性周期性麻痹", "detail": "在低钾性周期性麻痹患者中，已经鉴定出SCN4Agene中至少有7个突变，这种情况会导致与血液中钾含量低（低钾血症）相关的极端肌肉无力发作。SCN4Agene的突变约占这种情况所有病例的10%。每个已知的突变都会改变SCN4A蛋白中的单个氨基酸，从而改变骨骼肌细胞中钠通道的结构和功能。异常通道改变了钠离子的正常流动，从而阻止了肌肉正常收缩。低钾水平也导致了这个问题。由于运动需要肌肉收缩，因此离子传输中的这些变化会导致严重肌肉无力的长期发作。"}, {"name": "先天性副肌强直", "detail": "已知SCN4Agene中至少有18个突变会引起先天性副肌强直，这是一种肌肉疾病，其特征是持续的肌肉紧张（肌强直）发作会阻止肌肉正常放松。导致这种情况的SCN4Agene突变各自改变SCN4A蛋白中的单个氨基酸，从而改变骨骼肌细胞中钠通道的结构和功能。最常见的遗传变化是将氨基酸精氨酸替换为蛋白质1448位的其他几个氨基酸之一。(换行)突变延迟了SCN4A蛋白通道的关闭，一旦通道关闭，就会导致它们再次打开太快。这些变化增加了钠离子进入骨骼肌细胞的流量。额外钠离子的涌入会触发长时间的肌肉收缩，这是先天性副肌强直特征性肌强直发作的基础。持续高水平钠离子的肌肉可能根本无法收缩，从而导致肌肉无力发作。(换行)低温可能会增加SCN4Agene突变对改变的离子通道的影响，这可能有助于解释为什么暴露于寒冷会触发体征和症状。"}, {"name": "钾加重肌强直", "detail": "SCN4Agene中的几个突变会导致钾加重的肌强直，这种情况会导致肌强直发作，从而阻止肌肉正常放松。吃富含钾的食物可能会加重肌肉僵硬。与钾加重性肌强直相关的最常见遗传变化是在蛋白质位置1306处用其他几个氨基酸之一取代氨基酸甘氨酸。这些突变延迟了SCN4A蛋白通道的关闭，从而增加了钠离子进入骨骼肌细胞的流量。过量钾的存在刺激更多钠离子流入这些细胞。离子转运的这些变化触发了长时间的肌肉收缩，这是钾加重性肌强直的肌肉僵硬特征的基础。"}, {"name": "先天性肌无力综合征", "detail": "遗传学家庭参考资料提供有关先天性肌无力综合症的信息。"}, {"name": "其他疾病", "detail": "SCN4Agene的突变也与先天性肌无力综合症的一种形式有关，先天性肌无力综合症是出生后不久出现的肌肉疾病。患有这种疾病的人会出现全身肌肉无力和麻痹反复发作，特别影响用于说话和呼吸的肌肉。与这种情况相关的SCN4Agene突变将氨基酸缬氨酸替换为蛋白质1442位的氨基酸谷氨酸（写为Val1442Glu或V1442E）。这种遗传变化改变了骨骼肌细胞中钠通道的结构和功能。反复的肌肉收缩后，通道异常关闭，从而减少了钠离子流入肌肉细胞的流量。离子转运的这种破坏减少了进一步的肌肉收缩，导致肌肉无力和麻痹发作。"}]}' WHERE id = 17157;
UPDATE genes SET GHR_metadata_ch = '{"gene": "COL6A2", "conditions": [{"name": "胶原VI相关的肌病", "detail": "已经发现COL6A2gene的突变与胶原VI相关的肌病有关，胶原VI相关的肌病是一组严重程度不同且以肌肉无力和关节畸形为特征的疾病，称为挛缩。这是由于更改或有缺陷或无功能引起的？2（VI）链破坏功能性VI型胶原分子的形成。"}]}' WHERE id = 17164;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SH3BP2", "conditions": [{"name": "小天使", "detail": "在患有小天使的人中已经鉴定出SH3BP2gene中的至少11个突变。这些突变中的每一个都会改变SH3BP2蛋白关键区域中的单个蛋白结构单元（氨基酸）。这些遗传变化导致该蛋白质的过度活跃形式的产生。SH3BP2突变的影响仍在研究中，但研究人员认为，异常蛋白会改变与维持骨组织相关的细胞和某些免疫系统细胞中的关键信号通路。过度活跃的蛋白质可能会导致颌骨骨骼发炎，并触发破骨细胞数量的增加。过量的这些骨进食细胞导致上颌和下颌骨组织的异常破坏。骨质流失和炎症的结合可能是小天使特征性囊肿样生长的基础。"}]}' WHERE id = 17184;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SLC7A7", "conditions": [{"name": "赖氨酸尿酸蛋白不耐受", "detail": "已发现SLC7A7gene中的40多个突变会导致赖氨酸尿酸蛋白不耐受。所有这些突变都会损害y+LAT-1蛋白转运氨基酸的能力。芬兰血统的赖氨酸尿酸蛋白不耐受的人通常具有相同的突变。这种突变（写为IVS6-2A>T）破坏了gene指令用于制造y+LAT-1蛋白的方式，导致该蛋白在细胞中错位。（换行）y+LAT-1蛋白中的突变破坏氨基酸的运输，导致体内赖氨酸，精氨酸和鸟氨酸的缺乏以及尿液中异常大量的这些氨基酸。这些氨基酸在人体各种组织中的异常运输和短缺会导致赖氨酸尿酸蛋白不耐受的体征和症状。"}]}' WHERE id = 17191;
UPDATE genes SET GHR_metadata_ch = '{"gene": "BDNF", "conditions": [{"name": "阿片成瘾", "detail": "BCS1Lgene中的一些突变与阿片类药物成瘾有关，阿片类药物成瘾是一种引起重大健康，社会和经济问题的疾病，其特征是即使不再需要医学上的阿片类药物，也具有强烈的强迫性使用阿片类药物的冲动。长期使用阿片类药物会产生依赖性，如果停止使用，个体通常会出现身体和心理症状，如肌肉痉挛，腹泻和焦虑。"}, {"name": "WAGR综合征", "detail": "携带部分或全部BDNGgene的11号染色体区域的缺失与WAGRO综合征相关，WAGRO综合征是一种以儿童肾癌为特征的疾病，称为Wilms肿瘤，称为anirida的眼部问题，泌尿生殖系统异常，智力残疾，体重增加开始在童年时期，肥胖和自闭症谱系障碍的风险增加。"}, {"name": "其他疾病", "detail": "BDNGgene中的一些突变与发展为精神疾病（例如双相情感障碍，焦虑症和进食障碍）的风险增加有关，这些疾病是由于蛋白质中单个氨基酸的变化改变了其功能而引起的。"}]}' WHERE id = 17203;
UPDATE genes SET GHR_metadata_ch = '{"gene": "DCXR", "conditions": [{"name": "必需的戊糖", "detail": "DCXRgene中至少有两个突变会导致一种称为必需戊尿症的疾病，这种疾病几乎仅在具有Ashkenazi犹太血统的个体中发现。受影响的人尿液中的L-木酮糖含量很高，但没有相关的健康问题。这种情况下涉及的gene突变导致产生改变的DCXR蛋白，这些蛋白会迅速分解。没有这种蛋白质，L-木酮糖不能有效地转化为木糖醇，过量的糖会释放到尿液中。这些突变仅影响L-木酮糖还原酶的主要同工型，但次要同工型无法补偿DCXR分解L-木酮糖的功能丧失。据认为，其他过程能够分解有毒的α-二羰基化合物，这可能是原发性戊糖尿患者缺乏症状的原因。(换行)尽管研究表明，附着于精子细胞的DCXR蛋白缺乏可能与无法生育孩子（不育）有关，但具有基本戊糖尿的男性似乎具有正常的生殖功能。"}]}' WHERE id = 17206;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ALAS2", "conditions": [{"name": "卟啉症", "detail": "在患有一种称为X连锁显性红细胞生成性原卟啉症的卟啉症患者中发现了ALAS2gene的几个突变，这种疾病的特征是贫血，脾肿大和由于血液中过量产生和积累卟啉化合物而引起的皮肤对阳光过敏。"}, {"name": "X连锁铁粒幼细胞性贫血", "detail": "发现ALAS2gene中的几个突变会引起X连锁铁粒幼细胞性贫血，这种疾病的特征是红系ALA合酶缺陷，损害血红素合成并导致细胞中过量铁的积累，从而损害人体器官。"}]}' WHERE id = 17232;
UPDATE genes SET GHR_metadata_ch = '{"gene": "GLA", "conditions": [{"name": "法布里病", "detail": "在法布里病患者中已鉴定出GLAgene的370多个突变。这些遗传变化大多数是单个家庭独有的。最常见的突变类型会改变α-半乳糖苷酶a中的单个蛋白质结构单元（氨基酸）。其他突变会删除GLAgene的一部分，将额外的遗传物质插入gene中，或在制作α-半乳糖苷酶a的gene说明中插入过早的终止信号。GLAgene的改变会产生异常形式的酶，该酶无法有效分解globotriaosylceramide。结果，这种物质积聚在身体的细胞中，特别是皮肤血管内的细胞和肾脏，心脏和神经系统中的细胞。globotriaosylceramide的逐步积累损害这些细胞，导致法布里病的各种体征和症状。(换行)消除α-半乳糖苷酶A酶活性的突变会导致严重的经典形式的法布里病，通常始于儿童期。减少但不能完全消除酶活性的突变通常会导致轻度，迟发性疾病。"}]}' WHERE id = 17240;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ACTA1", "conditions": [{"name": "肌动蛋白积聚性肌病", "detail": "ACTA1gene的突变与肌动蛋白积聚性肌病有关，肌病是一种以功能失调的蛋白质为特征的疾病，该蛋白质损害肌动蛋白ATP的结合，导致肌肉无力和肌肉收缩受损。"}, {"name": "帽肌病", "detail": "ACTA1gene的突变与帽肌病有关，帽肌病是由异常蛋白质引起的疾病，导致细丝紊乱，导致肌肉细胞中的帽状结构。该临床特征包括肌肉收缩受损，肌肉无力。"}, {"name": "纤维类型不均的先天性肌病", "detail": "ACTA1gene的突变与先天性纤维类型失调有关，先天性纤维类型失调是一种以全身肌肉无力为特征的疾病，不会随着时间的推移而恶化，并且是由于蛋白质缺陷导致的，从而损害了肌小节功能。"}, {"name": "核内棒肌病", "detail": "ACTA1gene中的几个突变与核内棒状肌病有关，这种情况是由于肌动蛋白在肌肉细胞核中呈棒状积累并降低了肌纤维中肌动蛋白的利用率而引起的。这导致肌肉收缩受损，导致肌肉无力。"}, {"name": "Nemaline myopathy", "detail": "ACAT1gene的突变与一种称为合并性肾上腺肌病的肌肉疾病有关，这种疾病的特征是肌肉收缩受损，无力并形成蛋白质团块（聚集体），干扰肌肉细胞的正常功能。"}]}' WHERE id = 17241;
UPDATE genes SET GHR_metadata_ch = '{"gene": "PHKB", "conditions": [{"name": "IX型糖原贮积病", "detail": "已知PHKBgene中至少有21个突变会导致某种形式的糖原贮积病IX型（GSD IX），称为GSD IXb。这种形式的疾病影响肝脏和肌肉。由这种疾病引起的肝脏问题包括肝脏肿大（肝肿大），生长缓慢和低血糖期（低血糖）。这些功能通常会随着时间而改善。尽管一些受影响的人没有肌肉问题，但这种情况也可能导致轻度的肌肉无力。PHKBgene中的（换行）突变降低了肝脏和肌肉细胞中磷酸化酶b激酶的活性，尽管其机制尚不清楚。这种酶功能的降低会损害糖原分解。结果，糖原在细胞中积累，并且葡萄糖不可用于能量。无法分解肝脏中的糖原并减少肌肉细胞中的能量产生，导致了GSD-IXb的特征。"}]}' WHERE id = 17275;
UPDATE genes SET GHR_metadata_ch = '{"gene": "MSH2", "conditions": [{"name": "林奇综合症", "detail": "在所有已发现gene突变的Lynch综合征病例中，约有40%与MSH2gene的遗传突变有关。林奇综合症增加了许多类型癌症的风险，尤其是结肠癌（大肠）和直肠癌，这些癌症统称为大肠癌。Lynch综合征患者的子宫内膜癌（子宫内膜癌），卵巢癌，胃癌，小肠癌，肝癌，胆囊癌，上尿路癌和脑癌的风险也增加。（换行）涉及Lynch综合征的MSH2gene突变可能导致产生异常短或无活性的MSH2蛋白，其不能执行其正常功能。当MSH2蛋白不存在或无功能时，在细胞分裂过程中未修复的DNA错误数量将大大增加。随着细胞继续分裂，错误累积，这可能导致细胞功能异常，增加结肠或身体其他部位形成肿瘤的风险。（换行）MSH2gene中的一些突变导致称为Muir-Torre综合征的Lynch综合征的变体。除结肠直肠癌外，患有这种疾病的人患几种罕见皮肤肿瘤的风险增加。这些罕见的皮肤肿瘤包括皮脂腺瘤和癌，它们发生在产生称为皮脂（皮脂腺）的油性物质的腺体中。通常在暴露于阳光的皮肤区域也可能发生称为角膜棘皮瘤的多个快速生长的肿瘤。"}, {"name": "卵巢癌", "detail": "MSH2gene的遗传变化会增加患卵巢癌以及其他类型癌症的风险，这是Lynch综合征的一部分（如上所述）。患有Lynch综合征的女性一生中患卵巢癌的机会为8%至10%，而普通人群中为1.6%。"}]}' WHERE id = 17288;
UPDATE genes SET GHR_metadata_ch = '{"gene": "FTCD", "conditions": [{"name": "谷氨酸甲酰氨基转移酶缺乏症", "detail": "已经鉴定出至少三个导致谷氨酸甲酰氨基转移酶缺乏的FTCDgene突变。这些突变中的两个改变了甲酰氨基转移酶环脱氨酶序列中的氨基酸。这些突变降低了该酶的两种活性之一，即甲酰氨基转移酶。另一个突变导致产生甲酰氨基转移酶环脱氨酶，该酶被缩短，从而缺少参与环脱氨酶反应的酶部分。这些反应的破坏导致受影响个体的尿液中称为甲酰氨基谷氨酸（FIGLU）的分子的排泄，并且在一些严重的谷氨酸甲酰氨基转移酶缺乏症的情况下，血液中某些称为叶酸的B族维生素的积累。尚不清楚这些突变如何导致与谷氨酸甲酰氨基转移酶缺乏症相关的特定健康问题。"}]}' WHERE id = 17289;
UPDATE genes SET GHR_metadata_ch = '{"gene": "FA2H", "conditions": [{"name": "脂肪酸羟化酶相关的神经变性", "detail": "在患有脂肪酸羟化酶相关神经变性（FAHN）的人群中，FA2Hgene至少鉴定出9个突变。FAHN是一种神经系统进行性疾病，其特征是运动和视力问题始于儿童期或青春期，并随着年龄的增长而恶化。受影响个体的脑部扫描显示，铁在大脑中异常蓄积，尤其是在参与运动的区域。（换行）导致FAHN的FA2Hgene突变会降低或消除脂肪酸2-羟化酶的功能。该酶功能的降低可能导致髓鞘异常，易于恶化（脱髓鞘），导致白质损失（脑白质营养不良）。脑白质营养不良可能与FAHN中发生的运动问题和其他神经系统异常有关。尽管尚不清楚FA2Hgene突变如何导致铁的积累，但大脑中的铁积累也可能参与其中。(换行)FA2Hgene突变和FAHN中出现的一些运动问题的人曾经被归类为患有痉挛性截瘫35的单独疾病。据说该gene突变导致智力下降和视神经萎缩的人患有一种称为FA2H相关性白细胞营养不良的疾病。但是，现在通常将这些conditions视为FAHN的形式。"}]}' WHERE id = 17299;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ETV6", "conditions": [{"name": "PDGFRB相关的慢性嗜酸性粒细胞白血病", "detail": "PDGFRB相关的慢性嗜酸性粒细胞白血病是一种造血细胞癌症，可由称为易位的遗传重排引起，该易位将ETV6gene的一部分和另一个称为PDGFRB的gene的一部分聚集在一起，产生ETV6-PDGFRB融合gene。导致ETV6-PDGFRB融合gene的易位是体细胞突变，其在人的一生中获得并且最初发生在单个细胞中。该细胞继续生长和分裂，产生一组具有相同突变的细胞（克隆群体）。（换行）由ETV6-PDGFRB融合gene产生的蛋白质，称为ETV6/PDGFR？，功能不同于通常由单个gene产生的蛋白质。与正常的PDGFR不同？蛋白，融合蛋白总是有活性的，这意味着某些细胞信号通路不断开启。融合蛋白不能抑制正常ETV6蛋白调控的gene活性，因此gene活性增加。过度活跃的信号通路和异常的gene活性增加了细胞的增殖和存活。当ETV6-PDGFRB融合gene突变发生在发育成血细胞的细胞中时，称为嗜酸性粒细胞（偶尔还有其他白细胞，如嗜中性粒细胞和肥大细胞）的白细胞生长受到很好的控制，导致PDGFRB相关慢性嗜酸性粒细胞白血病。目前还不清楚为什么嗜酸性粒细胞优先受到这种遗传变化的影响。"}, {"name": "其他癌症", "detail": "已经发现涉及ETV6gene和超过30种其他gene的易位引起不同类型的白血病，包括急性髓性白血病（AML）和急性淋巴细胞白血病（ALL），以及称为骨髓增生异常综合征（MDS）的骨髓疾病。取决于与ETV6融合的gene，许多机制可导致这些病症，例如gene活性调节受损，信号传导异常或正常gene功能丧失。导致这些疾病的ETV6gene易位是体细胞突变，不是遗传的。"}]}' WHERE id = 17302;
UPDATE genes SET GHR_metadata_ch = '{"gene": "NBEAL2", "conditions": [{"name": "灰色血小板综合征", "detail": "已发现NBEAL2gene中至少有35个突变会引起灰色血小板综合征，这是一种与异常出血相关的疾病。大多数患有灰色血小板综合征的人也会患上一种称为骨髓纤维化的疾病，其特征是骨髓中的瘢痕组织（纤维化）积聚，阻止其产生足够的正常血细胞。（换行）NBEAL2gene中的突变破坏了巨核细胞中α颗粒的正常产生。没有α颗粒，血小板异常大，数量比平时少（大血小板减少症）。在显微镜下观察时，异常血小板也呈灰色，这使这种情况得名。缺乏α颗粒会损害血小板对损伤的反应，增加异常出血的风险。认为发生骨髓纤维化是因为通常包装成α颗粒的生长因子和其他蛋白质泄漏到骨髓中。蛋白质导致纤维化，影响骨髓制造新血细胞的能力。"}]}' WHERE id = 17308;
UPDATE genes SET GHR_metadata_ch = '{"gene": "EPOR", "conditions": [{"name": "家族性红细胞增多症", "detail": "已发现EPORgene中至少有16个突变会引起家族性红细胞增多症，这是一种遗传性疾病，其特征是红细胞数量增加和血栓异常风险增加。当家族性红细胞增多症由EPORgene突变引起时，通常称为ECYT1。（换行）EPORgene中大多数已鉴定的突变导致异常短版本的促红细胞生成素受体的产生。一些突变会改变受体中的单个蛋白质结构单元（氨基酸）。所有这些突变改变了受体的结构，导致其与促红细胞生成素结合后异常长时间保持活化。即使不再需要，过度活跃的受体也会发出红细胞的信号，导致血流中这些细胞过多。"}]}' WHERE id = 17345;
UPDATE genes SET GHR_metadata_ch = '{"gene": "IDH1", "conditions": [{"name": "Maffucci综合征", "detail": "IDH1gene的突变可引起Maffucci综合征，这是一种主要影响骨骼和皮肤的疾病。它的特征是多发性内生软骨瘤，是在骨骼中发展的软骨的非癌性（良性）生长，以及由异常血管缠结（血管瘤）组成的皮肤中的红色或紫色生长。（换行）导致Maffucci综合征的IDH1gene突变是体细胞的，这意味着它们发生在一个人的一生中并且不是遗传的。体细胞突变发生在单个细胞中。随着该细胞继续生长和分裂，源自其的细胞也具有相同的突变。在Maffucci综合征中，突变被认为发生在出生前早期发育的细胞中；由异常细胞产生的细胞具有突变，而身体的其他细胞则没有。这种情况称为马赛克。（换行）在Maffucci综合征患者的一些软骨瘤和血管瘤细胞以及少数受影响个体的骨髓或血液中发现了IDH1gene突变。这些突变阻止异柠檬酸脱氢酶1进行其通常的活性，即异柠檬酸转化为2-酮戊二酸。相反，改变后的酶具有新的异常功能：产生称为D-2-羟基戊二酸的化合物。由于遗传变化导致酶具有新功能，因此将其分类为“功能获得”突变。突变与疾病的体征和症状之间的关系尚不清楚。"}, {"name": "奥利尔病", "detail": "IDH1gene的突变也会引起Ollier病，这是一种类似于Maffucci综合征（如上所述）的疾病，但没有血管异常。（换行）与Maffucci综合征一样，引起Ollier病的IDH1gene突变是体细胞功能获得性突变，被认为发生在发育早期，导致镶嵌。在大多数患有Ollier病的人的内生软骨瘤细胞中发现了IDH1gene突变，但是突变与疾病的体征和症状之间的关系尚不清楚。"}, {"name": "胆管癌", "detail": "遗传学家庭参考提供有关胆管癌的信息。"}, {"name": "细胞遗传学正常的急性髓性白血病", "detail": "在一些患有血液癌症的人中已经鉴定出IDH1gene的突变，这种血液癌症被称为细胞遗传学正常的急性髓性白血病（CN-AML）。虽然大的染色体异常可能参与急性髓性白血病的发展，但大约一半的病例没有这些异常；这些被归类为CN-AML。IDH1gene突变发生在约16%的CN-AML患者中。（换行）涉及CN-AML的IDH1gene突变是体细胞突变，仅在癌变的细胞中发现。它们改变异柠檬酸脱氢酶1酶中的单个蛋白质构建块（氨基酸），用另一个氨基酸替换位置132处的氨基酸精氨酸。与Maffucci综合征和Ollier病（如上所述）一样，在CN-AML中发现的IDH1gene突变是功能获得性突变，可导致D-2-羟基戊二酸的产生。研究表明，D-2-羟基戊二酸的增加可能会干扰决定未成熟细胞最终将成为的细胞类型的过程（细胞命运决定）。具有体细胞IDH1gene突变的未成熟血细胞不是成为正常的成熟细胞，而是不受控制地分裂，从而导致CN-AML。尚不清楚为什么体细胞IDH1gene突变会导致这些各种疾病。"}, {"name": "原发性骨髓纤维化", "detail": "遗传学家庭参考提供有关原发性骨髓纤维化的信息。"}, {"name": "其他癌症", "detail": "IDH1gene的体细胞突变与其他几种形式的癌症有关，包括称为神经胶质瘤的脑肿瘤和称为软骨肉瘤的骨肿瘤。像引起CN-AML的遗传变化（如上所述）一样，在这些癌症中发现的IDH1gene突变是功能获得性突变。这些突变改变异柠檬酸脱氢酶1的功能，使其异常产生D-2-羟基戊二酸。与CN-AML一样，D-2-羟基戊二酸可能会阻止细胞成熟，导致未成熟细胞过度生产和肿瘤形成。目前尚不清楚为什么仅在这几种类型的癌症中发现IDH1gene突变。"}]}' WHERE id = 17347;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ACADM", "conditions": [{"name": "中链酰基辅酶A脱氢酶缺乏症", "detail": "已发现ACADM中的几个突变会导致中链酰基辅酶A脱氢酶（MCAD）缺乏。蛋白质不足会严重降低或消除酶的活性，并损害中链脂肪酸代谢。临床特征包括缺乏能量（嗜睡），低血糖（低血糖）并导致肝脏，脑组织损伤。"}]}' WHERE id = 17364;
UPDATE genes SET GHR_metadata_ch = '{"gene": "MMP14", "conditions": [{"name": "温彻斯特综合症", "detail": "已经发现MMP14gene中的至少一种突变引起温彻斯特综合征，温彻斯特综合征是一种罕见的遗传性骨病，其特征在于骨组织损失（骨溶解），特别是在手和脚中，以及关节和皮肤异常。该突变改变基质金属肽酶14中的单个蛋白质构建块（氨基酸）。具体而言，它用位置17处的氨基酸精氨酸取代了氨基酸苏氨酸（写为Thr17Arg或T17R）。（换行）鉴定的突变改变基质金属肽酶14，使得较少的酶能够到达细胞表面。结果，没有足够的酶来分解细胞外基质的组分并激活基质金属肽酶2。目前尚不清楚这种酶的缺乏如何导致温彻斯特综合症的体征和症状。基质金属肽酶2活化的丧失可能以某种方式破坏新骨产生的平衡和骨重建期间现有骨的破坏，导致骨组织的逐渐丧失。基质金属肽酶14的减少如何导致温彻斯特综合征的其他特征尚不清楚。"}]}' WHERE id = 17381;
UPDATE genes SET GHR_metadata_ch = '{"gene": "CCM2", "conditions": [{"name": "脑海绵状血管畸形", "detail": "在脑海绵状畸形家族中，已经鉴定出CCM2gene的数十种突变，这些家族是大脑中较弱且易于泄漏的血管集合。大多数这些突变导致异常短或畸形的malcavernin蛋白不起作用。这种蛋白质的缺乏可能会损害复合物的功能。结果，RhoA GTP酶信号异常开启，减弱细胞连接并增加血管壁的通透性。在一些脑海绵状血管畸形患者中，大脑渗漏增加可能导致健康问题，如头痛，癫痫发作和大脑出血（脑出血）。CCM2gene的（换行）突变约占所有家族性脑海绵状畸形病例的15%。"}]}' WHERE id = 17387;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ADSL", "conditions": [{"name": "腺苷琥珀酸裂解酶缺乏症", "detail": "已发现ADSLgene中的50多个突变会导致腺苷酸琥珀酸裂合酶缺乏症。这种情况会导致大脑功能障碍（脑病），从而导致精神和运动技能的发展延迟（精神运动延迟），影响沟通和社交互动的自闭症行为以及癫痫发作。这种情况下涉及的大多数突变都会改变腺苷酸琥珀酸裂合酶中的单个蛋白质结构单元（氨基酸）。改变后的酶具有正常功能的2%至20%，研究表明它们形成稳定的嘌呤体的能力较弱。（换行）腺苷酸琥珀酸裂解酶功能的降低，可能是由于嘌呤体的缺乏，导致SAICAR和SAMP的积累。这些物质通过不同的反应转化为琥珀酰氨基咪唑羧酰胺核糖苷（SAICAr）和琥珀酰腺苷（S-Ado）。检测体液中的这些物质可以帮助诊断腺苷酸琥珀酸裂合酶缺乏症。研究人员认为SAICAr和S-Ado有毒；由这些物质中的一种或两种引起的对脑组织的损害可能是腺苷酸琥珀酸裂合酶缺乏症中发生的神经系统问题的基础。(换行)研究表明，SAICAr相对于S-Ado的量反映了腺苷酸琥珀酸裂合酶缺乏症的严重程度。SAICAr比S-Ado多的人患有更严重的脑病和精神运动延迟。"}]}' WHERE id = 17399;
UPDATE genes SET GHR_metadata_ch = '{"gene": "RAB23", "conditions": [{"name": "木匠综合症", "detail": "已经发现RAB23gene中的十几个突变会引起Carpenter综合征，Carpenter综合征的特征是颅骨形成不规则，手指和脚趾异常以及许多其他特征。在北欧血统的Carpenter综合征患者中经常见到的一种突变是在蛋白质位置145（写为Leu145Term或L145X）处用过早的终止信号替换了蛋白质结构单元（氨基酸）亮氨酸。这种突变导致异常短而不稳定的蛋白质迅速分解。导致Carpenter综合征的其他RAB23gene突变会降低或消除RAB23蛋白的功能。目前尚不清楚这些突变如何导致Carpenter综合征的特定特征；但是，参与刺猬信号通路的蛋白质运输受损可能会导致这种疾病的发展。"}]}' WHERE id = 17438;
UPDATE genes SET GHR_metadata_ch = '{"gene": "AGT", "conditions": [{"name": "肾小管发育不全", "detail": "已经发现AGTgene中的几个突变引起称为肾小管发育不全的严重肾脏疾病，所述肾小管发育不全是一种以出生前肾脏发育异常，无尿，严重低血压（低血压）和胎儿发育期间身体没有足够氧气为特征的病症。这些问题是由于羊水减少（羊水过少）导致一系列先天缺陷（称为波特序列）引起的。"}, {"name": "其他疾病", "detail": "AGTgene的几种变异与一种称为原发性高血压的高血压（高血压）的易感性有关，原发性高血压是一种与许多遗传和环境因素相关的复杂疾病，可能导致更高水平的血管紧张素原蛋白。"}]}' WHERE id = 17439;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SIL1", "conditions": [{"name": "马里内斯科-干燥综合征", "detail": "已经发现SIL1gene中的十几个突变引起Marinesco-Sjögren综合征。这些突变中的大多数导致产生几乎没有活性的蛋白质。有缺陷的SIL1蛋白无法从BiP中去除ADP。然后BiP无法与ATP结合并重新开始蛋白质折叠过程。蛋白质折叠的破坏会损害蛋白质的运输，并导致蛋白质在内质网中积累。这种积累可能损害和破坏许多不同组织中的细胞，导致协调性差，肌肉无力和Marinesco-Sjögren综合征的其他特征。"}]}' WHERE id = 17457;
UPDATE genes SET GHR_metadata_ch = '{"gene": "COL17A1", "conditions": [{"name": "交界性大疱性表皮松解症", "detail": "已经在患有交界性大疱性表皮松解症（JEB）的人中鉴定出COL17A2gene的突变，该疾病的特征是由于摩擦或轻微创伤而形成水疱。这是由于COL17A2中的突变导致过早的pro-？1（XVII）链导致皮肤中的imapired XVII型胶原蛋白功能。该gene的突变与称为非Herlitz JEB的较温和形式的JEB相关，其特征在于仅限于手，膝盖和肘部的水泡。在婴儿中观察到另一种更严重的疾病形式，称为Herlitz-JEB，导致广泛的起泡，导致危及生命的并发症。"}]}' WHERE id = 17464;
UPDATE genes SET GHR_metadata_ch = '{"gene": "TPM3", "conditions": [{"name": "帽肌病", "detail": "在帽肌病患者中至少鉴定出两个TPM3gene突变。这些突变将蛋白质结构单元（氨基酸）精氨酸替换为蛋白质序列第168位的半胱氨酸或组氨酸氨基酸，记为Arg168Cys或Arg168His（也称为R168C或R168H）。这些TPM3gene突变的具体作用尚不清楚，但研究人员建议它们可能会干扰正常的肌动蛋白-肌球蛋白结合，损害肌肉收缩并导致帽肌病中发生的肌肉无力。"}, {"name": "先天性纤维类型不均衡", "detail": "已发现TPM3gene中至少有10个突变会导致先天性纤维类型失衡，这种疾病会导致全身肌肉无力，通常不会随着时间的推移而恶化。TPM3gene突变似乎是这种疾病的最常见原因。这些突变改变了慢肌中的单个氨基酸-原肌球蛋白，被认为会损害蛋白质与I型骨骼肌纤维内的肌球蛋白和肌动蛋白相互作用的能力，从而破坏肌肉收缩。先天性纤维类型不均衡的人，肌肉收缩效率低下会导致肌肉无力。"}, {"name": "Nemaline myopathy", "detail": "遗传学家庭参考资料提供有关肾上腺肌病的信息。"}, {"name": "其他疾病", "detail": "TPM3gene的突变也与称为肾上腺肌病的疾病有关。患有肾上腺肌病的人通常在整个身体中都有肌肉无力，包括面部，颈部和四肢的肌肉。当肾上腺肌病是由TPM3gene突变引起的时，受影响的个体通常在出生时或从幼儿期开始就出现肌肉无力。TPM3gene突变占所有肾上腺肌病病例的一小部分。"}]}' WHERE id = 17483;
UPDATE genes SET GHR_metadata_ch = '{"gene": "UGT1A1", "conditions": [{"name": "Crigler-Najjar综合征", "detail": "已鉴定出导致Crigler-Najjar综合征的UGT1A1gene中至少有85个突变。当每个细胞中UGT1A1gene的两个拷贝都改变时，就会发生这种情况。Crigler-Najjar综合征的特征是血液中非结合胆红素水平高（非结合高胆红素血症）和皮肤和眼睛发黄（黄疸）。由于未结合的胆红素在大脑中的积累，一些受影响的个体发展出一种称为核黄疸的脑损伤形式，这可能是致命的。导致Crigler-Najjar综合征的UGT1A1gene突变导致胆红素UGT酶功能降低或缺失。患有Crigler-Najjar综合征1型（CN1）的人没有酶功能，而患有Crigler-Najjar综合征2型（CN2）的人的正常功能不到20%。CN1的体征和症状比CN2严重。胆红素UGT功能的丧失降低了未结合胆红素的葡萄糖醛酸化。这种有毒物质会积聚在体内，引起高胆红素血症，黄疸，有时还会引起核黄疸。"}, {"name": "吉尔伯特综合症", "detail": "UGT1A1gene的改变可引起吉尔伯特综合征。这种情况的特征是轻度未结合的高胆红素血症，很少导致黄疸发作。(换行)吉尔伯特综合症在世界范围内发生，但某些突变在特定人群中更常见。在许多人群中，引起吉尔伯特综合症的最常见遗传变化发生在UGT1A1gene附近的一个区域，称为启动子区域，该区域控制胆红素UGT酶的产生。这种变化必须在UGT1A1gene的两个拷贝中发生，以引起吉尔伯特综合症。吉尔伯特综合症涉及的常见遗传变化称为UGT1A1*28，是由于在启动子区域的一个重要序列（称为TATA盒）中添加了两个DNA构建块（核苷酸）而引起的。正常的UGT1A1 TATA盒序列写为（TA）6TAA。UGT1A1*28序列包括一个额外的TA核苷酸对，并写为（TA）7TAA。这种遗传变化会产生比正常TATA盒更长的时间，并损害蛋白质的产生。(换行)但是，UGT1A1*28的变化在亚洲人群中并不常见。患有吉尔伯特综合症的亚洲人通常在UGT1A1gene的一个拷贝中发生突变，从而导致胆红素UGT酶中单个蛋白质结构单元（氨基酸）的变化。该群体中最常见的突变是在酶的71位（写为Gly71Arg或G71R）上用氨基酸精氨酸替换了氨基酸甘氨酸。这种类型的突变称为错义突变，导致酶功能降低。(换行)吉尔伯特综合征患者的胆红素UGT酶功能约为正常值的30%。结果，未结合的胆红素葡萄糖醛酸化速度不够快，并在体内积聚，引起轻度高胆红素血症。"}, {"name": "华法林抵抗", "detail": "遗传学家庭参考资料提供有关华法林耐药性的信息。"}, {"name": "其他疾病", "detail": "尽管黄疸在新生儿中很常见，但UGT1A1gene的突变会增加患上更严重的疾病（称为暂时性家族性新生儿高胆红素血症）的风险。在这种情况下，严重的非结合性高胆红素血症和黄疸发生在新生儿中，通常在1至2周内消失。一些婴儿会出现核黄疸（可能致命），听力下降或其他神经系统问题。G71R突变是与短暂性家族性新生儿高胆红素血症相关的最常见突变。具有UGT1A1gene突变的亚洲而非白人新生儿似乎有患此病的风险。(换行)有时新生儿黄疸与母乳喂养有关：婴儿母乳喂养时非结合胆红素水平升高，引起黄疸，长时间停止母乳喂养后恢复正常。这种情况通常称为母乳黄疸，在出生后5或10天出现，并在4个月大时消失。在患有母乳黄疸的婴儿中通常不会看到核黄疸。研究表明，受影响婴儿母亲的母乳中的一种物质会阻断葡萄糖醛酸化。此外，许多受影响的婴儿在一个拷贝的UGT1A1gene中有一个突变，最常见的是G71R突变，该突变被认为是未结合的高胆红素血症的基础。母乳中的物质可能会引发胆红素UGT酶功能受损的婴儿非结合胆红素的积聚。"}]}' WHERE id = 17484;
UPDATE genes SET GHR_metadata_ch = '{"gene": "FOLR1", "conditions": [{"name": "脑叶酸运输缺陷", "detail": "已发现FOLR1gene中至少有11个突变会导致脑叶酸转运不足，这种疾病的特征是神经系统问题始于2岁左右。这些突变大多数会改变叶酸受体α中的单个蛋白质结构单元（氨基酸）。FOLR1gene突变导致蛋白质缺乏或蛋白质功能异常。如果脑细胞中没有叶酸受体α，则血流中的5-MTHF不能转运到CSF中并传递到大脑。大脑中叶酸的缺乏（缺乏）会损害正常细胞功能，例如DNA，蛋白质和神经递质的产生。叶酸缺乏影响髓磷脂的稳定性，导致该组织的产生受损或分解增加，这种情况称为脑白质营养不良。由缺乏叶酸引起的这些脑部异常导致智力残疾，运动问题和典型的脑叶酸运输缺陷的反复发作（癫痫）。"}]}' WHERE id = 17506;
UPDATE genes SET GHR_metadata_ch = '{"gene": "GPC3", "conditions": [{"name": "辛普森-戈拉比-贝梅尔综合征", "detail": "在辛普森-戈拉比-贝梅尔综合征患者中，已经鉴定出GPC3gene的50多个突变。这种情况被归类为过度生长综合症，这意味着受影响的婴儿出生时（巨大儿）比正常大得多，并且继续以异常的速度增长和增重。该病症还可能与各种其他先天缺陷和健康问题有关。(换行)导致Simpson-Golabi-Behmel综合征的大多数突变删除部分或全部GPC3gene，这阻止细胞产生功能性磷脂酰肌醇蛋白聚糖3。其他突变插入或删除gene中的少量遗传物质，或改变磷脂酰肌醇蛋白聚糖3中的一个或几个蛋白质结构单元（氨基酸）。这些突变改变了蛋白质的结构。GPC3gene中的（换行）突变阻止磷脂酰肌醇蛋白聚糖3抑制hedgehog信号传导途径。由此产生的该途径的过度活跃导致出生前开始的细胞生长和分裂速率增加。这种增加的细胞增殖至少部分地解释了辛普森-戈拉比-贝梅尔综合征中发生的过度生长。目前尚不清楚刺猬信号的变化如何导致这种疾病可能发生的其他异常。"}]}' WHERE id = 17510;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ZIC2", "conditions": [{"name": "非综合征性全前脑", "detail": "已发现ZIC2gene中的80多个突变会导致非综合征性全前脑畸形。当大脑在早期发育过程中未能分成两半（半球）时，就会发生这种情况。ZIC2gene突变是非综合征性全前脑的第二大常见原因。具有ZIC2gene突变的个体的面部特征与其他gene突变引起的非综合征性全前脑的面部特征不同。这些独特的面部特征包括太阳穴处的头部变窄，指向上的眼睛外角（向上倾斜的睑裂），大耳朵，鼻孔上翘的短鼻子，以及鼻子和鼻子之间的宽而深的空间。嘴（人中）。目前尚不清楚ZIC2gene的突变如何导致这些面部特征。（换行）导致非综合征性全前脑的ZIC2gene突变降低或消除ZIC2蛋白的活性。如果没有足够的功能性ZIC2蛋白，则无法正确控制参与正常前脑发育的gene。结果，大脑不会分成两个半球。非综合征性全前脑的体征和症状是由大脑和面部的异常发育引起的。"}]}' WHERE id = 17520;
UPDATE genes SET GHR_metadata_ch = '{"gene": "GBE1", "conditions": [{"name": "成人聚葡聚糖身体疾病", "detail": "已发现GBE1gene中至少有三个突变会引起成人聚葡聚糖体病，这种疾病会影响神经系统。这些突变改变了糖原分支酶中的单个蛋白质结构单元（氨基酸）。在患有Ashkenazi犹太血统的受影响人群中，一种突变似乎更为常见。该突变用酶（写为Tyr329Ser或Y329S）中第329位的氨基酸丝氨酸取代了氨基酸酪氨酸。导致成人聚葡聚糖身体疾病的大多数突变都会导致酶的缺乏（缺乏）。结果，糖原具有较少的侧链。这些异常的糖原分子称为聚葡聚糖体，在细胞内积累并造成损害。在这种疾病中，神经细胞（神经元）似乎特别容易受到聚葡聚糖体积累的影响。对神经元的损伤导致成年聚葡聚糖身体疾病患者的感觉，虚弱和其他神经系统问题减少。"}, {"name": "糖原贮积病IV型", "detail": "已发现GBE1gene中约40个突变会导致IV型糖原贮积病（GSD IV）。这种疾病的特征是肝脏和肌肉问题，通常始于婴儿期，由异常糖原的积累引起。导致这种情况的大多数突变会改变糖原分支酶中的单个氨基酸。导致GSD IV的GBE1gene突变导致糖原分支酶严重缺乏或完全缺乏。结果，聚葡聚糖体在细胞中积累，导致损伤和细胞死亡。聚葡聚糖体在全身细胞中积聚，但肝细胞和肌肉细胞在GSD IV中受到最严重的影响。肝脏中糖原的积累会干扰肝脏功能，导致肝脏和肝脏疾病扩大。肌肉细胞无法分解糖原以获取能量会导致肌肉无力和消瘦。(换行)目前尚不清楚为什么肝脏和肌肉细胞受到GSD IV中聚葡聚糖体积累的影响，而神经元仅在成人聚葡聚糖体病中受到影响（如上所述）。"}]}' WHERE id = 17532;
UPDATE genes SET GHR_metadata_ch = '{"gene": "AASS", "conditions": [{"name": "高溶血血症", "detail": "AASSgene的突变与一种称为高溶血血症的疾病有关。氨基己二酸半醛合酶中的单个氨基酸变化的特征是酶活性降低或没有酶活性，这对于分解赖氨酸或糖精碱至关重要。这导致赖氨酸在没有任何健康状况的血液中蓄积，但是糖精在血液和尿液中的蓄积导致糖尿症，这是高赖氨酸血症的一种变体，其症状尚不清楚。"}]}' WHERE id = 17533;
UPDATE genes SET GHR_metadata_ch = '{"gene": "PHOX2B", "conditions": [{"name": "先天性中枢性通气不足综合征", "detail": "大多数导致先天性中枢通气不足综合征（CCHS）的PHOX2Bgene突变会在PHOX2B蛋白的聚丙氨酸束中添加额外的丙氨酸。这种类型的突变称为聚丙氨酸重复扩增。在患有这种疾病的个体中，有8%至10%发现了其他类型的PHOX2Bgene突变。（换行）突变被认为会干扰PHOX2B蛋白在促进神经元形成和分化中的作用，尤其是在自主神经系统中，从而导致调节CCHS中呼吸和其他自主神经系统功能障碍的问题。"}, {"name": "神经母细胞瘤", "detail": "在患有神经母细胞瘤的人中已经鉴定出PHOX2Bgene的几种突变，神经母细胞瘤是一种由未成熟神经细胞（神经母细胞）组成的癌性肿瘤。当关键gene中的gene突变累积时，就会发生神经母细胞瘤和其他癌症？那些控制细胞增殖或分化的？让细胞不受控制地生长和分裂形成肿瘤。在大多数情况下，这些gene变化是在一个人的一生中获得的，称为体细胞突变。体细胞突变仅存在于某些细胞中，不会遗传。不太常见的是，增加患癌症风险的gene突变可以从父母那里遗传。两种类型的突变都发生在神经母细胞瘤中。PHOX2Bgene的体细胞突变会增加散发性神经母细胞瘤的风险，而PHOX2Bgene的遗传突变会增加家族性神经母细胞瘤的风险。(换行)在一些神经母细胞瘤患者中，PHOX2Bgene的突变改变了PHOX2B蛋白中的单个蛋白质构建块（氨基酸）。其他受影响的个体可能在PHOX2Bgene中添加或删除了几个DNA构建块（核苷酸）。核苷酸的添加或缺失会改变PHOX2B蛋白中的氨基酸序列。在家族性和散发性神经母细胞瘤中发现了所有这些类型的突变。据信该突变会干扰PHOX2B蛋白在促进神经细胞分化中的作用，从而导致过多的未成熟神经细胞并导致神经母细胞瘤。"}, {"name": "其他疾病", "detail": "PHOX2Bgene的变异与涉及自主神经系统和源自神经c的组织的某些其他疾病的风险增加有关。在患有先天性中枢通气不足综合征和肠道神经功能障碍（称为Hirschsprung病）的人中，已经发现了特定的PHOX2Bgene变异（这种疾病的组合通常称为Haddad综合征）。Hirschsprung病中的神经问题导致受影响个体严重便秘，肠梗阻和结肠增大。在神经母细胞瘤和Hirschsprung病患者中也发现了PHOX2Bgene突变。此外，在一些死于婴儿猝死综合症（SIDS）的婴儿中发现了PHOX2Bgene变异。PHOX2Bgene变异可能会影响早期发育中神经元分化的调节，从而导致这些疾病的风险增加。"}]}' WHERE id = 17540;
UPDATE genes SET GHR_metadata_ch = '{"gene": "PCCA", "conditions": [{"name": "丙酸血症", "detail": "在丙酸血症患者中已鉴定出PCCAgene的45个以上突变。这些突变包括单个DNA构建模块（核苷酸）的变化以及PCCAgene中遗传物质的插入或缺失。PCCA突变会阻止功能性丙酰辅酶A羧化酶的产生或降低酶的活性。改变或缺失的酶无法正确处理蛋白质和脂质的某些部分。结果，丙酰辅酶a和其他潜在有害化合物可在体内积聚至毒性水平。这种积聚会损害大脑和神经系统，导致与丙酸血症相关的严重健康问题。"}]}' WHERE id = 17547;
UPDATE genes SET GHR_metadata_ch = '{"gene": "CASR", "conditions": [{"name": "常染色体显性低钙血症", "detail": "CASRgene中的突变可引起称为常染色体显性低钙血症1型的病症，其特征在于血液中钙水平低（低钙血症）。一些受影响的个体也缺乏甲状旁腺激素（甲状旁腺功能低下）。(换行)这种情况下涉及的大多数CASRgene突变都会改变CASR蛋白中的单个蛋白结构单元（氨基酸）。这些变化（称为激活突变）导致CaSR过度活跃，对钙更敏感，这意味着即使钙水平低也可以触发信号传导。过度活跃的CaSR阻止甲状旁腺激素的释放，从而阻止钙释放到血液中。此外，过度活跃的CaSR可防止钙从通过肾脏过滤的液体中重吸收。低钙血症可引起肌肉痉挛和癫痫发作，尽管约有一半患有这种疾病的人没有相关的健康问题。"}, {"name": "家族性孤立性甲状旁腺功能亢进", "detail": "在一些患有家族性孤立性甲状旁腺功能亢进症的人中发现了CASRgene的突变，这种疾病的特征是甲状旁腺过度活跃（原发性甲状旁腺功能亢进）。原发性甲状旁腺功能亢进破坏了血液中钙的正常平衡，这可能导致家族性孤立性甲状旁腺功能亢进症患者的肾结石，骨骼变薄（骨质疏松症），恶心，呕吐，高血压（高血压），虚弱和疲劳。这种情况是由每个细胞中gene的一个拷贝的变化引起的。与这种情况相关的CASRgene突变会改变CASR蛋白中的单个氨基酸。这些遗传变化被称为失活突变，因为改变的CaSR蛋白对钙的敏感性较低，因此需要异常高浓度的钙来触发信号传导。结果，即使血液中钙的浓度升高，甲状旁腺激素也会产生，使钙水平继续升高。此外，甲状旁腺细胞可能在没有控制的情况下增殖，这偶尔会导致家族性孤立性甲状旁腺功能亢进症患者甲状旁腺增大。从这些异常腺体过量产生甲状旁腺激素可能进一步刺激钙释放到血液中。高水平的钙会导致家族性孤立性甲状旁腺功能亢进的体征和症状。(换行)一些研究人员认为，由CASRgene突变引起的家族性孤立性甲状旁腺功能亢进是一种更为严重的类似疾病，称为家族性低钙血症性高钙血症（如下所述）。"}, {"name": "其他疾病", "detail": "CASRgene的突变与钙水平异常有关。导致CaSR功能降低的失活突变可导致家族性低钙血症性高钙血症。这种情况的特征是血液中钙含量高（高钙血症）和尿液中钙含量低（低钙尿症），但是受影响的个体通常没有与该疾病相关的症状。很少，受影响的个体甲状旁腺增大，甲状旁腺激素水平略有升高。像家族性孤立性甲状旁腺功能亢进症一样，这种情况是由CASRgene单拷贝的突变引起的。（换行）称为新生儿严重甲状旁腺功能亢进的更严重的病症是由导致很少或没有CaSR功能的gene突变引起的。在患有这种情况的人中，CASRgene的两个副本都被改变了。新生儿严重甲状旁腺功能亢进是一种潜在的致命疾病，在6个月以下的婴儿中变得明显。患病婴儿通常一个或多个甲状旁腺过度生长（甲状旁腺增生），导致血液中甲状旁腺激素水平升高。过量的激素异常刺激钙释放到血液中，引起高钙血症。钙通常从骨骼中去除，导致骨骼异常。这些人的极端高钙血症会导致神经系统问题，因为过量的钙会干扰神经信号传导。"}]}' WHERE id = 17560;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SLC12A1", "conditions": [{"name": "巴特综合症", "detail": "在I型Bartter综合征患者中已鉴定出SLC12A1gene的40多个突变。这种疾病非常严重，导致危及生命的健康问题，这些问题在出生前或出生后不久就变得明显。（换行）负责Bartter综合征的大多数SLC12A1gene突变会改变NKCC2蛋白中的单个蛋白结构单元（氨基酸）。其他突变会从蛋白质中删除氨基酸或导致异常短版本的NKCC2蛋白质的产生。每个已知的突变都会阻止NKCC2蛋白将离子转运到肾细胞中。结果，肾脏不能正常地重吸收盐，并且过量的盐通过尿液流失（盐浪费）。异常的盐分流失破坏了体内钠，钾和其他离子的正常平衡。这些不平衡是Bartter综合征的主要特征。"}, {"name": "其他疾病", "detail": "研究表明，SLC12A1gene的正常变异（多态性）可能有助于解释不同人群的血压变异。某些罕见的多态性似乎可以预防高血压（高血压），研究人员推测其他遗传变异可能会增加患高血压的风险。SLC12A1gene的变化可能会通过改变肾脏将盐重吸收到血液中的能力来影响血压。"}]}' WHERE id = 17564;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SCN8A", "conditions": [{"name": "SCN8A相关性癫痫伴脑病", "detail": "已发现SCN8Agene中的100多个突变会导致SCN8A相关性癫痫并伴有脑病。这种情况的特征是反复发作（癫痫），脑功能异常（脑病）和智力残疾。这种情况的体征和症状通常始于婴儿期。(换行)大多数这些SCN8Agene突变会改变Nav1.6通道中的单个蛋白质结构单元（氨基酸）。导致SCN8A相关性癫痫伴脑病的突变导致通道改变，通道保持开放的时间比平时更长，这增加了钠离子进入神经元的流量。持续开放的通道异常增加电信号，这可能导致大脑中神经元的过度激活（激发）。这种增加的神经元活动导致患有SCN8A相关性癫痫伴脑病的人癫痫发作。（换行）尚不清楚SCN8Agene突变如何导致智力残疾，运动问题以及SCN8A相关性癫痫伴脑病的其他特征。由于一些受影响的儿童在癫痫发作后会丧失先前获得的技能（发育退化），因此有人提出癫痫发作可能会损害脑功能，但目前尚不清楚情况是否如此。"}, {"name": "其他疾病", "detail": "已经发现SCN8Agene的突变会导致某些人的智力残疾和运动问题。与引起SCN8A相关性脑病癫痫的突变不同（如上所述），引起这种情况的SCN8Agene突变导致产生的Nav1.6钠通道活性低于正常水平，导致钠流入神经元。这种变化不会增加神经元信号传导，因此具有这些突变的个体不会发生癫痫发作。研究人员怀疑，协调运动的大脑部分神经元信号的减少可能是运动问题的原因。(换行)其他SCN8Agene突变可引起运动问题和癫痫发作，称为良性婴儿癫痫发作，通常在生命的第一年发展，并在3岁时停止。与大多数其他具有SCN8Agene突变的个体不同，这些个体具有正常的智力功能。尚不清楚为什么某些SCN8Agene突变会导致这些较轻的体征和症状，而其他突变会导致SCN8A相关性癫痫伴脑病的更严重特征。"}]}' WHERE id = 17595;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SLC6A3", "conditions": [{"name": "多巴胺转运蛋白缺乏综合征", "detail": "在多巴胺转运蛋白缺乏综合征患者中已鉴定出SLC6A3gene至少19个突变，这是一种罕见的运动障碍，会随着时间的推移而恶化。它的体征和症状通常始于婴儿期，但可能出现在儿童期或更晚时期。一些突变会改变多巴胺转运蛋白中的单个蛋白结构单元（氨基酸）。其他导致产生异常短的蛋白质或阻止细胞产生任何功能性蛋白质。所有这些突变均损害多巴胺转运蛋白的功能。因为受损的转运蛋白不能将多巴胺从突触间隙携带并返回神经元，所以多巴胺在神经元周围的空间中积聚。过量的多巴胺会改变神经元之间的信号传导，并可能抑制（抑制）通常会触发产生更多多巴胺的途径。尽管多巴胺在控制运动中起关键作用，但尚不清楚多巴胺信号的改变如何导致多巴胺转运蛋白缺乏综合征患者的特定运动异常。(换行)研究表明，出现体征和症状的年龄与多巴胺转运蛋白功能受到严重影响有关。从婴儿期开始出现运动问题的受影响个体的转运蛋白活性通常低于正常水平的5%。那些运动问题出现在儿童期或以后的人，尽管转运蛋白的活性仍低于正常水平，但转运蛋白的活性往往较高。研究人员推测，较高水平的转运蛋白活性可能会延迟这些个体的疾病发作。"}, {"name": "其他疾病", "detail": "已经研究了SLC6A3gene的变异（多态性）作为注意力缺陷多动障碍（ADHD）和自闭症谱系障碍（ASD）的可能危险因素。多动症通常始于儿童时期，其特征是过度活跃，冲动行为和难以注意。ASD代表一组影响沟通和社交互动的发展conditions。多巴胺信号传导的变化似乎在ADHD和ASD中都起着重要作用。但是，尚不清楚SLC6A3gene的变异可能如何涉及。多种遗传和环境因素，其中大多数仍然未知，可能决定了发展这些复杂疾病的风险。"}]}' WHERE id = 17600;
UPDATE genes SET GHR_metadata_ch = '{"gene": "HPRT1", "conditions": [{"name": "Lesch-Nyhan综合征", "detail": "已发现HPRT1gene中的200多个突变引起Lesch-Nyhan综合征。这些突变包括单个DNA构建模块（核苷酸）的变化或gene内少量DNA的插入或缺失。这些变化导致无功能或功能非常低的次黄嘌呤磷酸核糖基转移酶1。在这些conditions下，尿酸（嘌呤分解的废物）积聚在体内并可引起痛风性关节炎（由关节中的尿酸引起的关节炎），肾结石和膀胱结石。目前尚不清楚这种酶缺乏症如何引起Lesch-Nyhan综合征的神经和行为问题。"}, {"name": "其他疾病", "detail": "HPRT1gene中的某些突变也可引起以痛风性关节炎为特征的疾病，称为HPRT相关痛风，以前称为Kelley-Seegmiller综合征。患有这种情况的个体的次黄嘌呤磷酸核糖基转移酶1水平低于正常水平。患有这种疾病的人通常会出现肾脏问题，因为尿酸结晶的积累会形成肾结石。很少，这种情况会导致神经系统出现问题。"}]}' WHERE id = 17620;
UPDATE genes SET GHR_metadata_ch = '{"gene": "MSX2", "conditions": [{"name": "顶孔增大", "detail": "在1型顶孔扩大的人群中，已鉴定出MSX2gene至少10个突变。这种情况的特征是顶骨中的开口增大（孔），这是形成头骨顶部和侧面的两个骨头。胎儿发育期间顶骨中的开口是正常的，但它们通常在出生前关闭。在患有这种疾病的人中，顶孔终生保持开放。（换行）导致顶孔扩大的突变导致无法与DNA结合的MSX2蛋白的产生，从而改变了多个gene的调控。结果，几个细胞过程被破坏，包括增殖，分化和存活。在早期发育中，头骨似乎对MSX2蛋白活性的变化和细胞功能的变化特别敏感。具体而言，颅骨中与骨形成（骨化）有关的细胞无法正常工作，从而导致颅骨区域缺乏骨和顶孔增大。"}, {"name": "其他疾病", "detail": "MSX2gene中的至少两个突变会导致一种称为2型颅骨融合症（也称为波士顿型颅骨融合症）的疾病。颅骨融合症涉及颅骨过早闭合，导致头部畸形。患有2型颅骨融合症的人可能会出现颅骨畸形，包括前额突出（额头凸起），指向顶部的短而宽的头（头颅畸形）或苜蓿叶形的头骨（Kleeblattschaedel畸形）。大多数受影响的人有视力问题，少数人经历过癫痫发作。智力通常是正常的。（换行）尚不清楚MSX2gene的变化如何导致2型颅突神经病的颅骨过早闭合和顶壁孔扩大的骨形成受损（如上所述）。"}]}' WHERE id = 17630;
UPDATE genes SET GHR_metadata_ch = '{"gene": "PAX6", "conditions": [{"name": "无虹膜", "detail": "已发现PAX6gene中的280多个突变会引起虹膜虹膜，这是眼睛（虹膜）有色部分的缺失。这些突变中的大多数会在制造PAX6蛋白的说明中产生过早的终止信号，并导致异常短的无功能蛋白的产生。结果，调节其他gene活性的PAX6蛋白较少。（换行）导致无虹膜的大多数突变发生在PAX6gene内；但是，某些致病突变发生在通常调节PAX6gene表达的DNA邻近区域，称为调节区域。PAX6gene调控区的突变会降低PAX6gene的表达。这些突变导致功能性PAX6蛋白的缺乏，从而破坏了发育过程中眼睛的形成。"}, {"name": "吉莱斯皮综合症", "detail": "在吉莱斯皮综合症患者中，已经鉴定出PAX6gene中至少有两个突变，吉莱斯皮综合症是一种以眼睛异常为特征的疾病，包括虹膜部分缺失（部分无虹膜），运动协调困难（共济失调）和轻度至中度智力障碍。导致吉莱斯皮综合症的突变导致缺少PAX6蛋白或产生无法与DNA结合并调节其他gene活性的无功能PAX6蛋白。功能蛋白的缺乏会破坏胚胎发育，尤其是眼睛和大脑的发育，从而导致吉莱斯皮综合症的体征和症状。"}, {"name": "彼得斯异常", "detail": "已经发现PAX6gene中至少有两个突变引起彼得斯异常。这种情况的特征是眼前部某些结构异常发育，眼睛清晰的前表面（角膜）混浊。导致彼得斯异常的突变改变了PAX6蛋白中的单个蛋白结构单元（氨基酸）。这些突变减少但不消除蛋白质的功能，并且不如引起虹膜虹膜的突变严重（如上所述）。导致彼得斯异常的突变降低了PAX6蛋白与DNA结合的能力，破坏了其作为转录因子的作用。结果，眼睛的正常发育受到损害，导致彼得斯异常的特征。导致Peters异常的PAX6gene突变可导致同一家族成员中其他相关的眼部疾病。"}, {"name": "缺损", "detail": "遗传学家庭参考提供有关结肠癌的信息。"}, {"name": "小眼症", "detail": "遗传学家庭参考提供有关小眼症的信息。"}, {"name": "WAGR综合征", "detail": "PAX6gene位于11号染色体上的一个区域，该区域在WAGR综合征患者中被删除，WAGR综合征是一种影响许多身体系统的疾病，并以其主要特征命名：一种称为Wilms肿瘤的儿童肾癌，一种称为眼睛的问题anirida，泌尿生殖系统异常和智力残疾（以前称为智力低下）。这种缺失的结果是，受影响的个体在每个细胞中缺失了一个拷贝的PAX6gene。PAX6gene的丢失与WAGR综合征的特征性眼部特征（包括虹膜虹膜）有关，并可能影响大脑发育。"}, {"name": "其他疾病", "detail": "PAX6gene的突变可引起除虹膜虹膜和彼得斯异常以外的其他眼部疾病。导致这些眼部问题的突变发生在每个细胞中PAX6gene的一个拷贝中。这些突变大多数会改变PAX6蛋白中的单个氨基酸。这些突变会降低但不会消除蛋白质的正常功能，从而削弱其作为转录因子的作用。(换行)具有这些相对轻微的PAX6gene突变的个体可能出生时瞳孔不在眼睛中央（异位乳头），小眼睛（小眼症）和不发达的视神经，这些结构将信息从眼睛传递到大脑。轻度的PAX6突变也可能导致构成眼睛（眼球瘤）的结构间隙或分裂，或导致中央视力清晰（中央凹）的眼后部发育不全的区域。这些PAX6gene突变引起的其他疾病可能在出生时出现或在以后发展。这些情况可能包括眼睛晶状体混浊（白内障），不自主的眼球运动（眼球震颤）和称为角膜（角膜炎）的眼睛前表面发炎。(换行)目前尚不清楚为什么PAX6gene中某些突变的影响仅限于眼睛，而其他突变会影响身体许多部位的发育。"}]}' WHERE id = 17637;
UPDATE genes SET GHR_metadata_ch = '{"gene": "GNPAT", "conditions": [{"name": "点状软骨发育不良", "detail": "已发现GNPATgene中至少有五个突变会导致点状软骨发育不良2型（RCDP2）。这些突变阻止细胞产生任何功能性DHAPAT酶。该酶的缺乏会破坏过氧化物酶体功能并严重减少细胞内缩醛磷脂的量。目前尚不清楚这些异常如何导致长骨缩短，智力残疾以及RCDP2的其他特征。"}]}' WHERE id = 17649;
UPDATE genes SET GHR_metadata_ch = '{"gene": "PHOX2A", "conditions": [{"name": "先天性眼外肌纤维化", "detail": "PHOX2Agene中至少有四个突变可导致眼外肌先天性纤维化。这些突变导致了一种称为CFEOM2的疾病，这种疾病已在中东血统的几个家族中发现。(换行)导致眼外肌先天性纤维化的大多数突变都会导致PHOX2A蛋白异常短而无功能的产生。在人类中，缺乏这种蛋白质会阻止几种颅神经及其控制的眼外肌的正常发育。这些肌肉的异常发育和功能导致该疾病的特征，包括眼球运动受限和视力相关问题。尽管PHOX2A蛋白在自主神经系统发育中起重要作用，但PHOX2A突变似乎并不影响这部分神经系统的功能。"}]}' WHERE id = 17657;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SLC26A2", "conditions": [{"name": "软骨形成", "detail": "已发现SLC26A2gene中至少有8个突变会导致某种形式的软骨生成，称为1B型或Parenti-Fraccaro型。这种罕见的骨骼发育障碍的特征是四肢极短，手指和脚趾短，胸部狭窄，腹部突出而圆。这些异常会导致严重的健康问题，软骨发育不全的婴儿通常在出生前或出生后不久死亡。（换行）两个SLC26A2gene突变似乎是1B型软骨发育不全的相对常见原因。这些突变之一删除了SLC26A2蛋白中341位的单个蛋白结构单元（氨基酸）。这种遗传变化被写为Val341del。另一个突变在SLC26A2gene的特定位置删除了三个DNA构建块（碱基对）。这种遗传变化被写为c.1020_1022delTGT。（换行）导致1B型软骨形成的SLC26A2gene突变阻止SLC26A2gene产生任何功能性蛋白质。没有这种蛋白质，软骨细胞就不能吸收必需的硫酸根离子，细胞也不能产生正常的蛋白多糖。缺乏这些重要分子严重破坏了软骨的结构，使其在显微镜下看起来粗糙和海绵状。由于许多骨骼是在出生前和儿童早期从软骨发育而来的，因此SLC26A2gene突变可防止骨骼正常发育和生长，从而导致1B型软骨发育不全中出现严重的骨骼异常。"}, {"name": "Atelosteogenesis类型2", "detail": "在患有成骨细胞2型的人中至少鉴定出8个SLC26A2gene突变，这是另一种严重的软骨和骨骼发育疾病。受影响的个体通常在该gene的一个拷贝中具有破坏SLC26A2蛋白正常结构的突变，而在该gene的另一个拷贝中具有阻止任何功能蛋白产生的突变。（换行）导致2型骨生成的一种常见突变用蛋白质中第279位的氨基酸色氨酸取代氨基酸精氨酸（写为Arg279Trp或R279W）。在芬兰人群中，最常见的突变（通常写为IVS1+2T>C）会干扰SLC26A2蛋白的正常加工。（换行）SLC26A2gene突变改变SLC26A2转运蛋白的结构和功能，这破坏了软骨细胞吸收必需硫酸根离子的能力。然后细胞不能产生正常的蛋白多糖，这会影响软骨的结构以及骨骼的正常形成和生长。"}, {"name": "灾难性发育不良", "detail": "在灾难性异型增生患者中已鉴定出20多个SLC26A2gene突变。尽管灾难性的不典型增生倾向于不那么严重，但这种软骨和骨骼发育障碍的特征与2型骨生成不良（如上所述）相似。像患有2型骨质疏松症的人一样，患有灾难性发育不良的人通常在该gene的一个拷贝中发生突变，从而破坏SLC26A2蛋白的正常结构，而在该gene的另一个拷贝中发生突变，从而阻止了任何功能蛋白的产生。（换行）导致灾难性发育不良的SLC26A2gene突变破坏软骨细胞吸收必需硫酸根离子的能力。没有足够的硫酸盐，细胞就无法产生正常的蛋白聚糖。缺乏这些必需分子会影响软骨的结构以及骨骼的正常形成和生长。"}, {"name": "多发性骨phy发育不良", "detail": "在患有多发性骨phy发育不良的人中发现了SLC26A2gene的至少四个突变，这是一种软骨和骨骼发育障碍，主要影响手臂和腿部长骨的末端（骨phy）。SLC26A2gene突变会导致该疾病的隐性形式，其特征还在于手，脚和膝盖的畸形；脊柱弯曲异常（脊柱侧弯）；和其他出生缺陷。（换行）引起隐性多发性骨phy发育不良的突变通常将SLC26A2蛋白中的一个氨基酸替换为另一个氨基酸。最常见的突变是用蛋白质中279位的氨基酸色氨酸替换氨基酸精氨酸（写为Arg279Trp或R279W）。通常导致这种情况的另一种突变是将SLC26A2蛋白（写为Cys653Ser或C653S）中653位的氨基酸丝氨酸替换为氨基酸半胱氨酸。（换行）导致隐性多发性骨phy发育不良的SLC26A2gene突变的影响往往不如引起威胁生命的骨骼疾病（如1B型软骨发育不全）的突变严重。这些较温和的突变的结果是，SLC26A2蛋白可能保留了其作为硫酸根离子转运蛋白的某些功能。软骨和骨形成受到的损害程度较小，这可能有助于解释隐性多发性骨phy发育不良的症状和体征较轻。"}]}' WHERE id = 17665;
UPDATE genes SET GHR_metadata_ch = '{"gene": "EGLN1", "conditions": [{"name": "家族性红细胞增多症", "detail": "已发现EGLN1gene中至少有10个突变会引起家族性红细胞增多症，这是一种遗传性疾病，其特征是红细胞数量增加和血栓异常风险增加。当EGLN1gene突变导致家族性红细胞增多时，通常将其命名为ECYT3。（换行）一些EGLN1gene突变改变PHD2酶中的单个蛋白质构建块（氨基酸），而另一些导致产生异常短版本的酶。任何这些遗传变化都会破坏酶与HIF-2相互作用的能力？并将其作为销毁目标。因此，即使有足够的氧气，HIF也会积聚在细胞中。当不再需要时，额外HIF的存在导致红细胞的产生，导致血流中这些细胞过量。（换行）至少一种已知的EGLN1gene突变与家族性红细胞增多症和同一个体中称为副神经节瘤的肿瘤有关。副神经节瘤是神经系统的非癌性（良性）肿瘤。该突变写为His374Arg或H374R，用PHD2酶中374位的氨基酸精氨酸取代了氨基酸组氨酸。这种遗传变化改变了PHD2酶和HIF-2α之间的相互作用？，导致产生过多的红细胞。但是，尚不清楚该突变如何与副神经节瘤的发展相关。"}]}' WHERE id = 17699;
UPDATE genes SET GHR_metadata_ch = '{"gene": "CLPB", "conditions": [{"name": "CLPB缺陷", "detail": "已发现至少20个CLPBgene突变导致CLPB缺乏。这种情况的特征是神经系统问题，包括运动异常和癫痫发作；缺乏白细胞（中性粒细胞减少症），这可能增加感染的风险；和眼睛晶状体混浊（白内障）。另外，受影响的个体尿液中称为3-甲基谷氨酸的分子量增加，这似乎不会引起健康问题。这些功能的严重性差异很大。许多CLPBgene突变会导致异常短的CLPB蛋白，该蛋白可能会迅速分解。其他突变可能会降低CLPB的功能。该病的严重程度被认为与剩余的功能蛋白量有关：严重的CLPB缺乏症很可能是由于完全缺乏CLPB蛋白引起的，而当产生某些功能性CLPB蛋白时则会导致中度和轻度CLPB缺乏症。研究人员不确定这种蛋白质的减少或缺乏如何导致CLPB缺乏症的体征和症状。"}]}' WHERE id = 17703;
UPDATE genes SET GHR_metadata_ch = '{"gene": "MATR3", "conditions": [{"name": "远端肌病2", "detail": "在患有远端肌病2的人中，已经鉴定出MATR3gene中的至少一种突变，该病的特征是肌肉和声带无力。与远端肌病2相关的MATR3gene突变改变了matrin 3蛋白中的单个蛋白结构单元（氨基酸）。这种称为Ser85Cys（或S85C）的突变用蛋白质第85位的氨基酸半胱氨酸取代了氨基酸丝氨酸。（换行）S85C突变对matrin 3蛋白功能的影响尚不清楚，尽管一项研究表明该突变可能会改变蛋白在细胞核中的位置。研究人员正在努力确定这种gene突变如何导致远端肌病的体征和症状2。"}, {"name": "肌萎缩侧索硬化症", "detail": "遗传学家庭参考提供有关肌萎缩侧索硬化的信息。"}]}' WHERE id = 17733;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SFTPB", "conditions": [{"name": "表面活性剂功能障碍", "detail": "已经鉴定出SFTPBgene中导致表面活性剂功能障碍的30多个突变。由于SFTPBgene突变（通常称为SP-B缺乏症）引起的表面活性剂功能障碍在新生儿中引起严重的，通常是致命的呼吸问题。这些突变导致成熟SP-B的部分或完全丧失，导致表面活性剂组成异常和表面活性剂功能降低。另外，层状体的形成受到损害。缺乏正常的层状体会导致SP-C的异常加工，从而导致成熟SP-C的减少和未加工形式的SP-C的积累。功能性表面活性剂的丧失会增加肺泡的表面张力，导致呼吸困难和肺部塌陷。SP-B和SP-C功能障碍的结合可以解释为什么SP-B缺乏症的体征和症状如此严重。"}]}' WHERE id = 17743;
UPDATE genes SET GHR_metadata_ch = '{"gene": "RRM2B", "conditions": [{"name": "进行性眼外肌麻痹", "detail": "在患有进行性眼外肌麻痹的眼病患者中，已经鉴定出RRM2Bgene中至少有17个突变。这种疾病削弱了控制眼球运动并导致眼睑下垂（上睑下垂）的肌肉。一些受影响的人还有其他体征和症状，例如其他肌肉无力，极度疲倦（疲劳），内耳问题引起的听力下降（感觉神经性听力下降）和消化系统问题。(换行)通常，导致进行性外部眼肌麻痹的突变发生在RRM2Bgene的一个拷贝中，尽管该gene的两个拷贝很少被改变。与进行性眼外肌麻痹相关的RRM2Bgene突变导致RNR活性受损。这些突变导致肌肉组织中mtDNA的遗传物质大量缺失，这可能是因为RNR活性受损导致核苷酸短缺，尽管其机制尚不清楚。尽管该病的特征可能与氧化磷酸化受损有关，但研究人员尚未确定mtDNA的缺失如何导致进行性眼外肌麻痹的特定体征和症状。已经提出，眼肌通常受到线粒体缺陷的影响，因为它们特别依赖于氧化磷酸化作为能量。"}, {"name": "RRM2B相关的线粒体DNA耗竭综合征，脑肌病形式与肾小管病变", "detail": "RRM2Bgene中的十几个突变可导致RRM2B相关的线粒体DNA耗竭综合征，肾小管病（RRM2B-MDS）的脑肌病形式，这是一种影响多个身体系统的严重疾病。它通常会导致脑功能障碍，并伴有肌肉无力（脑病）和肾功能问题，称为肾小管病变。导致这种疾病的突变发生在RRM2Bgene的两个拷贝中。它们降低了RNR的活性或数量，这可能会损害mtDNA核苷酸的产生。可用于生产mtDNA分子的核苷酸短缺导致mtDNA量减少（称为mtDNA消耗）并损害许多不同类型细胞中的线粒体功能。（换行）氧化磷酸化的损害被认为是线粒体DNA耗竭综合征的体征和症状的基础。目前尚不清楚为什么某些RRM2Bgene突变会导致mtDNA的遗传物质缺失（如上述进行性眼外肌麻痹），而另一些会减少mtDNA的总量（如RRM2B-MDS）。"}]}' WHERE id = 17752;
UPDATE genes SET GHR_metadata_ch = '{"gene": "HPS3", "conditions": [{"name": "Hermansky-Pudlak综合征", "detail": "已发现HPS3gene中至少有7个突变会导致3型Hermansky-Pudlak综合征，这是该病的一种轻度形式。受影响的个体通常患有眼皮肤白化病，其特征是皮肤白皙，头发和眼睛浅色以及视力差。他们也可能有出血问题。导致3型Hermansky-Pudlak综合征的HPS3gene突变损害BLOC-2的正常功能，破坏整个身体细胞中LRO的大小，结构和功能。（换行）一种常见突变导致HPS3gene内遗传物质的缺失。该缺失包括大约3900个DNA构建块（核苷酸），被称为3.9kb缺失。它也写为339_4260del3904。在波多黎各中部地区的受影响个体中发现的这种突变阻止了任何HPS3蛋白的产生。(换行)在具有中欧和东欧（Ashkenazi）犹太背景的人中发现了HPS3gene的另一个突变。这种称为剪接位点突变的突变破坏了gene指令用于制造蛋白质的方式。此突变写为1163+1G>A，导致蛋白质异常短。(换行)由于异常的黑素体不能正确分布黑色素，因此患有Hermansky-Pudlak综合征的人的皮肤，头发和眼睛的颜色异常浅（眼皮肤白化病）。血小板内不存在致密颗粒会导致受影响个体的出血问题。"}]}' WHERE id = 17755;
UPDATE genes SET GHR_metadata_ch = '{"gene": "WT1", "conditions": [{"name": "Denys-Drash综合征", "detail": "已发现WT1gene中至少有80个突变会引起Denys-Drash综合征，这种疾病会影响肾脏和生殖器的发育，并且最常影响男性。这些突变几乎总是发生在称为外显子8和外显子9的gene区域。大多数突变会改变WT1蛋白中的单个蛋白结构单元（氨基酸）。导致Denys-Drash综合征的最常见突变（在约40%的病例中发现）用蛋白质394位的氨基酸色氨酸取代了氨基酸精氨酸（书面Arg349Trp或R349W）。（换行）导致Denys-Drash综合征的突变导致产生不能与DNA结合的异常WT1蛋白。结果，某些gene的活性不受调节，这损害了肾脏和生殖器的发育。这些器官的异常发育导致Denys-Drash综合征的体征和症状。（换行）很少，WT1gene的外显子8或外显子9中的突变引起称为Frasier综合征的相关病症（如下所述）。由于这两个conditions具有共同的遗传原因并具有重叠的特征，因此一些研究人员建议这两个conditions是频谱的一部分，而不是两个不同的conditions。"}, {"name": "弗雷西尔综合征", "detail": "已发现WT1gene中至少有7个突变会引起Frasier综合征，这种疾病会影响肾脏和生殖器的发育，并且最常影响男性。这些突变几乎总是发生在称为内含子9的gene区域。导致Frasier综合征的最常见突变（在超过一半的受影响个体中发现）改变了WT1gene中的单个DNA构建块（核苷酸），写为IVS+4C>T。这种突变和其他导致Frasier综合征的突变改变了gene指令拼接在一起以产生蛋白质的方式。（换行）导致Frasier综合征的WT1gene突变导致产生具有受损的控制gene活性和调节肾脏和生殖器官发育的能力的蛋白质，导致Frasier综合征的体征和症状。（换行）很少，WT1gene的内含子9中的突变引起称为Denys-Drash综合征的相关病症（如上所述）。由于这两个conditions具有共同的遗传原因并具有重叠的特征，因此一些研究人员建议这两个conditions是频谱的一部分，而不是两个不同的conditions。"}, {"name": "先天性肾病综合征", "detail": "遗传学家庭参考提供有关先天性肾病综合征的信息。"}, {"name": "细胞遗传学正常的急性髓性白血病", "detail": "遗传学家庭参考提供有关细胞遗传学正常的急性髓细胞性白血病的信息。"}, {"name": "前列腺癌", "detail": "遗传学家庭参考提供有关前列腺癌的信息。"}, {"name": "WAGR综合征", "detail": "WT1gene位于11号染色体上的一个区域，该区域在WAGR综合征患者中经常被删除，WAGR综合征是一种影响许多身体系统的疾病，并以其主要特征命名：儿童肾癌称为肾母细胞瘤，眼睛问题称为anirida，泌尿生殖系统异常和智力残疾（以前称为智力低下）。这种缺失的结果是，受影响的个体在每个细胞中缺失了一个WT1gene拷贝。该gene的丢失是泌尿生殖系统异常和受影响个体中肾母细胞瘤风险增加的原因。"}, {"name": "癌症", "detail": "WT1gene的突变可引起肾母细胞瘤，肾母细胞瘤是一种罕见的肾癌，通常发生在儿童早期。一些患有肾母细胞瘤的人在每个细胞中都有一个WT1gene拷贝的突变。其中大多数是在生殖细胞（卵和精子）形成或胎儿早期发育过程中发生的新突变，尽管有些可能是从父母那里遗传的。在其他患有Wilms肿瘤的人中，WT1gene突变仅存在于肿瘤细胞中。这些变化通常是躯体化的，这意味着它们是在一个人的一生中获得的。WT1gene突变，无论它们是体细胞的还是存在于每个细胞中，占肾母细胞瘤病例的10%至20%。（换行）WT1gene的活性（表达）的变化与几种其他形式的癌症相关。特别地，WT1gene在某些类型的肺癌，前列腺癌，乳腺癌和卵巢癌中异常表达。WT1gene的异常表达也发生在一些血液形成细胞（白血病）的癌症中，例如急性淋巴细胞白血病（ALL），慢性粒细胞白血病（CML）和儿童急性髓性白血病（AML）。尚不清楚WT1蛋白在癌症的发展或进展中起什么作用。"}, {"name": "其他疾病", "detail": "已发现WT1gene中至少有两个突变引起Meacham综合征。这种情况的特征是男性生殖器，心脏和diaphragm肌发育异常。患有这种情况的个体具有典型的男性染色体模式（46，XY），但具有外生殖器，其看起来不清楚男性或明显女性（模棱两可的生殖器）或具有完全女性的生殖器。另外，内部生殖器官是雌性的，但它们不能正常发育。患有Meacham综合征的个体通常具有从出生开始就存在的心脏缺陷，其严重程度可能有所不同。它们还在肌肉中有一个孔，将腹部与胸腔（膈肌）分开，称为先天性膈疝。Meacham综合征通常在婴儿期致命。据描述，大约有十二个人患有Meacham综合征。WT1gene中的（换行）突变也可引起称为孤立性肾病综合征的病症。这种情况的特点是肾脏无法过滤血液中的废物，导致尿液中的蛋白质，肿胀（水肿），最终导致肾功能衰竭。孤立性肾病综合征包括弥漫性肾小球硬化，其中瘢痕组织在肾脏中的微小血管（肾小球）簇中形成，以及局灶节段性肾小球硬化，其中仅肾脏的某些区域中的肾小球经历瘢痕形成。WT1gene的突变最常引起弥漫性肾小球硬化。"}]}' WHERE id = 17757;
UPDATE genes SET GHR_metadata_ch = '{"gene": "KRT10", "conditions": [{"name": "表皮松解性角化过度", "detail": "在表皮溶解性角化过度患者中发现了KRT10gene的数十种突变。这种情况的特征是早期皮肤呈红色起泡，后期皮肤厚实（角化过度）。具有KRT10gene突变的人通常患有NPS型表皮松解性角化过度，其特征是身体许多部位的皮肤较厚，而手掌或脚底则没有。(换行)大多数与表皮松解性角化过度相关的KRT10gene突变会改变角蛋白10蛋白中的单个蛋白结构单元（氨基酸）。这些氨基酸变化通常发生在蛋白质的中间丝形成中起作用的区域。突变改变了角蛋白10蛋白，似乎影响了中间丝如何相互作用形成网络。改变后的蛋白质仍会形成中间丝，但中间丝网络杂乱无章，无法正常运行。如果没有强大的网络，皮肤细胞就会变得脆弱并且容易受损，这可能导致摩擦或轻度创伤引起的起泡。目前尚不清楚这些突变如何导致角质形成细胞过度生长，从而导致皮肤角化过度。"}, {"name": "五彩纸屑鱼鳞病", "detail": "KRT10gene的突变会导致另一种皮肤疾病，称为带五彩纸屑的鱼鳞病（也称为先天性网状鱼鳞状红皮病），其特征是遍布全身的红色鳞状皮肤，并有小块正常皮肤，看起来像五彩纸屑。正常皮肤的斑块数量和大小随着年龄而增加。最初在人体每个细胞中发现的与这种情况有关的KRT10gene突变会改变用作蛋白质生产蓝图的遗传序列，从而导致异常角蛋白10蛋白质的生产。异常蛋白质的末端包括一个氨基酸精氨酸过量的区域；在正常角蛋白10蛋白中找不到这个富含精氨酸的区域。研究人员认为，这种异常的氨基酸序列将蛋白质引导到细胞核中，在细胞核中它无法形成强大的中间丝网络。该网络的丢失破坏了皮肤的外层，促进了红色，鳞状皮肤的发展。（换行）在一些异常细胞中，突变gene通过复杂的过程自我纠正，通过该过程在染色体之间交换遗传物质。结果，产生正常的角蛋白10蛋白并保留在细胞质中。细胞变得正常，并且随着其继续生长和分裂，在带五彩纸屑的鱼鳞病患者中形成正常皮肤的斑块。"}, {"name": "其他疾病", "detail": "表皮溶解性角化过度的周期性鱼鳞病是另一种由KRT10gene突变引起的皮肤疾病。这种情况类似于表皮松解性角化过度（如上所述），但是皮肤变化在短时间内消失，然后复发。复发性皮肤变化可持续数周或数月。"}]}' WHERE id = 17766;
UPDATE genes SET GHR_metadata_ch = '{"gene": "FBN1", "conditions": [{"name": "肢端发育不良", "detail": "在肢端发育不良的人群中至少鉴定出9个FBN1gene突变。这种情况的特征是身材矮小，四肢短，关节僵硬和独特的面部特征。（换行）导致肢端发育不良的FBN1gene突变位于称为外显子41和42的gene区域，并且改变称为TGF-β的原纤维蛋白-1蛋白质区域中的单个蛋白质构建块（氨基酸）？结合蛋白样结构域5。突变导致微纤维的减少和混乱。没有足够的正常微纤维来储存TGF-β？，生长因子异常活跃。这些影响可能导致肢端发育不良发生的身体异常，但机制尚不清楚。（换行）尚不清楚为什么导致肢端发育不良的FBN1gene突变导致身材矮小，而某些其他FBN1gene突变也会增加TGF-？活动会导致一种称为马凡氏综合症的疾病（见下文），其特征是身材高大。"}, {"name": "孤立的ectopia lentis", "detail": "已发现FBN1gene中的30多个突变会导致孤立的轻度外翻。在这种情况下，一只或两只眼睛的晶状体偏心（移位），这会导致视力问题。导致这种情况的大多数FBN1gene突变都会改变原纤维蛋白1蛋白中的单个氨基酸。结果，正常原纤维蛋白-1蛋白的产生减少，导致微原纤维形成减少或受损的微原纤维形成。如果没有足够的功能性微纤维将晶状体固定在眼前的中央位置，晶状体就会移位，从而导致孤立的角膜外翻和相关的视力问题。(换行)Ectopia lentis单独发生时被归类为孤立，没有影响其他身体系统的体征和症状。然而，一些最初被诊断为由FBN1gene突变引起的孤立性晶状体异位症的人后来发展出典型的称为马凡氏综合征（如下所述）的病症的其他特征，例如将血液从心脏分配到其余部分的大血管异常。身体（主动脉）。在这些情况下，诊断通常会从孤立的轻度外翻变为马凡氏综合症。"}, {"name": "马凡氏综合症", "detail": "研究人员已经鉴定出1300多种FBN1gene突变，这些突变会导致马凡氏综合症，这是一种影响支持人体关节和器官的结缔组织的疾病。结缔组织异常会导致这种疾病患者的心脏和眼睛问题。此外，受影响的人通常又高又苗条，手指和脚趾拉长以及其他骨骼异常。导致马凡氏综合症的大多数突变都会改变原纤维蛋白1蛋白中的单个氨基酸。其余的FBN1gene突变会导致异常的fibrillin-1蛋白无法正常运行。导致马凡氏综合症的FBN1gene突变会减少细胞产生的原纤维蛋白1的数量，改变原纤维蛋白1的结构或稳定性，或损害原纤维蛋白1从细胞中的转运。这些突变导致可用于形成微纤维的原纤维蛋白-1的量严重减少。没有足够的微纤维，过量的TGF-？生长因子被激活，许多组织的弹性降低，导致组织过度生长和不稳定以及马凡氏综合症的体征和症状。"}, {"name": "威尔-马切萨尼综合症", "detail": "在Weill-Marchesani综合征中也发现了FBN1gene的突变。鉴定出的突变之一删除了该gene的一部分，导致产生了不稳定形式的原纤维蛋白1蛋白。不稳定的蛋白质可能会干扰微纤维的组装。异常的微纤维会削弱结缔组织，从而导致与Weill-Marchesani综合征相关的眼睛，心脏和骨骼异常。"}, {"name": "家族性胸主动脉瘤和解剖", "detail": "遗传学家庭参考资料提供有关家族性胸主动脉瘤和解剖的信息。"}, {"name": "凝胶体发育不良", "detail": "遗传学家庭参考提供有关凝胶体发育异常的信息。"}, {"name": "Shprintzen-Goldberg综合征", "detail": "遗传学家庭参考资料提供了有关Shprintzen-Goldberg综合征的信息。"}, {"name": "其他疾病", "detail": "FBN1gene的突变会导致一种称为僵硬皮肤综合症的疾病。这种情况的特点是非常坚硬，厚实的皮肤覆盖了身体的大部分。异常的皮肤限制了运动，并可能导致称为挛缩的关节畸形，从而限制了某些关节的运动。僵硬的皮肤综合症的体征和症状通常在婴儿期至儿童中期变得明显。（换行）FBN1gene突变可引起另一种称为MASS综合征的病症。这种情况涉及身体几个部位的异常，包括二尖瓣（控制血液流过心脏的瓣膜之一），主动脉（将血液从心脏分配到身体其他部位的大血管），骨骼和皮肤。(换行)尚不清楚为什么FBN1gene中的不同突变会导致如此多种疾病。"}]}' WHERE id = 17778;
UPDATE genes SET GHR_metadata_ch = '{"gene": "HTT", "conditions": [{"name": "亨廷顿舞蹈病", "detail": "导致亨廷顿舞蹈病的遗传突变被称为CAG三核苷酸重复扩增。这种突变增加了HTTgene中CAG片段的大小。亨廷顿舞蹈病患者有36到120多个CAG重复。具有36至39个CAG重复的人可能会或可能不会出现亨廷顿病的体征和症状，而具有40个或更多重复的人几乎总是会出现这种疾病。（换行）扩展的CAG区段导致产生异常长版本的亨廷顿蛋白。细长的蛋白质被切成较小的有毒片段，这些片段结合在一起并积聚在神经元中，破坏了这些细胞的正常功能。这个过程特别影响大脑中有助于协调运动和控制思维和情绪（纹状体和大脑皮层）的区域。大脑这些区域神经元的功能障碍和最终死亡是亨廷顿舞蹈病症状和体征的基础。（换行）随着改变的HTTgene从一代传递到下一代，CAG三核苷酸重复的大小通常增加。大量重复通常与较早的体征和症状发作有关。这种现象称为预期。患有成人型亨廷顿舞蹈病（出现在成年中期）的人通常在HTTgene中具有40至50个CAG重复，而具有较少见的青少年形式的疾病（其出现在儿童期或青春期）的人倾向于有超过60个CAG重复。(换行)在HTTgene中具有27至35个CAG重复的个体不发展亨廷顿舞蹈病，但他们有患上该疾病的儿童的风险。当gene从父母传给孩子时，CAG三核苷酸重复序列的大小可能会延长到与亨廷顿舞蹈病相关的范围（36个重复或更多）。"}]}' WHERE id = 17786;
UPDATE genes SET GHR_metadata_ch = '{"gene": "CHMP2B", "conditions": [{"name": "CHMP2B相关的额颞叶痴呆", "detail": "在额颞叶痴呆患者中已发现CHMP2Bgene的一些变化。这些遗传变化中至少有两个被认为是导致该疾病的突变。尚不清楚其他遗传变化是否也引起疾病；它们可能是罕见的变异，与额颞叶痴呆的发展无关。(换行)大多数患有CHMP2B相关额颞叶痴呆的人都是一个丹麦大家庭的成员。该家族中受影响的个体具有特定的突变，写为532-1G>C，该突变改变了CHMP2Bgene中的单个DNA构建块（碱基对）。这种突变导致产生两种异常形式的带电荷的多囊泡体蛋白2B，它们都缺少C端结构域。(换行)没有C末端结构域，带电荷的多囊泡体蛋白2B作为ESCRT-III复合物的一部分不断打开（活动）。它无法与Vps4交互，因此不再需要时无法拆分复合体。结果，ESCRT-III复合物在细胞内积聚并破坏其他蛋白质的运输和降解。这些异常最终导致大脑中神经元的死亡。(换行)整个大脑中神经元的逐渐丧失是CHMP2B相关额颞叶痴呆的特征。这种疾病的许多特征是由大脑前部附近称为额叶和颞叶的区域的神经元死亡引起的。额叶参与推理，计划，判断和解决问题，而颞叶帮助处理听力，言语，记忆和情绪。目前尚不清楚为什么这种疾病的体征和症状主要与额叶和颞叶有关。"}, {"name": "肌萎缩侧索硬化症", "detail": "遗传学家庭参考提供有关肌萎缩侧索硬化的信息。"}]}' WHERE id = 17803;
UPDATE genes SET GHR_metadata_ch = '{"gene": "CSF1R", "conditions": [{"name": "成人发病的白质脑病伴轴突球状体和色素性胶质细胞", "detail": "在患有轴突球状体和色素性胶质细胞（ALSP）的成人发病性白质脑病患者中发现了十几个CSF1Rgene突变。ALSP是一种严重的神经系统疾病，其特征在于一种称为白质的脑组织受损。这种情况的症状通常始于成年，并发展为严重的认知和运动问题。ALSP中的大多数CSF1Rgene突变改变CSF-1受体中的单个蛋白质构建块（氨基酸）。其他突变以其他方式改变氨基酸序列。突变全部发生在激活其他蛋白质的受体区域（称为激酶结构域）。改变的受体可能无法刺激细胞信号传导途径。然而，尚不清楚gene突变如何导致ALSP患者的白质损伤或认知和运动问题。"}]}' WHERE id = 17808;
UPDATE genes SET GHR_metadata_ch = '{"gene": "MSH6", "conditions": [{"name": "林奇综合症", "detail": "据报道，约有10%的Lynch综合征家庭已发现MSH6gene突变。林奇综合症增加了许多类型癌症的风险，尤其是结肠癌（大肠）和直肠癌，这些癌症统称为大肠癌。Lynch综合征患者的子宫内膜癌（子宫内膜癌），卵巢癌，胃癌，小肠癌，肝癌，胆囊癌，上尿路癌和脑癌的风险也增加。子宫内膜癌在由MSH6gene突变引起的Lynch综合征女性中尤为常见。（换行）这种情况下涉及的MSH6gene突变导致异常短的，无功能的MSH6蛋白或该蛋白的部分活性形式的产生。当MSH6蛋白不存在或不起作用时，在细胞分裂过程中未修复的错误数量将大大增加。随着细胞继续分裂，错误累积，这可能导致细胞功能异常，增加结肠或身体其他部位形成肿瘤的风险。(换行)在少数人中，MSH6gene的突变会导致Lynch综合征的一种变体，称为Muir-Torre综合征。除结肠直肠癌外，患有这种疾病的人患几种罕见皮肤肿瘤的风险增加。这些罕见的皮肤肿瘤包括皮脂腺瘤和癌，它们发生在产生称为皮脂（皮脂腺）的油性物质的腺体中。通常在暴露于阳光的皮肤区域也可能发生称为角膜棘皮瘤的多个快速生长的肿瘤。"}, {"name": "卵巢癌", "detail": "遗传学家庭参考提供有关卵巢癌的信息。"}, {"name": "其他癌症", "detail": "虽然林奇综合征与一个拷贝的MSH6gene突变相关，但很少有受影响家庭中的个体继承两个MSH6gene突变，每个父母一个。在这些情况下，最常见的是在gene的两个拷贝中都发生相同的突变（纯合突变）。具有纯合MSH6gene突变的人患有不同于Lynch综合征的综合征。除结直肠癌外，还可能发展为血液癌症（白血病或淋巴瘤）。这些个体中的一些还将发展为神经纤维瘤病的特征，包括沿神经生长的非癌性肿瘤（神经纤维瘤）和称为咖啡色斑点的浅棕色皮肤斑块。这些人的结肠癌发病非常早，通常发生在儿童时期。这种涉及结肠癌，白血病或淋巴瘤以及神经纤维瘤病的综合征有时称为结肠。"}]}' WHERE id = 17814;
UPDATE genes SET GHR_metadata_ch = '{"gene": "G6PC", "conditions": [{"name": "I型糖原贮积病", "detail": "已发现G6PCgene中至少有85个突变会导致Ia型糖原贮积病（GSDIa）。这些突变大多数会改变葡萄糖6磷酸酶中的单个蛋白质结构单元（氨基酸）。某些特定的突变在某些种族或种族群体中更常见。G6PCgene中的突变会损害葡萄糖6磷酸酶的功能。当该酶不能正常工作时，葡萄糖6-磷酸不会分解，也不会产生葡萄糖。未分解成葡萄糖的葡萄糖6-磷酸转化为脂肪和糖原，糖原是储存在细胞内的复合糖。储存在细胞内的过多脂肪和糖原可能是有毒的。这种累积会损害整个身体的器官和组织，特别是肝脏和肾脏，导致GSDIa的体征和症状。"}]}' WHERE id = 17857;
UPDATE genes SET GHR_metadata_ch = '{"gene": "PDGFRB", "conditions": [{"name": "家族性特发性基底节钙化", "detail": "已发现PDGFRBgene中至少有两个突变会导致家族性特发性基底神经节钙化（FIBGC）。这种情况的特征是大脑中钙的异常沉积（钙化），这可能导致运动和精神问题。这些突变改变了PDGFR中的单个蛋白质结构单元（氨基酸）？蛋白质。尚不清楚PDGFRBgene突变如何引起FIBGC。突变可能会改变排列在大脑血管中的细胞内的信号传导，导致它们摄入过量的钙，并导致这些血管内壁钙化。或者，PDGFR的变化？蛋白质可以改变磷酸盐转运信号传导途径，导致磷酸盐水平升高。在大脑中，过量的磷酸盐与钙结合形成沉积物。(换行)PDGFRBgene在整个身体中都是活跃的（表达的）；目前尚不清楚为什么这些突变的影响仅限于基底神经节和FIBGC涉及的其他大脑区域。"}, {"name": "PDGFRB相关的慢性嗜酸性粒细胞白血病", "detail": "涉及PDGFRBgene的遗传重排（易位）引起一种称为PDGFRB相关的慢性嗜酸性粒细胞白血病的造血细胞癌症。这种情况的特点是嗜酸性粒细胞（一种白细胞）数量增加。这些易位中最常见的是将PDGFRBgene的一部分与另一个称为ETV6的gene结合在一起，该gene的功能是关闭gene活性。这些片段一起创建了ETV6-PDGFRB融合gene。有时，ETV6以外的gene与PDGFRBgene融合。导致这些融合gene的易位是体细胞突变，其在人的一生中获得并且最初发生在单个细胞中。该细胞继续生长和分裂，产生一组具有相同突变的细胞（克隆群体）。（换行）由ETV6-PDGFRB融合gene（以及其他PDGFRB融合gene）产生的蛋白质的功能不同于通常由单个gene产生的蛋白质。ETV6/PDGFR？融合蛋白不需要激活配体结合，也不能与DNA结合以关闭gene活性。结果，信号传导途径不断开启（组成性激活）并且gene活性增加，这增加了细胞的增殖和存活。（换行）当ETV6-PDGFRB融合gene突变发生在发育成血细胞的细胞中时，嗜酸性粒细胞（偶尔还有其他白细胞，如嗜中性粒细胞和肥大细胞）的生长控制不佳，导致PDGFRB相关慢性嗜酸性粒细胞白血病。目前还不清楚为什么嗜酸性粒细胞优先受到这种遗传变化的影响。"}]}' WHERE id = 17874;
UPDATE genes SET GHR_metadata_ch = '{"gene": "CP", "conditions": [{"name": "无铜蓝蛋白血症", "detail": "已经发现CPgene中的突变引起铜蓝蛋白血症，铜蓝蛋白血症是一种以铁蓄积，神经功能障碍和其他健康问题为特征的疾病，其由于异常短，不稳定或无功能的铜蓝蛋白形式的产生而导致功能受损。"}]}' WHERE id = 17903;
UPDATE genes SET GHR_metadata_ch = '{"gene": "CYP1B1", "conditions": [{"name": "早发性青光眼", "detail": "已鉴定出140多种CYP1B1gene突变引起早发性青光眼。患有这种疾病的人在40岁之前会在眼内承受更大的压力。该压力导致连接眼睛和大脑的视神经受损。5岁之前的青光眼患者（原发性先天性青光眼）中有20%至40%的CYP1B1gene突变。导致早发性青光眼的突变可能导致酶不稳定或形状错误。(换行)尚不清楚CYP1B1酶的缺陷如何引起青光眼的体征和症状。最近的研究表明，这些缺陷可能会干扰小梁网的早期发育，小梁网是网状运河的网络，有助于从眼睛排出多余的液体。如果液体不能排出，眼内压力可能会增加，这是青光眼的特征。(换行)CYP1B1酶可能与另一种由MYOCgene产生的称为myocilin的蛋白质相互作用。与仅在一个gene中发生突变的人相比，在MYOC和CYP1B1gene中均发生突变的人可能会在更早的年龄发展为青光眼，并具有更严重的症状。"}, {"name": "彼得斯异常", "detail": "已发现CYP1B1gene中的一些突变会导致Peters异常。这种情况的特征是眼睛前部某些结构异常发育，眼睛清晰的前表面（角膜）混浊。导致彼得斯异常的突变可能会损害CYP1B1酶的正常功能或破坏酶的产生。结果，正常酶缺乏（缺乏）。CYP1B1酶的缺乏可能会破坏眼睛的正常发育，尽管目前尚不清楚这种缺乏究竟如何导致Peters异常。"}]}' WHERE id = 17905;
UPDATE genes SET GHR_metadata_ch = '{"gene": "PCNT", "conditions": [{"name": "II型小头畸形骨发育不良原始侏儒症", "detail": "已发现PCNTgene中至少有30个突变会导致II型小头畸形骨发育不良原始侏儒症（MOPDII）。这些突变导致产生异常短的，无功能的pericentrin蛋白，该蛋白不能将其他蛋白锚定在中心体上。结果，中心体不能正确组装微管，导致细胞周期和细胞分裂的破坏。受损的细胞分裂导致细胞产生减少，而细胞周期的破坏可导致细胞死亡。细胞数量的总体减少导致骨骼短，小头畸形以及MOPDII的其他体征和症状。"}, {"name": "前列腺癌", "detail": "遗传学家庭参考提供有关前列腺癌的信息。"}, {"name": "癌症", "detail": "gene突变可以在一个人的一生中获得，并且仅存在于某些细胞中。这些突变称为体细胞突变，它们不是遗传的。PCNTgene中的体细胞突变可导致pericentrin蛋白产量增加。在实体瘤（包括前列腺肿瘤）以及造血细胞癌（白血病和淋巴瘤）中发现了pericentrin水平升高。中心体中更多的pericentrin导致细胞周期过度活化和细胞分裂增加。这种异常的细胞生长和分裂最终可能导致癌性肿瘤。"}, {"name": "其他疾病", "detail": "PCNTgene中的某些常见遗传变异（多态性）与某些个体患精神病（如精神分裂症和抑郁症）的风险增加有关。同样，在一些双相情感障碍患者中发现了pericentrin水平升高。尚不清楚PCNTgene的变化或pericentrin蛋白水平的升高如何与这些疾病相关。大量的遗传和环境因素，其中大多数仍然未知，可能决定了发展这些复杂疾病的风险。"}]}' WHERE id = 17906;
UPDATE genes SET GHR_metadata_ch = '{"gene": "TRPM6", "conditions": [{"name": "低镁血症伴继发性低钙血症", "detail": "已发现TRPM6gene中至少38个突变会导致低镁血症和继发性低钙血症。这种情况的特征是体内镁（低镁血症）和钙（低钙血症）含量低，从而导致婴儿期开始的神经系统问题，包括肌肉痉挛和癫痫发作。TRPM6gene突变导致缺乏功能蛋白。(换行)无功能的TRPM6通道阻止Mg2+在肠道中的吸收，并导致过多的Mg2+在尿液中释放。血液中缺乏Mg2+会损害甲状旁腺激素的产生，这可能会降低血液中的Ca2+水平。另外，低镁血症和低钙血症可破坏许多细胞过程并损害运动神经元的功能，导致这种病症特有的神经问题和运动障碍。如果病情没有得到有效治疗并且低Mg2+水平持续存在，体征和症状会随着时间的推移而恶化并可能导致早期死亡。"}]}' WHERE id = 17910;
UPDATE genes SET GHR_metadata_ch = '{"gene": "EP300", "conditions": [{"name": "鲁宾斯坦-泰必氏综合症", "detail": "在鲁宾斯坦-泰必氏综合症患者中已鉴定出EP300gene的几种突变。这些遗传变化是造成这种情况的一小部分病例的原因。一些突变导致产生异常小的，无功能的p300蛋白，而其他突变则阻止该gene的一个拷贝根本不产生任何蛋白。这些遗传变化都会导致每个细胞中EP300gene的一个功能拷贝丢失，从而使p300蛋白的量减少一半。尽管研究人员不确定这种蛋白质的减少如何导致Rubinstein-Taybi综合征的特定特征，但很明显，丢失一个拷贝的EP300gene会破坏出生前后的正常发育。"}, {"name": "前列腺癌", "detail": "遗传学家庭参考提供有关前列腺癌的信息。"}, {"name": "癌症", "detail": "很少有涉及22号染色体的染色体重排（易位）与某些类型的癌症有关。这些遗传变化是体细胞的，这意味着它们是在一个人的一生中获得的，并且仅存在于某些细胞中。在癌细胞中，易位可破坏含有EP300gene的22号染色体区域。例如，研究人员在几名患有称为急性髓性白血病（AML）的造血细胞癌患者中发现了8号染色体和22号染色体之间的易位。在少数接受过癌症治疗的人中发现了另一种涉及11号和22号染色体的易位。这种染色体变化与其他形式癌症化疗后AML的发展有关。（换行）已经在几种其他类型的癌症中鉴定了EP300gene中的体细胞突变。这些突变阻止gene产生任何功能蛋白。没有p300蛋白的细胞不能有效抑制生长和分裂，使癌性肿瘤发展和生长。EP300gene的体细胞突变已在少数实体瘤中发现，包括结肠癌和直肠癌，胃癌，乳腺癌和胰腺癌。研究表明，EP300突变也可能在某些前列腺癌的发展中起作用。这些遗传变化可以帮助预测前列腺肿瘤是否会增大或扩散到身体的其他部位。"}]}' WHERE id = 17928;
UPDATE genes SET GHR_metadata_ch = '{"gene": "CDKN1B", "conditions": [{"name": "多发性内分泌肿瘤", "detail": "已发现CDKN1Bgene中至少有8个突变会引起相对罕见的多发性内分泌肿瘤，称为4型。多发性内分泌肿瘤通常涉及两个或多个身体产生激素的腺体（称为内分泌腺）中肿瘤的发展。这些肿瘤可以是非癌性的或癌性的。在4型多发性内分泌肿瘤中受影响的最常见的内分泌腺是甲状旁腺和垂体，尽管也可能涉及其他内分泌腺和其他器官。（换行）导致多发性内分泌肿瘤4型的大多数CDKN1Bgene突变改变p27蛋白中的单个蛋白质构建块（氨基酸）。一些突变削弱了蛋白质与调节蛋白质相互作用的能力，而另一些则导致产生不稳定形式的p27，并迅速分解。还有其他突变阻止p27从细胞质移动到细胞核中。所有这些突变都减少了细胞核中可用于调节细胞周期的功能性p27的数量。缺乏功能性p27的细胞分裂过快或无法控制，形成肿瘤。目前还不清楚为什么这些肿瘤主要发生在内分泌腺中；研究表明，某些内分泌细胞可能特别依赖于p27蛋白来控制细胞分裂。"}, {"name": "其他肿瘤", "detail": "还发现CDKN1Bgene的突变会在单个内分泌腺（例如垂体或甲状旁腺）中引起肿瘤。这些突变被描述为体细胞突变；它们不是遗传的，仅存在于肿瘤细胞中。体细胞CDKN1Bgene突变已在散发性（非家族性）垂体腺瘤和散发性甲状旁腺腺瘤中发现，它们是甲状旁腺的非癌性肿瘤。像引起4型多发性内分泌肿瘤的突变一样，这些遗传变化减少了可用于控制细胞分裂的功能性p27的数量。结果，细胞变得更可能异常分裂并形成肿瘤。（换行）除了多发性内分泌肿瘤外，CDKN1Bgene在癌性肿瘤中很少发生突变。然而，在许多类型的癌症中发现了减少细胞核中p27蛋白的量或功能的调节变化。p27的缺乏与更具侵袭性的肿瘤和更差的预后有关。"}]}' WHERE id = 17947;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ACVRL1", "conditions": [{"name": "遗传性出血性毛细血管扩张", "detail": "ACVR1gene中的几个突变与2型遗传性出血性毛细血管扩张有关，这种疾病的特征是蛋白质缺陷或无功能，干扰了动脉和静脉之间边界的发展。"}, {"name": "肺动脉高压", "detail": "ACVR1gene中的几个突变与肺动脉高压有关，肺动脉高压是一种进行性疾病，其特征是由于对通过肺的血流的抵抗力增加，从而损害了心脏的右心室。这种疾病的症状包括运动时呼吸急促（呼吸困难）和晕厥，头晕，脚踝或腿部肿胀（水肿），胸痛和心率加快。"}, {"name": "其他疾病", "detail": "人们认为ACVRL1中的遗传多态性（IVS3-35 A>G）会影响大脑中血管的发育，从而导致没有其他遗传性出血性毛细血管扩张症状的人动静脉畸形。"}]}' WHERE id = 17982;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ACP5", "conditions": [{"name": "伴有免疫失调的脊椎软骨发育不良", "detail": "ACP5gene中的几个突变与具有免疫失调（SPENCDI）的脊椎软骨发育不良有关，这种疾病的特征是骨骼生长和免疫系统功能异常。突变会削弱或消除TRAP灭活骨桥蛋白的能力，从而导致其异常激活。这导致破骨细胞破坏骨骼，导致骨骼形状不规则，身材矮小，对自身免疫性疾病的敏感性增加，频繁感染以及其他特征，例如运动障碍和智力障碍。"}]}' WHERE id = 18000;
UPDATE genes SET GHR_metadata_ch = '{"gene": "HEXB", "conditions": [{"name": "山德霍夫病", "detail": "在HEXBgene中已鉴定出约30种引起Sandhoff病的突变。这些突变降低或消除了β-己糖胺酶A和β-己糖胺酶B的活性。故障或缺失的酶无法分解GM2神经节苷脂和其他分子，从而使这些化合物在细胞内积累。GM2神经节苷脂水平升高对中枢神经系统的神经细胞特别有毒。过量的GM2神经节苷脂导致这些细胞的进行性破坏，这导致Sandhoff病的许多特征。(换行)HEXBgene中大多数已知的突变都会导致严重的Sandhoff病，这在婴儿期就很明显。这些突变阻止细胞产生任何β-己糖胺酶A或β-己糖胺酶B，或导致产生这些酶的完全无功能的形式。最常见的突变会删除HEXBgene开头附近的大部分DNA，从而导致酶活性完全丧失。其他突变减少但不消除酶的活性；这些遗传变化是造成生命后期出现的较不严重的桑德霍夫病的原因。"}]}' WHERE id = 18004;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ALG1", "conditions": [{"name": "ALG1先天性糖基化疾病", "detail": "已经发现ALG1gene的突变会引起ALG1先天性糖基化障碍（ALG1-CDG），这是一种拥有属性智能障碍、发育迟缓、肌张力弱（低张力）以及其他影响许多身体系统的体征和症状，因为有缺陷/异常的酶会损害糖基化过程。"}]}' WHERE id = 18012;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SMOC1", "conditions": [{"name": "眼-肩峰综合征", "detail": "已发现SMOC1gene中至少有12个突变会引起眼顶肌综合征，这种情况会导致眼睛，手和脚的畸形。这些突变大多数可能导致SMOC-1蛋白功能异常。SMOC-1的丢失可能会破坏生长因子信号传导，从而损害骨骼，四肢和眼睛的正常发育。这些变化可能是眼-肩峰综合征的体征和症状的基础。"}]}' WHERE id = 18043;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ST3GAL5", "conditions": [{"name": "GM3合酶缺乏症", "detail": "已发现ST3GAL5gene中至少有一个突变会导致GM3合酶缺乏症，这种疾病的特征是反复发作（癫痫）和大脑发育问题。已知的突变用信号取代了单个蛋白质结构单元（氨基酸）精氨酸，从而阻止了蛋白质的过早生产。突变写为Arg288Ter或R288X，尽管在较旧的科学文章中有时写为Arg232Ter或R232X。该突变阻止了任何功能性GM3合酶的产生。没有这种酶，细胞就不能正常产生GM3或其他神经节苷脂。尚不清楚该酶的丧失如何导致GM3合酶缺乏症的体征和症状。研究人员正在努力确定是否缺乏神经节苷脂或用于制造神经节苷脂的化合物的积累，或两者兼而有之，这是癫痫发作和这种情况下发生的大脑发育的其他问题的基础。"}]}' WHERE id = 18074;
UPDATE genes SET GHR_metadata_ch = '{"gene": "TP63", "conditions": [{"name": "甲状结肠外胚层缺损唇腭裂综合征", "detail": "在患有强直性睑外胚层缺损-唇裂/pal裂（AEC）综合征的人中，已经鉴定出TP63gene中至少40个突变。这种情况是外胚层发育不良的一种形式，它是一组以皮肤，头发，指甲，牙齿和汗腺异常发育为特征的疾病。AEC综合征的其他特征包括上眼睑和下眼睑（ankyloblepharon filiforme adnatum）的部分或完全融合以及口腔顶部的开口（腭裂），唇裂（唇裂）或两者。（换行）负责AEC综合征的大多数TP63gene突变发生在称为无菌α基序（SAM）结构域和反式激活抑制（TI）结构域的p63蛋白区域中。这些区域的突变会干扰p63在正确的时间打开和关闭靶gene的能力。但是，尚不清楚这些变化如何导致外胚层发育异常和AEC综合征的具体特征。"}, {"name": "其他疾病", "detail": "TP63gene的突变会导致其他一些外胚层发育异常，其特征与AEC综合征重叠。这些情况包括外胚层，外胚层发育不良，裂口（EEC）综合征；acro-dermato-ungual-lacrimal-tooth（成人）综合征；和肢体乳房综合征（LMS）。这组疾病的特征是外胚层异常（影响皮肤，头发，指甲，牙齿和汗腺），唇裂和/或c裂以及手脚畸形的各种组合。还发现TP63gene中的（换行）突变会导致4型手足裂畸形（SHFM4），这种情况涉及手足畸形而没有任何其他体征或症状。另外，TP63gene突变是发生唇裂和/或c裂的罕见原因，而没有影响身体其他部位的特征。（换行）负责这些病症的TP63gene突变发生在TP63gene的不同区域，并以不同方式影响p63蛋白的功能。一些已知的突变可能会给p63蛋白带来新的异常功能（称为“功能获得”突变）或导致干扰正常细胞活性的p63蛋白版本（称为“显性负性”突变）。这些变化改变了p63与其他蛋白质相互作用，在正确的时间打开和关闭靶gene或两者兼而有之的能力。目前尚不清楚异常的p63活性如何破坏外胚层发育并导致TP63相关病症的特定特征。"}]}' WHERE id = 18126;
UPDATE genes SET GHR_metadata_ch = '{"gene": "HAX1", "conditions": [{"name": "严重的先天性中性粒细胞减少症", "detail": "已发现HAX1gene中至少有10个突变会导致严重的先天性中性粒细胞减少症，这种疾病的特征是中性粒细胞缺乏（缺乏）。中性粒细胞是一种白细胞，在炎症和抗感染中起作用。导致严重先天性中性粒细胞减少的大多数突变都会改变HAX-1蛋白中的单个蛋白结构单元（氨基酸）。导致严重先天性中性粒细胞减少的HAX1gene突变导致产生无功能的HAX-1蛋白。缺乏功能性HAX-1蛋白会破坏细胞凋亡的调节，导致嗜中性粒细胞过早死亡。中性粒细胞缺乏会导致严重先天性中性粒细胞减少症患者反复感染，炎症发作和其他免疫问题。(换行)具有某些HAX1gene突变的人除了严重的先天性中性粒细胞减少症外，还存在神经系统问题，例如癫痫发作和发育迟缓。这些突变破坏了HAX-1蛋白的两个可变剪接版本。仅导致严重的先天性中性粒细胞减少症的gene突变仅破坏HAX-1蛋白的一种版本。目前尚不清楚非功能性HAX-1蛋白如何引起某些受影响个体的神经系统特征。"}]}' WHERE id = 18128;
UPDATE genes SET GHR_metadata_ch = '{"gene": "TSPYL1", "conditions": [{"name": "婴儿猝死伴睾丸发育不全综合征", "detail": "TSPYL1gene的单个突变已导致所有已确定的婴儿猝死病例，并伴有睾丸综合征（SIDDT）发育不全，这种情况已在宾夕法尼亚州的Old Order Amish社区中报道。这种情况在生命的第一年是致命的；它的主要特征包括男性生殖系统异常，呼吸问题，心率缓慢或不均匀以及进食困难。（换行）导致SIDDT的突变将单个DNA构建块（核苷酸）插入TSPYL1gene。此突变写为457_458insG。额外的核苷酸改变了gene指令如何用于制造类似TSPY的1，这导致产生异常短的无功能蛋白质。TSPY样1功能的丧失似乎破坏了男性生殖系统和大脑（包括脑干）的正常发育。脑干功能异常，特别是涉及呼吸和心率的异常，可能是受影响婴儿猝死的原因。(换行)研究结果表明，TSPYL1gene的突变与普通人群的婴儿猝死综合症（SIDS）无关。小岛屿发展中国家是1岁以下儿童死亡的主要原因。"}, {"name": "其他疾病", "detail": "已经研究了TSPYL1gene的变化作为不育的潜在原因，这是无法生育孩子的原因。已经在几个不育男性和至少一个遗传男性（每个细胞中有一个X染色体和一个Y染色体）但具有女性外生殖器的人中鉴定出该gene的变异。但是，尚不清楚TSPYL1gene变异是否与这些异常有关或是偶然发生的。TSPYL1gene的变化不被认为是不育的主要原因。"}]}' WHERE id = 18133;
UPDATE genes SET GHR_metadata_ch = '{"gene": "NF1", "conditions": [{"name": "1型神经纤维瘤病", "detail": "已经鉴定出导致1型神经纤维瘤病的1000多个NF1突变。这些突变大多数是特定家族独有的。许多NF1突变导致产生极短版本的神经纤维蛋白。这种缩短的蛋白质不能发挥抑制细胞分裂的正常工作。当雪旺氏细胞中NF1gene的两个拷贝都发生突变时，由此导致的神经纤维蛋白损失将形成称为神经纤维瘤的非癌性肿瘤。研究表明，神经纤维瘤的形成需要雪旺氏细胞与其他细胞（包括肥大细胞）的相互作用。肥大细胞通常参与伤口愈合和组织修复。"}, {"name": "肺癌", "detail": "遗传学家庭参考提供有关肺癌的信息。"}, {"name": "癌症", "detail": "在极少数情况下，每个细胞中一个拷贝的NF1gene失活会增加患少年骨髓单核细胞白血病（JMML）的风险。少年骨髓单核细胞白血病是血液形成组织的癌症，通常发生在2岁以下的儿童中。这种情况导致骨髓产生过多的未成熟白细胞，不能发挥其正常的抗感染功能。这些异常细胞可以积聚在血液和骨髓中，为健康的白细胞，红细胞和血小板留出更少的空间。受此疾病影响的儿童可能会感到疲劳，发烧，容易出血或瘀伤。"}]}' WHERE id = 18152;
UPDATE genes SET GHR_metadata_ch = '{"gene": "POFUT1", "conditions": [{"name": "Dowling-Degos病", "detail": "已发现POFUT1gene中至少有13个突变引起Dowling-Degos病。这种情况会导致各种皮肤异常，包括异常深色肤色（色素沉着过度）的特征性花边图案，这种花边图案最常发生在身体的褶皱和折痕中。（换行）导致Dowling-Degos病的POFUT1gene突变导致异常短的蛋白质无功能或改变单个蛋白质结构单元（氨基酸），导致蛋白质功能的部分丧失。结果，蛋白O-岩藻糖基转移酶1不能或不能将岩藻糖分子添加到Notch受体。没有这些糖分子，Notch受体就不能与其配体结合，Notch途径也会停止。由于Notch通路的多种功能影响许多身体系统，Dowling-Degos病仅影响皮肤，因此尚不清楚这种病症的体征和症状是由于Notch信号受损还是蛋白O-岩藻糖基转移酶未知功能的破坏1在黑素细胞或其他皮肤细胞中。"}]}' WHERE id = 18179;
UPDATE genes SET GHR_metadata_ch = '{"gene": "HAL", "conditions": [{"name": "组氨酸血症", "detail": "已发现HALgene中至少有四个突变引起组织蛋白血症。所有这些突变改变了组氨酸酶中的单个氨基酸。这些突变被认为降低或消除了酶的活性，导致无法分解组氨酸。未分解的组氨酸会积聚在血液中，但通常不会引起健康问题。"}]}' WHERE id = 18190;
UPDATE genes SET GHR_metadata_ch = '{"gene": "COL4A1", "conditions": [{"name": "COL4A1相关的脑小血管疾病", "detail": "已经发现COL4A1gene的突变引起COL4A1相关的脑小血管疾病。这种情况是一组称为COL4A1相关疾病的疾病的一部分，这些疾病的体征和症状重叠，涉及脆弱的血管。与COL4A1相关的脑小血管疾病的特征是中风和眼部异常。大多数已确定的导致COL4A1相关的脑小血管疾病的COL4A1gene突变都会改变用于制造IV型胶原蛋白alpha1（IV）链的蛋白质结构单元（氨基酸）之一。具体而言，突变在该胶原链的各个位置之一处用不同的氨基酸替换了氨基酸甘氨酸。α1（IV）链中另一个氨基酸被甘氨酸取代可防止该链与其他链结合形成完整的IV型胶原分子。IV型胶原蛋白的这种改变阻止了蛋白质网络的形成和基底膜的正常发育，从而导致它们支持的组织减弱。全身血管变得脆弱，导致与COL4A1相关的脑小血管疾病的体征和症状。(换行)目前尚不清楚COL4A1gene的突变如何导致不同的疾病。该gene不同区域的突变可能在COL4A1相关疾病的各种体征和症状的发展中起作用。其他遗传变化以及环境因素也可能导致不同的COL4A1相关疾病的特征。"}, {"name": "家族性毛孔性脑病", "detail": "已经发现COL4A1gene的突变会引起家族性孔脑病，这是一组称为COL4A1相关疾病的血管疾病的另一成员。家族性孔脑病的特征是早期中风和脑囊肿。大多数已鉴定出的导致家族性孔脑病的COL4A1gene突变会改变用于制造IV型胶原蛋白alpha1（IV）链的氨基酸之一。具体而言，突变在该胶原链的各个位置之一处用不同的氨基酸替换了氨基酸甘氨酸。α1（IV）链中另一个氨基酸被甘氨酸取代可防止该链与其他链结合形成完整的IV型胶原分子。IV型胶原蛋白的这种改变阻止了蛋白质网络的形成和基底膜的正常发育，从而导致它们支持的组织减弱。大脑中的血管变得脆弱，导致家族性孔脑病的体征和症状。"}, {"name": "遗传性血管病伴肾病，动脉瘤和肌肉痉挛综合征", "detail": "已发现COL4A1gene的突变会导致遗传性血管病，并伴有肾病，动脉瘤和肌肉痉挛（HANAC）综合征，这是一组称为COL4A1相关疾病的疾病的第三个成员。HANAC综合征的特征是大脑和整个身体的血管减弱，肾脏疾病，肌肉痉挛和眼睛异常。导致HANAC综合征的大多数已鉴定的COL4A1gene突变都会改变用于制造IV型胶原蛋白alpha1（IV）链的氨基酸之一。具体而言，突变在该胶原链的各个位置之一处用不同的氨基酸替换了氨基酸甘氨酸。α1（IV）链中另一个氨基酸被甘氨酸取代可防止该链与其他链结合形成完整的IV型胶原分子。IV型胶原蛋白的这种改变阻止了蛋白质网络的形成和基底膜的正常发育。结果，它们支持的组织减弱，特别是在眼睛和肾脏中。脆弱的血管和基底膜变弱会导致HANAC综合征的体征和症状。"}]}' WHERE id = 18191;
UPDATE genes SET GHR_metadata_ch = '{"gene": "COQ2", "conditions": [{"name": "初级辅酶Q10缺乏症", "detail": "已经发现COLQgene的突变会导致原发性辅酶Q10缺乏，这是一种罕见的疾病，通常在婴儿期或儿童早期就变得明显；但可以发生在任何年龄，并且最常影响大脑，肌肉和肾脏。这是由于突变大大减少或消除了COQ2酶的产生并损害了辅酶Q10的功能和产生而引起的。"}, {"name": "多系统萎缩", "detail": "COQ2gene的突变与多系统萎缩的风险增加有关，多系统萎缩是一种进行性脑部疾病，会影响运动，平衡并破坏自主神经系统的功能。这是由于gene中的氨基酸变化极大地损害了脑中辅酶Q10的功能和产生。"}]}' WHERE id = 18201;
UPDATE genes SET GHR_metadata_ch = '{"gene": "NGLY1", "conditions": [{"name": "NGLY1先天性去糖基化疾病", "detail": "已发现NGLY1gene中至少有13个突变会导致NGLY1先天性去糖基化疾病（NGLY1-CDDG）。这种情况会影响许多身体系统，导致发育迟缓，运动异常，肝功能问题，眼睛异常以及眼泪减少或不存在（低泪或泪斑）。这些突变削弱了N-聚糖酶1酶的产生，导致该酶功能的严重降低或缺失。如果不去除聚糖，某些错误折叠的蛋白质将无法分解。据认为，异常蛋白质在细胞中积累并形成团块（聚集体）。这些聚集体可能会损害大脑，肝脏和眼睛中的细胞，从而导致NGLY1-CDDG的体征和症状。"}]}' WHERE id = 18239;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ASXL1", "conditions": [{"name": "Bohring-Opitz综合征", "detail": "已发现ASXL1gene中至少有18个突变会引起Bohring-Opitz综合征，这种疾病的特征是头部大小和形状异常，面部特征明显，关节异常，智力残疾以及其他体征和症状。导致Bohring-Opitz综合征的大多数ASXL1gene突变会在制造ASXL1蛋白的说明中产生过早的终止信号，从而导致异常短的无功能蛋白。这些ASXL1gene突变被描述为“功能丧失”，因为它们减少了可用的功能性ASXL1蛋白的量，这可能会破坏发育过程中HOXgene和其他gene活性的调节。这些gene活性的改变可能导致这种情况的神经和物理特征。"}, {"name": "癌症", "detail": "已经发现ASXL1gene的突变与血液形成细胞（白血病）的癌症有关，例如急性髓性白血病，慢性粒单核细胞白血病和骨髓增生异常综合症。这些突变是体细胞的，这意味着它们是在一个人的一生中获得的，并且仅存在于引起癌症的细胞中。（换行）与癌症相关的突变很可能是“功能获得”，这意味着它们会导致ASXL1蛋白过度活跃。研究人员认为，过度活跃的ASXL1蛋白导致gene活性调节不良。目前尚不清楚这种改变的gene调控如何在白血病的发展中发挥作用，但是过度活跃的gene很可能通过允许异常血细胞不受控制地生长和分裂来促进癌症的生长。"}]}' WHERE id = 18250;
UPDATE genes SET GHR_metadata_ch = '{"gene": "PROS1", "conditions": [{"name": "蛋白S缺乏症", "detail": "已发现PROS1gene中的220多个突变导致蛋白S缺乏。这些突变大多数会改变蛋白质S中的单个蛋白质结构单元（氨基酸），从而破坏其充当辅因子的能力。根据PROS1gene的突变，蛋白S缺乏症可分为三种类型。（换行）I型蛋白S缺乏症是由导致蛋白S水平降低的PROS1gene突变引起的。受影响的个体没有足够的蛋白S来控制血液凝固，这导致蛋白S缺乏中异常血凝块的风险增加。（换行）导致II型蛋白S缺乏的突变导致产生活性降低的蛋白S改变。患有这种疾病的个体的蛋白S水平正常，但该蛋白无法与参与凝血的其他分子相互作用。如果蛋白S不能正常工作，可能会形成异常的血凝块。(换行)当游离蛋白S含量低但蛋白S总量正常时，就会发生III型蛋白S缺乏症。因为游离蛋白S是比结合蛋白S更有效的辅因子，所以游离蛋白S水平的降低可以破坏凝血蛋白的失活。"}]}' WHERE id = 18251;
UPDATE genes SET GHR_metadata_ch = '{"gene": "EHMT1", "conditions": [{"name": "Kleefstra综合征", "detail": "Kleefstra综合征是一种影响身体许多部位的疾病，是由EHMT1gene缺失或突变导致其功能失效引起的。(换行)大多数患有Kleefstra综合征的人在每个细胞的9号染色体的一个拷贝上缺少约100万个DNA构建块（碱基对）的序列。缺失发生在染色体长（q）臂末端附近指定为q34.3的位置，该位置包含EHMT1gene。一些受影响的个体在同一区域具有较短或较长的缺失。（换行）每个细胞中9号染色体一个拷贝中EHMT1gene的丢失被认为是9q34.3缺失患者Kleefstra综合征特征的原因。但是，同一区域中其他gene的丢失可能会在某些受影响的个体中导致其他健康问题。(换行)约25%的Kleefstra综合征患者没有9号染色体遗传物质的缺失；相反，这些人的EHMT1gene有突变。这些突变中的一些改变了常染色体组蛋白甲基转移酶1中的单个蛋白质结构单元（氨基酸）。其他人在制造酶的指令中创建过早的停止信号或改变gene指令拼接在一起以产生酶的方式。这些变化通常会导致酶不稳定并迅速衰减，或者被禁用且无法正常运行。（换行）影响EHMT1gene的缺失或突变导致缺乏功能性常染色体组蛋白甲基转移酶1酶。缺乏这种酶会损害人体许多器官和组织中某些gene活性的适当控制，从而导致Kleefstra综合征的发育和功能特征异常。"}]}' WHERE id = 18285;
UPDATE genes SET GHR_metadata_ch = '{"gene": "UNC80", "conditions": [{"name": "UNC80缺陷", "detail": "在UNC80缺乏症患者中至少鉴定出10个UNC80gene突变，这种疾病会导致严重的神经系统和发育问题，从出生或婴儿早期就很明显。患有这种疾病的人中发生的突变导致缺少UNC80蛋白或产生异常蛋白。功能性UNC80蛋白的缺乏会损害NALCN通道体的稳定性和功能。人们认为神经元兴奋性因此受到不适当的调节，但尚不清楚这些变化如何导致UNC80缺乏症的特定特征。"}]}' WHERE id = 18287;
UPDATE genes SET GHR_metadata_ch = '{"gene": "TCOF1", "conditions": [{"name": "Treacher Collins综合征", "detail": "在Treacher Collins综合征患者中已鉴定出TCOF1gene的约200个突变，该疾病会影响骨骼和面部其他组织的发育。这些突变中的大多数会在TCOF1gene中插入或删除少量DNA构建块（碱基对），从而导致细胞中功能性糖蜜的数量减少。结果，rRNA的产生减少，这可能触发参与面部骨骼和组织早期发育的某些细胞的自我破坏（凋亡）。研究人员认为，这种异常的细胞死亡可能导致Treacher Collins综合征中发现的面部发育特定问题。但是，尚不清楚为什么减少rRNA的作用仅限于面部发育。"}]}' WHERE id = 18292;
UPDATE genes SET GHR_metadata_ch = '{"gene": "HMBS", "conditions": [{"name": "卟啉症", "detail": "在患有一种称为急性间歇性卟啉症的卟啉症患者中，已经鉴定出HMBSgene的300多个突变。这些突变中的一些改变了羟甲基胆烷合酶中的单个蛋白质结构单元（氨基酸）。其他突变会在HMBSgene内添加或删除遗传物质，从而改变酶的结构和功能。HMBSgene中的（换行）突变降低了羟甲基胆烷合酶的活性，使称为卟啉的化合物在肝脏和其他器官中积聚。这些化合物是在血红素生产的正常过程中形成的，但是羟甲基胆烷合酶活性的降低使它们积累到毒性水平。这种累积与某些药物，酒精，吸烟和节食等非遗传因素相结合，导致急性间歇性卟啉症患者出现严重腹痛和其他症状。"}]}' WHERE id = 18318;
UPDATE genes SET GHR_metadata_ch = '{"gene": "VPS13A", "conditions": [{"name": "舞蹈症acanthocytosis", "detail": "已发现VPS13Agene中约有75个突变引起舞蹈症棘皮细胞增多症。这些突变中的大多数会插入或删除VPS13Agene中一个或多个DNA（核苷酸）构建基块。gene中的这些变化导致异常小的，无功能的舞蹈蛋白的产生。尽管法裔加拿大人或日本人患有舞蹈症棘皮细胞增多症的人倾向于具有特定于其人群的突变，但许多突变特定于单个家庭。目前尚不清楚为什么VPS13Agene的突变仅影响大脑和红细胞，从而引起舞蹈症棘皮细胞增多症的体征和症状。"}]}' WHERE id = 18345;
UPDATE genes SET GHR_metadata_ch = '{"gene": "DOK7", "conditions": [{"name": "先天性肌无力综合征", "detail": "已发现DOK7gene中至少有45个突变会导致先天性肌无力综合症。经常发生的突变是在DOK7gene（写为1124_1127dupTGCC）中添加了四个DNA构建块（核苷酸）。该gene的突变导致产生不能激活MuSK蛋白的缺陷Dok-7蛋白。结果，神经肌肉接头中存在较少的AChR，这减少了神经和肌肉细胞之间的信号传导。这些信号异常导致先天性肌无力综合症的肌肉运动减少和肌肉无力。由于尚不清楚的原因，DOK7gene突变的人倾向于在肩膀，臀部和四肢出现肌肉无力，称为肢带肌肉无力。"}]}' WHERE id = 18346;
UPDATE genes SET GHR_metadata_ch = '{"gene": "LYST", "conditions": [{"name": "Chédiak-东综合征", "detail": "在患有Chediak-Higashi综合征的人中，已经鉴定出LYSTgene中至少30个突变。这些突变损害了溶酶体运输调节蛋白的正常功能，从而破坏了细胞内溶酶体和相关结构的大小，结构和功能。（换行）导致严重的儿童期Chediak-Higashi综合征的LYST突变通常会导致溶酶体运输调节蛋白异常短而无功能的产生。导致该疾病较轻，成年版本的突变通常会改变蛋白质中的单个蛋白质结构单元（氨基酸）。在这些情况下，改变的蛋白质可能保留某些功能。(换行)具有LYST突变的人在整个身体的细胞中具有异常大的溶酶体和相关结构。这些扩大的结构会干扰正常的细胞功能。例如，某些免疫系统细胞中扩大的溶酶体阻止这些细胞对细菌和其他外来入侵者作出适当反应。结果，故障的免疫系统无法保护身体免受严重的反复感染。（换行）在称为黑素细胞的色素细胞中，称为黑素体（与溶酶体有关）的细胞结构异常大。这些结构产生并分布称为黑色素的色素，黑色素是赋予皮肤，头发和眼睛颜色的物质。患有Chediak-Higashi综合征的人患有眼皮肤白化病，因为黑色素被困在巨大的黑素体内，无法促进皮肤，头发和眼睛的色素沉着。(换行)研究人员认为，血细胞碎片内称为血小板的异常溶酶体样结构是Chediak-Higashi综合征患者出现异常瘀伤和出血的基础。同样，神经细胞中异常的溶酶体可能会导致与该疾病相关的神经系统问题。"}]}' WHERE id = 18379;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SHH", "conditions": [{"name": "非综合征性全前脑", "detail": "已发现SHHgene中的100多个突变会引起非综合征性全前脑畸形。当大脑在早期发育过程中未能分成两个半球时，就会发生这种情况。SHHgene突变是非综合征性全前脑的最常见原因。这些突变降低或消除了声波刺猬的活性。如果没有这种蛋白质的正确活性，眼睛将无法正常形成，大脑也不会分成两个半球。如果眼睛没有移动到适当的位置，面部其他部位的发育会受到影响。非综合征性全前脑的体征和症状是由大脑和面部的异常发育引起的。"}, {"name": "缺损", "detail": "遗传学家庭参考提供有关结肠癌的信息。"}, {"name": "小眼症", "detail": "遗传学家庭参考提供有关小眼症的信息。"}]}' WHERE id = 18380;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ELANE", "conditions": [{"name": "周期性中性粒细胞减少症", "detail": "已发现ELANEgene中的15个以上突变会引起周期性中性粒细胞减少症，这种疾病的特征是中性粒细胞短缺（中性粒细胞减少症）发作和感染风险增加。导致周期性中性粒细胞减少的ELANEgene突变会改变中性粒细胞弹性蛋白酶中的单个蛋白质结构单元（氨基酸）。这些突变被认为会产生保留某些功能的异常蛋白质。但是，产生异常嗜中性粒细胞弹性蛋白酶蛋白的嗜中性粒细胞的寿命似乎比正常人短。中性粒细胞寿命较短被认为是造成这种情况循环性质的原因。当受影响的嗜中性粒细胞过早死亡时，由于身体需要时间补充供应，因此存在中性粒细胞短缺的时期。对于大多数受影响的个体，中性粒细胞减少症每21天复发一次。"}, {"name": "严重的先天性中性粒细胞减少症", "detail": "已经发现ELANEgene中的70多个突变会导致严重的先天性中性粒细胞减少症，这种疾病的特征是从婴儿期开始就出现中性粒细胞减少症。这些突变大多数会改变嗜中性粒细胞弹性蛋白酶的结构，使其折叠成不正确的三维形状。研究结果表明，错误折叠的中性粒细胞弹性蛋白酶蛋白在中性粒细胞中积累。这种积累可能会损害并杀死这些抗感染细胞。中性粒细胞缺乏会导致严重先天性中性粒细胞减少症患者反复感染，炎症发作和其他免疫问题。"}]}' WHERE id = 18428;
UPDATE genes SET GHR_metadata_ch = '{"gene": "LMBRD1", "conditions": [{"name": "甲基丙二酸血症伴高半胱氨酸尿症", "detail": "已发现LMBRD1gene中至少有9个突变会引起甲基丙二酸血症，并伴有高半胱氨酸尿症，cblF型，一种导致发育迟缓，眼缺陷，神经系统疾病和血液异常的疾病。这种情况下涉及的LMBRD1gene突变导致产生异常短的LMBD1蛋白，该蛋白无法发挥功能。功能性LMBD1蛋白的缺乏阻止了溶酶体中维生素B12的释放，因此维生素无法用于生产AdoCbl和MeCbl。由于这两个辅因子均缺失，因此需要它们的酶（甲基丙二酰辅酶A突变酶和蛋氨酸合酶）无法正常运行。结果，某些氨基酸，脂质和胆固醇不被分解，同型半胱氨酸不能转化为甲硫氨酸。这种双重缺陷导致有毒化合物以及高半胱氨酸的积累，并减少体内蛋氨酸的产生。这种失衡的组合导致甲基丙二酸血症伴高半胱氨酸尿症的体征和症状。"}]}' WHERE id = 18431;
UPDATE genes SET GHR_metadata_ch = '{"gene": "AMN", "conditions": [{"name": "Imerslund-Gräsbeck综合征", "detail": "已发现AMNgene突变会导致Imerslund-Gräsbeck综合征，该疾病的特征是体内维生素B12含量低，导致巨幼细胞性贫血，神经系统疾病和蛋白尿。这是由于无羊膜蛋白缺陷导致其抑制其与cubilin和维生素B12摄取的结合。"}]}' WHERE id = 18453;
UPDATE genes SET GHR_metadata_ch = '{"gene": "GNAQ", "conditions": [{"name": "Sturge-Weber综合征", "detail": "已发现GNAQgene中至少有一个突变引起Sturge-Weber综合征。Sturge-Weber综合征是一种影响某些血管发育的疾病，通常会导致三个主要特征：称为葡萄酒胎记的红色或粉红色胎记，脑部异常，眼压升高（青光眼）或其他眼部问题。与Sturge-Weber综合征相关的GNAQgene突变改变了G？q蛋白。它用G？中第183位的氨基酸谷氨酰胺取代氨基酸精氨酸？q蛋白（写为Arg183Gln或R183Q）。这种突变不是遗传的，而是在受孕后发生的。这种改变称为体细胞突变，仅存在于某些细胞中，特别是与血管形成有关的大脑，眼睛和皮肤中的细胞。（换行）激活后，改变的G？q蛋白不能将GTP转化为GDP。因此，蛋白质总是活跃的，并且由其控制的信号传导途径不断开启。这种增加的信号传导可能破坏血管发育的调节，导致Sturge-Weber综合征患者出生前血管的异常和过度形成。"}, {"name": "其他疾病", "detail": "GNAQgene中的R183Q突变还可以引起波特酒胎记，而没有通常与Sturge-Weber综合征相关的大脑或眼睛异常（如上所述）。与Sturge-Weber综合征一样，由GNAQgene突变引起的孤立的波特酒胎记通常位于面部的一侧，但可以位于两侧。据认为，引起孤立的葡萄酒胎记的体细胞GNAQgene突变在胎儿发育中比引起Sturge-Weber综合征的胎儿晚，因此影响较少的细胞和组织。（换行）在称为葡萄膜黑色素瘤的眼癌中也发现了GNAQgene的体细胞突变。这种癌症发生在称为葡萄膜的眼睛中间层。葡萄膜包括眼睛的有色部分（虹膜）和位于眼睛白色部分（巩膜）下方的相关组织。葡萄膜黑色素瘤中的GNAQgene突变导致蛋白质过度活跃，从而导致信号过度。这种异常信号传导可能导致细胞过度生长和癌性肿瘤的形成。尽管在葡萄膜黑色素瘤中发现了R183Q突变，但患有Sturge-Weber综合征或孤立的葡萄酒胎记的人患这种癌症的风险并未增加。导致葡萄膜黑色素瘤的GNAQgene突变通常发生在人的生命后期，通常在成年期，并且仅限于引起肿瘤的细胞。"}]}' WHERE id = 18459;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ISPD", "conditions": [{"name": "沃克-沃伯格综合症", "detail": "已发现ISPDgene中至少有17个突变会引起Walker-Warburg综合征，这是一组称为先天性肌营养不良的疾病中最严重的形式。Walker-Warburg综合征会导致骨骼肌无力以及大脑和眼睛异常。由于这种情况引起的问题的严重性，受影响的个体通常无法存活到儿童早期。（换行）参与Walker-Warburg综合征的ISPDgene突变阻止了正常的糖基化-dystroglycan。结果是-dystroglycan不再能够有效地将细胞锚定在周围的蛋白质和其他分子上。没有功能-dystroglycan为了稳定肌肉纤维，当它们反复收缩并使用时放松时会受到损坏。受损的纤维会随着时间的推移而减弱和死亡，这会影响Walker-Warburg综合征患者骨骼肌的发育，结构和功能。(换行)有缺陷-dystroglycan还影响大脑早期发育过程中神经元的迁移。一些神经元不是在到达预定目的地时停止，而是通过大脑表面迁移到周围充满液体的空间。研究人员认为，神经元迁移的这个问题会导致大脑异常，称为鹅卵石脑畸形，其中表面大脑缺乏正常的褶皱和凹槽，而是显得颠簸和不规则。关于ISPDgene突变对身体其他部位的影响知之甚少。"}, {"name": "肢带型肌营养不良症", "detail": "遗传学家庭参考资料提供有关肢带型肌营养不良症的信息。"}]}' WHERE id = 18484;
UPDATE genes SET GHR_metadata_ch = '{"gene": "FZD6", "conditions": [{"name": "非综合征型先天性指甲疾病10", "detail": "已发现FZD6gene中至少有三个突变导致非综合征性先天性指甲疾病10。这种情况的特征是指甲和脚趾甲异常厚，可能呈爪状。突变导致产生卷曲的6蛋白，该蛋白不能到达需要的细胞膜或不能将信号传递到细胞中。结果，Wnt信号被破坏，并且指甲的生长和发育受到不良调节，这导致指甲与下面的指甲床分离（甲亢）以及指甲和脚趾甲的其他异常。"}]}' WHERE id = 18492;
UPDATE genes SET GHR_metadata_ch = '{"gene": "NOD2", "conditions": [{"name": "布劳综合症", "detail": "已发现NOD2gene中至少有22个突变会引起Blau综合征，Blau综合征是一种炎症性疾病，始于儿童期，主要影响皮肤，关节和眼睛。这些突变改变了NOD2蛋白中的单个蛋白结构单元（氨基酸）。所有这些突变都会导致NOD2蛋白过度活跃，从而引发异常的炎症反应并引起肿胀和刺激。但是，尚不清楚异常活性蛋白如何引起影响Blau综合征特征的皮肤，关节和眼睛的特定炎症模式。(换行)NOD2gene突变也可引起早发性结节病，一些研究人员认为这种情况类似于Blau综合征的非遗传形式。"}, {"name": "克罗恩病", "detail": "NOD2gene的变异与克罗恩病（一种引起消化系统炎症的复杂疾病）的风险增加有关。特别是，NOD2gene的改变与克罗恩病的一种形式有关，克罗恩病会影响北欧血统人群的小肠下部（回肠）和结肠。在所有克罗恩病患者中，约有40%发现了三种最常见的NOD2变异。其中最常见的是3020insC或1007fs，导致产生的NOD2蛋白略短于正常水平。其他常见变异会改变NOD2蛋白中的单个氨基酸。目前尚不清楚这些遗传变化如何增加患克罗恩病的风险。研究表明，NOD2gene的改变阻止了蛋白质正确识别细菌，使这些微生物能够不受控制地生长并侵入肠道细胞。对这些细菌的异常免疫反应可能导致炎症和克罗恩病特有的消化问题。"}, {"name": "姚综合征", "detail": "NOD2gene的一些变异增加了患姚综合征的风险，姚综合征是一种导致发烧和异常炎症发作影响身体许多部位的疾病。患有这种情况的大多数人的NOD2gene都有变异，称为IVS8+158，位于称为内含子8的gene区域。姚综合征患者中另一个相对常见的NOD2gene变异将NOD2蛋白（写为Arg702Trp或R702W）中702位的氨基酸色氨酸替换为氨基酸精氨酸。在一些克罗恩病患者中也发现了这些变异（如上所述）。大约20%的姚综合征患者同时具有IVS8+158和R702W变异，并且一些受影响的个体在NOD2gene中具有其他变异。（换行）NOD2gene有一个，两个或多个变异的影响尚不清楚，尽管研究表明这些遗传变化可能会改变NOD2gene的活性（表达）。目前尚不清楚这些变异如何导致姚综合征患者的异常炎症。研究人员怀疑，感染等环境因素也可能在遗传变异增加风险的人群中引发疾病。"}, {"name": "癌症", "detail": "一些研究表明，NOD2gene的变化，特别是常见的3020insC变异（如上所述）与几种癌症的发展之间可能存在关联。尽管其中一些研究发现具有NOD2gene变异的人患癌症的风险增加，但其他研究未发现这种关联。目前尚不清楚该gene的变化如何导致癌症风险。"}, {"name": "其他疾病", "detail": "几项研究已将NOD2gene的变异视为称为移植物抗宿主病（GVHD）的疾病的可能危险因素。GVHD可以在某些癌症治疗后发生，例如同种异体干细胞移植。该程序通常用于治疗血液和免疫系统的癌症，用来自健康供体的干细胞代替患者患病的血液形成细胞（一种干细胞）。如果供体的干细胞（移植物）将患者的身体（宿主）识别为异物，它们可能会攻击器官和组织，如肝脏，消化系统和皮肤。GVHD是用于描述这种潜在严重反应的术语。(换行)一些研究表明，NOD2gene的变异影响同种异体干细胞移植后发生严重GVHD并发症的风险。干细胞供体和受体中NOD2gene变异的存在与严重反应的最大风险相关。然而，其他研究发现NOD2gene变化与发生GVHD的风险无关。"}]}' WHERE id = 18493;
UPDATE genes SET GHR_metadata_ch = '{"gene": "NLRP1", "conditions": [{"name": "白癜风", "detail": "研究表明，NLRP1gene的变异与白癜风的风险增加有关，白癜风是一种自身免疫性疾病，可导致肤色（色素沉着）发生斑片状变化。（换行）与白癜风相关的NLRP1gene变异之一将蛋白质构建块（氨基酸）亮氨酸改变为NLRP1蛋白质序列中第155位的氨基酸组氨酸，写为Leu155His或L155H。这种和其他变异可能影响NLRP1蛋白的活性，使得身体更难以控制炎症并防止免疫系统攻击其自身组织。虽然与白癜风相关的色素损失是由免疫系统攻击皮肤中的色素生成细胞（黑素细胞）引起的，但尚不清楚是什么特定情况触发免疫系统这样做。这种情况可能是由遗传和环境因素共同作用造成的，其中大部分尚未确定。"}, {"name": "自身免疫性艾迪生病", "detail": "遗传学家庭参考提供有关自身免疫性艾迪生病的信息。"}, {"name": "自身免疫性疾病", "detail": "NLRP1gene变异与自身免疫性疾病的风险增加有关。当免疫系统发生故障并攻击身体的组织和器官时，就会发生自身免疫性疾病。这些疾病包括1型糖尿病，其中胰腺中产生胰岛素的细胞被破坏，以及Addison病，其由对每个肾（肾上腺）顶部的小激素产生腺的自身免疫损伤引起。某些NLRP1gene变异似乎使受影响的个体更容易发生免疫系统过度活跃，从而损害人体自身的组织和器官。"}]}' WHERE id = 18532;
UPDATE genes SET GHR_metadata_ch = '{"gene": "EDARADD", "conditions": [{"name": "少汗性外胚层发育不良", "detail": "EDARADDgene中的突变是少汗性外胚层发育不良的常染色体隐性形式的罕见原因。通过常染色体隐性遗传，每个细胞中都有两个拷贝的EDARADDgene突变。（换行）导致少汗性外胚层发育不良的EDARADD突变改变了EDARADD蛋白中的单个蛋白结构单元（氨基酸）。这种遗传变化在蛋白质位置142（写为Glu142Lys）上用氨基酸赖氨酸取代了氨基酸谷氨酰胺。因为此突变发生在死亡域中，所以它阻止了EDARADD蛋白与ectodysplasin A受体有效相互作用。结果，该受体可能无法触发外胚层-中胚层相互作用以及毛囊，汗腺和其他外胚层结构正常发育所需的信号。外胚层发育的这种破坏导致少汗性外胚层发育不良的特征。"}]}' WHERE id = 18538;
UPDATE genes SET GHR_metadata_ch = '{"gene": "IFIH1", "conditions": [{"name": "Aicardi-Goutières综合征", "detail": "在Aicardi-Goutières综合征患者中至少发现了12个IFIH1gene突变。这种疾病的特征是免疫系统，皮肤和大脑异常，包括大脑部分钙沉积。（换行）涉及Aicardi-Goutières综合征的IFIH1gene突变被描述为“功能获得”，因为它们导致产生具有增强活性的MDA5蛋白。改变后的蛋白质可能更容易附着于RNA，甚至不是来自病毒的RNA片段，或附着于其他MDA5蛋白以形成细丝。备选地，当不再需要免疫信号传导时，含有改变的蛋白质的细丝可能不会被分解。由于这些变化，干扰素产生异常开启，导致过度的免疫系统活动和炎症。(换行)持续的炎症被认为会破坏体内钙的处理方式，导致Aicardi-Goutières综合征患者出现钙沉积。过度的炎症也被认为会损害大脑和皮肤中的细胞，导致这种疾病特征性的这些组织异常。"}, {"name": "MDA5缺陷", "detail": "已发现IFIH1gene中至少有四个遗传变化会导致MDA5缺乏症，这是一种免疫系统疾病（免疫缺陷），可导致肺和气道（呼吸道）反复发作，严重的病毒感染。感染最常见的原因是鼻病毒，RSV和流感病毒。导致这种情况的IFIH1gene变化被描述为“功能丧失”突变，因为它们导致无法起作用的MDA5蛋白版本发生变化。研究表明，改变后的蛋白质无法附着于病毒RNA或其他MDA5蛋白质以形成细丝。结果，干扰素的产生没有被打开。干扰素刺激的重要早期免疫反应的缺乏导致MDA5缺乏婴儿的严重病毒感染。"}, {"name": "其他疾病", "detail": "还发现IFIH1gene突变引起单例Merten综合征。Singleton-Merten综合征的一个特征是钙沉积在大血管中，将血液从心脏输送到身体的其余部分（主动脉）和心脏的某些瓣膜。其他体征和症状包括牙齿异常，低骨密度（骨质减少）和其他骨骼问题。一些具有IFIH1gene突变的人同时患有单例Merten综合征和Aicardi-Goutières综合征（如上所述）的体征和症状，这表明这两种情况可能是由IFIH1gene突变引起的频谱的一部分。（换行）与Aicardi-Goutières综合征一样，单例Merten综合征中涉及的IFIH1gene突变被描述为“功能获得”，并导致过度的免疫系统活性和炎症，破坏体内钙的处理。目前尚不清楚为什么在IFIH1gene中具有功能获得性突变的人会出现一种或另一种疾病的体征和症状。(换行)Singleton-Merten综合征和Aicardi-Goutières综合征均具有自身免疫特征，当免疫系统发生故障并损害身体自身的组织和器官时会发生自身免疫特征。IFIH1gene的常见变异（多态性）与其他自身免疫性疾病有关。据认为，增强MDA5蛋白活性的多态性增加了某些自身免疫性疾病的风险，而降低MDA5蛋白活性的多态性有助于预防其他疾病。"}]}' WHERE id = 18555;
UPDATE genes SET GHR_metadata_ch = '{"gene": "CHRNB2", "conditions": [{"name": "常染色体显性遗传夜间额叶癫痫", "detail": "在常染色体显性遗传性夜间额叶癫痫（ADNFLE）患者中，CHRNB2gene至少鉴定出三个突变。这些突变中的每一个都会改变？nAChR通道的2个亚基。(换行)CHRNB2突变使nAChR通道对神经递质乙酰胆碱更加敏感，从而使通道比平时更容易打开。穿过细胞膜的离子流的增加改变了神经递质的释放，这改变了神经元之间的信号传导。研究人员认为，大脑中某些神经元的过度兴奋会引发与癫痫发作相关的异常大脑活动。目前尚不清楚为什么ADNFLE中出现的癫痫发作始于大脑额叶，最常发生在睡眠期间。"}]}' WHERE id = 18576;
UPDATE genes SET GHR_metadata_ch = '{"gene": "KRT13", "conditions": [{"name": "白色海绵痣", "detail": "已经发现至少五个KRT13gene突变引起白色海绵痣，这种情况导致形成称为痣（单数：痣）的白色组织斑块，其表现为增厚的，天鹅绒般的海绵状组织。这些痣最常发生在口腔（口腔）粘膜上（复数：粘膜）。很少，白色海绵痣发生在鼻子，食道，生殖器或肛门的粘膜上。（换行）导致白色海绵痣的KRT13gene突变破坏角蛋白13的结构。结果，角蛋白13不能与角蛋白4正确地配合在一起，导致形成不规则的中间丝，这些中间丝很容易被摩擦或创伤很小而损坏。吃或刷牙时，口腔粘膜中脆弱的中间丝可能会受损。中间丝的损伤导致炎症并促进上皮细胞的异常生长和分裂（增殖），导致粘膜增厚并导致白色海绵痣。"}]}' WHERE id = 18605;
UPDATE genes SET GHR_metadata_ch = '{"gene": "CYP27B1", "conditions": [{"name": "维生素D依赖性佝偻病", "detail": "已发现CYP27B1gene中至少有70个突变引起维生素D依赖性佝偻病1A型（VDDR1A），也称为维生素D 1-羟化酶缺乏症。这种骨骼发育障碍的特征是血液中钙（低钙血症）和磷酸盐（低磷血症）含量低，从而导致容易骨折的软而弱的骨骼。这种情况的一个共同特征是腿部异常弯曲（弯曲）。（换行）导致这种情况的CYP27B1gene突变减少或消除1的功能-羟化酶。结果，维生素D不会转化为活性形式，也无法控制矿物质的吸收。由此导致的钙和磷酸盐从肠道吸收到血液中的减少意味着这些矿物质中很少沉积在发育中的骨骼中（骨骼矿化），从而导致骨骼柔软，虚弱和VDDR1A的其他特征。低钙血症还会在某些受影响的个体中引起肌肉无力和癫痫发作。"}, {"name": "自身免疫性艾迪生病", "detail": "遗传学家庭参考提供有关自身免疫性艾迪生病的信息。"}, {"name": "多发性硬化症", "detail": "遗传学家庭参考提供有关多发性硬化症的信息。"}]}' WHERE id = 18612;
UPDATE genes SET GHR_metadata_ch = '{"gene": "CYLD", "conditions": [{"name": "布鲁克-斯皮格勒综合症", "detail": "在Brooke-Spiegler综合征患者中至少鉴定出20个CYLDgene突变。这种情况的特征是多个非癌性（良性）肿瘤，这些肿瘤在与皮肤（皮肤附件）相关的结构中发展，例如汗腺和毛囊。患有Brooke-Spiegler综合征的人可能会发展出几种类型的皮肤附件肿瘤，包括称为螺旋腺瘤，毛发上皮瘤和圆柱状瘤的生长。螺旋腺瘤是汗腺的肿瘤。毛发上皮瘤起源于毛囊。虽然以前认为圆柱状瘤起源于汗腺，但现在人们普遍认为圆柱状瘤始于毛囊。(换行)患有Brooke-Spiegler综合征的人出生时每个细胞中CYLDgene的两个拷贝之一发生突变。这种突变阻止细胞从改变的gene拷贝中产生功能性CYLD蛋白。但是，通常从该gene的另一个正常拷贝中产生足够的蛋白质，以有效调节细胞生长。为了使肿瘤发展，涉及CYLDgene另一拷贝的遗传物质的第二次突变或缺失必须在人的一生中在某些细胞中发生。这些遗传变化称为体细胞突变，不是遗传的。（换行）当CYLDgene的两个拷贝在特定细胞中突变时，该细胞不能产生任何功能性CYLD蛋白。这种蛋白质的丧失损害了核因子-κ-B的调节，使细胞以不受控制的方式生长和分裂以形成肿瘤。在患有Brooke-Spiegler综合征的人中，第二次CYLD突变通常发生在受影响人一生中皮肤中不同类型的细胞中，导致多种类型的皮肤附件肿瘤的生长。"}, {"name": "家族性圆柱状瘤病", "detail": "在家族性圆柱瘤病患者中已经发现了30多个CYLDgene突变。患有这种疾病的人通常会发展出大量的圆柱状瘤。（换行）与Brooke-Spiegler综合征一样，家族性圆柱瘤病患者在每个细胞中出生时都有一个CYLDgene的突变拷贝，并且必须在某些细胞中发生涉及CYLDgene另一拷贝的遗传物质的第二个突变或缺失细胞在一个人的一生中。（换行）当CYLDgene的两个拷贝在特定毛囊细胞中突变时，那些细胞不能产生任何功能性CYLD蛋白。这种蛋白质的丢失使细胞以不受控制的方式生长和分裂，形成圆柱状瘤。"}, {"name": "多发性家族性毛发上皮瘤", "detail": "在具有多个家族性毛发上皮瘤的个体中已经鉴定出CYLDgene中至少22个突变。患有这种疾病的人通常会发展出大量的毛发上皮瘤。（换行）与Brooke-Spiegler综合征和家族性圆柱瘤病一样，CYLD相关的多发性家族性毛发上皮瘤患者在每个细胞中都有一个CYLDgene突变拷贝，第二个突变或缺失涉及另一个拷贝的遗传物质的CYLDgene必须在一个人的一生中发生在某些细胞中。（换行）当CYLDgene的两个拷贝在特定毛囊细胞中突变时，那些细胞不能产生任何功能性CYLD蛋白。这种蛋白质的丢失使细胞以不受控制的方式生长和分裂，形成毛发上皮瘤。(换行)一些研究人员认为家族性圆柱状瘤病，多发性家族性毛发上皮瘤和Brooke-Spiegler综合征是同一疾病的不同形式。目前尚不清楚为什么CYLDgene突变在这些疾病的每一种情况下都会导致不同类型的皮肤附件肿瘤，或者为什么在这些疾病中肿瘤通常局限于皮肤。"}, {"name": "癌症", "detail": "在某些癌性肿瘤中也发现了CYLDgene的体细胞突变和活性（表达）降低。这些癌症包括从骨髓细胞开始的多发性骨髓瘤，以及肾癌，肝癌，子宫癌和结肠癌。这些遗传变化可能损害CYLD蛋白的肿瘤抑制功能，使细胞以不受控制的方式生长和分裂并发生癌变。"}]}' WHERE id = 18631;
UPDATE genes SET GHR_metadata_ch = '{"gene": "PRICKLE1", "conditions": [{"name": "PRICKLE1相关的进行性肌阵挛性癫痫伴共济失调", "detail": "在患有共济失调的PRICKLE1相关性进行性肌阵挛性癫痫患者中，已经鉴定出PRICKLE1gene中至少有三个突变。每个突变都会改变prickle homolog 1蛋白中的单个蛋白结构单元（氨基酸）。已知的突变之一似乎破坏了刺痒同源物1和REST之间的相互作用，阻止了REST从细胞核中的转运。结果，REST可能不适当地抑制发育中的神经系统中的某些gene。目前尚不清楚PRICKLE1gene的突变如何导致运动问题，癫痫发作以及与PRICKLE1相关的进行性肌阵挛性癫痫伴共济失调的其他特征。"}]}' WHERE id = 18650;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ADAR", "conditions": [{"name": "Aicardi-Goutières综合征", "detail": "由于ADAR蛋白缺陷而导致Aicardi-Goutières综合征患者中已鉴定出ADARgene的几种突变，这削弱了其编辑RNA的能力。这种疾病的特征是严重的脑功能障碍（脑病），皮肤病变，免疫系统异常。"}, {"name": "其他疾病", "detail": "在对称性色素沉着症患者中已鉴定出ADARgene的几种突变，该疾病的特征是面部，手和脚上的雀斑状斑点（黄斑）比婴儿期或儿童早期的周围皮肤更暗或更亮。"}]}' WHERE id = 18653;
UPDATE genes SET GHR_metadata_ch = '{"gene": "PMM2", "conditions": [{"name": "PMM2先天性糖基化疾病", "detail": "已发现PMM2gene中的115多个突变会导致PMM2先天性糖基化疾病（PMM2-CDG，也称为Ia型先天性糖基化疾病）。这是一种严重的疾病，其特征是发育迟缓，肌张力弱（肌张力低下），脂肪分布异常以及其他各种体征和症状。导致PMM2-CDG的突变以不同方式改变PMM2酶的结构；但是，所有的突变似乎都导致酶活性降低。PMM2酶活性降低导致细胞内GDP-甘露糖短缺。结果，没有足够的活化甘露糖来形成寡糖。糖基化不能正常进行，因为产生不正确的寡糖。PMM2-CDG中的体征和症状可能是由于许多器官和组织中异常糖基化蛋白的产生。"}]}' WHERE id = 18673;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SLC9A6", "conditions": [{"name": "克里斯蒂安森综合症", "detail": "SLC9A6gene的突变导致克里斯蒂安森综合症。这种情况的特征是神经系统问题，包括智力障碍，癫痫发作以及无法走路或说话。SLC9A6gene中的突变通常会导致异常短的NHE6蛋白，该蛋白无功能或在细胞中迅速分解，从而导致缺少功能性NHE6通道。结果，内体中的pH不能适当维持。目前尚不清楚不受管制的内体pH如何导致克里斯蒂安森综合征患者的神经系统问题。一些研究表明，内体运输蛋白质对于学习和记忆很重要，但尚未确定内体pH或NHE6蛋白质在此过程中的作用。"}]}' WHERE id = 18711;
UPDATE genes SET GHR_metadata_ch = '{"gene": "MAN2B1", "conditions": [{"name": "α-甘露糖苷病", "detail": "在患有α-甘露糖苷病的人中，已经鉴定出MAN2B1gene的120多个突变，α-甘露糖苷病是一种罕见的遗传性疾病，会在身体的许多器官和组织中引起问题。受影响的个体可能具有智力残疾，独特的面部特征和骨骼异常。导致α-甘露糖苷病的某些MAN2B1gene突变会改变α-甘露糖苷酶中的一种蛋白质结构单元（氨基酸）。其他突变导致酶异常缩短，或导致酶被错误地拼凑在一起。（换行）这些突变会干扰α-甘露糖苷酶在分解含甘露糖的寡糖中发挥作用的能力。这些寡糖积聚在溶酶体中并导致细胞功能障碍并最终死亡。寡糖的异常积累和导致的细胞死亡会破坏组织和器官，从而导致α-甘露糖苷病的特征。"}]}' WHERE id = 18716;
UPDATE genes SET GHR_metadata_ch = '{"gene": "RPS14", "conditions": [{"name": "5q减综合征", "detail": "RPS14gene参与称为5q减（5q-）综合征的疾病。这种情况是一种称为骨髓增生异常综合征（MDS）的骨髓疾病，其中未成熟的血细胞不能正常发育。患有5q-综合征的个体通常缺乏红细胞（贫血）和称为巨核细胞的血细胞异常，巨核细胞产生血小板，即参与凝血的细胞。受影响的个体患上称为急性髓细胞性白血病（AML）的快速增长的血癌的风险也增加。（换行）5q-综合征是由染色体5的长（q）臂缺失DNA区域引起的。这种缺失发生在一个人一生中未成熟的血细胞中，并影响每个细胞中5号染色体的一个拷贝。大多数患有5q-综合征的人缺少约150万个DNA构建块（碱基对）的序列，也写为1.5兆碱基（Mb）。DNA的这一区域包含40个gene，包括RPS14。丢失一份RPS14gene拷贝会减少RPS14蛋白的产生量。研究表明，功能性核糖体蛋白的缺乏会增加骨髓中血液形成细胞的自我破坏，从而导致贫血。研究表明，该病的其他特征与DNA缺失片段中的其他gene有关。"}]}' WHERE id = 18720;
UPDATE genes SET GHR_metadata_ch = '{"gene": "PSENEN", "conditions": [{"name": "化脓性汗腺炎", "detail": "已发现PSENENgene中至少有三个突变会引起化脓性汗腺炎，这是一种慢性皮肤病，其特征是在毛囊中皮肤下反复出现沸腾状肿块（结节）。结节往往会发炎和疼痛，并且在愈合时会产生明显的疤痕。（换行）PSENENgene突变减少了细胞中产生的功能性PEN-2的数量，因此这种蛋白质中较少可作为-分泌酶复合物。由此导致的正常短缺-分泌酶损害细胞信号传导途径，包括Notch信号传导。尽管对该机制知之甚少，但研究表明，Notch信号异常可能会促进毛囊中复发性结节的发展并引发皮肤炎症。"}, {"name": "Dowling-Degos病", "detail": "遗传学家庭参考提供有关Dowling-Degos病的信息。"}]}' WHERE id = 18757;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SALL1", "conditions": [{"name": "Townes-Brocks综合征", "detail": "在Townes-Brocks综合征患者中已鉴定出SALL1gene的55个以上突变。研究人员最初认为，所有这些突变都阻止了每个细胞中一个gene拷贝产生任何蛋白质，从而导致发育过程中SALL1蛋白质的短缺。最近，他们发现某些突变导致异常小版本的SALL1蛋白产生，该蛋白在细胞内发生功能异常。故障蛋白会干扰SALL1蛋白的正常拷贝，阻止它们进入细胞核以调节gene活性。科学家怀疑这种类型的遗传变化可能是Townes-Brocks综合征更严重病例的基础。减少SALL1蛋白量的突变可能是造成这种情况较轻的原因。(换行)SALL1gene似乎是出生前许多不同器官和组织正常发育所必需的，这有助于解释为什么该gene的突变会导致与Townes-Brocks综合征相关的各种出生缺陷。但是，尚不确定SALL1突变如何导致这种情况的特定特征，包括肛门开口阻塞（肛门无孔），耳朵形状异常和手畸形。"}]}' WHERE id = 18790;
UPDATE genes SET GHR_metadata_ch = '{"gene": "RARS2", "conditions": [{"name": "桥小脑发育不全", "detail": "在患有称为桥小脑发育不全的大脑发育障碍的人中，已经鉴定出RARS2gene的至少13个突变。这种情况的主要特征包括发育迟缓，运动问题和智力障碍。大多数已知的RARS2gene突变会导致一种称为6型桥小脑发育不全（PCH6）的疾病。在具有另一种形式的疾病特征的个体中发现了一种突变，即1型桥小脑发育不全（PCH1）。（换行）导致桥小脑发育不全的RARS2gene突变显着降低或消除线粒体精氨酰-tRNA合成酶的功能。这种酶功能的丧失可能会破坏线粒体中新蛋白质的产生。但是，尚不清楚这些变化如何导致桥小脑发育不全患者的大脑发育异常。"}]}' WHERE id = 18809;
UPDATE genes SET GHR_metadata_ch = '{"gene": "TMEM173", "conditions": [{"name": "婴儿期发作的STING相关血管病变", "detail": "已在患有STING相关性血管病并在婴儿期发作（SAVI）的个体中鉴定出至少三个TMEM173gene突变，SAVI是一种潜在的威胁生命的疾病，会损害皮肤，肺，血管和其他组织。导致SAVI的突变被描述为“功能获得”突变，因为它们增强了STING蛋白的活性，导致β干扰素的过量生产。异常高的β干扰素水平会引起过度炎症，从而损害人体自身的细胞和组织，并导致SAVI的体征和症状。由异常增加的炎症引起的诸如SAVI的疾病被称为自身炎症性疾病。"}]}' WHERE id = 18823;
UPDATE genes SET GHR_metadata_ch = '{"gene": "MTR", "conditions": [{"name": "同型半胱氨酸尿症", "detail": "在高半胱氨酸尿症患者中已鉴定出MTRgene的20多个突变。这些突变中的许多导致产生异常小的，无功能的蛋氨酸合酶。其他突变会改变酶中的单个氨基酸。最常见的突变之一是在位置1173（写为Pro1173Leu或P1173L）上用氨基酸亮氨酸替换氨基酸脯氨酸，从而导致功能降低的酶。没有功能性蛋氨酸合酶，同型半胱氨酸不能转化为蛋氨酸。结果，同型半胱氨酸在血流中积聚，并且蛋氨酸的量减少。一些过量的同型半胱氨酸会从尿液中排出。研究人员尚未确定同型半胱氨酸和蛋氨酸水平的改变如何导致与同型半胱氨酸尿症相关的健康问题。"}, {"name": "其他疾病", "detail": "MTRgene的特定版本（变体）在出生前与各种健康问题相关。该变体用MTRgene（写为A2756G）中2756位的核苷酸鸟嘌呤替换了称为腺嘌呤的DNA（核苷酸）的一个组成部分。该变体与在大脑和脊髓发育期间发生的先天缺陷（神经管缺陷）的风险增加有关。一些研究表明，该变体还增加了患有唐氏综合症的孩子的风险，唐氏综合症是一种以智力残疾和相关健康问题为特征的疾病，但其他研究未发现风险增加。研究人员不知道为什么MTRgene的A2756G变体与神经管缺陷或唐氏综合症的风险之间可能存在联系。许多因素在确定这些复杂疾病的风险中起作用。"}]}' WHERE id = 18826;
UPDATE genes SET GHR_metadata_ch = '{"gene": "COL6A3", "conditions": [{"name": "胶原VI相关的肌病", "detail": "已经发现COL6A3gene的突变与胶原VI相关的肌病有关，胶原VI相关的肌病是一组严重程度不同且以肌肉无力和关节畸形为特征的疾病，称为挛缩。这是由于更改或有缺陷或无功能引起的？3（VI）链，其破坏功能性VI型胶原分子的形成。"}]}' WHERE id = 18847;
UPDATE genes SET GHR_metadata_ch = '{"gene": "COL9A1", "conditions": [{"name": "多发性骨phy发育不良", "detail": "已发现COL9A1gene突变会导致显性多发性骨phy发育异常，这是一种软骨和骨骼发育异常，主要影响手臂和腿部长骨的末端（骨phy）。该gene中的剪接位点突变（例如1-bp ins，IVS8，T，+3）将破坏所需的遗传信息？1（IX）链产生导致这种疾病的症状。"}, {"name": "斯蒂克勒综合症", "detail": "已发现COL9A1gene突变会引起Stickler综合征，这是一种遗传性疾病，其特征是独特的面部外观，眼睛异常，听力下降和关节问题。"}]}' WHERE id = 18871;
UPDATE genes SET GHR_metadata_ch = '{"gene": "CAT", "conditions": [{"name": "Acatalasemia", "detail": "已经发现CATgene中至少有13个突变引起acatalasemia，其特征在于过氧化氢酶活性非常低。许多患有acatalasemia的人从来没有任何相关的健康问题，尽管这种情况偶尔与口腔内的开放性溃疡（溃疡）有关，导致软组织死亡（坏疽）。Acatalasemia似乎也增加了患2型糖尿病（最常见的糖尿病）的风险，并且是其他常见复杂疾病的潜在风险因素。（换行）导致acatalasemia的突变发生在每个细胞中CATgene的两个拷贝中，并且它们将过氧化氢酶的活性降低至低于正常值的10%。这种酶的缺乏可以使过氧化氢在某些细胞中积累到毒性水平。例如，由口腔中的细菌产生的过氧化氢可能积聚并损害软组织，导致口腔溃疡和坏疽。过氧化氢的积累也可能损害胰腺的β细胞，其释放称为胰岛素的激素，其有助于控制血糖。β细胞功能异常被认为是阿卡塔血症患者2型糖尿病风险增加的基础。目前还不清楚为什么有些人没有与过氧化氢酶活性不足有关的健康问题。（换行）当每个细胞中CATgene的两个拷贝中只有一个具有突变时，就会发生称为低过氧化氢血症的相关病症。这种单一突变使过氧化氢酶的活性降低了大约一半。像acatalasemia一样，低碳酸血症通常不会引起任何健康问题。"}, {"name": "其他疾病", "detail": "CATgene和调节gene活性的DNA区域中的常见变异（多态性）可能与发生某些常见复杂疾病的风险有关。例如，研究人员正在研究这些多态性作为2型糖尿病和其他血糖调节障碍的潜在危险因素。CATgene多态性也可能与高血压（高血压），一种称为白癜风的皮肤病，骨骼变薄（骨质疏松症）以及血液中胆固醇和其他脂肪（脂质）水平升高有关，这增加了风险心脏病发作和中风。但是，尚不清楚CATgene的多态性如何影响过氧化氢酶的活性，以及该酶活性的变化如何影响一个人患这些疾病的风险。大量的遗传和生活方式因素，其中许多仍然未知，可能决定发展最常见，复杂conditions的风险。"}]}' WHERE id = 18874;
UPDATE genes SET GHR_metadata_ch = '{"gene": "KRT14", "conditions": [{"name": "表皮溶解水疱症", "detail": "在大疱性表皮松解症患者中已鉴定出KRT14gene的60多个突变，这种情况会导致皮肤非常脆弱并容易起泡。大多数这些遗传变化改变了用于制造角蛋白14的单一蛋白质构建块（氨基酸）。大疱性表皮松解症的最严重形式是Dowling-Meara型，通常是由于角蛋白14区域的变化而引起的，这对于角蛋白中间丝的正常组装至关重要。这种疾病的轻度形式，包括局部类型（以前称为Weber-Cockayne类型）和另一种广义类型（以前称为Koebner类型），通常是由影响蛋白质关键区域的变化引起的。(换行)KRT14gene突变改变角蛋白14的结构和功能，阻止其与角蛋白5有效工作并干扰角蛋白中间丝网络的组装。导致严重疾病形式的突变严重破坏角蛋白中间丝的组装，而导致较轻形式的突变则在较小程度上损害角蛋白丝的组装。该网络的破坏使角质形成细胞脆弱并易于破裂。对皮肤的轻微创伤，例如摩擦或刮擦，可导致这些细胞分解，导致形成疼痛的充满液体的水泡。"}, {"name": "Naegeli-Franceschetti-Jadassohn综合征/网状色素性皮肤病", "detail": "已经发现KRT14gene中的几个突变引起Naegeli-Franceschetti-Jadassohn综合征/网状色素性皮肤病（NFJS/DPR）。这种疾病是外胚层发育不良的一种罕见形式，是一组约150种疾病，其特征是外胚层组织（包括皮肤，头发，指甲，牙齿和汗腺）异常发育。NFJS和DPR最初被描述为单独的conditions；但是，它们现在通常被认为是同一疾病的形式。（换行）引起NFJS/DPR的KRT14gene突变最有可能减少角质形成细胞中功能性角蛋白14的量。这种蛋白质的缺乏使得这些细胞更可能自毁（经历细胞凋亡）。由此导致的角质形成细胞的损失改变了外胚层组织的正常发育和结构，这很可能是NFJS/DPR特有的大多数皮肤和指甲问题的基础。然而，目前尚不清楚角蛋白14的缺乏与深色肤色（网状色素沉着过度）的网状模式有何关系，这也是这种情况的标志。"}]}' WHERE id = 18882;
UPDATE genes SET GHR_metadata_ch = '{"gene": "EGR2", "conditions": [{"name": "进行性神经性腓骨肌萎缩症", "detail": "EGR2gene的突变可引起两种形式的Charcot-Marie-Tooth病，即1D型或4E型（有时称为先天性髓鞘减少性神经病）。这些突变改变了早期生长反应2蛋白中的单个蛋白结构单元（氨基酸）。结果，改变的蛋白质不能有效地与DNA结合，这破坏了对参与髓磷脂形成和维持的gene的控制。这种破坏导致髓磷脂的丧失（脱髓鞘）和神经冲动的传递受损。结果，减少了将大脑和脊髓连接到肌肉以及检测诸如触摸，疼痛，热和声音之类的感觉的感觉细胞的周围神经的功能，从而引起了Charcot-Marie-Tooth病的体征和症状。（换行）在患有1D型Charcot-Marie-Tooth病且也有听力损失的个体中已鉴定出特定突变。这种突变的结果是，氨基酸精氨酸被蛋白质381位的氨基酸组氨酸取代（写为Arg381His）。其他EGR2gene突变会导致严重的1D型（有时称为Dejerine-Sottas综合征），始于婴儿期或儿童早期。这些突变之一用蛋白质359位的氨基酸色氨酸取代了氨基酸精氨酸（写为Arg359Trp）。另一个在蛋白质位置383（Asp383Tyr）上用氨基酸酪氨酸取代了氨基酸天冬氨酸。目前尚不清楚为什么这些特定的突变会导致生命早期开始的严重症状。"}]}' WHERE id = 18891;
UPDATE genes SET GHR_metadata_ch = '{"gene": "MSX1", "conditions": [{"name": "沃尔夫-赫希霍恩综合症", "detail": "MSX1gene在Wolf-Hirschhorn综合征患者中经常被删除，尽管它位于4号染色体区域之外，而这个区域在患有这种疾病的患者中通常被删除。MSX1gene的丢失可能会破坏其他几个gene的调控，特别是与口腔和牙齿发育有关的gene。研究人员认为，MSX1gene的缺失会破坏早期发育中口腔结构的形成，从而导致牙齿缺失和其他牙齿异常。在某些患有Wolf-Hirschhorn综合征的人中，MSX1gene的缺失也可能导致嘴顶张开（c裂）和/或上唇裂开（唇裂）。"}, {"name": "其他疾病", "detail": "MSX1gene的变化也与口腔和牙齿发育的其他异常有关。例如，该gene的改变导致一些没有Wolf-Hirschhorn综合征的人出现唇裂和/或腭裂。这些遗传变异（多态性）与全球几个不同人群中孤立的唇裂和c裂有关。MSX1多态性似乎是在口腔裂孔中起作用的许多遗传和环境因素之一。（换行）至少六个MSX1突变导致少牙症，即多颗牙齿无法发育的情况。一些具有MSX1突变的个体合并了少牙症和唇裂和/或c裂。MSX1gene的突变可能会减少细胞内功能性MSX1蛋白的量，从而破坏口腔结构的早期发育。(换行)已发现MSX1gene的另一种突变会导致一个大家庭的Witkop综合征（也称为牙齿和指甲综合征）。这种罕见病的特征是缺失的牙齿数量可变，指甲和脚趾甲异常。导致Witkop综合征的MSX1突变（称为Ser202Ter或S202X）导致异常短而无功能的MSX1蛋白产生。这种蛋白质的损失会破坏早期发育过程中牙齿和指甲的形成。"}]}' WHERE id = 18909;
UPDATE genes SET GHR_metadata_ch = '{"gene": "FHL1", "conditions": [{"name": "艾梅氏肌失养症", "detail": "已经发现FHL1gene中至少有七个突变引起金刚砂-Dreifuss肌营养不良。这种情况会影响骨骼肌和心肌，导致称为挛缩的关节畸形，从而限制某些关节的运动；肌肉无力和消瘦会随着时间的推移而恶化；和心脏问题，包括猝死的风险增加。（换行）一些导致金刚砂-Dreifuss肌营养不良的FHL1gene突变会改变FHL1蛋白中的单个蛋白结构单元（氨基酸），而另一些会从FHL1gene中插入或删除少量DNA。所有已知的突变都会影响FHL1A亚型。根据突变发生的位置，它们也可能影响一种或两种其他同工型。(换行)研究表明，突变会减少细胞中产生的功能性FHL1蛋白的数量，或导致异常短而无功能的蛋白产生。这种蛋白质的缺乏会破坏心肌和骨骼肌细胞的正常结构和功能。但是，这些变化导致关节挛缩，肌肉无力和消瘦以及心脏问题的确切机制仍然未知。"}, {"name": "其他疾病", "detail": "FHL1gene突变也导致其他几种肌肉疾病。这些包括减少身体肌病，X连锁肩oper腹肌病，X连锁肌病伴姿势性肌肉萎缩（XMPMA）和刚性脊柱综合征。这些疾病与金刚砂-Dreifuss肌营养不良症一起被称为FHL1相关性肌病或FHL1病。这些疾病中常见的特征包括骨骼肌无力，尤其是在肩膀和小腿；涉及脊柱关节的挛缩（刚性脊柱）；和心脏异常。但是，这些疾病的发病年龄，肌肉无力的严重程度以及体征和症状恶化的速度各不相同。（换行）超过50个FHL1gene突变与FHL1相关的肌病有关。这些突变中的每一个都会影响部分或全部FHL1亚型。通常，影响所有三种同工型的突变比仅影响一种或两种同工型的突变引起更严重的体征和症状。研究人员提出了FHL1突变导致FHL1相关肌病的几种可能机制。在某些情况下，突变导致产生无功能形式的蛋白质或根本不产生蛋白质。在其他情况下，突变可能导致蛋白质异常形式的产生，该蛋白质可能在肌肉细胞内形成团块（称为还原体）。在身体肌病减轻，X连锁肩oper腹肌病和刚性脊柱综合征减轻的人群中发现了减少的身体，但尚不清楚它们与这些疾病的主要特征之间的关系。"}]}' WHERE id = 18913;
UPDATE genes SET GHR_metadata_ch = '{"gene": "RYR2", "conditions": [{"name": "儿茶酚胺能性多形性室性心动过速", "detail": "已发现RYR2gene中的70多个突变会引起儿茶酚胺能性多形性室性心动过速（CPVT）。几乎所有这些突变都会改变ryanodine受体2蛋白中的单个蛋白结构单元（氨基酸）。这些突变改变了RYR2通道的结构和功能。(换行)研究人员不确定RYR2gene突变如何导致室性心动过速，CPVT的特征是异常快速和不规则的心律（心律失常）。一些研究表明，突变会干扰RYR2通道的调节。其他研究发现，改变后的RYR2通道异常保持开放状态，从而使钙离子“泄漏”出肌浆网。显然，RYR2通道的结构和功能的变化破坏了对心肌细胞中钙离子流的仔细控制，这可能会触发CPVT患者的心律异常。"}, {"name": "致心律失常性右室心肌病", "detail": "遗传学家庭参考资料提供有关心律失常性右室心肌病的信息。"}, {"name": "家族性心房颤动", "detail": "遗传学家庭参考资料提供有关家族性心房颤动的信息。"}, {"name": "其他疾病", "detail": "已经发现RYR2gene中的其他几种突变会引起称为心律失常性右室心肌病（ARVC）的心脏病。这种情况会导致部分心肌随时间分解，从而增加心律不齐和猝死的风险。（换行）负责ARVC的RYR2gene突变改变了ryanodine受体2蛋白中的单个氨基酸。这些突变改变了RYR2通道的结构，这可能使钙离子“泄漏”出肌浆网。肌细胞内钙调节的这种失败可以触发ARVC的异常心律特征。"}]}' WHERE id = 18927;
UPDATE genes SET GHR_metadata_ch = '{"gene": "DPYS", "conditions": [{"name": "二氢嘧啶酶缺乏症", "detail": "在患有二氢嘧啶酶缺乏症的人中至少鉴定出23个DPYSgene突变，该疾病可在某些受影响的个体中引起神经和胃肠道问题。其他患有二氢嘧啶酶缺乏症的人没有与该疾病有关的体征或症状，在这些人中，只能通过实验室检查才能诊断出该病。患有二氢嘧啶酶缺乏症的人，包括那些没有其他症状的人，可能容易受到对氟嘧啶的严重，可能危及生命的毒性反应的影响。这些药物可能无法有效分解，并且可能在体内累积至毒性水平（氟嘧啶毒性），导致药物反应，包括胃肠道问题，血液异常和其他体征和症状。（换行）导致二氢嘧啶酶缺乏的DPYSgene突变大大降低或消除了二氢嘧啶酶的功能。结果，该酶不能开始分解5,6-二氢胸腺嘧啶和5,6-二氢尿嘧啶。过量的这些分子积聚在血液和周围的液体中并保护大脑和脊髓（脑脊液或CSF）并释放到尿液中。（换行）无法分解5,6-二氢胸腺嘧啶和5,6-二氢尿嘧啶与二氢嘧啶酶缺乏症的具体特征之间的关系尚不清楚。未能完成嘧啶分解的这一步骤也阻碍了该过程的最后一步，该过程产生β-氨基异丁酸和β-丙氨酸。这些分子的产生减少可能会损害其在神经系统中的功能，从而导致某些二氢嘧啶酶缺乏症患者出现神经系统问题。由于氟嘧啶类药物通过与嘧啶相同的三步过程分解，二氢嘧啶酶的缺乏可导致药物累积，引起氟嘧啶毒性。(换行)尚不清楚为什么某些二氢嘧啶酶缺乏症患者不会出现与该病有关的健康问题；其他遗传和环境因素可能有助于确定这种疾病的影响。"}]}' WHERE id = 18939;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SNCB", "conditions": [{"name": "路易氏体型失智症", "detail": "已经发现SNCBgene中的至少两个突变引起路易体痴呆。这种情况的特征是智力下降（痴呆）；幻觉；注意力和情绪的突然变化；以及运动问题，例如四肢僵硬，震颤以及平衡和协调能力受损。（换行）SNCBgene突变导致功能受损的蛋白质的产生。据认为，这种改变的蛋白质可能不能阻止α-突触核蛋白的积累。功能性β-突触核蛋白的减少可能导致α-突触核蛋白积聚和路易体的形成。这些异常的蛋白质簇存在于整个大脑的神经细胞中，在那里它们损害细胞功能并最终导致细胞死亡。随着时间的推移，神经细胞的损失越来越损害智力和运动功能以及情绪调节，导致路易体痴呆的体征和症状。"}]}' WHERE id = 18960;
UPDATE genes SET GHR_metadata_ch = '{"gene": "MOCS2", "conditions": [{"name": "钼辅助因子缺乏", "detail": "MOCS2gene突变会导致一种称为钼辅因子缺乏症的疾病。这种疾病的特征是从生命早期开始发作，脑功能障碍随着时间的推移而恶化（脑病）；这种情况通常在儿童早期就致命。已发现MOCS2gene中至少有十几个突变导致某种形式的疾病，称为B型或互补组B.（换行）参与钼辅因子缺乏的MOCS2gene突变可能消除MOCS2A，MOCS2B的功能，或两者兼而有之，尽管在极少数情况下不太严重，但仍可能保留某些蛋白质功能。没有任何一种钼蝶呤合酶，钼辅因子的生物合成就会受到损害。辅因子的丧失阻碍了依赖它的代谢酶的功能。(换行)由此导致的酶活性丧失导致某些化学物质的积累，包括亚硫酸盐，S-硫代半胱氨酸，黄嘌呤和次黄嘌呤，以及低水平的另一种化学物质。称为尿酸。（测试这些化学物质可以帮助诊断这种情况。）通常由亚硫酸盐氧化酶分解的亚硫酸盐是有毒的，特别是对大脑有毒。研究人员认为，亚硫酸盐（可能还有其他化学物质）异常高水平造成的损害会导致脑病，癫痫发作和钼辅因子缺乏症的其他特征。"}]}' WHERE id = 18961;
UPDATE genes SET GHR_metadata_ch = '{"gene": "F7", "conditions": [{"name": "VII因子缺乏症", "detail": "已经发现F7gene中的近300个突变会引起罕见的出血性疾病，称为VII因子缺乏症。这种疾病通常会导致流鼻血，容易瘀伤，牙龈出血，以及手术或身体伤害后长时间或过度出血。在严重的情况下，可能会发生危及生命的颅骨或胃肠道内出血事件。一些受影响的人没有出血问题。这种情况下涉及的F7gene突变减少了血液中凝血因子VII的含量。凝血因子VII的缺乏会阻止血液正常凝结，从而导致严重的异常出血事件。什么决定了病情的严重程度尚不清楚；它似乎与血液中凝血因子VII的量无关。"}]}' WHERE id = 18963;
UPDATE genes SET GHR_metadata_ch = '{"gene": "KRT86", "conditions": [{"name": "Monilethrix", "detail": "KRT86gene中的几个突变会导致monilethrix，这是一种头发状况，其特征是头发呈串珠状，头发短而脆，容易断裂。与这种情况相关的突变会改变K86蛋白中的单个蛋白结构单元（氨基酸）。氨基酸变化通常发生在K86蛋白的一个区域，该区域被认为在中间丝形成中很重要。在患有monilethrix的人中，受影响的毛干的皮质似乎异常。但是，尚不清楚KRT86gene的突变如何与皮质异常或头发的串珠外观相关。"}]}' WHERE id = 18977;
UPDATE genes SET GHR_metadata_ch = '{"gene": "F10", "conditions": [{"name": "X因子缺乏症", "detail": "已发现F10gene中至少有130个突变会引起罕见的出血性疾病，称为X因子缺乏症。这种疾病通常会导致流鼻血，容易瘀伤，皮肤下出血，牙龈出血，尿液中的血液（血尿），以及手术或创伤后长时间或过度出血。一些导致因子X缺乏的F10gene突变减少了血流中凝血因子X的量，导致称为I型的病症的形式。其他F10gene突变导致产生功能受损的凝血因子X蛋白，导致II型因子X缺乏症。凝血因子X的数量或功能减少可防止血液正常凝结，从而导致严重的异常出血事件。"}]}' WHERE id = 18979;
UPDATE genes SET GHR_metadata_ch = '{"gene": "GRIN2A", "conditions": [{"name": "癫痫失语症谱", "detail": "在一些患有癫痫失语症谱系的人中，已经鉴定出GRIN2Agene的50多个突变。这组病症的特征是大脑中的异常电活动，通常在慢波睡眠期间；丧失言语和语言能力，有时丧失其他发展技能；在许多情况下，反复发作（癫痫病）。Landau-Kleffner综合征（LKS）和癫痫性脑病在睡眠中持续出现尖峰和波动（ECSWS）处于频谱的最末端，而儿童癫痫伴中央颞尖峰（CECT）处于轻度末端。其他几种情况也有中等严重程度的体征和症状。（换行）许多GRIN2Agene突变导致产生无功能的GluN2A蛋白或根本阻止任何蛋白的产生。这些突变可能导致含有GluN2A亚基的NMDA受体数量减少。研究人员怀疑，结果，信号通过其他类型的NMDA受体发生，这些受体更容易激发神经元，导致大脑中的信号过度。其他突变导致异常GluN2A蛋白的产生，这些蛋白可能会改变NMDA受体的功能，从而可能增加信号传导。大脑中神经元的过度活动可导致癫痫发作和其他异常大脑活动，并可能导致神经元死亡。GluN2A的变化似乎特别影响涉及语音和语言的大脑区域中的信号传导，并在慢波睡眠期间破坏大脑活动，从而导致这组疾病的几种体征和症状。目前尚不清楚为什么一些GRIN2Agene突变导致相对温和的状况，而另一些则导致更严重的体征和症状。"}, {"name": "阿片成瘾", "detail": "遗传学家庭参考提供有关阿片类药物成瘾的信息。"}, {"name": "其他疾病", "detail": "在患有神经系统疾病的人中发现了GRIN2Agene突变，这些人的特征与癫痫失语症谱系障碍（如上所述）相似，但缺乏一致的语言问题。这些共有的特征可能包括反复发作（癫痫），通常是一种类型，起源于大脑罗兰区域的异常活动（罗兰性癫痫）；智力障碍；和发育迟缓。一些具有删除GRIN2Agene以及其他附近gene的16号染色体突变的人也具有异常的面部特征。正在研究不同GRIN2Agene突变的不同影响以及它们如何导致不同的神经系统疾病。"}]}' WHERE id = 18983;
UPDATE genes SET GHR_metadata_ch = '{"gene": "RGS9", "conditions": [{"name": "运动迟缓", "detail": "已发现RGS9gene中至少有两个突变会引起运动迟缓，这是一种罕见的影响视力的疾病。在患有弱视症的人中，眼睛比平时更适应不断变化的光线conditions（例如，在阳光明暗的日子里从黑暗的电影院走到日光下或开车进入黑暗的隧道）。一些受影响的人也很难看到小的移动物体，例如网球。（换行）引起运动迟缓的RGS9gene突变大大降低或消除了RGS9-1在光感受器中的功能。（这些突变似乎不会影响大脑中RGS9-2的功能。）RGS9-1功能的丧失阻止了光感受器在响应光后迅速恢复。通常，它们会在一秒钟内恢复到静止状态，但是在具有RGS9gene突变的人中，可能需要十秒或更长的时间。在此期间，光感受器不能响应光。这种延迟会导致暂时失明，以响应不断变化的光线conditions，并可能干扰看到小物体运动时的物体。"}]}' WHERE id = 18985;
UPDATE genes SET GHR_metadata_ch = '{"gene": "NPHS1", "conditions": [{"name": "先天性肾病综合征", "detail": "已发现NPHS1gene中至少有250个突变会导致先天性肾病综合征。这种情况是一种肾脏疾病，始于婴儿期，通常会在儿童早期导致不可逆的肾衰竭（终末期肾脏疾病）。（换行）NPHS1gene突变占芬兰型先天性肾病综合征的所有病例。这种情况在芬兰血统的人中发现。几乎所有病例都有两个特定的突变，这两个突变均导致异常短的，无功能的肾素蛋白。第一个突变称为Finnmajor，写为L41fsX90，占78%的病例。第二个突变称为Finnminor，写为R1109X，占16%的病例。（换行）NPHS1gene突变也可能导致非芬兰人的先天性肾病综合征。这些突变中的大多数会导致异常的肾素蛋白被困在细胞内，无法到达足细胞表面。足细胞表面功能性肾素的缺乏会损害正常缝隙隔膜的形成。没有功能性狭缝隔膜，分子异常通过肾脏并在尿液中排泄。肾脏的过滤能力从出生开始就恶化，最终导致终末期肾脏疾病。"}]}' WHERE id = 18995;
UPDATE genes SET GHR_metadata_ch = '{"gene": "KRT81", "conditions": [{"name": "Monilethrix", "detail": "在患有monilethrix的人中，已经鉴定出KRT81gene的至少两个突变，monilethrix是一种头发状况，其特征是呈串珠状的头发和容易断裂的短而脆的头发。与这种情况相关的突变会改变K81蛋白中的单个蛋白结构单元（氨基酸）。氨基酸变化通常发生在K81蛋白的一个区域，该区域被认为在中间丝形成中很重要。在患有monilethrix的人中，受影响的毛干的皮质似乎异常。但是，尚不清楚KRT81gene的突变如何与皮质异常或头发的串珠外观相关。"}]}' WHERE id = 19018;
UPDATE genes SET GHR_metadata_ch = '{"gene": "GAN", "conditions": [{"name": "巨大的轴索神经病", "detail": "在患有巨大轴突神经病的人中已经鉴定出GANgene中至少47个突变，这是一种遗传性疾病，导致运动和感觉丧失逐渐恶化。巨大的轴突神经病的特征是异常大（巨大）和恶化的轴突。（换行）GANgene突变导致不稳定的gigaxonin蛋白容易分解，导致细胞中的gigaxonin比正常情况少得多。在神经元中，gigaxonin的减少导致应该被泛素-蛋白酶体系统破坏的神经丝的积累。神经丝密集地堆积在患有巨大轴突神经病的人的巨大轴突中。巨大的轴突常见于周围神经，在大脑和脊髓（中枢神经系统）以及身体其他部位之间传递信号。但是，中枢神经系统的轴突也会受到影响。这些异常轴突不能正确传输信号并最终恶化，从而在周围神经和中枢神经系统中引起严重问题。"}]}' WHERE id = 19028;
UPDATE genes SET GHR_metadata_ch = '{"gene": "PDHX", "conditions": [{"name": "缺乏症", "detail": "PDHXgene的突变会在少数人中引起丙酮酸脱氢酶缺乏症。这种情况的特征是体内一种称为乳酸的化学物质（乳酸性酸中毒），发育迟缓和神经系统问题可能危及生命。与丙酮酸脱氢酶缺乏症相关的PDHXgene突变导致完全不存在E3结合蛋白。这种蛋白质的丢失会损害E3酶与丙酮酸脱氢酶复合物的结合，从而导致复合物活性降低。随着这种复合物功能的降低，丙酮酸积累并在另一种化学反应中转化为乳酸，引起乳酸性酸中毒。另外，细胞能量的产生减少了。特别依赖于这种能量形式的大脑受到严重影响，导致与丙酮酸脱氢酶缺乏症相关的神经系统问题。"}, {"name": "利综合征", "detail": "遗传学家庭参考提供有关Leigh综合征的信息。"}]}' WHERE id = 19033;
UPDATE genes SET GHR_metadata_ch = '{"gene": "IRAK4", "conditions": [{"name": "IRAK-4缺陷", "detail": "在IRAK-4缺乏症患者中已鉴定出IRAK4gene至少20个突变，IRAK-4缺乏症是一种免疫系统疾病，可导致反复侵袭性细菌感染。这些gene突变导致异常短的，无功能的IRAK-4蛋白或根本没有蛋白。功能性IRAK-4蛋白的丧失会阻止炎症的发作，以响应通常有助于抵抗感染的病原体或细胞因子。由于早期免疫反应不足，细菌感染经常发生并变得严重和侵入性。"}]}' WHERE id = 19041;
UPDATE genes SET GHR_metadata_ch = '{"gene": "IRGM", "conditions": [{"name": "克罗恩病", "detail": "IRGMgene内或附近的几种变异与发生克罗恩病的风险增加有关。这种增加的风险主要发生在北欧血统的人身上。IRGM变异会改变DNA区域中的单个DNA构建块（核苷酸），从而可以调节何时以及如何产生IRGM蛋白。研究人员怀疑涉及IRGM蛋白的变化可能会破坏自噬过程，阻止免疫系统有效破坏有害细菌。对肠壁细菌的异常免疫反应可能导致慢性炎症和克罗恩病特有的消化问题。"}]}' WHERE id = 19064;
UPDATE genes SET GHR_metadata_ch = '{"gene": "FH", "conditions": [{"name": "富马酸酶缺乏症", "detail": "富马酸酶缺乏症患者中已鉴定出20多种FHgene突变，这种疾病主要影响大脑，在婴儿期通常致命。富马酸酶缺乏症发生在每个细胞中继承FHgene两个突变拷贝的个体中。这些突变大多数将富马酸酶中的一个蛋白质结构单元（氨基酸）替换为另一个氨基酸。这些变化破坏了酶帮助将富马酸转化为苹果酸的能力，干扰了柠檬酸循环中该反应的功能。为细胞产生能量的过程的损害对发育中的大脑中的细胞特别有害，并且这种损害导致富马酸酶缺乏症的体征和症状。"}, {"name": "遗传性平滑肌瘤病和肾细胞癌", "detail": "据报道，FHgene中有70多个突变会导致遗传性平滑肌瘤病和肾细胞癌（HLRCC）。这些突变大多数将富马酸酶中的一个氨基酸替换为另一个氨基酸。(换行)HLRCC是一种疾病，其中受影响的个体倾向于在皮肤和女性子宫中发展出包含平滑肌组织（平滑肌瘤）的良性肿瘤。这种情况也增加了患肾癌的风险。HLRCC患者出生时每个细胞中都有一个FHgene的突变拷贝。某些细胞中FHgene的第二个拷贝也可能由于环境因素（例如来自太阳的紫外线辐射）或细胞分裂过程中DNA拷贝本身发生的错误而获得突变。这些变化称为体细胞突变，不是遗传的。（换行）FHgene突变可能会干扰酶在柠檬酸循环中的作用，从而导致富马酸酯的积累。研究人员认为，过量的富马酸盐可能会干扰细胞内氧水平的调节。具有两个FHgene突变拷贝的细胞中的慢性缺氧（缺氧）可能促进肿瘤形成并导致发展为平滑肌瘤和肾细胞癌的趋势。"}, {"name": "原发性大结节性肾上腺增生", "detail": "遗传学家庭参考提供有关原发性大结节性肾上腺增生的信息。"}]}' WHERE id = 19076;
UPDATE genes SET GHR_metadata_ch = '{"gene": "AR", "conditions": [{"name": "雄激素不敏感综合征", "detail": "已经发现ARgene的突变会引起雄激素不敏感综合征，这是一种影响出生前和青春期性发育的疾病。患有这种疾病的人在遗传上是男性，但他们无法对雄激素作出反应，并表现出女性的性别特征或男性和女性性发育的迹象。"}, {"name": "脊髓和延髓肌萎缩", "detail": "已经发现ARgene的突变会引起脊髓和延髓性肌萎缩，这是一种控制肌肉运动（运动神经元）的专门神经细胞疾病。这是由于ARgene的CAG三核苷酸重复序列的扩增及其积累引起的，导致运动神经元逐渐丧失，从而导致肌肉无力和消瘦（萎缩）。"}, {"name": "雄激素性脱发", "detail": "ARgene突变与雄激素性脱发的风险增加有关，雄激素性脱发是一种脱发疾病，称为男性型秃发，女性型秃发。这是由于ARgene变异导致头皮活动增加所致。"}, {"name": "前列腺癌", "detail": "ARgene的突变与前列腺癌有关，前列腺癌是男性所见的疾病。该病症最初无症状，并且随着肿瘤变大而表现出症状，并且与开始或停止尿液流动困难，不能完全排空膀胱的感觉，尿液或精液中的血液或射精疼痛相关。"}]}' WHERE id = 19086;
UPDATE genes SET GHR_metadata_ch = '{"gene": "REEP1", "conditions": [{"name": "远端遗传性运动神经病，V型", "detail": "遗传学家庭参考资料提供有关V型远端遗传性运动神经病的信息。"}, {"name": "痉挛性截瘫31型", "detail": "已发现REEP1gene中至少有44个突变引起痉挛性截瘫31型。这种情况的特征是肌肉僵硬（痉挛）和下肢麻痹（截瘫），这是由触发肌肉运动的神经细胞（神经元）变性引起的。导致这种情况的大多数REEP1gene突变会从gene中插入或去除小块DNA，或改变gene说明用于制备蛋白质的方式。这些突变通常会导致短而无功能的蛋白质迅速分解。结果，功能性REEP1蛋白减少。（换行）目前尚不清楚REEP1gene突变如何导致31型痉挛性截瘫的体征和症状。研究人员表明，受影响个体细胞中的线粒体产生能量的能力较弱，这可能导致神经元死亡并导致痉挛性截瘫31型的进行性运动问题；但是，导致这种情况的确切机制尚不清楚。"}]}' WHERE id = 19089;
UPDATE genes SET GHR_metadata_ch = '{"gene": "KRT83", "conditions": [{"name": "Monilethrix", "detail": "KRT83gene的突变会导致monilethrix，这是一种头发状况，其特征是呈串珠状的头发和容易断裂的短而脆的头发。与这种情况相关的突变会改变K83蛋白中的单个蛋白结构单元（氨基酸）。氨基酸变化通常发生在K83蛋白的一个区域，该区域被认为在中间丝形成中很重要。在患有monilethrix的人中，受影响的毛干的皮质似乎异常。但是，尚不清楚KRT83gene的突变如何与皮质异常或头发的串珠外观相关。"}]}' WHERE id = 19090;
UPDATE genes SET GHR_metadata_ch = '{"gene": "KRT16", "conditions": [{"name": "Pachyonychia congenita", "detail": "在先天性甲癣患者中已鉴定出KRT16gene至少19个突变，这是一种罕见病，主要影响指甲和皮肤。在大多数情况下，这种情况在生命的最初几个月内变得明显。与先天性甲癣相关的大多数KRT16gene突变都会改变角蛋白16中的单个蛋白质结构单元（氨基酸）。一些突变从蛋白质中删除了少量氨基酸。(换行)导致先天性甲癣的KRT16gene突变改变了角蛋白16的结构，阻止了它与角蛋白6a有效相互作用并干扰了角蛋白中间丝网络的组装。没有这个网络，皮肤细胞变得脆弱，容易受损，使皮肤抵抗摩擦和轻微创伤的能力降低。即使是步行等正常活动也会导致皮肤细胞分解，导致形成严重的疼痛水疱和老茧。此外，脆弱的皮肤细胞可能会因损伤而异常产生更多的角蛋白，这会使皮肤问题更加严重。有缺陷的角蛋白16还会破坏其他组织（如毛囊和指甲）的生长和功能，这就解释了为什么先天性厚甲症的体征和症状也会影响身体的其他部位。"}]}' WHERE id = 19123;
UPDATE genes SET GHR_metadata_ch = '{"gene": "EVC2", "conditions": [{"name": "埃利斯·范·克雷维德综合征", "detail": "已发现EVC2gene中的30多个突变会导致Ellis-van Creveld综合征，这是一种以侏儒症，指甲和牙齿异常以及心脏缺陷为特征的遗传性疾病。导致这种情况的突变发生在每个细胞中EVC2gene的两个拷贝中。大多数遗传变化导致产生异常小的，无功能的EVC2蛋白。尽管尚不清楚该蛋白功能的丧失如何成为Ellis-van Creveld综合征的体征和症状的基础，但研究人员认为，它可能会阻止发育中的胚胎中正常的声波刺猬信号传导。这种信号通路的问题可能最终导致这种情况下出现的异常骨骼生长和心脏缺陷。"}, {"name": "Weyers肢端发育不良", "detail": "已发现EVC2gene中至少有3个突变会导致Weyers肢端发育不良，这种情况会影响牙齿，指甲和骨骼的发育。它的体征和症状与Ellis-van Creveld综合征相似，但通常较轻。（换行）导致Weyers肢端发育不良的突变发生在每个细胞的EVC2gene的一个拷贝中。每个突变都会改变EVC2蛋白末端附近的单个蛋白构建块（氨基酸）。这些突变可能导致蛋白质异常短而功能异常的版本的产生。目前尚不清楚缺陷蛋白如何导致Weyers肢端发育不良的特定特征。研究表明，它会干扰发育中的胚胎中的声波刺猬信号，破坏牙齿，指甲和骨骼的正常形成和生长。"}]}' WHERE id = 19126;
UPDATE genes SET GHR_metadata_ch = '{"gene": "PARK7", "conditions": [{"name": "帕金森病", "detail": "研究人员已经鉴定出25种以上的PARK7gene突变，这些突变可导致帕金森氏病，帕金森氏病的特征是运动和平衡方面的进行性问题。这些突变与该疾病的早期发作形式有关，该疾病始于50岁之前。一些PARK7gene突变会导致异常小的DJ-1蛋白或改变用于制造该蛋白的结构单元（氨基酸）。改变后的蛋白质不稳定，如果有的话，不能正常工作。其他突变会删除PARK7gene的大部分，从而阻止任何功能性DJ-1蛋白的产生。(换行)目前尚不清楚功能性DJ-1蛋白的丧失如何导致帕金森病。一些研究表明，PARK7gene突变会破坏蛋白质的伴侣功能，从而导致错误折叠或损坏的蛋白质有毒堆积，并最终导致细胞死亡。另一种可能性是PARK7gene突变会损害蛋白质保护细胞免受破坏性氧化应激的能力。制造化学信使多巴胺的神经细胞特别容易受到氧化应激的影响。随着保护作用的减弱，自由基可能会造成足够的伤害来杀死这些神经细胞。产生多巴胺的神经细胞的进行性丧失是帕金森病的特征。这些细胞的死亡削弱了大脑和肌肉之间的交流，最终大脑无法控制肌肉运动。"}]}' WHERE id = 19130;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ADCY5", "conditions": [{"name": "ADCY5相关的运动障碍", "detail": "在患有ADCY5相关性运动障碍的人中至少鉴定出6个ADCY5gene突变，该疾病会导致异常的非自愿运动。这些突变被认为可增强腺苷酸环化酶5的酶活性并导致细胞内cAMP水平升高，因此被描述为“功能获得”突变。其他ADCY5gene突变会阻止腺苷酸环化酶5的产生。目前尚不清楚这两种类型的突变如何导致这种疾病中发生的异常运动。"}, {"name": "其他疾病", "detail": "ADCY5gene的某些正常变异（多态性）与2型糖尿病的风险增加有关，2型糖尿病是最常见的糖尿病形式，导致血糖控制受损。cAMP通常随着血糖的增加而增加，并参与刺激胰岛素产生的信号传导。研究人员认为，与2型糖尿病风险增加相关的多态性可能会降低腺苷酸环化酶5产生cAMP的能力，从而导致2型糖尿病对糖的异常反应。"}]}' WHERE id = 19136;
UPDATE genes SET GHR_metadata_ch = '{"gene": "CHD7", "conditions": [{"name": "CHARGE综合征", "detail": "已经发现CHD7gene的突变会引起CHARGE综合征，CHARGE综合征是一种影响身体许多部位的疾病，其特征是淋巴瘤，心脏缺陷，胆道闭锁，生长迟缓，耳朵和生殖器异常。突变会导致异常蛋白质，该蛋白质被认为会破坏染色质重塑和gene表达的调控。"}, {"name": "卡门氏症候群", "detail": "已经在患有Kallmann综合征的人中鉴定出CHD7gene的突变，该疾病的特征是性腺功能低下性腺功能减退和嗅觉受损。受影响的个体表现出一些较不严重的CHARGR综合征特征，例如耳朵形状异常和听力下降突变导致蛋白质改变，从而影响性激素产生所必需的嗅球和神经元的发育。"}]}' WHERE id = 19169;
UPDATE genes SET GHR_metadata_ch = '{"gene": "NAGA", "conditions": [{"name": "辛德勒病", "detail": "在辛德勒病患者中已鉴定出约七个NAGAgene突变。据信这些突变中的大多数会改变α-N-乙酰半乳糖胺酶的3维形状，从而干扰其分解糖蛋白和糖脂的能力。这些物质积聚在溶酶体中并导致细胞失效并最终死亡。身体各种组织和器官中的细胞损伤导致神经问题和辛德勒病的其他体征和症状。"}]}' WHERE id = 19170;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ABCB11", "conditions": [{"name": "良性复发性肝内胆汁淤积", "detail": "ABCB11gene的突变会导致一种称为良性复发性肝内胆汁淤积2型（BRIC2）的疾病，其特征是胆汁分泌受损或减少，胆汁盐转运和黄疸。此外，对个体进行BRIC2的晚期诊断会导致一种称为2型进行性家族性肝内胆汁淤积症的严重疾病。"}, {"name": "进行性家族性肝内胆汁淤积", "detail": "ABCB11gene的突变会导致一种严重的疾病，称为进行性家族性肝内胆汁淤积2型（PFIC2），其特征是胆盐运输减少或完全不存在，肝细胞蓄积导致肝衰竭。同样，具有非功能性BSEP蛋白的个体患肝细胞癌的风险增加。"}, {"name": "妊娠期肝内胆汁淤积", "detail": "具有ABCB11gene突变的女性患妊娠肝内胆汁淤积症的风险增加，其特征是胆汁分泌受损，妊娠晚期瘙痒，后来消失。怀孕期间雌激素和孕激素水平升高被认为有助于发展这种疾病的症状。在肝内胆汁淤积的女性中高度检测到最常见的错义突变（V444A），这导致肝脏中BSEP蛋白的减少。"}]}' WHERE id = 19171;
UPDATE genes SET GHR_metadata_ch = '{"gene": "CHRNA2", "conditions": [{"name": "常染色体显性遗传夜间额叶癫痫", "detail": "已发现CHRNA2gene的突变会引起常染色体显性遗传性夜间额叶癫痫（ADNFLE），这种疾病的特征是在睡眠中最常发生癫痫发作。CHRNA2突变使该通道对神经递质乙酰胆碱更加敏感，使其比平时更容易打开，从而改变了神经元信号传导。"}]}' WHERE id = 19179;
UPDATE genes SET GHR_metadata_ch = '{"gene": "CACNA1C", "conditions": [{"name": "Timothy综合征", "detail": "发现CACNA1Cgene突变与经典或1型蒂莫西综合征有关，蒂莫西综合征是一种罕见疾病，通常会影响心脏，手指（手指和脚趾）和神经系统。CaV1.2通道外显子8a的突变是造成这种情况的原因，导致心肌中钙离子超载，从而导致心律不齐和自闭症谱系障碍，癫痫发作和智力障碍。其他症状包括皮肤综合症，秃发，龋齿，频繁感染，低血糖和体温过低。"}, {"name": "布鲁格达氏症候群", "detail": "发现CACNA1Agene突变与Brugada综合征有关，Brugada综合征通常会破坏心脏的正常节律，其特征是心律不齐导致晕厥，癫痫发作，呼吸困难或猝死。"}]}' WHERE id = 19188;
UPDATE genes SET GHR_metadata_ch = '{"gene": "MLPH", "conditions": [{"name": "格里切利综合征", "detail": "已发现MLPHgene中至少有一个突变引起Griscelli综合征。这种遗传变化导致一种称为3型的疾病，其特征是皮肤异常光（色素沉着不足）和银灰色头发。与其他形式的Griscelli综合征不同，3型不涉及大脑或免疫系统异常。（换行）已知的MLPHgene突变改变了黑素蛋白中的单个蛋白构建块（氨基酸）。具体而言，它用蛋白质35位的氨基酸色氨酸取代了氨基酸精氨酸（写为Arg35Trp或R35W）。这种突变减少了黑色素生成的量；产生的少量蛋白质无法与由MYO5A和RAB27Agene制成的蛋白质形成复合物。没有这种复合物，黑素体就不能转运到黑素细胞的边缘。相反，它们聚集在黑素细胞中心附近，将黑色素捕获在这些细胞内，并防止皮肤和头发正常色素沉着。"}]}' WHERE id = 19211;
UPDATE genes SET GHR_metadata_ch = '{"gene": "EVC", "conditions": [{"name": "埃利斯·范·克雷维德综合征", "detail": "据报道，EVCgene中有25个以上的突变会引起Ellis-van Creveld综合征，这是一种以侏儒症，指甲和牙齿异常以及心脏缺陷为特征的遗传性疾病。导致这种情况的突变发生在每个细胞中EVCgene的两个拷贝中。这些遗传变化破坏了EVC蛋白的正常功能，或导致该蛋白异常小而无功能的产生。尽管尚不清楚该蛋白功能的丧失如何成为Ellis-van Creveld综合征的体征和症状的基础，但研究人员认为，它可能会阻止发育中的胚胎中正常的声波刺猬信号传导。这种信号通路的问题可能最终导致这种情况下出现的异常骨骼生长和心脏缺陷。"}, {"name": "Weyers肢端发育不良", "detail": "已发现EVCgene中至少有一个突变会导致Weyers肢端发育不良的特征，从而影响牙齿，指甲和骨骼的发育。Weyers肢端发育不良的体征和症状与Ellis-van Creveld综合征相似，但通常较轻。（换行）导致Weyers acrofacial dysostosis的EVCgene突变发生在每个细胞中的一个gene拷贝中。它改变了EVC蛋白中的单个蛋白质构建块（氨基酸），用位置307处的氨基酸脯氨酸取代了氨基酸丝氨酸（写为Ser307Pro）。目前尚不清楚这种遗传变化如何导致Weyers肢端发育不良的特定特征。研究表明，异常蛋白质会干扰发育中的胚胎中的声波刺猬信号，破坏牙齿，指甲和骨骼的正常形成和生长。"}]}' WHERE id = 19222;
UPDATE genes SET GHR_metadata_ch = '{"gene": "KRT17", "conditions": [{"name": "Pachyonychia congenita", "detail": "在先天性甲癣患者中已鉴定出KRT17gene的20多个突变，这是一种罕见病，主要影响指甲和皮肤。在大多数情况下，这种情况在生命的最初几个月内变得明显。与先天性甲癣相关的大多数KRT17gene突变都会改变角蛋白17中的单个蛋白质结构单元（氨基酸）。（换行）负责先天性甲癣的KRT17gene突变改变角蛋白17的结构，阻止其与角蛋白6b有效相互作用并干扰角蛋白中间丝网络的组装。没有这个网络，皮肤细胞变得脆弱，容易受损，使皮肤抵抗摩擦和轻微创伤的能力降低。即使是步行等正常活动也会导致皮肤细胞分解，导致形成疼痛的水疱和老茧。在皮脂腺中，异常的角蛋白丝导致称为脂肪囊瘤的皮脂填充囊肿的发展。有缺陷的角蛋白17也会破坏其他组织的生长和功能，如毛囊和指甲，这就解释了为什么先天性厚甲症的症状和体征也会影响身体的其他部位。"}, {"name": "脂肪细胞瘤多重", "detail": "已经发现KRT17gene中的至少四个突变引起称为遗传性脂肪细胞瘤多重性的皮肤病。研究人员建议，这种情况可能是先天性甲癣的轻度形式（变异）（如上所述）。像先天性粗甲虫一样，遗传性脂肪细胞瘤多发性涉及称为脂肪细胞瘤的多个皮脂腺囊肿的发展。尽管可能出现轻度的指甲和牙齿异常，但大多数患有遗传性脂肪囊瘤多发性疾病的人没有先天性甲癣的其他特征。（换行）导致遗传性脂肪细胞瘤多重的KRT17gene突变改变角蛋白17蛋白中的单个氨基酸，这干扰角蛋白中间丝网络的组装。在皮脂腺中，这些角蛋白异常导致脂肪细胞瘤的发展。目前尚不清楚为什么这些含有皮脂的囊肿通常是这种疾病的唯一特征。"}]}' WHERE id = 19223;
UPDATE genes SET GHR_metadata_ch = '{"gene": "CIITA", "conditions": [{"name": "裸淋巴细胞综合征II型", "detail": "已经发现CIITAgene中的十几个突变引起称为裸淋巴细胞综合征II型（BLS II）的免疫系统疾病。BLS II是一种联合免疫缺陷病（CID），其中受影响的个体实际上没有针对外来入侵者的免疫保护。因此，患有BLS II的个体在呼吸道，胃肠道和泌尿道中持续感染，这可能危及生命。（换行）BLS II中涉及的CIITAgene突变导致缺乏功能性CIITA蛋白。缺乏CIITA来协调打开MHC II类gene转录的因子会阻止MHC II类蛋白的产生。因此，淋巴细胞和其他免疫细胞表面缺乏任何MHC II类蛋白，身体难以清除细菌，病毒和真菌，导致BLS II特有的持续感染。"}, {"name": "自身免疫性艾迪生病", "detail": "遗传学家庭参考提供有关自身免疫性艾迪生病的信息。"}]}' WHERE id = 19241;
UPDATE genes SET GHR_metadata_ch = '{"gene": "TYROBP", "conditions": [{"name": "多囊性脂质膜性骨发育不良伴硬化性白质脑病", "detail": "在患有硬化性白质脑病（通常称为PLOSL）的多囊性脂质膜性骨发育不良的人中，已经鉴定出TYROBPgene中至少有六个突变。已发现一种TYROBP突变在芬兰血统的所有受影响人群中引起PLOSL。这种突变删除了TYROBPgene的很大一部分，从而阻止了细胞从该gene产生任何蛋白质。其他种群中的突变会导致蛋白质异常短而无功能的产生或阻止蛋白质到达细胞表面。(换行)研究人员认为PLOSL的体征和症状与破骨细胞和小胶质细胞中TYROBP-TREM2信号缺陷有关。这种疾病所见的骨骼异常可能与破骨细胞功能异常有关，破骨细胞在骨骼重塑过程中吸收骨组织的能力较弱。在中枢神经系统中，通过TYROBP-TREM2复合物的信号传导缺陷会导致小胶质细胞的广泛异常。研究人员正在努力确定这些异常如何导致与PLOSL相关的神经系统问题。"}]}' WHERE id = 19251;
UPDATE genes SET GHR_metadata_ch = '{"gene": "DES", "conditions": [{"name": "肌原纤维肌病", "detail": "已经发现DESgene中的40多个突变引起肌原纤维肌病。这些突变大多数会改变结蛋白中的单个蛋白质结构单元（氨基酸）。突变的结蛋白与肌节中的其他肌肉蛋白聚集在一起形成团块（聚集体）。聚集体阻止这些蛋白质正常运作。功能异常的结蛋白蛋白无法与Z盘正确相互作用，从而导致肌节结构异常和肌原纤维形成问题。导致肌原纤维肌病的DESgene突变会损害肌纤维的功能，导致肌无力和这种情况的其他特征。与其他gene突变引起的肌原纤维肌病患者相比，具有DESgene突变的人更有可能使心肌（心肌病）减弱。在某些情况下，心肌病是这种情况的首发症状。"}, {"name": "家族性扩张型心肌病", "detail": "遗传学家庭参考提供有关家族性扩张型心肌病的信息。"}, {"name": "其他疾病", "detail": "DESgene中的突变还引起一种称为扩张型心肌病1I型的心脏病。这种情况会扩大（扩张）并削弱心肌，阻止其有效地抽血。DESgene突变也被证明会引起另一种称为限制性心肌病的心肌病，其中心肌僵硬，每次收缩后都不能完全放松。尽管心肌病是肌原纤维肌病的征兆，但这些形式的心肌病与骨骼肌无力无关。DESgene中的（换行）突变也可能导致异常的心跳（心律不齐），这可能导致心力衰竭和猝死。(换行)研究人员不确定为什么DESgene中的某些突变会导致这些心脏问题，而不是肌原纤维肌病。"}]}' WHERE id = 19256;
UPDATE genes SET GHR_metadata_ch = '{"gene": "PMP22", "conditions": [{"name": "进行性神经性腓骨肌萎缩症", "detail": "PMP22gene的突变会导致几种形式的神经系统疾病，称为Charcot-Marie-Tooth病。（换行）每个细胞中额外的PMP22gene拷贝是导致1A型Charcot-Marie-Tooth病的最常见遗传变化。额外的gene导致PMP22蛋白的过量生产，从而阻止了该蛋白的正确加工。功能性PMP22蛋白量的减少会损害髓磷脂的形成。未加工的PMP22也可能破坏其他雪旺氏细胞活性，从而导致髓磷脂不稳定和丢失（脱髓鞘）。脱髓鞘降低了周围神经激活用于运动的肌肉或将信息从感觉细胞传递回大脑的能力。通常从青春期开始，受影响的人会经历小腿和手部肌肉的无力和消瘦（萎缩），并且对触摸，热和冷的敏感性降低。(换行)1A型Charcot-Marie-Tooth病也是由添加，删除或更改用于制造PMP22蛋白的结构单元（氨基酸）的突变引起的。改变后的蛋白质可能以较慢的速度加工，并且某些蛋白质异常加工。PMP22加工的这些破坏损害了雪旺氏细胞的正常功能，导致脱髓鞘并产生1A型Charcot-Marie-Tooth病的体征和症状。(换行)一些患有1型Charcot-Marie-Tooth病（称为1E型）的人会经历听力损失。1E型与PMP22gene中特定的氨基酸取代和缺失有关。最常报告的引起听力损失的突变是将氨基酸丙氨酸替换为蛋白质67位的氨基酸脯氨酸（也称为Ala67Pro）。（换行）PMP22gene中的一些突变导致严重形式的Charcot-Marie-Tooth病，有时称为Dejerine-Sottas病或3型Charcot-Marie-Tooth病。这种形式的疾病通常始于婴儿期，导致肌肉无力和萎缩，并延迟了行走等运动技能的发展。"}, {"name": "压力性麻痹的遗传性神经病", "detail": "每个细胞丢失一份PMP22gene拷贝是遗传性神经病的最常见遗传原因，与压力性麻痹有关。这种疾病的特征是经常发生麻木，刺痛或肌肉功能丧失，通常是由四肢神经受压引起的。删除一个拷贝的PMP22gene可将PMP22蛋白的产生量减少约一半。这种疾病也是由PMP22gene突变引起的，该突变改变了PMP22蛋白中的单个氨基酸或导致异常小蛋白的产生。这些异常蛋白质迅速分解。PMP22蛋白缺乏的后果尚不清楚。PMP22蛋白的缺乏可能会影响髓磷脂覆盖的结构，从而损害神经冲动的传递。此外，这种蛋白质的损失似乎使神经无法从压迫中恢复，这也会中断神经信号传导，导致遗传性神经病的体征和症状，并伴有压力性麻痹。"}]}' WHERE id = 19280;
UPDATE genes SET GHR_metadata_ch = '{"gene": "F13A1", "conditions": [{"name": "XIII因子缺乏症", "detail": "已发现F13A1gene中至少有140个突变会导致遗传性因子XIII缺乏症，这是一种罕见的出血性疾病。如果不进行治疗，受影响的个体发生异常出血事件的风险会大大增加，包括威胁生命的颅骨内出血（颅内出血）。F13A1gene突变严重降低了因子XIII的A亚基的数量或活性。在大多数具有这些突变的人中，血液中功能因子XIII的水平低于正常水平的5%。因子XIII活性的丧失削弱了新的血栓并阻止它们有效地止血。"}, {"name": "其他疾病", "detail": "已经研究了F13A1gene中的几种常见变异（多态性）作为涉及异常凝血的疾病的可能危险因素。最常见的F13A1gene多态性改变a亚基关键部分的单个蛋白质结构单元（氨基酸），用蛋白质位置34处的氨基酸亮氨酸取代氨基酸缬氨酸（写为Val34Leu或V34L）。这种遗传变化加速了因子XIII的激活。已经研究了Val34Leu多态性与心脏病，中风，反复流产和其他几种疾病的关系。但是，这些研究的结果一直存在矛盾，目前尚不清楚多态性是否代表任何这些情况的主要危险因素。"}]}' WHERE id = 19311;
UPDATE genes SET GHR_metadata_ch = '{"gene": "KRT6B", "conditions": [{"name": "Pachyonychia congenita", "detail": "在先天性甲癣患者中已鉴定出KRT6Bgene至少有四个突变，这是一种罕见病，主要影响指甲和皮肤。在大多数情况下，这种情况在生命的最初几个月内变得明显。这些突变要么改变角蛋白6b中的单个蛋白质结构单元（氨基酸），要么从蛋白质中删除少量氨基酸。(换行)导致先天性甲癣的KRT6Bgene突变改变了角蛋白6b的结构，阻止了它与角蛋白17有效相互作用并干扰了角蛋白中间丝网络的组装。没有这个网络，皮肤细胞变得脆弱，容易受损，使皮肤抵抗摩擦和轻微创伤的能力降低。即使是步行等正常活动也会导致皮肤细胞分解，导致形成疼痛的水疱和老茧。在皮脂腺中，异常的角蛋白丝导致称为脂肪囊瘤的皮脂填充囊肿的发展。有缺陷的角蛋白6b还会破坏其他组织（如毛囊和指甲）的生长和功能，这就解释了为什么先天性厚甲症的体征和症状也会影响身体的其他部位。"}]}' WHERE id = 19327;
UPDATE genes SET GHR_metadata_ch = '{"gene": "FGFR4", "conditions": [{"name": "前列腺癌", "detail": "遗传学家庭参考提供有关前列腺癌的信息。"}, {"name": "癌症", "detail": "FGFR4gene中引起氨基酸转换（蛋白质构建块）的变异（多态性）与几种类型的癌症相关，例如发生在乳腺癌，结肠癌，头颈癌和前列腺癌中的癌症。在具有这种多态性的人中，甘氨酸被蛋白质氨基酸链中第388位的精氨酸取代。这种变化很常见，似乎发生在约50%的人类中。尽管它对健康人没有不良影响，但该突变与某些癌症中疾病进展的加速有关。（换行）FGFR4gene的异常激活和活性增加也与垂体瘤和胃癌，胰腺癌和卵巢癌的发展有关。"}]}' WHERE id = 19335;
UPDATE genes SET GHR_metadata_ch = '{"gene": "KRT6C", "conditions": [{"name": "Pachyonychia congenita", "detail": "已发现KRT6Cgene中至少有四个突变会导致先天性甲癣，这是一种罕见病，主要影响指甲和皮肤。在大多数情况下，这种情况在生命的最初几个月内变得明显。（换行）与先天性甲癣相关的突变之一改变角蛋白6c蛋白中的单个蛋白质构建块（氨基酸）。具体而言，此突变将氨基酸谷氨酸替换为蛋白质472位的氨基酸赖氨酸（写为Glu472Lys或E472K）。其他KRT6Cgene突变从角蛋白6c蛋白中删除一个或多个氨基酸。(换行)所有已知的KRT6Cgene突变都会改变角蛋白6c的结构，并干扰角蛋白中间丝网络的组装。没有这个网络，皮肤细胞变得脆弱，容易受损，使皮肤抵抗摩擦和轻微创伤的能力降低。即使是步行等正常活动也会导致皮肤细胞分解，导致形成严重的疼痛水疱和老茧。此外，脆弱的皮肤细胞可能会因损伤而异常产生更多的角蛋白，这会使皮肤问题更加严重。有缺陷的角蛋白6c也会破坏其他组织的生长和功能，例如毛囊和指甲，这就解释了为什么先天性厚甲症的体征和症状也会影响身体的其他部位。"}]}' WHERE id = 19337;
UPDATE genes SET GHR_metadata_ch = '{"gene": "EIF2B1", "conditions": [{"name": "白质消失的白质脑病", "detail": "EIF2B1gene的突变已在少数白质消失的白质脑病患者中发现。这些突变导致eIF2B功能部分丧失。eIF2B功能受损使人体细胞更难以调节蛋白质合成并应对不断变化的conditions和压力。研究人员认为，白质中的细胞（神经纤维被称为髓鞘的脂肪物质覆盖，能够隔离和保护神经）可能特别受到压力异常反应的影响，导致白质消失的白质脑病的体征和症状。"}]}' WHERE id = 19347;
UPDATE genes SET GHR_metadata_ch = '{"gene": "CPS1", "conditions": [{"name": "氨基甲酰磷酸合成酶I缺乏症", "detail": "已发现CPS1gene的突变会导致氨基甲酰磷酸合成酶I缺乏症，该疾病的特征是由于体内氨的积累而引起的神经系统问题。突变导致酶的形状异常短或错误，从而削弱了其在尿素循环中的功能。"}, {"name": "其他疾病", "detail": "CPS1gene中的突变（Thr1405Asn）与新生儿和因一氧化氮（NO）生成减少而接受骨髓移植的个体发生循环系统疾病的风险增加有关。"}]}' WHERE id = 19351;
UPDATE genes SET GHR_metadata_ch = '{"gene": "AKT3", "conditions": [{"name": "巨脑-多小脑回-多指-脑积水综合征", "detail": "已发现AKT3gene中的几个突变会导致巨脑-多微gyria-多指-脑积水（MPPH）综合征，这是一种罕见的疾病，会影响大脑的发育，导致巨脑（大脑和头部较大）以及出生前大脑结构的其他异常。另外，通过功能获得性AKT3突变增加的蛋白质活性也导致过度的细胞生长和分裂。"}, {"name": "其他疾病", "detail": "AKT3gene的一些突变也与其他脑生长障碍有关，如没有MPPH综合征其他特征的巨脑畸形、小头畸形、由于缺失引起的孤立性半巨脑畸形、功能增强型AKT3突变或体细胞嵌合体损害大脑发育，通过损害PI3K-AKT-mTOR通路。"}]}' WHERE id = 19355;
UPDATE genes SET GHR_metadata_ch = '{"gene": "RNASEH2A", "conditions": [{"name": "Aicardi-Goutières综合征", "detail": "在患有Aicardi-Goutières综合征的人中已鉴定出RNASEH2Agene的至少42个突变，该疾病涉及严重的脑功能障碍（脑病），皮肤病变以及与免疫系统异常激活相关的其他健康问题。导致Aicardi-Goutières综合征的RNASEH2Agene突变可能导致功能异常的RNase H2复合物。该复合物的异常功能可能破坏转录，DNA复制，DNA修复，细胞死亡（凋亡）或其他过程。这种破坏被认为导致细胞中不需要的DNA和RNA的积累。这些DNA和RNA片段可能被误认为是病毒入侵者的遗传物质，引发多种身体系统的免疫系统反应，导致严重的脑功能障碍（脑病），皮肤损伤以及Aicardi-Goutières综合征的其他体征和症状。"}]}' WHERE id = 19387;
UPDATE genes SET GHR_metadata_ch = '{"gene": "STK11", "conditions": [{"name": "珀茨－杰格斯综合征", "detail": "STK11gene的遗传突变会导致Peutz-Jeghers综合征，这种疾病的特征是胃肠道中发生称为错构瘤性息肉的非癌性生长，并且罹患多种类型癌症的风险大大增加。在患有这种疾病的人中已经鉴定出340多个STK11gene突变。这些突变中的许多导致产生异常短的，无功能的丝氨酸/苏氨酸激酶11酶。其他突变会改变用于构建酶的单个蛋白质结构单元（氨基酸）。STK11gene的突变会损害酶的抑癌功能，使细胞在没有控制或命令的情况下生长和分裂。这种不受控制的细胞生长可导致错构瘤性息肉和癌性肿瘤的形成。"}, {"name": "乳腺癌", "detail": "作为Peutz-Jeghers综合征的一部分（如上所述），STK11gene的遗传变化极大地增加了患乳腺癌以及其他类型癌症的风险。这些突变被认为仅占所有乳腺癌病例的一小部分。"}, {"name": "肺癌", "detail": "遗传学家庭参考提供有关肺癌的信息。"}, {"name": "卵巢癌", "detail": "遗传学家庭参考提供有关卵巢癌的信息。"}, {"name": "其他癌症", "detail": "在各种形式的癌症中都发现了STK11gene的非遗传（体细胞）突变。体细胞突变是在一个人的一生中获得的，仅存在于某些细胞中。它们不会作为癌症综合征的一部分发生。已经以称为非小细胞肺癌，宫颈癌，结肠直肠癌，称为黑素瘤的侵袭性皮肤癌和胰腺癌的肺癌形式鉴定了体细胞STK11gene突变。这些突变损害丝氨酸/苏氨酸激酶11的功能，丝氨酸/苏氨酸激酶11可以使细胞不受控制地生长和分裂，并有助于形成癌性肿瘤。"}]}' WHERE id = 19405;
UPDATE genes SET GHR_metadata_ch = '{"gene": "MLYCD", "conditions": [{"name": "丙二酰辅酶A脱羧酶缺乏症", "detail": "在丙二酰辅酶A脱羧酶缺乏症患者中已鉴定出MLYCDgene的20多个突变。这些突变中的一些导致异常短的，无功能的丙二酰辅酶A脱羧酶的产生，或阻止gene产生任何这种酶。其他突变会改变酶的结构，因此无法将其传递到需要的细胞部分（例如线粒体和过氧化物酶体）。(换行)丙二酰辅酶A脱羧酶的缺乏破坏了脂肪酸形成和体内分解的正常平衡。结果，脂肪酸不能转化为能量，这导致了这种疾病的特征，包括低血糖（低血糖）和称为心肌病的心脏病。脂肪酸加工的副产物积聚在组织中，这也导致丙二酰辅酶A脱羧酶缺乏症的体征和症状。"}]}' WHERE id = 19408;
UPDATE genes SET GHR_metadata_ch = '{"gene": "MMP2", "conditions": [{"name": "多中心骨溶解，结节和关节炎", "detail": "已经发现MMP2gene中至少有8个突变引起多中心骨溶解，结节和关节病（MONA），这是一种罕见的遗传性骨病，其特征在于骨组织丢失（骨溶解），特别是在手和脚中，以及描述为关节病的相关关节问题。每个已知的MMP2gene突变都会消除基质金属肽酶2酶的功能，从而阻止IV型胶原蛋白的正常裂解。目前尚不清楚酶活性的丧失如何导致MONA的特定特征。研究人员怀疑它以某种方式破坏了新骨生成的平衡和骨重建过程中现有骨的破坏，导致骨组织逐渐丧失。基质金属肽酶2的缺乏如何导致MONA的其他特征，例如皮肤下的坚硬肿块（皮下结节）和皮肤异常，尚不清楚。"}, {"name": "椎间盘疾病", "detail": "遗传学家庭参考提供有关椎间盘疾病的信息。"}]}' WHERE id = 19485;
UPDATE genes SET GHR_metadata_ch = '{"gene": "LITAF", "conditions": [{"name": "进行性神经性腓骨肌萎缩症", "detail": "LITAFgene中至少有8个突变会导致一种称为1C型的Charcot-Marie-Tooth病。这些突变中的每一个都会改变单个DNA构建模块（碱基对），从而改变了制备LITAF蛋白的说明。目前尚不清楚这些突变如何导致1C型Charcot-Marie-Tooth病。异常的LITAF蛋白可能会错误地降解对神经功能至关重要的蛋白。另一种可能性是改变后的LITAF蛋白不能破坏对神经细胞有毒的物质。"}]}' WHERE id = 19499;
UPDATE genes SET GHR_metadata_ch = '{"gene": "KRT6A", "conditions": [{"name": "Pachyonychia congenita", "detail": "在先天性粗甲虫患者中已鉴定出约40个KRT6Agene突变，这是一种罕见病，主要影响指甲和皮肤。在大多数情况下，这种情况在生命的最初几个月内变得明显。这些突变大多数会改变角蛋白6a中的单个蛋白质结构单元（氨基酸）。一些突变添加或删除少量氨基酸。(换行)导致先天性甲癣的KRT6Agene突变改变了角蛋白6a的结构，阻止了它与角蛋白16有效相互作用并干扰了角蛋白中间丝网络的组装。没有这个网络，皮肤细胞变得脆弱，容易受损，使皮肤抵抗摩擦和轻微创伤的能力降低。即使是步行等正常活动也会导致皮肤细胞分解，导致形成严重的疼痛水疱和老茧。此外，脆弱的皮肤细胞可能会因损伤而异常产生更多的角蛋白，这会使皮肤问题更加严重。有缺陷的角蛋白6a还会破坏其他组织（如毛囊和指甲）的生长和功能，这就解释了为什么先天性厚甲症的体征和症状也会影响身体的其他部位。"}]}' WHERE id = 19507;
UPDATE genes SET GHR_metadata_ch = '{"gene": "CD40LG", "conditions": [{"name": "X连锁的高IgM综合征", "detail": "已发现CD40LGgene中的几个突变会引起X连锁的高IgM综合征，这种疾病的特征是由于免疫系统功能异常而容易感染。这是由于CD40配体的异常或丧失，其不能与CD40结合，损害免疫球蛋白合成和T细胞分化。"}]}' WHERE id = 19528;
UPDATE genes SET GHR_metadata_ch = '{"gene": "EDA", "conditions": [{"name": "少汗性外胚层发育不良", "detail": "在少汗性外胚层发育不良的人群中，EDAgene已鉴定出80多种不同的突变。这些突变导致该疾病的X连锁形式，占所有多汗症外胚层发育不良病例的95%。（X连锁疾病是由两个性染色体之一的X染色体上的gene突变引起的。）（换行）EDAgene中的一些突变改变单个DNA构建块（碱基对），而其他突变插入或删除gene中的遗传物质。这些变化导致产生非功能性形式的ectodysplasin a蛋白。这种异常蛋白质无法触发外胚层和中胚层之间正常相互作用所需的化学信号。没有这些信号，毛囊，牙齿，汗腺和其他外胚层结构将无法正常形成，从而导致汗湿性外胚层发育不良的特征。"}]}' WHERE id = 19530;
UPDATE genes SET GHR_metadata_ch = '{"gene": "MTRR", "conditions": [{"name": "同型半胱氨酸尿症", "detail": "在高半胱氨酸尿症患者中已鉴定出MTRRgene至少20个突变。这些突变中的一些改变了蛋氨酸合酶还原酶中的单个氨基酸。其他突变会导致酶的异常小而无功能的版本。所有这些突变阻止酶正常运行。没有蛋氨酸合酶还原酶，蛋氨酸合酶不能将高半胱氨酸转化为蛋氨酸。结果，同型半胱氨酸在血流中积聚，并且蛋氨酸的量减少。一些过量的同型半胱氨酸会从尿液中排出。研究人员尚未确定同型半胱氨酸和蛋氨酸水平的改变如何导致与同型半胱氨酸尿症相关的健康问题。"}, {"name": "其他疾病", "detail": "MTRRgene的特定版本（变体）可能与出生前各种健康问题的风险增加有关。该变体用MTRRgene第66位的核苷酸鸟嘌呤（写为A66G）替换了称为腺嘌呤的DNA（核苷酸）构建基块。这种变异与大脑和脊髓发育过程中发生的先天缺陷（神经管缺陷）有关。这种变异也可能增加患有唐氏综合症的孩子的风险，唐氏综合症是一种以智力残疾和相关健康问题为特征的疾病。研究人员尚未确定为什么MTRRgene的A66G变体与神经管缺陷或唐氏综合症的风险之间可能存在联系。许多因素在确定这些疾病的风险中起作用。"}]}' WHERE id = 19534;
UPDATE genes SET GHR_metadata_ch = '{"gene": "PURA", "conditions": [{"name": "5q31.3微缺失综合症", "detail": "5q31.3微缺失综合症是由染色体变化引起的，其中每个细胞中缺失了一小块5号染色体。这种罕见病的特征是言语和行走发育严重延迟或受损，肌张力弱（肌张力低下），呼吸问题，反复发作（癫痫）或癫痫样发作以及独特的面部特征。导致这种情况的缺失发生在染色体长臂（q）的指定q31.3位置。缺失的大小范围可以从数千到数百万个DNA构建块（碱基对）。缺失的区域通常包含至少三个gene，其中一个是PURA。(换行)丢失一份PURAgene被认为会改变正常的大脑发育并损害神经元的功能，从而导致5q31.3微缺失综合征患者的发育迟缓，肌张力低下和其他神经系统问题。一些研究表明，附近另一个称为NRG2的gene的丢失会增加体征和症状的严重程度。目前尚不清楚缺失区域中其他gene的缺失如何导致5q31.3微缺失综合征的发展。"}, {"name": "紫癜综合症", "detail": "已发现至少22种PURAgene突变会引起PURA综合征，该疾病的特征是智力残疾，言语和行走发育迟缓以及癫痫。这些遗传变化中的一些从PURAgene中去除了一小部分DNA。其他改变Pur中的单个构建块（氨基酸）？蛋白质或导致产生异常短的蛋白质。这些突变被认为可以减少功能性Pur的数量？蛋白质。虽然不了解Pur的部分损失如何？功能导致PURA综合征的体征和症状，研究人员怀疑它可能会改变正常的大脑发育并损害神经元的功能，从而导致患有此病的人出现发育问题和癫痫发作。"}]}' WHERE id = 19549;
UPDATE genes SET GHR_metadata_ch = '{"gene": "WFS1", "conditions": [{"name": "非综合征性听力损失", "detail": "在患有一种称为DFNA6的非综合征性听力损失的个体中，已鉴定出30多种WFS1gene突变。患有这种疾病的人有听力损失，没有影响身体其他部位的相关体征和症状。患有DFNA6非综合征性耳聋的人无法听到低音（低频声音），例如来自tuba或the＆quot的声音；m＆quot；在月球里。大多数WFS1gene突变会改变用于制造狼精的单个蛋白质结构单元（氨基酸）。WFS1gene突变可能会导致wolframin蛋白的三维形状发生变化，从而影响其功能。据认为，内耳细胞的丧失，以及负责听力的大脑部分细胞正常功能的破坏，导致受影响个体的听力丧失。研究人员还建议，改变的狼精会干扰内耳中钙的平衡，从而干扰听力过程。"}, {"name": "Wolfram综合征", "detail": "已发现WFS1gene中至少有200个突变引起Wolfram综合征。这种情况的特点是缺乏胰岛素导致血糖升高（糖尿病），神经退化，从眼睛到大脑的信息（视神经萎缩），以及其他一些涉及泌尿道，大脑的特征，和听力。每个细胞中WFS1gene的两个拷贝中的突变都是引起Wolfram综合征所必需的。一些突变会删除WFS1gene中的DNA片段或插入片段，从而导致无法产生功能性wolframin。其他突变会改变狼精蛋白中的单个蛋白结构单元（氨基酸），从而降低蛋白的功能。结果，细胞内的钙水平不受调节，内质网不能正常工作。（换行）当内质网不能起作用时，细胞触发其自身的细胞死亡（细胞凋亡）。胰腺中细胞的死亡，特别是产生胰岛素（β细胞）的细胞，会导致Wolfram综合征患者患糖尿病。沿视神经的细胞逐渐丧失最终导致受影响个体失明。其他身体系统中细胞的死亡可能会导致Wolfram综合征的各种体征和症状。"}, {"name": "其他疾病", "detail": "已发现一些WFS1gene突变会导致一种称为Wolfram样综合征的疾病，其特征是进行性听力丧失和视神经萎缩导致视力丧失，通常始于青春期。有些人患有这种疾病也会患上糖尿病。这些特征在Wolfram综合征中很常见，并且Wolfram样综合征被认为是该病的轻度形式。（换行）Wolfram样综合征是由每个细胞中的一个WFS1gene突变引起的。这些突变取代了狼精蛋白中的单个氨基酸，导致蛋白功能下降。功能性wolframin蛋白的减少会导致细胞死亡，特别是影响沿视神经的细胞，内耳内的细胞和胰腺中的β细胞。这些细胞的丢失有助于Wolfram样综合征的特征。没有突变的WFS1gene的第二个副本产生正常的wolframin。每个细胞中都存在一些正常的wolframin，这可能解释了为什么Wolfram样综合征不如Wolfram综合征严重。"}]}' WHERE id = 19551;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SOS1", "conditions": [{"name": "努南综合征", "detail": "在SOS1gene中已鉴定出55种以上引起Noonan综合征的突变。Noonan综合征的特征是面部特征轻度异常，身材矮小，心脏缺陷，出血问题，骨骼畸形以及许多其他体征和症状。SOS1gene突变会改变SOS1蛋白中的单个蛋白结构单元（氨基酸）。所得蛋白质要么连续打开（活动），要么具有延长的激活时间，而不是响应其他细胞蛋白质而迅速打开和关闭。蛋白质活性的这种增加破坏了控制细胞功能（例如生长和分裂）的RAS/MAPK信号传导途径的调节。这种失调可能导致心脏缺陷，生长问题，骨骼异常和Noonan综合征的其他特征。"}, {"name": "其他疾病", "detail": "SOS1gene突变也可导致1型遗传性牙龈纤维瘤病。这种疾病的特征是牙龈组织（牙龈）缓慢进行性过度生长。过多的这种组织会损害通过牙龈出现的牙齿，这可能导致言语和咀嚼食物的困难。已显示SOS1gene中的至少一种突变引起1型遗传性牙龈纤维瘤病。该突变是DNA（核苷酸）的单个构建块的添加。该突变将核苷酸胞嘧啶插入称为外显子21（写为3248_3249insC）的gene区域，并破坏了该gene的说明，从而缩短了蛋白质。与正常的SOS1蛋白不同，缩短的蛋白具有永久活性，因为它缺少调节其活性的区域。过度活跃的蛋白质不是响应来自细胞外部的特定信号而触发细胞生长，而是指导细胞不断生长和分裂。目前尚不清楚为什么仅在牙龈中可见组织过度生长。"}]}' WHERE id = 19556;
UPDATE genes SET GHR_metadata_ch = '{"gene": "KRT5", "conditions": [{"name": "Dowling-Degos病", "detail": "已发现KRT5gene中至少有五个突变引起Dowling-Degos病。这种情况会导致各种皮肤异常，包括异常深色肤色（色素沉着过度）的特征性花边图案，这种花边图案主要发生在身体的褶皱和折痕中。导致Dowling-Degos病的大多数KRT5gene突变都会导致角蛋白5蛋白的产生，该蛋白异常小且无功能，或阻止任何蛋白从该gene产生。功能性角蛋白5的缺乏（缺乏）会损害角蛋白中间丝的形成。结果，表皮的组织被改变，导致不同类型的皮肤异常的发展。另外，角蛋白5的缺乏可能破坏携带色素的黑素体进入角质形成细胞的运动，而角质形成细胞是正常皮肤色素沉着发展所必需的。黑素体运输的这种破坏被认为会导致Dowling-Degos病患者出现色素沉着异常。"}, {"name": "表皮溶解水疱症", "detail": "在单纯性表皮松解症患者中已鉴定出KRT5gene的130多个突变，这种情况导致皮肤非常脆弱并容易起泡。这些突变大多数会改变用于制造角蛋白5的单个蛋白质结构单元（氨基酸）。大疱性表皮松解症的最严重形式是Dowling-Meara型，通常是由于角蛋白5区域的变化而引起的，这对于角蛋白中间丝的正常组装至关重要。这种疾病的轻度形式，包括局部类型（以前称为Weber-Cockayne类型）和另一种广义类型（以前称为Koebner类型），通常是由影响蛋白质关键区域的变化引起的。色素沉着斑驳的另一种称为大疱性表皮松解症的疾病通常是由特定的KRT5突变引起的。此突变将氨基酸脯氨酸替换为蛋白质25位的氨基酸亮氨酸（写为Pro25Leu或P25L）。(换行)负责大疱性表皮松解的KRT5gene突变改变了角蛋白5的结构和功能，阻止了它与角蛋白14有效结合并干扰角蛋白中间丝网络的组装。导致严重疾病形式的突变严重破坏了角蛋白中间丝的组装，而导致较轻形式的突变则在较小程度上损害了角蛋白丝的组装。该网络的破坏使角质形成细胞脆弱并易于破裂。对皮肤的轻微创伤，例如摩擦或刮擦，可导致这些细胞分解，导致形成疼痛的充满液体的水泡。"}]}' WHERE id = 19565;
UPDATE genes SET GHR_metadata_ch = '{"gene": "NSD1", "conditions": [{"name": "索托斯综合症", "detail": "在Sotos综合征患者中已鉴定出NSD1gene的380多个突变。日本人群中最常见的突变是从包含NSD1gene的5号染色体区域删除遗传物质。在大多数其他人群中，gene本身的突变更为频繁。这些突变包括少量DNA的插入或缺失以及组成该gene的单个DNA构建块（碱基对）的变化。大多数突变会阻止NSD1gene的一个拷贝产生任何酶，或导致产生异常小的，无功能的酶。研究表明，减少的NSD1酶量会破坏参与生长发育的gene的正常活性。但是，尚不清楚在发育过程中这种酶的缺乏究竟如何导致过度生长，学习障碍以及Sotos综合征的其他体征和症状。"}, {"name": "癌症", "detail": "涉及NSD1gene的变化与称为儿童急性髓性白血病的血癌有关。当5号染色体的一部分断裂并重新附着到11号染色体的一部分时，就会发生这种变化。这种变化是在一个人的一生中获得的，仅存在于癌细胞中。这种类型的遗传变化称为体细胞突变，不是遗传的。与儿童急性髓细胞性白血病有关的遗传物质的重排，称为易位，异常地将5号染色体上的NSD1gene与11号染色体上的NUP98gene融合在一起。研究表明，融合的NUP98-NSD1gene开启促进未成熟血细胞生长的gene，并阻断将gene关闭的过程。这些未成熟细胞的过度生长导致急性髓性白血病的发展。（换行）涉及NSD1gene的不同类型的改变与称为神经母细胞瘤的神经组织癌和称为神经胶质瘤的一种脑癌相关。这种改变称为启动子高甲基化，可关闭NSD1酶的产生。研究人员推测，如果没有NSD1，则参与细胞生长和分裂的一个或多个gene的活性是不受控制的。结果，细胞可以不受控制地生长和分裂，导致癌症的发展。"}]}' WHERE id = 19568;
UPDATE genes SET GHR_metadata_ch = '{"gene": "AHCY", "conditions": [{"name": "高蛋氨酸血症", "detail": "高蛋氨酸血症患者中已经描述了AHCYgene的三个突变。在克罗地亚家庭中，一个突变用氨基酸半胱氨酸替代蛋白质143位的氨基酸酪氨酸（写为Tyr143Cys或Y143C）。另一个突变用位置112处的过早终止信号（写为Trp112X或W112X）替换了氨基酸色氨酸，导致酶异常短。发现一名美国患者除Y143C突变外，还具有一种突变，该突变可将氨基酸缬氨酸替换为89位的氨基酸丙氨酸（写为Ala89Val或A89V）。这些突变降低了S-腺苷同型半胱氨酸水解酶的活性，导致高蛋氨酸血症的体征和症状。"}]}' WHERE id = 19620;
UPDATE genes SET GHR_metadata_ch = '{"gene": "GCDH", "conditions": [{"name": "I型戊二酸血症", "detail": "GCDHgene中的突变阻止了戊二酰辅酶a酶的产生，或导致产生了无法在赖氨酸，羟赖氨酸和色氨酸分解中发挥作用的缺陷酶。这种酶缺乏症使这些氨基酸及其中间分解产物积聚到异常水平，从而损害神经系统，尤其是当身体处于压力下时。(换行)据报道，全世界有150多个GCDHgene突变导致I型戊二酸血症。这些突变大多数导致酶中一个氨基酸替换为另一个氨基酸。在Old Order Amish社区中，所有已知的I型戊二酸血症病例均来自用421位氨基酸缬氨酸替代氨基酸丙氨酸（写为Ala421Val或A421V）。在某些美洲原住民人群中发现了一些特定的突变。已发现属于北卡罗来纳州Lumbee社区的I型戊二酸血症患者具有突变，其中第414位的氨基酸谷氨酸被赖氨酸取代，命名为Glu414Lys或E414K。在加拿大的Ojibwa人群中普遍存在一种突变，该突变用核苷酸胸腺嘧啶（写为IVS1，G-T，+5）取代了称为鸟嘌呤的特定DNA构建块（核苷酸）。由于这种突变，产生了异常缩短的酶形式。许多不同的突变发生在其他人群中。"}]}' WHERE id = 19630;
UPDATE genes SET GHR_metadata_ch = '{"gene": "CYB5R3", "conditions": [{"name": "常染色体隐性遗传性高铁血红蛋白血症", "detail": "已发现CYB5R3gene中的65个以上突变会导致I型和II型常染色体隐性先天性高铁血红蛋白血症。这些CYB5R3gene突变大多数会导致I型常染色体隐性先天性高铁血红蛋白血症，其特征是人体组织缺氧，皮肤，嘴唇和指甲呈蓝色（发）。导致I型的突变通常会降低酶的活性或稳定性。结果，该酶不能有效地将三价铁转化为亚铁，导致红细胞内高铁血红蛋白增加10%至50%。高铁血红蛋白的这种增加和正常血红蛋白的相应减少减少了输送到组织的氧气量。改变的酶活性仅影响红细胞，因为其他细胞可以补偿酶活性的降低，但红细胞不能。（换行）引起常染色体隐性先天性高铁血红蛋白血症II型的CYB5R3gene突变导致两种类型中更严重的形式。除紫osis外，这种形式的人还存在神经系统问题。导致II型的突变通常会导致酶活性完全丧失。细胞不能补偿细胞色素b5还原酶3的完全丧失，这导致红细胞内高铁血红蛋白增加10%至70%。高铁血红蛋白的这种增加和正常血红蛋白的相应减少导致紫绀。其他细胞中缺乏酶活性会导致与II型相关的神经系统特征。研究人员怀疑，神经系统问题是由脂肪酸和胆固醇形成受损引起的，这会减少称为髓磷脂的脂肪物质的产生。髓鞘隔离神经细胞并促进神经冲动的快速传递。这种形成髓磷脂的能力降低（髓鞘减少）导致神经细胞的损失，特别是在大脑中。这些细胞的丢失可能导致II型常染色体隐性先天性高铁血红蛋白血症的脑病和运动障碍。"}, {"name": "其他疾病", "detail": "CYB5R3gene的正常变异（多态性）与早产并接受一氧化氮治疗以预防慢性肺部疾病的婴儿的高铁血红蛋白水平低有关。早产儿的肺部通常不发达，出生后需要支持。一氧化氮扩大了肺的血管，允许更多的氧气进入流过肺血管的血液。然而，一氧化氮与红细胞中的血红蛋白连接（结合），血红蛋白将血红蛋白转化为高铁血红蛋白。如果给早产儿施用过多的一氧化氮，则更多的血红蛋白转化为高铁血红蛋白，并且红细胞不能向身体的细胞和组织携带足够的氧气。这种并发症称为高铁血红蛋白毒性。与低高铁血红蛋白水平相关的多态性在CYB5R3gene的特定位置（写为9015G>a）用核苷酸腺嘌呤取代了DNA构建块（核苷酸）鸟嘌呤。结果，细胞色素b5还原酶3活性增加，导致高铁血红蛋白转化回血红蛋白的能力增加，氧输送增加，高铁血红蛋白毒性风险降低。(换行)CYB5R3gene的另一个多态性已被发现会增加吸烟的非洲裔美国女性患乳腺癌的风险。香烟烟雾中含有可能导致DNA错误的化学物质。这些错误阻止细胞控制其自身的生长和分裂，这可能导致癌性肿瘤的形成。通常，细胞色素b5还原酶3酶可以将这些化学物质分解成无毒物质，特别是靶向已显示与乳腺癌有关的化学物质。但是，具有特定CYB5R3gene多态性的女性会改变gene指令制备蛋白质的方式（写为I1M+6C>T），其酶活性会降低。结果，香烟烟雾中的化学物质不会分解。它们会导致DNA错误，使细胞无法控制或有序生长，从而导致乳腺癌。研究表明，与乳腺癌相关的CYB5R3gene多态性在非裔美国女性中的发生率比白人女性高90倍。"}]}' WHERE id = 19636;
UPDATE genes SET GHR_metadata_ch = '{"gene": "PDE6H", "conditions": [{"name": "色盲", "detail": "已经发现PDE6Hgene中的至少一种突变引起视力障碍色盲。这是一种非常罕见的原因，称为不完全色盲症。这种情况的特征是婴儿早期出现的色觉受限和其他视觉问题。（换行）鉴定的突变用抑制蛋白质产生的信号替换抑制性γ亚基开始附近的一个蛋白质构建块（氨基酸）。此突变写为Ser12Ter或S12X。这种变化阻止了任何功能性抑制性γ亚基的产生，从而干扰了视锥细胞特异性磷酸二酯酶的正常功能。这种酶的损伤破坏了视锥细胞的光转导过程；杆通常不受影响。(换行)由于色觉需要视锥，因此受影响的人难以区分某些颜色。他们的其他视觉问题也与视锥细胞故障有关。"}]}' WHERE id = 19637;
UPDATE genes SET GHR_metadata_ch = '{"gene": "PLP1", "conditions": [{"name": "Pelizaeus-Merzbacher病", "detail": "PLP1gene中有100多个突变导致Pelizaeus-Merzbacher病。几种类型的突变与疾病相关，并且在某种程度上，突变的类型可以影响疾病的严重性。最常见的突变类型是PLP1gene的重复，占所有Pelizaeus-Merzbacher病突变的50%至70%。PLP1gene重复通常与Pelizaeus-Merzbacher病的经典形式有关。在许多情况下，PLP1gene附近的gene也会被复制，但是拥有这些gene的额外拷贝并不会使疾病更加严重。有时，整个PLP1gene的缺失也可能导致经典的Pelizaeus-Merzbacher病。改变蛋白脂质蛋白1和DM20蛋白关键区域中一个蛋白质结构单元（氨基酸）的突变通常会导致更严重的Pelizaeus-Merzbacher病。所有这些突变都破坏了蛋白脂质蛋白1和DM20形成髓磷脂的能力。髓磷脂的缺乏会导致中枢神经系统的神经细胞功能障碍，从而导致Pelizaeus-Merzbacher病的体征和症状。"}, {"name": "痉挛性截瘫2型", "detail": "已鉴定出导致2型痉挛性截瘫的PLP1gene中的10多个突变。大多数突变会改变蛋白脂1蛋白非关键区域中的一个蛋白结构单元（氨基酸）。这些突变中的一些破坏了蛋白脂质1蛋白的产生，但不干扰其同工型DM20的产生。通常，整个PLP1gene的缺失也可能导致痉挛性截瘫2型，通常是复杂型。蛋白脂质1蛋白的任何变化似乎都会破坏其功能，但是引起2型痉挛性截瘫的突变似乎对髓磷脂产生的有害作用要小于引起更严重的Pelizaeus-Merzbacher病的突变。"}]}' WHERE id = 19638;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SERPINE1", "conditions": [{"name": "完全纤溶酶原激活物抑制剂1缺乏症", "detail": "在患有完全纤溶酶原激活物抑制剂1（PAI-1）缺乏症（一种导致异常出血的疾病）的人中，至少鉴定出三个SERPINE1gene突变。在患有这种疾病的人中，受伤后出血可能过多并且比平时持续更长时间。受影响的女性可能有与月经（月经过多）有关的过多出血，以及怀孕和分娩时的异常出血。（换行）导致完全PAI-1缺乏的SERPINE1gene突变导致PAI-1蛋白的产生受损，或导致PAI-1蛋白的产生无功能或不稳定并迅速分解。缺乏功能性PAI-1蛋白允许纤溶酶原激活剂过早溶解血凝块，导致与该病症相关的异常出血。"}]}' WHERE id = 19665;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ATP6V0A2", "conditions": [{"name": "皮肤松弛", "detail": "ATP6V0A2gene的突变与一种称为皮肤松弛II型的皮肤松弛有关，这是一种常染色体隐性遗传疾病，拥有属性松弛，松弛的皮肤，独特的面部特征，发育迟缓，智能障碍，癫痫发作，以及随着时间的推移而恶化的运动问题。这是由于缺乏破坏V-ATPase正常功能的a2亚基的产生而引起的。"}]}' WHERE id = 19714;
UPDATE genes SET GHR_metadata_ch = '{"gene": "RAG1", "conditions": [{"name": "联合免疫缺陷病", "detail": "已发现RAG1gene中至少有70个突变会引起称为Omenn综合征的免疫系统疾病。这种情况是一种严重的联合免疫缺陷症（SCID），它是一组疾病，其特征是几乎完全缺乏针对外来入侵者（例如细菌，病毒和真菌）的免疫保护。Omenn综合征的特征是抵抗感染和自身免疫的能力降低，其中免疫系统攻击人体自身的组织和器官。如果不进行治疗，Omenn综合征在婴儿期通常是致命的。(换行)导致Omenn综合征的大多数RAG1gene突变会改变RAG1蛋白中的单个蛋白结构单元（氨基酸）。这些变化会损害RAG复合物的形成和功能，包括其与DNA结合的能力。结果，V（D）J重组减少，B细胞和T细胞表面蛋白质的多样性受到严重限制，损害了细胞识别外来入侵者和抵抗感染的能力。B细胞和T细胞异常会导致Omenn综合征频繁，危及生命的感染。淋巴细胞功能的降低导致B细胞数量的减少，但T细胞的数量通常是正常的。异常的T细胞攻击人体自身的细胞和组织，这是Omenn综合征的自身免疫特征。"}, {"name": "其他疾病", "detail": "RAG1gene突变可引起免疫系统的其他疾病（免疫缺陷）。完全消除RAG1蛋白产生或功能的突变会导致某种形式的SCID，与很少或没有B细胞和T细胞相关。患有这种形式的SCID的人从婴儿期开始就反复发作，持续感染，通常在生命的第一年内致命。（换行）导致产生保留某些正常功能的RAG1蛋白的RAG1gene突变导致另一种形式的免疫缺陷。这种情况的特征是B细胞和T细胞数量有所减少，但是受影响的个体通常直到儿童晚期才发展为严重感染。具有这种形式的免疫缺陷的人也可能在各种组织中具有可引起组织损伤的炎症区域（肉芽肿）。"}]}' WHERE id = 19715;
UPDATE genes SET GHR_metadata_ch = '{"gene": "RAG2", "conditions": [{"name": "联合免疫缺陷病", "detail": "已经发现RAG2gene中的20多个突变会引起称为Omenn综合征的免疫系统疾病。这种情况是一种严重的联合免疫缺陷症（SCID），它是一组疾病，其特征是几乎完全缺乏针对外来入侵者（例如细菌，病毒和真菌）的免疫保护。Omenn综合征的特征是抵抗感染和自身免疫的能力降低，其中免疫系统攻击人体自身的组织和器官。如果不进行治疗，Omenn综合征在婴儿期通常是致命的。(换行)导致Omenn综合征的大多数RAG2gene突变会改变RAG2蛋白中的单个蛋白结构单元（氨基酸）。这些变化会损害RAG复合物的形成和功能，包括其与DNA结合的能力。结果，V（D）J重组减少，B细胞和T细胞表面蛋白质的多样性受到严重限制，损害了细胞识别外来入侵者和抵抗感染的能力。B细胞和T细胞异常会导致Omenn综合征频繁，危及生命的感染。淋巴细胞功能的降低导致B细胞数量的减少，但T细胞的数量通常是正常的。异常的T细胞攻击人体自身的细胞和组织，这是Omenn综合征的自身免疫特征。"}, {"name": "其他疾病", "detail": "RAG2gene突变可引起免疫系统的其他疾病（免疫缺陷）。完全消除RAG2蛋白产生或功能的突变会导致某种形式的SCID，与很少或没有B细胞或T细胞相关。患有这种形式的SCID的人从婴儿期开始就反复发作，持续感染，通常在生命的第一年内致命。（换行）导致产生保留某些正常功能的RAG2蛋白的RAG2gene突变导致另一种形式的免疫缺陷。这种情况的特征是B细胞和T细胞数量有所减少，但是受影响的个体通常直到儿童晚期才发展为严重感染。具有这种形式的免疫缺陷的人也可能在各种组织中具有可引起组织损伤的炎症区域（肉芽肿）。"}]}' WHERE id = 19723;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ESCO2", "conditions": [{"name": "罗伯茨综合症", "detail": "已发现至少26个突变引起罗伯茨综合征。所有这些突变都阻止细胞产生任何功能性ESCO2蛋白。一些突变会改变单个蛋白质的结构单元（氨基酸），而另一些则导致异常短的蛋白质。缺乏功能性ESCO2蛋白会导致姐妹染色单体之间的一些胶在染色体的收缩点（着丝粒）周围缺失。在罗伯茨综合征中，细胞通过延迟细胞分裂来响应异常的姐妹染色单体附着。延迟的细胞分裂可能是细胞应该自我毁灭的信号。罗伯茨综合征的体征和症状可能是由于早期发育过程中各种组织的细胞损失所致。(换行)研究人员最初怀疑罗伯茨综合征的严重程度不同是由ESCO2gene的不同类型的突变引起的。他们预测，轻度疾病患者的突变会降低ESCO2蛋白的活性，而重度疾病患者的突变会完全消除该蛋白的功能。但是，ESCO2gene中所有已知的突变都会阻止任何功能性ESCO2蛋白的产生。疾病严重程度变化的根本原因仍然未知。研究人员怀疑可能涉及其他遗传和环境因素。"}]}' WHERE id = 19764;
UPDATE genes SET GHR_metadata_ch = '{"gene": "GM2A", "conditions": [{"name": "GM2神经节病，AB变异", "detail": "在患有GM2神经节病（AB变异）的人中，仅发现了GM2Agene的少数突变。这些突变中的一些改变了GM2神经节苷脂激活剂中的单个蛋白质结构单元（氨基酸）。其他突变会从GM2Agene中删除少量DNA。这些遗传变化导致不稳定的激活蛋白迅速降解，或者阻止gene产生任何功能蛋白。没有GM2神经节苷脂激活剂，β-己糖胺酶A无法分解GM2神经节苷脂。结果，这种物质积累到毒性水平，特别是在脑和脊髓的神经细胞中。由GM2神经节苷脂积聚引起的进行性损伤导致这些细胞的破坏，这导致AB变体的体征和症状。"}]}' WHERE id = 19769;
UPDATE genes SET GHR_metadata_ch = '{"gene": "PIK3CD", "conditions": [{"name": "激活的PI3K三角洲综合征", "detail": "已经发现PIK3CDgene中的至少四个突变引起称为活化的PI3K-δ综合征的一种形式的免疫缺陷。免疫缺陷是免疫系统不能有效保护身体免受外来入侵者如细菌和病毒侵害的conditions。具有活化的PI3K-δ综合征的人通常具有反复的呼吸道细菌感染和慢性病毒感染。（换行）参与激活的PI3K-δ综合征的PIK3CDgene突变改变p110中的单个蛋白质构建块（氨基酸）？蛋白质；最常见的突变是将氨基酸谷氨酸替换为蛋白质1021位的氨基酸赖氨酸（写为Glu1021Lys或E1021K）。含有改变的p110的PI3K-δ酶？子单元异常打开（激活）。研究表明，这种过度活跃的信号传导导致T细胞成熟并过快死亡。过量的信号传导也在早期阻断B细胞的成熟；未成熟的B细胞不能对外来入侵者做出反应，并可能自毁。缺乏T细胞和B细胞使得患有这种疾病的人难以抵抗细菌和病毒感染。PI3K-δ信号传导的过度活化也可以刺激淋巴细胞的异常增殖，并且这些细胞的积累可以导致淋巴结肿大（淋巴结病）。活化的PI3K-δ综合征也增加了发展称为B细胞淋巴瘤的癌症形式的风险。"}]}' WHERE id = 19782;
UPDATE genes SET GHR_metadata_ch = '{"gene": "GPR101", "conditions": [{"name": "X连锁肢端肥大症", "detail": "涉及GPR101gene的遗传变化会导致一种称为X连锁肢端肥大症（X-LAG）的疾病，其特征是从婴儿期或幼儿期开始异常快速生长。该病症的体征和症状是由腺体增大（增生）或腺体中非癌性肿瘤（称为垂体腺瘤）的发展引起的。异常腺体释放的生长激素比正常人多，导致X-LAG患者快速生长。（换行）当X染色体上的少量遗传物质异常复制（重复）时，发生X-LAG。重复，通常称为Xq26.3微复制，发生在染色体的长（q）臂上指定为q26.3的位置。尽管可以复制多个gene，但仅需要额外的GPR101gene拷贝即可引起X-LAG。gene重复导致GPR101蛋白过量。目前尚不清楚额外的GPR101蛋白如何导致垂体腺瘤或增生或释放过量的生长激素。"}, {"name": "其他疾病", "detail": "在少数散发性肢端肥大症患者中发现了GPR101gene的变异，这种疾病始于成年，其特征是手脚过度生长，“面部粗糙”，心脏问题和其他异常。散发性肢端肥大症发生在没有家庭过度生长史的人群中。该变体导致产生改变的GPR101蛋白，其中蛋白结构单元（氨基酸）谷氨酸被位置308处的氨基酸天冬氨酸取代（写为Glu308Asp或E308D）。实验室研究表明，这种改变可导致生长激素释放的小幅增加并促进细胞的生长和分裂（增殖），尽管研究人员不确定它与受影响个体的肢端肥大症有何关联。(换行)尽管一些研究在少数散发性肢端肥大症患者中发现了E308D GPR101变体，但其他研究无法复制该结果，这表明遗传变化可能不会在疾病的发展中起主要作用。"}]}' WHERE id = 19797;
UPDATE genes SET GHR_metadata_ch = '{"gene": "CALR", "conditions": [{"name": "19p13.13缺失综合征", "detail": "遗传学家庭参考提供有关19p13.13缺失综合征的信息。"}, {"name": "原发性血小板增多症", "detail": "一些gene突变是在一个人的一生中获得的，并且仅存在于某些细胞中。这些被称为体细胞突变的变化不是遗传的。早期血液形成细胞中CALRgene的体细胞突变与原发性血小板增多症有关，该疾病的特征是血小板数量增加，血细胞碎片参与正常凝血。与这种情况相关的突变将少量遗传物质去除或添加到称为外显子9的CALRgene区域。这些遗传变化导致产生改变的钙网蛋白，其一端具有不同的结构单元（氨基酸）序列。目前尚不清楚钙网蛋白的改变如何影响蛋白质的功能或导致原发性血小板增多症的体征和症状。"}, {"name": "原发性骨髓纤维化", "detail": "CALRgene第9外显子的体细胞突变也与原发性骨髓纤维化有关，原发性骨髓纤维化是骨髓被瘢痕组织（纤维化）取代的病症。遗传变化对钙网蛋白功能的影响尚不清楚，研究人员正在努力确定这种改变的蛋白质如何与原发性骨髓纤维化有关。"}]}' WHERE id = 19806;
UPDATE genes SET GHR_metadata_ch = '{"gene": "KRT1", "conditions": [{"name": "表皮松解性角化过度", "detail": "在表皮溶解性角化过度患者中发现了数十种KRT1gene突变。这种情况是一种皮肤疾病，其特征是早期皮肤呈红色起泡，后期皮肤厚实（角化过度）。具有KRT1gene突变的人通常患有PS型表皮松解性角化过度，除了身体的其他部位外，其手掌和脚底皮肤厚（掌plant角化过度）。（换行）大多数与表皮松解性角化过度相关的KRT1gene突变会改变角蛋白1蛋白中的单个蛋白结构单元（氨基酸）。这些氨基酸变化通常发生在蛋白质的中间丝形成中起作用的区域。突变改变了角蛋白1蛋白，似乎影响了中间丝如何相互作用形成网络。改变的蛋白质仍然形成中间丝，但是中间丝网络较弱并且不能正常运行。如果没有强大的网络，皮肤细胞就会变得脆弱并且容易受损，这可能导致摩擦或轻度创伤引起的起泡。目前尚不清楚这些突变如何导致角质形成细胞过度生长，从而导致皮肤角化过度。"}, {"name": "其他疾病", "detail": "KRT1gene突变与许多其他皮肤疾病有关。在其中几种情况下，有掌跖角化过度，但身体其他部位的皮肤通常不会受到影响。由KRT1gene突变引起的称为表皮松解性掌plant角化病的情况相对较轻。受影响的个体通常患有掌plant角化过度，表皮脱离或松动（表皮松解），通常被视为起泡。患有非软解性掌plant角化病的人患有掌plant角化过度，没有表皮松解的迹象。在3型纹状掌plant角化病中，手掌和脚底上的皮肤增厚遵循特定的模式。（换行）KRT1gene突变也与称为Curth-Macklin鱼鳞病的皮肤病有关。这种情况涉及手掌和脚底严重角化过度，有时皮肤在大关节或躯干上。这种情况以角质形成细胞的特定变化为特征。(换行)由KRT1gene的遗传变化引起的另一种皮肤状况称为表皮松解性角化过度的周期性鱼鳞病，类似于表皮松解性角化过度，但皮肤变化在短时间内消失，然后复发。复发性皮肤变化可持续数周或数月。"}]}' WHERE id = 19841;
UPDATE genes SET GHR_metadata_ch = '{"gene": "NLRP3", "conditions": [{"name": "家族性冷性自身炎症综合征", "detail": "在家族性冷自身炎症综合征患者中已经鉴定出NLRP3gene的几个突变。这些突变位于称为外显子3的gene区域。研究人员认为，这些突变会导致冷冻蛋白过度活跃，从而导致发烧和炎症发作，通常是由于暴露于寒冷引起的。"}, {"name": "Muckle-Wells综合征", "detail": "在患有Muckle-Wells综合征的人中，已经鉴定出NLRP3gene的外显子3中至少有10个突变。据信这些突变引起过度活跃的冷冻蛋白，导致发烧和炎症发作，以及Muckle-Wells综合征中发生的听力损失和肾脏问题。"}, {"name": "新生儿多系统炎症性疾病", "detail": "在患有新生儿发作的多系统炎性疾病（NOMID）的人中，已经鉴定出NLRP3gene的约30个突变。几乎所有这些突变都在外显子3中发现。突变可能导致冷冻蛋白过度活跃，导致不适当的炎症反应，导致发烧发作和对人体细胞和组织的广泛炎症损害。目前尚不清楚为什么外显子3中的某些突变会导致NOMID的严重症状，有些会导致较不严重的家族性冷性自身炎综合征，而另一些会导致Muckle-Wells综合征，其严重程度中等。"}]}' WHERE id = 19845;
UPDATE genes SET GHR_metadata_ch = '{"gene": "TTPA", "conditions": [{"name": "共济失调伴维生素E缺乏症", "detail": "已经发现TTPAgene中的20多个突变会导致维生素E缺乏引起共济失调。这种情况的特征是神经系统问题的发展，包括由于称为自由基的有害分子的积累而难以协调运动（共济失调）。这些突变中的一些导致不能产生功能性蛋白质，而另一些则改变了？TTP蛋白，降低其功能。结果，身体不能保留或使用膳食维生素E，这导致血液中这种维生素水平降低和自由基积累。在日本人群中发现的一种TTPAgene突变将氨基酸组氨酸改变为氨基酸中第101位的氨基酸谷氨酰胺？TTP蛋白（写为His101Glu或H101Q）。这种突变与称为色素性视网膜炎的眼部疾病的发展有关，该疾病导致患有维生素E缺乏症的共济失调患者视力下降。（换行）TTPAgene突变导致无功能？要制造的TTP蛋白与从年轻开始的严重共济失调有关。减少但不消除蛋白质功能的突变与轻度共济失调相关，共济失调发生在较晚的年龄，进展较慢。"}]}' WHERE id = 19855;
UPDATE genes SET GHR_metadata_ch = '{"gene": "TECTA", "conditions": [{"name": "非综合征性听力损失", "detail": "研究人员已经鉴定出至少40种可导致非综合征性听力损失的TECTAgene突变，这是与其他体征和症状无关的听力损失。该gene的突变可导致两种形式的非综合征性听力损失：DFNA8/12和DFNB21。(换行)DFNA8/12以常染色体显性遗传，这意味着每个细胞中TECTAgene的一个突变拷贝足以引起这种情况。这种形式的听力损失可以在孩子学会说话（舌前）之前出现，或者在孩子学会说话（舌后）之后开始。在某些情况下，听力损失是稳定的，而在另一些情况下，随着时间的推移，听力损失会变得更加严重。（换行）导致DFNA8/12的TECTAgene突变改变了α-tectorin中的单个蛋白质结构单元（氨基酸）。听力损失的特征取决于发生突变的结构域。一个域中的突变倾向于影响听到中频声音的能力，而另一个域中的突变通常会影响听到高频声音的能力。所有这些突变都改变了盖膜的结构，并破坏了声音向神经冲动的转换。但是，尚不清楚为什么α-tectorin蛋白不同区域的变化会导致不同的听力损失特征。(换行)DFNB21以常染色体隐性遗传，这意味着TECTAgene的两个拷贝都在每个细胞中发生了突变。这种形式的听力损失通常是严重到严重的，并且是舌前的。（换行）导致DFNB21突变的TECTAgene突变会在制备α-tectorin蛋白的说明中产生过早的终止信号。这些突变导致产生无功能形式的α-tectorin或阻止细胞产生任何这种蛋白质。α-tectorin功能的完全丧失会改变盖膜的结构，从而使声音无法转换为神经冲动。"}]}' WHERE id = 19857;
UPDATE genes SET GHR_metadata_ch = '{"gene": "GJA1", "conditions": [{"name": "眼牙指发育不良", "detail": "已发现GJA1gene中超过45个突变会导致眼牙指发育不良。这些突变大多数会改变连接蛋白43中的一个蛋白质结构单元（氨基酸）。GJA1gene的不同变化会导致人们患有掌足角化病的眼指数字发育不良。掌plant角化病是一种导致手掌和脚底皮肤变厚，鳞屑和胼call体的病症。此突变删除了两个DNA构建块（核苷酸），以在制作连接蛋白43的说明中创建过早的终止信号。结果，产生异常短的无功能蛋白质。(换行)由异常连接蛋白43蛋白形成的通道通常永久关闭，阻止任何分子的运输。一些突变阻止连接蛋白43蛋白迁移到需要它们形成通道的细胞表面。通道功能的这些破坏会损害细胞之间的通讯，这被认为会导致眼牙指发育不良的眼睛，牙齿和手指异常。"}, {"name": "严重的先天性心脏病", "detail": "遗传学家庭参考资料提供有关严重先天性心脏病的信息。"}, {"name": "异位症候群", "detail": "遗传学家庭参考资料提供有关异型症候群的信息。"}, {"name": "其他疾病", "detail": "已经发现GJA1gene突变在少数非洲裔美国人中引起耳聋。需要进一步的研究来确定非裔美国人和其他耳聋种族的人中GJA1突变的普遍性。"}]}' WHERE id = 19867;
UPDATE genes SET GHR_metadata_ch = '{"gene": "AUH", "conditions": [{"name": "3-甲基谷氨酰辅酶A水合酶缺乏症", "detail": "已发现AUHgene突变会导致3-甲基谷氨酰辅酶a水合酶缺乏症，这种疾病的特征是代谢性酸中毒，酸尿症，运动障碍和认知等神经系统问题。突变导致缺乏3-甲基谷氨酰辅酶A水合酶活性并损害亮氨酸降解途径。"}]}' WHERE id = 19900;
UPDATE genes SET GHR_metadata_ch = '{"gene": "F9", "conditions": [{"name": "血友病", "detail": "F9gene的突变会导致一种称为血友病B的血友病。已经鉴定出该gene的900多种改变。最常见的突变会改变gene中的单个DNA构建块（碱基对）。一小部分突变会删除或插入gene内的多个碱基对或重排DNA片段。F9gene中的（换行）突变导致产生异常形式的凝血因子IX或减少该蛋白的量。改变或缺失的蛋白质不能有效参与血液凝固过程。结果，血液凝块不能在受伤时正确形成。这些凝血问题导致出血过多，难以控制。完全消除凝血因子IX活性的突变会导致严重的血友病。减少但不消除蛋白质活性的突变通常会导致轻度或中度血友病。（换行）F9gene序列开始附近的几个突变导致一种不寻常的血友病形式，称为血友病B Leyden。具有这些突变的人出生时功能性凝血因子IX的水平非常低，但是激素的变化会导致该蛋白的水平在青春期逐渐升高。结果，患有血友病B Leyden的成年人很少经历异常出血的发作。"}, {"name": "华法林敏感性", "detail": "遗传学家庭参考资料提供有关华法林敏感性的信息。"}, {"name": "其他疾病", "detail": "F9gene中的几种罕见突变会导致对一种名为华法林的药物的敏感性（超敏性）增加。这种药物是一种抗凝剂，这意味着它用于防止异常血栓的形成或生长。华法林通过减少活性因子IX和其他三种凝血蛋白的量起作用。（换行）负责华法林超敏反应的突变各自改变F9gene中的单个碱基对。这些突变不会引起B型血友病，具有这些遗传变化的人只有在接受华法林治疗后才会出现出血问题。华法林可将这些人的凝血因子IX含量降低至非常低的水平，从而防止血液正常凝结，并可能导致反复发作的严重出血问题。为避免这些并发症，华法林过敏患者可以使用其他抗凝药物治疗。"}]}' WHERE id = 19911;
UPDATE genes SET GHR_metadata_ch = '{"gene": "PABPN1", "conditions": [{"name": "眼咽肌营养不良", "detail": "已发现PABPN1gene中至少有10种不同的突变会引起眼咽肌营养不良。所有这些突变导致PABPN1蛋白具有扩展的聚丙氨酸束，长度为11至17个丙氨酸。额外的丙氨酸会导致PABPN1蛋白在肌肉细胞内形成团块，这些团块被认为会损害肌肉细胞的正常功能并最终导致细胞死亡。肌肉细胞的逐渐丧失很可能导致眼咽肌营养不良患者出现肌肉无力。"}]}' WHERE id = 19924;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ROR2", "conditions": [{"name": "罗宾诺综合症", "detail": "已发现ROR2gene中至少有16个突变会导致Robinow综合征的常染色体隐性遗传，这种情况会影响身体许多部位，尤其是骨骼的发育。常染色体隐性遗传意味着每个细胞中gene的两个拷贝都有突变。这些突变中的一些会改变ROR2蛋白中的单个蛋白结构单元（氨基酸），而另一些则导致该蛋白异常短而无功能的产生。因为这些遗传变化阻止了任何功能性ROR2蛋白的产生，所以它们被描述为“功能丧失”突变。ROR2蛋白的丢失会损害对正常发育至关重要的化学信号通路，尤其是面部，脊柱和四肢骨骼的形成。这些变化导致Robinow综合征的骨骼异常特征。Robinow综合征的其他特征包括生殖器异常和心脏缺陷，在早期发育过程中缺乏这种蛋白质也是其基础。"}, {"name": "其他疾病", "detail": "在患有称为近距离B1型疾病的人中，已经鉴定出ROR2gene的10多个突变。这种情况的特征是手指和脚趾异常短，尤其是第四和第五个手指，指甲和脚趾甲畸形或缺失。近距离B1型具有常染色体显性遗传模式，这意味着每个细胞中gene的一个改变的拷贝足以引起该疾病。与引起Robinow综合征的突变（如上所述）不同，负责近距离B1型的ROR2gene突变被描述为“功能获得”，因为它们似乎导致ROR2蛋白持续活跃。目前尚不清楚过度活跃的蛋白质如何破坏手和脚骨骼的发育。"}]}' WHERE id = 19934;
UPDATE genes SET GHR_metadata_ch = '{"gene": "TWIST1", "conditions": [{"name": "Saethre-Chotzen综合征", "detail": "在Saethre-Chotzen综合征患者中已鉴定出TWIST1gene的80多个突变。这些突变中的一些改变TWIST1蛋白中的单个蛋白质构建块（氨基酸），而其他突变删除或插入gene中的遗传物质。在某些情况下，这种情况是由涉及包含TWIST1gene的7号染色体区域的染色体异常（易位或缺失）引起的。（换行）TWIST1突变阻止每个细胞中gene的一个拷贝产生任何功能性蛋白质。功能性TWIST1蛋白的缺乏会影响颅骨，面部和四肢细胞的发育和成熟。这些异常是Saethre-Chotzen综合征的体征和症状的基础，尽管尚不清楚TWIST1蛋白的缺乏究竟如何导致特定特征，例如某些颅骨的过早融合。"}, {"name": "其他疾病", "detail": "TWIST1突变也在几例孤立性颅缝早闭症患者中发现，这是某些颅骨的过早融合，没有Saethre-Chotzen综合征的其他体征和症状。这些突变发生在称为TWIST-box结构域的区域中的gene末端附近。该结构域使TWIST1蛋白能够结合并调节称为RUNX2的gene，RUNX2是骨形成的关键调节因子。研究人员认为，TWIST-box结构域中的突变会阻止TWIST1蛋白有效控制RUNX2gene的活性，从而破坏颅骨中骨形成的正常模式并导致颅骨融合。"}]}' WHERE id = 19941;
UPDATE genes SET GHR_metadata_ch = '{"gene": "CYP7B1", "conditions": [{"name": "痉挛性截瘫5 a型", "detail": "已发现CYP7B1gene中至少有37个突变引起痉挛性截瘫5A型。这种情况的特征是肌肉僵硬（痉挛）和下肢严重无力（截瘫），通常始于青春期。大多数CYP7B1gene突变会改变氧固醇7-α-羟化酶中的单个蛋白质结构单元（氨基酸）。这种变化会降低酶的活性。其他突变导致功能酶完全丧失。（换行）降低的氧固醇7-α-羟化酶活性似乎不影响肝脏中的胆固醇分解或胆汁酸产生。肝脏中的另一种途径可以执行这些功能，这可以解释为什么氧固醇7-α-羟化酶活性的降低不会影响肝功能。(换行)在大脑中，酶活性的降低会导致胆固醇的积累，并改变由氧固醇7-α-羟化酶触发的神经甾体生成。神经甾体水平异常会损害细胞存活，可能导致神经细胞死亡。大脑中胆固醇的异常积聚也可能导致神经细胞死亡。这些细胞的丧失导致神经系统功能的恶化（神经变性），并引起运动问题，虚弱以及痉挛性截瘫5A型的其他体征和症状。"}]}' WHERE id = 19948;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SEPT9", "conditions": [{"name": "遗传性神经性肌萎缩", "detail": "在患有遗传性神经性肌萎缩的个体中已鉴定出一些SEPT9gene突变，该疾病的特征是肩部和手臂出现严重的疼痛和肌肉消瘦（肌萎缩）。最常见的突变导致septin-9蛋白质序列中第88位的氨基酸色氨酸替换了蛋白质结构单元（氨基酸）精氨酸，命名为Arg88Trp或R88W。这种突变出现在世界不同地区的几个无关家族中。在受影响的个体中也发现了SEPT9gene内遗传物质的重复。（换行）SEPT9gene的变化可能会以干扰其功能的方式改变某些septin-9同工型的氨基酸序列。这些突变也可能改变septin-9亚型的分布及其与人体某些组织中其他septin蛋白的相互作用。septin蛋白功能的这种变化似乎特别影响控制肩膀和手臂（臂丛神经）运动和感觉的神经网络，但其原因尚不清楚。(换行)由于遗传性神经性肌萎缩发作的许多触发因素也会影响免疫系统，研究人员认为这种疾病可能与自身免疫反应有关。但是，尚不清楚SEPT9gene突变与免疫功能之间的关系。当免疫系统发生故障并攻击身体自身的组织和器官时，就会发生自身免疫性疾病。对臂丛神经的自身免疫攻击可能导致遗传性神经性肌萎缩的体征和症状。"}, {"name": "癌症", "detail": "SEPT9gene活性（表达）的改变与某些癌症有关。改变的gene表达可以增强几种癌症相关事件，例如细胞分裂（增殖），细胞运动和滋养生长中的肿瘤的新血管（血管生成）的发展。septin-9蛋白特定同工型的产生增加与乳腺癌和前列腺癌有关。在许多其他癌症中也发现了SEPT9gene表达的改变，包括卵巢癌，胰腺癌，肺癌，肾癌，肝癌，甲状腺癌和食道癌。"}]}' WHERE id = 19951;
UPDATE genes SET GHR_metadata_ch = '{"gene": "TGFBR2", "conditions": [{"name": "家族性胸主动脉瘤和解剖", "detail": "在家族性胸主动脉瘤和解剖（家族性TAAD）患者中至少鉴定出9个TGFBR2gene突变。这种疾病涉及主动脉的问题，主动脉是将血液从心脏分配到身体其他部位的大血管。主动脉可以减弱和伸展，导致血管壁（动脉瘤）隆起。主动脉的拉伸也可能导致主动脉壁中的层突然撕裂（主动脉夹层）。主动脉瘤和夹层可导致危及生命的内出血。（换行）导致家族性TAAD的TGFBR2gene突变干扰信号转导。受干扰的信号传导会损害细胞的生长和发育。尚不清楚这些变化如何导致与家族性TAAD相关的特定主动脉异常。"}, {"name": "Loeys-Dietz综合征", "detail": "已发现TGFBR2gene中的100多个突变可导致II型Loeys-Dietz综合征。Loeys-Dietz综合征影响结缔组织，结缔组织为血管，骨骼和身体其他部位提供结构和支撑。这种类型的Loeys-Dietz综合征的特征是血管异常和骨骼畸形。导致Loeys-Dietz综合征的大多数TGFBR2gene突变改变TGF-β中的单个蛋白质构建块（氨基酸）？2型受体，导致功能很少或没有功能的受体。虽然受体功能严重下降，但TGF-？通路信号的发生强度甚至比正常情况更大。研究人员推测，这种信号通路中其他蛋白质的活性增加是为了补偿TGF-β的减少？受体2型活性；然而，导致信号增加的确切机制尚不清楚。过度活跃的信号通路破坏结缔组织和各种身体系统的发育，并导致II型Loeys-Dietz综合征的各种体征和症状。（换行）还发现一些引起Loeys-Dietz综合征II型的TGFBR2gene突变引起家族性TAAD（如上所述）。受影响的家庭可能包括一些患有Loeys-Dietz综合征的人和其他患有家族性TAAD的人。"}, {"name": "癌症", "detail": "一些TGFBR2gene突变是在一个人的一生中获得的，仅存在于某些细胞中。这些变化称为体细胞突变，不是遗传的。TGFBR2gene发生体细胞突变的人患各种癌症的风险似乎增加。体细胞TGFBR2gene突变可能会破坏有助于调节细胞分裂的信号传导过程。未经检查的细胞分裂可导致肿瘤的形成，特别是当TGFBR2gene突变发生在结肠，直肠和食道中时。据估计，30%的癌性（恶性）结肠肿瘤在其细胞中具有TGFBR2gene突变。"}]}' WHERE id = 19963;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SPTLC1", "conditions": [{"name": "遗传性感觉神经病IA型", "detail": "已发现SPTLC1gene中至少有9个突变引起IA型遗传性感觉神经病。这种情况的特征是腿和脚的神经异常（周围神经病）；感觉疼痛的能力下降，这可能导致开放性溃疡的发展；和可能损害活动能力的肌肉无力。SPTLC1gene突变会改变SPTLC1亚基中的单个蛋白质结构单元（氨基酸）。在全球多个受影响的家族中发现的一种突变将氨基酸半胱氨酸替换为SPTLC1亚基（写为Cys133Trp或C133W）中第133位的氨基酸色氨酸。（换行）SPTLC1gene突变减少了产生的功能性SPTLC1亚基的数量，这导致SPT酶的活性发生了变化。这种改变的酶产生称为脱氧鞘氨醇碱的分子，它通常不会产生。由于这种新功能，SPT酶减少了鞘脂的产生。总体而言，鞘脂的产生似乎没有减少，因为人体能够补偿SPT酶减少的产生。积累时，脱氧鞘氨醇碱对神经元有毒。由有毒分子积聚引起的神经细胞的逐渐破坏导致遗传性感觉神经病IA型患者的感觉丧失和肌肉无力。"}]}' WHERE id = 19969;
UPDATE genes SET GHR_metadata_ch = '{"gene": "GALNT3", "conditions": [{"name": "高磷酸盐血症家族性肿瘤性钙化病", "detail": "GALNT3gene至少有25个突变被发现会引起高磷血症家族性肿瘤性钙质沉着症（HFTC），这种疾病的拥有属性是血液中磷酸盐水平的增加（高磷血症）和身体组织中磷酸盐和钙的异常沉积（钙质沉着症）。GALNT3gene突变导致产生功能很少或没有功能的ppGalNacT3蛋白。结果，ppGalNacT3不能糖基化成纤维细胞生长因子23。成纤维细胞生长因子23被捕获在细胞内并被分解，而不是像往常一样从细胞中释放（分泌）。没有成纤维细胞生长因子23，更多的磷酸盐被肾脏重新吸收回到血液中，导致高磷血症。当过量的磷酸盐与钙结合形成积聚在软组织中的沉积物时，就会产生钙质沉着症。"}]}' WHERE id = 20033;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ETFA", "conditions": [{"name": "戊二酸血症II型", "detail": "ETFAgene中的某些突变阻止了电子转移黄素蛋白酶的产生。其他突变导致产生缺陷酶，其不能在分解脂肪和蛋白质的一系列反应（代谢途径）中发挥其作用。这种酶缺乏使这些营养素以及营养素被部分分解时产生的化合物积累到异常水平，特别是当身体处于压力下时。代谢不完全的有毒产物会损害许多身体系统中的细胞，导致II型戊二酸血症的体征和症状。"}]}' WHERE id = 20035;
UPDATE genes SET GHR_metadata_ch = '{"gene": "CPOX", "conditions": [{"name": "卟啉症", "detail": "已经发现CPOXgene的突变与两种类型的卟啉症有关：遗传性粪卟啉症和由于粪卟啉原氧化酶活性降低导致卟啉在体内积累而引起的称为硬卟啉症的变体。"}]}' WHERE id = 20044;
UPDATE genes SET GHR_metadata_ch = '{"gene": "GAMT", "conditions": [{"name": "胍基乙酸甲酯甲基转移酶缺乏症", "detail": "GAMTgene中至少有49个突变会导致胍基乙酸甲酯甲基转移酶缺乏症，这种疾病涉及智力障碍和癫痫发作。葡萄牙血统的大多数受影响的个体具有特定的突变，其中氨基酸色氨酸被酶中第20位的氨基酸丝氨酸取代（写为Trp20Ser或W20S）。（换行）GAMTgene突变会损害胍基乙酸甲酯甲基转移酶参与肌酸合成的能力，从而导致肌酸短缺。胍基乙酸甲酯甲基转移酶缺乏症的影响在需要大量能量的器官和组织（尤其是大脑）中最为严重。"}]}' WHERE id = 20062;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SLC34A1", "conditions": [{"name": "特发性婴儿高钙血症", "detail": "已发现SLC34A1gene中至少14个突变导致一种称为婴儿高钙血症2的特发性婴儿高钙血症，其特征在于血液中的高水平钙（高钙血症）和尿液（高钙尿症）以及肾脏中的钙沉积（肾钙质沉着症）。患有这种形式的特发性婴儿高钙血症的个体血液中的磷酸盐水平也很低（低磷血症）。高钙血症通常会导致婴儿呕吐，进食不良以及无法以预期的速度生长和增重（无法壮成长），尽管一些受影响的个体直到成年才出现这种疾病的体征和症状。受影响成年人的特征（无论是否在婴儿期出现症状）通常包括高钙尿症，肾钙化病和肾结石（肾结石）。（换行）导致婴儿高钙血症2的SLC34A1gene突变导致产生改变的NaPi-IIa通道，其不能将磷酸盐转运穿过肾细胞膜。结果，减少了磷酸盐的重吸收并且体内的磷酸盐水平低。(换行)磷酸盐稳态也受维生素D控制。当打开（活性）时，这种维生素刺激磷酸盐和钙从肠道吸收到血液中。为了提高由功能性NaPi-IIa通道丧失引起的低磷酸盐水平，维生素D被激活。过多的活性维生素D会增加钙在血液中的吸收，从而在受影响的个体中引起高钙血症。钙的异常平衡导致尿液中矿物质含量高，并可能导致钙在肾组织中沉积并形成肾结石。"}, {"name": "其他疾病", "detail": "SLC34A1gene的突变可导致其他几种健康状况，并伴有与低磷血症有关的各种体征和症状。如在婴儿高钙血症（如上所述）中，引起这些健康状况的gene突变阻止NaPi-IIa转运蛋白起作用，减少肾脏中的磷酸盐吸收并引起低磷血症。一些具有这些突变的人被诊断患有低磷血症性肾结石/骨质疏松症1，患有肾结石或低骨矿物质密度（骨质疏松症）。其他被诊断患有Fanconi肾小管综合征2的人会出现肾功能和低磷酸盐血症性rick病的问题，这是一种经常引起骨痛和弯曲腿的骨骼疾病。目前尚不清楚为什么该gene的突变会导致不同的体征和症状。"}]}' WHERE id = 20081;
UPDATE genes SET GHR_metadata_ch = '{"gene": "KRT3", "conditions": [{"name": "梅斯曼角膜营养不良", "detail": "已发现KRT3gene中至少有3个突变会引起Meesmann角膜营养不良，这是一种以角膜上皮中微小囊肿形成为特征的眼病。（换行）与Meesmann角膜营养不良相关的所有已鉴定的KRT3gene突变改变角蛋白3蛋白中的单个蛋白质构建块（氨基酸）。这些变化发生在蛋白质的一个区域，该区域对于中间丝的形成和稳定性至关重要。改变的角蛋白3蛋白干扰中间丝的组装，削弱角膜上皮的结构框架。结果，角膜的这一外层异常脆弱，并形成了以Meesmann角膜营养不良为特征的囊肿。囊肿可能含有大量异常角蛋白和其他细胞碎片。当囊肿破裂（破裂）时，会引起眼睛刺激，对光的敏感性增加（畏光）和相关症状。"}]}' WHERE id = 20109;
UPDATE genes SET GHR_metadata_ch = '{"gene": "KRT4", "conditions": [{"name": "白色海绵痣", "detail": "已经发现KRT4gene中至少有6个突变引起白色海绵痣，这种情况导致形成称为痣（单数：痣）的白色组织斑块，其表现为增厚的，天鹅绒般的海绵状组织。这些痣最常发生在口腔（口腔）粘膜上（复数：粘膜）。很少，白色海绵痣发生在鼻子，食道，生殖器或肛门的粘膜上。（换行）导致白色海绵痣的KRT4gene突变破坏角蛋白4的结构。结果，角蛋白4不能与角蛋白13正确地配合在一起，导致形成不规则的中间丝，这些中间丝很容易被摩擦或创伤很小而损坏。吃或刷牙时，口腔粘膜中脆弱的中间丝可能会受损。中间丝的损伤导致炎症并促进上皮细胞的异常生长和分裂（增殖），导致粘膜增厚并导致白色海绵痣。"}]}' WHERE id = 20124;
UPDATE genes SET GHR_metadata_ch = '{"gene": "LEP", "conditions": [{"name": "先天性瘦素缺乏症", "detail": "已经鉴定出至少七个导致先天性瘦素缺乏的LEPgene突变。这种疾病与过度饥饿，大量体重增加和指导性发育的激素产生减少（促性腺激素性性腺功能减退症）有关。导致先天性瘦素缺乏的LEPgene突变导致瘦素的缺乏。结果，不会发生触发饱腹感的信号传导，从而导致与该疾病相关的过度饥饿和体重增加。由于促性腺激素性性腺功能减退症发生在先天性瘦素缺乏症中，研究人员建议瘦素信号传导也参与调节控制性发育的激素。但是，这种参与的具体细节以及如何在先天性瘦素缺乏症中改变它尚不清楚。"}]}' WHERE id = 20136;
UPDATE genes SET GHR_metadata_ch = '{"gene": "RPL5", "conditions": [{"name": "钻石-布莱克凡贫血", "detail": "在患有Diamond-Blackfan贫血的个体中已鉴定出30多个RPL5gene突变。据信这些突变影响RPL5蛋白的稳定性或功能。研究表明，缺乏功能性核糖体蛋白可能会增加骨髓中造血细胞的自我破坏，导致红细胞数量减少（贫血）。细胞分裂的异常调节或凋亡的不适当触发可能会导致其他健康问题和异常的身体特征，从而影响某些Diamond-Blackfan贫血患者。"}]}' WHERE id = 20141;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SERPINA7", "conditions": [{"name": "遗传性甲状腺素结合球蛋白缺乏症", "detail": "在遗传性甲状腺素结合球蛋白缺乏症患者中，已鉴定出SERPINA7gene的25个以上突变。一些突变导致甲状腺素结合球蛋白的缩短，无功能的形式。这些遗传变化导致蛋白质的完全丧失，从而导致遗传性甲状腺素结合球蛋白缺乏症（TBG-CD）的完整形式。其他突变会改变甲状腺素结合球蛋白中的单个蛋白质结构单元（氨基酸）。这些突变改变了蛋白质的结构或加工过程，导致部分形式的疾病（TBG-PD）。（换行）当缺乏甲状腺素结合球蛋白时，循环甲状腺激素的量减少。这些变化不会引起甲状腺功能的任何问题。尽管遗传性甲状腺素结合球蛋白缺乏症不会引起任何健康问题，但它可能被误认为是更严重的甲状腺疾病（如甲状腺功能减退症）。因此，重要的是诊断遗传性甲状腺素结合球蛋白缺乏症，以避免不必要的治疗。"}, {"name": "其他疾病", "detail": "涉及SERPINA7gene的其他变化引起称为甲状腺素结合球蛋白过量（TBG-E）的病症。患有这种疾病的人的甲状腺素结合球蛋白水平异常高，通常比正常人高2至4倍。这种过量是由每个细胞中存在一个或多个额外的SERPINA7gene拷贝引起的。像甲状腺素结合球蛋白缺乏症一样，甲状腺素结合球蛋白过量不会引起甲状腺功能的任何问题。"}]}' WHERE id = 20144;
UPDATE genes SET GHR_metadata_ch = '{"gene": "F12", "conditions": [{"name": "遗传性血管性水肿", "detail": "F12gene中至少有两个突变与III型遗传性血管性水肿有关。这些突变改变了因子XII中的单个蛋白质结构单元（氨基酸），从而增加了蛋白质的活性。结果，产生更多的缓激肽，这允许额外的流体通过血管壁泄漏。体液在身体组织中的积聚导致III型遗传性血管性水肿患者的肿胀发作。"}, {"name": "其他疾病", "detail": "已经鉴定出导致XII因子缺乏的F12gene中大约20个突变。XII因子缺乏症是一种遗传性疾病，其特征是血液中缺乏XII因子。患有这种情况的人通常不会出现异常出血或其他症状。因子XII缺乏症通常在常规血液检测过程中发现，因为因子XII水平降低会导致血液在试管中凝结的时间更长。导致因子XII缺乏的大多数突变会改变单个氨基酸，从而改变因子XII的结构。尚不清楚为什么XII因子缺乏症患者不会像其他凝血因子缺乏症患者那样经历异常出血。"}]}' WHERE id = 20158;
UPDATE genes SET GHR_metadata_ch = '{"gene": "EXT2", "conditions": [{"name": "遗传性多发性骨软骨瘤", "detail": "About 220 mutations in the EXT2 gene have been identified in people with hereditary multiple osteochondromas type 2, a condition in which people develop multiple benign (noncancerous) bone tumors called osteochondromas. Most of these mutations prevent any functional exostosin-2 protein from being made, and are called \"loss-of-function\" mutations.  The loss of exostosin-2 protein function prevents it from forming a complex with the exostosin-1 protein and modifying heparan sulfate.  It is unclear how this impairment leads to the development of multiple osteochondromas.\r"}, {"name": "Potocki-Shaffer综合征", "detail": "导致EXT2gene缺失的遗传变化会导致一种称为Potocki-Shaffer综合征的疾病。患有这种情况的人有多个骨软骨瘤（如上所述）和两个骨头的开口增大，这些骨头构成了颅骨顶部和侧面的大部分（顶孔增大）。在一些患有Potocki-Shaffer综合征的人中看到的其他体征和症状包括智力残疾，发育迟缓，独特的面部特征，视力问题以及心脏，肾脏和泌尿道的缺陷。（换行）Potocki-Shaffer综合征（有时称为近端11p缺失综合征）是由11号染色体短（p）臂的遗传物质缺失引起的。在患有这种情况的人中，该区域内EXT2gene的缺失是导致多发性骨软骨瘤的原因。缺失可能导致exostosin-2蛋白减少，并且无法正确处理硫酸乙酰肝素。尽管硫酸乙酰肝素参与许多身体过程，但尚不清楚缺乏这种蛋白质如何导致多发性骨软骨瘤。缺失区域中其他gene的丢失可能有助于Potocki-Shaffer综合征的其他特征。具体而言，ALX4gene的缺失导致顶孔增大，而PHF21Agene的缺失导致智力残疾和独特的面部特征。"}, {"name": "其他疾病", "detail": "在患有癫痫性脊柱侧弯-大头畸形综合征的家庭中，发现EXT2gene至少有两个突变。在患有这种情况的个体中，癫痫发作通常始于儿童早期。受影响的个体还具有异常的脊柱弯曲（脊柱侧弯），异常大的头部（大头畸形），智力残疾和弱肌张力（肌张力低下）。与癫痫发作，脊柱侧弯，大头畸形综合征相关的EXT2gene突变改变了exostosin-2蛋白中的单个蛋白结构单元（氨基酸）。这些变化减少了功能性exostosin-2蛋白的量，这可能会破坏硫酸乙酰肝素的正常修饰。目前尚不清楚这种破坏如何导致病情的各种体征和症状。癫痫发作-脊柱侧弯-大头畸形综合征的个体似乎没有发展成骨软骨瘤（如上所述）。"}]}' WHERE id = 20218;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SGCB", "conditions": [{"name": "肢带型肌营养不良症", "detail": "在患有2E型肢带型肌营养不良症的人中，已鉴定出SGCBgene中约50个突变。肢带型肌营养不良症是一组相关疾病，其特征是肌肉无力和消瘦，尤其是在肩膀，臀部和四肢。(换行)一些患有2E型肢带型肌营养不良症的人也会出现称为扩张型心肌病的心脏病。扩张型心肌病是一种心脏病，可扩大和削弱心脏（心脏）肌肉，阻止其有效抽血。扩张型心肌病进展迅速，在许多情况下危及生命。由影响肌糖蛋白复合物的gene突变引起的肢带型肌营养不良症（换行）形式称为肌糖蛋白病。SGCBgene突变可能会阻止肌糖蛋白复合物形成或结合并稳定肌营养不良蛋白复合物。这些复合物的问题会降低肌纤维的强度和弹性，并导致肢带型肌营养不良症的体征和症状。"}]}' WHERE id = 20239;
UPDATE genes SET GHR_metadata_ch = '{"gene": "GSS", "conditions": [{"name": "谷胱甘肽合成酶缺乏症", "detail": "在谷胱甘肽合成酶缺乏症患者中已鉴定出GSSgene的30多个突变。这种情况的特征包括红细胞的异常破坏（溶血性贫血），尿液中大量称为5-氧代脯氨酸的化合物（5-氧代脯氨酸尿症）的释放，以及血液和组织中酸度升高（代谢性酸中毒））。受严重影响的个人也可能有神经系统问题。(换行)涉及谷胱甘肽合成酶缺乏的大多数GSS突变改变谷胱甘肽合成酶中的单个氨基酸。其他突变破坏了来自GSSgene的遗传信息如何拼接在一起以制作产生酶的蓝图。改变的谷胱甘肽合成酶可能不稳定，比平时短或形状错误。所有这些变化都会降低酶的活性并破坏γ-谷氨酰循环，从而阻止谷胱甘肽的充分产生。低水平的谷胱甘肽会影响体内的其他化学反应，导致5-氧代脯氨酸的过量生产。该化合物在红细胞和其他组织中的积累引起溶血性贫血和代谢性酸中毒，其释放导致5-氧代脯氨酸尿症。"}]}' WHERE id = 20242;
UPDATE genes SET GHR_metadata_ch = '{"gene": "MYH6", "conditions": [{"name": "病态窦房结综合征", "detail": "MYH6gene的至少一种变异与患病态窦房结综合征的风险增加有关。这种情况会影响窦房结（SA）的功能，窦房结是心脏中专门充当天然起搏器的细胞区域。在冰岛人群中发现的这种变异改变了一个单一的蛋白质结构单元（氨基酸）-肌球蛋白重链。具体而言，它在蛋白质位置721（写为Arg721Trp）上用氨基酸色氨酸取代了氨基酸精氨酸。研究人员发现，样本中约有一半具有Arg721Trp变异的人在其一生中发展为病态窦房结综合征，而未携带该变异的人中约有6%。他们推测这种变化可能会改变-肌球蛋白重链并破坏其在心肌收缩中的通常作用。这些变化可能会改变某些人心跳的方式，导致异常缓慢的心跳（心动过缓）和相关症状，如头晕，头晕和晕厥（晕厥）。"}, {"name": "家族性扩张型心肌病", "detail": "遗传学家庭参考提供有关家族性扩张型心肌病的信息。"}, {"name": "其他疾病", "detail": "已经发现MYH6gene的突变会引起其他几种心脏疾病。这些包括先天性心脏缺陷，特别是房间隔缺损（ASD），房间隔缺损是分隔心脏的两个上腔（心房）的壁（隔膜）中的孔。与ASD相关的大多数MYH6gene突变都会影响部分-肌球蛋白重链称为头部结构域。蛋白质的这一部分参与将II型肌球蛋白附着（结合）肌动蛋白。这两种蛋白质之间的异常相互作用可能会破坏早期心脏发育，从而导致心脏缺陷，例如ASD。(换行)MYH6gene突变也可引起心肌两种疾病，扩张型心肌病（DCM）和肥厚型心肌病（HCM）。DCM减弱并扩大心脏，而HCM的特征是心肌增厚（肥大）。这两种情况都会阻止心脏有效抽血，并增加心力衰竭和猝死的风险。与DCM和HCM相关的突变会影响-肌球蛋白重链，并且大多数会改变蛋白质中的单个氨基酸。这些变化可能会改变-肌球蛋白重链影响心肌结构和功能的方式。"}]}' WHERE id = 20252;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ALX4", "conditions": [{"name": "顶孔增大", "detail": "已发现ALX4gene中至少有8个突变会导致2型顶孔扩大。这种情况的特征是顶骨中的开口增大（孔），这是形成头骨顶部和侧面的两个骨头。胎儿发育期间顶骨中的开口是正常的，但它们通常在出生前关闭。在患有这种疾病的人中，顶孔终生保持开放。（换行）导致顶孔扩大的突变导致无法与DNA结合的ALX4蛋白的产生，从而改变了多个gene的调控。结果，几个细胞过程被破坏，包括增殖，分化和存活。在早期发育中，头骨似乎对ALX4蛋白活性的变化特别敏感。具体而言，颅骨中与骨形成（骨化）有关的细胞无法正常工作，从而导致颅骨区域缺乏骨并导致顶孔增大。"}, {"name": "额鼻发育不良", "detail": "已发现ALX4gene突变会导致2型额鼻发育不良，这种疾病的特征是面部畸形，男性生殖器异常，脱发（脱发）以及由于ALX4蛋白/转录因子异常或有缺陷而导致的顶孔增大。"}, {"name": "Potocki-Shaffer综合征", "detail": "ALX4gene的突变已被发现会引起Potocki-Shaffer综合征，这是一种由于ALX4转录因子异常或缺陷导致的拥有属性顶孔增大和多发性非癌性骨肿瘤（骨软骨瘤）、智能障碍、发育迟缓、面部特征明显、视力问题和心脏、肾脏和泌尿道缺陷的疾病"}]}' WHERE id = 20285;
UPDATE genes SET GHR_metadata_ch = '{"gene": "CACNA1A", "conditions": [{"name": "阵发性共济失调", "detail": "已经发现CACNA1Agene中的突变引起发作性共济失调2型（EA2），这是发作性共济失调的一种常见形式，其特征在于共济失调和不自主的眼球运动，称为眼球震颤。该病症是由于无功能的CaV2.1通道破坏了大脑中神经递质的释放而引起的。"}, {"name": "家族性偏瘫性偏头痛", "detail": "CACNA1Agene的变异已经在家族性偏瘫性偏头痛1型（FHM1）患者中发现，这种疾病的特征是偏头痛伴有一种称为先兆的神经症状，包括身体一侧暂时麻木或虚弱（偏瘫）并伴有共济失调和眼球震颤。氨基酸在结构和功能上改变CaV2.1通道，导致钙离子转运和神经递质释放增加。"}, {"name": "小脑萎缩症6型", "detail": "已知CACNA1Agene的突变会导致脊髓小脑性共济失调6型（SCA6），这是一种以进行性共济失调，眼球震颤和言语障碍（构音障碍）为特征的疾病，最常见于40-50？岁。这种情况是由于CAG重复序列增加而引起的，CAG重复序列产生异常长的α-1亚基形式，导致团块并破坏细胞转运钙离子和释放神经递质的能力，从而导致神经元死亡。"}, {"name": "偶发性偏瘫偏头痛", "detail": "已经发现CACNA1Agene的突变引起散发性偏瘫性偏头痛，并且这种情况的症状与FHM1的症状相同。在没有该病家族史的个体中观察到，其特征在于共济失调，眼球震颤以及偏头痛和先兆。"}, {"name": "19p13.13缺失综合征", "detail": "带有CACNA1Agene的19号染色体区域的缺失与19p13.13缺失综合征相关。受影响的个体在每个细胞中缺少一份CACNA1A和其他几个gene，通常表现出大头畸形，身材高大，智力残疾，癫痫发作，共济失调和其他健康问题。"}]}' WHERE id = 20293;
UPDATE genes SET GHR_metadata_ch = '{"gene": "KIF1B", "conditions": [{"name": "进行性神经性腓骨肌萎缩症", "detail": "在少数患有Charcot-Marie-Tooth病（称为2A型）的个体中，已经鉴定出一种KIF1Bgene突变。该突变改变了驱动蛋白家族成员1B的运动域中的蛋白质结构单元（氨基酸）之一。具体而言，氨基酸谷氨酰胺被蛋白质位置98处的氨基酸亮氨酸取代（写为Gln98Leu或Q98L）。尽管尚未完全了解此突变的作用，但它可能会破坏突触小泡的运输。神经末梢突触小泡的缺乏可能会损害神经冲动的传递，从而导致2A型Charcot-Marie-Tooth病的症状。但是，一些研究人员质疑KIF1Bgene突变是否确实在引起这种疾病中起作用。"}, {"name": "神经母细胞瘤", "detail": "在一些神经母细胞瘤患者中发现了含有KIF1Bgene的1号染色体区域的缺失，命名为1p36，神经母细胞瘤是一种由未成熟神经细胞（成神经细胞）组成的癌性肿瘤。1p36缺失是体细胞突变，这意味着它们发生在一个人的一生中，并且仅存在于癌变的细胞中。另外，已经在具有神经母细胞瘤病史的家族中鉴定了几种遗传的KIF1Bgene突变。这些突变改变了驱动蛋白家族成员1B蛋白中的单个氨基酸。研究表明，KIF1Bgene的缺失或突变可能会破坏细胞凋亡，使细胞生长和分裂过快或无法控制。这种不受调节的细胞生长可导致肿瘤的形成。"}, {"name": "非综合征性副神经节瘤", "detail": "据报道，患有一种称为嗜铬细胞瘤的副神经节瘤的个体存在KIF1Bgene突变。副神经节瘤是神经系统的非癌性（良性）肿瘤。嗜铬细胞瘤特别影响肾上腺，肾上腺是位于每个肾脏顶部的产生激素的小腺体。这些肿瘤通常不会引起任何症状，但在某些情况下，它们会产生过量的激素，导致危险的高血压。KIF1Bgene突变与非综合征性嗜铬细胞瘤有关，这意味着肿瘤的发生没有遗传综合征的其他特征。（换行）在非综合征嗜铬细胞瘤中鉴定的KIF1Bgene突变改变驱动蛋白家族成员1B蛋白中的单个氨基酸。研究表明，这些突变可能会破坏细胞凋亡，使细胞生长和分裂过快或以不受控制的方式分裂，并可能导致肿瘤形成。"}]}' WHERE id = 20308;
UPDATE genes SET GHR_metadata_ch = '{"gene": "DSPP", "conditions": [{"name": "牙本质生成不全", "detail": "在牙本质生成不全的人群中已鉴定出DSPPgene的20多个突变。这些遗传变化是造成这种疾病的两种形式，即II型和III型。该gene的突变也会导致II型牙本质发育不良，这种疾病的体征和症状与牙本质生成不全非常相似。但是，II型牙本质发育不良对乳牙（婴儿）的影响远大于恒牙。一些研究人员认为，这种类型的牙本质发育不良和II型和III型牙本质生成不全实际上是单一疾病的形式。(换行)大约一半的DSPPgene突变会影响牙本质唾液蛋白，从而改变其在细胞中的转运。其余的突变影响牙本质磷蛋白，干扰其正常产生和/或分泌。由于DSPP相关蛋白的这些异常，牙齿的牙本质异常柔软。牙本质有缺陷的牙齿变色，虚弱，容易断裂和腐烂。(换行)尽管DSPPgene在内耳中活跃，但尚不清楚DSPPgene突变是否与一些II型牙本质生成不全的老年人的听力损失有关。"}, {"name": "非综合征性听力损失", "detail": "遗传学家庭参考资料提供有关非综合征性听力损失的信息。"}]}' WHERE id = 20321;
UPDATE genes SET GHR_metadata_ch = '{"gene": "CLRN1", "conditions": [{"name": "亚瑟综合症", "detail": "CLRN1gene的突变会导致一种罕见的Usher综合征，称为IIIA型Usher综合征（USH3A），这种疾病的特征是听力丧失和视力丧失以及平衡与协调问题。CLN6突变导致异常短而无功能的clarin 1产生，从而破坏该蛋白的正常功能，从而导致III型Usher综合征。"}, {"name": "视网膜色素变性", "detail": "已经在色素性视网膜炎患者中发现了CLRN1gene的突变，色素性视网膜炎是一种影响视网膜导致视力丧失的疾病。主要症状是儿童时期夜视丧失，随着疾病的发展，受影响的个体成年后合法失明。"}]}' WHERE id = 20334;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SMC3", "conditions": [{"name": "Cornelia de Lange综合征", "detail": "已发现SMC3gene中至少有15个突变会引起Cornelia de Lange综合征，这是一种影响身体许多部位的发育障碍。研究人员估计，该gene的突变占该病所有病例的1%至2%。(换行)导致Cornelia de Lange综合征的大多数SMC3gene突变要么改变SMC3蛋白中的单个蛋白构建块（氨基酸），要么添加或删除蛋白中的少量氨基酸。这些突变中的每一个都会改变蛋白质的结构和功能，这可能会干扰粘着蛋白复合物的活性，并削弱其调节对正常发育至关重要的gene的能力。尽管研究人员尚不完全了解这些变化如何引起Cornelia de Lange综合征，但他们怀疑gene调控的改变可能是该病特征性的许多发育问题的基础。(换行)研究表明，SMC3gene中的突变倾向于导致某种形式的Cornelia de Lange综合征，其特征相对较轻。与最常见的疾病原因NIPBLgene突变相比，SMC3gene突变通常不会引起发育和生长的显着延迟，也不太可能引起重大的先天缺陷。"}]}' WHERE id = 20375;
UPDATE genes SET GHR_metadata_ch = '{"gene": "MYH7", "conditions": [{"name": "家族性肥厚型心肌病", "detail": "MYH7gene突变是家族性肥厚型心肌病的常见原因，占所有病例的35%。这种情况的特征是心肌增厚（肥大）。尽管一些患有家族性肥厚型心肌病的人没有明显的健康影响，但所有受影响的人都有心力衰竭和猝死的风险增加。（换行）大多数导致家族性肥厚型心肌病的MYH7gene突变会改变单蛋白结构单元（氨基酸）-肌球蛋白重链蛋白。改变后的蛋白质很可能掺入粗丝中，但可能无法正常发挥作用。目前尚不清楚MYH7gene突变如何导致家族性肥厚型心肌病的特征。"}, {"name": "远端肌病", "detail": "已发现MYH7gene中至少有六个突变引起Laing远端肌病。这种情况会导致进行性肌肉无力，特别是影响手臂和腿部。大多数突变导致-肌球蛋白重链。这些突变中的一些改变单个氨基酸，而其他突变从重链中删除单个氨基酸。MYH7gene的变化可能会破坏肌肉细胞中II型肌球蛋白的正常功能。具体来说，研究人员怀疑突变会改变-肌球蛋白重链。改变的尾部区域可能无法与其他蛋白质相互作用，包括其他肌球蛋白蛋白质的尾部区域。目前尚不清楚这些肌球蛋白结构和功能的变化如何导致Laing远端肌病患者进行性肌无力。"}, {"name": "室致密化不全", "detail": "已发现至少30个MYH7gene突变会导致左心室不紧致，这发生在心脏左下腔（左心室）发育不正常时。心肌减弱，无法有效地抽血。这些心脏异常可导致从完全缺乏症状到心源性猝死的广泛结果。其他体征和症状包括心律不规则（心律失常），呼吸短促（呼吸困难）和心力衰竭。(换行)目前尚不清楚MYH7gene突变如何导致左心室致密化不全。在出生前的正常发育过程中，心肌变得紧实，变得光滑而结实。MYH7gene突变可能导致此过程发生变化，导致左心室心肌不紧实，但变厚且呈海绵状。这种异常的心肌很弱并且不能有效收缩，导致左心室不紧致的各种体征和症状。"}, {"name": "肌球蛋白储存性肌病", "detail": "MYH7gene中至少有六个突变与肌球蛋白储存性肌病有关。这种情况会导致肌肉无力，其特征是在I型骨骼肌纤维内形成蛋白质团块，其中包括II型肌球蛋白。导致肌球蛋白储存性肌病的MYH7gene突变改变了心脏尾部区域的氨基酸-肌球蛋白重链。研究人员认为，这些突变会损害粗丝的正确形成。异常蛋白质积聚在I型骨骼肌纤维中，形成该疾病特有的蛋白质团块。目前尚不清楚该gene突变如何导致肌球蛋白储存性肌病患者的肌肉无力。"}, {"name": "先天性纤维类型不均衡", "detail": "遗传学家庭参考资料提供有关先天性纤维类型不均衡的信息。"}, {"name": "家族性扩张型心肌病", "detail": "遗传学家庭参考提供有关家族性扩张型心肌病的信息。"}, {"name": "家族性限制性心肌病", "detail": "遗传学家庭参考资料提供有关家族性限制性心肌病的信息。"}]}' WHERE id = 20382;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ETFB", "conditions": [{"name": "戊二酸血症II型", "detail": "ETFBgene中的某些突变阻止了电子转移黄素蛋白酶的产生。其他突变导致产生缺陷酶，其不能在分解脂肪和蛋白质的一系列反应（代谢途径）中发挥其作用。这种酶缺乏使这些营养素以及营养素被部分分解时产生的化合物积累到异常水平，特别是当身体处于压力下时。代谢不完全的有毒产物会损害许多身体系统中的细胞，导致II型戊二酸血症的体征和症状。"}]}' WHERE id = 20415;
UPDATE genes SET GHR_metadata_ch = '{"gene": "KRIT1", "conditions": [{"name": "脑海绵状血管畸形", "detail": "在脑海绵状畸形家族中已鉴定出100多个KRIT1gene突变，这些家族是大脑中较弱且易于渗漏的血管集合。实际上，所有这些突变都会在制备KRIT1蛋白的说明中产生过早的终止信号，从而阻止足够的KRIT1蛋白产生。这种蛋白质的缺乏可能会损害复合物的功能。结果，RhoA GTP酶信号异常开启，减弱细胞连接并增加血管壁的通透性。在一些脑海绵状血管畸形患者中，大脑渗漏增加可能导致健康问题，如头痛，癫痫发作和大脑出血（脑出血）。（换行）KRIT1gene突变占所有家族性脑海绵状畸形病例的50%。在西班牙裔传统人士中，一种特定的突变可导致多达70%的病例。该突变改变了KRIT1gene中1363位的单个DNA构建块（核苷酸），写为1363C>T。"}]}' WHERE id = 20429;
UPDATE genes SET GHR_metadata_ch = '{"gene": "GLRA1", "conditions": [{"name": "遗传性高血压", "detail": "已发现GLRA1gene中至少29个突变会导致遗传性高位神经痛。这些突变大多数改变了？甘氨酸受体蛋白的1个亚基。最常见的突变是在蛋白质271位（写为Arg271Leu或R271L）上用氨基酸亮氨酸替换了氨基酸精氨酸。（换行）导致遗传性多发性上睑下垂的GLRA1gene突变削弱了甘氨酸受体蛋白对配体甘氨酸的反应能力。一些GLRA1gene突变会改变甘氨酸受体的结构，从而阻止受体打开。其他突变阻止受体到达细胞膜。当甘氨酸受体无功能或缺失时，氯离子不能进入细胞，细胞信号传导增加。脊髓和脑干中这种过度活跃的细胞信号传导被认为会引起异常的肌肉运动，夸张的惊吓反应和遗传性多发性上睑下垂的其他症状。"}]}' WHERE id = 20437;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SCN1A", "conditions": [{"name": "家族性偏瘫性偏头痛", "detail": "在家族性偏瘫性偏头痛3型（FHM3）（一种在家庭中运行的偏头痛形式）的人中，已经鉴定出SCN1Agene中至少有七个突变。这些突变中的每一个都会改变NaV1.1通道中的单个蛋白质构建块（氨基酸），从而改变通道的结构。异常通道比平时开放的时间更长，这增加了钠离子流入神经元的流量。这种增加触发细胞释放更多的神经递质。由此产生的神经元之间信号传导的变化使得FHM3患者更容易发生这些严重的头痛。"}, {"name": "遗传性癫痫伴高热惊厥加", "detail": "已经发现SCN1Agene中的数百个突变会导致遗传性癫痫伴高热惊厥加（GEFS+），这是一系列严重程度不同的癫痫发作。这些情况包括从婴儿期开始通常在5岁时停止的简单的高热（与发烧相关）癫痫发作，以及高热惊厥加（FS+）。FS+涉及高热和其他类型的癫痫发作，包括与儿童期以后的发烧（高热惊厥）无关的癫痫发作。GEFS+频谱还包括其他情况，例如Dravet综合征（也称为婴儿的严重肌阵挛性癫痫或SMEI），这些情况会导致更严重的癫痫发作持续时间更长，并且可能难以控制。这些反复发作（癫痫病）会随着时间的推移而恶化，并经常伴有脑功能下降。（换行）GEFS+基础的SCN1Agene突变对NaV1.1通道的功能有多种影响。一些突变会改变通道中的单个氨基酸，从而改变通道的结构。其他导致生成NaV1.1通道的非功能版本或减少每个单元中生成的这些通道的数量。还有其他突变会改变通道关键区域的单个氨基酸。所有这些遗传变化都会影响NaV1.1通道将钠离子转运到神经元中的能力。一些突变被认为会降低通道活性，而另一些则可能会增加通道活性。但是，尚不清楚这些遗传变化如何构成癫痫发作的基础，或者为什么它们会导致一系列严重程度不同的癫痫发作。"}, {"name": "婴儿恶性移行性部分性癫痫发作", "detail": "遗传学家庭参考提供有关婴儿恶性迁移部分性癫痫发作的信息。"}, {"name": "其他疾病", "detail": "SCN1Agene的常见变化（多态性）与某些抗癫痫药的有效性有关。这种多态性被写为ICS5N+5G>A，它改变了SCN1Agene中的单个DNA构建块（核苷酸）。研究表明，这种多态性与抗癫痫药苯妥英钠和卡马西平的最大安全量（剂量）有关。这些药物通过阻断神经元中的钠通道（如NaV1.1）来治疗癫痫。太小的剂量可能无法有效控制癫痫发作，而太大的剂量可能会导致不良副作用。研究人员希望医生能够测试ICS5N+5G>A多态性，以帮助确定每个人最安全和最有效的抗癫痫药物剂量。"}]}' WHERE id = 20475;
UPDATE genes SET GHR_metadata_ch = '{"gene": "PNPLA3", "conditions": [{"name": "非酒精性脂肪性肝病", "detail": "PNPLA3gene的特定变异与发生非酒精性脂肪性肝病（NAFLD）的风险增加有关。NAFLD是肝脏中过量脂肪的累积，可导致类似于酒精滥用造成的损害的肝脏损害，但这种情况发生在不饮酒的人群中。在某些情况下，NAFLD导致肝脏炎症（非酒精性脂肪性肝炎，也称为NASH）和永久性肝损伤（肝硬化）。（换行）与NAFLD相关的PNPLA3gene变异将蛋白质构建块（氨基酸）异亮氨酸改变为蛋白质位置148处的氨基酸甲硫氨酸，写为Ile148Met或I148M。研究表明，改变的蛋白质导致肝脏中脂肪的产生增加和分解减少。正在进行研究以确定这种和其他遗传变化如何促成NAFLD及其并发症的发展。"}, {"name": "遗传性血色素沉着症", "detail": "遗传学家庭参考提供有关遗传性血色素沉着病的信息。"}, {"name": "其他疾病", "detail": "PNPLA3gene的I148M变异也与其他肝脏疾病的恶化有关，例如称为丙型肝炎的病毒感染。这种变异还增加了酒精中毒患者肝损伤的风险。这种作用的机制尚不清楚，但是改变后的蛋白质似乎会增加患有这些疾病的人的肝纤维化。"}]}' WHERE id = 20489;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SLC12A3", "conditions": [{"name": "吉特曼综合症", "detail": "在吉特曼综合症患者中已鉴定出SLC12A3gene的140多个突变。这些突变大多数会改变NCC共转运蛋白中的单个蛋白结构单元（氨基酸）。这些突变阻止蛋白质到达细胞膜或改变蛋白质转运钠离子和氯离子的能力。SLC12A3gene中的其他突变会插入或删除遗传物质，或导致异常短而无功能的NCC蛋白产生。SLC12A3gene中的（换行）突变损害肾脏重吸收盐的能力，导致尿液中过量盐的损失（盐浪费）。盐运输异常也会影响其他离子的重吸收，包括钾，镁和钙的离子。由此导致的体内离子失衡是吉特曼综合症的主要特征。"}, {"name": "其他疾病", "detail": "一些研究表明，SLC12A3gene的正常变异（多态性）可能有助于解释人与人之间的血压差异。某些罕见的多态性似乎也可以预防高血压（高血压）。SLC12A3gene的变化可能会通过改变肾脏将盐重吸收到血液中的能力来影响血压。但是，一些研究尚未发现SLC12A3gene变异与血压之间有任何关联。"}]}' WHERE id = 20524;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ABCA4", "conditions": [{"name": "视锥-视杆营养不良", "detail": "ABCA4gene的突变与称为视锥细胞营养不良的视力障碍有关，约30-60%被认为以常染色体隐性遗传。一种以视力丧失（敏锐度），对光的敏感性增加（畏光）和色觉受损为特征的疾病。携带ABCA4变异的个体的特征是由于存在无法从感光细胞中去除有毒物质的改变的蛋白质而导致进行性视力丧失。"}, {"name": "Stargardt黄斑变性", "detail": "ABCA4gene的突变与一种称为Stargardt黄斑变性的眼部疾病有关，这种疾病是由于蛋白质缺陷导致的，该蛋白质抑制了诸如以下物质的去除和积累：；来自感光细胞的N-视黄叉基-PE和脂褐素（一种脂肪黄色色素）导致进行性视力丧失，特别是影响中央和夜视。"}, {"name": "年龄相关性黄斑变性", "detail": "携带ARMS2，HTRA1gene的10q26区域的遗传变化以及CFH，APOE ABCA4gene的其他一些变化主要与年龄相关性黄斑变性的风险增加有关，黄斑变性的特征是黄斑缺损，这是导致中央视力的原因。年龄相关性黄斑变性分为两种形式：；干燥和湿润形式。干燥形式与黄色沉积物的积累相关，导致视力随时间缓慢丧失。然而，由于血管的异常生长和渗漏导致黄斑损伤，湿形式导致快速视力丧失。"}, {"name": "视网膜色素变性", "detail": "ABCA4gene的突变（〜2-5%）与称为色素性视网膜炎的非综合征性常染色体隐性遗传疾病有关。由于视网膜视杆细胞和视锥细胞的（光感受器）异常导致进行性视力丧失的病症导致几种临床表型，包括夜盲症，视力，眼底外观，后囊下白内障，视网膜深处的白点，透明身体等。"}]}' WHERE id = 20527;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SCN9A", "conditions": [{"name": "先天性疼痛不敏感", "detail": "已发现SCN9Agene中至少有13个突变会导致先天性对疼痛不敏感，这种情况会抑制感知身体疼痛的能力。导致先天性疼痛不敏感的SCN9Agene突变会在制作NaV1.7钠通道α亚基的说明中产生过早的停止信号。结果，产生了缩短的，无功能的亚基，其不能掺入通道中，导致功能性NaV1.7钠通道的丧失。这些通道的丧失损害了疼痛信号从损伤部位向大脑的传递，导致受影响的人对疼痛不敏感。嗅觉感觉神经元中该通道的丢失可能会损害与气味相关的信号向大脑的传输，从而导致嗅觉完全丧失（失眠）。"}, {"name": "红斑性肢痛症", "detail": "已发现SCN9Agene中的10多个突变会引起红血丝痛，这种疾病的特征是身体各个部位（尤其是手和脚）出现疼痛，发红和肿胀。所有鉴定出的突变都会改变NaV1.7钠通道中的一个蛋白质结构单元（氨基酸）。这些突变导致NaV1.7钠通道比平时更容易打开，并且比正常情况下保持更长的打开时间，从而增加了在伤害感受器内产生神经冲动的钠离子流。钠离子的这种增加增强了疼痛信号的传递，导致红血丝痛的体征和症状。"}, {"name": "阵发性极度疼痛障碍", "detail": "已发现SCN9Agene中约有10个突变会引起阵发性极度疼痛疾病。这种情况的特征是严重的疼痛发作，伴有皮肤发红和温暖（潮红），有时还伴有癫痫发作以及呼吸和心率变化。导致这种情况的突变会改变NaV1.7钠通道α亚基中的单个氨基酸。结果，钠通道关闭时不会完全关闭，从而使钠离子异常流入伤害感受器。钠离子的这种增加增强了疼痛信号的传递，导致阵发性极度疼痛障碍患者经历的疼痛发作。"}, {"name": "小纤维神经病变", "detail": "SCN9Agene突变约占小纤维神经病病例的30%，这种疾病的特征是严重的疼痛发作和冷热区分能力降低。导致这种情况的突变会改变NaV1.7钠通道α亚基中的单个氨基酸。由于α亚基的改变，关闭时钠通道不会完全关闭，从而使钠离子异常流入伤害感受器。钠离子的这种增加增强了疼痛信号的传递。在这种情况下，从伤害感受器延伸并传递疼痛信号（轴突）的小纤维随时间退化。这种变性的原因尚不清楚，但它可能是体征和症状的原因，例如温度分化的丧失。"}, {"name": "遗传性癫痫伴高热惊厥加", "detail": "遗传学家庭参考资料提供有关伴有高热惊厥的遗传性癫痫的信息。"}, {"name": "II型遗传性感觉和自主神经病", "detail": "遗传学家庭参考提供有关II型遗传性感觉和自主神经病的信息。"}, {"name": "其他疾病", "detail": "在一组高热惊厥患者中发现了SCN9Agene的至少三个突变，这是由高烧引起的惊厥。高热惊厥是幼儿最常见的惊厥类型，影响欧洲和北美2%至5%的儿童。患有高热惊厥的儿童在以后的生活中有2%至9%的机会发生与发烧无关的惊厥。当高热惊厥与SCN9Agene突变相关时，该病称为家族性高热惊厥3B。如果这些人继续发展而没有发烧，则该病被称为全身性癫痫，伴有7型高热惊厥。导致这些conditions的突变改变了NaV1.7钠通道α亚基中的单个氨基酸。钠通道的变化如何导致高热惊厥尚不清楚。SCN9Agene中的（换行）变体与另一个称为SCN1A的gene中的突变结合时，会改变某些个体中称为Dravet综合征的癫痫发作的进程。Dravet综合征的特征是婴儿期惊厥性癫痫发作，其次是儿童期失神性癫痫发作，这会导致短期意识丧失。在儿童中期，癫痫发作转变为全身性强直阵挛型，涉及肌肉僵硬，抽搐和意识丧失。全身性强直阵挛性癫痫发作也与癫痫发作时间延长有关，称为非惊厥性癫痫持续状态。这些事件可能导致混乱和持续数小时至数周的警觉性丧失。SCN1Agene突变是Dravet综合征的最常见原因，但是当受影响的个体也有SCN9Agene改变（可能不会引起健康问题）时，Dravet综合征的体征和症状会更加严重。例如，同时具有SCN1A和SCN9Agene变化的个体可能在婴儿期患有癫痫持续状态，并随时经历各种癫痫发作。尚不清楚SCN9Agene的变化如何导致Dravet综合征的体征和症状。"}]}' WHERE id = 20555;
UPDATE genes SET GHR_metadata_ch = '{"gene": "FIP1L1", "conditions": [{"name": "PDGFRA相关的慢性嗜酸性粒细胞白血病", "detail": "从4号染色体上删除遗传物质会将FIP1L1gene的一部分和另一个称为PDGFRA的gene的一部分聚集在一起，从而创建FIP1L1-PDGFRA融合gene。这种突变是一种体细胞突变，这意味着它是在一个人的一生中获得的，并且仅存在于某些细胞中。该融合gene引起PDGFRA相关的慢性嗜酸性粒细胞白血病，其是一种血细胞癌，其特征在于嗜酸性粒细胞（一种参与过敏反应的白细胞）数量增加。（换行）由融合gene产生的FIP1L1-PDGFRA蛋白具有正常PDGFRA蛋白的功能，其刺激细胞内的信号传导途径，其控制许多重要的细胞过程，例如细胞生长和分裂（增殖）和细胞存活。但是，与正常的PDGFRA蛋白不同，FIP1L1-PDGFRA蛋白不断打开（组成性激活），这意味着细胞始终接收到增殖信号。当FIP1L1-PDGFRA融合gene出现在血细胞前体中时，嗜酸性粒细胞（偶尔还有其他血细胞）的生长受到很差的控制，导致PDGFRA相关的慢性嗜酸性粒细胞白血病。目前还不清楚为什么嗜酸性粒细胞优先受到这种遗传变化的影响。"}]}' WHERE id = 20599;
UPDATE genes SET GHR_metadata_ch = '{"gene": "L2HGDH", "conditions": [{"name": "2-羟基戊二酸尿症", "detail": "研究人员已经确定了L2HGDHgene中的70多个突变，这些突变会导致一种称为L-2-羟基戊二酸尿症（L-2-HGA）的2-羟基戊二酸尿症。这种情况有多种体征和症状，这些体征和症状是由生命早期开始的大脑进行性损伤引起的。（换行）一些L2HGDHgene突变改变L-2-羟基戊二酸脱氢酶中的单个蛋白质构建块（氨基酸），这可能损害其功能。其他突变会在gene中插入或删除遗传物质，或导致产生异常短而无功能的酶。由于缺乏功能性酶，L-2-羟基戊二酸不会分解，而是在细胞中积累。在高水平，这种化合物可以破坏细胞并导致细胞死亡。脑细胞似乎最容易受到这种化合物的毒性作用，这可以解释为什么L-2-HGA的体征和症状主要涉及大脑。"}]}' WHERE id = 20613;
UPDATE genes SET GHR_metadata_ch = '{"gene": "FOXF1", "conditions": [{"name": "肺泡毛细血管发育不良伴肺静脉错位", "detail": "在肺泡毛细血管发育不良伴肺静脉错位（ACD/MPV）的婴儿中，已鉴定出FOXF1gene至少有四个突变。一些突变会改变用于制造FOXF1蛋白的单个蛋白结构单元（氨基酸）。其他突变会在FOXF1gene中插入或删除遗传物质。这些突变导致无法调节发育的失活蛋白，导致肺血管异常形成。患有FOXF1gene突变的患儿通常也有胃肠道异常。"}]}' WHERE id = 20638;
UPDATE genes SET GHR_metadata_ch = '{"gene": "PAX3", "conditions": [{"name": "颅面耳聋手综合征", "detail": "在患有颅面耳聋手综合征的个体中已鉴定出至少一种PAX3gene突变，该疾病的特征是独特的面部特征，严重的听力下降和可能限制运动的手部肌肉异常。该突变用PAX3蛋白中第47位的另一个称为赖氨酸的氨基酸（写为Asn47Lys或N47K）替换了称为天冬酰胺的单个蛋白质结构单元（氨基酸）。该突变似乎影响PAX3蛋白与DNA结合的能力。结果，PAX3蛋白不能控制其他gene的活性，并且不能指导神经嵴细胞形成特化组织。神经rest细胞缺乏专业化会导致颅面耳聋手综合征中颅面骨，神经组织和肌肉的生长受损。"}, {"name": "瓦尔登堡综合症", "detail": "在Waardenburg综合征患者中已鉴定出数十种PAX3gene突变，Waardenburg综合征是一组遗传病，可导致听力丧失以及头发，皮肤和眼睛的颜色（色素沉着）发生变化。具体而言，PAX3gene突变可导致I型和III型Waardenburg综合征。（换行）其中一些突变会改变PAX3蛋白中的单个氨基酸。其他突变导致PAX3蛋白异常小的版本。研究人员认为，所有PAX3gene突变都具有相同的作用：它们阻止PAX3蛋白与DNA结合并调节其他gene的活性。结果，黑素细胞不会在皮肤，头发，眼睛和内耳的某些区域发育，从而导致听力损失和色素沉着的斑片状损失，这是Waardenburg综合征的特征。此外，PAX3蛋白功能的丧失破坏了某些骨骼和肌肉的发育，导致Waardenburg综合征III型患者的手臂和手部异常。"}, {"name": "癌症", "detail": "涉及PAX3gene的遗传物质的重排与称为肺泡横纹肌肉瘤的肌肉组织癌症相关，其通常影响青少年和年轻人。这种遗传变化是体细胞的，这意味着它不是遗传的，只发生在引起癌症的细胞中。重排导致PAX3gene与13号染色体上的FOXO1Agene（也称为FKHR）融合。融合的PAX3-FOXO1Agene具有增强的激活参与肌生成的gene的能力并且可以防止细胞死亡。结果，肌肉细胞生长变得不受控制，这可能导致这种肌肉组织癌。"}]}' WHERE id = 20661;
UPDATE genes SET GHR_metadata_ch = '{"gene": "MYCN", "conditions": [{"name": "Feingold综合征", "detail": "已经发现至少29个涉及MYCNgene的突变引起Feingold综合征。大多数突变会导致蛋白质制造说明中的过早停止信号。在Feingold综合征的某些情况下，整个MYCNgene被删除。这些遗传变化阻止每个细胞中gene的一个拷贝产生任何功能性MYCN蛋白。结果，只有正常量的这种蛋白质的一半可用于控制胚胎发育过程中特定gene的活性。尚不清楚MYCN蛋白量的减少如何引起Feingold综合征的各种特征。"}, {"name": "神经母细胞瘤", "detail": "一些gene突变是在一个人的一生中获得的，并且仅存在于某些细胞中。这些不是遗传的变化称为体细胞突变。当DNA复制自身（复制）以准备细胞分裂时，有时会发生体细胞突变。复制过程中的错误可能导致细胞内gene的一个或多个额外拷贝。某些gene的额外拷贝的存在，称为gene扩增，可以成为肿瘤细胞形成和生长的基础。例如，在约25%的神经母细胞瘤中发现MYCNgene的扩增。神经母细胞瘤是一种在发育中的神经细胞中出现的癌性肿瘤。在这些肿瘤中，MYCNgene的拷贝数差异很大，但通常在50到100之间。MYCNgene的扩增与更严重形式的神经母细胞瘤有关。尚不清楚该gene的扩增如何导致神经母细胞瘤的侵袭性。"}, {"name": "视网膜母细胞瘤", "detail": "遗传学家庭参考提供有关视网膜母细胞瘤的信息。"}]}' WHERE id = 20689;
UPDATE genes SET GHR_metadata_ch = '{"gene": "YARS", "conditions": [{"name": "进行性神经性腓骨肌萎缩症", "detail": "YARSgene中至少有3个突变导致一种称为显性中间C的Charcot-Marie-Tooth病。一个突变用酪氨酰-tRNA合成酶蛋白41位的氨基酸精氨酸取代氨基酸甘氨酸（写为Gly41Arg或G41R）。另一个突变用蛋白质196位的氨基酸赖氨酸取代了氨基酸谷氨酸（写为Glu196Lys或E196K）。第三个YARSgene突变导致酪氨酰-tRNA合成酶的版本改变，缺少四个氨基酸。YARSgene中的突变可能会降低酪氨酰-tRNA合成酶的活性，这可能会影响任何含有酪氨酸的蛋白质的合成。目前尚不清楚这些突变如何导致Charcot-Marie-Tooth病的主要中间C形式。"}]}' WHERE id = 20709;
UPDATE genes SET GHR_metadata_ch = '{"gene": "TARDBP", "conditions": [{"name": "肌萎缩侧索硬化症", "detail": "已发现TARDBPgene中至少有60个突变会引起肌萎缩性侧索硬化症（ALS），这种疾病的特征是进行性肌肉无力，肌肉量减少和无法控制运动。大多数突变会改变TDP-43蛋白中的单个蛋白结构单元（氨基酸）。这些变化中的大多数会影响参与mRNA加工的蛋白质区域，可能会破坏其他蛋白质的产生。TDP-43蛋白的变化会导致蛋白错误折叠并形成蛋白团块（聚集体），这在某些ALS患者的控制肌肉运动（运动神经元）的神经细胞中已发现。目前尚不清楚TDP-43蛋白聚集体是否会导致导致ALS的神经细胞死亡，或者它们是否是垂死细胞的副产物。(换行)一些由TARDBPgene突变引起的ALS患者也会出现额颞叶痴呆（FTD），这是一种进行性脑部疾病，会影响人格，行为和语言。目前尚不清楚为什么某些具有TARDBPgene突变的人会发展为FTD，而另一些则不会。患有这两种疾病的人被诊断患有ALS-FTD。"}, {"name": "其他疾病", "detail": "已经发现TARDBPgene中的突变引起额颞叶痴呆（FTD），而没有肌萎缩性侧索硬化症（ALS，如上所述）的特征。由TARDBPgene突变引起的FTD的特征是逐渐丧失解决问题的能力和语言理解能力。受影响的人的性格和行为经常发生变化，这可能使其难以以适合社会的方式与他人互动。导致FTD的大多数TARDBPgene突变都会改变TDP-43蛋白中的单个氨基酸。这些突变被认为仅影响蛋白质的一部分，而使蛋白质的其他部分保持功能。由于这些TARDBPgene突变会导致蛋白质具有某些残留功能，因此该病的特征往往会出现在生命的后期，六十年代末或七十年代初。一些继承了改变的TARDBPgene的人从未发展FTD，这种情况称为外显率降低。"}]}' WHERE id = 20711;
UPDATE genes SET GHR_metadata_ch = '{"gene": "PKD2", "conditions": [{"name": "多囊肾病", "detail": "在多囊肾患者中已鉴定出PKD2gene的75个以上突变。这些突变约占所有常染色体显性遗传性多囊肾病（ADPKD）病例的15%，这是该疾病的最常见类型。PKD2gene中的突变包括单个DNA构建块（碱基对）的变化以及gene中少量碱基对的缺失或插入。预计大多数PKD2突变会导致产生异常小的，无功能的polycystin-2蛋白。尽管研究人员不确定缺乏polycystin-2如何导致囊肿的形成，但它可能会破坏蛋白质与polycystin-1的相互作用，并改变细胞内和原发纤毛中的信号传导。结果，衬在肾小管内的细胞可能异常生长和分裂，导致多囊肾病特征性的许多囊肿的生长。"}]}' WHERE id = 20721;
UPDATE genes SET GHR_metadata_ch = '{"gene": "FOXC2", "conditions": [{"name": "淋巴水肿-双歧综合征", "detail": "FOXC2gene中的50多个突变可引起淋巴水肿-distichiasis综合征。这些突变中的大多数会插入或删除一些DNA构建块（核苷酸），从而导致制备FOXC2蛋白的说明中出现过早的终止信号。这些突变导致FOXC2蛋白的产生异常小，并且不能有效地附着（结合）到DNA上。结果，改变的蛋白质不能调节其他gene的活性。其他突变会改变FOXC2蛋白区域中与DNA结合的一个蛋白结构单元（氨基酸），从而阻止该蛋白调节gene活性。目前尚不清楚为什么FOXC2gene的突变会影响眼部区域和淋巴管的发育，而淋巴管是受淋巴水肿-双歧病综合征影响的身体主要区域。"}]}' WHERE id = 20734;
UPDATE genes SET GHR_metadata_ch = '{"gene": "FANCC", "conditions": [{"name": "范可尼贫血", "detail": "已发现FANCCgene中至少有50个突变会引起Fanconi贫血，这是一种以骨髓功能下降，癌症风险增加和身体异常为特征的疾病。FANCCgene的突变约占所有Fanconi贫血病例的15%。在具有中欧和东欧（Ashkenazi）犹太背景的人中发现了FANCCgene的特定突变。这种突变（写为456+4A>T）破坏了gene指令用于制造蛋白质的方式。具有此突变的人往往比具有FANCCgene其他一些突变的人具有更严重的体征和症状。（换行）导致Fanconi贫血的FANCCgene中的大多数突变导致蛋白质功能缺失或降低。结果，FA核心复合物不能起作用并且整个FA途径被破坏。由于途径被破坏，DNA损伤无法有效修复，ICL会随着时间的推移而累积。ICL阻止DNA复制，最终导致由于不能产生新的DNA分子而导致的异常细胞死亡或由于缺乏DNA修复过程而导致的不受控制的细胞生长。快速分裂的细胞，例如骨髓细胞和发育中的胎儿细胞，尤其受到影响。这些细胞的死亡导致血细胞减少和范可尼贫血的身体异常。当DNA中的错误累积导致不受控制的细胞生长时，受影响的个体可能会发展为白血病或其他癌症。"}]}' WHERE id = 20747;
UPDATE genes SET GHR_metadata_ch = '{"gene": "PHF21A", "conditions": [{"name": "Potocki-Shaffer综合征", "detail": "导致PHF21Agene缺失的遗传变化会导致一种称为Potocki-Shaffer综合征的疾病。患有这种疾病的人的两块骨头的开口增大，这些骨头构成了颅骨顶部和侧面的大部分（顶孔增大）和多个非癌性骨肿瘤（骨软骨瘤）。在一些患有Potocki-Shaffer综合征的人中看到的其他体征和症状包括智力残疾，发育迟缓，独特的面部特征，视力问题以及心脏，肾脏和泌尿道的缺陷。（换行）Potocki-Shaffer综合征（也称为近端11p缺失综合征）是由11号染色体短（p）臂的遗传物质缺失引起的。在患有这种情况的人中，该区域内PHF21Agene的缺失是导致智力残疾和独特面部特征的原因。缺失可能导致PHF21A蛋白量减少。据认为，由此导致的组蛋白去甲基化的破坏改变了参与神经元和面部发育的gene的活性，导致智力残疾和独特的面部特征。在11号染色体的同一区域ALX4和EXT2中其他gene的丢失分别是顶孔扩大和多发性骨软骨瘤的基础。缺失区域中其他gene的丢失可能有助于Potocki-Shaffer综合征的其他特征。"}]}' WHERE id = 20754;
UPDATE genes SET GHR_metadata_ch = '{"gene": "MUC1", "conditions": [{"name": "髓样囊性肾病1型", "detail": "MUC1gene的突变会导致1型髓样囊性肾病（MCKD1）。这种情况的特征是肾功能受损，通常在成年期开始并逐渐恶化。一些受影响的个体在肾脏中形成称为髓样囊肿的充满液体的口袋。（换行）当将称为胞嘧啶的单个DNA构建块（核苷酸）插入MUC1gene时，发生这种情况。这些插入发生在gene的一个特定区域，该区域提供重复粘蛋白结构域的指示。这些突变导致粘蛋白1蛋白改变的产生，尽管尚不清楚这种改变如何引起肾脏疾病。为什么MUC1gene突变的影响仅限于肾脏也是未知的。"}]}' WHERE id = 20755;
UPDATE genes SET GHR_metadata_ch = '{"gene": "TRPS1", "conditions": [{"name": "I型Trichorhinophalangeal综合征", "detail": "已发现TRPS1gene中的130多个突变会导致I型毛发鼻指综合征（TRPS I）。TRPS I是导致骨骼和关节畸形的疾病；独特的面部特征；以及皮肤，头发，牙齿，汗腺和指甲的异常。导致这种情况的突变会影响每个细胞中TRPS1gene的一个拷贝。其中一些突变改变了单个蛋白质结构单元（氨基酸），并具有“显性负效应”，这意味着TRPS1gene一个拷贝产生的改变的蛋白质干扰了另一个拷贝产生的正常蛋白质的功能。其他突变会添加或删除少量遗传物质或改变gene说明用于制备蛋白质的方式。这些变化具有“功能丧失”的作用，并导致蛋白质的产生，该蛋白质不能进入细胞核，需要与DNA结合并抑制gene活性。由于该蛋白抑制gene活性的能力下降，特别是调节骨骼和软骨生长的gene，TRPS1gene突变的人手指和脚趾骨骼异常，关节异常，面部特征明显，以及TRPS I的其他体征和症状。"}, {"name": "Trichorhinophalangeal综合征II型", "detail": "TRPS1gene位于8号染色体上的一个区域，该区域在II型毛发鼻指综合征（TRPS II）患者中被删除。TRPS II是导致骨骼和关节畸形的疾病；独特的面部特征；智力障碍；以及皮肤，头发，牙齿，汗腺和指甲的异常。这种缺失的结果是，受影响的个体在每个细胞中缺失了一个TRPS1gene拷贝，TRPS1蛋白的产生减少了一半。研究人员认为，TRPS1蛋白的这种减少会削弱某些控制骨骼和软骨生长的gene的调控，从而导致TRPS II患者身材矮小，手指和脚趾的骨骼异常，关节异常以及独特的面部特征。TRPS1gene附近其他gene的缺失可能有助于这种情况的其他特征。"}]}' WHERE id = 20804;
UPDATE genes SET GHR_metadata_ch = '{"gene": "PDGFRA", "conditions": [{"name": "PDGFRA相关的慢性嗜酸性粒细胞白血病", "detail": "涉及PDGFRAgene的遗传异常会导致一种称为PDGFRA相关的慢性嗜酸性粒细胞白血病的血细胞癌。这种情况的特点是嗜酸性粒细胞数量增加，这是一种参与过敏反应的白细胞。这些遗传异常是体细胞突变，这是一个人一生中获得的仅存在于某些细胞中的突变。这些遗传异常中最常见的是从4号染色体上删除遗传物质，该遗传物质汇集了两个geneFIP1L1和PDGFRA的一部分，从而创建了FIP1L1-PDGFRA融合gene。有时，FIP1L1以外的gene与PDGFRAgene融合。改变PDGFRAgene中单个DNA结构单元的突变（点突变）也可能导致这种情况，尽管这些突变很少见。（换行）由FIP1L1-PDGFRA融合gene（以及其他PDGFRA融合gene）产生的蛋白质具有PDGFRA蛋白质的功能。但是，与正常的PDGFRA蛋白不同，融合蛋白不需要结合要激活的血小板衍生生长因子蛋白。类似地，PDGFRAgene中的点突变可导致PDGFRA蛋白在没有配体结合的情况下被激活。结果，信号传导途径不断打开（组成性激活），这增加了细胞的增殖和存活。当FIP1L1-PDGFRA融合gene突变或PDGFRAgene中的点突变发生在早期血细胞中时，嗜酸性粒细胞（有时还有其他血细胞）的生长受到很差的控制，从而导致PDGFRA相关的慢性嗜酸性粒细胞白血病。目前还不清楚为什么嗜酸性粒细胞优先受到这种遗传变化的影响。"}, {"name": "胃肠道间质瘤", "detail": "PDGFRAgene突变与胃肠道间质瘤（GIST）相关。GIST是一种发生在胃肠道的肿瘤，最常见于胃或小肠。与PDGFRAgene突变相关的大多数GIST发生在胃中。在大多数情况下，gene变化是在一个人的一生中获得的，被称为体细胞突变。导致零星GIST的体细胞突变仅存在于肿瘤细胞中，不会遗传。不太常见的是，增加发生GIST风险的PDGFRAgene突变是从父母那里遗传的，这可能导致家族性GIST。（换行）与GIST相关的PDGFRAgene突变产生不再需要结合待激活的血小板衍生生长因子蛋白的蛋白质。结果，PDGFRA蛋白和信号传导途径被组成性激活，这增加了细胞增殖和存活，导致肿瘤形成。"}, {"name": "其他疾病", "detail": "导致组成型活性PDGFRA蛋白的PDGFRAgene突变也与炎性纤维瘤息肉相关，炎性纤维瘤息肉是在胃肠道中形成的小的非癌性（良性）肿瘤。这些肿瘤由纤维组织组成，通常含有已知引起炎症的细胞（炎性细胞）。如在GIST中一样，组成型活性PDGFRA蛋白导致细胞过度生长和肿瘤形成。"}]}' WHERE id = 20826;
UPDATE genes SET GHR_metadata_ch = '{"gene": "FAM126A", "conditions": [{"name": "髓鞘减少和先天性白内障", "detail": "已发现FAM126Agene中至少有四个突变会引起髓鞘过少和先天性白内障。大多数突变会删除大部分gene或在制备hyccin的说明中产生过早的终止信号。这些突变阻止了任何功能蛋白的产生。一个FAM126Agene突变允许产生一些蛋白质。该突变用hyccin蛋白（写为Leu53Pro或L53P）中第53位的氨基酸脯氨酸取代了蛋白质结构单元（氨基酸）亮氨酸。具有Leu53Pro突变的人的症状往往比那些阻止任何蛋白质产生的突变的人轻。(换行)hyccin产生的任何中断都会损害其在髓鞘形成中的作用，导致神经系统问题，如智力残疾和行走困难。目前尚不清楚缺乏hyccin是如何导致双眼晶状体混浊（白内障）的，这种混浊通常在受影响的个体出生时就存在。神经系统问题和白内障是髓鞘过少和先天性白内障患者的特征。"}]}' WHERE id = 20846;
UPDATE genes SET GHR_metadata_ch = '{"gene": "PTCH1", "conditions": [{"name": "戈林综合症", "detail": "已发现PTCH1gene中超过225个突变会引起Gorlin综合征（也称为痣基底细胞癌综合征），这种疾病会影响身体的许多部位，并增加患各种癌性和非癌性肿瘤的风险。该gene中的突变阻止patched-1的产生或导致受体的异常形式的产生。改变或缺失的patched-1受体不能有效抑制细胞生长和分裂。结果，细胞不受控制地增殖以形成Gorlin综合征特征性的肿瘤。目前尚不清楚PTCH1gene突变是如何引起与这种情况相关的其他症状和体征的，包括手掌和脚底皮肤的小凹陷（凹陷）、异常大的头部大小（巨头症）和骨骼异常。"}, {"name": "非综合征性全前脑", "detail": "遗传学家庭参考资料提供有关非综合征性全前脑的信息。"}, {"name": "9q22.3微缺失", "detail": "PTCH1gene位于9号染色体的一个区域，该区域在具有9q22.3微缺失的人中被删除。这种缺失的结果是，受影响的个体在每个细胞中缺失了一个拷贝的PTCH1gene。研究人员认为，与9q22.3微缺失相关的许多特征，特别是Gorlin综合征的体征和症状（如上所述）是由PTCH1gene缺失引起的。当该gene缺失时，patched-1不能抑制细胞增殖。结果，细胞不受控制地分裂以形成Gorlin综合征特征的肿瘤。与9q22.3微缺失有关的其他体征和症状（例如发育迟缓，智力残疾，身体过度生长和其他身体异常）可能是由于9号染色体缺失区域中其他gene的丢失所致。"}, {"name": "癌症", "detail": "一些突变是在一个人的一生中获得的，并且仅存在于某些细胞中。这些称为体细胞突变的遗传变化不是遗传的。PTCH1gene的两个拷贝中的体细胞突变都与称为基底细胞癌的非遗传性（散发性）皮肤癌有关。其他零星类型的癌症可能与PTCH1gene的体细胞突变有关，包括某些形式的皮肤癌，称为髓母细胞瘤的儿童脑瘤，乳腺癌和结肠癌。称为角化囊性牙源性肿瘤的非癌性（良性）颌骨肿瘤也可能与体细胞PTCH1gene突变有关。"}, {"name": "其他疾病", "detail": "已发现PTCH1gene中至少有七个突变会引起非综合征性全前脑畸形。当大脑在早期发育过程中未能分成两半时，就会出现这种情况。PTCH1gene突变是非综合征性全前脑的罕见原因。这些突变阻止了正常脑细胞模式所必需的信号传导。非综合征性全前脑的体征和症状是由大脑和面部的异常发育引起的。"}]}' WHERE id = 20874;
UPDATE genes SET GHR_metadata_ch = '{"gene": "PRPS1", "conditions": [{"name": "艺术综合症", "detail": "在患有艺术综合症的人中至少鉴定出两个PRPS1gene突变，这种疾病在男性中引起严重的神经系统问题。女性也可能受到这种情况的影响，但她们通常症状要轻得多。（换行）导致Arts综合征的PRPS1gene突变改变了PRPP合成酶1酶中的单蛋白结构单元（氨基酸）。据信该突变导致产生具有很少或没有活性的不稳定酶。功能性PRPP合成酶1酶的缺乏破坏了嘌呤的制造和再循环。嘧啶的生产也受到影响，但不受嘧啶挽救途径的影响。嘌呤产生的中断以及嘧啶产生的程度较小，可能会损害细胞中的能量存储和运输。这些过程的损害可能对需要大量能量的组织（例如神经系统）产生特别严重的影响，从而导致艺术综合症的神经系统问题。"}, {"name": "进行性神经性腓骨肌萎缩症", "detail": "PRPS1gene中至少有两个突变会导致一种称为X5型的Charcot-Marie-Tooth病，也称为Rosenberg-Chutorian综合征。这些突变改变了PRPP合成酶1酶中的单个氨基酸。这些遗传变化降低了酶的活性，从而破坏了嘌呤和嘧啶的产生。由此导致的神经系统细胞中能量存储和运输的损害可能导致与该疾病相关的感觉丧失和四肢无力（周围神经病变），耳聋和视力丧失。(换行)一些研究人员认为，这种情况实际上并不是Charcot-Marie-Tooth病的一种形式。相反，他们将其归类为以周围神经病变，耳聋和视力丧失为特征的单独疾病。"}, {"name": "磷酸核糖焦磷酸合成酶超活性", "detail": "已经鉴定出PRPS1gene中大约七个突变，这些突变导致严重形式的磷酸核糖焦磷酸合成酶超活性（PRS超活性）。这些突变改变了PRPP合成酶1酶中的单个氨基酸，导致调节不良，过度活跃的酶。在较温和的PRS超活性形式中，PRPS1gene过度活跃的原因尚不清楚。PRPS1gene过度活跃会增加正常PRPP合成酶1酶的产生，从而增加PRPP的可用性。在两种形式的疾病中，都会产生过量的嘌呤。(换行)在这些conditions下，尿酸（嘌呤分解的废物）积聚在体内。尿酸积聚可引起痛风，痛风是由关节中的尿酸结晶引起的关节炎的一种形式。受影响的个体也可能发展由尿酸晶体形成的肾或膀胱结石。(换行)具有严重形式的PRS过度活跃的人具有其他症状，包括由内耳变化引起的听力丧失（感觉神经性听力丧失），肌张力弱（肌张力低下），肌肉协调受损（共济失调）和发育迟缓。尚不清楚引起严重形式的PRS过度活跃的PRPS1gene突变如何与这些神经系统问题相关。"}, {"name": "非综合征性听力损失", "detail": "遗传学家庭参考资料提供有关非综合征性听力损失的信息。"}]}' WHERE id = 20879;
UPDATE genes SET GHR_metadata_ch = '{"gene": "PSEN1", "conditions": [{"name": "阿尔茨海默病", "detail": "在患有早发性阿尔茨海默氏病（一种从65岁开始的退化性脑部疾病）的患者中，已鉴定出150多个PSEN1gene突变。PSEN1gene突变是早发性阿尔茨海默氏病的最常见原因，占病例的70%。(换行)几乎所有PSEN1gene突变都会改变PSEN1gene特定片段中DNA（核苷酸）的单个构建基块。这些突变导致产生异常的早老素1蛋白。有缺陷的早老素1干扰了-分泌酶复合物，它会改变APP的加工过程，并导致更长，有毒版本的淀粉样蛋白过量生产？肽。该蛋白质片段的拷贝粘在一起并积聚在大脑中，形成称为淀粉样斑块的团块，这是阿尔茨海默病的特征。有毒淀粉样蛋白的积累？肽和淀粉样斑块的形成可能导致神经元死亡以及该疾病的进行性体征和症状。"}, {"name": "化脓性汗腺炎", "detail": "已发现PSEN1gene中至少有一个突变会引起化脓性汗腺炎，这是一种慢性皮肤病，其特征是在毛囊中皮肤下反复出现沸腾状肿块（结节）。结节往往会发炎和疼痛，并且在愈合时会产生明显的疤痕。（换行）鉴定的突变从PSEN1gene中删除单个DNA构建块（核苷酸），写为725delC。这种遗传变化减少了细胞中产生的功能性早老素1的数量，因此可用的这种蛋白质的一部分较少-分泌酶复合物。由此导致的正常短缺-分泌酶损害细胞信号传导途径，包括Notch信号传导。尽管对该机制知之甚少，但研究表明，Notch信号异常可能会促进毛囊中复发性结节的发展并引发皮肤炎症。（换行）研究表明，引起化脓性汗腺炎的PSEN1gene突变对-分泌酶的功能比引起早发性阿尔茨海默氏病的突变高。这些差异可以解释为什么没有报道单个PSEN1gene突变引起两种疾病的体征和症状。"}, {"name": "家族性扩张型心肌病", "detail": "遗传学家庭参考提供有关家族性扩张型心肌病的信息。"}]}' WHERE id = 20883;
UPDATE genes SET GHR_metadata_ch = '{"gene": "IL2RG", "conditions": [{"name": "X连锁严重联合免疫缺陷", "detail": "在X连锁严重联合免疫缺陷症（SCID）患者中已鉴定出IL2RGgene的300多个突变。这些突变大多数涉及gene中一个或几个DNA结构单元（核苷酸）的变化。这些变化导致产生通用γ链的非功能性版本或阻止产生任何蛋白质。没有共同的γ链，重要的化学信号不会传递到细胞核，淋巴细胞也无法正常发育。缺乏功能性成熟淋巴细胞可防止免疫系统抵抗感染。"}]}' WHERE id = 20885;
UPDATE genes SET GHR_metadata_ch = '{"gene": "KHDC3L", "conditions": [{"name": "复发性葡萄胎", "detail": "已发现KHDC3Lgene中至少有四个突变会引起复发性葡萄胎。当胚胎发育不全，胎盘发育异常时，妊娠早期就会发生葡萄胎。胎盘是子宫中的一种固体结构，通常为生长中的胎儿提供营养，功能失调，表现为许多小囊，通常被描述为类似一堆葡萄。葡萄胎有癌变的危险。反复发生葡萄胎的妇女有一种称为复发性葡萄胎的病症。KHDC3Lgene突变导致5%患有此病的女性复发性葡萄胎。（换行）KHDC3Lgene突变导致功能受损的蛋白质的产生。结果，蛋白质在印迹过程中的作用减弱，导致许多不应该表达的母体gene的激活。胚胎发育过程中多个gene的过度表达导致葡萄胎特征性的胎儿和胎盘组织发育不良。因为KHDC3Lgene突变存在于包括卵母细胞在内的所有女性细胞中，所以在每次妊娠中都会发生与这些卵细胞一起发生的葡萄胎。"}]}' WHERE id = 20891;
UPDATE genes SET GHR_metadata_ch = '{"gene": "JPH3", "conditions": [{"name": "亨廷顿病样综合征", "detail": "已发现JPH3gene中的特定类型的突变会引起类似于亨廷顿病的体征和症状，包括不受控制的运动，情绪问题和思维能力丧失。研究人员将这种情况命名为亨廷顿病样2（HDL2）。（换行）与HDL2相关的突变增加了JPH3gene中CAG/CTG三核苷酸重复序列的大小。患有这种情况的人有44至59次CAG/CTG重复。具有29至约43个CAG/CTG重复的人可能会或可能不会出现HDL2的体征和症状。(换行)研究人员正在努力确定异常大的CAG/CTG段的影响。他们认为，突变的JPH3gene产生了信使RNA的改变版本，这是通常用于蛋白质生产的gene的分子蓝图。异常信使RNA在神经元内部形成团块，干扰这些细胞的正常功能。大脑某些区域神经元的功能障碍和最终死亡是HDL2体征和症状的基础。"}]}' WHERE id = 20892;
UPDATE genes SET GHR_metadata_ch = '{"gene": "GLB1", "conditions": [{"name": "GM1神经节病", "detail": "已发现GLB1gene中的80多个突变引起GM1神经节病。大多数突变会改变GLB1gene中的单个DNA构建块（核苷酸）。这些突变通常会影响两者的产生-半乳糖苷酶和弹性蛋白结合蛋白。（换行）导致GM1神经节病的GLB1gene突变减少或消除了-半乳糖苷酶。没有足够的功能-半乳糖苷酶，GM1神经节苷脂和硫酸角质素不能被分解。结果，这些物质在许多组织和器官中积累到毒性水平。在大脑中，由GM1神经节苷脂堆积引起的进行性损伤导致神经细胞破坏，从而导致GM1神经节苷脂病的许多体征和症状。(换行)尽管尚不清楚弹性蛋白结合蛋白在GM1神经节病发展中的作用，但该蛋白的改变可能导致某些GM1神经节病患者的心肌变弱（心肌病）。"}, {"name": "黏多醣贮积症IV型", "detail": "已发现GLB1gene中的10多个突变会导致IV型粘多糖贮积病（MPS IV）。这些突变大多数会改变gene中的单个核苷酸。所有导致MPS IV的突变都会破坏硫酸角质素的分解-半乳糖苷酶。GM1神经节苷脂的降解不受这些突变的影响。(换行)缺乏-半乳糖苷酶活性导致溶酶体内硫酸角质素的积累。由于硫酸角质素主要存在于软骨和角膜中，因此该物质的积聚会导致骨骼异常和角膜混浊。研究人员认为，GAG的积累还可能通过干扰溶酶体内其他蛋白质的功能并破坏细胞内分子的运动而导致MPS IV的特征。"}]}' WHERE id = 20900;
UPDATE genes SET GHR_metadata_ch = '{"gene": "DPY19L2", "conditions": [{"name": "全球精子症", "detail": "在患有全球精子症的男性中发现了至少17个DPY19L2gene突变，这种病症的特征是精子细胞异常，头部圆形，没有顶体。患有这种疾病的男性中约有70%的DPY19L2突变。这些突变中的大多数会删除gene的大区域或整个gene。其他改变DPY19L2蛋白中的单个蛋白质构建块（氨基酸）。这些突变导致功能性DPY19L2蛋白的丧失。没有这种蛋白质，形成的顶体不附着在细胞核上并从细胞中除去。结果，精子细胞没有顶体，精子的头部不伸长。异常的精子无法穿过卵细胞的外膜使其受精，从而导致受影响的男性无法生育孩子（不育）。研究人员已经描述了使卵细胞受精困难的异常精子细胞的其他特征，尽管尚不清楚DPY19L2gene的变化如何参与这些特征的发展。"}]}' WHERE id = 20908;
UPDATE genes SET GHR_metadata_ch = '{"gene": "DPYD", "conditions": [{"name": "二氢嘧啶脱氢酶缺乏症", "detail": "在患有二氢嘧啶脱氢酶缺乏症的人中，已经鉴定出DPYDgene的50多个突变。DPYDgene突变会干扰尿嘧啶和胸腺嘧啶的分解，并导致血液，尿液以及围绕大脑和脊髓的液体（脑脊液）中的这些分子过量。目前尚不清楚过量的尿嘧啶和胸腺嘧啶如何与影响某些二氢嘧啶脱氢酶缺乏症患者的特定神经系统问题相关。DPYDgene中的（换行）突变也会干扰具有类似于嘧啶结构的药物的分解，例如抗癌药5-氟尿嘧啶和卡培他滨。结果，这些药物在体内积聚并引起二氢嘧啶脱氢酶缺乏症患者可能发生的严重反应。"}]}' WHERE id = 20916;
UPDATE genes SET GHR_metadata_ch = '{"gene": "KIT", "conditions": [{"name": "Piebaldism", "detail": "在斑驳病患者中至少鉴定出69个KITgene突变。这种情况的特征是缺乏黑素细胞导致皮肤和头发出现白斑。导致斑驳的突变导致无功能的KIT蛋白。KIT信号的丧失被认为会破坏发育过程中黑素细胞的迁移和增殖，从而导致皮肤缺乏色素沉着。"}, {"name": "核心结合因子急性髓性白血病", "detail": "遗传学家庭参考提供有关核心结合因子急性髓性白血病的信息。"}, {"name": "胃肠道间质瘤", "detail": "KITgene突变是与胃肠道间质瘤（GIST）相关的最常见遗传变化。GIST是一种发生在胃肠道的肿瘤，最常见于胃或小肠。在大多数情况下，这些KITgene突变是在一个人的一生中获得的，称为体细胞突变。导致零星GIST的体细胞突变仅存在于肿瘤细胞中，不会遗传。不太常见的是，增加发生GIST风险的KITgene突变是从父母那里遗传的，这可能导致家族性GIST。（换行）与GIST相关的KITgene突变产生不再需要结合待激活的干细胞因子蛋白的蛋白质。结果，KIT蛋白和信号传导途径不断开启（组成性激活），这增加了ICC的增殖和存活，导致GIST形成。"}, {"name": "其他癌症", "detail": "已经在几种癌症中鉴定出KITgene的体细胞突变。KITgene突变涉及一些急性髓性白血病，这是一种称为骨髓细胞的血细胞癌症，以及鼻窦自然杀伤/T细胞淋巴瘤（NKTCL），这是另一种发生在鼻腔的血细胞癌通道。此外，一些患有精原细胞瘤（一种睾丸癌）的人具有KITgene突变。涉及急性髓性白血病和精原细胞瘤的遗传变化导致组成性激活的KIT蛋白。恒定的信号传导导致组成这些肿瘤的细胞过度增殖。目前尚不清楚NKTCL中的KIT突变如何参与该病症。"}, {"name": "其他疾病", "detail": "KITgene突变也参与肥大细胞增多症，这代表了一组相关疾病。这些病症的特征在于肥大细胞的过度生长，肥大细胞是在过敏反应或感染期间引发炎症的细胞。皮肤中过量肥大细胞的积累导致称为荨麻疹的病症，并且在其他器官中的积累导致全身性肥大细胞增多症。这组疾病中涉及的KITgene突变会导致组成性激活的KIT蛋白，从而导致肥大细胞过度生长。"}]}' WHERE id = 20920;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ABCA12", "conditions": [{"name": "丑角鱼鳞病", "detail": "ABCA12gene的突变与一种称为丑角鱼鳞病的皮肤疾病有关，这种疾病是由蛋白质缺陷引起的，缺乏脂质运输导致表皮形成受损，导致皮肤硬而厚的鳞片、过度脱水和增加感染的风险。"}, {"name": "非大疱性先天性鱼鳞状红皮病", "detail": "发现ABCA12gene中的几个突变与称为非大疱性先天性鱼鳞状红皮病（NBCIE）的皮肤疾病有关，该疾病是由于蛋白质功能降低引起的，该疾病损害了脂质运输，导致表皮形成受损。许多患有这种疾病的婴儿出生时都有一个紧密透明的护套，覆盖着他们的皮肤，称为火棉胶膜。NBCIE的特征还在于发红，出现细小的白鳞屑，脱水和感染风险增加。"}, {"name": "板状鱼鳞病", "detail": "发现约5-7%的ABCA12gene突变与称为层状鱼鳞病的疾病有关。这种疾病的特征是紧密的胶棉膜，持续时间短，皮肤鳞片状，并在婴儿中引起感染，脱水和呼吸系统疾病。受影响个体的其他表型包括脱发，手指和脚趾甲形成异常，出汗能力下降，对热量的敏感性增加，手掌和脚部皮肤增厚，皮肤发红和关节畸形。"}]}' WHERE id = 20929;
UPDATE genes SET GHR_metadata_ch = '{"gene": "RAD21", "conditions": [{"name": "Cornelia de Lange综合征", "detail": "在患有Cornelia de Lange综合征（一种影响身体许多部位的发育障碍）的人中，已经鉴定出RAD21gene中至少有8个突变。该gene的突变似乎是这种情况的罕见原因。(换行)Cornelia de Lange综合征的一些病例是由缺失引起的，该缺失去除了包括RAD21gene在内的8号染色体上的DNA片段。在这些情况下，每个细胞中一个拷贝的染色体缺少整个gene，因此细胞产生的RAD21蛋白量减少。在其他情况下，这种情况是由RAD21gene内的突变引起的，这些突变损害或消除了RAD21蛋白的功能。RAD21蛋白缺陷或缺失可能会改变粘着蛋白复合物的活性，从而削弱其调节对正常发育至关重要的gene的能力。尽管研究人员尚不完全了解这些变化如何引起Cornelia de Lange综合征，但他们怀疑gene调控的改变可能是该病特征性的许多发育问题的基础。(换行)研究表明，RAD21gene的突变会导致某种形式的Cornelia de Lange综合征，其特征相对较轻。与最常见的疾病原因NIPBLgene突变相比，RAD21gene突变引起的发育和生长延迟较小，引起重大先天缺陷的可能性较小。"}, {"name": "Trichorhinophalangeal综合征II型", "detail": "RAD21gene位于8号染色体上的一个区域，该区域在II型毛发鼻指综合征（TRPS II）患者中被删除。TRPS II是导致骨骼和关节畸形的疾病；独特的面部特征；智力障碍；以及皮肤，头发，牙齿，汗腺和指甲的异常。这种缺失的结果是，受影响的个体在每个细胞中都缺失了一个RAD21gene拷贝，因此细胞产生的RAD21蛋白量减少了。RAD21蛋白的缺乏可能导致智力残疾，但其机制尚不清楚。RAD21gene附近其他gene的缺失可能有助于这种情况的其他特征。"}]}' WHERE id = 20934;
UPDATE genes SET GHR_metadata_ch = '{"gene": "HSD17B3", "conditions": [{"name": "17β-羟基类固醇脱氢酶3缺乏症", "detail": "在HSD17B3gene中已鉴定出20多种导致17-β-羟基类固醇脱氢酶3缺乏的突变。在这种情况最常见的加沙阿拉伯人口中，几乎所有受影响的人都有两个相同突变的副本。该突变用蛋白质80位的氨基酸谷氨酰胺（写为Arg80Gln或R80Q）取代了蛋白质结构单元（氨基酸）精氨酸。HSD17B3gene中的（换行）突变导致17-β-羟基类固醇脱氢酶3酶几乎没有活性或没有活性，从而降低了睾丸激素的产生。睾酮的缺乏影响男性胎儿生殖道的发育，导致17-β-羟基类固醇脱氢酶3缺乏症中发生的外部性器官异常。"}]}' WHERE id = 20949;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ATL1", "conditions": [{"name": "痉挛性截瘫3A型", "detail": "已发现ATL1gene中约有60个突变会导致3A型痉挛性截瘫。这种情况的特征是从儿童期开始的肌肉僵硬（痉挛）和下肢无力（截瘫）。导致痉挛性截瘫3A型的大多数突变都会改变atlastin-1蛋白中的一个蛋白结构单元（氨基酸）。这些突变可能导致atlastin-1的异常活性，从而损害神经元的功能，包括这些细胞内物质的分布。缺乏功能性atlastin-1蛋白也可能限制轴突的生长。在皮质脊髓束的长神经元内，这些问题可导致细胞死亡。结果，神经元不能传递神经冲动，特别是传递到下肢的其他神经元和肌肉。这种受损的神经功能导致痉挛性截瘫3A型的体征和症状。"}, {"name": "其他疾病", "detail": "已经发现ATL1gene的突变引起称为遗传性感觉神经病ID型的病症。该病症的特征在于腿和脚的神经异常（周围神经病）。许多患有这种疾病的人会出现刺痛或刺痛感（感觉异常），缺乏反射，虚弱和感觉疼痛的能力下降。受影响的人经常脚上出现开放性溃疡（溃疡），并且由于他们无法感觉到这些溃疡的疼痛，他们可能不会寻求立即治疗。如果不进行治疗，溃疡可能会被感染，可能需要截肢周围区域。（换行）已发现至少四种ATL1gene突变引起遗传性感觉神经病ID型。这些突变损害神经细胞功能并减少神经冲动的传递，类似于引起痉挛性截瘫3A型的ATL1gene突变的作用（如上所述）。目前尚不清楚为什么某些ATL1gene突变会导致ID型遗传性感觉神经病，而另一些会导致3A型痉挛性截瘫。"}]}' WHERE id = 20965;
UPDATE genes SET GHR_metadata_ch = '{"gene": "IL31RA", "conditions": [{"name": "原发性局部皮肤淀粉样变性", "detail": "已发现IL31RAgene中至少有一个突变会引起2型原发性局部皮肤淀粉样变性（PLCA），这是一种发痒的皮肤病，其中在皮肤中积聚了称为淀粉样蛋白的异常蛋白质团。该突变改变IL-31RA中的单个蛋白质构建块（氨基酸），用蛋白质位置521处的氨基酸苯丙氨酸取代氨基酸丝氨酸（写为Ser521Phe或S521F）。这种变化发生在被认为与IL-31受体的其他亚基相互作用的蛋白质区域，并可能损害受体的形成。（换行）导致PLCA的IL31RAgene突变减少由IL-31触发的化学信号。虽然IL-31信号传导在瘙痒中起作用，但尚不清楚这种信号传导的减少如何与瘙痒皮肤或PLCA中淀粉样蛋白的积累有关。"}]}' WHERE id = 20971;
UPDATE genes SET GHR_metadata_ch = '{"gene": "PEX1", "conditions": [{"name": "Zellweger谱系障碍", "detail": "在Zellweger谱系障碍患者中已鉴定出PEX1gene至少114个突变，这是一组具有重叠体征和症状并影响身体许多部位的疾病。病情的严重程度各不相同，可能包括肌张力弱（肌张力低下），发育迟缓以及视力和听力问题。PEX1gene突变是Zellweger谱系障碍的最常见原因，在近70%的受影响个体中发现。(换行)在Zellweger谱系障碍患者中发现了两个常见的PEX1gene突变。一个突变用Pex1p中843位的氨基酸天冬氨酸（写为Gly843Asp或G843D）替换了蛋白质结构单元（氨基酸）甘氨酸。这种突变导致蛋白质水平降低。具有G843D突变的个体倾向于具有在病情谱较轻端的体征和症状。另一个常见的突变称为1700fs突变，导致异常短而无功能的Pex1p的产生。具有1700fs突变的人通常具有处于病情严重范围的体征和症状。导致Zellweger光谱紊乱的PEX1gene突变降低或消除了Pex1p蛋白的活性。如果没有足够的功能性Pex1p，则无法将酶正确导入过氧化物酶体。结果，细胞包含无法执行其常规功能的空过氧化物酶体。conditions谱的严重末端是由于细胞内缺乏功能性过氧化物酶体引起的。conditions谱的不太严重的末端是由允许形成一些过氧化物酶体的突变引起的。"}]}' WHERE id = 20986;
UPDATE genes SET GHR_metadata_ch = '{"gene": "MED12", "conditions": [{"name": "FG综合征", "detail": "已发现MED12gene中至少有两个突变会引起FG综合征，其特征是智力残疾，行为问题和身体异常，包括肌张力弱（肌张力低下）和肛门开口阻塞（肛门无孔）。（换行）导致FG综合征的突变各自改变MED12蛋白质中的单个蛋白质构建块（氨基酸）。一个突变用蛋白质961位的氨基酸色氨酸取代了氨基酸精氨酸（写为Arg961Trp或R961W）。另一个在蛋白质位置958（写为Gly958Glu或G958E）上用氨基酸谷氨酸替换了氨基酸甘氨酸。这些突变改变了MED12蛋白的结构，这可能破坏了其在发育过程中调节gene活性的能力。但是，尚不清楚遗传变化如何导致智力残疾和FG综合征的其他特征。"}, {"name": "Lujan综合征", "detail": "MED12gene中至少有一个突变会导致Lujan综合征，这是一种以智力残疾，行为问题和身体特征为特征的疾病，包括高大的身材和长而窄的脸。该突变不同于与FG综合征相关的遗传变化（如上所述）；Lujan综合征突变将MED12蛋白（写为Asn1007Ser或N1007S）的1007位氨基酸丝氨酸替换为氨基酸天冬酰胺。该突变改变了MED12蛋白的结构，并可能破坏了其调节gene活性的能力，但尚不清楚这些变化如何影响发育并导致Lujan综合征的特定特征。"}, {"name": "Ohdo综合征，Maat-Kievit-Brunner型", "detail": "已发现至少三个MED12gene突变会导致Maat-Kievit-Brunner型Ohdo综合征，这是一种罕见的疾病，其特征是智力残疾和独特的面部特征。导致这种疾病的突变会改变MED12gene中的单个氨基酸，尽管它们与引起FG综合征或Lujan综合征的遗传变化不同（如上所述）。导致Maat-Kievit-Brunner型Ohdo综合征的突变改变了MED12gene的结构，削弱了其控制gene活性的能力。目前尚不清楚这些变化如何导致该疾病的特定认知和身体特征。"}, {"name": "前列腺癌", "detail": "遗传学家庭参考提供有关前列腺癌的信息。"}, {"name": "肿瘤", "detail": "一些gene突变是在一个人的一生中获得的，并且仅存在于某些细胞中。这些被称为体细胞突变的变化不是遗传的。已经在几种类型的非癌性和癌性肿瘤中发现了MED12gene的体细胞突变。（换行）体细胞MED12gene突变存在于大多数子宫平滑肌瘤中，其是非癌性生长，也称为子宫肌瘤。子宫平滑肌瘤在成年女性中很常见。这些生长会导致盆腔疼痛和异常出血，并且在某些情况下会导致无法生育孩子（不孕症）。在一些癌性子宫肿瘤中也发现了体细胞MED12gene突变，包括平滑肌肉瘤和平滑肌肿瘤（STUMP），以及一些前列腺癌和结肠直肠癌。（换行）研究表明，体细胞MED12gene突变改变MED12蛋白的功能，这可能破坏正常细胞信号传导并损害细胞生长和其他细胞功能的调节。结果，某些细胞可能以不受控制的方式分裂，导致肿瘤的生长。"}, {"name": "其他疾病", "detail": "MED12gene中的一种特定变异（多态性），称为HOPA（12bp）多态性，与北欧血统的精神分裂症风险适度增加有关。这种变异是在MED12gene中插入了12个额外的DNA构建块（碱基对），这导致MED12蛋白中额外的四个氨基酸。由于该蛋白参与几种不同的信号传导途径，因此很难确定变异对脑功能的影响。HOPA（12bp）多态性是正在研究的许多因素之一，有助于解释精神分裂症的原因。大量的遗传和生活方式因素，其中大部分仍然未知，可能决定了患此病的风险。"}]}' WHERE id = 21001;
UPDATE genes SET GHR_metadata_ch = '{"gene": "CA5A", "conditions": [{"name": "碳酸酐酶VA缺乏症", "detail": "在碳酸酐酶VA缺乏症患者中至少鉴定出三个CA5Agene突变。这种遗传性疾病的特征是可能危及生命，包括进食不良，呕吐，体重减轻，疲倦（嗜睡），呼吸急促（呼吸急促），癫痫发作或昏迷。这些发作的风险被认为在儿童时期后下降。CA5Agene中的（换行）突变导致碳酸酐酶VA酶功能缺失或受损，从而导致碳酸氢盐产量降低。碳酸氢盐不足会导致对酸碱平衡的控制受损，并降低四种受影响的线粒体酶的活性，从而导致与碳酸酐酶VA缺乏症相关的各种生化异常，并导致这种疾病的发作。研究表明，随着患病个体的成熟，由CA5Bgene产生的相关酶可能会越来越多地弥补碳酸酐酶VA的缺乏，这可能导致儿童期后疾病发作的风险降低。"}]}' WHERE id = 21014;
UPDATE genes SET GHR_metadata_ch = '{"gene": "CRTAP", "conditions": [{"name": "成骨不全症", "detail": "CRTAPgene中至少有五个突变导致罕见的成骨不全症，通常被分类为VII型。这些突变中的几种阻止细胞产生任何软骨相关蛋白。没有这种蛋白质，骨骼和其他结缔组织将无法正常形成，从而导致非常严重的疾病形式。CRTAPgene中的另一个突变大大减少了产生的软骨相关蛋白的量，从而破坏了胶原蛋白的正常形成。这种遗传变化导致成骨不全症的症状和体征减轻。"}]}' WHERE id = 21063;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ROBO3", "conditions": [{"name": "水平凝视麻痹伴进行性脊柱侧弯", "detail": "在患有进行性脊柱侧弯（HGPPS）的水平注视麻痹患者中，ROBO3gene至少鉴定出19种不同的突变。这些突变以不同的方式改变了ROBO3蛋白的结构；但是，所有突变似乎都导致蛋白质功能异常。缺乏功能性ROBO3蛋白会破坏正常的脑干发育。(换行)在患有HGPPS的人中，运动和感觉神经元的轴突不会在脑干中交叉，而是停留在身体的同一侧。研究人员认为，这种错误布线是与疾病相关的眼球运动异常的根本原因。HGPPS进行性脊柱侧弯的原因尚不清楚。研究人员正在努力确定为什么ROBO3突变的影响似乎仅限于水平眼球运动和脊柱侧弯。"}]}' WHERE id = 21064;
UPDATE genes SET GHR_metadata_ch = '{"gene": "FMR1", "conditions": [{"name": "脆性X综合征", "detail": "几乎所有脆性X综合征病例都是由FMR1gene中CGG三核苷酸重复序列的扩增引起的。在这些情况下，CGG异常重复200到1000次以上，这使得该gene区域不稳定。结果，FMR1gene被关闭（沉默）并且产生很少或没有蛋白质。FMRP的丢失或短缺会破坏神经细胞的正常功能，进而破坏神经系统，从而导致严重的学习问题，智力障碍和脆性X综合征的其他特征。大约三分之一的具有FMR1gene突变和脆性X综合征特征性体征的男性也具有自闭症谱系障碍的特征，这些特征会影响沟通和社交互动。(换行)所有脆性X综合征病例中不到1%是由FMR1gene的其他变化引起的。突变可能会删除部分或全部gene，或更改用于制作FMRP的构建基块（氨基酸）之一。这些遗传变化改变了蛋白质的三维形状或阻止了任何蛋白质的产生。异常或缺失的蛋白质破坏神经系统功能，导致脆性X综合征的体征和症状。"}, {"name": "脆性X相关原发性卵巢功能不全", "detail": "FMR1gene中的三核苷酸重复扩增增加了女性患脆性X相关原发性卵巢功能不全（FXPOI）的风险。在这种情况下，FMR1gene中的CGG三核苷酸重复序列重复约55至200次，这被称为预突变。发展FXPOI的女性可能会经历不规则的月经周期，无法生育（不育），更年期提前以及被称为促卵泡激素（FSH）的激素水平升高。大约20%的具有突变的女性患有最严重的疾病形式，称为明显的POI，这会导致月经周期异常和40岁之前FSH水平升高，并经常导致不育。其他具有突变的女性具有隐匿性POI，这是一种较温和的疾病，可导致生育力降低，尽管女性的月经周期正常。（换行）由于未知原因，预突变导致包含重复扩增的异常FMR1 mRNA的过量产生。研究人员认为，异常的mRNA会导致FXPOI的体征和症状。据认为，mRNA与其他蛋白质连接（结合）并阻止它们执行其功能。此外，重复序列使蓝图中的蛋白质生产更加困难，因此，一些具有FMR1gene预突变的人的FMRP含量低于正常水平。结果，它们可能具有在脆性X综合征（如上所述）中看到的轻度身体特征，例如突出的耳朵，并且可能经历诸如焦虑或抑郁的情绪问题。"}, {"name": "脆性X相关震颤/共济失调综合征", "detail": "具有FMR1gene预突变的男性和某些女性患上被称为脆性X相关震颤/共济失调综合征（FXTAS）的疾病的风险增加。FXTAS的特征是运动（共济失调），震颤，记忆力减退，下肢感觉减退（周围神经病变）以及精神和行为改变等进行性问题。这种疾病通常发展到生命的晚期，并随着时间的推移而恶化。（换行）与FXPOI（如上所述）一样，预突变会导致包含扩展的重复区域的异常FMR1 mRNA的过量生产，研究人员认为这种异常mRNA会导致FXTAS。在FXTAS患者的大脑和神经细胞中发现的蛋白质和mRNA簇（核内包裹体）中发现了异常的mRNA。一些研究人员推测，内含物中发现的蛋白质无法发挥其正常功能，这可能导致FXTAS的体征和症状。另一个假设是，内含物可能导致对运动和心理功能重要的神经细胞死亡。但是，尚不清楚内含物在疾病发展中的确切作用。(换行)使用FXTAS的人的FMRP数量可能会减少。尽管认为FXTAS不涉及该蛋白的减少，但它可能导致脆弱X综合征（如上所述）中出现的特征出现轻度变化，例如耳朵突出，焦虑和情绪波动。"}, {"name": "其他疾病", "detail": "研究人员已经表明，一些在FMR1gene中具有预突变（55至200个CGG重复）的儿童具有学习障碍，注意力缺陷多动障碍（ADHD），智力障碍或影响交流和社交互动的发育障碍（例如自闭症谱系障碍）。"}]}' WHERE id = 21083;
UPDATE genes SET GHR_metadata_ch = '{"gene": "PHYH", "conditions": [{"name": "Refsum疾病", "detail": "已发现PHYHgene突变引起所有Refsum病病例的90%以上。已经鉴定出该gene中约30个突变。这些突变改变了植烷酰辅酶A羟化酶的结构或产生，从而降低了酶的活性。这种酶的缺乏破坏了过氧化物酶体中植烷酸的分解。结果，植烷酸和相关化合物积聚在身体组织中。植烷酸的积累对细胞有毒，尽管尚不清楚过量的这种物质如何影响视觉和嗅觉并引起Refsum病的其他特定特征。"}]}' WHERE id = 21131;
UPDATE genes SET GHR_metadata_ch = '{"gene": "AGL", "conditions": [{"name": "糖原贮积病III型", "detail": "已经发现AGLgene中的几个突变引起糖原贮积病III型（也称为GSDIII或Cori病），这是由于无功能酶引起的疾病，其不能分解糖原导致其积累。这导致肝脏和肌肉组织损伤，并且该疾病的特征在于肝脏疾病/损伤或肝脏增大（肝肿大），低血糖，高脂血症，肌病，肌张力低下和心肌病。"}]}' WHERE id = 21132;
UPDATE genes SET GHR_metadata_ch = '{"gene": "FBXL4", "conditions": [{"name": "FBXL4相关的脑肌病线粒体DNA耗竭综合征", "detail": "已发现FBXL4gene中的47个以上突变会导致FBXL4相关的脑脊髓病性mtDNA耗竭综合征。这种情况会影响多个身体系统，通常在儿童早期就致命。它主要与脑功能障碍和肌肉无力（脑病）有关。（换行）导致FBXL4相关的脑肌病性mtDNA耗竭综合征的许多突变会损害FBXL4蛋白与其他蛋白附着（结合）的能力，破坏蛋白复合物的形成，从而损害mtDNA的正常维持。mtDNA维护方面的问题可以减少mtDNA的数量（称为mtDNA消耗）。mtDNA的消耗会损害人体许多细胞和组织中的线粒体功能。线粒体功能降低最终导致细胞功能障碍，最明显的是影响大脑，肌肉和其他具有高能量需求的组织。这种细胞功能障碍导致脑病和FBXL4相关的脑肌病性mtDNA耗竭综合征的其他特征。"}, {"name": "利综合征", "detail": "遗传学家庭参考提供有关Leigh综合征的信息。"}]}' WHERE id = 21136;
UPDATE genes SET GHR_metadata_ch = '{"gene": "DDC", "conditions": [{"name": "芳香族l-氨基酸脱羧酶缺乏症", "detail": "DDCgene中的突变导致AADC酶的活性降低。没有足够的这种酶，神经细胞产生较少的多巴胺和5-羟色胺。多巴胺和5-羟色胺对于正常的神经系统功能是必需的，这些神经递质水平的变化会导致AADC缺乏症患者出现发育迟缓，智力残疾，异常运动和自主神经功能障碍。"}, {"name": "其他疾病", "detail": "研究表明，DDCgene的某些变异（多态性）与尼古丁依赖，精神分裂症，双相情感障碍和注意力缺陷多动障碍的风险增加有关；但是，其他研究并未支持这些发现。据信许多遗传和环境因素是造成这些复杂状况的原因。"}]}' WHERE id = 21140;
UPDATE genes SET GHR_metadata_ch = '{"gene": "FBLN5", "conditions": [{"name": "皮肤松弛", "detail": "在角质层松弛的人中，已经鉴定出FBLN5gene中至少有四个突变。该gene的突变可导致两种不同类型的角质层松弛：常染色体显性形式和常染色体隐性形式。在常染色体显性角质层松弛中，每个细胞中改变的FBLN5gene的一个拷贝足以引起该疾病的特征。在常染色体隐性角质松弛中，每个细胞中gene的两个拷贝都必须改变才能导致疾病。（换行）已知导致常染色体显性角质松弛的FBLN5突变导致异常长而无功能的fibulin-5产生。这种异常蛋白质会干扰FBLN5gene另一个未改变拷贝产生的正常fibulin-5。结果，细胞外基质中功能性fibulin-5的量大大减少。这种蛋白质的缺乏会阻止弹性纤维的组装，从而削弱皮肤，动脉，肺和其他器官的结缔组织。结缔组织中的这些缺陷是角质层松弛的主要特征。（换行）FBLN5突变导致常染色体隐性角质松弛，这些突变改变了fibulin-5中的单个蛋白质结构单元（氨基酸）。这些突变改变了蛋白质的结构，将其捕获在细胞内。由于有缺陷的fibulin-5从未进入细胞外基质，因此无法用于组装弹性纤维。正常弹性纤维的缺乏会削弱整个身体的结缔组织，从而导致角质松弛的体征和症状。"}, {"name": "年龄相关性黄斑变性", "detail": "遗传学家庭参考提供有关年龄相关性黄斑变性的信息。"}, {"name": "其他疾病", "detail": "研究人员一直在研究FBLN5突变作为年龄相关性黄斑变性的可能危险因素，黄斑变性是一种眼病，是全球老年人视力丧失的主要原因。在少数与年龄相关的黄斑变性患者中发现了FBLN5gene的突变，但是该gene的变化可能不是这种常见眼疾的主要危险因素。遗传和环境因素的结合可能决定患此病的风险。"}]}' WHERE id = 21159;
UPDATE genes SET GHR_metadata_ch = '{"gene": "MYH11", "conditions": [{"name": "核心结合因子急性髓性白血病", "detail": "涉及MYH11gene的遗传物质的重排涉及一种称为急性髓性白血病（AML）的血癌形式。这些重排中最常见的是染色体16区域的倒位（写为inv（16））。倒置涉及两个地方的染色体断裂；将得到的DNA片段反转并重新插入染色体中。不太常见的是，称为易位的重排发生在染色体16的两个拷贝之间（写为t（16；16））。在这种易位中，来自染色体每个拷贝的DNA片段断裂并互换。两种类型的遗传重排导致在16号染色体上发现的两个geneCBFB和MYH11融合。这些重排与成人AML病例的5%至8%有关。与inv（16）或t（16；16）相关的AML被归类为核心结合因子AML（CBF-AML）。（换行）由正常CBFBgene产生的蛋白质与称为RUNX1的另一种蛋白质相互作用以形成称为核心结合因子（CBF）的复合物。该复合物附着于DNA的特定区域并打开参与血细胞发育的gene。由融合geneCBF产生的蛋白质-MYH11，仍然可以绑定到RUNX1；但是，CBF的功能受损。CBF的存在-MYH11可能会阻止CBF与DNA的结合，从而削弱其控制gene活性的能力。或者，融合蛋白的MYH11部分可以与阻止复合物控制gene活性的其他蛋白相互作用。gene活性的这种变化阻断了血细胞的成熟（分化），并导致产生称为髓母细胞的异常，未成熟的白细胞。尽管inv（16）和t（16；16）对于白血病发展是重要的，但是髓母细胞发育成癌性白血病细胞通常需要一个或多个额外的遗传改变。"}, {"name": "家族性胸主动脉瘤和解剖", "detail": "遗传学家庭参考资料提供有关家族性胸主动脉瘤和解剖的信息。"}, {"name": "肠假性梗阻", "detail": "遗传学家庭参考提供有关肠道假性梗阻的信息。"}, {"name": "巨囊藻-微结肠-肠蠕动综合征", "detail": "遗传学家庭参考资料提供有关巨囊藻-微结肠-肠蠕动综合征的信息。"}]}' WHERE id = 21183;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SLC2A9", "conditions": [{"name": "肾低尿酸血症", "detail": "已经发现SLC2A9gene中至少13个突变引起肾低尿酸血症。这种情况导致血液中尿酸的量减少。肾性低尿酸血症通常不会引起任何健康问题，但在运动后会导致疼痛和恶心，肾结石或尿液中的血液（血尿）。导致肾脏低尿酸血症的大多数突变都取代了GLUT9蛋白中的单个蛋白结构单元（氨基酸），并严重降低或消除了蛋白重新吸收血液中尿酸的能力。结果，尿液中损失了过量的尿酸。虽然尚不清楚尿酸水平的这些变化如何导致肾性低尿酸血症的体征和症状，但尿酸的抗氧化特性的丧失可能与通过肾脏的尿酸增加在尿液中释放有助于这种情况的特点。"}, {"name": "其他疾病", "detail": "一些研究发现SLC2A9gene的变异与称为痛风的病症有关，痛风是由关节中的尿酸结晶引起的关节炎的一种形式。这些变体损害GLUT9蛋白将尿酸释放到尿液中的能力。结果，过多的尿酸被重新吸收到血液中，导致体内尿酸积聚。这种过量的尿酸通常以晶体的形式积聚在身体的关节中，导致疼痛的关节炎。然而，其他研究尚未发现SLC2A9gene变异与痛风之间存在关联。虽然SLC2A9gene在痛风中的作用可能尚不清楚，但已知生活方式，遗传和环境因素的组合在确定这种复杂疾病的风险中起作用。"}]}' WHERE id = 21193;
UPDATE genes SET GHR_metadata_ch = '{"gene": "EXT1", "conditions": [{"name": "遗传性多发性骨软骨瘤", "detail": "在患有1型遗传性多发性骨软骨瘤的人中，已经鉴定出EXT1gene的480个突变，在这种情况下，人们会发展出多种称为骨软骨瘤的良性（非癌性）骨肿瘤。这些突变大多数被称为“功能丧失”突变，因为它们阻止了任何功能性exostosin-1蛋白的产生。功能性exostosin-1蛋白的丧失使其无法与exostosin-2蛋白形成复合物，并向蛋白中添加硫酸乙酰肝素。目前尚不清楚这种损伤如何导致遗传性多发性骨软骨瘤的体征和症状。"}, {"name": "Trichorhinophalangeal综合征II型", "detail": "EXT1gene位于8号染色体上的一个区域，该区域在II型毛发鼻指综合征（TRPS II）患者中被删除。TRPS II是引起骨和关节畸形（包括多发性骨软骨瘤）的疾病（如上所述）；独特的面部特征；智力障碍；以及皮肤，头发，牙齿，汗腺和指甲的异常。这种删除的结果是，受影响的个体在每个细胞中都缺少一个EXT1gene拷贝。缺乏exostosin-1蛋白会导致TRPS II患者的骨软骨瘤。EXT1gene附近其他gene的缺失可能有助于这种情况的其他特征。"}]}' WHERE id = 21200;
UPDATE genes SET GHR_metadata_ch = '{"gene": "DRD5", "conditions": [{"name": "良性基本眼睑痉挛", "detail": "几项研究检查了DRD5gene的常见变异（多态性）与良性原发性眼睑痉挛之间的可能关系。这些研究的结果好坏参半。一些研究表明，多态性是一种称为等位gene2的短重复DNA片段，在患有良性原发性眼睑痉挛的人群中比在没有该疾病的人群中更常见。但是，其他研究发现这种多态性与良性原发性眼睑痉挛之间没有联系。研究人员仍在努力阐明DRD5gene的变异是否与这种疾病有关。"}, {"name": "其他疾病", "detail": "DRD5gene中的其他多态性似乎与称为注意力缺陷多动障碍（ADHD）的常见行为状况有关。这种情况通常始于儿童时期，其特征是过度活跃，冲动行为和难以引起注意。(换行)大多数关于DRD5gene和ADHD的研究都集中在位于gene开始附近的多态性上。该区域由两个连续重复多次的DNA构建块（碱基对）组成。该片段的大小范围为134至156个碱基对。多项研究表明，一个长148个碱基对的特定变异与ADHD风险适度增加有关。但是，尚不清楚这种多态性如何影响疾病的风险。DRD5gene的变异是许多正在研究的因素之一，有助于解释多动症的原因。大量的遗传和环境因素，其中大多数仍然未知，可能决定了发展这种复杂疾病的风险。"}]}' WHERE id = 21237;
UPDATE genes SET GHR_metadata_ch = '{"gene": "TNFRSF11B", "conditions": [{"name": "青少年佩吉特病", "detail": "已发现TNFRSF11Bgene中至少有六个突变会导致青少年Paget病。这些突变中的每一个都大大降低了骨保护素的功能或阻止细胞产生任何这种蛋白质。没有骨保护素，RANKL仅与RANK结合。由此产生的化学信号传导的增加刺激了过多破骨细胞的产生并触发这些细胞异常地分解骨。在患有佩吉特病的青少年中，骨骼的分解和替换速度比平时快得多。当新骨组织生长时，它比正常骨更弱，组织更少。骨重塑的这些问题导致整个骨骼的骨骼变得异常大，畸形并且容易破裂（断裂）。"}, {"name": "骨Paget病", "detail": "研究表明，TNFRSF11Bgene的几种正常变异（多态性）可能会增加患经典佩吉特骨病的风险，尤其是女性。这些遗传变化可能影响骨保护素的数量或功能。但是，尚不清楚该gene的多态性如何影响疾病风险。"}]}' WHERE id = 21259;
UPDATE genes SET GHR_metadata_ch = '{"gene": "DBT", "conditions": [{"name": "枫糖浆尿病", "detail": "在枫糖浆尿病患者中已鉴定出DBTgene的70多个突变，最常见于轻度变异患者。这些变异形式在婴儿期或儿童期后期变得明显，如果不加以治疗，它们将导致发育迟缓和其他健康问题。（换行）DBTgene中的突变包括单个DNA构建块（碱基对）的变化以及DBTgene中少量DNA的插入或缺失。这些变化破坏了E2成分的正常功能，阻止了BCKD酶复合物有效分解亮氨酸，异亮氨酸和缬氨酸。结果，这些氨基酸及其副产物在体内积聚。这种积累对细胞和组织有毒，特别是在神经系统中。这些物质的积累会导致发育迟缓和与枫糖浆尿病相关的其他健康问题。"}]}' WHERE id = 21275;
UPDATE genes SET GHR_metadata_ch = '{"gene": "PROP1", "conditions": [{"name": "合并垂体激素缺乏症", "detail": "已发现PROP1gene中至少有25个突变会导致合并的垂体激素缺乏症。最常见的突变会删除PROP1gene中的两个DNA构建块（写为301-302delAG）。这种缺失导致产生不能正常发挥功能的异常短的蛋白质。缩短的蛋白质不能控制其他gene的活性，这会减少垂体细胞分化并阻止激素从垂体释放。缺乏这些激素会导致身材矮小，青春期延迟或缺乏，以及垂体激素缺乏症的其他体征和症状。"}]}' WHERE id = 21281;
UPDATE genes SET GHR_metadata_ch = '{"gene": "AFF2", "conditions": [{"name": "脆性XE综合征", "detail": "AFF2gene的突变会导致脆性XE综合征，这种疾病的特征是轻度智力障碍和学习困难。几乎所有情况都是由称为CCG三核苷酸重复扩增的突变引起的，其中CCG三核苷酸异常重复超过200次，这使得该gene区域不稳定。结果，AFF2gene被关闭（沉默），并且没有蛋白质产生。目前尚不清楚这种蛋白质的缺乏如何导致智力功能问题。（换行）很少，AFF2gene的遗传物质的小缺失与脆性XE综合征相关，尽管这些缺失如何影响蛋白质并导致智力残疾尚不清楚。"}]}' WHERE id = 21296;
UPDATE genes SET GHR_metadata_ch = '{"gene": "HEPACAM", "conditions": [{"name": "皮质下囊肿的大脑白质脑病", "detail": "已发现HEPACAMgene中至少有20个突变会引起皮质下囊肿的大脑白质脑病。(换行)这种情况影响大脑发育和功能，导致运动和反复发作的问题。HEPACAMgene突变导致无法正确地将蛋白质转运至细胞连接处的蛋白质。尚不清楚神经元细胞连接处缺乏功能性GlialCAM蛋白（或某些其他蛋白）如何损害大脑发育和功能，从而导致皮质下囊肿的大脑白质脑病的体征和症状。HEPACAMgene中的（换行）突变会导致两种类型的皮质下囊肿的大脑白质脑病，即2A型和2B型；这些类型加在一起占所有案例的20%。两种类型之间的主要区别在于2B型的体征和症状随时间改善。这种改善的原因尚不清楚。"}]}' WHERE id = 21300;
UPDATE genes SET GHR_metadata_ch = '{"gene": "CYBA", "conditions": [{"name": "慢性肉芽肿病", "detail": "已发现CYBAgene中的40多个突变会引起慢性肉芽肿性疾病。患有这种疾病的人由于免疫系统减弱而发生反复发作的感染和炎症的风险增加。CYBAgene突变引起的这种情况不到所有病例的5%。这些突变大多数会改变细胞色素b-245α链中蛋白质（氨基酸）的单个结构单元，或导致其异常短而无功能。改变的蛋白质不仅会降低α链的功能，还会降低其β链伴侣的功能。没有这些亚基，NADPH氧化酶就无法正常组装或发挥作用。结果，吞噬细胞不能产生活性氧来杀死外来入侵者，并且嗜中性粒细胞活性不受调节。缺乏NADPH氧化酶会使受影响的个体容易受到多种类型的感染和过度炎症的影响。"}]}' WHERE id = 21304;
UPDATE genes SET GHR_metadata_ch = '{"gene": "TRIP11", "conditions": [{"name": "软骨形成", "detail": "已发现TRIP11gene中至少有9个突变会导致某种形式的软骨生成，称为1A型或休斯顿哈里斯型。这种罕见的骨骼发育障碍的特征是四肢极短，胸部狭窄，肋骨容易骨折以及颅骨，脊柱和骨盆缺乏正常的骨形成（骨化）。这些异常会导致严重的健康问题，软骨发育不全的婴儿通常在出生前或出生后不久死亡。（换行）与1A型软骨发育不全相关的TRIP11gene突变导致产生无功能形式的GMAP-210蛋白或阻止细胞产生任何这种蛋白。研究表明，GMAP-210活性不足会改变高尔基体的结构和功能，从而损害蛋白质修饰。软骨细胞似乎对这些变化特别敏感，这些细胞中高尔基体的功能异常可能是1A型软骨发育不全中骨形成问题的基础。"}]}' WHERE id = 21322;
UPDATE genes SET GHR_metadata_ch = '{"gene": "XPA", "conditions": [{"name": "着色性干皮症", "detail": "已发现XPAgene中至少有25个突变会引起色素干皮症。该gene的突变导致了一种非常严重的疾病，这种疾病在日本人群中比在其他人群中更为常见。大多数日本人患有干性色素性皮肤病具有相同的XPAgene突变，其写为IVS3AS，G>C。这种突变阻止细胞产生任何功能性XPA蛋白。在日本和其他地方已有报道的其他XPAgene突变导致XPA蛋白缺陷版本的产生或大大减少了在细胞中产生的该蛋白的量。(换行)XPA蛋白的部分或全部丢失会阻止细胞正常修复DNA损伤。结果，异常在DNA中积累，导致细胞发生故障并最终癌变或死亡。DNA修复中的这些问题会导致色素干皮症患者对阳光下的紫外线极为敏感。当紫外线破坏控制细胞生长和分裂的gene时，细胞会生长得太快，并且无法控制。结果，患有色素干皮症的人患癌症的风险大大增加。这些癌症最常发生在暴露于阳光下的身体部位，如皮肤和眼睛。(换行)当XPAgene突变引起色素干皮症时，通常与进行性神经系统异常有关。这些神经系统问题包括听力丧失，协调性差，行走困难，运动问题，智力功能丧失，吞咽和说话困难以及癫痫发作。尽管大脑没有暴露在紫外线下，但神经系统异常被认为是DNA损伤累积的结果。研究人员怀疑其他因素会破坏神经细胞中的DNA。目前还不清楚为什么有些色素干皮症患者会出现神经系统异常，而另一些则不会。"}]}' WHERE id = 21328;
UPDATE genes SET GHR_metadata_ch = '{"gene": "WRN", "conditions": [{"name": "沃纳综合征", "detail": "已知WRNgene中有60多个突变会引起Werner综合征。这些突变大多数导致异常短的，无功能的Werner蛋白。研究表明，这种缩短的蛋白质不会转运到细胞核中，而细胞核通常会与DNA相互作用。此外，缩短的蛋白质比正常的Werner蛋白质更快地分解，从而减少了细胞中该蛋白质的量。如果细胞核中没有正常的Werner蛋白，则DNA复制，修复和转录会受到破坏。研究人员仍在确定WRNgene的突变如何导致Werner综合征的体征和症状。"}, {"name": "前列腺癌", "detail": "遗传学家庭参考提供有关前列腺癌的信息。"}, {"name": "癌症", "detail": "一个人的gene发生一些变化是在那个人的一生中获得的，并且只存在于某些细胞中。这些差异称为体细胞变化，不是遗传的。WRNgene的体细胞变化在非遗传性肿瘤中发现，涉及一个称为甲基化的过程。甲基化是一种化学修饰，将称为甲基的小分子连接到DNA的某些片段上。当WRNgene上连接了太多的甲基（超甲基化）时，该gene将被关闭，不会产生Werner蛋白。没有这种蛋白质，细胞对DNA损伤不会正常反应。Werner蛋白的缺乏允许突变在其他gene中积累，这可能导致细胞以不受控制的方式生长和分裂。这种不受调节的细胞生长可导致癌性肿瘤的形成。已经在许多不同类型的肿瘤中发现了WRNgene的高甲基化，包括结肠，直肠，肺，胃，前列腺，乳腺和甲状腺肿瘤。"}]}' WHERE id = 21337;
UPDATE genes SET GHR_metadata_ch = '{"gene": "OPN1SW", "conditions": [{"name": "色觉缺陷", "detail": "已发现OPN1SWgene中至少有六个突变会导致相对罕见的色觉缺陷形式，称为蓝黄色色觉缺陷或tritan缺陷。这些缺陷会导致区分蓝色和绿色阴影的问题，并导致难以区分深蓝色和黑色。每个已知的OPN1SWgene突变都会改变短波敏感感光色素中的单个蛋白质结构单元（氨基酸），从而导致感光色素部分或完全失去功能。研究人员建议，没有功能性光色素的S视锥细胞过早死亡或无法正常向大脑传输视觉信号。（换行）当OPN1SWgene突变导致完全无功能的S视锥细胞时，色觉完全取决于其他两种视锥细胞。由S锥功能完全丧失引起的特定类型的蓝黄色色觉缺陷称为tritanopia。当S锥功能异常时，会发生一种不太严重的蓝黄色视觉缺陷，称为tritanomaly。"}]}' WHERE id = 21339;
UPDATE genes SET GHR_metadata_ch = '{"gene": "GJB4", "conditions": [{"name": "变异性红斑角化病", "detail": "在患有变异性红斑角化病和进展性红斑角化病（EKVP）的人中至少鉴定出7个GJB4gene突变，这是一种皮肤疾病，其特征是角化过度区域（皮肤异常增厚）和暂时变红的斑块（称为红斑区域）。这些突变中的每一个都改变了用于制造连接蛋白30.3的单个蛋白质构建块（氨基酸）。研究表明，异常蛋白质可以积聚在称为内质网（ER）的细胞结构中，引发称为ER应激的有害过程。研究人员怀疑内质网应激损伤并导致表皮细胞过早死亡。这种细胞死亡导致皮肤炎症，这似乎是红斑区域发展的基础。表皮损伤和细胞死亡导致角化过度的机制知之甚少。"}]}' WHERE id = 21399;
UPDATE genes SET GHR_metadata_ch = '{"gene": "GJB3", "conditions": [{"name": "变异性红斑角化病", "detail": "在患有变异性红斑角化病和进展性红斑角化病（EKVP）的人中已鉴定出至少10个GJB3gene突变，该皮肤疾病的特征是角化过度区域（皮肤异常增厚）和暂时变红的斑块（称为红斑区域）。这些突变中的每一个都改变了用于制造连接蛋白31的单个蛋白质构建块（氨基酸）。研究表明，异常蛋白质可以积聚在称为内质网（ER）的细胞结构中，引发称为ER应激的有害过程。研究人员怀疑内质网应激损伤并导致表皮细胞过早死亡。这种细胞死亡导致皮肤炎症，这似乎是红斑区域发展的基础。表皮损伤和细胞死亡导致角化过度的机制知之甚少。"}, {"name": "非综合征性听力损失", "detail": "遗传学家庭参考资料提供有关非综合征性听力损失的信息。"}]}' WHERE id = 21406;
UPDATE genes SET GHR_metadata_ch = '{"gene": "MYO7A", "conditions": [{"name": "非综合征性听力损失", "detail": "研究人员已经鉴定出几种可能导致非综合征性听力损失的MYO7Agene突变，这是与其他体征和症状无关的听力损失。该gene的突变被认为会导致两种形式的非综合征性听力损失：DFNA11和DFNB2。(换行)DFNA11以常染色体显性遗传，这意味着每个细胞中只有一个MYO7Agene的突变拷贝足以引起这种情况。这种形式的听力损失始于儿童学会说话（舌后）后的童年，并随着时间的推移变得更加严重。(换行)导致DFNA11的大多数突变会改变肌球蛋白VIIA中的单个蛋白质结构单元（氨基酸），从而导致异常蛋白质无法正常工作。其他遗传变化会从MYO7Agene的关键区域删除少量DNA，这可能会改变蛋白质的结构。研究人员怀疑，改变后的蛋白质会破坏内耳中立体纤毛的生长和组织，从而导致听力下降。(换行)DFNB2以常染色体隐性遗传，这意味着MYO7Agene的两个拷贝都在每个细胞中发生了突变。听力损失可以是舌后的，也可以在孩子学会说话之前开始（舌前）。一些研究人员建议患有DFNB2的人实际上可能患有Usher综合征（如下所述），因为一些被认为患有非综合征性听力损失的人在以后的生活中会发展为色素性视网膜炎（Usher综合征的视力障碍）。但是，其他被诊断患有DFNB2的个体从未发展为色素性视网膜炎，最近的研究表明DFNB2和Usher综合征可能是由MYO7Agene的不同突变引起的。（换行）引起DFNB2的突变会改变肌球蛋白VIIA的结构和功能，但它们可能无法完全消除蛋白质的功能。最近的研究发现，该蛋白质可能在视网膜中保留足够的功能以允许正常视力，但在内耳中却没有足够的功能来允许正常听力。"}, {"name": "亚瑟综合症", "detail": "在I型Usher综合征患者中已鉴定出MYO7Agene的200多个突变，其特征是听力下降，视力下降以及平衡与协调问题。具体而言，MYO7Agene突变导致一种称为Usher综合征IB型（USH1B）的疾病，占所有Usher综合征I型病例的一半以上。（换行）许多这些遗传变化改变了单个肌球蛋白VIIA蛋白关键区域的蛋白质结构单元（氨基酸）。其他突变在制造肌球蛋白VIIA的说明中引入了过早的终止信号。还有其他突变在MYO7Agene中插入或删除少量DNA。所有这些变化导致肌球蛋白VIIA的非功能性形式的产生或阻止任何这种蛋白质的产生。内耳缺乏肌球蛋白VIIA会破坏立体纤毛的正常发育和功能，从而导致听力下降以及难以平衡和协调。视网膜中缺乏肌球蛋白VIIA会导致色素性视网膜炎，在这种情况下，视网膜的感光细胞逐渐恶化，导致进行性视力丧失。"}, {"name": "与年龄有关的听力损失", "detail": "遗传学家庭参考资料提供有关与年龄有关的听力损失的信息。"}]}' WHERE id = 21434;
UPDATE genes SET GHR_metadata_ch = '{"gene": "CACNA1D", "conditions": [{"name": "产生醛固酮的腺瘤", "detail": "发现CACNA1Dgene中的体细胞突变会引起产生醛固酮的腺瘤，这种疾病的特征是肾上腺良性肿瘤，高血压以及心脏病发作和中风的风险增加。突变更容易增加钙离子的转运，并过度激活肾上腺中的钙/钙调蛋白途径，导致醛固酮生成增加，从而破坏其维持正常血压的能力。"}, {"name": "其他疾病", "detail": "CACNA1Dgene中的遗传突变与多种情况有关，例如：；产生醛固酮的腺瘤，原发性醛固酮增多症，癫痫发作和神经系统异常（PASNA），自闭症谱系障碍和窦房结功能障碍和耳聋（SANDD），这种疾病的特征是心动过缓，心律不齐和听力障碍。"}]}' WHERE id = 21438;
UPDATE genes SET GHR_metadata_ch = '{"gene": "F2", "conditions": [{"name": "凝血酶原缺乏", "detail": "已发现F2gene中的50多个突变会导致凝血酶原缺乏。这些突变大多数会改变凝血酶原中的一种蛋白质结构单元（氨基酸）。一些突变会大大降低凝血酶原的活性，并可能导致严重的出血事件。其他突变允许中等量的凝血酶原活性，通常引起轻度出血发作。鉴定出的突变均未消除凝血酶原功能。研究人员认为，人们不能完全缺乏凝血酶原。"}, {"name": "凝血酶原血栓形成倾向", "detail": "导致大多数凝血酶原血栓形成倾向的突变会改变F2gene中的一个DNA构建块（核苷酸）。具体而言，它用20210位的核苷酸腺嘌呤取代了核苷酸鸟嘌呤（写为G20210A或20210G>A）。这种突变发生在称为3''非翻译区的gene区域，导致gene过度活跃并导致产生过多的凝血酶原。丰富的凝血酶原导致更多的凝血酶，促进血凝块的形成。"}]}' WHERE id = 21440;
UPDATE genes SET GHR_metadata_ch = '{"gene": "GJB1", "conditions": [{"name": "进行性神经性腓骨肌萎缩症", "detail": "研究人员已经在X型Charcot-Marie-Tooth病患者中发现了大约300个GJB1gene突变，该疾病的特征是肌肉无力和感觉问题，尤其是在手和脚上。这些突变中的一些还导致患有此类Charcot-Marie-Tooth病的个体听力下降。大多数GJB1gene突变会改变connexin-32蛋白中的单个蛋白结构单元（氨基酸）。一些GJB1gene突变导致蛋白质大小异常。(换行)目前尚不清楚GJB1gene突变如何导致Charcot-Marie-Tooth病的特征，包括髓鞘丢失（脱髓鞘）和周围神经系统神经冲动传递减慢。改变后的蛋白质可能会迅速降解或捕获在细胞内，从而阻止其到达细胞膜形成间隙连接。在某些情况下，改变的蛋白质到达细胞膜，但不能形成功能正常的间隙连接。功能性间隙连接的丧失可能损害雪旺氏细胞的正常活动，例如髓磷脂的产生。间隙连接功能异常也可能破坏雪旺氏细胞与基础神经细胞之间的通讯，从而干扰神经冲动的传递。(换行)除了与该疾病相关的周围神经系统问题外，还报道了一些由GJB1gene突变引起的Charcot-Marie-Tooth病患者中枢神经系统脱髓鞘的证据。这些中枢神经系统异常通常不会引起任何症状，但是可以通过神经冲动的电测试或影像学检查来确定。研究表明，另一种功能与connexin-32重叠的连接蛋白有助于补偿中枢神经系统少突胶质细胞中突变的connexin-32蛋白。"}]}' WHERE id = 21445;
UPDATE genes SET GHR_metadata_ch = '{"gene": "CLCN2", "conditions": [{"name": "CLCN2相关的白质脑病", "detail": "已发现CLCN2gene中至少有18个突变引起CLCN2相关的白质脑病。这种情况的主要特征是协调和平衡问题（共济失调），但也可能导致学习障碍，频繁头痛和视力问题。（换行）一些CLCN2gene突变改变ClC-2通道中的单个蛋白质构建块（氨基酸），损害通道的稳定性并降低通道功能。其他CLCN2gene突变通常会导致异常短通道蛋白的产生，从而导致通道功能完全丧失。缩短的蛋白质被捕获在细胞内并且不能到达细胞膜或快速分解。(换行)由于ClC-2通道活性降低，某些脑细胞和神经元周围的髓磷脂充满了过多的水，无法正常工作。充满液体的髓磷脂不能有效地传递神经冲动，导致神经系统问题，例如共济失调和CLCN2相关的白质脑病的其他体征和症状。"}, {"name": "青少年肌阵挛性癫痫", "detail": "遗传学家庭参考提供有关青少年肌阵挛性癫痫的信息。"}]}' WHERE id = 21457;
UPDATE genes SET GHR_metadata_ch = '{"gene": "IDS", "conditions": [{"name": "黏多醣贮积症II型", "detail": "已经发现IDSgene中的300多个突变会导致II型粘多糖贮积病（MPS II）。改变一个DNA结构单元（核苷酸）的突变是最常见的。所有导致MPS II的突变都会降低或完全消除I2S的功能。通常无法确定某种突变是否会引起严重或轻度的MPS II；但是，导致完全不存在I2S的突变会导致更严重的疾病形式。(换行)I2S酶活性的缺乏导致硫酸乙酰肝素和硫酸皮肤素在细胞内，特别是在溶酶体内的积累。这些GAG的积累增加了溶酶体的大小，这就是为什么MPS II中许多组织和器官增大的原因。研究人员认为，积累的GAG也可能干扰溶酶体内其他蛋白质的功能，破坏细胞内分子的运动。"}]}' WHERE id = 21463;
UPDATE genes SET GHR_metadata_ch = '{"gene": "TGFBR1", "conditions": [{"name": "Loeys-Dietz综合征", "detail": "已发现TGFBR1gene中的35个以上突变会导致I型Loeys-Dietz综合征。Loeys-Dietz综合征影响结缔组织，结缔组织为血管，骨骼和身体其他部位提供结构和支持。这种类型的Loeys-Dietz综合征的特征是血管异常和骨骼畸形。导致Loeys-Dietz综合征的TGFBR1gene突变存在于每个细胞中该gene的一个拷贝中。大多数这些突变改变TGF-β中的单个蛋白质构建块（氨基酸）？1型受体，导致功能很少或没有功能的受体。虽然受体功能严重下降，但TGF-？通路信号的发生强度甚至比正常情况更大。研究人员推测，这种信号通路中其他蛋白质的活性增加是为了补偿TGF-β的减少？受体1型活性；然而，导致信号增加的确切机制尚不清楚。过度活跃的信号通路破坏结缔组织和各种身体系统的发育，并导致I型Loeys-Dietz综合征的各种体征和症状。"}, {"name": "家族性胸主动脉瘤和解剖", "detail": "遗传学家庭参考资料提供有关家族性胸主动脉瘤和解剖的信息。"}, {"name": "前列腺癌", "detail": "遗传学家庭参考提供有关前列腺癌的信息。"}, {"name": "其他癌症", "detail": "已发现TGFBR1gene中的10多个突变会增加患上一种称为多发性自愈鳞状上皮瘤（MSSE）的皮肤癌的风险。这种情况，也称为弗格森-史密斯病，其特征是形成多个侵入性皮肤肿瘤，这些肿瘤在数周内无法控制地生长，但随后突然萎缩并死亡，留下非癌性瘢痕。(换行)MSSE患者每个细胞中TGFBR1gene的一个拷贝都有突变。肿瘤在MSSE中形成需要TGFBR1gene第二个拷贝中的另一个突变。第二种突变称为体细胞突变，仅在肿瘤细胞中发现，不遗传。与导致I型Loeys-Dietz综合征的TGFBR1gene突变（如上所述）不同，导致MSSE的突变根本阻止了任何蛋白质的产生。某些细胞完全缺乏功能性受体导致TGF-β完全丧失？通路信号传导和严重降低的肿瘤抑制作用，使皮肤癌得以形成。导致MSSE中多个皮肤肿瘤自发愈合的机制尚不清楚。"}]}' WHERE id = 21490;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ABCC6", "conditions": [{"name": "婴儿全身动脉钙化", "detail": "ABCC6gene的突变与威胁生命的疾病有关，称为婴儿全身动脉钙化（GACI），其特征是存在缺陷蛋白，该蛋白削弱其释放ATP的能力，导致血管（动脉）和其他组织钙化异常。在患有弹性假黄瘤（PXE）的个体中也发现了大多数引起GACI的突变。"}, {"name": "弹性假黄瘤", "detail": "ABCC6gene的突变会导致一种称为弹性假黄瘤（PXE）的健康状况，其特征是存在缺陷蛋白，该缺陷蛋白会损害其释放ATP或转运通常可防止矿化的物质的能力，从而导致钙和其他矿物质在弹性纤维中的异常积累。Ex23_29del是在美国PXE患者中发现的最常见的缺失突变。"}]}' WHERE id = 21493;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SNCA", "conditions": [{"name": "路易氏体型失智症", "detail": "已发现SNCAgene中至少有六个突变会导致路易体痴呆。这种情况的特征是智力下降（痴呆）；幻觉；注意力和情绪的突然变化；以及帕金森氏病的运动问题（如下所述），例如四肢僵硬，震颤以及平衡与协调能力受损。这种情况的一个特征是路易体，它是大脑中α-突触核蛋白的异常簇。路易体也发生在帕金森病中，但是在路易体痴呆的大脑中它们往往更广泛。（换行）在路易体痴呆中，SNCAgene突变导致产生具有异常形状的α-突触核蛋白。畸形的蛋白质聚集在一起，形成路易体的主要成分。这些蛋白质簇存在于整个大脑的神经元中，在那里它们损害细胞功能并最终导致细胞死亡。随着时间的推移，神经元的丧失越来越损害智力和运动功能以及情绪的调节，导致路易体痴呆的体征和症状。"}, {"name": "帕金森病", "detail": "已发现SNCAgene中至少有30个突变会引起帕金森氏病，帕金森氏病的特征是运动和平衡方面的进行性问题。SNCAgene突变与该疾病的早期发作形式有关，通常在50岁之前出现。已经发现SNCAgene的其他变异会增加患帕金森氏病的风险，尽管它们似乎不是该病的直接原因。(换行)研究人员描述了帕金森病患者SNCAgene的两种类型的改变。一种类型改变用于制备α-突触核蛋白的单个蛋白质构建块（氨基酸）。在某些情况下，氨基酸丙氨酸被蛋白质53位的氨基酸苏氨酸（写为Ala53Thr或A53T）或30位的氨基酸脯氨酸（写为Ala30Pro或A30P）取代。这些突变导致α-突触核蛋白呈现不正确的三维形状（错误折叠）。在另一种类型的改变中，每个细胞中两个SNCAgene之一被不适当地复制或复制。SNCAgene的额外拷贝导致过量的α-突触核蛋白。(换行)目前尚不清楚SNCAgene的改变如何导致帕金森病。这种情况涉及产生多巴胺的神经元的选择性死亡或损伤。错误折叠或过量的α-突触核蛋白可能聚集在一起形成路易体并损害这些神经元在大脑特定区域的功能。路易体可能破坏多巴胺的调节，这使得多巴胺积累到毒性水平并最终杀死神经元。研究人员还怀疑路易体失速或关闭去除不需要的蛋白质的细胞机器。结果，不需要的蛋白质可能会阻塞神经元并损害其功能。当产生多巴胺的神经元受损或死亡时，出现帕金森病的症状。这些细胞的丧失削弱了大脑和肌肉之间的交流，最终大脑无法控制肌肉运动。路易体在大脑中称为黑质的区域中的存在，其控制平衡和运动，是帕金森病的特征。"}, {"name": "多系统萎缩", "detail": "已发现SNCAgene的几种常见变异会增加多系统萎缩的风险，这是一种进行性脑部疾病，会影响运动和平衡并破坏自主神经系统的功能。自主神经系统控制主要是非自愿的行为，例如调节血压。（换行）鉴定的gene变异各自改变SNCAgene中的单个DNA构建块（核苷酸）。研究人员正在努力确定这些变化是否改变α-突触核蛋白的功能以及它们如何影响发生多系统萎缩的风险。SNCAgene的变异似乎会影响欧洲人后裔的疾病风险；但是，研究表明，该gene的变化与中国人口或韩国人的多系统萎缩无关。目前尚不清楚SNCAgene变异是否是其他地理和种族背景人群中这种情况的危险因素。"}]}' WHERE id = 21496;
UPDATE genes SET GHR_metadata_ch = '{"gene": "MTHFR", "conditions": [{"name": "同型半胱氨酸尿症", "detail": "在患有高半胱氨酸尿症的人中已经鉴定出MTHFRgene中的至少40个突变，其中身体不能正确处理高半胱氨酸和甲硫氨酸。患有这种疾病的人经常会出现眼睛问题，凝血异常，骨骼异常和认知问题。导致高半胱氨酸尿症的大多数突变会改变亚甲基四氢叶酸还原酶中的单个氨基酸。这些变化削弱了酶的功能，有些变化导致酶被关闭（失活）。其他突变导致产生异常小的，无功能的酶。没有功能性亚甲基四氢叶酸还原酶，同型半胱氨酸不能转化为蛋氨酸。结果，同型半胱氨酸在血流中积聚，并且蛋氨酸的量减少。一些过量的高半胱氨酸会从尿液中排泄（高半胱氨酸尿症）。研究人员尚未确定同型半胱氨酸和蛋氨酸水平的改变如何导致影响同型半胱氨酸尿症患者身体多个部位的各种健康问题。"}, {"name": "与年龄有关的听力损失", "detail": "遗传学家庭参考资料提供有关与年龄有关的听力损失的信息。"}, {"name": "无脑", "detail": "MTHFRgene中的几种变异（多态性）与神经管缺陷的风险增加有关，神经管缺陷是在脑和脊髓发育期间发生的一组出生缺陷。无脑畸形是最常见的神经管缺陷类型之一。受影响的人缺少大脑的大部分，头骨缺失或形成不完整。（换行）与神经管缺陷相关的研究最多的多态性改变了MTHFRgene中的单个DNA构建块（核苷酸）。具体而言，它在位置677处用核苷酸胸腺嘧啶取代了核苷酸胞嘧啶（写为677C>T）。这种常见的变体导致亚甲基四氢叶酸还原酶的形式，其在较高温度下具有降低的活性（不耐热）。具有677C>T多态性的人，特别是那些具有两个遗传变异拷贝的人，由于亚甲基四氢叶酸还原酶活性降低，其血液中同型半胱氨酸水平升高。(换行)研究人员研究了神经管缺陷患者及其母亲的MTHFRgene多态性，但目前尚不清楚这些变异如何影响发育中的大脑和脊髓。神经管缺陷风险增加可能与亚甲基四氢叶酸还原酶处理叶酸的能力差异有关；这种维生素的缺乏是神经管缺陷的既定风险因素。(换行)尽管MTHFRgene多态性与神经管缺陷风险增加有关，但这些变异在全世界许多人群中都很常见。大多数具有MTHFRgene多态性的人没有神经管缺陷，并且他们的孩子通常也不受影响。MTHFRgene的变化只是被认为有助于这些复杂病症的许多遗传和环境因素之一。"}, {"name": "脊柱裂", "detail": "MTHFRgene的多态性也与脊柱裂（另一种常见类型的神经管缺陷）的风险增加有关。在患有这种情况的人中，当脊柱形成时，脊柱的骨骼不会完全围绕脊髓的发育神经闭合。结果，脊髓的一部分可以突出穿过脊柱中的开口，导致永久性神经损伤。（换行）如上所述，MTHFRgene的变异可能通过改变亚甲基四氢叶酸还原酶处理叶酸的能力而增加神经管缺陷的风险。但是，这些变化在全球许多人群中都很常见。大多数具有MTHFRgene多态性的人没有神经管缺陷，他们的孩子也没有。"}, {"name": "其他疾病", "detail": "还已经研究了MTHFRgene中的多态性作为各种常见病症的可能危险因素。这些包括心脏病，中风，高血压（高血压），妊娠期高血压（先兆子痫），称为青光眼的眼病，精神疾病和某些类型的癌症。研究表明，在MTHFRgene的两个拷贝上均具有677C>T多态性的个体患血管疾病（包括心脏病和中风）的风险增加。677C>T多态性也被认为是唇裂和pa裂的危险因素，唇裂是一种先天性缺陷，其中上唇有裂口，嘴顶有开口。许多MTHFRgene多态性改变或降低亚甲基四氢叶酸还原酶的活性，导致血液中同型半胱氨酸的增加。同型半胱氨酸水平的这种增加可能有助于许多这些病症的发展。(换行)对患有这些疾病的人的MTHFRgene变异的研究结果好坏参半，在某些研究中发现了关联，而在其他研究中则没有。因此，尚不清楚MTHFRgene的变化在这些疾病中起什么作用。可能还有其他因素影响同型半胱氨酸的加工，同型半胱氨酸水平的变化在一个人是否患有任何这些疾病中起作用。大量的遗传和环境因素，其中大多数仍然未知，可能决定了发展最常见，复杂conditions的风险。"}]}' WHERE id = 21499;
UPDATE genes SET GHR_metadata_ch = '{"gene": "AMH", "conditions": [{"name": "持续性苗勒管综合征", "detail": "已经在患有持续性苗勒氏管综合征1型的人中发现了AMHgene的突变，这是一种性发育障碍，会影响男性，并且是由于AMH蛋白缺陷而引起的，该蛋白无法与其受体结合并抑制苗勒氏管细胞的凋亡。患有这种综合征的人的男性生殖器官正常，有子宫和输卵管，并表现出隐睾症或腹股沟疝的症状。"}]}' WHERE id = 21505;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ACADVL", "conditions": [{"name": "超长链酰基辅酶A脱氢酶缺乏症", "detail": "已经发现ACADVL中的几种突变由于非功能性蛋白质而导致非常长链的酰基辅酶A脱氢酶（VLCAD）缺乏，所述非功能性蛋白质严重降低或消除酶活性并损害非常长链的脂肪酸代谢。临床特征包括缺乏能量（嗜睡），低血糖（低血糖）并导致心脏肝脏，脑组织损伤。"}]}' WHERE id = 21522;
UPDATE genes SET GHR_metadata_ch = '{"gene": "RYR1", "conditions": [{"name": "中枢核心疾病", "detail": "在中枢核心疾病（CCD）患者中已鉴定出RYR1gene的90多个突变。这些突变大多数会影响ryanodine受体1蛋白关键区域的单个蛋白结构单元（氨基酸）。这些突变改变了RYR1通道的结构，从而改变了肌肉细胞内钙离子的正常流动。钙离子释放的中断会阻止肌肉正常收缩，从而导致中枢核心疾病的肌肉无力。(换行)研究人员提出了两种机制来解释RYR1gene突变如何成为中枢核心疾病患者肌肉无力的基础。一些遗传变化导致RYR1通道“泄漏”，从而使钙离子缓慢但连续地从肌浆网中流出。泄漏的通道大大减少了钙离子的存储量。结果，肌浆网中没有足够的钙离子来触发肌肉收缩。肌肉无力是由于骨骼肌无法适当收缩所致。（换行）其他RYR1gene突变以阻碍钙离子正常流动的方式改变RYR1通道的结构。尽管肌浆网存储了大量这些离子，但受体无法响应通常的信号释放它们。如果没有足够的钙离子在适当的时间从肌浆网流出，肌肉就无法正常收缩，从而导致肌肉无力。这种机制被称为E-C解偶联。"}, {"name": "先天性纤维类型不均衡", "detail": "已发现RYR1gene中至少有七个突变会导致先天性纤维类型失衡，这种疾病会导致全身肌肉无力，通常不会随着时间的推移而恶化。一些突变改变了ryanodine受体1蛋白中的单个氨基酸。其他RYR1gene突变会在制造受体的说明中产生过早的终止信号，从而导致异常短的无功能蛋白。研究人员怀疑，尽管尚不清楚RYR1gene突变在这种情况下的作用，但RYR1通道的破坏可能在肌肉无力和先天性纤维类型比例失调的其他特征中起作用。"}, {"name": "多微核病", "detail": "已发现RYR1gene中的几个突变会导致非典型形式的多微核疾病。这些突变改变了ryanodine受体1蛋白中的单个氨基酸，从而改变了该蛋白的结构和功能。这些变化的影响尚不清楚。一些突变可能会减少细胞产生的ryanodine受体1蛋白的量或导致该蛋白的不稳定形式。其他突变可能会干扰RYR1通道的正常调节。研究人员认为，某些RYR1gene突变会改变通道的形状，从而使钙离子无法正常流过。钙离子运输的中断会阻止肌肉正常收缩，从而导致多微核疾病的肌肉无力。"}, {"name": "恶性高热", "detail": "已知RYR1gene中至少有217个突变会增加恶性高热的风险。这些突变大多数会改变ryanodine受体1蛋白重要区域的单个氨基酸。这些突变改变了RYR1通道的结构，使其对某些药物（特别是某些麻醉气体和手术中使用的一种肌肉松弛剂）的反应更容易打开和关闭得更慢。结果，大量的钙离子从肌肉细胞内的肌质网释放。过量的可用钙离子会导致骨骼肌异常收缩，从而导致恶性高热患者的肌肉僵硬。肌肉细胞内钙离子浓度的增加也会激活产生热量（导致体温升高）并产生过量酸（导致酸中毒）的过程。(换行)RYR1gene的许多其他变化已被描述为恶性高热风险增加的人。但是，尚不清楚这些变化是否与恶性高热风险直接相关。"}, {"name": "中心核肌病", "detail": "遗传学家庭参考资料提供有关中心核肌病的信息。"}]}' WHERE id = 21556;
UPDATE genes SET GHR_metadata_ch = '{"gene": "COL11A1", "conditions": [{"name": "纤维软骨形成", "detail": "已在1型纤维软骨生成症患者中鉴定出COL11A1gene突变，这是一种以严重骨骼异常，听力下降，视力下降和肺发育受损，婴儿早期和儿童期观察到的呼吸衰竭为特征的骨骼生长障碍。突变导致异常或无功能的pro-alpha1（XI）链产生，导致XI型胶原蛋白缺陷，从而破坏骨骼形成并引起内耳变化。"}, {"name": "斯蒂克勒综合症", "detail": "发现COL11A1gene突变与II型Stickler综合征相关，该疾病的特征是独特的面部外观，眼睛异常，听力下降和关节问题。突变导致异常或无功能的pro-alpha1（XI）链受损，从而损害XI型胶原蛋白的产生或组装，从而破坏身体不同部位结缔组织的正常发育。COL11A1中的突变也会引起类似的疾病，称为马歇尔综合症。"}, {"name": "椎间盘疾病", "detail": "COL11A1gene的突变被发现与椎间盘疾病有关，这种疾病的拥有属性是一个或多个椎间盘的分解（退化），这些椎间盘分离脊柱（椎骨），导致背部、颈部、手臂和腿部疼痛、虚弱和麻木。"}, {"name": "骨关节炎", "detail": "发现COL11A1gene突变与骨关节炎有关，骨关节炎是一种常见的关节疾病，主要发生在老年人中，其特征是软骨破裂影响身体的许多部位，包括手，肩，脊柱，膝盖或臀部。受影响的个人？s经历疼痛，僵硬，运动受限，关节组织发炎，骨（骨赘）异常生长，并且该病症还与心血管疾病风险增加有关。"}, {"name": "其他疾病", "detail": "发现COL11A1gene突变与骨关节炎有关，骨关节炎是一种常见的关节疾病，主要发生在老年人中，其特征是软骨破裂影响身体的许多部位，包括手，肩，脊柱，膝盖或臀部。受影响的个人？s经历疼痛，僵硬，运动受限，关节组织发炎，骨（骨赘）异常生长，并且该病症还与心血管疾病风险增加有关。"}]}' WHERE id = 21562;
UPDATE genes SET GHR_metadata_ch = '{"gene": "WDR35", "conditions": [{"name": "颅外胚层发育不良", "detail": "WDR35gene是颅外胚层发育不良患者中最常见的突变gene；在受影响的个体中已经鉴定出该gene中的至少八个突变。这种情况的特征是头部拉长（头畸形），额头突出，面部特征其他独特；短骨头；以及某些称为外胚层组织的组织异常，包括牙齿，头发，指甲和皮肤。（换行）涉及颅外胚层发育不良的WDR35gene突变减少了功能性WDR35蛋白的量。IFT-A复合物这一成分的缺乏或活性降低会损害整个复合物的功能，从而破坏纤毛尖端蛋白质和物质的运输。结果，纤毛的组装和维护受到损害，这导致纤毛数量减少以及其形状和结构异常。尽管机制尚不清楚，但正常纤毛的丧失会阻碍骨骼和其他组织的正常发育，从而导致颅外胚层发育不良的特征。一些研究人员认为，鞭毛内运输受阻会阻止通过声波刺猬通路的信号传导，这可能会影响整个身体多个组织中的细胞生长和其他功能。"}, {"name": "窒息性胸营养不良", "detail": "遗传学家庭参考提供有关窒息性胸肌营养不良的信息。"}, {"name": "其他疾病", "detail": "WDR35gene突变可导致5型短肋多指症，这是一种严重疾病，其体征和症状与颅外胚层发育不良相似（如上所述）；但是，受影响的人无法生存到出生。5型短肋多指的骨骼异常包括极短的肋骨，短的手臂和腿以及多余的手指或脚趾（多指）。其他功能包括一个或多个器官的异常，包括心脏，肾脏，肝脏和肠道。这种情况的严重性被认为是由WDR35蛋白的严重功能丧失引起的，与颅外胚层发育不良相比，WDR35蛋白可能更大程度地破坏纤毛的功能。"}]}' WHERE id = 21605;
UPDATE genes SET GHR_metadata_ch = '{"gene": "CDT1", "conditions": [{"name": "梅尔-戈林综合征", "detail": "发现CDT1gene突变会导致Meier-Gorlin综合征，这种疾病的特征是身材矮小，膝盖发育不全和耳朵小。突变通常会导致异常的短蛋白，从而损害复制前复合物，从而破坏复制许可。"}]}' WHERE id = 21608;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ASH1L", "conditions": [{"name": "自闭症谱系障碍", "detail": "在自闭症谱系障碍（ASD）患者中已鉴定出至少七个ASH1Lgene突变，这是一种以社交技能受损，沟通问题和重复行为为特征的多种疾病。与ASD相关的某些ASH1Lgene突变会改变赖氨酸特异性甲基转移酶2H酶中的一个组成部分（氨基酸）。其他删除ASH1Lgene序列中的遗传物质或导致过早的终止信号，从而导致异常短的酶。这些突变的结果是该酶无功能。缺乏功能性赖氨酸特异性甲基转移酶2H酶会破坏组蛋白甲基化。赖氨酸特异性甲基转移酶2H调控的gene表达的变化影响大脑发育并增加ASD的风险。其他gene的正常变异以及环境风险因素（例如父母年龄，出生并发症和其他尚未发现的因素）也会影响个人患此病的风险。"}]}' WHERE id = 21615;
UPDATE genes SET GHR_metadata_ch = '{"gene": "IRF5", "conditions": [{"name": "系统性硬皮病", "detail": "IRF5gene的几种正常变异与发生系统性硬皮病的风险增加有关，系统性硬皮病是一种自身免疫性疾病，其特征是皮肤和内部器官中疤痕组织的堆积（纤维化）。尽管已知IRF5gene可刺激免疫系统对病毒的反应，但尚不清楚gene变异如何导致系统性硬皮病的风险增加。研究人员认为，遗传和环境因素的结合可能在疾病的发展中起作用。"}, {"name": "类风湿关节炎", "detail": "遗传学家庭参考提供有关类风湿关节炎的信息。"}, {"name": "系统性红斑狼疮", "detail": "遗传学家庭参考提供有关系统性红斑狼疮的信息。"}, {"name": "溃疡性结肠炎", "detail": "遗传学家庭参考提供有关溃疡性结肠炎的信息。"}, {"name": "自身免疫性疾病", "detail": "研究表明，IRF5gene的正常变异与几种自身免疫性疾病的风险增加有关。当免疫系统发生故障并攻击身体的组织和器官时，就会发生自身免疫性疾病。这些疾病包括系统性红斑狼疮，干燥综合征和类风湿性关节炎。(换行)有证据表明IRF5gene的某些变异与gene活性增加和细胞因子升高有关。但是，尚不清楚这些作用在自身免疫性疾病风险增加中起什么作用（如果有的话）。研究人员认为，遗传和环境因素的结合可能有助于这些疾病的发展。"}]}' WHERE id = 21622;
UPDATE genes SET GHR_metadata_ch = '{"gene": "APRT", "conditions": [{"name": "腺嘌呤磷酸核糖基转移酶缺乏症", "detail": "已发现APRTgene中至少40个突变导致腺嘌呤磷酸核糖基转移酶（APRT）缺乏，这是一种影响肾脏和泌尿道的病症。大多数这些突变改变APRT酶中的单个蛋白质构建块（氨基酸）。导致APRT缺陷的突变分为两组，称为APRT*J等位gene和APRT*Q0等位gene。APRT*J等位gene由一个突变组成，该突变用APRT酶中第136位的氨基酸苏氨酸取代氨基酸甲硫氨酸（写为Met136Thr或M136T）。这种突变降低了酶的功能。M136T突变几乎只发生在患有这种疾病的日本人中；大多数受影响的个体在每个细胞中的APRTgene的两个拷贝上都具有这种突变。APRT*Q0等位gene由所有其他APRTgene突变组成。这些突变中最常见的突变（书面IVS4+2insT）会改变用于制造该酶的遗传指令，从而导致异常短的无功能酶。据估计，这种突变发生在40%的受影响欧洲人中。（换行）APRTgene突变导致缺乏阻止腺嘌呤转化为AMP的功能性酶。结果，腺嘌呤转化为另一种称为2,8-二羟基腺嘌呤（2,8-DHA）的分子。2,8-DHA在尿液中结晶，在肾脏和泌尿道中形成结石。结果，肾功能可能下降，这可能导致终末期肾病（ESRD），这是危及生命的肾功能衰竭。"}]}' WHERE id = 21623;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ALDOB", "conditions": [{"name": "遗传性果糖不耐受", "detail": "发现ALDOgene中的几个突变会导致遗传性果糖不耐症，这种疾病的特征是摄入含果糖的食物后恶心和肠道不适。不耐受是由于无功能的醛缩酶B酶导致肝细胞中果糖-1-磷酸的积累，导致细胞能量降低，低血糖和肝细胞死亡。"}]}' WHERE id = 21634;
UPDATE genes SET GHR_metadata_ch = '{"gene": "GALNS", "conditions": [{"name": "黏多醣贮积症IV型", "detail": "已发现GALNSgene中的148个以上突变会导致IV型粘多糖贮积病（MPS IV）。这些突变大多数会改变gene中的单个DNA结构单元（核苷酸）。所有引起MPS IV的突变都会降低或消除N-乙酰半乳糖胺6-硫酸酯酶的功能。(换行)缺乏N-乙酰半乳糖胺6-硫酸酯酶活性导致硫酸角质素在溶酶体内积累。由于硫酸角质素主要存在于软骨和角膜中，因此该物质的积聚会导致骨骼异常和角膜混浊。研究人员认为，GAG的积累还可能通过干扰溶酶体内其他蛋白质的功能并破坏细胞内分子的运动而导致MPS IV的特征。"}]}' WHERE id = 21653;
UPDATE genes SET GHR_metadata_ch = '{"gene": "PLOD1", "conditions": [{"name": "埃勒斯-当洛二氏综合症", "detail": "已发现PLOD1gene中的30多个突变会导致一种称为后凸型的Ehlers-Danlos综合征。Ehlers-Danlos综合征是一组影响支持皮肤，骨骼，血管以及许多其他器官和组织的结缔组织的疾病。脊柱后凸型的特征是关节活动范围异常大（活动过度），肌张力弱（肌张力低下）以及脊柱严重，进行性弯曲（脊柱后凸畸形），可能会干扰呼吸。（换行）最常见的PLOD1gene突变异常复制（重复）该gene的大部分，导致产生无功能形式的赖氨酰羟化酶1酶。其他几种突变会引入阻止任何功能酶产生的过早终止信号。赖氨酰羟化酶1活性的丧失大大减少了羟赖氨酸的量，这损害了胶原分子之间的交联。胶原纤维网络的这种破坏削弱了结缔组织，导致了Ehlers-Danlos综合征的脊柱后凸型的体征和症状。"}]}' WHERE id = 21666;
UPDATE genes SET GHR_metadata_ch = '{"gene": "PYGL", "conditions": [{"name": "糖原贮积病VI型", "detail": "已发现PYGLgene中至少有17个突变引起VI型糖原贮积病（GSDVI）。大多数突变会改变肝糖原磷酸化酶中的单个蛋白质结构单元（氨基酸），从而影响该酶的正常功能。在Old Order Mennonite种群中，常见突变（写为1620+1G>a）破坏了PYGLgene指令制备酶的方式。肝糖原磷酸化酶缺陷会损害糖原的正常分解。结果，肝细胞不能利用糖原作为能量，因此肝功能受损。肝细胞内糖原分解的缺乏导致GSDVI的主要特征。"}]}' WHERE id = 21691;
UPDATE genes SET GHR_metadata_ch = '{"gene": "MAP3K1", "conditions": [{"name": "Swyer综合征", "detail": "MAP3K1gene的突变被认为占Swyer综合征病例的18%，Swyer综合征是一种影响性发育的疾病，也称为46，XY完全性腺发育不全或46，XY纯性腺发育不全。(换行)人们通常在每个细胞中有46条染色体。46条染色体中的两条，称为X和Y，被称为性染色体，因为它们有助于确定一个人是否会发展出男性或女性的性别特征。女孩和女人通常有两条X染色体（46，XX核型），而男孩和男人通常有一条X染色体和一条Y染色体（46，XY核型）。（换行）导致Swyer综合征的MAP3K1gene突变增加辅因子结合，其减少导致男性性别分化的信号传导并增强导致女性性别分化的信号传导。结果，具有典型男性46，XY核型的受影响个体将不会发育出男性性腺（睾丸），但会发育出女性生殖结构（子宫和输卵管）。"}, {"name": "乳腺癌", "detail": "遗传学家庭参考提供有关乳腺癌的信息。"}, {"name": "朗格汉斯细胞组织细胞增生症", "detail": "遗传学家庭参考提供有关朗格汉斯细胞组织细胞增生症的信息。"}, {"name": "其他疾病", "detail": "在患有46，XY性发育障碍的人中也发现了MAP3K1gene突变，这也被称为部分性腺发育不全。与引起Swyer综合征的突变相比，这些突变对与性发育相关的信号传导可能具有相似但不太明显的影响（如上所述）。受影响的个体可能具有外表不清晰的男性或女性（模棱两可的生殖器）或生殖器和生殖器官的其他异常。"}]}' WHERE id = 21700;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SMARCAD1", "conditions": [{"name": "Adermatoglyphia", "detail": "已发现SMARCAD1gene中至少有四个突变会引起皮纹，这是手和脚上没有皮纹。由于受影响的人的手指垫上没有皮肤脊，因此无法根据指纹进行识别。Adermatoglyphia可以在没有任何相关体征和症状的情况下发生，或者它可能与其他特征相关，通常影响皮肤。（换行）引起adermatoglyphia的突变影响SMARCAD1蛋白的皮肤特异性同种型，但不影响全长同种型。这些遗传变化阻止了该gene的一个拷贝产生任何功能性皮肤特异性同工型，从而减少了该蛋白在皮肤细胞中的总量。尽管尚不清楚这些遗传变化如何引起皮纹病，但研究人员推测，SMARCAD1蛋白的皮肤特异性版本的缺乏会损害正常皮肤发育和功能（包括皮纹形成）所需的信号通路。"}]}' WHERE id = 21709;
UPDATE genes SET GHR_metadata_ch = '{"gene": "LRP2", "conditions": [{"name": "Donnai-Barrow综合征", "detail": "在Donnai-Barrow综合征患者中至少鉴定出12个LRP2gene突变。据信这些突变导致不存在功能性巨蛋白。肾小管中功能性巨蛋白的缺乏导致巨蛋白的各种配体在尿液中排泄而不是被吸收回到血流中。Donnai-Barrow综合征的特征可能是由于巨蛋白无法帮助吸收这些配体，生化信号通路的破坏或无功能巨蛋白的其他作用所致。但是，尚不清楚这些异常如何导致该疾病的特定体征和症状。（换行）还发现先前被归类为称为面-眼-听诊-肾（FOAR）综合征的单独疾病的疾病是由LRP2gene突变引起的。现在认为FOAR综合征与Donnai-Barrow综合征是同一种疾病。"}, {"name": "前列腺癌", "detail": "遗传学家庭参考提供有关前列腺癌的信息。"}, {"name": "癌症", "detail": "LRP2gene中某些常见的遗传变异（多态性）可能与受影响男性前列腺癌的进展，复发和严重程度不同有关。雄激素是雄性激素，有助于癌性（恶性）前列腺肿瘤的生长，是巨蛋白的配体之一。最近的一项研究表明，前列腺肿瘤细胞可能产生增加量的巨蛋白，从而吸收更多的雄激素。增加巨蛋白活性的LRP2gene多态性可能会促进更具侵略性的肿瘤生长，而降低巨蛋白活性的多态性可能会减慢肿瘤的生长。"}, {"name": "其他疾病", "detail": "某些LRP2gene多态性也与血液中低密度脂蛋白（LDL）和胆固醇水平升高有关。LDL是血液中胆固醇的主要载体。胆固醇是一种蜡状，脂肪状物质，在体内产生并从动物食物中获得。LDL水平升高可能导致过量的胆固醇通过血液循环并积聚在血管壁上，增加一个人患心血管疾病的风险。（换行）LDL属于巨蛋白的配体，研究人员认为LRP2gene变化导致巨蛋白功能的变化可能影响LDL水平。"}]}' WHERE id = 21716;
UPDATE genes SET GHR_metadata_ch = '{"gene": "BRCA1", "conditions": [{"name": "乳腺癌", "detail": "BRCA1gene中的种系突变与乳腺癌相关，这些突变与男性和女性患乳腺癌的风险增加有关。临床症状包括乳房内或乳房附近的肿块或增厚，大小或形状的变化乳房，乳头溢液，压痛或缩回（向内转向），皮肤刺激和凹陷或鳞屑。BRCA1gene突变导致异常短的蛋白质或没有蛋白质无法修复受损的DNA，从而导致不受控制的生长和细胞分裂，从而导致肿瘤形成。"}, {"name": "卵巢癌", "detail": "BRCA1gene中的种系突变与乳腺癌风险增加相关，卵巢癌风险也增加约35-60%，通常称为遗传性乳腺癌和卵巢癌（HBOC）综合征。癌症进展表现出临床症状，包括骨盆或下腹部疼痛或沉重感，腹胀，进食时感觉充分，背痛，月经间或绝经后阴道出血，或尿或排便习惯改变。"}, {"name": "前列腺癌", "detail": "BRCA1gene的遗传突变与男性通常在中年或晚年出现的前列腺癌风险增加有关。癌症进展显示症状包括例如；难以开始或停止尿液流动，无法完全排空膀胱，尿液或精液中的血液或射精疼痛。这是由于BRCA1肿瘤抑制活性的异常丧失所致，该活性未能修复受损的DNA，从而导致不受控制的生长和细胞分裂，从而导致肿瘤形成。"}, {"name": "其他癌症", "detail": "BRCA1gene的遗传突变与胰腺癌和结肠癌的风险增加相关。异常的BRCA1蛋白失去其抑癌活性，无法修复受损的DNA，从而导致细胞生长失控和分裂，从而导致癌症的发展。"}]}' WHERE id = 21723;
UPDATE genes SET GHR_metadata_ch = '{"gene": "GNS", "conditions": [{"name": "黏多醣贮积症III型", "detail": "GNSgene中的突变会导致IIID型粘多糖贮积病（MPS IIID）。这些突变大多数会改变gene中的单个DNA结构单元（核苷酸）。所有引起MPS IIID的突变都会降低或消除N-乙酰氨基葡萄糖-6-硫酸酯酶的功能。(换行)缺乏N-乙酰葡糖胺-6-硫酸酯酶活性破坏了硫酸乙酰肝素的分解。结果，部分分解的GAG在溶酶体内积累。研究人员认为，GAG的积累会干扰溶酶体内其他蛋白质的功能，并破坏细胞的正常功能。尚不清楚为什么硫酸乙酰肝素的积累主要影响MPS IIID中的中枢神经系统。"}]}' WHERE id = 21726;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ADGRE2", "conditions": [{"name": "振动性荨麻疹", "detail": "在患有振动性荨麻疹的人中已经鉴定出ADGRE2gene中的至少一种突变，其中皮肤上的振动，重复拉伸或摩擦导致过敏症状，例如荨麻疹（荨麻疹），肿胀（血管性水肿），发红（红斑）和瘙痒（瘙痒）。可以通过毛巾擦干，拍手，跑步，在车辆中颠簸行驶或其他重复刺激来引起反应。（换行）导致振动性荨麻疹的ADGRE2gene突变，写为Cys492Tyr或C492Y，用蛋白质序列492位的氨基酸酪氨酸取代蛋白质结构单元（氨基酸）半胱氨酸。取代改变了蛋白质结构，并导致两个亚基之间的相互作用不稳定。这种脆弱的连接可以更容易地断开；皮肤的振动，摩擦或拉伸可破坏肥大细胞中亚基之间的关联。研究人员建议，一旦亚基断开，β亚基就会发出肥大细胞信号，对振动性荨麻疹中发生的皮肤过敏症状做出反应并产生过敏症状。"}]}' WHERE id = 21735;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SAMD9L", "conditions": [{"name": "共济失调-全血细胞减少综合征", "detail": "已发现SAMD9Lgene中至少有四个遗传突变会导致共济失调-全血细胞减少综合征，这是一种罕见病，会影响骨髓中的小脑和造血细胞。这种情况会导致神经系统问题，例如共济失调，难以平衡和协调。它还与全血细胞减少有关，全血细胞减少是血细胞数量减少，包括红细胞，白细胞和血小板。患有共济失调-全血细胞减少综合征的人血液中某些癌症状况的风险增加，特别是骨髓增生异常综合征和急性髓性白血病。（换行）导致共济失调-全血细胞减少综合征的突变基本上存在于身体的所有细胞中。它们被描述为“功能获得”。它们增加了SAMD9L蛋白阻断细胞生长和分裂的能力。在骨髓中，导致的细胞增殖减少导致血细胞短缺。目前尚不清楚这些突变的影响如何与共济失调和与共济失调-全血细胞减少综合征相关的其他神经系统问题相关。（换行）似乎自相矛盾的是，SAMD9Lgene的功能获得性突变增强了该蛋白的肿瘤抑制功能，可能增加发生癌症的风险，例如骨髓增生异常综合症和急性髓细胞性白血病。似乎骨髓中某些具有遗传性功能获得性SAMD9Lgene突变的细胞可以发展出其他遗传变化，这些变化与较轻的全血细胞减少症相关，但增加了患癌症的风险。这些变化包括禁用SAMD9Lgene的突变（“功能丧失”突变）或删除包含SAMD9Lgene的7号染色体长臂的一部分。这些额外的变化补偿了骨髓细胞中功能获得性突变的影响。它们可防止过度活跃的SAMD9L蛋白过度限制细胞增殖，从而降低受影响个体全血细胞减少症的严重程度。但是，第7号染色体长臂上SAMD9Lgene和其他gene的丢失可能使细胞无法控制地生长和分裂，从而导致癌症。7号染色体长臂的缺失是骨髓增生异常综合症和白血病的众所周知的危险因素。"}, {"name": "癌症", "detail": "已经发现SAMD9Lgene中的突变以称为肝细胞癌的肝癌形式存在。与引起共济失调-全血细胞减少综合征的突变（如上所述）不同，这些遗传变化是体细胞的，这意味着它们是在一个人的一生中获得的，并且仅存在于引起肿瘤的细胞中。突变被描述为“功能丧失”。它们禁用SAMD9Lgene，从而阻止SAMD9L蛋白有效调节细胞增殖。结果，肝脏中的细胞可以在没有控制或命令的情况下生长和分裂，导致癌症。"}]}' WHERE id = 21739;
UPDATE genes SET GHR_metadata_ch = '{"gene": "YWHAE", "conditions": [{"name": "Miller-Dieker syndrome", "detail": "Miller-Dieker综合征的特征性体征和症状是由17号染色​​体短（p）臂末端附近的遗传物质缺失引起的。通常删除的染色体区域包含多个gene，包括YWHAEgene。删除的结果是，患有这种情况的人每个细胞中只有一个YWHAEgene拷贝，而不是通常的两个拷贝。（换行）每个细胞中YWHAEgene的一个拷贝的缺失减少了14-3-3的量？蛋白质减少约一半。短缺14-3-3？Miller-Dieker综合征患者的蛋白质会增加lissencephaly的严重程度（大脑发育过程中大脑表面异常光滑的问题）。"}, {"name": "精神分裂症", "detail": "遗传学家庭参考提供有关精神分裂症的信息。"}, {"name": "其他疾病", "detail": "仅涉及YWHAEgene的缺失也可能导致健康问题。具有YWHAEgene缺失的人在每个细胞中缺失一个gene拷贝，这减少了14-3-3的量？大约一半产生的蛋白质。这种蛋白质的缺乏（短缺）被认为会导致身材矮小；智力障碍；独特的面部特征，包括突出的前额，宽鼻梁和小颚。具有YWHAEgene缺失的人没有小脑畸形，但倾向于患有其他脑部异常。"}]}' WHERE id = 21758;
UPDATE genes SET GHR_metadata_ch = '{"gene": "MATN3", "conditions": [{"name": "多发性骨phy发育不良", "detail": "MATN3gene中至少有14种不同的突变已显示出轻度形式的多发性骨phy发育不良。所有的突变都会改变matrilin-3区域（称为a结构域）内或附近的一个蛋白质结构单元（氨基酸）。一种遗传变化约占所有MATN3突变的40%。该突变用121位的氨基酸色氨酸取代了氨基酸精氨酸（写为Arg121Trp或R121W）。(换行)研究人员认为，MATN3gene的突变会阻止matrilin-3正确折叠。matrilin-3没有被转运到软骨细胞的细胞外基质，而是保留在内质网中。内质网是细胞内参与蛋白质加工和运输的结构。这种细胞结构最终变得如此之大以至于它不再能够正常运作，并且软骨细胞死亡。软骨细胞过早死亡导致长骨生长减少和身材矮小。"}]}' WHERE id = 21766;
UPDATE genes SET GHR_metadata_ch = '{"gene": "COL4A5", "conditions": [{"name": "阿尔波特综合症", "detail": "已经发现COL4A5gene的突变引起Alport综合征，Alport综合征是一种以肾脏瘢痕形成，肾衰竭为特征的疾病，并且还导致内耳功能异常导致听力丧失。突变严重减少或阻止了α5（IV）链的产生，导致肾脏，内耳和眼睛的基底膜中IV型胶原α3-4-5网络严重缺乏。"}]}' WHERE id = 21778;
UPDATE genes SET GHR_metadata_ch = '{"gene": "MFN2", "conditions": [{"name": "进行性神经性腓骨肌萎缩症", "detail": "研究人员已经鉴定出大约50种MFN2gene突变，这些突变会导致一种称为2A型的Charcot-Marie-Tooth病。几乎所有这些突变都会改变mitofusin 2中的单个蛋白质结构单元（氨基酸）。这些遗传变化改变了线粒体融合蛋白2的关键区域，并且该蛋白无法正常发挥功能。一些突变会在制作mitofusin 2的说明中产生过早的终止信号。结果，不产生蛋白质，或产生异常小的蛋白质。（换行）几个MFN2gene突变导致影响视力的2A型Charcot-Marie-Tooth病的变体。（此变体也称为遗传性运动和感觉神经病VI。）视力丧失是由将信息从眼睛传递到大脑的神经变性引起的（视神经萎缩）。具有这种变异的人通常会在10岁之前经历严重的Charcot-Marie-Tooth病症状。(换行)目前尚不清楚MFN2gene突变如何导致2A型Charcot-Marie-Tooth病特有的神经问题。研究人员认为，如果没有功能性的mitofusin 2，线粒体不能正常融合或在细胞内正常移动，这可能会破坏细胞的能量供应。神经细胞可能对中断的能量供应特别敏感。"}]}' WHERE id = 21786;
UPDATE genes SET GHR_metadata_ch = '{"gene": "LAMA2", "conditions": [{"name": "LAMA2相关的肌营养不良症", "detail": "在患有LAMA2相关性肌营养不良症的个体中已鉴定出100多个LAMA2gene突变，该疾病会导致骨骼肌无力和消瘦（萎缩）。这种情况通常以两种方式之一出现：严重的早发型或较轻的晚发型。大多数导致早发性LAMA2相关性肌营养不良症的LAMA2gene突变导致缺乏功能性层粘连蛋白α-2亚基。导致迟发性LAMA2相关性肌营养不良的突变通常会导致功能性层粘连蛋白α-2亚基减少（缺乏）。层粘连蛋白α-2亚基的缺乏或缺乏会导致层粘连蛋白2和层粘连蛋白4的相应缺乏，从而降低肌肉组织的强度和稳定性，并导致LAMA2相关性肌营养不良的体征和症状。"}]}' WHERE id = 21794;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ABCA1", "conditions": [{"name": "家族性HDL缺乏症", "detail": "ABCA1gene的突变会导致一种称为家族性高密度脂蛋白（HDL）缺乏症的疾病，其特征是血液中HDL水平降低和心血管疾病的早期发作。单个拷贝的改变gene足以引起这种缺陷；但是，改变后的gene的两个副本通常会导致更严重的相关疾病形式，称为丹吉尔病。"}, {"name": "丹吉尔病", "detail": "ABCA1gene的突变导致称为丹吉尔病的严重病症，其特征在于由于抑制胆固醇和磷脂释放而导致的低HDL水平，导致组织增大的细胞积聚，扁桃体获得导致细胞功能受损的黄橙色，细胞死亡并增加患心血管疾病的风险。"}]}' WHERE id = 21800;
UPDATE genes SET GHR_metadata_ch = '{"gene": "AGXT", "conditions": [{"name": "原发性高草酸尿症", "detail": "据报道，AGXTgene的几种变异会导致1型原发性高草酸尿症，这种情况是由于丙氨酸-乙醛酸氨基转移酶缺陷导致所谓草酸盐的过量生产而引起的，这会损害乙醛酸向甘氨酸的转化。过量的草酸盐会导致肾脏和膀胱结石，从儿童期到成年早期，任何年龄段都会出现肾脏疾病。它还会导致全身性草酸沉着症，影响骨骼和血管壁。"}]}' WHERE id = 21830;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ACSF3", "conditions": [{"name": "丙二酸和甲基丙二酸尿症合并", "detail": "ACSF3gene的突变与一种称为丙二酸和甲基丙二酸尿症（CMAMMA）的疾病有关，该疾病的特征是体内丙二酸和甲基丙二酸水平升高。这种情况导致发育，从儿童期开始的生长问题和从成年期开始的神经系统问题。"}]}' WHERE id = 21853;
UPDATE genes SET GHR_metadata_ch = '{"gene": "FAH", "conditions": [{"name": "酪氨酸血症", "detail": "已发现至少86种导致I型酪氨酸血症的FAH突变。这种疾病的特征是严重的肝肾疾病，神经系统疾病以及从婴儿期开始的其他体征和症状。导致这种情况的改变的FAHgene产生不稳定或失活的酶，导致富马酸乙酰乙酸水解酶活性降低或缺失。最常见的FAH突变破坏了gene指令用于制备酶的方式。该突变（写为IVS12+5G>A）称为剪接位点突变，可导致异常短的酶。如果没有足够的富马酸乙酰乙酸水解酶活性，酪氨酸及其副产物就无法正确分解。结果，富马酸乙酰乙酸酯积聚在肝脏和肾脏中。认为富马酸乙酰乙酸盐水平升高对细胞有毒，并且该物质的积累可能引起肝脏和肾脏问题以及I型酪氨酸血症特征的其他特征。（换行）在几种I型酪氨酸血症病例中，FAHgene已经观察到突变在一些肝细胞中恢复到正常状态。如果有足够的细胞具有产生正常富马酸乙酰乙酸水解酶的回复gene，则可以达到一定水平的酶活性。研究人员发现症状的严重程度与肝细胞逆转程度之间存在相关性。I型酪氨酸血症症状严重的人几乎没有回复的细胞，而症状较轻的人则有许多带有回复的FAHgene的细胞。"}]}' WHERE id = 21854;
UPDATE genes SET GHR_metadata_ch = '{"gene": "GRM6", "conditions": [{"name": "常染色体隐性先天性静止性夜盲", "detail": "已发现GRM6gene中至少有25个突变会导致常染色体隐性先天性固定性夜盲，其特征是无法在弱光和其他视力问题（例如近视（近视））中看到。大多数GRM6gene突变会通过改变蛋白质中的单个蛋白质结构单元（氨基酸）来损害mGluR6蛋白质的功能。这些突变阻止蛋白质到达需要与谷氨酸结合的细胞膜。在细胞表面没有任何mGluR6蛋白的情况下，双极细胞无法检测到低光照下杆状细胞释放的谷氨酸，因此不会传输视觉信号。大脑没有接收到杆发送的视觉信息，导致难以在弱光下看到。"}]}' WHERE id = 21856;
UPDATE genes SET GHR_metadata_ch = '{"gene": "EGFR", "conditions": [{"name": "肺癌", "detail": "EGFRgene中至少有8个突变与肺癌有关。肺癌是一种肺部某些细胞变得异常并无法控制地繁殖形成肿瘤的疾病。肺癌在其早期阶段可能不会引起体征或症状。几乎所有这些EGFRgene突变都发生在一个人的一生中（体细胞），并且仅存在于癌细胞中。（换行）EGFRgene中的体细胞突变最常发生在称为非小细胞肺癌的一种肺癌中，特别是称为腺癌的形式。这些突变在从未吸烟的疾病患者中最常见。体细胞EGFRgene突变在亚洲人群中比在白人人群中更常见，在30%至40%的受影响亚洲人中发生，而白人肺癌则为10%至15%。（换行）与肺癌相关的大多数体细胞EGFRgene突变删除称为外显子19的gene部分中的遗传物质或改变称为外显子21的另一区域中的DNA构建块（核苷酸）。这些gene变化导致受体蛋白不断开启（组成性激活），即使它不与配体结合。结果，细胞不断接收信号以增殖和存活，导致肿瘤形成。当这些遗传变化发生在肺部细胞中时，肺癌就会发展。其他遗传，环境和生活方式因素会导致一个人的癌症风险。(换行)具有EGFRgene突变的肺癌倾向于对特异性靶向过度活跃的表皮生长因子受体蛋白的治疗作出反应，所述表皮生长因子受体蛋白允许癌细胞不断生长和分裂。"}]}' WHERE id = 21858;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ADAMTS2", "conditions": [{"name": "埃勒斯-当洛二氏综合症", "detail": "在患有Ehlers-Danlos综合征（称为皮肤稀疏症类型）的人中，已经鉴定出ADAMTS2gene的几种突变，该突变是由于ADAMTS2酶的缺陷/无功能而引起的，该酶削弱了其正确处理胶原蛋白的活性。这种类型的综合症的特征是皮肤柔软，脆弱，凹陷和皱纹，容易瘀伤，并具有独特的面部特征。"}]}' WHERE id = 21882;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ADGRG1", "conditions": [{"name": "多小脑回", "detail": "在患有严重形式的多微gyria（称为双侧额顶多微gyria（BFPP））的人中，已经鉴定出ADGRG1gene中至少有11个突变。这种疾病会导致智力残疾，发育迟缓，言语和运动问题以及反复发作（癫痫病）。大多数已识别的突变会改变ADGRG1蛋白中的单个蛋白结构单元（氨基酸）。这些突变干扰蛋白质的正常加工。异常蛋白质被困在细胞内，无法到达细胞表面以执行其正常的信号传导功能。(换行)ADGRG1蛋白功能的丧失可能会破坏发育中的大脑中神经元的正常迁移。结果，大脑皮层的某些区域？被称为额叶和顶叶的区域？形成过多的褶皱（称为回旋），褶皱异常小。这些脑部异常会导致智力残疾和与双侧额顶多菌小gyria相关的其他神经系统问题。"}]}' WHERE id = 21895;
UPDATE genes SET GHR_metadata_ch = '{"gene": "FKTN", "conditions": [{"name": "福山型先天性肌营养不良症", "detail": "已发现FKTNgene中至少有18个突变会导致福山先天性肌营养不良，这种情况会导致骨骼肌无力以及脑和眼异常。这种形式的先天性肌营养不良症几乎只在日本出现。实际上，患有这种情况的每个人都至少有一个相同突变的拷贝，即在FKTNgene中插入了约3000个额外的DNA构建块（3 kb[kb]）。这种插入发生在称为3''非翻译区的gene的一部分中，这有助于调节gene的活性。研究人员认为，3 kb的插入减少了该gene产生的福建蛋白的量。(换行)福库汀的缺乏会阻止正常的糖基化吗-dystroglycan。结果是-dystroglycan不再能够有效地将细胞锚定在周围的蛋白质和其他分子上。没有功能-dystroglycan为了稳定肌肉纤维，当它们反复收缩并使用时放松时会受到损坏。受损的纤维会随着时间的推移而减弱和死亡，从而影响福山先天性肌营养不良症患者骨骼肌的发育，结构和功能。(换行)有缺陷-dystroglycan还影响大脑早期发育过程中神经元的迁移。一些神经元不是在到达预定目的地时停止，而是通过大脑表面迁移到周围充满液体的空间。研究人员认为，神经元迁移的这个问题会导致大脑异常，称为鹅卵石脑畸形，其中表面大脑缺乏正常的褶皱和凹槽，而是显得颠簸和不规则。关于FKTN突变在身体其他部位的影响知之甚少。"}, {"name": "肢带型肌营养不良症", "detail": "遗传学家庭参考资料提供有关肢带型肌营养不良症的信息。"}, {"name": "沃克-沃伯格综合症", "detail": "在患有Walker-Warburg综合征（最严重的先天性肌营养不良症）的人中，至少鉴定出11种FKTNgene突变。这种情况在全世界的人群中都有发现。像福山先天性肌营养不良症（如上所述），沃克-沃伯格综合症与骨骼肌无力以及眼和脑异常有关，包括鹅卵石性小脑畸形；但是，患有Walker-Warburg综合征的人的大脑和眼睛异常更为严重，仅生活在婴儿期或儿童早期。与这种情况相关的FKTNgene突变会阻止任何功能性福建蛋白的产生，从而导致在Walker-Warburg综合征中出现严重的肌肉，眼睛和大脑问题。"}, {"name": "其他疾病", "detail": "FKTNgene的突变会导致其他影响骨骼肌和心脏的疾病。与福山先天性肌营养不良症（如上所述）主要限于日本人群不同，这些疾病已在全球多个人群中得到描述。(换行)已发现几名患有扩张型心肌病的心脏病患者的FKTNgene突变。这种情况会削弱并扩大心脏，阻止其有效抽血。当扩张型心肌病与FKTNgene突变相关时，它被称为1X型（DCM1X）。除心脏问题外，一些患有DCM1X的人从成年开始就经历了轻度的骨骼肌无力。(换行)FKTNgene的变化减少但不消除福建蛋白的产生，导致在DCM1X和肢带型肌营养不良症中出现的严重医学问题有所减轻（与上文相关）。"}]}' WHERE id = 21905;
UPDATE genes SET GHR_metadata_ch = '{"gene": "CCND2", "conditions": [{"name": "巨脑-多小脑回-多指-脑积水综合征", "detail": "已发现CCND2gene中至少有七个突变会引起巨脑-多微gyria-多指-脑积水（MPPH）综合征。这种罕见的情况会影响大脑的发育，导致异常大的大脑和头部大小（大头畸形）以及称为双侧肩周聚小胶质细胞增多症（BPP）的大脑异常。一些受影响的人的一只或多只手或脚上也有额外的手指或脚趾（多指）。（换行）每个已知的突变都会改变细胞周期蛋白D2蛋白中的单个蛋白结构单元（氨基酸）。这些变化可防止蛋白质在不再需要时被分解（降解）。细胞中细胞周期蛋白D2的最终积累会触发它们继续分裂，否则它们将不会分裂，从而导致异常的细胞增殖。在大脑中，细胞数量的增加导致出生前开始的大脑快速异常生长。尚不清楚细胞周期蛋白D2的积累如何促进多指，尽管多余的手指可能与发育中的手和脚中异常的细胞增殖有关。"}]}' WHERE id = 21927;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ANKRD11", "conditions": [{"name": "KBG综合征", "detail": "已发现几种ANKRD11gene突变会导致KBG综合征，该疾病的特征是上前牙较大和其他异常的面部特征，骨骼异常和智力残疾。这些突变大多数会导致异常短的ANKRD11蛋白，该蛋白可能几乎没有功能。人们认为这种蛋白质功能的降低是该病的体征和症状的基础。由于人们认为ANKRD11在神经元和大脑发育中起重要作用，因此研究人员推测其功能的部分丧失可能导致KBG综合征的发育迟缓和智力障碍。但是，该机制尚不完全清楚。还不清楚ANKRD11功能的丧失如何导致该病的骨骼特征。（换行）另一种影响ANKRD11gene的突变，称为16q24.3微缺失，从指定q24.3区域的16号染色体上删除了遗传物质。缺失的区域通常会删除ANKRD11和ZNF778gene，尽管其他附近的gene也可能受到影响。具有这种类型突变的人的体征和症状与KBG综合征相似，包括异常的面部特征和智力障碍。许多人还具有大脑异常和自闭症谱系障碍的特征，其特征是沟通和社交能力受损。一些研究人员认为，这些微缺失与KBG综合征的差异足以被视为一种单独的疾病，称为16q24.3微缺失综合征。"}]}' WHERE id = 21929;
UPDATE genes SET GHR_metadata_ch = '{"gene": "MTM1", "conditions": [{"name": "X连锁肌管肌病", "detail": "已发现MTM1gene中的200多个突变引起X连锁肌管肌病。一些MTM1gene突变会改变肌管蛋白中的一种蛋白质结构单元（氨基酸），而其他突变会导致异常短的无功能酶。阻止任何功能性肌管蛋白产生的MTM1gene突变倾向于导致更严重的疾病。轻度受影响的个体倾向于具有允许产生一些功能性肌管蛋白的MTM1突变。MTM1gene中的（换行）突变被认为破坏了肌管蛋白在肌肉细胞发育和维持中的作用，导致肌肉无力以及X连锁肌管肌病的其他体征和症状。"}]}' WHERE id = 21947;
UPDATE genes SET GHR_metadata_ch = '{"gene": "YY1AP1", "conditions": [{"name": "格兰奇综合症", "detail": "在格兰奇综合征患者中，YY1AP1gene至少鉴定出五个突变。这种罕见的情况会导致血管变窄（狭窄）和阻塞（闭塞），从而向许多器官和组织（包括肾脏，大脑和心脏）供血。格兰奇综合症还可能与手指和脚趾短（近距离），某些手指或脚趾融合（近距离），容易断裂的脆弱骨骼，学习障碍和心脏缺陷有关。YY1AP1gene中的（换行）突变被描述为“功能丧失”，因为它们阻止了任何功能性YY1AP1蛋白的产生。这种蛋白质的丧失破坏了INO80染色质重塑复合物的功能，从而改变了平滑肌细胞中多个gene的活性。研究人员怀疑这些gene表达的变化导致细胞增殖和分化减少。然而，目前还不清楚这些变化如何导致动脉狭窄和阻塞。还不清楚YY1AP1gene突变如何与Grange综合征的其他特征相关，例如骨骼异常和学习障碍。"}]}' WHERE id = 21957;
UPDATE genes SET GHR_metadata_ch = '{"gene": "FGF23", "conditions": [{"name": "遗传性低磷佝偻病", "detail": "已经发现FGF23gene中的至少三个突变引起罕见形式的遗传性低磷酸盐血症性rick病，称为常染色体显性低磷酸盐血症性rick病。这些突变改变了成纤维细胞生长因子23中的单个蛋白质结构单元（氨基酸），从而阻止了蛋白质的裂解。结果，蛋白质没有失活，并且增加量的全长活性蛋白质在血流中循环。成纤维细胞生长因子23的过度活跃会减少肾脏对磷酸盐的重吸收，从而导致血液中磷酸盐水平低（低磷血症）以及常染色体显性低磷病患者骨骼生长的相关问题。"}, {"name": "高磷酸盐血症家族性肿瘤性钙化病", "detail": "已经发现FGF23gene中至少有7个突变引起高磷血症性家族性肿瘤性钙质沉着症（HFTC），其特征在于血液中磷酸盐水平增加（高磷酸盐血症）和体内磷酸盐和钙的异常沉积（钙质沉着症）。组织。FGF23gene的突变导致功能降低的蛋白质的产生。这种非功能性蛋白质在细胞中迅速分解，导致缺乏可用的成纤维细胞生长因子23。这种蛋白质短缺减少了信号传导并增加了由肾脏重新吸收回到血流中的磷酸盐的量，导致高磷血症。当过量的磷酸盐与钙结合形成积聚在软组织中的沉积物时，就会产生钙质沉着症。"}]}' WHERE id = 21980;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SPG7", "conditions": [{"name": "痉挛性截瘫7型", "detail": "已发现导致痉挛性截瘫7型的10多种突变。这些突变大多数会改变截瘫患者的单个蛋白质结构单元（氨基酸）。当截瘫突变时，它不能与线粒体内的其他蛋白质组织形成m-AAA蛋白酶。由无功能的m-AAA蛋白酶引起的神经细胞中无法使用的蛋白质的积累会损害线粒体功能并减少神经细胞信号传导，从而导致7型痉挛性截瘫的主要体征和症状。"}, {"name": "进行性眼外肌麻痹", "detail": "遗传学家庭参考资料提供有关进行性眼外肌麻痹的信息。"}]}' WHERE id = 21987;
UPDATE genes SET GHR_metadata_ch = '{"gene": "LEMD3", "conditions": [{"name": "Buschke-Ollendorff综合征", "detail": "已发现LEMD3gene中至少有23个突变会引起Buschke-Ollendorff综合征，这是一种罕见的结缔组织疾病。（结缔组织为整个身体的结构提供强度和灵活性。）该病症的特征在于结缔组织痣和骨质疏松症。结缔组织痣是皮肤上的小的非癌性肿块，广泛存在于患有这种疾病的人中。骨性关节炎是一种骨骼异常，其特征是在x射线上可以看到小而圆形的骨密度增加区域。（换行）每个已知的LEMD3gene突变都会阻止每个细胞中该gene的一个拷贝产生功能性LEMD3蛋白，从而使LEMD3蛋白的总量减少约一半。这种蛋白质的短缺阻止了它控制BMP和TGF-？有效地发出信号，导致通过这两种途径的信号增强。研究表明，增强的信号传导增加了骨组织的形成，导致骨密度过高的区域。尚不清楚异常信号如何与Buschke-Ollendorff综合征患者结缔组织痣的发展相关。"}, {"name": "其他疾病", "detail": "LEMD3gene突变也发现于孤立性骨性角化病患者中，该病涉及骨密度增加的区域，该区域没有结缔组织痣而表现为Buschke-Ollendorff综合征。有时，骨质疏松症会与另一种更为严重的骨骼异常（称为骨髓增生症）一起发生。这种异常导致骨骼的外层变得过于厚而致密，并且往往局限于一只手臂或一条腿。骨化不全可能与疼痛，僵硬和关节活动受限有关，并随着时间的推移而恶化。(换行)研究人员正在努力确定LEMD3gene突变如何在某些人中引起Buschke-Ollendorff综合征，而在另一些人中引起孤立的骨性骨质疏松症或骨性骨质疏松症伴骨髓增生症。在某些情况下，已发现相同的突变会在单个家庭的不同成员中引起所有这些情况。每个已知的突变都会减少细胞中产生的功能性LEMD3蛋白的量，从而异常增强BMP和TGF-？发信号并导致骨密度增加的区域。"}]}' WHERE id = 21992;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ELP1", "conditions": [{"name": "家族性自主神经功能障碍", "detail": "几乎所有患有家族性自主神经功能障碍的个体在每个细胞中都有两个相同突变的拷贝。这种突变可以破坏ELP1gene中的信息在转录过程中如何剪接在一起。由于这种剪接错误，产生减少量的ELP1蛋白。但是，此突变的行为不一致。一些细胞产生接近正常量的ELP1蛋白，而其他细胞？特别是脑细胞？几乎没有蛋白质。(换行)在少数报道的家族性自主神经功能障碍病例中，研究人员发现了其他突变，这些突变改变了ELP1蛋白中的一种蛋白结构单元（氨基酸）。在这些情况下，氨基酸精氨酸被696位的氨基酸脯氨酸取代（写为Arg696Pro），或者氨基酸脯氨酸被914位的氨基酸亮氨酸取代（写为Pro914Leu）。具有这些不适当的氨基酸取代之一的人也具有剪接突变。这些突变一起损害ELP1蛋白。（换行）目前尚不清楚ELP1gene突变如何导致家族性自主神经功能障碍的体征和症状。ELP1蛋白含量的减少可能通过破坏细胞骨架和细胞运动而损害神经细胞的生长和发育。"}]}' WHERE id = 22015;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ALG12", "conditions": [{"name": "ALG12先天性糖基化疾病", "detail": "ALG12gene的突变已被发现引起ALG12先天性糖基化障碍（ALG12-CDG），这是一种拥有属性生长发育迟缓、肌张力弱（低张力）以及其他影响许多身体系统的体征和症状的疾病，因为有缺陷/异常的酶会损害糖基化过程。"}]}' WHERE id = 22035;
UPDATE genes SET GHR_metadata_ch = '{"gene": "TAF1", "conditions": [{"name": "X连锁肌张力障碍-帕金森病", "detail": "在X连锁性肌张力障碍-帕金森病患者中，已经确定了TAF1/DYT3多转录系统的一些变化。一些改变gene中的单个DNA构建块（核苷酸）；这些变化被描述为疾病特异性单核苷酸变化（DSC）。另一个遗传变化从gene中删除了少量核苷酸。研究人员不确定这些变化如何与疾病的运动异常特征相关。（换行）X连锁性肌张力障碍-帕金森病也可能与TAF1/DYT3多转录系统中额外的DNA片段有关。额外的片段来自反转录转座子的插入，反转录转座子是一小段DNA，可以移动到细胞遗传物质的不同位置。当逆转录转座子将自身插入gene内或gene附近时，它可能破坏gene的功能。在这种情况下，反转录转座子的插入可能会干扰TAF1/DYT3多转录系统的正常功能。(换行)研究人员怀疑TAF1/DYT3多转录系统的变化会破坏神经元关键gene的调控。这种缺陷导致这些细胞最终死亡，特别是在称为尾状核和壳核的大脑区域。这些区域对于正常运动，学习和记忆至关重要。目前尚不清楚为什么TAF1/DYT3多转录系统变化的影响似乎仅限于肌张力障碍和帕金森病。"}]}' WHERE id = 22042;
UPDATE genes SET GHR_metadata_ch = '{"gene": "NOTCH3", "conditions": [{"name": "伴有皮质下梗死和白质脑病的脑常染色体显性遗传性动脉病", "detail": "已发现NOTCH3gene中的270多个突变会导致大脑常染色体显性遗传性动脉病，并伴有皮层下梗塞和白质脑病，通常称为CADASIL。几乎所有这些突变都会改变NOTCH3蛋白中的单个蛋白结构单元（氨基酸）。涉及大多数突变的氨基酸是半胱氨酸。在NOTCH3蛋白的某个区域（称为EGF样结构域）中添加或删除半胱氨酸分子可能会影响血管平滑肌细胞中NOTCH3的功能。NOTCH3功能的破坏可导致这些细胞的自我破坏（凋亡）。血管平滑肌细胞的损伤被认为会导致中风复发和CADASIL的其他体征和症状。"}, {"name": "外侧脑膜膨出综合征", "detail": "在外侧脑膜膨出综合征患者中至少鉴定出六个NOTCH3gene突变。这种疾病影响神经系统，骨骼和肌肉以及其他身体系统。该病的特征是异常，称为外侧脑膜膨出。外侧脑膜膨出是通过脊柱（椎骨）中的间隙围绕脊髓（称为脑膜）的膜的突起。突起最常见，通常在下脊柱较大。与这种疾病相关的脑膜膨出可能会损害从脊柱扩散到身体其他部位的神经。（换行）引起外侧脑膜膨出综合征的突变发生在称为外显子33的区域的gene末端。这些gene突变导致NOTCH3蛋白具有异常短的（截短的）NICD。缩短的蛋白质缺少通常导致NICD在细胞核中发挥功能并不再需要后通常导致NICD分解的部分。结果，细胞中NICD的存在延长，并且蛋白质继续影响其他gene的活性。然而，这种延长的NICD活动的结果及其与外侧脑膜膨出综合征的具体特征的联系尚不清楚。"}]}' WHERE id = 22059;
UPDATE genes SET GHR_metadata_ch = '{"gene": "NLRP12", "conditions": [{"name": "家族性冷性自身炎症综合征", "detail": "在瓜德罗普岛加勒比海群岛的家族性冷性自身炎综合征家族中，已经鉴定出NLRP12gene的两个突变。这些突变似乎降低了monarch-1蛋白抑制炎症反应的能力，导致在这种疾病中出现发热和炎症。"}]}' WHERE id = 22062;
UPDATE genes SET GHR_metadata_ch = '{"gene": "GNA11", "conditions": [{"name": "常染色体显性低钙血症", "detail": "在常染色体显性遗传性低钙血症2型个体中，至少发现了GNA11gene的5个突变。这种情况的特点是血液中钙含量低（低钙血症）。这种情况下涉及的突变改变了G？中的单个蛋白质结构单元（氨基酸）？11.这些遗传变化被称为激活突变，因为改变的α亚基过度活跃，即使水平非常低，也会发出阻止钙水平升高的信号。结果，血液中的钙水平异常低，导致低钙血症。低钙血症可引起肌肉痉挛和癫痫发作，尽管约有一半的患者没有相关的健康问题。(换行)因为过度活跃的G？11信号阻断甲状旁腺激素的产生，血液中这种激素的水平可能异常低，这被称为甲状旁腺功能减退症。因此，常染色体显性遗传性低钙血症有时被称为常染色体显性遗传性甲状旁腺功能减退症。"}, {"name": "癌症", "detail": "引起常染色体显性遗传性低钙血症的gene突变（如上所述）通常是遗传性的，并存在于体内的每个细胞中（称为种系突变）。然而，一些gene突变不是遗传的，而是在一个人的一生中获得的。这些变化称为体细胞突变，仅存在于某些细胞中。在眼睛的癌性肿瘤葡萄膜黑色素瘤中发现了GNA11gene的体细胞突变。这些肿瘤发生在眼睛的有色部分（虹膜）或称为睫状体和脉络膜的相关组织中（统称为这些组织称为葡萄膜）。不太常见的是，GNA11gene突变与一种称为蓝色痣的皮肤肿瘤有关，因其特有的蓝色外观而得名。这种类型的增长通常是非癌性的。GNA11gene突变正在激活，导致产生过度活跃的G？11刺激葡萄膜或皮肤中色素生成细胞（黑素细胞）不受控制的增殖的蛋白质。(换行)葡萄膜黑色素瘤或蓝色痣患者的血液中钙水平正常，患有遗传性GNA11相关钙浓度紊乱的人似乎没有增加患这些类型肿瘤之一的风险。"}, {"name": "其他疾病", "detail": "GNA11gene中的种系突变也与钙浓度异常有关。损害G的遗传变化？11信号导致家族性低钙血症性高钙血症2型。与常染色体显性遗传性低钙血症（如上所述）相反，这种情况的特征是血液中钙水平高（高钙血症）和尿液中钙水平低（低钙尿症）。因为G？11阻断甲状旁腺激素释放的信号受损，血液中的钙水平升高，导致高钙血症。受影响的个体通常没有与该病症相关的症状。"}]}' WHERE id = 22089;
UPDATE genes SET GHR_metadata_ch = '{"gene": "TREX1", "conditions": [{"name": "Aicardi-Goutières综合征", "detail": "在患有Aicardi-Goutières综合征的人中已经鉴定出TREX1gene中至少82个突变，Aicardi-Goutières综合征是一种涉及严重脑功能障碍（脑病），皮肤损伤和其他健康问题的疾病。据信大多数这些突变阻止了3-prime修复核酸外切酶1酶的产生。研究人员认为，这种酶的缺失导致细胞中不需要的DNA和RNA的积累。这些DNA和RNA分子可能被细胞误认为病毒入侵者的遗传物质，引发免疫系统反应，损害大脑，皮肤和其他器官和系统，并导致Aicardi-Goutières综合征的体征和症状。"}, {"name": "系统性红斑狼疮", "detail": "遗传学家庭参考提供有关系统性红斑狼疮的信息。"}, {"name": "其他疾病", "detail": "在患有其他涉及免疫系统疾病的人中也发现了TREX1gene的突变。这些疾病包括称为系统性红斑狼疮（SLE）的慢性炎症性疾病，包括主要影响皮肤的称为冻疮狼疮的罕见形式的SLE。SLE的特征，特别是冻疮狼疮，通常也发生在患有Aicardi-Goutières综合征的人中（如上所述）。（换行）TREX1gene突变也被发现患有称为常染色体显性视网膜血管病变伴脑白质营养不良的疾病，其影响排列在眼后部（视网膜）的专门光敏组织中的大脑和血管）。(换行)与Aicardi-Goutières综合征一样，3-prime修复核酸外切酶1酶的缺失或功能受损可能导致免疫系统功能障碍，损害大脑，皮肤，血管和身体其他部位。目前尚不清楚为什么同一gene的突变会导致几种不同的疾病。"}]}' WHERE id = 22091;
UPDATE genes SET GHR_metadata_ch = '{"gene": "THPO", "conditions": [{"name": "原发性血小板增多症", "detail": "在原发性血小板增多症患者中发现了THPOgene的几个突变，这种疾病的特征是血液中血小板数量增加。由于血小板是参与凝血的血细胞碎片，因此原发性血小板增多症患者常见凝血异常（血栓形成）。（换行）THPOgene突变发现于具有称为家族性原发性血小板增多症的遗传形式的家族中。这些突变影响通常阻断（抑制）血小板生成素蛋白产生的gene区域（称为翻译的过程）。THPOgene突变导致蛋白质翻译增加。过量的蛋白质可异常激活血小板生成素受体和信号传导途径，导致巨核细胞过量产生和血小板数量增加。过量的血小板可引起血栓形成，导致原发性血小板增多症的许多体征和症状。"}]}' WHERE id = 22103;
UPDATE genes SET GHR_metadata_ch = '{"gene": "COL8A2", "conditions": [{"name": "Fuchs内皮营养不良", "detail": "已发现COL8A2gene中至少有两个突变会引起Fuchs内皮营养不良，这是一种以视力逐渐模糊和对强光敏感为特征的眼部疾病。COL8A2gene突变与Fuchs内皮营养不良的罕见，早发变异有关，其中视力问题通常始于二十多岁。（换行）导致Fuchs内皮营养不良的早期发作变体的COL8A2gene突变取代了α2（VIII）胶原蛋白中的单个蛋白质构建块。一个突变用位置450处的氨基酸色氨酸替换氨基酸亮氨酸（写为Leu450Trp或L450W）。另一个突变用位置455的氨基酸赖氨酸（Gln455Lys或Q455K）取代了氨基酸谷氨酰胺。这些突变削弱了α2（VIII）胶原的结构，可能阻止了异常蛋白掺入VIII型胶原纤维中。结果，角膜中的VIII型胶原蛋白数量减少，特别是在Descemet膜中。这种异常的Descemet膜导致角膜内皮细胞死亡，导致角膜因液体肿胀。随着时间的流逝，角膜内皮细胞继续死亡，这会导致Fuchs内皮营养不良的早期发作人群出现视力问题。"}]}' WHERE id = 22155;
UPDATE genes SET GHR_metadata_ch = '{"gene": "TNFRSF13B", "conditions": [{"name": "常见的可变免疫缺陷", "detail": "TNFRSF13Bgene中的25个以上突变与常见的可变免疫缺陷症（CVID）相关。这种情况会损害免疫系统，导致反复感染的风险增加；自身免疫性疾病，当免疫系统发生故障并攻击身体的组织和器官时发生；和某些癌症。（换行）大多数与CVID相关的TNFRSF13Bgene突变会改变TACI蛋白中的单个蛋白结构单元（氨基酸）。在CVID患者中最常见的突变是将TACI蛋白（写为Cys104Arg或C104R）中第104位的氨基酸精氨酸替换为半胱氨酸。这种突变削弱了TACI与其他蛋白质相互作用，破坏细胞信号传导并阻止正常B细胞​​成熟和抗体产生的能力。某些抗体的短缺（缺乏）使得人们难以抵抗感染。随着时间的推移，免疫反应异常和不足可能会导致CVID患者的癌症风险增加。(换行)一些具有TNFRSF13Bgene突变的人不会出现CVID的体征和症状。在这些个体中，可能需要其他遗传或环境因素才能发生这种情况。"}]}' WHERE id = 22217;
UPDATE genes SET GHR_metadata_ch = '{"gene": "MLC1", "conditions": [{"name": "皮质下囊肿的大脑白质脑病", "detail": "已发现MLC1gene中的80多个突变会引起1型皮层下囊肿的大脑白质脑病；这种类型占所有案件的75%。这种情况会影响大脑的发育和功能，导致运动和反复发作的问题。导致这种情况的大多数MLC1gene突变都会改变MLC1蛋白中的单个蛋白结构单元（氨基酸）。这些变化改变了MLC1蛋白的结构或阻止了细胞产生任何蛋白。尚不清楚星形胶质细胞连接处缺乏MLC1蛋白如何损害大脑发育和功能，从而导致1型皮质下囊肿的大脑白质脑病的体征和症状。"}]}' WHERE id = 22226;
UPDATE genes SET GHR_metadata_ch = '{"gene": "WNT5A", "conditions": [{"name": "罗宾诺综合症", "detail": "已发现WNT5Agene中至少有四个突变会导致Robinow综合征的常染色体显性遗传形式，这种情况会影响身体许多部位，尤其是骨骼的发育。常染色体显性遗传意味着每个细胞中一个拷贝的改变的gene足以引起疾病。每个已知的突变都会改变WNT5A蛋白中的单个蛋白构建块（氨基酸）。这些突变改变了蛋白质的结构，这可能会影响其与Wnt信号传导中涉及的其他蛋白质的相互作用。这些信号通路的损伤破坏了许多器官和组织的正常发育，导致了Robinow综合征的特征。"}]}' WHERE id = 22228;
UPDATE genes SET GHR_metadata_ch = '{"gene": "IGFBP7", "conditions": [{"name": "视网膜动脉大动脉瘤伴瓣上肺动脉狭窄", "detail": "在患有瓣膜上肺动脉狭窄（RAMSVPS）的视网膜动脉大动脉瘤患者中已鉴定出单个IGFBP7gene突变，该疾病会影响眼睛和心脏的血管。在受影响的个体中，突变发生在每个细胞中gene的两个拷贝中。该突变写为830-1G>A，称为剪接位点突变，这意味着它影响蛋白质拼接的方式。由突变的IGFBP7gene产生的蛋白质异常短并且不能正常发挥功能。(换行)如果没有正常运作的IGFBP7蛋白来控制BRAF信号传导，则该信号传导增加。目前尚不清楚这种增加与RAMSVPS中发生的特定血管异常有何关系，或者为什么这些异常仅限于眼睛和肺动脉。研究人员建议，身体各个部位正常水平的IGFBP7蛋白差异或不同组织中功能相似的其他蛋白的存在可能解释了该疾病的特定体征和症状。"}]}' WHERE id = 22256;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ARSB", "conditions": [{"name": "黏多醣贮积症VI型", "detail": "已经发现ARSBgene的突变引起粘多糖贮积症VI型（MPS VI），这是由于芳基硫酸酯酶B活性的降低/消除导致GAG积累而引起的病症，其消除溶酶体活性，引发炎症和细胞死亡。这种疾病的临床特征包括大头畸形，脑积水，明显的面部特征，大舌（巨舌症），心脏瓣膜异常，肝脾肿大，脐疝或腹股沟疝导致频繁的上呼吸道感染，睡眠呼吸暂停，角膜通常变浑浊导致显着的视力丧失，复发性耳部感染和听力丧失。"}]}' WHERE id = 22258;
UPDATE genes SET GHR_metadata_ch = '{"gene": "AKT1", "conditions": [{"name": "变形杆菌综合征", "detail": "已发现AKT1gene中至少有一个突变会引起变形杆菌综合征，这是一种罕见病，其特征是骨骼，皮肤和其他组织过度生长。此突变改变了AKT1激酶中的单个蛋白质结构单元（氨基酸）。具体而言，它在蛋白质位置17（写为Glu17Lys或E17K）上用氨基酸赖氨酸取代了氨基酸谷氨酸。突变不是从父母那里继承的；在患有变形杆菌综合征的人中，突变在出生前发育的早期阶段随机出现在一个细胞中。随着细胞继续生长和分裂，一些细胞将发生突变，而其他细胞则不会。具有和不具有gene突变的细胞混合物称为镶嵌。（换行）Glu17Lys突变导致产生过度活跃的AKT1激酶，当它不应该时开启。异常活跃的蛋白质破坏细胞调节其自身生长的能力，使细胞异常生长和分裂。各种组织和器官中细胞增殖的增加导致变形杆菌综合征的过度生长特征。研究表明，AKT1gene突变在经历过度生长的细胞群中比在正常生长的身体部位更常见。"}, {"name": "卵巢癌", "detail": "遗传学家庭参考提供有关卵巢癌的信息。"}, {"name": "癌症", "detail": "在一小部分乳腺癌，卵巢癌和结肠直肠癌中也发现了AKT1gene中的Glu17Lys突变（如上所述）。在这些情况下，突变是体细胞的，这意味着它是在一个人的一生中获得的，并且仅存在于肿瘤细胞中。该突变异常激活AKT1激酶，使细胞在没有控制或命令的情况下生长和分裂。这种无序的细胞增殖导致癌性肿瘤的发展。（换行）尽管仅在少数类型的癌症中报道了Glu17Lys突变，但在许多类型的癌症中发现AKT1gene的活性（表达）增加。"}, {"name": "其他疾病", "detail": "在精神分裂症患者中，AKT1gene的几种常见变异（多态性）比在没有该疾病的患者中更常见。这些多态性改变了AKT1gene中的单个DNA构建块（核苷酸）。尚不清楚遗传变化是否对AKT1激酶的结构或功能有影响，如果是，它们与精神分裂症的发展如何相关。AKT1gene多态性似乎是导致这种复杂精神疾病发展的许多遗传和环境因素之一。"}]}' WHERE id = 22259;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SLCO1B3", "conditions": [{"name": "转子综合症", "detail": "SLCO1B3gene的突变与转子综合征有关。这种情况的特征是血液中胆红素水平升高，可导致皮肤变黄和眼睛变白（黄疸）。为了使这种情况发生，个体必须在SLCO1B3gene和称为SLCO1B1的相关gene中具有突变。该相关gene提供了制备称为OATP1B1的蛋白质的说明，该蛋白质具有与OATP1B3类似的转运功能。在某些情况下，这种情况是由于删除了部分SLCO1B3和SLCO1B1gene的遗传物质而引起的，因此不会产生功能性OATP1B3或OATP1B1蛋白。大多数导致转子综合征的突变会导致异常短的，无功能的OATP1B3和OATP1B1蛋白。没有任何一种转运蛋白的功能，胆红素被肝脏吸收并从体内清除的效率较低。这种物质的积累导致转子综合征患者的黄疸。"}]}' WHERE id = 22267;
UPDATE genes SET GHR_metadata_ch = '{"gene": "EYA1", "conditions": [{"name": "支气管肾/支气管综合征", "detail": "在患有支气管肾（BOR）综合征的人中，已经鉴定出EYA1gene中至少160个突变，这种情况会破坏颈部组织的发育并导致耳朵和肾脏的畸形。还发现EYA1gene突变会引起分支性（BO）综合征，除了肾脏（肾）畸形外，它还包括许多与BOR综合征相同的特征。否则，这两种情况非常相似，以至于研究人员经常将它们放在一起考虑（BOR/BO综合征或支气管肾频谱疾病）。（换行）许多导致BOR/BO综合征的突变会改变EYA1蛋白的3维结构，从而阻止其与其他蛋白有效相互作用。因为这些蛋白质相互作用对于胚胎发育过程中某些gene的激活是必需的，所以改变后的EYA1蛋白质会损害出生前许多组织的正常发育。BOR/BO综合征的主要体征和症状是由第二个branch弓，耳朵和（在BOR综合征中）肾脏的异常发育引起的。（换行）在某些情况下，相同的EYA1gene突变会在一个家庭的某些成员中引起BOR综合征，而在另一些家庭中引起BO综合征。这种可变性可能是由于影响EYA1蛋白在肾脏中的功能的其他未知gene的变化所致。"}, {"name": "其他疾病", "detail": "EYA1gene的一些突变与眼睛异常有关，包括晶状体混浊（白内障）和眼睛清晰的前表面混浊（角膜）。这些异常的发生没有BOR/BO综合征的特征。研究人员认为，负责眼部异常的EYA1gene突变对蛋白质功能的影响不如BOR/BO综合征的突变严重。"}]}' WHERE id = 22297;
UPDATE genes SET GHR_metadata_ch = '{"gene": "RPS17", "conditions": [{"name": "钻石-布莱克凡贫血", "detail": "在患有Diamond-Blackfan贫血的个体中至少鉴定出三个RPS17gene突变。据信这些突变影响RPS17蛋白的稳定性或功能。研究表明，缺乏功能性核糖体蛋白可能会增加骨髓中造血细胞的自我破坏，导致红细胞数量减少（贫血）。细胞分裂的异常调节或凋亡的不适当触发可能会导致其他健康问题和异常的身体特征，从而影响某些Diamond-Blackfan贫血患者。"}]}' WHERE id = 22325;
UPDATE genes SET GHR_metadata_ch = '{"gene": "RIT1", "conditions": [{"name": "努南综合征", "detail": "已发现RIT1gene中至少有14个突变引起Noonan综合征。这种情况的特征是面部特征轻度异常，身材矮小，心脏缺陷，出血问题，骨骼畸形以及许多其他体征和症状。由RIT1gene突变引起的Noonan综合征患者通常会因体液积聚（淋巴水肿）而肿胀。由RIT1gene突变引起的Noonan综合征患者很少会患上癌症，包括称为急性淋巴细胞白血病的血癌。（换行）与Noonan综合征相关的RIT1gene突变会改变RIT1蛋白中的单个蛋白结构单元（氨基酸）。突变导致产生改变的RIT1蛋白，该蛋白要么连续打开（激活），要么具有延长的激活时间，而不是响应其他细胞蛋白而迅速打开和关闭。异常活跃的蛋白质会改变正常的RAS/MAPK信号传导并导致异常的细胞增殖，从而破坏整个身体器官和组织的发育，从而导致Noonan综合征的体征和症状。"}, {"name": "肺癌", "detail": "遗传学家庭参考提供有关肺癌的信息。"}, {"name": "癌症", "detail": "在几种不同类型的血细胞癌（白血病）中发现了RIT1gene的突变，包括急性髓细胞性白血病（AML），慢性粒单核细胞性白血病（CMML）和称为骨髓增生异常综合症（MDS）的骨髓疾病。与引起Noonan综合征的RIT1gene突变（如上所述）相似，这些癌症中涉及的突变可能会导致组成型活性蛋白。结果，细胞生长不断促进，细胞不受控制地生长和分裂，导致癌症形成。与Noonan综合征突变不同，与白血病相关的RIT1gene突变是体细胞突变，这意味着它们发生在人的一生中，不会遗传。"}]}' WHERE id = 22377;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SAMHD1", "conditions": [{"name": "Aicardi-Goutières综合征", "detail": "在患有Aicardi-Goutières综合征的人中，已经鉴定出SAMHD1gene中至少有78个突变，该疾病涉及严重的脑功能障碍（脑病），皮肤病变和其他健康问题。研究表明，该gene的突变可能导致SAMHD1蛋白的产生，该蛋白不能正确调节可用dNTP的量。结果，DNA维持受损，使DNA损伤在细胞中积累。研究表明，这种DNA损伤可能导致细胞信号传导不适当地激活免疫反应。结果，免疫系统攻击身体自身的组织和器官，对大脑，皮肤和其他身体系统造成炎症损伤，导致Aicardi-Goutières综合征的特征。"}]}' WHERE id = 22400;
UPDATE genes SET GHR_metadata_ch = '{"gene": "FANCA", "conditions": [{"name": "范可尼贫血", "detail": "已发现FANCAgene中的450多个突变会引起Fanconi贫血，这是一种以骨髓功能下降，癌症风险增加和身体异常为特征的疾病。FANCAgene的突变占所有Fanconi贫血病例的60%至70%。这些突变改变了单个DNA构建模块（核苷酸）或在FANCAgene中插入或删除了DNA片段。一些突变允许产生具有某些残留功能的FANCA蛋白；其他突变会阻止任何FANCA蛋白的产生。与允许某些FANCA蛋白产生的突变相比，阻止all蛋白产生的突变通常会导致较早年龄的血细胞短缺，并增加罹患血细胞癌（白血病）的风险。FANCAgene中的（换行）突变导致无功能的FA核心复合物，从而破坏了整个FA途径。结果，DNA损伤无法有效修复，ICL会随着时间的推移而累积。ICL阻止DNA复制，最终导致由于不能产生新的DNA分子而导致的异常细胞死亡或由于缺乏DNA修复过程而导致的不受控制的细胞生长。快速分裂的细胞，例如骨髓细胞和发育中的胎儿细胞，尤其受到影响。这些细胞的死亡导致血细胞减少和范可尼贫血的身体异常。当DNA中的错误累积导致不受控制的细胞生长时，受影响的个体可能会发展为白血病或其他癌症。"}]}' WHERE id = 22425;
UPDATE genes SET GHR_metadata_ch = '{"gene": "MYBPC3", "conditions": [{"name": "家族性肥厚型心肌病", "detail": "MYBPC3gene突变是家族性肥厚型心肌病的常见原因，占所有病例的30%。这种情况的特征是心肌增厚（肥大）。尽管一些患有家族性肥厚型心肌病的人没有明显的健康影响，但所有受影响的人都有心力衰竭和猝死的风险增加。（换行）导致家族性肥厚性心肌病的MYBPC3gene突变导致异常短或以其他方式改变的心脏MyBP-C蛋白。这些变化如何导致心肌肥大尚不清楚。"}, {"name": "室致密化不全", "detail": "已发现MYBPC3gene中至少有四个突变会导致左心室不紧致，这发生在心脏左下腔（左心室）发育不正确时。心肌减弱，无法有效地抽血。这些心脏异常可导致从完全缺乏症状到心源性猝死的广泛结果。其他体征和症状包括心律不规则（心律失常），呼吸短促（呼吸困难）和心力衰竭。(换行)目前尚不清楚MYBPC3gene突变如何导致左心室致密化不全。在出生前的正常发育过程中，心肌变得紧实，变得光滑而结实。MYBPC3gene突变可能导致此过程发生变化，导致左心室心肌不紧实，但变厚且呈海绵状。这种异常的心肌很弱并且不能有效收缩，导致左心室不紧致的各种体征和症状。"}, {"name": "家族性扩张型心肌病", "detail": "遗传学家庭参考提供有关家族性扩张型心肌病的信息。"}]}' WHERE id = 22468;
UPDATE genes SET GHR_metadata_ch = '{"gene": "MLH1", "conditions": [{"name": "林奇综合症", "detail": "在所有已发现gene突变的Lynch综合征病例中，约有50%与MLH1gene的遗传突变有关。在患有这种疾病的人中发现了数百个MLH1gene突变。林奇综合症增加了许多类型癌症的风险，尤其是结肠癌（大肠）和直肠癌，这些癌症统称为大肠癌。Lynch综合征患者的子宫内膜癌（子宫内膜癌），卵巢癌，胃癌，小肠癌，肝癌，胆囊癌，上尿路癌和脑癌的风险也增加。(换行)这种情况下涉及的MLH1gene突变会阻止MLH1蛋白的产生或导致该蛋白的功能改变而无法正常运行。当MLH1蛋白不存在或不起作用时，在细胞分裂过程中未修复的DNA错误数量将大大增加。随着细胞继续分裂，错误累积，这可能导致细胞功能异常，增加结肠或身体其他部位形成肿瘤的风险。（换行）MLH1gene中的一些突变导致称为Turcot综合征的Lynch综合征的变体。除结肠直肠癌外，患有Turcot综合征的人倾向于发展一种称为胶质母细胞瘤的特定类型的脑肿瘤。(换行)Lynch综合征的另一种变体，称为Muir-Torre综合征，也可能是由MLH1gene突变引起的。除结肠直肠癌外，患有这种疾病的人患几种罕见皮肤肿瘤的风险增加。这些罕见的皮肤肿瘤包括皮脂腺瘤和癌，它们发生在产生称为皮脂（皮脂腺）的油性物质的腺体中。通常在暴露于阳光的皮肤区域也可能发生称为角膜棘皮瘤的多个快速生长的肿瘤。"}, {"name": "卵巢癌", "detail": "MLH1gene的遗传变化会增加患卵巢癌以及其他类型癌症的风险，这是Lynch综合征的一部分（如上所述）。患有Lynch综合征的女性一生中患卵巢癌的机会为8%至10%，而普通人群中为1.6%。"}, {"name": "其他癌症", "detail": "虽然林奇综合征与MLH1gene的一个拷贝中的突变相关，但很少有受影响家庭中的个体继承两个MLH1gene突变，每个父母一个。在这些情况下，最常见的是在gene的两个拷贝中都发生相同的突变（纯合突变）。具有纯合MLH1gene突变的人患有不同于Lynch综合征的综合征。除结直肠癌外，这些人还可能患上血液癌症（白血病或淋巴瘤）。这些个体中的一些还将发展为神经纤维瘤病的特征，包括沿神经生长的非癌性肿瘤（神经纤维瘤）和称为咖啡色斑点的浅棕色皮肤斑块。这些人的结肠癌发病非常早，通常发生在儿童时期。这种涉及结肠癌，白血病或淋巴瘤以及神经纤维瘤病的综合征有时称为结肠。"}]}' WHERE id = 22482;
UPDATE genes SET GHR_metadata_ch = '{"gene": "FN1", "conditions": [{"name": "纤连蛋白肾小球病", "detail": "已经发现FN1gene中的至少三个突变引起纤连蛋白肾小球病，这是一种进行性肾病，通常在成年期开始并导致不可逆的肾衰竭（终末期肾病）。FN1gene突变约占纤连蛋白肾小球病病例的40%。FN1gene突变会改变纤连蛋白1蛋白中的单个蛋白结构单元（氨基酸）。在多个家族中发生的一个突变将纤连蛋白-1蛋白（写为Tyr973Cys或Y973C）中973位的氨基酸半胱氨酸替换为氨基酸酪氨酸。FN1gene突变会削弱蛋白质与细胞和蛋白质结合的能力。未结合的纤连蛋白-1蛋白，特别是可溶性血浆纤连蛋白-1，沉积在肾脏的肾小球中。这些结构是肾脏中的微小血管簇，过滤血液中的废物，然后释放到尿液中。尽管肾小球中存在丰富的纤连蛋白-1，但支持血管的细胞外基质很弱，因为改变的纤连蛋白-1不能帮助基质的持续形成。如果没有强大的细胞支持网络，肾小球就无法过滤废物。结果，通常由身体保留的产品，例如蛋白质和血液，在尿液中释放，并且酸不能从血液中正确过滤。随着时间的推移，肾脏过滤废物的能力下降，直到肾脏不再起作用，导致终末期肾病。"}]}' WHERE id = 22485;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SLCO1B1", "conditions": [{"name": "转子综合症", "detail": "SLCO1B1gene的突变与转子综合征有关。这种情况的特征是血液中胆红素水平升高，可导致皮肤变黄和眼睛变白（黄疸）。为了使这种情况发生，个体必须在SLCO1B1gene和称为SLCO1B3的相关gene中具有突变。该相关gene提供了制备称为OATP1B3的蛋白质的说明，该蛋白质具有与OATP1B1类似的转运功能。在某些情况下，这种情况是由于删除了部分SLCO1B1和SLCO1B3gene的遗传物质而引起的，因此不会产生功能性OATP1B1或OATP1B3蛋白。大多数导致转子综合征的突变会导致异常短的，无功能的OATP1B1和OATP1B3蛋白。没有任何一种转运蛋白的功能，胆红素被肝脏吸收并从体内清除的效率较低。这种物质的积累导致转子综合征患者的黄疸。"}, {"name": "其他疾病", "detail": "SLCO1B1gene的某些常见变异（多态性）与处理某些药物（包括他汀类药物）的能力降低有关。在大约15%的人群中发现的与这种异常相关的最广泛研究的多态性改变了OATP1B1蛋白中的单个蛋白质构建块：174位的氨基酸缬氨酸被氨基酸丙氨酸取代（写为V174A或SLCO1B1*5）。由该版本的SLCO1B1gene产生的蛋白质不太能够将化合物转运到肝脏中，从而导致体内化合物水平升高。(换行)当他汀类药物不能有效地输送到肝脏时，它们会积聚在体内，并可能导致称为他汀类药物引起的肌病的病症。这种情况会导致肌肉疲劳，疼痛，压痛，虚弱和痉挛。服用他汀类药物的具有V174A多态性的人患他汀类药物引起的肌病的风险增加。"}]}' WHERE id = 22498;
UPDATE genes SET GHR_metadata_ch = '{"gene": "FLI1", "conditions": [{"name": "尤文肉瘤", "detail": "涉及FLI1gene的突变会导致一种称为尤因肉瘤的癌性肿瘤。这些肿瘤发展在骨骼或软组织，如神经和软骨。尤因肉瘤有几种类型，包括骨尤因肉瘤，骨外尤因肉瘤，周围原始神经外胚层肿瘤和阿斯金肿瘤。导致这些肿瘤的突变是在一个人的一生中获得的，并且仅存在于肿瘤细胞中。这种类型的遗传变化称为体细胞突变，不是遗传的。导致尤因肉瘤的最常见突变是11号染色体和22号染色体之间遗传物质的重排（易位）。这种易位，写为t（11；22），将11号染色体上的FLI1gene的一部分与22号染色体上称为EWSR1的另一个gene的一部分融合，产生EWSR1/FLI1融合gene。（换行）由EWSR1/FLI1融合gene产生的蛋白质，称为EWS/FLI，具有两种gene的蛋白质产物的功能。与FLI蛋白一样，EWSR1gene产生的EWS蛋白可以调节转录。EWS/FLI蛋白具有FLI蛋白的DNA结合功能以及EWS蛋白的转录调节功能。据认为，EWS/FLI蛋白异常开启和关闭各种gene的转录。这种转录失调导致不受控制的生长和分裂（增殖）以及细胞的异常成熟和存活，从而导致肿瘤发展。（换行）EWSR1/FLI1融合gene发生在约85%的尤因肉瘤中。其余的肿瘤是由EWSR1gene与其他gene融合的易位引起的。"}, {"name": "雅各布森综合症", "detail": "Genetics Home Reference提供有关Jacobsen综合征的信息。"}]}' WHERE id = 22506;
UPDATE genes SET GHR_metadata_ch = '{"gene": "COL7A1", "conditions": [{"name": "营养不良性大疱性表皮松解症", "detail": "已经在营养不良性大疱性表皮松解症患者中鉴定出COL7A1gene的突变，该疾病导致两个皮肤层分离，由于摩擦或其他轻微创伤而导致水泡和广泛的瘢痕形成，并且与包括鳞状细胞癌在内的癌症风险增加有关。COL7A1突变可引起常染色体隐性遗传性营养不良性大疱性表皮松解症（RDEB），称为Hallopeau-Siemens型（RDEB-HS），这是最严重的经典形式，而轻度形式称为非Hallopeau-Siemens型（非HS RDEB）。营养不良性大疱性表皮松解症（DDEB）的一种较温和的常染色体显性遗传形式也由于一份COL7A1gene的突变而引起。这些形式是由于突变改变结构或破坏VII型胶原蛋白的产生而产生的，这削弱了锚定原纤维将表皮连接至真皮的能力。"}]}' WHERE id = 22510;
UPDATE genes SET GHR_metadata_ch = '{"gene": "GNAI3", "conditions": [{"name": "耳con突综合征", "detail": "已发现GNAI3gene中至少有两个突变会引起耳con突综合征，这种疾病主要影响耳朵和下颌（下颌骨）的发育。鉴定出的突变改变了抑制性α亚基中的单个蛋白质结构单元（氨基酸）。这些突变可能会改变抑制性α亚基的结构并损害G蛋白信号传导。异常信号会改变下颌的形成：下颌的形状不像上颌骨（上颌骨）那样正常发育，而不是正常发育。形状异常会导致下巴异常小（微棘皮症）和下颌功能问题。研究人员正在努力确定该gene的突变如何导致与耳con突综合征相关的其他发育异常。"}]}' WHERE id = 22520;
UPDATE genes SET GHR_metadata_ch = '{"gene": "GNAT2", "conditions": [{"name": "色盲", "detail": "已发现GNAT2gene中至少有10个突变引起视力障碍色盲。这些突变是完全性色盲的相对罕见原因，色盲是一种疾病，其特征是完全缺乏色觉和婴儿早期出现的其他视觉问题。GNAT2gene突变也已在少数色盲不完全的个体中发现，这是一种与色觉受限相关的轻度疾病。（换行）完全性色盲的基础GNAT2gene突变导致转导蛋白的视锥细胞特异性α亚基异常小，无功能。没有这个亚基，视锥细胞就没有功能性转导蛋白，它们也不能进行光转导。（由GNAT2gene产生的亚基是视锥细胞特有的，因此视杆通常不受这种疾病的影响。）视锥细胞功能的丧失是完全色盲患者缺乏色觉和其他视觉问题的基础。（换行）至少一种已知的GNAT2gene突变导致不完全的色盲。突变写为c.461+2G>A，影响gene指令拼接在一起形成亚基蛋白的方式。这种突变允许产生一些功能性的视锥细胞特异性α亚基，尽管该亚基的数量大大减少。结果，少量功能性转导蛋白可用于在光转导中发挥作用，并且部分功能性视锥细胞可将一些视觉信息传递给大脑。"}]}' WHERE id = 22528;
UPDATE genes SET GHR_metadata_ch = '{"gene": "FLCN", "conditions": [{"name": "Birt-Hogg-Dubé综合征", "detail": "在患有Birt-Hogg-Dubé综合征的人中已经鉴定出FLCNgene的几种突变，该疾病的特征是多种非癌性（良性）皮肤肿瘤，其他肿瘤和肺囊肿的风险增加。这些突变大多数会插入或删除卵泡蛋白中的一个或多个蛋白质结构单元（氨基酸）。这些突变导致该蛋白异常小的，无功能的形式的产生。没有卵泡蛋白，研究人员认为细胞可以不受控制地生长和分裂，形成癌性或非癌性肿瘤。他们尚未确定卵泡蛋白的丢失如何增加通常与Birt-Hogg-Dubé综合征相关的肺部异常风险。"}, {"name": "原发性自发性气胸", "detail": "已发现FLCNgene中至少有8个突变引起原发性自发性气胸。当空气在肺和胸腔之间的空间（多个空间）中异常积聚时，会发生这种情况，可能导致肺部部分或完全塌陷。这些突变中的许多导致产生异常短且无功能的卵泡蛋白。研究人员尚未确定FLCNgene突变如何导致原发性自发性气胸的发展。一种理论认为，卵泡蛋白蛋白的改变可能会触发肺组织内的炎症，从而导致组织中形成小囊空气（气泡）。这些气泡可能会破裂，导致空气渗入胸膜腔。具有与原发性自发性气胸相关的FLCNgene突变的人似乎都出现了气泡，但据估计，这些人中只有40%继续患有原发性自发性气胸。"}, {"name": "其他癌症", "detail": "一些gene突变是在一个人的一生中获得的，并且仅存在于某些细胞中。这些称为体细胞突变的变化不是遗传的。FLCNgene中的体细胞突变可能与几种类型的非遗传性（散发性）肿瘤有关。具体而言，在某些透明细胞肾细胞癌（一种肾癌）和某些结肠癌中已鉴定出体细胞FLCN突变。这些突变可能会改变卵泡蛋白的结构，破坏其抑癌功能。研究人员不知道FLCN突变如何导致这些特定形式的癌症。"}]}' WHERE id = 22530;
UPDATE genes SET GHR_metadata_ch = '{"gene": "KCNJ1", "conditions": [{"name": "巴特综合症", "detail": "在II型Bartter综合征患者中已鉴定出KCNJ1gene的数十个突变。这种形式的疾病会导致严重或威胁生命的健康问题，这些问题在出生前或出生后不久就变得明显。（换行）导致Bartter综合征的某些KCNJ1gene突变会改变ROMK蛋白中的单个蛋白结构单元（氨基酸）。这些突变阻止蛋白质到达细胞膜或改变通道转运钾离子的能力。KCNJ1gene中的其他突变会从蛋白质中删除氨基酸或导致异常短而无功能的ROMK产生。(换行)功能性ROMK的丧失会影响NKCC2蛋白的正常活性，从而阻止其将离子转运到肾细胞中。结果，肾脏不能正常地重吸收盐，并且过量的盐通过尿液流失（盐浪费）。异常的盐分流失破坏了体内钠，钾和其他离子的正常平衡。这些不平衡是Bartter综合征的主要特征。"}, {"name": "其他疾病", "detail": "研究表明，KCNJ1gene的正常变异（多态性）可能有助于解释不同人群中血压的变异。某些罕见的多态性似乎可以预防高血压（高血压），研究人员推测其他遗传变异可能会增加患高血压的风险。KCNJ1gene的变化可能会通过改变肾脏将盐重吸收到血液中的能力来影响血压。"}]}' WHERE id = 22590;
UPDATE genes SET GHR_metadata_ch = '{"gene": "COL1A2", "conditions": [{"name": "埃勒斯-当洛二氏综合症", "detail": "已经发现COL1A2gene的突变会导致一种形式的Ehlers-Danlos综合征，称为关节炎型，这种疾病的特征是出生时双髋活动过度和脱位。COL1A2gene的两个拷贝中的突变都与心脏瓣膜类型有关，这是一种罕见的疾病，其特征是心脏瓣膜异常，皮肤高度伸展（弹性）和关节活动过度。这是由于有缺陷或无功能的pro？2（I）链导致I型胶原纤维在皮肤和其他组织中的组装受损。"}, {"name": "成骨不全症", "detail": "已发现COL1A2gene突变会导致严重形式的成骨不全症，包括II，III和IV型。受影响的个体的特征是骨骼因轻度创伤或无原因而容易破裂。COL1A2中的突变也会导致这种疾病的轻度（I型）形式。这种情况是由于生产pro？2（I）缺少关键区域或由于c端区域的氨基酸取代而干扰I型胶原的组装和产生。"}, {"name": "其他疾病", "detail": "发现COL1A2gene的突变与成骨不全症和Ehlers-Danlos综合征的体征和症状有关。这是由于重要部分的删除或重复导致pro-的异常缩短版本？2（I）链从而改变I型胶原原纤维的结构和功能。"}]}' WHERE id = 22599;
UPDATE genes SET GHR_metadata_ch = '{"gene": "KCNJ5", "conditions": [{"name": "产生醛固酮的腺瘤", "detail": "KCNJ5gene的突变会导致约40%的醛固酮生成腺瘤，这是在肾上腺中形成的非癌性（良性）肿瘤。这些肿瘤中涉及的遗传变化称为体细胞突变，是在一个人的一生中获得的，仅存在于引起肿瘤的肾上腺细胞中。(换行)与此情况相关的KCNJ5gene突变会改变钾通道中的单个蛋白质结构单元（氨基酸）。改变的钾通道选择性较低，允许其他离子，特别是钠通过。钠离子流入肾上腺细胞会影响穿过细胞膜的电荷，从而激活另一种允许钙离子进入的通道。钙离子的流入过度激活了称为钙/钙调蛋白途径的过程，该过程增加醛固酮的产生，导致醛固酮过量并导致高血压（高血压）和心脏病发作和中风的风险增加。肾上腺中钙/钙调蛋白途径的过度活化也增加细胞生长和分裂（增殖），其促进腺瘤形成。"}, {"name": "家族性醛固酮增多症", "detail": "已在III型家族性醛固酮增多症患者中鉴定出遗传的KCNJ5gene突变。这些突变称为种系突变，存在于人体的每个细胞中。家族性醛固酮增多症会导致高血压，一些受影响的人的肾上腺异常大（肾上腺增生）。如在产生醛固酮的腺瘤中（如上所述），KCNJ5gene突变导致产生选择性较低的钾通道。离子通过这些通道的异常流动导致醛固酮生成增加，从而引起高血压。"}, {"name": "罗曼诺-沃德综合症", "detail": "遗传学家庭参考资料提供有关Romano-Ward综合征的信息。"}]}' WHERE id = 22600;
UPDATE genes SET GHR_metadata_ch = '{"gene": "DCX", "conditions": [{"name": "孤立的lissencephaly序列", "detail": "已发现DCXgene中的70多个突变会导致孤立的lissencephaly序列（ILS）。这种情况的特征是大脑发育异常，导致大脑外观光滑（lissencephaly），而不是正常的褶皱和凹槽。ILS患者有严重的神经系统问题，包括从婴儿期开始的智力残疾和反复发作（癫痫病）。导致ILS的大多数DCXgene突变会改变双皮质素中的单个蛋白质结构单元（氨基酸），通常会导致蛋白质功能很少或没有。缺乏正常的双皮质素会影响微管的稳定性和组织，削弱其移动细胞的能力。发育中的大脑中的神经元受到特别影响，导致与ILS相关的神经系统问题。"}, {"name": "其他疾病", "detail": "DCXgene中的突变可引起称为皮质下带异位症的病症，其特征在于脑发育异常，通常不如ILS严重（如上所述）。当神经元迁移到不应该是的大脑区域（异位症）并形成白色组织的带状簇时，就会发生皮层下带异位症。由于这些条带位于称为大脑皮层的大脑区域下方，因此据说它们是皮质下的。皮质下带异位症的症状取决于大脑异常的严重程度，从严重的智力障碍和癫痫到轻度或无癫痫的正常智力可能有所不同。（换行）皮层下带异位症通常会影响具有影响每个细胞中DCXgene一个拷贝的突变的雌性，而具有一个DCXgene突变的雄性通常会患有ILS。女性可以发展为ILS，男性可以发展为皮质下带异位症，但这些情况很少见。"}]}' WHERE id = 22635;
UPDATE genes SET GHR_metadata_ch = '{"gene": "KCNA1", "conditions": [{"name": "阵发性共济失调", "detail": "在患有发作性共济失调1型（EA1）的人中，已经鉴定出KCNA1gene中至少有20个突变。患有这种疾病的人会出现短暂的，反复发作的协调和平衡差（共济失调）。在发作之间，许多受影响的个体会经历肌强直，肌强直是一种肌肉异常，可引起不自主的肌肉痉挛，僵硬和连续的细肌肉抽搐，表现为皮肤下波纹。(换行)大多数导致发作性共济失调的KCNA1突变会改变Kv1.1通道α亚基中的单个蛋白质结构单元（氨基酸）。其中一些变化阻止了功能通道的组装，而其他突变则改变了通道的结构。当Kv1.1通道缺失或异常时，钾离子进入神经元的流量减少。钾离子的这种减少会过度刺激大脑中的某些神经元，从而破坏这些细胞之间的正常通讯。尽管神经元之间信号传导的变化是发作性共济失调患者中不协调运动发作的基础，但尚不清楚钾离子转运的改变如何导致该病的特定特征。"}, {"name": "其他疾病", "detail": "已经发现KCNA1gene中的突变引起影响神经系统的一系列体征和症状。在至少一个家庭中，孤立的肌强直（持续的肌肉抽搐和痉挛，无共济失调发作）已归因于KCNA1突变。在少数癫痫患者中也发现了该gene的变化。像发作性共济失调的基础KCNA1突变一样，引起孤立的肌强直和癫痫的突变会减少钾离子通过Kv1.1通道的流动，从而破坏大脑神经元之间的正常通讯。研究人员正在努力确定为什么这个单一gene的突变会导致几种不同的神经系统疾病。"}]}' WHERE id = 22655;
UPDATE genes SET GHR_metadata_ch = '{"gene": "HOGA1", "conditions": [{"name": "原发性高草酸尿症", "detail": "已发现HOGA1gene中至少有24个突变会导致3型原发性高草酸尿症。这种情况是由称为草酸盐的物质过量产生引起的。过量的这种物质会导致肾脏和膀胱结石，这些结石始于儿童早期，通常会导致尿液中的血液（血尿）和尿路感染。大约一半的受影响个体中存在一种特定的突变，该突变会改变gene指示用于制造酶的方式（写为700+5G>T）。由于HOGA1gene突变，HOGA酶无法分解4-羟基-2-氧代戊二酸，从而导致该物质在肝细胞线粒体中积聚。(换行)目前尚不清楚4-羟基-2-氧代戊二酸的积累如何导致原发性高草酸尿症3型患者草酸盐过量产生。一些研究人员认为，4-羟基-2-氧代戊二酸的积累会干扰其他酶的活性，从而导致转化为草酸盐的物质积累。其他研究人员认为线粒体中过量的4-羟基-2-氧代戊二酸可能会泄漏到肝细胞中。然后这些细胞内的酶将4-羟基-2-氧代戊二酸转化为乙醛酸，然后将乙醛酸转化为草酸盐。在患有原发性高草酸尿症3型的个体中，草酸盐通过肾脏过滤并作为废物在尿液中排泄或与钙结合形成草酸钙，草酸钙是肾脏和膀胱结石的主要成分。"}]}' WHERE id = 22677;
UPDATE genes SET GHR_metadata_ch = '{"gene": "MC1R", "conditions": [{"name": "眼皮肤白化病", "detail": "MC1Rgene的某些遗传变化会改变2型眼皮肤白化病患者的外观。这种形式的白化病是由OCA2gene突变引起的，其特征是白发，浅色的眼睛，乳白色的皮肤和视力问题。OCA2和MC1Rgene都有遗传变化的人具有2型眼皮肤白化病的许多常见特征；但是，它们通常具有红色头发，而不是在这种情况下看到的通常的黄色，金色或浅棕色头发。"}, {"name": "癌症", "detail": "MC1Rgene的许多遗传变化会增加患皮肤癌的风险，包括从黑素细胞（黑色素瘤）开始的常见，严重形式的皮肤癌。MC1Rgene的改变破坏了黑皮质素1受体触发黑素细胞中eumelanin产生的能力。由于真黑素通常可以保护皮肤免受紫外线辐射的有害影响，因此缺乏这种色素会使白皙的皮肤更容易受到阳光照射的伤害。太阳紫外线辐射引起的皮肤损伤是发展黑色素瘤和其他形式皮肤癌的主要危险因素。(换行)研究表明，在没有紫外线辐射相关的皮肤损伤的情况下，MC1Rgene的变异也可能增加患黑色素瘤的风险。在这些情况下，黑色素瘤可能发生在深色或浅色皮肤的人身上。这些癌症通常与与黑色素瘤风险相关的其他gene（例如BRAF和CDKN2Agene）的突变有关。研究人员正在努力解释MC1R变异，其他遗传和环境因素以及黑色素瘤风险之间的复杂关系。"}]}' WHERE id = 22697;
UPDATE genes SET GHR_metadata_ch = '{"gene": "RAPSN", "conditions": [{"name": "先天性肌无力综合征", "detail": "已发现RAPSNgene中至少有45个突变会导致先天性肌无力综合症。这些突变大多数会改变rapsyn蛋白中的单个蛋白结构单元（氨基酸）。常见突变用rapsyn蛋白（写为Asn88Lys或N88K）中第88位的氨基酸赖氨酸替换氨基酸天冬酰胺。RAPSNgene中的大多数突变导致功能性rapsyn蛋白减少。rapsyn蛋白的缺乏导致rapsyn与AChR蛋白之间的结合减少，这导致肌肉细胞膜中受体蛋白的混乱和受体数量的减少。结果，神经肌肉接头处的信号传导减少，这导致肌肉运动减少和先天性肌无力综合症的肌肉无力特征。"}, {"name": "多发性翼状syndrome肉综合征", "detail": "遗传学家庭参考提供有关多发性翼状syndrome肉综合征的信息。"}]}' WHERE id = 22727;
UPDATE genes SET GHR_metadata_ch = '{"gene": "TSEN34", "conditions": [{"name": "桥小脑发育不全", "detail": "在患有称为桥小脑发育不全的大脑发育障碍的人中，已经鉴定出TSEN34gene的至少一种突变。这种情况的主要特征包括发育迟缓，运动问题和智力障碍。TSEN34gene突变导致一小部分称为2型桥小脑发育不全（PCH2）的疾病。当PCH2由TSEN34gene突变产生时，有时会将其更具体地归类为PCH2C。（换行）导致PCH2C的突变将TSEN34蛋白（写为Arg58Trp或R58W）中第58位的氨基酸色氨酸替换为氨基酸精氨酸。这种突变削弱了tRNA剪接核酸内切酶复合物的功能，这可能会破坏RNA分子的加工并影响许多类型蛋白质的产生。出生前，这些变化似乎对快速生长的组织（例如大脑中的组织）产生最严重的影响。但是，尚不清楚在这种情况下，tRNA剪接核酸内切酶复合物功能降低如何导致大脑发育异常。"}]}' WHERE id = 22740;
UPDATE genes SET GHR_metadata_ch = '{"gene": "COQ6", "conditions": [{"name": "初级辅酶Q10缺乏症", "detail": "Mutations in the COQ6 gene have been found to cause primary coenzyme Q10 deficiency, a rare disorder that usually becomes apparent in infancy or early childhood; but can occur at any age and most often affects the brain, muscles, and kidneys. This is caused due to an abnormal structure of the enzyme that greatly reduces the function and production of coenzyme Q10."}]}' WHERE id = 22743;
UPDATE genes SET GHR_metadata_ch = '{"gene": "HARS2", "conditions": [{"name": "Perrault综合征", "detail": "已发现HARS2gene中至少有两个突变引起Perrault综合征。这种罕见病的特征是男性和女性的听力下降，受影响女性的卵巢疾病和异常。参与Perrault综合征的HARS2gene突变会降低线粒体组氨酸tRNA合成酶的活性。功能性线粒体组氨酸-tRNA合成酶的缺乏阻止了线粒体内新蛋白质的正常组装。研究人员推测，蛋白质组装受损会破坏线粒体能量的产生。但是，尚不清楚HARS2gene突变究竟如何导致受影响个体的听力问题和卵巢异常。"}]}' WHERE id = 22744;
UPDATE genes SET GHR_metadata_ch = '{"gene": "VWF", "conditions": [{"name": "冯维勒布兰德病", "detail": "已发现VWFgene中的300多个突变会引起von Willebrand病。减少von Willebrand因子数量的VWFgene突变会导致1型von Willebrand病。患有1型血管性血友病的人血液中有血管性血友病因子，但数量减少。破坏von Willebrand因子功能的突变会导致2型von Willebrand疾病的四种亚型。这些突变通常会改变用于制造von Willebrand因子的蛋白质结构单元（氨基酸）之一，这可能会破坏该因子结合形成血凝块所需的各种细胞和蛋白质的能力。导致异常短而无功能的von Willebrand因子的突变通常会导致更严重的3型von Willebrand疾病。von Willebrand因子数量的减少或其功能问题减慢了血栓的形成，这导致了von Willebrand病中出血时间的延长。"}]}' WHERE id = 22746;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ARG1", "conditions": [{"name": "精氨酸酶缺乏症", "detail": "在精氨酸酶缺乏症患者中鉴定出ARG1gene突变，精氨酸酶缺乏症是一种以神经系统问题，腿部僵硬（肌肉痉挛），生长缓慢，发育迟缓，最终丧失发育里程碑，智力残疾，癫痫发作，震颤和共济失调为特征的疾病。该疾病是由于酶产生不稳定，较短，异常/丢失而引起的，该酶损害尿素循环，导致体内氨积累过多。"}]}' WHERE id = 22748;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ALAD", "conditions": [{"name": "卟啉症", "detail": "ALADgene中至少有10个突变可引起一种罕见的卟啉症，称为ALAD缺乏性卟啉症。这些突变大多数会改变δ-氨基乙酰丙酸脱水酶中的单个蛋白质结构单元（氨基酸）。这些变化降低了酶的活性，使δ-氨基乙酰丙酸在体内积累到毒性水平。该化合物是在血红素生产的正常过程中形成的，但是δ-氨基乙酰丙酸脱水酶的活性降低使其积累到毒性水平。非常高水平的这种化合物会引起腹痛，呕吐以及ALAD缺乏性卟啉症的其他体征和症状的发作。"}, {"name": "其他疾病", "detail": "ALADgene的常见变异（多态性）可能会影响暴露于环境铅的人群发生铅中毒的风险。铅是一种重金属，吸入或摄入时有毒。铅中毒可引起涉及神经系统，血液，肾脏和生殖系统的重大健康问题。（换行）已经研究最广泛的ALAD变异在δ-氨基乙酰丙酸脱水酶（写为Gly177Cys或G177C）中用177位的氨基酸半胱氨酸取代氨基酸甘氨酸。这种变化可能会影响一个人血液和骨骼中的铅含量。尽管一些研究表明这种变异会增加铅中毒的风险，但其他研究尚未发现这种关联。"}]}' WHERE id = 22761;
UPDATE genes SET GHR_metadata_ch = '{"gene": "WASHC5", "conditions": [{"name": "痉挛性截瘫8型", "detail": "已发现WASHC5gene中至少有3个突变引起8型痉挛性截瘫。这些突变改变了strumpellin蛋白中的单个结构单元（氨基酸）。在多个家族中发现的一种突变将strumpellin中626位的氨基酸苯丙氨酸替换为氨基酸缬氨酸（写为Val626Phe或V626F）。WASHC5gene突变被认为改变了strumpellin蛋白的结构。尚不清楚改变的strumpellin蛋白如何导致肌肉无力，肌肉僵硬和8型痉挛性截瘫的其他特征。"}, {"name": "癌症", "detail": "Research has shown that the WASHC5 gene is abnormally active (overexpressed) in certain types of prostate cancer. Scientists do not know what causes this abnormal expression and have not determined whether the WASHC5 gene plays a role in the development of prostate cancer.\r"}]}' WHERE id = 22771;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SQSTM1", "conditions": [{"name": "骨Paget病", "detail": "已发现SQSTM1gene中的20多个突变会导致骨骼Paget病。许多SQSTM1gene突变会改变p62蛋白中的单个蛋白结构单元（氨基酸）。最常见的突变是在蛋白质392位（写为Pro392Leu或P392L）上用氨基酸亮氨酸替换氨基酸脯氨酸。（线突破）通过一种尚不为人所知的机制，SQSTM1gene突变似乎过度激活了促进破骨细胞形成的化学信号通路。增加的信号传导刺激过多破骨细胞的产生并触发这些细胞异常分解骨。在患有Paget骨病的人中，受影响的骨头被分解并且比平时更快地更换。当新骨组织生长时，它比正常骨更弱，组织更少。骨重塑的这些问题导致某些骨骼变得异常大，畸形并且容易断裂（断裂）。目前尚不清楚为什么这种疾病会影响一些骨骼而不影响其他骨骼。"}, {"name": "肌萎缩侧索硬化症", "detail": "遗传学家庭参考提供有关肌萎缩侧索硬化的信息。"}]}' WHERE id = 22772;
UPDATE genes SET GHR_metadata_ch = '{"gene": "AAAS", "conditions": [{"name": "三A综合征或", "detail": "该AAASgene的缺陷与一种称为triple-a综合征（也称为贲门失弛缓症-addisonianism-alacrima综合征（AAAS）或Allgrove综合征）的疾病有关，这种疾病的特征是ALADIN蛋白的结构和功能异常变化导致蛋白质定位不当并破坏分子穿过核膜的运动，从而抑制导致DNA损伤和细胞死亡的DNA修复。"}]}' WHERE id = 22773;
UPDATE genes SET GHR_metadata_ch = '{"gene": "LHCGR", "conditions": [{"name": "家族性男性有限性早熟", "detail": "在家族性男性有限性早熟的男孩和男性中至少鉴定出17个LHCGRgene突变。这些突变取代了黄体生成素/绒毛膜促性腺激素受体中的单个蛋白质结构单元（氨基酸）。突变导致受体不断打开（组成性激活），即使未与黄体生成素或绒毛膜促性腺激素连接（结合）。研究人员认为，氨基酸序列的变化可能通过改变受体的形状或其他特性而导致组成型激活。(换行)在男性中，过度活跃的受体导致Leydig细胞产生过量的睾酮，导致家族性男性有限的性早熟。受影响的男孩在2至5岁之间开始表现出青春期迹象，例如生殖器生长和阴毛。过度活跃的受体对女性没有明显影响。"}, {"name": "Leydig细胞发育不全", "detail": "导致Leydig细胞发育不全的LHCGRgene突变破坏黄体生成素/绒毛膜促性腺激素受体功能，阻碍身体对这些激素的反应能力。在男性中，突变导致Leydig细胞发育不良或缺失，并损害睾丸激素的产生。缺乏睾酮会干扰出生前男性生殖器官的发育以及青春期出现的变化。(换行)阻止任何功能性受体蛋白产生的突变会导致Leydig细胞发育不全的更严重体征和症状。具有典型男性染色体模式（46，XY）的受影响个体具有女性外生殖器和未降的小睾丸，这意味着它们异常地位于骨盆，腹部或腹股沟中。受严重影响的个体在青春期不会出现继发性特征，例如体毛增加。（换行）允许某些受体蛋白功能的LHCGRgene突变引起Leydig细胞发育不全的轻微体征和症状。受影响的男性可能有一系列生殖器异常，包括小阴茎（微阴茎），阴茎下侧尿道开口（尿道下裂）或分为两个裂片的阴囊（双裂阴囊）。由于这些异常，外生殖器可能看不清楚男性或女性（不明确的生殖器）。"}, {"name": "其他疾病", "detail": "尽管遗传女性（每个细胞中有两条X染色体）的人可能会在LHCGRgene的两个拷贝中遗传突变，从而破坏黄体生成素/绒毛膜促性腺激素受体功能，但由于没有Leydig细胞，因此他们没有Leydig细胞发育不全。具有相同LHCGRgene突变导致男性Leydig细胞发育不全的女性具有正常的女性生殖器和正常的乳房和阴毛发育，但是他们可能比平时晚（16岁以后）开始月经并且月经周期不规则。这些突变还可以防止排卵，导致无法生育（不育）。"}]}' WHERE id = 22800;
UPDATE genes SET GHR_metadata_ch = '{"gene": "MTTP", "conditions": [{"name": "Abetalipoproteinemia", "detail": "在MTTPgene中已经鉴定出超过30种引起无β脂蛋白血症的突变。一种特殊的突变在Ashkenazi（东欧和中欧）犹太血统的人中更为常见；此突变用位置865处的终止信号（写为Gly865X或G865X）替换了蛋白质结构单元（氨基酸）甘氨酸。由于这种氨基酸变化，产生了异常小的，无功能的蛋白质形式。同样，导致abetalipoproteinemia的许多其他突变产生异常短的微粒体甘油三酯转移蛋白。缩短的蛋白质不能产生β-脂蛋白，导致乳糜微粒，低密度脂蛋白和极低密度脂蛋白的严重缺乏。这些脂蛋白的缺乏会阻止膳食脂肪和脂溶性维生素被吸收并携带到身体的其他部位，从而导致abeta脂蛋白血症患者出现营养和神经问题。"}]}' WHERE id = 22802;
UPDATE genes SET GHR_metadata_ch = '{"gene": "UMOD", "conditions": [{"name": "尿调节素相关性肾病", "detail": "已发现UMODgene中的40多个突变会导致与尿调节素相关的肾脏疾病。这些突变中的大多数改变用于制造尿调节素的单一蛋白质构建块（氨基酸）。这些突变改变了蛋白质的结构，阻止了其从肾细胞中释放出来。尿调节素异常积聚可能引发肾脏细胞的自我破坏（凋亡），引起肾脏疾病。"}]}' WHERE id = 22824;
UPDATE genes SET GHR_metadata_ch = '{"gene": "NSDHL", "conditions": [{"name": "先天性半发育不良伴鱼鳞状红皮病和肢体缺损", "detail": "At least 18 mutations in the NSDHL gene have been found to cause congenital hemidysplasia with ichthyosiform erythroderma and limb defects (CHILD syndrome).  Most of these mutations change single protein building blocks (amino acids) in the NSDHL enzyme.  A few other mutations delete part or all of the NSDHL gene.   Each of the identified mutations likely prevents the production of any functional NSDHL enzyme, which disrupts the normal synthesis of cholesterol within cells. A shortage of this enzyme may also allow potentially toxic byproducts of cholesterol synthesis to build up in the body''s tissues.  Researchers suspect that low cholesterol levels and/or an accumulation of other substances disrupts the growth and development of many parts of the body. It is not known, however, how a disturbance in cholesterol synthesis leads to the specific features of CHILD syndrome.\r"}]}' WHERE id = 22825;
UPDATE genes SET GHR_metadata_ch = '{"gene": "HOXA13", "conditions": [{"name": "手足生殖器综合征", "detail": "已发现HOXA13gene中至少有14个突变引起手足生殖器综合征。这些突变中超过一半影响HOXA13gene中的一个聚丙氨酸束。这些突变将额外的丙氨酸添加到这些区域，使其异常长且不稳定。产生的改变的蛋白质被细胞降解，因此在早期发育过程中无法调节其他gene的活性。这些变化影响手，脚，泌尿道和生殖系统的发育。(换行)其他HOXA13突变导致异常短而无功能的HOXA13蛋白产生或蛋白中单个氨基酸发生变化。将一个氨基酸替换为另一个氨基酸的突变可能会改变HOXA13蛋白的折叠方式。改变的蛋白质可能正常起作用或不起作用或不与DNA结合。与导致该蛋白无功能版本的突变相比，导致HOXA13蛋白改变但功能正常的突变可能导致手足生殖器综合征的更严重体征和症状。"}, {"name": "癌症", "detail": "涉及7号染色体短（p）臂的染色体重排（易位）与罕见的白血病（一种造血细胞癌）有关。这些易位破坏了7号染色体的区域，该区域包含几个相似的同源盒gene，包括HOXA13。（换行）在癌细胞内，研究人员发现几个白血病患者的7号染色体和11号染色体之间存在易位。这些重排异常融合了HOXA13的一部分或7号染色体上的类似gene与11号染色体上的NUP98gene的一部分。由融合gene产生的蛋白质可能表明异常细胞在没有控制或命令的情况下继续分裂，这可能有助于癌症的发展。"}]}' WHERE id = 22834;
UPDATE genes SET GHR_metadata_ch = '{"gene": "LMNA", "conditions": [{"name": "进行性神经性腓骨肌萎缩症", "detail": "在患有Charcot-Marie-Tooth病（称为2B1型）的人中，至少鉴定出一种LMNAgene突变。Charcot-Marie-Tooth病的特征是神经损伤，可导致感觉丧失和脚，腿和手的肌肉消瘦（萎缩）。该突变改变了层粘连蛋白a和层粘连蛋白C蛋白中的单个氨基酸。具体而言，氨基酸精氨酸被蛋白质位置298处的氨基酸半胱氨酸取代（写为Arg298Cys或R298C）。尽管尚未完全了解其作用，但Arg298Cys突变会改变蛋白质的一个区域，该区域对于与其他分子的相互作用很重要。目前尚不清楚这些改变的蛋白质如何导致2B1型Charcot-Marie-Tooth病的体征和症状。"}, {"name": "艾梅氏肌失养症", "detail": "在患有Emery-Dreifuss肌营养不良症的人中，已经鉴定出LMNAgene的130多个突变，这种情况会影响用于运动的肌肉（骨骼肌）和心脏（心肌）。这种情况的特征是称为挛缩的关节畸形，这限制了某些关节的运动；肌肉无力和消瘦会随着时间的推移而恶化；和心脏问题，包括猝死的风险增加。（换行）导致金刚砂-Dreifuss肌营养不良的大多数LMNAgene突变都会改变lamin A和C中的单个氨基酸，从而改变这些蛋白质的结构。LMNAgene突变在细胞内的作用尚不清楚。核纤层蛋白A和C的异常形式可能会改变某些gene的活性或削弱细胞核的结构，从而使细胞更加脆弱。研究人员继续研究LMNA突变如何影响骨骼肌和心肌，从而导致Emery-Dreifuss肌营养不良的特征。"}, {"name": "家族性部分脂肪营养不良", "detail": "已发现LMNAgene中的几个突变会导致2型家族性部分脂肪营养不良（也称为家族性部分脂肪营养不良，Dunnigan型），这是一种罕见病，其特征是体内脂肪（脂肪）组织分布异常。脂肪组织从手臂，腿部和臀部丢失，多余的脂肪沉积在面部，颈部和腹部。异常脂肪储存与脂肪细胞的发育和功能的变化有关，脂肪细胞是脂肪组织中的脂肪储存细胞。LMNAgene突变对脂肪细胞的影响尚不清楚。研究表明，这些突变可能会削弱核膜，最终导致这些细胞过早死亡，并使身体无法正确储存和使用脂肪。脂肪组织的这些异常会改变激素的产生并影响人体的许多器官。然而，目前还不清楚为什么这些变化会导致身体某些部位的脂肪流失，并在其他部位异常储存。"}, {"name": "早衰症", "detail": "在大多数Hutchinson-Gilford早衰综合征患者中发现了LMNAgene的特定突变，这种疾病导致从儿童期开始急剧，迅速地出现衰老。该突变改变了gene中的单个DNA构建块（核苷酸）。具体而言，该突变用位置1824处的核苷酸胸腺嘧啶取代了核苷酸胞嘧啶（写为C1824T）。有时也将此突变称为Gly608Gly或G608G，它是指受突变影响的层粘连蛋白A蛋白中的位置。尽管预计C1824T突变不会改变氨基酸，但它会改变gene说明用于制造蛋白质的方式。C1824T突变导致称为progerin的lamin A蛋白异常版本，该蛋白在一端附近缺失50个氨基酸。此突变的位置不会影响层粘连蛋白C的产生。LMNAgene中的其他突变已在少数具有Hutchinson-Gilford早衰综合症特征的人中发现。(换行)导致这种疾病的突变导致prelamin A的异常版本，无法在细胞内正确处理。当改变的蛋白质掺入到叶片中时，它会破坏核膜的形状。随着时间的流逝，这种改变的蛋白质的积累似乎破坏了细胞核的结构和功能，使细胞更有可能过早死亡。研究人员正在努力确定这些变化如何导致Hutchinson-Gilford早衰综合征的体征和症状。"}, {"name": "肢带型肌营养不良症", "detail": "在患有1B型肢带型肌营养不良症的人中，已经鉴定出LMNAgene中至少40个突变。肢带型肌营养不良症是一组相关疾病，其特征是肌肉无力和消瘦，尤其是在肩膀，臀部和四肢。(换行)导致肢带型肌营养不良的LMNAgene突变可能会损害lamin蛋白的功能。核纤层蛋白功能受损可能会导致脆弱，容易受损的细胞核或影响多种细胞活性的调控不当的gene。尚不清楚LMNAgene突变的影响如何与肢带型肌营养不良症的特定体征和症状相关。"}, {"name": "下颌骨发育不良", "detail": "LMNAgene中至少有四个突变会导致A型下颌骨发育不良（MADA）。这种情况的特点是各种体征和症状，其中可能包括骨骼异常；斑驳或斑片状肤色；和皮肤下脂肪组织的损失，特别是影响四肢（A型脂肪营养不良）。导致这种情况的LMNAgene突变会改变lamin A和lamin C蛋白中的单个氨基酸。最常见的突变是用氨基酸组氨酸（写为Arg527His或R527H）替换527位的氨基酸精氨酸。(换行)在这种情况下LMNAgene突变的影响尚不清楚。氨基酸的变化可能会影响lamin A或lamin C蛋白的结构，或两者兼而有之，并改变它们与核层中其他蛋白的相互作用方式。一些研究人员推测，这些变化破坏了核膜，使细胞更加脆弱；但是，尚不清楚改变的层粘连蛋白如何导致MADA的体征和症状。"}, {"name": "家族性心房颤动", "detail": "遗传学家庭参考资料提供有关家族性心房颤动的信息。"}, {"name": "家族性扩张型心肌病", "detail": "遗传学家庭参考提供有关家族性扩张型心肌病的信息。"}, {"name": "室致密化不全", "detail": "遗传学家庭参考提供有关左心室不紧致的信息。"}, {"name": "其他疾病", "detail": "已经发现LMNAgene中的突变引起其他几种情况。由lamin蛋白突变引起的健康状况称为laminopathies。这些疾病通常具有重叠的体征和症状，研究人员怀疑在某些情况下它们代表单一病症的变体而不是单独的病症。(换行)除了上面列出的健康状况外，LMNAgene的突变还会导致非典型的早衰综合症（APS）；这种情况的特征与哈钦森-吉尔福德早衰综合症和下颌骨发育不良相似。与Hutchinson-Gilford早衰综合征一样，APS患儿看起来好像过早衰老，尽管APS的体征和症状通常稍晚开始。APS还可以引起与下颌骨发育不良相似的骨骼发育和脂肪分布异常，尽管它们在APS中通常较轻。LMNAgene中的（换行）突变也已在患有致命性限制性皮肤病的疾病的新生儿中发现。患有这种疾病的婴儿皮肤紧绷，僵硬；肺不发达；和其他异常。他们通常无法生存到生命的第一周。(换行)研究人员尚未确定LMNAgene的突变如何导致这种多样化的疾病，但是核层在细胞中的多种作用可能有助于解释各种各样的体征和症状。"}]}' WHERE id = 22869;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ALX3", "conditions": [{"name": "额鼻发育不良", "detail": "At least seven mutations in the ALX3 gene have been found to cause frontonasal dysplasia. ALX3 gene mutations cause a form of the disorder called frontonasal dysplasia type 1, which particularly affects the development of the nose and surrounding tissues. ALX3 gene mutations that cause this condition severely reduce or eliminate the function of the ALX3 protein. As a result, the protein cannot bind to DNA and regulate gene function, which leads to poorly controlled cell proliferation and migration during development. This abnormal cell growth and movement impairs development of structures in the middle of the face, particularly the nose, leading to openings (clefts) in the nose. This abnormal development can also interfere with the proper formation of the skull, leading to the skull malformations typical of frontonasal dysplasia type 1.\r"}]}' WHERE id = 22880;
UPDATE genes SET GHR_metadata_ch = '{"gene": "GYS2", "conditions": [{"name": "0型糖原贮积症", "detail": "已发现GYS2gene中约有20个突变会导致某种形式的糖原贮积病0型（GSD 0），从而影响肝脏。导致这种情况的大多数GYS2gene突变会导致功能性糖原合酶的缺乏，从而导致肝细胞中糖原的完全缺乏。通常，糖原是由剩余的葡萄糖形成的，在进餐期间消耗葡萄糖后，葡萄糖不会立即被细胞使用。对于无法形成糖原的GSD 0患者，体内会释放出额外的葡萄糖。因此，患有这种情况的人长期没有食物（禁食）就没有储存的能量。在此期间，受影响的个体可能会出现低血糖（低血糖）和恶心以及GSD 0的其他体征和症状。"}]}' WHERE id = 22931;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SGCE", "conditions": [{"name": "肌阵挛-肌张力障碍", "detail": "已发现SGCEgene中的110多个突变会引起肌阵挛性肌张力障碍，这是一种运动障碍，其特征是颈部，躯干和手臂（肌阵挛）出现不自主的肌肉抽搐。这些突变大多数会导致异常短而无功能的-快速分解的糖聚糖蛋白。其他突变会阻止蛋白质到达需要的细胞膜。功能蛋白的缺乏似乎会影响参与协调和控制运动的大脑区域（分别为小脑和基底神经节），并导致肌阵挛性肌张力障碍的非自愿运动。尚不清楚为什么SGCEgene突变似乎仅影响大脑的这些区域。（换行）当突变影响SGCEgene的父本拷贝时发生肌阵挛-肌张力障碍。超过95%的从母亲那里继承SGCEgene突变的人没有出现这种情况的迹象或症状。从母亲那里继承SGCEgene突变的人很少会出现肌阵挛性肌张力障碍的特征。目前还不清楚为什么一个应该被关闭的gene在这些罕见的情况下是活跃的。"}]}' WHERE id = 22951;
UPDATE genes SET GHR_metadata_ch = '{"gene": "LDHB", "conditions": [{"name": "乳酸脱氢酶缺乏症", "detail": "已发现LDHBgene中超过15个突变会导致乳酸脱氢酶缺乏症，特别是乳酸脱氢酶B缺乏症。这种情况与功能性乳酸脱氢酶的减少有关，但似乎没有引起任何体征或症状。大多数LDHBgene突变改变乳酸脱氢酶-B亚基中的单个蛋白质构建块（氨基酸）。LDHBgene突变导致产生不能形成乳酸脱氢酶的异常乳酸脱氢酶-B亚基。尽管乳酸脱氢酶-B缺乏症患者的心肌细胞中乳酸脱氢酶活性降低，但它们似乎没有与其病症相关的任何体征或症状。目前尚不清楚为什么这种类型的酶缺乏症不会引起任何健康问题。"}]}' WHERE id = 22965;
UPDATE genes SET GHR_metadata_ch = '{"gene": "STAT3", "conditions": [{"name": "常染色体显性遗传性高IgE综合征", "detail": "在常染色体显性遗传性高IgE综合征（AD-HIES）患者中已鉴定出STAT3gene的100多个种系突变，这是一种免疫系统疾病，可导致皮肤和肺部反复感染以及骨骼和牙齿异常。这种情况下涉及的大多数突变都会改变STAT3蛋白中的单个氨基酸。这些突变发生在蛋白质的区域，这些区域对于其激活或与DNA结合的能力至关重要。（换行）导致AD-HIES的STAT3gene变化会改变STAT3蛋白的结构和功能，从而削弱其控制其他gene活性的能力。由于这些突变导致蛋白质正常功能受损，因此被称为“功能丧失”突变。STAT3信号功能的缺乏破坏了T细胞（特别是称为Th17细胞的子集）和其他免疫系统细胞的正常成熟。由此产生的免疫系统异常使AD-HIES患者极易受到感染，尤其是影响肺和皮肤的细菌和真菌感染。此外，STAT3蛋白在骨组织形成和维持中的作用可能有助于解释为什么STAT3gene突变会导致这种情况的骨骼和牙齿异常。"}, {"name": "克罗恩病", "detail": "遗传学家庭参考提供有关克罗恩病的信息。"}, {"name": "前列腺癌", "detail": "遗传学家庭参考提供有关前列腺癌的信息。"}, {"name": "带状疱疹", "detail": "遗传学家庭参考提供有关带状疱疹的信息。"}, {"name": "自身免疫性疾病", "detail": "已发现至少20种STAT3gene突变引起一种称为自身免疫性疾病的免疫系统异常，其中免疫系统发生故障并攻击身体自身的细胞和组织。在具有这些突变的人中，自身免疫通常始于婴儿期或儿童早期，并涉及多个身体系统。这些个体中的自身免疫性疾病通常由免疫系统攻击胰腺（1型糖尿病），红细胞（自身免疫性溶血性贫血），血小板（自身免疫性血小板减少症）或消化道组织（自身免疫性肠病）。这种情况下涉及的突变通常是遗传性的，并存在于人体的每个细胞中（称为种系突变）。它们改变了STAT3蛋白中的单个蛋白结构单元（氨基酸），导致蛋白异常活跃。由于这种作用，突变被归类为“功能获得”。(换行)通常，STAT3蛋白会响应控制细胞生长和发育的信号而打开和关闭。即使在没有这些化学信号的情况下，该蛋白质的持续活性形式也会将信息传递给细胞核。尚不清楚STAT3过度活跃如何导致免疫系统失调和自身免疫。"}, {"name": "癌症", "detail": "在大约三分之一的称为大颗粒淋巴细胞白血病（LGL）的血癌病例中发现了STAT3gene突变，其特征是异常大的白细胞（淋巴细胞）积累，并包含称为颗粒的结构。受影响的个体也可能患有自身免疫性疾病，主要是类风湿性关节炎或自身免疫性溶血性贫血，以及其他血细胞异常，例如纯红细胞发育不良。根据所涉及的白细胞类型，有两种形式的病症：T细胞大颗粒淋巴细胞白血病（T-LGL）和NK细胞慢性淋巴组织增生性疾病（CLPD-NKs）。两种形式都有相同的体征和症状。（换行）与引起自身免疫的突变（如上所述）不同，LGL相关的STAT3gene突变不是遗传的，仅在异常细胞中发现（称为体细胞突变）。LGL中涉及的突变被归类为“功能获得”，导致STAT3蛋白过度活跃。研究人员认为，过度活跃的STAT3蛋白指导细胞继续生长和分裂，并防止受损细胞自毁（经历细胞凋亡）。过量的STAT3蛋白可能通过使异常细胞不受控制地生长和分裂而促进癌症的生长。"}]}' WHERE id = 22978;
UPDATE genes SET GHR_metadata_ch = '{"gene": "TMEM70", "conditions": [{"name": "线粒体复合体V缺乏", "detail": "在患有线粒体复合体V缺乏症（一种具有多种体征和症状的疾病）的人中，已经鉴定出TMEM70gene中至少有12个突变。这些gene突变中的一些是罗姆人或阿拉伯人后裔特有的，占TMEM70gene突变引起的线粒体复合体V缺乏症的大多数。这种疾病也可能是由其他gene的突变引起的。(换行)线粒体复合物V缺乏的体征和症状在需要大量能量的器官和组织中最为突出，例如大脑和心脏。可能发生脑功能异常（脑病）和其他神经系统问题。线粒体复合物V缺乏症的另一个常见特征，尤其是由TMEM70gene突变引起的，是肥厚型心肌病。这种情况的特征是心脏（心脏）肌肉增厚（肥大），可导致心力衰竭。TMEM70gene突变改变跨膜蛋白70并损害其在复杂V装配中执行其功能的能力。结果，细胞中复合物V的量减少。由此导致的氧化磷酸化和能量产生的损害导致线粒体复合体V缺乏的体征和症状。"}]}' WHERE id = 23001;
UPDATE genes SET GHR_metadata_ch = '{"gene": "RAI1", "conditions": [{"name": "Potocki-Lupski综合征", "detail": "人们认为在每个细胞中具有额外的RAI1gene拷贝是Potocki-Lupski综合征许多主要特征的基础。这种情况的特征是发育迟缓，轻度至中度智力障碍，行为问题，包括自闭症谱系障碍（影响社交互动和沟通），睡眠障碍和其他健康问题。这种情况是由于在位置p11.2处17号染色​​体的一小段短（p）臂异常复制（重复）所致。在大约三分之二的受影响个体中，重复的片段包括大约370万个DNA构建块（碱基对），也写为3.7兆碱基（Mb）。（此段的删除会导致史密斯-马格尼斯综合症，如下所述。）在其余三分之一的情况下，重复项较大或较小，范围从小于1 Mb到几乎20 Mb。所有这些重复都会影响每个细胞中17号染色​​体的两个拷贝之一。(换行)所有已知引起Potocki-Lupski综合征的重复都包含RAI1gene。研究表明，重复会增加RAI1蛋白的量，从而破坏影响昼夜节律的gene的表达。这些变化可能解释了Potocki-Lupski综合征发生的睡眠障碍。目前尚不清楚在重复区域中额外复制的RAI1gene或其他gene如何导致智力残疾以及这种情况的其他体征和症状。"}, {"name": "史密斯-马格尼斯综合征", "detail": "研究人员认为，RAI1gene的部分或全部功能丧失是Smith-Magenis综合征的大多数体征和症状的原因。这种情况的主要特征包括轻度至中度智力障碍，语言和语言技能延迟，独特的面部特征，睡眠障碍，行为问题以及影响身体许多部位的其他异常。(换行)在大多数患有Smith-Magenis综合征的人中，该病症是由于每个细胞中17号染色​​体的一小块缺失所致。最常见的是，位于17p11.2的该染色体片段与在Potocki-Lupski综合征中复制的染色体片段相同（如上所述）。有时删除会更大或更小。所有的缺失都影响每个细胞中17号染色​​体的两个拷贝之一。(换行)所有已知引起史密斯-马格尼斯综合征的缺失均包含RAI1gene。研究表明，该缺失导致细胞中RAI1蛋白的量减少，从而破坏了与昼夜节律有关的gene的表达。这些变化可能是Smith-Magenis综合征发生的睡眠障碍的原因。目前尚不清楚RAI1gene一个拷贝的丢失如何导致与这种情况相关的其他身体，精神和行为问题。(换行)一小部分Smith-Magenis综合征患者的RAI1gene突变而不是染色体缺失。尽管这些人具有该病的许多主要特征，但与缺失者相比，他们具有某些其他特征的可能性较小，包括身材矮小，听力下降以及心脏或肾脏异常。在缺失的人中，缺失区域中其他gene的缺失很可能是这些额外体征和症状的原因；这些gene的作用正在研究中。"}]}' WHERE id = 23010;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ABCC9", "conditions": [{"name": "Cantúsyndrome", "detail": "ABCC9gene的突变与一种称为Cantú综合征的罕见病有关，这种病会导致SUR2蛋白缺陷，从而削弱其调节K-ATP通道活性的能力。该综合征的特征是头发过度生长（过度肥大），独特的面部外观和心脏缺陷。"}, {"name": "家族性心房颤动", "detail": "ABCC9gene的几个突变与家族性心房颤动有关，家族性心房颤动是一种常染色体显性遗传性疾病，其特征是心律不齐，这是由于心房颤动导致头晕，胸痛，心pit，呼吸急促或晕厥（如果不及时治疗）。还发现心房颤动与中风和猝死风险增加有关。"}, {"name": "家族性扩张型心肌病", "detail": "ABCC9gene中的几个突变与一种遗传性疾病有关，这种遗传性疾病称为家族性扩张型心肌病，原因是心肌变薄和变弱导致心力衰竭。其他症状包括心律不齐，呼吸急促（呼吸困难），极度疲倦（疲劳），晕厥发作（晕厥）以及腿，脚肿胀，从婴儿期到成年后期都可能发生。"}, {"name": "其他疾病", "detail": "在患有扩张型心肌病的个体中鉴定出ABCC9gene突变，该扩张型心肌病是由于心肌变薄和变弱而导致心脏抽血的原因。其他症状包括心律失常，呼吸急促（呼吸困难），极度疲倦（疲劳）和腿部和脚部肿胀。"}]}' WHERE id = 23024;
UPDATE genes SET GHR_metadata_ch = '{"gene": "MAP2K2", "conditions": [{"name": "心血管系统皮肤综合征", "detail": "已经在患有心血管系统皮肤综合征的人中鉴定出MAP2K2gene中的至少13个突变。这些突变大多数会改变MEK2蛋白激酶中的单个蛋白结构单元（氨基酸），尽管一个突变会从蛋白中删除几个氨基酸。这些遗传变化异常激活MEK2激酶，从而破坏全身细胞中严格调控的RAS/MAPK信号通路。改变的信号传导干扰许多器官和组织的正常发育，导致心脏皮肤综合征的特征。"}]}' WHERE id = 23025;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ABCG5", "conditions": [{"name": "谷固醇血症", "detail": "已经在患有由于固醇转运蛋白缺陷引起的称为谷固醇血症的个体中鉴定出ABCG5gene的突变，这削弱了其从体内消除植物固醇，胆固醇导致其积累的能力。症状包括动脉粥样硬化，脂肪性皮肤生长（黄瘤），贫血和其他血液异常。"}, {"name": "其他疾病", "detail": "ABCG5gene的突变与胆结石的风险增加有关，这是由于有缺陷的固醇转运蛋白引起的，该蛋白泵入的胆固醇比溶解在胆管和胆囊的胆汁中的胆固醇高。"}]}' WHERE id = 23051;
UPDATE genes SET GHR_metadata_ch = '{"gene": "LCT", "conditions": [{"name": "乳糖不耐症", "detail": "至少有9个LCTgene突变会导致先天性乳糖酶缺乏症，也称为先天性alactasia。在这种疾病中，婴儿无法分解母乳或配方奶粉中的乳糖（乳糖不耐症）。LCTgene突变会改变乳糖酶中的单个蛋白质结构单元（氨基酸）或导致酶异常短。据信该突变会干扰乳糖酶的功能，导致小肠中未消化的乳糖并引起严重的腹泻。(换行)成年期的乳糖不耐症是由婴儿期后LCTgene的活性（表达）逐渐降低引起的，这在大多数人中发生。"}]}' WHERE id = 23085;
UPDATE genes SET GHR_metadata_ch = '{"gene": "VDR", "conditions": [{"name": "维生素D依赖性佝偻病", "detail": "VDRgene突变导致维生素D依赖性佝偻病2A型（VDDR2A），也称为遗传性维生素D抗性佝偻病（HVDRR）。这种骨骼发育障碍的特征是血液中钙（低钙血症）和磷酸盐（低磷血症）含量低，从而导致容易骨折的软而弱的骨骼（病）。这种情况的一个共同特征是腿部异常弯曲（弯曲）。（换行）导致这种情况的VDRgene突变阻止VDR蛋白正常运作。VDRgene的一些变化导致VDR蛋白的异常短版本。其他导致产生不能与骨化三醇，RXR或DNA结合的异常受体。尽管体内含有大量骨化三醇，但改变后的VDR蛋白不能刺激对矿物质吸收重要的gene活性。肠道中钙和磷酸盐的吸收不足会减慢这些矿物质沉积到发育中的骨骼中（骨骼矿化），从而导致骨骼柔软，虚弱和VDDR2A的其他特征。低钙血症还会在某些受影响的个体中引起肌肉无力和癫痫发作。大多数VDRgene突变会损害毛发生长，导致脱发（脱发）；但是，阻止VDR与骨化三醇相互作用的突变不会引起脱发，这表明骨化三醇对于受体在头发发育中的作用不是必需的。"}, {"name": "椎间盘疾病", "detail": "遗传学家庭参考提供有关椎间盘疾病的信息。"}]}' WHERE id = 23096;
UPDATE genes SET GHR_metadata_ch = '{"gene": "TNFRSF1A", "conditions": [{"name": "肿瘤坏死因子受体相关周期综合征", "detail": "已发现TNFRSF1Agene中的60多个突变会引起肿瘤坏死因子受体相关的周期性综合征（通常称为TRAPS）。这些突变中的大多数导致单个蛋白质结构单元（氨基酸）的变化，通常涉及氨基酸半胱氨酸。半胱氨酸含有与其他半胱氨酸形成连接的硫原子，称为二硫键。二硫键通过连接蛋白质不同区域的半胱氨酸帮助蛋白质折叠。这些键稳定蛋白质并使其具有适当的形状以执行其特定功能。（折线）当TNFR1蛋白中的半胱氨酸被其他氨基酸取代时，不会形成二硫键，并且蛋白被错误折叠。这些错误折叠的蛋白质被捕获在细胞内，无法到达细胞表面与TNF相互作用。在细胞内，这些蛋白质聚集在一起，被认为会触发引发炎症的替代途径。蛋白质团块不断激活这些替代性炎症途径，导致TRAPS患者过度炎症。此外，由于只有一个拷贝的TNFRSF1Agene具有突变，因此会产生一些正常的TNFR1蛋白，并且可以与TNF蛋白结合，从而导致其他炎症。尚不清楚凋亡途径的破坏是否在TRAPS的体征和症状中起作用。(换行)一些TNFRSF1Agene突变的人不会出现TRAPS，或者会出现非常轻微的疾病特征。这种可变性的原因尚不清楚，但研究人员认为其他因素，例如其他遗传变化或环境因素，可能在引起TRAPS中起作用。"}, {"name": "多发性硬化症", "detail": "遗传学家庭参考提供有关多发性硬化症的信息。"}]}' WHERE id = 23100;
UPDATE genes SET GHR_metadata_ch = '{"gene": "GDAP1", "conditions": [{"name": "进行性神经性腓骨肌萎缩症", "detail": "研究人员已经确定了GDAP1gene中的50多个突变，这些突变可导致Charcot-Marie-Tooth病，这种疾病会导致肌肉无力，肌肉消瘦（萎缩）以及脚，腿和手的感觉丧失。这些突变中的一些会在制造GDAP1蛋白的说明中产生过早的终止信号。结果，细胞产生异常短的蛋白质，该蛋白质不太可能正确插入线粒体的外膜中。其他突变会改变用于制造GDAP1蛋白的单个结构单元（氨基酸），这可能会改变其形状或功能。(换行)尚不清楚GDAP1gene突变如何导致Charcot-Marie-Tooth病的特征。这些突变可能会损害线粒体改变形状的能力，但是这种损害如何影响周围神经尚不清楚。"}]}' WHERE id = 23107;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ERCC8", "conditions": [{"name": "柯凯因氏症候群", "detail": "研究人员已经鉴定出30多种可导致Cockayne综合征的ERCC8gene突变。这种罕见的疾病包括多种特征，包括异常小的头部大小（小头畸形），生长缓慢导致身材矮小，发育迟缓以及对日光的敏感性增加（光敏性）。（换行）一些ERCC8gene突变导致产生异常短版本的CSA蛋白，其不能正常运作。其他突变会改变用于制造CSA蛋白的构建基块（氨基酸）之一，这也会导致蛋白功能异常。（换行）ERCC8gene突变导致Cockayne综合征的机制尚不清楚。改变的CSA蛋白可能会破坏DNA修复。结果，受损的DNA不能固定，这会破坏gene转录并阻止蛋白质的正常生产。这些异常损害细胞功能并最终导致许多器官和组织中细胞的死亡。错误的DNA修复是受影响个体光敏性的基础，研究人员怀疑它也有助于Cockayne综合征的其他特征。目前尚不清楚ERCC8gene突变如何导致这种情况的所有不同特征。"}, {"name": "紫外线敏感综合症", "detail": "ERCC8gene中的至少一个突变可引起紫外线敏感综合征，这种疾病的特征是对太阳紫外线的异常敏感。紫外线敏感综合征患者容易晒伤，皮肤雀斑或其他肤色变化（色素沉着）。已知的突变将CSA蛋白（写为Trp361Cys或W361C）中361位的半胱氨酸替换为氨基酸色氨酸。尽管这种变化对蛋白质功能的影响尚不清楚，但它以某种方式阻止了细胞修复由紫外线引起的DNA损伤，并阻止了受损gene的转录。尚不清楚异常的CSA蛋白究竟如何引起紫外线敏感综合征的体征和症状。另外，尚不清楚为什么Trp361Cys突变会引起光敏性而没有Cockayne综合征的其他特征（如上所述）。"}]}' WHERE id = 23111;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ABCG8", "conditions": [{"name": "谷固醇血症", "detail": "已经在个体中鉴定出ABCG8gene的突变，该个体患有由于固醇转运蛋白缺陷引起的谷固醇血症，这削弱了其从体内消除植物固醇，胆固醇导致其积累的能力。症状包括动脉粥样硬化，脂肪性皮肤生长（黄瘤），贫血和其他血液异常"}, {"name": "其他疾病", "detail": "ABCG8gene的突变与胆结石的风险增加有关，这是由于有缺陷的固醇转运蛋白引起的，该蛋白泵入的胆固醇比溶解在胆管和胆囊的胆汁中的胆固醇高。"}]}' WHERE id = 23152;
UPDATE genes SET GHR_metadata_ch = '{"gene": "GUSB", "conditions": [{"name": "黏多醣贮积症VII型", "detail": "已发现GUSBgene中至少有55个突变会导致VII型粘多糖贮积病（MPS VII）。这些突变大多数会改变gene中的单个DNA结构单元（核苷酸）。所有导致MPS VII的突变都会降低或消除其功能-葡萄糖醛酸酶。（线路中断）的短缺（不足）-葡萄糖醛酸苷酶导致几乎所有组织和器官中溶酶体内硫酸皮肤素，硫酸乙酰肝素和硫酸软骨素的积累。这些GAG的积累增加了溶酶体的大小，这就是MPS VII中许多组织和器官增大的原因。研究人员认为，积累的GAG也可能干扰溶酶体内其他蛋白质的功能，破坏细胞的许多正常功能。"}]}' WHERE id = 23202;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ENPP1", "conditions": [{"name": "科尔病", "detail": "在科尔病患者中至少发现了三个ENPP1gene突变。患有这种疾病的人的皮肤区域异常浅色（色素沉着不足），通常在手臂和腿部，手掌和脚底的皮肤增厚斑点（点状掌plant角化病）。（换行）引起Cole病的ENPP1gene突变改变了SMB2结构域的结构，这改变了其与胰岛素受体的相互作用并影响细胞信号传导。由此导致的ENPP1在黑色素转运和角质形成细胞发育中的作用受损导致色素沉着不足和角化病。突变也可能损害蛋白质控制钙化的能力，这可能是某些患有这种疾病的人中异常钙沉积的原因。由于尚不清楚的原因，由这些ENPP1gene突变引起的胰岛素信号传导变化似乎并不影响血糖控制。"}, {"name": "婴儿全身动脉钙化", "detail": "ENPP1gene中的40多个突变已经在具有婴儿动脉钙化（GACI）的个体中被鉴定，GACI是一种危及生命的疾病，其特征在于将血液从心脏输送到身体其他部位的血管中的钙化（动脉）。导致GACI的突变被认为会损害ENPP1蛋白在细胞外ATP分解和焦磷酸盐产生中的作用。焦磷酸盐的可用性降低可能会干扰体内钙化的控制，并导致GACI的体征和症状。"}, {"name": "遗传性低磷佝偻病", "detail": "遗传学家庭参考提供有关遗传性低磷病的信息。"}, {"name": "其他疾病", "detail": "ENPP1gene的正常变异（多态性）与2型糖尿病（最常见的糖尿病形式）的风险增加有关。2型糖尿病是由激素胰岛素抵抗引起的，导致血糖控制受损。与2型糖尿病相关的多态性导致蛋白质的产生，该蛋白质的蛋白质结构单元（氨基酸）谷氨酰胺位于蛋白质位置121，写为Q121，而不是该位置的氨基酸赖氨酸（K121）。ENPP1蛋白的Q121变体比K121版本更强烈地抑制胰岛素受体的功能，增加胰岛素抵抗和2型糖尿病的风险。"}]}' WHERE id = 23214;
UPDATE genes SET GHR_metadata_ch = '{"gene": "DLL3", "conditions": [{"name": "脊椎肋骨发育不良", "detail": "已发现DLL3gene中至少有20个突变会导致1型脊椎肋骨发育不良，这是最常见的脊椎肋骨发育不良类型。这种情况的特征是脊柱和肋骨的骨骼异常发育。DLL3gene中已知的突变会阻止任何蛋白质的产生或导致异常短的无功能蛋白质的产生。当DLL3蛋白无功能或不存在时，NOTCH1蛋白异常活跃且不会振荡，因此无法正确进行体节分割。这导致在1型脊椎肋骨发育不全中看到的脊柱和肋骨骨骼畸形和融合。"}]}' WHERE id = 23217;
UPDATE genes SET GHR_metadata_ch = '{"gene": "LIPH", "conditions": [{"name": "常染色体隐性遗传不足", "detail": "已发现LIPHgene中的15个以上突变会引起常染色体隐性遗传不足，这种情况会导致头皮以及身体其他部位的头发稀疏生长（发育不足）。一些突变特定于具有巴基斯坦或日本血统的人群，或俄罗斯的马里和楚瓦什人群。LIPHgene突变导致产生几乎没有功能的脂肪酶H酶。没有功能性脂肪酶H，就不会产生LPA。缺乏LPA会损害许多细胞功能，包括组成毛囊的细胞的增殖和成熟。结果，毛囊在结构上异常并且通常不发达。不规则的毛囊会改变毛干的结构和生长，导致脆弱的头发容易断裂。表皮中脂肪酶H功能的缺乏可能导致皮肤问题，有时在常染色体隐性遗传不足的个体中可见。"}, {"name": "其他疾病", "detail": "LIPHgene中的突变也可引起称为常染色体隐性毛发的毛发状况。患有这种疾病的人的头发异常粗糙，干燥，细密，卷曲。毛发通常仅影响头皮，并且从出生时就存在。在某些情况下，受影响的人会随着年龄的增长而发展为低毛病。某些LIPHgene突变会在某些人中引起常染色体隐性毛发，在另一些人中甚至在同一家庭成员中引起常染色体隐性毛发不足（如上所述）。由于共同的遗传原因和重叠特征，不确定这两种情况是单独的疾病还是同一疾病谱的一部分。"}]}' WHERE id = 23223;
UPDATE genes SET GHR_metadata_ch = '{"gene": "CAVIN1", "conditions": [{"name": "先天性全身性脂肪营养不良", "detail": "已发现CAVIN1gene中的10多个突变会导致4型先天性全身性脂肪营养不良（也称为Berardinelli-Seip先天性脂肪营养不良）。这种罕见病的特征是几乎完全没有脂肪组织和非常肌肉的外观。脂肪组织短缺导致多种健康问题，包括在血流中循环的称为甘油三酯的高水平脂肪（高甘油三酯血症）和糖尿病。先天性全身性脂肪营养不良4型的其他特征包括肌肉无力，发育迟缓，关节异常，胃下部变窄（幽门狭窄）以及可能导致猝死的严重心律异常（心律不齐）。（换行）所有鉴定的CAVIN1gene突变都阻止细胞产生任何功能性cavin-1。缺乏这种蛋白质可能会损害小窝的形成。研究人员怀疑细胞膜上这些重要结构的短缺会破坏许多细胞功能。但是，尚不清楚cavin-1的缺乏如何导致体内脂肪的流失以及与先天性全身性脂肪营养不良4型相关的其他健康问题。"}]}' WHERE id = 23236;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SCNN1A", "conditions": [{"name": "假性低醛固酮增多症1型", "detail": "SCNN1Agene中至少有十二个突变会导致1型假性低醛固酮增多症（PHA1）。这种情况通常始于婴儿期，其特征是血液中钠水平低（低钠血症）和钾水平高（高钾血症）以及严重脱水。特别是，SCNN1Agene突变与常染色体隐性PHA1有关，这是一种严重的疾病形式，不会随着年龄的增长而改善。（换行）SCNN1Agene中的大多数突变导致ENaC通道的α亚基蛋白缩短。其他突变会删除一小块DNA或更改α亚基蛋白中的单个蛋白构建块（氨基酸）。SCNN1Agene突变导致ENaC通道活性降低或缺失。结果，钠的重吸收受到损害，导致低钠血症和常染色体隐性PHA1的其他体征和症状。ENaC通道在肺上皮细胞中的功能降低导致肺中过多的液体和反复的肺部感染。"}, {"name": "其他疾病", "detail": "一些患有囊性纤维化样综合征的人的SCNN1Agene具有突变或正常的gene变异（多态性）。患有囊性纤维化样综合征（也称为非典型囊性纤维化或支气管扩张伴或不伴2型汗液氯化物升高）的人的体征和症状类似于囊性纤维化，包括呼吸问题和肺部感染。但是，最常与囊性纤维化相关的geneCFTR的变化不能解释这种情况的发展。据认为，SCNN1Agene中的突变或gene变异可破坏钠转运和体液平衡，从而导致囊性纤维化样综合征的体征和症状。"}]}' WHERE id = 23255;
UPDATE genes SET GHR_metadata_ch = '{"gene": "MCM6", "conditions": [{"name": "乳糖不耐症", "detail": "MCM6gene内称为调控元件的特定DNA序列有助于控制称为LCT的附近gene的活性（表达）。LCTgene提供了制备称为乳糖酶的酶的说明。这种酶有助于消化乳糖，一种在牛奶和其他乳制品中发现的糖。成年后乳糖不耐症是由婴儿期后LCTgene表达逐渐降低引起的，这种情况在大多数人中都会发生。（换行）在调节LCTgene表达的调节元件中已经鉴定出至少四种变体。这些变化改变了调节元件中的单个DNA构建块（核苷酸）。每种变化都会导致小肠中乳糖酶的持续产生以及终生消化乳糖的能力。没有这些变化的人随着年龄的增长消化乳糖的能力降低，导致乳糖不耐症的体征和症状。"}]}' WHERE id = 23268;
UPDATE genes SET GHR_metadata_ch = '{"gene": "CLCNKA", "conditions": [{"name": "巴特综合症", "detail": "几位患有Bartter综合征的人的CLCNKAgene和一个称为CLCNKB的密切相关的gene都有突变。CLCNKBgene提供了制作非常相似的氯离子通道ClC-Kb的说明，该通道也存在于肾脏和内耳中。CLCNKA和CLCNKBgene突变的组合会导致威胁生命的疾病，称为IV型Bartter综合征。这种情况也称为产前Bartter综合征伴感音神经性耳聋，因为受影响的个体因内耳异常而听力下降。CLCNKA和CLCNKBgene中的（换行）突变阻止ClC-Ka和ClC-Kb通道在肾脏中转运氯离子。结果，肾脏不能正常地重吸收盐，并且过量的盐通过尿液流失（盐浪费）。异常的盐分流失破坏了体内离子的正常平衡。这种失衡是Bartter综合征的许多主要特征的基础，包括无法以预期的速度生长和增重（壮成长），脱水，便秘和尿量增加（多尿）。内耳ClC-Ka和ClC-Kb功能的丧失是IV型Bartter综合征的听力损失特征。"}, {"name": "其他疾病", "detail": "研究表明，CLCNKAgene中的几种正常变异（多态性）可能与盐敏感性高血压有关，盐敏感性高血压是一种与血液中盐水平升高有关的高血压。但是，CLCNKA多态性与高血压之间的这种关联尚未得到证实。CLCNKAgene的变化可能会通过改变肾脏将盐重吸收到血液中的能力来影响血压。"}]}' WHERE id = 23311;
UPDATE genes SET GHR_metadata_ch = '{"gene": "HESX1", "conditions": [{"name": "合并垂体激素缺乏症", "detail": "遗传学家庭参考资料提供有关合并垂体激素缺乏症的信息。"}, {"name": "视光发育不良", "detail": "在患有视光发育不良的人中，已经鉴定出HESX1gene中至少有五个突变。这些突变中的一些改变了HESX1gene中的单个DNA构建块（碱基对），而另一些则插入或删除了该gene中的遗传物质。该gene中的突变改变了HESX1蛋白的功能，例如通过阻止其与DNA结合并抑制其他gene的活性。HESX1gene突变破坏垂体，视神经和其他大脑结构的形成和早期发育。这些大脑发育异常导致视光发育不良的特征。（换行）研究表明，HESX1gene的突变是视光发育不良的罕见原因。"}, {"name": "其他疾病", "detail": "已经发现十几种HESX1gene突变会导致没有其他视光发育不良特征的人垂体发育不全（发育不全）。垂体发育不全导致生长，繁殖和其他关键身体功能所需的激素短缺。受影响的个体也可能有影响身体其他部位的体征和症状，包括生殖器异常，视力障碍，独特的面部特征和额外的（多余的）手指。HESX1gene中的（换行）突变导致缺陷或无功能的HESX1蛋白的产生，从而在胚胎发育的关键阶段破坏垂体的形成。一些突变阻止HESX1蛋白与DNA结合并抑制其他gene的活性。其他突变会阻止HESX1蛋白与PROP1蛋白相互作用以协调大脑发育。目前尚不清楚HESX1gene的突变如何引起影响身体其他部位的体征和症状。"}]}' WHERE id = 23319;
UPDATE genes SET GHR_metadata_ch = '{"gene": "CLCNKB", "conditions": [{"name": "巴特综合症", "detail": "在III型Bartter综合征患者中已鉴定出CLCNKBgene的30多个突变。这种形式的疾病（也称为经典Bartter综合征）始于儿童期，往往不如其他类型的Bartter综合征严重。(换行)导致Bartter综合征III型的许多突变都删除了整个CLCNKBgene。其他突变会改变ClC-Kb通道中的单个蛋白质结构单元（氨基酸），或导致异常短而无功能的ClC-Kb版本。功能性ClC-Kb通道的丧失会损害氯离子在肾脏中的运输。结果，肾脏不能正常地重吸收盐，并且过量的盐通过尿液流失（盐浪费）。异常的盐分流失破坏了体内离子的正常平衡。这种失衡是III型Bartter综合征的许多主要特征的基础。(换行)几位患有更严重Bartter综合征的人在CLCNKBgene和一个密切相关的称为CLCNKA的gene中都有突变。CLCNKAgene提供了制作非常相似的氯离子通道ClC-Ka的说明，该通道也存在于肾脏和内耳中。CLCNKA和CLCNKBgene突变的组合会导致威胁生命的疾病，称为IV型Bartter综合征或伴有感音神经性耳聋的产前Bartter综合征。除了浪费盐分外，这种疾病的特征还在于内耳ClC-Ka和ClC-Kb功能丧失导致的听力丧失。"}, {"name": "吉特曼综合症", "detail": "CLCNKBgene中的突变是吉特曼综合症的罕见原因。像导致Bartter综合征的突变一样，与Gitelman综合征相关的遗传变化会损害肾脏重吸收盐的能力，从而导致盐的浪费。盐运输异常也会影响其他离子的重吸收，包括钾，镁和钙的离子。由此导致的体内离子失衡导致吉特曼综合症的特征。"}, {"name": "其他疾病", "detail": "CLCNKBgene的常见变异（多态性）与盐敏感性高血压有关，盐敏感性高血压是一种与血液中盐水平升高有关的高血压。该多态性用ClC-Kb通道（也称为Thr481Ser或T481S）中481位的氨基酸丝氨酸取代了氨基酸苏氨酸。这种遗传变化增加了ClC-Kb通道的活性，该通道指导肾脏将更多的盐重新吸收到血液中。过量的盐会升高血压并增加患高血压的风险。"}]}' WHERE id = 23354;
UPDATE genes SET GHR_metadata_ch = '{"gene": "AMHR2", "conditions": [{"name": "持续性苗勒管综合征", "detail": "持续性苗勒氏管综合征2型是一种影响男性的性发育障碍，是由AMHR2gene突变引起的。患有这种情况的男性除了正常的男性生殖器官外，还具有女性生殖器官。在患有持续性苗勒氏管综合征2型的人群中，至少鉴定出AMHR2gene的24个突变。大多数突变会改变AMH受体2型蛋白中的单个蛋白结构单元（氨基酸）。其他突变会导致过早的停止信号，从而导致蛋白质异常短。还有其他突变从AMHR2gene中删除DNA区域，这改变了蛋白质的说明。最常见的突变是27个DNA构建块（核苷酸）的缺失，发生在大约一半具有AMHR2gene突变的受影响个体中。AMHR2gene中的（换行）突变导致异常蛋白质卡在苗勒管细胞内而不在表面上发现。因此，细胞不能接收凋亡信号。结果，苗勒管持续存在并成为子宫和输卵管。由于2型AMH受体不参与男性生殖器官的形成，因此受影响的男性也具有男性生殖器官。"}]}' WHERE id = 23357;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ASL", "conditions": [{"name": "精氨酸琥珀酸尿症", "detail": "ASLgene中的一些突变与精氨酸琥珀酸尿症有关，精氨酸琥珀酸尿症是一种遗传性疾病，其特征是嗜睡，癫痫发作，发育迟缓，智力残疾，进行性肝损伤，皮肤病变和头发脆弱。这是由于有缺陷的精氨琥珀酸裂合酶引起的，该酶导致体内氨积累增加，从而导致神经系统问题。"}]}' WHERE id = 23376;
UPDATE genes SET GHR_metadata_ch = '{"gene": "COL2A1", "conditions": [{"name": "软骨形成", "detail": "已经发现COL2A1gene的突变导致一种称为2型或Langer-Saldino型的软骨发育不全，这是一种罕见的骨发育障碍，其特征是手臂和腿部短，胸部狭窄，肋骨短，肺部发育不全，脊柱和骨盆缺乏正常的骨形成（骨化），婴儿的这些严重问题导致死亡。这是由于pro-alpha1（II）链的变化阻止了成熟II型胶原蛋白的正常生产。"}, {"name": "捷克发育不良", "detail": "COL2A1gene的突变会导致捷克发育不良，这种情况会影响关节功能和骨骼发育。这是由于前α1（II）链的变化干扰了成熟II型胶原蛋白的正常生产。"}, {"name": "软骨发育不良", "detail": "已经发现COL2A1gene的突变会导致软骨发育不良，这是一种严重的骨生长障碍，其特征是身体小，四肢短，脊柱和骨盆骨形成异常。这是由于氨基酸替代或由于缺少关键部分而引起的。pro-alpha1（II）链干扰成熟II型胶原的正常产生。"}, {"name": "最尖锐的发育不良", "detail": "已发现COL2A1gene突变会导致最尖锐的发育异常，这是一种骨骼生长障碍，其特征是身材矮小（侏儒症）以及其他骨骼异常，视力和听力问题。这是由于产生异常短的pro-alpha1（II）链，该链与正常长度的链连接，导致异常短的II型胶原分子。"}, {"name": "Legg-Calv-Perthes病", "detail": "已经发现COL2A1gene的突变引起Legg-Calvé-Perthes病，这是一种以股骨头破裂导致髋部疼痛和跛行为特征的骨骼异常。这是由于前α1（II）链中的氨基酸取代导致产生较不稳定的II型胶原纤维所致。"}, {"name": "Platyspondylic致死性骨骼发育不良，Torrance型", "detail": "COL2A1gene的突变已经在那些患有颈椎病致死性骨骼发育不良、Torrance型、一种严重的骨骼生长障碍的人身上被发现，这种拥有属性是手臂和腿部非常短、胸部短肋骨、骨盆骨骼发育不全、短指、扁平脊柱骨骼（颈椎病）和下背部过度弯曲（前凸）的人身上。这是由于前α1（II）链的C肽结构域发生突变，导致其在软骨细胞中积聚，从而导致II型胶原蛋白产生量减少。"}, {"name": "脊柱干met端发育不良，Strudwick型", "detail": "已发现COL2A1gene突变会导致脊椎干met端发育异常，Strudwick型，一种骨骼生长障碍，其特征是侏儒症，骨骼异常和视力问题。这是由于前α1（II）链中的单个氨基酸变化导致抑制稳定的三链II型胶原引起的。"}, {"name": "先天性脊柱干phy端发育不良", "detail": "已发现COL2A1gene突变会导致先天性脊柱干phy端发育异常，这是一种骨骼生长障碍，其特征是侏儒症，骨骼异常，视力和听力问题。这是由于pro-alpha1（II）链产生异常或较短，从而干扰了成熟的三链II型胶原分子的形成。"}, {"name": "脊柱侧周发育不良", "detail": "已发现COL2A1gene的突变会导致脊柱侧周发育不良，这是一种骨骼生长障碍，其特征是肩突，短指，身材矮小和其他骨骼异常。这是由于C肽结构域中的COL2A1gene突变引起的，这对于将pro-alpha1（II）链彼此连接并破坏II型胶原的形成是必需的。"}, {"name": "斯蒂克勒综合症", "detail": "已发现COL2A1gene突变会导致I型Stickler综合征，这种疾病的特征是独特的面部外观，眼睛异常，听力下降和关节问题。这是由于COL2A1gene突变导致pro-alpha1（II）链产生异常短或丢失，从而破坏了II型胶原的形成。"}, {"name": "其他疾病", "detail": "已发现COL2A1gene突变会导致股骨头缺血性坏死，这种情况始于儿童期，但与Legg-Calvé-Perthes病相似。其特征在于由于血液供应不足和骨修复不足导致的股骨破裂，导致疼痛，跛行并导致腿的长度不等。"}]}' WHERE id = 23378;
UPDATE genes SET GHR_metadata_ch = '{"gene": "FZD4", "conditions": [{"name": "家族性渗出性玻璃体视网膜病变", "detail": "在患有称为家族性渗出性玻璃体视网膜病变的眼疾患者中，已经鉴定出FZD4gene的20多个突变。这些突变中的一些改变了frizzled-4中的单个蛋白质结构单元（氨基酸），而另一些则插入或删除了FZD4gene中的遗传物质。大多数FZD4突变会减少细胞内产生的frizzled-4的数量。其他突变被认为导致产生不能与norrin结合的不稳定蛋白质。（折线）frizzled-4量的减少会破坏发育中眼睛的化学信号传导，从而干扰视网膜边缘血管的形成。在一些家族性渗出性玻璃体视网膜病变患者中，由此导致的对该组织的异常血液供应导致视网膜损伤和视力丧失。"}]}' WHERE id = 23390;
UPDATE genes SET GHR_metadata_ch = '{"gene": "OPA1", "conditions": [{"name": "1型视神经萎缩", "detail": "已发现OPA1gene中至少240个突变会导致1型视神经萎缩。这种情况通常会导致从儿童期开始的视力丧失，并随着时间的推移而恶化。受影响的个人也可能在色觉方面存在问题，特别是区分蓝色和绿色的色调。(换行)导致1型视神经萎缩的大多数OPA1gene突变会在制造OPA1蛋白的说明中产生过早的停止信号。结果，产生异常小的蛋白质，其可能不稳定并迅速分解。在丹麦血统的个体中引起1型视神经萎缩的最常见突变是通过删除OPA1gene（写为2826delT）中的一个DNA构建块（核苷酸）而导致异常小的蛋白质。（换行）导致1型视神经萎缩的OPA1gene突变导致线粒体功能问题。线粒体变得畸形和混乱，能量产生能力降低。mtDNA的维持也可能受损，导致mtDNA突变，也导致线粒体功能障碍。含有这些功能不良的线粒体的细胞更容易凋亡。特别地，视网膜内称为视网膜神经节细胞的细胞随时间死亡。视网膜神经节细胞的特殊延伸称为轴突，形成视神经，因此当视网膜神经节细胞死亡时，视神经分解（萎缩）并且不能将视觉信息传递给大脑。随着视神经萎缩，视力恶化，导致1型视神经萎缩的体征和症状。（换行）虽然OPA1蛋白存在于全身细胞中，但视网膜神经节细胞似乎对OPA1gene突变的影响特别敏感。这些细胞具有特别高的能量需求，当线粒体功能发生变化和能量产生减少时，它们更可能发生故障并死亡。"}, {"name": "进行性眼外肌麻痹", "detail": "遗传学家庭参考资料提供有关进行性眼外肌麻痹的信息。"}, {"name": "其他疾病", "detail": "OPA1gene突变的个体中约有20%患有视神经萎缩1型（如上所述）的视力问题以及其他健康问题。一些OPA1gene突变会导致一种称为1型视神经萎缩和耳聋的疾病，从而导致视力丧失和听力丧失。（换行）OPA1突变也可引起称为常染色体显性视神经萎缩（ADOA）-plus综合征的病症。ADOA+综合征包括视力和听力丧失、控制眼球运动的肌肉无力（进行性眼外肌麻痹）、平衡和协调困难（共济失调）、用于肌肉运动和感觉的神经紊乱（运动和感觉神经病）和骨骼肌无力（肌病）。(换行)由OPA1gene突变引起的最严重的病症是Behr综合征，其特征在于从幼儿期开始的神经问题。患有Behr综合征的人会出现视神经萎缩，脑功能障碍（脑病），四肢感觉丧失和无力（周围神经病变），运动协调困难（共济失调），进食和消化困难以及发育迟缓。（换行）这些病症的特征可能是由于线粒体功能差导致多个组织中细胞的损失。目前尚不清楚为什么OPA1gene突变仅影响1型视神经萎缩患者的眼睛，而在其他患者中影响更广泛。研究人员推测，某些OPA1gene突变会导致蛋白质改变，从而干扰该gene非突变拷贝产生的正常蛋白质的功能，从而进一步损害OPA1蛋白质的功能。（换行）虽然1型视神经萎缩和ADOA plus综合征是由每个细胞中一个gene拷贝中的OPA1gene突变引起的，但患有Behr综合征的个体在每个细胞中的两个OPA1gene拷贝中都有突变。该gene两个拷贝的改变都大大减少了功能性OPA1蛋白的量，这很可能导致Behr综合征的严重体征和症状。"}]}' WHERE id = 23411;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ABCD4", "conditions": [{"name": "甲基丙二酸血症伴高半胱氨酸尿症", "detail": "ABCC8gene的突变与一种称为甲基丙二酸血症的疾病有关，该疾病伴有高半胱氨酸尿症，cblJ型是由于蛋白质缺陷导致的，该缺陷阻止了维生素B12的释放，加工和高半胱氨酸向蛋氨酸的转化。该病的特征是发育迟缓，眼睛缺陷，神经系统问题和血液异常。"}]}' WHERE id = 23429;
UPDATE genes SET GHR_metadata_ch = '{"gene": "TYMP", "conditions": [{"name": "线粒体神经胃肠道脑病", "detail": "在线粒体神经胃肠道脑病（MNGIE）患者中已鉴定出TYMPgene的约50个突变。TYMP突变大大降低或消除了胸苷磷酸化酶的活性。这种酶的缺乏使得胸苷在体内积累到非常高的水平。过量的胸苷似乎会破坏mtDNA，破坏其通常的维护和修复。结果，突变可以在mtDNA中积累，导致其变得不稳定。线粒体的mtDNA也可能比平时少（mtDNA消耗）。这些遗传变化损害线粒体的正常功能。尽管mtDNA异常是MNGIE疾病特征性消化和神经系统问题的基础，但尚不清楚线粒体缺陷如何导致该疾病的特定特征。"}]}' WHERE id = 23474;
UPDATE genes SET GHR_metadata_ch = '{"gene": "KCNQ3", "conditions": [{"name": "良性家族性新生儿癫痫发作", "detail": "在一些患有良性家族性新生儿癫痫发作（BFNS）的人中发现了KCNQ3gene的突变，这种疾病的特征是新生儿反复发作。癫痫发作始于生命的第3天左右，通常在1至4个月内消失。在患有这种情况的人中至少鉴定出三个突变，这些突变改变了KCNQ3蛋白中的单个蛋白结构单元（氨基酸）。这些突变的结果是，M电流降低。研究人员认为，将电流降低25%足以引起BFNS。M电流降低会导致神经元过度兴奋，这已知会引起癫痫发作。目前尚不清楚为什么癫痫发作会在4个月大时停止。有人提出，由KCNQ2和KCNQ3蛋白形成的钾通道在防止新生儿神经元过度兴奋中起主要作用，但在婴儿期也会出现其他机制。"}]}' WHERE id = 23518;
UPDATE genes SET GHR_metadata_ch = '{"gene": "CXCR4", "conditions": [{"name": "Waldenström巨球蛋白血症", "detail": "大约30%的Waldenström巨球蛋白血症患者发现CXCR4gene突变。这种罕见形式的血癌的特征在于骨髓中称为淋巴浆细胞的异常白细胞过多以及称为IgM的蛋白质的过量产生。这些突变是在一个人的一生中获得的，仅存在于异常的白细胞中。这种类型的遗传变化称为体细胞突变，不是遗传的。Waldenström巨球蛋白血症被认为是由多种遗传变化引起的，其中之一可能是CXCR4gene突变。（换行）涉及Waldenström巨球蛋白血症的CXCR4gene突变导致产生不能内化的异常短的CXCR4蛋白，延长由该蛋白激活的信号传导。这种信号传导导致含有异常蛋白质的细胞的存活和增殖增强，这可能有助于Waldenström巨球蛋白血症特有的淋巴浆细胞的丰度。"}, {"name": "其他疾病", "detail": "已发现CXCR4gene中至少有9个突变会引起WHIM综合征，该病的特征是免疫功能受损以及细菌和病毒反复感染。在患有Waldenström巨球蛋白血症的个体中也发现了几种引起WHIM综合征的突变（如上所述）。但是，在WHIM综合征中，突变通常是遗传性的，并且存在于人体的每个细胞中（称为种系突变）。（换行）与Waldenström巨球蛋白血症一样，引起WHIM综合征的CXCR4gene突变会损害CXCR4蛋白的内在化。研究人员认为，膜中CXCR4蛋白比平时长的白细胞异常保留在骨髓中（这种情况称为髓鞘炎）。缺乏通过身体循环的这些免疫细胞可能会损害身体对细菌和病毒的免疫反应，从而导致WHIM综合征中常见的反复感染。"}]}' WHERE id = 23520;
UPDATE genes SET GHR_metadata_ch = '{"gene": "TK2", "conditions": [{"name": "TK2相关的线粒体DNA耗竭综合征，肌病形式", "detail": "已发现TK2gene中的30多个突变会导致TK2相关的线粒体DNA耗竭综合征，即肌病形式（TK2-MDS）。TK2-MDS是一种遗传性疾病，通常在儿童早期就开始引起进行性肌无力（肌病）。导致这种情况的突变中约三分之二改变了胸苷激酶2中的单个蛋白质结构单元（氨基酸）。所有TK2gene突变都会导致酶活性降低，从而损害mtDNA核苷酸的回收利用。可用于修复和生产mtDNA分子的核苷酸短缺导致mtDNA量减少（称为mtDNA消耗）并损害线粒体功能。更大的mtDNA消耗倾向于导致更严重的体征和症状。患有TK2-MDS的人的肌肉细胞中mtDNA的含量非常低，范围为正常值的5%至30%。其他组织可以具有正常量至正常量的mtDNA的60%。即使是具有相同突变的个体，受影响个体之间mtDNA丢失量变化的原因也未知。(换行)目前尚不清楚为什么TK2gene突变通常仅影响肌肉组织，但是当mtDNA丢失并且细胞中产生的能量减少时，肌肉细胞的高能量需求可能使它们最容易受到细胞死亡的影响。"}, {"name": "进行性眼外肌麻痹", "detail": "遗传学家庭参考资料提供有关进行性眼外肌麻痹的信息。"}]}' WHERE id = 23568;
UPDATE genes SET GHR_metadata_ch = '{"gene": "TYR", "conditions": [{"name": "眼皮肤白化病", "detail": "在1型眼皮肤白化病患者中已鉴定出TYRgene的100多个突变。这些突变破坏了黑色素的正常产生，从而减少了头发，皮肤和眼睛的着色，并导致视力问题。大多数TYR突变会消除酪氨酸酶的活性，从而阻止黑素细胞终生产生任何黑色素。这些突变引起一种称为1A型（OCA1A）的眼皮肤白化病。患有这种白化病的人有白发，浅色的眼睛和非常苍白的皮肤，不会晒黑。TYRgene中的其他突变会降低但不会消除酪氨酸酶活性。这些允许产生一些黑色素的突变会导致1B型眼皮肤白化病（OCA1B）。1B型人也出生时有白发，浅色的眼睛和苍白的皮肤，但头发和眼睛的颜色通常会随着时间的推移而变黑，皮肤可能会晒黑。"}]}' WHERE id = 23574;
UPDATE genes SET GHR_metadata_ch = '{"gene": "GCH1", "conditions": [{"name": "多巴反应性肌张力障碍", "detail": "已发现GCH1gene中的140多个突变会引起dopa反应性肌张力障碍。这种情况的特征是不自主的肌肉收缩（肌张力障碍），震颤和其他不受控制的运动，通常对称为左旋多巴的药物治疗有反应。当每个细胞中一个拷贝的GCH1gene突变时，就会产生Dopa反应性肌张力障碍。导致这种情况的大多数GCH1gene突变会改变GTP环水解酶1酶中的单个氨基酸。研究人员认为，这种异常的酶可能会干扰正常版本的GTP环水解酶1的活性，该酶是由没有突变的gene拷贝产生的。结果，受影响个体中的工作酶量减少了80%或更多。功能性GTP环水解酶1酶的减少导致产生较少的多巴胺和5-羟色胺，导致运动问题和dopa反应性肌张力障碍的其他特征。"}, {"name": "四氢生物蝶呤缺乏症", "detail": "已发现GCH1gene中至少有七个突变会导致四氢生物蝶呤缺乏。当这种情况是由GCH1gene突变引起的时，它被称为GTP环水解酶1（GTPCH1）缺乏症。GTPCH1缺乏症约占所有四氢生物蝶呤缺乏症病例的4%。（换行）当每个细胞中有两个GCH1gene拷贝发生突变时，就会导致GTPCH1缺陷。导致这种情况的大多数突变都会改变GTP环水解酶1中的单个氨基酸。这些突变大大降低或消除了该酶的活性。如果没有足够的GTP环水解酶1，则几乎不会产生四氢生物蝶呤。结果，该辅因子不能参与化学反应，例如苯丙氨酸向酪氨酸的转化。如果苯丙氨酸不转化为酪氨酸，它可以在血液和其他组织中积累到毒性水平。大脑中的神经细胞对苯丙氨酸水平特别敏感，这就是为什么过量的这种物质会导致脑损伤的原因。(换行)此外，GTP环化水解酶1活性的降低破坏了大脑中某些神经递质的产生。由于神经递质对于正常的大脑功能是必需的，因此这些化学物质水平的变化会导致GTPCH1缺乏症患者的智力残疾。四氢生物蝶呤缺乏症比多巴反应性肌张力障碍更严重，可能是因为GCH1gene的两个拷贝都发生了突变，这导致了比多巴反应性肌张力障碍更严重的酶缺乏症，后者只有一个拷贝的gene发生了突变。"}]}' WHERE id = 23580;
UPDATE genes SET GHR_metadata_ch = '{"gene": "MANBA", "conditions": [{"name": "β-甘露糖苷病", "detail": "在MANBAgene中已鉴定出约12种引起β-甘露糖苷病的突变。突变导致β-甘露糖苷酶活性很小或没有活性，并干扰该酶在分解含甘露糖的二糖中发挥作用的能力。这些二糖逐渐积聚在溶酶体中，导致细胞功能异常，从而导致β-甘露糖苷病的体征和症状。"}]}' WHERE id = 23604;
UPDATE genes SET GHR_metadata_ch = '{"gene": "FLT4", "conditions": [{"name": "米尔罗伊病", "detail": "已发现FLT4gene中至少有19个突变引起Milroy病。该gene中的大多数突变会在称为酪氨酸激酶结构域的区域中改变单个蛋白质构建块（氨基酸）。这些区域的突变破坏VEGFR-3信号传导并导致携带淋巴液（淋巴管）的管变小或不存在。如果淋巴液没有正确运输，它会积聚在身体组织中并引起肿胀（淋巴水肿）。尚不清楚FLT4gene的突变如何导致Milroy病的其他体征和症状。"}]}' WHERE id = 23611;
UPDATE genes SET GHR_metadata_ch = '{"gene": "FKBP14", "conditions": [{"name": "埃勒斯-当洛二氏综合症", "detail": "FKBP14gene的突变是罕见的Ehlers-Danlos综合征（称为脊柱后凸型（kEDS-FKBP14））的原因之一。Ehlers-Danlos综合征是一组影响支持皮肤，骨骼，血管以及许多其他器官和组织的结缔组织的疾病。脊柱后凸型的特征是关节活动范围异常大（活动过度）；肌张力弱（肌张力低下）；严重，渐进的脊柱弯曲（脊柱后凸畸形），可能会干扰呼吸；和脆弱的血管，可能会撕裂（破裂），导致内部出血。当后凸型是由FKBP14gene突变引起的时，受影响的个体也可能有肌肉萎缩（萎缩）和出生时出现的听力损失。（换行）已发现至少四个FKBP14gene突变引起Ehlers-Danlos综合征的脊柱后凸型。这些突变会影响每个细胞中gene的两个拷贝，从而异常复制（重复）或从gene中删除少量DNA。额外或缺失的遗传物质阻止gene产生功能性FK506结合蛋白14。该蛋白质的损失破坏了内质网的活性，包括折叠前胶原和加工细胞外基质的其他组分。结果，细胞外基质变得杂乱无章，这削弱了整个身体的结缔组织并导致该疾病的体征和症状。"}]}' WHERE id = 23622;
UPDATE genes SET GHR_metadata_ch = '{"gene": "HPS1", "conditions": [{"name": "Hermansky-Pudlak综合征", "detail": "已发现HPS1gene中至少有31个突变引起1型Hermansky-Pudlak综合征。患有这种疾病的个体通常患有眼皮肤白化病，这种疾病的特征是皮肤白皙，头发和眼睛浅色以及视力低下。他们也可能有出血问题和称为肺纤维化的严重肺部疾病。导致1型Hermansky-Pudlak综合征的HPS1gene突变损害BLOC-3的正常功能，破坏整个身体细胞中LRO的大小，结构和功能。最常见的突变会导致HPS1gene内的遗传物质重复，并在波多黎各西北部的人群中发现。具体来说，此突变会导致该gene内额外的16个DNA构建块（核苷酸）（写为1470_1486dup16）。(换行)由于异常的黑素体不能正确分布黑色素，患有Hermansky-Pudlak综合征的人的皮肤，头发和眼睛的颜色异常浅。血小板内不存在致密颗粒会导致受影响个体的出血问题。由于某些肺细胞中的LRO异常，肺纤维化也可能发展。"}]}' WHERE id = 23643;
UPDATE genes SET GHR_metadata_ch = '{"gene": "TINF2", "conditions": [{"name": "先天性角化不良", "detail": "在先天性角化病患者中已鉴定出TINF2gene中至少有15个突变，包括这种疾病的严重形式，称为Revesz综合征。先天性角化病的特征是肤色（色素沉着），口腔内白斑（口腔白斑）以及指甲和脚趾甲异常形成（指甲营养不良）的变化。先天性角化病患者发生几种危及生命的疾病的风险增加，包括癌症和称为肺纤维化的进行性肺病。许多受影响的个体也会出现一种称为再生障碍性贫血的严重疾病，也称为骨髓衰竭，这种情况发生在骨髓不能产生足够的新血细胞时。(换行)导致先天性角化不全的大多数TINF2gene突变会改变TINF2蛋白中的单个蛋白结构单元（氨基酸），可能会破坏该蛋白的功能。突变导致庇护蛋白复合物功能障碍，干扰其对端粒的保护并导致端粒长度减少。端粒缩短会导致遗传物质受损，导致细胞停止分裂或发生凋亡。(换行)快速分裂的细胞特别容易受到端粒缩短的影响。结果，先天性角化病患者可能会遇到各种影响体内细胞快速分裂的问题，如甲床细胞，毛囊，皮肤，口腔内壁（口腔粘膜）和骨髓。（换行）端粒维持不足导致的染色体断裂和不稳定可能导致遗传变化，使细胞以不受控制的方式分裂，导致一些先天性角化不良患者发生癌症。"}]}' WHERE id = 23651;
UPDATE genes SET GHR_metadata_ch = '{"gene": "NPC2", "conditions": [{"name": "尼曼匹克症", "detail": "More than 20 mutations in the NPC2 gene have been found to cause Niemann-Pick disease type C2.  This type of Niemann-Pick disease is characterized by a buildup of fat within cells that leads to movement problems, neurological impairment, lung and liver disease, and speech and feeding problems. The NPC2 gene mutations that cause Niemann-Pick disease type C2 reduce or eliminate NPC2 protein activity, which prevents movement of cholesterol and other lipids, leading to their accumulation in cells. Because these lipids are not in their proper location in cells, many normal cell functions that require lipids (such as cell membrane formation) are impaired. The accumulation of lipids and the cell dysfunction eventually leads to cell death, causing the tissue and organ damage seen in Niemann-Pick disease type C2.\r"}]}' WHERE id = 23678;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ARSA", "conditions": [{"name": "变色性白细胞营养不良", "detail": "ARID1Bgene的突变与异色性白细胞营养不良有关，异色性白细胞营养不良是由于芳基硫酸酯酶a活性破坏导致神经系统功能恶化而引起的疾病。这导致硫化物分解的损害，导致其积累，髓磷脂的破坏和白质的损失（脑白质营养不良）。该疾病的临床特征包括周围神经病变，尿失禁，癫痫发作，瘫痪，言语丧失，失明和听力下降。"}]}' WHERE id = 23692;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SBDS", "conditions": [{"name": "Shwachman-Diamond综合征", "detail": "在Shwachman-Diamond综合征患者中至少鉴定出SBDSgene的20个突变。这些突变大多数是由于SBDSgene与称为假gene的非常相似但无功能的DNA片段之间的遗传物质交换所致，假gene非常靠近7号染色体上的SBDSgene。这种类型的DNA交换称为gene转换。来自假gene的遗传物质含有错误，当引入SBDSgene时，会破坏gene指令用于制造蛋白质的方式。(换行)Shwachman-Diamond综合征患者中两个最常见的突变是由SBDSgene和附近的假gene之间的交换引起的。这些突变之一，写为258+2T>C，改变了称为内含子2的gene区域中的单个DNA构建块（核苷酸）。这种称为剪接位点突变的突变阻止了任何功能性SBDS蛋白的产生。另一个常见突变，写为183-184TA>CT，改变了SBDSgene中的两个核苷酸。这种遗传变化在制备SBDS蛋白的说明中引入了过早的停止信号。目前尚不清楚这种突变是导致蛋白质异常缩短还是阻止任何蛋白质的产生。（换行）当突变损害SBDS蛋白的正常功能时，导致Shwachman-Diamond综合征的特征。由于该蛋白的功能未知，研究人员尚未确定这些突变是如何导致骨髓异常，癌症风险增加以及该病的其他体征和症状的基础。"}]}' WHERE id = 23717;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SHANK3", "conditions": [{"name": "22q13.3缺失综合征", "detail": "22q13.3缺失综合征（也称为Phelan-McDermid综合征）的特征性体征和症状是由22号染色体长（q）臂末端附近的缺失引起的。通常被删除的染色体区域被认为包含许多gene，包括SHANK3gene。缺失的结果是，患有这种情况的人每个细胞中只有一个SHANK3gene拷贝，而不是通常的两个拷贝。(换行)研究人员认为，SHANK3gene的缺失和SHANK3蛋白产生量的减少是22q13.3缺失综合征的许多特征的原因。由于缺乏SHANK3蛋白而导致的神经元之间突触功能和细胞间通讯功能的下降被认为是导致22q13.3缺失综合征患者发育迟缓，智力残疾以及言语缺失或严重延迟的原因。"}, {"name": "自闭症谱系障碍", "detail": "在患有自闭症谱系障碍（ASD）的人中至少发现了43个SHANK3gene突变，这是一种以沟通和社交技能受损以及重复行为为特征的多种疾病。这些突变大多数破坏SHANK3蛋白的功能或阻止蛋白的产生。尚不清楚SHANK3gene的变化如何与患ASD的风险相关。研究人员怀疑神经元之间的交流中断会导致这种情况的发展。其他gene和环境因素的变化也被认为会影响这种复杂疾病的风险。"}]}' WHERE id = 23725;
UPDATE genes SET GHR_metadata_ch = '{"gene": "TGM1", "conditions": [{"name": "板状鱼鳞病", "detail": "已经发现TGM1gene中的许多突变引起层状鱼鳞病，这种情况导致覆盖身体大部分的鳞状皮肤和其他皮肤异常。导致这种情况的某些TGM1gene突变会改变转谷氨酰胺酶1酶中的单个DNA结构单元（核苷酸）。最常发生的突变（写为877-2A>G）影响gene指令拼接在一起形成酶的方式，并导致异常缩短的无功能酶。其他TGM1gene突变会导致转谷氨酰胺酶1酶无法正常运行，异常短或不产生。缺乏功能性转谷氨酰胺酶1会阻止角化细胞包膜的形成，从而导致层状鱼鳞病的皮肤异常。"}, {"name": "其他疾病", "detail": "除层状鱼鳞病（如上所述）外，还发现TGM1gene突变引起其他形式的鱼鳞病。在一种称为自愈胶棉婴儿的类型中，受影响的婴儿出生时具有一个紧密，透明的护套，覆盖着他们的皮肤，称为胶棉膜。该膜通常在生命的最初几周内干燥并剥落，受影响的婴儿通常在几个月内表现出接近正常的皮肤。另一种类型的鱼鳞病，称为泳衣鱼鳞病，其特征是鳞状皮肤仅限于躯干。"}]}' WHERE id = 23739;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ACAN", "conditions": [{"name": "家族性剥离性骨软骨炎", "detail": "已发现ACAN中的突变会导致家族性剥离性骨软骨炎，这是由于聚集蛋白聚糖蛋白CLD中的单个氨基酸变化阻止了其与无功能软骨其他成分的附着。这种情况的临床特征包括影响膝盖，肘部，臀部或脚踝的多处骨病变，身材矮小，骨关节炎的早期发展。"}, {"name": "椎间盘疾病", "detail": "在椎间盘疾病中发现了ACAN突变。一种以一个或多个椎间盘退变为特征的病症，该椎间盘将易于发生疝的脊柱（椎骨）的骨骼分开，导致背部，颈部，手臂和腿部疼痛，虚弱和麻木的小骨生长（骨刺），行走问题，膀胱和肠道控制，运动和神经损伤受损。"}, {"name": "其他疾病", "detail": "ACAN中的突变与称为脊椎干met端发育不良，聚集蛋白聚糖型和脊椎干phy端发育不良，金伯利型的两种情况有关。"}]}' WHERE id = 23752;
UPDATE genes SET GHR_metadata_ch = '{"gene": "GSN", "conditions": [{"name": "II型格子状角膜营养不良", "detail": "At least two mutations in the GSN gene cause lattice corneal dystrophy type II. This condition is characterized by the accumulation of protein clumps called amyloid deposits in many tissues throughout the body, including the clear, outer covering of the eye (the cornea); the skin; and the nerves. These protein clumps contain the gelsolin protein. (line-break) GSN gene mutations that cause lattice corneal dystrophy type II change a single protein building block (amino acid) in the gelsolin protein: the amino acid aspartic acid at position 187. The most common mutation replaces the aspartic acid with the amino acid asparagine (written as Asp187Asn or D187N). Another mutation replaces the aspartic acid with the amino acid tyrosine (written as Asp187Tyr or D187Y). (line-break) The amino acid change is found in both the cellular and secreted forms of the gelsolin protein. However, only the secreted form of the protein is involved in the amyloid deposits. The altered gelsolin protein is broken down differently than the normal protein, which results in an abnormal gelsolin protein fragment that is released from the cell. These protein fragments accumulate and form amyloid deposits. Amyloid deposits in the eyes, skin, and nerves lead to the signs and symptoms of lattice corneal dystrophy type II, such as vision impairment; paralysis of facial muscles; and thick, sagging skin.\r"}]}' WHERE id = 23768;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SERPING1", "conditions": [{"name": "遗传性血管性水肿", "detail": "已发现SERPING1gene中的250多个突变会导致I型和II型遗传性血管性水肿。导致I型的突变发生在整个gene中，并导致血液中C1抑制剂水平降低。引起II型的突变通常发生在称为外显子8的gene的特定区域，并导致产生异常功能的C1抑制剂。如果没有适当水平的功能性C1抑制剂，血浆激肽释放酶和XIIa因子的活性就不能被阻断，并且会产生过量的缓激肽。过量的液体通过血管壁泄漏并积聚在身体组织中，导致遗传性血管性水肿I型和II型个体出现反复发作的肿胀。"}]}' WHERE id = 23779;
UPDATE genes SET GHR_metadata_ch = '{"gene": "TG", "conditions": [{"name": "先天性甲状腺功能减退症", "detail": "TGgene的突变可导致先天性甲状腺功能减退，这种疾病的特征是从出生开始甲状腺激素水平异常低。在这种情况下涉及的TGgene突变要么删除TGgene的一小部分，要么改变DNA构建块之一（碱基对）。结果，改变了甲状腺球蛋白的三维形状，减少了可用于甲状腺激素产生的结构正确的蛋白质的量。在大多数受影响的个体中，甲状腺肿大（甲状腺肿）以弥补激素产生减少。由于TGgene突变引起的病例是由于甲状腺激素合成的破坏，因此将其归类为甲状腺功能异常。"}, {"name": "格雷夫斯病", "detail": "遗传学家庭参考提供有关格雷夫斯病的信息。"}, {"name": "桥本甲状腺炎", "detail": "遗传学家庭参考提供有关桥本甲状腺炎的信息。"}, {"name": "其他疾病", "detail": "在一些甲状腺肿但甲状腺球蛋白水平正常或接近正常的人中也发现了TGgene突变。这些突变要么删除TGgene的一部分，要么改变一个DNA碱基对。结果，改变了甲状腺球蛋白的三维形状，减少了可用于甲状腺激素产生的结构正确的蛋白质的量。甲状腺肿大，形成甲状腺肿，以补偿甲状腺球蛋白水平降低。有了这种补偿，甲状腺激素水平是正常的，因此受影响的个体没有先天性甲状腺功能减退症的其他体征和症状（如上所述）。目前尚不清楚为什么甲状腺肿大可以补偿一些受影响的个体而不是其他个体（导致先天性甲状腺功能减退症）。"}]}' WHERE id = 23780;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SLC25A20", "conditions": [{"name": "肉碱酰基肉碱转位酶缺乏症", "detail": "已发现SLC25A20gene中至少有27个突变会导致肉碱酰基肉碱转位酶（CACT）缺乏。尽管这些突变以不同方式改变了CACT蛋白的结构，但它们都导致蛋白的短缺（缺乏）。没有足够的功能性CACT蛋白，长链脂肪酸就无法转运到线粒体中。结果，这些脂肪酸不会转化为能量。能量产生减少会导致CACT缺乏症的某些特征，例如低血糖（低血糖）和脂肪分解产物水平低（低酮症）。脂肪酸和长链酰基肉碱（仍附着在肉碱上的脂肪酸）也可能在细胞中积聚并损害肝脏，心脏和肌肉。这种异常积聚会导致该疾病的其他体征和症状。"}]}' WHERE id = 23788;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SDHB", "conditions": [{"name": "遗传性副神经节瘤嗜铬细胞瘤", "detail": "在遗传性副神经节瘤嗜铬细胞瘤4型患者中，已经鉴定出SDHBgene的150多个突变。患有这种疾病的人患有副神经节瘤，嗜铬细胞瘤或两者兼有。副神经节瘤和嗜铬细胞瘤（一种副神经节瘤）是与神经系统相关的非癌性肿瘤。遗传的SDHBgene突变使个体易患此病，并且需要删除该gene正常拷贝的体细胞突变才能引起4型遗传性副神经节瘤嗜铬细胞瘤。（换行）大多数遗传的SDHBgene突变会改变SDHB蛋白序列中的单个蛋白结构单元（氨基酸）或导致蛋白缩短。结果，SDH酶活性很少或没有。由于突变的SDH酶无法将琥珀酸转化为富马酸，因此琥珀酸会在细胞中积累。过量的琥珀酸盐异常稳定HIF，HIF也在细胞中积聚。过量的HIF刺激细胞分裂并在不需要时触发血管的产生。快速和不受控制的细胞分裂以及新血管的形成可导致遗传性副神经节瘤嗜铬细胞瘤患者的肿瘤发展。"}, {"name": "非综合征性副神经节瘤", "detail": "在某些非综合征性副神经节瘤或嗜铬细胞瘤病例中发现了SDHBgene突变，这是该病的非遗传形式。这些突变大多数会改变SDHB蛋白中的单个氨基酸。与4型遗传性副神经节瘤嗜铬细胞瘤一样，这些突变有望降低SDH酶的活性，从而稳定HIF蛋白，使其在细胞中积聚。过量的HIF蛋白异常刺激细胞分裂和血管形成，这可能导致肿瘤形成。"}, {"name": "考登综合症", "detail": "在患有考登综合症或称为考登综合症的类似疾病的人中，已经鉴定出SDHBgene的至少10个变体。这些疾病的特征是称为错构瘤的多种肿瘤样生长，以及罹患某些癌症（尤其是乳腺癌，甲状腺癌和子宫内膜癌（子宫内膜癌））的风险增加。（换行）与Cowden综合征和Cowden样综合征相关的SDHBgene变体改变SDHB蛋白中的单个氨基酸，这可能改变SDH酶的功能。研究表明，有缺陷的酶可以使细胞不受控制地生长和分裂，从而导致错构瘤和癌性肿瘤的形成。但是，研究人员不确定所鉴定的SDHBgene变异是否与Cowden综合征和Cowden样综合征直接相关。没有这些conditions的特征，很少在人中发现上述某些变体。"}, {"name": "胃肠道间质瘤", "detail": "遗传学家庭参考提供有关胃肠道间质瘤的信息。"}, {"name": "其他癌症", "detail": "SDHBgene与多种癌症有关。在少数患有胃肠道间质瘤（GIST）的人中发现了SDHBgene的突变，胃肠道间质瘤是一种发生在胃肠道的肿瘤，或肾细胞癌是一种肾癌。SDHBgene突变已在人们中发现，这种疾病称为Carney-Stratakis综合征，其中受影响的人同时患有副神经节瘤和GIST，或者患有肾细胞癌和副神经节瘤。遗传的SDHBgene突变使个体容易患癌症。需要删除gene正常拷贝的其他突变才能引起这些形式的GIST，肾细胞癌和副神经节瘤。第二种突变称为体细胞突变，是在一个人的一生中获得的，仅存在于肿瘤细胞中。（换行）SDHBgene的突变导致细胞中SDHB蛋白量的减少和SDH酶活性的丧失。此外，即使没有SDHBgene突变，胃肠道间质瘤的一部分也降低了SDHB蛋白并丧失了SDH酶的活性。SDH酶活性的缺乏导致异常的缺氧信号传导和肿瘤的形成。"}]}' WHERE id = 23794;
UPDATE genes SET GHR_metadata_ch = '{"gene": "D2HGDH", "conditions": [{"name": "2-羟基戊二酸尿症", "detail": "研究人员已经鉴定出超过30种D2HGDHgene突变，这些突变导致一种称为I型D-2-羟基戊二酸尿症（D-2-HGA）的2-羟基戊二酸尿症。这种情况有多种体征和症状，主要是由于进行性从生命早期开始损害大脑。（换行）一些D2HGDHgene突变改变D-2-羟基戊二酸脱氢酶中的单个蛋白质构建块（氨基酸），这可能损害其功能。其他突变导致产生异常短的，无功能的酶形式。由于缺乏功能性酶，D-2-羟基戊二酸不会分解，而是在细胞中积累。在高水平，这种化合物可以破坏细胞并导致细胞死亡。脑细胞似乎最容易受到这种化合物的毒性作用，这可以解释为什么D-2-HGA I型的体征和症状主要涉及大脑。"}]}' WHERE id = 23796;
UPDATE genes SET GHR_metadata_ch = '{"gene": "PFKM", "conditions": [{"name": "糖原贮积病VII型", "detail": "已发现PFKMgene中至少有20个突变会导致VII型糖原贮积病（GSDVII）。这种情况的特征是无法分解肌肉细胞中的糖原，导致肌肉痉挛和无力，严重程度可能因受影响的个体而异。导致GSDVII的PFKMgene突变导致产生功能很少或没有功能的PFKM亚基。一种PFKMgene突变是Ashkenazi犹太血统人群中大多数GSDVII病例的原因。这种突变（写为IVS5+1G>A）导致gene内遗传物质的少量缺失，这改变了gene指令拼接在一起的方式，并导致产生无功能的亚基。（换行）没有功能性PFKM亚基，骨骼肌中不形成功能性磷酸果糖激酶，糖原不能完全分解。部分分解的糖原在肌肉细胞中积累。无法获得糖原作为能量来源的肌肉在中等压力（例如运动）后变得虚弱和抽筋，在某些情况下开始分解。在其他组织中，组成磷酸果糖激酶的其他亚基可能弥补了PFKM亚基的缺乏，并且该酶能够保留一些功能。这种补偿可能有助于解释为什么其他组织不受PFKMgene突变的影响。目前尚不清楚为什么某些患有GSDVII的人比其他人受到更严重的影响。"}]}' WHERE id = 23805;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ACAD8", "conditions": [{"name": "异丁酰辅酶A脱氢酶缺乏症", "detail": "已发现ACAD8中的突变会导致异丁酰辅酶A脱氢酶（IBD）缺乏，这是由于蛋白质减少或无功能而导致的，从而消除了缬氨酸的分解代谢和能量产生。"}]}' WHERE id = 23809;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SLC25A13", "conditions": [{"name": "瓜氨酸血症", "detail": "在成人II型瓜氨酸血症患者中已鉴定出SLC25A13gene的20多个突变。这种情况通常会在成年期开始引起神经系统问题，例如精神错乱，烦躁不安，烦躁和癫痫发作。几乎所有已识别的突变都会导致产生不稳定的柠檬酸蛋白，该蛋白会迅速分解或异常短而无功能的蛋白。(换行)缺乏功能性柠檬酸阻断苹果酸-天冬氨酸穿梭，包括将天冬氨酸转运出线粒体。柠檬酸的这种损失抑制了蛋白质和核苷酸的正常产生。它还减少了可参与尿素循环的天冬氨酸的量。结果，肝脏不能有效地将过量的氮转化为尿素。尿素循环的中断使氮（以氨的形式）和尿素循环的其他副产物（例如瓜氨酸）积聚在血液中。氨是有毒的，特别是对神经系统，这有助于解释成人发作的II型瓜氨酸血症患者异常行为和其他神经系统问题的发展。SLC25A13gene中的（换行）突变也已在患有由柠檬酸缺乏症（NICCD）引起的称为新生儿肝内胆汁淤积的肝病的婴儿中发现。这种肝脏疾病也称为新生儿发作的II型瓜氨酸血症。NICCD阻止胆汁（肝脏产生的消化液）流动，并阻止身体正确处理某些营养素。患有NICCD的婴儿的血液中不会积聚氨，并且症状和体征通常在一年内消失。在极少数情况下，这些人从NICCD康复后会出现另一种称为柠檬酸缺乏症（FTTDCD）引起的壮成长和血脂异常的症状和体征。许多患有NICCD或FTTDCD的个体在SLC25A13gene中具有与成人发作的II型瓜氨酸血症相同的突变。几年甚至几十年后，一些患有NICCD或FTTDCD的人发展出成人发作的II型瓜氨酸血症的特征。"}]}' WHERE id = 23818;
UPDATE genes SET GHR_metadata_ch = '{"gene": "GARS", "conditions": [{"name": "进行性神经性腓骨肌萎缩症", "detail": "研究人员已经确定了GARSgene中的一些突变，这些突变会导致一种称为2D型的Charcot-Marie-Tooth病。这些突变改变了用于制备甘氨酰-tRNA合成酶的单个氨基酸。目前尚不清楚GARSgene突变如何导致2D型Charcot-Marie-Tooth病。突变可能会降低糖基-tRNA合成酶的活性。科学家认为，神经细胞可能对这种酶活性的降低特别敏感。特别地，甘氨酰-tRNA合成酶活性的降低可能损害神经细胞（轴突）的专门生长物传递神经冲动的能力。"}, {"name": "远端遗传性运动神经病，V型", "detail": "在患有V型远端遗传性运动神经病的个体中已鉴定出几种GARSgene突变。这些突变改变了用于制造甘氨酰-tRNA合成酶的单个氨基酸。目前尚不清楚GARSgene突变如何导致这种疾病。突变可能会降低糖基-tRNA合成酶的活性。与Charcot-Marie-Tooth病一样，甘氨酰-tRNA合成酶活性的降低可能会损害神经冲动的传递。"}]}' WHERE id = 23820;
UPDATE genes SET GHR_metadata_ch = '{"gene": "HPSE2", "conditions": [{"name": "Ochoa综合征", "detail": "在患有Ochoa综合征（也称为urofacial综合征）的人中至少鉴定出9个HPSE2gene突变，该疾病会导致泌尿系统问题和异常的面部表情。这些突变导致乙酰肝素酶2蛋白发生变化，可能阻止其发挥功能。HPSE2gene突变与Ochoa综合征特征之间的联系尚不清楚。由于控制面部表情和排尿的大脑区域非常接近，因此一些研究人员建议，遗传变化可能导致该大脑区域异常，这可能是Ochoa综合征症状的原因。其他研究人员认为，乙酰肝素酶2蛋白缺陷可能会导致泌尿道发育或面部和膀胱肌肉功能出现问题。"}]}' WHERE id = 23842;
UPDATE genes SET GHR_metadata_ch = '{"gene": "PADI3", "conditions": [{"name": "难辨的头发综合症", "detail": "已发现PADI3gene中至少有9个突变会导致难以梳理的头发综合症。这种情况的特点是干燥，卷曲，金色的头皮头发不能梳理。这种情况通常会随着时间的推移而改善，到青春期，患有难以梳理的头发综合症的人的头发平坦且质地正常或接近正常。（换行）PADI3gene突变可能导致肽精氨酸脱亚胺酶III型酶几乎没有活性。结果，该酶不能使其他蛋白质脱氨基。特别地，III型肽精氨酸脱亚胺酶不能使称为毛透明质酸的毛干内的蛋白质脱亚胺。Trichohyalin需要进行这种修饰，以便它可以附着（结合）其他蛋白质，使毛干呈圆柱形。没有trichohyalin的脱氨基，毛干的横截面变成三角形，心脏状或扁平状。这些有角度的发干导致卷发的头发不会平躺，这是典型的难以梳理的头发综合症。"}]}' WHERE id = 23843;
UPDATE genes SET GHR_metadata_ch = '{"gene": "CISD2", "conditions": [{"name": "Wolfram综合征", "detail": "已经发现CISD2gene的突变会引起Wolfram综合征，Wolfram综合征是一种以糖尿病，视神经萎缩性胃肠道溃疡，受伤后出血过多以及涉及泌尿道，大脑和听力的其他特征为特征的疾病。这是由于损害线粒体功能的异常或无功能的CISD2蛋白引起的。由CISD2gene突变引起的Wolfram综合征有时称为2型Wolfram综合征。"}]}' WHERE id = 23856;
UPDATE genes SET GHR_metadata_ch = '{"gene": "MYD88", "conditions": [{"name": "MyD88缺陷", "detail": "已经发现MYD88gene中至少有四个突变引起称为MYD88缺陷的病症。患有这种疾病的个体会反复发生细菌感染。与Waldenström巨球蛋白血症和其他血液疾病（如下所述）不同，导致MYD88缺陷的gene突变是遗传的，并且在身体的每个细胞中都有发现（称为种系突变）。这些突变导致产生无功能的蛋白质或根本不产生蛋白质。结果，该蛋白质无法传递刺激免疫反应的信号，从而导致多种严重感染的发生。"}, {"name": "Waldenström巨球蛋白血症", "detail": "在超过90%的Waldenström巨球蛋白血症患者中发现了MYD88gene的特定突变。这种罕见形式的血癌的特征在于骨髓中称为淋巴浆细胞的异常白细胞过多以及称为IgM的蛋白质的过量产生。这种情况下涉及的突变改变了MyD88蛋白中的单个蛋白质构建块（氨基酸），用265位氨基酸脯氨酸（写为Leu265Pro或L265P）取代了氨基酸亮氨酸。该突变是在一个人的一生中获得的，仅存在于异常的白细胞中。这种类型的遗传变化称为体细胞突变，不是遗传的。Waldenström巨球蛋白血症被认为是由多种遗传变化引起的，包括MYD88gene突变。(换行)改变的MyD88蛋白不断起作用（过度活跃）。它刺激激活核因子-κ-B的信号分子，即使没有来自细胞外部的信号。研究人员认为，异常活跃的核因子-κB可以使应该发生细胞凋亡的异常细胞存活，这可能有助于Waldenström巨球蛋白血症中淋巴浆细胞的积累。"}, {"name": "其他癌症", "detail": "在其他一些血细胞癌（包括弥漫性大B细胞淋巴瘤（DLBCL）和边缘区淋巴瘤）中也发现了MYD88gene中的体细胞L265P突变。突变促进疾病发展的机制被认为与Waldenström巨球蛋白血症（如上所述）相同。发生的癌症类型可能取决于获得L265P突变的细胞类型。这种突变被认为是涉及这些癌症发展的许多遗传变化之一。"}, {"name": "其他疾病", "detail": "L265P突变也在约50%至80%的血液疾病病例中发现，称为意义不明的IgM单克隆丙种球蛋白病（IgM MGUS）。患有这种疾病的人血液中的IgM水平略有升高。IgM MGUS可转化为Waldenström巨球蛋白血症（如上所述）或其他血细胞癌症或病症；当IgM MGUS中存在MYD88gene突变时，该病症更可能进展。"}]}' WHERE id = 23865;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ENAM", "conditions": [{"name": "釉质发育不全", "detail": "在患有称为牙釉质发育不全的牙齿发育障碍的人中，已经鉴定出ENAMgene中至少有14个突变。该gene的突变导致这种情况的常染色体显性和常染色体隐性形式。（换行）以常染色体显性形式，每个细胞中ENAMgene的一个拷贝被改变。这些突变对牙釉质形成具有多种影响。这些突变中的一些减少了从该gene的一个拷贝产生的enamelin的量。其他突变导致产生缺少关键区域的异常短版本的enamelin。减少量的enamelin或改变形式的蛋白质可导致牙釉质发育的严重问题或引起较轻的缺陷，例如牙齿中的浅凹坑或水平凹槽。（换行）在牙釉质发育不全的常染色体隐性形式中，每个细胞中ENAMgene的两个拷贝都被改变。这些突变导致产生异常形式的enamelin，从而阻止釉质正常发育。继承ENAMgene两个突变拷贝的人的牙釉质存在严重缺陷；结果，该保护罩可能非常薄或完全不存在。"}]}' WHERE id = 23890;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SLC6A8", "conditions": [{"name": "X连锁肌酸缺乏症", "detail": "在X连锁肌酸缺乏症患者中已鉴定出SLC6A8gene至少80个突变，该疾病会导致智力残疾，行为问题，癫痫发作和肌肉无力。SLC6A8gene突变会损害转运蛋白将肌酸带入细胞的能力，从而导致肌酸短缺（缺乏）。肌酸缺乏的影响在需要大量能量的器官和组织中最为严重，尤其是大脑。"}]}' WHERE id = 23931;
UPDATE genes SET GHR_metadata_ch = '{"gene": "GHRHR", "conditions": [{"name": "孤立的生长激素缺乏症", "detail": "已发现GHRHRgene中的20多个突变会导致孤立的生长激素缺乏症，这种疾病的特征是生长缓慢和身材矮小。GHRHRgene的突变会导致某些孤立的IB型生长激素缺乏症。这些突变大多数会取代受体蛋白中的单个蛋白结构单元（氨基酸），从而阻止功能性受体的产生。非功能性受体不能有效地触发生长激素的产生或发出其从脑下垂体释放的信号。结果，具有GHRHRgene突变的人体内几乎没有生长激素产生或可用，导致孤立的IB型生长激素缺乏症。"}]}' WHERE id = 23946;
UPDATE genes SET GHR_metadata_ch = '{"gene": "RELN", "conditions": [{"name": "小脑发育不全的小脑畸形", "detail": "已发现RELNgene中至少有六个突变会导致小脑发育不全（LCH）引起小脑畸形。这种情况会影响大脑发育，导致大脑外观光滑（lissencephaly），而不是其正常的褶皱和凹槽。此外，参与协调运动的大脑区域异常小且发育不全（小脑发育不全）。导致LCH的RELNgene突变导致完全缺乏reelin。结果，触发神经元迁移的信号传导途径未被激活。如果没有reelin，神经元就会杂乱无章，大脑的正常褶皱和凹槽无法形成，大脑结构也无法正常发育。这种大脑发育障碍导致智力残疾，整体发育迟缓，运动问题以及LCH的其他体征和症状。"}, {"name": "自闭症谱系障碍", "detail": "遗传学家庭参考提供有关自闭症谱系障碍的信息。"}, {"name": "肌阵挛-肌张力障碍", "detail": "遗传学家庭参考提供有关肌阵挛性肌张力障碍的信息。"}, {"name": "其他疾病", "detail": "研究表明，RELNgene的某些变异（多态性）与精神疾病（如精神分裂症和双相情感障碍）的风险增加有关。具有这些多态性的女性特别容易患双相情感障碍。此外，导致reelin产量下降（但并非完全不存在）的某些遗传变化可能是自闭症谱系障碍的危险因素，自闭症谱系障碍会影响沟通和社交互动。但是，其他研究并未支持这些发现。据信许多遗传和环境因素是造成这些复杂状况的原因。"}]}' WHERE id = 23993;
UPDATE genes SET GHR_metadata_ch = '{"gene": "PTPN22", "conditions": [{"name": "白癜风", "detail": "研究表明，PTPN22gene的R620W变异与白癜风的风险增加有关，白癜风是一种自身免疫性疾病，可导致肤色（色素沉着）发生斑片状变化。(换行)与其他自身免疫性疾病一样，这种变异可能会影响PTPN22蛋白的活性，使身体更难以控制免疫系统并阻止其攻击自身组织。虽然与白癜风相关的色素损失是由免疫系统攻击皮肤中的色素生成细胞（黑素细胞）引起的，但尚不清楚是什么特定情况触发免疫系统这样做。这种情况可能是由遗传和环境因素共同作用造成的，其中大部分尚未确定。"}, {"name": "自身免疫性艾迪生病", "detail": "遗传学家庭参考提供有关自身免疫性艾迪生病的信息。"}, {"name": "格雷夫斯病", "detail": "遗传学家庭参考提供有关格雷夫斯病的信息。"}, {"name": "特发性炎症性肌病", "detail": "遗传学家庭参考提供有关特发性炎性肌病的信息。"}, {"name": "幼年特发性关节炎", "detail": "遗传学家庭参考提供有关青少年特发性关节炎的信息。"}, {"name": "类风湿关节炎", "detail": "遗传学家庭参考提供有关类风湿关节炎的信息。"}, {"name": "系统性红斑狼疮", "detail": "遗传学家庭参考提供有关系统性红斑狼疮的信息。"}, {"name": "系统性硬皮病", "detail": "遗传学家庭参考提供有关系统性硬皮病的信息。"}, {"name": "1型糖尿病", "detail": "遗传学家庭参考提供有关1型糖尿病的信息。"}, {"name": "自身免疫性疾病", "detail": "研究表明，PTPN22gene的变异与几种自身免疫性疾病的风险增加有关。当免疫系统发生故障并攻击身体的组织和器官时，就会发生自身免疫性疾病。这些疾病包括1型糖尿病，类风湿性关节炎，自身免疫性甲状腺疾病和系统性红斑狼疮。（换行）与自身免疫性疾病相关的PTPN22gene变异将蛋白质构建块（氨基酸）精氨酸改变为PTPN22蛋白质序列中第620位的氨基酸色氨酸，写为Arg620Trp或R620W。这种变化可能影响PTPN22蛋白的活性，使身体更难以控制炎症并防止免疫系统攻击其自身组织。"}]}' WHERE id = 24017;
UPDATE genes SET GHR_metadata_ch = '{"gene": "NDRG1", "conditions": [{"name": "进行性神经性腓骨肌萎缩症", "detail": "研究人员已经鉴定出至少两种NDRG1gene突变，这些突变会导致一种称为4D型的Charcot-Marie-Tooth病。一个突变会在制造NDRG1蛋白的说明中产生过早的终止信号，从而导致蛋白异常短。另一个突变导致缺少关键片段的蛋白质的产生。这两个突变都可能导致蛋白质功能异常。(换行)目前尚不清楚NDRG1gene突变如何导致4D型Charcot-Marie-Tooth病。雪旺氏细胞使用大量脂质和蛋白质来制造髓磷脂，并且它们依赖于这些物质的充足供应。NDRG1gene突变可能会损害NDRG1蛋白与帮助脂质分布在体内的其他蛋白的相互作用。结果，可能破坏向雪旺氏细胞的脂质供应，影响髓磷脂的产生并改变神经冲动的传递。NDRG1gene突变也可能干扰轴突存活所必需的信号传导。髓磷脂的丧失和神经冲动传递的问题是4D型Charcot-Marie-Tooth病的征兆。"}]}' WHERE id = 24026;
UPDATE genes SET GHR_metadata_ch = '{"gene": "POLG", "conditions": [{"name": "Alpers-Huttenlocher综合征", "detail": "POLGgene中有许多突变会导致Alpers-Huttenlocher综合征。Alpers-Huttenlocher综合征是称为POLG相关疾病的一组疾病的一部分，这些疾病具有影响肌肉，神经和大脑相关功能的重叠体征和症状。Alpers-Huttenlocher综合征的特征是癫痫发作，精神和运动能力丧失（精神运动退化）和肝病。丙戊酸是癫痫发作的常见治疗方法，可引起Alpers-Huttenlocher综合征的肝脏疾病或使其恶化。（换行）大多数POLGgene突变改变pol？的α亚基中的单个蛋白质构建块（氨基酸）？。突变可以对pol？的功能产生多种影响？。α亚基可能失去附着于β亚基形成pol？的能力？。或者，突变的pol？可能无法结合现有的mtDNA链作为模板。或者，它可能具有降低的吸引其用于形成新DNA的DNA构建块（核苷酸）的能力。这些影响会损害DNA合成，并可能导致插入错误的核苷酸并降低修复错误的能力。（换行）Alpers-Huttenlocher综合征中最常见的POLGgene突变用467位氨基酸苏氨酸取代氨基酸丙氨酸（写为Ala467Thr或A467T）。这种突变会阻止α亚基附着于β亚基并降低pol的能力合成DNA的能力。Ala467Thr突变在其他与POLG相关的疾病中也很常见。不同的情况可能部分由POLG另一拷贝中的突变决定，但仍有一些突变组合可能导致一种以上的疾病。目前尚不清楚相同的突变如何导致不同的情况。(换行)尽管机制尚不清楚，但许多患有Alpers-Huttenlocher综合征的人的mtDNA拷贝数较少（mtDNA消耗）。仅在受疾病影响的组织中可见这种异常。MtDNA消耗导致氧化磷酸化受损和细胞能量减少。这些损伤影响细胞不连续分裂的组织，如脑，肌肉和肝脏。这些组织受到的影响最大，因为它们更依赖于氧化磷酸化来获取能量，并且这些组织中受损的细胞通常不会被新细胞替代。这些组织缺乏能量供应可以解释Alpers-Huttenlocher综合征的体征和症状。"}, {"name": "共济失调神经病谱", "detail": "由POLGgene突变引起的另一种疾病是共济失调神经病谱，这是一种与POLG相关的疾病，其特征是协调和平衡问题（共济失调）和神经功能紊乱（神经病）。先前称为线粒体隐性共济失调综合征（MIRAS）和感觉性共济失调神经病-构音障碍和眼肌麻痹（SANDO）的疾病现已包括在共济失调神经病谱中。（换行）如上所述，POLGgene中的大多数突变都会改变polα亚基中的单个氨基酸？，这些突变会对pol？的功能产生一些影响？。结果，波尔？复制mtDNA的能力降低。共济失调神经病谱中最常见的POLGgene突变与Alpers-Huttenlocher综合征Ala467Thr中的相同。目前尚不清楚相同的突变如何导致不同的疾病。(换行)与其他与POLG相关的疾病一样，患有共济失调神经病谱的人通常在受疾病影响的组织（例如大脑）中具有mtDNA消耗。由于氧化磷酸化的减少，MtDNA的消耗减少了细胞可用的能量，这可能解释了共济失调神经病谱的体征和症状。"}, {"name": "儿童肌脑肝病谱", "detail": "儿童期肌脑肝病综合征（MCHS）也由POLGgene突变引起。MCHS是一种与POLG相关的疾病，会影响肌肉（肌肉），大脑（大脑）和肝脏（肝脏）。(换行)POLGgene中的许多突变都可能导致MCHS。这些突变大多数改变pol？的α亚基中的单个氨基酸？。这些突变降低了pol的活性？，减少mtDNA复制。(换行)与其他与POLG相关的疾病一样，患有MCH的人通常在肌肉，大脑或肝组织中的mtDNA消耗。MtDNA耗竭会损害这些组织中的氧化磷酸化并降低细胞可用的能量，这可能会导致MCH的体征和症状。"}, {"name": "肌阵挛性癫痫肌病感觉共济失调", "detail": "POLGgene中的突变会导致另一种与POLG相关的疾病，称为肌阵挛性癫痫性肌病感觉共济失调（MEMSA），其特征是反复发作（癫痫），肌肉无力（肌病）以及协调与平衡问题（共济失调）。（换行）参与MEMSA的大多数POLGgene突变改变polα亚基中的单个氨基酸？。这些突变导致pol？复制mtDNA的能力降低。(换行)与其他与POLG相关的疾病一样，患有MEMSA的人通常在受影响的组织（例如肌肉或大脑）中会消耗mtDNA。MtDNA耗竭导致受影响组织中的氧化磷酸化受损和能量储备减少，这可能导致MEMSA的体征和症状。"}, {"name": "进行性眼外肌麻痹", "detail": "POLGgene中的突变通常导致一种称为进行性眼外肌麻痹的眼病，这是另一种与POLG相关的疾病。这种情况削弱了控制眼球运动并导致眼睑下垂（上睑下垂）的肌肉。(换行)至少有67个POLGgene突变会导致进行性眼外肌麻痹。大多数POLGgene突变会改变polα亚基中的单个氨基酸？，这会降低mtDNA复制的效率。与另一种与POLG相关的疾病Alpers-Huttenlocher综合征一样，进行性外眼肌麻痹中最常见的POLGgene突变是Ala467Thr。目前尚不清楚相同的突变如何导致不同的疾病。（换行）在进行性眼外肌麻痹中，POLGgene的突变导致肌肉组织中mtDNA的遗传物质大量缺失，而不是在其他POLG相关疾病中看到的整体mtDNA消耗。这种差异的原因尚不清楚。尽管该病的特征可能与氧化磷酸化受损有关，但研究人员尚未确定mtDNA的缺失如何导致进行性眼外肌麻痹的特定体征和症状。已经提出，眼肌通常受到线粒体缺陷的影响，因为它们特别依赖于氧化磷酸化作为能量。"}, {"name": "利综合征", "detail": "遗传学家庭参考提供有关Leigh综合征的信息。"}]}' WHERE id = 24050;
UPDATE genes SET GHR_metadata_ch = '{"gene": "H19", "conditions": [{"name": "Beckwith-Wiedemann综合征", "detail": "Beckwith-Wiedemann综合征是一种以过度生长和其他影响身体许多部位的体征和症状为特征的疾病，可能由影响IC1区域的变化引起。在一些患有这种情况的人中，IC1区域的母系遗传拷贝和父系遗传拷贝都附着有甲基（超甲基化）。因为IC1区域控制着H19和IGF2gene的gene组印迹，所以这种异常破坏了两个gene的调控。具体而言，IC1区域的高甲基化导致许多组织中H19gene活性的丧失和IGF2gene活性的增加。通常抑制生长的H19gene活性的丧失和促进生长的IGF2gene活性的增加一起导致Beckwith-Wiedemann综合征患者的过度生长。（换行）在少数情况下，Beckwith-Wiedemann综合征是由IC1区域少量DNA缺失引起的。像异常甲基化一样，这些缺失会改变H19和IGF2gene的活性。"}, {"name": "乳腺癌", "detail": "遗传学家庭参考提供有关乳腺癌的信息。"}, {"name": "罗素-银综合症", "detail": "IC1区域甲基化的变化也是Russell-Silver综合征的一些病例的原因，Russell-Silver综合征是一种以出生前后生长缓慢为特征的疾病。这些变化与Beckwith-Wiedemann综合征不同，对生长有相反的影响。（换行）在Russell-Silver综合征中，IC1区域的父系遗传拷贝通常具有太少的甲基连接（低甲基化）。IC1区域的低甲基化导致H19gene的活性增加和许多组织中IGF2gene活性的丧失。抑制生长的H19gene活性的增加和通常促进生长的IGF2gene活性的丧失一起导致Russell-Silver综合征患者的生长不良和身材矮小。"}]}' WHERE id = 24069;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ALDH4A1", "conditions": [{"name": "高蛋白血症", "detail": "发现ALDH4A1gene中的几个突变会引起II型高蛋白血症，这种疾病的特征是癫痫发作或智力残疾，这是由于酶缺陷或无功能导致脯氨酸水平升高和吡咯啉-5-羧酸盐积累所致。"}]}' WHERE id = 24102;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ABCD1", "conditions": [{"name": "X联肾上腺皮质营养不良", "detail": "ABCD1gene的突变与X连锁的肾上腺白质营养不良有关，这是由于蛋白质缺陷或无功能而引起的，该蛋白质阻止了ALDP的产生和VLCFA的分解。该病的特征是不同程度的认知和运动问题以及激素失衡。"}]}' WHERE id = 24106;
UPDATE genes SET GHR_metadata_ch = '{"gene": "RAB7A", "conditions": [{"name": "进行性神经性腓骨肌萎缩症", "detail": "研究人员已经鉴定出至少四个RAB7Agene突变，这些突变会导致一种称为2B型的Charcot-Marie-Tooth病。这种形式的疾病的特征是感觉严重降低，尤其是四肢。在患有这种疾病的个体中鉴定出的RAB7Agene突变改变了用于制造RAB7A蛋白的单蛋白结构单元（氨基酸）。这些变化可能会改变RAB7A蛋白的结构并损害其功能。(换行)尚不清楚RAB7Agene突变如何引起Charcot-Marie-Tooth病的体征和症状。研究人员认为，改变后的RAB7A蛋白可能会影响神经递质的内吞作用和运输，神经递质是在神经细胞（神经元）之间传递信号的化学信使。在整个神经系统中传递神经冲动的神经元（轴突）的特殊延伸可能非常长，特别是在周围神经系统中。周围神经系统由将大脑和脊髓连接到肌肉和感觉细胞的神经组成，所述感觉细胞检测诸如触摸，疼痛，热和声音的感觉。周围神经系统的极长轴突可能对神经递质的运输受损特别敏感。神经递质信号传导中断可能导致Charcot-Marie-Tooth病特有的虚弱和感觉问题。"}]}' WHERE id = 24123;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ADA", "conditions": [{"name": "腺苷脱氨酶缺乏症", "detail": "在由于腺苷脱氨酶缺乏而导致的称为严重联合免疫缺陷症（SCID）的人中，已经鉴定出ADAgene的几种突变，从而阻止了其分解脱氧腺苷导致其积累的能力。这种有毒化合物干扰淋巴细胞的发育和维持。"}]}' WHERE id = 24131;
UPDATE genes SET GHR_metadata_ch = '{"gene": "KIF7", "conditions": [{"name": "肩call骨综合征", "detail": "在肩call骨综合征患者中至少鉴定出KIF7gene的20个突变。这种罕见病的特征是某些大脑异常，手指和脚趾过多（多指）以及独特的面部特征，包括眼睛间距大（过度肌肉发达）和额头突出。导致肩call骨综合征的大多数KIF7gene突变都会导致异常短而无功能的KIF7蛋白产生，或阻止任何蛋白从该gene产生。尽管这些突变可能在早期发育过程中破坏声波刺猬信号，但对这些突变的影响知之甚少。目前尚不清楚这些变化如何损害肩call骨综合征患者的大脑，四肢和身体其他部位的发育。"}, {"name": "伯特症候群", "detail": "遗传学家庭参考提供有关Joubert综合征的信息。"}]}' WHERE id = 24162;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ACAD9", "conditions": [{"name": "ACAD9缺陷", "detail": "已发现ACAD9中的几个突变会导致ACAD9缺乏，这是由于蛋白质缺陷导致的，从而消除了复杂的I组装，长链脂肪酸氧化和能量产生。这种缺陷的临床特征包括肌肉无力（肌病），心脏问题，智力障碍和其他体征和症状。"}, {"name": "线粒体复合体I缺乏", "detail": "Several mutations in ACAD9 have been found to be associated with mitochondrial complex I deficiency caused due to deficient or nonfunctional of complex I and impairs energy production. The clinical symptoms include neurological problems such as encephalopathy, epilepsy, intellectual disability, ataxia, dystonia, hypotonia, myalgia, and extreme exercise intolerance and lactic acidosis leading to nausea, vomiting, weakness, and rapid breathing that can be life-threatening. These symptoms are evident at any time from birth to adulthood."}]}' WHERE id = 24186;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SCNN1G", "conditions": [{"name": "利德尔综合症", "detail": "SCNN1Ggene中至少有5个突变可引起称为Liddle综合征的疾病。Liddle综合征患者通常从儿童期开始就患有高血压（高血压）和血液中钾水平低（低钾血症）。与Liddle综合征相关的SCNN1Ggene突变导致异常短的γ亚基蛋白的产生。这些变化影响参与其分解（降解）信号传导的γ亚基蛋白的重要区域。突变的结果是，蛋白质不会降解，并且更多的ENaC通道保留在细胞表面。细胞表面通道的增加允许重吸收过量的钠（其次是水），这导致高血压。钠重吸收到血液中与从血液中去除钾有关，因此过量的钠重吸收导致低钾血症。"}, {"name": "假性低醛固酮增多症1型", "detail": "SCNN1Ggene的突变与1型假性低醛固酮增多症（PHA1）有关。这种情况通常始于婴儿期，其特征是血液中钠含量低（低钠血症）和钾含量高（高钾血症），以及由于尿液中过量钠和液体的流失而导致的严重脱水。特别是，SCNN1Ggene突变与常染色体隐性PHA1有关，这是一种严重的疾病形式，不会随着年龄的增长而改善。（换行）SCNN1Ggene中的大多数突变导致异常短的γ亚基蛋白。这些突变导致ENaC通道活性降低或缺失。结果，钠的重吸收受到损害，导致低钠血症和常染色体隐性PHA1的其他体征和症状。ENaC通道在肺上皮细胞中的功能降低导致肺中过多的液体和反复的肺部感染。"}, {"name": "其他疾病", "detail": "一些患有囊性纤维化样综合征的人的SCNN1Ggene具有突变或正常的gene变异（多态性）。患有囊性纤维化样综合征（也称为非典型囊性纤维化或支气管扩张伴或不伴3型汗液氯化物升高）的人的体征和症状类似于囊性纤维化，包括呼吸问题和肺部感染。但是，最常与囊性纤维化相关的geneCFTR的变化不能解释这种情况的发展。据认为，SCNN1Ggene中的突变或gene变异可破坏钠转运和体液平衡，从而导致囊性纤维化样综合征的体征和症状。"}]}' WHERE id = 24206;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SCNN1B", "conditions": [{"name": "利德尔综合症", "detail": "SCNN1Bgene中至少有16个突变可引起称为Liddle综合征的疾病。Liddle综合征患者通常从儿童期开始就患有高血压（高血压）和血液中钾水平低（低钾血症）。与Liddle综合征相关的SCNN1Bgene突变导致异常短的β亚基蛋白产生，或导致蛋白中单个蛋白结构单元（氨基酸）的替换。这些变化影响参与其分解（降解）信号传导的蛋白质的重要区域。突变的结果是，蛋白质不会降解，并且更多的ENaC通道保留在细胞表面。细胞表面通道的增加异常增加钠（其次是水）的重吸收，这导致高血压。钠重吸收到血液中与从血液中去除钾有关，因此过量的钠重吸收导致低钾血症。"}, {"name": "假性低醛固酮增多症1型", "detail": "SCNN1Bgene的突变已在1型假性低醛固酮增多症（PHA1）患者中发现。这种情况通常始于婴儿期，其特征是血液中钠含量低（低钠血症）和钾含量高（高钾血症），以及由于尿液中过量钠和液体的流失而导致的严重脱水。特别是，SCNN1Bgene突变与常染色体隐性PHA1有关，这是一种严重的疾病形式，不会随着年龄的增长而改善。导致PHA1的SCNN1Bgene突变通常导致β亚基蛋白中单个氨基酸的替换或导致异常短的蛋白。这些突变导致ENaC通道活性降低或缺失。结果，钠的重吸收受到损害，导致低钠血症和常染色体隐性PHA1的其他体征和症状。ENaC通道在肺上皮细胞中的功能降低导致肺中过多的液体和反复的肺部感染。"}, {"name": "其他疾病", "detail": "一些患有囊性纤维化样综合征的人的SCNN1Bgene具有突变或正常的gene变异（多态性）。患有囊性纤维化样综合征（也称为非典型囊性纤维化或支气管扩张伴或不伴1型汗液氯化物升高）的人的体征和症状类似于囊性纤维化，包括呼吸问题和肺部感染。但是，最常与囊性纤维化相关的geneCFTR的变化不能解释这种情况的发展。据认为，SCNN1Bgene的突变或gene变异会破坏钠的转运和体液平衡，从而导致囊性纤维化样综合征的体征和症状。"}]}' WHERE id = 24214;
UPDATE genes SET GHR_metadata_ch = '{"gene": "MESP2", "conditions": [{"name": "脊椎肋骨发育不良", "detail": "遗传学家庭参考提供有关脊椎肋骨发育不良的信息。"}, {"name": "脊椎胸椎发育不良", "detail": "已发现MESP2gene中至少有3个突变会导致脊柱胸椎发育不良，这种疾病的特征是脊柱和肋骨骨骼发育异常。所有已知的突变都取代了蛋白质序列中的一个蛋白质结构单元（氨基酸）。最常见的突变是在位置103（写为Glu103Ter或E103X）处用过早的终止信号替换氨基酸谷氨酸。类似的突变发生在氨基酸位置230（写为Glu230Ter或E230X）。第三个突变用第125位的氨基酸缬氨酸取代了氨基酸亮氨酸（写为Leu125Val或L125V）。大多数受影响的个体在MESP2gene的两个拷贝中均具有Glu103Ter突变。但是，少数患有脊椎胸椎发育不全的人在一个MESP2gene拷贝中具有Glu103Ter突变，在另一拷贝中具有Leu125Val或Glu230Ter突变。MESP2gene中的（换行）突变阻止任何蛋白质的产生或导致异常短的无功能蛋白质的产生。当MESP2蛋白无功能或不存在时，NOTCH1蛋白异常活跃，并且不会形成将未来椎骨彼此分开的边界。这导致脊柱胸椎发育不良中出现的脊柱和肋骨骨骼畸形和融合。"}]}' WHERE id = 24225;
UPDATE genes SET GHR_metadata_ch = '{"gene": "GAA", "conditions": [{"name": "庞贝氏症", "detail": "在庞贝病患者中已鉴定出GAAgene的200多个突变。这些突变中的许多改变了用于制造酸性α-葡萄糖苷酶的蛋白质结构单元（氨基酸）之一。其他突变会在GAAgene中插入或删除遗传物质。该gene的突变显着降低了酸性α-葡萄糖苷酶的活性，阻止了该酶有效地分解糖原。结果，这种复合糖可以在溶酶体中积累到毒性水平。糖原的异常积累会损害整个身体的器官和组织，尤其是肌肉，从而导致进行性肌肉无力，心脏问题和庞贝病的其他特征。"}]}' WHERE id = 24236;
UPDATE genes SET GHR_metadata_ch = '{"gene": "CBFB", "conditions": [{"name": "核心结合因子急性髓性白血病", "detail": "影响CBFBgene的遗传物质的重排涉及一种称为急性髓性白血病（AML）的血癌形式。由于遗传变化影响CBF，因此该病被归类为核心结合因子AML（CBF-AML）。这些重排中最常见的是染色体16区域的倒位（写为inv（16））。倒置涉及两个地方的染色体断裂；将得到的DNA片段反转并重新插入染色体中。不太常见的是，称为易位的重排发生在染色体16的两个拷贝之间（写为t（16；16））。在这种易位中，来自染色体每个拷贝的DNA片段断裂并互换。两种类型的遗传重排导致16号染色体上两个gene的部分CBFB和MYH11融合。这些重排与成人AML病例的5%至8%有关。（换行）当这些重排发生在早期血细胞中时，RUNX1蛋白的功能受到特别影响。由融合gene产生的蛋白质称为CBF-MYH11，仍然可以绑定到RUNX1以形成CBF。但是，CBF的功能受损。CBF的存在-MYH11可能会阻止CBF与DNA的结合，从而阻止RUNX1控制gene活性。或者，融合蛋白的MYH11部分可能与阻止RUNX1控制gene活性的其他蛋白相互作用。gene活性的这种变化阻断了血细胞的成熟（分化），并导致产生称为髓母细胞的异常，未成熟的白细胞。尽管inv（16）和t（16；16）对于白血病发展是重要的，但是髓母细胞发育成癌性白血病细胞通常需要一个或多个额外的遗传改变。"}]}' WHERE id = 24246;
UPDATE genes SET GHR_metadata_ch = '{"gene": "AMPD1", "conditions": [{"name": "腺苷一磷酸脱氨酶缺乏症", "detail": "AMPD1gene突变已被发现导致AMP脱氨酶缺乏症，这是一种以运动后疲劳，骨骼肌疼痛或无力（肌痛）或长时间体力活动（运动不耐受）为特征的疾病，并由于缺陷而表现出肌张力低下和肌肉萎缩（萎缩）/无功能的AMP脱氨酶。"}]}' WHERE id = 24285;
UPDATE genes SET GHR_metadata_ch = '{"gene": "EARS2", "conditions": [{"name": "Leukoencephalopathy with thalamus and brainstem involvement and high lactate", "detail": "At least 23 mutations in the EARS2 gene have been found in individuals with leukoencephalopathy with thalamus and brainstem involvement and high lactate (LTBL), a condition characterized by abnormalities in certain brain regions, including the thalamus and the brainstem (the part of the brain that connects to the spinal cord), and a high level of a substance called lactate in the brain and elsewhere in the body. Affected individuals typically have problems with thinking and motor abilities and with controlling muscle function. (line-break) The EARS2 gene mutations involved in LTBL likely reduce the amount of mitochondrial glutamyl-tRNA synthetase. A shortage of this protein is thought to prevent the normal assembly of new proteins within mitochondria. Researchers speculate that impaired protein assembly disrupts mitochondrial energy production. However, it is unclear exactly how EARS2 gene mutations lead to the features of LTBL.\r"}, {"name": "利综合征", "detail": "遗传学家庭参考提供有关Leigh综合征的信息。"}]}' WHERE id = 24289;
UPDATE genes SET GHR_metadata_ch = '{"gene": "TET2", "conditions": [{"name": "原发性血小板增多症", "detail": "一些gene突变是在一个人的一生中获得的，并且仅存在于某些细胞中。这些被称为体细胞突变的变化不是遗传的。已经在少数患有原发性血小板增多症的人中鉴定出TET2gene的体细胞突变，这是一种以血液中大量血小板为特征的疾病。血小板是参与血液凝固的血细胞碎片。（换行）TET2gene突变以不同方式改变TET2蛋白；但是，它们似乎都导致蛋白质功能异常。这些突变在原发性血小板增多症发展中的作用尚不清楚。"}, {"name": "真性红细胞增多症", "detail": "TET2gene中的体细胞突变与真性红细胞增多症有关，真性红细胞增多症是一种以血细胞生成不受控制为特征的疾病。这些突变被认为导致无功能的蛋白质。在大约16%的真性红细胞增多症患者中发现了该gene的突变。目前尚不清楚这些突变在真性红细胞增多症的发展中起什么作用。"}, {"name": "原发性骨髓纤维化", "detail": "TET2gene的体细胞突变与原发性骨髓纤维化有关。这种情况的特征是骨髓（产生血细胞的组织）中的瘢痕组织（纤维化）。目前尚不清楚TET2gene突变在原发性骨髓纤维化发展中起什么作用。"}, {"name": "其他疾病", "detail": "体细胞TET2gene突变还与某些类型的血液形成细胞癌（白血病）以及称为骨髓增生异常综合症的血液和骨髓疾病有关。这些突变被认为导致无功能的TET2蛋白。造血干细胞中TET2蛋白的丢失可能导致这些细胞的生长和分裂不受控制。研究人员正在努力确定TET2gene突变在骨髓疾病发展中起什么作用。"}]}' WHERE id = 24293;
UPDATE genes SET GHR_metadata_ch = '{"gene": "NRAS", "conditions": [{"name": "巨大的先天性黑素细胞痣", "detail": "已发现NRASgene中至少有两个突变会导致巨大的先天性黑素细胞痣。这种情况的特征是从出生开始就存在大量非癌性的异常深色皮肤斑块，并增加了一种称为黑素瘤的皮肤细胞癌的风险。导致这种情况的NRASgene突变是体细胞的，这意味着它们发生在一个人的一生中，并且仅存在于某些细胞中。突变发生在胚胎发育过程中的细胞中，这些细胞将发育成产生色素的皮肤细胞（黑素细胞）。导致这种情况的突变会影响N-Ras蛋白中的单个蛋白结构单元（氨基酸）。具体而言，突变将第61位的氨基酸谷氨酰胺替换为赖氨酸或精氨酸（写为Gln61Lys或Q61K和Gln61Arg或Q61R）。这些突变导致产生不断打开（组成型活性）的N-Ras蛋白。过度活跃的蛋白质不是响应来自细胞外部的特定信号而触发细胞生长，而是指导细胞不断生长和分裂。早期黑素细胞的不受控制的细胞生长导致巨大的先天性黑素细胞痣特征性的大片深色皮肤。出生后黑素细胞的不受控制的细胞生长导致巨大的先天性黑素细胞痣患者发生黑素瘤的风险。"}, {"name": "努南综合征", "detail": "遗传学家庭参考资料提供有关Noonan综合征的信息。"}, {"name": "自身免疫性淋巴组织增生综合征", "detail": "遗传学家庭参考提供有关自身免疫性淋巴组织增生综合症的信息。"}, {"name": "核心结合因子急性髓性白血病", "detail": "遗传学家庭参考提供有关核心结合因子急性髓性白血病的信息。"}, {"name": "细胞遗传学正常的急性髓性白血病", "detail": "遗传学家庭参考提供有关细胞遗传学正常的急性髓细胞性白血病的信息。"}, {"name": "表皮痣", "detail": "遗传学家庭参考提供有关表皮痣的信息。"}, {"name": "肺癌", "detail": "遗传学家庭参考提供有关肺癌的信息。"}, {"name": "癌症", "detail": "NRASgene中的体细胞突变与几种类型的癌症的发展有关。这些突变导致组成型活性的N-Ras蛋白，并且可以指导细胞在没有控制的情况下生长和分裂。研究表明，NRASgene突变在侵袭性皮肤癌黑色素瘤中很常见，包括没有巨大先天性黑素细胞痣的个体（如上所述）。在其他类型的癌症中也发现了NRASgene的突变。（换行）由于尚不清楚的原因，NRASgene的遗传突变似乎并未增加Noonan综合征患者患癌症的风险。"}]}' WHERE id = 24294;
UPDATE genes SET GHR_metadata_ch = '{"gene": "IDH2", "conditions": [{"name": "2-羟基戊二酸尿症", "detail": "已经发现IDH2gene中的至少两个突变引起一种称为II型D-2-羟基戊二酸尿症（D-2-HGA）的2-羟基戊二酸尿症。这种情况有多种体征和症状，主要是由于生命早期开始对大脑进行性损害所致。（换行）导致II型D-2-HGA的突变存在于所有受影响人的细胞中。这些突变阻止异柠檬酸脱氢酶2进行其通常的活性，即异柠檬酸转化为2-酮戊二酸。相反，改变后的酶具有新的异常功能：产生称为D-2-羟基戊二酸的化合物。由于遗传变化导致酶具有新功能，因此将其分类为“功能获得”突变。(换行)在D-2-HGA II型患者中，D-2-羟基戊二酸在细胞中异常积聚。在高水平，这种化合物可以破坏细胞并导致细胞死亡。脑细胞似乎最容易受到这种化合物的毒性作用，这可以解释为什么D-2-HGA II型的体征和症状主要涉及大脑。但是，某些患有这种疾病的人的心脏也变弱和肿大（心肌病）。尚不清楚为什么D-2-羟基戊二酸的积累可能与心肌病有关。"}, {"name": "Maffucci综合征", "detail": "IDH2gene的突变可引起Maffucci综合征，这是一种主要影响骨骼和皮肤的疾病。它的特征是多发性内生软骨瘤，是在骨骼中发展的软骨的非癌性（良性）生长，以及由异常血管缠结（血管瘤）组成的皮肤中的红色或紫色生长。(换行)与Maffucci综合征相关的突变是体细胞的，这意味着它们发生在一个人的一生中并且不是遗传的。体细胞突变发生在单个细胞中。随着该细胞继续生长和分裂，源自其的细胞也具有相同的突变。在Maffucci综合征中，突变被认为发生在出生前早期发育的细胞中；由异常细胞产生的细胞具有突变，而身体的其他细胞则没有。这种情况称为马赛克。在一些内生软骨瘤细胞中发现了IDH2gene突变，并且与前面讨论的其他IDH2gene突变一样，它们似乎是导致D-2-羟基戊二酸产生的功能获得性突变，但是这种异常累积物质和疾病的体征和症状尚不清楚。"}, {"name": "奥利尔病", "detail": "IDH2gene的突变也可引起Ollier病，这是一种类似于Maffucci综合征（如上所述）但没有血管异常的疾病。(换行)与Maffucci综合征一样，引起Ollier病的IDH2gene突变是体细胞功能获得性突变，被认为发生在发育早期，导致镶嵌。在一些患有Ollier病的人的软骨瘤细胞中发现了IDH2gene突变，但是突变与疾病的体征和症状之间的关系尚不清楚，并且尚不清楚为什么IDH2突变会引起这些各种疾病。"}, {"name": "胆管癌", "detail": "遗传学家庭参考提供有关胆管癌的信息。"}, {"name": "细胞遗传学正常的急性髓性白血病", "detail": "在一些患有一种称为细胞遗传学正常的急性髓性白血病（CN-AML）的血癌形式的人中，已经鉴定出IDH2gene的突变。虽然大的染色体异常可能参与急性髓性白血病的发展，但大约一半的病例没有这些异常；这些被归类为CN-AML。CN-AML患者中近20%的IDH2gene突变。（换行）涉及CN-AML的IDH2gene突变称为体细胞突变；它们仅在癌变且未遗传的细胞中发现。这些突变改变了异柠檬酸脱氢酶2酶中的单个蛋白质结构单元（氨基酸）。像引起上述情况的遗传变化一样，在CN-AML中发现的IDH2gene突变是功能获得性突变。这些突变改变异柠檬酸脱氢酶2的功能，使其异常产生D-2-羟基戊二酸。研究表明，D-2-羟基戊二酸的增加可能会干扰决定未成熟细胞最终将成为的细胞类型的过程（细胞命运决定）。具有体细胞IDH2gene突变的未成熟血细胞不是成为正常的成熟细胞，而是变得癌变并无法控制地分裂，这在CN-AML的发展中起作用。"}, {"name": "原发性骨髓纤维化", "detail": "遗传学家庭参考提供有关原发性骨髓纤维化的信息。"}, {"name": "其他癌症", "detail": "IDH2gene的体细胞突变与其他形式的癌症有关，包括称为神经胶质瘤的脑肿瘤。它们还与原发性骨髓纤维化有关，原发性骨髓纤维化是一种影响骨髓功能和新血细胞产生的癌症相关疾病。（换行）与引起上述病症的遗传变化一样，在这些癌症中发现的IDH2gene突变是功能获得性突变，其导致D-2-羟基戊二酸的异常产生。如在CN-AML中，D-2-羟基戊二酸可能阻断细胞的成熟，导致未成熟细胞的过量产生和肿瘤的形成。目前尚不清楚为什么仅在这几种类型的癌症中发现IDH2gene突变。"}]}' WHERE id = 24295;
UPDATE genes SET GHR_metadata_ch = '{"gene": "FLNB", "conditions": [{"name": "Atelosteogenesis类型1", "detail": "已经鉴定出至少七个FLNBgene突变，这些突变会导致1型骨生成，这是一种影响全身骨骼发育的疾病。突变会改变纤维蛋白B蛋白中的单个蛋白结构单元（氨基酸）或删除一小部分蛋白序列，从而导致蛋白异常。这种异常蛋白质似乎具有新的非典型功能，干扰软骨细胞的正常增殖或分化，损害骨化并导致1型骨质疏松症的体征和症状。"}, {"name": "Atelosteogenesis类型3", "detail": "已经鉴定出至少六个FLNBgene突变，这些突变会导致3型骨生成，这是一种影响全身骨骼发育的疾病。这些突变会改变纤维蛋白B蛋白中的单个氨基酸或删除一小部分蛋白序列，从而导致蛋白异常。这种异常蛋白质似乎具有新的非典型功能，干扰软骨细胞的正常增殖或分化，损害骨化并导致3型骨质疏松症的体征和症状。"}, {"name": "回旋镖发育不良", "detail": "已经鉴定出至少两个导致回旋镖发育异常的FLNBgene突变，该疾病影响整个身体骨骼的发育。这些突变会改变纤维蛋白B蛋白中的单个氨基酸或删除一小部分蛋白序列，从而导致蛋白异常。这种异常蛋白质似乎具有新的非典型功能，会干扰软骨细胞的正常增殖或分化，损害骨化并导致回旋镖发育不良的体征和症状。"}, {"name": "拉森综合征", "detail": "已经鉴定出至少13种FLNBgene突变引起Larsen综合征，Larsen综合征是一种影响全身骨骼发育的疾病。这些突变会改变纤维蛋白B蛋白中的单个氨基酸或删除一小部分蛋白序列，从而导致蛋白异常。这种异常的蛋白质似乎具有新的非典型功能，会干扰软骨细胞的正常增殖或分化，损害骨化并导致拉森综合征的体征和症状。（换行）尚不清楚为什么FLNBgene中的类似突变会导致四种不同的疾病：1型骨生成，3型骨生成，回旋镖发育不良或Larsen综合征。FLNBgene区域中称为外显子2至5的某些突变似乎具有最有害的作用，通常导致更严重的疾病，1型骨生成不良和回旋镖发育不良。较轻的3型骨生成和Larsen综合征通常是由外显子2至5的明显轻度突变或外显子27至33的突变引起的。在少数情况下，相同的突变与不同人群中的一种以上的这些疾病有关。"}, {"name": "脊椎腕骨滑膜综合征", "detail": "已经鉴定出至少五个FLNBgene突变，这些突变会导致脊椎腕骨滑膜综合症，这种疾病会影响整个身体骨骼的发育。这些突变可能发生在gene的任何区域，导致产生异常短的纤维蛋白B蛋白，该蛋白不稳定并迅速分解。纤维蛋白B蛋白的丢失似乎导致不适当的（异位）骨化，导致脊柱，手腕和脚踝的骨骼融合以及脊椎腕骨滑膜综合症的其他体征和症状。"}, {"name": "其他疾病", "detail": "FLNBgene中的几种常见变异（多态性）与低骨矿物质密度（骨质疏松症）相关，这会削弱骨骼并使其易于骨折。FLNBgene变异可能会影响整个生命周期中骨结构的维持，并导致骨矿物质密度的差异。"}]}' WHERE id = 24296;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ABCC2", "conditions": [{"name": "Dubin-Johnson综合征", "detail": "ABCC2中的突变会导致一种称为Dubin-Johnson综合征的疾病，其特征是存在缺陷的MRP2抑制其向细胞表面的转运以及体内胆红素的排泄，从而导致该综合征患者出现黄疸。"}, {"name": "癌症", "detail": "ABCC2gene的过度表达导致许多类型的癌症。MRP2表达增加会损害癌症药物代谢并促进药物代谢。"}]}' WHERE id = 24303;
UPDATE genes SET GHR_metadata_ch = '{"gene": "GP9", "conditions": [{"name": "伯纳德-苏里尔综合症", "detail": "已发现至少28个GP9gene突变会引起Bernard-Soulier综合征，该病的特征是血小板数量比正常人减少（大血小板减少症）和出血过多。这些突变导致产生改变的GPIX亚基，其可能过早分解或不能到达血小板表面。血小板表面缺乏该亚基会阻止GPIb-IX-V复合物的形成。没有GPIb-IX-V，血小板不能在损伤部位聚集形成凝块，导致与Bernard-Soulier综合征相关的出血问题。"}]}' WHERE id = 24304;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ZEB2", "conditions": [{"name": "Mowat-Wilson综合征", "detail": "在Mowat-Wilson综合征患者中已鉴定出ZEB2gene的180多个突变。这些突变几乎总是使ZEB2gene的一个拷贝失活。在某些情况下，整个gene被删除。在其他情况下，gene内的突变导致ZEB2蛋白异常短而无功能的产生。(换行)ZEB2蛋白的缺乏破坏了出生前许多器官和组织的形成。神经c衍生结构（例如神经系统和面部特征）的异常发育是Mowat-Wilson综合征的许多体征和症状的基础。ZEB2蛋白在控制消化道的神经发育中的作用可能有助于解释为什么许多患有这种疾病的人也患有Hirschsprung病，这是一种导致严重便秘，肠梗阻和结肠扩大的肠道疾病。"}]}' WHERE id = 24337;
UPDATE genes SET GHR_metadata_ch = '{"gene": "NTRK1", "conditions": [{"name": "先天性对无汗症疼痛不敏感", "detail": "NTRK1gene的突变会导致先天性对无汗症（CIPA）的疼痛不敏感，这种疾病的特征是无法感觉到疼痛以及出汗减少或缺乏（无汗症）。已知NTRK1gene中的许多突变引起该病症。许多NTRK1gene突变导致蛋白质不能被磷酸化激活，这意味着突变的NTRK1蛋白质不能将细胞生长和存活信号传递给神经元。如果没有适当的信号传导，神经元就会死于称为细胞凋亡的自我毁灭过程。感觉神经元的丧失导致CIPA患者无法感觉到疼痛。此外，患有CIPA的人会失去导致汗腺的神经，从而导致受影响的人出现多汗症。"}, {"name": "癌症", "detail": "NTRK1gene的突变经常在患有甲状腺乳头状癌的常见甲状腺癌患者中发现。这些突变是在一个人的一生中获得的，仅存在于某些细胞中。这种突变称为体细胞突变。当遗传物质的重排将NTRK1gene的一部分与另一个gene结合时，就会发生甲状腺乳头状癌中涉及的突变。已知至少三个其他gene参与这些重排：TPM3gene，TPRgene和TFGgene。所有这些gene重排产生称为TRK癌蛋白的突变蛋白。与正常的NTRK1蛋白不同，TRK癌蛋白不必通过与NGF结合而被激活？蛋白质；它们总是被打开。蛋白质信号的不断激活使细胞不断生长和分裂，这可能导致甲状腺乳头状癌。"}]}' WHERE id = 24352;
UPDATE genes SET GHR_metadata_ch = '{"gene": "TSHB", "conditions": [{"name": "先天性甲状腺功能减退症", "detail": "研究人员已经鉴定出至少10种与先天性甲状腺功能减退有关的TSHBgene突变，这种疾病的特征是从出生开始甲状腺激素水平异常低。TSHBgene突变是一种称为中枢性先天性甲状腺功能减退症的疾病的主要原因，这种疾病发生在垂体对甲状腺激素产生的刺激受损时。（换行）涉及先天性甲状腺功能减退症的TSHBgene突变改变促甲状腺激素β亚基的大小或形状。许多突变会影响β亚基的安全带区域。一些突变严重缩短了β亚基，部分或全部消除了安全带区域。其他突变会改变用于制造β亚基的蛋白质结构单元（氨基酸）。结果，安全带区域不能绕α子单元弯曲。TSHBgene突变阻止垂体产生功能性促甲状腺激素或其释放（分泌）。结果，不刺激甲状腺激素的产生，导致先天性甲状腺功能减退症特有的低激素水平。此外，甲状腺的大小减小（发育不良），因为它的生长不受刺激。"}]}' WHERE id = 24399;
UPDATE genes SET GHR_metadata_ch = '{"gene": "CARD14", "conditions": [{"name": "家族性糠疹rubra pilaris", "detail": "在家族性糠疹rubra pilaris患者中已鉴定出CARD14gene的至少三个突变，这是一种罕见的遗传性皮肤病，其特征是细小鳞片覆盖着斑片状的鲑鱼色皮疹。这些突变导致NF-κB过度活化？B信号，触发皮肤异常炎症反应。研究人员正在努力确定这些变化如何导致家族性糠疹糠疹的具体特征。"}, {"name": "广义脓疱型银屑病", "detail": "在广泛性脓疱性牛皮癣（GPP）患者中发现了至少三个CARD14gene突变，这是一种罕见且严重的炎症性皮肤病，称为牛皮癣。患有GPP的个体反复发作，其中大部分皮肤变红并发炎，并形成小的充满脓液的水泡（脓疱）。皮肤问题可伴有全身发热和其他炎症迹象（全身性炎症）。这些发作被认为是由感染，某些药物，怀孕或身体其他压力引起的。与CARD14相关的GPP的大多数人也有最常见的牛皮癣症状，称为寻常型牛皮癣（PV），其特征是皮肤呈红色鳞状斑块（斑块）。（换行）与GPP相关的CARD14gene突变（包括具有PV的GPP）导致产生改变的CARD14蛋白，其更容易打开NF-？B、 过度活跃的NF-？B信号传导增加参与身体炎症反应的gene的活性。由此产生的异常炎症导致GPP和PV特有的皮肤问题。（换行）CARD14gene突变增加发生GPP的风险。并非每个gene突变的人都有炎症的特征性问题。人们认为这种复杂的状况是遗传和环境因素共同作用的结果。"}, {"name": "银屑病关节炎", "detail": "遗传学家庭参考提供有关银屑病关节炎的信息。"}, {"name": "其他疾病", "detail": "CARD14gene的改变也可能导致其他形式的牛皮癣。在牛皮癣家族中已经鉴定出至少两个突变。在没有该家族病史的人群中，CARD14gene的其他几种罕见变体与牛皮癣风险增加相关。研究表明，这些遗传变化增强了NF-κB的活化？B、 导致异常炎症，这是牛皮癣的特征。但是，CARD14gene突变似乎是这种情况的罕见危险因素。其他因素与特定的CARD14gene突变相结合可能有助于确定哪种形式的皮肤炎症发展。"}]}' WHERE id = 24400;
UPDATE genes SET GHR_metadata_ch = '{"gene": "PAFAH1B1", "conditions": [{"name": "孤立的lissencephaly序列", "detail": "已发现PAFAH1B1gene中的70多个突变会导致孤立的小脑序列（ILS）。这种情况的特征是大脑发育异常，导致大脑外观光滑（lissencephaly），而不是正常的褶皱和凹槽。ILS患者有严重的神经系统问题，包括智力障碍和反复发作（癫痫病）。导致ILS的大多数PAFAH1B1gene突变会导致异常小的，无功能的PAFAH1B1蛋白产生。PAFAH1B1gene突变占所有ILS病例的一半以上。（换行）由于PAFAH1B1gene突变，PAF水平不受控制，微管的正常功能受损。发育中的大脑中的神经元受到特别影响，这会损害大脑发育并导致ILS特有的严重神经系统问题。"}, {"name": "米勒-迪克综合征", "detail": "Miller-Dieker综合征的特征性体征和症状是由17号染色​​体短（p）臂末端附近的遗传物质缺失引起的。通常缺失的染色体区域包含多个gene，包括PAFAH1B1gene。缺失的结果是，患有这种情况的人在每个细胞中只有一个PAFAH1B1gene拷贝，而不是通常的两个拷贝。（换行）每个细胞中PAFAH1B1gene的一个拷贝的缺失使PAFAH1B1蛋白的量减少约一半。研究人员认为，这种蛋白质的缺乏是造成Miller-Dieker综合征许多特征的原因，包括智力残疾，发育迟缓和癫痫。由缺乏PAFAH1B1蛋白引起的神经元迁移减少是造成Miller-Dieker综合征特征的小脑畸形的原因。（换行）在17号染色​​体的同一区域缺失的其他gene是造成Miller-Dieker综合征的其他特征的原因，例如独特的面部特征，生长缓慢和呼吸困难。"}, {"name": "其他疾病", "detail": "在某些情况下，PAFAH1B1gene突变仅存在于人体的某些细胞中，这种情况称为镶嵌症。PAFAH1B1gene突变的镶嵌术会导致较轻的脑部异常，称为皮层下带异位症。当神经元迁移到不应该是的大脑区域（异位症）并形成异常的带状簇时，就会发生这种异常。由于这些条带位于大脑皮层下方，因此据说它们是皮层下的。皮质下带异位症的体征和症状从严重的智力障碍和癫痫病到正常的智力（轻度或无癫痫病）不等。"}]}' WHERE id = 24409;
UPDATE genes SET GHR_metadata_ch = '{"gene": "CNBP", "conditions": [{"name": "肌强直性营养不良", "detail": "2型强直性肌营养不良症是由CNBPgene突变（称为四核苷酸重复扩增）引起的。这种突变增加了CNBPgene中重复CCTG片段的大小。患有2型强直性肌营养不良症的人具有75至11000多次CCTG重复。(换行)突变的CNBPgene产生信使RNA的改变形式，这是通常用于指导蛋白质产生的gene的分子蓝图。研究人员发现，改变后的信使RNA捕获蛋白质，在细胞内形成团块。团块干扰许多其他蛋白质的产生。这些变化阻止了其他组织中的肌肉细胞和细胞正常运作，导致肌肉无力和2型肌强直性营养不良的其他特征。"}]}' WHERE id = 24410;
UPDATE genes SET GHR_metadata_ch = '{"gene": "NGF", "conditions": [{"name": "V型遗传性感觉和自主神经病", "detail": "据报道，NGFgene中至少有一个突变会引起遗传性感觉和自主神经病V型（HSAN5），这种疾病的特征是无法感觉到疼痛和冷热。这种突变改变了NGF中的单个蛋白质构建块（氨基酸）？蛋白质。氨基酸精氨酸在位置100处被氨基酸色氨酸取代（写为Arg100Trp或R100W）。研究表明，突变的NGF？蛋白质不能与p75NTR受体结合，并且它通过NTRK1受体改变信号传导。此外，患有HSAN5的人的感觉神经元数量减少。然而，NGFgene突变导致无法感觉到疼痛和温度感觉的机制尚不清楚。虽然NGF？蛋白质在许多类型的神经元中都很重要，只有感觉神经元似乎在HSAN5患者中受到影响。"}]}' WHERE id = 24411;
UPDATE genes SET GHR_metadata_ch = '{"gene": "CASQ2", "conditions": [{"name": "儿茶酚胺能性多形性室性心动过速", "detail": "已经在儿茶酚胺能性多形性室性心动过速（CPVT）患者中发现了CASQ2gene的突变，这种情况是由无功能的calsequestrin 2蛋白引起的，该蛋白损害心肌收缩和舒张，导致心律不齐和其他症状，包括不规则的心跳，称为室性心动过速，导致头晕，头晕，和昏厥。"}]}' WHERE id = 24435;
UPDATE genes SET GHR_metadata_ch = '{"gene": "OTX2", "conditions": [{"name": "视光发育不良", "detail": "在具有视光发育不良的主要特征的人群中，已经鉴定出OTX2gene中至少有8个突变。这些突变中的一些阻止了功能性OTX2蛋白的产生。其他突变导致蛋白质的缺陷形式不能调节其他gene的活性。(换行)OTX2蛋白的缺乏会破坏眼睛，视神经，垂体和其他大脑结构的形成和早期发育。这些发育问题导致视光发育不良的主要特征，包括眼部异常，垂体发育不全（垂体发育不全）和学习困难。但是，即使在同一家庭的受影响成员中，与OTX2gene突变相关的体征和症状也有很大差异。OTX2gene突变患者中已报道的其他特征包括发育迟缓，生长缓慢和癫痫发作。（换行）研究表明，OTX2gene的突变是视光发育不良的罕见原因。"}, {"name": "缺损", "detail": "遗传学家庭参考提供有关结肠癌的信息。"}, {"name": "合并垂体激素缺乏症", "detail": "遗传学家庭参考资料提供有关合并垂体激素缺乏症的信息。"}, {"name": "小眼症", "detail": "遗传学家庭参考提供有关小眼症的信息。"}]}' WHERE id = 24437;
UPDATE genes SET GHR_metadata_ch = '{"gene": "CEP57", "conditions": [{"name": "马赛克杂色非整倍性综合征", "detail": "已发现至少三个CEP57gene突变会导致2型镶嵌杂色非整倍性（MVA）综合征。这种情况的特征是染色体数目异常的细胞，这种情况称为非整倍性。受影响的人出生前后生长缓慢，可能患有轻度智力障碍和其他健康问题。（换行）涉及2型MVA综合征的CEP57gene突变可能会减少细胞中功能性CEP57蛋白的量。目前尚不清楚这些变化如何导致病情的特征。研究人员推测，蛋白质在稳定纺锤体微管中的作用受损可能会阻止细胞分裂过程中染色体的正常分离，从而导致非整倍性。尽管他们不确定CEP57gene突变如何导致2型MVA综合征的其他特征，但一些人认为功能性CEP57蛋白的缺乏会阻止FGF2的正确转运，从而可能损害细胞信号传导并导致生长发育问题。"}]}' WHERE id = 24459;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ATP1A1", "conditions": [{"name": "产生醛固酮的腺瘤", "detail": "已发现ATP1A1gene的突变会引起产生醛固酮的腺瘤，这是在肾上腺中形成的非癌性（良性）肿瘤。这些肿瘤中涉及的遗传变化称为体细胞突变，是在一个人的一生中获得的，仅存在于引起肿瘤的肾上腺细胞中。ATP1A1gene的变化约占产生醛固酮腺瘤病例的6%。（换行）ATP1A1gene突变损害Na+/K+ATP酶的正常功能。异常泵允许钠或氢（H+）离子进入细胞。这些离子的异常流动增加醛固酮的产生，导致醛固酮过量并导致高血压（高血压）和心脏病发作和中风的风险增加。由于Na+/K+ATP酶功能受损导致的肾上腺细胞变化也增加细胞生长和分裂（增殖），促进腺瘤形成。"}]}' WHERE id = 24468;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SCN5A", "conditions": [{"name": "布鲁格达氏症候群", "detail": "在患有Brugada综合征的人中已鉴定出SCN5Agene的400多个突变，Brugada综合征是一种以心律不规则（心律不齐）为特征的心脏病。SCN5Agene突变还引起突然的意外夜间死亡综合症（SUNDS），最初在东南亚人群中描述。此后，研究人员确定SUNDS和Brugada综合征是同一种疾病。（换行）与Brugada综合征相关的一些SCN5Agene突变改变了SCN5A蛋白中的单个蛋白构建块（氨基酸）。这些突变改变了由SCN5A蛋白形成的离子通道的结构，并破坏了钠离子流入心肌细胞。其他突变阻止SCN5Agene产生任何功能性离子通道，这也减少了钠离子的向内流动。离子传输的中断改变了心跳的方式，导致在Brugada综合征和SUNDS中经常发现的心律不齐。"}, {"name": "进行性家族性心脏传导阻滞", "detail": "已发现SCN5Agene中的一些突变会导致进行性家族性心脏传导阻滞。这种情况会改变心脏的正常跳动，并可能导致晕厥（晕厥）或心脏骤停和死亡。SCN5Agene突变会改变SCN5A蛋白中的单个氨基酸。用这种改变的蛋白质制成的通道几乎不允许钠进入细胞。具有这些改变的通道的心脏细胞难以产生和传输协调正常心跳的电信号。这种信号的中断被称为心脏传导阻滞。受损的心脏细胞死亡，随着时间的推移导致瘢痕组织（纤维化）的累积，使心脏阻滞恶化。"}, {"name": "罗曼诺-沃德综合症", "detail": "已知SCN5Agene中至少有238个突变会引起Romano-Ward综合征，这是称为长QT综合征的心律失常的最常见形式。该gene的突变占Romano-Ward综合征病例的5%至10%。在患有这种情况的个体中，心肌在心跳之间需要比平时更长的时间来充电。（换行）引起Romano-Ward综合征的SCN5Agene突变包括单个氨基酸的变化以及SCN5A蛋白中少量氨基酸的缺失或插入。用这些改变的SCN5A蛋白制成的通道比平时开放的时间更长，这使钠离子继续异常流入心肌细胞。通道关闭的这种延迟改变了心脏中电信号的传输，增加了可能导致晕厥或猝死的不规则心跳的风险。"}, {"name": "病态窦房结综合征", "detail": "已发现SCN5Agene中至少有16个突变会引起另一种称为病态窦房结综合征的心脏病。这种情况会影响窦房结（SA）的功能，窦房结是心脏中专门充当天然起搏器的细胞区域。导致病态窦房结综合征的SCN5Agene突变导致产生无功能的钠通道或无法正确运输离子的异常通道。这些离子的流动对于产生开始每次心跳并将这些信号传播到心脏其他区域的电脉冲至关重要。突变会减少钠离子的流动，从而改变SA节点创建和传输电信号的能力。这些变化增加了异常快速或慢速心跳的风险，这可能导致头晕，头晕，晕厥和相关症状。"}, {"name": "家族性心房颤动", "detail": "遗传学家庭参考资料提供有关家族性心房颤动的信息。"}, {"name": "家族性扩张型心肌病", "detail": "遗传学家庭参考提供有关家族性扩张型心肌病的信息。"}, {"name": "室致密化不全", "detail": "遗传学家庭参考提供有关左心室不紧致的信息。"}, {"name": "其他疾病", "detail": "SCN5Agene的变异与其他几种心脏病有关。这些包括潜在的危及生命的心律失常形式，称为心房颤动和心室颤动。与这些情况相关的遗传变异会改变钠离子通过通道的流量，从而导致异常的心律并影响心脏抽血的能力。（换行）在某些婴儿猝死综合症（SIDS）病例中也发现了SCN5Agene突变。小岛屿发展中国家是1岁以下婴儿死亡的主要原因。它的特点是突然和无法解释的死亡，通常在睡眠期间。研究人员正在努力确定SCN5Agene的变化如何导致SIDS。其他遗传和环境因素，其中许多尚未确定，也参与确定这种疾病的风险。(换行)某些药物，包括用于治疗心律失常，感染，癫痫发作和精神障碍的药物，可能导致某些人心律异常。这种药物引起的心脏病被称为获得性长QT综合征，增加了心脏骤停和猝死的风险。一小部分获得性长QT综合征病例发生在SCN5Agene发生潜在变化的人群中。"}]}' WHERE id = 24479;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ORC4", "conditions": [{"name": "梅尔-戈林综合征", "detail": "ORC4gene的突变会导致Meier-Gorlin综合征，这种疾病的特征是身材矮小，膝盖发育不全和耳朵小。这些突变通常通过改变单个蛋白质构建块（氨基酸）或导致产生异常短版本的ORC4蛋白质来改变ORC4蛋白质。患有这种情况的人中最常见的ORC4gene突变是用氨基酸半胱氨酸（写为Tyr174Cys）替换174位的氨基酸酪氨酸。这些更改的结果是，复制前复合体的组装受到损害，从而破坏了复制许可；但是，尚不清楚复制许可证的减少如何导致Meier-Gorlin综合征。研究人员推测，这种减少会延迟细胞分裂过程，从而减缓发育过程中骨骼和其他组织的生长。不知道为什么膝盖和耳朵的发育受到特别影响。"}]}' WHERE id = 24480;
UPDATE genes SET GHR_metadata_ch = '{"gene": "MTMR2", "conditions": [{"name": "进行性神经性腓骨肌萎缩症", "detail": "研究人员已经鉴定出至少10种MTMR2gene突变，这些突变会导致一种称为4B1型的Charcot-Marie-Tooth病。这些突变中的一些会在制造MTMR2蛋白的说明中产生过早的终止信号，从而导致蛋白异常短。其他突变通过改变用于制造蛋白质的结构单元（氨基酸）之一来改变蛋白质的结构。所有这些突变都可能损害蛋白质修饰化学信使的能力，从而破坏脂质和蛋白质的运输。（换行）尚不清楚MTMR2gene突变如何导致4B1型Charcot-Marie-Tooth病的特征。科学家认为，当MTMR2蛋白受损时，神经系统中称为施万细胞的特殊细胞特别脆弱。施万细胞产生髓鞘，髓鞘是一种覆盖神经并促进神经冲动快速传递的保护物质。雪旺氏细胞依靠大量脂质和蛋白质在细胞内的有效转运来制备髓磷脂。运输中断可能会影响髓磷脂的产生，从而改变神经冲动的传递。神经冲动传递障碍是4B1型Charcot-Marie-Tooth病的征兆。"}]}' WHERE id = 24485;
UPDATE genes SET GHR_metadata_ch = '{"gene": "L1CAM", "conditions": [{"name": "L1综合征", "detail": "已发现L1CAMgene中的350多个突变引起L1综合征。L1综合征描述了一组严重程度不同的疾病，主要影响神经系统，几乎仅在男性中发生。L1综合征患者经常有脑部异常，智力障碍和运动问题。导致这种情况的L1CAMgene突变会导致L1蛋白无法促进细胞间粘附或参与各种神经元功能。这些功能的破坏可能会阻碍大脑的生长发育，从而导致L1综合征的体征和症状。（换行）一些L1CAMgene突变导致异常短且无功能的蛋白质的产生或导致蛋白质的完全缺失。这些突变通常会导致严重的L1综合征病例。其他突变会改变L1蛋白中的单个蛋白结构单元（氨基酸），从而削弱该蛋白与细胞表面其他蛋白相互作用的能力，或阻止该蛋白到达需要的细胞表面。这些突变通常导致L1综合征的轻度形式。虽然gene突变对L1蛋白的影响有时可以提供病情严重程度的线索，但具有相同或相似突变的个体通常具有非常不同的体征和症状。"}, {"name": "其他疾病", "detail": "很少在既有大脑积液（脑积水）又有Hirschsprung病的个体中发现L1CAMgene突变。Hirschsprung病是一种肠道疾病，其特征是肠道部分缺乏神经。目前尚不清楚这组特征是否应归类为L1综合征疾病组（如上所述）的一部分或作为单独的病症。导致这些特征的L1CAMgene突变可能会破坏大脑和肠道中神经细胞的正常迁移和功能，从而导致脑积水和Hirschsprung疾病。"}]}' WHERE id = 24491;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SGSH", "conditions": [{"name": "黏多醣贮积症III型", "detail": "已发现SGSHgene中的80多个突变会导致IIIA型粘多糖贮积病（MPS IIIA）。这些突变大多数会改变gene中的单个DNA结构单元（核苷酸）。所有引起MPS IIIA的突变都会降低或消除磺酰胺酶的功能。（换行）磺酰胺酶活性的缺乏破坏了称为硫酸乙酰肝素的GAG子集的分解。结果，部分分解的硫酸乙酰肝素积聚在溶酶体内。研究人员认为，GAG的积累会干扰溶酶体内其他蛋白质的功能，并破坏细胞的正常功能。尚不清楚为什么硫酸乙酰肝素的积累主要影响MPS IIIA中的中枢神经系统。"}]}' WHERE id = 24505;
UPDATE genes SET GHR_metadata_ch = '{"gene": "KRAS", "conditions": [{"name": "心血管系统皮肤综合征", "detail": "KRASgene突变是心血管系统皮肤综合征的罕见原因，占不到5%的病例。已经在具有该疾病特征的人中鉴定出KRASgene的几种突变，包括心脏缺陷，独特的面部特征和皮肤异常。这些突变存在于人体的所有细胞中，被称为种系突变。突变改变了K-Ras蛋白中的单个蛋白结构单元（氨基酸）。改变的蛋白质显示出增加的GTP结合和降低的将GTP转化为GDP的能力。这些作用导致K-Ras蛋白的激活时间延长，从而在发育过程中改变了严格调控的Ras/MAPK信号传导。改变的信号干扰整个身体器官和组织的发育，导致心脏皮肤综合征的各种体征和症状。"}, {"name": "努南综合征", "detail": "遗传学家庭参考资料提供有关Noonan综合征的信息。"}, {"name": "自身免疫性淋巴组织增生综合征", "detail": "遗传学家庭参考提供有关自身免疫性淋巴组织增生综合症的信息。"}, {"name": "胆管癌", "detail": "遗传学家庭参考提供有关胆管癌的信息。"}, {"name": "核心结合因子急性髓性白血病", "detail": "遗传学家庭参考提供有关核心结合因子急性髓性白血病的信息。"}, {"name": "表皮痣", "detail": "遗传学家庭参考提供有关表皮痣的信息。"}, {"name": "肺癌", "detail": "KRASgene中至少有三个突变与肺癌有关。肺癌是一种肺部某些细胞变得异常并无法控制地繁殖形成肿瘤的疾病。肺癌在其早期阶段可能不会引起体征或症状。这些KRASgene突变是体细胞的，这意味着它们是在一个人的一生中获得的，并且仅存在于肿瘤细胞中。体细胞突变不是遗传的。几乎所有与肺癌相关的KRASgene突变都会改变K-Ras蛋白中第12或13位的氨基酸甘氨酸（Gly12或Gly13）或第61位的氨基酸谷氨酰胺（Gln61）。这些突变导致K-Ras蛋白不断打开（组成性激活）并指导细胞以不受控制的方式增殖，从而导致肿瘤形成。当这些遗传变化发生在肺部细胞中时，肺癌就会发展。（换行）KRASgene突变在所有肺癌病例中占15%至25%，但在白人人群中比在亚洲人群中更常见；25%至50%的肺癌白人具有KRASgene突变，而5%至15%的肺癌亚洲人具有KRASgene突变。(换行)KRASgene突变在患有肺癌的长期吸烟者中比非吸烟者更常见。具有KRASgene突变的肺癌通常表明预后不良，并且与对几种癌症治疗的抗性有关。"}, {"name": "其他疾病", "detail": "KRASgene中的种系突变也会引起一种疾病，其主要特征与心血管系统皮肤综合征（如上所述）和两种相关疾病（称为Noonan综合征和Costello综合征）重叠。这种情况已被描述为KRAS突变相关的表型。患有这种疾病的人有各种体征和症状，包括轻度至中度智力障碍，独特的面部特征，身材矮小，异常大头（大头畸形）和稀疏稀疏的头发。(换行)据报道，患有这种疾病的人至少有9个KRASgene突变。这些突变中的每一个都改变了K-Ras蛋白中的单个氨基酸。这些遗传变化异常激活蛋白质，从而改变整个身体细胞中的化学信号。改变的信号传导干扰许多器官和组织的正常发育，导致KRAS突变相关表型的特征。"}, {"name": "其他癌症", "detail": "KRASgene中的体细胞突变参与几种类型的癌症的发展，特别是胰腺癌和结肠直肠癌。这些突变导致K-Ras蛋白比引起心血管系统皮肤综合征的突变更强烈地过度活化（如上所述）。异常的K-Ras蛋白始终具有活性，可以指导细胞以不受控制的方式增殖。"}]}' WHERE id = 24514;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SCN10A", "conditions": [{"name": "小纤维神经病变", "detail": "SCN10Agene突变约占小纤维神经病病例的5%，这种疾病的特征是严重的疼痛发作和冷热区分能力降低。导致这种情况的突变会改变NaV1.8钠通道α亚基中的单个蛋白质结构单元（氨基酸）。许多突变导致NaV1.8钠通道比平时更容易打开，从而增加了在伤害感受器内产生神经冲动的钠离子流。钠离子的这种增加增强了疼痛信号的传递，导致个体对可能不会引起疼痛的刺激更敏感。在这种情况下，从伤害感受器延伸并传递疼痛信号（轴突）的小纤维随时间退化。这种变性的原因尚不清楚，但它可能是体征和症状的原因，例如温度分化的丧失。"}, {"name": "其他疾病", "detail": "已发现SCN10Agene中的某些常见变异（多态性）会增加发生不规则心跳（心律不齐）的风险。这些多态性导致产生改变的NaV1.8钠通道，其可以破坏控制心跳的电信号。具体而言，SCN10Agene的变化与一种称为心脏传导阻滞的心律不齐有关。当心脏的电信号减慢或中断时，会发生心脏传导阻滞。NaV1.8钠通道的变化如何导致心脏传导阻滞尚不清楚。"}]}' WHERE id = 24536;
UPDATE genes SET GHR_metadata_ch = '{"gene": "KCNK9", "conditions": [{"name": "KCNK9印迹综合征", "detail": "已发现KCNK9gene的至少两个变化对TASK3通道蛋白具有相同的作用，可引起KCNK9印迹综合征。这种情况的特征是出生时肌肉张力弱（肌张力低下），会影响进食能力。受影响的人通常具有智力障碍，并且言语和运动技能（例如步行）的发展延迟。因为父亲的KCNK9gene拷贝被沉默，所以只有当母亲遗传的gene拷贝发生突变时才会发生这种情况。（换行）导致KCNK9印迹综合征的gene突变改变TASK3通道中的单个蛋白质构建块（氨基酸）；氨基酸精氨酸取代236位的氨基酸甘氨酸（写为Gly236Arg或G236R）。这种改变使离子通过TASK3通道的流量减少了80%。研究表明，某些TASK3通道改变的神经元无法重复产生电信号。通过TASK3通道的离子传输减少会破坏正常的神经元发育和兴奋性。神经元功能受损可能是KCNK9印迹综合征特征性肌张力低下，智力障碍和发育问题的基础。"}]}' WHERE id = 24538;
UPDATE genes SET GHR_metadata_ch = '{"gene": "AVPR2", "conditions": [{"name": "肾源性尿崩症", "detail": "已经在患有肾源性尿崩症的人中鉴定出AVPR2gene的突变，所述尿崩症的特征在于过量的尿液（多尿）导致体内多饮，脱水或水不平衡，并且如果不维持这种状况则导致膀胱和肾的损害导致疼痛，感染和肾功能衰竭。该病症是由于V2受体的形状异常引起的，其不能与ADH相互作用并维持水稳态。"}, {"name": "其他疾病", "detail": "已经发现AVPR2gene中的突变引起称为不适当抗利尿作用的肾源性综合征（NSIAD）的肾病，其特征在于血液中盐水平低（低钠血症），血清低渗透压，脑肿胀和其他严重并发症。该病症是由于单个氨基酸变化引起的，即使在没有ADH的情况下也导致V2受体的组成型活化，并且无论液体摄入如何，尿液浓度都会增加。"}]}' WHERE id = 24587;
UPDATE genes SET GHR_metadata_ch = '{"gene": "EIF2B2", "conditions": [{"name": "白质消失的白质脑病", "detail": "已经在一些白质消失的白质脑病患者中鉴定出EIF2B2gene的突变，包括一些受影响的女性，其具有神经学特征伴随卵巢衰竭（卵巢脑白质营养不良）的病症的变体。这些突变导致eIF2B功能部分丧失。eIF2B功能受损使人体细胞更难以调节蛋白质合成并应对不断变化的conditions和压力。研究人员认为，白质中的细胞（神经纤维被称为髓鞘的脂肪物质覆盖，能够隔离和保护神经）可能特别受到压力异常反应的影响，导致白质消失的白质脑病的体征和症状。"}]}' WHERE id = 24602;
UPDATE genes SET GHR_metadata_ch = '{"gene": "JAK3", "conditions": [{"name": "JAK3缺陷型严重联合免疫缺陷", "detail": "在患有JAK3缺陷的严重联合免疫缺陷症（SCID）的人群中已鉴定出50多个JAK3gene突变，这是一种免疫系统受损，可导致反复和持续的感染，可能危及生命。（换行）JAK3gene中的大多数突变阻止JAK3蛋白的产生或导致蛋白质不稳定并迅速分解。其他导致产生不能发挥其功能的蛋白质。没有功能性JAK3蛋白，某些化学信号不会传递到细胞核，损害淋巴细胞的生长和发育。结果，具有JAK3缺陷的SCID的个体具有很少或没有T细胞和自然杀伤细胞，并且具有正常数量的功能不良的B细胞。功能性淋巴细胞的短缺导致患有这种病症的人易受感染。"}]}' WHERE id = 24611;
UPDATE genes SET GHR_metadata_ch = '{"gene": "HSD3B2", "conditions": [{"name": "3-β-羟基类固醇脱氢酶缺乏症", "detail": "已发现HSD3B2gene中至少有37个突变导致3-HSD缺陷。大多数这些突变改变了3中的单个蛋白质构建块（氨基酸）-HSD酶，通常会降低酶的活性。允许产生某些功能性酶的突变，尽管水平降低，但会导致不太严重，非盐浪费或非经典形式的3-HSD缺陷。其他突变导致产生异常短的，完全无功能的3-HSD酶，导致这种情况下更严重的食盐浪费形式。所有类型的3-HSD缺乏会干扰多种激素的产生，并导致性发育和成熟异常。"}]}' WHERE id = 24629;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SLC5A5", "conditions": [{"name": "先天性甲状腺功能减退症", "detail": "在先天性甲状腺功能减退症患者中已鉴定出几种SLC5A5gene突变，这种疾病的特征是从出生开始甲状腺激素水平异常低。这些突变中约有一半删除了SLC5A5gene的一部分或破坏了蛋白质的产生，从而导致异常小的无功能蛋白质。其余的突变改变了用于制造NIS蛋白的构建基块（氨基酸）之一。一些氨基酸取代阻止NIS蛋白定位在细胞膜中，从而阻止碘化物转运。其他氨基酸取代不会影响NIS蛋白的膜位置，但会改变蛋白的3维形状，从而损害其功能。（换行）SLC5A5gene突变减少或阻止碘化物转运。结果，甲状腺不能有效地积累碘化物，这减少了甲状腺激素的产生。与这些gene突变相关的先天性甲状腺功能减退症的体征和症状范围从轻度到重度，具体取决于剩余的激素产生水平。在许多情况下，甲状腺肿大（甲状腺肿）以弥补激素产生减少。由于SLC5A5gene突变引起的病例是由于甲状腺激素合成的破坏，因此将其归类为甲状腺功能异常。"}]}' WHERE id = 24631;
UPDATE genes SET GHR_metadata_ch = '{"gene": "PHGDH", "conditions": [{"name": "磷酸甘油酸脱氢酶缺乏症", "detail": "已发现PHGDHgene中至少有8个突变会导致磷酸甘油酸脱氢酶缺乏症。这种情况的特征是头部异常小（小头畸形），严重的发育迟缓和难以治疗的反复发作（顽固性癫痫）。导致这种情况的大多数突变都会改变磷酸甘油酸脱氢酶中的单个氨基酸。突变导致产生功能降低的酶。结果，比正常情况下更少的3-磷酸甘油酸转化为3-磷酸羟基丙酮酸，并且丝氨酸生产在第一步停止。丝氨酸的缺乏可能会阻止大脑中蛋白质和神经递质的产生，并损害正常细胞和髓磷脂的形成。正常大脑发育中的这些破坏导致小头畸形，严重的发育迟缓以及磷酸甘油酸脱氢酶缺乏症的其他体征和症状。"}]}' WHERE id = 24660;
UPDATE genes SET GHR_metadata_ch = '{"gene": "CLPP", "conditions": [{"name": "Perrault综合征", "detail": "在患有Perrault综合征的家庭中发现了CLPPgene的至少三个突变，这种疾病的特征是受影响的男性和女性听力下降，而受影响的女性卵巢异常。涉及Perrault综合征的CLPPgene突变可能会改变由CLPP复合物形成的桶形腔室的结构。这些变化可能会损害错误折叠的线粒体蛋白的分解，从而影响线粒体功能；但是，尚不清楚这些变化如何导致Perrault综合征的特征。"}]}' WHERE id = 24664;
UPDATE genes SET GHR_metadata_ch = '{"gene": "PINK1", "conditions": [{"name": "帕金森病", "detail": "研究人员已经确定了PINK1gene中的70多个突变，这些突变可导致帕金森氏病，帕金森氏病的特征是运动和平衡方面的进行性问题。PINK1gene突变与该疾病的早期发作形式有关，通常在50岁之前开始。（换行）许多PINK1gene突变改变或消除激酶结构域，导致蛋白质功能丧失。至少一种突变影响线粒体靶向基序，并可能破坏蛋白质向线粒体的递送。由于PTEN诱导的假定激酶1活性降低或缺失，线粒体可能会发生功能异常，尤其是在细胞受压时。如果没有为基本活动提供能量，细胞可能会死亡。目前尚不清楚PINK1gene突变如何导致帕金森氏病特征性神经细胞的选择性死亡。这些细胞的丧失削弱了大脑和肌肉之间的交流，最终大脑无法控制肌肉运动。"}]}' WHERE id = 24670;
UPDATE genes SET GHR_metadata_ch = '{"gene": "NOTCH2", "conditions": [{"name": "阿拉吉欧综合症", "detail": "NOTCH2gene突变似乎是Alagille综合征的相对罕见原因，Alagille综合征可影响肝脏，心脏和身体其他部位。NOTCH2gene中至少有10个突变与该病有关。这些突变可以影响Notch2受体的细胞内或细胞外结构域。遗传变化可能导致产生异常小的受体或折叠成错误的三维形状。这些突变被描述为“功能丧失”，因为有缺陷的受体无法与其配体结合并触发细胞内的信号传导。据信破坏的Notch2信号传导会影响许多器官和组织的发育，从而导致Alagille综合征的体征和症状。"}, {"name": "Hajdu-Cheney综合征", "detail": "NOTCH2gene中的一些突变与Hajdu-Cheney综合征有关，Hajdu-Cheney综合征是一种罕见的疾病，可影响身体的许多部位，尤其是骨骼。受影响的个体患有肢端骨溶解症，这是最严重影响手和脚的骨组织损失。大多数患有这种疾病的人也患有骨质疏松症，这会导致骨骼变脆并容易骨折；独特的面部特征；脊柱异常；和身材矮小。此外，Hajdu-Cheney综合征可影响关节，牙齿，心脏，肾脏和身体其他部位。（换行）与Hajdu-Cheney综合征相关的突变都发生在称为外显子34的区域的NOTCH2gene末端附近。这些突变导致缺少PEST结构域的Notch2受体异常缩短。没有这个结构域，受体不能正常分解，细胞内的Notch2信号传导应该停止后继续。由于与Hajdu-Cheney综合征相关的NOTCH2gene突变导致NOTCH2信号异常增加，因此将其描述为“功能获得性”突变。(换行)研究人员不确定Notch2信号的过量与Hajdu-Cheney综合征的各种特征有何关系。他们怀疑该疾病的骨骼特征，包括肢端骨溶解，骨质疏松和独特的面部特征，可能是由于骨骼发育和重塑异常所致。通过过度活跃的Notch2受体发出的过量信号可能会增加旧骨的去除，减少新骨的形成或两者兼而有之。尚不清楚过度活跃的受体如何导致这种情况的其他体征和症状。"}, {"name": "癌症", "detail": "在某些形式的淋巴瘤中也发现了NOTCH2gene的突变，淋巴瘤是一组由免疫系统细胞引起的癌症。这些突变是体细胞的，这意味着它们不是遗传的。体细胞突变是在一个人的一生中获得的，仅存在于某些细胞中。与淋巴瘤相关的NOTCH2gene突变被描述为“功能获得”，因为它们增加了某些免疫系统细胞中NOTCH2gene的活性（表达）。在一些受影响的细胞中，已经发现了突变gene的额外拷贝，进一步增加了gene活性。该gene的过表达可能导致免疫系统细胞中不受控制的细胞生长和细胞分裂，这可能导致淋巴瘤的发展。"}]}' WHERE id = 24697;
UPDATE genes SET GHR_metadata_ch = '{"gene": "PIK3R2", "conditions": [{"name": "巨脑-多小脑回-多指-脑积水综合征", "detail": "已发现PIK3R2gene中至少有四个突变会引起巨脑-多微gyria-多指-脑积水（MPPH）综合征。这种罕见的情况会影响大脑的发育，导致大脑和头部异常大（巨头畸形）以及大脑结构的其他异常。一些受影响的人的一只或多只手或脚上也有额外的手指或脚趾（多指）。（换行）每个已知的突变改变P85中的单个蛋白质构建块（氨基酸）？PI3K的亚基。最常见的突变是在位置373处用氨基酸精氨酸替换氨基酸甘氨酸（写为Gly373Arg或G373R）。所有突变都被描述为“功能获得”，因为它们增加了PI3K的活性。这种增强的活性增加了通过PI3K-AKT-mTOR途径的化学信号传导，从而导致过度的细胞生长和分裂。在大脑中，细胞数量的增加导致出生前开始的大脑快速异常生长。尽管额外的手指可能与发育中的手和脚中异常的细胞增殖有关，但尚不清楚这些变化如何导致多指畸形。"}, {"name": "其他疾病", "detail": "已发现PIK3R2gene的突变会导致大脑异常，称为双侧周壁多微gyria（BPP）。大脑表面通常有许多脊或褶皱，称为回旋。在患有BPP的人中，称为perisylvian区域的大脑区域发展出太多的回旋，并且褶皱异常小。BPP是与MPPH综合征相关的主要脑部异常之一（如上所述），但是在没有MPPH综合征其他体征和症状的BPP患者中也发现了PIK3R2gene的突变。（换行）像引起MPPH综合征的遗传变化一样，与BPP相关的PIK3R2gene突变是功能获得性的，最终导致发育中的大脑中细胞生长和分裂的增加。细胞数量的增加会导致出生前回旋的异常发育。Gly373Arg是MPPH综合征患者中最常见的突变，也可引起BPP。(换行)在某些BPP病例中，PIK3R2gene突变从出生开始就存在于身体的每个细胞中。在其他情况下，突变是体细胞的，这意味着它发生在胚胎发育的某个时刻。随着细胞继续生长和分裂，其中一些细胞将发生遗传变化，而另一些则不会（这种情况称为镶嵌现象）。目前尚不清楚为什么同一gene的突变，有时甚至是同一突变，会在某些人中引起BPP，而在另一些人中引起MPPH。具有突变的脑细胞的数量和位置（在镶嵌的情况下）可能有助于确定将发生哪些脑生长异常。"}]}' WHERE id = 24701;
UPDATE genes SET GHR_metadata_ch = '{"gene": "RNF213", "conditions": [{"name": "烟雾病", "detail": "RNF213gene中至少有24个遗传变化与烟雾病有关。这种情况的特征是大脑中的血管变窄，随后小而脆弱的血管网络生长。由此导致的大脑血流不足导致中风和其他病症特征。（换行）与烟雾病有关的RNF213gene的变化取代了RNF213蛋白中的单个蛋白结构单元（氨基酸）。最常见的变化是该gene的正常变异（多态性），该gene在蛋白质位置4810（写为Arg4810Lys或R4810K）处用氨基酸赖氨酸取代了氨基酸精氨酸。这种遗传变化在亚洲人群的大多数受影响个体中发现，在这些人群的未受影响个体中相对常见，发生率约为1%。（这种现象，即gene拷贝改变的人从未出现这种情况，被称为外显率降低。）但是，在欧洲血统的受影响个体中未发现Arg4810Lys的变化。这些个体具有罕见的RNF213gene突变，可改变蛋白质中的单个氨基酸。(换行)关于这些变化对RNF213蛋白功能的影响知之甚少，研究人员不确定这些变化如何导致烟雾病的特征性血管异常。目前尚不清楚RNF213gene突变是否与大脑血管狭窄有关。由于未知原因，患有烟雾病的人的细胞和组织生长中涉及的蛋白质水平升高，包括血管生长（血管生成）。这些蛋白质的过量可以解释烟雾病特有的新血管的生长。尚不清楚RNF213gene的变化是否与这些蛋白质的过量生产有关。(换行)对烟雾病的病因知之甚少。研究表明，其他gene可能与疾病的发展有关。有人提出，其他因素，如感染或炎症，结合遗传变化可能是烟雾病发展所必需的。"}]}' WHERE id = 24720;
UPDATE genes SET GHR_metadata_ch = '{"gene": "MMP20", "conditions": [{"name": "釉质发育不全", "detail": "在患有常染色体隐性形式的牙齿发育障碍（称为牙釉质发育不全）的人中，已经鉴定出MMP20gene中至少有七个突变。常染色体隐性遗传意味着每个细胞中MMP20gene的两个拷贝都被改变了。这种情况下涉及的MMP20gene突变阻止细胞产生功能性溶血素。没有这种蛋白质的功能，釉原蛋白和其他蛋白质在釉质形成过程中不会被切割。因为这些蛋白质保留在牙釉质中，所以在形成过程中不会硬化。所得牙釉质柔软，晶体结构异常。牙釉质有缺陷的牙齿异常粗糙，变色且容易断裂。"}]}' WHERE id = 24734;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ASPA", "conditions": [{"name": "卡纳万病", "detail": "已知ASPAgene中的几种突变会引起Canavan病，这是一种罕见的遗传性疾病，会影响大脑发育。这种情况是一组白细胞营养不良症之一，有两种主要形式：最常见和最严重的形式，称为新生儿/婴儿Canavan病？一种具有受影响个体的形式，其不发展运动技能并且特征在于肌张力低下，大头畸形，易怒，进食和吞咽困难，癫痫发作和睡眠障碍，其由于酶活性的严重损害而影响NAA分解并导致其积累，干扰髓鞘形成。"}]}' WHERE id = 24741;
UPDATE genes SET GHR_metadata_ch = '{"gene": "POLR3B", "conditions": [{"name": "Pol III相关性白细胞营养不良", "detail": "至少有54个POLR3Bgene突变与Pol III相关的白细胞营养不良有关。脑白质营养不良是涉及神经系统白质异常的病症。白质由被称为髓磷脂的脂肪物质覆盖的神经纤维组成，其隔离神经纤维并促进神经冲动的快速传递。形成髓磷脂的能力降低（髓鞘减少）会导致Pol III相关性白细胞营养不良的体征和症状，包括智力残疾和协调运动困难（共济失调）。在这种疾病中，牙齿（牙列）的发育也异常。（换行）在Pol III相关的脑白质营养不良中，POLR3Bgene突变可能损害RNA聚合酶III酶的亚基正确组装的能力或导致RNA聚合酶III与DNA结合的能力受损。RNA聚合酶III分子功能降低可能会影响身体许多部位的发育和功能，但POLR3Bgene突变与这些疾病的具体体征和症状之间的关系尚不清楚。(换行)Pol III相关性脑白质营养不良患者可能有不同的体征和症状组合。这些不同的临床特征组合最初被描述为单独的疾病。受影响的个体可能被诊断为共济失调，牙列延迟和髓鞘减少（ADDH）；髓鞘减少，牙髓不足，性腺功能低下性腺功能减退（4H综合征）；震颤共济失调伴中枢性髓鞘减少（TACH）；少尿症的白细胞营养不良（LO）；或伴有小脑萎缩和call体发育不全（HCAHC）的髓鞘减少。由于后来发现这些疾病具有相同的遗传原因，研究人员现在将它们归类为单一病症Pol III相关脑白质营养不良的变异。"}]}' WHERE id = 24758;
UPDATE genes SET GHR_metadata_ch = '{"gene": "FOXG1", "conditions": [{"name": "FOXG1综合征", "detail": "涉及FOXG1gene的变化会导致FOXG1综合征，这是一种罕见的疾病，其特征是发育受损和大脑结构异常。这种情况以前被描述为瑞特综合征的先天性变异，这是一种类似的早期发育障碍。但是，医生和研究人员已经确定了这两种情况之间的一些重要差异，因此现在通常认为FOXG1综合征与Rett综合征不同。（换行）在患有FOXG1综合征的人中，已经鉴定出FOXG1gene内至少11个突变。这种情况也可能是由于14号染色体长臂（q）区域的遗传物质缺失所致，该区域包括FOXG1gene和几个相邻gene。所有这些遗传变化都会阻止叉头盒G1的产生或损害蛋白质的功能。这种蛋白质的缺乏会破坏出生前开始的正常大脑发育，这似乎是FOXG1综合征特有的大脑畸形和严重发育问题的基础。"}, {"name": "其他疾病", "detail": "已经发现少数人具有包含FOXG1gene的14号染色体部分的额外副本（重复）。这些重复与从婴儿期开始的反复发作（癫痫），智力障碍和严重的言语障碍有关。在几名被诊断患有West综合征的婴儿中也发现了FOXG1gene的重复，West综合征的特征是婴儿期开始的癫痫病，严重至严重的智力障碍以及相关的脑部异常。尽管机制尚不清楚，但研究人员认为，额外的遗传物质会通过改变早期大脑发育而导致这些发育问题。"}]}' WHERE id = 24766;
UPDATE genes SET GHR_metadata_ch = '{"gene": "TUBB4A", "conditions": [{"name": "TUBB4A相关性白细胞营养不良", "detail": "TUBB4Agene中的30多个突变导致TUBB4A相关的白细胞营养不良。这种疾病的特征是白质异常，特别是神经系统产生髓磷脂的能力降低（髓鞘减少）。TUBB4A相关的白细胞营养不良的严重程度范围很广，在频谱的最严重端称为基底节和小脑萎缩（H-ABC）的髓鞘减少，在最轻端称为孤立的髓鞘减少；一些受影响个体的特征介于这两个极端之间。除了某些大脑区域（包括帮助控制运动的区域）的髓鞘减少外，受影响的个体还可能在这些区域出现脑组织破裂（萎缩），运动异常，吞咽和说话困难以及学习问题。（换行）导致TUBB4A相关性脑白质营养不良的突变改变了单蛋白结构单元（氨基酸）-微管蛋白。H-ABC最常见的原因是突变，该突变在蛋白质249位（写为Asp249Asn或D249N）上用氨基酸天冬酰胺取代了氨基酸天冬氨酸。这个和其他TUBB4Agene突变被认为会改变-微管蛋白，可能会损害微管的形成或稳定性。虽然尚不清楚这些遗传变化如何导致TUBB4A相关性脑白质营养不良的体征和症状，但研究人员怀疑微管问题会损害神经元迁移或神经元内重要物质的转运，这可能导致这些细胞功能障碍和丧失。大脑，特别是壳核，小脑和白质。这些大脑区域的异常是TUBB4A相关性白细胞营养不良中可能发生的运动，言语和学习问题的基础。目前尚不清楚是什么导致这种疾病的严重程度范围广泛。"}, {"name": "其他疾病", "detail": "已发现至少两个TUBB4Agene突变会引起另一种称为肌张力障碍4（也称为DYT4肌张力障碍或耳语性发声障碍）的神经系统疾病，其特征是通常在成年期开始出现微弱的耳语（发声障碍）。受影响的成年人后来会出现不自主的肌肉收缩（肌张力障碍），行走困难，有时还会吞咽困难（吞咽困难）。肌张力障碍4的个体没有髓鞘过少或其他脑部异常，例如与TUBB4A相关的白细胞营养不良的人（如上所述）。（换行）导致肌张力障碍4的最常见的TUBB4Agene突变用蛋白质2位的氨基酸甘氨酸取代氨基酸精氨酸（写为Arg2Gly或R2G）。导致这种情况的突变被认为破坏了-微管蛋白，因此，微管的形成或稳定性。但是，尚不清楚这些遗传变化如何导致肌张力障碍4的体征和症状，或者为什么它们不像其他TUBB4Agene突变那样引起髓鞘过少。"}]}' WHERE id = 24783;
UPDATE genes SET GHR_metadata_ch = '{"gene": "PDHB", "conditions": [{"name": "缺乏症", "detail": "PDHBgene中的突变是丙酮酸脱氢酶缺乏症的非常罕见的原因。这种情况的特征是体内一种称为乳酸的化学物质（乳酸性酸中毒），发育迟缓和神经系统问题可能危及生命。这些突变改变了E1 beta蛋白中的单个蛋白结构单元（氨基酸），导致无法正常运行的异常E1 beta蛋白。异常蛋白质可能无法与E1α相互作用形成E1酶，也无法与E1酶进行化学反应所需的其他因素相互作用。功能性E1β的降低导致丙酮酸脱氢酶复合物活性的降低。随着这种复合物功能的降低，丙酮酸积累并在另一种化学反应中转化为乳酸，引起乳酸性酸中毒。另外，细胞能量的产生减少了。特别依赖于这种能量形式的大脑受到严重影响，导致与丙酮酸脱氢酶缺乏症相关的神经系统问题。"}, {"name": "利综合征", "detail": "遗传学家庭参考提供有关Leigh综合征的信息。"}]}' WHERE id = 24797;
UPDATE genes SET GHR_metadata_ch = '{"gene": "DYNC2H1", "conditions": [{"name": "窒息性胸营养不良", "detail": "窒息性胸肌营养不良症患者中已鉴定出DYNC2H1gene的50多个突变，这是一种遗传性骨生长疾病，其特征是胸部小，肋骨短，手臂和腿部骨骼缩短。该gene的突变占该病所有病例的一半。大多数已知的突变会改变DYNC2H1蛋白中的单个蛋白结构单元（氨基酸）。用改变的蛋白质制成的dynein-2复合物无法正常工作，这会破坏从纤毛尖端到基部的IFT，并导致尖端积聚物质。研究人员推测，IFT的这些变化会改变某些信号通路，包括声波刺猬通路，这可能是窒息性胸肌营养不良的骨骼生长特征异常的基础。(换行)在一些受影响的个体中，窒息性胸营养不良也与肾脏，肝脏，视网膜和其他组织的异常有关。但是，当该疾病是由DYNC2H1gene突变引起的时，其特征通常仅限于骨骼生长问题。造成这种差异的原因尚不清楚。"}, {"name": "其他疾病", "detail": "DYNC2H1gene的突变也被发现引起另外两种骨生长障碍：II型短肋多指综合征（SRPS II型），也称为Majewski综合征，III型短肋多指综合征（SRPS III型），也称为Verma-Naumoff综合征或Saldino-Noonan综合征。这些疾病的体征和症状与窒息性胸肌营养不良相似，包括胸部狭窄和肋骨短。但是，SRPS II型和III型往往比窒息性胸肌营养不良更为严重，受影响的个体通常在出生前或出生后不久死亡。（换行）在SRPS II型患者中发现约10个DYNC2H1gene突变，并且在SRPS III型患者中发现至少4个突变。与导致窒息性胸营养不良的突变一样，这些遗传变化损害功能的dynein-2复合物和破坏纤毛内的IFT。尽管机制似乎相似，但尚不清楚为什么某些DYNC2H1gene突变的影响比其他突变更严重。引起SRPS II型和III型的突变可能比引起窒息性胸肌营养不良的突变更严重地影响蛋白质功能。"}]}' WHERE id = 24816;
UPDATE genes SET GHR_metadata_ch = '{"gene": "C3", "conditions": [{"name": "C3肾小球病", "detail": "已经发现C3gene中的突变引起称为C3肾小球病的罕见形式的肾病，这是导致肾损伤和终末期肾病（ESRD）的病症。功能获得性突变过度激活补体系统并防止肾脏有效过滤体液和体内废物。临床特征包括蛋白尿，血尿，尿量减少，血液中C3蛋白水平低以及身体许多部位肿胀。"}, {"name": "非典型溶血性尿毒症综合征", "detail": "已发现C3gene的突变会引起称为非典型溶血性尿毒症综合征的肾脏疾病，这种疾病的特征是凝血异常，溶血性贫血，血小板减少和肾衰竭。大多数具有表现出异常凝块的综合征的个体经历肾损伤和导致终末期肾病（ESRD）的急性肾衰竭。"}, {"name": "年龄相关性黄斑变性", "detail": "C3gene的变异与年龄相关性黄斑变性有关，黄斑变性是一种影响中央视力所必需的黄斑的疾病。一种称为干燥形式的疾病的常见形式的特征在于视网膜下称为玻璃疣的黄色沉积物，随着时间的推移导致视力缓慢丧失。湿形式迅速影响视力丧失，其特征在于黄斑下异常脆弱的血管的生长。"}, {"name": "其他疾病", "detail": "已发现C3gene的变异会导致C3缺乏症，这是一种罕见病，其特征是从儿童期开始就反复发生细菌感染。“功能丧失”C3突变会导致蛋白质异常或缺失，从而抑制补体系统对外来入侵者的正常激活。"}]}' WHERE id = 24818;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ELN", "conditions": [{"name": "皮肤松弛", "detail": "在患有皮肤病的人中，至少有16种ELNgene突变被称为角质层松弛。ELNgene突变会导致一种称为常染色体显性角质松弛的疾病，其特征是皮肤松弛，下垂；在称为主动脉的大血管中异常鼓胀（动脉瘤）的风险增加；还有一种称为肺气肿的肺部疾病，会使呼吸困难。（换行）导致常染色体显性角质松弛的ELN突变导致异常长版本的原弹性蛋白的产生。异常蛋白质可能会干扰成熟弹性蛋白的形成和弹性纤维的组装，从而削弱皮肤和血管中的结缔组织。结缔组织中的这种缺陷是角质层松弛的主要特征。"}, {"name": "瓣上主动脉瓣狭窄", "detail": "已经发现ELNgene中超过60个突变导致瓣上主动脉瓣狭窄（SVAS），这是一种从出生开始就存在的心脏缺陷，其特征在于将血液从心脏输送到身体其他部位的大血管变窄（主动脉）。导致SVAS的大多数ELNgene突变都会导致原弹性蛋白的产生减少。原弹性蛋白的缺乏减少了加工并可用于形成弹性纤维的成熟弹性蛋白的量。结果，构成主动脉的弹性纤维比正常纤维薄。为了补偿，沿主动脉排列的平滑肌细胞数量增加，使主动脉比平时更厚更窄。增厚的主动脉不太柔韧并且对恒定血流和心脏泵送的应力具有弹性。随着时间的推移，主动脉壁可能会受损。主动脉狭窄导致心脏更努力地工作以将血液泵送通过主动脉，这可导致呼吸短促，胸痛并最终导致心力衰竭。"}, {"name": "7q11.23复制综合征", "detail": "ELNgene位于7q11.23复制综合征患者中复制的7号染色体区域。这种重复的结果是，患有7q11.23复制综合征的人在每个细胞中都有额外的ELNgene拷贝和其他几个gene。7q11.23复制综合征可引起多种神经和行为问题以及其他异常。(换行)约有一半患有7q11.23复制综合征的人主动脉增大（扩张）；随着时间的推移，这种扩大可能会变得更糟。如果主动脉壁分层（主动脉夹层）或破裂（破裂），主动脉扩张可导致危及生命的并发症。每个细胞中额外的ELNgene拷贝可能导致产生的原弹性蛋白量超过正常水平，研究人员建议，这种过量可能与7q11.23复制综合征中主动脉扩张的风险增加有关；然而，主动脉扩张的具体原因仍不清楚。"}, {"name": "威廉姆斯综合症", "detail": "The ELN gene is located in a region of chromosome 7 that is deleted in people with Williams syndrome. As a result of this deletion, people with Williams syndrome are missing one copy of the ELN gene in each cell. This loss reduces the production of elastin by half, which disrupts the normal structure of elastic fibers in many connective tissues. Large blood vessels with abnormal elastic fibers are often thicker and less resilient than normal. These vessels can narrow, increasing the resistance to normal blood flow and leading to serious medical problems. (line-break) In people with Williams syndrome, a loss of the ELN gene is associated with connective tissue abnormalities, such a joint problems and loose skin, and cardiovascular disease, particularly SVAS.\r"}]}' WHERE id = 24830;
UPDATE genes SET GHR_metadata_ch = '{"gene": "KMT2D", "conditions": [{"name": "歌舞伎症候群", "detail": "在歌舞uki综合症患者中已鉴定出KMT2Dgene的数百种突变，该疾病的特征是独特的面部特征，智力残疾和影响身体其他部位的异常。（换行）与歌舞伎综合征相关的KMT2Dgene突变改变赖氨酸特异性甲基转移酶2D酶中的一个构建块（氨基酸），删除KMT2Dgene序列中的遗传物质，或导致导致异常短的酶。这些突变的结果是该酶无功能。缺乏功能性赖氨酸特异性甲基转移酶2D酶会破坏其在组蛋白甲基化中的作用，并损害人体许多器官和组织中某些gene的正确激活，从而导致歌舞uki综合症的发育和功能异常。(换行)尽管赖氨酸特异性甲基转移酶2D被认为是一种肿瘤抑制因子，但这种酶功能的丧失似乎并未增加歌舞uki综合症患者的癌症风险。"}, {"name": "癌症", "detail": "一些gene突变发生在一个人的一生中。这种称为体细胞突变的突变仅存在于某些细胞中。已经在某些癌症中鉴定出KMT2Dgene的体细胞突变。这些包括髓母细胞瘤（儿童时期发生的癌性脑肿瘤）和称为淋巴瘤的血液相关癌症。这些突变中的大多数会导致异常短的，无功能的赖氨酸特异性甲基转移酶2D酶，该酶无法发挥其作为肿瘤抑制因子的作用，从而导致癌症的发展。（换行）已经在乳腺癌和结肠癌的肿瘤中鉴定了赖氨酸特异性甲基转移酶2D的量增加和酶在细胞内分布的改变。尚不清楚这些变化是否主要是由于KMT2Dgene活性增加（过表达），肿瘤细胞中gene的额外拷贝，酶的稳定性或加工改变或其他机制引起的。过量的赖氨酸特异性甲基转移酶2D可能会破坏其他gene的调控。结果，细胞可能生长和分裂过快或以不受控制的方式分裂，导致癌症。"}]}' WHERE id = 24845;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ALPL", "conditions": [{"name": "低磷酸酯脢症", "detail": "在低磷血症患者中已鉴定出ALPLgene的几种突变，这是由无功能或异常的碱性磷酸酶引起的疾病，以及无机焦磷酸盐的积累导致骨骼和牙齿矿化不良。"}]}' WHERE id = 24856;
UPDATE genes SET GHR_metadata_ch = '{"gene": "MEOX1", "conditions": [{"name": "Klippel-Feil综合征", "detail": "已经发现MEOX1gene中至少三个突变引起Klippel-Feil综合征。这种情况的特征是颈部（颈椎）中两个或多个脊柱的异常连接（融合）以及影响身体许多部位的多种其他特征。MEOX1gene突变导致缺乏功能性同源盒蛋白MOX-1。尽管尚不清楚该蛋白丢失对椎骨发育的影响，但缺少该蛋白可能会导致不规则的体节分割和不正确的椎骨定位。结果，颈椎在发育过程中不分离，而是融合在一起。目前尚不清楚为什么这种情况比其他骨骼更严重地影响颈椎。"}]}' WHERE id = 24871;
UPDATE genes SET GHR_metadata_ch = '{"gene": "CLN3", "conditions": [{"name": "CLN3疾病", "detail": "已发现CLN3gene突变会引起CLN3（类固醇脂褐藻病，神经元，3）疾病，这是一种遗传性疾病，主要影响神经系统，其特征是视力障碍，智力残疾，运动问题，言语障碍和癫痫发作恶化。大多数CLN3是缺失突变，其损害了battenin的功能，导致溶酶体中蛋白质和其他物质的积累，导致神经细胞损伤和细胞死亡。"}]}' WHERE id = 24883;
UPDATE genes SET GHR_metadata_ch = '{"gene": "CTNS", "conditions": [{"name": "胱氨酸", "detail": "在CTNSgene中已鉴定出80多种引起胱氨酸病的不同突变。最常见的突变是大部分CTNSgene的缺失（有时称为57 kb缺失），导致胱氨酸酶完全丧失。在欧洲人后裔中，这种缺失约占胱氨酸病病例的50%。其他突变导致产生不能执行其正常转运功能的异常短的蛋白质。改变CTNSgene很小区域的突变可能使转运蛋白保留其某些常规活性，从而导致较轻的胱氨酸病形式。"}]}' WHERE id = 24905;
UPDATE genes SET GHR_metadata_ch = '{"gene": "DHH", "conditions": [{"name": "Swyer综合征", "detail": "DHHgene突变已在少数Swyer综合征患者中发现，Swyer综合征是一种影响性发育的疾病，也称为46，XY完全性腺发育不全或46，XY纯性腺发育不全。受影响的个体在每个细胞中具有DHHgene的两个突变拷贝。(换行)人们通常在每个细胞中有46条染色体。46条染色体中的两条，称为X和Y，被称为性染色体，因为它们有助于确定一个人是否会发展出男性或女性的性别特征。女孩和女人通常有两条X染色体（46，XX核型），男孩和男人通常有一条X染色体和一条Y染色体（46，XY核型）。Swyer综合征患者DHHgene突变影响性别分化过程，阻止46，XY核型受影响个体发育为男性性腺（睾丸），并导致其发育为女性生殖结构（子宫和输卵管）。"}, {"name": "其他疾病", "detail": "DHHgene突变已在患有46，XY性发育障碍（也称为部分性腺发育不全）的人中发现。这些个体在每个细胞中都有一个突变的DHHgene。他们可能有外生殖器，看起来不清楚男性或女性（不明确的生殖器）或生殖器和生殖器官的其他变化。(换行)除了性腺发育不全外，一些具有DHH突变的人也有神经异常。这些异常影响将大脑和脊髓连接到肌肉和感觉细胞的神经，所述感觉细胞检测诸如触摸，疼痛，热和声音（周围神经系统）的感觉。受影响的个体可能会出现四肢无力和感觉丧失（周围神经病变）。"}]}' WHERE id = 24908;
UPDATE genes SET GHR_metadata_ch = '{"gene": "MMADHC", "conditions": [{"name": "同型半胱氨酸尿症", "detail": "MMADHCgene中至少有七个突变会导致一种称为高半胱氨酸尿症的疾病，其特征是骨骼问题和智力残疾。导致高半胱氨酸尿症的MMADHCgene突变导致蛋白质不能将维生素B12转运到产生MeCbl的细胞质中。由此导致的MeCb1短缺损害甲硫氨酸合酶将高半胱氨酸转化为甲硫氨酸。结果，同型半胱氨酸在血液中积聚并且甲硫氨酸被耗尽。一些过量的同型半胱氨酸会从尿液中排出。研究人员尚未确定同型半胱氨酸和蛋氨酸水平的改变如何导致与同型半胱氨酸尿症相关的健康问题。"}, {"name": "甲基丙二酸血症", "detail": "已发现MMADHCgene中至少有3个突变会引起甲基丙二酸血症，这种疾病的特征是进食困难，发育迟缓和长期健康问题。导致这种情况的MMADHCgene突变导致蛋白质无法将维生素B12转运至线粒体以产生AdoCbl。AdoCbl的缺乏会损害甲基丙二酰辅酶A突变酶的功能。结果，某些蛋白质和脂质不能正确分解。这种缺陷使有毒化合物积聚在人体的器官和组织中，引起甲基丙二酸血症的体征和症状。"}, {"name": "甲基丙二酸血症伴高半胱氨酸尿症", "detail": "至少三个MMADHCgene突变可引起甲基丙二酸血症，并伴有高半胱氨酸尿症cblD型，这是一种具有上述两种疾病特征的疾病。患有这种综合疾病的人有发育迟缓，眼睛缺陷，神经系统问题和血液异常。导致这种情况的MMADHCgene突变导致无法将维生素B12转运至线粒体或细胞质的蛋白质的产生，从而破坏AdoCbl和MeCbl的产生。由于这两个辅因子均缺失，因此需要它们的酶（甲基丙二酰辅酶A突变酶和蛋氨酸合酶）无法正常运行。结果，某些氨基酸，脂质和胆固醇不被分解，同型半胱氨酸不能转化为甲硫氨酸。这种双重缺陷导致有毒化合物以及高半胱氨酸的积累，并减少体内蛋氨酸的产生。这种失衡的组合导致甲基丙二酸血症伴高半胱氨酸尿症的体征和症状。"}]}' WHERE id = 24922;
UPDATE genes SET GHR_metadata_ch = '{"gene": "HFE2", "conditions": [{"name": "遗传性血色素沉着症", "detail": "研究人员已经鉴定出20多种导致2型血色素沉着病的HFE2gene突变，这是一种从儿童期或青春期开始的遗传性血色素沉着病。大多数HFE2gene突变会改变用于制造血juvelin的蛋白质结构单元（氨基酸）之一。最常见的是，氨基酸甘氨酸被蛋白质320位的氨基酸缬氨酸取代（写为Gly320Val）。其他突变会在制备血juvelin蛋白的说明中产生过早的终止信号。结果，产生异常小的蛋白质。HFE2gene中的（换行）突变导致无法正常运行的血juvelin蛋白改变。没有足够的血juvelin，蛋白质hepcidin的水平会降低，铁平衡会受到干扰。结果，消化过程中吸收的铁过多，导致铁超载并损害体内组织和器官。"}]}' WHERE id = 24929;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SOST", "conditions": [{"name": "SOST相关的硬化性骨发育不良", "detail": "已发现SOSTgene内或附近的至少六个突变会导致SOST相关的硬化性骨发育不良。有两种形式的SOST相关的硬化性骨发育不良：硬化症和van Buchem病。硬化症是较严重的类型，在南非的南非荷兰人中最常见，而较轻的范布赫姆病最常见于荷兰血统。（换行）导致硬化症的大多数突变导致制造硬化蛋白的说明中的过早停止信号。这些突变阻止了任何功能蛋白的产生。（换行）在荷兰血统的人中引起范布赫姆病的最常见突变是在与SOSTgene相邻的DNA区域中缺失52000个DNA构建块（核苷酸）。该区域称为调节区域，通常控制gene的活性（表达）。调节区域内的这种缺失降低了SOSTgene的表达，导致功能性硬化蛋白的缺乏。（换行）骨细胞中硬化蛋白的缺乏或缺乏破坏了蛋白质对骨生长的抑制作用，导致过度的骨形成。结果，骨头比平时更密集和更宽，特别是头骨的骨头。这些骨异常是SOST相关硬化性骨发育不良的特征。"}]}' WHERE id = 24939;
UPDATE genes SET GHR_metadata_ch = '{"gene": "RBM8A", "conditions": [{"name": "血小板减少症-无radius骨综合征", "detail": "RBM8Agene的突变会导致血小板减少症-无radius骨（TAR）综合征。这种疾病的特征是在每个前臂中没有称为radius骨的骨骼，并且凝血（血小板）中涉及的血细胞碎片短缺（缺乏）。(换行)大多数患有TAR综合征的人在一个RBM8Agene拷贝中发生突变，并从1号染色体上删除了遗传物质，其中包括每个细胞中另一个RBM8Agene拷贝。少数受影响的个体在每个细胞的RBM8Agene的两个拷贝中都有突变，并且在1号染色体上没有缺失。导致TAR综合征的RBM8Agene突变减少了细胞中RNA结合基序蛋白8A的数量。与TAR综合征有关的缺失消除了1号染色体长臂（q）中称为1q21.1的区域中至少200000个DNA构建块（200 kb或200 kb）。该缺失消除了每个细胞中RBM8Agene的一个拷贝和可能从中产生的RNA结合基序蛋白8A。(换行)具有RBM8Agene突变和1号染色体缺失或具有两个gene突变的人具有减少量的RNA结合基序蛋白8A。人们认为这种减少会引起某些组织发育的问题，但尚不清楚它如何引起TAR综合征的特定体征和症状。没有报道在1号染色体的两个拷贝上都发生包含RBM8Agene的缺失的情况；研究表明，RNA结合基序蛋白8A的完全丧失与生命不相容。"}]}' WHERE id = 24946;
UPDATE genes SET GHR_metadata_ch = '{"gene": "AMT", "conditions": [{"name": "甘氨酸脑病", "detail": "AMTgene的突变与甘氨酸脑病有关，甘氨酸脑病是一种以体内甘氨酸过多为特征的疾病，其原因是酶缺陷导致智力残疾，癫痫发作和呼吸困难。"}]}' WHERE id = 24969;
UPDATE genes SET GHR_metadata_ch = '{"gene": "COG5", "conditions": [{"name": "COG5先天性糖基化疾病", "detail": "已知COG5gene的突变会导致COG5先天性糖基化疾病（COG5-CDG），这种疾病会导致发育迟缓，智力障碍和其他异常。突变减少或消除了COG5蛋白，导致高尔基体中逆行转运的破坏和糖基化异常。"}]}' WHERE id = 24970;
UPDATE genes SET GHR_metadata_ch = '{"gene": "INSR", "conditions": [{"name": "Donohue综合征", "detail": "研究人员已经鉴定出150多种INSRgene突变，这些突变会导致一系列相关疾病，称为严重胰岛素抵抗综合征。这些包括Donohue综合征和其他两种疾病，Rabson-Mendenhall综合征和A型胰岛素抵抗综合征（如下所述）。胰岛素抵抗是身体组织和器官对胰岛素没有适当反应的一种情况。严重的胰岛素抵抗综合征的特征在于调节血糖水平以及全身器官和组织的发育和功能受损。Donohue综合征是这三种综合征中最严重的一种；受影响的儿童无法存活到2岁以上。（换行）与Donohue综合征相关的INSRgene突变发生在每个细胞中该gene的两个拷贝中。这些突变中的一些干扰了最初由INSRgene产生的蛋白质的正常加工，从而阻止了功能性胰岛素受体的形成。其他突变会损害受体与胰岛素结合的能力或破坏胰岛素结合通常触发的细胞信号传导途径。所有这些突变大大降低或消除了胰岛素受体的功能。尽管胰岛素存在于血流中，但没有功能性受体，它不能对细胞和组织发挥作用。这种对胰岛素作用的严重抵抗会损害血糖调节并影响发育的许多方面。"}, {"name": "Rabson-Mendenhall综合征", "detail": "INSRgene突变还引起Rabson-Mendenhall综合征，这是一种严重的胰岛素抵抗综合征，其特征在严重程度介于Donohue综合征（如上所述）和a型胰岛素抵抗综合征（如下所述）之间。受影响的个体具有与Donohue综合征相似的特征，但通常可以存活到十几岁或二十多岁。胰岛素抵抗最终导致称为糖尿病的病症，其中血糖水平可能变得危险地高。（换行）导致Rabson-Mendenhall综合征的INSRgene突变发生在每个细胞的gene的两个拷贝中。这些突变减少了到达细胞膜或破坏这些受体功能的胰岛素受体的数量，但通常不如与Donohue综合征相关的突变严重。尽管胰岛素存在于血流中，但没有足够的功能性受体，它就不能对细胞和组织发挥作用。这种对胰岛素作用的严重抵抗会损害血糖调节并影响发育的许多方面。"}, {"name": "A型胰岛素抵抗综合征", "detail": "INSRgene突变也是最轻度的严重胰岛素抵抗综合征A型胰岛素抵抗综合征的基础。患有这种疾病的女性会出现月经周期异常，体毛过度生长（多毛症），卵巢囊肿，称为黑棘皮病的皮肤病和糖尿病。受影响的男性往往有较少的体征和症状，尽管他们也患有糖尿病。A型胰岛素抵抗综合征的特征通常直到青春期或更晚才变得明显，并且通常不会危及生命。（换行）导致A型胰岛素抵抗综合征的大多数INSRgene突变发生在每个细胞中该gene的两个拷贝之一中。这些突变导致产生不能正确传递信号的有缺陷的胰岛素受体。这些变化被描述为“显性负性”突变，因为由gene的突变拷贝产生的错误受体会干扰由gene的非突变拷贝产生的正常受体。较少见的是，A型胰岛素抵抗综合征是由每个细胞中INSRgene的两个拷贝中的突变引起的。这些突变削弱了胰岛素受体的功能。(换行)尽管胰岛素存在于血流中，但有缺陷的受体使其对细胞和组织发挥作用的能力降低。这种对胰岛素作用的严重抵抗会损害血糖调节并导致糖尿病。在患有A型胰岛素抵抗综合征的女性中，血液中过量的胰岛素在青春期与激素因子相互作用，导致月经异常，卵巢囊肿和该疾病的其他特征。"}]}' WHERE id = 24976;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SERPINA6", "conditions": [{"name": "皮质类固醇结合球蛋白缺乏症", "detail": "已发现SERPINA6gene中的10多个突变会导致皮质类固醇结合球蛋白缺乏。这种情况会导致极度疲倦（疲劳），低血压（低血压）或慢性疼痛。但是，有些人没有任何症状。（换行）导致皮质类固醇结合球蛋白缺乏的突变通常会降低CBG蛋白与皮质醇结合的能力；一些严重的突变会阻止任何CBG蛋白的产生。由于功能性CBG与皮质醇结合较少，皮质类固醇结合球蛋白缺乏的人通常具有增加的未结合皮质醇水平。通常，身体会减少皮质醇的产生以补偿，从而导致总皮质醇的减少。(换行)尚不清楚CBG蛋白和总皮质醇的减少如何导致皮质类固醇结合球蛋白缺乏的体征和症状。由于需要CBG蛋白在某些时候将皮质醇转运到特定组织，可能是因为皮质醇在体内可用，但皮质醇没有到达需要它的组织。皮质醇的减少可能影响血管，导致血压异常。一些研究人员认为这些特征可能会相互影响，疲劳可能是慢性疼痛的结果，而不是疾病本身的症状。可能还有其他遗传或环境因素会影响受影响的个体是否更容易出现疼痛或疲劳。"}, {"name": "其他疾病", "detail": "SERPINA6gene的正常变异（多态性）与患慢性疲劳综合征的风险增加有关。这种情况涉及持续数月的极度疲劳（疲劳）发作，并干扰日常活动以及一般症状，如喉咙痛或头痛。与慢性疲劳综合征相关的SERPINA6gene多态性用CBG蛋白中224位的氨基酸丝氨酸（写为Ala224Ser或A224S）取代蛋白质结构单元（氨基酸）丙氨酸。这种变化可能会增加可用的CBG蛋白量。尚不清楚增加CBG蛋白水平如何导致发展为慢性疲劳综合征的风险，但是遗传和环境的许多其他因素都可能导致这种复杂疾病的发展。"}]}' WHERE id = 24989;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SERPINA1", "conditions": [{"name": "Alpha-1抗胰蛋白酶缺乏症", "detail": "SERPINA1gene中已鉴定出120多个突变。这些突变中的一些不影响α-1抗胰蛋白酶的产生，而另一些则导致蛋白质短缺（缺乏）。如果没有足够的功能性α-1抗胰蛋白酶，嗜中性粒细胞弹性蛋白酶会破坏肺部（肺泡）的小气囊并引起肺部疾病。肺泡过度损伤会导致肺气肿，这是一种不可逆转的肺部疾病，会导致呼吸急促。（换行）许多SERPINA1gene突变会改变α-1抗胰蛋白酶中的单个蛋白质结构单元（氨基酸），从而改变蛋白质的结构。导致α-1抗胰蛋白酶缺乏的最常见突变是在蛋白质位置342（写为Glu342Lys或E342K）上用氨基酸赖氨酸取代了氨基酸谷氨酸。该突变导致称为Z等位gene的SERPINA1gene版本产生非常少的α-1抗胰蛋白酶。（换行）异常的α-1抗胰蛋白酶蛋白可以结合在一起形成不能离开肝脏的大分子或聚合物。这些聚合物的积累导致肝损伤。此外，肺组织被破坏，因为没有足够的α-1抗胰蛋白酶可用于预防中性粒细胞弹性蛋白酶。α-1抗胰蛋白酶的聚合物也可能导致过度炎症，这可以解释α-1抗胰蛋白酶缺乏的一些其他特征，例如称为脂膜炎的皮肤病。（换行）其他SERPINA1gene突变导致产生异常小形式的α-1抗胰蛋白酶，其在肝脏中迅速分解。结果，肺中几乎没有或没有α-1抗胰蛋白酶。尽管具有这些突变的个体的肝脏保持健康，但肺不受中性粒细胞弹性蛋白酶的保护。"}]}' WHERE id = 25003;
UPDATE genes SET GHR_metadata_ch = '{"gene": "LIMK1", "conditions": [{"name": "威廉姆斯综合症", "detail": "LIMK1gene位于Williams综合征患者中缺失的7号染色体区域。由于这种缺失，患有这种情况的人在每个细胞中缺少一个LIMK1gene拷贝。一些研究表明，该gene的缺失会导致威廉姆斯综合征中视觉空间任务（例如书写和绘图）的特征性问题；但是，其他研究尚未发现这种联系。尽管该gene的缺失可能会影响大脑中神经细胞的发育和功能，但研究人员尚未确定LIMK1蛋白量的减少如何与威廉姆斯综合征中所见的特定损伤相关。"}, {"name": "癌症", "detail": "LIMK1蛋白在一些癌性肿瘤中以异常高的水平（过表达）产生。例如，已经在称为黑素瘤的皮肤癌形式和卵巢癌，肺癌，乳腺癌和前列腺癌中发现了增加量的这种蛋白质。研究人员认为，高水平的LIMK1蛋白可能与肌动蛋白丝组织的变化以及肿瘤侵入其他组织的机会增加有关。"}]}' WHERE id = 25013;
UPDATE genes SET GHR_metadata_ch = '{"gene": "MCOLN1", "conditions": [{"name": "IV型粘脂病", "detail": "已发现MCOLN1gene中至少有22个突变引起IV型粘膜脂溢性疾病。这些突变大多数导致无功能蛋白的产生或阻止任何蛋白的产生。MCOLN1gene的两个突变几乎导致了Ashkenazi犹太血统的所有IV型粘膜脂溢性疾病。最常见的突变写为406-2A>G，可改变gene中称为内含子3的区域中的单个DNA构建块（核苷酸）。该突变称为剪接位点突变，在制备粘蛋白-1的说明中引入了过早的终止信号。另一个突变写为511 6943del，在MCOLN1gene开始附近删除了大量的DNA。这两种突变均导致异常短的无功能蛋白的产生。(换行)缺乏功能性粘蛋白-1会损害脂质和蛋白质的运输，导致这些物质在溶酶体内积聚。尚不清楚MCOLN1gene的突变如何导致IV型粘脂病患者的智力和运动技能发展延迟（精神运动延迟），进行性视力丧失和胃酸分泌受损（胃酸缺乏症）。"}]}' WHERE id = 25020;
UPDATE genes SET GHR_metadata_ch = '{"gene": "IFT43", "conditions": [{"name": "颅外胚层发育不良", "detail": "IFT43gene的突变是颅外胚层发育不良的罕见原因。这种情况的特征是头部拉长（头畸形），额头突出，面部特征其他独特；短骨头；以及某些称为外胚层组织的组织异常，包括牙齿，头发，指甲和皮肤。（换行）涉及颅外胚层发育不良的IFT43gene突变导致异常短的IFT43蛋白的产生，其可能迅速分解。IFT-A复合物中此成分的缺乏会损害整个复合物的功能，从而破坏蛋白质和材料从纤毛尖端的运输。结果，纤毛的组装和维护受到损害，这导致纤毛数量减少以及其形状和结构异常。尽管机制尚不清楚，但正常纤毛的丧失会阻碍骨骼和其他组织的正常发育，从而导致颅外胚层发育不良的特征。一些研究人员认为，鞭毛内运输受阻会阻止通过声波刺猬通路的信号传导，这可能会影响整个身体多个组织中的细胞生长和其他功能。"}]}' WHERE id = 25026;
UPDATE genes SET GHR_metadata_ch = '{"gene": "TP53", "conditions": [{"name": "乳腺癌", "detail": "TP53gene的遗传变化极大地增加了患乳腺癌以及其他几种癌症的风险，这是一种称为Li-Fraumeni综合征的罕见癌症综合征的一部分（如下所述）。这些突变被认为仅占所有乳腺癌病例的一小部分。（换行）TP53gene中的非遗传（体细胞）突变比遗传突变更常见，发生在所有乳腺癌的20%至40%。这些体细胞突变是在一个人的一生中获得的，仅存在于癌变的细胞中。与体细胞突变相关的癌症不会作为癌症综合征的一部分发生。这些突变大多数会改变p53蛋白中的单个蛋白结构单元（氨基酸），从而降低或消除该蛋白的抑癌功能。这种改变的p53蛋白不能有效调节细胞增殖。具体而言，它不能触发DNA突变或受损的细胞凋亡。结果，DNA损伤可以在细胞中积累。这些细胞可能继续以不受控制的方式分裂，导致肿瘤生长。（换行）与没有TP53gene突变的乳腺癌相比，具有这些遗传变化的肿瘤倾向于具有较差的预后。它们更可能具有攻击性，对某些抗癌药物和放射线的治疗有抵抗力，并在治疗后回来（复发）。"}, {"name": "膀胱癌", "detail": "在一些膀胱癌病例中发现了体细胞TP53gene突变。这些突变大多数会改变p53中的单个氨基酸。这种改变的p53蛋白不能调节细胞增殖，也不能触发DNA突变或受损的细胞凋亡。结果，DNA损伤可以在细胞中积累。这些细胞可能继续以不受控制的方式分裂，导致肿瘤生长。TP53gene的突变可能有助于预测膀胱癌是否会进展并扩散到附近的组织，以及治疗后疾病是否会复发。"}, {"name": "胆管癌", "detail": "遗传学家庭参考提供有关胆管癌的信息。"}, {"name": "头颈部鳞状细胞癌", "detail": "在所有头颈部鳞状细胞癌（HNSCC）的近一半中都发现了TP53gene的体细胞突变。这种类型的癌性肿瘤发生在口腔，鼻子和喉咙的潮湿衬里。HNSCC中涉及的大多数TP53gene突变都会改变p53中的单个氨基酸；这些变化会损害蛋白质的功能。没有功能性p53，细胞增殖不受调节。结果，细胞积累DNA损伤并继续以不受控制的方式分裂，导致肿瘤生长。"}, {"name": "李-佛美尼症候群", "detail": "尽管在许多类型的癌症中都发现了TP53gene的体细胞突变，但Li-Fraumeni综合征似乎是唯一与该gene的遗传突变相关的癌症综合征。这种情况大大增加了患包括乳腺癌在内的几种癌症的风险；骨癌；和称为软组织肉瘤的软组织癌（如肌肉），特别是在儿童和年轻人中。在患有Li-Fraumeni综合征的个体中，已经鉴定出TP53gene中至少140种不同的突变。（换行）许多与Li-Fraumeni综合征相关的突变改变了与DNA结合的p53蛋白部分中的单个氨基酸。其他突变会从gene中删除少量DNA。这些突变导致p53蛋白发生改变，无法有效调节细胞增殖，也无法触发DNA突变或受损的细胞凋亡。结果，DNA损伤可以在细胞中积累。这些细胞可能继续以不受控制的方式分裂，导致肿瘤的生长。"}, {"name": "肺癌", "detail": "在所有肺癌的近一半中都发现了TP53gene的体细胞突变。肺癌是一种肺部某些细胞变得异常并无法控制地繁殖形成肿瘤的疾病。在疾病的早期阶段可能不会出现体征和症状。(换行)根据在显微镜下观察时受影响细胞的大小，肺癌通常分为两种类型，小细胞肺癌和非小细胞肺癌。小细胞肺癌几乎总是有TP53gene突变；但是，这些突变也可能发生在非小细胞肺癌中。TP53gene突变会改变p53中的单个氨基酸，从而损害蛋白质的功能。没有功能性p53，细胞增殖不能有效调节，DNA损伤可以在细胞中积累。这些细胞可能继续以不受控制的方式分裂，导致肿瘤生长。其他遗传，环境和生活方式因素会导致一个人的癌症风险；在肺癌中，最大的风险因素是长期吸烟者。"}, {"name": "卵巢癌", "detail": "体细胞TP53gene突变在卵巢癌中很常见，几乎发生在一半的卵巢肿瘤中。这些突变导致p53蛋白不能控制细胞增殖。具体而言，它不能触发DNA突变或受损的细胞凋亡。结果，DNA损伤可以在细胞中积累。这些细胞可能继续以不受控制的方式分裂，导致肿瘤生长。"}, {"name": "其他癌症", "detail": "TP53gene中的体细胞突变是人类癌症中最常见的遗传变化，约占所有癌症的一半。除上述癌症外，还在几种类型的脑瘤，结肠直肠癌，肝癌，一种称为骨肉瘤的骨癌，一种称为横纹肌肉瘤的肌肉组织癌中发现了体细胞TP53gene突变，和一种称为肾上腺皮质癌的癌症，它影响肾上腺的外层（每个肾脏顶部产生小激素的腺体）。（换行）大多数TP53突变改变p53蛋白中的单个氨基酸，这导致产生不能控制细胞增殖并且不能在具有突变或受损DNA的细胞中触发细胞凋亡的蛋白质的改变形式。结果，DNA损伤可以在细胞中积累。这些细胞可能继续以不受控制的方式分裂，导致肿瘤生长。"}]}' WHERE id = 25033;
UPDATE genes SET GHR_metadata_ch = '{"gene": "PAX2", "conditions": [{"name": "肾小球瘤综合征", "detail": "已发现PAX2gene中的20多个突变会导致肾小球瘤综合征。大多数突变是特定于每个受影响家庭的；但是，在多个受影响的个体中发现了一个突变。此突变将一个DNA构建块（核苷酸）插入PAX2gene（写为619insG）。大多数突变发生在与DNA连接的蛋白质区域，从而削弱了其作为转录因子的功能。缺乏功能性PAX2蛋白会破坏胚胎发育过程中某些组织（尤其是肾脏和眼睛）的形成，从而导致肾小球瘤综合征的体征和症状。"}, {"name": "癌症", "detail": "PAX2gene表达的改变与某些癌症有关。这种改变的表达被认为增强了gene保护细胞免于细胞死亡的能力，从而允许肿瘤生长（增殖）。（换行）PAX2gene在某些类型的肾癌，前列腺癌，乳腺癌和卵巢癌中异常活跃（过表达）。PAX2gene的过表达也见于称为肾母细胞瘤的罕见形式的肾癌和称为卡波西肉瘤的软组织癌。卡波西肉瘤导致癌性病变在皮肤下和粘膜下（例如口腔和消化道的潮湿衬里）发展。通常，癌细胞中高水平的PAX2gene表达与更具侵袭性的癌症相关。"}]}' WHERE id = 25044;
UPDATE genes SET GHR_metadata_ch = '{"gene": "TGFB3", "conditions": [{"name": "Loeys-Dietz综合征", "detail": "已发现TGFB3gene中至少有11个突变会导致V型Loeys-Dietz综合征。这种疾病会影响结缔组织，结缔组织为血管，骨骼和身体的许多其他部位提供结构和支持。V型Loeys-Dietz综合征的特征是血管异常，心脏缺陷和骨骼畸形。导致这种情况的TGFB3gene突变导致产生TGFβ-3种蛋白质，功能很少或没有。结果，蛋白质不能与其受体结合。虽然TGF-3蛋白及其受体不结合，TGF-？通路信号的发生强度甚至比正常情况更大。研究人员推测，这种信号通路中其他蛋白质的活性增加，以弥补TGFβ的减少-3活动；然而，导致信号增加的确切机制尚不清楚。过度活跃的信号通路破坏结缔组织和各种身体系统的发育，并导致V型Loeys-Dietz综合征的体征和症状。"}, {"name": "致心律失常性右室心肌病", "detail": "遗传学家庭参考资料提供有关心律失常性右室心肌病的信息。"}]}' WHERE id = 25053;
UPDATE genes SET GHR_metadata_ch = '{"gene": "PNPLA6", "conditions": [{"name": "Boucher-Neuhäuser综合征", "detail": "已发现PNPLA6gene中的十几个突变会导致Boucher-Neuhäuser综合征，该疾病的特征是协调和平衡问题（共济失调），视力障碍和青春期延迟。突变被认为损害NTE蛋白的功能。研究人员不确定这种功能下降如何导致病情的体征和症状。他们推测溶血磷脂酰胆碱代谢受损会改变细胞膜中化合物的平衡。这种不平衡可能会损害轴突，导致Boucher-Neuhäuser综合征的运动和视觉问题。这种不平衡也被认为会损害性发育中涉及的激素的释放，这是受影响个体青春期延迟的原因。"}, {"name": "戈登·霍姆斯综合症", "detail": "PNPLA6gene中至少有6个突变会导致Gordon Holmes综合征，这是一种罕见的疾病，其特征是共济失调和指导性发育的激素产生减少（促性腺激素性性腺功能减退症），从而导致青春期延迟或缺乏或其他生殖问题。与Boucher-Neuhäuser综合征（如上所述）一样，引起Gordon Holmes综合征的突变会损害NTE蛋白的功能，研究人员怀疑这会破坏细胞膜中溶血磷脂酰胆碱的水平。人们认为，由此导致的细胞膜化合物失衡会损害大脑中的神经元，导致共济失调，并损害垂体释放与性发育有关的激素，从而导致性腺功能低下性腺功能减退。目前尚不清楚同一gene中的突变如何导致特征的不同组合。"}, {"name": "其他疾病", "detail": "PNPLA6gene的突变会导致一系列连续的神经系统疾病，称为PNPLA6相关疾病。该组的病症包括Boucher-Neuhäuser综合征（如上所述），Gordon Holmes综合征（如上所述），Oliver-McFarlane综合征，Laurence-Moon综合征和痉挛性截瘫39型。PNPLA6相关疾病的特征是重叠的体征和症状的组合，包括共济失调、肌肉僵硬（痉挛）、异常快速（轻快）反射、四肢感觉减退（周围神经病）、智能障碍或其他认知问题、眼睛异常、视力受损、毛发异常、克氏综合征、，垂体功能减退（垂体功能低下）。尚不清楚单个gene中的突变如何引起如此广泛的疾病。"}]}' WHERE id = 25056;
UPDATE genes SET GHR_metadata_ch = '{"gene": "TUBA1A", "conditions": [{"name": "孤立的lissencephaly序列", "detail": "已发现TUBA1Agene中约40个突变会导致孤立的lissencephaly序列（ILS）。这种情况的特征是大脑发育异常，导致大脑外观光滑（lissencephaly），而不是正常的褶皱和凹槽。ILS患者有严重的神经系统问题，包括智力障碍和反复发作（癫痫病）。这些突变中的大多数会改变单个蛋白质的结构单元（氨基酸）-微管蛋白。由此产生的异常-微管蛋白不能形成微管或与其他蛋白质相互作用。结果，微管的整体功能降低。在发育中的大脑中，微管功能的这种下降会损害神经元的正常迁移，并导致大脑褶皱和凹槽的形成减少或大脑的外观完全光滑，从而导致ILS特有的神经系统问题。"}, {"name": "小脑发育不全的小脑畸形", "detail": "已发现TUBA1Agene中至少有10个突变会引起小脑发育不全（LCH）的小脑畸形。这种情况会影响大脑发育，导致小脑畸形和大脑异常小而欠发达的区域，称为小脑（小脑发育不全）。导致LCH的TUBA1Agene突变改变了-微管蛋白。这些改变的蛋白质仍然可以掺入微管中，但研究表明这些微管的功能降低或异常。(换行)在发育中的大脑中，微管功能受损会阻止神经元的正常迁移。结果，大脑的正常褶皱和凹槽无法形成，小脑和其他大脑结构也无法正常发育。这种大脑发育障碍导致智力残疾，整体发育迟缓，运动问题以及LCH的其他体征和症状。"}]}' WHERE id = 25059;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SLC26A4", "conditions": [{"name": "先天性甲状腺功能减退症", "detail": "遗传学家庭参考提供有关先天性甲状腺功能减退症的信息。"}, {"name": "非综合征性听力损失", "detail": "在非综合征性听力损失患者中已鉴定出数十种SLC26A4gene突变，这是与影响身体其他部位的体征和症状无关的听力损失。该gene的突变会导致一种称为DFNB4的非综合征性听力损失。这种形式的听力损失可以在孩子学会说话（舌前）之前出现，也可以在孩子学会说话（舌后）之后开始。大多数患有DFNB4的人还具有异常大的前庭导水管（扩大的前庭导水管或EVA）。SLC26A4gene中的（换行）突变会损害或消除pendrin的活性，从而破坏内耳离子的平衡。这些变化可能会影响内耳结构的发展，包括耳蜗和前庭导水管。研究表明，离子水平的变化也会导致听力所需的内耳感觉细胞的损失。"}, {"name": "彭德雷综合症", "detail": "研究人员已经在Pendred综合征患者中发现了SLC26A4gene的150多个突变。这种情况的特征是甲状腺肿大（称为甲状腺肿），听力下降和内耳的其他异常，包括前庭导水管增大。(换行)一些SLC26A4gene突变会改变用于制造pendrin的单蛋白结构单元（氨基酸）。其他突变会在SLC26A4gene中添加或删除少量DNA。所有这些遗传变化都会损害或消除pendrin的活性，从而破坏离子的运输。在甲状腺中，破坏的离子转运阻止碘离子可用于甲状腺激素的产生。为了弥补感知到的碘化物缺乏，甲状腺组织扩大形成甲状腺肿。在内耳中，受损的pendrin活性会改变离子的平衡，这可能会影响包括耳蜗和前庭导水管在内的结构的发展。研究表明，离子水平的变化也会导致听力所需的内耳感觉细胞的损失。(换行)由于它们的体征和症状重叠，因此很难将Pendred综合征与非综合征性听力损失（DFNB4，如上所述）区分开。还发现许多与Pendred综合征相关的SLC26A4gene突变会引起DFNB4。该gene的突变也可引起其他甲状腺异常；在少数人中，SLC26A4gene突变与异常小的甲状腺（甲状腺发育不全）相关，导致出生时甲状腺功能丧失（先天性甲状腺功能减退）。目前尚不清楚由SLC26A4gene突变引起的Pendred综合征，DFNB4和甲状腺发育不全是否最好被视为单独的疾病或一系列相关的体征和症状。"}, {"name": "与年龄有关的听力损失", "detail": "遗传学家庭参考资料提供有关与年龄有关的听力损失的信息。"}]}' WHERE id = 25074;
UPDATE genes SET GHR_metadata_ch = '{"gene": "TPI1", "conditions": [{"name": "Triosephosphate isomerase deficiency", "detail": "已发现TPI1gene中至少有12个突变会导致磷酸丙糖异构酶缺乏症。这种情况的特征是缺乏红细胞（贫血），运动问题，对感染的敏感性增加以及可能影响呼吸和心脏功能的肌肉无力。（换行）TPI1gene突变可导致产生活性降低的酶。结果，糖酵解受损并且细胞的能量供应减少。一种TPI1gene突变约占磷酸丙糖异构酶缺乏症病例的80%。这种变化用磷酸丙糖异构酶1酶（写为Glu104Asp或E104D）中第104位的氨基酸天冬氨酸代替了蛋白质结构单元（氨基酸）谷氨酸。这种突变导致酶不稳定并削弱其形成二聚体并变得活跃的能力。(换行)红细胞仅依赖于葡萄糖的能量分解。没有功能性磷酸丙糖异构酶1酶将DHAP转化为3-磷酸甘油醛，红细胞就会积累大量有毒的DHAP。与其他细胞不同，红细胞没有替代途径来分解DHAP。由于DHAP的积累和细胞能量的缺乏，红细胞比正常人更早死亡。(换行)具有高能量需求的细胞，例如脑中的神经细胞，白细胞和心脏（心脏）肌细胞也由于糖酵解受损引起的能量减少而易于细胞死亡。磷酸丙糖异构酶缺乏症患者的大脑中参与协调运动的神经细胞（小脑）尤其受到影响。红细胞和白细胞，脑中神经细胞和心肌细胞的死亡导致磷酸丙糖异构酶缺乏症的体征和症状。"}]}' WHERE id = 25105;
UPDATE genes SET GHR_metadata_ch = '{"gene": "CLIP2", "conditions": [{"name": "威廉姆斯综合症", "detail": "CLIP2gene位于威廉姆斯综合征患者中缺失的7号染色体区域。这种删除的结果是，患有这种情况的人在每个细胞中都缺少一个CLIP2gene拷贝。研究表明，该gene的缺失可能有助于威廉姆斯综合征的一些特征，包括独特的行为特征和其他涉及神经系统的症状。该gene的缺失可能破坏细胞骨架的正常调节并影响大脑中神经细胞的结构。尚不清楚这些变化如何与威廉姆斯综合征的特征性体征和症状相关。"}]}' WHERE id = 25112;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ATP1A2", "conditions": [{"name": "儿童交替性偏瘫", "detail": "ATP1A2gene的突变与儿童交替性偏瘫有关，这种情况以反复发作的暂时性麻痹为特征，通常影响身体的一侧（偏瘫）。这是由α-2亚基的单个氨基酸变化引起的，导致缺陷Na+/K+ATP酶，其损害其运输离子的能力。"}, {"name": "家族性偏瘫性偏头痛", "detail": "已经在患有家族性偏瘫性偏头痛2型（FHM2）的人中鉴定出ATP1A2gene的突变，该病症以偏头痛为特征，具有称为先兆的神经症状模式，包括身体一侧的暂时麻木或虚弱（偏瘫）。这是由于α-2亚基中单个氨基酸的变化导致Na+/K+ATPase缺陷，从而削弱了其转运离子的能力。"}, {"name": "偶发性偏瘫偏头痛", "detail": "已经发现ATP1A2gene的突变引起散发性偏瘫性偏头痛，其与FHM2相同，但发生在没有该病家族史的个体中。这是由于α-2亚基中单个氨基酸的变化导致Na+/K+ATPase缺陷，从而削弱了其转运离子的能力。"}]}' WHERE id = 25129;
UPDATE genes SET GHR_metadata_ch = '{"gene": "GTF2IRD1", "conditions": [{"name": "威廉姆斯综合症", "detail": "GTF2IRD1gene位于威廉姆斯综合征患者中缺失的7号染色体区域。这种删除的结果是，患有这种情况的人在每个细胞中都缺少一个GTF2IRD1gene拷贝。研究表明，该gene的缺失可能有助于威廉姆斯综合症的某些特征，包括独特的面部特征，牙齿异常以及视觉空间任务（如书写和绘图）的问题。研究人员正在研究该gene的缺失如何与这些特定特征相关。"}]}' WHERE id = 25133;
UPDATE genes SET GHR_metadata_ch = '{"gene": "DLD", "conditions": [{"name": "二氢脂酰胺脱氢酶缺乏症", "detail": "已发现DLDgene中至少有17个突变会导致二氢脂酰胺脱氢酶缺乏症。这种严重疾病的体征和症状差异很大，但最常见的是组织中乳酸可能危及生命（乳酸性酸中毒），神经系统问题和肝脏疾病。（换行）大多数DLD突变会改变二氢脂酰胺脱氢酶中的单个氨基酸，从而阻止BCKD，PDH和？KGDH酶复合物功能正常。BCKD功能受损会导致体内缬氨酸，异亮氨酸和亮氨酸及其副产物积聚。这种积累对细胞和组织有毒，特别是在神经系统中，并且导致二氢脂酰胺脱氢酶缺乏症患者的神经系统问题。丙酮酸脱氢酶功能的降低导致丙酮酸的积累，丙酮酸在另一种化学反应中转化为乳酸，从而导致受影响个体的乳酸性酸中毒。减值？KGDH导致α-酮戊二酸的积累，也可能导致乳酸性酸中毒。这三种酶复合物的功能降低也减少了细胞能量的产生。需要特别大量能量的大脑受到严重影响，导致与二氢脂酰胺脱氢酶缺乏症相关的神经系统问题。肝脏问题也可能与细胞能量产生减少有关。每个复合物的损伤程度有助于这种情况特征的可变性。"}, {"name": "利综合征", "detail": "遗传学家庭参考提供有关Leigh综合征的信息。"}]}' WHERE id = 25137;
UPDATE genes SET GHR_metadata_ch = '{"gene": "DICER1", "conditions": [{"name": "DICER1综合征", "detail": "DICER1gene的突变会导致DICER1综合征。患有这种疾病的人患多种类型肿瘤的风险增加，特别是某些肺部肿瘤（胸膜肺母细胞瘤）；肾脏（囊性肾瘤）；卵巢（Sertoli-Leydig肿瘤）；甲状腺是下颈部的蝴蝶状腺体（多结节性甲状腺肿）。这些突变大多数会导致异常短的Dicer蛋白，很可能无法产生miRNA。如果没有miRNA的调控，gene表达异常，这可能导致细胞不受控制地生长和分裂并导致肿瘤形成。"}]}' WHERE id = 25141;
UPDATE genes SET GHR_metadata_ch = '{"gene": "GTF2I", "conditions": [{"name": "7q11.23复制综合征", "detail": "GTF2Igene位于7q11.23复制综合征患者中复制的7号染色体区域。这种重复的结果是，患有7q11.23复制综合征的人在每个细胞中都有额外的GTF2Igene拷贝和其他几个gene。7q11.23复制综合征可引起多种神经和行为问题以及其他异常。(换行)与7q11.23复制综合征相关的行为问题包括焦虑症（例如社交恐惧症和选择性默症，在某些情况下无法说话），注意力缺陷多动障碍（ADHD），身体攻击，过度反抗行为（对立障碍），以及影响沟通和社交互动的自闭症行为。研究表明，额外的GTF2Igene拷贝可能与7q11.23复制综合征的某些行为特征有关，但这种作用的机制尚不清楚。尽管GTF2Igene在免疫功能中起作用，但受影响的个体似乎没有与该疾病相关的免疫异常。"}, {"name": "威廉姆斯综合症", "detail": "GTF2Igene位于Williams综合征患者中缺失的7号染色体区域。由于这种缺失，患有这种情况的人在每个细胞中缺少一个GTF2Igene拷贝。研究表明，该gene的缺失是威廉姆斯综合征患者智力残疾的部分原因。该gene的缺失也可能导致牙齿异常以及在这种疾病中看到的视觉空间任务（例如书写和绘图）的特征性问题。研究人员正在研究涉及该gene的缺失如何与威廉姆斯综合征的这些特定特征相关。"}]}' WHERE id = 25149;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ATN1", "conditions": [{"name": "牙本质苍白球萎缩", "detail": "ATN1gene的突变与牙本质-睑板肌萎缩症（DRPLA）有关，该病的特征是不自主运动，智力下降，癫痫发作，行为改变，智力残疾，共济失调，舞蹈性运动，精神病症状（如妄想）和痴呆。这种情况是由于ATN1gene的CAG重复区域长度异常引起的，通常重复两次或三次，然后通常长度导致脑细胞损伤。"}]}' WHERE id = 25151;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SIX1", "conditions": [{"name": "支气管肾/支气管综合征", "detail": "在患有支气管肾（BOR）综合征的人中，已经鉴定出SIX1gene中至少有9个突变，这种情况会破坏颈部组织的发育并导致耳朵和肾脏的畸形。还发现了一些SIX1gene突变会引起分支性（BO）综合征，其中包括许多与BOR综合征相同的特征，除了肾脏（肾）畸形。否则，这两种情况非常相似，以至于研究人员经常将它们放在一起考虑（BOR/BO综合征或支气管肾频谱疾病）。在某些情况下，相同的SIX1gene突变会在一个家庭的某些成员中引起BOR综合征，而在另一些家庭中引起BO综合征。(换行)大多数已知的SIX1gene突变会改变SIX1蛋白中的单个蛋白结构单元（氨基酸）。这些突变中的一些阻止SIX1蛋白与其他蛋白相互作用，例如EYA1gene产生的蛋白。其他突变影响SIX1蛋白与DNA结合的能力。这两个功能对于SIX1蛋白在胚胎发育过程中调节gene活性都是必需的。当SIX1蛋白有缺陷时，它会损害出生前许多组织的正常发育。BOR/BO综合征的主要体征和症状是由第二个branch弓，耳朵和（在BOR综合征中）肾脏的异常发育引起的。"}]}' WHERE id = 25162;
UPDATE genes SET GHR_metadata_ch = '{"gene": "NCF1", "conditions": [{"name": "慢性肉芽肿病", "detail": "NCF1gene突变约占慢性肉芽肿病病例的25%。患有这种疾病的人由于免疫系统减弱而发生反复发作的感染和炎症的风险增加。导致这种疾病的突变发生在每个细胞的NCF1gene的两个拷贝中。95%的受影响个体具有称为delta GT的突变，该突变在称为外显子2（写为75_76delGT）的区域从NCF1gene中删除了两个DNA构建块。这种遗传变化导致异常短的，无功能的嗜中性粒细胞胞质因子1的产生。其他较不常见的突变也会破坏嗜中性粒细胞胞质因子1的功能或产生。没有这种蛋白质，NADPH氧化酶就无法正常组装或起作用。结果，吞噬细胞不能产生活性氧来杀死外来入侵者，并且中性粒细胞活性不受调节。缺乏NADPH氧化酶会使受影响的个体容易受到多种类型的感染和过度炎症的影响。"}, {"name": "威廉姆斯综合症", "detail": "The NCF1 gene is located in a region of chromosome 7 that is often deleted in people with Williams syndrome. Williams syndrome is a developmental disorder that affects many parts of the body. As a result of the deletion of part of chromosome 7, some people with this condition are missing one copy of the NCF1 gene in each cell. Researchers have found that the loss of this gene is a protective factor that appears to lower the risk of developing high blood pressure (hypertension). People with Williams syndrome whose NCF1 gene is not deleted have a higher risk of developing hypertension. (line-break) People with only one copy of the NCF1 gene have reduced levels of the neutrophil cytosolic factor 1 protein, which decreases the activity of NADPH oxidase and results in the production of fewer reactive oxygen species. Studies suggest that reactive oxygen species play an important role in blood vessel changes related to hypertension.\r"}]}' WHERE id = 25172;
UPDATE genes SET GHR_metadata_ch = '{"gene": "NEB", "conditions": [{"name": "Nemaline myopathy", "detail": "已发现NEBgene中有60多个突变引起肾上腺肌病。这些突变中的一些改变了星云蛋白中的单个蛋白结构单元（氨基酸），或导致异常短蛋白的产生。其他突变在NEBgene中插入或删除遗传物质。大多数NEBgene突变会改变结构或减少星云蛋白的产生。缺乏功能性星云蛋白会损害肌肉细胞的收缩能力。无效的肌肉收缩会导致肌肉无力和肾上腺肌病的其他特征。"}]}' WHERE id = 25173;
UPDATE genes SET GHR_metadata_ch = '{"gene": "PIK3R1", "conditions": [{"name": "身材矮小，过度伸展，疝气，眼压，Rieger异常，出牙延迟", "detail": "据报道，PIK3R1gene中至少有七个突变会导致一种称为身材矮小，过度伸展，疝气，眼压，Rieger异常和出牙延迟（通常称为short综合征）的疾病。这种情况的特征是影响身体许多部位的体征和症状，包括皮肤，眼睛，牙齿和关节。已在至少10个受影响的家族中鉴定出的最常见突变改变了PI3K调节亚基中的单个蛋白质结构单元（氨基酸）。具体而言，氨基酸精氨酸在蛋白质位置649（写为Arg649Trp或R649W）被氨基酸色氨酸取代。PIK3R1gene的突变会改变亚基的结构，从而降低PI3K参与细胞信号传导的能力。由于突变会降低酶的活性，因此将其描述为“功能丧失”突变。(换行)研究人员正在努力确定PIK3R1gene突变如何导致SHORT综合征的特定特征。PI3K在胰岛素活性中的作用可能与胰岛素抵抗和糖尿病有关，这是一些SHORT综合征患者血糖调节的问题。异常的脂肪细胞成熟可能导致皮肤下脂肪组织的缺乏（脂肪萎缩），这是该病的另一个常见特征。尚不清楚减少的PI3K信号传导如何与SHORT综合征的其他体征和症状相关。"}, {"name": "癌症", "detail": "一些gene突变是在一个人的一生中获得的，并且仅存在于某些细胞中。这些被称为体细胞突变的变化不是遗传的。已经在一些子宫内膜癌（子宫内膜癌）和称为胶质母细胞瘤的脑癌形式中鉴定了体细胞PIK3R1gene突变。较少见的是，在结肠癌，卵巢癌和乳腺癌中发现了PIK3R1gene的体细胞突变。（换行）PIK3R1gene中与癌症相关的变化改变了调节亚基，使其不再能够控制PI3K的活性，从而显着增加PI3K信号传导。因为遗传变化增强了酶的活性，所以它们被归类为“功能获得”突变。增加的PI3K信号传导似乎促进了癌性肿瘤特有的不受控制的细胞生长和分裂。目前尚不清楚为什么这些突变在某些类型的癌症中似乎比在其他类型的癌症中更常见。"}]}' WHERE id = 25188;
UPDATE genes SET GHR_metadata_ch = '{"gene": "TWNK", "conditions": [{"name": "共济失调神经病谱", "detail": "在少数患有共济失调神经病谱的人中发现了TWNKgene的突变。这种情况的特征是协调和平衡问题（共济失调）和神经功能紊乱（神经病）。先前称为线粒体隐性共济失调综合征（MIRAS）和感觉性共济失调神经病-构音障碍和眼肌麻痹（SANDO）的疾病现已包括在共济失调神经病谱中。TWNKgene中的（换行）突变破坏了Twinkle的功能，并导致受影响个体肌肉组织中mtDNA的大量缺失。但是，尚不清楚TWNKgene的突变如何引起共济失调神经病谱的体征和症状。"}, {"name": "小儿发作性脊髓小脑共济失调", "detail": "已发现TWNKgene中至少有六个突变会导致婴儿发作性脊髓小脑共济失调（IOSCA）。最常见的突变是将Twinkle蛋白中第508位的半胱氨酸替换为Tyr508Cys或Y508C的蛋白质结构单元（氨基酸）酪氨酸。大多数受影响的个体在每个细胞中都有该gene突变的两个副本。据报道，至少有两个额外的突变是特定家族独有的。在这些情况下，受影响的个体在每个细胞中都有一个拷贝的家族特异性突变和一个拷贝的Tyr508Cys突变。（换行）导致IOSCA的TWNKgene突变干扰Twinkle蛋白的功能并导致mtDNA数量减少（mtDNA消耗）。线粒体功能受损，尤其是在神经系统中（需要大量能量），会导致神经功能障碍和与IOSCA相关的其他问题。"}, {"name": "Perrault综合征", "detail": "在患有Perrault综合征的家庭中已鉴定出至少四个TWNKgene突变，该疾病的特征是受影响的男性和女性听力下降，而受影响的女性卵巢异常。这种情况下涉及的突变改变了Twinkle蛋白中的单个氨基酸。研究人员预测，这些突变会损害蛋白质的解旋酶活性。但是，尚不清楚TWNKgene突变如何在受影响的个体中导致听力问题和卵巢异常。"}, {"name": "进行性眼外肌麻痹", "detail": "在患有进行性眼外肌麻痹的眼病患者中，至少鉴定出40种TWNKgene突变。这种疾病削弱了控制眼球运动并导致眼睑下垂（上睑下垂）的肌肉。研究人员推测，突变的Twinkle蛋白损害了解旋酶活性，从而使DNA复制过程停滞。尽管机制尚不清楚，但复制停滞似乎导致肌肉组织中mtDNA的遗传物质大量缺失。尽管该病的特征可能与氧化磷酸化受损有关，但研究人员尚未确定mtDNA的缺失如何导致进行性眼外肌麻痹的特定体征和症状。已经提出，眼肌通常受到线粒体缺陷的影响，因为它们特别依赖于氧化磷酸化作为能量。"}, {"name": "其他疾病", "detail": "在少数家庭中，TWNKgene突变导致mtDNA耗竭综合征，肝脑形式。患有这种疾病的人经历肌肉张力弱（低张力），肝功能下降，发育迟缓，癫痫发作，四肢感觉丧失和虚弱（周围神经病变）。"}]}' WHERE id = 25190;
UPDATE genes SET GHR_metadata_ch = '{"gene": "NCSTN", "conditions": [{"name": "化脓性汗腺炎", "detail": "已发现NCSTNgene中至少有11个突变会引起化脓性汗腺炎，这是一种慢性皮肤病，其特征是在毛囊中皮肤下反复出现沸腾状肿块（结节）。结节往往会发炎和疼痛，并且在愈合时会产生明显的疤痕。（换行）NCSTNgene突变减少了细胞中产生的功能性尼卡斯汀的量，因此这种蛋白质中可作为-分泌酶复合物。由此导致的正常短缺-分泌酶损害细胞信号传导途径，包括Notch信号传导。尽管对该机制知之甚少，但研究表明，Notch信号异常可能会促进毛囊中复发性结节的发展并引发皮肤炎症。"}]}' WHERE id = 25191;
UPDATE genes SET GHR_metadata_ch = '{"gene": "NAGS", "conditions": [{"name": "N-乙酰谷氨酸合酶缺乏症", "detail": "在N-乙酰谷氨酸合酶缺乏症患者中已鉴定出约12个NAGSgene突变。突变的NAGSgene可能导致N-乙酰谷氨酸合酶短于正常形状或形状错误，或者可能根本阻止该酶的产生。(换行)酶的形状影响其控制化学反应的能力。如果N-乙酰谷氨酸合酶受损或缺失，则N-乙酰谷氨酸的产生量将低于正常量，或根本不产生。N-乙酰谷氨酸的这种缺乏会干扰氨基甲酰磷酸合成酶I的适当活化，从而阻止其在尿素循环中发挥作用。过量的氮不会转化为尿素排泄，氨会积聚在体内。这种积累引起神经系统问题以及N-乙酰谷氨酸合酶缺乏症的其他体征和症状。"}]}' WHERE id = 25238;
UPDATE genes SET GHR_metadata_ch = '{"gene": "PPOX", "conditions": [{"name": "卟啉症", "detail": "在患有称为杂色卟啉症的卟啉症患者中，已鉴定出PPOXgene的130多个突变。在约95%患有这种疾病的南非家庭中发现了特定的PPOXgene突变。该突变改变了原卟啉原氧化酶中的单个蛋白质结构单元（氨基酸），用59位的氨基酸色氨酸取代了氨基酸精氨酸（写为Arg59Trp或R59W）。PPOXgene中的（换行）突变降低了原卟啉原氧化酶的活性，使称为卟啉前体的化合物在体内积聚。这些化合物是在血红素生成的正常过程中形成的，但是原卟啉原氧化酶活性的降低使它们积累到毒性水平。非遗传因素，例如某些药物，酒精，节食以及其他尚未发现的遗传因素，也有助于杂色卟啉症的特征。"}]}' WHERE id = 25257;
UPDATE genes SET GHR_metadata_ch = '{"gene": "MPZ", "conditions": [{"name": "进行性神经性腓骨肌萎缩症", "detail": "研究人员已经鉴定出120多种MPZgene突变，这些突变会导致一种称为1B型的Charcot-Marie-Tooth病。这些突变中的许多改变了髓磷脂蛋白零中的单个蛋白结构单元（氨基酸）。其他MPZgene突变导致缺少一个或多个氨基酸的蛋白质。改变的髓磷脂蛋白零可能无法与其他髓磷脂成分正确相互作用，这可能会破坏髓磷脂的形成和维持。结果，周围神经细胞不能激活用于运动的肌肉或将信息从感觉细胞传递回大脑，从而导致Charcot-Marie-Tooth病特有的虚弱和感觉问题。（换行）一些MPZgene突变导致严重形式的1B型Charcot-Marie-Tooth病。症状始于婴儿期或儿童早期，包括行走等运动技能的发展延迟。Charcot-Marie-Tooth病的早期发作形式有时称为Dejerine-Sottas综合征，先天性髓鞘减少或Roussy-Levy综合征。研究人员认为，导致严重疾病的MPZgene突变可能会在早期发育过程中破坏髓磷脂的形成。（换行）MPZgene中的几个突变引起其他形式的Charcot-Marie-Tooth病，称为2I型，2J型和显性中间D.这些形式的Charcot-Marie-Tooth病通常直到成年，影响神经细胞（轴突）的特殊生长，这些细胞将冲动传递给肌肉和其他神经细胞。患有2J型Charcot-Marie-Tooth病的人也可能有听力损失和光线通过的眼睛开口异常（瞳孔）。目前尚不清楚MPZgene突变如何导致这些异常。"}]}' WHERE id = 25269;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SDHC", "conditions": [{"name": "遗传性副神经节瘤嗜铬细胞瘤", "detail": "已发现SDHCgene中的30多个突变会增加3型遗传性副神经节瘤嗜铬细胞瘤的风险。患有这种疾病的人患有副神经节瘤，嗜铬细胞瘤或两者兼有。遗传的SDHCgene突变使个体容易患病，并且需要删除SDHCgene正常拷贝的体细胞突变才能引起3型遗传性副神经节瘤嗜铬细胞瘤。（换行）大多数遗传的SDHCgene突变会改变SDHC蛋白序列中的单个蛋白结构单元（氨基酸）或导致蛋白缩短。结果，SDH酶活性很少或没有。由于突变的SDH酶无法将琥珀酸转化为富马酸，因此琥珀酸会在细胞中积累。过量的琥珀酸盐异常稳定HIF，HIF也在细胞中积聚。过量的HIF刺激细胞分裂并在不需要时触发血管的产生。快速和不受控制的细胞分裂以及新血管的形成可导致遗传性副神经节瘤嗜铬细胞瘤患者的肿瘤发展。"}, {"name": "胃肠道间质瘤", "detail": "遗传学家庭参考提供有关胃肠道间质瘤的信息。"}, {"name": "癌症", "detail": "在少数患有胃肠道间质瘤（GIST）的人中发现了SDHCgene的突变，胃肠道间质瘤是一种胃肠道癌。SDHCgene突变也已在与神经系统相关的非癌性肿瘤患者中被发现，称为副神经节瘤或嗜铬细胞瘤（一种副神经节瘤）。一些受影响的人同时患有副神经节瘤和GIST，这被称为Carney-Stratakis综合征。遗传的SDHCgene突变使个体容易患癌症。需要删除gene正常拷贝的其他突变才能引起这些形式的GIST和副神经节瘤。第二种突变称为体细胞突变，是在一个人的一生中获得的，仅存在于肿瘤细胞中。SDHCgene的（换行）突变导致SDH酶活性丧失，从而导致异常的缺氧信号传导和肿瘤的形成。"}]}' WHERE id = 25270;
UPDATE genes SET GHR_metadata_ch = '{"gene": "CUBN", "conditions": [{"name": "Imerslund-Gräsbeck综合征", "detail": "已发现CUBNgene中至少有35个突变会引起一种称为Imerslund-Gräsbeck综合征的疾病。这种情况的特点是体内维生素B12水平低，导致称为巨幼红细胞性贫血的血液疾病。大约一半的受影响个体尿液中也有过量的蛋白质（蛋白尿），有些人有神经系统问题。（换行）在芬兰血统的人中发现的最常见的CUBNgene突变改变了cubilin蛋白中的单个蛋白结构单元（氨基酸）；氨基酸脯氨酸被1297位的氨基酸亮氨酸取代（写为Pro1297Leu或P1297L）。蛋白质改变损害了cubilin与维生素B12结合的能力。维生素不是被摄入肠细胞，而是从体内释放出来。缺乏这种必需的维生素会损害红细胞的正常发育，导致巨幼红细胞性贫血。此外，低水平的维生素B12会影响中枢神经系统，引起神经系统问题。（换行）其他CUBNgene突变阻止cubilin附着于无羊膜蛋白或导致cubilin蛋白的量或功能减少。这些变化损害了cubilin在肾脏和小肠中的功能。在肾脏中没有无羊膜功能，蛋白质不会重新吸收到体内，而是在尿液中释放，导致蛋白尿。具有这些突变的个体会发展为巨幼细胞性贫血和蛋白尿。"}]}' WHERE id = 25308;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SLC19A2", "conditions": [{"name": "硫胺素反应性巨幼细胞性贫血综合征", "detail": "已发现SLC19A2gene中至少有17个突变会引起硫胺素反应性巨幼细胞性贫血综合征。这些突变大多数导致异常短的，无功能的硫胺素转运蛋白1的产生。其他突变会改变硫胺素转运蛋白1中的单个蛋白质结构单元（氨基酸），从而破坏蛋白质的正确折叠或阻止其到达细胞表面。所有这些突变都阻止硫胺素转运蛋白1将硫胺素带入细胞。(换行)目前尚不清楚硫胺素转运蛋白1的缺乏如何导致巨幼红细胞性贫血，糖尿病和听力丧失的看似无关的症状。研究表明，运输硫胺素的另一种方法存在于身体的所有细胞中，除了血细胞和胰岛素形成（分别在骨髓和胰腺中）和内耳细胞。"}]}' WHERE id = 25343;
UPDATE genes SET GHR_metadata_ch = '{"gene": "F5", "conditions": [{"name": "因子V缺乏症", "detail": "已发现F5gene中至少有100个突变会引起罕见的出血性疾病，称为V因子缺乏症。这些突变阻止功能性凝血因子V的产生或显着减少血流中蛋白质的量。患有这种疾病的人血液中凝血因子V的正常水平通常不到10%；受影响最严重的人不到1%。功能因子V的减少可防止血液正常凝结，从而引起严重的异常出血事件。因子V缺乏症是由F5gene的两个拷贝中的突变引起的，尽管一些人在该gene的单个拷贝中具有突变，但存在轻度出血问题。"}, {"name": "因子V Leiden血栓形成倾向", "detail": "因子V Leiden是F5gene中特定突变的name。该突变改变了因子V蛋白中的单个蛋白结构单元（氨基酸）。具体而言，它在蛋白质位置506（写为Arg506Gln或R506Q）上用氨基酸谷氨酰胺取代了氨基酸精氨酸。因为位置506是APC通常切割凝血因子V的位点之一，所以因子V Leiden突变会减慢APC使该因子失活的速度。结果，活化形式的凝血因子V和凝血因子VIIIa在循环中持续更长时间，增加了发生异常血凝块的风险。这种形成可阻塞血管的异常凝块的趋势称为血栓形成倾向。（换行）在F5gene的一个或两个拷贝中存在因子V Leiden突变可引起血栓形成倾向；与单拷贝的突变相比，两拷贝的突变导致发生异常血栓的风险更高。"}, {"name": "其他疾病", "detail": "一些人在F5gene的一个拷贝中具有因子V Leiden突变（Arg506Gln），并且在每个细胞中该gene的另一个拷贝中具有与因子V缺乏相关的突变。因子V Leiden突变导致产生对APC失活具有抗性的异常凝血因子V蛋白，而另一突变阻止任何凝血因子V蛋白的产生。突变的这种组合与异常血凝块的风险增加相关，类似于具有两个因子V Leiden突变拷贝的风险。这种罕见的情况称为假纯合APC抗性或假纯合因子V Leiden。（换行）因子V Leiden突变涉及一些称为Budd-Chiari综合征的病症。这种情况的特点是肝脏血流阻塞，这可能是由血凝块引起的。患有血栓形成倾向的人，包括由因子V Leiden突变引起的人，患Budd-Chiari综合征的风险增加。该综合征的体征和症状包括腹部疼痛，异常大的肝脏（肝肿大）和腹部内壁积聚的液体（腹水）。"}]}' WHERE id = 25348;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ISCU", "conditions": [{"name": "缺乏铁硫簇装配酶的肌病", "detail": "已发现ISCUgene中至少有两个突变会导致肌病，而铁硫簇装配酶缺乏。更常见的突变，写为IVS5+382G>C，改变了gene指令拼接在一起以产生酶的方式。大多数受影响的个体在每个细胞的ISCUgene的两个拷贝中都有这种突变。已经在一个家族中鉴定出的另一个突变，将氨基酸甘氨酸替换为酶中第50位的氨基酸谷氨酸（写为Gly50Glu或G50E）。该家族中受影响的个体在每个细胞的ISCUgene的一个拷贝中具有G50E突变，而在该gene的另一个拷贝中具有IVS5+382G>C突变。突变的这种组合导致该疾病的严重变异，其特征在于进行性肌肉无力和消瘦。ISCUgene中的（换行）突变严重限制了细胞中产生的铁硫簇装配酶的数量。这种酶的缺乏阻止了含有Fe-S簇的蛋白质的正常生产，这会破坏各种细胞活动。铁硫簇组装酶量的减少特别损害骨骼肌细胞。在这些细胞的线粒体内，缺乏这种酶会导致能量产生和铁超载的问题。这些缺陷导致肌肉无力，疼痛和缺乏铁硫簇组装酶的肌病的其他特征。"}]}' WHERE id = 25353;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SLC4A1", "conditions": [{"name": "SLC4A1相关的远端肾小管酸中毒", "detail": "已发现至少18个SLC4A1gene突变引起SLC4A1相关的远端肾小管酸中毒，这是一种肾（肾）疾病，导致血液中酸的积累（代谢性酸中毒）。一些患有这种病症的人也有血细胞异常，例如遗传性球形红细胞增多症，遗传性口腔细胞增多症或东南亚卵母细胞增多症（如下所述）。血细胞异常可导致溶血性贫血，其中异常红细胞过早分解。SLC4A1gene突变导致SLC4A1相关的远端肾小管酸中毒的常染色体显性和常染色体隐性形式。（换行）涉及SLC4A1相关的远端肾小管酸中毒的SLC4A1gene突变导致产生改变的AE1蛋白，其粘附在细胞内或被贩运到细胞的错误侧。在该病症的常染色体显性形式中，gene突变仅影响SLC4A1gene的一个拷贝，并且正常AE1蛋白由另一个拷贝产生。但是，改变后的蛋白质会附着在正常蛋白质上，使其无法到达正确的位置，从而导致细胞膜正确部分的AE1蛋白质严重减少或缺失。在常染色体隐性SLC4A1相关的远端肾小管酸中毒中，SLC4A1gene的两个拷贝都发生了突变，因此从该gene产生的所有蛋白质都被改变并且不能正确运输。肾细胞膜中AE1的位置不正确或不存在会破坏碳酸氢盐的交换，因此，酸不能释放到尿液中。相反，酸在血液中积聚，导致代谢性酸中毒。无法从体内去除酸还会导致远端肾小管酸中毒的其他特征，包括柔软，虚弱的骨头；钙沉积在肾脏中；和肾结石。(换行)研究表明，在血型糖蛋白A的帮助下，改变后的AE1蛋白通常可以到达红细胞中的细胞膜，这解释了为什么大多数患有SLC4A1相关性远端肾小管酸中毒的人没有血细胞异常。但是，糖蛋白A无法帮助某些改变的AE1蛋白，也不会转运到红细胞膜上。没有AE1，红细胞不稳定；这些异常红细胞的分解可能导致溶血性贫血。"}, {"name": "遗传性球形红细胞增多症", "detail": "遗传学家庭参考提供有关遗传性球囊增多症的信息。"}, {"name": "其他疾病", "detail": "SLC4A1gene的突变可引起多种血液疾病，包括遗传性球形红细胞增多症，遗传性口腔细胞增多症和东南亚卵母细胞增多症。这些conditions中的每一个都具有常染色体显性遗传模式，这意味着SLC4A1gene一个拷贝中的突变足以引起该疾病。（换行）遗传性球囊增多症中涉及的突变导致AE1蛋白减少，这导致异常的红细胞呈圆形和球形，而不是扁平的盘状，并且比平时更脆弱。异常细胞过早分解，引起溶血性贫血。由红细胞分解引起的遗传性球形细胞增多症的其他特征包括皮肤变黄和眼睛白斑（黄疸），胆结石和脾肿大（脾肿大）。(换行)遗传性造口细胞增多症的特征是形状异常的红细胞，类似于嘴或“造口”。这种情况是由异常的AE1蛋白引起的，该蛋白允许带正电荷的钠（Na+）和钾（K+）分子泄漏出细胞。结果，红细胞不稳定并且比平时更快地分解，导致溶血性贫血和相关特征。(换行)东南亚卵细胞增多症是疟疾流行地区最常见的血液病，主要是东南亚国家，包括泰国和马来西亚。导致这种疾病的突变（通常称为SAO突变）删除了AE1蛋白中的9个蛋白结构单元（氨基酸）。这种变化导致红细胞表面AE1的减少；结果，细胞异常刚性和椭圆形。异常细胞通常不会在受影响的个体中引起任何健康问题。研究表明，由SLC4A1gene一个拷贝的突变引起的东南亚卵母细胞增多症可预防疟疾的严重神经系统并发症。但是，在该gene的两个拷贝中都具有SAO突变可能在出生前具有致命性。（换行）虽然细胞膜中AE1蛋白的缺乏影响这些血液疾病中红细胞的结构，但研究人员推测肾细胞中有足够的正常AE1蛋白用于阴离子交换和酸释放，因此受影响的个体不发展远端肾小管酸中毒（如上所述）。"}]}' WHERE id = 25357;
UPDATE genes SET GHR_metadata_ch = '{"gene": "FMO3", "conditions": [{"name": "三甲基胺尿症", "detail": "在患有三甲基尿症的人群中已鉴定出FMO3gene的25个以上突变。这些突变大多数导致产生小的，无功能的FMO3酶。其他突变会改变用于构建酶的单个结构单元（氨基酸），从而改变其形状并破坏其功能。没有足够的功能性FMO3酶，人体就无法有效地将三甲胺转化为三甲胺-N-氧化物。结果，三甲胺在体内积聚并在受影响的人的汗液，尿液和呼吸中释放。该化合物的排泄是三甲基氨基尿症强烈的体味特征的原因。研究表明，饮食和压力也在确定鱼类气味的强度方面发挥作用。"}]}' WHERE id = 25365;
UPDATE genes SET GHR_metadata_ch = '{"gene": "MYOC", "conditions": [{"name": "早发性青光眼", "detail": "大约10%至33%的青少年开角型青光眼患者的MYOCgene有突变。在一些原发性先天性青光眼患者中也检测到MYOCgene突变。已经鉴定出40多个MYOCgene突变。（换行）MYOCgene中的突变可能会改变myocilin蛋白，从而阻碍其与其他蛋白的相互作用。未掺入蛋白质复合物中的有缺陷的肌球蛋白可能会积聚在小梁网和睫状体中。过量的蛋白质可能会阻止足够的液体从眼睛流出，导致眼内压升高并引起早发性青光眼的体征和症状。(换行)MYOC和CYP1B1gene均发生突变的个体可能比仅其中一个gene发生突变的个体更早发生青光眼。"}, {"name": "其他疾病", "detail": "一小部分患有迟发性原发性开角型青光眼（POAG）（最常见的成人青光眼）的个体的MYOCgene发生突变。然而，大多数这种情况是由其他疾病，衰老和吸烟等生活方式因素引起的。"}]}' WHERE id = 25369;
UPDATE genes SET GHR_metadata_ch = '{"gene": "DARS2", "conditions": [{"name": "脑干和脊髓受累及乳酸升高的白质脑病", "detail": "在患有脑干和脊髓受累以及乳酸升高（LBSL）的白质脑病患者中，已经鉴定出DARS2gene中至少有25个突变，这种情况会影响大脑和脊髓并导致行走困难。导致这种情况的最常见突变破坏了遗传信息拼凑在一起的方式，从而为生产线粒体天冬氨酰-tRNA合成酶创造了蓝图。蓝图的大部分副本拼接不正确，这可以防止酶的产生。然而，一些拷贝被正确地拼接在一起，并且产生少量正常酶。其他突变会改变酶中的单个氨基酸。这种类型的突变导致线粒体天冬氨酰-tRNA合成酶活性降低。由于活性降低，该酶难以将天冬氨酸添加到tRNA中，这阻碍了该氨基酸添加到线粒体蛋白中。(换行)目前尚不清楚gene突变如何导致LBSL的体征和症状。研究人员不了解为什么线粒体天冬氨酰-tRNA合成酶活性降低会特别影响大脑和脊髓的某些部位。"}]}' WHERE id = 25396;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SERPINC1", "conditions": [{"name": "遗传性抗凝血酶缺乏症", "detail": "已发现SERPINC1gene中至少有220个突变会导致遗传性抗凝血酶缺乏症。这些突变中的大多数会改变抗凝血酶中的单个蛋白质结构单元（氨基酸），从而破坏其控制血液凝固的能力。（换行）遗传性抗凝血酶缺乏症可根据SERPINC1gene突变分为I型和II型。（换行）I型遗传性抗凝血酶缺乏症是由SERPINC1gene突变引起的，该突变阻止细胞从改变的gene产生抗凝血酶。这种类型的个体在每个细胞中只有一个SERPINC1gene的工作拷贝，这导致抗凝血酶正常量的大约一半。受影响的个体没有足够的抗凝血酶来灭活凝血蛋白，这会导致遗传性抗凝血酶缺乏症中异常血栓形成的风险增加。（换行）导致II型遗传性抗凝血酶缺乏症的突变导致产生具有降低的活性的改变的抗凝血酶。患有这种病症的个体通常具有正常水平的抗凝血酶，但蛋白质不能正常运作。可以根据突变是否影响与凝血酶和其他凝血因子（IIa型），肝素（IIb型）或两者（IIc型）的结合来进一步划分II型。具有遗传性抗凝血酶缺乏症IIb型的个体比具有其他形式的这种病症的个体具有更低的形成异常血凝块的风险，因为抗凝血酶能够在没有肝素的情况下灭活凝血蛋白。"}]}' WHERE id = 25401;
UPDATE genes SET GHR_metadata_ch = '{"gene": "GRN", "conditions": [{"name": "GRN相关的额颞叶痴呆", "detail": "在与GRN相关的额颞叶痴呆患者中，已鉴定出GRNgene的65个以上突变。最常见的突变写为Arg493Ter或R493X，在制造颗粒蛋白的说明中会产生过早的终止信号。导致GRN相关额颞叶痴呆的大多数突变都会阻止每个细胞中GRNgene的一个拷贝产生任何蛋白质。这些遗传变化的结果是，细胞仅产生通常量的颗粒蛋白的一半。(换行)目前尚不清楚颗粒蛋白的缺乏如何导致GRN相关额颞叶痴呆的特征。但是，研究表明，该疾病的特征是在某些脑细胞中积累了一种称为TAR DNA结合蛋白（TDP-43）的蛋白质。TDP-43蛋白形成团块（聚集体），可能干扰细胞功能并最终导致细胞死亡。研究人员正在努力确定GRNgene的突变以及由此导致的颗粒蛋白损失如何与大脑中TDP-43的积累有关。(换行)GRN相关额颞叶痴呆的特征是由于大脑前部附近称为额叶和颞叶的区域神经元逐渐丧失所致。额叶参与推理，计划，判断和解决问题，而颞叶帮助处理听力，言语，记忆和情绪。这些区域神经元的死亡导致许多关键脑功能的问题。但是，尚不清楚为什么与GRN相关的额颞叶痴呆患者的额叶和颞叶神经元丢失比其他大脑区域更频繁。"}]}' WHERE id = 25419;
UPDATE genes SET GHR_metadata_ch = '{"gene": "NPHS2", "conditions": [{"name": "先天性肾病综合征", "detail": "已发现NPHS2gene中至少有170个突变引起先天性肾病综合征。这种情况是一种肾脏疾病，始于婴儿期，通常会在儿童早期导致不可逆的肾衰竭（终末期肾脏疾病）。该gene的突变似乎是先天性肾病综合征的最常见原因。大多数NPHS2gene突变会改变podocin蛋白中的单个蛋白结构单元（氨基酸）。这些突变导致功能蛋白的减少或缺失，从而损害正常狭缝隔膜的形成。没有功能性狭缝隔膜，分子异常通过肾脏并在尿液中排泄。肾脏的过滤能力从出生开始就恶化，最终导致终末期肾脏疾病。"}, {"name": "其他疾病", "detail": "NPHS2gene突变可导致其他形式的肾病综合征，这些综合征会在以后的生活中发展。在一种称为婴儿肾病综合征的形式中，该病的体征和症状出现在4至12个月大之间。这种情况的特征类似于先天性肾病综合征（如上所述），但通常不太严重。引起婴儿肾病综合征的NPHS2gene突变对podocin功能的影响可能小于引起先天性肾病综合征的突变，这是该疾病较晚发作的原因。"}]}' WHERE id = 25442;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ITGA2B", "conditions": [{"name": "格兰兹曼血小板减少症", "detail": "已发现ITGA2Bgene中至少有200个突变会引起Glanzmann血小板减少症，这是一种罕见的出血性疾病。导致这种疾病的突变发生在每个细胞中gene的两个拷贝中，并且损害αIIb亚基的产生或活性，这破坏了功能性整联蛋白的形成？IIb？3.功能性整合素缺乏（缺乏）？IIb？3防止纤维蛋白原或其他蛋白质的充分结合，损害血栓的形成。缺乏血小板凝聚力会导致格兰兹曼血小板减少症患者经历长时间或自发性出血事件。"}, {"name": "其他疾病", "detail": "ITGA2Bgene的突变也可引起另一种罕见的出血性疾病，称为血小板型出血性疾病16。患有这种疾病的人有类似于格兰兹曼血小板减少症（如上所述）的体征和症状，包括频繁的流鼻血（鼻出血），牙龈出血，或皮肤下出血引起的皮肤红色或紫色斑点（瘀斑），但发作通常较温和。(换行)与Glanzmann血小板减少症不同，这种疾病是由每个细胞中只有一个ITGA2Bgene拷贝的突变引起的，并且突变导致形成一些整联蛋白？IIb？3即使没有形成凝块，异常打开（活动）。这种异常活跃的蛋白质无法到达血小板表面，在凝块形成过程中需要与其他血小板结合。过度活跃的整合素？IIb？3在血小板形成期间不适当地结合细胞内的凝血蛋白，导致血小板变得畸形和大。形状异常的血小板寿命缩短，因此血小板数量经常减少，这会损害血块形成。（减少血小板数量的组合被称为大血小板减少症。）（换行）由于导致这种疾病的突变只影响ITGA2Bgene的一个拷贝，一些正常的整合素形成并产生正常的血小板，这就解释了受影响个体的轻微体征和症状。"}]}' WHERE id = 25448;
UPDATE genes SET GHR_metadata_ch = '{"gene": "LAMC2", "conditions": [{"name": "交界性大疱性表皮松解症", "detail": "在交界性大疱性表皮松解症（JEB）患者中，已经鉴定出LAMC2gene的30多个突变。该疾病的更严重形式称为Herlitz JEB，通常是由严重破坏功能性层粘连蛋白332产生的突变引起的。这些突变中的大多数会在制备层粘连蛋白332的γ亚基的说明中导致过早的终止信号，从而阻止了该蛋白的组装。没有层粘连蛋白332，表皮仅微弱地连接到皮肤的下层。摩擦或其他轻微创伤（例如摩擦或刮擦）可导致皮肤层分离，导致形成水泡。患有Herlitz JEB的婴儿会出现广泛的水疱，从而导致危及生命的并发症。（换行）其他LAMC2gene突变导致较温和形式的交界性表皮松解性大疱性非Herlitz JEB。这些突变中的一些改变了层粘连蛋白332的γ亚基中的单个蛋白质结构单元（氨基酸）。其他人在γ亚基中添加或删除少量氨基酸或改变gene指令用于制备亚基的方式。负责非Herlitz JEB的遗传变化通常会导致层粘连蛋白332蛋白的产生，该蛋白保留了其某些功能。受影响的人会出现水泡，但可能仅限于手，脚，膝盖和肘部，并且在新生儿期后通常会有所改善。"}]}' WHERE id = 25470;
UPDATE genes SET GHR_metadata_ch = '{"gene": "NCF2", "conditions": [{"name": "慢性肉芽肿病", "detail": "已发现NCF2gene中的50多个突变会引起慢性肉芽肿性疾病。患有这种疾病的人由于免疫系统减弱而发生反复发作的感染和炎症的风险增加。NCF2gene的突变引起这种情况的所有病例的不到5%。这些突变改变了嗜中性粒细胞胞浆因子2蛋白中的单个蛋白结构单元（氨基酸），这导致该蛋白异常短而无功能或改变了其3维结构。所有这些突变都会降低嗜中性粒细胞胞质因子2蛋白的功能或阻止其产生。没有这种蛋白质，NADPH氧化酶就无法正常组装或起作用。结果，吞噬细胞不能产生活性氧来杀死外来入侵者，并且中性粒细胞活性不受调节。缺乏NADPH氧化酶会使受影响的个体容易受到多种类型的感染和过度炎症的影响。"}, {"name": "系统性红斑狼疮", "detail": "遗传学家庭参考提供有关系统性红斑狼疮的信息。"}, {"name": "自身免疫性疾病", "detail": "研究表明，NCF2gene的某些正常变异会增加患系统性红斑狼疮的风险。这种情况是一组称为自身免疫性疾病的相关疾病之一，当免疫系统发生故障并攻击身体的组织和器官时会发生这种疾病。与系统性红斑狼疮风险增加相关的变体改变了NCF2gene中的单个DNA构建块（核苷酸）。这些变化被认为导致产生具有改变的功能的嗜中性粒细胞胞质因子2蛋白，其损害NADPH氧化酶的功能。因此，当外来入侵者引发免疫反应时，会产生较少的活性氧。这种活性氧的缺乏导致身体通过激活更多的免疫细胞和产生更多的免疫蛋白而过度补偿。过度活跃的免疫反应增加了免疫细胞攻击人体组织和器官的风险，导致系统性红斑狼疮。研究人员认为，遗传和环境因素的结合在这种复杂疾病的发展中起着重要作用。"}]}' WHERE id = 25480;
UPDATE genes SET GHR_metadata_ch = '{"gene": "UBE3B", "conditions": [{"name": "考夫曼眼脑面综合征", "detail": "在考夫曼眼脑面部综合征患者中至少鉴定出15个UBE3Bgene突变，这是一种以眼部疾病（oculo-），严重智力障碍（-cerebro-）和独特的面部特征模式（-facial）为特征的疾病。与这种疾病相关的突变被认为会导致异常的UBE3B蛋白，该蛋白不能正常发挥功能或不稳定并迅速分解。这种蛋白质功能的丧失可能会阻止细胞消除某些不必要的蛋白质，从而导致神经系统和身体其他部位的发育和功能出现问题。"}]}' WHERE id = 25522;
UPDATE genes SET GHR_metadata_ch = '{"gene": "CDC73", "conditions": [{"name": "家族性孤立性甲状旁腺功能亢进", "detail": "在一些家族性孤立性甲状旁腺功能亢进症的家庭中发现了CDC73gene的遗传突变，这种疾病的特征是甲状旁腺功能亢进（原发性甲状旁腺功能亢进）。这些腺体释放出一种激素，有助于控制血液中钙的正常平衡。原发性甲状旁腺功能亢进破坏了这种平衡，可导致高血钙水平（高钙血症），肾结石，骨骼变薄（骨质疏松症），恶心，呕吐，高血压（高血压），虚弱和疲劳。原发性甲状旁腺功能亢进是甲状旁腺功能亢进下颌肿瘤综合征的特征（如下所述）；然而，家族性孤立性甲状旁腺功能亢进症是在甲状旁腺功能亢进症患者中诊断出来的，而不是甲状旁腺功能亢进症颌骨肿瘤综合征的其他特征。导致家族性孤立性甲状旁腺功能亢进的CDC73gene突变可能导致副纤蛋白活性降低。副纤蛋白活性降低可引起细胞增殖增加，导致涉及甲状旁腺的肿瘤形成。家族性甲状旁腺功能亢进症患者的甲状旁腺肿瘤通常是非癌性的（良性的）。肿瘤导致腺体过度活跃，这种过度活跃导致病症的体征和症状。与家族性甲状旁腺功能亢进症相关的突变被认为对蛋白质功能的影响不如引起甲状旁腺功能亢进症颌骨肿瘤综合征的突变严重。偶尔，家族性孤立性甲状旁腺功能亢进症患者后来发展为甲状旁腺功能亢进-颌骨肿瘤综合征的特征，尽管有些人从未这样做。由CDC73gene突变引起的家族性孤立性甲状旁腺功能亢进可能是甲状旁腺功能亢进-颌骨肿瘤综合征的早期或轻度形式。"}, {"name": "甲状旁腺功能亢进下颌肿瘤综合征", "detail": "已发现CDC73gene中超过45个遗传突变会引起甲状旁腺功能亢进-下颌肿瘤综合征，这种疾病的特征是一种良性肿瘤，称为下颌纤维瘤，甲状旁腺肿瘤会引起甲状旁腺功能亢进。甲状旁腺功能亢进破坏血液中钙的正常平衡，导致甲状旁腺功能亢进下颌肿瘤综合征患者的肾结石，骨质疏松症，恶心，呕吐，高血压，虚弱和疲劳。（换行）导致这种情况的大多数CDC73gene突变会导致副纤蛋白异常短且无功能。没有功能性副纤蛋白，细胞增殖就不能正常调节。由于副纤蛋白功能丧失导致的不受控制的细胞分裂可导致甲状旁腺功能亢进-下颌肿瘤综合征患者的甲状旁腺，下颌和其他组织中肿瘤的形成。甲状旁腺肿瘤可能是癌性或非癌性的，干扰腺体的正常功能并导致甲状旁腺功能亢进-下颌肿瘤综合征患者的原发性甲状旁腺功能亢进。"}, {"name": "甲状旁腺癌", "detail": "在多达70%的甲状旁腺癌病例中发现CDC73gene突变。在大约三分之一的受影响个体中，该gene发生了变化，该突变是从亲本遗传而来的，并存在于人体的所有细胞中（种系突变）。但是，并非每个继承CDC73gene突变的人最终都会发展为甲状旁腺癌。其他遗传和非遗传因素也会导致一个人的癌症风险。（换行）在剩余的三分之二具有CDC73gene突变的个体中，该病症与甲状旁腺细胞一生中发生的突变（体细胞突变）有关。在具有种系或体细胞CDC73gene突变的个体中，必须在CDC73gene另一拷贝中发生第二个突变才能使甲状旁腺癌发展。具有两个改变的CDC73gene拷贝的甲状旁腺细胞不产生功能性副纤蛋白。结果，细胞生长和分裂不受控制，这可能导致甲状旁腺癌。（折线）甲状旁腺癌的风险显着增加是甲状旁腺功能亢进症颌骨肿瘤综合征和家族性孤立性甲状旁腺功能亢进症（均如上所述）的特征。甲状旁腺癌发生在15%的甲状旁腺功能亢进下颌肿瘤综合征患者和1%的家族性甲状旁腺功能亢进患者中。目前尚不清楚为什么某些CDC73gene突变会导致孤立的甲状旁腺癌，另一些会导致甲状旁腺和颌骨的非癌性肿瘤，而另一些仅引起甲状旁腺功能亢进。"}]}' WHERE id = 25525;
UPDATE genes SET GHR_metadata_ch = '{"gene": "EFTUD2", "conditions": [{"name": "下颌面发育不全伴小头畸形", "detail": "在患有小头畸形（MFDM）的下颌面发育不良的人群中，已鉴定出50多个EFTUD2gene突变。这种疾病会导致头部和面部畸形，智力残疾以及影响身体其他部位的异常。这些异常包括食管闭锁（食管阻塞）和气管食管瘘（食管与气管之间的异常连接），气管食管瘘允许来自食管的液体进入气道并干扰呼吸。（换行）导致MFDM的EFTUD2gene突变导致每个细胞中该gene的一个拷贝产生很少或没有功能性酶。这种酶的缺乏可能会损害mRNA的加工。这些突变与MFDM特定症状之间的关系尚不清楚。"}]}' WHERE id = 25529;
UPDATE genes SET GHR_metadata_ch = '{"gene": "CFH", "conditions": [{"name": "C3肾小球病", "detail": "已经发现CFHgene中的几个突变引起C3肾小球病，C3肾小球病是导致肾损伤和终末期肾病（ESRD）的罕见形式的肾病，其特征在于血液中C3蛋白水平低，蛋白尿，血尿，减少身体许多部位的尿量和肿胀量。该病是由于异常或无功能的蛋白质导致补体系统过度活化，导致肾脏肾小球受损。"}, {"name": "年龄相关性黄斑变性", "detail": "在与年龄相关的黄斑变性患者中已鉴定出CFHgene的几种突变，黄斑变性是一种主要影响黄斑的眼病，导致视力丧失，随着时间的推移，老年人的视力逐渐发展。这是由于在视网膜下积聚了称为玻璃疣的淡黄色沉积物。"}, {"name": "非典型溶血性尿毒症综合征", "detail": "在非典型溶血性尿毒症综合征患者中已鉴定出CFHgene的几种突变，这种疾病导致肾脏小血管中的血栓（血栓）异常，其特征是溶血性贫血，血小板减少和肾衰竭。补体因子H蛋白c末端结构域的突变导致异常或无功能的蛋白，导致肾细胞表面补体系统过度活化。"}, {"name": "其他疾病", "detail": "CFHgene的变异与一种称为基底层玻璃膜疣（BLD）的眼病有关，这种疾病的特征是从成年早期开始视网膜下的玻璃膜疣积聚（与年龄相关的黄斑变性相反，后者开始于晚年生活）。"}]}' WHERE id = 25532;
UPDATE genes SET GHR_metadata_ch = '{"gene": "CFHR5", "conditions": [{"name": "C3肾小球病", "detail": "已经发现CFHgene中的几个突变引起C3肾小球病，C3肾小球病是导致肾损伤和终末期肾病（ESRD）的罕见形式的肾病，其特征在于血液中C3蛋白水平低，蛋白尿，血尿，减少身体许多部位的尿量和肿胀量。该病是由于CFHR5gene的重复改变了其补体因子H相关5的结构和功能，并阻止了其有效调节补体系统。"}, {"name": "Atypical hemolytic-uremic syndrome", "detail": "在非典型溶血性尿毒症综合征患者中已鉴定出CFHR5gene的几种突变，这种情况会导致肾脏小血管中的血栓（血栓）异常。该病的特征是与凝血异常有关的三个主要特征：溶血性贫血，血小板减少和肾衰竭。"}, {"name": "年龄相关性黄斑变性", "detail": "在与年龄相关的黄斑变性患者中已经鉴定出CFHR5gene的几个突变，黄斑变性是一种主要影响黄斑的眼病，导致视力丧失，随着时间的推移，老年人的视力逐渐发展。这是由于在视网膜下积聚了称为玻璃疣的淡黄色沉积物。"}]}' WHERE id = 25534;
UPDATE genes SET GHR_metadata_ch = '{"gene": "ASPM", "conditions": [{"name": "常染色体隐性原发性小头畸形", "detail": "ASPMgene突变是常染色体隐性原发性小头畸形的最常见原因（通常缩短为MCPH，代表“小头畸形原发性遗传”）。这种情况的特征是头部和大脑异常小，智力残疾和发育迟缓。已发现ASPMgene中有80多个突变引起MCPH。(换行)几乎所有负责MCPH的ASPMgene突变都会减少ASPM蛋白的产生。产生的蛋白质比正常蛋白质短，被认为部分或全部无功能。功能性ASPM蛋白的缺乏会损害细胞分裂，尤其是在发育中的大脑的神经祖细胞中。结果，产生较少的成熟神经元，并且受影响的个体出生时具有异常小的大脑。头部小，智力障碍和发育迟缓都是大脑小的结果。(换行)由于ASPM蛋白存在于全身细胞中，因此尚不清楚为什么ASPMgene突变比其他细胞类型更严重地影响神经祖细胞。一些研究人员认为，神经祖细胞比其他类型的细胞对ASPM蛋白的缺乏更敏感。其他研究人员建议，另一种蛋白质可能能够补偿大脑外细胞中ASPM蛋白质的损失。"}, {"name": "年龄相关性黄斑变性", "detail": "遗传学家庭参考提供有关年龄相关性黄斑变性的信息。"}, {"name": "癌症", "detail": "ASPMgene在几种类型的癌症中上调，这意味着它比癌细胞中通常产生更多的ASPM蛋白。特别是，已经在称为神经胶质瘤的脑肿瘤和称为肝细胞癌的肝肿瘤中研究了ASPMgene的上调。目前尚不清楚为什么ASPMgene在这些癌症中异常活跃，或者额外的ASPM蛋白可能对癌细胞产生什么影响。但是，研究表明，ASPMgene异常高的活性与癌症进展，扩散到身体其他部位（转移）和复发有关。"}]}' WHERE id = 25537;
UPDATE genes SET GHR_metadata_ch = '{"gene": "F13B", "conditions": [{"name": "XIII因子缺乏症", "detail": "已发现F13Bgene中至少有17个突变会导致遗传性因子XIII缺乏症，这是一种罕见的出血性疾病。如果不进行治疗，受影响的个体发生异常出血事件的风险会大大增加，包括威胁生命的颅骨内出血（颅内出血）。F13Bgene突变严重减少了因子XIII的B亚基的数量或破坏了其功能，阻止了其稳定和保护A亚基。由此导致的因子XIII活性丧失削弱了新的血栓并阻止它们有效地止血。"}, {"name": "年龄相关性黄斑变性", "detail": "遗传学家庭参考提供有关年龄相关性黄斑变性的信息。"}]}' WHERE id = 25538;
UPDATE genes SET GHR_metadata_ch = '{"gene": "CRB1", "conditions": [{"name": "莱伯先天性黑蒙", "detail": "已发现CRB1gene中的50多个突变会导致Leber先天性黑ama病。该gene的突变占该病所有病例的9%至13%。(换行)导致Leber先天性黑病的大多数CRB1gene突变会导致CRB1蛋白异常短而无功能的版本，或显着减少细胞中产生的这种蛋白的量。CRB1蛋白的缺乏会破坏视网膜的早期发育。视网膜变得异常厚，不会形成正常的分层结构。这些变化会从生命的早期开始导致严重的视力障碍。"}, {"name": "视锥-视杆营养不良", "detail": "遗传学家庭参考提供有关锥杆营养不良的信息。"}, {"name": "视网膜色素变性", "detail": "遗传学家庭参考提供有关视网膜色素变性的信息。"}, {"name": "其他疾病", "detail": "在另一种称为色素性视网膜炎的眼疾患者中，已经鉴定出CRB1gene至少有35个突变。这种情况的特征是由视网膜中感光细胞的逐渐退化引起的进行性视力丧失。CRB1gene突变会导致几种罕见的色素性视网膜炎形式，这些形式可通过其特定的视网膜变化加以区分。（换行）导致视网膜色素变性的CRB1gene突变导致CRB1蛋白功能的部分或全部丧失。正常CRB1蛋白的缺乏会损害视网膜的发育并导致感光细胞的逐步退化。(换行)目前尚不清楚为什么一些CRB1gene突变的人患有与Leber先天性黑蒙相关的严重早期视力障碍，而其他人则经历更多渐进性视力丧失和与色素性视网膜炎相关的其他眼部问题。研究人员怀疑其他遗传因素可能会改变CRB1gene突变的影响，从而影响这些疾病的严重程度。"}]}' WHERE id = 25540;
UPDATE genes SET GHR_metadata_ch = '{"gene": "RPL35A", "conditions": [{"name": "钻石-布莱克凡贫血", "detail": "在患有Diamond-Blackfan贫血的个体中至少鉴定出五个RPL35Agene突变。据信这些突变影响RPL35A蛋白的稳定性或功能。研究表明，缺乏功能性核糖体蛋白可能会增加骨髓中造血细胞的自我破坏，导致红细胞数量减少（贫血）。细胞分裂的异常调节或凋亡的不适当触发可能会导致其他健康问题和异常的身体特征，从而影响某些Diamond-Blackfan贫血患者。"}]}' WHERE id = 25545;
UPDATE genes SET GHR_metadata_ch = '{"gene": "MMAB", "conditions": [{"name": "甲基丙二酸血症", "detail": "已发现MMABgene中至少有25个突变会引起甲基丙二酸血症，这种疾病的特征是进食困难，发育迟缓和长期健康问题。这些遗传变化中的一些删除或复制了MMABgene中的少量遗传物质。其他突变会改变用于制造MMAB酶的单个蛋白质结构单元（氨基酸）。研究人员认为，几乎所有这些突变都会导致酶的非功能性产生。结果，无法正确制作AdoCbl。AdoCbl的缺乏会损害甲基丙二酰辅酶A突变酶的功能，从而导致某些蛋白质和脂质的不完全分解。这种缺陷使有毒化合物积聚在人体的器官和组织中，引起甲基丙二酸血症的体征和症状。"}]}' WHERE id = 25552;
UPDATE genes SET GHR_metadata_ch = '{"gene": "GFAP", "conditions": [{"name": "亚历山大病", "detail": "研究人员已经鉴定出导致亚历山大病的50多种GFAP突变。这些突变大多数改变了用于制造神经胶质纤维酸性蛋白的构件（氨基酸）之一。一些突变增加或去除了蛋白质中的两个氨基酸。所有这些变化都改变了胶质纤维酸性蛋白的结构。改变的蛋白质可能会干扰正常中间丝的形成。结果，异常的神经胶质纤维酸性蛋白可能在星形胶质细胞中积累，有助于形成Rosenthal纤维，从而损害细胞功能。尚不清楚星形胶质细胞受损如何导致髓磷脂的异常维持，从而引起亚历山大病的体征和症状。"}]}' WHERE id = 25561;
UPDATE genes SET GHR_metadata_ch = '{"gene": "CACNA1S", "conditions": [{"name": "低钾性周期性麻痹", "detail": "已经在低钾性周期性麻痹患者中发现了CACNA1Sgene的突变，这种疾病的特征是由于钙离子转运受损而导致的肌肉无力，从而抑制了肌肉的收缩。"}, {"name": "恶性高热", "detail": "CACNA1Sgene的突变与恶性高热有关，恶性高热是对某些药物特别是一些麻醉剂和手术期间使用的一种肌肉松弛剂引起的病症，其特征是肌肉僵硬，肌纤维破裂（横纹肌溶解），高烧，血液和其他组织中的酸水平升高（酸中毒），心率加快。"}]}' WHERE id = 25569;
UPDATE genes SET GHR_metadata_ch = '{"gene": "MVK", "conditions": [{"name": "甲羟戊酸激酶缺乏症", "detail": "已经发现MVKgene中至少80个突变引起甲羟戊酸激酶缺乏。甲羟戊酸激酶缺乏症有两种类型，它们通过体征和症状的严重程度来区分。两种类型中较轻的一种称为高免疫球蛋白D综合征（HIDS），而较严重的一种称为甲羟戊酸尿症（MVA）。导致甲羟戊酸激酶缺乏的大多数MVKgene突变都会导致甲羟戊酸激酶中单个蛋白质结构单元（氨基酸）的变化。在大约80%的HIDS患者中发现了一种突变，该突变用酶中337位的氨基酸异亮氨酸取代了氨基酸缬氨酸（写为Val337Ile或V337I）。在MVA患者中从未发现V337I突变。（换行）大多数MVKgene突变导致产生不稳定的甲羟戊酸激酶并折叠成不正确的三维形状，导致甲羟戊酸激酶活性降低。酶缺乏（缺乏）的严重程度决定了病情的严重程度。具有约1%至20%正常甲羟戊酸激酶活性的人通常会发展为HIDS。正常酶活性低于1%的个体通常会发展MVA。尽管缺乏甲羟戊酸激酶活性，但甲羟戊酸激酶缺乏症患者通常会正常产生胆固醇，类固醇激素和胆汁酸。(换行)一些研究人员认为这些特征可能是由于甲羟戊酸（甲羟戊酸激酶通常起作用的物质）的积累。其他研究人员认为甲羟戊酸产生的物质短缺，例如某些细胞所需的物质功能，导致发烧发作和这种情况的其他特征。引起炎症反应如发热，皮疹，免疫系统蛋白升高以及甲羟戊酸激酶缺乏症的许多其他特征的确切机制尚不清楚。"}]}' WHERE id = 25571;
UPDATE genes SET GHR_metadata_ch = '{"gene": "TNNT2", "conditions": [{"name": "家族性肥厚型心肌病", "detail": "TNNT2gene的突变可引起家族性肥厚型心肌病，这种疾病的特征是心肌增厚（肥大）。在患有这种情况的个体中约有5%发现TNNT2gene突变。尽管一些肥厚型心肌病患者没有明显的健康影响，但所有受影响的个体都有心力衰竭和猝死的风险增加。(换行)家族性肥厚型心肌病中的大多数TNNT2gene突变会改变心肌肌钙蛋白T蛋白中的单个蛋白结构单元（氨基酸）。改变后的蛋白质可能会掺入肌钙蛋白复合物中，但可能无法正常发挥作用。但是，尚不清楚gene突变如何导致家族性肥厚型心肌病的特征。"}, {"name": "家族性扩张型心肌病", "detail": "遗传学家庭参考提供有关家族性扩张型心肌病的信息。"}, {"name": "家族性限制性心肌病", "detail": "遗传学家庭参考资料提供有关家族性限制性心肌病的信息。"}, {"name": "室致密化不全", "detail": "遗传学家庭参考提供有关左心室不紧致的信息。"}, {"name": "其他疾病", "detail": "在患有其他心脏病的人中发现了TNNT2gene的突变，包括扩张型心肌病和左心室不紧致。但是，尚不清楚TNNT2gene突变在两种疾病中的作用。扩张型心肌病是一种削弱和扩大心脏，防止其有效抽血的疾病。扩张型心肌病会增加心力衰竭和过早死亡的风险。当心脏左下腔（左心室）未正确发育时，发生左心室不紧致。心肌减弱，无法有效抽血，常导致心力衰竭。左心室不紧致的个体也可能发生异常心律（心律失常）。"}]}' WHERE id = 25584;
UPDATE genes SET GHR_metadata_ch = '{"gene": "TRPV4", "conditions": [{"name": "进行性神经性腓骨肌萎缩症", "detail": "已发现TRPV4gene中至少有七个突变会导致2C型Charcot-Marie-Tooth病（也称为2C型遗传性运动和感觉神经病）。这种情况会影响周围神经，周围神经将大脑和脊髓连接到肌肉以及检测触觉，疼痛，热和声音等感觉的感觉细胞。Charcot-Marie-Tooth病会导致感觉丧失，肌肉无力以及脚，手和腿的肌肉消瘦（萎缩）。由TRPV4gene突变引起的2C型Charcot-Marie-Tooth病具有其他体征和症状，通常在其他形式的疾病中不会发生。这些额外的体征和症状包括由内耳神经损伤引起的听力损失（感觉神经性听力损失），导致声音嘶哑的声带无力，以及将腹部与胸腔（膈肌）分开的肌肉无力，这可能会导致呼吸问题。（换行）导致2C型Charcot-Marie-Tooth病的TRPV4gene突变各自改变TRPV4钙通道中的单个蛋白质构建块（氨基酸）。研究表明，这些突变使通道过度活化，增加了可以流入细胞的钙量。目前尚不清楚为什么这种变化会特别影响神经系统，以及这种变化如何与与这种情况相关的特定神经系统问题相关。"}, {"name": "Metatropic发育不良", "detail": "在患有亚热带发育不良的人中，已经鉴定出TRPV4gene中至少有10个突变，亚热带发育不良是一种以身材矮小（侏儒症）和其他骨骼异常为特征的骨骼疾病。这些突变大多数会改变TRPV4钙通道中的单个氨基酸。但是，一些突变会在TRPV4gene中插入或删除DNA片段。(换行)研究表明，引起亚热带发育异常的TRPV4gene突变会过度激活TRPV4钙通道。软骨形成细胞（软骨细胞）中钙的增加可能破坏软骨和骨的早期发育。但是，尚不清楚为什么这些突变会特别影响软骨细胞，以及TRPV4通道活性的变化如何导致与亚热带发育不良相关的特定骨骼异常。"}, {"name": "其他疾病", "detail": "TRPV4gene的突变会导致多种其他情况，其中大多数会影响发育中的骨骼或神经系统。(换行)除了亚热带发育异常外，与TRPV4gene突变相关的骨骼疾病还包括常染色体显性遗传性运动障碍；脊椎骨干发育不良，Kozlowski型；Maroteaux型脊柱干phy端发育不良；和震颤不典型增生。这些相关疾病包括身材矮小，脊柱的左右弯曲异常（脊柱后凸畸形）以及骨骼发育的其他问题。TRPV4gene中的（换行）突变也会引起神经系统疾病。除了2 c型进行性神经性腓骨肌萎缩症，这一系列的相关疾病还包括先天性远端嵴髓性肌肉萎缩症，即拥有属性腿部和臀部的肌肉无力，以及肩胛腓骨嵴髓性肌肉萎缩症，这包括肩部和小腿的肌肉无力和萎缩。(换行)导致这些骨骼和神经系统疾病的大多数TRPV4gene突变会改变TRPV4钙通道中的单个氨基酸。这些突变可能导致通道过度活跃，尽管一些研究表明该突变可能对不同组织中的通道功能产生不同的影响。已发现某些TRPV4gene突变会导致某些人的骨骼疾病和其他人的神经系统疾病。此外，某些TRPV4gene突变可在同一个人中引起骨骼和神经系统特征。研究人员正在努力确定TRPV4gene突变如何引起这种广泛的体征和症状。(换行)另一种骨骼疾病，被称为近距离家族性数字关节炎，也与TRPV4gene的突变有关。这种情况的特征是手指和脚趾关节炎（关节炎），手指和脚趾缩短（近距离接触）。导致这种情况的突变似乎损害了TRPV4钙通道的功能，阻止了它有效地运输钙离子。目前尚不清楚通道功能的丧失如何导致这种情况的特定特征。TRPV4gene的常见变异（多态性）与另外两种疾病有关：低钠血症（一种可导致危险脑肿胀的水不平衡状态）和慢性阻塞性肺病（COPD），一种常见的肺部疾病导致呼吸困难。尚未确定TRPV4钙通道功能的差异如何与这两种情况相关。(换行)由于TRPV4gene的突变与如此广泛的疾病相关，因此一些研究人员建议将所有与TRPV4相关的疾病称为TRPV4相关的周围神经病变和骨发育不良（TRPV4-PNAB）或TRPV4病。"}]}' WHERE id = 25597;
UPDATE genes SET GHR_metadata_ch = '{"gene": "EMG1", "conditions": [{"name": "Bowen-Conradi综合征", "detail": "在美国和加拿大的Hutterite人群中常见的EMG1gene突变会导致Bowen-Conradi综合征，这种疾病会影响身体的许多部位，通常在生命的最初几个月致命。导致Bowen-Conradi综合征的突变称为Asp86Gly或D86G，将EMG1蛋白中第86位的蛋白质结构单元（氨基酸）天冬氨酸变为氨基酸甘氨酸。研究表明，该突变使蛋白质不稳定，导致核仁中可用的EMG1蛋白质数量减少。这种蛋白质在核仁中的短缺会损害核糖体的产生，这可能会减少细胞的生长和分裂（增殖）；但是，尚不清楚EMG1gene突变如何导致Bowen-Conradi综合征的特定体征和症状。"}]}' WHERE id = 25691;
UPDATE genes SET GHR_metadata_ch = '{"gene": "TSC2", "conditions": [{"name": "淋巴管平滑肌瘤病", "detail": "TSC2gene的突变会导致大多数称为淋巴管平滑肌瘤病（LAM）的疾病。这种破坏性肺部疾病的特征是肺部平滑肌样组织异常过度生长。它几乎只发生在女性身上，引起咳嗽，呼吸短促，胸痛和肺部塌陷。（换行）LAM可以单独发生（分离的或散发的LAM）或与称为结节性硬化症复合体（如下所述）的病症组合发生。研究人员建议，散发性LAM是由TSC2gene的随机突变引起的，该突变发生在发育的早期。结果，身体的一些细胞具有正常版本的gene，而其他细胞具有突变版本。这种情况称为马赛克。当女性一生中某些细胞中TSC2gene的另一个拷贝发生突变（体细胞突变）时，她可能会发展为LAM。"}, {"name": "结节性硬化症", "detail": "在患有结节性硬化症的个体中已鉴定出TSC2gene的1100多个突变，该疾病的特征是发育问题和身体许多部位非癌性肿瘤的生长。这些突变中的大多数会在TSC2gene中插入或删除少量DNA构建块（碱基对）。其他突变会改变TSC2gene中的单个碱基对，或在制造结核菌素的说明中产生过早的终止信号。(换行)患有TSC2相关结节性硬化症复合体的人出生时每个细胞中都有一个TSC2gene的突变拷贝。这种突变阻止了细胞从该gene的该拷贝中产生功能性结核菌素。但是，通常从TSC2gene的另一个正常拷贝中产生足够的结核菌素，以有效调节细胞生长。对于某些类型的肿瘤发展，涉及该gene的另一个拷贝的第二个突变必须在人的一生中在某些细胞中发生。（换行）当TSC2gene的两个拷贝在特定细胞中突变时，该细胞不能产生任何功能性结核菌素。这种蛋白质的损失使得细胞以不受控制的方式生长和分裂以形成肿瘤。管蛋白的缺乏也会干扰某些细胞的正常发育。在患有TSC2相关性结节性硬化症的人中，第二个TSC2gene突变通常发生在受影响人一生中的多个细胞中。不同类型细胞中结核菌素的丢失会破坏正常发育，并导致许多不同器官和组织中肿瘤的生长。"}]}' WHERE id = 25743;
UPDATE genes SET GHR_metadata_ch = '{"gene": "FRAS1", "conditions": [{"name": "弗雷泽综合症", "detail": "FRAS1gene中至少有24个突变会导致一种称为Fraser综合征的疾病；FRAS1gene突变约占这种情况的一半。弗雷泽综合症会影响出生前的发育，其特征是眼睛被皮肤完全覆盖（隐性眼球），手指和脚趾之间的皮肤融合（皮肤上有组织），以及肾脏和其他器官和组织的异常。（换行）参与Fraser综合征的FRAS1gene中的大多数突变导致产生功能很少或没有功能的异常蛋白质。异常蛋白质可能会迅速分解，从而阻止FRAS/FREM复合物的形成。皮肤基底膜中缺乏FRAS/FREM复合物会导致顶层分离，从而在发育过程中形成水泡。这些水泡可能会阻止出生前某些结构的正确形成，从而导致隐性眼球炎和皮肤综合症。尚不清楚缺乏FRAS/FREM复合物如何导致肾脏异常和Fraser综合征的其他问题。"}, {"name": "其他疾病", "detail": "在肾脏和泌尿道异常但没有弗雷泽综合征的其他体征和症状的人中也发现了FRAS1gene的突变（如上所述）。此类异常归为先天性肾脏和泌尿道异常（CAKUT）。具有FRAS1gene突变的人的常见异常是缺少一个肾脏（一种称为肾脏发育不全的疾病）。参与CAKUT的FRAS1gene突变通常会改变FRAS1蛋白中的单个蛋白结构单元（氨基酸）。研究人员推测，这些突变的影响比引起弗雷泽综合症的突变的影响要轻；一些FRAS1蛋白功能可能仍然存在。这些gene突变如何影响FRAS/FREM复合体或导致肾脏发育不全和其他CAKUT尚不清楚。"}]}' WHERE id = 25752;
UPDATE genes SET GHR_metadata_ch = '{"gene": "MT-TH", "conditions": [{"name": "线粒体脑病，乳酸性酸中毒和中风样发作", "detail": "少数具有线粒体脑病，乳酸性酸中毒和中风样发作（MELAS）特征的人的MT-THgene发生了突变。这种情况的特征是反复发作的严重头痛，肌肉无力（肌病），听力下降，中风样发作，包括意识丧失，癫痫发作和其他影响神经系统的问题。一些具有MT-THgene突变的人还具有另一种线粒体疾病的特征，称为肌阵挛性癫痫，伴有参差不齐的红色纤维（MERRF）；这些附加功能可能包括肌肉抽搐（肌阵挛），运动协调困难（共济失调）和称为参差不齐的红色纤维的异常肌肉细胞。这种体征和症状的组合称为MERRF/MELAS重叠综合征。（换行）导致MELAS和MERRF/MELAS重叠综合征的MT-THgene突变改变gene中的单个DNA构建块（核苷酸）。研究人员尚未确定这些遗传变化如何改变线粒体中的能量产生或引起MELAS或MERRF/MELAS重叠综合征的各种体征和症状。"}, {"name": "肌阵挛性癫痫伴红色纤维参差不齐", "detail": "如上所述，一些具有MT-THgene突变的个体具有具有参差不齐的红色纤维（MERRF）和MELAS的肌阵挛性癫痫的特征。该重叠综合征中涉及的突变在gene位置12147（写为G12147A）处用核苷酸腺嘌呤取代了核苷酸鸟嘌呤。尚不清楚为什么这种突变会导致MERRF和MELAS重叠。"}, {"name": "其他疾病", "detail": "MT-THgene的另一个突变可能会增加患上称为心肌病的心脏病的风险。心肌病患者的心肌无力，无法有效泵血。已经在几名患有心肌病但没有线粒体疾病的其他常见体征（例如神经系统异常）的成年人中发现了MT-THgene的特定变化。该突变用gene位置12192处的核苷酸腺嘌呤取代核苷酸鸟嘌呤（写为G12192A）。目前尚不清楚为什么线粒体DNA的这种改变可能会增加一个人患心脏病的风险而不影响身体的其他部位。"}]}' WHERE id = 25789;
UPDATE genes SET GHR_metadata_ch = '{"gene": "MT-TE", "conditions": [{"name": "母系遗传性糖尿病和耳聋", "detail": "在少数患有母亲遗传性糖尿病和耳聋（MIDD）的人中发现了MT-TEgene的突变。患有这种疾病的人患有糖尿病，有时会听力下降，尤其是高音调。受影响的人也可能有肌肉无力（肌病）和眼睛，心脏或肾脏的问题。这种情况下涉及的突变用位置14709处的核苷酸胞嘧啶（写为T14709C）替换了DNA构建块（核苷酸）胸腺嘧啶。这种突变可能会损害线粒体帮助触发胰岛素释放的能力。在受影响的个体中，当β细胞不能产生足够的胰岛素来有效调节血糖时，就会导致糖尿病。研究人员尚未确定T14709C突变如何导致听力损失或MIDD的其他特征。"}, {"name": "其他疾病", "detail": "MT-TEgene的突变也与婴儿短暂性线粒体肌病（也称为良性COX缺乏性肌病）有关。这种罕见的情况发生在生命的最初几个月内，并导致严重的肌肉无力，肌肉张力差（肌张力低下）以及体内称为乳酸的化学物质积聚（乳酸性酸中毒）。受影响的婴儿通常难以喂养，需要机器的支持以帮助他们呼吸。几个月后，体征和症状有所改善，大多数受影响的个体在2或3岁时未显示该病的症状。（换行）涉及婴儿短暂性线粒体肌病的突变改变线粒体DNA中的单核苷酸。具体而言，将位置14674处的核苷酸胸腺嘧啶替换为核苷酸胞嘧啶或鸟嘌呤（分别写为T14674C或T14674G）。这些突变损害氧化磷酸化。结果，肌肉细胞不能产生足够的能量，导致影响婴儿短暂性线粒体肌病的婴儿的肌肉问题。目前尚不清楚为什么只涉及肌肉或受影响的婴儿如何从疾病中恢复过来。"}]}' WHERE id = 25790;
UPDATE genes SET GHR_metadata_ch = '{"gene": "MT-TK", "conditions": [{"name": "母系遗传性糖尿病和耳聋", "detail": "在少数患有母体遗传性糖尿病和耳聋（MIDD）的人中发现了MT-TKgene的突变，这是一种以糖尿病和听力损失为特征的疾病，尤其是高音调。较少见的是，受影响的人的眼睛，肌肉，心脏或肾脏有问题。这种情况下涉及的突变改变了MT-TKgene中的单个DNA构建块（核苷酸）；核苷酸腺嘌呤在gene位置8296（写为A8296G）被核苷酸鸟嘌呤取代。研究人员认为，A8296G突变会损害线粒体帮助触发胰岛素释放的能力。在受影响的个体中，当β细胞不能产生足够的胰岛素来有效调节血糖时，就会导致糖尿病。研究人员尚未确定A8296G突变如何导致听力损失或MIDD的其他特征。"}, {"name": "肌阵挛性癫痫伴红色纤维参差不齐", "detail": "在具有参差不齐的红色纤维（MERRF）的肌阵挛性癫痫患者中，已经鉴定出MT-TKgene的几个突变。这种情况的特征是肌肉抽搐（肌阵挛），反复发作（癫痫），称为参差不齐的红色纤维的异常肌肉细胞以及神经系统的其他问题。这种情况下涉及的大多数突变都会改变gene中的单个核苷酸。一种突变引起所有MERRF病例的约80%。这种遗传变化在gene位置8344（写为A8344G）上用核苷酸鸟嘌呤取代了核苷酸腺嘌呤。A8344G突变削弱了线粒体制造蛋白质，利用氧气和产生能量的能力。研究人员尚未确定MT-TKgene的变化如何导致MERRF的特定体征和症状。他们继续研究线粒体gene突变在各种组织中，特别是在大脑中的作用。(换行)少数MT-TKgene突变的人具有MERRF的一些特征和另一种称为线粒体脑病，乳酸性酸中毒和中风样发作（MELAS）的线粒体疾病的一些特征。据说这些受影响的个体患有MERRF/MELAS重叠综合征。该综合征的其他体征和症状包括反复发作的严重头痛，肌肉无力（肌病），运动协调困难（共济失调），听力下降以及包括意识丧失在内的中风样发作。导致MERRF/MELAS重叠综合征的MT-TKgene突变会改变该gene中的单个核苷酸。具体而言，它在gene位置8356（写为T8356C）上用核苷酸胞嘧啶取代了核苷酸胸腺嘧啶。目前尚不清楚这种遗传变化如何导致MERRF/MELAS重叠综合征的体征和症状。"}, {"name": "利综合征", "detail": "遗传学家庭参考提供有关Leigh综合征的信息。"}, {"name": "其他疾病", "detail": "A8344G突变是在MERRF患者中发现的最常见突变（如上所述），也可能导致进行性脑部疾病，称为Leigh综合征。这种情况的体征和症状通常始于婴儿期或儿童早期，包括呕吐，癫痫发作，发育迟缓，肌病和运动问题。患有这种疾病的人也可能发生心脏病，肾脏问题和呼吸困难。研究人员尚未确定为什么只有一些具有A8344G突变的人会出现Leigh综合征的体征和症状。(换行)以心肌（心肌病）减弱和听力丧失为特征的疾病也由MT-TKgene突变引起。受影响的个体也可能患有肌病和共济失调。该突变用gene内8363位的核苷酸腺嘌呤（写为G8363A）取代了核苷酸鸟嘌呤。目前尚不清楚MT-TKgene的这种改变如何导致心肌病，听力下降和其他症状。"}]}' WHERE id = 25791;
UPDATE genes SET GHR_metadata_ch = '{"gene": "MT-TL1", "conditions": [{"name": "母系遗传性糖尿病和耳聋", "detail": "MT-TL1gene中至少有一个突变会导致母亲遗传性糖尿病和耳聋（MIDD）。患有这种疾病的人患有糖尿病，有时会听力下降，尤其是高音调。较少见的是，受影响的人的眼睛，肌肉，心脏或肾脏有问题。MT-TL1gene突变是MIDD中最常见的突变，涉及85%的病例。它改变了MT-TL1gene中的单个DNA构建块（核苷酸）；核苷酸腺嘌呤被gene中3243位的核苷酸鸟嘌呤取代（写为A3243G）。（换行）A3243G突变降低了tRNALeu（UUR）将亮氨酸添加到正在组装的蛋白质中的能力，这减慢了蛋白质的产生。研究人员认为，A3243G突变削弱了线粒体帮助触发胰岛素释放的能力。在MIDD患者中，当β细胞不能产生足够的胰岛素来有效调节血糖时，就会导致糖尿病。研究人员尚未确定A3243G突变如何导致听力损失或MIDD的其他特征。"}, {"name": "线粒体脑病，乳酸性酸中毒和中风样发作", "detail": "已经在患有线粒体脑病，乳酸性酸中毒和中风样发作（MELAS）的人中鉴定出MT-TL1gene的几种突变。这种情况的特征是反复发作的严重头痛，肌肉无力（肌病），听力下降，中风样发作，包括意识丧失，癫痫发作和其他影响神经系统的问题。这些突变大多数会改变gene中的单个核苷酸。A3243G突变（如上所述）是MELAS中最常见的突变。它负责所有MELAS案件的约80%。这种突变削弱了线粒体制造蛋白质，利用氧气和产生能量的能力。研究人员尚未确定mtDNA的变化如何导致MELAS的特定体征和症状。他们继续研究线粒体gene突变在不同组织中，特别是在大脑中的作用。"}, {"name": "肌阵挛性癫痫伴红色纤维参差不齐", "detail": "在一些具有红色纤维参差不齐（MERRF）的肌阵挛性癫痫特征的人中发现了MT-TL1gene的突变。这些人还具有MELAS的一些特征（如上所述）。这种体征和症状的组合称为MERRF/MELAS重叠综合征。该综合征的特征包括肌肉抽搐（肌阵挛），肌肉无力（肌病），运动协调困难（共济失调），听力下降，癫痫发作和糖尿病。（换行）导致MERRF/MELAS重叠综合征的突变均会改变MT-TL1gene中的单个核苷酸。研究人员尚未确定这些遗传变化如何导致MERRF/MELAS重叠综合征的体征和症状。"}, {"name": "进行性眼外肌麻痹", "detail": "MT-TL1gene的突变导致了一些称为进行性眼外肌麻痹的眼病。这种疾病削弱了控制眼球运动并导致眼睑下垂（上睑下垂）的肌肉。(换行)一些进行性眼外肌麻痹病例是由A3243G突变引起的，这种突变与通常引起MELAS和MIDD的遗传变化相同（如上所述）。目前尚不清楚相同的MT-TL1gene突变如何导致不同的情况。研究人员尚未确定mtDNA的变化如何导致进行性眼外肌麻痹的特定体征和症状，尽管该病的特征可能与氧化磷酸化受损有关。已经提出，眼肌通常受到线粒体缺陷的影响，因为它们特别依赖于氧化磷酸化作为能量。"}, {"name": "利综合征", "detail": "遗传学家庭参考提供有关Leigh综合征的信息。"}, {"name": "线粒体复合体I缺乏", "detail": "遗传学家庭参考提供有关线粒体复合体I缺乏的信息。"}, {"name": "其他疾病", "detail": "据报道，MT-TL1gene中约有20个突变，其中大多数改变了该gene中的单个核苷酸。这些突变与主要影响肌肉和神经系统的各种体征和症状有关。具有MT-TL1突变的人通常会出现肌肉无力，疼痛和极度疲劳，尤其是在运动过程中（运动不耐症）。在某些情况下，心肌也会减弱，这被称为心肌病。这种异常会阻止心脏正常抽动。(换行)一些MT-TL1gene发生变化的儿童经历了发育迟缓，精神问题或自闭症谱系障碍（影响沟通和社交互动）。在少数婴儿猝死综合症（SIDS）病例中也发现了MT-TL1突变，这是1岁以下儿童死亡的主要原因。"}]}' WHERE id = 25792;
UPDATE genes SET GHR_metadata_ch = '{"gene": "MT-TS1", "conditions": [{"name": "肌阵挛性癫痫伴红色纤维参差不齐", "detail": "在少数具有参差不齐的红色纤维（MERRF）的肌阵挛性癫痫变异形式的人中发现了MT-TS1gene的突变。在这些情况下，受影响的个体通常会出现肌肉抽搐（肌阵挛），肌肉无力（肌病），运动协调困难（共济失调），听力下降，癫痫发作和智力障碍。已经发现MT-TS1gene中的两个突变引起这些症状。一个突变用gene位置7512（写为T7512C）的核苷酸胞嘧啶取代了DNA构建块（核苷酸）胸腺嘧啶。另一个突变在7472位插入一个额外的胞嘧啶（写为7472insC）。研究人员尚未确定这些遗传变化如何导致MERRF变异形式。"}, {"name": "非综合征性听力损失", "detail": "遗传学家庭参考资料提供有关非综合征性听力损失的信息。"}, {"name": "掌plant角化病伴耳聋", "detail": "一些导致听力丧失的MT-TS1gene突变可在受影响的个体中引起其他体征和症状。例如，一种突变会导致称为耳聋的掌plant角化病的皮肤疾病。除听力损失外，这种情况还会导致手掌和脚底的皮肤变厚，鳞片状和变稠。（换行）导致这种特征组合的遗传变化将MT-TS1gene（写为A7445G）中7445位的核苷酸鸟嘌呤替换为核苷酸腺嘌呤。这种突变可能会破坏tRNASer（UCN）分子的正常生产。结果，较少的tRNASer（UCN）可用于在线粒体内组装蛋白质。这些变化减少了氧化磷酸化所需蛋白质的产生，这可能损害线粒体产生ATP的能力。目前尚不清楚为什么在这种情况下突变的影响仅限于内耳和皮肤中的细胞。"}, {"name": "其他疾病", "detail": "在某些家庭中，MT-TS1gene的突变会导致与听力损失无关的健康问题。例如，在与运动相关的肌肉疼痛，虚弱和极度疲劳（运动不耐症）的人中发现了一种突变。导致这些症状的遗传变化将MT-TS1gene（写为A7497G）中7497位的核苷酸鸟嘌呤替换为核苷酸腺嘌呤。(换行)目前尚不清楚为什么MT-TS1gene的变化会导致如此多种症状和体征。即使在一个家庭中，受影响的个体也可能由于相同的遗传变化而导致不同的健康问题。研究人员认为，其他遗传和环境因素有助于确定MT-TS1gene突变是否导致孤立的听力损失，与其他体征和症状相关的听力损失或与听力无关的特征。"}]}' WHERE id = 25793;
UPDATE genes SET GHR_metadata_ch = '{"gene": "MT-TV", "conditions": [{"name": "线粒体脑病，乳酸性酸中毒和中风样发作", "detail": "MT-TVgene的突变是线粒体脑病，乳酸性酸中毒和中风样发作（MELAS）的非常罕见的原因。大多数MELAS病例是由其他线粒体gene突变引起的，但也有少数病例是由MT-TVgene突变引起的。已经鉴定出至少两个突变，每个突变改变gene中的单个DNA结构单元（核苷酸）。这些突变之一在gene位置1642（写为G1642A）上用核苷酸腺嘌呤取代了核苷酸鸟嘌呤。另一个突变将核苷酸鸟嘌呤改变为位置1644（G1644A）的核苷酸腺嘌呤。MT-TVgene的改变可以减少可用于在线粒体内组装蛋白质的tRNAVal的量。因为这些蛋白质是氧化磷酸化所必需的，所以MT-TV突变可能会损害线粒体产生能量的能力。研究人员尚未确定线粒体能量产生的变化如何导致MELAS的特定特征。"}, {"name": "利综合征", "detail": "遗传学家庭参考提供有关Leigh综合征的信息。"}, {"name": "其他疾病", "detail": "已经报道了MT-TVgene中的其他一些突变，其中大多数改变了该gene中的单个核苷酸。这些突变导致主要影响肌肉和神经系统的各种体征和症状。与MT-TV突变相关的医学问题包括反复发作的偏头痛，肌肉无力和运动问题，协调性差，癫痫发作，听力下降，学习障碍和智力功能丧失（痴呆）。少数受影响的个体也报告了一种称为心肌病的心脏病，它会削弱和扩大心肌。(换行)MT-TVgene的变化已被确定为Leigh综合征的罕见原因，Leigh综合征是一种进行性脑部疾病，通常出现在婴儿期或儿童早期。在少数情况下，在具有直到成年才出现的体征和症状的人中发现了MT-TV突变。受影响的个体可能会出现呕吐，癫痫发作，发育迟缓，肌肉无力和运动问题。患有这种疾病的人也可能发生心脏病，肾脏问题和呼吸困难。(换行)目前尚不清楚为什么MT-TVgene的变化会导致如此多种症状和体征。即使在一个家庭中，受影响的个体也可能由于相同的遗传变化而导致不同的健康问题。"}]}' WHERE id = 25794;
UPDATE genes SET GHR_metadata_ch = '{"gene": "NEFL", "conditions": [{"name": "进行性神经性腓骨肌萎缩症", "detail": "研究人员已经鉴定出10多种NEFLgene突变，这些突变会导致两种形式的Charcot-Marie-Tooth病，分别称为1F型和2E型。大多数NEFLgene突变会改变神经丝轻亚基中的单个蛋白质结构单元（氨基酸）。其他突变删除或复制了NEFLgene的一部分，从而改变了使神经丝轻亚基成为指令。NEFLgene突变可能会改变神经丝轻亚基的大小或形状。(换行)改变的神经丝轻亚基可能破坏神经丝的组装或其向轴突的运输。这些破坏可能导致轴突异常并损害神经冲动的传递。轴突异常是2E型Charcot-Marie-Tooth病的征兆。在某些情况下，轴突直径减小可能导致神经信号传输速度降低，这是1F型Charcot-Marie-Tooth病的特征。"}]}' WHERE id = 25795;
UPDATE genes SET GHR_metadata_ch = '{"gene": "SRD5A2", "conditions": [{"name": "5-α还原酶缺乏症", "detail": "在5-α还原酶缺乏症患者中已鉴定出SRD5A2gene中约50个突变。这些突变大多数会改变类固醇5-α还原酶2中的单个蛋白质结构单元（氨基酸）。这些遗传变化中的一些使酶完全失活。其他突变会降低但不会消除酶的功能。(换行)由于SRD5A2突变，身体无法有效地将睾丸激素转化为生殖组织中的DHT。DHT的缺乏会破坏出生前外生殖器的形成。患有5-α还原酶缺乏症的人在遗传上是男性，每个细胞中有一条X和一条Y染色体，但他们可能出生时的外生殖器主要是女性，或者不是男性或明显是女性（有时称为模棱两可的生殖器）。其他受影响的婴儿的外生殖器主要表现为男性，但他们通常具有异常小的阴茎（微阴茎）和阴茎下侧的尿道开口（尿道下裂）。(换行)在青春期，睾丸产生更多的睾丸激素。研究人员认为，患有5-α还原酶缺乏症的人会因这种激素水平升高而发展出继发性男性特征。一些受影响的人还保留了少量的5-α还原酶2活性，这可能会产生DHT并有助于青春期第二性征的发展。"}, {"name": "前列腺癌", "detail": "遗传学家庭参考提供有关前列腺癌的信息。"}, {"name": "癌症", "detail": "SRD5A2gene的某些正常变异（多态性）可能与前列腺癌有关。这些多态性中的两个已被广泛研究。最常见的变异是在类固醇5-α还原酶2（写为Val89Leu或V89L）中第89位的氨基酸亮氨酸取代了氨基酸缬氨酸。另一个变体用酶中第49位的氨基酸苏氨酸取代了氨基酸丙氨酸（写为Ala49Thr或A49T）。一些研究表明，这些变异与罹患前列腺癌或患有更具侵略性的疾病的风险增加有关。然而，其他研究并未显示这些关联。尚不清楚SRD5A2多态性在前列腺癌风险中起什么作用。(换行)一些gene突变是在一个人的一生中获得的，并且仅存在于某些细胞中。这些被称为体细胞突变的变化不是遗传的。研究表明，前列腺癌细胞中的体细胞SRD5A2突变可能与前列腺癌的进展有关。这些突变可能会增加类固醇5-α还原酶2的活性，从而提高前列腺组织中DHT的水平。研究表明，DHT等雄激素可以刺激前列腺癌的生长。"}, {"name": "其他疾病", "detail": "在女性中，SRD5A2gene的某些多态性可能会影响患多囊卵巢综合征（PCOS）的风险。PCOS的特征是激素失衡，可导致月经不调，痤疮，体毛过多（多毛症）和体重增加。一些遗传变异会增加卵巢中类固醇5-α还原酶2的活性，这可能会导致这种情况的体征和症状。其他变异，包括常见的多态性Val89Leu，会降低类固醇5-α还原酶2的活性，并降低患PCOS的风险。"}]}' WHERE id = 25796;
